INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 102801, 1, "焼きそばパンが\n食べたいな～\nあいつと…", 102, 0, 8, 1007, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102801, 1, "ねぇあんた、どこか\n安いお店知らない？", 1, 0, 1, 1008, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102801, 1, "あたし、エレガントに\n振る舞えてる？\n…そう？　嬉しい♪", 2, 0, 1, 1009, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102801, 1, "いっぱい働いて遊んで\nよく休んでまた働く。\nそれが健全な生活よね", 3, 0, 1, 1010, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102801, 1, "一緒に部屋の\n整頓でもする？\nハマると楽しいわよ♪", 4, 0, 1, 1011, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102801, 1, "あんたがいると\n子供たちも喜ぶわ♪\nもちろんあたしもね！", 101, 0, 4, 1012, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102801, 1, "ね、あんた今、幸せ？\n何びっくりしてるのよ\n大事なことじゃないの", 102, 0, 8, 1013, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102801, 1, "面倒な用事は午前中に\n片づけてしまうのが、\n時間の使い方のコツよ", 1, 1, 1, 1014, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102801, 1, "午後のティータイム\nとかどう？\n良いカフェがあるの♪", 2, 2, 1, 1015, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102801, 1, "さ、エレガントな夜を\n楽しみましょ。\nモダンな音楽でも\nかけましょうか？", 3, 3, 1, 1016, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 102801, 1, "レディたるもの\n身嗜みはしっかりとね\n見本にならないと♪", 1, 0, 1, 1017, 0);
