INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102901, 1, "ね、次のライブ\n観に来てくれる？\nいい席とっとくから！", 4, 0, 1, 1061, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102901, 1, "ねぇ、台本読むの\nつきあってくれる？\n恋人役に\nなってほしいんだ", 101, 0, 4, 1062, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102901, 1, "新曲、聞いてくれた？\nキミとのことを\nテーマにしたんだよ。\nどこかは秘密だけど♪", 102, 0, 8, 1063, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102901, 1, "朝の発声練習に\n行くけど、キミも\n一緒に来ない？", 1, 1, 1, 1064, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102901, 1, "この時間は\nお腹すくけど…\n水着撮影があるから\nお昼抜いてるんだ", 2, 2, 1, 1065, 0);
