INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 102902, 1, "ファンはもちろん\n大事だけど…\n彼のことは、もっと\n大切にしたいな…", 101, 0, 8, 1081, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 102902, 1, "トップアイドルの\nさらに先…\n彼とならその景色も\n見られるかな", 102, 0, 8, 1082, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102902, 1, "新曲の衣装、もうすぐ\n出来あがるんだって！\n楽しみだな～", 1, 0, 1, 1083, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102902, 1, "アイドルノゾミだけ\nじゃなくて、私の事も\nちゃんと見てね？", 2, 0, 1, 1084, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102902, 1, "キミに響かないなら\nファンにも響くわけ\nないよ！　練習練習！", 3, 0, 1, 1085, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102902, 1, "衣装のスカートは\n鉄壁なんだよ！\n残念でした～", 4, 0, 1, 1086, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102902, 1, "新曲、聞いてくれた？\nキミとのことを\nテーマにしたんだよ\nどこかは秘密だけど♪", 101, 0, 8, 1087, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102902, 1, "ねぇ、私がトップ\nアイドルになっても\nちゃんと隣にいてね\n遠くに行かないでね？", 102, 0, 8, 1088, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102902, 1, "朝の発声練習に\n行くけど、キミも\n一緒に来ない？", 1, 1, 1, 1089, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102902, 1, "お昼ごはん行かない？\n誰にも知られてない\nお店があるんだけど！", 2, 2, 1, 1090, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102902, 1, "夜の帳が二人を包み\n…こういう方向性の\n歌詞も悪くないなぁ", 3, 3, 1, 1091, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 102902, 1, "身だしなみオッケー！\nアイドルはいつでも\n完璧でいなくちゃ！", 1, 0, 1, 1092, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 102902, 1, "ふぅ…\nこんな姿、ファンには\n見せられないな～", 2, 0, 1, 1093, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 102902, 1, "かわいい～♪\nこれは強力な\nライバル登場かな！", 3, 0, 1, 1094, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 102902, 1, "みんなのイメージする\nアイドルの香りは\nこんな感じかな～", 4, 0, 1, 1095, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 102902, 1, "体も暖まったし！\nダンスレッスンに\n行こうかな♪", 5, 0, 1, 1096, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 102902, 1, "ベストタイム更新！\nライブに向けて\n体力つけないと…", 6, 0, 1, 1097, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 102902, 1, "太っちゃったら\nどうしよう…？\nでも手が止まらない～", 7, 0, 1, 1098, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 102902, 1, "この恋愛小説ステキ！\n私もアイドルじゃ\nなかったら…", 8, 0, 1, 1099, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103001, 1, "手裏剣の数が\n合わないデス！\nどこかに落とした\nデスか…？", 1, 0, 1, 1101, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103001, 1, "クセモノ、隠れても\nむだデス！\n…気のせいデシタ", 2, 0, 1, 1102, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103001, 1, "金は天下の\nマワリモノ！\nところで、\nどういう意味デス？", 3, 0, 1, 1103, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103001, 1, "むむ、アクダイカンの\n気配がするデース", 4, 0, 1, 1104, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103001, 1, "クニトリ合戦、\n楽しみデース！", 5, 0, 1, 1105, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103001, 1, "ショーグンと\n出会えて、\nワタシは果報者デス！", 101, 0, 4, 1106, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103001, 1, "最近、ショーグンの\nそばにいると\nドキドキするデス…", 102, 0, 8, 1107, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103001, 1, "なんでも\n命令するデス！\nショーグンの命は\n絶対デス！", 1, 0, 1, 1108, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103001, 1, "あっ、ショーグン、\n出陣はいつで\nゴザルデス！？", 2, 0, 1, 1109, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103001, 1, "さ、さすが\nショーグンデス…\nいつの間に背後を！？", 3, 0, 1, 1110, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103001, 1, "出陣の合図に\n備えるデス！\nほら貝は\nどこデスか！？", 4, 0, 1, 1111, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103001, 1, "見つめられると\nドキドキするデス…\n新しい忍法デスか？", 101, 0, 4, 1112, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103001, 1, "ショーグン！\nえへへ…これからも\nおそばに置いて\nクダサイね？", 102, 0, 8, 1113, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103001, 1, "おはようデス！\n今日も一日\n忍法日和デス！", 1, 1, 1, 1114, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103001, 1, "そろそろお腹\n空いてきたデス。\n昼ごはんにするデス！", 2, 2, 1, 1115, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103001, 1, "明日もきっと\nいい日デスよ、\nショーグン！", 3, 3, 1, 1116, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103001, 1, "ちょんまげに\nしてみるのも\n面白いかもデス！", 1, 0, 1, 1117, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103001, 1, "心地よくて\n眠くなってきマス…\nもしやテキコクの\n罠デスか！？", 2, 0, 1, 1118, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103001, 1, "これを変わり身の\n術に…かわいそう\nだから止めるデス", 3, 0, 1, 1119, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103001, 1, "ワビサビというもの\nデスね！\n素晴らしいデス！", 4, 0, 1, 1120, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103001, 1, "身体を暖めて\nイクサに向かうデス！", 5, 0, 1, 1121, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103001, 1, "アクロバットな忍法を\n使うには、体を\n鍛えないと危険デス！", 6, 0, 1, 1122, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103001, 1, "うーん、\n美味しいデス…\n幸せデス～", 7, 0, 1, 1123, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103001, 1, "これは…\nＢＵＳＨＩＤＯ…\nデスか？", 8, 0, 1, 1124, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103002, 1, "ふんふんふ～ん\n武器の手入れは\n念入りにしマスよ～", 1, 0, 1, 1126, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103002, 1, "クセモノ、隠れても\nむだデス！\n…気のせいデシタ", 2, 0, 1, 1127, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103002, 1, "金は天下の\nマワリモノ！\nところで\nどういう意味デス？", 3, 0, 1, 1128, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103002, 1, "むむ、アクダイカンの\n気配がするデース", 4, 0, 1, 1129, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103002, 1, "クニトリ合戦\n楽しみデース！", 5, 0, 1, 1130, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103002, 1, "最近、ショーグンの\nそばにいると\nドキドキするデス…", 101, 0, 8, 1131, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103002, 1, "ショーグンの身は\n何があってもワタシが\n守るデス！", 102, 0, 8, 1132, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103002, 1, "あっ、ショーグン\n出陣はいつで\nゴザルデス！？", 1, 0, 1, 1133, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103002, 1, "イクサの前に\nホネ休めも大事デスよ\nゆっくりするデース！", 2, 0, 1, 1134, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103002, 1, "さ、さすがショーグン\nデス…いつの間に\n背後を！？", 3, 0, 1, 1135, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103002, 1, "一緒に忍法考える\nデス！\nショーグン用の\n忍法も必要デスね！", 4, 0, 1, 1136, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103002, 1, "見つめられると\nドキドキするデス…\n新しい忍法デスか？", 101, 0, 8, 1137, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103002, 1, "ショーグン！\nえへへ…これからも\nおそばに置いて\nクダサイね？", 102, 0, 8, 1138, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103002, 1, "いい朝デス！\nトーゴク風の朝ゴハン\n作るデス！", 1, 1, 1, 1139, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103002, 1, "もうお昼デス\nお昼寝するデスか？", 2, 2, 1, 1140, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103002, 1, "そろそろおネム\nデース…\n一緒に寝るデス？", 3, 3, 1, 1141, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103002, 1, "服の乱れは心の乱れ\nデス！　きっちり\nしないといけないデス", 1, 0, 1, 1142, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103002, 1, "心地よくて眠く\nなってきマス…\nもしやテキコクの\n罠デスか！？", 2, 0, 1, 1143, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103002, 1, "これを変わり身の\n術に…かわいそう\nだから止めるデス", 3, 0, 1, 1144, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103002, 1, "ワビサビというもの\nデスね！\n素晴らしいデス！", 4, 0, 1, 1145, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103002, 1, "身体を暖めてイクサに\n向かうデス！", 5, 0, 1, 1146, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103002, 1, "ショーグンのお役に\n立つため、修行\nあるのみデス！", 6, 0, 1, 1147, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103002, 1, "美味しいデス…\n酢昆布を和えて\nみたいデス", 7, 0, 1, 1148, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103002, 1, "物事を知って\n来たるべきイクサに\n活かすデス", 8, 0, 1, 1149, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103101, 1, "お父さん、そろそろ\n子離れを…しない？\nそうですか…", 1, 0, 1, 1151, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103101, 1, "父は、いつも男性に\nきびしいですね", 2, 0, 1, 1152, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103101, 1, "占ってほしい人は、\nいつでも言って\nください", 3, 0, 1, 1153, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103101, 1, "あなたの手相は…\nて、手を握るのって\n緊張しますね", 4, 0, 1, 1154, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103101, 1, "この部屋、風水的にも\n運気が上がりそうです", 5, 0, 1, 1155, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103101, 1, "あの人とのこと占って\nみようかな…\n好き…嫌い…\n好き♪…き、嫌い", 101, 0, 4, 1156, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103101, 1, "私との相性ですか？\nそれなら占う必要は\nありませんよ…\nふふっ♪", 102, 0, 8, 1157, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103101, 1, "女難の相が\n出ていますね…\n気をつけてください", 1, 0, 1, 1158, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103101, 1, "今日のあなたの運勢は\n大きょ…\n大吉です", 2, 0, 1, 1159, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103101, 1, "父と散歩中です。\n毎日決まったコースを\n散歩させないと、\nストレスが溜まるので", 3, 0, 1, 1160, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103101, 1, "小僧！\nシノブに触るとは\nいい度胸じゃねぇか！", 4, 0, 1, 1161, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103101, 1, "あなたに触られると、\n動悸が早くなります\nもしかして心霊現象？", 101, 0, 4, 1162, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103101, 1, "今日もたくさんの幽霊\nに慕われていますね。\n素敵です", 102, 0, 8, 1163, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103101, 1, "おはようございます\n今日の運勢を\n占いましょうか？", 1, 1, 1, 1164, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103101, 1, "今日のラッキー\nアイテムはドクロです\n良ければ父を\nお貸ししましょうか？", 2, 2, 1, 1165, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103101, 1, "今日のあなたの運気は\n最高潮でした。\nもうすぐ今日は\n終わりますが", 3, 3, 1, 1166, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103101, 1, "お父さん。\nいつも、身だしなみに\n気を使ってるけど、\nあまり変わってないよ", 1, 0, 1, 1167, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103101, 1, "一休みしましょう。\n憑かれて疲れました", 2, 0, 1, 1168, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103101, 1, "かわいい…\n付喪神が宿らないかな", 3, 0, 1, 1169, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103101, 1, "お父さん、\nリラックスし過ぎると\n成仏しちゃうよ？", 4, 0, 1, 1170, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103101, 1, "気持ちいいです…\n極楽浄土みたい…", 5, 0, 1, 1171, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103101, 1, "はぁはぁ…\nお、お父さん、\n代わりに運動して…", 6, 0, 1, 1172, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103101, 1, "お父さんも\n食べたいの？\n死んでるのに？", 7, 0, 1, 1173, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103101, 1, "この本…\nもしかして予言書？", 8, 0, 1, 1174, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103102, 1, "ここにいる幽霊は\nみんな幸せそうです", 1, 0, 1, 1176, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103102, 1, "お父さんが\n女の子ばかりに\n声をかけるので\n困っています", 2, 0, 1, 1177, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103102, 1, "成仏はうれしいこと\nです…でも友達が\n減るのは寂しいですね", 3, 0, 1, 1178, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103102, 1, "幽霊に好かれる人は\n素敵な方です\nもちろん、あなたも", 4, 0, 1, 1179, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103102, 1, "お父さん、そろそろ\n子離れを…しない？\nそうですか…", 5, 0, 1, 1180, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103102, 1, "生きている人に\n好かれるには、\nどうすればいい\nのでしょう？", 101, 0, 8, 1181, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103102, 1, "お父さん、どうしたら\nあの人のことを\n認めてくれますか？", 102, 0, 8, 1182, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103102, 1, "この部屋、風水的にも\n運気が上がりそうです", 1, 0, 1, 1183, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103102, 1, "シノブには指一本\n触れさせん！\nガブッ！！", 2, 0, 1, 1184, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103102, 1, "あなたの手相は…\nて、手を握るのって\n緊張しますね", 3, 0, 1, 1185, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103102, 1, "ラップ音？\nいえ、父のいびきです", 4, 0, 1, 1186, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103102, 1, "私との相性ですか？\nそれなら占う必要は\nありませんよ…\nふふっ♪", 101, 0, 8, 1187, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103102, 1, "前世って信じますか？\n私とあなたは\nもしかすると…", 102, 0, 8, 1188, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103102, 1, "今日のラッキー\nアイテムはドクロです\n良ければ父を\nお貸ししましょうか？", 1, 1, 1, 1189, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103102, 1, "明るい時間は\nあまり得意では\nないんです…", 2, 2, 1, 1190, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103102, 1, "夜のお散歩に\n行きませんか？\n知り合いの幽霊に\n挨拶して回るんです", 3, 3, 1, 1191, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103102, 1, "お父さん\nいつも、身だしなみに\n気を使ってるけど\nあまり変わってないよ", 1, 0, 1, 1192, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103102, 1, "うとうと…\nあれ…\n走馬灯が見える…", 2, 0, 1, 1193, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103102, 1, "かわいい…\nお父さんみたい…", 3, 0, 1, 1194, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103102, 1, "お父さん\nリラックスし過ぎると\n成仏しちゃうよ？", 4, 0, 1, 1195, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103102, 1, "快適です…\n幽霊もどんどん\n集まってきますね…", 5, 0, 1, 1196, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103102, 1, "はぁはぁ…\nお、お父さん、\n代わりに運動して…", 6, 0, 1, 1197, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103102, 1, "お父さんの分も、\n私が食べるね", 7, 0, 1, 1198, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103102, 1, "この本の占い\n私の占いと違う…", 8, 0, 1, 1199, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103201, 1, "私だって、一人で\n買い物ぐらい\nできますわ…きっと", 1, 0, 1, 1201, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103201, 1, "そろそろ新しい\n案件を始めないと\nいけませんわね", 2, 0, 1, 1202, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103201, 1, "一日も早く、\n一人前の商人に\nなりたいものです", 3, 0, 1, 1203, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103201, 1, "平和を買うのには\nどれほどのお金が\n必要なのかしら？", 4, 0, 1, 1204, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103201, 1, "ここももう少し広いと\n落ち着くのですけど。\n改築しませんこと？", 5, 0, 1, 1205, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103201, 1, "あの方はお金で雇った\nわけではないのに\n私といてくださる…", 101, 0, 4, 1206, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103201, 1, "たとえ私が間違っても\nきっとあの方が\n正してくれますわ…", 102, 0, 8, 1207, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103201, 1, "私と、庶民向けの\nお店へ一緒に\n参りませんか？", 1, 0, 1, 1208, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103201, 1, "資産運用？\nお幾らほど\n増やしたいのです？", 2, 0, 1, 1209, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103201, 1, "どんぶり勘定だとよく\n言われるのですが…\n例えが小さすぎます！", 3, 0, 1, 1210, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103201, 1, "お金で解決するのは\n悪いことでは\nありませんわよね？", 4, 0, 1, 1211, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103201, 1, "その、よろしければ\n私とお散歩など\nいかがです？", 101, 0, 4, 1212, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103201, 1, "もしあなたさまが\n道に迷われるなら、\n私もお供いたします！", 102, 0, 8, 1213, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103201, 1, "早起きは三ルピの徳と\n申しますが…\nもっとお得ですわね", 1, 1, 1, 1214, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103201, 1, "昼食は町の屋台で\n立ったまま食べて\nみたいですわ！", 2, 2, 1, 1215, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103201, 1, "よろしければ\nお休み前のミルクを\n御一緒にいかが？", 3, 3, 1, 1216, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103201, 1, "だらしない身なりでは\n取引相手に信用して\nいただけませんものね", 1, 0, 1, 1217, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103201, 1, "この感触と安らぎ…\n素晴らしい\n費用対効果ですわ…", 2, 0, 1, 1218, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103201, 1, "何の見返りも求めず\nただひたすら愛でて\nいたいのです！", 3, 0, 1, 1219, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103201, 1, "目に見えないものに\nお金をかけるのが\n真の贅沢ですわ！", 4, 0, 1, 1220, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103201, 1, "誰ひとり寒い思いを\nしない町にするために\n私にできる事は…", 5, 0, 1, 1221, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103201, 1, "健康はお金では\n買えませんが\n運動器具は別ですわ！", 6, 0, 1, 1222, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103201, 1, "なかなか\n美味ですわ\nシェフはどちらに？", 7, 0, 1, 1223, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103201, 1, "経済の仕組みを\nしっかりと\n学ばなくては…", 8, 0, 1, 1224, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103202, 1, "そろそろ新しい\n案件を始めないと\nいけませんわね", 1, 0, 1, 1226, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103202, 1, "ええと、これは\nおいくらで買ったん\nでしたっけ？", 2, 0, 1, 1227, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103202, 1, "お金を見せたら\n態度が変わる人は\n信用できませんわ", 3, 0, 1, 1228, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103202, 1, "地図が読めずとも\n道に迷わない方って\nいらっしゃるのですね", 4, 0, 1, 1229, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103202, 1, "この狭さも\n慣れれば\n快適ですわ♪", 5, 0, 1, 1230, 0);
