INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103202, 1, "たとえ私が間違っても\nきっとあの方が\n正してくれますわ…", 102, 0, 8, 1232, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103202, 1, "一人前の商人に\n相応しい居振る舞いを\nしたいものですわ", 1, 0, 1, 1233, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103202, 1, "幼い頃\nお店屋さんごっこを\nなさいましたか？", 2, 0, 1, 1234, 0);
