INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103202, 1, "今度一緒に…\nパーティにでも\n行きませんこと？", 4, 0, 1, 1236, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103202, 1, "その、よろしければ\n私とお散歩など\nいかがです？", 101, 0, 8, 1237, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103202, 1, "あなたさまの優しさは\nお金には\n代えられませんわね", 102, 0, 8, 1238, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103202, 1, "なぜだか\n眠り足りないような\n感じがしますわ…", 1, 1, 1, 1239, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103202, 1, "昼食は町の屋台で\n立ったまま食べて\nみたいですわ！", 2, 2, 1, 1240, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103202, 1, "よろしければ\nお休み前のミルクを\n御一緒にいかが？", 3, 3, 1, 1241, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103202, 1, "ドレスは汚れていない\nようですが…やだ\n髪がほつれてますわ！", 1, 0, 1, 1242, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103202, 1, "こういったものに\n腰を沈めると…\nき、気が緩んで…", 2, 0, 1, 1243, 0);
