INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103202, 1, "香りは質のいいものと\nそうでないものが\nはっきり分かりますわ", 4, 0, 1, 1245, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103202, 1, "誰ひとり寒い思いを\nしない町にするために\n私にできる事は…", 5, 0, 1, 1246, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103202, 1, "目標は遠くに\n置いた方が励みに\nなりますわ！", 6, 0, 1, 1247, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103202, 1, "なかなか\n美味ですわ\nシェフはどちらに？", 7, 0, 1, 1248, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103202, 1, "商人って、物語では\n悪役にされやすいの\nですね…", 8, 0, 1, 1249, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103301, 1, "家の中のものを使って\n物ボケ、考えるべ！", 1, 0, 1, 1251, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103301, 1, "毎日の日課…それは\n炊事、洗濯、大喜利\nだべ！", 2, 0, 1, 1252, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103301, 1, "うちに塀ができたら、\nへー、って言いたい！", 3, 0, 1, 1253, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103301, 1, "オラが作る料理は\nこの後スタッフで\n美味しくいただくべよ", 4, 0, 1, 1254, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103301, 1, "エリザベスも\n連れてきてぇ～", 5, 0, 1, 1255, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103301, 1, "あんちゃんも、\nエリザベスと同じ位\nめんこいべ♪", 101, 0, 4, 1256, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103301, 1, "オラとあんちゃん、\n息がぴったりに\nなってきたべ…！", 102, 0, 8, 1257, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103301, 1, "おぉ！　そんな急に\nツッコまれたら、\nびっくらこくべやー！", 1, 0, 1, 1258, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103301, 1, "ネタはオラが書くけど\nあんちゃんが\n書きたかったら\n書いてもいいんだぞ？", 2, 0, 1, 1259, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103301, 1, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！", 3, 0, 1, 1260, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103301, 1, "押すなよ、\n絶対押すなよ\n…なんて\n言ってないべさ？", 4, 0, 1, 1261, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103301, 1, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～", 101, 0, 4, 1262, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103301, 1, "将来的には夫婦漫才\nってのも…あ、ひ、\nひとりごとだべさ～！", 102, 0, 8, 1263, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103301, 1, "おはよう！　一緒に\n牛の乳しぼりいくべ？", 1, 1, 1, 1264, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103301, 1, "お昼だ～腹が減っては\n笑いはできぬ～！", 2, 2, 1, 1265, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103301, 1, "今日もよく働いたべ！\nさ、今からは\nお笑いの練習だ～！", 3, 3, 1, 1266, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103301, 1, "舞台に上がる前に\n身なりは整えねぇと\nだめだべ～！", 1, 0, 1, 1267, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103301, 1, "ナイスなイスに\n座りたい、\nなんちゃって！", 2, 0, 1, 1268, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103301, 1, "めんこいのは、\nスベっても許される\nからずるいべな～？", 3, 0, 1, 1269, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103301, 1, "必要以上に\n癒されちまうべや～", 4, 0, 1, 1270, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103301, 1, "ぽかぽかだ…\nウケた会場みてぇだ", 5, 0, 1, 1271, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103301, 1, "お笑い芸人は、\n体が資本だべさ～！", 6, 0, 1, 1272, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103301, 1, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～", 7, 0, 1, 1273, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103301, 1, "ネタ作りのヒントが\n載ってねぇか探すべ", 8, 0, 1, 1274, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103302, 1, "さぁ今日も張り切って\n漫才の練習するべさ！", 1, 0, 1, 1276, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103302, 1, "この部屋でも、商品\n売っていいんだべか？", 2, 0, 1, 1277, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103302, 1, "眼鏡を頭の上に乗せて\n「メガネメガネ～」\nって言う練習した方が\nいいべか？", 3, 0, 1, 1278, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103302, 1, "漫才を持ち時間に\n収めるには、何度も\n練習するしかねぇべ", 4, 0, 1, 1279, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103302, 1, "エリザベスも\n連れてきてぇ～", 5, 0, 1, 1280, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103302, 1, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～", 101, 0, 8, 1281, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103302, 1, "あんちゃんに\n採れたての食材を\n食わしてやりてぇ～", 102, 0, 8, 1282, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103302, 1, "押すなよ\n絶対押すなよ\n…なんて\n言ってないべさ？", 1, 0, 1, 1283, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103302, 1, "あんちゃん、百連\nツッコミ特訓だ～！", 2, 0, 1, 1284, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103302, 1, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！", 3, 0, 1, 1285, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103302, 1, "「なんでだべ～！」\nって言いながら\nツッコむといいべ！", 4, 0, 1, 1286, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103302, 1, "あはは～あんちゃんの\nツッコミはやっぱり\n気持ちいいなぁ～", 101, 0, 8, 1287, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103302, 1, "将来的には夫婦漫才\nってのも…あ、ひ\nひとりごとだべさ～！", 102, 0, 8, 1288, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103302, 1, "おはよう！\n今日もいい\nネタ作り日和だ！", 1, 1, 1, 1289, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103302, 1, "お昼だな\nご飯を食べたら、\nネタの練習するべ！", 2, 2, 1, 1290, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103302, 1, "もうこんな時間だべ\nネタ作りは\n明日にするべ…", 3, 3, 1, 1291, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103302, 1, "うーん…衣装、\nずれてねぇべか？", 1, 0, 1, 1292, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103302, 1, "ナイスなイスに\n座りたい\nなんちゃって！", 2, 0, 1, 1293, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103302, 1, "はう～\nなまらめんこいべや～", 3, 0, 1, 1294, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103302, 1, "必要以上に\n癒されちまうべや～", 4, 0, 1, 1295, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103302, 1, "笑いをとることを\n忘れるほど\nあったけぇべ～", 5, 0, 1, 1296, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103302, 1, "たくさん運動して\n動きでも笑いがとれる\nように頑張るべ～！", 6, 0, 1, 1297, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103302, 1, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～", 7, 0, 1, 1298, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103302, 1, "幅広く教養を\n身につけて\nいいネタ作るべ！", 8, 0, 1, 1299, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103401, 1, "通りにいる\nカップル…爆発\nすればいいのに…", 1, 0, 1, 1301, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103401, 1, "う～ん、仕事なんて\nしないで、\n飲んでたいな～", 2, 0, 1, 1302, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103401, 1, "今日は一日、\n外に出ない！\nもう決めたわ！", 3, 0, 1, 1303, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103401, 1, "お腹空いた…\nけど、外に出るのは\n面倒だなぁ", 4, 0, 1, 1304, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103401, 1, "うぅ…頭がガンガン\nする…昨日、\n飲み過ぎたかな…", 5, 0, 1, 1305, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103401, 1, "デートとか…\n誘っても嫌がられ\nないかな…？", 101, 0, 4, 1306, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103401, 1, "もしかして運命の\n相手だったり…\nするのかな", 102, 0, 8, 1307, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103401, 1, "やっぱり一杯目が\n一番おいしいわ～！\n…え、見てた！？", 1, 0, 1, 1308, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103401, 1, "頼れるお姉さんだって\nところ、もっと見せて\nあげるからね", 2, 0, 1, 1309, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103401, 1, "ねえ、一緒に買い物に\n行かない？　その…\nの、飲み物とか…", 3, 0, 1, 1310, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103401, 1, "ひっく、うふふ…\nもっとお姉さんと\nお話しようよ～", 4, 0, 1, 1311, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103401, 1, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…", 101, 0, 4, 1312, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103401, 1, "いつか…\nキミと一緒に飲めたら\n楽しいだろうな", 102, 0, 8, 1313, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103401, 1, "おはよう…朝は\nちょっとつらいな…\n低血圧なのよ…", 1, 1, 1, 1314, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103401, 1, "おっひるー♪\nなに食べよっかな～♪", 2, 2, 1, 1315, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103401, 1, "ふわあ…\n眠る前にちょっと\n一杯…冗談よ", 3, 3, 1, 1316, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103401, 1, "うう…\n髪がボサボサ…\nちゃんとしなきゃ…", 1, 0, 1, 1317, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103401, 1, "自然体でゴロゴロ\nするのって\n幸せだなぁ…", 2, 0, 1, 1318, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103401, 1, "こういうの男の子に\nプレゼントして\nもらいたいな…", 3, 0, 1, 1319, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103401, 1, "うわあ、\nこれ、私の好きな\n香りだー！", 4, 0, 1, 1320, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103401, 1, "ぽっかぽか…\nあとは熱い一杯が\nあれば…！", 5, 0, 1, 1321, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103401, 1, "家でゴロゴロして\n付いちゃったお肉を\n落とさないと…！", 6, 0, 1, 1322, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103401, 1, "もぐもぐ…\nおーいしー！\n…太らないよね？", 7, 0, 1, 1323, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103401, 1, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ", 8, 0, 1, 1324, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103402, 1, "仕事終わりの一杯の\nために、今日も\n頑張らないと", 1, 0, 1, 1326, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103402, 1, "今日は\nのんびり\n過ごそうかな～", 2, 0, 1, 1327, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103402, 1, "あ…\nお皿、洗って\nなかった…", 3, 0, 1, 1328, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103402, 1, "もう少し、大人の\n魅力が出せるように\nなりたいな", 4, 0, 1, 1329, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103402, 1, "素敵な彼氏…\n欲しいなぁ…", 5, 0, 1, 1330, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103402, 1, "彼と私\n釣り合うかな…\n大丈夫よね？", 101, 0, 8, 1331, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103402, 1, "今後のために\nちょっとは花嫁修業\nしてみようかな…？", 102, 0, 8, 1332, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103402, 1, "何事も\nお姉さんと一緒に\n楽しく頑張りましょ？", 1, 0, 1, 1333, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103402, 1, "ね、ねえ！\nキミは年上と年下\nどっちが好きかな？", 2, 0, 1, 1334, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103402, 1, "もう…\n構ってくれないと\n泣いちゃうぞ～？", 3, 0, 1, 1335, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103402, 1, "お姉さんが～\n可愛がって\nあげようか～？", 4, 0, 1, 1336, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103402, 1, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…", 101, 0, 8, 1337, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103402, 1, "お姉さんとチュー\nする？　ふふふ～\n冗談で～す！", 102, 0, 8, 1338, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103402, 1, "さーて、今日も\nいっちょやるぞー！", 1, 1, 1, 1339, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103402, 1, "後でのんびり\nストレッチでも\nしようっと♪", 2, 2, 1, 1340, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103402, 1, "一日よく働いた～！\n自分にごほうび\nあげたいなー♪", 3, 3, 1, 1341, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103402, 1, "仕事の前に、髪とか\n整えないと…", 1, 0, 1, 1342, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103402, 1, "自然体でゴロゴロ\nするのって\n幸せだなぁ…", 2, 0, 1, 1343, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103402, 1, "もう大人だし…\nあんまり可愛いのは\n似合わないかな…？", 3, 0, 1, 1344, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103402, 1, "うわあ\nこれ、私の好きな\n香りだー！", 4, 0, 1, 1345, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103402, 1, "あったかーい…\nだ、だらけ\nちゃいそう…", 5, 0, 1, 1346, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103402, 1, "地道な努力が\nリア充への\n第一歩！", 6, 0, 1, 1347, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103402, 1, "おーいしっ♪\nこのために\n生きてるわねえ", 7, 0, 1, 1348, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103402, 1, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ", 8, 0, 1, 1349, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103801, 1, "大切な人に\n本をすすめるのって\nけっこう難しいなぁ", 1, 0, 1, 1351, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103801, 1, "き、きらーん☆\nお姉ちゃんに\n似てるかな…？", 2, 0, 1, 1352, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103801, 1, "そろそろ好きな\n作家さんの新刊が\n出るから楽しみ…", 3, 0, 1, 1353, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103801, 1, "ちょっと、休憩…\n本でも読もうかな", 4, 0, 1, 1354, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103801, 1, "もっとたくさんの本に\n囲まれて生活したいな", 5, 0, 1, 1355, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103801, 1, "むかしむかしある所に\nシオリと\nあの人が…", 101, 0, 4, 1356, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103801, 1, "この本の主人公、\nあの人に\n似ているかも…\nかっこいいな", 102, 0, 8, 1357, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103801, 1, "ケホケホ…\nコンコン…\nゴホッゴホッ…\nへ、平気ですよ？", 1, 0, 1, 1358, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103801, 1, "あ…お姉ちゃんと\n約束があったんだ…", 2, 0, 1, 1359, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103801, 1, "しおりを探している？\nあ、本に挟む方ですね\nドキドキしました…", 3, 0, 1, 1360, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103801, 1, "最近調子がいいのは\n牧場の牛乳やチーズの\nおかげでしょうか？", 4, 0, 1, 1361, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103801, 1, "読みたい本も\nあなたと行きたい所も\n増えるばっかりです", 101, 0, 4, 1362, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103801, 1, "私の病気、\n悪化してるのかも！？\nすごく胸がドキドキ\nするんです…", 102, 0, 8, 1363, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103801, 1, "えっと、朝食の前には\nこの薬を飲んで…\n食後にはこの薬を…", 1, 1, 1, 1364, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103801, 1, "ふぅ…午前中\n動いたので体力が…\nすこし休みますね", 2, 2, 1, 1365, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103801, 1, "早く寝ないと\n体に良くないですよね\nでも気になっている\n本があって…", 3, 3, 1, 1366, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103801, 1, "寝てばかりなので、\n髪が乱れてないかな\n…？", 1, 0, 1, 1367, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103801, 1, "少し休もう\n…やっぱり\nたくさん休もう", 2, 0, 1, 1368, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103801, 1, "かわいいものが\n枕元にあると\n安心できるんですよ", 3, 0, 1, 1369, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103801, 1, "いい香り…\n病は気からと言うので\n雰囲気も大事ですね", 4, 0, 1, 1370, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103801, 1, "こうして温まって\nいると、病気も\n治る気がします", 5, 0, 1, 1371, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103801, 1, "運動…イメージ\nトレーニングだけじゃ\nだめでしょうか…", 6, 0, 1, 1372, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103801, 1, "少食ですけど\nこれならたくさん\n食べられそうです", 7, 0, 1, 1373, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103801, 1, "続きが気になって\n読むのを止める\nタイミングが…", 8, 0, 1, 1374, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103802, 1, "しっかり休まないと\n早く回復するように", 1, 0, 1, 1376, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103802, 1, "き、きらーん☆\nお姉ちゃんに\n似てるかな…？　", 2, 0, 1, 1377, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103802, 1, "病室よりも…\nずっとこの部屋に\nいたいですね…　", 3, 0, 1, 1378, 0);
