INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103802, 1, "むかしむかしある所に\nシオリと\nあの人が…", 101, 0, 8, 1381, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103802, 1, "あの人の\nためにも早く\n病気を治さないと…", 102, 0, 8, 1382, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103802, 1, "ケホケホ…\nコンコン…\nゴホッゴホッ…\nへ、平気ですよ？", 1, 0, 1, 1383, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103802, 1, "新記録です！\n一週間連続、\n平熱を保ちました！", 2, 0, 1, 1384, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103802, 1, "あ…お薬の時間…\n苦いけど我慢しなきゃ　", 3, 0, 1, 1385, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103802, 1, "最近調子がいいのは\n牧場の牛乳やチーズの\nおかげでしょうか？", 4, 0, 1, 1386, 0);
