INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103802, 1, "私の病気\n悪化してるのかも！？\nすごく胸がドキドキ\nするんです…", 102, 0, 8, 1388, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103802, 1, "えっと、朝食の前には\nこの薬を飲んで…\n食後にはこの薬を…", 1, 1, 1, 1389, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103802, 1, "…お昼の薬？\nあ、飲み忘れてました\nありがとうございます", 2, 2, 1, 1390, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103802, 1, "この薬を飲むと\n眠くなっちゃうんです\nでもまだ、あなたと\nお話したいし～", 3, 3, 1, 1391, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103802, 1, "寝てばかりなので、\n髪が乱れてないかな\n…？　", 1, 0, 1, 1392, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103802, 1, "少し休もう\n…やっぱり\nたくさん休もう", 2, 0, 1, 1393, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103802, 1, "この子がいれば\n病室に一人でも\n寂しくないですね", 3, 0, 1, 1394, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103802, 1, "いろんな効能が？\n健康になる香りも\nあるのでしょうか？", 4, 0, 1, 1395, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103802, 1, "こうして温まって\nいると、病気も\n治る気がします", 5, 0, 1, 1396, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103802, 1, "私も、少しは\n体力をつけないと\nいけませんよね　", 6, 0, 1, 1397, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103802, 1, "いっぱい食べて\n健康な体にならないと", 7, 0, 1, 1398, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103802, 1, "続きが気になって\n読むのを止める\nタイミングが…", 8, 0, 1, 1399, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104001, 1, "友達ができたら、\n山の上でお弁当…\n夢が広がります…", 1, 0, 1, 1401, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104001, 1, "あ、忘れずに\nお花に水を\nあげなきゃ…", 2, 0, 1, 1402, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104001, 1, "お菓子を作って、\nみなさんに振る舞って\n友達作戦！　とか…", 3, 0, 1, 1403, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104001, 1, "そろそろ新しい\n草人形を作って\n友達作りの特訓を…", 4, 0, 1, 1404, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104001, 1, "植物のお世話って、\nぼっちにとっては、\n救い…なんです…", 5, 0, 1, 1405, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104001, 1, "ＢＢ団の活動日…\nまだかな…\nうふふふっ", 101, 0, 4, 1406, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104001, 1, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…", 102, 0, 8, 1407, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104001, 1, "あ、こんにちは！\n今日もＢＢ団で\nお世話になります！", 1, 0, 1, 1408, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104001, 1, "あの…私の友達作りの\n練習方法、間違って\nませんよね…？", 2, 0, 1, 1409, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104001, 1, "そうだ、今度、\n森に一緒に木の苗を\n植えに行きませんか？", 3, 0, 1, 1410, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104001, 1, "人とお話するのって、\n私、大好きです！\n友達ふやすぞ～！", 4, 0, 1, 1411, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104001, 1, "あなたと話してると、\nまるで私がぼっちじゃ\nないみたいです！", 101, 0, 4, 1412, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104001, 1, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです", 102, 0, 8, 1413, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104001, 1, "おはようございます！\n今日もＢＢ団、\nがんばるぞー！", 1, 1, 1, 1414, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104001, 1, "なんだか、お腹が\n空いてきました。\nお昼ごはんー♪", 2, 2, 1, 1415, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104001, 1, "もう眠いかもです…\n明日は友達、\nできるといいなあ…", 3, 3, 1, 1416, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104001, 1, "私がもっと\n可愛ければ、\n友達できるかなあ？", 1, 0, 1, 1417, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104001, 1, "ふわあ…のんびり\nしすぎないように\n気を付けなきゃです", 2, 0, 1, 1418, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104001, 1, "かわいいなあ。\nこの子とも\n友達になりたい…", 3, 0, 1, 1419, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104001, 1, "いい香りです…\n精神統一して\n友達のイメトレ…", 4, 0, 1, 1420, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104001, 1, "灯りを見てると、\n心がなごみます～", 5, 0, 1, 1421, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104001, 1, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！", 6, 0, 1, 1422, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104001, 1, "おいしいです…\n私に友達がいたら、\n分けてあげたいな", 7, 0, 1, 1423, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104001, 1, "友達作りの\n参考になりそうなこと\n書いてないかな～？", 8, 0, 1, 1424, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104002, 1, "友達作りは、焦らず\n慌てず、じっくりと…", 1, 0, 1, 1426, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104002, 1, "ひとつ屋根の下\nって感じ…\nいいですねえ…", 2, 0, 1, 1427, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104002, 1, "煮汁、煮汁が\n噴きこぼれて\nいませんか～", 3, 0, 1, 1428, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104002, 1, "今度、みなさんと\nピクニックとか…\nいってみたいな", 4, 0, 1, 1429, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104002, 1, "ぼっちでもいい\nたくましく育てば\n…なんて", 5, 0, 1, 1430, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104002, 1, "最近、毎日あの人の\nこと考えちゃいます\nどうしてだろう…", 101, 0, 8, 1431, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104002, 1, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…", 102, 0, 8, 1432, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104002, 1, "あ！　ＢＢ団の活動\nですか？　嬉しくて\n飛び跳ねちゃいます！", 1, 0, 1, 1433, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104002, 1, "あなたとのお話で友達\n作りの練習になるなら\n一石二鳥です！", 2, 0, 1, 1434, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104002, 1, "私少しは人と話しても\nテンパらなくなって\nきましたかね…？", 3, 0, 1, 1435, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104002, 1, "あの、友達作りの\n新しい作戦\n考えてみたんですっ！", 4, 0, 1, 1436, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104002, 1, "あなたとお話する時の\nこの気持ち、私の宝物\nなんです…えへへ", 101, 0, 8, 1437, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104002, 1, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです", 102, 0, 8, 1438, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104002, 1, "朝ですね！\nこんないい朝は\n友達作りのチャンス！", 1, 1, 1, 1439, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104002, 1, "あの…そろそろお腹\nすきませんか？\nお昼ごはんは…", 2, 2, 1, 1440, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104002, 1, "もう眠いかもです…\n明日は友達\nできるといいなあ…", 3, 3, 1, 1441, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104002, 1, "私…見た目\nそこまで変じゃ\nないですよね…？", 1, 0, 1, 1442, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104002, 1, "ほわあ～…\nふかふかって\nいいものですね～", 2, 0, 1, 1443, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104002, 1, "この子みたいな\nあいくるしさを\n目指したいです！", 3, 0, 1, 1444, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104002, 1, "灯りを見てると\n心がなごみます～", 4, 0, 1, 1445, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104002, 1, "ぬくぬくで、なんだか\n眠くなっちゃいますね", 5, 0, 1, 1446, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104002, 1, "身体を鍛えて\nみなさんを\n守ります！", 6, 0, 1, 1447, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104002, 1, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！", 7, 0, 1, 1448, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104002, 1, "友達作りの\n参考になりそうなこと\n書いてないかな～？", 8, 0, 1, 1449, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104201, 1, "唱喚の歌は、みだりに\n歌っていいものでは\nないんです", 1, 0, 1, 1451, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104201, 1, "ここでアイドルの\n練習は、さすがに\n迷惑ですよね？", 2, 0, 1, 1452, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104201, 1, "練習してもあんまり\n疲れなくなった…\nアイドルのおかげかな", 3, 0, 1, 1453, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104201, 1, "人々の平和を守る旅に\n同行してくれる人を\n探していて…", 4, 0, 1, 1454, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104201, 1, "あとで曲を復習して\nおかないと…", 5, 0, 1, 1455, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104201, 1, "踊りを見られるのは\nまだ恥ずかしいけど\nあの人になら…", 101, 0, 4, 1456, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104201, 1, "浜辺で曲を書こうと\n思うんですけど…\n一緒に行きませんか？", 102, 0, 8, 1457, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104201, 1, "いつも応援ありがとう\nございます♪　これは\nあ、握手の練習です！", 1, 0, 1, 1458, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104201, 1, "え、もう練習の時間？\nごめんなさい\nすぐ準備します！", 2, 0, 1, 1459, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104201, 1, "守るべき人たちに\n笑顔でいてもらうのも\n大切なんですね", 3, 0, 1, 1460, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104201, 1, "歌える曲を増やそうと\n思って。どんな曲が\n聞きたいですか？", 4, 0, 1, 1461, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104201, 1, "～～♪　あれ？\n今私、歌ってました？\n気づきませんでした", 101, 0, 4, 1462, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104201, 1, "…歌って欲しい？\n喜んで♪\nどの歌にしましょう？", 102, 0, 8, 1463, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104201, 1, "おはようございます♪\n少し歌の練習を\nしてきますね", 1, 1, 1, 1464, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104201, 1, "もうお昼なんですか？\n練習に夢中で\n気づきませんでした", 2, 2, 1, 1465, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104201, 1, "明日の予定は…\nアイドルはやることが\n多いみたいで…", 3, 3, 1, 1466, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104201, 1, "見た目については\n前よりも気をつけてと\nノゾミに言われてます", 1, 0, 1, 1467, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104201, 1, "歌の調子をよくする\nためには休息も\n必要ですよね…", 2, 0, 1, 1468, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104201, 1, "この子たちを\n相手に練習して\nみようかな…♪", 3, 0, 1, 1469, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104201, 1, "…疲れてるのかな\n落ち着けているのが\n自分でもわかります", 4, 0, 1, 1470, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104201, 1, "ここにいると\n穏やかな旋律が\n浮かんできます…", 5, 0, 1, 1471, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104201, 1, "歌うためには\n体力づくりは\n欠かせません", 6, 0, 1, 1472, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104201, 1, "歌った後は\nお腹が空いて\nしまうんです", 7, 0, 1, 1473, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104201, 1, "音楽について\n理論も勉強\nしないと…", 8, 0, 1, 1474, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104202, 1, "歌うことの楽しさ\n分かってきました\nもっと歌いたいな…", 1, 0, 1, 1476, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104202, 1, "練習してもあんまり\n疲れなくなった…\nアイドルのおかげかな", 2, 0, 1, 1477, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104202, 1, "精霊も強くなったし\nもっとたくさんの人を\n守りたいです", 3, 0, 1, 1478, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104202, 1, "最近、よく頭の中に\n歌が浮かんできます\n不思議な感覚…", 4, 0, 1, 1479, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104202, 1, "楽譜を買いました\n思いついた歌を\n書き留めておくために", 5, 0, 1, 1480, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104202, 1, "浜辺で曲を書こうと\n思うんですけど…\n一緒に行きませんか？", 101, 0, 8, 1481, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104202, 1, "私の中にある\n私だけの歌…\n作ってみようかな？", 102, 0, 8, 1482, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104202, 1, "守るべき人たちに\n笑顔でいてもらうのも\n大切なんですね", 1, 0, 1, 1483, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104202, 1, "歌える曲を増やそうと\n思って。どんな曲が\n聞きたいですか？", 2, 0, 1, 1484, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104202, 1, "魔物退治ですか！？\n…違います？\n良かった…", 3, 0, 1, 1485, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104202, 1, "いつも応援ありがとう\nございます♪　これは\nあ、握手の練習です！", 4, 0, 1, 1486, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104202, 1, "…歌って欲しい？\n喜んで♪\nどの歌にしましょう？", 101, 0, 8, 1487, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104202, 1, "～～♪　あれ？\n今私、歌ってました？\n気づきませんでした", 102, 0, 8, 1488, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104202, 1, "呼吸を深く、大きく…\nこれでちゃんと\n目が覚めますよ", 1, 1, 1, 1489, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104202, 1, "少し休んだら\n午後の練習に\n行ってきますね", 2, 2, 1, 1490, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104202, 1, "曲を作っていたら\nもうこんな時間…\nおやすみなさい", 3, 3, 1, 1491, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104202, 1, "見た目については\n前よりも気をつけてと\nノゾミに言われてます", 1, 0, 1, 1492, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104202, 1, "あ～…♪\nよく体がほぐれます…\nいい歌が歌えそうです", 2, 0, 1, 1493, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104202, 1, "かわいいと思う\n気持ちを、歌にしても\nいいかも知れませんね", 3, 0, 1, 1494, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104202, 1, "…疲れてるのかな\n落ち着けているのが\n自分でもわかります", 4, 0, 1, 1495, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104202, 1, "ここにいると\n穏やかな旋律が\n浮かんできます…", 5, 0, 1, 1496, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104202, 1, "もっと体力をつけて\n最高のライブを\nファンに届けます！", 6, 0, 1, 1497, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104202, 1, "これでも一応\n体形には気をつけて\n食べてるんですよ？", 7, 0, 1, 1498, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104202, 1, "読書は歌に\n新しい風を\n吹き込んでくれます", 8, 0, 1, 1499, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104301, 1, "店の新メニューでも\n考えるかな", 1, 0, 1, 1501, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104301, 1, "あたしの店から、\n出前を届けようか？", 2, 0, 1, 1502, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104301, 1, "パトロール、\nパトロール…\nここは異常なしかな？", 3, 0, 1, 1503, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104301, 1, "腹へったら食う！\n悩んだら寝る！\nそれが一番だろ？", 4, 0, 1, 1504, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104301, 1, "のんびりし過ぎるのも\n…性に合わねぇな", 5, 0, 1, 1505, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104301, 1, "そろそろあいつ、\n腹すかしてるん\nじゃないかな？", 101, 0, 4, 1506, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104301, 1, "今日の献立は…\nあいつの好きなものに\nしてやるか", 102, 0, 8, 1507, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104301, 1, "強くなりたいなら、\nあたしがビシバシ\n鍛えてやるよ", 1, 0, 1, 1508, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104301, 1, "天気がいい日は\n外で運動したく\nならねぇか？", 2, 0, 1, 1509, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104301, 1, "腹減ったか？　なんか\n作ってやろうか？", 3, 0, 1, 1510, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104301, 1, "ん？　なんだよ、\n相談事か？\nなんでも言ってくれ", 4, 0, 1, 1511, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104301, 1, "お前が好きな飯って\nなんだ？\n今度、作ってやるよ", 101, 0, 4, 1512, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104301, 1, "お前って不思議だよな\n一緒にいるとなんだか\n…なんでもねーよ", 102, 0, 8, 1513, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104301, 1, "気持ちいい朝だな！\n体を動かしたい気分だ\n付き合ってくれるか", 1, 1, 1, 1514, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104301, 1, "昼飯もう食ったか？\nまだなら、あたしが\nなんか作ってやるよ", 2, 2, 1, 1515, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104301, 1, "日中たっぷり動いたら\nそのぶん夜はしっかり\n休んどけよな", 3, 3, 1, 1516, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104301, 1, "た、たまにはその…\nかわいい格好とか\nしても…", 1, 0, 1, 1517, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104301, 1, "ふわぁ、\n一休みすっかなぁ～", 2, 0, 1, 1518, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104301, 1, "うっ！　か、かわいい\nじゃねぇか", 3, 0, 1, 1519, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104301, 1, "へー　いい匂いだな\nあいつもこういうの\n好きなのかな…", 4, 0, 1, 1520, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104301, 1, "ふわぁ〜！\n温かいと、つい\nウトウトしちまうな", 5, 0, 1, 1521, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104301, 1, "オラオラオラーーー！\n燃えるぜーーー！", 6, 0, 1, 1522, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104301, 1, "いい味してるぜ！\nうちの店でも\n出してみたいな", 7, 0, 1, 1523, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104301, 1, "文字を読んでると\nどうしても眠く\nなっちまうんだよなぁ", 8, 0, 1, 1524, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104302, 1, "パトロール終了～\n本日も異常なしっと！", 1, 0, 1, 1526, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104302, 1, "あたしの店から\n出前を届けようか？", 2, 0, 1, 1527, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104302, 1, "あいつ、早く\n帰ってこないかな", 3, 0, 1, 1528, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104302, 1, "あいつ\n腹すかせてないと\nいいんだけど…", 4, 0, 1, 1529, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104302, 1, "のんびりし過ぎるのも\n…性に合わねぇな", 5, 0, 1, 1530, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104302, 1, "あいつの事を思うと\n胸があったかくなる？\nなんだコレ…？", 101, 0, 8, 1531, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104302, 1, "今日の献立は…\nあいつの好きなものに\nしてやるか", 102, 0, 8, 1532, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104302, 1, "ん？　なんだよ\n相談事か？\nなんでも言ってくれ", 1, 0, 1, 1533, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104302, 1, "天気がいい日は\n外で運動したく\nならねぇか？", 2, 0, 1, 1534, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104302, 1, "腹減ったか？　なんか\n作ってやろうか？", 3, 0, 1, 1535, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104302, 1, "腹へったら食う！\n悩んだら寝る！\nそれが一番だろ？", 4, 0, 1, 1536, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104302, 1, "あのさ…できれば\nもっとそばに…いや\nなんでもねぇ忘れろ！", 101, 0, 8, 1537, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104302, 1, "お前って不思議だよな\n一緒にいるとなんだか\n…なんでもねーよ", 102, 0, 8, 1538, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104302, 1, "気持ちいい朝だな！\n体を動かしたい気分だ\n付き合ってくれるか？", 1, 1, 1, 1539, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104302, 1, "昼飯もう食ったか？\nまだなら、あたしが\nなんか作ってやるよ", 2, 2, 1, 1540, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104302, 1, "あんま夜更しすんなよ\n明日に響いたら\n大変だろ？", 3, 3, 1, 1541, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104302, 1, "た、たまにはその…\nかわいい格好とか\nしても…", 1, 0, 1, 1542, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104302, 1, "ふぅ～♪\n疲れがとれるな～", 2, 0, 1, 1543, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104302, 1, "うっ！　か、かわいい\nじゃねぇか", 3, 0, 1, 1544, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104302, 1, "へー　いい匂いだな\nあいつもこういうの\n好きなのかな…", 4, 0, 1, 1545, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104302, 1, "ふわぁ～！\n温かいと、つい\nウトウトしちまうな", 5, 0, 1, 1546, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104302, 1, "オラオラオラーーー！\n燃えるぜーーー！", 6, 0, 1, 1547, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104302, 1, "いい味してるぜ！\nうちの店でも\n出してみたいな", 7, 0, 1, 1548, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104302, 1, "料理だけじゃなく\n経営術とかも\n学んどかねぇと…", 8, 0, 1, 1549, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104501, 1, "最近なにも\nしてくれません…\n焦らしてるんですね", 1, 0, 1, 1551, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104501, 1, "ぐふふ、じゅるり…\nい、いけない、また\nいつの間にかよだれが", 2, 0, 1, 1552, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104501, 1, "好きな武器といえば\n剣より槍より\nやっぱり鞭…ぐふふ", 3, 0, 1, 1553, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104501, 1, "「無数の触手が\nクウカの柔肌に\n食い込み」…ぐふふ", 4, 0, 1, 1554, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104501, 1, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！", 5, 0, 1, 1555, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104501, 1, "ドＳさんを登場\nさせるだけで、妄想に\n無限の広がりが！", 101, 0, 4, 1556, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104501, 1, "あっ…\nジロジロ見てたの\n気づいちゃいました？", 102, 0, 8, 1557, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104501, 1, "視線を感じると\n思ったら…やはり\nドＳさんでしたか", 1, 0, 1, 1558, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104501, 1, "今日は\nどんなヒドイことを\nしてくれるんですか？\nぐふふふ", 2, 0, 1, 1559, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104501, 1, "いつの間にそこに！？\nまさかクウカを監視\nしていたのですか！？", 3, 0, 1, 1560, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104501, 1, "クウカに触るつもり\nですか？　触るつもり\nなのですねっ！？", 4, 0, 1, 1561, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104501, 1, "クウカにナニをする\nつもりですか…\n期待してしまいます！", 101, 0, 4, 1562, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104501, 1, "ドＳさんの視界に入る\nだけでドキドキが…\nこれが恋！？", 102, 0, 8, 1563, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104501, 1, "おはようございます。\n朝からお盛んですね？\nぐふふ…", 1, 1, 1, 1564, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104501, 1, "こんなにも日の高い\nうちからドＳさんは…\nじゅるり", 2, 2, 1, 1565, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104501, 1, "夜はこれから、\nですよ…\nぐふ、ぐふふふ…", 3, 3, 1, 1566, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104501, 1, "キレイにおめかし\nしたのに力ずくで…\nぐふふふ", 1, 0, 1, 1567, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104501, 1, "ソファの背にクウカを\n押し付けて…ぐふふ\nいいですね\nソファドン", 2, 0, 1, 1568, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104501, 1, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ", 3, 0, 1, 1569, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104501, 1, "香りを吸ったクウカの\n頬は紅潮し目はうるみ\nもはや抗う力も…！", 4, 0, 1, 1570, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104501, 1, "ああ、身体が\n火照って…\nぐふ、ぐふふ…", 5, 0, 1, 1571, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104501, 1, "はぁ、はぁ、こうして\nクウカの体力を\n奪ってから…ぐふふ", 6, 0, 1, 1572, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104501, 1, "食欲が満たされたら\nつ、次はやはり…！\nそうなのですね！？", 7, 0, 1, 1573, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104501, 1, "この本の下に、\nそういう本を\nカムフラージュ？", 8, 0, 1, 1574, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104502, 1, "「ドＳさんと踊り子\n乱舞の一夜」…\n次の日記はこれで…", 1, 0, 1, 1576, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104502, 1, "最近なにも\nしてくれません…\n焦らしてるんですね", 2, 0, 1, 1577, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104502, 1, "も、妄想が\n足りません…\n禁断症状がっ…！", 3, 0, 1, 1578, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104502, 1, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！", 4, 0, 1, 1579, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104502, 1, "次はどんなドＳを\nされるのか、想像する\nだけで…ぐふふふ", 5, 0, 1, 1580, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104502, 1, "じっとしてると\nドＳさんのことばかり\n考えてしまいますね", 101, 0, 8, 1581, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104502, 1, "妄想が止まらなくて\nクウカどうにかなって\nしまいそうです！", 102, 0, 8, 1582, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104502, 1, "ドＳさんといると体が\n火照って…クウカは\nいやらしい子です！", 1, 0, 1, 1583, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104502, 1, "あっ…\nジロジロ見てたの\n気づいちゃいました？", 2, 0, 1, 1584, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104502, 1, "ドＳさんのおかげで\n新しい扉が開けそう\nです…じゅるり", 3, 0, 1, 1585, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104502, 1, "いやー！　ドＳさんに\n襲われ…襲って\nくれないんですか？", 4, 0, 1, 1586, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104502, 1, "クウカはドＳさんに\n身も心も堕とされて\nしまったのですね", 101, 0, 8, 1587, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104502, 1, "ドＳさんがお望みなら\nクウカは\nどんなことだって！", 102, 0, 8, 1588, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104502, 1, "朝だからこそ\nできる妄想が\nあります…ぐふふ", 1, 1, 1, 1589, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104502, 1, "こんな日の高いうち\nからドＳさんは…\nじゅるり", 2, 2, 1, 1590, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104502, 1, "今夜は一晩中…\nなんですね？\nぐふふ…", 3, 3, 1, 1591, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104502, 1, "意外と難しいんですよ\n正しい亀甲縛りと\nいうものは…", 1, 0, 1, 1592, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104502, 1, "くつろいで油断\nしているスキに何を\nされるのでしょう！？", 2, 0, 1, 1593, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104502, 1, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ", 3, 0, 1, 1594, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104502, 1, "これは禁断の香り…\nああっ一段階上の\n妄想が開花するうぅ！", 4, 0, 1, 1595, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104502, 1, "ああっ、暖気がまるで\nロウソクのように\nクウカを攻めますっ！", 5, 0, 1, 1596, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104502, 1, "激しい攻めを受けきる\nためには、鍛えないと\nいけないのですね", 6, 0, 1, 1597, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104502, 1, "こんなにいっぱい\n入りません…\nお腹にっ！", 7, 0, 1, 1598, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104502, 1, "どんな本も\nよく見ると\nいやらしいです…！", 8, 0, 1, 1599, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104601, 1, "この前遊んだ猫、\nモッフモフ\nだったにゃあ…♪", 1, 0, 1, 1601, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104601, 1, "ほっぺやお腹が、\n肉球ぐらいぷにぷに…\nこれはまずいにゃ…", 2, 0, 1, 1602, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104601, 1, "大きな仕事が\n決まったにゃ！\n腕が鳴るにゃ～♪", 3, 0, 1, 1603, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104601, 1, "おやつのたい焼き、\nつぶあんとこしあん、\nどっちに\nしようかにゃ？", 4, 0, 1, 1604, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104601, 1, "うにゃー…\nもうちょっとお昼寝\nしたかったにゃ…\nむにゃむにゃ…", 5, 0, 1, 1605, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104601, 1, "キミに呼ばれると、\n猫まっしぐらな気分に\nなっちゃうにゃ♪", 101, 0, 4, 1606, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104601, 1, "キミのおかげで\nギルドの仕事も\n大成功だにゃ♪\nありがとにゃ♪", 102, 0, 8, 1607, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104601, 1, "うずうず…その指、\nじゃれついても\nいいかにゃ？", 1, 0, 1, 1608, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104601, 1, "にゃ？　あたしに何か\nご入り用かにゃ？\nお安くしとくにゃ～♪", 2, 0, 1, 1609, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104601, 1, "うーん、なんだか\n退屈にゃ～\n遊んで、遊んで～？", 3, 0, 1, 1610, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104601, 1, "素早い動きのコツ？\nタダじゃ教えて\nあげられないにゃ～♪", 4, 0, 1, 1611, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104601, 1, "そんなにあたしを\nなでたいにゃ？\nキミならなでさせて\nあげてもいいかにゃー", 101, 0, 4, 1612, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104601, 1, "ふにゃあ、ねえねえ、\n一緒にゴロゴロ\nしようにゃあ？", 102, 0, 8, 1613, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104601, 1, "いい朝にゃ！\n顔を洗ってくるにゃ！", 1, 1, 1, 1614, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104601, 1, "むーん、そろそろ\nお昼…猫たちにも\n猫缶を…", 2, 2, 1, 1615, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104601, 1, "ふわあ…眠いにゃ…\n猫は布団で\n丸くなるにゃ", 3, 3, 1, 1616, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104601, 1, "客商売では、\n外見のチェックも\n欠かせないのにゃ！", 1, 0, 1, 1617, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104601, 1, "うにゃ～！\nお昼寝に\nぴったりにゃ", 2, 0, 1, 1618, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104601, 1, "これ…量産したら\n子どもにバカ売れ\nしそうだにゃ！", 3, 0, 1, 1619, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104601, 1, "この灯り、まるで\n猫みたいな\n癒し効果にゃ！", 4, 0, 1, 1620, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104601, 1, "ふにゃあ、\nあったかいと猫は丸く\nなっちゃうのにゃあ…", 5, 0, 1, 1621, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104601, 1, "本当は外で\n運動したいん\nだけどにゃ", 6, 0, 1, 1622, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104601, 1, "むーん、\n美味しいにゃー！\nたい焼きに\n匹敵するにゃ！", 7, 0, 1, 1623, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104601, 1, "ふむふむ…猫の\nしつけ方は、と…", 8, 0, 1, 1624, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104602, 1, "かわいいネコの置物を\nゲットしたにゃ！\nどこに飾ろうか\nにゃ～♪", 1, 0, 1, 1626, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104602, 1, "仕事の交渉がうまく\n行ったにゃ～♪\nいい気分だにゃ～♪", 2, 0, 1, 1627, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104602, 1, "ねこじゃらしには\n食いついちゃうにゃ\nびしっ！　びしっ！", 3, 0, 1, 1628, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104602, 1, "ほっぺやお腹が、肉球\nぐらいぷにぷに…\nこれはまずいにゃ…", 4, 0, 1, 1629, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104602, 1, "さっきのたいやき\nおいしかったにゃ♪\nごろごろごろ…", 5, 0, 1, 1630, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104602, 1, "手作りのたい焼き\nあいつは食べて\nくれるかにゃ…？", 101, 0, 8, 1631, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104602, 1, "あいつには、いつか\nたっぷり恩返しが\nしたいにゃ…", 102, 0, 8, 1632, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104602, 1, "にゃーに？　あたしの\nことがそんなに気に\nなるのかにゃ？", 1, 0, 1, 1633, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104602, 1, "あたしのこと…\nしっかり捕まえ\nといてにゃ？", 2, 0, 1, 1634, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104602, 1, "あたしの力が\n必要かにゃ？　猫の手\n貸してあげるにゃ♪", 3, 0, 1, 1635, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104602, 1, "たいやきとクレープを\n合わせれば…\n新しい商売のニオイが\nするにゃ…！", 4, 0, 1, 1636, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104602, 1, "ふにゃあ、ねえねえ\n一緒にゴロゴロ\nしようにゃあ？", 101, 0, 8, 1637, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104602, 1, "キミと一緒にいると\n楽しくなってつい\nじゃれちゃうにゃ！", 102, 0, 8, 1638, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104602, 1, "いい朝にゃ！\n顔を洗ってくるにゃ！", 1, 1, 1, 1639, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104602, 1, "そろそろおっひる～\nにゃーんか\n食べたい…", 2, 2, 1, 1640, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104602, 1, "うーん、眠くなって\nきたにゃ。あたしは\n夜行性じゃないのにゃ", 3, 3, 1, 1641, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104602, 1, "にゃにゃ…\n髪が乱れてるにゃ", 1, 0, 1, 1642, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104602, 1, "ふっかふかにゃ～\n猫がねころん\nじゃうにゃ", 2, 0, 1, 1643, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104602, 1, "これ、猫と匹敵する\nぐらいかわいいにゃ！", 3, 0, 1, 1644, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104602, 1, "猫でもこわくない\n優しい灯りにゃ！", 4, 0, 1, 1645, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104602, 1, "ぽっかぽか\nしあわせな気分にゃ～", 5, 0, 1, 1646, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104602, 1, "雨の日でも運動\nできるのは、濡れ\nなくて便利だにゃ！", 6, 0, 1, 1647, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104602, 1, "美味しいにゃ～！\nこれ、朝昼晩\n食べたいにゃ！", 7, 0, 1, 1648, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104602, 1, "本を読んで\n猫の気持ちを\n勉強するのにゃ！", 8, 0, 1, 1649, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104801, 1, "内職手伝える人\nいないかしら？", 1, 0, 1, 1651, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104801, 1, "次の依頼は、\nこういう計画で…\nうん、完璧ね！", 2, 0, 1, 1652, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104801, 1, "次の特売セールに\n向けて、しっかり\n準備しないと！", 3, 0, 1, 1653, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104801, 1, "配達の後に売り子\n次に採取のバイト…\nうん、効率的ね", 4, 0, 1, 1654, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104801, 1, "一日に受ける\nバイトの数、もう少し\n増やしたいわね", 5, 0, 1, 1655, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104801, 1, "師匠として\n頼りにされたいし\nもっと頑張らなきゃ！", 101, 0, 4, 1656, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104801, 1, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね", 102, 0, 8, 1657, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104801, 1, "あら、\nちょうどよかった\n今から一緒に\nバイトでもどう？", 1, 0, 1, 1658, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104801, 1, "相談事かしら？\nそれくらいなら\n無料でいいわよ", 2, 0, 1, 1659, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104801, 1, "背後の気配なら\nすぐ分かるわ\n試してみる？", 3, 0, 1, 1660, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104801, 1, "道具やお金の管理は\n大丈夫？　手伝うから\nいつでも言って", 4, 0, 1, 1661, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104801, 1, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね", 101, 0, 4, 1662, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104801, 1, "ねえ、あの…\n私はキミにとって…\nいや、何でもないわ", 102, 0, 8, 1663, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104801, 1, "おはよう！\n今日も朝から\nバイトよ！", 1, 1, 1, 1664, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104801, 1, "お昼時ね。\n少し休憩したら、\nまた働くわよ！", 2, 2, 1, 1665, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104801, 1, "もうこんな時間！\n明日もバイトで\n朝早いのよ…", 3, 3, 1, 1666, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104801, 1, "接客業の依頼が\nある時は、外見も\nキレイにしないとね", 1, 0, 1, 1667, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104801, 1, "はぁ～バイトで\n疲れたから癒される～", 2, 0, 1, 1668, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104801, 1, "これを内職で\n作ったら\n儲かるかしら？", 3, 0, 1, 1669, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104801, 1, "リフレッシュできたわ\nこれでまた、バリバリ\n働けるわね！", 4, 0, 1, 1670, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104801, 1, "風邪を引いたら効率が\n落ちちゃうから、\n暖めないと！", 5, 0, 1, 1671, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104801, 1, "働き続けるには\n健康第一！　それには\n運動が一番よ", 6, 0, 1, 1672, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104801, 1, "もぐもぐ…\n腹が減っては\n戦ができぬ、よ！", 7, 0, 1, 1673, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104801, 1, "日々絶えず学んで、\n生活に活かすのよ！", 8, 0, 1, 1674, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104802, 1, "家計簿をつけなきゃ！\n覚えている時に\nやっておかないとね", 1, 0, 1, 1676, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104802, 1, "次の依頼は\nこういう計画で…\nうん、完璧ね！", 2, 0, 1, 1677, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104802, 1, "ギルドの経費\nあとちょっとだけ\n削れそうね…", 3, 0, 1, 1678, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104802, 1, "少しでも時給のいい\nバイトがしたいけど\nなかなか\n出会えないのよ", 4, 0, 1, 1679, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104802, 1, "今度のバイトも\nなかなか大変そう\nね…腕が鳴るわ", 5, 0, 1, 1680, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104802, 1, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね", 101, 0, 8, 1681, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104802, 1, "私、どうしたんだろ？\n仕事中もずっと\n彼のこと考えてる…", 102, 0, 8, 1682, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104802, 1, "この間はヘルプに\n来てくれてありがとう\n頼んで正解だったわ", 1, 0, 1, 1683, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104802, 1, "見つけた！　ちょうど\n頼みたいことが\nあるんだけど、いい？", 2, 0, 1, 1684, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104802, 1, "今から\n休憩するところなの\n一緒にどう？", 3, 0, 1, 1685, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104802, 1, "ん？　どうしたの？\n私のことが\n気になるの？", 4, 0, 1, 1686, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104802, 1, "…たまには甘えても\nいい？　私だって\n弱る時はあるのよ", 101, 0, 8, 1687, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104802, 1, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね", 102, 0, 8, 1688, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104802, 1, "おはよう！\nいい朝ね。一緒に\nランニングでもいく？", 1, 1, 1, 1689, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104802, 1, "お昼時ね\n少し休憩したら\nまた働くわよ！", 2, 2, 1, 1690, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104802, 1, "もうこんな時間！\n明日もバイトで\n朝早いのよ…", 3, 3, 1, 1691, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104802, 1, "女の子らしく…\nどうも忘れちゃう\nのよね", 1, 0, 1, 1692, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104802, 1, "ここで少し\n休んだら\nまたバイトよ！", 2, 0, 1, 1693, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104802, 1, "これを内職で\n作ったら\n儲かるかしら？", 3, 0, 1, 1694, 0);
