INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105002, 1, "頭撫でないで！\nお子様みたいに\nあつかわないでって\n言ってるのに～！", 3, 0, 1, 1785, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105002, 1, "壁ドンの練習…\nあれ？　壁ドンって\n男がするんだっけ？", 4, 0, 1, 1786, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105002, 1, "ねーねー！\nアタシ、かわいい？\nかわいいわよね？", 101, 0, 8, 1787, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105002, 1, "これから出かけるの？\nしっかりアタシを\nエスコートしてね！", 102, 0, 8, 1788, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105002, 1, "レディの朝は\n忙しいのよ", 1, 1, 1, 1789, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105002, 1, "お腹すいたー\nダイエット\nやめちゃおうかなー", 2, 2, 1, 1790, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105002, 1, "エイジングケアを\nしないとねー", 3, 3, 1, 1791, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105002, 1, "身だしなみチェック\nオッケー！　これで\nいつアイツが来ても\n大丈夫ね！", 1, 0, 1, 1792, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105002, 1, "あ！　これ雑誌で\n見たことあるわ～\nだからきっと\nオシャレなのよ！", 2, 0, 1, 1793, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105002, 1, "あたしがもっと\nオシャレにかわいく\nしてあげようか？", 3, 0, 1, 1794, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105002, 1, "オシャレ～♪\n大人なレディって\n感じよね♪", 4, 0, 1, 1795, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105002, 1, "あったかーい♪\nここで宿題\nしようかしら？", 5, 0, 1, 1796, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105002, 1, "運動したら成長\nしないかしら？\n背とか胸とか…", 6, 0, 1, 1797, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105002, 1, "レディは、食べ物にも\n気を使うのよ", 7, 0, 1, 1798, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105002, 1, "おもしろーい！\n…でも絵本って\n子供っぽいかしら？", 8, 0, 1, 1799, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105101, 1, "魔法と科学\nどっちも極めて\nみせるわ", 1, 0, 1, 1801, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105101, 1, "仕事も片付いたし\n部屋で\n実験実験…♪", 2, 0, 1, 1802, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105101, 1, "たまった治療代の\n請求書を\nまとめないと…", 3, 0, 1, 1803, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105101, 1, "新薬が出来たし\nすぐに試したいけど…\n実験台がいないわ", 4, 0, 1, 1804, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105101, 1, "部屋にこもりすぎると\n曜日の感覚が\nなくなるわね", 5, 0, 1, 1805, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105101, 1, "あの子の記憶を\n取り戻せる薬…\n作れないものかしら", 101, 0, 4, 1806, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105101, 1, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら", 102, 0, 8, 1807, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105101, 1, "あら、ヒマなら\n薬草の整理を\n手伝って", 1, 0, 1, 1808, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105101, 1, "もしかして実験に\n付き合ってくれるの？\nえ？　違う？", 2, 0, 1, 1809, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105101, 1, "具合が悪い？\nなら診てあげる\nまずは触診から…", 3, 0, 1, 1810, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105101, 1, "植物に興味あるの？\nなら教えてあげるわ\n色々とね…", 4, 0, 1, 1811, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105101, 1, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？", 101, 0, 4, 1812, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105101, 1, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも", 102, 0, 8, 1813, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105101, 1, "お昼までに調合用の\n花を集めたいの\n手伝ってくれる？", 1, 1, 1, 1814, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105101, 1, "あら、もうお昼？\nそろそろ一息\n入れましょうか", 2, 2, 1, 1815, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105101, 1, "夜も更けて来たわね\n私は朝まで実験だけど\n見学していく？", 3, 3, 1, 1816, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105101, 1, "医者たるもの\nいつだって\n清潔に…♪", 1, 0, 1, 1817, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105101, 1, "いいわねこれ\n仮眠の時に\n使わせてもらうわ", 2, 0, 1, 1818, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105101, 1, "ぬいぐるみなら\n動物も…", 3, 0, 1, 1819, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105101, 1, "いい香り…\n仕事場にも\n置いてみようかしら", 4, 0, 1, 1820, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105101, 1, "ここにいると\nついウトウト\nしちゃうわ…", 5, 0, 1, 1821, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105101, 1, "座ってる時間が多いと\n運動不足になるし\nたまにはいいわね", 6, 0, 1, 1822, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105101, 1, "研究していると\nつい食べるのを\n忘れちゃうのよね", 7, 0, 1, 1823, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105101, 1, "実験もいいけど\nいつか成果を\n本にしないと…", 8, 0, 1, 1824, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105102, 1, "徹夜はツラいけど\n集中すると時間の\n感覚がなくなって…", 1, 0, 1, 1826, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105102, 1, "痛みに悶える顔って\nどうしてあんなに\n興奮するのかしら？", 2, 0, 1, 1827, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105102, 1, "彼に罵られたら\nどんな気分かしら\n意外と快感かも？", 3, 0, 1, 1828, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105102, 1, "毒を薄めると\n薬になるのって\n今でも不思議だわ", 4, 0, 1, 1829, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105102, 1, "綺麗なバラには\nトゲがあるけど\n魅力の一つよね", 5, 0, 1, 1830, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105102, 1, "カレをさりげなく\n惚れさせる方法を\n研究するわよ…♪", 101, 0, 8, 1831, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105102, 1, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら", 102, 0, 8, 1832, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105102, 1, "構って欲しいの？\nじゃあ、君の体を\n調べさせて？", 1, 0, 1, 1833, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105102, 1, "実験に付き合って\n大丈夫よ、試すのは\nただの栄養剤だから", 2, 0, 1, 1834, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105102, 1, "欲しい薬が\nあったら言ってね\n調合してみるから", 3, 0, 1, 1835, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105102, 1, "おしおきされたいの？\nキミにだったら\nいつでもしてあげる", 4, 0, 1, 1836, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105102, 1, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？", 101, 0, 8, 1837, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105102, 1, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも", 102, 0, 8, 1838, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105102, 1, "朝しか取れない\n植物があるの\n採取しに行くわよ", 1, 1, 1, 1839, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105102, 1, "お昼時ってやけに\n眠たくなるわよね\n…調べたくなるわ", 2, 2, 1, 1840, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105102, 1, "今日くらいは\nゆっくり眠りたいわ\n…キミと一緒にね", 3, 3, 1, 1841, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105102, 1, "身なりについて\n研究するのも\n楽しそうね", 1, 0, 1, 1842, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105102, 1, "休息に必要な道具は\n妥協せずに選ぶ！\nこれだけは譲れないわ", 2, 0, 1, 1843, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105102, 1, "たまにはこういう\n可愛らしいものに\n触れないとね♪", 3, 0, 1, 1844, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105102, 1, "いい香り…\n仕事場にも\n置いてみようかしら", 4, 0, 1, 1845, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105102, 1, "仕事がなかったら\nこのまますーっと\n眠りたいのに…", 5, 0, 1, 1846, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105102, 1, "運動しないと\n体型の維持が…", 6, 0, 1, 1847, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105102, 1, "適度な食事も\n大切よね", 7, 0, 1, 1848, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105102, 1, "まさか私が\n恋愛読本を読む\n日が来るなんて…", 8, 0, 1, 1849, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105201, 1, "ぺっぺっ！\n…い、いけないっ！", 1, 0, 1, 1851, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105201, 1, "いっけな～い！\nひづめで床に傷が！", 2, 0, 1, 1852, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105201, 1, "太っちゃったかも～！\n伐採でダイエット\nしなくっちゃ！", 3, 0, 1, 1853, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105201, 1, "きちんと生活すると\n毛づやが良くなる\nみたいなのよね～", 4, 0, 1, 1854, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105201, 1, "みんな、体重\nどれくらいなのかな？", 5, 0, 1, 1855, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105201, 1, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう", 101, 0, 4, 1856, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105201, 1, "いつか人間の女の子\nみたいなデート、\nしてみたいな～♪", 102, 0, 8, 1857, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105201, 1, "私ね「どこにでもいる\n普通の女の子」って\n言葉が憧れなんだ～♪", 1, 0, 1, 1858, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105201, 1, "チャームポイント？\n自分では分からない\nけど…まつげかなぁ？", 2, 0, 1, 1859, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105201, 1, "リマリマ～♪\nはっ！？\nわ、私ったら今何を？", 3, 0, 1, 1860, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105201, 1, "力仕事ならまかせて！", 4, 0, 1, 1861, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105201, 1, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪", 101, 0, 4, 1862, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105201, 1, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？", 102, 0, 8, 1863, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105201, 1, "今朝は寝癖がひどいわ\nしっぽの辺りとか～", 1, 1, 1, 1864, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105201, 1, "ランチは\nおしゃれなカフェで\nしてみたいな～♪", 2, 2, 1, 1865, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105201, 1, "お肌のケアに\nパックしながら\n寝ようかな～", 3, 3, 1, 1866, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105201, 1, "やだっ、\nまた寝ぐせついてる！\n直さないと！", 1, 0, 1, 1867, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105201, 1, "ミシッっていった！\n私、重すぎるかも！？", 2, 0, 1, 1868, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105201, 1, "あ！　あなたも\n美人さんね～♪", 3, 0, 1, 1869, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105201, 1, "はぁ、まったり…\n四足歩行しちゃい\nそうだわ…", 4, 0, 1, 1870, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105201, 1, "あったか～い♪\n…私、毛皮だから\n暑すぎるかも", 5, 0, 1, 1871, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105201, 1, "な、なんだか野生に\n帰っちゃいそう！", 6, 0, 1, 1872, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105201, 1, "ぱくぱく、もぐもぐ、\nぺっぺっ…\nやだ、はしたない！", 7, 0, 1, 1873, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105201, 1, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～", 8, 0, 1, 1874, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105202, 1, "素敵ね～、居るだけで\n女子力があがりそうな\nお部屋だわ～", 1, 0, 1, 1876, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105202, 1, "私、大丈夫かしら？\n他の子に怖がられたり\nしないかな…？", 2, 0, 1, 1877, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105202, 1, "動物苑…って\n嫌なギルド名ね…", 3, 0, 1, 1878, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105202, 1, "好き、嫌い、好き…\nあっ！　うっかりして\n花占いのお花\n食べちゃった！", 4, 0, 1, 1879, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105202, 1, "ここ、女の子らしい\nものでいっぱいね～！\nときめいちゃうわ～♪", 5, 0, 1, 1880, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105202, 1, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう", 101, 0, 8, 1881, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105202, 1, "あの人が\n来てくれるまで薪割り\nでもしてよっかな？", 102, 0, 8, 1882, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105202, 1, "一緒にお出かけ？\nうれしいけど…\n心の準備が！\nはわわわ！", 1, 0, 1, 1883, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105202, 1, "どうしたの？\n急に抱きついてきて…\n癒されたいの？", 2, 0, 1, 1884, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105202, 1, "ぺっぺっ！\n…い、いけないっ！", 3, 0, 1, 1885, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105202, 1, "メタモルアップルを\nここで食べたら、\nみんな驚くわよね", 4, 0, 1, 1886, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105202, 1, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？", 101, 0, 8, 1887, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105202, 1, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪", 102, 0, 8, 1888, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105202, 1, "今朝は寝癖がひどいわ\nしっぽの辺りとか～", 1, 1, 1, 1889, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105202, 1, "ダイエット中だから\nお昼は抜いてるの～", 2, 2, 1, 1890, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105202, 1, "お肌のケアに\nパックしながら\n寝ようかな～", 3, 3, 1, 1891, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105202, 1, "ちゃんとかわいく\nしてないと刈られ…\n狩られちゃうもんね！", 1, 0, 1, 1892, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105202, 1, "ミシッっていった！\n私、重すぎるかも！？", 2, 0, 1, 1893, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105202, 1, "あ！　あなたも\n美人さんね～♪", 3, 0, 1, 1894, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105202, 1, "いい香り～\n森の中にいるみたいに\nとっても落ち着く～", 4, 0, 1, 1895, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105202, 1, "素敵～♪　…\nきゃっ！　私の毛が\nたいへんなことに～！", 5, 0, 1, 1896, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105202, 1, "な、なんだか野生に\n帰っちゃいそう！", 6, 0, 1, 1897, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105202, 1, "何を食べるかより\n誰と食べるかが\n大事なのよね", 7, 0, 1, 1898, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105202, 1, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～", 8, 0, 1, 1899, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105301, 1, "ヴァイスフリューゲル\nを盛り上げる方法、\n共に語らおう！", 1, 0, 1, 1901, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105301, 1, "私は子供ではない…！\nが、お菓子は\nいただこう！", 2, 0, 1, 1902, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105301, 1, "部屋はきちんと\n片づけておかなくては\n部屋の汚れは\n心の汚れだ", 3, 0, 1, 1903, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105301, 1, "むむむ…明日\n目が覚めたら、身長が\n伸びていないものか…", 4, 0, 1, 1904, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105301, 1, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…", 5, 0, 1, 1905, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105301, 1, "あいつとの新たな\n鍛錬が\n待ち遠しいな…！", 101, 0, 4, 1906, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105301, 1, "最近、あいつと\n過ごしていて、毎日が\n充実しているな…", 102, 0, 8, 1907, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105301, 1, "軍人の本分は日々の\n研鑽にあるからな。\n今日も鍛錬に\n行かないか？", 1, 0, 1, 1908, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105301, 1, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…", 2, 0, 1, 1909, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105301, 1, "貴公にも、飴をくれて\nやろうか？\nあまいぞ～！\n美味しいぞ～！", 3, 0, 1, 1910, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105301, 1, "私ともっと高く、\n共に飛ぼう！", 4, 0, 1, 1911, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105301, 1, "貴公は、本当に頼りに\nなる男だな…いや、\nふとそう思っただけだ", 101, 0, 4, 1912, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105301, 1, "貴公と共にいられる\nことは私の喜びだ。\nこれからも\nよろしく頼むぞ", 102, 0, 8, 1913, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105301, 1, "うむ、いい朝だ。\n本国の夜明けを\n思い出してしまうな！", 1, 1, 1, 1914, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105301, 1, "そろそろ昼食に\nするか。腹が減っては\n戦はできないからな", 2, 2, 1, 1915, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105301, 1, "ふわああ…私は子供\nではないが…眠い時は\n眠いのだ…", 3, 3, 1, 1916, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105301, 1, "身だしなみは、心の\n在りようを映すから\n気をつけなくてはな", 1, 0, 1, 1917, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105301, 1, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…", 2, 0, 1, 1918, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105301, 1, "うむ…このかわいさ…\n本国に持って\n帰りたい…", 3, 0, 1, 1919, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105301, 1, "このともし火、戦いに\n明け暮れる我らの心を\n癒すものだな…", 4, 0, 1, 1920, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105301, 1, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！", 5, 0, 1, 1921, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105301, 1, "うおおおおおっ！\n鍛錬、鍛錬、\n鍛錬っ！！", 6, 0, 1, 1922, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105301, 1, "いつ戦いになっても\n困らぬように、\n腹八分目に\nしておくのだ！", 7, 0, 1, 1923, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105301, 1, "これを読んで、新たな\n戦術を編み出すぞっ！", 8, 0, 1, 1924, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105302, 1, "たまには掃除でも\nするか。部屋が\n綺麗になるのは\n喜ばしいことだ", 1, 0, 1, 1926, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105302, 1, "部屋の中では\n剣を振り回せないのが\n玉に瑕だな…", 2, 0, 1, 1927, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105302, 1, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…", 3, 0, 1, 1928, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105302, 1, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…", 4, 0, 1, 1929, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105302, 1, "家庭を守れない者が\n国を守れるはずが\nない。この部屋も\n私が守るぞ！", 5, 0, 1, 1930, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105302, 1, "あいつは今頃、何を\nしているのだろうな…\nふふっ", 101, 0, 8, 1931, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105302, 1, "あいつと会える日が\nこんなにも待ち遠しく\nなるとはな…", 102, 0, 8, 1932, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105302, 1, "軍人の本分は日々の\n研鑽にあるからな\n今日も鍛錬に\n行かないか？", 1, 0, 1, 1933, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105302, 1, "おお、今日も鍛錬の\n時間だったか！\nすまないな\nすぐ行く！", 2, 0, 1, 1934, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105302, 1, "私は子供ではない！\nが…たまには童心に\n帰りたい時もある…", 3, 0, 1, 1935, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105302, 1, "貴公にも、飴を\nくれてやろうか？\nあまいぞ～！\n美味しいぞ～！", 4, 0, 1, 1936, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105302, 1, "私と歩きたいのか…？\nそうだな。二人で\n散歩でも行こうか", 101, 0, 8, 1937, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105302, 1, "貴公と共にいられる\nことは私の喜びだ\nこれからも\nよろしく頼むぞ", 102, 0, 8, 1938, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105302, 1, "うむ、いい朝だ\n本国の夜明けを\n思い出してしまうな！", 1, 1, 1, 1939, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105302, 1, "そろそろ昼時だな\n鍛錬の手を緩めて\n昼食にするか", 2, 2, 1, 1940, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105302, 1, "寝る前に…あと少し\nだけ、鍛錬を…", 3, 3, 1, 1941, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105302, 1, "ヴァイス\nフリューゲルの一員と\nして恥ずかしくないよう\n身なりを整えなくては", 1, 0, 1, 1942, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105302, 1, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…", 2, 0, 1, 1943, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105302, 1, "うむ…このかわいさ…\n本国に持って\n帰りたい…", 3, 0, 1, 1944, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105302, 1, "このともし火\n戦いに明け暮れる\n我らの心を\n癒すものだな…", 4, 0, 1, 1945, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105302, 1, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！", 5, 0, 1, 1946, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105302, 1, "うおおおおおっ！\n鍛錬、鍛錬\n鍛錬っ！！", 6, 0, 1, 1947, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105302, 1, "いつ戦いになっても\n困らぬように\n腹八分目に\nしておくのだ！", 7, 0, 1, 1948, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105302, 1, "うむ…脳細胞が\n活性化してきたぞ\n読書は大事だな", 8, 0, 1, 1949, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105701, 1, "私たちの騎空艇\nどんなデザインに\nしよう…？", 1, 0, 1, 1951, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105701, 1, "団員募集の\nポスター\n作ろうっと♪", 2, 0, 1, 1952, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105701, 1, "父さん…\n今頃どこで\n何してるんだろ？", 3, 0, 1, 1953, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105701, 1, "騎空艇に乗って、\n誰もいったことがない\n場所を見つけたいな", 4, 0, 1, 1954, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105701, 1, "父さん以上の\n世界一の\n騎空士になりたい！", 5, 0, 1, 1955, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105701, 1, "仲間と行く空の旅…\n何度想像しても\nドキドキするなぁ", 101, 0, 4, 1956, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105701, 1, "父さん…\n私にもね、大切な\n仲間ができたよ", 102, 0, 8, 1957, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105701, 1, "仕事の依頼？\nすぐに支度\nしてくるね！", 1, 0, 1, 1958, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105701, 1, "ねえねえ！\n団員募集のポスター\n一緒に作ろうよ！", 2, 0, 1, 1959, 0);
