INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105902, 1, "のんびりした時間帯\nですね…ふぁ～…\nし、失礼致しました", 2, 2, 1, 2140, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105902, 1, "ふむ、都会は夜でも\nこんなにも、にぎやか\nなのですね", 3, 3, 1, 2141, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105902, 1, "主さまに会う前に\n身なりを整えなくては\nいけませんね", 1, 0, 1, 2142, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105902, 1, "このふかふかの優しさ\n主さまのようです", 2, 0, 1, 2143, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105902, 1, "かわいい♪\n主さまにそっくり♪", 3, 0, 1, 2144, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105902, 1, "この心地よさ…\n主さまのようです♪", 4, 0, 1, 2145, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105902, 1, "ここで洗濯物を\n乾かせるでしょうか　", 5, 0, 1, 2146, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105902, 1, "はぁっはぁっはぁっ\n…すべては\n主さまのため…", 6, 0, 1, 2147, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105902, 1, "都会の味…\nとてもおいしいです\n…もぐもぐもぐ", 7, 0, 1, 2148, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105902, 1, "都会のことを\n学ばないと…\nふむふむ", 8, 0, 1, 2149, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 190801, 1, "ふぁ～…あっ\n見られちゃい\nましたか？", 1, 0, 1, 2151, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 190801, 1, "ギルドハウスの\n使い心地は\nどうですか？", 2, 0, 1, 2152, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 190801, 1, "ご用があれば\nなんでも言って\nくださいね～♪", 3, 0, 1, 2153, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 190801, 1, "ときどきみなさんと\nクエストに行きたいな\nって思っちゃいます", 4, 0, 1, 2154, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 190801, 1, "おはようございます！\nお掃除の方、\nはじめていきますね", 1, 1, 1, 2155, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 190801, 1, "皆さん、お昼ごはんの\nご要望がありましたら\n用意致しますよ♪", 2, 2, 1, 2156, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 190801, 1, "ここは夜でも\nにぎやかですね", 3, 3, 1, 2157, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104701, 1, "…\n少しうとうと\nしてしまった", 1, 0, 1, 2158, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104701, 1, "室内だし兜を脱ぐか\n…いや\nやはりやめておこう", 2, 0, 1, 2159, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104701, 1, "部屋にいると\n鎧が蒸し暑くなるが\n我慢せねば…", 3, 0, 1, 2160, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104701, 1, "仕事が門番だからか\n部屋にいても\nじっとしてしまう…", 4, 0, 1, 2161, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104701, 1, "少年と会話したことを\n思いだすと\n自然と楽しくなるな", 5, 0, 1, 2162, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104701, 1, "少年のことを考えると\n顔がにやけてしまう", 101, 0, 4, 2163, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104701, 1, "少年は今頃\n平和に過ごせて\nいるかな…", 102, 0, 8, 2164, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104701, 1, "ん？　悪いが\n兜は脱がないぞ", 1, 0, 1, 2165, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104701, 1, "私と遊びたいのか？\nそう言われても\n何をしていいやら…", 2, 0, 1, 2166, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104701, 1, "鎧が重くないかって？\n大丈夫。けっこう\n鍛えているからな", 3, 0, 1, 2167, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104701, 1, "少年、寒くはないか？\nいや自分がこうなので\n人が薄着に見えてね", 4, 0, 1, 2168, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104701, 1, "君といると、兜の中が\nいつもより暑いんだ", 101, 0, 4, 2169, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104701, 1, "私としゃべりたい？\n物好きだな\nでも、嬉しいよ", 102, 0, 8, 2170, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104701, 1, "早いな少年\nえ？　私の仕事に\n合わせてくれたのか", 1, 1, 1, 2171, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104701, 1, "昼食の誘い？\n嬉しいけど\n人前では食べないんだ", 2, 2, 1, 2172, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104701, 1, "王宮騎士団の\n日報を書かなければ…\n『何事もなし』、と", 3, 3, 1, 2173, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104701, 1, "鎧を着ているから\n感触が分かりづらい\nのが残念だ…", 2, 0, 1, 2175, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104701, 1, "か、かわいいな…\n私には似合わないが\nぎゅ…ってしよう", 3, 0, 1, 2176, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104701, 1, "良い香りだ…\n普段はどうしても\n鉄くさくなるからな", 4, 0, 1, 2177, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104701, 1, "わかっていたけど\n鎧を着たまま暖炉に\nあたると暑い…", 5, 0, 1, 2178, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104701, 1, "兜を脱がずに食べる\nうん、段々コツが\n掴めてきた…", 7, 0, 1, 2180, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104701, 1, "兜があっても\n意外と本くらいは\n難なく読めるんだ", 8, 0, 1, 2181, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 102601, 1, "居心地よくて\nゴロゴロしちゃうよ～\n…いつも通りだね", 1, 0, 1, 2183, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 102601, 1, "ドングリの木\n近くに生えてない？", 2, 0, 1, 2184, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 102601, 1, "快適な寝床があれば\nそれだけで幸せだねぇ", 3, 0, 1, 2185, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 102601, 1, "あたしと一緒に\nだらけてくれる人が\nいそうだねぇ", 4, 0, 1, 2186, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 102601, 1, "ここなら牧場よりも\n速く配達が\n届きそうだね～", 5, 0, 1, 2187, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 102601, 1, "あんパン買ったし\nあいつにも\n分けてあげよう", 101, 0, 4, 2188, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 102601, 1, "あいつと一緒に\nだらけられたら\n最高に幸せだね", 102, 0, 8, 2189, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102601, 1, "ドングリでも\nつまみながら\n一緒にゲームしようよ", 1, 0, 1, 2190, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102601, 1, "横になりたいから\n枕になっておくれよ～\nあんたあったかいしさ", 2, 0, 1, 2191, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102601, 1, "ドングリあげるから\nあんパン買ってきて～", 3, 0, 1, 2192, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102601, 1, "う～\n疲れちゃったよ～\nおんぶして運んで～", 4, 0, 1, 2193, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102601, 1, "あんたといると\nリア充って\n感じがするよ", 101, 0, 4, 2194, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102601, 1, "あんたが一生\n世話してくれたら\n人生安泰なんだけどね", 102, 0, 8, 2195, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102601, 1, "ふわ～っ…\nおはよう…あたしは\nまだ眠いよ", 1, 1, 1, 2196, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102601, 1, "そろそろお昼寝でも\nしたい気分だねぇ", 2, 2, 1, 2197, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102601, 1, "徹夜でゲームするけど\nあんたもどうだい？\nあたしは強いよ", 3, 3, 1, 2198, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 102601, 1, "あ…だめだこれ。\n気持ち良くて\n落ちそう…", 2, 0, 1, 2200, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 102601, 1, "かわいいものに\n囲まれて寝るのも\nよさそうだねぇ…", 3, 0, 1, 2201, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 102601, 1, "心身ともに\nリラックスした状態で\nぐうたら出来そうだね", 4, 0, 1, 2202, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 102601, 1, "ふい～…\nごくらくごくらく…\n暖かいねぇ", 5, 0, 1, 2203, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 102601, 1, "食べてすぐ寝ると\n牛になるって\n本当なのかな？", 7, 0, 1, 2205, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 102601, 1, "おー！　この本なら\nあたしも居眠りせずに\n読めそうだよ", 8, 0, 1, 2206, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106301, 1, "ランドソル…\nおっきな街だなぁ…", 1, 0, 1, 2208, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106301, 1, "ロザリア…\nきっと、いるよね？", 2, 0, 1, 2209, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106301, 1, "最近、弓の練習\nサボっちゃってるな…", 3, 0, 1, 2210, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106301, 1, "今日の私は\n森番じゃなくて\n家番だよ！", 4, 0, 1, 2211, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106301, 1, "ちょっとだけ\n慣れてきたかも？", 5, 0, 1, 2212, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106301, 1, "いつか、\nあの人と一緒に…", 101, 0, 4, 2213, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106301, 1, "…なんか最近、\nあの人のこと\nばっかり…", 102, 0, 8, 2214, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106301, 1, "もしよかったら\n街に出かけない？", 1, 0, 1, 2215, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106301, 1, "この部屋、\n妖精さんも\n気に入るかも！", 2, 0, 1, 2216, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106301, 1, "今日は\nなににしよっか？", 3, 0, 1, 2217, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106301, 1, "今日はどこ行く？", 4, 0, 1, 2218, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106301, 1, "たまには二人で…\nな、なんちゃって！", 101, 0, 4, 2219, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106301, 1, "最近、\nずっと楽しいんだ！\nあなたのおかげかな", 102, 0, 8, 2220, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106301, 1, "おはよう！\n今日も一緒に\n頑張ろうね！", 1, 1, 1, 2221, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106301, 1, "お昼はどうする？\nあなたのオススメに\n連れて行って欲しいな", 2, 2, 1, 2222, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106301, 1, "ランドソルは\n夜でも賑やかだね！", 3, 3, 1, 2223, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106301, 1, "わぁっ！\nすっごく\nふわふわだよ！", 2, 0, 1, 2225, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106301, 1, "かわい～！\nぎゅってしたく\nなっちゃうかも！", 3, 0, 1, 2226, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106301, 1, "くんくん\nなんか…いい匂い？", 4, 0, 1, 2227, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106301, 1, "すっごいなー\nあったかいなー！", 5, 0, 1, 2228, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106301, 1, "わぁっ、おいしい！\nもっと食べたい\nかも…！", 7, 0, 1, 2230, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106301, 1, "も、文字は苦手…\nでも、頑張らないと\nだよね…！", 8, 0, 1, 2231, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103601, 1, "にんじんとピーマンを\n食べないでいい方法\nないかな…", 1, 0, 1, 2233, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103601, 1, "カップケーキが\n食べたいな…", 2, 0, 1, 2234, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103601, 1, "予習が終わりました！\nさて、次は復習を\nしないと…", 3, 0, 1, 2235, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103601, 1, "あの人の部屋にいる\n私って…もしかして\nふしんしゃなんじゃ？", 4, 0, 1, 2236, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103601, 1, "男の人は、みんな\nへんたいだと\n聞きました…", 5, 0, 1, 2237, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103601, 1, "あの人に、一緒に\n遊んでほしいけど…\nな、なんていえば？", 101, 0, 4, 2238, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103601, 1, "早く大人になりたいな\n…あの人と同じくらい", 102, 0, 8, 2239, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103601, 1, "ぼ、防犯魔石を\n鳴らしますよ！？", 1, 0, 1, 2240, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103601, 1, "へんたい！？　…\nあ、あなたでしたか～\nやっぱりへんたーい！", 2, 0, 1, 2241, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103601, 1, "これ宿題です\nやっておいて\nくださいね", 3, 0, 1, 2242, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103601, 1, "あ、遅刻ですよ\n待っていたんですから", 4, 0, 1, 2243, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103601, 1, "にらめっこですか？\nあ、あなたの前で変顔\nするのはちょっと…", 101, 0, 4, 2244, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103601, 1, "あの…何歳まで\n子供だと思いますか？", 102, 0, 8, 2245, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103601, 1, "顔を洗って、着替えて\n朝ごはんを食べて、\n歯を磨いて…", 1, 1, 1, 2246, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103601, 1, "て、鉄棒の練習！？　\nほ、他のことを\nしませんか？", 2, 2, 1, 2247, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103601, 1, "ね、眠くないです\n子供ではありません\nから…ふわぁ", 3, 3, 1, 2248, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103601, 1, "ひと休みしよっと\n………うん、\nひと休みおしまい♪", 2, 0, 1, 2250, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103601, 1, "つ、つぶらな瞳には\n弱いんです", 3, 0, 1, 2251, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103601, 1, "リラックスできますね\n集中力も\n上がりそうです", 4, 0, 1, 2252, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103601, 1, "あったかいと\n眠くなっちゃうな…\n勉強しなくちゃなのに", 5, 0, 1, 2253, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103601, 1, "ピーマンもこれくらい\nおいしければ\nいいのに…", 7, 0, 1, 2255, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103601, 1, "この本、むずかしい\n…もっと勉強\nがんばらなくちゃ", 8, 0, 1, 2256, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101501, 1, "今日はみんなに\nなにを読んで\nあげようかしら", 1, 0, 1, 2258, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101501, 1, "子どもたちと同じ\n目線で考えるのが\n大事よね", 2, 0, 1, 2259, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101501, 1, "お天気が良い日は\nみんなでお散歩に\n行きたいわね～", 3, 0, 1, 2260, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101501, 1, "鬼ごっこにかくれんぼ\n…みんなは\nどっちが好きかしら", 4, 0, 1, 2261, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101501, 1, "お遊戯会の配役、\n考え出すと\n大変だわ～", 5, 0, 1, 2262, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101501, 1, "彼は優しいから、\nきっと良い\nお父さんになるわね", 101, 0, 4, 2263, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101501, 1, "彼には私がいないと\n…っていうのは少し\n過保護すぎるかしら？", 102, 0, 8, 2264, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101501, 1, "保育園の行事を\n考えてるんだけど…\n良い案ないかしら？", 1, 0, 1, 2265, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101501, 1, "今度、子どもたちと\nおやつを作るから、\n食べに来てね", 2, 0, 1, 2266, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101501, 1, "よかったら一緒に\n絵本を作らない？", 3, 0, 1, 2267, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101501, 1, "あなたはいつも元気ね\n先生も見習わないと♪", 4, 0, 1, 2268, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101501, 1, "先生は\nずーっと、ずーっと、\nあなたの味方だからね", 101, 0, 4, 2269, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101501, 1, "あなたみたいな\nいい子をお世話できて\n先生は幸せものだわ", 102, 0, 8, 2270, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101501, 1, "おはよう～\nちゃんと朝ごはんは\n食べた？", 1, 1, 1, 2271, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101501, 1, "お昼寝の時間ね♪\nあなたも一緒に\n寝ましょうか", 2, 2, 1, 2272, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101501, 1, "すっかり真っ暗ね。\n今日も一日、\nよくできました♪", 3, 3, 1, 2273, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101501, 1, "ふかふかねぇ♪\n年甲斐もなく、\n飛び跳ねちゃいそう", 2, 0, 1, 2275, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101501, 1, "あらあら、かわいい！\n保育園のみんなに\n見せてあげたいわ", 3, 0, 1, 2276, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101501, 1, "いい香りね～\n保育園にも\n置こうかしら", 4, 0, 1, 2277, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101501, 1, "寒い日は、ここで\n子どもたちに絵本を\n読んであげましょ♪", 5, 0, 1, 2278, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101501, 1, "美味しい～♪\n遠足に持っていったら\nみんな喜びそう", 7, 0, 1, 2280, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101501, 1, "みんなが\n楽しめる読み聞かせの\n方法特集♪　素敵ねぇ", 8, 0, 1, 2281, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104401, 1, "世界の前に、\nこの部屋を征服\nしてやろう♪", 1, 0, 1, 2283, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104401, 1, "拠点とはやはり\n斯くあるべきじゃの", 2, 0, 1, 2284, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104401, 1, "眷属の物はわらわの物\n故に、この部屋は\nわらわの部屋じゃな！", 3, 0, 1, 2285, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104401, 1, "永く眠っている間に\n随分と世界は変わった\nようじゃな…", 4, 0, 1, 2286, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104401, 1, "うるさいのう～\nまったく、近頃の\n若いもんは…", 5, 0, 1, 2287, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104401, 1, "あやつが来てから\n動悸が治まらぬ…\n如何ような病で\nあろうかの…", 101, 0, 4, 2288, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104401, 1, "眷属よ、どこに行った\nのじゃ？\nわらわの世話を\n許可するのじゃ", 102, 0, 8, 2289, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104401, 1, "いずれ世界は\n我が手中に収まる\nふふふ、\n楽しみじゃのう", 1, 0, 1, 2290, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104401, 1, "眷属よ、わらわから\n離れてはならんぞ！\n子供に戻ってしまう\nではないか！", 2, 0, 1, 2291, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104401, 1, "トマトジュースが\n欲しいのじゃ！\n野菜ジュースでは\nダメだぞ！", 3, 0, 1, 2292, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104401, 1, "蚊にくわれたぞ！\nわらわの血を吸う\nとは～！", 4, 0, 1, 2293, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104401, 1, "こら、頭をなでるな\nお主はこの姿でも\n子供扱いするのか？", 101, 0, 4, 2294, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104401, 1, "お主が望むなら世界の\n半分をくれてやっても\n良いのじゃぞ？", 102, 0, 8, 2295, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104401, 1, "朝早くから感心じゃの\nさすがわらわの\n眷属じゃ", 1, 1, 1, 2296, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104401, 1, "太陽は苦手じゃ…\nこの部屋の中で\n過ごそうぞ", 2, 2, 1, 2297, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104401, 1, "夜も更けてきた！\nわらわ達の時間は\nこれからじゃ～♪", 3, 3, 1, 2298, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104401, 1, "よい座り心地じゃ\nわらわの玉座としよう", 2, 0, 1, 2300, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104401, 1, "可愛いではないか\nわらわの眷属に\nしてやろうかのう", 3, 0, 1, 2301, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104401, 1, "うむ、良い気持ちじゃ\nこれを作った者、\n褒めてつかわすぞ", 4, 0, 1, 2302, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104401, 1, "あたたかい…\n身も心も\n浄化されるようじゃ", 5, 0, 1, 2303, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104401, 1, "吸血鬼のわらわが、\nこのようなもの…\nうまい♪", 7, 0, 1, 2305, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 104401, 1, "このまま大人の姿を\n保つ方法…どこかに\n書いてないかのう", 8, 0, 1, 2306, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105401, 1, "いろんな人がいて\nいろんな服を着てて…\n目の保養になります♪", 1, 0, 1, 2308, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105401, 1, "レイ様への新作\nどんな服にしよう…\n方向性が絞れない～！", 2, 0, 1, 2309, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105401, 1, "忘れないうちに\n隠し糸のメンテナンス\nやっておこうっと！", 3, 0, 1, 2310, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105401, 1, "むっ！　あの人の\nスリーサイズは…\nほほう…なるほど♪", 4, 0, 1, 2311, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105401, 1, "ノゾミさんの\n衣装はああして\nチカさんのは…", 5, 0, 1, 2312, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105401, 1, "私がアイドル…\n…実感ないけど\n精一杯やってみよう", 101, 0, 4, 2313, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105401, 1, "まさか私が\nアイドルになるなんて\n思ってなかったです", 102, 0, 8, 2314, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105401, 1, "なんですかぁ？\n衣装作りで疲れて\n眠いんですぅ…", 1, 0, 1, 2315, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105401, 1, "あーっ！　また\n服がほつれてる…\nしょうがないですね", 2, 0, 1, 2316, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105401, 1, "はっ！　ちょうど今\n新しいデザインが\n思い浮かびました！", 3, 0, 1, 2317, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105401, 1, "暇そうですね？\n時間あるなら仕事\n手伝ってください！", 4, 0, 1, 2318, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105401, 1, "どうしました？\n新しい服が\n欲しくなりましたか？", 101, 0, 4, 2319, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105401, 1, "騎士さんのおかげで\nカルミナでも\n頑張れてますよ！", 102, 0, 8, 2320, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105401, 1, "おはようございます…\nなんで朝って毎日\nやってくるんでしょう", 1, 1, 1, 2321, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105401, 1, "お腹空きましたね\n騎士さんはちゃんと\n御飯食べましたか？", 2, 2, 1, 2322, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105401, 1, "夜の方が\n作業が捗るのは\n私だけですかね？", 3, 3, 1, 2323, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105401, 1, "このふかふか具合\nいい素材を\n使ってますね", 2, 0, 1, 2325, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105401, 1, "小さくて可愛い♪\nそうだ！　レイ様の\nぬいぐるみを…！", 3, 0, 1, 2326, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105401, 1, "こういう時くらい\n何も考えずに\nリラックスしないと♪", 4, 0, 1, 2327, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105401, 1, "炎を使った服って\nカッコ良いかも…\nでも材料がな～…", 5, 0, 1, 2328, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105401, 1, "何をするにも\n食事は大切！\n体力が資本です！", 7, 0, 1, 2330, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105401, 1, "私もいつか\nデザインを\nまとめて本に…", 8, 0, 1, 2331, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107501, 1, "またお魚を\n捕りにいこうかな♪", 1, 0, 1, 2333, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107501, 1, "そろそろみんなも\nお腹が空くころ\nじゃないですか～？", 2, 0, 1, 2334, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107501, 1, "ここなら、\n日焼けの心配は\nありませんね！", 3, 0, 1, 2335, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107501, 1, "お刺身に焼き貝…\nお腹が魚介類を\n求めてますっ！", 4, 0, 1, 2336, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107501, 1, "泳ぐのも食べるのも\n大得意です！", 5, 0, 1, 2337, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107501, 1, "あの人と二人で\n浜辺を散歩…\n素敵ですねぇ", 101, 0, 4, 2338, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107501, 1, "あの人に水着を\n褒めてもらえたら、\nすごく嬉しいなぁ", 102, 0, 8, 2339, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107501, 1, "たまには釣りじゃなく\n一緒に素潜りでも\n行きませんか？", 1, 0, 1, 2340, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107501, 1, "男の子に水着を\n見られるのって\n少し緊張しちゃいます", 2, 0, 1, 2341, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107501, 1, "ギルドハウスの\nみんなとも、\n海に行きたいですね！", 3, 0, 1, 2342, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107501, 1, "あなたの水着、\n私が選んであげます！", 4, 0, 1, 2343, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107501, 1, "泳ぎの練習なら\n付き合いますよ！\n手取り足取り♪", 101, 0, 4, 2344, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107501, 1, "水着姿が好きなら\nいくらでも\n着てあげますよぉ♪", 102, 0, 8, 2345, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107501, 1, "朝の海も\n幻想的で\nいいですよねぇ～！", 1, 1, 1, 2346, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107501, 1, "お昼はみんなで、\n海鮮バーベキューとか\nどうでしょう！", 2, 2, 1, 2347, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107501, 1, "夜の海は星が\nきれいですよ！\n見に行きましょう！", 3, 3, 1, 2348, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107501, 1, "泳ぎ疲れたので\nちょっと休憩です～", 2, 0, 1, 2350, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107501, 1, "あなたにも、\n可愛い水着を\n着せてあげたいです", 3, 0, 1, 2351, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107501, 1, "なんだか、\nリゾート気分です…", 4, 0, 1, 2352, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107501, 1, "ふい～、泳ぎで冷えた\n身体を暖めましょう～", 5, 0, 1, 2353, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107501, 1, "おいひ～です～\nこれ、海水浴にも\n持っていきたいですね", 7, 0, 1, 2355, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107501, 1, "南国のフルーツ図鑑\n…じゅるり…", 8, 0, 1, 2356, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107501, 1, "わ～♪　おいしそ…\nか、かわいいですね～", 10, 0, 1, 2358, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107501, 1, "これくらい大きな貝…\n食べたいな～\n食べたいな～～", 11, 0, 1, 2359, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107601, 1, "この格好で\n部屋をうろつくのは\n不思議な感じがします", 1, 0, 1, 2360, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107601, 1, "南国の果実を使って\n主さまにデザートを\nお作りしましょう", 2, 0, 1, 2361, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107601, 1, "せっかくの水着ですし\nこれから定期的に\n海水浴に行きたいです", 3, 0, 1, 2362, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107601, 1, "海の生き物も、\n森の生き物に負けず\n可愛かったです…", 4, 0, 1, 2363, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107601, 1, "クロールの練習なら、\n部屋でもできそうです", 5, 0, 1, 2364, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107601, 1, "主さまに、水着姿を\n褒めていただけた…\nえへへ♪", 101, 0, 4, 2365, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107601, 1, "水着で主さまの\nお世話をしたら、\n喜びますでしょうか…", 102, 0, 8, 2366, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107601, 1, "お疲れでしたら\nわたくしの膝を\nお使いくださいね", 1, 0, 1, 2367, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107601, 1, "主さま、海の家で\nつかうおこづかいを\n渡しておきますね", 2, 0, 1, 2368, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107601, 1, "海の美しさは、\n森とはちがう\n魅力がありました", 3, 0, 1, 2369, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107601, 1, "この髪飾りですか？\n造花ですから\n水の中も平気です", 4, 0, 1, 2370, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107601, 1, "水着でくっついて\n眠ると、いつもより\n密着して暖かいです", 101, 0, 4, 2371, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107601, 1, "はじめての海が、\n主さまと一緒で\n幸せです♪", 102, 0, 8, 2372, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107601, 1, "おはようございます。\n泳ぎに行く準備は\n万端です", 1, 1, 1, 2373, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107601, 1, "午後は泳ぐのをやめて\nスイカ割りなど\nいかがでしょう？", 2, 2, 1, 2374, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107601, 1, "夜も更けてきました…\n波の音を聞きながら\n少しお話しませんか？", 3, 3, 1, 2375, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107601, 1, "あぅ…この家具には、\n人をダメにする魔法が\nかけられています…", 2, 0, 1, 2377, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107601, 1, "ふふっ♪\nあなたも、海水浴に\n行きますか？", 3, 0, 1, 2378, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107601, 1, "この香り…\nきっと主さまも\n気に入ります", 4, 0, 1, 2379, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107601, 1, "これは暖かいです…\n後ほど主さまと一緒に\nあたりましょう", 5, 0, 1, 2380, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107601, 1, "泳いだ後の食事は\n一段と美味しく\n感じます", 7, 0, 1, 2382, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107601, 1, "深海魚の生態…\nこれは主さまにも\nお伝えしなくては…", 8, 0, 1, 2383, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107601, 1, "顔立ちが、主さまに\n似ております…\nかわいらしい♪", 10, 0, 1, 2385, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107601, 1, "ふかふかですね\n主さまのようです♪", 11, 0, 1, 2386, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107701, 1, "お掃除、お掃除\nふんふふ～ん☆\nあ、聞かれてました？", 1, 0, 1, 2387, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107701, 1, "日焼けには回復魔法が\n効きますよ。どうして\n逃げるんですか～！", 2, 0, 1, 2388, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107701, 1, "今日はドジを\nしませんように！", 3, 0, 1, 2389, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107701, 1, "接客業を極めれば\nメイドとして\n成長できるかも？", 4, 0, 1, 2390, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107701, 1, "水着って\n動きやすいんですけど\n人の目が気になります", 5, 0, 1, 2391, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107701, 1, "あの人の気持ちを知る\n魔法とかあれば…\nな、なんでもないです", 101, 0, 4, 2392, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107701, 1, "水着でご奉仕したら、\nあの人は喜ぶかな？", 102, 0, 8, 2393, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107701, 1, "暑いのでしたら、\n私が魔法で涼しく…\nえ？　いらない？", 1, 0, 1, 2394, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107701, 1, "何か御用ですか？\n特にないのでしたら、\nお話ししましょう♪", 2, 0, 1, 2395, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107701, 1, "水着姿でも\nメイドはメイドです\nご奉仕しますよ！", 3, 0, 1, 2396, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107701, 1, "ひゃっ！　あ、危うく\n転ぶところでした…\n…きゃうっ！", 4, 0, 1, 2397, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107701, 1, "どうかしましたか？\nあなたの御用なら、\n喜んでお受けしますよ", 101, 0, 4, 2398, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107701, 1, "水着姿をあなたに\n見られると、\nドキドキしちゃいます", 102, 0, 8, 2399, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107701, 1, "朝は暑くてやる気が\n出ない？　でしたら、\nえぇい、元気になぁれ", 1, 1, 1, 2400, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107701, 1, "お腹がぺこぺこなら、\nお昼は海の家で\n食べましょう♪", 2, 2, 1, 2401, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107701, 1, "夜の浜辺でお散歩も\nいいですね。たくさん\n転びそうですけど…", 3, 3, 1, 2402, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107701, 1, "休憩しながら、\nお掃除すれば\n一石二鳥です！", 2, 0, 1, 2404, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107701, 1, "きれいにすれば\nかわいさも倍増\nですよ～♪", 3, 0, 1, 2405, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107701, 1, "素敵な香りです～\n救護院に置けば、\nみんな喜ぶかも～", 4, 0, 1, 2406, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107701, 1, "あったかいですね…\nはっ！　いけません！\nメイドがサボるなんて", 5, 0, 1, 2407, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107701, 1, "料理のできない\nメイドなんて…\nはぐはぐ、おいしい♪", 7, 0, 1, 2409, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107701, 1, "水着姿で接客をすると\n売り上げが倍になる\n…本当ですか、これ", 8, 0, 1, 2410, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107701, 1, "えへへ♪\n南の島まで\n乗せてください！", 10, 0, 1, 2412, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107701, 1, "なんだか、\n人魚姫に\nなった気分です♪", 11, 0, 1, 2413, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107801, 1, "みんなで過ごす\n休日っていいわよね…\nずっと続かないかな", 1, 0, 1, 2414, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107801, 1, "海に浮輪でぷかぷか\n浮かぶのって\n癖になりそう", 2, 0, 1, 2415, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107801, 1, "ビーチにいると\n日焼けが心配だわ", 3, 0, 1, 2416, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107801, 1, "水着姿って楽よね。\n室内だとちょっと\n落ち着かないけど…", 4, 0, 1, 2417, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107801, 1, "お、泳ぎの練習なんて\nしないからね！", 5, 0, 1, 2418, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107801, 1, "楽しすぎて立場を\n忘れそう…あんたの\nせいなんだから！", 101, 0, 4, 2419, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107801, 1, "みんなで遊んで\n美味しいもの食べて\nほんっとに楽しいっ♪", 102, 0, 8, 2420, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107801, 1, "マジであっついわね…\nよっし！　海に入って\n涼みましょ！", 1, 0, 1, 2421, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107801, 1, "シャワー浴びたばかり\nなのに…また、遊び\nたくなってきたわ！", 2, 0, 1, 2422, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107801, 1, "遊び足りないって？\nしょうがないわね…\n付き合ってあげるわ♪", 3, 0, 1, 2423, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107801, 1, "一応伝えておくけど\n猫って水が苦手なの！\nわかった！？", 4, 0, 1, 2424, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107801, 1, "あたしと話したいの？\nふふ。\nまぁ、いいわよ。\nほら隣に座りなさい", 101, 0, 4, 2425, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107801, 1, "こんなに楽しいの\n初めてかも…\nずっと続けばいいのに", 102, 0, 8, 2426, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107801, 1, "は、早起きしたのは、\n楽しみで眠れなかった\nからじゃないわよ！", 1, 1, 1, 2427, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107801, 1, "昼食後のストレッチ\n終わり！　さあ、\nビーチが待ってるわ！", 2, 2, 1, 2428, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107801, 1, "夜の海に散歩に\n行こうかな～\nあんたも来なさいよ☆", 3, 3, 1, 2429, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107801, 1, "海ではしゃぎすぎて\n疲れたわ…ちょっと\n休もうかしら？", 2, 0, 1, 2431, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107801, 1, "こんな魔物がいたら\n操るのも楽しいかも", 3, 0, 1, 2432, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107801, 1, "にゃう～♪\nまるで南国にいる\n気分ね♪", 4, 0, 1, 2433, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107801, 1, "あったかぁい♪\n身体が冷えてきたから\n丁度いいわ！", 5, 0, 1, 2434, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107801, 1, "運動後のご飯って\nなんでこんなに\n美味しいんだろ♪", 7, 0, 1, 2436, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107801, 1, "どんなカナヅチでも、\n絶対必ず泳げる方法、\n書いてないかしら？", 8, 0, 1, 2437, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107801, 1, "あんたもあたしが\n操ってあげる！\nなんてね～♪", 10, 0, 1, 2439, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107801, 1, "リゾートリゾートっ\nのんびりしましょ♪", 11, 0, 1, 2440, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108001, 1, "ここなら思う存分\nデスクワークに\n励めるわね！", 1, 0, 1, 2441, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108001, 1, "武器のメンテナンスも\nしっかりしないと\n最高効率は望めないわ", 2, 0, 1, 2442, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108001, 1, "準備をしっかり整えて\nもっともっと\n稼がなくちゃね！", 3, 0, 1, 2443, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108001, 1, "少し休んだら\n次のバイトに\n行ってくるわね！", 4, 0, 1, 2444, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108001, 1, "この格好でここに\nいるのって\n気恥ずかしいわね…", 5, 0, 1, 2445, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108001, 1, "キミが望むなら\n今日のバイト\n休んでもいいわよ？", 101, 0, 4, 2446, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108001, 1, "キミだけね…\n私のことをここまで\n理解してくれるのは", 102, 0, 8, 2447, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108001, 1, "普段と違う格好\nだからって\nそんなに見ないでよ…", 1, 0, 1, 2448, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108001, 1, "キミを立派な\nバイト戦士にするのが\n師匠としての役目よね", 2, 0, 1, 2449, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108001, 1, "そろそろ次のバイトに\n行かないと…\nキミも一緒に来る？", 3, 0, 1, 2450, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108001, 1, "効率は大事だけど\nゆっくりするのも\n悪くないわね", 4, 0, 1, 2451, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108001, 1, "一緒の時間が楽しくて\nバイトのことを\n忘れちゃいそうね", 101, 0, 4, 2452, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108001, 1, "キミと一緒なら\nどんなミッションも\n楽勝ね！", 102, 0, 8, 2453, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108001, 1, "おはよう、いい朝ね！\n今日もめいっぱい\n稼ぐわよ！", 1, 1, 1, 2454, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108001, 1, "もうお昼だったのね…\n内職のバイトに\n集中しすぎちゃったわ", 2, 2, 1, 2455, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108001, 1, "スケジュールは、と…\nこういう管理の徹底が\n効率を生み出すのよ♪", 3, 3, 1, 2456, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108001, 1, "しっかり働いて休む…\n最高効率にまた一歩\n近づいたわね", 2, 0, 1, 2458, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108001, 1, "なごんじゃうわね…\nよぉし、次のバイトも\n頑張らないと！", 3, 0, 1, 2459, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108001, 1, "いい香りね…\n働く意欲が\n湧いてくるわ", 4, 0, 1, 2460, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108001, 1, "身体が冷えたから\n助かるわ…冷えは\n女の天敵だもの", 5, 0, 1, 2461, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108001, 1, "海のバイトは\n体力勝負よ\nエネルギーはしっかり\n摂取しないとね！", 7, 0, 1, 2463, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108001, 1, "座学も大切ね\nもっと知識を\n身につけないと…", 8, 0, 1, 2464, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108001, 1, "海の楽園みたいね…\n今だけは効率を\n忘れちゃいそう…", 10, 0, 1, 2466, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108001, 1, "落ち着くわ…\n海とひとつになった\n気分ね！", 11, 0, 1, 2467, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107901, 1, "ちょっと休憩にゃ～\n潮風は体に\n良くないにゃ～", 1, 0, 1, 2468, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107901, 1, "猫グッズで\n海の家を飾りたいにゃ", 2, 0, 1, 2469, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107901, 1, "海の幸は美味しいけど\n水はイヤだにゃ…", 3, 0, 1, 2470, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107901, 1, "開店にゃ！\nこのたい焼きなら\nきっと大繁盛にゃ！", 4, 0, 1, 2471, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107901, 1, "海と言えば水着にゃ\n似合ってるかにゃ？", 5, 0, 1, 2472, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107901, 1, "キミが頼むなら\n手伝ってあげても\nいいにゃ", 101, 0, 4, 2473, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107901, 1, "あたしの宝は\nここにあるにゃ～", 102, 0, 8, 2474, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107901, 1, "にゃ？\nそんなに見つめても\n何も出ないにゃ", 1, 0, 1, 2475, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107901, 1, "今からキミを\nあたしの相棒に\n任命するにゃ！", 2, 0, 1, 2476, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107901, 1, "これから\n開店の準備にゃ\nキミも手伝って\nくれるのかにゃ？", 3, 0, 1, 2477, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107901, 1, "海の家で出す\n新しいメニューを\n考え中なのにゃ～", 4, 0, 1, 2478, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107901, 1, "キミと一緒なら\n売上げナンバーワンに\nなれるにゃ！", 101, 0, 4, 2479, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107901, 1, "キミと一緒にいると\n楽しくて、もっと\nいたくなるにゃ", 102, 0, 8, 2480, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107901, 1, "おはようにゃ！\n今日も頑張って\nいくにゃー！", 1, 1, 1, 2481, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107901, 1, "お腹すいたにゃ～…\nお魚、ちょっとだけ、\nつまみ食いするにゃ", 2, 2, 1, 2482, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107901, 1, "明日の準備は万端にゃ\nいつでも\n出発できるにゃ", 3, 3, 1, 2483, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107901, 1, "このふかふか具合\nまるで子猫の\n毛並みのようにゃ～", 2, 0, 1, 2485, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107901, 1, "にゃにゃ～\nなんてかわいさにゃ～", 3, 0, 1, 2486, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107901, 1, "この匂い…\nもう少し香ばしいと\nいいかもしれないにゃ", 4, 0, 1, 2487, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107901, 1, "にゃ～…\nあったかくて\nぬくぬくだにゃ～", 5, 0, 1, 2488, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107901, 1, "働くにはまず体力にゃ\nしっかり食べて\nスタミナアップ\nだにゃ!", 7, 0, 1, 2490, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107901, 1, "ふむふむ…\nここにお宝が\n眠ってるんだにゃ", 8, 0, 1, 2491, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107901, 1, "シードレイクにゃ！\n本物は怖かったけど、\nぬいぐるみなら\nかわいいにゃ～", 10, 0, 1, 2493, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107901, 1, "貝なのにふかふかの\nクッションにゃ！\n屋台の前にも\n置いてみたいにゃあ", 11, 0, 1, 2494, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103701, 1, "人の家って、道場とは\n違った香りがするね", 1, 0, 1, 2495, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103701, 1, "ちょっと退屈だね…\nあの人をからかいに\n行こうかな♪", 2, 0, 1, 2496, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103701, 1, "普通の女の子みたいに\n遊びたいって思う\nこともあるんだよね", 3, 0, 1, 2497, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103701, 1, "剣の手入れを怠らない\nようにしないと。\nいざという時、困るし", 4, 0, 1, 2498, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103701, 1, "もっと\n上手く立ち回って\n出世したいな", 5, 0, 1, 2499, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103701, 1, "おかえりなさい！\nふふ、なんだか奥さん\nになった気分だよ♪", 101, 0, 4, 2500, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103701, 1, "恥ずかしくて、あなた\nを見られないよ…\n私らしくないなぁ…", 102, 0, 8, 2501, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103701, 1, "私、お話好きなんだ。\nどうせなら楽しい話を\nしたいな♪", 1, 0, 1, 2502, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103701, 1, "女の子っぽい子と、\n男勝りな子、あなたは\nどっちが好き？", 2, 0, 1, 2503, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103701, 1, "そろそろ見回りの\n時間かな。あなたも\n一緒に行く？", 3, 0, 1, 2504, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103701, 1, "構って欲しいのかな？\nそれじゃ一緒に\nランニングに行こうか", 4, 0, 1, 2505, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103701, 1, "二人で出かけるなら、\n手を繋がないとね。\nあはは、冗談冗談♪", 101, 0, 4, 2506, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103701, 1, "ねえ、あなた♪\nおや、顔が真っ赤だ！\n…ふふ、私も、かな？", 102, 0, 8, 2507, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103701, 1, "私は朝から鍛錬だよ。\n慣れてるけど大変だ。\nお互いに頑張ろうね", 1, 1, 1, 2508, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103701, 1, "鍛錬でお腹ぺこぺこ。\nお昼ごはんが\n楽しみだね", 2, 2, 1, 2509, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103701, 1, "今日も一日見回りと\n鍛錬を頑張ったよ。\nあなたもお疲れさま", 3, 3, 1, 2510, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103701, 1, "いざという時のため\nにも、休憩は大事だね", 2, 0, 1, 2512, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103701, 1, "こういうものには\n今まで\n縁がなかったけど。\nうん、可愛いね♪", 3, 0, 1, 2513, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103701, 1, "精神統一に通じる\nものがあるね。\nふぅ、癒されるなぁ", 4, 0, 1, 2514, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103701, 1, "あったかいなぁ。\nたまにはのんびり\nしようか", 5, 0, 1, 2515, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103701, 1, "鍛錬後はすごくお腹が\n空くんだよね。\nう～ん、おいしい♪", 7, 0, 1, 2517, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 103701, 1, "剣術書だけで強くなる\nには飛びぬけた才能と\n努力が必要だよね", 8, 0, 1, 2518, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100501, 1, "正義の刃で\n悪を挫く！\n…キマったッス♪", 1, 0, 1, 2520, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100501, 1, "ヒーローの\n秘密特訓を\n始めるッスよ～！", 2, 0, 1, 2521, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100501, 1, "正義の味方目指して\n今日も明日も\n全力ッスよ♪", 3, 0, 1, 2522, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100501, 1, "一日一善！\nヒーローだったら\n当たり前ッス♪", 4, 0, 1, 2523, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100501, 1, "どうッスか\nこのポーズ！\nイイ感じッスか？", 5, 0, 1, 2524, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100501, 1, "た、たまには\nよく頑張ったねって\n褒めてほしいッス…", 101, 0, 4, 2525, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100501, 1, "自分を信じてくれる\nあんたと一緒なら\n何でもやれるッス！", 102, 0, 8, 2526, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100501, 1, "おつかれッス♪\n冒険の準備は\nばっちりッスよ！", 1, 0, 1, 2527, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100501, 1, "どうしたんッスか？\n…はっ！　もしや敵が\n迫ってるんスかっ！？", 2, 0, 1, 2528, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100501, 1, "べ、別に\n頭を撫でてほしいとか\n思ってないッスよ？", 3, 0, 1, 2529, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100501, 1, "スーパーヒーロー\nトラタイガーは\n絶対無敵ッス！", 4, 0, 1, 2530, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100501, 1, "あんたと自分で\n正義の心を燃やして\n悪を一網打尽ッス！", 101, 0, 4, 2531, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100501, 1, "あんたと一緒だと\n幸せな気持ちに\nなるんッス…♪", 102, 0, 8, 2532, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100501, 1, "お、早いッスね～♪\n今日も一日一善\nよろしくッス！", 1, 1, 1, 2533, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100501, 1, "一休みしながら\nヒーローについて\n語り合うッスよ～！", 2, 2, 1, 2534, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100501, 1, "夜のパトロールで\n一緒に平和を\n守るッス！", 3, 3, 1, 2535, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100501, 1, "ふぅぅ…\n落ち着くッスね\n戦士の休息ッス♪", 2, 0, 1, 2537, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100501, 1, "くぅぅ、可愛いッス…\nヒーローが守るべき\n愛らしさッスね！", 3, 0, 1, 2538, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100501, 1, "なんスかこの香り？\nすぅぅ…ふぅぅ…\n力がみなぎるッス！", 4, 0, 1, 2539, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100501, 1, "優しい光ッスね…\nヒーローの優しさ\nそのものッス！", 5, 0, 1, 2540, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100501, 1, "あむあむあむ…\nんぅう、元気が溢れて\n止まらないッス！", 7, 0, 1, 2542, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100501, 1, "トラタイガーのお話は\n笑いあり涙ありで\n超おすすめッス！", 8, 0, 1, 2543, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108101, 1, "ハッピーハロウィン\nお父さん、お菓子は…\n食べられないね", 1, 0, 1, 2545, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108101, 1, "トリックオアトリート\nお菓子をくれないと\n不吉な占いをします", 2, 0, 1, 2546, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108101, 1, "ふふ、あなたの後ろで\n霊のみなさんが\n踊り始めました", 3, 0, 1, 2547, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108101, 1, "霊感がいつも以上に\n冴えているので\n占いの調子も良好です", 4, 0, 1, 2548, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108101, 1, "父はハロウィンが\n大好きだそうです\n大胆な格好をする子が\n多いので…", 5, 0, 1, 2549, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108101, 1, "あなたは霊たちにも\n大人気ですよね\n私も見てほしいな…", 101, 0, 4, 2550, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108101, 1, "ハロウィンの賑やかさ\n今は好きです…\nあなたといられるので", 102, 0, 8, 2551, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108101, 1, "霊のみなさんとの\nダンスパーティー…\n楽しみです", 1, 0, 1, 2552, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108101, 1, "お父さん、元気だね\nえ？　ここは色んな\n女の子に会えて幸せ？", 2, 0, 1, 2553, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108101, 1, "ハロウィンのお菓子\n食べすぎちゃったな…\nでも、美味しかった…", 3, 0, 1, 2554, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108101, 1, "あなたの女難の相…\n悪化していますね\nお祓いが必要かも", 4, 0, 1, 2555, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108101, 1, "あの、霊のみなさんが\nあなたと踊りたいと…\nえっと、私も…", 101, 0, 4, 2556, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108101, 1, "あなたと手を繋ぐと\n胸が苦しくなります\nでも、繋ぎたいな…", 102, 0, 8, 2557, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108101, 1, "ハッピーハロウィン…\n父が元気なので\n今日の運気は良好です", 1, 1, 1, 2558, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108101, 1, "父はお昼寝中です\n本当に寝ているかは\n私にも分かりませんが", 2, 2, 1, 2559, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108101, 1, "お父さん、\nもう眠っちゃったの？\n…眠れるんだったっけ", 3, 3, 1, 2560, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108101, 1, "落ち着きますね…\n霊のみなさんも\n喜んでいます", 2, 0, 1, 2562, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108101, 1, "つん、つん。ふふっ♪\n癒やされます…", 3, 0, 1, 2563, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108101, 1, "精神が研ぎ澄まされる\n香りですね…\n占いが捗ります", 4, 0, 1, 2564, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108101, 1, "あったかいね\nお父さん…\nえ？　分からない？", 5, 0, 1, 2565, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108101, 1, "霊のみなさんは\n食べられないので…\n私がいただきます", 7, 0, 1, 2567, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108101, 1, "お父さんを元に戻す\n手掛かり…\n書かれていないかな？", 8, 0, 1, 2568, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108101, 1, "わぁ、お菓子が沢山…", 12, 0, 1, 2570, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108201, 1, "無料でプリンが\nもらえるなんて、\nハロウィンはすごいの", 1, 0, 1, 2572, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108201, 1, "生きてるときも、\nミヤコはハロウィンが\n好きだった気がするの", 2, 0, 1, 2573, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108201, 1, "みんなに仮装を\n見せつけて、\nプリンをもらうの～", 3, 0, 1, 2574, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108201, 1, "この仮装で、\nみんなに恐怖を\n教えてやるの！", 4, 0, 1, 2575, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108201, 1, "アオーン、アオーン！\nミヤコ、狼女に\nなりきってるの～", 5, 0, 1, 2576, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108201, 1, "アイツに秘密で、\nパーティーの\n飾り付けをするの♪", 101, 0, 4, 2577, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108201, 1, "アイツが狼男の\n仮装をすれば、\nおそろいなの～", 102, 0, 8, 2578, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108201, 1, "プリンをくれなきゃ、\nオマエをカボチャに\nしてやるの～", 1, 0, 1, 2579, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108201, 1, "ハロウィンは幽霊でも\n参加できるから\n楽しいの～！", 2, 0, 1, 2580, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108201, 1, "プリンがないなら\nカボチャプリンを\n食べればいいの！", 3, 0, 1, 2581, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108201, 1, "プリンの可能性は\n無限大なの～", 4, 0, 1, 2582, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108201, 1, "オマエの隣で\nプリンを食べると、\nいつもより美味しいの", 101, 0, 4, 2583, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108201, 1, "オマエが死んでも、\nミヤコはずーっと\n一緒にいてやるの～", 102, 0, 8, 2584, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108201, 1, "朝ごはんは\nカボチャプリンなの！\nハロウィンっぽいの～", 1, 1, 1, 2585, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108201, 1, "ランチは豪華に\nプリンアラモードなの", 2, 2, 1, 2586, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108201, 1, "夜は幽霊たちと\nハロウィンするの！\nオマエも来るの！", 3, 3, 1, 2587, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108201, 1, "幽霊にも休息は\n必要なの！\nプリンもってこいなの", 2, 0, 1, 2589, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108201, 1, "オマエもミヤコの\nパーティーに\n招待してあげるの～", 3, 0, 1, 2590, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108201, 1, "癒されるの～\nまるで天国なの～", 4, 0, 1, 2591, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108201, 1, "ここでなら、\n焼きプリンが\n作れるの！", 5, 0, 1, 2592, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108201, 1, "腹が減っては\nハロウィンは\nできぬなの！", 7, 0, 1, 2594, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108201, 1, "丸ごとカボチャ\nプリン特集！？\n美味しそうなの～♪", 8, 0, 1, 2595, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108201, 1, "お菓子がたくさんで、\nミヤコはとっても\nハッピーなの♪", 12, 0, 1, 2597, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108301, 1, "魔女以外にも、\n色々な仮装を\nしてみたいわね", 1, 0, 1, 2599, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108301, 1, "チョコやアメよりも、\nアタシに相応しい\nスイーツがあるはず…", 2, 0, 1, 2600, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108301, 1, "お菓子の食べ過ぎは、\nお母さんに\n怒られるかも…", 3, 0, 1, 2601, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108301, 1, "この格好で学院に\n行ったら、\nみんな驚くかしら", 4, 0, 1, 2602, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108301, 1, "大人のレディは、\nどういうハロウィンを\n過ごすべきかしら…？", 5, 0, 1, 2603, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108301, 1, "お菓子、アイツの分も\nとっといてあげよ！\n大人の余裕ってやつね", 101, 0, 4, 2604, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108301, 1, "アイツを魅了する\n魔法とかないかしら？", 102, 0, 8, 2605, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108301, 1, "おしゃれスイーツを\nくれなきゃ\nイタズラするわよ！", 1, 0, 1, 2606, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108301, 1, "アタシの大人センスで\nアンタに似合う仮装を\n選んであげるわ！", 2, 0, 1, 2607, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108301, 1, "不気味よねぇ…\nジャックオーランタン\nこ、怖くないわよ？", 3, 0, 1, 2608, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108301, 1, "ハロウィンが楽しくて\n宿題やり忘れてたわ…\nて、手伝って～！", 4, 0, 1, 2609, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108301, 1, "お菓子をもらうのも\nアンタと一緒なら\n恥ずかしくないわ！", 101, 0, 4, 2610, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108301, 1, "アタシのエスコートを\n任せられるのは、\nアンタだけなんだから", 102, 0, 8, 2611, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108301, 1, "朝食はオシャレに\nグリーンスムージー！\n…に、にがぁ", 1, 1, 1, 2612, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108301, 1, "昼下がりはカフェで\n優雅なティータイム\n…憧れるわ～", 2, 2, 1, 2613, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108301, 1, "ハロウィンナイトの\nはじまりよ！\n街に繰り出しましょ！", 3, 3, 1, 2614, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108301, 1, "あう～、お菓子の\n食べすぎで\n動けな～い", 2, 0, 1, 2616, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108301, 1, "アンタも仮装する？\nいまよりもーっと\nかわいくなるわよ！", 3, 0, 1, 2617, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108301, 1, "大人な香りに\n包まれてる～♪", 4, 0, 1, 2618, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108301, 1, "ポカポカね～♪\n…ああー！\nチョコが溶けてる！", 5, 0, 1, 2619, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108301, 1, "おいしい～♪\n…じゃなくて！\nび、美味ですわ～", 7, 0, 1, 2621, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108301, 1, "この服かわい～♪\nって、なにこの値段！\nお小遣い何年分よ！？", 8, 0, 1, 2622, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108301, 1, "お、お菓子なんかで\n喜ぶほど、子供じゃ…", 12, 0, 1, 2624, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107101, 1, "次はどんな策で団長を\nからかってみようか…", 1, 0, 1, 2626, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107101, 1, "む？\n血の臭いがするな\n祭りの時間か☆", 2, 0, 1, 2627, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107101, 1, "平和に飼い殺される\n前に、混沌の愛を\n育みたいね", 3, 0, 1, 2628, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107101, 1, "本気を出した団長と\nいつか手加減なしに\n殺り合いたいな", 4, 0, 1, 2629, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107101, 1, "おばさん呼ばわりする\n新米騎士の小娘を\n懲らしめてやろうか", 5, 0, 1, 2630, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107101, 1, "胸を触られるまで\n誓約のことなど\nすっかり忘れていたな", 101, 0, 4, 2631, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107101, 1, "まさかこのワタシが\nプロポーズするとはな\n…笑えるじゃないか", 102, 0, 8, 2632, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107101, 1, "面白い暇つぶしを\n思いついたんだ\n戯れに付き合え☆", 1, 0, 1, 2633, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107101, 1, "退屈で死にそうだ\n愉しい話題を\nワタシに提供してくれ", 2, 0, 1, 2634, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107101, 1, "なんだ、暇なのか？\n心躍ることでも探しに\n出かけようではないか", 3, 0, 1, 2635, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107101, 1, "このワタシを愉快に\nさせてくれる事でも\nあったのか？", 4, 0, 1, 2636, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107101, 1, "ワタシに求婚した男の\n数だと？\nいちいち覚えてないな", 101, 0, 4, 2637, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107101, 1, "最強無敵の女と\n結婚できるんだ\n誇りに思えよ☆", 102, 0, 8, 2638, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107101, 1, "なにか愉快なことが\n起こりそうな朝だ☆\nワクワクするね", 1, 1, 1, 2639, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107101, 1, "うーむ、腹がすいたな\n愉快なことで\nこの空腹を満たしたい", 2, 2, 1, 2640, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107101, 1, "これからは大人の\n時間だ。愉しみ方を\n知りたいのかい？", 3, 3, 1, 2641, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107101, 1, "このワタシとした事が\n穏やかな気分に\nなってしまうとは…", 2, 0, 1, 2643, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107101, 1, "可愛い姿をしたものが\n惨たらしい\n醜態を晒すのを\n想像してしまう", 3, 0, 1, 2644, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107101, 1, "悪くはない香りだ\nだが、ワタシは血の\n臭いを嗅ぎたいぞ☆", 4, 0, 1, 2645, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107101, 1, "ぬるい、ぬるすぎる！\n地獄の業火には\nほど遠い熱さだ！", 5, 0, 1, 2646, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107101, 1, "ワタシの腕前に\n勝るとも劣らない\nなかなかの味だ", 7, 0, 1, 2648, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107101, 1, "意中の男性を絶対に\n落とす方法…？\nく、下らんっ！", 8, 0, 1, 2649, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105601, 1, "明日はあの釣り場に\n行くとしようかな\nあの子も誘ってみるか", 1, 0, 1, 2651, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105601, 1, "ぷはーっ！\n体を動かしたあとの\n一杯は格別だね", 2, 0, 1, 2652, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105601, 1, "天気が良いと\n昼寝か釣りかで\n迷っちまうな…", 3, 0, 1, 2653, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105601, 1, "大物が釣れたときの\n快感は、他じゃ\n味わえないね", 4, 0, 1, 2654, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105601, 1, "ふあぁ～…\n昼寝でもするか", 5, 0, 1, 2655, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105601, 1, "ふふっ…\nあの子、見た目より\n根性あるんだね", 101, 0, 4, 2656, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105601, 1, "あの子が強くなるには\nどんな魚を食べさせて\nあげればいいのやら…", 102, 0, 8, 2657, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105601, 1, "姉御って呼ぶんじゃ\nないよ。からかうと\nタダじゃおかないよ", 1, 0, 1, 2658, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105601, 1, "釣りのコツ？\n何事も経験が大事だ\n今から行くかい？", 2, 0, 1, 2659, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105601, 1, "どうしたのさ？\n助太刀が必要かい？\nアタシにまかせときな", 3, 0, 1, 2660, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105601, 1, "困ってる人がいたら\nまた力を貸しておくれ\n頼りにしてるよ", 4, 0, 1, 2661, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105601, 1, "なにか困りごとかい？\nどうやら昼寝は\nまた今度だね", 101, 0, 4, 2662, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105601, 1, "よしよし！\nお人好しのお前さんを\n労ってあげるよ", 102, 0, 8, 2663, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105601, 1, "おはよう、お前さん\n魚屋は早起きでなきゃ\nつとまらないよ？", 1, 1, 1, 2664, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105601, 1, "昼飯かい？　今朝\n仕入れた魚があるんだ\n食べてみるかい？", 2, 2, 1, 2665, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105601, 1, "夜釣りにぴったりの\n月が出てるよ\nいい釣りが出来そうだ", 3, 3, 1, 2666, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105601, 1, "コイツに座って\nのんびり釣りが\nできりゃいいのにな", 2, 0, 1, 2668, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105601, 1, "よしよし…♪\nおっと、かわいい物も\nつい抱きしめちまうな", 3, 0, 1, 2669, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105601, 1, "これで魚の臭いを\n消せれば、猫も\n寄らなくなるのかね", 4, 0, 1, 2670, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105601, 1, "冬場の釣りも\nこれくらい暖かけりゃ\nいいんだけどね", 5, 0, 1, 2671, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105601, 1, "腹が減っては\n釣りもできぬ、ってな\nまずは腹ごしらえだ", 7, 0, 1, 2673, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105601, 1, "へぇ…こんな魚が\nいたとは…いつか\n釣りたいもんだね", 8, 0, 1, 2674, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101301, 1, "流石に室内で\n魔法ぶっぱしたら\n怒られるよねぇ", 1, 0, 1, 2676, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101301, 1, "可愛い女の子が\nたくさんいる！\nここは楽園か！？", 2, 0, 1, 2677, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101301, 1, "みんなに漫画とか\n勧めてみようかな！\n布教用のがあるし！", 3, 0, 1, 2678, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101301, 1, "わ、私一人の\n部屋じゃないから\n色々自重せねば！", 4, 0, 1, 2679, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101301, 1, "「ぬるぽ」\n…くっ、\n誰も反応なしか！", 5, 0, 1, 2680, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101301, 1, "次はどの漫画を\n貸そうかなー♪", 101, 0, 4, 2681, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101301, 1, "次の定期巡回で\nとっておきの店に\n連れてってあげよう！", 102, 0, 8, 2682, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101301, 1, "君に似合いそうな\n衣装を買ったよ！\n君もいざ魔法少女に！", 1, 0, 1, 2683, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101301, 1, "さあ！\n暇なら定期巡回に\nレッツラゴー！", 2, 0, 1, 2684, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101301, 1, "戦闘だけじゃなくて、\n日常にも役立つ\n魔法を習得したいね！", 3, 0, 1, 2685, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101301, 1, "新魔法思いついた！\n私って天才！？", 4, 0, 1, 2686, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101301, 1, "決め台詞は超大事！\n君の決め台詞も\n私が考えてあげよう！", 101, 0, 4, 2687, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101301, 1, "私ってもしやリア充？\n…そんなバカな！\n非リア代表のはずが！", 102, 0, 8, 2688, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101301, 1, "おはよー…\n徹夜で本読んでたから\nまだ眠い～", 1, 1, 1, 2689, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101301, 1, "昼飯食べて、\n午後の定期巡回も\n頑張りましょー！", 2, 2, 1, 2690, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101301, 1, "夜からが、\nオタ活本番でしょ！", 3, 3, 1, 2691, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101301, 1, "ふいー！\nくつろいで本を読む！\n最高だねー！", 2, 0, 1, 2693, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101301, 1, "君、可愛いね～♪\n魔法少女の\n使い魔みたい！", 3, 0, 1, 2694, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101301, 1, "良い匂いに\n釣られてしまった…\nまさか、孔明の罠！？", 4, 0, 1, 2695, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101301, 1, "暖かいけど、\nここで本を読むのは\nちょっち危ないかな", 5, 0, 1, 2696, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101301, 1, "ウマ―！\nこれの詳細きぼんぬ！", 7, 0, 1, 2698, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101301, 1, "こ、古代語の本！？\n現代語でおk…", 8, 0, 1, 2699, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 190901, 1, "ふふふっ♪\n次はどんな悪戯を\nしてやろうかしら♪", 1, 0, 1, 2701, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 190901, 1, "幻影湖の霧を吸った\n人間たちの\nリアクションって、\nサイコーよねぇ～♪", 2, 0, 1, 2702, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 190901, 1, "室内だけとはいえ…\n自由に動けるって、\nやっぱりいいわ～♪", 3, 0, 1, 2703, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 190901, 1, "改めて見ると良い\n部屋ね…悪戯とか\nしやすそうだしぃ♪", 4, 0, 1, 2704, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 190901, 1, "私を封じていた部屋\nなのに…急に居心地が\n良くなったわね♪", 5, 0, 1, 2705, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 190901, 1, "あんたの頭、座り心地\n良いわね…私専用の\n椅子にしてあげる♪", 1, 0, 1, 2706, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 190901, 1, "外は大変みたいねぇ…\nまあ、私は好き勝手に\nやらせてもらうわ♪", 2, 0, 1, 2707, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 190901, 1, "顔に落書された？\n誰か知らないけど、\n悪いことする奴も\nいるものねぇ～♪", 3, 0, 1, 2708, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 190901, 1, "フィオのやつ…\nどこをウロチョロ\nしてるのかしら？", 4, 0, 1, 2709, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 190901, 1, "人間の朝は大変ねぇ…\n私はその様を見ながら\nゆっくりするわね♪", 1, 1, 1, 2710, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 190901, 1, "う～私も、お日様を\n浴びながら、お外を\nお散歩したーい！", 2, 2, 1, 2711, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 190901, 1, "この暗さ…閉じ込め\nられていた時を\n思い出すわね…", 3, 3, 1, 2712, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 190901, 1, "悪戯しすぎて\n疲れたから、ちょっと\n休憩しよっと♪", 2, 0, 1, 2714, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 190901, 1, "ガイド妖精たる私が、\nこんな造形物に\n惹かれる…わけが……\nかわいい♪", 3, 0, 1, 2715, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 190901, 1, "この香りを囮にして、\n周りに悪戯を仕込む…\n完璧ね♪", 4, 0, 1, 2716, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 190901, 1, "不思議ねぇ…私は\n温度とか関係ないのに\n離れられないわ", 5, 0, 1, 2717, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 190901, 1, "食事の必要がない体…\nつまり、食べたければ\n食べていいのよね～♪", 7, 0, 1, 2719, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 190901, 1, "へぇ…最近はこんな\n悪戯もあるのねぇ…\nあいつで試さないと♪", 8, 0, 1, 2720, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108401, 1, "メリークリスマス！\n歌で幸せを\n届けます…！", 1, 0, 1, 2722, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108401, 1, "あなたと出会えたのは\n偶然ではなくて…\nクリスマスの奇跡だと\n思うんです", 2, 0, 1, 2723, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108401, 1, "クリスマスは華やかで\nつい子供のように\nはしゃいでしまいます", 3, 0, 1, 2724, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108401, 1, "曲が書けるのは\nあなたが側に\nいてくれるからです…", 4, 0, 1, 2725, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108401, 1, "感動を与えられる歌…\nあなたと一緒になら\n生み出せるはず…", 5, 0, 1, 2726, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108401, 1, "クリスマスの幸せを\nあなたと\n感じ合えたらな…", 101, 0, 4, 2727, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108401, 1, "あなたの幸せを願った\nクリスマスソング…\nいつか披露しますね", 102, 0, 8, 2728, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108401, 1, "みなさんの笑顔を\n守りたいから…\n私は歌い続けます", 1, 0, 1, 2729, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108401, 1, "歌を届ける\nサンタさんに\nなりたいな…", 2, 0, 1, 2730, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108401, 1, "お暇でしたら\nクリスマスのお祝いを\nしませんか？", 3, 0, 1, 2731, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108401, 1, "ステージに立つ前に\n喉を整えないと…\n発声練習は念入りに…", 4, 0, 1, 2732, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108401, 1, "まわりはかわいい子\nばっかりだけど…\nこの服なら\nアピールになるかな？", 101, 0, 4, 2733, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108401, 1, "歌でならいくらでも\nあの人への気持ちを\n込められるのに…", 102, 0, 8, 2734, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108401, 1, "今日も一日\n幸せな歌で世界が\n包まれますように…", 1, 1, 1, 2735, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108401, 1, "お茶を飲みながら\n好きな音楽を\n語り合いませんか？", 2, 2, 1, 2736, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108401, 1, "ふふ、寝かしつけの\n子守唄を\nお望みですか？", 3, 3, 1, 2737, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108401, 1, "ふかふかですね\n心地良すぎて\n眠ってしまいそう…", 2, 0, 1, 2739, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108401, 1, "素敵…\n優しい歌みたいに\n気持ちが落ち着く…", 3, 0, 1, 2740, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108401, 1, "インスピレーションが\n溢れてくる…\nいい曲が書けそう…", 4, 0, 1, 2741, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108401, 1, "この光みたいに\n灯火になるような\n歌を歌いたい…", 5, 0, 1, 2742, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108401, 1, "喉の健康のためにも\n食事はしっかり\nとるようにしています", 7, 0, 1, 2744, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108401, 1, "物語には\n曲作りのヒントが\n詰まっているんです", 8, 0, 1, 2745, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108401, 1, "絵が飛び出してくる\nなんて…精霊が宿って\nいるのでしょうか？", 14, 0, 1, 2747, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108401, 1, "か、可愛い…♪\nちょっとだけ触っても\nいいですよね…？", 15, 0, 1, 2748, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108501, 1, "クリスマスでも、\n演劇の練習は\nしっかりやらないと！", 1, 0, 1, 2749, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108501, 1, "あうぅ…\nクリスマスの準備が\n終わらないよぅ…", 2, 0, 1, 2750, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108501, 1, "アヤネちゃんが\n摘み食いしないように\n見ておかないと…", 3, 0, 1, 2751, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108501, 1, "サンタさんみたいに、\n皆に笑顔で接する…\nうぅ…私には無理ぃ…", 4, 0, 1, 2752, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108501, 1, "新しいお洋服～♪\nスズメお姉ちゃんに\nお礼をいわないと♪", 5, 0, 1, 2753, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108501, 1, "お兄ちゃんの為に、\nプレゼントを\n用意しないと♪", 101, 0, 4, 2754, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108501, 1, "クリスマス…\n恋人の日…ひゃわっ！\nわ、私ってば何を…！", 102, 0, 8, 2755, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108501, 1, "メリー\nクリスマスです、\nお兄ちゃん♪", 1, 0, 1, 2756, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108501, 1, "そ、そんなに\n見られると…\n恥ずかしいよぉ…", 2, 0, 1, 2757, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108501, 1, "ひゃわぁっ！？\n急に触られると、\n驚くからやめてぇ…", 3, 0, 1, 2758, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108501, 1, "あ…クリスマスの\n準備を手伝って\nくれるんですか？", 4, 0, 1, 2759, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108501, 1, "お兄ちゃんと\n過ごすクリスマス、\n楽しみですぅ…♪", 101, 0, 4, 2760, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108501, 1, "クリスマスはその…\n二人で…あうぅ…\nなんでもないですぅ…", 102, 0, 8, 2761, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108501, 1, "うう…パーティーが\n楽しみで、あんまり\n眠れなかったよぅ…", 1, 1, 1, 2762, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108501, 1, "ケーキを食べられなく\nなっちゃうから、\nお昼は軽めにしないと", 2, 2, 1, 2763, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108501, 1, "ちょっと眠いけど…\n今日は少しだけ\n夜更かししたいです♪", 3, 3, 1, 2764, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108501, 1, "ふかふかぁ…\nクリスマスの準備中に\n眠くなっちゃいますぅ", 2, 0, 1, 2766, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108501, 1, "この子と一緒に\nおままごとしたいな♪", 3, 0, 1, 2767, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108501, 1, "いい匂い…♪\nみんなで\nアロマパーティー\nしたいなぁ…♪", 4, 0, 1, 2768, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108501, 1, "ぬっくぬく…♪\nもう、ここから\n離れたくないよぉ…♪", 5, 0, 1, 2769, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108501, 1, "はむはむ…♪\nクリスマスのお料理\nより美味しいかも～♪", 7, 0, 1, 2771, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108501, 1, "すごい…\nクリスマスの\n演劇って、\nこんなにあるんだ…", 8, 0, 1, 2772, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108501, 1, "すごい…まるで、\nクリスマスの演劇を\n観てるようですぅ…♪", 14, 0, 1, 2774, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108501, 1, "とっても可愛いです♪\nお芝居の相手役に\nしたいなぁ…♪", 15, 0, 1, 2775, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108601, 1, "甘ぁいケーキも、\nデッカイ七面鳥も、\nぜーんぶ私のだー♪", 1, 0, 1, 2776, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108601, 1, "毎日が\nクリスマスだったら\nいいのにな～♪", 2, 0, 1, 2777, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108601, 1, "プレゼントはあるし、\n料理は美味しいし、\nクリスマス最高ー♪", 3, 0, 1, 2778, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108601, 1, "ここでぷうきちの\nクリスマス一発芸♪\n『無茶ぶりするな！』", 4, 0, 1, 2779, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108601, 1, "ぷうきちがいつもより\n重いかも…太ったの？\n『衣装の重さだよ！』", 5, 0, 1, 2780, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108601, 1, "お兄ちゃんが\n一緒にいてくれると\nクリスマス楽しいね♪", 101, 0, 4, 2781, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108601, 1, "お兄ちゃんに抱きつく\nと、なんだかドキドキ\nする…なんでかな？", 102, 0, 8, 2782, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108601, 1, "メリークリスマス♪\nプレゼントは、\nいつでも受付中だよ♪", 1, 0, 1, 2783, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108601, 1, "このクマさん耳\n可愛いでしょ♪\nぷうきちと\nお揃いなんだー♪", 2, 0, 1, 2784, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108601, 1, "お兄ちゃん…私、\n甘ぁいケーキが\n食べたいなぁ…♪", 3, 0, 1, 2785, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108601, 1, "クリスマスぷうきちは\nぬくぬく感が凄いよ！\nお兄ちゃんもさわる？", 4, 0, 1, 2786, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108601, 1, "えへへ…お兄ちゃんの\n手は、大きくて、\n暖かくて大好き～♪", 101, 0, 4, 2787, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108601, 1, "来年のクリスマスも、\nこうやって\nお兄ちゃんに\n甘えていたいな～♪", 102, 0, 8, 2788, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108601, 1, "あれぇ…\n私のケーキはぁ…？\n『ちゃんと起きろ』", 1, 1, 1, 2789, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108601, 1, "お昼はデッカイ\n七面鳥が食べたーい♪\n『夜まで我慢しろ』", 2, 2, 1, 2790, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108601, 1, "『もう寝る時間だぞ』\nサンタさんが来る\nまで起きてるぅ…", 3, 3, 1, 2791, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108601, 1, "このふかふか具合…\nぷうきち！\n負けてられないよ！", 2, 0, 1, 2793, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108601, 1, "この子、\nすっごくかわいい～♪\n『ライバル登場か！』", 3, 0, 1, 2794, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108601, 1, "良い匂い…あっ！\nぷうきちにこの\n匂いを移せば…！\n『匂い袋にするな』", 4, 0, 1, 2795, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108601, 1, "あったか～い♪\nえへへ、ぷうきちも\nあっためてあげる♪", 5, 0, 1, 2796, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108601, 1, "ぷうきち！　これ、\nとってもおいしいよ♪\n『食べすぎるなよ』", 7, 0, 1, 2798, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108601, 1, "お、大人の私なら、\nこれぐらい…読め…\n『無理するな…』", 8, 0, 1, 2799, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108601, 1, "絵が飛び出す…\nぷうきちに絵本を\n入れれば、イタズラに\n使えるかも…♪", 14, 0, 1, 2801, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108601, 1, "か、可愛いけど…\nぷうきちサンタの方が\n可愛いんだからぁ…！", 15, 0, 1, 2802, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108901, 1, "着物も慣れると\n動きやすいものさ", 1, 0, 1, 2803, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108901, 1, "鼻緒が緩んでしまった\n下駄で修業は\n無理があったかな…", 2, 0, 1, 2804, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108901, 1, "小腹が空いた…\nユイが作った餅、\nまだ食べられるかな？", 3, 0, 1, 2805, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108901, 1, "服装も変わったし\nいつもと違う戦い方を\n想定しないと…", 4, 0, 1, 2806, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108901, 1, "…ヒヨリは動きすぎて\n着崩れしてないか\n心配だ", 5, 0, 1, 2807, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108901, 1, "これからもずっと\n仲間と、彼と\n一緒に…", 101, 0, 4, 2808, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108901, 1, "願いを叶えるために\nもっと自分を\n鍛えていかないと！", 102, 0, 8, 2809, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108901, 1, "頭の飾りがずれてた？\nそうか、直してくれて\nありがとう", 1, 0, 1, 2810, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108901, 1, "着物がそんなに\n珍しい？\nキミも着てみる？\n…冗談だよ", 2, 0, 1, 2811, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108901, 1, "肩掛けの触り心地が\n気に入った？\nならもっと触る？", 3, 0, 1, 2812, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108901, 1, "ああ、この刀？\n釣り仲間に見繕って\nもらったんだ", 4, 0, 1, 2813, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108901, 1, "キミといると\n気持ちが段々と\n安らいでいくよ", 101, 0, 4, 2814, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108901, 1, "キミは私の行く先を\n照らしてくれる\n光のような存在だよ", 102, 0, 8, 2815, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108901, 1, "おはよう\n朝の修行を始めるけど\nキミもどう？", 1, 1, 1, 2816, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108901, 1, "この時間に軽く\n休憩をとるといい\n能率がよくなるよ", 2, 2, 1, 2817, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108901, 1, "もう夜か…\n明日もまた頑張ろう\nそれじゃ、おやすみ", 3, 3, 1, 2818, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108901, 1, "…疲れていたのかな\nもう少しで眠る\nところだった…", 2, 0, 1, 2820, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108901, 1, "ふふっ、正月衣装を\n着せてやるのも\nよさそうだ", 3, 0, 1, 2821, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108901, 1, "ふぅ…いい香りだ\n慌ただしい日々から\n心が解放される", 4, 0, 1, 2822, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108901, 1, "この心地よさ…\n寝てしまわないよう\n気をつけないと", 5, 0, 1, 2823, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108901, 1, "おいしいけど\n帯がきつくなる前に\n手を止めよう…", 7, 0, 1, 2825, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108901, 1, "外を出歩くのも\nいいけど、中で\n読書もありだな", 8, 0, 1, 2826, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108801, 1, "…騎士クンって\nおせち料理で\n何が好きなんだろう？", 1, 0, 1, 2828, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108801, 1, "着物でお掃除や\nお洗濯するの\n慣れてきた♪", 2, 0, 1, 2829, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108801, 1, "今年は\nどんな福袋を\n買おうかな…♪", 3, 0, 1, 2830, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108801, 1, "お餅に使う調味料\nみんなで違うから\n色々用意しなきゃ", 4, 0, 1, 2831, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108801, 1, "ほっぺがお餅みたいに\nぷにぷに…\n食べ過ぎちゃった…", 5, 0, 1, 2832, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108801, 1, "来年も、再来年も\nみんなで一緒に\n新年を迎えたいな", 101, 0, 4, 2833, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108801, 1, "お願いです…\nいつかこの気持ちが\n伝わりますように…", 102, 0, 8, 2834, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108801, 1, "髪飾りが落ちそう？\nじゃあ…騎士クン\nつけ直してくれる？", 1, 0, 1, 2835, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108801, 1, "着物が着たいの？\nじゃあ今度一緒に\n買いに…行く？", 2, 0, 1, 2836, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108801, 1, "あっ、騎士クン\nお雑煮のお餅\nいくつ入れたい？", 3, 0, 1, 2837, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108801, 1, "外、寒かったよね？\nはいこれ。熱い甘粥\n飲むと温まるよ", 4, 0, 1, 2838, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108801, 1, "今年は騎士クンと…\nえっ？　騎士クン！？\nな、なんでもないよ！", 101, 0, 4, 2839, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108801, 1, "騎士クンの願い事\nわたしが\n叶えてあげたいな…", 102, 0, 8, 2840, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108801, 1, "おはよう\n太陽の光に当たると\n元気が出るよ", 1, 1, 1, 2841, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108801, 1, "お昼ごはん\n作らなきゃ…\n騎士クンも食べる？", 2, 2, 1, 2842, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108801, 1, "一日が終わるのは\nあっという間だよね\n一年も同じなのかな？", 3, 3, 1, 2843, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108801, 1, "ふぅ…\n帯も少し\nゆるめちゃおう…", 2, 0, 1, 2845, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108801, 1, "お正月用に\n着物を縫って\nあげたいな", 3, 0, 1, 2846, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108801, 1, "これでお肌が\n保湿出来たら\nいいのになぁ", 4, 0, 1, 2847, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108801, 1, "ん～…\nぬくぬくしてて\n動きたくないよ～", 5, 0, 1, 2848, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108801, 1, "…冬眠しないのに\n熊さんみたいに\n食べちゃいそう", 7, 0, 1, 2850, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108801, 1, "年末年始は\n告白のチャンス…\nむ、無理無理！", 8, 0, 1, 2851, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108701, 1, "みんな忙しそう…\n待っててね、\nすぐ助けるから！", 1, 0, 1, 2853, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108701, 1, "ユイちゃんと\nレイさん…よし！\nどっちも手伝おう！", 2, 0, 1, 2854, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108701, 1, "あっ、着崩れてる！\nわわっ、どうしよ！\n帯が結べないよー！", 3, 0, 1, 2855, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108701, 1, "初詣楽しみー！\n出店はどこから\n行こうかな？", 4, 0, 1, 2856, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108701, 1, "お正月だし\nみんなで楽しく\n遊んじゃおー！", 5, 0, 1, 2857, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108701, 1, "うーん…\n騎士くんはお正月\n何してるんだろ…", 101, 0, 4, 2858, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108701, 1, "ううぅー…\n騎士くんを見てると\nドキってなっちゃう…", 102, 0, 8, 2859, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108701, 1, "あたしから\nおいしそうな匂い？\nくんくん…ソースだ！", 1, 0, 1, 2860, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108701, 1, "ヘアピンが…\n拾ってくれたんだ！\nありがとう騎士くん！", 2, 0, 1, 2861, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108701, 1, "髪飾りがかわいい？\n耳の近く触りたい？\n…優しくね？　", 3, 0, 1, 2862, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108701, 1, "グローブで食べるの\n大変そう？　特訓して\n今はお箸も使えるよ！", 4, 0, 1, 2863, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108701, 1, "えっ、ほっぺが赤い？\nそうかな…？\n気のせい気のせい！", 101, 0, 4, 2864, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108701, 1, "騎士くんといるとね…\nなんでもない！\nまだ内緒っ！", 102, 0, 8, 2865, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108701, 1, "おはよー！\n今日も明るく元気に\nがんばろー！", 1, 1, 1, 2866, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108701, 1, "お腹すいてない？\n一緒にご飯食べよ！\n何にしようかなー♪", 2, 2, 1, 2867, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108701, 1, "今日もいっぱい\nがんばったー！\nいつも？　…そう？", 3, 3, 1, 2868, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108701, 1, "休憩休憩♪\nちょっと休んで\nガンガン動くぞー！", 2, 0, 1, 2870, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108701, 1, "かわいい♪\nでも、壊しそうで\nちょっと怖い…", 3, 0, 1, 2871, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108701, 1, "はぁー♪\nいい香りー…\n気分スッキリ♪", 4, 0, 1, 2872, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108701, 1, "ほっこりしてて\nぬくぬく…\n丸まりたい…", 5, 0, 1, 2873, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108701, 1, "ど、どうしよう…！\n手が、止まんないよ！", 7, 0, 1, 2875, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 108701, 1, "へー…お年玉って\n昔はお餅だったんだ\n…今のほうがいいね", 8, 0, 1, 2876, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109001, 1, "甘いチョコの香りを\n漂わせて…ふふっ\nこれで準備万端ですわ", 1, 0, 1, 2878, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109001, 1, "ここを私のチョコで\n埋め尽くしますわ", 2, 0, 1, 2879, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109001, 1, "エプロンを着て\nあの方を待つ私…\nまるで…クスクス", 3, 0, 1, 2880, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109001, 1, "キッチンはどこかしら\n今のうちに作って\nおきたいものが…", 4, 0, 1, 2881, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109001, 1, "あのお方を私のチョコ\nなしでは生きられない\n体に…ふふっ\n冗談ですわ…", 5, 0, 1, 2882, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109001, 1, "このチョコは運命の味\n一口食べれば私の虜…\nそれも運命ですわ…", 101, 0, 4, 2883, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109001, 1, "私ともあろうものが\nなんだかドキドキして\nきましたわ…", 102, 0, 8, 2884, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109001, 1, "あなた様。私に\n隠しているものなど\nありませんよね…？", 1, 0, 1, 2885, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109001, 1, "私の手作りチョコです\n味の方にも自信\nありますのよ？", 2, 0, 1, 2886, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109001, 1, "少し苦いのは\nチョコがビターなだけ\nお気になさらず…", 3, 0, 1, 2887, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109001, 1, "解毒薬はどこに\nいれたかしら…？", 4, 0, 1, 2888, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109001, 1, "運命で結ばれている\nあなた様と私…\nチョコも特別でないと\nいけませんわ", 101, 0, 4, 2889, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109001, 1, "チョコを食べた後は\nメインディッシュ…\nさぁ、私を召し上がれ", 102, 0, 8, 2890, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109001, 1, "目覚めのチョコですわ\nすっきり目が覚めます\nしばらく眠れなく\nなりますけど…", 1, 1, 1, 2891, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109001, 1, "おやつのチョコですわ\nデザートにどうぞ\n空腹を感じなく\nなりますわ…クスクス", 2, 2, 1, 2892, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109001, 1, "お夜食のチョコですわ\nとてもよく眠れます\nさ、溶けてしまう前に", 3, 3, 1, 2893, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109001, 1, "この心地良さ…\n参考になりますわ\n次の薬は…クスクス", 2, 0, 1, 2895, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109001, 1, "この中、何か\n入りそうですわね\n…チョコレート、とか", 3, 0, 1, 2896, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109001, 1, "いい香り…この香りと\n同じものを調合して\n私も持っていたい", 4, 0, 1, 2897, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109001, 1, "この暖かさ…\nチョコが溶けないよう\n気を付けなくては", 5, 0, 1, 2898, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109001, 1, "いい味をしていますわ\n私も腕を磨きましょう\n料理と、調合の腕を…", 7, 0, 1, 2900, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109001, 1, "勉強になりますわ\n知識はいくらあっても\n困りませんもの\n特に、人体の知識は…", 8, 0, 1, 2901, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109101, 1, "弟くんのことを\n考えると\nあっという間に時間が\n過ぎちゃう…", 1, 0, 1, 2903, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109101, 1, "お姉ちゃんの\n元気の源は、弟くんの\n喜ぶ顔なんだから♪", 2, 0, 1, 2904, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109101, 1, "弟くんと会ったら\nまずは抱きしめて\nそれからそれから…", 3, 0, 1, 2905, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109101, 1, "今日のお姉ちゃんは\n昨日のお姉ちゃんより\nいいお姉ちゃんだよ♪", 4, 0, 1, 2906, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109101, 1, "弟くん、もっと\nお姉ちゃんに\n甘えてもいいのに♪", 5, 0, 1, 2907, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109101, 1, "お姉ちゃんのチョコ\n食べさせてあげたい…", 101, 0, 4, 2908, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109101, 1, "ドキドキしてきた…\nお姉ちゃんなのに\nどうしてかなぁ…", 102, 0, 8, 2909, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109101, 1, "お姉ちゃんを\n好きなだけ\n触っていいよ♪", 1, 0, 1, 2910, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109101, 1, "そんなにお姉ちゃんの\nチョコが欲しいの？\n食いしん坊さん♪", 2, 0, 1, 2911, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109101, 1, "ナデナデするのは\nお姉ちゃんの仕事！", 3, 0, 1, 2912, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109101, 1, "ありがとうなんて…\nお姉ちゃんとして\n当たり前だよ♪", 4, 0, 1, 2913, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109101, 1, "どうしたのかなっ？\nお姉ちゃんはいつでも\n弟くんのそばにいるよ", 101, 0, 4, 2914, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109101, 1, "チョコを作ったよ\n隠し味は、たくさんの\n弟くんへの愛情♪", 102, 0, 8, 2915, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109101, 1, "おはよ。お姉ちゃんが\n顔を洗うの\n手伝ってあげるね♪", 1, 1, 1, 2916, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109101, 1, "ご飯を食べたら\n眠たくなるよね…\nお姉ちゃんと寝る？", 2, 2, 1, 2917, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109101, 1, "そろそろ眠くなった？\nお姉ちゃんが添い寝\nしてあげるね♪", 3, 3, 1, 2918, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109101, 1, "この気持ちよさは\nお姉ちゃんも太鼓判♪", 2, 0, 1, 2920, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109101, 1, "このかわいさは\nお姉ちゃんも大満足♪", 3, 0, 1, 2921, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109101, 1, "この香り、チョコに\n使えるかな…？", 4, 0, 1, 2922, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109101, 1, "弟くんと一緒に\nぬくぬくしたいな…♪", 5, 0, 1, 2923, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109101, 1, "すごくおいしいねっ♪\nあとで運動しなきゃ…", 7, 0, 1, 2925, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109101, 1, "本を読んでる\n知的なお姉ちゃん\n素敵でしょ♪", 8, 0, 1, 2926, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106101, 1, "アタシって…\nここにいても\nいいのか…？", 1, 0, 1, 2928, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106101, 1, "ちゃんとした建物に\nいるのが久しぶりで\nそわそわする…", 2, 0, 1, 2929, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106101, 1, "ここで天楼覇断剣\n出す練習したら\nアイツら怒るかな？", 3, 0, 1, 2930, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106101, 1, "ギルドハウスか…\nこういう隠れ家\n欲しかったんだよな", 4, 0, 1, 2931, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106101, 1, "めちゃくちゃ暇だ…\n…なんかやること\nないのか？", 5, 0, 1, 2932, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106101, 1, "みんなが早く\nアタシのこと\n思い出さないかな", 101, 0, 4, 2933, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106101, 1, "アイツはいいヤツだし\n戦う時はアタシも\n協力してやるか！", 102, 0, 8, 2934, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106101, 1, "ちゃんはいらん！\nノウェムでいい！\n分かったか？", 1, 0, 1, 2935, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106101, 1, "どうした？\n戦うのか！？\n…なんだ、違うのか", 2, 0, 1, 2936, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106101, 1, "身長のことは\nあんまり言うな！\n気にしてるからな！", 3, 0, 1, 2937, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106101, 1, "アタシの戦い方は\nなんでもアリだ！\nだから見習うなよ？", 4, 0, 1, 2938, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106101, 1, "オマエとの思い出か…\n敵として戦ってばっか\nだったな！", 101, 0, 4, 2939, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106101, 1, "オマエやオクトーと\n一緒に、あちこち\nいってみたいんだ！", 102, 0, 8, 2940, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106101, 1, "よっ！　おはよう！\n朝だぞー！\n起きてるかー？", 1, 1, 1, 2941, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106101, 1, "腹減ったー！\n飯食うぞ、飯！\nオマエも行くよな？", 2, 2, 1, 2942, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106101, 1, "ふわぁ…\nまだ起きてんのか？\n元気だなオマエ…", 3, 3, 1, 2943, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106101, 1, "ふぃ～…\n極楽極楽…", 2, 0, 1, 2945, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106101, 1, "おいオマエ！\n寝技の練習に\n付き合ってもらうぞ", 3, 0, 1, 2946, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106101, 1, "くんくん…\nたまにはこういうのも\n悪くないかもな", 4, 0, 1, 2947, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106101, 1, "あー…\nもっと前にコイツと\n出会いたかった…", 5, 0, 1, 2948, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106101, 1, "VRでも飯はうまい！\n腹が減るのはちゃんと\n生きてる気がするな", 7, 0, 1, 2950, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106101, 1, "勉強は大事だな！\nうん！…だめだ！\n全然わからん！", 8, 0, 1, 2951, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101401, 1, "探偵というのは\n謎を追い求める\n生き物なのさっ！", 1, 0, 1, 2953, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101401, 1, "事件のオチは\n往々にして\n取るに足らないものさ", 2, 0, 1, 2954, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101401, 1, "読書で得る知識が\n探偵の\n血肉になるんだよ", 3, 0, 1, 2955, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101401, 1, "この前の\n捜査資料…\nどこにあったかな？", 4, 0, 1, 2956, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101401, 1, "どんな小さな謎も\n私にとっては\n宝石みたいなものさ！", 5, 0, 1, 2957, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101401, 1, "助手くんと一緒だと\nよい発想が浮かぶ…\nどうしてだろうね？", 101, 0, 4, 2958, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101401, 1, "助手くんの好きな\nタイプって…\nす、推理してみるっ！", 102, 0, 8, 2959, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101401, 1, "珈琲、淹れたてだよ\n一杯どうだい？", 1, 0, 1, 2960, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101401, 1, "事件の考察を\nまとめてみたんだけど\n聞いてくれるかな？", 2, 0, 1, 2961, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101401, 1, "小さな違和感を\n見逃さない…\n探偵の心得だよ", 3, 0, 1, 2962, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101401, 1, "機能的な手帳とペン…\nこの仕事には\n欠かせないね！", 4, 0, 1, 2963, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101401, 1, "助手くんに\n珈琲…\n淹れてほしいなぁ～", 101, 0, 4, 2964, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101401, 1, "助手くんは\n私にとって\n最高の相棒だよ☆", 102, 0, 8, 2965, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101401, 1, "朝はいいひらめきが\n得られるんだ…\nさあ捜査を始めよう！", 1, 1, 1, 2966, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101401, 1, "一休みしたら\n推理の答え合わせを\nしてみようか？", 2, 2, 1, 2967, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101401, 1, "夜の張りこみかい？\nなら私も\n同行させてほしいな！", 3, 3, 1, 2968, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101401, 1, "脳を酷使した後は\nゆっくり休む…\nこれも仕事だよ？", 2, 0, 1, 2970, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101401, 1, "この愛らしさ…\n要捜査対象だね！", 3, 0, 1, 2971, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101401, 1, "思考が活性化される\n素敵な香りだね…", 4, 0, 1, 2972, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101401, 1, "くぅ、心地よすぎる…\n今だけは捜査も\n一休みだね♪", 5, 0, 1, 2973, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101401, 1, "いっぱい食べて\nいっぱい考える\n探偵の本分だね！", 7, 0, 1, 2975, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101401, 1, "探偵だからね\nミステリには\n一家言あるんだ", 8, 0, 1, 2976, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105501, 1, "先輩、素敵だなぁ…\nもっと近づければ\nいいのに…", 1, 0, 1, 2978, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105501, 1, "せ、先輩がいた部屋！\n私には刺激が強い…", 2, 0, 1, 2979, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105501, 1, "先輩を観察しやすい\n場所はあるかな…", 3, 0, 1, 2980, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105501, 1, "バ、バイト代出たし、\nせ、先輩をご飯とかに\n誘いたい…な", 4, 0, 1, 2981, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105501, 1, "今頃先輩は\nあの辺かな…？\n見に行ってみよう！", 5, 0, 1, 2982, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105501, 1, "私と先輩の出会いって\n思い返すと少女漫画\nみたい…えへへ", 101, 0, 4, 2983, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105501, 1, "せ、先輩になら\n観察されてもいいなぁ", 102, 0, 8, 2984, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105501, 1, "バイト先に新しい子が\n入って来たんです…\nわ、私、先輩です♪", 1, 0, 1, 2985, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105501, 1, "せ、先輩…？\nえっと、あの、その、\nあわわわわ…！", 2, 0, 1, 2986, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105501, 1, "えへへ…えへへ～\nせ、先輩から\n話しかけてくれました", 3, 0, 1, 2987, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105501, 1, "ま、また…\n先輩と一緒に\nバイトしたい…です", 4, 0, 1, 2988, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105501, 1, "私と先輩が同じ空間に\n…ゆ、夢みたいです", 101, 0, 4, 2989, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105501, 1, "せ、先輩のそばに\n居られて、私\n今、とっても\n幸せです…", 102, 0, 8, 2990, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105501, 1, "せ、先輩…！\nおひゃっ！\nおはようございます！", 1, 1, 1, 2991, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105501, 1, "先輩ランチに\n行きましょう！\nよし、練習はバッチリ", 2, 2, 1, 2992, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105501, 1, "あ、せ、先輩。\nえと、そ、外…\nもう真っ暗ですねぇ", 3, 3, 1, 2993, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105501, 1, "こ、ここで先輩も\n休んだり…はっ！\nわ、私ったらなにを！", 2, 0, 1, 2995, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105501, 1, "かわいいですねー\n君も一緒に、\n先輩を観察する？", 3, 0, 1, 2996, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105501, 1, "ここから先輩の\n香りがすればなあ", 4, 0, 1, 2997, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105501, 1, "先輩と一緒に\nぬくぬくできたら\n最高なんだけどなぁ", 5, 0, 1, 2998, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105501, 1, "おいし～\nこれは\n先輩も好きな味かな", 7, 0, 1, 3000, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 105501, 1, "す、好きな人に\n近づく方法…", 8, 0, 1, 3001, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109201, 1, "私の力は、大切な\n人たちを護るために\n役立てたいんだ", 1, 0, 1, 3003, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109201, 1, "きれいな景色と\n美味しいご飯！\nこの世界も素敵だね", 2, 0, 1, 3004, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109201, 1, "この世界のみんなとも\nもっと仲良く\nなりたいな！", 3, 0, 1, 3005, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109201, 1, "わー！\nあれもこれも\n気になっちゃうな！", 4, 0, 1, 3006, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109201, 1, "しっかりしなきゃって\n思ってても、\nグレアといるとつい\n甘えちゃうんだよね～", 5, 0, 1, 3007, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109201, 1, "違う世界でも\n友だちができて、\nすごくうれしいよ！", 101, 0, 4, 3008, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109201, 1, "みんなとも一緒に\n授業とか受けたいな～\n絶対楽しそうだもん！", 102, 0, 8, 3009, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109201, 1, "歩き方がきれい？\nえへへ、そうかな…？", 1, 0, 1, 3010, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109201, 1, "勉強は好きだよ！\n知識が増えていくのは\nやっぱり楽しいから", 2, 0, 1, 3011, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109201, 1, "私たちの国マナリアは\n人々の間に魔法が\n伝えられた、その\n始まりの地なんだ！", 3, 0, 1, 3012, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109201, 1, "この部屋にも、\nもっとたくさん本棚が\n増えないかな～…？", 4, 0, 1, 3013, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109201, 1, "あなたも魔法、\n覚えてみる？\n私、教えるよ！", 101, 0, 4, 3014, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109201, 1, "お茶の時間に\nしたいな～…\n一緒にどう？", 102, 0, 8, 3015, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109201, 1, "おはようっ！\n今日も素敵な\n一日にしようね！", 1, 1, 1, 3016, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109201, 1, "お昼ご飯は\nちゃんと食べた？\n午後も頑張ろうね！", 2, 2, 1, 3017, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109201, 1, "つい本の続きが\n気になって、夜更かし\nしちゃうんだよね～…", 3, 3, 1, 3018, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109201, 1, "はぁ、気持ちいい～…\nちょっとだけ\n眠っちゃおうかな…", 2, 0, 1, 3020, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109201, 1, "わあ、これ可愛い！\nグレアにも見せて\nあげたいな", 3, 0, 1, 3021, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109201, 1, "いい香り～！\nなんだか気持ちが\n落ち着くね", 4, 0, 1, 3022, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109201, 1, "あぁ、あったかくて\n幸せ～♪　身体が\nポカポカしてきたよ！", 5, 0, 1, 3023, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109201, 1, "こ、これ美味し～！\n止められないよー！", 7, 0, 1, 3025, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109201, 1, "ふんふん…\n…はっ！　む、夢中に\nなっちゃってた…", 8, 0, 1, 3026, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109301, 1, "新しい建物に入ると\n必ず一度は迷子に\nなっちゃうですよ…", 1, 0, 1, 3028, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109301, 1, "おやつはどこですか？\nお腹が空いて\nきちゃったですぅ", 2, 0, 1, 3029, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109301, 1, "宿題、７つぐらい\n出しそびれたですけど\nまあ大丈夫ですぅ！", 3, 0, 1, 3030, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109301, 1, "不思議なこと\nばかりですけど…\nまぁ大丈夫です！\nこれもせいしゅんです", 4, 0, 1, 3031, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109301, 1, "なにか面白そうなもの\nないですかね～？", 5, 0, 1, 3032, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109301, 1, "ここのみんなとも\nもうお友だちですぅ！", 101, 0, 4, 3033, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109301, 1, "おなかが減ったです…", 102, 0, 8, 3034, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109301, 1, "ごはんも美味しいし\nここはいいところです\nルゥは幸せですぅ！", 1, 0, 1, 3035, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109301, 1, "魔法の話ですか？\nそれはアンせんぱいに\n聞いてほしいですぅ…", 2, 0, 1, 3036, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109301, 1, "あなたもお勉強\n苦手ですか？", 3, 0, 1, 3037, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109301, 1, "ルゥの帽子、\nかわいいです？\nお気に入りですぅ♪", 4, 0, 1, 3038, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109301, 1, "お友だちいっぱいで、\nルゥはうれしいです♪", 101, 0, 4, 3039, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109301, 1, "一緒にお勉強…！？\nぐえぇ、イヤですぅ！", 102, 0, 8, 3040, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109301, 1, "朝ですねぇ…ふわわ…\nルゥはまだ夢の中に\nいたいですぅ…", 1, 1, 1, 3041, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109301, 1, "午後もいっぱい\n遊ぶですよ～！", 2, 2, 1, 3042, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109301, 1, "夜ふかしって\nドキドキするです！\nせいしゅんですぅ！", 3, 3, 1, 3043, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109301, 1, "ふかふかですぅ～♪\nおひるねが\nしたくなるですぅ！", 2, 0, 1, 3045, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109301, 1, "これふかふかですぅ！\n机でうとうとする時の\n枕によさそうですぅ", 3, 0, 1, 3046, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109301, 1, "ふぁぁ、いいにおい…\nなんだか気持ちが\nよくなってきたですぅ", 4, 0, 1, 3047, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109301, 1, "ぽっかぽかですぅ～！\nあったまったら\nまた遊びにいくですぅ", 5, 0, 1, 3048, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109301, 1, "お、おいしいですぅ！\n全部ルゥに\nくださいですぅ！", 7, 0, 1, 3050, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109301, 1, "本は…読むと、\nすぐ眠気がぁ…\n…ぐぅ…", 8, 0, 1, 3051, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109401, 1, "ピアノ…\nどこかにないかな？", 1, 0, 1, 3053, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109401, 1, "ふぅ…\n落ち着くな…", 2, 0, 1, 3054, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109401, 1, "マナリアはいつも\n賑やかなんだ…", 3, 0, 1, 3055, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109401, 1, "みんな…\n今頃何してるかな？", 4, 0, 1, 3056, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109401, 1, "まさか違う世界に\n来ちゃうなんて…\nびっくりだよね…", 5, 0, 1, 3057, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109401, 1, "人と話すのは\n得意じゃないな…", 101, 0, 4, 3058, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109401, 1, "ここのみんなと一緒に\n学院に通えたら…\nちょっと面白いかも", 102, 0, 8, 3059, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109401, 1, "やっぱり、私の翼や\n尻尾…気になる？", 1, 0, 1, 3060, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109401, 1, "一番得意なのは、\nやっぱり炎魔法かな…\n加減は難しいけど…", 2, 0, 1, 3061, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109401, 1, "私の故郷は竜の国…\n険しい山脈を\n越えた先にあるんだ", 3, 0, 1, 3062, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109401, 1, "アン…？\nあっ、ご、ごめんね！\n間違えちゃった…", 4, 0, 1, 3063, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109401, 1, "ふん…ふん…♪\n…わっ！\n今の、聞いてた…？\nは、恥ずかしい…", 101, 0, 4, 3064, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109401, 1, "私に手伝えること、\nなにかある…？\nなんでも言ってね", 102, 0, 8, 3065, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109401, 1, "朝の空気って\nすごく\n静かで透き通ってて…\nなんだか落ち着くよね", 1, 1, 1, 3066, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109401, 1, "学院食堂のランチも\n美味しいけど…\nここのご飯も好きかな", 2, 2, 1, 3067, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109401, 1, "そろそろベッドに\n行かないとだね\n…寝相、今夜こそ\n気を付けよう…！", 3, 3, 1, 3068, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109401, 1, "羽を伸ばしたく\nなっちゃうね…ふふ", 2, 0, 1, 3070, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109401, 1, "わ、私が持つには\nかわいすぎるかな…\nでも、素敵だね", 3, 0, 1, 3071, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109401, 1, "いい香り…\nこれ、\nアンも好きそう…", 4, 0, 1, 3072, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109401, 1, "火を見ていると\n少し落ち着くんだ…\nへ、変かな？", 5, 0, 1, 3073, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109401, 1, "わ、美味しい…！\nこれ、どうやって\n作ったのかな…？", 7, 0, 1, 3075, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109401, 1, "この本…アンにも\n見せてあげたら\n喜ぶかな、ふふっ", 8, 0, 1, 3076, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109501, 1, "着物はきれいですが\n歩きにくいです…\nいっそ脱いで…ぐふふ", 1, 0, 1, 3078, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109501, 1, "あっ、いけません\n着物がはだけて…はっ\nもしやドＳさんが…！", 2, 0, 1, 3079, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109501, 1, "この部屋は妄想する\nのにうってつけです…\nぐふふ…じゅるり", 3, 0, 1, 3080, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109501, 1, "扇をゆっくりと開く\nこの感触…なぜだか\n艶めかしさを感じます", 4, 0, 1, 3081, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109501, 1, "「マッサージをすると\n言いながらドＳさんの\n手は次第に」…ぐふふ", 5, 0, 1, 3082, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109501, 1, "…ドＳさんの視線を\n思い出しながら踊りの\n練習です…イイ！", 101, 0, 4, 3083, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109501, 1, "よいではないか\nよいではないか～\nいけませんご堪忍を～", 102, 0, 8, 3084, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109501, 1, "あ～れ～～～！\nドＳさんもお好きな人\nですねぇ…ぐふふ", 1, 0, 1, 3085, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109501, 1, "どこにいてもドＳさん\nの気配を感じ取れる\nようになりました…", 2, 0, 1, 3086, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109501, 1, "こんな場所で\nなんというプレイを！\n…妄想でした", 3, 0, 1, 3087, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109501, 1, "クウカを押し倒して\nそのまま…じゅるり\nああっ、着物が…！", 4, 0, 1, 3088, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109501, 1, "ドＳさんは優しいです\n優しいのにドＳとは\nさすが人格破綻者…！", 101, 0, 4, 3089, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109501, 1, "ドＳさんの考えている\nことが伝わってきます\nはい…クウカ脱ぎます", 102, 0, 8, 3090, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109501, 1, "眩しい朝日がクウカの\n体を焦がします\n…それもイイ！", 1, 1, 1, 3091, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109501, 1, "お昼のランチは\nクウカがいいだなんて\n大胆過ぎです…じゅる", 2, 2, 1, 3092, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109501, 1, "…今夜のドＳさんは\n大胆です…ぐふふ…\nはっ、視線が…！？", 3, 3, 1, 3093, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109501, 1, "この心地良さ、\n心が休まりすぎて\n体が疼いてきます…！", 2, 0, 1, 3095, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109501, 1, "すごく愛らしいです…\n妄想も出てこない\nくらい…じゅるり", 3, 0, 1, 3096, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109501, 1, "この香りに包まれて\nいると、いい気持ちに\nなって…いきますぅ…", 4, 0, 1, 3097, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109501, 1, "「熱を帯びた体は\nどうしようもなく\n誰かを求め…」ぐふふ", 5, 0, 1, 3098, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109501, 1, "お腹が膨らんで\nいきます…それは\nまるで…ぐふふ", 7, 0, 1, 3100, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109501, 1, "クウカの探している\nジャンルはどこに\nありますかね？", 8, 0, 1, 3101, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109601, 1, "カチョーフーゲツは\nトーゴクの心を\n表した言葉デスね", 1, 0, 1, 3103, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109601, 1, "オー！　新しい忍法を\n思いついたデス！\nさっそく試すデス！", 2, 0, 1, 3104, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109601, 1, "トーゴクの天気には\n槍が降る日も\nあるそうデス…", 3, 0, 1, 3105, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109601, 1, "我がショーグンは\n永遠にフメツデース\nいざ出陣デース！", 4, 0, 1, 3106, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109601, 1, "壁に耳あり\n障子に…ん？\nメアリーって誰デス？", 5, 0, 1, 3107, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109601, 1, "ショーグンがいれば\nワタシはどんな\n忍法も使いこなせマス", 101, 0, 4, 3108, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109601, 1, "不届き者はどこで\nゴザルデス？　忍法で\n退治してくれヨウ！", 102, 0, 8, 3109, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109601, 1, "ショーグン！　新しい\n忍法を覚えたデス！\n見てほしいデース！", 1, 0, 1, 3110, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109601, 1, "レッツ手裏剣道デス！\n投げてソウロウで\nゴザルデース！", 2, 0, 1, 3111, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109601, 1, "ふりかかる火の粉は\nワタシが払うデス！\nバンバン！", 3, 0, 1, 3112, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109601, 1, "とっておきの忍法が\n吹き荒れるデス！\nよそ見はダメデスヨ？", 4, 0, 1, 3113, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109601, 1, "ショーグンは\n何階建てのお城を\n建てたいデスか？", 101, 0, 4, 3114, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109601, 1, "テンシュカクから\n平和な城下町を\n眺めてみたいデス…", 102, 0, 8, 3115, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109601, 1, "おはようございマス！\n本日はオヒガラモヨク\n良い日になりそうデス", 1, 1, 1, 3116, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109601, 1, "お腹すいたデース…\nショーグンもご飯に\nしまショウ！", 2, 2, 1, 3117, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109601, 1, "闇討ちに遭わぬよう\n夜は気を付けて\n歩くデスよー？", 3, 3, 1, 3118, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109601, 1, "天下分け目の\n戦いに備えて…\nマッタリデース", 2, 0, 1, 3120, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109601, 1, "口寄せの術で\n召喚したいくらい\nかわいいデース", 3, 0, 1, 3121, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109601, 1, "いい匂いデ～ス\n香り玉の開発の\n参考になりそうデスね", 4, 0, 1, 3122, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109601, 1, "ワタシの懐よりも\nゾーリが暖まりマス…\nライバル出現デス…！", 5, 0, 1, 3123, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109601, 1, "腹が減っては\n戦はできぬデース！\n天下統一に向けて\n腹ごしらえデス！", 7, 0, 1, 3125, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109601, 1, "ムム、暗号文デス！？\n解読には時間が\nかかりそうデース…", 8, 0, 1, 3126, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109701, 1, "掃除も終わりましたし\nそろそろ、食事の\n準備をしなくては", 1, 0, 1, 3128, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109701, 1, "異世界の演劇…\n許されるならば、\n観てみたいです", 2, 0, 1, 3129, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109701, 1, "こちらの世界には、\nマヨネーズが普通に\n存在するのですね…", 3, 0, 1, 3130, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109701, 1, "こちらの世界は食材も\n面白い物が多いですね\nなにかレシピを\n考えてみましょうか", 4, 0, 1, 3131, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109701, 1, "みょんみょんみょん…\n魔法の練習は\nこれぐらいですね", 5, 0, 1, 3132, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109701, 1, "倒木の撤去で思わぬ\n収入となりました\nサレン様に\nお渡ししておかないと", 101, 0, 4, 3133, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109701, 1, "料理を美味しく\n召し上がって頂けると\nメイド冥利につきます", 102, 0, 8, 3134, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109701, 1, "お出かけでしたら、\nレムも同行\nさせてくださいね", 1, 0, 1, 3135, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109701, 1, "レムにできること\nでしたら、なんでも\nお申し付けください", 2, 0, 1, 3136, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109701, 1, "じーー。いえ、\nその髪の長さが少し、\nとても少し、かなり\n少し気になったもので", 3, 0, 1, 3137, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109701, 1, "鉄球ですか？\nあれは護身用ですので\n問題はありませんよ？", 4, 0, 1, 3138, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109701, 1, "計算の勉強は\n進んでおりますか？\nレムでよろしければ、\nいつでもお教えします", 101, 0, 4, 3139, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109701, 1, "次はもっと、大きな\n魔物料理に挑戦します\nワイバーンの姿焼き\nなんてどうでしょうか", 102, 0, 8, 3140, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109701, 1, "おはようございます\n朝食の準備が\nできておりますので、\n食卓におこしください", 1, 1, 1, 3141, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109701, 1, "昼食の時間ですね\n何か食べたいものは\nありますか？", 2, 2, 1, 3142, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109701, 1, "寝ないのか、ですか？\nメイドの就寝は、\n屋敷で最後と\n決まっておりますので", 3, 3, 1, 3143, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109701, 1, "ふんわり…\nこのふんわりさは、\n鬼をも黙らせます…", 2, 0, 1, 3145, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109701, 1, "ふふっ…異世界でも\nかわいいものは\n共通なのですね", 3, 0, 1, 3146, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109701, 1, "よい香りですね\n魔女教の悪臭とは\n大違いです", 4, 0, 1, 3147, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109701, 1, "ぬくぬくです…はっ！\nこれはいけません！\n人を…いえ、\n鬼をだめにします！", 5, 0, 1, 3148, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109701, 1, "はむはむ…美味しい！\nこの味を再現できたら\n褒めてもらえる\nかもしれません…！", 7, 0, 1, 3150, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109701, 1, "絵がいっぱい…\n異世界の本は\nとても面白いですね", 8, 0, 1, 3151, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109801, 1, "異世界であろうと、\nラムのやるべきことは\n変わらないわ\nまずは休憩するわよ", 1, 0, 1, 3153, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109801, 1, "マヨネーズがある\nということは、\nバルスの故郷が近い\nのかもしれないわね", 2, 0, 1, 3154, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109801, 1, "この世界では、バルス\nの言う言葉が使われる\nときがあるのね\n忌々しいかぎりだわ", 3, 0, 1, 3155, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109801, 1, "ラムたち以外にも\nメイドが居るのね\nラムが…いえ、\n仕事を押しつけるわ", 4, 0, 1, 3156, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109801, 1, "…千里眼で視るかぎり\n周囲に異変はないわね\n危険がないなら、\nあとはレムに任せるわ", 5, 0, 1, 3157, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109801, 1, "…ラムもまた、\n「お姉ちゃん」って\n呼ばれる日が\nくるのかしらね", 101, 0, 4, 3158, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109801, 1, "ハッ！　ラムが作る\n蒸かし芋は完璧ね\n…気が向いたら、また\n作ってやろうかしら", 102, 0, 8, 3159, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109801, 1, "ラムに構う暇が\nあるなら働きなさい\nそうすれば、\nラムが楽をできるわ", 1, 0, 1, 3160, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109801, 1, "ラムに何かを\n期待しても無駄よ\nラムに命令できるのは\nロズワール様だけだわ", 2, 0, 1, 3161, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109801, 1, "暇なら買い物に\n付き合いなさい\n荷物を\n持たせてあげるわ", 3, 0, 1, 3162, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109801, 1, "ラムになんの用？\nふざけた内容なら\nねじ切るわよ", 4, 0, 1, 3163, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109801, 1, "そういえば、あなたは\n記憶喪失だったわね…\n思い出す努力をなさい\n…家族が悲しむわよ", 101, 0, 4, 3164, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109801, 1, "蒸かし芋が欲しい？\n随分と厚かましいわね\n…まだ残りがあるから\n好きに食べなさい", 102, 0, 8, 3165, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109801, 1, "だらしない顔ね\n顔を洗って\nしゃんとなさい", 1, 1, 1, 3166, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109801, 1, "お腹が空く時間ね\nラムの昼食を\n用意させてあげるわ", 2, 2, 1, 3167, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109801, 1, "メイドが寝るのは\n屋敷で最後…\nラムが寝るために、\nさっさと寝なさい", 3, 3, 1, 3168, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109801, 1, "…ここから動かない\nという、誓いを\n立てたくなるわね", 2, 0, 1, 3170, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109801, 1, "これは…かわいいわね\nもちろん、\nラムほどではないけど", 3, 0, 1, 3171, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109801, 1, "…香炉かしら？\nこちらの世界にも\n質のよい物があるのね", 4, 0, 1, 3172, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109801, 1, "見つけてしまったの\nかもしれないわ…\nラムの楽園を！", 5, 0, 1, 3173, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109801, 1, "…はむっ\nラムの蒸かし芋ほど\nではないけど、\nなかなかいけるわね", 7, 0, 1, 3175, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109801, 1, "異世界ならではの\n面白い内容もあるわね\nとても興味深いわ", 8, 0, 1, 3176, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109901, 1, "ねえパック、\nにゃんにゃか\nにゃかにゃんしよっか", 1, 0, 1, 3178, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109901, 1, "時間もあるし、\nこっちの世界の石で\n石積みしようかな", 2, 0, 1, 3179, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109901, 1, "私もお姉さんだし、\nサレンさんを\n見習わないと…うん！\n私、すごーく頑張る！", 3, 0, 1, 3180, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109901, 1, "この世界は、銀髪で\nハーフエルフでも受け\n入れてくれるのね…\nなんだか嬉しいな", 4, 0, 1, 3181, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109901, 1, "今日のご飯…\nピーマルは\n入ってないわよね…？", 5, 0, 1, 3182, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109901, 1, "また、みんなで\n一緒に買い物に\n行きたいな", 101, 0, 4, 3183, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109901, 1, "パック以外から\n家族って呼ばれたの\n初めて…ふふっ♪\nちょっと嬉しいかも！", 102, 0, 8, 3184, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109901, 1, "今からパックの\n毛繕いをするけど…\n一緒にする？", 1, 0, 1, 3185, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109901, 1, "私も何かお手伝いを…\nあ、そうだ！\n料理とかどうかな！", 2, 0, 1, 3186, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109901, 1, "ねえ、ここで\n歌の練習って、\nしてもいいかな？", 3, 0, 1, 3187, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109901, 1, "…あ、ごめんなさい！\n話しかけられるのって\nあまり慣れてなくて…", 4, 0, 1, 3188, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109901, 1, "すごーく頑張るから、\nこれからも勉強を\n手伝ってね", 101, 0, 4, 3189, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109901, 1, "せーの、びくとりー！\nふふっ…また、一緒に\nラジーオタイソー\nしようね♪", 102, 0, 8, 3190, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109901, 1, "ちゃんと起きてる？\nふふっ…よかった。\nあ、べ、別に心配した\nわけじゃないからね？", 1, 1, 1, 3191, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109901, 1, "お昼ご飯が楽しみね♪\nあ、こっちの世界って\nまよねーずはあるの？", 2, 2, 1, 3192, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109901, 1, "そろそろ寝る時間よ。\n夜更かししたら、\nめっ！　だからね！", 3, 3, 1, 3193, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109901, 1, "勉強も終わったし…\nちょっ、ちょっとだけ\n休んでもいいよね…？", 2, 0, 1, 3195, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109901, 1, "か、かわいい…！\nパックのかわいさに\n匹敵するかも…！", 3, 0, 1, 3196, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109901, 1, "良い香り…\nロズワールの屋敷にも\nこういうのって\nあるのかな？", 4, 0, 1, 3197, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109901, 1, "あったかい…\nずっとここに…はっ！\nダメダメ！　王選の\nために勉強しないと！", 5, 0, 1, 3198, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109901, 1, "あ…これ美味しい！\nこっちの世界は色んな\n食べ物があるのね。", 7, 0, 1, 3200, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 109901, 1, "知らないことが\nいっぱい書いてある…\n異世界の本って、\n面白いのね", 8, 0, 1, 3201, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110001, 1, "海ってマジ最高！\nビーチの撮影も\n超楽しいしー♪", 1, 0, 1, 3203, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110001, 1, "20パーって何パ？\n今度ヒデサイに\n聞いてみよっ！", 2, 0, 1, 3204, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110001, 1, "夏の思い出を\nヒデサイと一緒に\n作りたいな～", 3, 0, 1, 3205, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110001, 1, "テンションあがる\n水着ゲットして\n超ラッキーだよ～♪", 4, 0, 1, 3206, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110001, 1, "休憩が終わったら\n撮影にもどらないと！\n気合入れてこ♪", 5, 0, 1, 3207, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110001, 1, "ヒデサイとビーチで\nパーティーしたいな～\n超楽しそうだよね！", 101, 0, 4, 3208, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110001, 1, "ヒデサイといると\n楽しくてうれしくて…\nカレシってこんな感じ\nなのかなぁ？", 102, 0, 8, 3209, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110001, 1, "見て見て～！\n夏らしいネイルに\nしてみたんだ～♪", 1, 0, 1, 3210, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110001, 1, "ヒデサイ～\nこの水着どうかな？\nイケてるっしょ♪", 2, 0, 1, 3211, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110001, 1, "水着姿の写真？\nもちいいよ！\nかわいく撮ってね！", 3, 0, 1, 3212, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110001, 1, "うちだって\n夏の海みたいに\nキラキラしちゃうよ！", 4, 0, 1, 3213, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110001, 1, "ヒデサイとうちの\nフィーリングって\nめっちゃエモいよね♪", 101, 0, 4, 3214, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110001, 1, "ねーねーヒデサイ！\n一緒に泳ぎにいこっ！\nほら、手繋いで♪", 102, 0, 8, 3215, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110001, 1, "水着オッケー！\nネイルもばっちり！\n朝から遊んじゃおー♪", 1, 1, 1, 3216, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110001, 1, "お昼食べたら\n昼寝して\nリフレッシュしよー♪", 2, 2, 1, 3217, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110001, 1, "水着ってマジで\n最強かも…このまま\nお風呂にも入れるし♪", 3, 3, 1, 3218, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110001, 1, "マジ気持ちいいー♪\nおに癒やされるん\nですけど～！", 2, 0, 1, 3220, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110001, 1, "やばばばばー！\nかわいすぎだってば！\nマジらぶだよ～♪", 3, 0, 1, 3221, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110001, 1, "あ、この香り超好き！\nモデル仲間に\n教えたげよ～♪", 4, 0, 1, 3222, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110001, 1, "あったか～い♪\n夏の太陽みたい！", 5, 0, 1, 3223, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110001, 1, "マジ美味しいねこれ♪\nビーチで\n流行っちゃうかも！", 7, 0, 1, 3225, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110001, 1, "夏のために\n最新ファッションを\n勉強ちう！", 8, 0, 1, 3226, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110001, 1, "なにコレもにカワ～♪\nなんて生き物？\nヒデサイ知ってる？", 10, 0, 1, 3228, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110001, 1, "うわっ超フカフカ～\nなんかうち眠く\nなってきちゃった…", 11, 0, 1, 3229, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110101, 1, "海の家でのんびり\n過ごしましょう\n熱中症には注意してね", 1, 0, 1, 3230, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110101, 1, "はぁ、どうやったら\n泳げるように\nなれるのかしら～", 2, 0, 1, 3231, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110101, 1, "海で日焼け止めを\n塗らないと、あとで\n大変なことになるわよ", 3, 0, 1, 3232, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110101, 1, "久しぶりに水着を\n着たんだけど\n変じゃなかったかな…", 4, 0, 1, 3233, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110101, 1, "波の音って落ち着くわ\n授業中に聞いたら\nみんな寝ちゃうかも？", 5, 0, 1, 3234, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110101, 1, "海が楽しいって\n思えたのも\nあの子のおかげね", 101, 0, 4, 3235, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110101, 1, "あの子と二人で海に\n来られてよかったわ\n一日があっという間ね", 102, 0, 8, 3236, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110101, 1, "男の子と海だなんて\n少女漫画みたいで\n憧れてたの～", 1, 0, 1, 3237, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110101, 1, "夏休みの宿題は\nちゃんとやった？\n遊んでばかりではダメ", 2, 0, 1, 3238, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110101, 1, "昼の海と夜の海\nどっちが好き？　私は\n君といる海が一番よ", 3, 0, 1, 3239, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110101, 1, "ふう、疲れたわ～\n冷たいジュースで\n元気回復ね！", 4, 0, 1, 3240, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110101, 1, "海で見た君の背中\nすごく大きく見えたわ\nやっぱり男の子なのね", 101, 0, 4, 3241, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110101, 1, "君と一緒に見た\n夜の海…\n忘れられそうにないわ", 102, 0, 8, 3242, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110101, 1, "準備ができたら\n海に出かけようよ\n今日も楽しみましょう", 1, 1, 1, 3243, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110101, 1, "あら、もうお昼？\n海の家でランチに\nしましょ♪", 2, 2, 1, 3244, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110101, 1, "夜の海岸についてきて\nくれる？　暗がりは\n一人じゃ怖くて…", 3, 3, 1, 3245, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110101, 1, "泳ぐ練習をしたら\n疲れちゃったわ～\nちょっと一休み…", 2, 0, 1, 3247, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110101, 1, "かわいいわね～\n浮き輪に乗せて\n海に浮かべたいわ～", 3, 0, 1, 3248, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110101, 1, "この匂い…\nなんだか夏の海を\n思い出すわ～", 4, 0, 1, 3249, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110101, 1, "夜の海風で冷えない\nように、しっかりと\n身体を暖めましょうね", 5, 0, 1, 3250, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110101, 1, "海で遊ぶ前に\nしっかり食べて\n体力つけましょう", 7, 0, 1, 3252, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110101, 1, "夏休み中でも\nしっかりお勉強\nしないと！", 8, 0, 1, 3253, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110101, 1, "この子に乗れば\n海の上でも\nすいすい進めるかしら", 10, 0, 1, 3255, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110101, 1, "ビーナスの誕生！\n…なんちゃって\nはしゃぎすぎかしら？", 11, 0, 1, 3256, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110301, 1, "夏の海には商機が\nあるはず…\nのがす手はないわね♪", 1, 0, 1, 3257, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110301, 1, "あたしが作った\n日焼け止め…\nこれは売れそうね！", 2, 0, 1, 3258, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110301, 1, "子どもたちのために\n浮き輪を用意して\nおかないと…", 3, 0, 1, 3259, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110301, 1, "海っていいわよね！\nみんなで羽を伸ばしに\n行きたいわ！", 4, 0, 1, 3260, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110301, 1, "波の音を聞いて\nのんびりするのも\n悪くないわね…", 5, 0, 1, 3261, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110301, 1, "ただの砂遊びも\nあんたと一緒なら\n楽しいから不思議ね…", 101, 0, 4, 3262, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110301, 1, "あんたがあたしの\nなんであれ、大切な\n存在ではあるのよね…", 102, 0, 8, 3263, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110301, 1, "夏の海って\nすごい人よね…\n迷子にならないでよ？", 1, 0, 1, 3264, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110301, 1, "ぼんやり海に浮かんで\n波に揺られるのも\n悪くないわよ？", 2, 0, 1, 3265, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110301, 1, "ビーチで子どもたちと\n一緒に砂のお城を\n作りたいわ♪", 3, 0, 1, 3266, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110301, 1, "海で食べる\n焼きそばパン…\nいいかもしれないわね", 4, 0, 1, 3267, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110301, 1, "あたしの水着\n似合ってる？\nそう、ありがとう♪", 101, 0, 4, 3268, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110301, 1, "あんたが居ると\nうれしい…って、\n子どもたちが\n言ってたわ！", 102, 0, 8, 3269, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110301, 1, "朝の浜辺って\nいいわよね\n散歩しに行きたいわ", 1, 1, 1, 3270, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110301, 1, "海の家のご飯って\nなんであんなに\nおいしいのかしら♪", 2, 2, 1, 3271, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110301, 1, "夜の海って、\nロマンチックよね。\nずっと眺めてたいわ…", 3, 3, 1, 3272, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110301, 1, "やわらかくて\n気持ちいい…\nこのまま寝ちゃいそう", 2, 0, 1, 3274, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110301, 1, "海に一緒に入れる\nぬいぐるみとか\nないかしら？", 3, 0, 1, 3275, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110301, 1, "夏らしい\nいい香りね…\nなんの香りかしら？", 4, 0, 1, 3276, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110301, 1, "あったかい…\n濡れた体を乾かすには\nちょうどいいわね♪", 5, 0, 1, 3277, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110301, 1, "泳いだ後の食事は\n普段の何倍も\nおいしく感じるわね！", 7, 0, 1, 3279, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110301, 1, "海に行く前にいろいろ\n調べなきゃね。備え\nあれば憂いなしよ！", 8, 0, 1, 3280, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110301, 1, "海の守り神？\nずいぶんかわいらしい\n神様ね♪", 10, 0, 1, 3282, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110301, 1, "このぐらい\n大きな貝があったら\n食費も浮くかしら？", 11, 0, 1, 3283, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110401, 1, "浜辺で食うものって\nなんであんなに\nうまいのかな～", 1, 0, 1, 3284, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110401, 1, "いてて…\nちょっと日焼け\nしすぎたかな", 2, 0, 1, 3285, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110401, 1, "砂浜で走ると\nいいトレーニングに\nなるんだぜ", 3, 0, 1, 3286, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110401, 1, "夏ならではの\n料理…か\n何がいいかな…", 4, 0, 1, 3287, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110401, 1, "やっぱりここは\n涼しくて快適だな", 5, 0, 1, 3288, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110401, 1, "泳ぎの練習だったら\nいつでも付き合って\nやるからな！", 101, 0, 4, 3289, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110401, 1, "水着で部屋の中に\nいるのって\n変な感じだな…", 102, 0, 8, 3290, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110401, 1, "うおっ！\nきゅ、急に\n触るなって…\nびっくりした", 1, 0, 1, 3291, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110401, 1, "え、砂がついてた？\n払ってくれたのか\nありがとよ", 2, 0, 1, 3292, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110401, 1, "夏バテしてないか？\nここで休んでけよ", 3, 0, 1, 3293, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110401, 1, "…そう見られると\n照れるっていうか…", 4, 0, 1, 3294, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110401, 1, "カキ氷とか\n食いたくないか？\n氷、削ってやるよ", 101, 0, 4, 3295, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110401, 1, "おまえと\n夏の思い出ができて\nよかったよ", 102, 0, 8, 3296, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110401, 1, "ふう…\n朝一番で泳ぐの\nやっぱ気持ちいいな", 1, 1, 1, 3297, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110401, 1, "一番あちぃ時間だ\n水分補給は\nこまめにな！", 2, 2, 1, 3298, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110401, 1, "今日もお疲れ\n明日のパトロールも\nよろしくな", 3, 3, 1, 3299, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110401, 1, "泳ぎ疲れたからな\n少し休憩だ…", 2, 0, 1, 3301, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110401, 1, "か、かわいい…\nいや、なんでもねぇ！", 3, 0, 1, 3302, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110401, 1, "この香り落ち着くな\nこういうのも\n悪くねーかも…", 4, 0, 1, 3303, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110401, 1, "あったけえ…！\n泳ぎ終わった後って\nけっこう体冷えてよ…", 5, 0, 1, 3304, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110401, 1, "海で食べると\nまた一味違うよな", 7, 0, 1, 3306, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110401, 1, "夏のサバイバル特集…\nちょっと読んでみるか", 8, 0, 1, 3307, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110401, 1, "へー！　こいつ、\nよくできてんな！\nいいなあ、実物も\n乗れたらいいのに", 10, 0, 1, 3309, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110401, 1, "か、かわ…！\nいや、じゃなくて\nあ、あたしがここに\n寝ていいのか…？", 11, 0, 1, 3310, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110501, 1, "故郷の花を飾っても\nいいかな～？\n部屋が楽しくなるよ～", 1, 0, 1, 3311, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110501, 1, "水着で歩いてると\n泳ぎたくなるさ～\n我慢できないさ～！", 2, 0, 1, 3312, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110501, 1, "夏が眩しいのは\nてぃーだがにこにこ\n笑ってるからさ～！", 3, 0, 1, 3313, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110501, 1, "泳ぎ疲れたら\n涼しい木陰で\nお昼寝するさ～", 4, 0, 1, 3314, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110501, 1, "クロールも\n犬かきも\n大得意さ～！", 5, 0, 1, 3315, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110501, 1, "なんだか歌いたい\n気分さ～\nキミもどう～？", 101, 0, 4, 3316, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110501, 1, "海の近くに住んで\nキミと歌って踊って\nそれが理想さ～！", 102, 0, 8, 3317, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110501, 1, "私とキミ\nどっちがたくさん\n泳げるか\n競争するさ～！", 1, 0, 1, 3318, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110501, 1, "キミも三線に興味が\nあるのかな～？\n私が教えるさ～！", 2, 0, 1, 3319, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110501, 1, "嫌なことがあっても\n私と歌って踊れば\nなんくるないさー！", 3, 0, 1, 3320, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110501, 1, "たまには時間を忘れて\n私とのーんびり\n海を眺めるさ～", 4, 0, 1, 3321, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110501, 1, "ぶるぶるぶるっ！\n毛が濡れちゃっても\nへっちゃらさ～！", 101, 0, 4, 3322, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110501, 1, "泳ぐ前は準備体操～！\n背中押してあげるから\nしっかりやるさ～！", 102, 0, 8, 3323, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110501, 1, "朝の浜辺は\nちゅらさんよ～\n一緒に歩こうよー！", 1, 1, 1, 3324, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110501, 1, "お昼ご飯は\n決まったかな～？\n私はタコライスさ～！", 2, 2, 1, 3325, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110501, 1, "星と波音の中で\n三線鳴らすのも\nオツなものさ～", 3, 3, 1, 3326, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110501, 1, "ちょうどよかったさー\nゆっくり休んだら\nまた泳ぎに行くさ～！", 2, 0, 1, 3328, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110501, 1, "砂浜に飾って\nおきたいさ～\nきっとかわいいさ～！", 3, 0, 1, 3329, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110501, 1, "くんくん…はあ\n癒されるさ…\n故郷の夏の匂いさ～", 4, 0, 1, 3330, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110501, 1, "おおー…！\n濡れた髪が\nみるみる乾いてくさ～", 5, 0, 1, 3331, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110501, 1, "ん～！　これ、\n海で食べたら\nもっとおいしいさ～", 7, 0, 1, 3333, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110501, 1, "海の生物図鑑ね～\nこの前みかけた魚も\n載ってるかな～？", 8, 0, 1, 3334, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110501, 1, "わぁ～！　みてみて！\n背中に乗れるさー！\nキミと一緒に\n乗りたいさ～！", 10, 0, 1, 3336, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110501, 1, "ふかふかさ～\n海の底の\n人魚になった気分さ～", 11, 0, 1, 3337, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105801, 1, "あの人が\nわたしの騎士だったら\nいいのにな…", 103, 0, 12, 3338, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105801, 1, "あなたのおかげで\nわたしの心はいつも\nお腹いっぱいです♪", 103, 0, 12, 3339, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105901, 1, "許されるのならば、\n今すぐにでも、\n主さまのおそばに\n行きたいです…", 103, 0, 12, 3340, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105901, 1, "主さま…\nお慕いしております…\nいつまでも、おそばに\n居させてください…", 103, 0, 12, 3341, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106001, 1, "なんか最近、\nあいつのことしか\n考えてない…\nあたし…どうしよう…", 103, 0, 12, 3342, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106001, 1, "そ、外に行くなら、\n連れていきなさいよね\nその…あたしが\n守ってあげるから…", 103, 0, 12, 3343, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110601, 1, "部屋の中は涼しくて\n気持ちええどすなぁ\n天国やわぁ", 1, 0, 1, 3344, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110601, 1, "クラゲはんって\nフリルいっぱいの\nドレスみたいやねぇ", 2, 0, 1, 3345, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110601, 1, "外でセミはんが\n元気に鳴いてはるねぇ\n今日もええ天気やわぁ", 3, 0, 1, 3346, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110601, 1, "こないに暑いと\n海に入って\n涼みたくなるわぁ", 4, 0, 1, 3347, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110601, 1, "いつか\nマホマホ王国にも\n海の家作りまひょ♪", 5, 0, 1, 3348, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110601, 1, "うちも\n付き合いますえ\n泳ぎの練習しまひょ♪", 101, 0, 4, 3349, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110601, 1, "うちの里帰り\n王子はんも一緒に\n来まへんか？", 102, 0, 8, 3350, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110601, 1, "王子はんは日焼けより\n色白美人の方が\n好きでっしゃろか？", 1, 0, 1, 3351, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110601, 1, "うちと一緒に\n浮き輪で海、\nプカプカしまへん？", 2, 0, 1, 3352, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110601, 1, "日が落ちたら\n線香花火\nやりたいわぁ♪", 3, 0, 1, 3353, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110601, 1, "木陰でのんびり\nお昼寝…\n最高やね～", 4, 0, 1, 3354, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110601, 1, "お祭ではぐれても\n王子はんなら見つけて\nくれはります？", 101, 0, 4, 3355, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110601, 1, "楽しい夏やわぁ\nずっとこんな日が\nつづくとええね", 102, 0, 8, 3356, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110601, 1, "うちにしては早起き？\n暑くて目が早う\n覚めてもうたんどす", 1, 1, 1, 3357, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110601, 1, "お昼は冷たい\nおそうめんとか\nどうでっしゃろ", 2, 2, 1, 3358, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110601, 1, "夜は星がきれいやね\n二人で浜辺でも\n行きまへんか？", 3, 3, 1, 3359, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110601, 1, "うーん、遊び疲れた\n身体にちょうどええ\nほっこり具合やわぁ", 2, 0, 1, 3361, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110601, 1, "かいらしい子は\nみーんな\nマホマホ王国の\n住人どすえ♪", 3, 0, 1, 3362, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110601, 1, "この香り…\n南国にいる気分やねぇ\n癒されるわぁ～", 4, 0, 1, 3363, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110601, 1, "これで\n冷えた身体も\nポカポカやねぇ", 5, 0, 1, 3364, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110601, 1, "外はまだ暑いやろうし\nバテんよう\nしっかり食べんとね", 7, 0, 1, 3366, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110601, 1, "海の中にもお城が\nあるんやねぇ\n行ってみたいわぁ", 8, 0, 1, 3367, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110601, 1, "この子、ふかふかやね\nこのままマホマホ王国\n乗せてってくれはる？", 10, 0, 1, 3369, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110601, 1, "人魚姫になっても\n最後は王子はんと\nハッピーエンド\nどすえ～", 11, 0, 1, 3370, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107001, 1, "真似したくなる\n人がここには\n大勢いますね", 1, 0, 1, 3371, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107001, 1, "偽物を一人\n紛れこませました\n…冗談です", 2, 0, 1, 3372, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107001, 1, "動物に変身して\nその辺りを\n散歩してきます", 3, 0, 1, 3373, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107001, 1, "失った記憶は\nどの程度回復するの\nでしょうね", 4, 0, 1, 3374, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107001, 1, "人や物だけでなく\nいずれは世界すら\n真似てみせます", 5, 0, 1, 3375, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107001, 1, "次は彼に\nどんなことを\n教えましょうか…", 101, 0, 4, 3376, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107001, 1, "知識はともかく\n私の力はまだ\n必要なようですね", 102, 0, 8, 3377, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107001, 1, "どうしました？\n…何でもない？\n…そうですか", 1, 0, 1, 3378, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107001, 1, "退屈しています\n芸でもして\n楽しませなさい", 2, 0, 1, 3379, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107001, 1, "研究のためです\n少し話し相手に\nなりなさい", 3, 0, 1, 3380, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107001, 1, "何か真似して欲しい？\n私の能力を何だと\n思っているのですか", 4, 0, 1, 3381, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107001, 1, "貴方にはまだ色々と\n教えるべきことが\nありますね", 101, 0, 4, 3382, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107001, 1, "随分多くの女性と\n知り合いなのですね\n驚きました", 102, 0, 8, 3383, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107001, 1, "…\nおはようございます\n…まだ朝ですよ", 1, 1, 1, 3384, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107001, 1, "食事にします\n…どうしました？\n早く持ってきなさい", 2, 2, 1, 3385, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107001, 1, "早く寝なさい\n夜更かしすると\n背が伸びませんよ？", 3, 3, 1, 3386, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107001, 1, "マサキより\n座り心地は\nいいですね", 2, 0, 1, 3388, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107001, 1, "意外に複雑な\n構造ですね", 3, 0, 1, 3389, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107001, 1, "真似るなら\n香りまで\n再現しなくては", 4, 0, 1, 3390, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107001, 1, "発火現象の\n模倣…なかなか\n難しいものです", 5, 0, 1, 3391, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107001, 1, "偽物や分身を作るには\nエネルギーが\n必要不可欠なのです", 7, 0, 1, 3393, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 107001, 1, "暇つぶしには\nちょうどよさそうです\n…", 8, 0, 1, 3394, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110701, 1, "学院かぁ…\n友達の作り方の\n授業はないかな？", 1, 0, 1, 3396, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110701, 1, "学院でぼっちに\nなったときのために、\n避難場所を\n探しておかないと…", 2, 0, 1, 3397, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110701, 1, "ぼっちの私が学院…\nな、なんだか\n目まいがしてきました", 3, 0, 1, 3398, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110701, 1, "クラスメイトに\n話しかける練習…\nむ、難しすぎます…！", 4, 0, 1, 3399, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110701, 1, "目指せ友達100人…\n無理かな…10人…\nや、やっぱり一人！", 5, 0, 1, 3400, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110701, 1, "あなたと学院生活が\n送れるなんて、\nとっても\nうれしいです！", 101, 0, 4, 3401, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110701, 1, "えへへ…\n学院でも、\nあなたと一緒に\n居られてうれしいな♪", 102, 0, 8, 3402, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110701, 1, "が、学院ってあんなに\n人が居るんですね…\n友達作りのイメトレを\nやり直さないと！", 1, 0, 1, 3403, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110701, 1, "私にとって\n部活動とはＢＢ団！\n今日もたくさん\n活動しましょうね！", 2, 0, 1, 3404, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110701, 1, "休み時間は寝たふり…\nご飯はおトイレ…\nぼっち対策は完璧です", 3, 0, 1, 3405, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110701, 1, "学院で友達を作る\n勉強は完璧ですよ！\n購買部でパンを買って\n渡すんですよね！", 4, 0, 1, 3406, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110701, 1, "学院でも、\nＢＢ団の活動を\nしていきましょうね♪", 101, 0, 4, 3407, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110701, 1, "ぼっちに学院生活は\n厳しいですね…でも、\nあなたと一緒なら\n乗りこえられそうです", 102, 0, 8, 3408, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110701, 1, "もうこんな時間…\nあの…一緒に学院に\n行ってくれませんか？", 1, 1, 1, 3409, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110701, 1, "お昼ですね！\n言ってくれれば、\nパンでもお握りでも\n買ってきますよ！", 2, 2, 1, 3410, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110701, 1, "今日もぼっち…\nいつか友達と一緒に、\nパジャマパーティー\nしたいなぁ…", 3, 3, 1, 3411, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110701, 1, "森とは違う\n心地良さが…このまま\n眠っちゃいそうです…", 2, 0, 1, 3413, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110701, 1, "かわいい…この子を、\n話す練習の相手役に\nしてみようかな？", 3, 0, 1, 3414, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110701, 1, "いい香り…\nいつ友達ができても\nいいように、私の\n部屋にも置こうかな", 4, 0, 1, 3415, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110701, 1, "こ、これは…\nぼっちで\n日陰者の私には\n暖かすぎます！", 5, 0, 1, 3416, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110701, 1, "おいしい…！\nお弁当にして、学院に\n持っていけないかな？", 7, 0, 1, 3418, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110701, 1, "学院の授業に\nついていくためにも、\n勉強しておこうかな…", 8, 0, 1, 3419, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110801, 1, "あんた、何してんの。\nいいけど、なんでも", 1, 0, 1, 3421, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110801, 1, "邪魔。どいて。\nいや怒ってないし。\nコレがフツーだから", 2, 0, 1, 3422, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110801, 1, "いいけど、\n散らかしめの部屋だと\n片付けたくなるよね…", 3, 0, 1, 3423, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110801, 1, "暇なときは明日の\nコーデ考えてるよ。\n考えるのはタダだし", 4, 0, 1, 3424, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110801, 1, "昔よくシオリン\n構いすぎてハツネに\n文句言われたなァ…", 5, 0, 1, 3425, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110801, 1, "なんかさ。あんたって\n謎に女子トモ多いよね\n…いいけど、べつに", 101, 0, 4, 3426, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110801, 1, "ねえ。あのさあ。\nあんた弁当で好きな\n具とかってあんの", 102, 0, 8, 3427, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110801, 1, "あ？　何。なんか用。\nや、ないならないで\nいいけど、べつに", 1, 0, 1, 3428, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110801, 1, "ごめ、ちょっと\n黙っててくんない。\n明日の試験ヤバだから", 2, 0, 1, 3429, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110801, 1, "歩き方が静か？\n癖になってるんだよね\n足音を殺すの", 3, 0, 1, 3430, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110801, 1, "うちの血液型？\n大雑把のОだけど。\nなんか文句あんの", 4, 0, 1, 3431, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110801, 1, "ひゃんっ！\nや、だからさ…急に\n話しかけんなっての", 101, 0, 4, 3432, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110801, 1, "な、何…あんまジッと\n見ないでくんない…\nなんか、その、ハズい", 102, 0, 8, 3433, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110801, 1, "早起きは得意な方だと\n思うけど。昔から\nそれがデフォだったし", 1, 1, 1, 3434, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110801, 1, "こんな時間か…\n昼ごはん\nサクッと作っちゃお", 2, 2, 1, 3435, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110801, 1, "あ？　夜遊び？\nはッ…そんなん\n５歳で卒業したっての", 3, 3, 1, 3436, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110801, 1, "あーヤバじゃんコレ…\nふかついてる場合\nじゃないっつーのに…", 2, 0, 1, 3438, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110801, 1, "なんだこれ\nかわいいかよ", 3, 0, 1, 3439, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110801, 1, "ふーん。\nこの匂い…\nなんかいいじゃん", 4, 0, 1, 3440, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110801, 1, "暖炉はアドじゃん。\n煮物、焼き物、蒸し物\nいろいろ作れそう", 5, 0, 1, 3441, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110801, 1, "何コレ\n食べ物困んないじゃん\nうちにも一台くんない", 7, 0, 1, 3443, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110801, 1, "本かァー…んー\n学校の勉強だけで\nいいかなァ…", 8, 0, 1, 3444, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111101, 1, "イタズラってなにを\nしたらいいのかな？", 1, 0, 1, 3446, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111101, 1, "カップケーキ\n上手に作れました\n食べてくれるかな…", 2, 0, 1, 3447, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111101, 1, "あの人、\nお菓子くれるかな？\nくれなかったら…ふふ", 3, 0, 1, 3448, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111101, 1, "オバケ…いないよね？\n怖くなんてないけど！", 4, 0, 1, 3449, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111101, 1, "ハロウィンは\n楽しいけどオバケの\nお祭りなんだよね…", 5, 0, 1, 3450, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111101, 1, "にゃ～、にゃお～！\nちゃんと怖いかな？\n早く帰ってこないかな", 101, 0, 4, 3451, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111101, 1, "あの人のことだから\nお菓子欲しがるよね…\n準備はバッチリです♪", 102, 0, 8, 3452, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111101, 1, "えへへ。猫さんのお耳\nよく出来てるでしょ？", 1, 0, 1, 3453, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111101, 1, "触りましたね？\nイタズラしましたね？\nへんた～い！", 2, 0, 1, 3454, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111101, 1, "フー！　頭を撫でたら\n怒りますよ！　あれ？\n猫みたいでした？", 3, 0, 1, 3455, 0);
