INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105201, 1, "あなたとなら\nどんなことだって\nできる気がするわ♪", 103, 0, 12, 3555, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106501, 1, "あーあ、退屈だな\nカレでも探しに\nいこうかな", 1, 0, 1, 3556, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106501, 1, "この前のケンカは\n最高に燃えたなぁ\n…拳が疼いてきたぜ", 2, 0, 1, 3557, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106501, 1, "一体、この世界は\nどうなっちまったって\nいうんだ…？", 3, 0, 1, 3558, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106501, 1, "もう仕事の時間か\nボスも人使いが荒いぜ\n…ま、いいけどさ！", 4, 0, 1, 3559, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106501, 1, "オレたち以外に\nドラゴン族はあと何人\nいるんだろうな…", 5, 0, 1, 3560, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106501, 1, "カレの真剣な顔\n面白かったな\nまた遊びたいぜ", 101, 0, 4, 3561, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106501, 1, "どうしてカレがそばに\nいると、腹の底から\n力が湧いてくるんだ？", 102, 0, 8, 3562, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106501, 1, "よっす！\nなんかオイシイ話でも\nあったりしない？", 1, 0, 1, 3563, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106501, 1, "なあ、オレと遊ぼうぜ\n心配すんなよ。危ない\nことはよしとくからさ", 2, 0, 1, 3564, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106501, 1, "どうした。オレに\nケンカ売ろうってか？\n…ははは、冗談だよ！", 3, 0, 1, 3565, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106501, 1, "ちょうど退屈してた\nところだ、特訓に\n付き合ってくれよ", 4, 0, 1, 3566, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106501, 1, "ったくよぉ…\nオレがかわいいなんて\n冗談はもうよせよ？", 101, 0, 4, 3567, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106501, 1, "次の仕事も一緒に\n来るだろ？　断っても\n連れて行くけどな！", 102, 0, 8, 3568, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106501, 1, "おはよう！　今日の\n予定はまずケンカして\nで、その後もケンカだ", 1, 1, 1, 3569, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106501, 1, "よっす！\n腹ごしらえが済んだら\nオレと遊ぼうぜ！", 2, 2, 1, 3570, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106501, 1, "夜は危ないから\nあんま出歩くな？\n…だから面白えんだろ", 3, 3, 1, 3571, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106501, 1, "次のケンカに備えて\n今は休んでおくか…\n全力で暴れたいからな", 2, 0, 1, 3573, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106501, 1, "ボスの飼ってる\nラーゴが、中に\n入ってるんじゃ？", 3, 0, 1, 3574, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106501, 1, "ああ、たぎる匂いが\nする…ひと暴れ\nしたくなってきた！", 4, 0, 1, 3575, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106501, 1, "おおぉ…\nオレの拳もなんだか\n熱くなってきたぜ…！", 5, 0, 1, 3576, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106501, 1, "いつでも全力で\nケンカするために\n力を蓄えないとな", 7, 0, 1, 3578, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106501, 1, "やっぱりな…オレが\n知ってる地図とは\nまるで違ってやがる", 8, 0, 1, 3579, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111501, 1, "さぁプレゼントで\n埋め尽くしてやろう！\n大いに喜ぶがいいよ！", 1, 0, 1, 3581, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111501, 1, "メリークリスマス☆\n聖夜の宴だ\n派手に楽しむとしよう", 2, 0, 1, 3582, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111501, 1, "煙突から入る？\nいいや、ワタシは\nいつでも正面からさ♪", 3, 0, 1, 3583, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111501, 1, "サンタクロースの\nお出ましだ！\n歓迎するがいいよ♪", 4, 0, 1, 3584, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111501, 1, "飾り付けが足りん！\nワタシが演出して\nやろうじゃないか！", 5, 0, 1, 3585, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111501, 1, "人の喜ぶ顔を見るのも\n悪くはないな…おっと\nワタシらしくないな☆", 101, 0, 4, 3586, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111501, 1, "プレゼントはまだか？\nワタシを喜ばせたら\nお返しは弾むぞ♪", 102, 0, 8, 3587, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111501, 1, "ワタシを呼んだか？\n退屈な要件だったら\n覚悟しておくことだ", 1, 0, 1, 3588, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111501, 1, "なんだ？\nワタシへの挑戦か？\nよし、受けた♪", 2, 0, 1, 3589, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111501, 1, "おっと、当たらんよ\nワタシの乱数聖域は\n絶対だからな…♪", 3, 0, 1, 3590, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111501, 1, "なんだ、オマエも\n暇なのか？\n奇遇だな、ワタシもさ", 4, 0, 1, 3591, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111501, 1, "なにか愉しいことでも\nしようじゃないか\nさぁ、何をする？", 101, 0, 4, 3592, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111501, 1, "こうもワタシに\n気安く触れるのは\nオマエくらいだよ…♪", 102, 0, 8, 3593, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111501, 1, "朝だろうと関係ない！\nさぁ愉しく苛烈に\n騒ごうじゃないか♪", 1, 1, 1, 3594, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111501, 1, "調子の出てくる時間だ\nしっかりワタシを\n愉しませてくれよ♪", 2, 2, 1, 3595, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111501, 1, "眠いなどと言わせんぞ\nまだまだ一日は\n終わらんからな！", 3, 3, 1, 3596, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111501, 1, "いい具合じゃないか\nこのまま持って帰って\nしまおうか…♪", 2, 0, 1, 3598, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111501, 1, "なかなか愛らしい\n造形をしているな？\nワタシには負けるがな", 3, 0, 1, 3599, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111501, 1, "ほう、悪くない香りだ\n戦いの匂いをうまく\n隠してくれそうだ…♪", 4, 0, 1, 3600, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111501, 1, "体が温まってくると\nひと暴れしたくなるな", 5, 0, 1, 3601, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111501, 1, "ワタシの舌は厳しいぞ\n…ふーむ、なるほど\n認めざるをえんな", 7, 0, 1, 3603, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111501, 1, "クリスティーナさんは\n知識の収集も\n欠かさないんだよ", 8, 0, 1, 3604, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111501, 1, "これは驚いたな\nだがもっと、もっとだ\nワタシを退屈させるな", 14, 0, 1, 3606, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111501, 1, "こんな戯れだって\n軽くこなしてしまう\nクリスティーナさんさ", 15, 0, 1, 3607, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111601, 1, "メリークリスマス！\nさあ、パーティーを\n盛り上げるよ！", 1, 0, 1, 3608, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111601, 1, "衣装一つで全然\n気分が違うね！\n最高のクリスマスに\nなりそうだよ！", 2, 0, 1, 3609, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111601, 1, "ツリーのてっぺんの\n星って、なんだか\n欲しくなるんだよね", 3, 0, 1, 3610, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111601, 1, "めいっぱい\nクリスマスを楽しむよ\n遊びだって本気\nなんだからね！", 4, 0, 1, 3611, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111601, 1, "赤、白、緑、金\nクリスマスカラー\nってお洒落だよね", 5, 0, 1, 3612, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111601, 1, "雪、降らないかな？\nホワイトクリスマス\nってちょっと\n憧れるなぁ", 101, 0, 4, 3613, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111601, 1, "キミと過ごすこの\n楽しい時間がずーっと\n続けばいいのにな", 102, 0, 8, 3614, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111601, 1, "クリスマスライブ\nチケット用意するから\n絶対来てよね！", 1, 0, 1, 3615, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111601, 1, "カンパーイ！\nジュースだけど\nちょっと大人の\n気分だね", 2, 0, 1, 3616, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111601, 1, "クリスマス衣装どう？\nかわいいでしょ\nあっ、どこ見てるの！", 3, 0, 1, 3617, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111601, 1, "ねえ、後で街に\n行こうよ\n大きなツリーを\n一緒に見たいんだ♪", 4, 0, 1, 3618, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111601, 1, "ねえ、ダンスの練習に\n付き合ってくれない？\n大丈夫、手取り足取り\n教えてあげるから♪", 101, 0, 4, 3619, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111601, 1, "来年のクリスマスも\n隣にキミが\nいてくれたら\nうれしいのにな", 102, 0, 8, 3620, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111601, 1, "クリスマスって朝から\nドキドキしちゃうね\nなんだか子どもみたい", 1, 1, 1, 3621, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111601, 1, "パーティーの準備も\n大分進んだね\n一息ついて\nランチにしよっか", 2, 2, 1, 3622, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111601, 1, "静かな夜だね\n心臓の音まで\n聞こえちゃいそう…", 3, 3, 1, 3623, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111601, 1, "あ～、この絶妙な\n柔らかさ…\nいい仕事してるなぁ…", 2, 0, 1, 3625, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111601, 1, "アイドル顔負けの\nかわいさ、ズルい！\nでもかわいい！", 3, 0, 1, 3626, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111601, 1, "ん～、落ち着くなぁ\n今日はゆっくり\n眠れそうだよ", 4, 0, 1, 3627, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111601, 1, "ぬくぬく～\nどうしよう、ここから\n動きたくないなぁ", 5, 0, 1, 3628, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111601, 1, "ダンスの練習もしたし\nちょっとくらいなら\nいいよね！", 7, 0, 1, 3630, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111601, 1, "アイドルといえど\nかわいいだけじゃダメ\n知識も必要だよね", 8, 0, 1, 3631, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111601, 1, "面白ーい！\nこの演出、ライブでも\nできないかな？", 14, 0, 1, 3633, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111601, 1, "もふもふ～♪\n寒い日には最高の\n抱き心地だね！", 15, 0, 1, 3634, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101801, 1, "彼ともっとお話し\nしたいな…もう少し\n補習を増やそうかしら", 103, 0, 12, 3635, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101801, 1, "君のこともっともっと\n教えて欲しいな。私に\n補習授業してくれる？", 103, 0, 12, 3636, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111701, 1, "この姿ならあやつも\n子ども扱いできまい！\n…じゃがなぜか、少し\n物足りないような…", 1, 0, 1, 3637, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111701, 1, "時間もあるし、\n聖夜の宴の\n準備をするかのう♪", 2, 0, 1, 3638, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111701, 1, "…わらわの知る聖夜と\n随分違うが、宴は宴\n楽しませて\nもらおうかのう♪", 3, 0, 1, 3639, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111701, 1, "大人の姿で\nできること…ふむ\n少しあやつを\nからかってやるか♪", 4, 0, 1, 3640, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111701, 1, "…クリスマスは\n恋人たちの夜！？\nち、近頃の若いもんは\n破廉恥なのじゃ！", 5, 0, 1, 3641, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111701, 1, "…あやつがおらんと、\n宴も物足りぬな\n…馬鹿者が\n早く帰ってこい…", 101, 0, 4, 3642, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111701, 1, "誰も見ておらぬな…？\n…よし、あやつへの\nプレゼントを\n準備するとしようか！", 102, 0, 8, 3643, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111701, 1, "クリスマスの\nチキンにケーキ…\nべ、別に楽しみになど\nしておらぬぞ！", 1, 0, 1, 3644, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111701, 1, "この姿では、わらわの\n頭をなでるのは\n難しかろう…べ、\n別に残念ではないわ！", 2, 0, 1, 3645, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111701, 1, "メリー\nクリスマスじゃ！\nさあ、わらわのために\n宴の準備をするのじゃ", 3, 0, 1, 3646, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111701, 1, "そんなにこの衣装が\n気になるのか？\n…だったら、直に\n触ってみるがよい♪", 4, 0, 1, 3647, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111701, 1, "いつもと逆に、お主を\n子ども扱いしてやるか\nほれ、膝枕をしてやる\nからそばに来い♪", 101, 0, 4, 3648, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111701, 1, "…せ、聖夜の予定は\n空けておくのじゃぞ？\nなんでって…\nな、なんでもじゃ！", 102, 0, 8, 3649, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111701, 1, "もう朝か…聖夜の宴が\n楽しみで…ではなく！\n準備のせいで、\nあまり眠れんかったぞ", 1, 1, 1, 3650, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111701, 1, "…ずっと雪が\n降っておれば、\n陽光が強いこの時間も\n怯えずにすむのにのう", 2, 2, 1, 3651, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111701, 1, "時は来た…\nさあ眷属よ！\nわらわと共に、\n聖夜の宴を楽しもうぞ", 3, 3, 1, 3652, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111701, 1, "良い椅子ではないか\nここから聖夜の宴を\n眺めるのもよいかもな", 2, 0, 1, 3654, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111701, 1, "か、かわい…こほん\nなかなか良い\nデザインをしておるな", 3, 0, 1, 3655, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111701, 1, "ふむ、良い香りじゃ\n聖夜の宴でも\nこれを使うとしよう", 4, 0, 1, 3656, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111701, 1, "これが癒し…はっ！\nあやうく浄化される\nところであった…", 5, 0, 1, 3657, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111701, 1, "ニ、ニンニクは\n入っておらぬよな？\n…うむ、美味じゃ♪", 7, 0, 1, 3659, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111701, 1, "聖夜は恋人たちの夜…\n…き、気になるわけ\nではないが、\n少し調べてみるか", 8, 0, 1, 3660, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111701, 1, "ほう…これは面白い\n魔法の使い方じゃな\nまるで本物の\n演劇のようじゃ", 14, 0, 1, 3662, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111701, 1, "こ、この程度の\nかわいさに誘惑される\nわらわでは…\nふわもこなのじゃ～♪", 15, 0, 1, 3663, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112001, 1, "おせち料理も\n魔物尽くしだったり\nしないわよね！", 1, 0, 1, 3664, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112001, 1, "初詣で何をお祈り\nしようかしら…\n定番でいいわよね？", 2, 0, 1, 3665, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112001, 1, "初日の出って\n特別な感じがして\nいいわよね…", 3, 0, 1, 3666, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112001, 1, "寒い中並んで飲む\n甘酒って不思議なほど\nおいしいわよね", 4, 0, 1, 3667, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112001, 1, "羽根つきで勝負…\nあいつにだったら\n勝てるかしら？", 5, 0, 1, 3668, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112001, 1, "年賀状になんて書こう\n…いろいろと\n恥ずかしくなるのよね", 101, 0, 4, 3669, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112001, 1, "今年もあいつと\n居られること…\n結構うれしいわ", 102, 0, 8, 3670, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112001, 1, "何じっと\n見てんのよ？\nお年玉はあげないわよ", 1, 0, 1, 3671, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112001, 1, "おせち料理一緒に\n食べる？\n結構おいしいわよ", 2, 0, 1, 3672, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112001, 1, "羽根つきで負けたら\n顔に墨塗られるのよ\n覚悟しなさい！", 3, 0, 1, 3673, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112001, 1, "門松飾るから\nちょっと手伝って\nくれる？", 4, 0, 1, 3674, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112001, 1, "あんたと一緒に\n初詣行くのも悪くない\n…かも", 101, 0, 4, 3675, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112001, 1, "初詣で何をお祈り\nしたか？　うっさい！\n内緒よ！内緒！", 102, 0, 8, 3676, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112001, 1, "もう朝なの…\n正月くらいゆっくり\n寝ていたいわよね…", 1, 1, 1, 3677, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112001, 1, "おせちに飽きたら\n次は何を食べよう\nかしら…", 2, 2, 1, 3678, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112001, 1, "そろそろ寝ましょ\nいい初夢が見られると\nいいわね", 3, 3, 1, 3679, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112001, 1, "この格好でいるのも\nなかなか疲れるわね\n休憩～", 2, 0, 1, 3681, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112001, 1, "か、かわいい…う～ん\n鏡餅も顔を描けば\nかわいくなるかしら？", 3, 0, 1, 3682, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112001, 1, "いい匂いね\n落ち着いて\n眠れそう…", 4, 0, 1, 3683, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112001, 1, "ここでお餅焼いたら\n一段とおいしく\nなりそうね", 5, 0, 1, 3684, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112001, 1, "正月に食べるものは\nなんでもおいしく\n感じちゃうわね！", 7, 0, 1, 3686, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112001, 1, "魔物を使わない\nおせちを\n調べておかなきゃ…", 8, 0, 1, 3687, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112101, 1, "新年を気持ちよく\n過ごせるように\nお掃除がんばります！", 1, 0, 1, 3689, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112101, 1, "神殿に通っていたら\nいつの間にか足腰が\n鍛えられたような…", 2, 0, 1, 3690, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112101, 1, "神殿付近は屋台も\nあるので、良い香りが\n漂ってくるんですよ～", 3, 0, 1, 3691, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112101, 1, "おせちを作れるように\nなれば、子どもたちも\n喜んでくれるかな…？", 4, 0, 1, 3692, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112101, 1, "コホォォォォォ…！\nふぅ、深呼吸すると\n落ち着きますね♪", 5, 0, 1, 3693, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112101, 1, "お仕事以外の時も\nあなたと…い、いえ！\nなんでもないです！", 101, 0, 4, 3694, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112101, 1, "あなたと一緒だと\nなんだかポカポカ\nしてくるんです…", 102, 0, 8, 3695, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112101, 1, "この格好をしていると\nなんだか神殿にいる\n気分になりますね", 1, 0, 1, 3696, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112101, 1, "そういえば神殿で\n甘酒をもらったんです\nあなたも飲みますか？", 2, 0, 1, 3697, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112101, 1, "あっ、髪飾りが\nズレちゃってました！\nいけないいけない…", 3, 0, 1, 3698, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112101, 1, "ひゃわっ！？\nく、くすぐったり\nしないでください～", 4, 0, 1, 3699, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112101, 1, "そ、そんなに\n見つめられると\nて、照れちゃいます…", 101, 0, 4, 3700, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112101, 1, "今度、私が考えた\n運気上昇のおまじない\nかけてあげますね♪", 102, 0, 8, 3701, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112101, 1, "おはようございます\n神殿のお手伝いに行く\n準備もしなきゃですね", 1, 1, 1, 3702, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112101, 1, "お昼になりましたし\nご飯は私が作…\nえっ、ダメですか！？", 2, 2, 1, 3703, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112101, 1, "明日も早いですし\nそろそろ寝る支度を…\nふわぁ…", 3, 3, 1, 3704, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112101, 1, "お掃除も終わったので\n少し休憩を…ふわぁ…\n急に眠気が…", 2, 0, 1, 3706, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112101, 1, "新年を気持ちよく\n過ごせるようこの子も\nきれいにしましょう♪", 3, 0, 1, 3707, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112101, 1, "神殿のお香のような\n香りだと落ち着いて\n過ごせそうですよね", 4, 0, 1, 3708, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112101, 1, "ぽかぽかですね～…\n甘酒も飲んで体の\n芯から温めましょう", 5, 0, 1, 3709, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112101, 1, "今年こそ\n料理も上手に…\nもぐもぐもぐもぐ…", 7, 0, 1, 3711, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112101, 1, "失敗続きだと\n逆に運が良くなる…\nほ、本当ですかぁ…？", 8, 0, 1, 3712, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103401, 1, "キミにならもう\nどんな私を見られても\n大丈夫かな…♪", 103, 0, 12, 3714, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103401, 1, "なになにどしたのぉ？\nお姉さんが撫でたげる\nこっちにきなさぁい♪", 103, 0, 12, 3715, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111901, 1, "わたくしの舞を\n主さまは喜んで\nくださるでしょうか…", 1, 0, 1, 3716, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111901, 1, "年を越したあとに\n食べるおそばも\nおいしいです", 2, 0, 1, 3717, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111901, 1, "甘酒を飲むと\n心まで温まる\n気がいたします", 3, 0, 1, 3718, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111901, 1, "主さまのために\n真心を込めたおせちを\nご用意しなくては", 4, 0, 1, 3719, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111901, 1, "おみくじを引くのは\nドキドキしつつも\n楽しいものです", 5, 0, 1, 3720, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111901, 1, "主さまの温もりは\n心まで温めてくれる\nようです", 101, 0, 4, 3721, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111901, 1, "うまくできなくとも\n心を込めて精いっぱい\n舞いましょう", 102, 0, 8, 3722, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111901, 1, "主さま、この晴れ着は\n似合っている\nでしょうか？", 1, 0, 1, 3723, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111901, 1, "お餅はゆっくりと\n召し上がって\nくださいまし", 2, 0, 1, 3724, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111901, 1, "主さまのお顔の\n福笑いなら、完璧に\nできる気がいたします", 3, 0, 1, 3725, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111901, 1, "主さまはどのような\n初夢をご覧になった\nのでしょう？", 4, 0, 1, 3726, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111901, 1, "甘酒の飲み過ぎは\nいけませんよ？\nめっ、でございます", 101, 0, 4, 3727, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111901, 1, "二人羽織とは\nあんなにも暖かい\nものなのですね…", 102, 0, 8, 3728, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111901, 1, "おはようございます\n寒い季節の朝日は\nとても美しいですね", 1, 1, 1, 3729, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111901, 1, "おいしくとも\n食べ過ぎは正月太りの\nもとでございます", 2, 2, 1, 3730, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111901, 1, "いい夢が見られるよう\n暖かくして\n眠るといたしましょう", 3, 3, 1, 3731, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111901, 1, "ふかふか…\nこのまま\n寝てしまいそうです", 2, 0, 1, 3733, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111901, 1, "あなたと一緒に\nお正月遊びを\n楽しんでみたいです", 3, 0, 1, 3734, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111901, 1, "神殿の独特な香りを\nおうちに持ち込むのは\nどうでしょうか", 4, 0, 1, 3735, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111901, 1, "風邪をひきませんよう\n暖かくいたしましょう", 5, 0, 1, 3736, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111901, 1, "食べ過ぎは\n正月太りの\nもとだというのに…", 7, 0, 1, 3738, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111901, 1, "主さまのために\n参拝の作法を\n予習しておきましょう", 8, 0, 1, 3739, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112201, 1, "…この杖、変身前には\n無いパーツがあるけど\nこれはどこから…うん\n考えないようにしよう", 1, 0, 1, 3741, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112201, 1, "フ、フリフリの\nかわいい衣装…はっ！\n素に戻ってはダメだ！", 2, 0, 1, 3742, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112201, 1, "魔法探偵\nミスティ★カスミ！\n魔法の力で捜査開始！\n…練習終わりっと", 3, 0, 1, 3743, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112201, 1, "怪しい視線を感じる…\nまさか、近くに\n闇の力を持つ人が！？", 4, 0, 1, 3744, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112201, 1, "時間もあるし、\n魔法少女の修行を…\nじゃ、じゃなくて！\n探偵の仕事をしよう！", 5, 0, 1, 3745, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112201, 1, "助手くんとずっと\n一緒に居られるし、\n魔法少女も\n案外悪くないかも♪", 101, 0, 4, 3746, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112201, 1, "魔法少女の訓練のため\n助手くんの所に行こう\nかな…あ、あくまで\n訓練のためにね！", 102, 0, 8, 3747, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112201, 1, "…髪型が急に\n変わって驚いた？\nき、気になるなら…\n少し触ってみるかい？", 1, 0, 1, 3748, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112201, 1, "助手くんは魔法少女に\nなりたいのかい？\nその場合は魔法騎士…\nいや、魔法助手とか？", 2, 0, 1, 3749, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112201, 1, "頭に浮かぶ呪文…\n強制変身…助手くん\n魔法少女とは\n呪いか何かなのかな？", 3, 0, 1, 3750, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112201, 1, "あ、あまり触らないで\nくれ…魔法少女になる\nと感覚が鋭くなって、\n耳も敏感に…ひゃう！", 4, 0, 1, 3751, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112201, 1, "や、やっぱりこの\n格好は変かな…え、\nかわいい？　じゃあ…\nいや、でも、う～ん", 101, 0, 4, 3752, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112201, 1, "…この変身は助手くん\nのせいでもあるんだよ\nだから…せ、責任を\n取ってもらうからね！", 102, 0, 8, 3753, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112201, 1, "おはよう助手くん\nさあ、そろそろ\n街のパトロールに\n行こうじゃないか！", 1, 1, 1, 3754, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112201, 1, "もうお昼…助手くん、\nそろそろ本業に戻ろう\n…その顔は何かな？\n私の本業は探偵だぞ！", 2, 2, 1, 3755, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112201, 1, "夜だけど、私は\nもう少し仕事をするよ\n魔法少女の強い体は\nこんなときに便利だね", 3, 3, 1, 3756, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112201, 1, "気持ちいい…\nこの癒しを、魔法と\n論理の二つの観点から\n解明したいね…", 2, 0, 1, 3758, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112201, 1, "ダ、ダメ！！\nここで感情のままに\n飛びついたら、私の\nイメージがぁ～！", 3, 0, 1, 3759, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112201, 1, "心が安らぐ香りだね…\n…今なら、魔法少女も\nすんなりと受け入れ…\nてはダメだっ！", 4, 0, 1, 3760, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112201, 1, "あったかい…\n魔法少女だろうと\n休息は必要だし、少し\n休んでいこうかな", 5, 0, 1, 3761, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112201, 1, "魔法少女も探偵も、\nやっぱり体が資本！\n食べられるときに\n食べておかないとね♪", 7, 0, 1, 3763, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112201, 1, "…魔法少女\nセリイちゃんの本は\nどこにあるのかな？", 8, 0, 1, 3764, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112301, 1, "魔法少女かぁ…\nせっかくだし思う存分\n体を動かしてみたいな", 1, 0, 1, 3766, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112301, 1, "ニャットちゃん…\nこの格好を見て\n私って気づくかな？", 2, 0, 1, 3767, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112301, 1, "ふふ…\n今日はなんだか\n調子がいいかも", 3, 0, 1, 3768, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112301, 1, "魔法は便利だけど\nあまり頼りすぎちゃ\nダメだよね", 4, 0, 1, 3769, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112301, 1, "さっき街で小さな子に\n声かけられちゃった\n照れるけどうれしいな", 5, 0, 1, 3770, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112301, 1, "あの人がいると\nなんだかホッとする\n不思議…", 101, 0, 4, 3771, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112301, 1, "魔法少女になっても\nあの人への気持ちは\nずっと同じ…", 102, 0, 8, 3772, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112301, 1, "今日の私…ですか？\nはいとっても元気です\n…あなたのおかげかも", 1, 0, 1, 3773, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112301, 1, "よかったら魔法の\n練習に付き合って\nくれませんか？", 2, 0, 1, 3774, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112301, 1, "あの…\nこの格好どうですか？\n似合ってると…いいな", 3, 0, 1, 3775, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112301, 1, "…な、なんですか？\nあまり見られると\n恥ずかしいです…", 4, 0, 1, 3776, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112301, 1, "私、あなたのそばに\nいられたら、それで…", 101, 0, 4, 3777, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112301, 1, "想い人と結ばれる\n魔法は…ひゃっ！？\n今の聞いてましたか？", 102, 0, 8, 3778, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112301, 1, "おはようございます\n今日も街の平和の\nために頑張らなきゃ", 1, 1, 1, 3779, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112301, 1, "お腹すきましたね。\nあの…一緒にランチは\nいかがですか？", 2, 2, 1, 3780, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112301, 1, "あ、もうこんな時間…\nふふ、まだこんなに\n体力が余ってるのに", 3, 3, 1, 3781, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112301, 1, "気持ちいい…\nこの気持ち良さは\nどんな魔法も勝てない", 2, 0, 1, 3783, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112301, 1, "何これ、かわいい…\nお姉ちゃんにも\n見せてあげたいな", 3, 0, 1, 3784, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112301, 1, "ん～、いい香り\n…これ、魔法で\n出せたりしないかな？", 4, 0, 1, 3785, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112301, 1, "あったか～い\nえへへ…なんだか\nお姉ちゃんみたい", 5, 0, 1, 3786, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112301, 1, "おいしいなぁ、これ\n魔法少女になってから\n食欲旺盛かも…", 7, 0, 1, 3788, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112301, 1, "ふふ…\n魔法少女になっても\n読書はやめられないな", 8, 0, 1, 3789, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101001, 1, "みらくるまほりん\nくるりんぱ～♪\n王子はんのために\n花嫁修業や～♪", 103, 0, 12, 3791, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101001, 1, "あ、あのな王子はん\nできたらその…\nお姫さまだっこを\nお願いしてもええ…？", 103, 0, 12, 3792, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180401, 1, "やっぱりわたしたちの\nギルドハウスの\n居心地は最高ですね☆", 1, 0, 1, 3793, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180401, 1, "王宮じゃできないこと\nやっちゃいましょう♪\n例えば、えっと～…", 2, 0, 1, 3794, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180401, 1, "そうだ、隠しておいた\nおやつがありました！\nまだ食べられますね☆", 3, 0, 1, 3795, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180401, 1, "ごはんはみんなで\n楽しく食べましょう！\nそれが一番の\n調味料です♪", 4, 0, 1, 3796, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180401, 1, "やっぱりここが、\nわたしの帰る場所です\n…ただいま☆", 5, 0, 1, 3797, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180401, 1, "なんだか物足りません\nきっと、あの人が隣に\nいないからですね…", 101, 0, 4, 3798, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180401, 1, "どこを見ても、大切な\n思い出がたくさん\n詰まっていますね♪", 102, 0, 8, 3799, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180401, 1, "無礼者～、なんて\n冗談です♪　どんどん\n触っちゃってください", 1, 0, 1, 3800, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180401, 1, "ぷにぷにでしょう～？\nい～っぱい食べるのが\nぷにぷにの秘訣です！", 2, 0, 1, 3801, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180401, 1, "握手したいんですか？\nはい、にぎにぎ～！\nもう離しませんよ♪", 3, 0, 1, 3802, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180401, 1, "どうかしましたか？\n退屈してるんでしたら\nお散歩に行きましょう", 4, 0, 1, 3803, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180401, 1, "ごはん王子♪\nわたしは王女ですから\nこれでピッタリですね", 101, 0, 4, 3804, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180401, 1, "大好きです～\n愛してます～\n…ほんと、ですよ♪", 102, 0, 8, 3805, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180401, 1, "朝ごはんは大事です！\n山盛りごはんを食べて\n元気いっぱいですよ☆", 1, 1, 1, 3806, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180401, 1, "お腹ぺこぺこになる\n時間ですね～！\nごはん特盛、一つ！", 2, 2, 1, 3807, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180401, 1, "お腹いっぱい、今日は\nよく眠れそうですね♪\n明日もおいしい一日を", 3, 3, 1, 3808, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 180401, 1, "休憩休憩～♪\nお腹を休めて、次の\nごはんに挑みます…☆", 2, 0, 1, 3810, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 180401, 1, "かわいらしい\nぬいぐるみですね～\nキャルちゃんみたい☆", 3, 0, 1, 3811, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 180401, 1, "すんすん…この匂い\n食欲増進の効果が\nありそうですね…？", 4, 0, 1, 3812, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 180401, 1, "体温が上がってくると\n眠くなっちゃいます…\nほわぁ～", 5, 0, 1, 3813, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 180401, 1, "もぐもぐもぐもぐもぐ\nむぐむぐむぐむぐむぐ\nはぐはぐはぐはぐはぐ", 7, 0, 1, 3815, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 180401, 1, "教養を身に着けるには\n読書が一番です！\nえっと次のレシピは～", 8, 0, 1, 3816, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112401, 1, "アイドルらしくなる\nコツって、何か\nあるんでしょうか？", 1, 0, 1, 3818, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112401, 1, "色んな人と話すのが\n好きなんです。つい\n長くなっちゃって…", 2, 0, 1, 3819, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112401, 1, "アイドルを目指すって\n決めてから、毎日が\nワクワクなんです♪", 3, 0, 1, 3820, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112401, 1, "ちゃんとストレッチ\nしておかなくっちゃ\nよいしょ、よいしょ…", 4, 0, 1, 3821, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112401, 1, "リンちゃんミオちゃん\n二人に負けないように\n私、頑張ります！", 5, 0, 1, 3822, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112401, 1, "私の笑顔は素敵だって\n誰かに褒められた…\nそんな気がするんです", 101, 0, 4, 3823, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112401, 1, "私たちを支えてくれる\n誰かが、ずっと一緒に\nいてくれたような…", 102, 0, 8, 3824, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112401, 1, "どうかしましたか？\n何か困ってるのなら\nお手伝いしますよ！", 1, 0, 1, 3825, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112401, 1, "みなさん個性的で\n普通じゃない人ばかり\nいるような気が…", 2, 0, 1, 3826, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112401, 1, "今日はポカポカした\nいい天気ですね～\nお散歩日和です！", 3, 0, 1, 3827, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112401, 1, "今、ミルクティーを\n淹れてたんです♪\nあなたも飲みますか？", 4, 0, 1, 3828, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112401, 1, "私の得意なこと？\nうーんなんでしょう…\nあっ、肩たたきです！", 101, 0, 4, 3829, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112401, 1, "生・ハム・メローン！\nあ、ライブの掛け声を\n練習してたんですよ♪", 102, 0, 8, 3830, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112401, 1, "おはようございます！\n今日も一日\n頑張りましょうね！", 1, 1, 1, 3831, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112401, 1, "しっかりお昼を食べて\n午後からも張り切って\nいきましょうね♪", 2, 2, 1, 3832, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112401, 1, "ぐっすり眠れるよう\n早めに用事を済ませて\nお休みしましょうね", 3, 3, 1, 3833, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112401, 1, "ちょっと休んだら\nレッスンの続きも\n頑張ります！", 2, 0, 1, 3835, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112401, 1, "えへへ～\nふわふわもこもこで\nかわいいです～♪", 3, 0, 1, 3836, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112401, 1, "リラックスできる\nいい香りですね～♪", 4, 0, 1, 3837, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112401, 1, "わぁ、ポカポカです♪\n温かい飲み物も\nほしくなりますね", 5, 0, 1, 3838, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112401, 1, "レッスンしたら\nお腹が空いちゃって…\nぱくぱくもぐもぐ…", 7, 0, 1, 3840, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112401, 1, "レッスンに役立つ\n運動の情報とか\n載ってるでしょうか…", 8, 0, 1, 3841, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112601, 1, "きらきらーん☆\nよし、次のステージは\nこれで行けるかも！", 1, 0, 1, 3843, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112601, 1, "私の元気でみんなも\n元気になってくれたら\nうれしいな～", 2, 0, 1, 3844, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112601, 1, "ちょっとレッスンで\n疲れちゃったかも…\nでも頑張るぞっ！", 3, 0, 1, 3845, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112601, 1, "晩ごはん何にしよう？\nそーだ、しぶりんにも\n聞いてみよっと！", 4, 0, 1, 3846, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112601, 1, "元気だから笑顔になる\nじゃなくて\n笑顔だから\n元気になるんだよね～", 5, 0, 1, 3847, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112601, 1, "声援を聞いてる時ね\nすごく懐かしいって\n思うことがあるんだ", 101, 0, 4, 3848, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112601, 1, "私を応援してくれる\n一番大事な人が\n隣にいた気がする…", 102, 0, 8, 3849, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112601, 1, "なになにー？\nミオちゃんに\n何か用かな～？", 1, 0, 1, 3850, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112601, 1, "おっ、やっほー！\n元気そうだね～、キミ\n私？\nもちろん元気～！", 2, 0, 1, 3851, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112601, 1, "ねぇねぇ、ちょっと\n遊びに行かない？\nたまには息抜きで！", 3, 0, 1, 3852, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112601, 1, "ね、この魔物料理さ\nプリンって言い張れば\n食べてもらえそうかな", 4, 0, 1, 3853, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112601, 1, "えへへ\nいつもありがとねっ！\nパワーもらってるよ～", 101, 0, 4, 3854, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112601, 1, "どうしたどうした～\nミオちゃんの魅力に\nメロメロなのかい？", 102, 0, 8, 3855, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112601, 1, "おっはよ～！\n一日の始まりだし\nテンション上げてこ！", 1, 1, 1, 3856, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112601, 1, "お腹空いた～…\nねぇねぇ、一緒に\nお昼食べようよ～", 2, 2, 1, 3857, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112601, 1, "ふわぁ～…\n眠くなってきちゃった\nそろそろ休もっかなぁ", 3, 3, 1, 3858, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112601, 1, "わぁ、ふっかふか～！\nレッスンの疲れも\n吹っ飛んじゃうね☆", 2, 0, 1, 3860, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112601, 1, "何これ…かわいーっ！\n早くしまむーに\n教えてあげなきゃ！", 3, 0, 1, 3861, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112601, 1, "すぅ～…\nうんっ、これ私の\n好きな香りだな～♪", 4, 0, 1, 3862, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112601, 1, "ポッカポカだね～♪\n…あ～ん、ここから\n動きたくないーっ！", 5, 0, 1, 3863, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112601, 1, "わっ、おーいしー！\nこれはテンション\n上がっちゃうね♪", 7, 0, 1, 3865, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112601, 1, "ふむふむ…\nふむふむふむ…！\n勉強になった、ブイ☆", 8, 0, 1, 3866, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100101, 1, "なんでドキドキが\n止まらないの…？\n騎士くんのせいだー！", 103, 0, 12, 3868, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100101, 1, "ドキドキしてるの\nあたしだけ、かな…\n騎士くんはどう…？", 103, 0, 12, 3869, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100201, 1, "騎士クンの前で\n好きって言えるように\n練習しないと…", 103, 0, 12, 3870, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100201, 1, "騎士クン…大好き\n…き、騎士クン！？\nなんでここに！？", 103, 0, 12, 3871, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100301, 1, "困ったな…\n彼が居ないだけで、\nここまで\n寂しいと思うなんて…", 103, 0, 12, 3872, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100301, 1, "えっと…\n今日は訓練じゃなくて\nその…二人でゆっくり\n過ごしたいな…", 103, 0, 12, 3873, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112501, 1, "アイドルの心構え…\nどういう風に考えると\nしっくりくるのかな", 1, 0, 1, 3874, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112501, 1, "何か始めるのって\nドキドキするよね\nだから最近楽しいんだ", 2, 0, 1, 3875, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112501, 1, "冒険者の時って毎日\nバタバタしてたけど…\n目標ができると違うね", 3, 0, 1, 3876, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112501, 1, "少しお腹が空いた…\nチョコレート一粒だけ\n食べようかな", 4, 0, 1, 3877, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112501, 1, "ウヅキもミオも\n頑張ってるし\n私も負けられないね", 5, 0, 1, 3878, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112501, 1, "レッスンしてる時さ\n何か、誰かが足りない\nそう思うことがあるの", 101, 0, 4, 3879, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112501, 1, "大事なのは笑顔…\n誰か、大切な人から\n言われた気がするんだ", 102, 0, 8, 3880, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112501, 1, "どうしたの、何か用？\n手伝えることがあれば\nすぐに言ってね", 1, 0, 1, 3881, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112501, 1, "姿勢がいい？\n普段から鍛えてるし\n自然とそうなるのかも", 2, 0, 1, 3882, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112501, 1, "さっき散歩してたら\n犬を見かけたんだ\n人懐っこい子だったよ", 3, 0, 1, 3883, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112501, 1, "今から冒険？\nなら私も一緒に行くよ\n前衛は任せておいて", 4, 0, 1, 3884, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112501, 1, "今からレッスンに\n行くつもりだったんだ\n少し手伝ってくれる？", 101, 0, 4, 3885, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112501, 1, "蒼の剣を受けよ！\nえ、何してるのって？\nただの発声練習だけど", 102, 0, 8, 3886, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112501, 1, "おはよう、早いね\n今日は依頼もないし\nゆっくりしようかな", 1, 1, 1, 3887, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112501, 1, "そろそろお昼だね\nうーん、今日は\n何を食べよう…", 2, 2, 1, 3888, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112501, 1, "もうこんな時間…\n明日も早いし\nそろそろ寝なくちゃ", 3, 3, 1, 3889, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112501, 1, "レッスンで疲れたし\nちょっとだけ\n休んでおこうかな", 2, 0, 1, 3891, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112501, 1, "ふふっ、かわいい\nふわふわしたものって\n撫でたくなるんだよね", 3, 0, 1, 3892, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112501, 1, "うん、いい香り…\nこういうものがあると\n落ち着くからいいよね", 4, 0, 1, 3893, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112501, 1, "温かい場所って\n居心地がいいから\nのんびりしちゃうよ", 5, 0, 1, 3894, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112501, 1, "レッスン頑張ったら\nすぐお腹が空くし\n食べておかないと…！", 7, 0, 1, 3896, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112501, 1, "アイドルのことを\nもっと勉強して\n成長しなくちゃ", 8, 0, 1, 3897, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111001, 1, "ああ、知りたい…\nなんでもかんでも\nとにかく知りたい…", 1, 0, 1, 3899, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111001, 1, "ああ、眠い…\nいつでもどこでも\nとにかく眠い…", 2, 0, 1, 3900, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111001, 1, "君、気を付けたまえ\nぼーっと歩いていると\nほわっ！　転んだ…", 3, 0, 1, 3901, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111001, 1, "ミツキ師にいただいた\n新作サプリ…丁度よい\n被験体はいないものか", 4, 0, 1, 3902, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111001, 1, "サレンディア救護院に\nユニちゃん８歳として\n潜入済みだが何か？", 5, 0, 1, 3903, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111001, 1, "君がユニちゃんズの\n同志であること、\nぼくは誇りに思うぞ", 101, 0, 4, 3904, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111001, 1, "ぼくは青春がしたい\nおい、君\nぼくと青春しないか", 102, 0, 8, 3905, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111001, 1, "ああ、そうだな。\nぼくもそう思う。\n……えっ、何が？", 1, 0, 1, 3906, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111001, 1, "待て、話しかけるな。\n今の閃きをメモに…\n…ああっ、忘れた！", 2, 0, 1, 3907, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111001, 1, "歩き方が綺麗？\n何を今更なこと\n言っとるんだ君は", 3, 0, 1, 3908, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111001, 1, "ぼくの血液型？\n大らかのОだ。\n何か異論あるかね", 4, 0, 1, 3909, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111001, 1, "君の声は精神に\n高揚をもたらすな…\n波形を分析したいぞ", 101, 0, 4, 3910, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111001, 1, "妙だな…最近\n君に話しかけられると\n心拍に乱れを覚える", 102, 0, 8, 3911, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111001, 1, "おっと、朝だな。\nそろそろ寝るか、\n朝更かしは体に悪い", 1, 1, 1, 3912, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111001, 1, "ふむ、昼餉時か…\n今日のランチは\nサプリ定食にしよう", 2, 2, 1, 3913, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111001, 1, "もう夜だな、\nそろそろ起きるか。\n最高の一日にするぞ！", 3, 3, 1, 3914, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111001, 1, "ほわぁ～\n副交感神経が…\n優位に…ふふっひ", 2, 0, 1, 3916, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111001, 1, "かわいすぎるな…\nセクシーユニには\nミスマッチか…", 3, 0, 1, 3917, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111001, 1, "ほぁあアあァぁー…\nいい香りー…\nあアァあ…イイ…", 4, 0, 1, 3918, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111001, 1, "炎はいいぞ\nそこには人類発展の\nドラマがある", 5, 0, 1, 3919, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111001, 1, "フオオー！\n次から次へと固形食物\n原理気になるー！", 7, 0, 1, 3921, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111001, 1, "良書は時間を忘れる\n…ふむ、今度\n布団を持ってこよう", 8, 0, 1, 3922, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101201, 1, "きらーん☆\nハツネちゃんに\n不可能はないのだ☆", 103, 0, 12, 3924, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101201, 1, "キミと一緒だと\n寝ちゃうのが\nもったいないよ～", 103, 0, 12, 3925, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 192101, 1, "ちぇるーん☆", 1, 0, 1, 3933, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110901, 1, "ちぇる～ん☆\n今日もチエルは\nかわいいですよね～？", 1, 0, 1, 3951, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110901, 1, "どこかに楽しいもの\n落ちてないかにゃー", 2, 0, 1, 3952, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110901, 1, "チエルのオキニは\n甘々カフェラテ！\nシクヨロでぇ～す☆", 3, 0, 1, 3953, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110901, 1, "先輩、ひまですよね？\nチエル遊んであげても\nいいですけど？", 4, 0, 1, 3954, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110901, 1, "はぁー…しゅき…\nカルミナ尊い…\n一生すこれる…", 5, 0, 1, 3955, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110901, 1, "先輩、ひまー\nちょっとは構えよーう\nデュクシ！デュクシ！", 101, 0, 4, 3956, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 110901, 1, "先輩なんか\nおごって～！\nてか…おごらなくても\nいいから遊んでよ…", 102, 0, 8, 3957, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110901, 1, "え、なんですか？\n告白だったら\n間に合ってますけど", 1, 0, 1, 3958, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110901, 1, "ちょっと静かに！\n今ヤバかわポーズの\nイメトレ中なんです！", 2, 0, 1, 3959, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110901, 1, "足取りが軽やか？\nあー…ダンスとか\n習ってるからですかね", 3, 0, 1, 3960, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110901, 1, "チエルの血液型？\n大型新人のОです\n何か問題でも？", 4, 0, 1, 3961, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110901, 1, "先輩って何気に\nいい声してますよね\n言うことは変だけど", 101, 0, 4, 3962, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110901, 1, "え、なんですか？\nたまにだったら\n告白してもいいですよ", 102, 0, 8, 3963, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110901, 1, "おはちぇる～ん☆\nハイ、ご一緒に～？", 1, 1, 1, 3964, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110901, 1, "今日のランチは\nどこで\n映えよっかにゃー", 2, 2, 1, 3965, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 110901, 1, "こんばんちぇるるん☆\nハイ、ご一緒に～？", 3, 3, 1, 3966, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110901, 1, "ふかふかチエルン\n略して、ふかルン♪", 2, 0, 1, 3968, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110901, 1, "かわいいこの子と\nかわいいチエル！\n映ぁ～え～る～！", 3, 0, 1, 3969, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110901, 1, "かわいい香り～！\n持って帰ろっと！\nすぅーっ…", 4, 0, 1, 3970, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110901, 1, "マシュマロは\n焼かない方のが\nかわいくないです？", 5, 0, 1, 3971, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110901, 1, "チエル食べても\n太らない体質\nなんでぇ～！", 7, 0, 1, 3973, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 110901, 1, "読書は好きですよ\nシンプルに楽しいし\nギャップ萌えでしょ？", 8, 0, 1, 3974, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112701, 1, "みんなのあたしを\n見る目が違うね\nま、気にしないけどね", 1, 0, 1, 3976, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112701, 1, "今日も疲れた～\nやる気ゼロだし\nもう寝ちゃおうかな", 2, 0, 1, 3977, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112701, 1, "あたしの代わりに誰か\n働いてくれないかね", 3, 0, 1, 3978, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112701, 1, "労働の後に食べる\nあんパン最高～", 4, 0, 1, 3979, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112701, 1, "仕事の合間に\nちょっと一息いれよ\nあ～ドングリ美味し～", 5, 0, 1, 3980, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112701, 1, "報酬も入ったことだし\nあいつに何か\n買ってあげようかな", 101, 0, 4, 3981, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112701, 1, "次の休みはあいつと\n一日中だらだら\nしたいな～", 102, 0, 8, 3982, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112701, 1, "あたしは褒められて\n伸びるタイプなんだ\nだから褒めて褒めて", 1, 0, 1, 3983, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112701, 1, "お、差し入れ？\n気が利くねぇ\nあんがと", 2, 0, 1, 3984, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112701, 1, "新しい依頼？\n今日はもう無理～\n閉店で～す", 3, 0, 1, 3985, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112701, 1, "サ、サボってないよ！\nちょっと休憩してた\nだけだから！", 4, 0, 1, 3986, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112701, 1, "ねえ、一緒に\nサボろうよ？\n働き過ぎはよくないよ", 101, 0, 4, 3987, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112701, 1, "あんたが近くにいると\n仕事の疲れも\n和らぐんだよね～", 102, 0, 8, 3988, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112701, 1, "はぁ…もう起きなきゃ\nつらい労働の\nはじまりだ～", 1, 1, 1, 3989, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112701, 1, "うぅ、お昼に\nあんパン食べ過ぎた…\n苦しい…眠い…", 2, 2, 1, 3990, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112701, 1, "明日は仕事ないし\nガッツリ夜ふかし\nしちゃうよ～", 3, 3, 1, 3991, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112701, 1, "猫みたいに\n丸くなっちゃうよ～\nあたしリスだけどね", 2, 0, 1, 3993, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112701, 1, "ふかふか～\nあたしの尻尾と\n互角とはやるね～", 3, 0, 1, 3994, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112701, 1, "あんパンの香りの\nアロマとか\nあればいいのにな～", 4, 0, 1, 3995, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112701, 1, "これは危険な\nあたたかさだね～\n労働意欲が\nなくなるよ～", 5, 0, 1, 3996, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112701, 1, "いっぱい働いたから\nいっぱい食べても\nカロリー０だよ", 7, 0, 1, 3998, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112701, 1, "あたしだってたまには\n本くらい読むって\n全然似合わないけどね", 8, 0, 1, 3999, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104601, 1, "あたしの作った\nオリジナルたい焼き…\nあいつ、\n喜んでくれるかにゃ？", 103, 0, 12, 4001, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104601, 1, "あ、お帰りにゃー♪\nえへへ…キミに\nお帰りって言えるのが\nなんかうれしいにゃ♪", 103, 0, 12, 4002, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112801, 1, "フロンティア\nスピリッツを\nお見せするべ！", 1, 0, 1, 4003, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112801, 1, "この鎧を着ると\n気が引き締まる\n感じがするべ", 2, 0, 1, 4004, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112801, 1, "搾りたての牛乳飲んで\n今日も一日\nがんばるべ！", 3, 0, 1, 4005, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112801, 1, "みんなに牧場の\n乳製品を\n味わってもらいてぇべ", 4, 0, 1, 4006, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112801, 1, "ここはいいとこだべ\nうちの動物たちも\n連れてきてやりてぇな", 5, 0, 1, 4007, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112801, 1, "みんなオラより\n若ぇのに働いてて\n立派だべな", 101, 0, 4, 4008, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112801, 1, "牧場もギルドの\nみんなもオラが\nまとめて面倒見るべ！", 102, 0, 8, 4009, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112801, 1, "さあ、今日も\n忙しくなるべよ\n気合入れてくべ！", 1, 0, 1, 4010, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112801, 1, "お、あんちゃん\n丁度いいとこに！\n一緒に街に\n行商しに行くべ！", 2, 0, 1, 4011, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112801, 1, "牧場仕事で培った力が\n戦いでも役立つなら\n一石二鳥だべさ！", 3, 0, 1, 4012, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112801, 1, "疲れた時は牧場産の\nミルク菓子で\n糖分補給だべ", 4, 0, 1, 4013, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112801, 1, "これ、牧場の\n新製品だべ\n一番最初に味見\nしてほしいべさ", 101, 0, 4, 4014, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112801, 1, "働きづめはよくねぇベ\n膝枕してやっから\nこっち来るべさ", 102, 0, 8, 4015, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112801, 1, "ふぁあ…\n酪農家の朝は\n早ぇんだべ…", 1, 1, 1, 4016, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112801, 1, "ごはんの時間だべ！\n腹が減っては酪農も\n戦いもできねえべ！", 2, 2, 1, 4017, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112801, 1, "そろそろ寝るべ！\n健全なお笑いは\n健全な肉体に宿るべ！", 3, 3, 1, 4018, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112801, 1, "牧草みたいに\nふっかふかだべ～", 2, 0, 1, 4020, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112801, 1, "この子、目の感じが\nエリザベスに似てるべ", 3, 0, 1, 4021, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112801, 1, "いい匂いだべ～\n疲れた体に\nしみるべな～", 4, 0, 1, 4022, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112801, 1, "あったけぇな～\n牧場にも\n置いてみっかな～", 5, 0, 1, 4023, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112801, 1, "うまいべ！\n生産者のこだわりを\n感じるべさ", 7, 0, 1, 4025, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112801, 1, "酪農も戦いも\nまずは知識だべ！", 8, 0, 1, 4026, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113001, 1, "ぴょん、ぴょん♪\nこの服のおかげで、\n体が軽いなぁ…♪", 1, 0, 1, 4028, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113001, 1, "今の内に部屋の\n掃除をしておこっと…\nみんな喜んで\nくれるといいなぁ", 2, 0, 1, 4029, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113001, 1, "耳と尻尾…この服、\n本当にウサギさん\nみたいだなぁ", 3, 0, 1, 4030, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113001, 1, "なんだか先輩の\n気配がするような…\n気のせいかな？", 4, 0, 1, 4031, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113001, 1, "ウサギさんの真似を\nしたら、存在感が薄い\nとか言われないかな？\nア、アユミだぴょん♪", 5, 0, 1, 4032, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113001, 1, "えへへ…\nいつもとは違う先輩が\nたくさん見られて、\nうれしいな♪", 101, 0, 4, 4033, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113001, 1, "本の中の世界で先輩と\n旅をするなんて…\n幸せすぎて、罰が\n当たりそうだよぉ…", 102, 0, 8, 4034, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113001, 1, "じ～…はっ！？\nす、すみません！\n話しかけてもらえる\nなんて思ってなくて！", 1, 0, 1, 4035, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113001, 1, "先輩がすぐそばに…\nし、幸せすぎて\n倒れそうですぅ！", 2, 0, 1, 4036, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113001, 1, "え、えっと…あの…！\nうう…いざ先輩と\n向き合うと\n緊張しちゃうよぉ…", 3, 0, 1, 4037, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113001, 1, "はうっ！？\nしょ、正面から見る\n先輩は、私にとって\n神々しすぎますぅ…！", 4, 0, 1, 4038, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113001, 1, "先輩…その…\nい、一緒にご飯でも\nどうでひゅか！\nうう…またかんだぁ…", 101, 0, 4, 4039, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113001, 1, "せ、先輩…\nハーブティーを\n入れたんですが、\n一緒にどうですか…？", 102, 0, 8, 4040, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113001, 1, "せ、先輩！\nおはようございます！\nえへへ、練習通りに\nバッチリ言えたぁ♪", 1, 1, 1, 4041, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113001, 1, "あ、もうお昼ですね\n時計を持ってて\n良かったです", 2, 2, 1, 4042, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113001, 1, "もうこんな時間…\nお休みする\n準備をしちゃいますね", 3, 3, 1, 4043, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113001, 1, "あ…ちょっとだけ\n先輩の匂いがする…\nえへへ、幸せだなぁ♪", 2, 0, 1, 4045, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113001, 1, "…私もウサギさんの\nぬいぐるみとして、\n先輩のそばに…はっ！\n私ったら何を…！", 3, 0, 1, 4046, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113001, 1, "あ、いい香り…\n先輩もこういうのが\n好きなのかな？", 4, 0, 1, 4047, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113001, 1, "先輩が隣に\n居たら、もっと\n暖かいのになぁ…♪", 5, 0, 1, 4048, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113001, 1, "おいしい…でも、\n先輩と一緒に\n食べたら、もっと\nおいしいだろうな…", 7, 0, 1, 4050, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113001, 1, "ウサギさんって、穴を\n掘ったりするんだ…\n私も、隠れたいときに\n掘ってみようかな？", 8, 0, 1, 4051, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180501, 1, "ネビアさまのように\n普段からパタパタと\n飛んでみるのも…", 1, 0, 1, 4053, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180501, 1, "ご立派になられた\n主さまと並べるように\nもっと精進しなくては", 2, 0, 1, 4054, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180501, 1, "月々の食費が\n増えてしまいました\n申し訳ございません…", 3, 0, 1, 4055, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180501, 1, "ワンワンのほうの\n主さまは、この姿に\n驚くでしょうか…？", 4, 0, 1, 4056, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180501, 1, "主さまと離れていても\n主さまとの絆は\n胸に感じております…", 5, 0, 1, 4057, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180501, 1, "このギルドハウスは\nみなさまの帰る場所…\nわたくしが守ります", 101, 0, 4, 4058, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180501, 1, "どんな未来が\n待ち受けようとも\n主さまと添い遂げます", 102, 0, 8, 4059, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180501, 1, "おでかけですか？\nではわたくしも\nお供いたします", 1, 0, 1, 4060, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180501, 1, "もう二度と主さまと\n離れたくはありません\nいつ何時も…", 2, 0, 1, 4061, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180501, 1, "なにかお困りごと\nでございますか？\nわたくしに何なりと…", 3, 0, 1, 4062, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180501, 1, "どうぞゆっくりと\n心ゆくまでくつろいで\nくださいまし、主さま", 4, 0, 1, 4063, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180501, 1, "この羽で主さまを\nどこまでも遠く…\n高くへと導きます", 101, 0, 4, 4064, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180501, 1, "主さま、主さま…♪\nおそばにいられるのが\nうれしくて、つい…", 102, 0, 8, 4065, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180501, 1, "おはようございます\n今日も一日、主さまの\nサポートをいたします", 1, 1, 1, 4066, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180501, 1, "今日も頑張っている\n主さまに、スタンプを\nもう一つ…ポン！", 2, 2, 1, 4067, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180501, 1, "寝付けないのでしたら\nわたくしが子守唄を\n歌ってさしあげます", 3, 3, 1, 4068, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 180501, 1, "ふかふか…\nまるで主さまのような\n柔らかさでございます", 2, 0, 1, 4070, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 180501, 1, "あなたも一緒に\n主さまと遊びますか？\n至福の時間ですよ♪", 3, 0, 1, 4071, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 180501, 1, "主さまの優しくて\nくすぐったい匂いが\nたまらなく恋しいです", 4, 0, 1, 4072, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 180501, 1, "主さまの心と\n繋がったときのような\n温もりを感じます", 5, 0, 1, 4073, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 180501, 1, "いけませんっ…！\n元の姿に戻ると、また\n食べてしまうのに…！", 7, 0, 1, 4075, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 180501, 1, "主さまに読み聞かせる\n絵本は…妖精が主役の\n物語にいたしましょう", 8, 0, 1, 4076, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104801, 1, "…彼のこと弟みたいに\n思ってきたのに…私、\nどうしたらいいの…？", 103, 0, 12, 4078, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104801, 1, "よ、予定がなかったら\nでいいんだけど…\nその、ふ、二人で\n訓練とか…どうかな？", 103, 0, 12, 4079, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112901, 1, "おめかしもしたし\nあとはお兄ちゃんを\n待つだけですね～♪", 1, 0, 1, 4080, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112901, 1, "お兄ちゃんの身の回り\nをお片付け～、これぞ\nデキる妹アピール！", 2, 0, 1, 4081, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112901, 1, "あ、衣装がほつれてる\nこれくらいなら自分で\n繕えますね…！", 3, 0, 1, 4082, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112901, 1, "この格好で歩いてると\n見慣れた景色も\n違うものに見えますね", 4, 0, 1, 4083, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112901, 1, "勉強もちゃんとします\n…でも、一人だと\n集中力がもちません～", 5, 0, 1, 4084, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112901, 1, "お兄ちゃんを大好きな\n妹が待ってますよ～\n…まだですかね～", 101, 0, 4, 4085, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 112901, 1, "お兄ちゃんのために\n掃除洗濯をする私って\nお嫁さんみたい…\n…えへへ～", 102, 0, 8, 4086, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112901, 1, "おに～いちゃん！\n縫い物、一緒に\nやりましょうよ！", 1, 0, 1, 4087, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112901, 1, "かわいい妹を\n愛でたくなりました？\n思う存分、どうぞ♪", 2, 0, 1, 4088, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112901, 1, "わっ、ビックリした～\nもう、お兄ちゃんてば\nイタズラっ子ですね～", 3, 0, 1, 4089, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112901, 1, "一緒にお買い物\n行きませんか？\n妹とデートですよっ♪", 4, 0, 1, 4090, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112901, 1, "お兄ちゃん…頭を\n撫でてくれませんか？\nえへへ、これが一番♪", 101, 0, 4, 4091, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112901, 1, "お兄ちゃん大好き♪\n何度でも言いますよ！\nお兄ちゃん大好きっ♪", 102, 0, 8, 4092, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112901, 1, "おはよ～お兄ちゃん！\n妹モーニングコールの\n時間ですよっ♪", 1, 1, 1, 4093, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112901, 1, "そろそろ休憩ですね！\n甘いお菓子で\n元気回復しましょ～！", 2, 2, 1, 4094, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 112901, 1, "夜更かし厳禁！\nでも、もう少しだけ\nお話ししたいなぁ～", 3, 3, 1, 4095, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112901, 1, "ふっかふかですよ！\nお兄ちゃんと一緒に\n座りたいなぁ…♪", 2, 0, 1, 4097, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112901, 1, "このぬいぐるみの\nかわいさ…う～ん\n負けられません…！", 3, 0, 1, 4098, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112901, 1, "香りにも気をつかって\nパーフェクトな妹に\nなっちゃいますから！", 4, 0, 1, 4099, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112901, 1, "はぁ～ぬくぬくです～\nお兄ちゃんと一緒に\n温まりたいなぁ…♪", 5, 0, 1, 4100, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112901, 1, "これは止まりませんね\nもぐもぐ…いくらでも\n食べられちゃいます", 7, 0, 1, 4102, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 112901, 1, "これ、デザインの\n参考になりそう！\n私にも作れるかなぁ", 8, 0, 1, 4103, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106601, 1, "退屈です…\nなにか楽しいことは\nないですか？", 1, 0, 1, 4105, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106601, 1, "ギャングの仕事は\n今日はお休みです\nのんびりするですよ", 2, 0, 1, 4106, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106601, 1, "ひうっ！　あたしの\n尻尾なんて食べても\nおいしくないですよ？", 3, 0, 1, 4107, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106601, 1, "ふう…どうやら\n例の件はまだボスに\nバレてないですね…", 4, 0, 1, 4108, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106601, 1, "下克上…いい言葉です\nって、ボスに聞かれて\nないですよね…？", 5, 0, 1, 4109, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106601, 1, "いつか絶対ビッグに\nなってやるです！\nあたしを信じるです！", 101, 0, 4, 4110, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106601, 1, "あたしが覚醒したら\nきっと誰よりも\n強くなるはずです！", 102, 0, 8, 4111, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106601, 1, "なんですか？\n貢ぎ物なら\nいつでも歓迎ですよ！", 1, 0, 1, 4112, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106601, 1, "あたしだって\nやる時はやるですよ！\nその時がこないだけ！", 2, 0, 1, 4113, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106601, 1, "しりとり勝負ですか？\nいい度胸ですね、\nかかってくるです！", 3, 0, 1, 4114, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106601, 1, "お兄さんは\nヒマそうですね…\nうらやましいです", 4, 0, 1, 4115, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106601, 1, "かまってほしいです？\nまったく…いいですよ\nしょうがないですね", 101, 0, 4, 4116, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106601, 1, "お兄さんのことは\nこのあたしが\n守ってやるですよ！", 102, 0, 8, 4117, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106601, 1, "デキるギャングの\n朝は早いのです…\nふぁあ…", 1, 1, 1, 4118, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106601, 1, "お腹がすいたですね\nなにか食べにいくです\nお兄さんのおごりで！", 2, 2, 1, 4119, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106601, 1, "ふぁあ…むにゅう…\nまだ起きてるですよぉ", 3, 3, 1, 4120, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106601, 1, "はぁ～…\nもうここから一歩も\n動きたくないです", 2, 0, 1, 4122, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106601, 1, "なかなかいいモフりを\nしてやがるです…\n舎弟にしてやるですよ", 3, 0, 1, 4123, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106601, 1, "なんかおしゃれな\n匂いがするですね…", 4, 0, 1, 4124, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106601, 1, "ふふーん！\nあたしのブレスの方が\n火力は上ですね！", 5, 0, 1, 4125, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106601, 1, "あたしに貢ぎ物とは\nよく分かってるですね\n食べてやるですよ", 7, 0, 1, 4127, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106601, 1, "ふーむ…\nもっと楽しい本は\nないですかね？", 8, 0, 1, 4128, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113201, 1, "海に眠る秘宝…\n心躍る響きだ！\n早く見つけたい…！", 1, 0, 1, 4130, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113201, 1, "うう～、暑いぃ…\nかき氷を食べて\nすっきりしたいぃ…", 2, 0, 1, 4131, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113201, 1, "アターック！\nいや、もっとこう…\nアタァーック！", 3, 0, 1, 4132, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113201, 1, "スイカ割りで\n目を回さない\nコツはなんだろうか…", 4, 0, 1, 4133, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113201, 1, "せっかく海に来たのだ\n泳ぎも上手に\nなりたいものだな", 5, 0, 1, 4134, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113201, 1, "ふっふっふ…\n疼く…疼くぞ！\n日焼けの痕が…", 101, 0, 4, 4135, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113201, 1, "シグルドと私は\n来世でも共にある…\nつまり我らはずっと…\nはわわ…！", 102, 0, 8, 4136, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113201, 1, "シグルド、私と共に\n日光浴に行かないか？\n光の力を集めよう！", 1, 0, 1, 4137, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113201, 1, "この格好だからできる\n必殺技を考えている\n何か案はないか？", 2, 0, 1, 4138, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113201, 1, "シグルド～！\nスイカを貰ったから\n一緒に食べよう！", 3, 0, 1, 4139, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113201, 1, "こう日差しが強いと\n日焼けしそうだな\n跡になってないか…？", 4, 0, 1, 4140, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113201, 1, "我らの夏の黙示録\nしっかりと記録\nしておかねばな", 101, 0, 4, 4141, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113201, 1, "ちゃんと日焼けどめを\n塗ってもらうべき\nだったかな…？\nシ、シグルドに…！", 102, 0, 8, 4142, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113201, 1, "朝だぞシグルド！\nさぁ、日光を浴びて\n光の加護を得るのだ！", 1, 1, 1, 4143, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113201, 1, "もう昼食の時間か…\n私の魔力も減ったし\n早く補給しなければ", 2, 2, 1, 4144, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113201, 1, "夜でも我らの活動は\n終わることはない！\nさぁ準備を…ふわぁ…", 3, 3, 1, 4145, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113201, 1, "海で遊んでいると\n疲れてしまうからな\n少し休まるとしよう", 2, 0, 1, 4147, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113201, 1, "えへへ～、君の名前は\n何にしまちょうかね♪\n…はっ、見たのか！？", 3, 0, 1, 4148, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113201, 1, "心地よい香りで\n我が精神も安らぐ…", 4, 0, 1, 4149, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113201, 1, "別に寒くなどないぞ！\n寒くないが、ちょっと\n暖まりたくなってな！", 5, 0, 1, 4150, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113201, 1, "もぐ…食べ過ぎた分は\n魔力に変換されるのだ\n…たぶん", 7, 0, 1, 4152, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113201, 1, "…なな、なんだ！？\nシグルドはこういう\n趣味なのか…！？", 8, 0, 1, 4153, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113201, 1, "むむ、この乗り心地…\nいざゆかん、\nシードラゴンよ！", 10, 0, 1, 4155, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113201, 1, "疾風の冥姫の名に\nふさわしき\n深海の玉座よ…\nふああ…柔らかい～", 11, 0, 1, 4156, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180201, 1, "この姿でいると\nわたしもお姫様に\nなれた気がする…♪", 1, 0, 1, 4157, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180201, 1, "お腹空いてきちゃった\nまずは買い出しに…\n…変身解かなくちゃ！", 2, 0, 1, 4158, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180201, 1, "胸のところが空いてて\nスースーするの\nまだ慣れないな…", 3, 0, 1, 4159, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180201, 1, "治癒魔法に使ってた\n魔力を攻撃魔法に…\n頭の中でも練習練習", 4, 0, 1, 4160, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180201, 1, "何もできなかった\nわたしから、少しは\n変われたのかな…？", 5, 0, 1, 4161, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180201, 1, "騎士クン…\n今どこにいるの…？\n…会いたいな", 101, 0, 4, 4162, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180201, 1, "離れてても分かるよ…\n温かい力で騎士クンに\n守られてるの", 102, 0, 8, 4163, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180201, 1, "わたしが戦えてるのは\n騎士クンのおかげだよ\nいつもありがとう！", 1, 0, 1, 4164, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180201, 1, "お腹空いてそう…？\n…内緒で作ったおやつ\n騎士クンも食べる？", 2, 0, 1, 4165, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180201, 1, "ひゃうっ！　え！？\nせ、背中はダメ～！\n言ってから触って～！", 3, 0, 1, 4166, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180201, 1, "この格好、どうかな？\nえ？　似合ってる…？\n本当？　…うれしい！", 4, 0, 1, 4167, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180201, 1, "かわいい？　そうだね\nひらひらが…わたし？\nええっ、そ、そんな！", 101, 0, 4, 4168, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180201, 1, "騎士クン、これからも\nずっと…わたしの\nそばにいてください", 102, 0, 8, 4169, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180201, 1, "朝からこんなに\n食べる日が来るなんて\n思ってなかったよ…", 1, 1, 1, 4170, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180201, 1, "お昼を作らなくちゃ\n騎士クン、一緒に\nお料理してみる？", 2, 2, 1, 4171, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180201, 1, "騎士クン、もう眠い？\n眠るまで、わたしが\nずっとそばにいるね", 3, 3, 1, 4172, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 180201, 1, "攻撃参加するからこそ\n適度に体を休めるのは\n大切だよね！", 2, 0, 1, 4174, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 180201, 1, "かわいい…騎士クンと\nシェフィちゃんにも\nプレゼントしたいな", 3, 0, 1, 4175, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 180201, 1, "いい香り～…\nあれ？　お腹が空いて\nきちゃった…！？", 4, 0, 1, 4176, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 180201, 1, "温かい…\n騎士クンがくれる\n力みたい…", 5, 0, 1, 4177, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 180201, 1, "ぱくっ、\nあむ、はむっ…\nううぅ～…やっぱり\n手が止まらないよぉ～", 7, 0, 1, 4179, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 180201, 1, "新しく覚えたいな…\nみんなで食べられる\n大皿向けの料理♪", 8, 0, 1, 4180, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104901, 1, "お姉ちゃんの愛\nこのままじゃ\nあふれちゃうよ…♪", 103, 0, 12, 4182, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104901, 1, "弟くん♪\n弟くんっ♪\nおっとうっとくんっ♪", 103, 0, 12, 4183, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113101, 1, "泳ぐのもいいけど\n今日は砂浜で\nのんびりしようかね", 1, 0, 1, 4184, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113101, 1, "さてと\nちょっくらひと泳ぎ\n行ってくるかね", 2, 0, 1, 4185, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113101, 1, "あっついねぇ…\nうちわがないと\nしんどいったらないよ", 3, 0, 1, 4186, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113101, 1, "冷たすぎる飲み物で\n体を冷やさないよう\n気をつけないと…", 4, 0, 1, 4187, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113101, 1, "夏はあっという間だし\n悔いがないように\n楽しまないとね", 5, 0, 1, 4188, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113101, 1, "きっとこの夏は\nアタシにとって大事な\n想い出になるね…", 101, 0, 4, 4189, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113101, 1, "…前に比べたら\nアタシたちの距離も\n近づいたみたいだね", 102, 0, 8, 4190, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113101, 1, "もし暇なら\n今日も笹舟釣り\n一緒にどうだい？", 1, 0, 1, 4191, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113101, 1, "お前さん、釣りに\n興味が湧いたなら今度\n道具を見に行こうよ", 2, 0, 1, 4192, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113101, 1, "今から砂浜に行くんだ\nお前さんも一緒に\n散歩するかい？", 3, 0, 1, 4193, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113101, 1, "え、日焼け跡？\nおや…本当だ\nすぐ焼けちまうねえ", 4, 0, 1, 4194, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113101, 1, "ひゃっ！？\nちょっと！\n日焼け跡をいきなり\n触らないでおくれ！", 101, 0, 4, 4195, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113101, 1, "お前さんは、その…\nアタシと一緒にいて\n楽しかったかい？", 102, 0, 8, 4196, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113101, 1, "おはようさん\n今日も砂浜に行って\nゆっくりしようか", 1, 1, 1, 4197, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113101, 1, "お昼か…\nそうだ、よかったら\n刺身定食、作ろうか？", 2, 2, 1, 4198, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113101, 1, "夜の空を見てるとさ\nなつかしい気持ちに\nなるんだよね", 3, 3, 1, 4199, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113101, 1, "適度に休憩して\n夏バテしないよう\n気をつけないとね", 2, 0, 1, 4201, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113101, 1, "あんたたちも\n海を楽しみたいって\n思っているのかね", 3, 0, 1, 4202, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113101, 1, "ん～…香りでも\nバカンスらしさを\n楽しめるもんだね", 4, 0, 1, 4203, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113101, 1, "こういう格好だと\n少し冷えちまうんだよ", 5, 0, 1, 4204, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113101, 1, "もぐもぐもぐ…\n遊んだ分、お腹が\n空いちまってねぇ", 7, 0, 1, 4206, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113101, 1, "…ん、なんだいこの本\n催眠…うーん読めない\nナナカに聞いてみるか", 8, 0, 1, 4207, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113101, 1, "へえ…\nなかなかいい\n乗り心地じゃないか", 10, 0, 1, 4209, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113101, 1, "そうそうハマグリって\n竿でも釣れるんだよ\n知ってたかい？", 11, 0, 1, 4210, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113301, 1, "海を満喫しなきゃね\nちょっとひと泳ぎ\n行ってこようかな？", 1, 0, 1, 4211, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113301, 1, "う～ん…♪\nいつもと違った\n開放感～\nなんちゃって！", 2, 0, 1, 4212, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113301, 1, "今のナナカちゃんは\nたぶん水属性！", 3, 0, 1, 4213, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113301, 1, "水着もバッチリ！\nサイカワサイツヨ\nナナカちゃ～ん☆", 4, 0, 1, 4214, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113301, 1, "んっふっふ\nドキ！　水着だらけの\nギルドハウス！\nとかどうかな？", 5, 0, 1, 4215, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113301, 1, "そんなに見られると\nちょ～っと\n恥ずかしいかも…\nなんてねなんてね！", 101, 0, 4, 4216, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113301, 1, "ちょっとくらい\nドキッとしてくれても\nいいのにな～、もう…", 102, 0, 8, 4217, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113301, 1, "水着魔法少女の\n相方がほしいなぁ～…\nね、君やってみない？", 1, 0, 1, 4218, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113301, 1, "いつもの格好と同じ？\nも～、わかってない！\nセクスィー度が違う！", 2, 0, 1, 4219, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113301, 1, "どうかなどうかな？\nこのひらひらのとこが\nかわいーかなー\nとか思うんだけど！", 3, 0, 1, 4220, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113301, 1, "せっかくだから\nギャルゲーイベント\nごっことか\nやってみる？", 4, 0, 1, 4221, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113301, 1, "ナナカちゃんの\n好感度を上げたい？\nならおでかけだー！", 101, 0, 4, 4222, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113301, 1, "おうちで水着って\nなんだかコスプレ\nみたいで楽しいかも♪", 102, 0, 8, 4223, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113301, 1, "おっはよーう！\n今日はバカンス日和！\n海へゴーですぞ～♪", 1, 1, 1, 4224, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113301, 1, "お昼ご飯どうしよ～…\nあ、ナナカちゃんと\nどっか食べに行く？", 2, 2, 1, 4225, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113301, 1, "夜は本を読むのが\n捗りまくりですな～！\n今日はなに読もっかな", 3, 3, 1, 4226, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113301, 1, "次のイベントもあるし\nちょっと休んで体力を\n回復しなきゃね～", 2, 0, 1, 4228, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113301, 1, "こういうマスコット\n私にもいたらなぁ\nいっそ作っちゃう…？", 3, 0, 1, 4229, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113301, 1, "水着魔法少女に\n似合う香りってのも\n考えてみよっかな♪", 4, 0, 1, 4230, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113301, 1, "夜になると急に冷えて\n肌寒いんだよね～…", 5, 0, 1, 4231, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113301, 1, "あんまり食べると\nお腹が出て…でも…\nうう、止まらない～！", 7, 0, 1, 4233, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113301, 1, "この催眠魔法の本\n誰にもばれないように\n保管しとかなきゃね…", 8, 0, 1, 4234, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113301, 1, "ゴーゴーゴーゴーゴー\nナナカちゃーん♪", 10, 0, 1, 4236, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113301, 1, "ナナカちゃんの\n誕生～\nなんちゃって", 11, 0, 1, 4237, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113401, 1, "ファイトファイト☆\n一生懸命\n応援するね！", 1, 0, 1, 4238, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113401, 1, "みんながんばってる！\n私も応援でみんなを\n盛り上げなきゃ！", 2, 0, 1, 4239, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113401, 1, "もし元気のない人が\nいたら…応援で少し\nでも元気にしたいな！", 3, 0, 1, 4240, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113401, 1, "がんばれ～☆\nよし、発声練習OK！\n応援は、声も大事！", 4, 0, 1, 4241, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113401, 1, "せっかく水着なんだし\n泳いだり潜ったりして\n遊びたいな～！", 5, 0, 1, 4242, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113401, 1, "チアリーダーとして\nキミを精一杯\n応援したいな…！", 101, 0, 4, 4243, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113401, 1, "キミへの応援\nちゃんと\n届いてるかな…", 102, 0, 8, 4244, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113401, 1, "えへへ…今ね、\nキミへの応援歌を\n考えてたのだ～♪", 1, 0, 1, 4245, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113401, 1, "どうしたの？\n元気がないなら\n私が応援するよ☆", 2, 0, 1, 4246, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113401, 1, "がんばったあとは休憩\nしなきゃね！　私と\n一緒にお昼寝する？", 3, 0, 1, 4247, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113401, 1, "シオリンとおそろいの\n水着で応援…\nしてみたいなぁ～", 4, 0, 1, 4248, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113401, 1, "次はなにして遊ぶ？\nキミがしたいことを\n教えてよ！", 101, 0, 4, 4249, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113401, 1, "キミを応援\nすることができて…\n私、すごく幸せ…", 102, 0, 8, 4250, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113401, 1, "キミって早起き\nなんだね！　私も\n見習わなきゃ！", 1, 1, 1, 4251, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113401, 1, "ちょっとお疲れかな？\nそれじゃあ\n応援してあげるね♪", 2, 2, 1, 4252, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113401, 1, "遅い時間まで\nがんばってえらいね！\nでも無理はダメだよ♪", 3, 3, 1, 4253, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113401, 1, "ちょっとだけ休憩…\n…あう、座り心地が\n良すぎて…眠気が…", 2, 0, 1, 4255, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113401, 1, "ここにいるみんなを\n私の応援でもっと\n元気にするのだ～！", 3, 0, 1, 4256, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113401, 1, "元気の出る香り～♪\nこれもうまく応援に\n活かせないかな～？", 4, 0, 1, 4257, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113401, 1, "ぽかぽかあったまって\n元気をチャージ\nするのだ～☆", 5, 0, 1, 4258, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113401, 1, "いっぱい食べて、\nみんなを応援する\nパワーをつけなきゃ！", 7, 0, 1, 4260, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113401, 1, "みんなを元気にする\n方法は…どこかに\n書いてないかなぁ…？", 8, 0, 1, 4261, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113401, 1, "わぁ、この子\n怪獣さんみたいで\nかわいいね～♪", 10, 0, 1, 4263, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113401, 1, "水着でこれに\n乗ってると海にいる\n気分になれるね！", 11, 0, 1, 4264, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113501, 1, "すごく忙しいけど\nとても充実した気分\nこれが青春なのかしら", 1, 0, 1, 4265, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113501, 1, "森の静かな雰囲気も\n海の開放的な空気も\nどちらも素敵ね～", 2, 0, 1, 4266, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113501, 1, "汚れたお洗濯物は\nみんなが頑張った証\nなんだか愛おしいわ", 3, 0, 1, 4267, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113501, 1, "みんなを見てると\n何だか私も\n走りたくなっちゃうわ", 4, 0, 1, 4268, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113501, 1, "せっかくの水着だし\n泳ぐ練習でも\nしてみようかしら", 5, 0, 1, 4269, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113501, 1, "みんな～ファイト～！\n応援するのも大事よね\n練習しておかなきゃ♪", 101, 0, 4, 4270, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113501, 1, "先生に教えられること\nまだまだい～っぱいね\n次は何がいいかしら♪", 102, 0, 8, 4271, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113501, 1, "今日も暑くなりそうね\nこまめな水分補給を\n忘れないでね", 1, 0, 1, 4272, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113501, 1, "手伝ってくれるの？\nありがとう♪\n先生、頼りにしてるわ", 2, 0, 1, 4273, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113501, 1, "休憩はこまめに\nとりましょうね\n無理は厳禁よ", 3, 0, 1, 4274, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113501, 1, "海の遊びって\nどんなのがあるの？\n先生に教えてね", 4, 0, 1, 4275, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113501, 1, "水着は初めてだけど\nどう？　変じゃない？\nふふ、ありがとう", 101, 0, 4, 4276, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113501, 1, "あなたと過ごした\nこの夏は、きっと…\n忘れられない思い出ね", 102, 0, 8, 4277, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113501, 1, "おはよう～\n朝練前にちゃんと\nごはん食べましょうね", 1, 1, 1, 4278, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113501, 1, "お弁当作ってきたの\n栄養いっぱい摂って\n午後も頑張ろうね", 2, 2, 1, 4279, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113501, 1, "もうお休みしなきゃね\n先生が子守唄を歌って\nあげましょうか？", 3, 3, 1, 4280, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113501, 1, "ふふ、いい気持ちねぇ\n先生、少しだけ\n休憩しちゃおうかしら", 2, 0, 1, 4282, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113501, 1, "あらかわいい♪\nチームにもこういう\nマスコットが欲しいわ", 3, 0, 1, 4283, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113501, 1, "あら、海の香り？\n波の音まで聞こえて\nきちゃいそうだわ", 4, 0, 1, 4284, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113501, 1, "うふふ\nこれなら濡れた水着を\n乾かすのも簡単ね♪", 5, 0, 1, 4285, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113501, 1, "これ、美味しいわね♪\nでも、食べ過ぎは\n禁物よ", 7, 0, 1, 4287, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113501, 1, "先生だって日々勉強よ\n新しいことを知るのは\nとても楽しいわ♪", 8, 0, 1, 4288, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113501, 1, "あらあら、楽しいわね\nみんなもきっと\nおおはしゃぎね", 10, 0, 1, 4290, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113501, 1, "ふふ、これで\n夏らしい気分を\n満喫するわよ♪", 11, 0, 1, 4291, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 102301, 1, "サンドイッチも\n作ったし、\nお兄ちゃんのお出迎え\n準備は完璧だね♪", 103, 0, 12, 4292, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102301, 1, "えへへ、お兄ちゃんと\nぷうきちが居てくれる\nだけで、なんだか\nうれしくなっちゃう♪", 103, 0, 12, 4293, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113601, 1, "少し赤くなっているな\n日焼けなんてしたのは\nいつ以来だろう", 1, 0, 1, 4294, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113601, 1, "この格好で\n過ごすのは\n少し勇気がいるな…", 2, 0, 1, 4295, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113601, 1, "暑いのは平気だが\n肌が外気に触れるの\nにはまだ慣れないな…", 3, 0, 1, 4296, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113601, 1, "この格好でも\n堂々としていれば\n恥ずかしくはない…\nのかな？", 4, 0, 1, 4297, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113601, 1, "おっと…鎧姿の時と\n重心が違うせいか\nちょっと不安定だな", 5, 0, 1, 4298, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113601, 1, "この水着…\n私に似合って\nいるのだろうか…？\n少年には聞きづらいな", 101, 0, 4, 4299, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113601, 1, "少年との\n海のバカンス…\nまた行きたい\nものだな", 102, 0, 8, 4300, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113601, 1, "あまり見るのは\nよしてくれ…\n恥ずかしいじゃ\nないか…", 1, 0, 1, 4301, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113601, 1, "どうかしたのかい？\n私でよければ\n話し相手くらいには\nなろうじゃないか", 2, 0, 1, 4302, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113601, 1, "ひゃっ！\nす、すまない\nちょっと過敏に\nなっているようだ", 3, 0, 1, 4303, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113601, 1, "…っくしゅん！\nすまない\n薄着には慣れて\nないものでね", 4, 0, 1, 4304, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113601, 1, "まあ、少年になら\n見られても…\nい、いや！\nなんでもない！", 101, 0, 4, 4305, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113601, 1, "私がこうして\nゆっくりできるのは\n少年がそばにいて\nくれるからだよ", 102, 0, 8, 4306, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113601, 1, "この時間なら\nビーチにも人は\n少ないだろうね…\n遊びに行くかい？", 1, 1, 1, 4307, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113601, 1, "昼食のお誘いかい？\nいいよ、付き合おう", 2, 2, 1, 4308, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113601, 1, "もう夜か…\n一日が経つのは\nはやいものだね", 3, 3, 1, 4309, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113601, 1, "これは楽でいいな…\nいつもは鎧姿で\n家具を傷つけないか\n気を使っているからね", 2, 0, 1, 4311, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113601, 1, "うん…ふわふわの\n感触が\nよくわかるな…", 3, 0, 1, 4312, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113601, 1, "いつもは\n鎧に匂いが\n移ってしまうのが\n悩みなんだよ…", 4, 0, 1, 4313, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113601, 1, "うーむ…\n涼しいのに暖かい\nなんだか奇妙な気分だ", 5, 0, 1, 4314, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113601, 1, "兜を気にせず\n食べられるのは\n楽でいいな…", 7, 0, 1, 4316, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113601, 1, "ふむ…鎧がないと\nページがめくりやすい", 8, 0, 1, 4317, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113601, 1, "身軽でいいな…\n鎧を着ていると\n騎乗訓練の時\n馬に申し訳がなくてね", 10, 0, 1, 4319, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113601, 1, "貝をうまく使えば\n水中でも\n守りを固められそうだ", 11, 0, 1, 4320, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106801, 1, "どこをアジトと\nつなげようかな～…\nこのへんにしよっと♪", 1, 0, 1, 4321, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106801, 1, "ここってちょっと\n変わった建物だね\n妖精がいるでしょ？", 2, 0, 1, 4322, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106801, 1, "色んな人を観察できて\n眼福眼福…♪", 3, 0, 1, 4323, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106801, 1, "家具の配置変更なら\nアタシにおまかせあれ\n…家の形は変えない？", 4, 0, 1, 4324, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106801, 1, "新メニューどうしよう\n思いついた人がいたら\nアタシに教えてね！", 5, 0, 1, 4325, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106801, 1, "少年、早く帰って\nこないかな…\nよし、迎えに行こう！", 101, 0, 4, 4326, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106801, 1, "結局またあちこち\n移動する日々だし…\n少年に会いたいな～", 102, 0, 8, 4327, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106801, 1, "どしたの少年？\nお姉さんに何でも\n相談してみな～？", 1, 0, 1, 4328, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106801, 1, "あれをああして…\nおや少年！　ごめんね\nすぐに気づけなくて！", 2, 0, 1, 4329, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106801, 1, "何？　クリスが\nアタシを探してる？\n…いないって言って！", 3, 0, 1, 4330, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106801, 1, "よければリノちゃんと\nシズルちゃんにも\n顔を見せてあげて", 4, 0, 1, 4331, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106801, 1, "少年～！\nもうヘトヘトだよ～！\nお願い癒して～！", 101, 0, 4, 4332, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106801, 1, "…アタシが素直に\n助けてって言えるのは\n少年だけだよ", 102, 0, 8, 4333, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106801, 1, "朝だよ少年！\n爽やかな朝の\nクレープはいかが？", 1, 1, 1, 4334, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106801, 1, "お腹空いたな～\nお昼のクレープ\n少年は食べる？", 2, 2, 1, 4335, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106801, 1, "夜までクレープは\nやめとけ？　ごめん\nそれだけは無理…！", 3, 3, 1, 4336, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106801, 1, "アタシこんなに\nゆっくりしちゃって\nいいんだっけ～？", 2, 0, 1, 4338, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106801, 1, "…これ\nネネカが変身\nしてないよね？", 3, 0, 1, 4339, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106801, 1, "いい香りだ…\n解析完了\n今度生成しようっと♪", 4, 0, 1, 4340, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106801, 1, "安心するな～…\nアジトに置けないから\n羨ましいよ", 5, 0, 1, 4341, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106801, 1, "食べ物を消化するって\n仕組み、どんなものか\n気になる…", 7, 0, 1, 4343, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106801, 1, "速読できるけど\n本を読む時くらい\n文字にじっくり\n浸かりたいんだよね…", 8, 0, 1, 4344, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113701, 1, "エンジェルとして\nみんなを元気に…！\nアカリ、頑張ります♪", 1, 0, 1, 4346, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113701, 1, "お姉ちゃんと一緒に\nお兄ちゃんを…えへへ\nまだかな、お兄ちゃん", 2, 0, 1, 4347, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113701, 1, "エンジェルの音色を\n目指して、今日も\nサックスの練習です♪", 3, 0, 1, 4348, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113701, 1, "お兄ちゃんとの特訓も\n頑張らないと…\n厳しくしてくださいね", 4, 0, 1, 4349, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113701, 1, "かわいいエンジェルの\n格好、お兄ちゃんなら\n喜んでくれますよね♪", 5, 0, 1, 4350, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113701, 1, "エンジェルの力で\nお兄ちゃんのハートも\nイチコロですよ♪", 101, 0, 4, 4351, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113701, 1, "アカリが一番\n元気にしたいのは\nお兄ちゃんですよ♪", 102, 0, 8, 4352, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113701, 1, "お兄ちゃん？\n触りたいなら…\nこことかどうですか♪", 1, 0, 1, 4353, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113701, 1, "特訓の時間ですか？\nアカリ、楽しみに\nしてたんですよ♪", 2, 0, 1, 4354, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113701, 1, "あ、お兄ちゃん！\nそろそろアカリと\n休憩…しませんか♪", 3, 0, 1, 4355, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113701, 1, "この羽、触り心地\nいいですか？　あんっ\nくすぐったいですよぉ", 4, 0, 1, 4356, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113701, 1, "お兄ちゃんも元気に\nなりたいんですか？\nアカリにお任せです♪", 101, 0, 4, 4357, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113701, 1, "やんっ♪\nお兄ちゃんったら\nダイタンですね♪", 102, 0, 8, 4358, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113701, 1, "お兄ちゃん、朝ですよ\nすっごく気持ちのいい\nお天気ですよ…♪", 1, 1, 1, 4359, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113701, 1, "お昼寝しませんか\nお兄ちゃん？\nアカリの膝枕で…♪", 2, 2, 1, 4360, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113701, 1, "夜ですね、お兄ちゃん\nアカリ、なんだか胸が\nドキドキしちゃいます", 3, 3, 1, 4361, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113701, 1, "フカフカですね…\n隣にお兄ちゃんが\nいてくれたらなぁ…♪", 2, 0, 1, 4363, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113701, 1, "えへへ、アカリも\nこんな風にかわいく\nなりたいな～", 3, 0, 1, 4364, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113701, 1, "お兄ちゃんが好きな\n匂いなのかな？\n参考にしますね！", 4, 0, 1, 4365, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113701, 1, "体が火照ってきて\nなんだかアカリ…\nクラクラしちゃう…", 5, 0, 1, 4366, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113701, 1, "はむ、んぐ…あっ\nほっぺについちゃった\nベトベトですよぉ～", 7, 0, 1, 4368, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113701, 1, "エンジェルって色んな\n物語に出てきますよね\nもっと調べなくちゃ！", 8, 0, 1, 4369, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103201, 1, "あの方の前では\n常に気高く\nありたいですわね", 103, 0, 12, 4371, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103201, 1, "どんなに道に迷っても\nあなたさまの元には\n絶対に辿り着く\n自信がありますわ", 103, 0, 12, 4372, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 102801, 1, "あんたと一緒に\nいるときが一番楽しい\n一時かもしれないわ", 103, 0, 12, 4373, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102801, 1, "いつかあんたと一緒に\nサレンディア救護院を\n切り盛り…なんてね♪", 103, 0, 12, 4374, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113801, 1, "今日も\nエンジェルとして、\nみんなの幸せの\nために頑張らないと", 1, 0, 1, 4375, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113801, 1, "せっかく羽が\nついてるのに\n飛べないのが残念だわ", 2, 0, 1, 4376, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113801, 1, "この格好、子供たちに\n妙にウケがいいわね\nま、悪くないけど", 3, 0, 1, 4377, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113801, 1, "や、やっぱりみんな\n注目してくるわね…\nエンジェルって珍しい\nからしかたないか", 4, 0, 1, 4378, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113801, 1, "エンジェルになると\n人見知りがなおる！\n…わけないか", 5, 0, 1, 4379, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113801, 1, "エンジェルの活動は\n大変だけど、自分が\n成長してるって\n実感がうれしいわね", 101, 0, 4, 4380, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113801, 1, "もし誰かの願いを\nかなえられるなら\n私はアイツの…\nって何考えてるのよ！", 102, 0, 8, 4381, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113801, 1, "何ボサっとしてるの！\nさあ、街に出て\n人助けするわよ！", 1, 0, 1, 4382, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113801, 1, "エンジェリック\nアロー！\nなんてね…え、アンタ\nいつから見てたのよ！", 2, 0, 1, 4383, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113801, 1, "コラ！\n羽は触らないで！\n壊れたらどうするのよ", 3, 0, 1, 4384, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113801, 1, "エンジェルじゃなく\nても、アンタはいつも\n人助けしてるわよね\n…ちょっと尊敬するわ", 4, 0, 1, 4385, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113801, 1, "この服なら他の子より\n私を見てくれるかも…\nって、な、何\n見てるのよ！", 101, 0, 4, 4386, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113801, 1, "ね、ねえ、もし私が\n困ってたら、一番に\n駆けつけて…やっぱり\n何でもないわ！", 102, 0, 8, 4387, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113801, 1, "おはよう…あ、あの、\nエンジェルとして\n町に行くんだけど…\n一緒に行かない？", 1, 1, 1, 4388, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113801, 1, "ふぅ、いいことした\n後のランチは\nおいしいわね", 2, 2, 1, 4389, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113801, 1, "もうこんな時間…\n夜更かししてないで\n早く寝なさいよね", 3, 3, 1, 4390, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113801, 1, "いい座り心地ね～\nまさに天にも昇る\n心地よさだわ～", 2, 0, 1, 4392, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113801, 1, "ふふ、かわいい…\nあ、羽付けたら\nもっとかわいく\nなるんじゃないかな？", 3, 0, 1, 4393, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113801, 1, "はぁ～落ち着くわ～\n回復アイテムみたいで\n癒やされちゃうわ～", 4, 0, 1, 4394, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113801, 1, "わっ、とと…\n危ない、羽が\nこげちゃうところ\nだったわ", 5, 0, 1, 4395, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113801, 1, "人の役に立つのって\nすごく大変だから、\nこれでライフ回復よ！", 7, 0, 1, 4397, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113801, 1, "エンジェルに関する\n本はあるかしら…あ！\nこれ探してた攻略本！", 8, 0, 1, 4398, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113901, 1, "ふっふっふ\n吸血鬼らしく\n驚かせてあげましょう", 1, 0, 1, 4400, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113901, 1, "獲物はどこですか～！\nう～ん、もうちょっと\n迫力が欲しいですね", 2, 0, 1, 4401, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113901, 1, "トリックオアトリート\n…返事がないので\nイタズラしておきます", 3, 0, 1, 4402, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113901, 1, "ハロウィンですし\nたまには騎士さんでも\n誘ってあげますかね", 4, 0, 1, 4403, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113901, 1, "マントは動きに\nメリハリがついて\nいいですね", 5, 0, 1, 4404, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113901, 1, "色んな仮装が見られて\nやっぱりハロウィンは\n最高ですね", 101, 0, 4, 4405, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 113901, 1, "吸血鬼らしい行動って\n噛みついたり…いや\nそれは大胆過ぎです！", 102, 0, 8, 4406, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113901, 1, "呼びましたか？\n手短にお願いしますよ\n忙しいんですから！", 1, 0, 1, 4407, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113901, 1, "え？　衣装に穴が？\nありがとうございます\n…よく見てるんですね", 2, 0, 1, 4408, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113901, 1, "ふわあ…吸血鬼の\n姿でも、眠い時は\n眠いんですよね…", 3, 0, 1, 4409, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113901, 1, "ハロウィンってライブ\nとは違った賑やかさが\nあってワクワクします", 4, 0, 1, 4410, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113901, 1, "私を驚かそうとしても\n無駄ですよ！\nすぐ分かるんですから", 101, 0, 4, 4411, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113901, 1, "騎士さん、一緒に\nハロウィンを…いいえ\nなんでもないですっ！", 102, 0, 8, 4412, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113901, 1, "仕事で寝不足だから\n太陽がまぶしいです…\n私、吸血鬼っぽいかも", 1, 1, 1, 4413, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113901, 1, "お昼ご飯は真っ赤な…\nトマト尽くしのお弁当\nにしてみました", 2, 2, 1, 4414, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 113901, 1, "夜はハロウィンが\n盛り上がる時間…\n行きますよ騎士さん！", 3, 3, 1, 4415, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113901, 1, "このフィット感\nどうにか服作りにも\n活かせれば…！", 2, 0, 1, 4417, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113901, 1, "よく出来てますね…！\nぬいぐるみの服を作る\nのも楽しそうですね！", 3, 0, 1, 4418, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113901, 1, "いい感じですね…\n服に合わせた香りって\nいうのも、いいかも…", 4, 0, 1, 4419, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113901, 1, "はぁ、温まりますね…\nおっと、マントに火が\n点いちゃいそうです！", 5, 0, 1, 4420, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113901, 1, "あぁ、美味しい～\n食べた分は、働いて\n燃焼しますからね！", 7, 0, 1, 4422, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113901, 1, "伝説の吸血鬼…\n夜を統べる者…\nほうほう…", 8, 0, 1, 4423, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 113901, 1, "やっぱりハロウィンと\nいえばお菓子です！\nさぁ配りましょうか！", 12, 0, 1, 4425, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114101, 1, "この格好…\nカッコイイのに\n動きやすくて\n着心地も最高ッス！", 1, 0, 1, 4427, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114101, 1, "キメポーズはこう…\nいやこっちのほうが…\nなやましいッス…！", 2, 0, 1, 4428, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114101, 1, "トリックオア\nジャスティス！\nって言ったら何か\nもらえるッスかね？", 3, 0, 1, 4429, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114101, 1, "新しい必殺技の\n特訓もしなくちゃ\nいけないッスね…\n忙しいッス！", 4, 0, 1, 4430, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114101, 1, "自分のこの姿\nトモね～ちゃんにも\nみせてあげるッス！", 5, 0, 1, 4431, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114101, 1, "ホワイトタイガー\n参上ッス！\n今のポーズは\nキマったッス！", 101, 0, 4, 4432, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114101, 1, "あの人と一緒だと\nほんとにヒーローに\nなれる気がするッス！", 102, 0, 8, 4433, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114101, 1, "どうッスか？\nこの色、自分に\n似合ってるッスかね？", 1, 0, 1, 4434, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114101, 1, "ヒマだったら\n必殺技の特訓に\n付き合ってもらって\nいいッスか？", 2, 0, 1, 4435, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114101, 1, "ちょうどいいところに\n来たッスね！\nこのキメポーズ\nどうッスか？", 3, 0, 1, 4436, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114101, 1, "お！　この格好、\n気になるッスか？\nじっくり見ても\nいいッスよ♪", 4, 0, 1, 4437, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114101, 1, "あんたも一緒に\n新しい技の練習\nどうッスか？", 101, 0, 4, 4438, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114101, 1, "今日もあんたと一緒に\nランドソルの平和を\n守るッスよ！", 102, 0, 8, 4439, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114101, 1, "朝ッス！\n一緒に早朝特訓\nやるッスよ！", 1, 1, 1, 4440, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114101, 1, "お昼休憩ッス！\nヒーローにも\n休息は必要ッス！", 2, 2, 1, 4441, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114101, 1, "闇夜に映える\n白い衣装で\n平和を守るッスよ！", 3, 3, 1, 4442, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114101, 1, "トラも狩りの時以外は\nごろごろしてるって\n本で読んだッスよー", 2, 0, 1, 4444, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114101, 1, "もこもこッスね♪\nこのかわいらしさを\n守ってあげるのも\nヒーローの役目ッス！", 3, 0, 1, 4445, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114101, 1, "いい匂いッスね…\nリラックスして\n瞑想するッス！", 4, 0, 1, 4446, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114101, 1, "燃える炎は\nヒーローの心意気ッス\nいつまでも\n見てられるッスよ", 5, 0, 1, 4447, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114101, 1, "もぐもぐ…\nいっぱい食べて\n強いヒーローに\nなるッスよ！", 7, 0, 1, 4449, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114101, 1, "ちゃんと勉強の本も\n読んでるッスよ…！\nヒーローには賢さも\n必要らしいッス！", 8, 0, 1, 4450, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114101, 1, "わわっ、お菓子が\nいっぱいッス！\nみんなで\n食べるッスよ！", 12, 0, 1, 4452, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103001, 1, "ワタシがチューシンと\nなった今、天下統一は\n目の前デス！", 103, 0, 12, 4454, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103001, 1, "もうショーグンってば\nそれはセイシツに\nすることデ～ス…", 103, 0, 12, 4455, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114001, 1, "女幽霊…うらめしや…\nうーん、これは\nちょっと違う気が\nするな…", 1, 0, 1, 4456, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114001, 1, "何だか寒気がする…\nま、まさか\nこの部屋にも幽霊が？", 2, 0, 1, 4457, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114001, 1, "ハロウィンは楽しいね\n童心に帰ったような\n気分だよ", 3, 0, 1, 4458, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114001, 1, "今度のハロウィンは\nユイとヒヨリを\n誘ってみようかな", 4, 0, 1, 4459, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114001, 1, "ハロウィンのお菓子は\nどれもかわいいね\n…一つくらいなら\nいいかな？", 5, 0, 1, 4460, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114001, 1, "次は彼と\nどんなハロウィンを\n過ごそうかな…", 101, 0, 4, 4461, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114001, 1, "こんな賑やかさも\nいいものだね\n彼といられるのなら\nなおさら…", 102, 0, 8, 4462, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114001, 1, "…いたずらしようと\nしてない、キミ？\nふふ、お見通しだよ", 1, 0, 1, 4463, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114001, 1, "え、私にもお菓子を？\nありがとう…ふふ\nうれしいものだね\nハッピーハロウィン！", 2, 0, 1, 4464, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114001, 1, "どうしたの？\n出かけるなら\n私も同行しようか", 3, 0, 1, 4465, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114001, 1, "この格好、舞踏会にも\n出られそうだね\n一曲、踊ってみる？", 4, 0, 1, 4466, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114001, 1, "やるからには本気で\nやらせてもらおう\nトリックオア\nトリート！", 101, 0, 4, 4467, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114001, 1, "他の子に目移りする\nのも分かるが、その…\nせっかくの特別な衣装\n見てほしいな", 102, 0, 8, 4468, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114001, 1, "寝起きの体を\n慣らしておこうか\nほら、剣を取って", 1, 1, 1, 4469, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114001, 1, "お菓子もいいけど\nちゃんと昼食も\nとるんだよ", 2, 2, 1, 4470, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114001, 1, "さあ、行こうか！\nハロウィンは\nこれからが本番だよ", 3, 3, 1, 4471, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114001, 1, "ハロウィンはまだ\nこれからだ\n一休みしようか", 2, 0, 1, 4473, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114001, 1, "キミたちも\n仮装に興味は\nあるかな？　…ふふ", 3, 0, 1, 4474, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114001, 1, "いい香りだけど\n幽霊が集まったり\nしないだろうか…？", 4, 0, 1, 4475, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114001, 1, "暖かい…一息ついたら\nハロウィンの\nパレードを\n見に行こうかな", 5, 0, 1, 4476, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114001, 1, "ハロウィンを本気で\n楽しむなら\n食事もしっかり\nとっておかないとね", 7, 0, 1, 4478, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114001, 1, "ハロウィンの\nホラー小説か…\nけ、結構\n怖いものだね…？", 8, 0, 1, 4479, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114001, 1, "お菓子がいっぱいだ…\nこういうのって\n見ているだけで心が\n躍ってしまうね", 12, 0, 1, 4481, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114201, 1, "こんなにかわいい\n武器で戦えるとは…\nすごいものだな\n魔法少女は", 1, 0, 1, 4483, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114201, 1, "決めポーズの訓練も\nしっかりやっておくか\n…誰も見ていないな？", 2, 0, 1, 4484, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114201, 1, "こういう格好は\nユキあたりがすごく\n似合いそうだな", 3, 0, 1, 4485, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114201, 1, "子どもたちにサインを\n求められてしまった…\nこれもまた私の使命！", 4, 0, 1, 4486, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114201, 1, "この格好は目立つな…\nいや、提督とは人々の\n視線を集めるものだ！", 5, 0, 1, 4487, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114201, 1, "あいつと作戦会議を\nしておくか。あれで\nなかなか頼れるからな", 101, 0, 4, 4488, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114201, 1, "あいつには私の勇姿を\nちゃんと見ていて\nほしいものだな", 102, 0, 8, 4489, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114201, 1, "一仕事終えたことだし\n一緒に甘いものでも\n食べに行くか？", 1, 0, 1, 4490, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114201, 1, "怪しげな気配がするな\n貴公も警戒を\n怠るなよ！", 2, 0, 1, 4491, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114201, 1, "シオリに魔法少女の\n本を借りたんだ\n一緒に読まないか？", 3, 0, 1, 4492, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114201, 1, "巡回の時間まで\nお茶にでもするか\n貴公も付き合うがいい", 4, 0, 1, 4493, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114201, 1, "貴公がサポートして\nくれるのなら心強いな\n頼りにしているぞ", 101, 0, 4, 4494, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114201, 1, "頭を撫でるな～！\n…まったく、貴公だけ\nだぞ、こんな風に私を\n撫でてくるのは…", 102, 0, 8, 4495, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114201, 1, "起きろ貴公！\n魔法少女の朝は\n早いぞ！", 1, 1, 1, 4496, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114201, 1, "ほら、食後の\nデザートだ\nて、適度な糖分は\n体に必要なんだ！", 2, 2, 1, 4497, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114201, 1, "ふわぁ…魔法少女も…\n眠気、には…い、いや\n寝てないぞ！？", 3, 3, 1, 4498, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114201, 1, "任務や訓練で\n疲れた体に\nこれは効くなぁ…", 2, 0, 1, 4500, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114201, 1, "うむ、かわいいな\nモーラのやつを隣に\n並べようか", 3, 0, 1, 4501, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114201, 1, "ああ、身も心も\n安らぐ…\nまるで魔法だな…", 4, 0, 1, 4502, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114201, 1, "この温かさ…\n愛と希望に\n通じるものがある…", 5, 0, 1, 4503, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114201, 1, "食事の大切さは\n魔法少女も軍人も\n変わりない\nしっかり食べるんだ！", 7, 0, 1, 4505, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114201, 1, "魔法少女の本を\n読んでるのだが\nわ、私もこんななのか", 8, 0, 1, 4506, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103301, 1, "オラとあんちゃんは\nボケもツッコミも\nあうんの呼吸だべ♪", 103, 0, 12, 4508, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103301, 1, "あんちゃんと二人で\nネタ合わせ…初めての\n共同作業だべ♪", 103, 0, 12, 4509, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 192501, 1, "ぴっぷる！", 1, 0, 1, 4510, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 192501, 1, "ぴっぷるぴっぷる♪", 2, 0, 1, 4511, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 192501, 1, "ぴっ！", 3, 0, 1, 4512, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 192501, 1, "ぷっぷー", 4, 0, 1, 4513, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 192501, 1, "ぴぃ？", 5, 0, 1, 4514, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 192501, 1, "ぴっぷるんるん♪", 1, 0, 1, 4515, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 192501, 1, "ぷぅ！", 2, 0, 1, 4516, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 192501, 1, "ぴぴっ♪", 3, 0, 1, 4517, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 192501, 1, "ぷぅー…", 4, 0, 1, 4518, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 192501, 1, "ちゅ！", 1, 1, 1, 4519, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 192501, 1, "ぷはー", 2, 2, 1, 4520, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 192501, 1, "ぴぇー", 3, 3, 1, 4521, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114301, 1, "魔法少女になったら\n髪型も変わるんだね\n色々試してみようかな", 1, 0, 1, 4531, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114301, 1, "…煌く剣技で、\n悪を断つ！\nうん、今日も\n完璧なポーズだね♪", 2, 0, 1, 4532, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114301, 1, "ミクマ流剣術で、\n魔法少女の技に使え\nそうなものは…よし、\n早速試してみよっと♪", 3, 0, 1, 4533, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114301, 1, "魔法少女になったから\nには、サインの練習も\nしないと…\nよし、頑張るぞ！", 4, 0, 1, 4534, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114301, 1, "魔法少女の姿はすごく\nかわいいけど…この\n姿はジュンさんたちに\n見せられないなぁ…", 5, 0, 1, 4535, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114301, 1, "あの人は魔法少女\nのこと好きなのかな？\n…好きだったら\nうれしいな♪", 101, 0, 4, 4536, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114301, 1, "…やっぱり、\nあの人がいないと\n特訓に身が入らない…\n早く帰ってこないかな", 102, 0, 8, 4537, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114301, 1, "もし時間があるなら、\n私と街の見回りに\n行かない？", 1, 0, 1, 4538, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114301, 1, "あ、いいところに！\n新しい魔法剣術を\n編み出したから、\n見てくれないかな？", 2, 0, 1, 4539, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114301, 1, "魔法少女の姿が気に\nなるのは分かるけど…\nず、ずっと見られる\nのは照れるからやめて", 3, 0, 1, 4540, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114301, 1, "変身ポーズ中の動き、\n少し変えた方が\nいいかな…\nあなたはどう思う？", 4, 0, 1, 4541, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114301, 1, "よければ、\n魔法少女の特訓に\n付き合ってくれない？\nその…二人きりでさ…", 101, 0, 4, 4542, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114301, 1, "私…あなただけの\n魔法少女になっても…\nふふっ、な～んてね♪", 102, 0, 8, 4543, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114301, 1, "今日もいい朝だね\nさあ、一緒に\n街の見回りに行こうか", 1, 1, 1, 4544, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114301, 1, "もう昼か…なにか\n食べたいものある？\n簡単なのでいいなら、\n私が作ってあげるよ", 2, 2, 1, 4545, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114301, 1, "そろそろ休もうか\n…なんなら、添い寝\nしてあげようか？\nふふっ、冗談だよ♪", 3, 3, 1, 4546, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114301, 1, "これは…\n心身ともに安らぐね\nなんだか\n眠くなっちゃいそう", 2, 0, 1, 4548, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114301, 1, "か、かわいい…これ、\n魔法で動かせたり\nしないかな？", 3, 0, 1, 4549, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114301, 1, "いい香りだね\nなんだか頭が冴えて\nきたし、新しい技でも\n考えようかな", 4, 0, 1, 4550, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114301, 1, "あったかいな…\nこの温もりは、\n魔法少女の魔法でも\n再現不可能だね…", 5, 0, 1, 4551, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114301, 1, "こ、これは…\nおいしすぎて手が\n止まらないかも", 7, 0, 1, 4553, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114301, 1, "この本の\nキャラの動き、\n変身ポーズの\n参考にできるかも！", 8, 0, 1, 4554, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114401, 1, "メリークリスマス！\nさあ、素敵な\nパーティーを始めると\nいたしましょう！", 1, 0, 1, 4556, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114401, 1, "ふふっ、\nいくつになっても、\nやはりクリスマスは、\nワクワクしますわね♪", 2, 0, 1, 4557, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114401, 1, "自分で料理をしてみて\nシェフたちのすごさが\n改めて分かりましたわ", 3, 0, 1, 4558, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114401, 1, "何事も自分で\n体験してみると\n新しい発見が\nあるものですわね♪", 4, 0, 1, 4559, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114401, 1, "やはり、クリスマスは\n街が活発になり\nますわね…これは、\n絶好の商機ですわ♪", 5, 0, 1, 4560, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114401, 1, "他のクリスマス料理も\n作れるようになったら\nあのお方は食べて\nくださるかしら…？", 101, 0, 4, 4561, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114401, 1, "あのお方に贈る\nプレゼント…\nこれはかなりの\n難題ですわね…", 102, 0, 8, 4562, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114401, 1, "メリークリスマス！\nよろしければ、一緒に\nクリスマスの準備を\nいたしませんか？", 1, 0, 1, 4563, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114401, 1, "まあ、クリスマスの\n飾りつけを手伝って\nいただけるのですか？\nありがとうございます", 2, 0, 1, 4564, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114401, 1, "クリスマスに合わせて\nあつらえた自慢の衣装\nなのですが…その、\nいかがでしょうか？", 3, 0, 1, 4565, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114401, 1, "幼い頃はサンタさんが\nいらっしゃるのを\n待ちわびたものです\n今は…内緒ですわ♪", 4, 0, 1, 4566, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114401, 1, "も、もしお時間が\nあるようでしたら、\nクリスマスの街を\n一緒に歩きませんか？", 101, 0, 4, 4567, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114401, 1, "来年のクリスマスも\nあのお方と一緒に\n迎えられたら…", 102, 0, 8, 4568, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114401, 1, "時は金なり…\nせっかくの朝ですもの\n時間を有効活用\nいたしましょう！", 1, 1, 1, 4569, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114401, 1, "お昼になりましたわね\nさあ、\nパーティーの準備を\n進めますわよ！", 2, 2, 1, 4570, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114401, 1, "静かな夜ですわね…\nもうちょっとだけ、\nあなたさまとお話を\nしていたい気分です", 3, 3, 1, 4571, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114401, 1, "適度な休息は\n成功の秘訣ですわ！", 2, 0, 1, 4573, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114401, 1, "このかわいらしさ…\nいつまでも見て\nいられそうですわ♪", 3, 0, 1, 4574, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114401, 1, "人をもてなすには\nこうしたところにも\n気を配らないと\nいけませんわね", 4, 0, 1, 4575, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114401, 1, "暖かい…\nこういう憩いの\nひと時こそ、大事に\nしたいものですわね", 5, 0, 1, 4576, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114401, 1, "この奥深い味わい…\n何か隠し味など\n使われていまして？", 7, 0, 1, 4578, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114401, 1, "クリスマス料理の\nレシピも、\nこれだけあると\n迷ってしまいますわね", 8, 0, 1, 4579, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114401, 1, "この絵本、とっても\n面白いつくりですわね\nうちの商品に\nしたいぐらいですわ", 14, 0, 1, 4581, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114401, 1, "これだけ大きな\n着ぐるみ…\n職人の技術が\n光りますわね", 15, 0, 1, 4582, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114601, 1, "クリスマスといえば\nパーティーよね！\nいっぱい飲むわよ～♪", 1, 0, 1, 4583, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114601, 1, "私を麦しゅわだけの\n女と思ったら\n大間違いなんだから♪", 2, 0, 1, 4584, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114601, 1, "クリスマスらしい\n飲み物も結構いいわね\n私も今度\n作ってみようかしら", 3, 0, 1, 4585, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114601, 1, "暖かい部屋で飲む\n麦しゅわも\nまた格別なのよね…", 4, 0, 1, 4586, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114601, 1, "今日はオフの日だし\n朝から飲んでも\n怒られないわよね？", 5, 0, 1, 4587, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114601, 1, "クリスマスの\nデートスポットで\n彼と一緒に…ってなに\n考えてるのユカリ！？", 101, 0, 4, 4588, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114601, 1, "背伸びしなくても\nありのままの自分を\n受け入れてくれるって\nすごく…幸せ…", 102, 0, 8, 4589, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114601, 1, "大丈夫よ～\n飲んでない\n飲んでないひゃら～", 1, 0, 1, 4590, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114601, 1, "クリスマスは\n特別だもの\n特別な一杯を\n飲まないとね♪", 2, 0, 1, 4591, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114601, 1, "メリークリスマース！\nほらほらぁ\n乾杯しましょ♪", 3, 0, 1, 4592, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114601, 1, "お姉さんの\n頼れるところ\n見せてあげるわね", 4, 0, 1, 4593, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114601, 1, "何かあったら\n頼ってくれても\nいいんだからね？", 101, 0, 4, 4594, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114601, 1, "ふふ…これからも\nキミのこと…\n頼りにしちゃうからね", 102, 0, 8, 4595, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114601, 1, "さわやかな朝ね\n…二日酔いがないと\n朝ってこんなに\n気持ちいいのね", 1, 1, 1, 4596, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114601, 1, "昼間から飲むのって\nまた格別よね…って\nガマン、ガマンよ…", 2, 2, 1, 4597, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114601, 1, "もうこんな時間かあ…\nあとは寝るだけだし\nちょっとくらいは…", 3, 3, 1, 4598, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114601, 1, "ゴロゴロするのって\n最高ね～\nあとは麦しゅわが\nあればなぁ", 2, 0, 1, 4600, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114601, 1, "私だって…\nかわいいものは\n好きでいいわよね？", 3, 0, 1, 4601, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114601, 1, "んっ～…いい香りね\n日頃の疲れが\n取れる気がするわ", 4, 0, 1, 4602, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114601, 1, "ポカポカする～\n…なんだかのどが\nかわいてきたわね", 5, 0, 1, 4603, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114601, 1, "もぐもぐ…労働の\n後のご飯はやっぱり\nおいしいわぁ…", 7, 0, 1, 4605, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114601, 1, "クリスマスデート\n完全攻略マニュアル？\n…読んでおかなくちゃ", 8, 0, 1, 4606, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114601, 1, "あら、絵が飛び出して\n見えるけど…\n酔っ払ってるせいじゃ\nないわよね…？", 14, 0, 1, 4608, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114601, 1, "いい手触りね～\nウチにもほしいけど\n麦しゅわをこぼしたら\n大変そうだわ…", 15, 0, 1, 4609, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101601, 1, "ヒデサイの視線\nずっと独り占め\nしてたいな～", 103, 0, 12, 4610, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101601, 1, "ヒデサイといるだけで\nうち、おに楽しいし！\nヒデサイもっしょ～？", 103, 0, 12, 4611, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114501, 1, "メリークリスマス！\nふふ、口にするだけで\n何だか楽しくなるわね", 1, 0, 1, 4612, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114501, 1, "いつもは子どもたちを\n見守る方だけど\n今ははしゃいでも\nいいわよね？", 2, 0, 1, 4613, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114501, 1, "ツリーや料理\nそしてプレゼント！\nいくつになっても\nやっぱり心躍るわね♪", 3, 0, 1, 4614, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114501, 1, "飾り付けも料理も\nいいセンスしてるわね\n救護院のパーティーの\n参考にさせてもらうわ", 4, 0, 1, 4615, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114501, 1, "焼きそばパンを\nクリスマスの定番に\nするにはどうすれば…", 5, 0, 1, 4616, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114501, 1, "これ、あいつの\n好きそうな料理だわ\n取っといて食べさせて\nあげようかしら♪", 101, 0, 4, 4617, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114501, 1, "あいつのプレゼント\nちょっとだけ奮発\nしたのよね…\n喜んでくれるかしら", 102, 0, 8, 4618, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114501, 1, "このドレス\n似合ってるかしら？\n今だけは見とれても\n許してあげるわ♪", 1, 0, 1, 4619, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114501, 1, "見て、このケーキ！\nおいしそうでしょ\nたまにはこんな贅沢も\nいいわよね♪", 2, 0, 1, 4620, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114501, 1, "料理も飲み物も\nおいしいわね！\nいっぱい食べて\n元を取らないと♪", 3, 0, 1, 4621, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114501, 1, "子どもたちと一緒に\n雪遊び、楽しそうね\nあんたもどう？", 4, 0, 1, 4622, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114501, 1, "せっかくの\nクリスマスなんだもの\nエスコート\nよろしくね♪", 101, 0, 4, 4623, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114501, 1, "寒い？　そう…\nなら、もっとこっち\n来ていいわよ…？", 102, 0, 8, 4624, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114501, 1, "ほら、早く起きて？\nパーティー用の\n買い出しに\n付き合ってちょうだい", 1, 1, 1, 4625, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114501, 1, "パーティーの料理の\n準備をしながらお昼も\n作っておいたわ\n我ながらいい手際ね", 2, 2, 1, 4626, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114501, 1, "にぎやかなパーティー\nもいいけど、聖夜の\n余韻を感じるのも\n結構素敵ね", 3, 3, 1, 4627, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114501, 1, "いい座り心地だわ～\n腕のいい職人の仕事ね\nうちでも扱いたいわ", 2, 0, 1, 4629, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114501, 1, "かわいい！\n子どもたちに\nプレゼントしたら\n喜んでくれるかも", 3, 0, 1, 4630, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114501, 1, "華やかな香りね\nクリスマスに\nピッタリだわ", 4, 0, 1, 4631, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114501, 1, "寒い日にはやっぱり\nこれよね…\nはぁ、極楽極楽♪", 5, 0, 1, 4632, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114501, 1, "んー、おいしいわ！\n焼きそばパン味の\nものも、作れない\nかしら？", 7, 0, 1, 4634, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114501, 1, "たまにはのんびり\n読書でも…なんだか\nラブロマンスばかりね\nクリスマスだから？", 8, 0, 1, 4635, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114501, 1, "これ、面白いわね！\n子どもたちの\nプレゼントに\nピッタリだわ", 14, 0, 1, 4637, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114501, 1, "こんないい毛並み\n初めて見るわ…\n一体どこで手に\n入るのかしら？", 15, 0, 1, 4638, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114701, 1, "アタシもみんなと\n一緒に正月を\n楽しんでいいのか？", 1, 0, 1, 4639, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114701, 1, "今年こそ、もう少し\n身長が伸びます\nように…", 2, 0, 1, 4640, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114701, 1, "げっ！　お年玉が\n全然残ってないぞ！？\nそんな、嘘だろ…？", 3, 0, 1, 4641, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114701, 1, "大勢で過ごす\n正月か…初めてだけど\n悪くないもんだな", 4, 0, 1, 4642, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114701, 1, "んー…ここじゃ\n天楼覇断剣の\n出番はなさそうだな", 5, 0, 1, 4643, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114701, 1, "アイツらと作る\n戦い以外の思い出も\nもっと増やしたいな", 101, 0, 4, 4644, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 114701, 1, "こんなに賑やかな\n正月は初めてだ\n…温かくて、いいな", 102, 0, 8, 4645, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114701, 1, "記憶がなくったって\n楽しい思い出は\n新しく作れるよな？", 1, 0, 1, 4646, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114701, 1, "早くでかけるぞ！\nアタシはもっと\n正月を楽しみたい！", 2, 0, 1, 4647, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114701, 1, "なあ、オマエはもう\nノウェムって名前\n覚えてくれたよな？", 3, 0, 1, 4648, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114701, 1, "この服、アタシに\n似合ってるのか？\n…そっか。\nありがとな！", 4, 0, 1, 4649, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114701, 1, "オマエらと見た朝日は\nきっと忘れないぞ！\n思い出ができたな！", 101, 0, 4, 4650, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114701, 1, "いつかオクトーも\n一緒に、みんなで\n正月を過ごせたらな…", 102, 0, 8, 4651, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114701, 1, "朝から元気だな！\nアタシもいっちょ\n見習ってみるか！", 1, 1, 1, 4652, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114701, 1, "昼飯は何にする？\n正月はうまいものが\n多くて迷うよな！", 2, 2, 1, 4653, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 114701, 1, "いい夜だな！\nアタシと一緒に\n夜更かしを楽しむか？", 3, 3, 1, 4654, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114701, 1, "正月らしく、たまには\nゆっくりするのも\nいいかもな…", 2, 0, 1, 4656, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114701, 1, "なんだ？\nオマエも初詣に\n行きたいのか？", 3, 0, 1, 4657, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114701, 1, "すーはー…\n神殿のニオイがするな\nいい事ありそうだ！", 4, 0, 1, 4658, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114701, 1, "コイツがあれば\nうまい魚が\n焼けそうだな…", 5, 0, 1, 4659, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114701, 1, "ううー\n食べ過ぎて帯が\nきつくなってきたぞ…", 7, 0, 1, 4661, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 114701, 1, "おみくじって\n書いてあることが\nむずかしいんだよな…", 8, 0, 1, 4662, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180101, 1, "困ってる人は\nいないみたいだし…\nちょっとここで、\n特訓しようかな♪", 1, 0, 1, 4664, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180101, 1, "空が飛べるんだから\nもっとたくさんの人を\n助けにいけちゃう！\n…ここじゃ飛べないや", 2, 0, 1, 4665, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180101, 1, "炎の翼\nかっこいいなー！\nこれ、もっと大きく\nできないのかな？", 3, 0, 1, 4666, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180101, 1, "やっぱり変身すると\nお腹すくんだね…\n…今ならペコさんの\n食欲の理由が分かるよ", 4, 0, 1, 4667, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180101, 1, "色なんかは違うけど…\nマントがあるから\n騎士くんとお揃いに\nなったみたい♪", 5, 0, 1, 4668, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180101, 1, "…騎士くんがいないと\nなんか調子でない…\n早く会いたいよぉ…", 101, 0, 4, 4669, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180101, 1, "この姿になると、\n騎士くんを近くに\n感じる…なんだろ\nドキドキしちゃう…", 102, 0, 8, 4670, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180101, 1, "騎士くん騎士くん！\n今大丈夫？\nトレーニングメニュー\n考えるの手伝って！", 1, 0, 1, 4671, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180101, 1, "…うにゃっ！？\nこ、この姿になっても\n尻尾は触っちゃ\nダメー！", 2, 0, 1, 4672, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180101, 1, "もしかして、この服が\n気になってるの？\nだったら、もっと\n近くで見ていいよ♪", 3, 0, 1, 4673, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180101, 1, "あ、騎士くん！\n困ってる人がいないか\n見回りに行くんだけど\nついてきて欲しいな！", 4, 0, 1, 4674, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180101, 1, "耳に触りたいの？\nだったら頭もなでて！\nそしたら、好きなだけ\n触っていいよ♪", 101, 0, 4, 4675, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180101, 1, "…あたし、強くなって\n騎士くんを守るよ！\nだから…これからも\n一緒にいていい？", 102, 0, 8, 4676, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180101, 1, "おっはよー！\nさあさあ騎士くん！\n今日も一緒に\nはりきっていこー♪", 1, 1, 1, 4677, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180101, 1, "お昼、お昼～♪\n今日のご飯は何かな～\nあ、騎士くん！\n早くお昼食べ行こ！", 2, 2, 1, 4678, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180101, 1, "今日もお疲れ様ー！\n騎士くん、あたしが\nマッサージしよっか？\n結構評判なんだよ！", 3, 3, 1, 4679, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 180101, 1, "うにゃぁ…\nこの気持ちよさ…\n溶けちゃいそう…\nにゃぁ…", 2, 0, 1, 4681, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 180101, 1, "か、かわいい～\nこれ、なでなで\nしていいんだよね…？\nよしよし…♪", 3, 0, 1, 4682, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 180101, 1, "とってもいい香り！\n気分も落ち着くし、\n特訓の後なんかに\nいいかもー！", 4, 0, 1, 4683, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 180101, 1, "ぬくぬくだぁ…\nなんだか、\nふにゃふにゃに\nなっちゃうよぉ…", 5, 0, 1, 4684, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 180101, 1, "お、おいひぃ…！\nこの姿になると\nお腹すくし、すっごく\n助かっちゃった！", 7, 0, 1, 4686, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 180101, 1, "この本で勉強して、\nぶんぶりょーどーの\n超ウルトラヒヨリに\nなっちゃうぞー！", 8, 0, 1, 4687, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111801, 1, "今年もみんなで\nおいしいものを\nたくさん食べたいです", 1, 0, 1, 4689, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111801, 1, "挑戦は大切ですよね☆\nというわけで\nおせちに魔物を…", 2, 0, 1, 4690, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111801, 1, "おせちは縁起物です！\n食べれば食べるほど\n幸せになるはずです！", 3, 0, 1, 4691, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111801, 1, "食べ物がたくさん\n入った福袋は\nないんでしょうか…？", 4, 0, 1, 4692, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111801, 1, "初詣で何を\nお願いしましょう…\n五穀豊穣？", 5, 0, 1, 4693, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111801, 1, "ずっとこうして\n年の始まりを\n過ごしたいですね", 101, 0, 4, 4694, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 111801, 1, "いつか二人きりで\n初日の出を\n見たいです", 102, 0, 8, 4695, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111801, 1, "お餅おかわりです！\nいくらでも\n食べられますよ！", 1, 0, 1, 4696, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111801, 1, "おせちの食材を\n取りに行きませんか？\nいい運動です☆", 2, 0, 1, 4697, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111801, 1, "凧揚げって\n難しいですね～\n糸が絡まっちゃいます", 3, 0, 1, 4698, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111801, 1, "一年の計は元旦にあり\n今年一年分の\n献立を作りましょう！", 4, 0, 1, 4699, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111801, 1, "ほらほら！\nもっと近くで着物を\n見ていいんですよ☆", 101, 0, 4, 4700, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111801, 1, "あなたはどんな\n初夢を見ましたか？\nわたしは…内緒です☆", 102, 0, 8, 4701, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111801, 1, "年明けの朝は\n心なしか\n気持ちいいですね☆", 1, 1, 1, 4702, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111801, 1, "お雑煮のあとは\nおしるこで\nおやつタイムです！", 2, 2, 1, 4703, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 111801, 1, "あたたかくして\n幸せな初夢を\n見ましょうね", 3, 3, 1, 4704, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111801, 1, "はぁ～屋台巡りを\n満喫しました☆\n一休み一休み", 2, 0, 1, 4706, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111801, 1, "縁起のいい\nぬいぐるみを並べて\n福を呼びましょう！", 3, 0, 1, 4707, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111801, 1, "おしるこのアロマって\nないんでしょうか？\nそれなら作るしか…", 4, 0, 1, 4708, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111801, 1, "ここでお餅を\n焼いたらおいしく\nなりそうですね～♪", 5, 0, 1, 4709, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111801, 1, "おいしすぎて\n食べる手が\n止まりません～", 7, 0, 1, 4711, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 111801, 1, "新しいおせち料理の\nヒントは…なるほど…\nふむふむ…", 8, 0, 1, 4712, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100601, 1, "お兄ちゃんとあんな\n特訓やこんな特訓が\nしたいなぁ、えへへ♪", 103, 0, 12, 4714, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100601, 1, "お兄ちゃんに褒めて\nもらうと、身体が\nじぃーんと\n熱くなっちゃいます♪", 103, 0, 12, 4715, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115001, 1, "見目麗しい衣装に\n身を包むのも\n悪い気分では\nありませんね", 1, 0, 1, 4716, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115001, 1, "縁起物で\n験を担ぐというのも\nたまにはよいでしょう", 2, 0, 1, 4717, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115001, 1, "非効率なので\nあまり行いませんが\n私も手料理くらいは\n作れるのですよ", 3, 0, 1, 4718, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115001, 1, "研究のためなら\n時に遠路を超え\n実地へ赴くことも\n必要なのです", 4, 0, 1, 4719, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115001, 1, "トゥンヌスの\n突然変異…\n大いに興味を\nそそられますね", 5, 0, 1, 4720, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115001, 1, "漁師の技法も\n再現できたことで\nまた一つ、仮説が\n実証できました", 101, 0, 4, 4721, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115001, 1, "正月くらいは\n私ものんびりしたい\n気持ちなのですよ\n意外ですか？", 102, 0, 8, 4722, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115001, 1, "模倣に頼らずとも\n衣装に合わせた\n所作程度は\nできて当然です", 1, 0, 1, 4723, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115001, 1, "お年玉の使い道は\n思いつきましたか\nゆっくり考えても\nいいのですよ", 2, 0, 1, 4724, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115001, 1, "トランプでも花札でも\n相手をしましょう\n手心が必要なら\nいつでも言いなさい", 3, 0, 1, 4725, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115001, 1, "初詣でお祈りする\n内容ですか\n…ヒミツ、として\nおきましょう", 4, 0, 1, 4726, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115001, 1, "私たちでオーマ中の\nトゥンヌスを\n釣り上げましょうか\n…冗談ですよ", 101, 0, 4, 4727, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115001, 1, "見返りを求めない願い\nそれは祈りに通じるの\nかもしれませんね", 102, 0, 8, 4728, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115001, 1, "…貴方がその眠たげな\n顔を洗ってくる間に、\n温かいお茶でも\n淹れておきましょう", 1, 1, 1, 4729, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115001, 1, "さて、本格的に\n活動を再開します\n貴方も同行しなさい\n昼食は済みましたか？", 2, 2, 1, 4730, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115001, 1, "私はもう少し\n作業を片付けてから\n寝ることにします\n早く眠りなさい", 3, 3, 1, 4731, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 115001, 1, "少し休みます…\n時間が来たら\n起こすように", 2, 0, 1, 4733, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 115001, 1, "贈り物を模倣して\n潜入…試してみる\n価値はありそうですね", 3, 0, 1, 4734, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 115001, 1, "嗅覚から得られる\n情報量は侮れません\n動物の模倣において\n注意すべき点ですよ", 4, 0, 1, 4735, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 115001, 1, "穏やかな炎による\nリラックス効果は\n本能によるものかも\nしれませんね", 5, 0, 1, 4736, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 115001, 1, "いかなる強者でも\n兵站、補給は\n必要なものです", 7, 0, 1, 4738, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 115001, 1, "あらゆる学問は\n完璧な模倣のための\n大きな礎となります", 8, 0, 1, 4739, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115501, 1, "新たに得たこの力で\n仲間と目指す未来を\n切り拓いてみせます", 1, 0, 1, 4741, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115501, 1, "ここは、みなさまと…\nわたくしの\n帰る場所でございます", 2, 0, 1, 4742, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115501, 1, "主さまも精霊の召喚を\n試してみますか？", 3, 0, 1, 4743, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115501, 1, "主さま、おこづかいは\n足りていますか？\n遠慮は無用ですよ", 4, 0, 1, 4744, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115501, 1, "この装束をまとうと\n自然と背筋が伸びる\n思いがいたします", 5, 0, 1, 4745, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115501, 1, "進化した精霊に\n負けぬよう\n修行に励みます", 101, 0, 4, 4746, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115501, 1, "仲間たちといられる\nこの幸せが、末永く\n続きますように…", 102, 0, 8, 4747, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115501, 1, "主さまのお手手は\n大きくて温かいですね\nぎゅっぎゅっ…ふふ♪", 1, 0, 1, 4748, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115501, 1, "今度、主さまにも\n里に伝わる昔話を\nお話しいたしましょう", 2, 0, 1, 4749, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115501, 1, "里で採れた果物を\n持って参りました\n召し上がってください", 3, 0, 1, 4750, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115501, 1, "里のお土産を\nここのみなさまに\nお配りいたしましょう", 4, 0, 1, 4751, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115501, 1, "仲間と囲む食卓…この\n温もりは、みなさまの\n心の温かさなのですね", 101, 0, 4, 4752, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115501, 1, "主さまに名前を\n呼んでいただくと\n心が温かくなります♪", 102, 0, 8, 4753, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115501, 1, "ふふっ…主さま\n寝癖がついて\nおりますよ？", 1, 1, 1, 4754, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115501, 1, "今日の昼食は\n里の伝統料理です\nお口に合うでしょうか", 2, 2, 1, 4755, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115501, 1, "主さまが眠れるよう\n安らぎの風を…\nおやすみなさいませ", 3, 3, 1, 4756, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 115501, 1, "この癒し…\n主さまにも味わって\nいただきたい…", 2, 0, 1, 4758, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 115501, 1, "この愛らしさ…\n精霊たちと似たものを\n感じます…♪", 3, 0, 1, 4759, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 115501, 1, "精霊よ…この安らぐ\n香りを、隅々まで\n届けてくださいまし", 4, 0, 1, 4760, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 115501, 1, "瞑想するのに\nちょうどよい暖かさで\nございます", 5, 0, 1, 4761, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 115501, 1, "進化した精霊を\n使役するためにも\nよく食べて体力を…！", 7, 0, 1, 4763, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 115501, 1, "風の精霊について\nもっと詳しく知って\nおかなければ…", 8, 0, 1, 4764, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115601, 1, "儀式の時を思い出して\n精霊に呼びかける…\n出ておいで～♪", 1, 0, 1, 4766, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115601, 1, "ひゃうっ！　もう…\n急に背中を触るなんて\nいたずら好きな精霊に\n育てた覚えはないのに", 2, 0, 1, 4767, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115601, 1, "儀式用の衣装…\nあんまり激しい動きは\nできないよね…でも\n戦う時は気にしない！", 3, 0, 1, 4768, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115601, 1, "そうだ…精霊に\n騎士クンのそばで\n見守っていてって\n頼んでみようかな…", 4, 0, 1, 4769, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115601, 1, "ふふっ、こうやって、\n楽しく精霊とお話し\nしていられる、平穏な\n日々が続くといいな", 5, 0, 1, 4770, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115601, 1, "き、きき、騎士クン！\nわたしと、あの…！\nうう…練習ですら\n上手く誘えないよぉ…", 101, 0, 4, 4771, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115601, 1, "あっ、精霊が…\nふふ、応援ありがとう\nわたしの想い…いつか\n必ず伝えるね", 102, 0, 8, 4772, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115601, 1, "新しく覚えた料理\n作ってみたんだけど\n騎士クンも食べない？", 1, 0, 1, 4773, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115601, 1, "騎士クン、\n疲れてない？\nわたしと精霊で\n治療するよ？", 2, 0, 1, 4774, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115601, 1, "もう、いたずらしない\n…騎士クン！？\nご、ごめんなさい！\n精霊だと思って！", 3, 0, 1, 4775, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115601, 1, "騎士クン！\n時間が合ったら、\n精霊と一緒に\nお話しない？", 4, 0, 1, 4776, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115601, 1, "…こういう服着るの\n初めてなんだけど…\nえっ、かわいい…？\nあ、ありがとう…！", 101, 0, 4, 4777, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115601, 1, "困ったことがあったら\n相談してね。わたし、\n騎士クンのためなら…\n色々できるよ、色々！", 102, 0, 8, 4778, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115601, 1, "おはよう騎士クン！\n今からお外で\n瞑想するんだけど…\n騎士クンもどうかな？", 1, 1, 1, 4779, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115601, 1, "お昼の時間だ\nご飯ならわたしが\n用意するから\nゆっくりしてて", 2, 2, 1, 4780, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115601, 1, "もうこんな時間…\n騎士クン、はいどうぞ\n寝る前の温かいミルク\nぐっすり眠れるよ♪", 3, 3, 1, 4781, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 115601, 1, "…うん、落ち着く\n精霊との魔法で\nこんな風に誰かを\n安心させたいな", 2, 0, 1, 4783, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 115601, 1, "かわいい…\n精霊も気に入ってる…\nそうだ、同じ物\n作ってあげようかな？", 3, 0, 1, 4784, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 115601, 1, "いい香り…これ、\nうちに置いたら、\nお母さん喜ぶかも！", 4, 0, 1, 4785, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 115601, 1, "あったかい…\nまるで誰かが\n抱きしめてくれた\nみたい…", 5, 0, 1, 4786, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 115601, 1, "自然の恵みに\n感謝して…\nいただきます♪", 7, 0, 1, 4788, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 115601, 1, "もっと精霊について\n勉強して、みんなを\n守れるように\nなりたいな…", 8, 0, 1, 4789, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 102201, 1, "アイツといると胸が\nぎゅーっとなる…\nこれってもしかして…", 103, 0, 12, 4791, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102201, 1, "アンタとならきっと\nどんなゲームでも…\nそ、その、たた、\n楽しいって思えるわ！", 103, 0, 12, 4792, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106401, 1, "色んな人と話したら\n記憶探しの手がかりが\n見つかるかも…？", 1, 0, 1, 4793, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106401, 1, "アイスクリームでも\n作ろうかしら？\nバニラにミントに…", 2, 0, 1, 4794, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106401, 1, "ネビアに話しかけると\n逃げられるのよね…\nもう、襲わないのに…", 3, 0, 1, 4795, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106401, 1, "「コッコロさん」、\n「コッコロさん」…\n…間違えないように…", 4, 0, 1, 4796, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106401, 1, "最近少し太ったかも？\nきっとおいしいもの、\n食べ過ぎね…", 5, 0, 1, 4797, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106401, 1, "ここにいると、\nとても落ち着くわ…\n自分の家、だものね", 101, 0, 4, 4798, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106401, 1, "みんなと一緒の時間、\nずっと記憶に残したい\n…そう思っているのよ", 102, 0, 8, 4799, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106401, 1, "ドラゴン族って、\nそんなに珍しいの？\nあまり実感ないけど…", 1, 0, 1, 4800, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106401, 1, "料理、練習してるのよ\n…味見してもらえる？\n期待はしないでね", 2, 0, 1, 4801, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106401, 1, "頭を叩いたら、\n記憶が戻ったり\nしないかしら…？\nやらなくていいわよ？", 3, 0, 1, 4802, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106401, 1, "赤ちゃんみたいだった\n時のことは、忘れて…\nお願いだから…", 4, 0, 1, 4803, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106401, 1, "ひゃんっ！？\nし、しっぽを掴むのは\nダメよ？", 101, 0, 4, 4804, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106401, 1, "記憶が戻っても…\n私は、あなたたちと\n一緒にいたいわ", 102, 0, 8, 4805, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106401, 1, "おはよう。\n寝ぐせついてるわよ？\n…ふふっ、これでよし", 1, 1, 1, 4806, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106401, 1, "そろそろお昼かしら？\nおいしいごはんを、\nみんなで一緒に…ね", 2, 2, 1, 4807, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106401, 1, "もうこんな時間ね。\nそろそろ休みましょ？\n…ふわぁ…", 3, 3, 1, 4808, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106401, 1, "ふかふか、ぎゅう～…\n…あっ、これはその…\nストレッチしててっ…", 2, 0, 1, 4810, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106401, 1, "あまり、興味ないわ。\nけど…けど…\n…か、かわいい…", 3, 0, 1, 4811, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106401, 1, "いい香りね。\nとっても落ち着くわ…\n…っくしゅっ", 4, 0, 1, 4812, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106401, 1, "冷やす方が得意だけど\nあったかいのも、\nキライじゃないのよ", 5, 0, 1, 4813, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106401, 1, "別に、食いしん坊\nじゃないのよ？\nでも、もう一口だけ…", 7, 0, 1, 4815, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106401, 1, "ん…なるほどね。\n勉強になるわ…", 8, 0, 1, 4816, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115701, 1, "水着の探偵か…\nちょっと恥ずかしい\n気がしてきたよ…", 1, 0, 1, 4818, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115701, 1, "依頼があれば\n海でも山でも\n赴くさ！", 2, 0, 1, 4819, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115701, 1, "今のうちに\n助手くんの秘密を…\nわ…私はなにをっ！？", 3, 0, 1, 4820, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115701, 1, "休暇も時には必要さ\n海でゆっくりと…\nむっ？　事件の気配！", 4, 0, 1, 4821, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115701, 1, "探偵の秘密道具…\n水着だと隠す\n場所がない…！", 5, 0, 1, 4822, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115701, 1, "そろそろ助手くんが\n現れる時間かな？\nふふっ、楽しみだね", 101, 0, 4, 4823, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115701, 1, "真実は私と助手くん\n二人だからこそ\n解き明かせるのさ", 102, 0, 8, 4824, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115701, 1, "ひゃああっ！？\nわ、私の不意を突く\nとは…やるね助手くん", 1, 0, 1, 4825, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115701, 1, "さて、なにか気づいた\nことはあるかな？\n言ってごらん♪", 2, 0, 1, 4826, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115701, 1, "この感触…分かった！\n助手くんだね！\nふふっ、やっぱり♪", 3, 0, 1, 4827, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115701, 1, "水着…どうかな？\n探偵らしさは\n残してるつもりだけど", 4, 0, 1, 4828, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115701, 1, "ん？　依頼かな？\n助手くんの頼みなら\nなんでもお安い御用さ", 101, 0, 4, 4829, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115701, 1, "助手くん、私と海に\n行かないかな\n…事件じゃないよ？", 102, 0, 8, 4830, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115701, 1, "珈琲でも淹れて\n目を覚ますとしよう\n一緒に飲むかい？", 1, 1, 1, 4831, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115701, 1, "昼食は済ませた？\nなにを食べたか\n当ててあげよう！", 2, 2, 1, 4832, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115701, 1, "夜は謎が深まる時間…\n闇に覆い隠された真実\n必ず暴いてみせる！", 3, 3, 1, 4833, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 115701, 1, "さて、推理の時間だ\n…サボってないよ？\n考えてるんだからね？", 2, 0, 1, 4835, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 115701, 1, "これはもしや、中に\n秘密のメッセージが？\n…かわいいだけだね", 3, 0, 1, 4836, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 115701, 1, "ふむ、なんだか\n頭がスッキリするね\n…閃いた！", 4, 0, 1, 4837, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 115701, 1, "ポカポカするね…\n助手くん\n君もおいでよ", 5, 0, 1, 4838, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 115701, 1, "はむ、はむ…んぐっ\nた、食べてるところを\n見つめられると～…", 7, 0, 1, 4840, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 115701, 1, "秘密の依頼とか\nこういう場所に\n隠したりするよね…♪", 8, 0, 1, 4841, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 115701, 1, "これはいい乗り心地だ\n事件現場にこれで登場\n…は、やめておこう", 10, 0, 1, 4843, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 115701, 1, "不思議な感触だね…\nでも、いい発想が\n生まれてきそうだ…♪", 11, 0, 1, 4844, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180601, 1, "これはあたしが\n前へ進むために\nあいつがくれた\n大切な力…！", 1, 0, 1, 4845, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180601, 1, "チェンジ・\nプリンセスフォーム！\n練習練習、っと", 2, 0, 1, 4846, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180601, 1, "もうあいつに助けて\nもらいっぱなしの\nあたしは…\n卒業しなきゃね", 3, 0, 1, 4847, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180601, 1, "気ままに、お気楽に\nあいつと冒険する…\nそれって幸せよね", 4, 0, 1, 4848, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180601, 1, "プリンセスフォーム…\nお姫様みたいね\nあたしがお姫様か…\nそっかぁ…ふふ♪", 5, 0, 1, 4849, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180601, 1, "あいつと繋がって\n生まれたこの絆は\nあたしの宝物だわ", 101, 0, 4, 4850, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180601, 1, "あたしの命の使い方…\n全て受け止めてくれた\nあいつにだったら…", 102, 0, 8, 4851, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180601, 1, "ふふ～ん♪\nプリンセスフォームの\n天才魔法使い\nキャル様に用かしら？", 1, 0, 1, 4852, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180601, 1, "これからは\nあたしの力で\nなんでもパパっと\n解決してあげるわね！", 2, 0, 1, 4853, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180601, 1, "魔力がみなぎってる…\nねえ、もしかして\n今のあたしって本気で\nすごいんじゃない？", 3, 0, 1, 4854, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180601, 1, "変身の反動で\nお腹が空いても\n魔物料理だけは\nいやだからね！？", 4, 0, 1, 4855, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180601, 1, "ねぇ、あんたになら\nあたしの心…\n全部預けていいかも", 101, 0, 4, 4856, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180601, 1, "あいつと一緒に\nいられたら、他には\n何も…って、あああ\nあんたいつの間に！？", 102, 0, 8, 4857, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180601, 1, "早起きも悪くないわね\n読みかけの魔導書\n今のうちに\n読んじゃおうかしら", 1, 1, 1, 4858, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180601, 1, "ねぇ～お腹すいたー\nあんたなんか\n作りなさいよ\n魔物料理以外でね！", 2, 2, 1, 4859, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180601, 1, "ふぁぁ…眠すぎて\n今なら変身しても\nお腹が減らない\n自信があるわ…", 3, 3, 1, 4860, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 180601, 1, "あー♪　最っ高！\n休んで魔力を高めて…\nあいつの役に\n立ってあげなきゃね", 2, 0, 1, 4862, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 180601, 1, "なにこれかわいい…！\nコレクションにしたく\nなっちゃうじゃない", 3, 0, 1, 4863, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 180601, 1, "この香り…すごく\nリラックスできるわ…\nあ、いい感じの魔法\n思いついちゃったかも", 4, 0, 1, 4864, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 180601, 1, "あったかい…\n落ち着くわ…\nあいつの手の\nぬくもりみたいね", 5, 0, 1, 4865, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 180601, 1, "プリンセスフォーム…\nこ、こんなに\nお腹が減るのね\n甘く見てたわ…！", 7, 0, 1, 4867, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 180601, 1, "ま、魔物料理の本！？\n誰よこんな本\n買ったの！　って…\n一人しかいないか", 8, 0, 1, 4868, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115801, 1, "憧れの舞踏会…\n私、きちんと\n踊れるかしら", 1, 0, 1, 4870, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115801, 1, "アン・ドゥ・トロワ\nアン・ドゥ・トロワ\n練習はばっちりね♪", 2, 0, 1, 4871, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115801, 1, "こんなにすてきな\nドレスを着られて\n本当に夢みたいだわ♪", 3, 0, 1, 4872, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115801, 1, "魔法はいつか解ける…\nでも、その時間は心に\n残り続けるわ♪", 4, 0, 1, 4873, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115801, 1, "ガラスの靴は夢へ\n踏み出す魔法の道具…\n私、がんばっちゃう！", 5, 0, 1, 4874, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115801, 1, "舞踏会であなたと\n一緒に踊れたら\nいいのにな", 101, 0, 4, 4875, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115801, 1, "舞踏会に行く前に\n運命の王子様には\n出会ってるのよね", 102, 0, 8, 4876, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115801, 1, "どうかしら？\n私、きちんとドレス\n着られてる？", 1, 0, 1, 4877, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115801, 1, "あなたも一緒に\n踊ってみる？\nしゃなりしゃなり♪", 2, 0, 1, 4878, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115801, 1, "あなたにエスコート\nされてきれいなお城を\n歩いてみたいわ♪", 3, 0, 1, 4879, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115801, 1, "どう？　レッスンの\n成果はきちんと\n出てるかしら？", 4, 0, 1, 4880, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115801, 1, "あなたが隣にいれば\n私も立派なお姫様に\n見えるかしら♪", 101, 0, 4, 4881, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115801, 1, "私にとって\n一番の魔法はあなたが\nくれる勇気よ♪", 102, 0, 8, 4882, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115801, 1, "さぁ！　今日も一日\n立派な淑女を目指して\nいきましょう♪", 1, 1, 1, 4883, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115801, 1, "お昼ごはん代わりに\nメタモルアップルを\n食べておきましょう", 2, 2, 1, 4884, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115801, 1, "そろそろ魔法が解ける\n時間…でも淑女の\n心得は忘れないわ♪", 3, 3, 1, 4885, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 115801, 1, "淑女にも休息は\n必要よね…少しだけ\n休ませてもらおっと", 2, 0, 1, 4887, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 115801, 1, "かわいいものに\n囲まれた部屋も\n女の子の夢よね…", 3, 0, 1, 4888, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 115801, 1, "すてきな香り…\nお花かしら？\n森で探してみようかな", 4, 0, 1, 4889, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 115801, 1, "暖かい…暖炉のそばで\nおとぎ話を聞くのも\nいいわよね♪", 5, 0, 1, 4890, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 115801, 1, "レッスンのために\n栄養補給して\nおかないと！", 7, 0, 1, 4892, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 115801, 1, "教養を身に着ける\nことも大切よね！\nなになに…", 8, 0, 1, 4893, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115901, 1, "や、やっぱスカートは\n落ち着かねえな…\nなんか、\nスースーするしよ…", 1, 0, 1, 4895, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115901, 1, "せっかくこんな格好\nしてるし、いつもとは\n違う剣術の訓練を\nしてみるかな", 2, 0, 1, 4896, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115901, 1, "さて、他の連中が\n帰ってくる前に、\nなんか料理でも\n作っといてやるか", 3, 0, 1, 4897, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115901, 1, "こ、こんな服着てる\nわけだし、紅茶とか\n楽しんじゃっても\n変じゃないよな！", 4, 0, 1, 4898, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115901, 1, "暇だし運動でも…\nあ、あぶねえ！\nスカートなの忘れてた\n…誰も見てねえよな？", 5, 0, 1, 4899, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115901, 1, "紅茶が切れてるな…\nよし、あいつと一緒に\n買い物にでも行くか♪", 101, 0, 4, 4900, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 115901, 1, "…あいつがいねえと、\nつまんねえな…ったく\n飯用意してやるから、\n早く帰ってこいよな…", 102, 0, 8, 4901, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115901, 1, "あ、あんまり\nジロジロ見んなよ\n…結構、\n恥ずかしいんだからな", 1, 0, 1, 4902, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115901, 1, "ダンスの相手は\n無理だが、\n剣の訓練なら\nいつでも付き合うぜ！", 2, 0, 1, 4903, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115901, 1, "この服に\n触りたいのか？\nすごい作りだし、\n気持ちは分かるぜ", 3, 0, 1, 4904, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115901, 1, "お、いいところに！\nクッキーを焼く\nところだったんだが、\nおまえも食うか？", 4, 0, 1, 4905, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115901, 1, "おまえにもこういう服\n似合うんじゃねえか？\n今度あたしが\n選んでやるよ♪", 101, 0, 4, 4906, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115901, 1, "…おまえの前でなら、\nこういう格好を\nすんのも…\nな、なんでもねえよ！", 102, 0, 8, 4907, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115901, 1, "朝の訓練を始めるか\n時間あるなら、\nおまえも付き合えよな", 1, 1, 1, 4908, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115901, 1, "もうこんな時間か…\n昼飯作るけど、\nなんか\n食いたいものあるか？", 2, 2, 1, 4909, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 115901, 1, "眠れないなら、\nホットミルク飲むか？\n体あったまって、\n眠くなると思うぜ", 3, 3, 1, 4910, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 115901, 1, "ちょっと休憩して…\nおっと、服にしわが\nできないように\nしねえとな", 2, 0, 1, 4912, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 115901, 1, "だ、誰も\n見てねえよな？\n…よ～しよしよし、\nかわいいなおまえー♪", 3, 0, 1, 4913, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 115901, 1, "すげぇいい香りだな\nうちの居酒屋にも…\nいや、合わねえか", 4, 0, 1, 4914, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 115901, 1, "あったけぇ…\nちょっとだけなら、\n寝ちまっても\nいいよな…？", 5, 0, 1, 4915, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 115901, 1, "これうめえな！\nたぶん材料は…よし！\nあたしも作ってみるか", 7, 0, 1, 4917, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 115901, 1, "この本は…\n誰でも分かる作法…か\n最低限このぐらいは\n勉強しとくか…", 8, 0, 1, 4918, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 102001, 1, "ミミがおとなに\nなっても\nおにいちゃんとは\nずーっといっしょ♪", 103, 0, 12, 4920, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102001, 1, "ミミとくべつなだっこ\nしてほしいな…うん！\nおひめさまだっこ～♪", 103, 0, 12, 4921, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100401, 1, "次はにいちゃんに\nどんなイタズラしよう\n早く会いたいな～", 103, 0, 12, 4922, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100401, 1, "ミソギの特別な\nイタズラ見てくれる？\nいいよねっ♪", 103, 0, 12, 4923, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103601, 1, "大人になったら、\nあの人と何しようかな\n…楽しみだなぁ", 103, 0, 12, 4924, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103601, 1, "頭を撫でないで\nください！　もう、\n少しだけ、ですからね", 103, 0, 12, 4925, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116001, 1, "くるくる〜♪\nうちのドレス\nかわええなぁ", 1, 0, 1, 4926, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116001, 1, "日差しは苦手やけど\nたまにはひなたの下で\nぽかぽかしたいわぁ", 2, 0, 1, 4927, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116001, 1, "外はお花が咲いてる\n久しぶりにお花を\n活けたいなぁ", 3, 0, 1, 4928, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116001, 1, "お花をたくさん集めて\nギルドハウスに飾れば\n喜んでくれはるかな", 4, 0, 1, 4929, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116001, 1, "あたたかい日は\n紅茶を飲みながら\nゆっくりしたいなぁ", 5, 0, 1, 4930, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116001, 1, "お稽古事を始めるなら\nうちに言ってな\nどんなことも教えたる", 101, 0, 4, 4931, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116001, 1, "うちにいろんな夢を\n見せてくれはって\nほんま、おおきにな♪", 102, 0, 8, 4932, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116001, 1, "王子はん\n一緒におとぎの国へ\nお散歩にいきまひょ♪", 1, 0, 1, 4933, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116001, 1, "ふんふんふ〜ん♪\n今な、王子はんに\n花冠を作ってるんよ", 2, 0, 1, 4934, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116001, 1, "王子はんは\nお菓子好きどす？\nうち、作りますわ", 3, 0, 1, 4935, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116001, 1, "うーん。ドレスの柄は\nお花とお星さま\nどっちにしまひょか", 4, 0, 1, 4936, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116001, 1, "王子はんとの\n思い出が増えるたび\nうちの宝物も増えます", 101, 0, 4, 4937, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116001, 1, "王子はんと一緒なら\nどんな場所でも\n夢の国になりますわ", 102, 0, 8, 4938, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116001, 1, "夢の中におりたいけど\n王子はんに会いたくて\n起きてもうたわ♪", 1, 1, 1, 4939, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116001, 1, "王子はん一緒に\nアフタヌーンティー\nいかがやろか？", 2, 2, 1, 4940, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116001, 1, "夜の舞踏会…\n夢が広がるわぁ\nもちろんお相手は…♪", 3, 3, 1, 4941, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116001, 1, "ふわふわや〜\nなんや心地よぉて\n眠ってしまいそうや…", 2, 0, 1, 4943, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116001, 1, "かいらしいなぁ\n新しいお洋服でも\n作ってあげまひょか♪", 3, 0, 1, 4944, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116001, 1, "お花やろうか…\nリラックスできて\nええ香りやねぇ", 4, 0, 1, 4945, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116001, 1, "こういう場所で\n絵本を読むと\n気分が出てええなぁ♪", 5, 0, 1, 4946, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116001, 1, "舞踏会を楽しむなら\nたくさん食べて\nたくさん踊りまひょ", 7, 0, 1, 4948, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116001, 1, "うち、この本好きや\n王子さまと結ばれる\nお姫さまのお話なんよ", 8, 0, 1, 4949, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116301, 1, "はぁ、宿題めんどい…\nこれは楽しくない…\nこんな青春いらない…", 1, 0, 1, 4951, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116301, 1, "奨学金の道は遠いなぁ\nメルクリちゃんたちが\nお金くれればいいのに", 2, 0, 1, 4952, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116301, 1, "アオイちゃん元気かな\nチエルに会いたくて\n泣いてるんだろうな…", 3, 0, 1, 4953, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116301, 1, "暇だし鏡でも見るか…\nうわ、顔良っ！\nえ誰この超絶美少女！", 4, 0, 1, 4954, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116301, 1, "ワンツーワンツー♪\nうーん…カルミナの\n振りコピ難しいにゃー", 5, 0, 1, 4955, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116301, 1, "先輩も運動してます？\nチエルが一緒に走って\nあげてもいいですよ？", 101, 0, 4, 4956, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116301, 1, "来年のテレフェスも\n今から楽しみだなあ…\nまた一緒にお化け屋敷\n入ってくださいね", 102, 0, 8, 4957, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116301, 1, "ちぇる～ん☆\nフェス仕様でも\nかわいいちえるん！", 1, 0, 1, 4958, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116301, 1, "いま話しかけないで！\n来年の聖学祭ライブの\nイメトレ中なんです！", 2, 0, 1, 4959, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116301, 1, "クロエ先輩のウェア姿\nめっちゃカッコよ！\n思わずおへそ触ったら\nぶたれましたけど", 3, 0, 1, 4960, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116301, 1, "ユニ先輩の体操着姿…\nあれはあれで実に良き\n一生見てられますよね", 4, 0, 1, 4961, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116301, 1, "先輩って体操着持って\nないんですか？　ハ？\nチエルのを貸せと…？\nえっ…変態かな？", 101, 0, 4, 4962, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116301, 1, "テレフェスでは先輩も\n地味に頑張ってました\nね。まあ格好よかった\nんじゃないですか…", 102, 0, 8, 4963, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116301, 1, "おはちぇる～ん☆\nさあ今日も楽しいを\n探しに行きますよー！", 1, 1, 1, 4964, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116301, 1, "先輩ビッグニュース！\n今からお昼ごはん\nおごらせてあげます！", 2, 2, 1, 4965, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116301, 1, "こんばんちぇるるん☆\nさあ朝まで楽しいを\n探しに行きますよー！", 3, 3, 1, 4966, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116301, 1, "はぁ…ふかふか～…\n運動の後は…眠たく…\nなり…ま…", 2, 0, 1, 4968, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116301, 1, "ユニ先輩に似合いそ！\nいやクロエ先輩かな？\nチエルは当然すぎて…", 3, 0, 1, 4969, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116301, 1, "やん、いい香り～！\nまるでチエルのために\n存在するみたい！", 4, 0, 1, 4970, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116301, 1, "この火を使って\n聖火リレーしましょう\n先輩、一本抜いて！", 5, 0, 1, 4971, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116301, 1, "あむあむ…ちゃんと\n食べないとユニ先輩\nみたいになっちゃう！", 7, 0, 1, 4973, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116301, 1, "ふむふむ…ちゃんと\n読まないとクロエ先輩\nみたいになっちゃう！", 8, 0, 1, 4974, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 102901, 1, "今なら書ける気がする\n私の本当の\n想いを込めた\nラブソングを！", 103, 0, 12, 4976, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102901, 1, "輝きを増した私を\nいっぱい見てね！\n…ちょ、ちょっと\n見つめすぎだよ！", 103, 0, 12, 4977, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116201, 1, "テレフェスかぁー…\nあんまいい記憶ないな\nよし、忘れとこ", 1, 0, 1, 4978, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116201, 1, "暇だし鏡でも見るか…\nふふ～ん…いいじゃん\nあ？　何見てんだコラ", 2, 0, 1, 4979, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116201, 1, "ワンツー、ワンツー♪\nああクソ、難しいな…\nあ？　何見てんだコラ", 3, 0, 1, 4980, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116201, 1, "アオイ元気してるかな\nまた絶叫したり白目\n剥いたりしてんのかな", 4, 0, 1, 4981, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116201, 1, "ミフユねえさんが\nバイトリーダーしてる\n工場…うちも行くか…", 5, 0, 1, 4982, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116201, 1, "うーん…今日の晩御飯\n何にすっかなァ…\nあんたも食べてく？", 101, 0, 4, 4983, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116201, 1, "来年のテレフェスは\nどーなンだかねえ？\nま、一緒にまた弟の\n相手してやってよ", 102, 0, 8, 4984, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116201, 1, "体操着なんてフツーに\n着てもつまんないし？\nその辺センス出るよね", 1, 0, 1, 4985, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116201, 1, "奨学金の道、遠くね？\nアキノ先輩と仲良く\nなった方が早いかも…", 2, 0, 1, 4986, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116201, 1, "あんた体操着は持って\nないの。ハ？　うちの\n貸せって？　いいけど\n…いや、よくねえよ", 3, 0, 1, 4987, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116201, 1, "にゃんっ！\nや、だからさ…急に\n話しかけんなっての", 4, 0, 1, 4988, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116201, 1, "え、なに？　この服？\nハ？　似合ってる？\n…え、聞こえなかった\nちょ、もっかい言って", 101, 0, 4, 4989, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116201, 1, "あんたもテレフェスで\n一応は頑張ってたよね\nま格好よかったんじゃ\nないの。知んないけど", 102, 0, 8, 4990, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116201, 1, "ふぁ～あ…おはーす\n昨日も寝たの遅くて\nまだ目がショボだよ…", 1, 1, 1, 4991, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116201, 1, "なに。あんた昼御飯\nまだなの。じゃ、うち\n適当になんか作ろうか", 2, 2, 1, 4992, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116201, 1, "夜っていいよね。色々\n見えすぎない感じとか\n…この話したっけ？", 3, 3, 1, 4993, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116201, 1, "あ、ヤバ…運動の後に\nこのフカついたのは…\n秒でスヤれる…すやぁ", 2, 0, 1, 4995, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116201, 1, "へーこんなんチエルに\n似合いそうじゃん。\n…いやユニ先輩かな？", 3, 0, 1, 4996, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116201, 1, "お、なんか良さげな\n香りじゃん。これは\n何の…いや分かんね", 4, 0, 1, 4997, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116201, 1, "熱ッ！　この野郎なに\n爆ぜてンだコラァ！\n濡らすぞ木、てめえ！", 5, 0, 1, 4998, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116201, 1, "何コレよく分かんない\nけど、今日モヤシしか\n食ってないから助かる", 7, 0, 1, 5000, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116201, 1, "今日、体操着だしね。\n読書には逆アドかな。\nうん帰ろ。いやあ残念", 8, 0, 1, 5001, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116501, 1, "ビッグボス・イノリ様\nふっふっふ…\nしびれる響きです！", 1, 0, 1, 5003, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116501, 1, "いつかお兄さんを\nあたしの側近に\nしてやるですよ！", 2, 0, 1, 5004, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116501, 1, "ビッグボスになったら\nみんなからたくさん\nお菓子をもらうです！", 3, 0, 1, 5005, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116501, 1, "ふふん！\nこの服はあたしにしか\n着こなせませんよ！", 4, 0, 1, 5006, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116501, 1, "ばーんと立って\nどーんと腕を組む！\nこれがボスのポーズ！", 5, 0, 1, 5007, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116501, 1, "イノリ様の側近に\n選ばれるなんて\n名誉なことですよ！", 101, 0, 4, 5008, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116501, 1, "未来の側近として\nあたしをしっかり\n甘やかすですよ！", 102, 0, 8, 5009, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116501, 1, "あたしがお兄さんを\n立派なギャングに\nしてあげますからね！", 1, 0, 1, 5010, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116501, 1, "側近たるもの\nボスを甘やかなさいと\nだめですよ！", 2, 0, 1, 5011, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116501, 1, "なに見てやがるです…\nくぅ、分かりました！\nお菓子は半分こです！", 3, 0, 1, 5012, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116501, 1, "お兄さんも\nあたしがボスなら\nうれしいですよね！", 4, 0, 1, 5013, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116501, 1, "お兄さんが側近なら\nあたしも安心して\n夜眠れるですよ！", 101, 0, 4, 5014, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116501, 1, "ビッグボスになったら\nお兄さんにいい思い\nさせてあげますから！", 102, 0, 8, 5015, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116501, 1, "ボスたるもの\n早起きは鉄則ですよ！\nふぁあ…", 1, 1, 1, 5016, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116501, 1, "お腹が空きました…\nご飯食べにいきますよ", 2, 2, 1, 5017, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116501, 1, "ボスたるものぉ〜…\n夜更かしぐらい当然…\nぐー…", 3, 3, 1, 5018, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116501, 1, "は〜これは人をだめに\nしやがるやつですね\nあたし専用です", 2, 0, 1, 5020, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116501, 1, "これはけしからん\nかわいらしさですね\nあたしにぴったりです", 3, 0, 1, 5021, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116501, 1, "高級な香りがするです\nボスになったら\n部屋に置いてやるです", 4, 0, 1, 5022, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116501, 1, "あったかいですねぇ\nちょっとこのまま\n一休憩です…", 5, 0, 1, 5023, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116501, 1, "はむはむ♪\nボスたるもの食べねば\n戦はできませんよ！", 7, 0, 1, 5025, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116501, 1, "ボスなら経済書ぐらい\n…う。難しくて\n読めねーです…", 8, 0, 1, 5026, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 102501, 1, "あなたはドジな私を\n受け入れてくれる…\nだから、頑張れます！", 103, 0, 12, 5028, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102501, 1, "何でも仰ってください\nちょっぴり大胆な\nことでも…な～んて♪", 103, 0, 12, 5029, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116601, 1, "あーあー、よし\nのどの調子もいいし\nはりきってガイド\nしてくるか！", 1, 0, 1, 5030, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116601, 1, "さあ、そろそろ行くか\nガイドが\n遅刻したんじゃ\n話になんないからな", 2, 0, 1, 5031, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116601, 1, "しれっとドラゴン族が\n旅行に来たり\nしてくれないかな", 3, 0, 1, 5032, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116601, 1, "たぎるガイド先…\n火山、魔物の巣…\nダメだ、そんなとこ\nオレしか行けないや", 4, 0, 1, 5033, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116601, 1, "この前行った絶景\nスポットよかったな\nキレイな景色を見る\nのもいいもんだぜ", 5, 0, 1, 5034, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116601, 1, "お、今日はカレと\n一緒か。なんだか\nやる気出てきたぜ！", 101, 0, 4, 5035, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116601, 1, "カレにガイドして\nもらったら\nどこに行っても\n楽しいだろうな", 102, 0, 8, 5036, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116601, 1, "よっしゃあ！\nどっちが上手く\nガイドできるか\n勝負しようぜ！", 1, 0, 1, 5037, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116601, 1, "飯でも食いがてら\nおススメの店の\n下調べでもしようぜ！", 2, 0, 1, 5038, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116601, 1, "なかなかいいだろ？\nこの格好。キミも\n作ってもらったら\nどうだ？", 3, 0, 1, 5039, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116601, 1, "普通は行けない\n場所とか行きたいよな\nキミならどんな\nとこがいい？", 4, 0, 1, 5040, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116601, 1, "ガイドの仕事は\nキミの方が得意そうだ\n参考にさせてもらうぜ\nよろしくな！", 101, 0, 4, 5041, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116601, 1, "キミの探してきた店\n結構よさそうじゃん\nそうだ、今度二人で\n下見に行こうぜ！", 102, 0, 8, 5042, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116601, 1, "いい朝だな。絶好の\n仕事日和だぜ！\n今日もよろしくな！", 1, 1, 1, 5043, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116601, 1, "よし、昼飯にしよう\nこの後も激務だし\n飯抜きじゃあ\nぶっ倒れちまうからな", 2, 2, 1, 5044, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116601, 1, "お疲れさん！　さーて\n次はオレと遊んで\nくれよ。まだまだ\nいけるだろ？", 3, 3, 1, 5045, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116601, 1, "ガイドは一日\n歩きっぱなしだからな\nゆっくり休ませて\nもらうぜ", 2, 0, 1, 5047, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116601, 1, "これ、どこで\n売ってるんだろ？\n土産の情報も\n仕入れとかなきゃな", 3, 0, 1, 5048, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116601, 1, "アロマとかよく\n分かんねえけど\n土産にはいいかもな\n覚えておくぜ", 4, 0, 1, 5049, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116601, 1, "体が温まると心も\n熱くなるんだよな\nいい仕事が\nできそうだぜ", 5, 0, 1, 5050, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116601, 1, "食べ放題か\nこれはお客さんも\n喜んでくれそうだな！", 7, 0, 1, 5052, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116601, 1, "へえ、こんなスポット\nあったんだ。やっぱ\nたまには勉強も\nしとくもんだな", 8, 0, 1, 5053, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116701, 1, "バイト先になじめる\nように笑顔の練習を！\nに、にへぇ〜…", 1, 0, 1, 5055, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116701, 1, "働いて働いて働いて\n…いつかその先に\nぼっちの出口が\n見えるのかも…！", 2, 0, 1, 5056, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116701, 1, "バイトのイメトレを\n始めます。ポーズを\n決めて…よし！\n角度を変えて…よし！", 3, 0, 1, 5057, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116701, 1, "バイト仲間に入る\nためには厳しい修業が\n必須と聞きました…！", 4, 0, 1, 5058, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116701, 1, "どんどん技術を磨いて\nバイト仲間として\n認めてもらうんです！\n目指せ職人技…！", 5, 0, 1, 5059, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116701, 1, "あの人が応援して\nくれる…だから必ず\nバイト仲間の輪に\n入ってみせます…！", 101, 0, 4, 5060, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116701, 1, "あの人がいてくれると\n勇気が湧いてきます…\nぼっち卒業を夢見ても\nいいんだ、って…♪", 102, 0, 8, 5061, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116701, 1, "緊張するけど\n思い切って声をかけて\nみます…バイト仲間に\n入るために…！", 1, 0, 1, 5062, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116701, 1, "ぼっちの私が\nおもちゃ作りで\n人と共同作業…！\nか、感動です…！", 2, 0, 1, 5063, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116701, 1, "好きなおもちゃって\nありますか？　私は\nもちろんマイフレンド\nくんです！", 3, 0, 1, 5064, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116701, 1, "私が作ったおもちゃも\n誰かの友達に\nなってたら\nいいなぁ…♪", 4, 0, 1, 5065, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116701, 1, "今日も明日もバイト\nですね！　バイト\n仲間ができる日が\n楽しみです…♪", 101, 0, 4, 5066, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116701, 1, "脱ぼっちを目指して\nこれからも一緒に\n頑張りましょう！\n目指せ、バイト仲間！", 102, 0, 8, 5067, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116701, 1, "おはようございます\n今日も気合いを入れて\nおもちゃ作りします！", 1, 1, 1, 5068, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116701, 1, "ランチの時間です…♪\nもちろん工場の隅で\nぼっち飯です…！", 2, 2, 1, 5069, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116701, 1, "ふわぁ…今日も\nお疲れさまでした\n明日も働きます～！", 3, 3, 1, 5070, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116701, 1, "バイトで頑張った日は\nふわふわの誘惑に\n勝てません…", 2, 0, 1, 5072, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116701, 1, "こういうおもちゃの\nアイデアも\n素敵かも…♪", 3, 0, 1, 5073, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116701, 1, "いい香りがする\nおもちゃはどうかな…\n森の薬草を使って…", 4, 0, 1, 5074, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116701, 1, "ぽかぽか…\n幸せなひとときですね", 5, 0, 1, 5075, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116701, 1, "もぐもぐ…たくさん\n食べて厳しいバイトを\n乗り切ります…！", 7, 0, 1, 5077, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116701, 1, "ふむふむ…おもちゃの\n設計図を見るのも\n勉強になりますね！", 8, 0, 1, 5078, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116801, 1, "う～ん\n次はどんなたい焼きを\n作ろうかにゃぁ…", 1, 0, 1, 5080, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116801, 1, "あたしが売る\nたい焼きには\n夢が詰まってるにゃ！", 2, 0, 1, 5081, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116801, 1, "いつか絶対に\nたい焼き工場を\n建て直してみせるにゃ", 3, 0, 1, 5082, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116801, 1, "たまにたい焼き以外の\nおやつも食べるにゃ\nたとえば煮干しとか！", 4, 0, 1, 5083, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116801, 1, "悪い商売人がいたら\nファントムキャッツに\n知らせるにゃ！", 5, 0, 1, 5084, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116801, 1, "キミと一緒にいると\nたい焼きのアイデアが\n次々出てくるにゃ♪", 101, 0, 4, 5085, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116801, 1, "出来ないことも\nキミと一緒にいれば\n出来る気がするにゃ♪", 102, 0, 8, 5086, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116801, 1, "なんにゃ～？\nお姉さんにかまって\nほしいのかにゃ♪", 1, 0, 1, 5087, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116801, 1, "キミ、いいところに！\nあたしとライバル店へ\n視察にいくにゃ！", 2, 0, 1, 5088, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116801, 1, "くんくん…この匂い！\nたい焼き持ってるなら\nちょうだいにゃ♪", 3, 0, 1, 5089, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116801, 1, "次はどんなたい焼きを\n売ろうかにゃ～\nキミの案も欲しいにゃ", 4, 0, 1, 5090, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116801, 1, "ファントムキャッツに\n相棒が必要にゃ\nたとえばキミとか…", 101, 0, 4, 5091, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116801, 1, "相棒！\n…ってキミのこと\n呼んでいいかにゃ？", 102, 0, 8, 5092, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116801, 1, "早起きは三文の徳にゃ\n今日もたい焼き\n売れますよーににゃ！", 1, 1, 1, 5093, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116801, 1, "お腹空いたにゃ…\n賄いのたい焼きを\nいただくにゃ！", 2, 2, 1, 5094, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116801, 1, "猫は夜が本番にゃ\nでもあたしは…\nふわぁ～眠いにゃ…", 3, 3, 1, 5095, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116801, 1, "ふかふかにゃ～\nここで体を丸めて\n一眠りしたいにゃ", 2, 0, 1, 5097, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116801, 1, "これは…！\n猫にも負けない\nかわいさだにゃ…！", 3, 0, 1, 5098, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116801, 1, "いい香りにゃ～\nゴロゴロのどが\n鳴りそうだにゃ～", 4, 0, 1, 5099, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116801, 1, "ふにゃ～…\nこのまま働かずに\nまるまってたいにゃ…", 5, 0, 1, 5100, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116801, 1, "商売は戦いにゃ\nたくさん食べて\nたくさん働くにゃ！", 7, 0, 1, 5102, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116801, 1, "ふむふむ。たい焼きの\nアレンジレシピ集…\nためになるにゃ…", 8, 0, 1, 5103, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 102101, 1, "お芝居の前に\nお兄ちゃんの顔を\n見ると、とっても\n落ち着きますぅ…♪", 103, 0, 12, 5105, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102101, 1, "お兄ちゃんにもっと\n見てもらいたい…\nだから私…\n強くなりたいです…！", 103, 0, 12, 5106, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116901, 1, "さらなる効率化で\nみんなといい仕事を\nしたいわね！", 1, 0, 1, 5107, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116901, 1, "他のバイトとの\nスケジュールを\n調整しないと…", 2, 0, 1, 5108, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116901, 1, "時給がアップしたわ！\n働きが認められると\nやる気が出るわね", 3, 0, 1, 5109, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116901, 1, "効率的に動ければ\n業務を追加できる…\n燃えてきたわ！", 4, 0, 1, 5110, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116901, 1, "帳簿付けのコツ…\nユカリさんに教わって\nみようかしら", 5, 0, 1, 5111, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116901, 1, "バイトリーダーとして\nみんなをビシバシ\n鍛えていくわ！", 101, 0, 4, 5112, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116901, 1, "戦いでもバイトでも\nキミなら安心して\n背中を預けられるわね", 102, 0, 8, 5113, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116901, 1, "どうしたの？\n仕事のことなら\nなんでも相談してね！", 1, 0, 1, 5114, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116901, 1, "さすがは私の弟子ね\nこれなら安心して\n仕事を任せられるわ", 2, 0, 1, 5115, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116901, 1, "みんなのお手本になる\n働きぶりを見せないと\nいけないわね！", 3, 0, 1, 5116, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116901, 1, "キミのこと…もっと\n頼りにしちゃっても\nいいのかしら…？", 4, 0, 1, 5117, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116901, 1, "ふふ、キミも一人前の\nバイト戦士として\n立派に成長したわね！", 101, 0, 4, 5118, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116901, 1, "新しいおもちゃの\n企画書を作ってみたの\nちょっと見てくれる？", 102, 0, 8, 5119, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116901, 1, "朝は一番効率がいい\n時間帯よ！\nバリバリ働くわよ！", 1, 1, 1, 5120, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116901, 1, "さ、お昼に行きましょ\n今日の日替わり定食は\n何かしら…楽しみね♪", 2, 2, 1, 5121, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116901, 1, "もうこんな時間…\nちゃんと寝て明日に\n備えなくちゃね", 3, 3, 1, 5122, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116901, 1, "適度な労働と休息の\nサイクルが最大効率を\n生み出すのよ…ふぅ…", 2, 0, 1, 5124, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116901, 1, "はぁ～なんて素敵な\n抱き心地なのかしら…\n最高効率の癒やしね♪", 3, 0, 1, 5125, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116901, 1, "落ち着く香りね…\nなんだか集中力が\nアップした気がするわ", 4, 0, 1, 5126, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116901, 1, "ふぅ…癒されるわね…\n仕事のオンオフの\n切り替えは重要よね", 5, 0, 1, 5127, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116901, 1, "働くためには\nエネルギーをちゃんと\n摂っておかないと", 7, 0, 1, 5129, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116901, 1, "効率を上げるため\n生産管理の勉強も\nしておきたいわね", 8, 0, 1, 5130, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180301, 1, "変身している間に\n色々と試しておこう\nまずは…", 1, 0, 1, 5132, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180301, 1, "飛べるというのは\n便利だな。料理が\n暖かいうちに\n持ち帰れる", 2, 0, 1, 5133, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180301, 1, "プリンセスか…\n柄ではないけれど\n嫌な気はしないな", 3, 0, 1, 5134, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180301, 1, "この力で\n何を成すべきなのか…\n剣に聞いてみよう", 4, 0, 1, 5135, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180301, 1, "変身したとて私は私\n一時的に強くなろうと\n元が弱くてはだめだ", 5, 0, 1, 5136, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180301, 1, "変身していない時でも\n彼がそばにいると\n力が湧いてくる…", 101, 0, 4, 5137, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 180301, 1, "私の思い…\nいつか伝えてみせるよ\n…きっといつか！", 102, 0, 8, 5138, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180301, 1, "何者かと戦うの？\nそれとも修行？\nどちらだとしても\n付き合うよ", 1, 0, 1, 5139, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180301, 1, "変身し続けたら\nどれだけ空腹に…\n私は試さないからね？", 2, 0, 1, 5140, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180301, 1, "本物のプリンセスに\nなるのは御免だが\nナイトがいて\nくれるのは頼もしいよ", 3, 0, 1, 5141, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180301, 1, "いつかこの力を\n誰かを救うために…\n戦い以外に使える時が\nくることを願うよ", 4, 0, 1, 5142, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180301, 1, "キミに感じてほしいな\n私に流れてくる\nキミからの力を…\nその…私の、思いも…", 101, 0, 4, 5143, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180301, 1, "思いを告げる…\nそれがこんなにも\n難しいなんて…\nでも、いつか必ず…！", 102, 0, 8, 5144, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180301, 1, "おはよう！\nいい変身日和だ！\n…ふふっ、冗談だよ", 1, 1, 1, 5145, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180301, 1, "いつもより多く\n昼食を用意しよう\n変身が解けたら大変だ", 2, 2, 1, 5146, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 180301, 1, "一日お疲れ様\n私もそろそろ変身を\n解いて休むとするよ", 3, 3, 1, 5147, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 180301, 1, "変身してしまうと\n動かずにいても\n空腹になるのかな…？", 2, 0, 1, 5149, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 180301, 1, "私もキミたちくらい\n可愛げがある姿に\nなっているかな？", 3, 0, 1, 5150, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 180301, 1, "香りに対する感覚も\nいつもより鋭く\nなっているみたいだ…", 4, 0, 1, 5151, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 180301, 1, "ありがたいな\n少し足元が\n冷えていたところだ", 5, 0, 1, 5152, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 180301, 1, "これは…！\n持ち運べるように\n改良できないかな？", 7, 0, 1, 5154, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 180301, 1, "祖父に貰った\n技法書を読んで\n初心に帰ろう", 8, 0, 1, 5155, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117001, 1, "最強の惚れ薬を作って\nあのおかたの心を\n私の思いのままに…", 1, 0, 1, 5157, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117001, 1, "夏といえば海にスイカ\n花火に水着…\nそして惚れ薬ですわ…", 2, 0, 1, 5158, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117001, 1, "あのおかたの心を\n手に入れるための\n準備を進めましょう\nこの夏こそ確実に…", 3, 0, 1, 5159, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117001, 1, "あのおかたに近づく\n虫どもを、どうやって\n片付けようかしら…\nまったく忌々しいこと", 4, 0, 1, 5160, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117001, 1, "…クスクス\n薬の調合を考えるのは\n至福の時間ですわ…", 5, 0, 1, 5161, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117001, 1, "あのおかたの全てが\n早く私のものに\nなりますように…", 101, 0, 4, 5162, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117001, 1, "かき氷も花火も海も…\nあなた様がいてこそ…\nそう、あなた様は\n私の全てなのですわ", 102, 0, 8, 5163, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117001, 1, "二人だけの夏を\n楽しみましょう…？\n…クスクス", 1, 0, 1, 5164, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117001, 1, "あら、あなた様\n私に秘密でどこに\n行こうとして\nいますの…？", 2, 0, 1, 5165, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117001, 1, "海に行きませんか？\nあなた様と一緒に\n過ごすには\n最高の場所ですから…", 3, 0, 1, 5166, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117001, 1, "ちょうどいいところに\nいらっしゃいました\nできたての薬を…\nさぁどうぞ…クスクス", 4, 0, 1, 5167, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117001, 1, "夏の海のきらめきも\nあなた様の存在が\nなければ色あせて\nしまいますわ…", 101, 0, 4, 5168, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117001, 1, "私とあなた様は\n結ばれる運命に\nあるのです…\n逃がしませんわ…", 102, 0, 8, 5169, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117001, 1, "起きてすぐに会いに\n来てくださるなんて\nようやく私の伴侶たる\n自覚が…クスクス", 1, 1, 1, 5170, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117001, 1, "お食事はまだですか？\n特製の薬草ランチを\n召し上がれ…クスクス", 2, 2, 1, 5171, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117001, 1, "私の夢を見られる薬を\n差し上げますわ…\nおやすみなさいませ\nあなた様…クスクス", 3, 3, 1, 5172, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117001, 1, "いい肌触りですわ…\n考え事にもぴったり\n眠くなるのが\n玉に瑕ですが…", 2, 0, 1, 5174, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117001, 1, "こういったものに\n揮発性の薬を仕込む\nのも一つの手ですわね", 3, 0, 1, 5175, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117001, 1, "この香り…開発中の\n薬に使えそうな気が\nしますわ…クスクス", 4, 0, 1, 5176, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117001, 1, "あのおかたの\nぬくもりを\n思い出しますわ…", 5, 0, 1, 5177, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117001, 1, "美味しい…この味なら\n薬を混ぜても違和感は\nなさそうですわ…", 7, 0, 1, 5179, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117001, 1, "この薬の調合法…\n今度試してみましょう\nもちろんあのおかたに\n飲んでいただいて…", 8, 0, 1, 5180, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117001, 1, "二人乗りができると\nいいですわね…", 10, 0, 1, 5182, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117001, 1, "ふかふかですわ…\nあのおかたの分も\n欲しくなります…", 11, 0, 1, 5183, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101701, 1, "今日はキミと\nいつまでも歌って\n踊りたい気分さ～♪", 103, 0, 12, 5184, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101701, 1, "あは～♪\nキミの隣は、故郷より\n落ち着くかも～…♪", 103, 0, 12, 5185, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117101, 1, "この水着…\n一番に弟くんに\n見せたいな♪", 1, 0, 1, 5186, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117101, 1, "弟くんをこっそり\n海に運んで…\nサプライズしちゃお♪", 2, 0, 1, 5187, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117101, 1, "いつもと違う髪型に\nしてみたけど…\n気に入ってくれるかな", 3, 0, 1, 5188, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117101, 1, "海は楽しみだけど\n日焼けしないように\n気を付けなきゃね", 4, 0, 1, 5189, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117101, 1, "まだかな、弟くん\n水着のお姉ちゃんが\nお出迎えだよっ♪", 5, 0, 1, 5190, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117101, 1, "海が見えるおうちで\n弟くんと一緒に\n暮らしたいなぁ～", 101, 0, 4, 5191, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117101, 1, "弟くんの水着…\nどんな感じかなぁ\n…あ、想像してたら\nドキドキして…えへへ", 102, 0, 8, 5192, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117101, 1, "こーら、水着の\nお姉ちゃんは\nおさわり禁止♪\n…なんてね？", 1, 0, 1, 5193, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117101, 1, "なぁに？\nふふっ、何回でも\n呼んでいいんだよ♪", 2, 0, 1, 5194, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117101, 1, "海で迷子に\nならないように\n手を繋ごうね♪", 3, 0, 1, 5195, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117101, 1, "お姉ちゃんが\n海の楽しみ方を\n教えてあげるねっ！", 4, 0, 1, 5196, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117101, 1, "わっ、弟くん\nどこ触ってるの？\nも～驚いちゃったぞ♪", 101, 0, 4, 5197, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117101, 1, "お姉ちゃん、海でも\nずっとキミを見てるよ\n…安心してね", 102, 0, 8, 5198, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117101, 1, "まだ眠そうだね？\nほら、海が待ってるよ\n早く起きて起きて♪", 1, 1, 1, 5199, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117101, 1, "大丈夫、疲れてない？\n海で遊び疲れた時は\n素直に休憩しようね", 2, 2, 1, 5200, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117101, 1, "ほら、早く寝ないと\n明日もお姉ちゃんと\n遊べないぞ♪", 3, 3, 1, 5201, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117101, 1, "だらけたお姉ちゃんは\n弟くんには\n見せられないな～", 2, 0, 1, 5203, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117101, 1, "ふふっ、かわいいね♪\n弟くんが持つと\nもっとかわいいよっ♪", 3, 0, 1, 5204, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117101, 1, "いい香りだね…\n一味違うお姉ちゃんに\nなれそう…♪", 4, 0, 1, 5205, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117101, 1, "あったか～い♪\nほら、弟くんも\nおいでよ♪", 5, 0, 1, 5206, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117101, 1, "よく食べて、いつでも\n弟くんの呼び声に\n応える準備を…！", 7, 0, 1, 5208, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117101, 1, "弟くんの水着の写真\nこの辺りに隠して\nおこうかな", 8, 0, 1, 5209, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117101, 1, "この子の背に乗って\nお姉ちゃんと弟くんの\n楽園へ…な～んて♪", 10, 0, 1, 5211, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117101, 1, "海を感じる感触だね\n弟くんとの楽しい夏を\n思い出しちゃうな…♪", 11, 0, 1, 5212, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117201, 1, "この夏、誰よりも\nキラキラしてみせる！\nだって私は\nアイドルだから！", 1, 0, 1, 5213, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117201, 1, "日傘にアームカバー\n帽子にサングラス…\n紫外線対策は完璧！", 2, 0, 1, 5214, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117201, 1, "とっておきの\nサマーソングを\nみんなに届けるよ！", 3, 0, 1, 5215, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117201, 1, "なんにもしないのって\n苦手かも…\n彼、早く来ないかな？", 4, 0, 1, 5216, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117201, 1, "ラムネって\n夏って感じするよね\n後で彼と飲もっと♪", 5, 0, 1, 5217, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117201, 1, "夏は恋の季節！\n燃えるような恋…\nいつかしたいな", 101, 0, 4, 5218, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117201, 1, "今年の夏の目標は\n…彼との思い出を\nいっぱい作ること！", 102, 0, 8, 5219, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117201, 1, "私の水着写真集\n見てくれたんだ！\nどう、かわいかった？", 1, 0, 1, 5220, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117201, 1, "スイカ割りしたいの？\nいいね、楽しそう！\nみんなも誘おうよ♪", 2, 0, 1, 5221, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117201, 1, "ねぇ、見て見て！\n水着の映える\nポーズ考えたんだ！", 3, 0, 1, 5222, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117201, 1, "ライブ中に\n花火を打ち上げたら\n絶対盛り上がるよね！", 4, 0, 1, 5223, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117201, 1, "最近、忙しいけど…\nキミの顔見たら\n疲れなんて\n消えちゃった！", 101, 0, 4, 5224, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117201, 1, "この夏もいーっぱい\nドキドキさせるから！\n覚悟しててよね♪", 102, 0, 8, 5225, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117201, 1, "外は朝から暑いね\nけど、負けないから！\nお仕事、頑張るぞー！", 1, 1, 1, 5226, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117201, 1, "自分へのご褒美に、\nアイス食べちゃお♪\nキミも一緒に食べよ！", 2, 2, 1, 5227, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117201, 1, "夏の夜って\nワクワクするね♪\nホタル見に行こうよ！", 3, 3, 1, 5228, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117201, 1, "ふわぁ…眠い…\n仕事とかの疲れ、\nたまってるのかな…？", 2, 0, 1, 5230, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117201, 1, "私のぬいぐるみが\nグッズになったら…\n彼にかわいがって\nもらえるかな？", 3, 0, 1, 5231, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117201, 1, "この香り…\n夏限定のやつでしょ？\nいいセンスしてるね♪", 4, 0, 1, 5232, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117201, 1, "暑～い！\nけど…汗かいて\n気持ちいいかも！", 5, 0, 1, 5233, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117201, 1, "レッスンの後のご飯は\n特別おいしい♪\n…もうひとつだけ\n食べちゃおっかな", 7, 0, 1, 5235, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117201, 1, "あ、この前撮影した\n水着グラビアだ\nうんうん、いい感じ！", 8, 0, 1, 5236, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117201, 1, "この子、もふもふだね\n触るのクセになりそう\nもふもふもふもふ…", 10, 0, 1, 5238, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117201, 1, "わ、かわいい♪\n今度のグラビア撮影で\nこれ使ってもいい？", 11, 0, 1, 5239, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117401, 1, "水着っていいですよね\n海に浮かぶ一輪の花\nって感じがして！", 1, 0, 1, 5240, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117401, 1, "自分の水着を作るのも\n誰かの水着を作るのも\n楽しいですね♪", 2, 0, 1, 5241, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117401, 1, "水着の最終チェック！\n伸縮性や動きやすさは\n…いい感じですね！", 3, 0, 1, 5242, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117401, 1, "振り付けや衣装作りに\n歌や踊りの練習…\n…やることがいっぱい", 4, 0, 1, 5243, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117401, 1, "はぁ…レイ様の水着…\n私に作らせて\nもらえないかなぁ～…", 5, 0, 1, 5244, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117401, 1, "騎士さんのことは\n気に入らないですけど\n別に嫌いという\nわけじゃなくて…", 101, 0, 4, 5245, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117401, 1, "騎士さん暇そうですね\nに、荷物持ちとして\n誘ってみようかな…", 102, 0, 8, 5246, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117401, 1, "騎士さんいいところに\nこの衣装\nどう思います？", 1, 0, 1, 5247, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117401, 1, "何見てるんですか？\nもしかしてこの水着の\n良さが分かります！？", 2, 0, 1, 5248, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117401, 1, "真夏でも涼しい…\nそれも水着の良さの\n一つですね", 3, 0, 1, 5249, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117401, 1, "仕立て屋も\nアイドルも\n諦めません！", 4, 0, 1, 5250, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117401, 1, "夏と言えば\nバカンスですけど…\n予定、あるんですか？", 101, 0, 4, 5251, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117401, 1, "う～ん…騎士さんを\n海へ誘うには…って\nなんでもないですよ！", 102, 0, 8, 5252, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117401, 1, "ふー…夏はやっぱり\n朝が一番過ごしやすい\nですね～！", 1, 1, 1, 5253, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117401, 1, "暑い時こそ海です！\n体力づくりのためにも\n泳ぎにいきますよ！", 2, 2, 1, 5254, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117401, 1, "外で虫が鳴いてる…\n夏って感じがして\n風流ですねぇ…", 3, 3, 1, 5255, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117401, 1, "少し休憩です…\nこのまま寝ないよう\n気をつけないと…", 2, 0, 1, 5257, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117401, 1, "か、かわいい…！\nこの感動を次の\nステージ衣装にも…！", 3, 0, 1, 5258, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117401, 1, "いい香りですね～\n波の音が聞こえて\nきそうです…", 4, 0, 1, 5259, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117401, 1, "泳いだ後は\n少し寒いので\nちょうどいいですね", 5, 0, 1, 5260, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117401, 1, "夏バテしないように\n栄養はしっかり\n取らないと！", 7, 0, 1, 5262, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117401, 1, "次のトレンドは…\nふむふむ、やっぱり\nこう来ましたか…", 8, 0, 1, 5263, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117401, 1, "デザインはもちろん\n触り心地も\n最高ですね♪", 10, 0, 1, 5265, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117401, 1, "夏の撮影スポットに\n活用できそうですね\nさっそく衣装考えて…", 11, 0, 1, 5266, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104001, 1, "えへへ…♪\n団長さんが初めての\n友達で本当に良かった", 103, 0, 12, 5267, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104001, 1, "あっ、団長さん！\n今日のＢＢ団の活動\nなんですけど…二人で\n大縄跳びしませんか！", 103, 0, 12, 5268, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117301, 1, "この衣装で\nライブするのは\n恥ずかしいな…", 1, 0, 1, 5269, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117301, 1, "せっかくの夏だし\n肝試しに\n行ってみたいかも…", 2, 0, 1, 5270, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117301, 1, "歌もダンスも\nより上を目指すために\n夏合宿をしよう…！", 3, 0, 1, 5271, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117301, 1, "夏の暑さにも負けない\n熱いライブに\nしてみせる…！", 4, 0, 1, 5272, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117301, 1, "新曲のアイデア\nまとめたいな…\n少し海辺を散歩しよう", 5, 0, 1, 5273, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117301, 1, "かき氷、食べたいな\n…あの人は何味が\n好きなんだろう？", 101, 0, 4, 5274, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117301, 1, "水着、似合ってるって\n言ってもらえるかな…", 102, 0, 8, 5275, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117301, 1, "サインして、ですか？\nいいですけど…\n少し照れますね…", 1, 0, 1, 5276, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117301, 1, "少し待ってください\n今、新曲の楽譜を\n書いているので…", 2, 0, 1, 5277, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117301, 1, "なんでしょう？\nもしかして…\nボイトレに興味が？", 3, 0, 1, 5278, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117301, 1, "今度のライブでは\n夏らしい爽やかな歌を\nファンにお届けします", 4, 0, 1, 5279, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117301, 1, "…水鉄砲で遊ぼう？\nふふ。楽しそうですね\nお手柔らかに…♪", 101, 0, 4, 5280, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117301, 1, "あなたの声を\n聴いていると、\n何だか落ち着きます…", 102, 0, 8, 5281, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117301, 1, "今日も暑くなりそう…\nお互いしっかりと\n水分補給しましょうね", 1, 1, 1, 5282, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117301, 1, "お腹空きましたよね\n海の家で、\nなにか買ってきます", 2, 2, 1, 5283, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117301, 1, "夜の海で月明かりと\n波の音を楽しもうかと\n…一緒に行きますか？", 3, 3, 1, 5284, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117301, 1, "レッスンだけでなく\nリラックスすることも\n大事、ですよね", 2, 0, 1, 5286, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117301, 1, "ふふ、かわいい\n精霊も負けないくらい\nかわいいですけどね", 3, 0, 1, 5287, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117301, 1, "夏らしい爽やかで\nいい匂い…あなたに\nピッタリな香りですね", 4, 0, 1, 5288, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117301, 1, "海に入った後は、\n体が冷えてしまいます\n一緒に温まりましょう", 5, 0, 1, 5289, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117301, 1, "アイドルとして、\n食べすぎには\n気をつけないと…", 7, 0, 1, 5291, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117301, 1, "夏は恋の季節…\nガンガン攻めるべし…\nなるほど…", 8, 0, 1, 5292, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117301, 1, "この子に乗りながら\nライブをする…\nというのはどうですか", 10, 0, 1, 5294, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117301, 1, "まるで人魚になった\n気分…なんて、ふふ", 11, 0, 1, 5295, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118301, 1, "お姉ちゃんとして\nシオリンにかっこいい\nとこ見せなくっちゃ☆", 1, 0, 1, 5296, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118301, 1, "いつかシオリンと\n一緒に空を飛んで\n空中散歩してみたいな", 2, 0, 1, 5297, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118301, 1, "すこし眠くなってきた\nかも…シオリンと\nお昼寝しようかな", 3, 0, 1, 5298, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118301, 1, "お散歩してこよっと！\nシオリンとお出かけ\nするのにいい場所\n探さなくっちゃ！", 4, 0, 1, 5299, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118301, 1, "シオリンに過保護って\n言われちゃった…♪\nそれでも心配しちゃう\nのが姉心…だよね？", 5, 0, 1, 5300, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118301, 1, "気持ちいい日差し！\nシオリンと彼と、\nお昼寝したら\n気持ちよさそ～♪", 101, 0, 4, 5301, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118301, 1, "今度天気がいい日には\nシオリンと彼を誘って\n一緒にお出かけ\nしたいな～♪", 102, 0, 8, 5302, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118301, 1, "今日はご機嫌なんだ♪\nだって、シオリンの\n調子がいいんだもん☆", 1, 0, 1, 5303, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118301, 1, "今からシオリンと\nピクニックに\n行くんだ♪　そうだ！\nキミも一緒にどう？", 2, 0, 1, 5304, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118301, 1, "わっ、びっくりした！\nシオリンから借りた本\n読むのに集中\nしてたから…", 3, 0, 1, 5305, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118301, 1, "うん？　どうしたの？\nあっ、もしかして\nこの服、気になる？", 4, 0, 1, 5306, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118301, 1, "シオリンもキミも元気\nいっぱいだと、私も\n元気になっちゃうよ☆", 101, 0, 4, 5307, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118301, 1, "シオリンの前だけ\nじゃなくて、キミにも\nかっこいい私の姿を\n見てほしいな☆", 102, 0, 8, 5308, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118301, 1, "おはよう！　えへへっ\n今日は早起きなんだ♪\nシオリンにかっこ悪い\nとこ見せたくないから", 1, 1, 1, 5309, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118301, 1, "おなかすいたなぁ～…\nシオリンと一緒に\nおいしいもの食べに\n行こうかな！", 2, 2, 1, 5310, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118301, 1, "もうこんな時間！？\nシオリンと一緒だと\n時間が早く\n過ぎていっちゃうね！", 3, 3, 1, 5311, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118301, 1, "この快適な寝心地…！\nシオリンにもおすすめ\nしなきゃ…！", 2, 0, 1, 5313, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118301, 1, "わあ、かわいい！\nそれにさわり心地も\nいいし、抱き枕に\nちょうどいいかも！", 3, 0, 1, 5314, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118301, 1, "いいにおい…♪\nなんだかリラックス\nできて…すやぁ…", 4, 0, 1, 5315, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118301, 1, "ぽかぽか～☆\nシオリンと一緒に\nぬくぬくしたら\n気持ちよさそう♪", 5, 0, 1, 5316, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118301, 1, "仲のいい人が一緒だと\nつい食が進んじゃうね\nえへへ…♪", 7, 0, 1, 5318, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118301, 1, "ふむふむ…この本…\nシオリンが好きかも！\n今度おすすめしよっと", 8, 0, 1, 5319, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118401, 1, "お姉ちゃん、\n私のために無理は\nしてないかな…？", 1, 0, 1, 5322, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118401, 1, "新しく買った絵本…\nこういうのなら\nお姉ちゃんと楽しく\n読めるよね", 2, 0, 1, 5323, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118401, 1, "ニャットちゃんを\nここに連れてきて\nみんなで遊べないかな", 3, 0, 1, 5324, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118401, 1, "本屋さんに行こうかな\nみんなで読める\n面白い本が\nあるといいなぁ", 4, 0, 1, 5325, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118401, 1, "たまには…読むだけ\nじゃなくて、自分で\nお話を書いて\nみようかな…？", 5, 0, 1, 5326, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118401, 1, "時々…お姉ちゃんと\nあの人が似てるって\n思うことがあります…", 101, 0, 4, 5327, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118401, 1, "お姉ちゃんとあの人…\n優しくて頼もしい\n二人に囲まれて…\n私、幸せ者だなぁ…", 102, 0, 8, 5328, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118401, 1, "どうかしましたか？\nもしかして…あなたも\n一緒にこの本を\n読みたい…とか？", 1, 0, 1, 5329, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118401, 1, "けほっ…けほっ…\nあっ、心配いりません\n飲み物を飲んで\nむせただけですから", 2, 0, 1, 5330, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118401, 1, "この格好…\n似合ってますか…？\nえへへ、照れますね…", 3, 0, 1, 5331, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118401, 1, "なぁに、お姉ちゃん？\n…あっ、すみません\n読書に集中していて…\n間違えちゃいました…", 4, 0, 1, 5332, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118401, 1, "お姉ちゃん、時々無理\nしちゃうから…\nあなたも一緒だと\n安心できます", 101, 0, 4, 5333, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118401, 1, "お姉ちゃんとあなたと\n頼れる人に囲まれて\n私は幸せものですね", 102, 0, 8, 5334, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118401, 1, "なんだか朝から\n調子がいい…今日は\nいい日になりそうです", 1, 1, 1, 5335, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118401, 1, "せっかくだから、\nお姉ちゃんを誘って\nお外でランチも\nいいかも", 2, 2, 1, 5336, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118401, 1, "お姉ちゃんに心配\nかけないように…もう\nちょっと本を読んだら\n寝なくちゃね", 3, 3, 1, 5337, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118401, 1, "ふんわり柔らかくて\n気持ちいい手触り…\nお姉ちゃんが寝るのに\nちょうどよさそう", 2, 0, 1, 5339, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118401, 1, "ふふっ、かわいい♪\n絵本の物語みたいに\nおしゃべりできない\nかな…？", 3, 0, 1, 5340, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118401, 1, "素敵な落ち着く香り♪\nなんだかお姉ちゃん\nみたい、ふふっ", 4, 0, 1, 5341, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118401, 1, "あったかくてほっと\nするなぁ\nここでゆっくり\n読書しようかな", 5, 0, 1, 5342, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118401, 1, "この味…\nお姉ちゃんが好きそう\n少しもらって、\n持って行こうかな…？", 7, 0, 1, 5344, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118401, 1, "この本みたいな、\n心躍る大冒険も\nお姉ちゃんと一緒なら\nできちゃいそう", 8, 0, 1, 5345, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117501, 1, "いい部屋ね\nここを私の分院に\nするのもいいかも\nしれないわね…♪", 1, 0, 1, 5348, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117501, 1, "珍しいカンポーが\n手に入ったから\n色々試してみたいわ\n色々と、ね♪", 2, 0, 1, 5349, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117501, 1, "この服を着ると\n自分が医者だって\nより実感するわね\nまあ、闇医者だけどね", 3, 0, 1, 5350, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117501, 1, "新薬開発のために\n薬草を採取して\nおこうかしら", 4, 0, 1, 5351, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117501, 1, "いつ急患が来ても\n対応できるように\nしておきましょうか", 5, 0, 1, 5352, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117501, 1, "あの子の研究資料も\nだいぶ増えてきたわね\nちゃんとまとめて\nおかないと♪", 101, 0, 4, 5353, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117501, 1, "あの子は大事な\n研究対象。だから\nいつまでも健康で\nいてもらわないとね♪", 102, 0, 8, 5354, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117501, 1, "薬に興味があるの？\nなら、今度ゆっくりと\n教えてあげるわね", 1, 0, 1, 5355, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117501, 1, "少し時間があいたから\n一緒に新薬の実験を\nしましょうか♪", 2, 0, 1, 5356, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117501, 1, "どうしたの？\nまさか急患…！？\nあら、違うのね\nならよかったわ", 3, 0, 1, 5357, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117501, 1, "具合が悪い時はすぐに\n言うのよ。君には\nいつでも健康でいて\nほしいから", 4, 0, 1, 5358, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117501, 1, "少し仮眠したいわね\n肩…いいえ、膝を\n貸してもらえる？", 101, 0, 4, 5359, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117501, 1, "たまには…君の隣なら\n闇医者も悪い研究者も\nお休みにするのも\n悪くないわね♪", 102, 0, 8, 5360, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117501, 1, "たまには朝の陽ざしを\n浴びるのも…\n悪くないわね", 1, 1, 1, 5361, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117501, 1, "天気がいい日は\nいい薬草が採れるの\n君も集めるの、\n手伝ってくれる？", 2, 2, 1, 5362, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117501, 1, "いけない、もうこんな\n時間なのね\n診察に没頭すると\n時間感覚がなくなるわ", 3, 3, 1, 5363, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117501, 1, "もう少し固い方が\n私好みだけど…\n悪くない寝心地ね", 2, 0, 1, 5365, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117501, 1, "このぬいぐるみ…\n闇医者として\n興味深いわね…", 3, 0, 1, 5366, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117501, 1, "落ち着く香りね\n私も診察室で\n使ってみようかしら", 4, 0, 1, 5367, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117501, 1, "冷えは病につながるわ\nしっかり温めて\n体をいたわりましょ", 5, 0, 1, 5368, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117501, 1, "おいしい♪\n研究のお供に\nもってこいの\n味だわ", 7, 0, 1, 5370, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117501, 1, "医学は日進月歩…\n闇医者とはいえ\n最新の論文には\n目を通しておかないと", 8, 0, 1, 5371, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117601, 1, "白衣の天使かあ\nまさにボクのための\n呼び方だね", 1, 0, 1, 5400, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117601, 1, "美しさとは\n日々の努力が\n作るものなんだよ", 2, 0, 1, 5401, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117601, 1, "大変だ…\nボクは美しく\nなりすぎたかも", 3, 0, 1, 5402, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117601, 1, "ボクのかわいさで\n癒やされて\nみんな健康に\nなっちゃうね", 4, 0, 1, 5403, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117601, 1, "どんな服装をしても\nボクの美しさは\n変わらないね", 5, 0, 1, 5404, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117601, 1, "これからはボクも\n美の伝道師として\nがんばらないとね♪", 101, 0, 4, 5405, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117601, 1, "ボクのかわいい笑顔を\n近くで見る以上の\n癒やしはないよね♪", 102, 0, 8, 5406, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117601, 1, "どう？　看護師姿の\nボクもかわいいでしょ\nもっと近くで見る？", 1, 0, 1, 5407, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117601, 1, "ほら！　美しいボクの\n看護師姿を見たら\n元気でちゃうでしょ？", 2, 0, 1, 5408, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117601, 1, "白衣の天使のボクも\n最高に美しいね\n存分に癒されてよ♪", 3, 0, 1, 5409, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117601, 1, "具合が悪いの？\nならボクの美しい顔を\n処方してあげる♪", 4, 0, 1, 5410, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117601, 1, "さあ今日もボクと\n一緒に美の追求に\n付き合ってもらうよ♪", 101, 0, 4, 5411, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117601, 1, "かわいい天使のボクが\nいつもそばにいるんだ\nキミって幸せ者だね♪", 102, 0, 8, 5412, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117601, 1, "さすがボク\n目が覚めるような\n美しさでしょ？", 1, 1, 1, 5413, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117601, 1, "美は食で作られる\n美容にいいものを\n食べようよ！", 2, 2, 1, 5414, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117601, 1, "ふああ…早く寝よ？\n夜更かしは美容の\n大敵だからね", 3, 3, 1, 5415, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117601, 1, "ふう～…\nリラックスする\nボクも美しい", 2, 0, 1, 5417, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117601, 1, "ボクが抱きしめると\n普段よりかわいく\n見えちゃうね♪", 3, 0, 1, 5418, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117601, 1, "美しいボクは\n常にいい香りを\nしていなくちゃね", 4, 0, 1, 5419, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117601, 1, "光がボクを照らしてる\nんじゃなくて、ボクが\n光を輝かせてるのさ", 5, 0, 1, 5420, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117601, 1, "バランスのいい食事が\n美しいボクを\n作るのさ", 7, 0, 1, 5422, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117601, 1, "ふむふむ…なるほど\nこうすればより美しく\nなれるかもしれない", 8, 0, 1, 5423, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105701, 1, "仲間のためなら\nきっと…どこまでも\n強くなれるよね", 103, 0, 12, 5425, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105701, 1, "プリンセスフォーム？\n私と同じような力を\n持ってる人がいるの？\n会ってみたいな～！", 103, 0, 12, 5426, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118001, 1, "ここの空気は、\nどうもゆるすぎて…\n落ち着きませんわね", 1, 0, 1, 5427, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118001, 1, "陛下のお越しになる\n別宅としては、\n少し警備が\n甘すぎでは？", 2, 0, 1, 5428, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118001, 1, "あらあら？\nサッシにホコリが！\nちょっとネコ助！\n掃除は念入りに！", 3, 0, 1, 5429, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118001, 1, "いつまで入り浸って\nいるのかと、\n秘書に叱られて\nしまいましたわ…", 4, 0, 1, 5430, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118001, 1, "もういっそ、\nここに間借りして\n仕事しようかしら…？", 5, 0, 1, 5431, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118001, 1, "邪魔している身ですし\n家事くらい\nお手伝いさせなさいな", 101, 0, 4, 5432, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118001, 1, "本当は、意外と\n居心地が良いのかも…\n陛下の気持ち、\n少し分かりましたわ", 102, 0, 8, 5433, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118001, 1, "ちょっ…わたくしを\n叩いても、ホコリなど\nあまり出ませんわよ！", 1, 0, 1, 5434, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118001, 1, "陛下っ？\nなんだ…貴方でしたの\n用件は手短にお願いね", 2, 0, 1, 5435, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118001, 1, "別に、好き嫌いは\nありませんけれど…\n魔物料理って、意外と\n美味なものですわね", 3, 0, 1, 5436, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118001, 1, "言っておきますけれど\nわたくしは商売人です\n荒事は専門外ですわよ", 4, 0, 1, 5437, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118001, 1, "プリンセスナイトの\n能力…なにか儲け話に\n使えませんかしらね", 101, 0, 4, 5438, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118001, 1, "貴方がたのことも、\n少しくらいは\n信用してないことも\nありませんでしてよ", 102, 0, 8, 5439, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118001, 1, "おはようございます\nさ、今日もバリバリ\n働きますわよ！", 1, 1, 1, 5440, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118001, 1, "お昼ですわね\n栄養補給をしつつ\n働きますわよ！", 2, 2, 1, 5441, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118001, 1, "定時ですって？\n関係ありません！\nわたくし、まだまだ\n働きますわよ～っ！", 3, 3, 1, 5442, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118001, 1, "うっ…いけませんわ\n日頃の疲れが…\nふわぁ…", 2, 0, 1, 5444, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118001, 1, "もふもふ…ふふっ♪\n…なんですのっ、\nなにかおかしくて！？", 3, 0, 1, 5445, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118001, 1, "いい香りですわね\nわたくしの執務室にも\n置きたいくらいですわ", 4, 0, 1, 5446, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118001, 1, "いけませんわ…\nこんなに暖かいと\n日頃の疲れが…ふわぁ", 5, 0, 1, 5447, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118001, 1, "もう一つ食べたら\n仕事に戻りますわよ？\nあと一つだけ…", 7, 0, 1, 5449, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118001, 1, "商人にとって\n知識は武器ですわ\n陛下のため、貴方も\nもっと勉強なさい？", 8, 0, 1, 5450, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117701, 1, "故郷のお菓子を\nいろいろ作ったんだ～\nみんなに\n配ってくるさ～♪", 1, 0, 1, 5452, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117701, 1, "彼ってどんな仮装が\n似合うかな～？\nシーサーとか？", 2, 0, 1, 5453, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117701, 1, "カボチャチャンプルー\n作ってみよっと！\nきっとおいしいさ～♪", 3, 0, 1, 5454, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117701, 1, "視線を感じるさ～\nもしかして幽霊？\nおしゃべりするさ～♪", 4, 0, 1, 5455, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117701, 1, "さっき作った\nサーターアンダギー、\n早く彼に\n食べてほしいさ～♪", 5, 0, 1, 5456, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117701, 1, "彼のために、\n特別なイタズラを\n考えとくさ～♪", 101, 0, 4, 5457, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117701, 1, "いつか彼と、\n故郷のハロウィンを\n楽しみたいさ～♪", 102, 0, 8, 5458, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117701, 1, "ハロウィンは\n私たちが守るさ～♪\nさ、見回りに行こ！", 1, 0, 1, 5459, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117701, 1, "この制服、\nハロウィンらしくて\n大好きさ～♪", 2, 0, 1, 5460, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117701, 1, "イタズラする気ね～？\nどんなことされるのか\n楽しみさ～♪", 3, 0, 1, 5461, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117701, 1, "カボチャのランタン、\n一緒に作ろ！\n絶対楽しいよ～♪", 4, 0, 1, 5462, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117701, 1, "お菓子くれるの？\nあは～、ありがとー♪\nもぐもぐ…\n優しい味がするさ～♪", 101, 0, 4, 5463, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117701, 1, "キミと一緒に過ごす\nハロウィンは、\nドキドキとワクワクが\n止まらないさ～♪", 102, 0, 8, 5464, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117701, 1, "ハロウィンは\n朝から大忙しさ～！\nお菓子作り、\n手伝ってほしいさ～", 1, 1, 1, 5465, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117701, 1, "お化け屋敷に行こ！\n一緒にいっぱい\nドキドキしようね～♪", 2, 2, 1, 5466, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117701, 1, "ここからが\nハロウィン本番さ～！\n幽霊たちと\nいっぱい遊ぶさ～♪", 3, 3, 1, 5467, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117701, 1, "ちょっと休んだら、\nお菓子作りを\n再開するさ～♪", 2, 0, 1, 5469, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117701, 1, "でーじかわいいね～♪\nこの子に仮装させたら\nきっと似合うさ～♪", 3, 0, 1, 5470, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117701, 1, "お菓子みたいに\n甘い匂い…\nハロウィンらしい\nいい香りさ～♪", 4, 0, 1, 5471, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117701, 1, "ランドソルの秋って\n冷えるけど…\nこれがあれば\n体も心もぽかぽかさ～", 5, 0, 1, 5472, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117701, 1, "故郷のご飯も\nおいしいけど、\nこっちのご飯も\nでーじおいしいさ～♪", 7, 0, 1, 5474, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117701, 1, "パンプキンパイの\nレシピは…なるほど～\n勉強になるさ～♪", 8, 0, 1, 5475, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117701, 1, "全部おいしそうで\nどれから食べるか\n迷っちゃうさ～♪", 12, 0, 1, 5477, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117801, 1, "ムっ！　幽霊の気配を\n感じるデス！\nさあ姿を現すデス！", 1, 0, 1, 5479, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117801, 1, "ムムム…\n妖魔の力を忍法に\n使えないでショウか…", 2, 0, 1, 5480, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117801, 1, "怪しい妖魔を\n成敗するデス！\nハロウィンの平和は\nワタシが守りマスよ！", 3, 0, 1, 5481, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117801, 1, "ワタシの祭囃子が\n聞こえマスかー？\nさあ集まるデース！\n陽気に踊りまショウ！", 4, 0, 1, 5482, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117801, 1, "お祭りは\n楽しんだ者勝ちデス！\n踊らにゃ\nソンソンデス！", 5, 0, 1, 5483, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117801, 1, "幽霊になる前に\n天下統一を\n果たしたいデスね", 101, 0, 4, 5484, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117801, 1, "あの世に行っても\nショーグンは\nワタシの大事な\nショーグンデス！", 102, 0, 8, 5485, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117801, 1, "ショーグン！\n悪霊退治に\n出かけるデース！", 1, 0, 1, 5486, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117801, 1, "妖魔を家来に\nしてみるのは\nどうデス？", 2, 0, 1, 5487, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117801, 1, "マントを使って\nウツセミの術の精度を\n高めるデス！", 3, 0, 1, 5488, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117801, 1, "ハロウィンにちなんだ\n新しい忍法…\nすなわち妖忍法の\n開発を急ぐデス！", 4, 0, 1, 5489, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117801, 1, "あの世でも\nショーグンに\nお仕えしマスよー！", 101, 0, 4, 5490, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117801, 1, "ショーグンもワタシと\nハロウィンパレードを\n踊り明かしまセンか？", 102, 0, 8, 5491, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117801, 1, "おはようございマス！\n天下統一に向けて\n急いで準備するデス！", 1, 1, 1, 5492, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117801, 1, "お腹減ってまセンか？\nハロウィンスイーツで\n休憩するデ〜ス", 2, 2, 1, 5493, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117801, 1, "夜更かしすると幽霊が\nでるかもしれまセン…\nヒュ〜ドロロロ〜…", 3, 3, 1, 5494, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117801, 1, "もふもふなところが\nわたあめちゃんに\n似ていマス。わんっ！", 2, 0, 1, 5496, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117801, 1, "ぬいぐるみや人形には\n魂が宿るという\n言い伝えがありマス…\nマカフシギデ〜ス", 3, 0, 1, 5497, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117801, 1, "ウ〜ム…\nなんとも怪しい\n香りがしマスね！", 4, 0, 1, 5498, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117801, 1, "パレードの熱気に\n負けないくらい\n熱いデス！", 5, 0, 1, 5499, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117801, 1, "ついつい食べすぎて\nしまうのは\n妖魔のせいデスか！？", 7, 0, 1, 5501, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117801, 1, "トーゴク妖怪の\n図鑑デス！　個性的な\n妖怪ばかりデスね！", 8, 0, 1, 5502, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117801, 1, "ハロウィンの甘味を\n集めまシタよ！\nショーグンへの\n貢ぎ物デス！", 12, 0, 1, 5504, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101301, 1, "彼と二人で\n買い物に…って…\nこれってデートに\nなっちゃうのかな！？", 103, 0, 12, 5531, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101301, 1, "とうっ！　どうどう？\n今の決めポーズ！\nかっこいいよね♪", 103, 0, 12, 5532, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117901, 1, "ヒデサイとおそろの\nハロウィンコスとか\nしてみたいな～", 1, 0, 1, 5533, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117901, 1, "カボチャ料理かぁ…\n作ったらヒデサイ\n食べてくれるかな～", 2, 0, 1, 5534, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117901, 1, "ハロウィンスイーツ…\nどれもおいしすぎて\n激ヤバなんですけど！", 3, 0, 1, 5535, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117901, 1, "霊界ってどんなとこ\nなんだろ～\nわりと近所だったり？", 4, 0, 1, 5536, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117901, 1, "服をハロウィンっぽく\nアレンジするのも\nたのしーよね♪", 5, 0, 1, 5537, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117901, 1, "ヒデサイと一緒に\nハロウィンパーティー\n…えへへ、楽しみ～", 101, 0, 4, 5538, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 117901, 1, "ヒデサイといると\nドキドキする…\nなんでだろ～？", 102, 0, 8, 5539, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117901, 1, "ねねね、うちらも\nハロウィンパレード\n見に行こー？", 1, 0, 1, 5540, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117901, 1, "ハロウィンセイバーズ\nしゅつどーう！\nヒデサイも行こ～♪", 2, 0, 1, 5541, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117901, 1, "マントのロゴ\nかっこいいっしょ～？\nカボきちってゆーんだ", 3, 0, 1, 5542, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117901, 1, "トリックオア\nトリート！\nねえねえヒデサイ\nお菓子ちょうだい♪", 4, 0, 1, 5543, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117901, 1, "ヒデサイも\nメイクしてみる？\nハロウィンぽいやつ♪", 101, 0, 4, 5544, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117901, 1, "ヒデサイのこと\nうちがいつでも\nあっためたげるね！\n…あ！　手、手の話！", 102, 0, 8, 5545, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117901, 1, "ハロウィンのイベント\n朝からでも\nやってるかな～？", 1, 1, 1, 5546, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117901, 1, "カボチャでおやつ\n作ってみたんだ～\nヒデサイ食べてみて！", 2, 2, 1, 5547, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 117901, 1, "ハロウィンの夜って\nやっぱテンション\n上がるよね～♪", 3, 3, 1, 5548, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117901, 1, "超ふかふかしてる～\nちょっと休憩させて\nもらっちゃお", 2, 0, 1, 5550, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117901, 1, "はぁ…\nもにカワすぎて\nつらいんですけど～！", 3, 0, 1, 5551, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117901, 1, "ハロウィン限定の\nフレグランスじゃん！\nいい匂いする～♪", 4, 0, 1, 5552, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117901, 1, "やばばば…\nあったかくて\n眠くなっちゃう～", 5, 0, 1, 5553, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117901, 1, "これ食べて\nもうひとがんばり\nしよーっと！", 7, 0, 1, 5555, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117901, 1, "ハロウィンっぽい\nファッションとか\n載ってないかなぁ？", 8, 0, 1, 5556, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 117901, 1, "お菓子いっぱいだ～！\nあとでヒデサイにも\n持ってってあげよ～", 12, 0, 1, 5558, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118501, 1, "お掃除が終わったら\n次は書類を片付けて…\nその次は…", 1, 0, 1, 5560, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118501, 1, "皆さんが冒険している\n間の留守は私に\nお任せください♪", 2, 0, 1, 5561, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118501, 1, "美食殿の皆さんが\n持ってきてくれる\nお土産が楽しみです♪", 3, 0, 1, 5562, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118501, 1, "ネビアさんとは\nときどき一緒にお茶\nする仲なんです", 4, 0, 1, 5563, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118501, 1, "ギルドハウスの乱れは\nギルドの乱れ\n整理整頓が大切です", 5, 0, 1, 5564, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118501, 1, "素敵なギルドハウスの\n管理人ができて\nとっても幸せです♪", 101, 0, 4, 5565, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118501, 1, "皆さんをほんの少し\n特別扱いしちゃう\n私がいます", 102, 0, 8, 5566, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118501, 1, "何かわからないことが\nあるときは私に\n聞いてくださいね", 1, 0, 1, 5567, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118501, 1, "書類を書いていると\n落ち着きます…\n一緒にどうですか？", 2, 0, 1, 5568, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118501, 1, "あなたのまわりは\nいつも賑やかで\n楽しいですね♪", 3, 0, 1, 5569, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118501, 1, "あっ！　お頼みしたい\nことがあって\n探してたんです♪", 4, 0, 1, 5570, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118501, 1, "ギブアンドテイクは\n大切です。たまには\nお礼させてください♪", 101, 0, 4, 5571, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118501, 1, "陰に日向にあなたを\n支えていきます♪\n期待していてください", 102, 0, 8, 5572, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118501, 1, "今日も一日\n管理人として\nがんばります♪", 1, 1, 1, 5573, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118501, 1, "お腹がすきました…\nそろそろお昼に\nしませんか？", 2, 2, 1, 5574, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118501, 1, "もうこんな時間…\n皆さんといると一日が\nあっという間です", 3, 3, 1, 5575, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118501, 1, "良質なお仕事は\n良質な休憩から\nです♪", 2, 0, 1, 5577, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118501, 1, "かわいいものに\n囲まれてお仕事できる\nのは幸せですね♪", 3, 0, 1, 5578, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118501, 1, "お仕事に集中できる\nアロマを設置するのも\nよさそうですね♪", 4, 0, 1, 5579, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118501, 1, "ポカポカ温まりながら\n書類仕事をするのも\nいいものですね…", 5, 0, 1, 5580, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118501, 1, "栄養補給しないと\n頭が回りませんからね\nきちんと食べましょう", 7, 0, 1, 5582, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118501, 1, "新条例はこのように\n活用されている、と…\nふむふむ…", 8, 0, 1, 5583, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106701, 1, "会合用の資料と\n連載のネーム…\nどっちを片付けよう？", 1, 0, 1, 5585, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106701, 1, "イノリちゃん、また\nお金くすねてる…\n帰ってきたら\nお尻ペンペンだ♪", 2, 0, 1, 5586, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106701, 1, "カヤちゃんの装備\n傷むの早いなぁ〜\nメンテしてあげないと", 3, 0, 1, 5587, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106701, 1, "そうだ、ラーゴのご飯\n買いに行かないと\n今日は何がいいかな？", 4, 0, 1, 5588, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106701, 1, "ここの人たちは見てて\n飽きない人ばっかり♪\n漫画の参考にしよ〜", 5, 0, 1, 5589, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106701, 1, "また一緒に買い物にも\n行きたいし、冒険も\nしたいな〜…♪", 101, 0, 4, 5590, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106701, 1, "…君は見てるだけで\n満足なの？ ちゃんと\n触って欲しいな…♪", 102, 0, 8, 5591, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106701, 1, "私の目が見たいの？\nいいけど…魅了されて\n魂が壊れちゃうよ…？\n…冗談で〜す☆", 1, 0, 1, 5592, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106701, 1, "ラーゴがね、君に\n遊んで欲しいって☆\n仲良くしてあげてね", 2, 0, 1, 5593, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106701, 1, "アシスタントの仕事\n気に入ってくれたなら\nまたお願いしていい？", 3, 0, 1, 5594, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106701, 1, "私が覚醒できるか？\n見せてあげたいけど…\nすごいことになるから\nまた今度で♪", 4, 0, 1, 5595, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106701, 1, "楽しいトラブルが\nあったら、とりあえず\n声はかけて欲しいな♪", 101, 0, 4, 5596, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106701, 1, "マッサージお願い♪\n変なところは…\n少しだけなら☆", 102, 0, 8, 5597, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106701, 1, "おはよう〜♪\n今日も一日、一緒に\nがんばっていこうね", 1, 1, 1, 5598, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106701, 1, "お腹空いちゃった…\n一緒にお昼行かない？\nいいお店があるんだ〜", 2, 2, 1, 5599, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106701, 1, "君、眠れないの？\n誰かに見られてる？\n…きっと気のせいだよ", 3, 3, 1, 5600, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106701, 1, "ちょっと休憩〜…\nこのまま原稿すれば\n疲れなくなるのかな？", 2, 0, 1, 5602, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106701, 1, "かわいい〜♪\nラーゴのお友達に\nなってくれる？", 3, 0, 1, 5603, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106701, 1, "いい香り〜♪\nこの目でどう見えるか\n伝えられたらいいのに", 4, 0, 1, 5604, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106701, 1, "ほどほどなのが\nちょうどいい時も\nあるよね…♪", 5, 0, 1, 5605, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106701, 1, "この味付け…\nあっちでも再現\nできるかな？", 7, 0, 1, 5607, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106701, 1, "ふふっ、面白い♪\nイノリちゃんの買う\n漫画にハズレなし☆", 8, 0, 1, 5608, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 119001, 1, "私…23歳だもん\n魔法少女だなんて\n名乗れないわ…", 1, 0, 1, 5610, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 119001, 1, "あん、どぅ、とろわ〜\nみんなイイ子に\nな〜る♪", 2, 0, 1, 5611, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 119001, 1, "あっ！　イケナイ子の\n気配がするわ！\n風紀の乱れは\n先生が許しませんっ！", 3, 0, 1, 5612, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 119001, 1, "ここで闇の力が\n発動したら大変だわ！\nやだっ…そう思ったら\n緊張して…ダメぇ〜！", 4, 0, 1, 5613, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 119001, 1, "オイタした子はみんな\nどうしてうれしそうに\n私の愛のムチを\n欲しがるのかしら…？", 5, 0, 1, 5614, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 119001, 1, "あの子に次は何を\n教えてあげようかしら\n愛？　希望？\nそれとも…♪", 101, 0, 4, 5615, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 119001, 1, "あの子のことを\n考えると、愛と希望で\n胸がいっぱいになるの", 102, 0, 8, 5616, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119001, 1, "先生なのに闇の力を\n持ってるのは秘密よ？\n他の子には\n知られたくないから…", 1, 0, 1, 5617, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119001, 1, "秘密の授業の内容？\nそれは…あんなことや\nこんなことよ♪\nそれから、それから…", 2, 0, 1, 5618, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119001, 1, "しーっ！　私の正体が\nルーセント学院の\n先生だって、ここでは\n秘密にしててね？", 3, 0, 1, 5619, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119001, 1, "あら？　補習して\nほしいの？　いいわよ\n君のお願いなら\nなんでも聞いちゃう♪", 4, 0, 1, 5620, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119001, 1, "イイ子にはごほうびを\nあげなくちゃ！\n君はなにが欲しい？\n遠慮しちゃダメよ？", 101, 0, 4, 5621, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119001, 1, "君との秘密が\nまた一つ\n増えちゃったね♪", 102, 0, 8, 5622, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119001, 1, "今日も一日\n元気に過ごしましょう\n先生が指導してあげる", 1, 1, 1, 5623, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119001, 1, "夜まで頑張れるように\nお昼ご飯はしっかり\n食べましょう〜！", 2, 2, 1, 5624, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119001, 1, "夜更かしはダメよ？\nイケナイ子が\nすることです！", 3, 3, 1, 5625, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 119001, 1, "闇の力に振り回されて\nもうヘトヘトよ〜\n心も体も\n癒されたいわ〜", 2, 0, 1, 5627, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 119001, 1, "『秘密の学び舎』に\nこっそり連れて\n行っちゃおうかしら♪", 3, 0, 1, 5628, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 119001, 1, "イケナイ甘い香り…\nなんだか胸の奥が\n熱くなってきたわ…♪", 4, 0, 1, 5629, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 119001, 1, "やんっ♪\n燃え上がる愛みたい♪\n火照ってきちゃった♪", 5, 0, 1, 5630, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 119001, 1, "生徒の見本に\nなれるように\n好き嫌いはしないの", 7, 0, 1, 5632, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 119001, 1, "『学び舎に咲く禁断の\n愛』？　気になるわ…\nううん、ダメよっ！\nでも…ちょっとだけ…", 8, 0, 1, 5633, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106301, 1, "次に会う時は、\nまたあそこに一緒に…\nえへへ…！", 103, 0, 12, 5635, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106301, 1, "横顔見てるの、\nバレてないよね…？\n…ちらっ", 103, 0, 12, 5636, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105301, 1, "背が少し伸びたか！？\nあ、あいつのところへ\n報告に行かねばっ！", 103, 0, 12, 5637, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105301, 1, "私も貴公の隣に\n立っていられるよう\n日々精進していくぞ！", 103, 0, 12, 5638, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 119101, 1, "ああ～\nドＭミストが\nあふれてしまいます～", 1, 0, 1, 5639, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 119101, 1, "魔法少女として\nクウカ頑張ります～！\n愛と希望の妄想を…！", 2, 0, 1, 5640, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 119101, 1, "決めポーズの練習より\nやられポーズの練習を\nしておきます…♪", 3, 0, 1, 5641, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 119101, 1, "快感を溜めに溜めて\n放つドＭビームは\n最高ですよ～", 4, 0, 1, 5642, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 119101, 1, "はっ！\nこれは闇の気配！\n…クウカ自身でした", 5, 0, 1, 5643, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 119101, 1, "ドＳさん…ぐふふ…\nクウカを縛りあげ…\nじゅるりぃ～♪", 101, 0, 4, 5644, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 119101, 1, "ドＳさんに攻められる\nことで、クウカは進化\nしてゆくのです～！", 102, 0, 8, 5645, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119101, 1, "はぅっ！\nもっと激しく…！\n情熱的に～～～！", 1, 0, 1, 5646, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119101, 1, "クウカの変身シーンを\nじっくり眺めたい！？\nは、恥ずかしいです～", 2, 0, 1, 5647, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119101, 1, "「本当に魔法少女か\n確かめてやるよ…！」\nドＳさんはそう言って\nクウカを…ぐふふ…♪", 3, 0, 1, 5648, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119101, 1, "愛と希望の力が\nクウカの中に…\nこの胸の高鳴りが…？", 4, 0, 1, 5649, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119101, 1, "ひゃあんっ！\nそこです～っ！\nもっと、もっと～♪", 101, 0, 4, 5650, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119101, 1, "クウカをこんなに\n悦ばせるのは\nドＳさんだけです…♪", 102, 0, 8, 5651, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119101, 1, "明るい時間に出歩くと\n目立ちますね\n魔法少女の衣装…", 1, 1, 1, 5652, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119101, 1, "お昼の見回りです～♪\nああ～うっかりドＭを\n増やしてしまいました", 2, 2, 1, 5653, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119101, 1, "夜！　それはクウカが\n活性化する時間です！\n闇の活動開始です～♪", 3, 3, 1, 5654, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 119101, 1, "気の緩んだクウカを\nドＳさんの鋭い牙が\n狙っています～♪", 2, 0, 1, 5656, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 119101, 1, "魔法少女のマスコット\nにいいかもしれません\nもっと鎖とか飾りを…", 3, 0, 1, 5657, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 119101, 1, "魅惑的な香りです～\nドＭミストの香りに\n似て…いや違いますね", 4, 0, 1, 5658, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 119101, 1, "火照る体…紅潮した\nクウカの肢体を熱く\n見つめるドＳさん…", 5, 0, 1, 5659, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 119101, 1, "食欲を満たした次は\n何を満たせば\nいいですかねぇ～♪", 7, 0, 1, 5661, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 119101, 1, "魔法少女がひどい目に\n遭う物語は…\nなさそうですね…", 8, 0, 1, 5662, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 119901, 1, "ケーキも悪くないけど\nプリンが一番なの\nでもせっかくだから\n両方もらってやるの！", 1, 0, 1, 5664, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 119901, 1, "クリスマスって聞くと\nなんだかあったかい\n気分になるの～", 2, 0, 1, 5665, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 119901, 1, "煙突がなくても\nミヤコはどこからでも\nおうちに入れるの♪", 3, 0, 1, 5666, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 119901, 1, "イブもプリン！\nクリスマスもプリン！\nプレゼントもプリン！", 4, 0, 1, 5667, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 119901, 1, "クリスマスツリーの\n高いところの\n飾りつけは、ミヤコに\nまかせろなの～！", 5, 0, 1, 5668, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 119901, 1, "アイツのプレゼント\n楽しみなの～\nきっと最っ高に特別な\nプリンなの～♪", 101, 0, 4, 5669, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 119901, 1, "ミヤコがサンタなら\nアイツはトナカイなの\nつまりお似合いって\nことなの～♪", 102, 0, 8, 5670, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119901, 1, "ミヤコ、プレゼントを\n配りにいくの～♪\nオマエも手伝えなの！", 1, 0, 1, 5671, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119901, 1, "オマエにも飾りつけ\nしてやるの！\n頭にプリンを乗せると\nかわいいの♪", 2, 0, 1, 5672, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119901, 1, "幽霊のミヤコが\nいるなら、サンタも\nいて当たり前なの～！", 3, 0, 1, 5673, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119901, 1, "靴下にプリンを\n入れたらつぶれるの\nだからプリンは直接\nミヤコによこすの～！", 4, 0, 1, 5674, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119901, 1, "今日はプリンだけじゃ\n満足してやらないの！\nミヤコをたくさん\n甘やかせなの！", 101, 0, 4, 5675, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119901, 1, "ミヤコのプリン\nオマエになら一個…\nうう～、一口だけ\nあげてもいいの！", 102, 0, 8, 5676, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119901, 1, "サンタの朝は早いの～\nミヤコも早起きして\nプレゼント準備したの\nとってもえらいの♪", 1, 1, 1, 5677, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119901, 1, "今日は一日中\nみんなと遊ぶの～！\nオマエも来いなの！", 2, 2, 1, 5678, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119901, 1, "みんなが寝たら\nチャンスなの～\nこっそりプレゼント\n配りにいくの♪", 3, 3, 1, 5679, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 119901, 1, "むふー、気持ちいいの\nこういうプレゼントも\nいいかもしれないの", 2, 0, 1, 5681, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 119901, 1, "これ気に入ったの～\nプリンとアイツの\n次くらいなの♪", 3, 0, 1, 5682, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 119901, 1, "いいにおいなの～\nうっかり成仏\nしちゃいそうなの…", 4, 0, 1, 5683, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 119901, 1, "ここに飾っといた\n雪だるま\nなくなっちゃったの\nきっと成仏したの～", 5, 0, 1, 5684, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 119901, 1, "これはオマエがくれる\nプリンとは\n別腹なの～", 7, 0, 1, 5686, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 119901, 1, "プリンケーキ…\nこれならミヤコにも\n作れそうなの！\nさっそく作るの！", 8, 0, 1, 5687, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 119901, 1, "びっくりしたの～！\n幽霊のミヤコを\n驚かすなんて…\nなかなかやるの！", 14, 0, 1, 5689, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 119901, 1, "コイツの中…\nあったかいの～♪\n昼寝にぴったりなの～", 15, 0, 1, 5690, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 119201, 1, "メリークリスマス！\n聖夜に笑顔を\nお届けするべ♪", 1, 0, 1, 5691, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 119201, 1, "クリスマス前は\n乳製品がよく売れて\nありがてぇけんど…\n目が回る忙しさだ～", 2, 0, 1, 5692, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 119201, 1, "牧場で売る\nクリスマスリースを\n作っておかねぇと…", 3, 0, 1, 5693, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 119201, 1, "うちの動物たちにも\nクリスマスプレゼント\n用意しなきゃなぁ", 4, 0, 1, 5694, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 119201, 1, "クリスマスに向けた\n限定商品とか\n作ってみるべか？", 5, 0, 1, 5695, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 119201, 1, "あんちゃんへの\nプレゼント選び…\nここでボケるか…？\nやめておくべか…？", 101, 0, 4, 5696, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 119201, 1, "オラ、あんちゃんから\n本当にたくさんの物を\nもらってんなぁ…", 102, 0, 8, 5697, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119201, 1, "雪遊びがしたいべか？\nへへっ、あんちゃん\nには負けねぇど！", 1, 0, 1, 5698, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119201, 1, "ディナーと一緒に\n聖夜のお笑いライブを\nすんのはどうだべか？\nツッコミは任せたべ！", 2, 0, 1, 5699, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119201, 1, "寒くなってくると\n鍋が食べたくなるべや\nあんちゃんはどうだ？", 3, 0, 1, 5700, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119201, 1, "あんちゃんは\nサンタさんのこと\n信じてるだか？", 4, 0, 1, 5701, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119201, 1, "どでかいクリスマス\nケーキをこさえるべ\nあんちゃんも作るの\n手伝ってくんろ♪", 101, 0, 4, 5702, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119201, 1, "あんちゃんが\nオラの旦那だったら…\nって、あんちゃん！\n今の聞いてただか！？", 102, 0, 8, 5703, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119201, 1, "牧場の朝は早いど！\nさぁてと、まずは\n雪かきしに行くべや", 1, 1, 1, 5704, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119201, 1, "あんちゃんも一緒に\nお昼食べてくか？\n採れたての冬野菜は\nうんまいど～♪", 2, 2, 1, 5705, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119201, 1, "よい子は早く寝ねぇと\nサンタさんが\n来てくれねぇべよ？", 3, 3, 1, 5706, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 119201, 1, "こういうのリンリンが\n好きそうだな…\nプレゼント候補に\n入れておくべ！", 2, 0, 1, 5708, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 119201, 1, "もふもふしてて\nなまらめんこくて…\nあぁ…癒されるべや…", 3, 0, 1, 5709, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 119201, 1, "この匂い…\n気持ちが和むべや\n夢心地になっちまうな", 4, 0, 1, 5710, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 119201, 1, "あったけぇところは\n落ち着くなぁ…\nつい眠くなっちまう", 5, 0, 1, 5711, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 119201, 1, "こ、この味は…！\n…うーん、もっと\nリアクション芸も\n練習しねぇとな", 7, 0, 1, 5713, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 119201, 1, "シオシオって\nどんな本読んでんだ？\n…難しい字が\nぎっしりだべや…", 8, 0, 1, 5714, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 119201, 1, "この仕掛け…\nオラのコントに\n活かせるかも\nしんねぇな", 14, 0, 1, 5716, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 119201, 1, "この着ぐるみで\nリマリマのモノマネを\n練習してみるべか…？", 15, 0, 1, 5717, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105001, 1, "もっとキレイになって\nアイツにぎゃふんと\n言わせてやるんだから", 103, 0, 12, 5718, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105001, 1, "アタシのこと\nどう思う？\nかわいいって…そこは\nキレイって言ってよね", 103, 0, 12, 5719, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 119301, 1, "ハートマークの刺繍…\nは、やりすぎですかね\nいやちょっとくらい\nアピールしてみても…", 1, 0, 1, 5720, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 119301, 1, "雪降らないかな～\nそしたらお兄ちゃんと\n雪だるま作ったり\nできるのに～", 2, 0, 1, 5721, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 119301, 1, "プレゼントを入れる\n靴下…いくつ編んでも\n足りません～！", 3, 0, 1, 5722, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 119301, 1, "お兄ちゃんとの\nクリスマスデートは\n早い者勝ちですからね\n負けませんよ～！", 4, 0, 1, 5723, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 119301, 1, "クリスマスだし…\n受験勉強は今日だけ\nお休みです♪", 5, 0, 1, 5724, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 119301, 1, "お兄ちゃんと\nおそろいのコーデで\nクリスマスデート…\nしたいなぁ～！", 101, 0, 4, 5725, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 119301, 1, "お兄ちゃんに\nプレゼントする編み物\nこれで３着目～と♪\nあれ、作りすぎ？", 102, 0, 8, 5726, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119301, 1, "えへへ、もっと近くで\n見たくありませんか？\n私のとっておきの\nクリスマスコーデ♪", 1, 0, 1, 5727, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119301, 1, "お兄ちゃんが風邪を\n引いたら一大事です！\n私の編み物で暖かく\nしてあげます♪", 2, 0, 1, 5728, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119301, 1, "お兄ちゃん、ほっぺに\nケーキのクリームが\nついちゃってますよ♪\n私がとってあげます！", 3, 0, 1, 5729, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119301, 1, "お兄ちゃん！\nケーキ、あーんって\nしてあげます！\nほら、あーん♪", 4, 0, 1, 5730, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119301, 1, "クリスマスは大切な\n人と過ごすものっ！\nお兄ちゃん…妹の予定\n空いてますよっ！", 101, 0, 4, 5731, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119301, 1, "クリスマスはずっと、\nお兄ちゃんと一緒に\nいたい…って言ったら\n欲張り…すぎですか？", 102, 0, 8, 5732, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119301, 1, "早起きは３本お得！\n…クリスマスなら\n追加で10本ぐらい\nもらえちゃうかも！？", 1, 1, 1, 5733, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119301, 1, "お昼はケーキ？\nそれとも七面鳥？\nごちそういっぱいで\n迷っちゃいますね～♪", 2, 2, 1, 5734, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 119301, 1, "お兄ちゃんと\nクリスマスに夜更かし\n…えへへ、ちょっぴり\nドキドキしちゃう♪", 3, 3, 1, 5735, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 119301, 1, "編み物をするには\n絶好のくつろぎ空間\nですね～♪", 2, 0, 1, 5737, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 119301, 1, "なんというかわいさ…\nクリスマス\nプレゼントに\nもってこいですね！", 3, 0, 1, 5738, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 119301, 1, "これがお兄ちゃんの\nお部屋の香り…？\nんん～！　ずっと\nかいでいたいです～♪", 4, 0, 1, 5739, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 119301, 1, "ふぁ、あったか～い…\n冬場はこういうのが\nありがたいですね～♪", 5, 0, 1, 5740, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 119301, 1, "ごちそうだぁ～♪\nほら、お兄ちゃんも\n食べて食べてっ！", 7, 0, 1, 5742, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 119301, 1, "クリスマスデートの\n必勝法…こ、これは\n読むしかないです！", 8, 0, 1, 5743, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 119301, 1, "わっ、すごいです～！\nこれを飾っておけば\nクリスマスが一層\n賑やかになりますね♪", 14, 0, 1, 5745, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 119301, 1, "さぷらーいずっ！\nリマちゃんじゃなくて\nリノちゃんでした～！", 15, 0, 1, 5746, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120601, 1, "ミミにもミソギにも\nあの人にも頼りに\nしてもらえるように\nしっかりしなきゃ！", 1, 0, 1, 5747, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120601, 1, "ミミはにんじんを\n食べられてすごいな…\n私も見習わなきゃ\nがんばろう…！", 2, 0, 1, 5748, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120601, 1, "ミソギから怖い話\n聞いちゃった…\nうぅ…誰か一緒に\nいてくれないかな", 3, 0, 1, 5749, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120601, 1, "立派な大人に\nなれるように\nいっぱい勉強\nしておかなくっちゃ！", 4, 0, 1, 5750, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120601, 1, "なんだか…あの人の\nあやしい視線を\n感じるような…？", 5, 0, 1, 5751, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120601, 1, "あの人に勉強を\n教えられるように\nしっかり予習を\nしておこうっと…！", 101, 0, 4, 5752, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120601, 1, "今日はあの人と\n一緒に冒険できる…\nなんだかワクワク\nしてきちゃった…！", 102, 0, 8, 5753, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120601, 1, "あ、いいとこに来て\nくれましたね！\nあなたに頼みたい\nことがあるんです！", 1, 0, 1, 5754, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120601, 1, "きゃあっ！？\nへ、変なところを\n触らないでください！", 2, 0, 1, 5755, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120601, 1, "どうですかこの格好？\nかわいい、ですか…？\nそ、そうですか…\nありがとうございます", 3, 0, 1, 5756, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120601, 1, "ひゃあっ…！\nすみません、勉強に\n集中してたので\n驚いちゃいました", 4, 0, 1, 5757, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120601, 1, "一緒に勉強して立派な\n大人になりましょうね\n私がそばで見張ってて\nあげます", 101, 0, 4, 5758, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120601, 1, "まだ立派ではないかも\nですけど、私よりも\n大人なあなたのことは\n頼りにしてますからね", 102, 0, 8, 5759, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120601, 1, "立派な大人は\n朝の時間の使い方が\n上手だと聞きました\nあなたはどうですか？", 1, 1, 1, 5760, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120601, 1, "ミミとミソギと\nあなたと…大事な人と\n一緒のお昼ごはんは\nやさしい味がしますね", 2, 2, 1, 5761, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120601, 1, "うぅ…怖い話を\n思い出しちゃいました\n寝るまで一緒に\nいてくれますか…？", 3, 3, 1, 5762, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120601, 1, "すごい座り心地…！\nとっても勉強が\nはかどりそう…！", 2, 0, 1, 5764, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120601, 1, "この子…なんだか\nミミに似てるような\n気がする…ふふっ♪", 3, 0, 1, 5765, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120601, 1, "いい香り…♪\n勉強で疲れた\n頭がリフレッシュ\nできそうです", 4, 0, 1, 5766, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120601, 1, "ぽかぽかだ…♪\nミミとミソギと\nあの人と一緒に\nいるときみたい", 5, 0, 1, 5767, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120601, 1, "いっぱい食べたら\n早く大人になれたり\nしないかな…？", 7, 0, 1, 5769, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120601, 1, "あう…\n知らない単語が…\nちゃんと調べて\n覚えなくっちゃ…！", 8, 0, 1, 5770, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120401, 1, "うさぎさんが\nぴょ～んぴょん♪\nみんなえがおで\nるんたった～♪", 1, 0, 1, 5773, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120401, 1, "キョウカちゃんも\nいつかにんじんさんを\n好きになってくれたら\nうれしいな～♪", 2, 0, 1, 5774, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120401, 1, "今日のミソギちゃんの\nイタズラ、びっくり\nしちゃったけど\nおもしろかったな～♪", 3, 0, 1, 5775, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120401, 1, "このおうち、いつも\nいろんな人がいて\nドキドキぴょんぴょん\nって楽しいな～♪", 4, 0, 1, 5776, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120401, 1, "いいお天気～♪\nお外でみんなと\nいっぱい遊びたいな♪", 5, 0, 1, 5777, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120401, 1, "ミソギちゃんも\nキョウカちゃんも\nおにいちゃんがいると\nずっと笑顔だね～♪", 101, 0, 4, 5778, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120401, 1, "おにいちゃんにはやく\nあいたいな～♪\nまだかな、まだかな♪", 102, 0, 8, 5779, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120401, 1, "えへへ♪\nおにいちゃんに\nなでなでされると\nぽかぽかするね♪", 1, 0, 1, 5780, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120401, 1, "聞いて、おにいちゃん\nさっきね、かわいい\nちょうちょさんに\n会ったんだよ～♪", 2, 0, 1, 5781, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120401, 1, "ふわっ…！\nおにいちゃんの顔を\nお絵かきしてたから\nびっくりしちゃった♪", 3, 0, 1, 5782, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120401, 1, "おにいちゃんも\n遊びたいの？\nそれじゃあ二人も\n呼んで遊ぼう♪", 4, 0, 1, 5783, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120401, 1, "おにいちゃんも\nみんなも、優しい人\nばっかりで\nミミ幸せ～♪", 101, 0, 4, 5784, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120401, 1, "おにいちゃん♪\n高い高いしてほしいな\nすっごくぴょんぴょん\nできて、好きなんだ♪", 102, 0, 8, 5785, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120401, 1, "おはよう～♪\nおにいちゃん、今日は\nおひさまみたいに\nきらきら元気だね～♪", 1, 1, 1, 5786, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120401, 1, "おひるごはんだね～♪\nにんじんさんを\nいっぱい食べて\n元気いっぱい♪", 2, 2, 1, 5787, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120401, 1, "ふわあ…\n眠くなってきちゃった\nおにいちゃん、\n一緒に寝よう～？", 3, 3, 1, 5788, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120401, 1, "ふわぁ、気持ちいい♪\nずっとこうやって\n座ってたくなるね～♪", 2, 0, 1, 5790, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120401, 1, "わあ、かわいい～\nぎゅ～っ、ぎゅ～っ♪\nこれで仲よしだね\nえへへ♪", 3, 0, 1, 5791, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120401, 1, "気持ちいいにおい～♪\nぴょんぴょんしたく\nなってきちゃった♪", 4, 0, 1, 5792, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120401, 1, "あったかぽかぽか♪\nおにいちゃんも\nきてくれたら、もっと\nあったかくなりそう♪", 5, 0, 1, 5793, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120401, 1, "もぐもぐ…ごっくん♪\nおっきくなりたいから\nいっぱい食べるよ～♪", 7, 0, 1, 5795, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120401, 1, "えへへ、おもしろ～い\nこの絵本ね、\nキョウカちゃんから\n教えてもらったんだ♪", 8, 0, 1, 5796, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120501, 1, "この部屋…探索の\nしがいがありそう！\nついでにイタズラも\nしかけちゃお♪", 1, 0, 1, 5799, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120501, 1, "ミミってああ見えて\n実は大胆で度胸も\nあるよね…ちょっと\nかっこいいな…！", 2, 0, 1, 5800, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120501, 1, "怖い話を聞いた時の\nキョウカって\n面白いんだよね～♪", 3, 0, 1, 5801, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120501, 1, "にいちゃんが\n慣れちゃわないように\nまた新しいイタズラを\n考えなきゃ！", 4, 0, 1, 5802, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120501, 1, "イタズラもいいけど\nミミとキョウカと\nにいちゃんと、普通に\n遊ぶのも楽しいなぁ♪", 5, 0, 1, 5803, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120501, 1, "ミミにキョウカ、\nそれににいちゃん！\nみんなそろえば愉快な\n冒険が始まりそう！", 101, 0, 4, 5804, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120501, 1, "次はどんなイタズラを\nしたら、にいちゃんは\n喜んでくれるかな？", 102, 0, 8, 5805, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120501, 1, "今ミソギに触ったね？\nヤバいよにいちゃん…\n逃げないとトラップが\n発動しちゃうよ…！", 1, 0, 1, 5806, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120501, 1, "わわっ！？　びっくり\nしたあ…！　新しい\nトラップを作ってて\n気づかなかったよ～", 2, 0, 1, 5807, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120501, 1, "お？　にいちゃん、\nイタズラする側にも\n興味があるの？", 3, 0, 1, 5808, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120501, 1, "にいちゃんなにか用？\nあ、もしかして…\nミソギと遊びたく\nなっちゃった？", 4, 0, 1, 5809, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120501, 1, "ベストタイミングだよ\nにいちゃん！\n今からみんなで探検に\n行くところなんだ！", 101, 0, 4, 5810, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120501, 1, "にいちゃんってさ\nホントに優しいよね\nなにしても怒らないし\n…ありがとね♪", 102, 0, 8, 5811, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120501, 1, "にいちゃんおはよー！\n景気づけに本日\n一発目のイタズラ、\nやっちゃうよ～♪", 1, 1, 1, 5812, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120501, 1, "お昼ごはんはロシアン\nサンドイッチだよ！\nさあ、にいちゃん！\n好きなの選んでよ！", 2, 2, 1, 5813, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120501, 1, "夜ってなんだか\nワクワクしてなかなか\n眠れないんだよね\nにいちゃんはどう？", 3, 3, 1, 5814, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120501, 1, "この座り心地のよさ…\nなにかのトラップに\nうまく使えないかな？", 2, 0, 1, 5816, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120501, 1, "こういうのに細工して\nミミやキョウカを\n驚かせると、いい反応\nするんだよね♪", 3, 0, 1, 5817, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120501, 1, "においをうまく使った\nイタズラって…\nなにかないかな？", 4, 0, 1, 5818, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120501, 1, "あったまりながら\n新しいイタズラを\n考えると、いい案が\n出て来たりするんだ♪", 5, 0, 1, 5819, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120501, 1, "おいしくてついつい\n食べ過ぎちゃうなぁ…\nはっ…！？　これって\nまさかトラップ…！？", 7, 0, 1, 5821, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120501, 1, "このキョウカから\n借りた冒険物語\nすっごく\nおもしろいんだよね！", 8, 0, 1, 5822, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120801, 1, "晴れ着を着ると\nなんだか心が躍るね\n足取りも軽く\nなるってもんさ", 1, 0, 1, 5825, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120801, 1, "正月の食べ物ってのは\n美味いもんだね\nいくらでも\n食べれちまうよ", 2, 0, 1, 5826, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120801, 1, "普段は好き勝手してる\nアタシらも\n正月くらいは\n大人しくするさ", 3, 0, 1, 5827, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120801, 1, "年越しそばに雑煮\nこの季節は\n温かいものが\n体に沁みるね", 4, 0, 1, 5828, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120801, 1, "正月だからって\nのんびりしすぎたね\nちょっと釣りにでも\n行こうかね", 5, 0, 1, 5829, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120801, 1, "いい正月だね\nいつかあの子と\n一杯やりたいもんだよ", 101, 0, 4, 5830, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120801, 1, "大事な人と過ごす\n正月ってのがこんなに\n楽しいもんだとはね", 102, 0, 8, 5831, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120801, 1, "部屋があったかくて\n顔が火照っちまったよ\nちょいと外に涼みに\n行かないか？", 1, 0, 1, 5832, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120801, 1, "餅ってのは\n何をつけてもうまいね\nお前さんは\nどの味が好きだい？", 2, 0, 1, 5833, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120801, 1, "お祝いの鯛が\n足りないって？\nそれじゃあアタシが\n釣ってきてやるよ", 3, 0, 1, 5834, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120801, 1, "おせちが美味いのは\n分かるけど\n食べ過ぎには\n気を付けるんだよ", 4, 0, 1, 5835, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120801, 1, "お前さんのおかげで\nいい正月を\n迎えられたよ\nありがとさん！", 101, 0, 4, 5836, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120801, 1, "慣れない晴れ着も\nお前さんに見て\nもらえたなら\n着たかいがあったね", 102, 0, 8, 5837, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120801, 1, "おはようさん\n寒いのは分かるけど\nいい加減布団から\n出て顔洗ってきな", 1, 1, 1, 5838, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120801, 1, "今日の昼飯は雑煮だ\nアタシの郷土の味\nしっかり味わって\nおくれよ", 2, 2, 1, 5839, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120801, 1, "おや、お前さんも\n夜ふかしかい？\n退屈してたところさ\n付き合っておくれよ", 3, 3, 1, 5840, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120801, 1, "晴れ着だと座り方にも\n気を遣うね\nしわになったら大変さ", 2, 0, 1, 5842, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120801, 1, "せっかくだし\nこの子にも晴れ着を\n着せてあげたいね", 3, 0, 1, 5843, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120801, 1, "これは冬の花の香り…\n風情があって\nいいじゃないか", 4, 0, 1, 5844, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120801, 1, "あったかいねぇ…\nこんな寒い日には\nまさに天国だよ", 5, 0, 1, 5845, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120801, 1, "いつも食べてる\nものでも正月に\n食べると\n新鮮な気がするね", 7, 0, 1, 5847, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120801, 1, "せっかくの正月休みだ\nたまにはゆっくりと\n本でも読もうかね", 8, 0, 1, 5848, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120701, 1, "新年のあいさつ…\n私も街のみんなに\nしてみようかしら…", 1, 0, 1, 5850, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120701, 1, "正月はゆっくり過ごす\nのよね…ゆっくりって\n何したらいいのかしら", 2, 0, 1, 5851, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120701, 1, "この格好も\n慣れてくれば\n動きやすいわ", 3, 0, 1, 5852, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120701, 1, "う～ん、今年の抱負…\nやりたいことが\nたくさんあるのよね", 4, 0, 1, 5853, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120701, 1, "おせち料理\nネビアの分も\n用意しなくちゃね", 5, 0, 1, 5854, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120701, 1, "ふふっ\n正月っていいわね\nみんな楽しそうだもの", 101, 0, 4, 5855, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120701, 1, "新年の思い出…\nこれからもっと作るわ\nそれが私の抱負よ", 102, 0, 8, 5856, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120701, 1, "今日は家で過ごすの？\nあなたと一緒なら\nきっと楽しいわね", 1, 0, 1, 5857, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120701, 1, "どう？　この格好？\nコッコロさんは\n褒めてくれたけど…", 2, 0, 1, 5858, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120701, 1, "ちょうど良かったわ\nあなたも大掃除\n手伝ってくれる？", 3, 0, 1, 5859, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120701, 1, "これから料理するけど\nお雑煮と焼き餅\nどっちが好きかしら？", 4, 0, 1, 5860, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120701, 1, "『今の私』にとって\n初めての正月だもの\nあなたと楽しまないと", 101, 0, 4, 5861, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120701, 1, "去年よりも今年…\nもっと成長するからね\n…お兄ちゃん♪", 102, 0, 8, 5862, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120701, 1, "おはよう\nせっかくの正月だもの\n朝から楽しまないとね", 1, 1, 1, 5863, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120701, 1, "ねぇ…\n良かったら正月の遊び\n教えてくれるかしら？", 2, 2, 1, 5864, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120701, 1, "ふわぁ～…今日が\n終わっちゃうのが…\nもったいないわね…", 3, 3, 1, 5865, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120701, 1, "ふぅ…お参り後だから\n心地よすぎて眠って\nしまいそうだわ…", 2, 0, 1, 5867, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120701, 1, "ふふっ\nかわいいものって\n癒されるわね…", 3, 0, 1, 5868, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120701, 1, "…ん\nこれが梅の香り？\n…いい匂いね", 4, 0, 1, 5869, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120701, 1, "キャルじゃないけど…\nこのまま丸く\nなりたいところね…", 5, 0, 1, 5870, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120701, 1, "はむはむっ…♪\nせっかくの正月だもの\n堪能しなくちゃね♪", 7, 0, 1, 5872, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120701, 1, "…ふんふん\n帯の結び方って\nこんなにあるのね…", 8, 0, 1, 5873, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104201, 1, "今の私が作れる\n最高の一曲…\nあの人に届くといいな", 103, 0, 12, 5875, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104201, 1, "あの、歌を…\n聞いてくれませんか？\nあなたに贈る\n特別な歌を", 103, 0, 12, 5876, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120901, 1, "ひい、ふう…\nむむ、みなに配る\nお年玉のポチ袋が\n残りわずかなのじゃ…", 1, 0, 1, 5877, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120901, 1, "宴の準備はわらわに\n任せておくのじゃ！\n支配者の手腕、\n見せつけてくれる！", 2, 0, 1, 5878, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120901, 1, "新年の願掛けなら\nわらわにすればよい\n支配者に頼むほうが\n確実じゃろう？", 3, 0, 1, 5879, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120901, 1, "みなからもらった\nこの年賀状とやら…\nわらわも\n書いてみようかの♪", 4, 0, 1, 5880, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120901, 1, "正月飾りにもいろいろ\n種類があるんじゃの～\nどれも華々しくて\nわらわ好みじゃ♪", 5, 0, 1, 5881, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120901, 1, "あやつへのお年玉は\nちと多めにしておくか\n…日ごろの感謝も\n込めてのう", 101, 0, 4, 5882, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120901, 1, "あやつのことを想うと\nそれだけで行く年\n来る年が楽しみになる\nふふ、愉快なやつじゃ", 102, 0, 8, 5883, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120901, 1, "みなに晴れ着を\n褒められて、とても\nよい気分なのじゃ♪\n次はお主の番じゃぞ？", 1, 0, 1, 5884, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120901, 1, "お主へのお年玉は\nいちばん最後じゃ！\n残り物には福が宿る\nそうじゃからのう", 2, 0, 1, 5885, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120901, 1, "この季節は手が\nかじかむのう…ほれ、\nお主！　わらわの手を\nあたためるのじゃ！", 3, 0, 1, 5886, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120901, 1, "お主のために雑煮を\n手作りしてみたのじゃ\n食べていくがよい♪", 4, 0, 1, 5887, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120901, 1, "眷属のお主に\n正月休みはまだ早い！\nずっとわらわのそばに\n控えておくのじゃ♪", 101, 0, 4, 5888, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120901, 1, "ふふ、これほど楽しい\n正月は初めてじゃ…\nお主と過ごす時間は\nいつだって特別じゃな", 102, 0, 8, 5889, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120901, 1, "早起きしてわらわを\n待っておったのか？\nさすがはわらわの眷属\n殊勝な心がけじゃな♪", 1, 1, 1, 5890, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120901, 1, "食事どきじゃな\n正月の宴会料理が\nわらわたちを\n待っておるぞ♪", 2, 2, 1, 5891, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120901, 1, "夜も更けてきたのう\n冬の夜は長い…\n心ゆくまでお主と\n過ごせるわけじゃな♪", 3, 3, 1, 5892, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120901, 1, "実に心地がよい♪\nこのまま寝正月も\n悪くないかも\nしれぬのう", 2, 0, 1, 5894, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120901, 1, "愛いやつじゃのう\nわらわのそばに置いて\nやってもよいぞ♪", 3, 0, 1, 5895, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120901, 1, "品のある香りじゃのう\n寝室に置いておけば\nなにかご利益に\nあずかれそうじゃな", 4, 0, 1, 5896, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120901, 1, "心地よい…\nわらわはしばらく\nここから動かぬぞ\nお主も付き合うがよい", 5, 0, 1, 5897, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120901, 1, "舌鼓が鳴るのう♪\n正月の宴には\n持ってこいの品じゃ！", 7, 0, 1, 5899, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120901, 1, "ほう、新春にはそんな\n習わしが…正月を\n制するには、まだまだ\n勉強が必要じゃのう", 8, 0, 1, 5900, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121001, 1, "久しぶりの我が家って\n感じです！　やっぱり\nここが一番ですね〜！", 1, 0, 1, 5902, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121001, 1, "さあ食べましょう！\nみんなで囲める食卓を\n心から味わいますよ☆", 2, 0, 1, 5903, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121001, 1, "ランドソルに\n帰ってきた途端に\nお腹が空きました〜！", 3, 0, 1, 5904, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121001, 1, "ただいま☆\n幸せ特盛りの\nわたしの日常！", 4, 0, 1, 5905, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121001, 1, "ようやくランドソルに\n帰ってきたことですし\nやっぱり魔物料理を\nいただきましょう☆", 5, 0, 1, 5906, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121001, 1, "あの人との絆を\n守るために\nもっともっと食べて\n強くならなくちゃ☆", 101, 0, 4, 5907, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121001, 1, "いつもありがとうって\nあの人を思いっきり\n抱きしめたいです☆", 102, 0, 8, 5908, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121001, 1, "あなたの想いは\nこの身をかけて\n全力で受け止めます！", 1, 0, 1, 5909, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121001, 1, "おかえりなさい☆\nここにいるみんなも\nあなたの帰りをずっと\n待っていましたよ！", 2, 0, 1, 5910, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121001, 1, "このギルドハウスで\n冒険の疲れを\n癒しましょう〜！", 3, 0, 1, 5911, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121001, 1, "あなたの想いが\n全力全開を超える\n力をくれました！", 4, 0, 1, 5912, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121001, 1, "日常が続く幸せを\nあなたと一緒に\nかみしめたいです☆", 101, 0, 4, 5913, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121001, 1, "ぎゅ〜っ！\nわたしたちは\nどんなことがあっても\nずっと一緒ですよ？", 102, 0, 8, 5914, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121001, 1, "おはようございます！\nあなたと過ごせる\n幸せを、今日も\n味わいますよ〜！", 1, 1, 1, 5915, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121001, 1, "ご飯の時間ですよー！\nあなたも一緒に\n食べましょうね！", 2, 2, 1, 5916, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121001, 1, "明日もあなたが隣に\nいてくれることを\n心から願ってますよ\nおやすみなさい☆", 3, 3, 1, 5917, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121001, 1, "ふい〜…\n安心できる場所で\nくつろげるって\n幸せですね〜…", 2, 0, 1, 5919, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121001, 1, "じゅるり…\n食べたくなっちゃう\nくらい、かわいい☆", 3, 0, 1, 5920, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121001, 1, "くんくん…おおー！\nステーキの香り！\nやばいですね☆", 4, 0, 1, 5921, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121001, 1, "体がぽかぽかしてきて\nわたしの食欲も\n燃えてきましたよー！", 5, 0, 1, 5922, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121001, 1, "わたしの食欲に\nついてこられますか？\nまだまだ食べますよ！", 7, 0, 1, 5924, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121001, 1, "これがキャルちゃんの\n言っていた、キスで\n目覚める王子様の\nおとぎ話ですか〜☆", 8, 0, 1, 5925, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121201, 1, "自分で自分を\n分析するのも、これは\nこれで面白い…♪", 1, 0, 1, 5927, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121201, 1, "強過ぎる力の扱いには\n注意しないと…\n七冠がいい例だね", 2, 0, 1, 5928, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121201, 1, "少年の交友関係は\n計り知れないな…\nアタシも含むのか！", 3, 0, 1, 5929, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121201, 1, "原理を徹底解明して\n再発を防ぐぞー！\n…誰か手伝って～！", 4, 0, 1, 5930, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121201, 1, "これは祝福にも\n呪いにもなる…\n忘れずにいよう", 5, 0, 1, 5931, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121201, 1, "少年が\n目を覚ましてくれて\n本当によかった…", 101, 0, 4, 5932, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121201, 1, "彼以外を選んでいたら\n今回の事態は\n乗り切れなかったな…\nさすがだよ、少年", 102, 0, 8, 5933, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121201, 1, "少年！　調子はどう？\nクレープ食べられる？\nっていうか食べて？", 1, 0, 1, 5934, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121201, 1, "元気そうで何よりだ\nまた店を手伝いに\nきてよ。二人もキミに\n会いたがってたしさ", 2, 0, 1, 5935, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121201, 1, "キミの診察もあるし\n今はなるべくそばに\nいるよ。迷惑じゃ\nなければだけど", 3, 0, 1, 5936, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121201, 1, "やっぱり少年は\nあちこちで人を\n助けてる時が\n一番輝いてるよ", 4, 0, 1, 5937, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121201, 1, "アタシは大したこと\nしてないけど\nみんなのことは\n褒めてあげてね", 101, 0, 4, 5938, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121201, 1, "みんなを笑顔に\nできるのは\nやっぱり少年だね", 102, 0, 8, 5939, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121201, 1, "やあ少年\n朝寝坊はしてない？\nそれじゃ、よい１日を", 1, 1, 1, 5940, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121201, 1, "おや、少年！\nまかないのクレープ\n作ったけど、食べる？", 2, 2, 1, 5941, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121201, 1, "ふわぁ…もう夜か…\n研究してたら\nあっという間だね…\nおやすみ、少年", 3, 3, 1, 5942, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121201, 1, "体がなまってたのか\n前線に出ると疲れる…\nまさか…年のせい！？", 2, 0, 1, 5944, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121201, 1, "キミたちも今の\nアタシと同じ感じに\nアレンジしてあげよう", 3, 0, 1, 5945, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121201, 1, "誰が置いたんだろう？\nいい香りだねぇ…\nうん、気に入った♪", 4, 0, 1, 5946, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121201, 1, "何も考えてない時間も\n実は思考にとって\n大事なんだよね～…", 5, 0, 1, 5947, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121201, 1, "食の大切さは\nいつの食事からも\n感じられるね♪", 7, 0, 1, 5949, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121201, 1, "ふむ…面白い！\n今度全巻\n借りにこようっと", 8, 0, 1, 5950, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116401, 1, "聖学祭か…\n良い記憶はないな\nよし忘れよう", 1, 0, 1, 5952, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116401, 1, "健全なる精神は\n健全なる肉体に宿る…\nはっ、知ったことかよ", 2, 0, 1, 5953, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116401, 1, "暇だし鏡でも見るか…\nえへへ、ユニだよ♪\nよし子供料金いけるな", 3, 0, 1, 5954, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116401, 1, "アオイ君は元気に\nしているだろうか…\n彼女は人一倍ぼくに\n懐いていたからな…", 4, 0, 1, 5955, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116401, 1, "そろそろシズル君は\n姉という幻想から\n目覚めただろうか…", 5, 0, 1, 5956, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116401, 1, "ぼくは来年も聖学祭に\n行くさ…卒業しても\n君らとは永遠だ", 101, 0, 4, 5957, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 116401, 1, "最近同志の登校率が\n減少傾向だな…\nまあ仕方ないのだが…\nないのだが…", 102, 0, 8, 5958, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116401, 1, "ほわあああ！\nおい急に話しかけるな\n学びがこぼれる", 1, 0, 1, 5959, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116401, 1, "この体操着、周囲から\n大層評判が良いのだよ\nたいそうだけにな\nどやっ！", 2, 0, 1, 5960, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116401, 1, "何を着ていようと\nぼくはぼくさ\n外見など所詮は\n魂の識別符に過ぎん", 3, 0, 1, 5961, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116401, 1, "救護院のアヤネ君が\n最近ぼくの自称年齢に\n疑念を持ち始めてな…\nあの熊…殺るか…", 4, 0, 1, 5962, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116401, 1, "うん？　なんだね？\nなに、用は無い？\n用も無いのに呼ぶとは\nもしや…これが友達！", 101, 0, 4, 5963, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116401, 1, "この服が似合ってる？\nな、なんだね突然…\nやめたまえよでゅふふ", 102, 0, 8, 5964, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116401, 1, "ふぁ～あ…\n朝も更けてきたな…\nそろそろ寝るか…", 1, 1, 1, 5965, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116401, 1, "ふぁ～あ…もう昼だ…\n人間の起きている\n時間ではないな…\n早く寝なくては…", 2, 2, 1, 5966, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 116401, 1, "うーん、よく寝た！\n気持ちのいい夜だな\n今日も一日がんばるぞ", 3, 3, 1, 5967, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116401, 1, "ほわぁ～…\nもしやこれは都で噂の\n人を駄目にするやつ…", 2, 0, 1, 5969, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116401, 1, "ふむ、愛くるしいな\nだが似合い過ぎる…\nぼくが持つと我ながら\n子供にしか見えん", 3, 0, 1, 5970, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116401, 1, "アロマか、興味深いな\nミツキ印ポーションも\n気化させて鼻腔から\n吸入してみるか…", 4, 0, 1, 5971, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116401, 1, "んふ、んふふ…\n火ってイイよね…\nずっと見てられるね…", 5, 0, 1, 5972, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116401, 1, "や、やめろ…\nとめどなく固形食物を\n生産するのはやめろ…", 7, 0, 1, 5974, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 116401, 1, "この可愛げのない筆致\nやはり作者は理系か！\nぐああ目が理系になる", 8, 0, 1, 5975, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121101, 1, "…あいつのためなら\n無理くらいいくらでも\nしてやるわよ", 1, 0, 1, 5977, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121101, 1, "この姿、\n魔力の制御ムズ過ぎ…\nま、あたしは優秀だし\nどうとでもなるけど！", 2, 0, 1, 5978, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121101, 1, "あいつのこと、\n全部受け入れるって\n決めたから！", 3, 0, 1, 5979, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121101, 1, "今なら誰だろうと\n負ける気がしないわ\nドラゴンでもなんでも\nかかってこいっての！", 4, 0, 1, 5980, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121101, 1, "散々助けられた分、\n今度はあたしが\nあいつのこと助けるわ", 5, 0, 1, 5981, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121101, 1, "あいつとの\nこれまでの冒険は\nどれも、あたしの\n大切な思い出…", 101, 0, 4, 5982, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121101, 1, "もしまたあいつが\n寝たままになったら\nなんだってするわ\nキ…キスでも…", 102, 0, 8, 5983, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121101, 1, "なに、お腹空いたの？\nなら、おいしいご飯を\n食べに行きましょ♪", 1, 0, 1, 5984, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121101, 1, "やっぱ、\nあんたのアホ面見ると\n安心するわ♪", 2, 0, 1, 5985, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121101, 1, "【美食殿】はあんたと\nぺコリーヌがいなきゃ\n始まんないっての", 3, 0, 1, 5986, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121101, 1, "ひゃあっ！？\n急に声かけないで！？\n魔力が暴発しても\n知らないわよ！", 4, 0, 1, 5987, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121101, 1, "…あんたとこうして\nぼーっと過ごす時間、\nまあ…嫌いじゃないわ", 101, 0, 4, 5988, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121101, 1, "あんたが信じて\n見守っててくれれば…\nあたし、\nなんだってできるわ", 102, 0, 8, 5989, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121101, 1, "おはよ\n早く顔洗いなさいよ、\nこのねぼすけ♪", 1, 1, 1, 5990, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121101, 1, "今日のお昼は\nどこで食べる？\nあんたが\n決めていいわよ", 2, 2, 1, 5991, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121101, 1, "もしあんたがまた\n夢に囚われても\nあたしが助けるわ\n安心して寝なさいよ", 3, 3, 1, 5992, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121101, 1, "気持ちいい～…♪\nあいつもたまには\nこうして休ませなきゃ", 2, 0, 1, 5994, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121101, 1, "一目惚れしたかも…！\nこの子、\n部屋に飾りたいわー♪", 3, 0, 1, 5995, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121101, 1, "へぇ、あいつって\nこういう匂いが\n好きなのかしら…？", 4, 0, 1, 5996, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121101, 1, "暖かくて最高…♪\n丸くなっちゃいそう…\nうにゃあ…♪", 5, 0, 1, 5997, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121101, 1, "イケるわね、これ！\nやっぱり食べるって\n大事よねー♪", 7, 0, 1, 5999, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121101, 1, "ファーストキスは\nどんな味、ね…\nふ、ふ～ん…？\nなるほど…", 8, 0, 1, 6000, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118101, 1, "お友だち…\nできるかしら…？\nこんなわたしでも…", 1, 0, 1, 6002, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118101, 1, "あまり、きょろきょろ\nしちゃダメよね…\n変な子だと、\n思われてしまうわ…", 2, 0, 1, 6003, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118101, 1, "広い場所、\n落ち着かない…\nはぅ…どこにいれば…", 3, 0, 1, 6004, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118101, 1, "～♪", 4, 0, 1, 6005, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118101, 1, "【レイジ・レギオン】\n…またいつか、\nみんなと…", 5, 0, 1, 6006, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118101, 1, "…一度でいいから、\nわたしもママって…\nあっ、その…\n…な、なんでもないわ", 101, 0, 4, 6007, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118101, 1, "ここが、今のわたしに\nとっての帰る場所…\n…そう思っても、\nいいのかしら…？", 102, 0, 8, 6008, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118101, 1, "ええと…\nお腹、空いてる？\nおねむじゃない？", 1, 0, 1, 6009, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118101, 1, "ここにいると\n邪魔かしら…？\nならもっと隅っこに…", 2, 0, 1, 6010, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118101, 1, "お喋り…？\nわたしでいいの…？\nうれしい…", 3, 0, 1, 6011, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118101, 1, "あなたの好きな歌…\n教えて…？\n練習、するから…", 4, 0, 1, 6012, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118101, 1, "ここは賑やかだけど…\nうるさいとは感じない\nむしろ、心地いい…", 101, 0, 4, 6013, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118101, 1, "安心、して…\nわたしの歌で、\nあなたを守るから…", 102, 0, 8, 6014, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118101, 1, "ちょっとだけ…\n朝は、苦手…\n…ふぁ…", 1, 1, 1, 6015, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118101, 1, "お昼は、食べた？\n良かったら、一緒に…\n少し…焦げてるけど…", 2, 2, 1, 6016, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118101, 1, "眠れないのかしら…\nじゃ、じゃあ…\n子守歌…聴く？", 3, 3, 1, 6017, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118101, 1, "こんな特等席…\n恐縮、です…", 2, 0, 1, 6019, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118101, 1, "い、今のうちに、\nこの子でお世話の\n練習を…", 3, 0, 1, 6020, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118101, 1, "いい香り…\n…つらい気持ちも\n癒してくれる", 4, 0, 1, 6021, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118101, 1, "あったかい…\nいつまでも、\nこうしていられそう…", 5, 0, 1, 6022, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118101, 1, "おいしく出来てる…\nすごいわ…\n見習わなくっちゃ…", 7, 0, 1, 6024, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118101, 1, "『赤ちゃんとママの\nスキンシップ入門』…\n…べ、勉強になるわ…", 8, 0, 1, 6025, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121301, 1, "きれいなお部屋…！\n立派な舞台の\nセットみたい…♪", 1, 0, 1, 6027, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121301, 1, "お芝居では私が先輩\nなんだから、しっかり\n私がミサキちゃんを\nエスコートしなきゃ", 2, 0, 1, 6028, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121301, 1, "～～♪\n歌うお芝居も多いから\nもっと上手く\nなりたいな…！", 3, 0, 1, 6029, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121301, 1, "ここにいる人たちと\n仲良くなりたいな…\nそしたらきっと演技の\n幅も広げられるよね", 4, 0, 1, 6030, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121301, 1, "機会があったら\nアヤネちゃんとも\nお芝居してみたいな♪", 5, 0, 1, 6031, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121301, 1, "お兄ちゃんに褒めて\nもらえるように\nお芝居の練習\nいっぱいしなきゃ…♪", 101, 0, 4, 6032, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121301, 1, "あう…お兄ちゃんを\n意識したら、台詞が\n飛んじゃったよぅ…！", 102, 0, 8, 6033, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121301, 1, "あっ、お兄ちゃん…♪\nお芝居の練習を\n見てくれませんか…？", 1, 0, 1, 6034, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121301, 1, "いまの発声練習\nどうでしたか？\nひゃん…！？\n頭は撫でないでぇ～", 2, 0, 1, 6035, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121301, 1, "ミサキちゃんも\nお芝居、とっても\n上達してるんですよ\n私も負けられません♪", 3, 0, 1, 6036, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121301, 1, "また物語を聞かせて\nほしい、ですか…？\n私なんかでよければ\nもちろんよろこんで♪", 4, 0, 1, 6037, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121301, 1, "新しいお話を\n考えてみました\n聞いてくれますか…？\nよかった…♪", 101, 0, 4, 6038, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121301, 1, "いつか…舞台上じゃ\nなくても、照れずに\nお兄ちゃんに気持ちを\n伝えてみせますね…♪", 102, 0, 8, 6039, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121301, 1, "おはようございます♪\nマヒルさんにもらった\nミルク、一緒に\n飲みませんか？", 1, 1, 1, 6040, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121301, 1, "牧場のみなさんに\nドングリパンを\nいただきました～\nおひとつどうぞ♪", 2, 2, 1, 6041, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121301, 1, "寝る前にまたココアを\n飲みながら、お話を\n聞いてもらえますか？\nえへへ、うれしいな♪", 3, 3, 1, 6042, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121301, 1, "お芝居の練習の\nあとは、ここで\nお兄ちゃんと\nゆっくりしたいな♪", 2, 0, 1, 6044, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121301, 1, "今日も演技の\n練習に付き合ってね♪", 3, 0, 1, 6045, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121301, 1, "素敵な香り…\nこのイメージも\nお芝居に\n活かせないかな…？", 4, 0, 1, 6046, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121301, 1, "私もお芝居で\nみんなの心を\nぽかぽか温められる\nようになりたいな…♪", 5, 0, 1, 6047, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121301, 1, "舞台に立つんだから\n食べ過ぎないように\n気をつけないと。\nでも…おいしいなぁ♪", 7, 0, 1, 6049, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121301, 1, "このお話、面白い…\n脚本を書く\n参考にもなりそう…♪", 8, 0, 1, 6050, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107501, 1, "えへへ…♪\n二人で見た夕日…\nキレイでしたね…", 103, 0, 12, 6052, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107501, 1, "これから泳ぎの\n練習ですか？\n付き合いますよ♪", 103, 0, 12, 6053, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107601, 1, "主さまを導くことで\nわたくしも未知なる\n領域へと辿り着けます", 103, 0, 12, 6054, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107601, 1, "主さまとなら\n海よりも深く\n空よりも高い場所へも\n目指せそうです", 103, 0, 12, 6055, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107801, 1, "あいつと二人で\n夏の遠い海に…って\nあたしってば\nなに考えてるのよ！", 103, 0, 12, 6056, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107801, 1, "あたしが楽しく\n過ごせるのはあんたが\nいてくれたからよ\nか、感謝してるわ", 103, 0, 12, 6057, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121401, 1, "はぁ…アタシってば\nこんなにかわいくって\nキレイなんて罪よね～", 1, 0, 1, 6058, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121401, 1, "舞台の衣装を\nクリーニング\nしとかなくちゃ…", 2, 0, 1, 6059, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121401, 1, "舞台映えするメイク…\nスズナなら\n知ってるかしら？", 3, 0, 1, 6060, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121401, 1, "物産展の会計係と\n山盛りの算数の宿題…\nどっちが簡単かしら…", 4, 0, 1, 6061, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121401, 1, "牧場と温泉の仕事に\nお芝居の練習に…\nちょ、ちょっと休憩～", 5, 0, 1, 6062, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121401, 1, "お客さんの視線も\nうれしいけど…\nあいつも見てて\nくれてるかしら？", 101, 0, 4, 6063, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121401, 1, "もっともっと\nセクシーになって\nあいつをビックリ\nさせてやるわ♪", 102, 0, 8, 6064, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121401, 1, "アンタも休憩？\nしっかり働くために\n休むのも大事よね", 1, 0, 1, 6065, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121401, 1, "ふふん♪　アタシも\n料理ができるように\nなったんだから！\n簡単なのだけだけど…", 2, 0, 1, 6066, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121401, 1, "温泉ってお肌に\nいいのよね？\nもっとキレイに\nなったらどうしよう〜", 3, 0, 1, 6067, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121401, 1, "物産展の荷物は\nこれでよし…っと\nそれじゃアンタも\n運ぶの手伝ってよね♪", 4, 0, 1, 6068, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121401, 1, "いつもと違うアタシに\n見とれちゃった？\n仕方ないわね～♪", 101, 0, 4, 6069, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121401, 1, "お花見って風流で\n大人って感じ♪\n…でもお団子も\n忘れないでよね！", 102, 0, 8, 6070, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121401, 1, "みんなに朝ごはんを\n作ってみたら\n喜んでくれるかしら？", 1, 1, 1, 6071, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121401, 1, "お客さんがいっぱい\n来る時間帯ね！\n元気出していくわ！", 2, 2, 1, 6072, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121401, 1, "そろそろ寝ないと…\n睡眠不足はお肌の\n大敵なんだから", 3, 3, 1, 6073, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121401, 1, "ふかふかしてて\n気持ちいい～♪\n疲れが癒されるぅ…", 2, 0, 1, 6075, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121401, 1, "この子もアタシと\nおそろいの衣装に\nしてあげようかしら♪", 3, 0, 1, 6076, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121401, 1, "素敵な衣装で\nいい香りを\n身にまとう…ふふ、\n大人のたしなみよね♪", 4, 0, 1, 6077, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121401, 1, "あったか～い♪\nここでお昼寝したら\n気持ちよさそう…", 5, 0, 1, 6078, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121401, 1, "お仕事がんばると\nいつもよりお腹\n空いちゃう～！", 7, 0, 1, 6080, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121401, 1, "最新のトレンド\n載ってないかしら？\n物産展の売り物に\n取り入れなくちゃ！", 8, 0, 1, 6081, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121501, 1, "怪盗のこと…\nお父さんお母さんに\nもし知られたら\nなんて説明しよう…", 1, 0, 1, 6083, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121501, 1, "たとえ怪盗でも、人の\n家にお邪魔するときは\n身だしなみをしっかり\nしないと…", 2, 0, 1, 6084, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121501, 1, "鍵開けって難しい…\nうーん、もっと\n練習しておかなくちゃ", 3, 0, 1, 6085, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121501, 1, "姿が見えない\n怪盗の噂…？\nわ、私のことじゃ\nありませんように…！", 4, 0, 1, 6086, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121501, 1, "予告状って…\n切手はどこに貼れば\nいいんだろう…？", 5, 0, 1, 6087, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121501, 1, "怪盗活動にも\n慣れてきたけど…\nやっぱり先輩が\nいないと心細いなぁ…", 101, 0, 4, 6088, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121501, 1, "せ、先輩の心も\nいつか盗めるかな…？\n…わわわ、私ってば\nなに言ってるんだろ…", 102, 0, 8, 6089, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121501, 1, "怪盗が大活躍って\n新聞で読んだ…？\nだっ、誰のこと\nでしょうね…あはは", 1, 0, 1, 6090, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121501, 1, "尾行に潜入…\n存在感がないのって\n意外と役立つことも\nあるんですね", 2, 0, 1, 6091, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121501, 1, "せせせ、先輩っ！\n帽子をぎゅーって\nかぶったら、抜けなく\nなっちゃいました…！", 3, 0, 1, 6092, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121501, 1, "ぬ、盗み聞きなんて\nしてませんよ？\nみんなが私に気づかず\n話し始めるだけで…", 4, 0, 1, 6093, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121501, 1, "秘密道具が見たい？\nそ、それは内緒です！\n秘密道具ですから…\nで、でも先輩になら…", 101, 0, 4, 6094, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121501, 1, "えへへ\n見つかっちゃいました\n先輩はいつも私を\n見つけてくれますね…", 102, 0, 8, 6095, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121501, 1, "おはようございまふ…\n秘密道具のお手入れで\n夜更かししちゃって…\nちょっと眠たいです～", 1, 1, 1, 6096, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121501, 1, "怪盗が昼に活動する…\n案外そのほうが\n意外性が\nあるのかも…？", 2, 2, 1, 6097, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121501, 1, "外が暗くなりましたね\n…そろそろ怪盗の活動\n始めましょうか？", 3, 3, 1, 6098, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121501, 1, "ここでくつろぐと\n疲れが取れます～\n…今日も怪盗\nがんばろっと", 2, 0, 1, 6100, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121501, 1, "わぁ、かわいい…！\n心を盗まれるって\nこういうことかな…？", 3, 0, 1, 6101, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121501, 1, "素敵な香り…でも\n服に香りがついたら\n隠れてるときに\n見つかっちゃうかな…", 4, 0, 1, 6102, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121501, 1, "ぽかぽかで\nいい気持ち…\n夜道を歩く前に\n温まっておこうっと…", 5, 0, 1, 6103, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121501, 1, "怪盗活動に備えて\n今のうちにしっかり\n食べておかなきゃ…！", 7, 0, 1, 6105, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121501, 1, "女の子は秘密が\n多いほうが魅力的…？\nそ、そうなんだ…", 8, 0, 1, 6106, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104501, 1, "ドＳさんはいつも\nクウカの妄想の中に…\nぐふふ、じゅるり～♪", 103, 0, 12, 6108, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104501, 1, "やさしい触り方も\n嫌いじゃありません…\nは…恥ずかしいですが", 103, 0, 12, 6109, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121601, 1, "この大怪盗さまに\n盗めないものなんて\nないです！　たぶん！", 1, 0, 1, 6110, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121601, 1, "あたしにふさわしい\nビッグなお宝は\nどこにあるですかね～", 2, 0, 1, 6111, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121601, 1, "そろーりそろーり…\n気配を消せばお菓子も\n食べ放題…にやり", 3, 0, 1, 6112, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121601, 1, "またサボってたのが\nバレたです…\nボスの地獄耳～！", 4, 0, 1, 6113, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121601, 1, "ギャングで大怪盗…！\nこの肩書の多さ！\nまさに有能の証です！", 5, 0, 1, 6114, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121601, 1, "お兄さんだったら…\nお宝を山分けして\nあげてもいいですよ", 101, 0, 4, 6115, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121601, 1, "お兄さんはアレですよ\n大怪盗イノリさまの\n相棒みたいなものです", 102, 0, 8, 6116, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121601, 1, "何事も用心深さが\n大切ですよ。ビ…\nビビりじゃないです！", 1, 0, 1, 6117, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121601, 1, "ぬ、盗み聞き？\nしてないですよっ？\nぴゅーぴゅーぴゅー", 2, 0, 1, 6118, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121601, 1, "情報が欲しければ\nお菓子と交換ですよ\nさぁ、交換交換っ♪", 3, 0, 1, 6119, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121601, 1, "しーっ！\n今、尾行中なんです\n話はあとで…", 4, 0, 1, 6120, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121601, 1, "お兄さんと話してると\n時間が速いような…？\nこの時間泥棒！", 101, 0, 4, 6121, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121601, 1, "ちょうどよかった！\nいい話があるんです\n一口乗りませんか？", 102, 0, 8, 6122, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121601, 1, "おてんとうさまが\n出てやがるですね…\n悪事がやりにくいです", 1, 1, 1, 6123, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121601, 1, "もうこんな時間ですか\nご飯にお昼寝…\nいやー忙しいです♪", 2, 2, 1, 6124, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121601, 1, "ふっふっふ！　夜こそ\n大怪盗の時間です！\n……ふぁあ", 3, 3, 1, 6125, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121601, 1, "うーん…\n警備の目がないと\n気楽でいいですねぇ", 2, 0, 1, 6127, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121601, 1, "くう…！\nこのかわいさは\n盗めないです…♪", 3, 0, 1, 6128, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121601, 1, "犯行現場に残ったのは\n香水の匂いだけ…\nふふ、おしゃれ大怪盗", 4, 0, 1, 6129, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121601, 1, "意外とこういう場所に\n隠し扉があったり…\nしないです？", 5, 0, 1, 6130, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121601, 1, "もぐもぐ…\nこの美味しさ…\nとんでもない罠ですね", 7, 0, 1, 6132, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121601, 1, "むむむ…ちっとも\n分からないです…\nほとんど暗号ですよ", 8, 0, 1, 6133, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121701, 1, "あら、サレンさんは\nどこに行かれた\nのかしら？", 1, 0, 1, 6135, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121701, 1, "ふんふんふーん♪\nなんだか華やいだ\n気分ですわ♪", 2, 0, 1, 6136, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121701, 1, "さて…次の事業は\nどうしましょう\n何か面白いことは…", 3, 0, 1, 6137, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121701, 1, "おーっほっほっほ！\nあら…楽しくて、つい\n笑ってしまいましたわ", 4, 0, 1, 6138, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121701, 1, "センスがあって\nいいお部屋ですわね\n私も見習いたいですわ", 5, 0, 1, 6139, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121701, 1, "サレンさんとあのお方\n仲がいいですわね\nも、もちろん仲がいい\nのはよいことですわ！", 101, 0, 4, 6140, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121701, 1, "どんなときでも\n正々堂々！\nそれが私の\n誇りですわ！", 102, 0, 8, 6141, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121701, 1, "ふふ、ごきげんよう\n私、絶好調でしてよ", 1, 0, 1, 6142, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121701, 1, "どうかされましたか？\n何かお困りごと\nでしたら\n是非力になりますわよ", 2, 0, 1, 6143, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121701, 1, "たまにはこうして\nゆっくりと\nお話しするのも\nいいものですわね", 3, 0, 1, 6144, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121701, 1, "次の事業に関して\n考え事をしてましたの\nよければ相談に乗って\nくださるかしら？", 4, 0, 1, 6145, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121701, 1, "よろしければ\nお話しませんこと？\nサレンさんのお話を\n聞かせてほしいですわ", 101, 0, 4, 6146, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121701, 1, "ふふっ…\n先ほどサレンさんと\nあなたさまのお話を\nしていたところですわ", 102, 0, 8, 6147, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121701, 1, "さあ！\n今日も一日\n全力で参りますわ！", 1, 1, 1, 6148, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121701, 1, "お昼はどうしましょう\nサレンさんをお誘い\nしてみようかしら", 2, 2, 1, 6149, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121701, 1, "もう少しだけ…\n明日の準備をして\nおかないと…", 3, 3, 1, 6150, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121701, 1, "はぁ～…いいですわね\nはっ、私としたことが\nはしたないですわね", 2, 0, 1, 6152, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121701, 1, "かわいらしいですわ\nいくつになっても\nぎゅっと\nしたくなりますわね", 3, 0, 1, 6153, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121701, 1, "落ち着きますわね\nサレンさんはどんな\n香りが好きなのかしら", 4, 0, 1, 6154, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121701, 1, "ふふっ、燃え盛る\n情熱の炎…\nいいですわね", 5, 0, 1, 6155, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121701, 1, "このおいしさで\n元手がゼロ…\nすばらしい\n商品ですわ！", 7, 0, 1, 6157, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121701, 1, "これは…ふむふむ\n新しい事業のヒントに\nなりそうですわ", 8, 0, 1, 6158, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121801, 1, "さて、と…\n今日は何をしなくちゃ\nいけないんだっけ？", 1, 0, 1, 6161, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121801, 1, "やることは山積み\nだけど、手を動かす\nのは嫌いじゃないわ♪", 2, 0, 1, 6162, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121801, 1, "あら？　アキノさんの\n声が聞こえたような\n気がしたんだけど…", 3, 0, 1, 6163, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121801, 1, "アキノさんといると\nついこっちまで笑顔に\nなっちゃうのよね", 4, 0, 1, 6164, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121801, 1, "弱音なんか\nはいてられないわ\nアキノさんに\n笑われちゃうもの", 5, 0, 1, 6165, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121801, 1, "気が付けばアキノさん\nのペースだけど…\n不思議と嫌では\nないのよね", 101, 0, 4, 6166, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121801, 1, "あたしを好敵手って\n言ってくれる\nアキノさんのためにも\nしっかりしなくちゃ", 102, 0, 8, 6167, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121801, 1, "どうしたの？\nアキノさんなら\nさっきそこに\nいなかった？", 1, 0, 1, 6168, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121801, 1, "あたしなら大丈夫\n救護院だって\nみんなが助けて\nくれるからね", 2, 0, 1, 6169, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121801, 1, "さっきアキノさんが\nあんたを探してたわよ\nなにかしたの？", 3, 0, 1, 6170, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121801, 1, "こう見えてヒマじゃ\nないんだけど…\n少しくらいなら\n付き合ってあげるわ", 4, 0, 1, 6171, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121801, 1, "ねえ、あんたと\nアキノさんって…\nううん、やっぱり\nなんでもないわ…", 101, 0, 4, 6172, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121801, 1, "アキノさんも誘って\nお茶でもしない？\n話したいことが\nたくさんあるの♪", 102, 0, 8, 6173, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121801, 1, "早起きは三文の得よ\nいい一日を迎えるため\n目を覚まさなきゃ", 1, 1, 1, 6174, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121801, 1, "今日のお昼は…\nアキノさんと同席する\n予定だったわね\nあんたもどう？", 2, 2, 1, 6175, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121801, 1, "ふぁ…っと、だめね\nたるんだところを\nアキノさんに\n見せるわけには", 3, 3, 1, 6176, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121801, 1, "ん～…\n落ち着くわね\nこのまま\n寝ちゃいそう…", 2, 0, 1, 6178, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121801, 1, "救護院の皆も\nぬいぐるみが\n大好きなのよ", 3, 0, 1, 6179, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121801, 1, "いい香りね…\nこれはアキノさんの\n趣味かしら？", 4, 0, 1, 6180, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121801, 1, "こんなに激しく\n燃えているのに\nあったかくて落ち着く\nまるでアキノさんね", 5, 0, 1, 6181, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121801, 1, "どんどん出てくる…\nこれ、救護院にも\nほしいわね…", 7, 0, 1, 6183, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121801, 1, "ふむふむ…\n前にアキノさんが\n言ってたのは\nこのことかしら？", 8, 0, 1, 6184, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122001, 1, "この衣装、私には\nちょっとかわい過ぎて\n恥ずかしいな…", 1, 0, 1, 6187, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122001, 1, "一部の人からは\n海賊より海賊らしいと\n評判の父です", 2, 0, 1, 6188, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122001, 1, "潮風で錆びないよう\n武器の手入れを\nしておきましょうか", 3, 0, 1, 6189, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122001, 1, "宝探しのお手伝いなら\n力になれると思います\n父のやる気次第ですが", 4, 0, 1, 6190, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122001, 1, "除霊は気を遣います\n一歩間違えると父まで\n成仏させてしまうので", 5, 0, 1, 6191, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122001, 1, "あの人と見た\n海の景色を…ずっと\n忘れずにいたいな", 101, 0, 4, 6192, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122001, 1, "幽霊になっても\n一目会えるなら…\n私はきっとあの人に…", 102, 0, 8, 6193, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122001, 1, "ボートを漕ぐの、少し\n上手になったんですよ\n今度お見せしますね", 1, 0, 1, 6194, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122001, 1, "航海に向けた\n買い出しが必要ですね\n予定は空いていますか", 2, 0, 1, 6195, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122001, 1, "呪われた財宝の\n解呪依頼が多くて…\nふぅ…えっと次は…", 3, 0, 1, 6196, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122001, 1, "夜の航海では\n父が明かり代わりに\nなってくれるんです", 4, 0, 1, 6197, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122001, 1, "船の上では食材が\n限られるので料理の\nいい練習になります", 101, 0, 4, 6198, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122001, 1, "この先もずっと\nあなたと一緒に\nたくさんの思い出を\n作っていきたいです", 102, 0, 8, 6199, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122001, 1, "あの人の寝顔…\nもう少しだけ\n眺めていたいな…", 1, 1, 1, 6200, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122001, 1, "船の上では\n規則正しい生活が\n重要ですから", 2, 2, 1, 6201, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122001, 1, "夜の海は幽霊が\n見分けやすいんです\n青白く光るので", 3, 3, 1, 6202, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122001, 1, "海に揺られるような\n心地よさですね\n気が休まります…", 2, 0, 1, 6204, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122001, 1, "あなたも一緒に\n海を見に行きますか？\n…なんて。ふふっ", 3, 0, 1, 6205, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122001, 1, "爽やかな香りですね…\n一面の青い海が\n目に浮かぶようです", 4, 0, 1, 6206, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122001, 1, "温かい…\n潮風で冷えた体に\n沁みますね", 5, 0, 1, 6207, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122001, 1, "あ、おいしい…\n日持ちするなら船旅に\n持っていこうかな…", 7, 0, 1, 6209, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122001, 1, "アンナさんに教えて\nもらった小説は…\nあ、これですね", 8, 0, 1, 6210, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 102601, 1, "なにもしないを\nしてるんだよ\nあ～いそがしいそがし", 103, 0, 12, 6212, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102601, 1, "はぁ～あんたといると\nどこまでも気持ちよく\nダラダラできるね～", 103, 0, 12, 6213, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121901, 1, "暗黒騎士にして、\n伝説の海賊でもある\n私に不可能はないっ！", 1, 0, 1, 6214, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121901, 1, "海賊団の船員、\n随時募集中です…と\nこれで新たな盟友が\n増えるはずだ…！", 2, 0, 1, 6215, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121901, 1, "肩に乗せるペットが\nほしいな。今のうちに\nペット用品を買いに\n行かなくては…！", 3, 0, 1, 6216, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121901, 1, "財宝の地図はどこで\n売っているのだろう…\n古本屋？　古物商？", 4, 0, 1, 6217, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121901, 1, "この海賊衣装に\n身を包んでいると\n前世の新たな記憶が\nよみがえってくるな！", 5, 0, 1, 6218, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121901, 1, "いずれはシグルドと\n海原への冒険に\n繰り出したいな…\nきっと楽しいぞ！", 101, 0, 4, 6219, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 121901, 1, "待ちわびたぞ盟友よ！\n私と貴様あっての\n海賊団…だからその…\n今日はそばにいてくれ", 102, 0, 8, 6220, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121901, 1, "シグルド！　港町で\nお魚のおすそ分けを\nもらったんだ！\n一緒に食べよう！", 1, 0, 1, 6221, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121901, 1, "シグルドもこのコート\n羽織ってみないか？\nふふっ、きっとすごく\n似合うと思うぞ！", 2, 0, 1, 6222, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121901, 1, "海賊船の新たな\n秘密兵器を作ったぞ！\nシグルド、これから\n見に来ないか！？", 3, 0, 1, 6223, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121901, 1, "私は伝説の海賊だから\n泳ぐのだって得意だ！\nふ、深いところは\n少し怖いが…", 4, 0, 1, 6224, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121901, 1, "ふふ、いつ無人島に\n漂着してもいいように\n非常食は準備済みだ\nシグルドの分もある！", 101, 0, 4, 6225, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121901, 1, "冥風戦記の新章が\n完成したぞ…！\nシグルド、まずは\n貴様に読んでほしい！", 102, 0, 8, 6226, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121901, 1, "ふっ、天気明朗\n絶好の航海日和だ！\n早速海に繰り出そう！", 1, 1, 1, 6227, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121901, 1, "昼食用に魚を\n釣っておいたぞ！\nふふ、いかにも\n海賊っぽいだろう？", 2, 2, 1, 6228, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 121901, 1, "夜の海は真っ黒で\n少しだけ怖いな…\nシグルド、私の\nそばを離れるなよ…！", 3, 3, 1, 6229, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121901, 1, "大海賊がくつろぐのに\n相応しい逸品だ…！\nえっと…これ、どこの\nお店で買えますか？", 2, 0, 1, 6231, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121901, 1, "わわ、かわいい…！\n船室のベッドに\n置いておきたいな…\n連れてってはダメか？", 3, 0, 1, 6232, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121901, 1, "おぉ、この香りは…！\n集中力が上がりそうだ\n海賊船の船長室に\nぜひ置きたいな！", 4, 0, 1, 6233, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121901, 1, "これが船乗りの\n伝承に聞く、海原を\nさまよう神秘の火か！\n…暖まるな！", 5, 0, 1, 6234, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121901, 1, "腹が減っては\n海賊はできぬ！\n…小説も書けぬ！", 7, 0, 1, 6236, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 121901, 1, "女海賊と騎士はその後\n恋に落ち…？　恋愛\nモノではないかっ！？\nき、興味深くはあるが", 8, 0, 1, 6237, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122101, 1, "いつもの山籠もりが\nブームになるなんて…\n人生、なにが起きるか\n分からないなぁ", 1, 0, 1, 6239, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122101, 1, "キャンプ道具…\nいえ、キャンプギア！\nカッコいい響き…♪", 2, 0, 1, 6240, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122101, 1, "レッツ、ＢＢキャン！\nこのフレーズ\n流行るかも…？", 3, 0, 1, 6241, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122101, 1, "キャンプ仲間…\n募集してみようかな？\n友達も兼任で…", 4, 0, 1, 6242, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122101, 1, "ユカリさんと\nジュンさん…元気かな\nまた一緒に\nキャンプしたいなぁ…", 5, 0, 1, 6243, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122101, 1, "私の取材が載った本…\n【フォレスティエ】に\n送らなくちゃ…♪", 101, 0, 4, 6244, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122101, 1, "もし、あの人が\n落ち込むことが\nあったら…その時は\n私が力になりたいな", 102, 0, 8, 6245, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122101, 1, "ブッシュクラフトは\nマイフレンドくんの\nカスタムにも\n活かせる技術です！", 1, 0, 1, 6246, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122101, 1, "会話に花が咲く…\nって言いますよね\n私の場合、会話相手が\nお花そのものですけど", 2, 0, 1, 6247, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122101, 1, "この服、実は…\n昔ミサト先生に\nこしらえてもらった\n物なんです！", 3, 0, 1, 6248, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122101, 1, "昔はやまびこで会話の\n練習をしてたんです\nほとんどオウム返しで\n気まずかったなぁ～", 4, 0, 1, 6249, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122101, 1, "どうもマイスターです\n…あ、すみません\nみなさんの期待に\n応えようと練習中で…", 101, 0, 4, 6250, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122101, 1, "たとえぼっちを\n卒業しても…\n私は一生ＢＢ団です！", 102, 0, 8, 6251, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122101, 1, "小鳥さんのさえずりで\n目が覚める…\n心地いいものですね", 1, 1, 1, 6252, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122101, 1, "キャンプといえば\nおいしいご飯です！\n腕を振るいますよ！", 2, 2, 1, 6253, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122101, 1, "すっかり夜ですね…\nおやすみの前に\n星でも見ませんか？", 3, 3, 1, 6254, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122101, 1, "都会でくつろぎ…\n自然の中でくつろぐ…\nくつろぎ無限ループ…", 2, 0, 1, 6256, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122101, 1, "しっかり観察すれば\nブッシュクラフトで\n再現できるかも…？", 3, 0, 1, 6257, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122101, 1, "はぁ～…やっぱり\n木の香りを嗅ぐと\n肩の力が抜けますねぇ", 4, 0, 1, 6258, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122101, 1, "ああ…あったまります\nなんだか心に\nゆとりがでますね…", 5, 0, 1, 6259, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122101, 1, "このおいしさ…！\n今度はキャンプに\n持っていこうかな…", 7, 0, 1, 6261, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122101, 1, "ふむふむ…\nいろんな知識があると\nキャンプはもっと\n楽しくなりますよね", 8, 0, 1, 6262, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122201, 1, "部屋がキャンプ用品で\n埋まっていく…\nキャンプ…なんて\n恐ろしい趣味なの…？", 1, 0, 1, 6264, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122201, 1, "仕事でも\nアウトドアでも\n頼れるお姉さんに\nなれたらいいなぁ…", 2, 0, 1, 6265, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122201, 1, "この服、かわいい上に\n本当に快適ね～♪\nおしゃれにキャンプを\n楽しめて最高だわ！", 3, 0, 1, 6266, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122201, 1, "ん〜次はどんな\nキャンプ飯を\n作ろうかしら？\n彼の好物は、っと…", 4, 0, 1, 6267, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122201, 1, "早く週末が\n来ないかしら…\n仕事を忘れてパーっと\nキャンプ飲みを…！", 5, 0, 1, 6268, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122201, 1, "新しいキャンプ飯の\nレシピを覚えたわ！\n彼も喜んで\nくれるはず…よね♪", 101, 0, 4, 6269, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122201, 1, "彼の分の食器も\nこっそり買い揃えちゃ\nおうかな…そ、それは\nさすがにまだ早いか…", 102, 0, 8, 6270, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122201, 1, "今度、財団主催で\n大規模なキャンプを\nやってみるのも\nいいかもしれないわね", 1, 0, 1, 6271, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122201, 1, "ソロキャンプも\n悪くはないけど\n私はやっぱりキミと…\nな、なんでもない！", 2, 0, 1, 6272, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122201, 1, "キャンプのことなら\n私に聞いて。なんでも\n教えてあげるから！\n…調べた上でね！", 3, 0, 1, 6273, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122201, 1, "キャンプ場で飲む\n最高の一杯は\n他じゃ味わえないわ！\nさあ、乾杯っ！", 4, 0, 1, 6274, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122201, 1, "うん、決めた！　次は\n禁しゅわキャンプ！\nだってキミとゆっくり\n話したいんだもん♪", 101, 0, 4, 6275, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122201, 1, "キミのおかげで\n本当の最高の一杯が\n味わえたわ…\nありがとう♪", 102, 0, 8, 6276, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122201, 1, "んー、気持ちいい！\nキャンプが趣味に\nなってから、寝覚めが\nよくなったわ♪", 1, 1, 1, 6277, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122201, 1, "お昼は私がごちそう\nするわ。キャンプ飯で\n鍛えた料理の腕前を\n披露しちゃうわよ～！", 2, 2, 1, 6278, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122201, 1, "そろそろ寝ましょ？\n明日も朝日を眺める\nためにね。もちろん\nコーヒー付きで♪", 3, 3, 1, 6279, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122201, 1, "くつろぐのに\n持ってこいね～\nテントの中に置いても\nよさそうだわ！", 2, 0, 1, 6281, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122201, 1, "ふふ、かわいいわね～\n…今度キャンプ場に\n連れていこうかしら？", 3, 0, 1, 6282, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122201, 1, "うん、いい匂い…♪\nキャンプ場を思い出す\n自然の香りだわ〜", 4, 0, 1, 6283, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122201, 1, "はぁ、温まるぅ～\n冬のキャンプで\n使ってみたいわね", 5, 0, 1, 6284, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122201, 1, "おいしい～♪\nキャンプ飲みの\nおつまみに\nぴったりね！", 7, 0, 1, 6286, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122201, 1, "へえ…こんなキャンプ\n飯もあるんだ？\n彼、好きだったり\nするかな…", 8, 0, 1, 6287, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100701, 1, "一生ついててやるの\n幽霊の一生は\nなっが～～～いの！", 103, 0, 12, 6289, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100701, 1, "む～…あんまり触ると\nすり抜けてやるの！\nウソなの。むふふ♪", 103, 0, 12, 6290, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122301, 1, "んー…\n今日はベスちゃんと\nなにして遊ぼーかなー", 1, 0, 1, 6291, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122301, 1, "ヴァンピィちゃんも\nけんぞくの友だちと\n仲良くなりたいな♪", 2, 0, 1, 6292, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122301, 1, "けんぞくのお部屋は\nヴァンピィちゃんが\n守ってあげなくちゃ！", 3, 0, 1, 6293, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122301, 1, "お掃除するなら\nヴァンピィちゃんも\n手伝ってあげよー♪", 4, 0, 1, 6294, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122301, 1, "ふーん、ふーん♪\nけんぞくってこーゆー\nお部屋が好きなんだー", 5, 0, 1, 6295, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122301, 1, "ひまだなー\nけんぞくをさそって\nお散歩いこっかな♪", 101, 0, 4, 6296, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122301, 1, "今日のスープに\nけんぞくの大好物を\n入れてあげよっと♪", 102, 0, 8, 6297, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122301, 1, "けんぞくー！\nヴァンピィちゃんと\nお出かけしよ♪", 1, 0, 1, 6298, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122301, 1, "よしよし！\n今日のけんぞくも\nいい子だね♪", 2, 0, 1, 6299, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122301, 1, "んんー…\nやっぱりけんぞくと\nベスちゃんは似てる？", 3, 0, 1, 6300, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122301, 1, "いーい？\nヴァンピィちゃんの\nいうこと聞くのよ？", 4, 0, 1, 6301, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122301, 1, "ヴァンピィちゃんの\nけんぞくだもん！\n大切にするからね！", 101, 0, 4, 6302, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122301, 1, "えへへっ！\nやっぱりけんぞくって\nやさしー匂いがする♪", 102, 0, 8, 6303, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122301, 1, "おはよー…\nヴァンピィちゃんは\nもうちょっと眠るねー", 1, 1, 1, 6304, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122301, 1, "ヴァンピィちゃん\nひなたぼっこ\nだーいすき！", 2, 2, 1, 6305, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122301, 1, "んー！　夜だ♪\nヴァンピィちゃん\n絶好調だよ！", 3, 3, 1, 6306, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122301, 1, "わー！\nふかふかだ！\nきもちぃー♪", 2, 0, 1, 6308, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122301, 1, "かわいー♪\nベスちゃんに負けない\n抱き心地！", 3, 0, 1, 6309, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122301, 1, "この香り…なんだろ？\nヴァンピィちゃん\n気に入っちゃった♪", 4, 0, 1, 6310, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122301, 1, "あったかいねー…\nヴァンピィちゃん\nずっとここにいるー…", 5, 0, 1, 6311, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122301, 1, "はむはむ…\nんー！\nおいしー♪", 7, 0, 1, 6313, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122301, 1, "ヴァンピィちゃん\nこの本\n好きなんだよねー♪", 8, 0, 1, 6314, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122501, 1, "夏といえば\nかき氷だよね\n近くにお店は…", 1, 0, 1, 6316, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122501, 1, "この格好でいるのも\nなじんできたし\nちょっと鍛錬でも…", 2, 0, 1, 6317, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122501, 1, "動きやすいけれど…\nいつもと違う服装は\n少し落ち着かないな", 3, 0, 1, 6318, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122501, 1, "夏の空気に\n当てられると思わず\n気が緩んでしまうね", 4, 0, 1, 6319, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122501, 1, "砂浜で走ると\nいい具合に体幹を\n鍛えられそうだ", 5, 0, 1, 6320, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122501, 1, "終わらない夏は\nないけれど…\nつい願ってしまうな", 101, 0, 4, 6321, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122501, 1, "胸を張って彼と\n並び立てるよう\n私も、もっと強く…！", 102, 0, 8, 6322, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122501, 1, "どうしたの？\n行きたい場所が\nあるなら付き合おうか", 1, 0, 1, 6323, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122501, 1, "快晴で風は穏やか…\n絶好の釣り日和だね\nさあ、出かけようか", 2, 0, 1, 6324, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122501, 1, "今日の予定を\n立ててるんだけど…\nキミの希望も\n聞きたいな", 3, 0, 1, 6325, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122501, 1, "ビーチスポーツか…\nキミとなら\nいい勝負ができそうだ", 4, 0, 1, 6326, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122501, 1, "私たちの夏に\n遠慮は無用だよ\n思いきり楽しもう！", 101, 0, 4, 6327, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122501, 1, "いつかまた一緒に\n花火を見に行こう\n…約束だよ？", 102, 0, 8, 6328, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122501, 1, "潮騒の音で\n目覚めるのも\nいいものだね", 1, 1, 1, 6329, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122501, 1, "今朝釣ってきた魚が\nあるんだ。これで\nお昼ご飯にしようか", 2, 2, 1, 6330, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122501, 1, "夜にもイベントが\nあるみたいなんだ\n一緒に見に行かない？", 3, 3, 1, 6331, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122501, 1, "気持ちが安らぐね…\nうっかり眠って…\nしまい…そうだ…", 2, 0, 1, 6333, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122501, 1, "肌触りのよさが\nこの格好だと\nよく分かるよ", 3, 0, 1, 6334, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122501, 1, "海をイメージさせる\n香りだね…\n爽やかで落ち着くよ", 4, 0, 1, 6335, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122501, 1, "濡れたままだと\n風邪を引いてしまうよ\nほら、キミもおいで", 5, 0, 1, 6336, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122501, 1, "夏を楽しみきるには\nエネルギーが\n必要だからね", 7, 0, 1, 6338, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122501, 1, "大切な人と過ごせる\n観光スポット紹介…\n…こ、このページは\nもういいかな！", 8, 0, 1, 6339, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122501, 1, "濡らしてもいいなら\n海に持っていって\n乗るのもよさそうだ", 10, 0, 1, 6341, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122501, 1, "こういう名画を\n見た気がするな…\nこんな視点なんだね", 11, 0, 1, 6342, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122401, 1, "水着って\n動きやすくて\nいいよねー♪\nいっちにーさんしー♪", 1, 0, 1, 6343, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122401, 1, "ユイちゃんにもらった\n日焼け止め\n塗っておいて\nよかった～", 2, 0, 1, 6344, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122401, 1, "なんだか暑いなあ…\nせっかくだし\nプールで泳いで\nこようかな？", 3, 0, 1, 6345, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122401, 1, "この格好で\n出かけようとしたら\nレイさんに\n怒られちゃった…", 4, 0, 1, 6346, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122401, 1, "たとえ水着姿でも\n困ってる人を見たら\n助けに行くからね！", 5, 0, 1, 6347, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122401, 1, "バカンス楽しかった～\nまた騎士くんと\n一緒に行きたいな♪", 101, 0, 4, 6348, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122401, 1, "この水着、あたしに\n似合ってるかなあ？\n騎士くんはどう\n思ってるんだろ…", 102, 0, 8, 6349, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122401, 1, "騎士くん！\n海に行くなら\n準備運動\n忘れずにね！", 1, 0, 1, 6350, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122401, 1, "泳ぐ練習がしたいの？\nいいよ！　水着だから\nなにかあったらすぐに\n助けてあげるね！", 2, 0, 1, 6351, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122401, 1, "騎士くんの水着姿も\nかっこよかったよ！\nまた遊びに行こ！\n約束ね！", 3, 0, 1, 6352, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122401, 1, "このグローブの向日葵\nかわいいでしょ？\nユイちゃんが\n選んでくれたんだ♪", 4, 0, 1, 6353, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122401, 1, "実はこの水着\nいつも着てる\n服とおなじなんだ…\nって、冗談だよ？", 101, 0, 4, 6354, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122401, 1, "変な日焼けしてないか\n見てもらってもいい？\n…なし！　今のなし！\nやっぱりなしー！", 102, 0, 8, 6355, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122401, 1, "夏のお日様に\n負けないよう\n今日も元気いっぱい\nがんばるぞー！", 1, 1, 1, 6356, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122401, 1, "お腹減ってきちゃった\n騎士くんはお腹\n空いてない？\n…聞こえちゃった♪", 2, 2, 1, 6357, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122401, 1, "今日も一日\n楽しかったー！\n明日もまた\n楽しもうね！", 3, 3, 1, 6358, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122401, 1, "ふわあ…\nふわふわなのが\nいつもより\nよくわかるよー…", 2, 0, 1, 6360, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122401, 1, "そうだ！　水着なら\n濡れてもいいし\n今度洗ってあげるね♪", 3, 0, 1, 6361, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122401, 1, "んー…いい匂い♪\nなんだか\n身体がぽかぽか\nしてきちゃった！", 4, 0, 1, 6362, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122401, 1, "水着だから、えっと…\n暖か涼しい…！\n不思議な感じー", 5, 0, 1, 6363, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122401, 1, "思いっきり身体を\n動かした後だから\nいくらでも\n食べちゃえるよー！", 7, 0, 1, 6365, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122401, 1, "ふむふむ…\nなるほどー\n水着の種類って\nそんなにあるんだぁ…", 8, 0, 1, 6366, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122401, 1, "乗り心地バッチリだね\nしゅっぱつしんこー！\nなんちゃって♪", 10, 0, 1, 6368, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122401, 1, "わっ、ぽよんぽよん！\nトランポリンみたいで\nた～のし～！", 11, 0, 1, 6369, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101501, 1, "彼のことを考えてると\n幸せで胸が温かくなる\n…私ったら、もう", 103, 0, 12, 6370, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101501, 1, "うふふふふ\nくすぐったいわ～♪\nもう、イタズラっ子ね\n次は先生の番よ～♪", 103, 0, 12, 6371, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122601, 1, "騎士クンの汗\n拭いてあげたいな…\n余計なお世話かな…？", 1, 0, 1, 6372, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122601, 1, "夏バテ気味かも…\nそうだ、今夜は\nかば焼きにしよう♪", 2, 0, 1, 6373, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122601, 1, "青い空、白い雲\n眩しい日差し…！\n夏って好きだな…", 3, 0, 1, 6374, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122601, 1, "ヒリヒリする…？\n日に焼けちゃったかな\nふー…ふー…", 4, 0, 1, 6375, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122601, 1, "花火大会があるんだ…\n夜、浜辺…2人きり…\nむ、無理だよ〜！", 5, 0, 1, 6376, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122601, 1, "水着って不思議…\n引っ込み思案が少し\nおさまる…気がする", 101, 0, 4, 6377, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122601, 1, "騎士クンとまた\n海に行きたいな…\nまた、一緒に…", 102, 0, 8, 6378, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122601, 1, "ダ、ダイエット！？\nしてないよ！？\n元々これ位だよ…？", 1, 0, 1, 6379, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122601, 1, "スイカ割りのコツ？\nわたしは、騎士クンに\n教わりたいな…", 2, 0, 1, 6380, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122601, 1, "騎士クン。うちわで\n扇いであげるね\nどこに置いたかな…", 3, 0, 1, 6381, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122601, 1, "うぅ…暑いね…\n騎士クン、温度計は\n見ない方がいいよ～", 4, 0, 1, 6382, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122601, 1, "じ、じっと見ないで…\nええっと…ちょっと！\nちょっとならいいよ…", 101, 0, 4, 6383, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122601, 1, "え、肝試し？\nう〜ん、うう〜ん…\n…騎士クンとなら！", 102, 0, 8, 6384, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122601, 1, "おはよう騎士クン\n見て、あっちの空\nもう入道雲が出てる", 1, 1, 1, 6385, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122601, 1, "夕立、来るかなぁ…\nもし雷が鳴ったら\nそばにいてくれる…？", 2, 2, 1, 6386, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122601, 1, "しー。…ほら、虫の声\n…わ、わたしの心臓の\n音は気にしないで…！", 3, 3, 1, 6387, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122601, 1, "ふふ、水着でゴロゴロ\nするのってなんだか…\nいけないことみたい", 2, 0, 1, 6389, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122601, 1, "かわいい～♪\n日光浴させて\nあげたいな♪", 3, 0, 1, 6390, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122601, 1, "ほんのり潮の香りが…\n香ばしくて塩辛くて…\nあ…おなか鳴っちゃう", 4, 0, 1, 6391, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122601, 1, "水着、乾かせるかも…\nそうだ、みんなの分も\n持ってこよう！", 5, 0, 1, 6392, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122601, 1, "夏バテなんてしてたら\nみんなの足を\n引っ張っちゃうよね\nいただきます！", 7, 0, 1, 6394, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122601, 1, "騎士クンの、どうして\n海はしょっぱいの？\nって質問の答えは…\n分かんないよぉ～", 8, 0, 1, 6395, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122601, 1, "顔は怒ってるのに\nなんだかかわいい…\n誰かに似てる…？", 10, 0, 1, 6397, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122601, 1, "これを枕に、ビーチで\n騎士クンと…なんて…\nなんて…なんて…！", 11, 0, 1, 6398, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122901, 1, "夏休み、\n毎日たのしいな～♪\nつぎはなにしよう？", 1, 0, 1, 6399, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122901, 1, "自由研究、\nなににしよっかな～？\nプチグリフォンさんの\nかんさつにっきとか？", 2, 0, 1, 6400, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122901, 1, "夏休みって、\n給食ないから…\nそれはちょっぴり\nさみしいかも…？", 3, 0, 1, 6401, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122901, 1, "砂浜をはだしで歩くの\nきもちいいよね♪\nくすぐったいけど\nミミ、好きなんだ～♪", 4, 0, 1, 6402, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122901, 1, "きれいな貝がら\n見つけちゃった♪\nミミのたからもの！", 5, 0, 1, 6403, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122901, 1, "ミミ、浴衣を\n買ってもらったんだ♪\n今度、おにいちゃんに\n見せてあげたいな！", 101, 0, 4, 6404, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122901, 1, "ミミ、おにいちゃんと\nいっぱい夏休みの\n思い出つくりたいな♪", 102, 0, 8, 6405, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122901, 1, "ねえ、おにいちゃん…\n夏休みの宿題、\nわかんないとこ\nおしえてくれる…？", 1, 0, 1, 6406, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122901, 1, "おかあさんにね、\n麦茶つくるの上手って\nほめられたんだ～♪\n飲んでみて♪", 2, 0, 1, 6407, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122901, 1, "夏休みの宿題で\nポスターかくんだ～♪\nおにいちゃんを\nかいてもいい？", 3, 0, 1, 6408, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122901, 1, "線香花火やりたいな♪\nミミ、長持ちさせるの\n上手なんだよ♪", 4, 0, 1, 6409, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122901, 1, "おにいちゃんの\n好きな本ってなあに？\nその本で読書感想文\n書こうかな♪", 101, 0, 4, 6410, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122901, 1, "海であそんだ後ね、\nおにいちゃんに\n髪を拭いてもらうの\nすきなんだ～♪", 102, 0, 8, 6411, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122901, 1, "夏休みって、\nワクワクして\n早起きしちゃうよね♪", 1, 1, 1, 6412, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122901, 1, "わぁ♪　お昼は、\n流しそうめんなんだ？\nミミ、流してみたい♪", 2, 2, 1, 6413, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122901, 1, "こわい話聞いて、\nねむれないの…\nねえ、おにいちゃん、\nいっしょに寝ていい？", 3, 3, 1, 6414, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122901, 1, "ちょっぴりやすんだら\nまた、いっしょに\n海であそぼうね♪", 2, 0, 1, 6416, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122901, 1, "すっごくかわいいね♪\nこの子のこと、\n絵日記にかこうっと♪", 3, 0, 1, 6417, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122901, 1, "くんくん…\nスイカのにおいだね♪\nこのにおいだいすき！", 4, 0, 1, 6418, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122901, 1, "およいだあとは、\nちゃんと体を\nあっためなくちゃね！", 5, 0, 1, 6419, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122901, 1, "夏休みのご飯って\nなんだかとくべつ\nおいしい気がするね♪", 7, 0, 1, 6421, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122901, 1, "夏の星座って\nすっごくきれいだね♪\nいつかみんなと\nいっしょに見たいな♪", 8, 0, 1, 6422, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122901, 1, "わぁ♪　この子、\nとってもかわいいね！\nおともだちに\nなりたいな♪", 10, 0, 1, 6424, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122901, 1, "ミミ、人魚姫に\nなったみたい！\nおにいちゃん、\nとなりにすわって♪", 11, 0, 1, 6425, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122801, 1, "水着姿になると\nバカンス気分を\n味わえちゃうね！", 1, 0, 1, 6426, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122801, 1, "海に行くの待ちきれ\nなくて、もう水着に\n着替えちゃった！", 2, 0, 1, 6427, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122801, 1, "海に行ったら\nずぶぬれ系のイタズラ\nたくさんしかけよう！", 3, 0, 1, 6428, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122801, 1, "部屋の中で泳ぎの練習\nしよう！　にいちゃん\nも水着に着替えて！", 4, 0, 1, 6429, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122801, 1, "お日さまとお揃いの\n水着なんだ～♪\n似合ってるかなぁ？", 5, 0, 1, 6430, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122801, 1, "海といえば冒険！\n海底に眠る財宝を\n見つけちゃおう！", 101, 0, 4, 6431, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122801, 1, "にいちゃんと一緒だと\n部屋にいても海気分♪\nスイカわりしよっか！", 102, 0, 8, 6432, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122801, 1, "にいちゃん！\n早く海に行く準備\nしなよ～！", 1, 0, 1, 6433, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122801, 1, "ねーにいちゃん\n何秒息止められるか\n勝負しよーよ！", 2, 0, 1, 6434, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122801, 1, "にいちゃんはアイスと\nかき氷どっちが好き？\nミソギは…どっちも！", 3, 0, 1, 6435, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122801, 1, "日焼け止めクリーム\nキョウカに\nもらったんだ～", 4, 0, 1, 6436, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122801, 1, "水中メガネがあれば\n水鉄砲くらっても\n平気だね！", 101, 0, 4, 6437, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122801, 1, "水着なら、毎日夏休み\n気分！　にいちゃんも\n着てみなよ～！", 102, 0, 8, 6438, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122801, 1, "にいちゃんおはよー！\n早く顔洗って、\nいっぱい遊んじゃお！", 1, 1, 1, 6439, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122801, 1, "にいちゃんデザートは\nイチゴのかき氷だよ！\n…激辛ってバレた？", 2, 2, 1, 6440, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122801, 1, "今日は楽しかった～！\n明日もイタズラ日和に\nなるといいな！", 3, 3, 1, 6441, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122801, 1, "落とし穴の下に\nやわらかいの置けば\nケガしないよね！", 2, 0, 1, 6443, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122801, 1, "かわいいね～！\nこの子も海に\n連れてっちゃおうよ！", 3, 0, 1, 6444, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122801, 1, "このにおいを利用して\nイタズラするには…\nにいちゃんどう思う？", 4, 0, 1, 6445, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122801, 1, "水着を着て暖炉で\nあったまるのって、\n変でおもしろいね～！", 5, 0, 1, 6446, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122801, 1, "海辺で食べる\nお昼ごはんって最高♪\nってことで海行こう！", 7, 0, 1, 6448, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122801, 1, "いつか海を越えて、\n地図にも載ってない\n場所を見つけたいな！", 8, 0, 1, 6449, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122801, 1, "この口から水が出たら\nみんな驚くよね！\nぷぷ…細工しちゃお！", 10, 0, 1, 6451, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122801, 1, "変な音がするイタズラ\nを仕込んどいて…\n誰か座らないかな～♪", 11, 0, 1, 6452, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 102701, 1, "あなた様と私の愛を\n周りに見せつけて\n差し上げましょう♪", 103, 0, 12, 6453, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102701, 1, "呼びましたか？\n呼びましたね？\nでは愛を語りましょう\nそうしましょう", 103, 0, 12, 6454, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122701, 1, "水着で歩き回るのは\n恥ずかしいな…\n誰も見てないよね？", 1, 0, 1, 6455, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122701, 1, "夏はやっぱり\nかき氷ですよね\n何色がいいかなぁ…♪", 2, 0, 1, 6456, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122701, 1, "あの人、泳げないって\n聞きました！\n私が教えてあげないと", 3, 0, 1, 6457, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122701, 1, "夏休みの宿題は\n計画的に進めましょう\n当たり前のことです！", 4, 0, 1, 6458, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122701, 1, "防犯魔石、どこに\n着けておこうかなぁ\n…ここにしようかな", 5, 0, 1, 6459, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122701, 1, "海の楽しみ方は\n予習済みです！\nあとは実践ですね♪", 101, 0, 4, 6460, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122701, 1, "水着…きっとあの人に\n見られちゃいますよね\n…褒めてくれるかな…", 102, 0, 8, 6461, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122701, 1, "触りたいんですか？\n…いいですよ\n帽子くらいなら", 1, 0, 1, 6462, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122701, 1, "夏は体調管理に\n気をつけましょう\n私が管理してあげます", 2, 0, 1, 6463, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122701, 1, "海に行くのが楽しみで\nたまらないんですか？\n私はそこまで…嘘です", 3, 0, 1, 6464, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122701, 1, "いつもより薄着だから\nなんだか恥ずかしい…\nみ、見ないでください", 4, 0, 1, 6465, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122701, 1, "へんたいふしんしゃ～\nえっ？　ああ、これは\n海に叫ぶ練習です", 101, 0, 4, 6466, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122701, 1, "あなたと一緒に\n海に行くなんて…\nなんだか大人…えへへ", 102, 0, 8, 6467, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122701, 1, "おはようございます\nさぁ、海に行って\n私と運動しましょう", 1, 1, 1, 6468, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122701, 1, "あっつ～い…\nはっ！？　ダメですよ\nシャキッとしないと！", 2, 2, 1, 6469, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122701, 1, "明日も海で\n遊ぶために、しっかり\nお休みしましょうね", 3, 3, 1, 6470, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122701, 1, "夏を楽しむときは\n休憩も大事ですよね\n計画的に、計画的に！", 2, 0, 1, 6472, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122701, 1, "かわいいなぁ～\n海のお土産屋さんに\n売ってるかなぁ？", 3, 0, 1, 6473, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122701, 1, "大人な香りがします\n私も違いが分かる\n素敵な女性になります", 4, 0, 1, 6474, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122701, 1, "濡れた体を\n乾かしましょう\nタオル、タオル…", 5, 0, 1, 6475, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122701, 1, "泳いだ後は\n栄養補給…だけど\n…おなか膨らんじゃう", 7, 0, 1, 6477, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122701, 1, "遊ぶだけじゃなくて\nお勉強もしてますよ\nあなたもどうですか？", 8, 0, 1, 6478, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122701, 1, "ミミが喜びそう…♪\nわ、私ははしゃいだり\nしませんけど…っ！", 10, 0, 1, 6480, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 122701, 1, "ミソギがいたずらに\n使いそう…しっかりと\n試しておかないと！", 11, 0, 1, 6481, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123001, 1, "あたしがまさか\nここにいるなんて…\n自分でも不思議だわ", 1, 0, 1, 6482, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123001, 1, "昔みたいにあちこち\n飛び回れないのって\nちょっと不便かも", 2, 0, 1, 6483, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123001, 1, "こうして誰かと一緒に\n暮らせるのもあいつや\nみんなのおかげね", 3, 0, 1, 6484, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123001, 1, "ここで厄介になるなら\nあたしも家事とか\n手伝わないと…", 4, 0, 1, 6485, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123001, 1, "…だめ！　誰かの\n世話をせずには\nいられないわ！", 5, 0, 1, 6486, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123001, 1, "あいつたちが\n帰ってくるところだし\n綺麗にしないと…", 101, 0, 4, 6487, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123001, 1, "せっかくあたしが\nいるっていうのに\n少しは構いなさいよ…", 102, 0, 8, 6488, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123001, 1, "おいしい店とか\n知らないの？\n食べに行きましょ", 1, 0, 1, 6489, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123001, 1, "あんた暇でしょ？\nあたしのやること\n手伝いなさい", 2, 0, 1, 6490, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123001, 1, "コッコロたんと\n遊んでくるから\n留守番よろしく♪", 3, 0, 1, 6491, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123001, 1, "あらおかえり…って\nボロボロじゃない！\nまた人助けしてたの？", 4, 0, 1, 6492, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123001, 1, "やっぱりあたしは\nあんたのそばが\n一番落ち着くみたい", 101, 0, 4, 6493, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123001, 1, "どこまでだって\nあんたと一緒に\nいてやるんだから", 102, 0, 8, 6494, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123001, 1, "ふわぁ…おはよう\n…大きくなったせいか\n朝は特に体が重いわ", 1, 1, 1, 6495, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123001, 1, "お昼ご飯が食べたい？\nしょうがないわね…\nなんか作って\nあげるから", 2, 2, 1, 6496, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123001, 1, "あんたまだ成長期だし\nさっさと寝なさい\n大きくなれないわよ？", 3, 3, 1, 6497, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123001, 1, "たまにはあたしも\nゆっくりしないと\nもたないのよ…", 2, 0, 1, 6499, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123001, 1, "かわいいじゃな～い♪\nコッコロたんに\nプレゼントしたいわ", 3, 0, 1, 6500, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123001, 1, "いい香りね\n塔にもこういうの\n置いておこうかしら", 4, 0, 1, 6501, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123001, 1, "炎か…前は\n羽が焼けそうで\n怖かったっけ", 5, 0, 1, 6502, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123001, 1, "あむ…妖精に\n体重増減の設定って\nあったっけ…？", 7, 0, 1, 6504, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123001, 1, "記憶のデータと違って\n物語はいいわね\n結末が読めなくて", 8, 0, 1, 6505, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123101, 1, "冒険のお土産\nみんなにおすそわけ\nせなあきまへんな", 1, 0, 1, 6507, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123101, 1, "おとぎ話よりも\n不思議なことが\nあるもんやねぇ", 2, 0, 1, 6508, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123101, 1, "冒険しに出かけるなら\n動きやすい服装に\nせななぁ", 3, 0, 1, 6509, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123101, 1, "地図に水筒に…\n他に忘れもんは\nあらへんやろか？", 4, 0, 1, 6510, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123101, 1, "これまでの冒険のこと\nノートに書いて\nまとめてますんよ", 5, 0, 1, 6511, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123101, 1, "王子はんとの冒険は\nどれもマホマホ王国の\n大事な歴史や♪", 101, 0, 4, 6512, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123101, 1, "どこへ冒険するときも\nうちと王子はんは\n一緒どす♪", 102, 0, 8, 6513, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123101, 1, "魔法で火ぃつける\n練習してますんよ～\nよう爆発しますけど…", 1, 0, 1, 6514, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123101, 1, "うちと王子はんで\n冒険道具をお揃いに\nするんもええなぁ♪", 2, 0, 1, 6515, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123101, 1, "テント張りも随分\n上達したんよ\n王子はんのおかげやね", 3, 0, 1, 6516, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123101, 1, "ランスロットたちの\n新しい冒険衣装\n思いついたんよ♪", 4, 0, 1, 6517, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123101, 1, "迷子になっても\n王子はんと一緒なら\nきっと大丈夫やね♪", 101, 0, 4, 6518, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123101, 1, "王子はんのこと、\nきっちり守れるように\nなってみせますえ", 102, 0, 8, 6519, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123101, 1, "お寝坊さんは\n布団めくって\n起こしにいきますえ～", 1, 1, 1, 6520, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123101, 1, "支度が終わったら\n今日の冒険に\n出かけまひょか", 2, 2, 1, 6521, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123101, 1, "もうちょっと\nおしゃべりしたいん？\nあと少しだけやで♪", 3, 3, 1, 6522, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123101, 1, "冒険で疲れた体に\nこの癒しぱわーは\nあきまへん…すや…", 2, 0, 1, 6524, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123101, 1, "ふふ、あんさんも\nうちと一緒に\n冒険します？", 3, 0, 1, 6525, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123101, 1, "虫除けのアロマは\n冒険の必需品どす\n忘れんようにせんと", 4, 0, 1, 6526, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123101, 1, "うちも魔法使わんでも\n火ぃ起こせるよう\n練習せんとあかんなぁ", 5, 0, 1, 6527, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123101, 1, "この料理…\n冒険するときに\n作ってみまひょか", 7, 0, 1, 6529, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123101, 1, "王子はんとの\n愛と冒険のおとぎ話…\nはぁ…素敵やわぁ♪", 8, 0, 1, 6530, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103101, 1, "あの人との\n最良の未来のために\n占いも霊能力ももっと\n精進しなきゃ", 103, 0, 12, 6532, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103101, 1, "あなたともっと深く\nつながりたいです\n絆も、そして魂までも", 103, 0, 12, 6533, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104401, 1, "絆の契約…\nあやつが望むのなら\n考えぬでもないがのう", 103, 0, 12, 6534, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104401, 1, "なんじゃ？\nわらわの姿に\n見惚れたかの？", 103, 0, 12, 6535, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123201, 1, "旅の荷物って多いな…\nねぇぷうきち～\nすこしは持ってよぉ～", 1, 0, 1, 6536, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123201, 1, "傘はなくてもいっか\nぷうきちがいるし♪\n『おいなんだと…？』", 2, 0, 1, 6537, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123201, 1, "旅先で人見知りなんか\nしてちゃだめだよね…\nまずはあいさつだ…！", 3, 0, 1, 6538, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123201, 1, "おみやげはやっぱり\nご当地ぬいぐるみ\nだよね♪　ワクワク…", 4, 0, 1, 6539, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123201, 1, "知らない場所かぁ…\nうぅ…考えただけで\nすでにホームシック…", 5, 0, 1, 6540, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123201, 1, "野宿するってことは…\nお兄ちゃんのマントで\n寝るチャンスかも…！", 101, 0, 4, 6541, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123201, 1, "どんなに危険な冒険も\nお兄ちゃんと一緒なら\n大丈夫！　…だよね？", 102, 0, 8, 6542, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123201, 1, "ぷうきちのアイマスク\nも腹巻も持ってくの！\n旅の必需品なの～！", 1, 0, 1, 6543, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123201, 1, "これ全部背負うのって\nけっこうむずかしいの\n順番まちがえると大変", 2, 0, 1, 6544, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123201, 1, "えへへ～\nなでなでして？　帽子\nの上からでもいいよ♪", 3, 0, 1, 6545, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123201, 1, "『悪いが絆創膏の部分\nはアンタッチャブルだ\n綿が飛び出ちまう』", 4, 0, 1, 6546, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123201, 1, "お兄ちゃんと一緒に\nもっともっといろんな\n景色が見たいな！", 101, 0, 4, 6547, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123201, 1, "旅って、終わる時は\nきっとさびしいよね…\nお兄ちゃん、帰り道は\n手をつないでもいい？", 102, 0, 8, 6548, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123201, 1, "出発が楽しみすぎて\n寝られなかった…\n『旅行のお約束だな』", 1, 1, 1, 6549, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123201, 1, "わぁ　いいお天気！\n旅の間もずーっと\nぴーかんだといいな♪", 2, 2, 1, 6550, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123201, 1, "知らない場所で寝るの\n怖いな…寝る時だけ\n帰ってこようかな…", 3, 3, 1, 6551, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123201, 1, "旅先にはクッションも\nソファもないよね…\n『俺でいいだろ』", 2, 0, 1, 6553, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123201, 1, "ぬいぐるみのみんな\n旅の無事を\n祈ってて…！", 3, 0, 1, 6554, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123201, 1, "旅先の香りも\n思い出に持って\n帰れたらいいのにな～", 4, 0, 1, 6555, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123201, 1, "旅行用に携帯用暖炉\nがあるといいかも…！\n『まだ荷物増やす？』", 5, 0, 1, 6556, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123201, 1, "『もう出発だぜ？』\nえーまってまって！\n腹ごしらえがまだ…", 7, 0, 1, 6558, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123201, 1, "世界ってこんなに広い\nんだ…！　はやく自分\nの目で見てみたいな～", 8, 0, 1, 6559, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123301, 1, "はちゃめちゃヒマ～\nなーんか楽し～コト\nないかなー", 1, 0, 1, 6561, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123301, 1, "ここ、いートコだねー\nこんどカリザきゅんも\nつれてこよー", 2, 0, 1, 6562, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123301, 1, "かわゆいショタが\n見た～い♪\n触りた～～～い♪", 3, 0, 1, 6563, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123301, 1, "なんか服きっついなー\nちょい太ったかー？\nん～\nま・気のせーっしょ♪", 4, 0, 1, 6564, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123301, 1, "あ～やべ\nおねえちゃんに\nメッセすんの忘れたー\nまいっかー", 5, 0, 1, 6565, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123301, 1, "パンケーキ食いてー\n騎士きゅん\n誘ってみよーっと♪", 101, 0, 4, 6566, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123301, 1, "きょーも～\n好きぴに囲まれて～\nシ・ア・ワ・セ♪", 102, 0, 8, 6567, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123301, 1, "アタシと遊ばな～い？\nってなんか\nナンパみスゴー\nウケる～", 1, 0, 1, 6568, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123301, 1, "んー？\n今はだるいからパース\nあとでねー", 2, 0, 1, 6569, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123301, 1, "カリザきゅん\n見なかった～？\nおねえちゃんから\n逃げるとかオシオキー", 3, 0, 1, 6570, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123301, 1, "ネイルしたいけど\n無理なんよねー\n爪の間にめっちゃ\nスライム入っからさー", 4, 0, 1, 6571, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123301, 1, "おねえちゃんに\nかまってほし～の？\nかわゆ～い♪", 101, 0, 4, 6572, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123301, 1, "な～に騎士きゅん♪\nここでイチャイチャ～\nし・ちゃ・う～？", 102, 0, 8, 6573, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123301, 1, "ねんむ～\nけど外から\nショタの声したんで～\nつい起きちった～", 1, 1, 1, 6574, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123301, 1, "ランチの後はやっぱ\nデザートっしょー\nベツバラってヤツ～♪", 2, 2, 1, 6575, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123301, 1, "こんな時間まで\n起きてるコは～\n食べちゃうぞ～♪", 3, 3, 1, 6576, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123301, 1, "あーラクー\nいっしょーここで\nキューケーできるわー", 2, 0, 1, 6578, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123301, 1, "なにこれ～\nかわいいじゃ～ん\nショタの次くらいに～", 3, 0, 1, 6579, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123301, 1, "いーニオイする～\nなんのニオイとかー\nよくわかんないけどー", 4, 0, 1, 6580, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123301, 1, "あったか～♪\n溶けてスライム\n戻りそ～", 5, 0, 1, 6581, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123301, 1, "ン・ン・ン～♪\nお・い・し～♪\nも～っと食べるぅ～♪", 7, 0, 1, 6583, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123301, 1, "わ～なんかムズー\n読めなー\nウケるー", 8, 0, 1, 6584, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123501, 1, "あんパンって\nお菓子に入るのかな？\nハロウィンで\nもらえたりしない？", 1, 0, 1, 6586, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123501, 1, "お菓子をくれなきゃ\nぐうたらするぞ～！\nハロウィン以外でも！", 2, 0, 1, 6587, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123501, 1, "お化けになったら\n仕事しないで\nゴロゴロし放題\nなのかな？", 3, 0, 1, 6588, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123501, 1, "おっと…この仮装\n袖がすごく長いからさ\n引っ掛けないように\n気を付けないとね～", 4, 0, 1, 6589, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123501, 1, "イタズラするのも\n体力がいるよね～\n楽にできそうなの\n何かないかな？", 5, 0, 1, 6590, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123501, 1, "あいつって\n仮装とかするのかな？\n似合いそうなのは\nどんなお化けだろ？", 101, 0, 4, 6591, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123501, 1, "ずっとハロウィンなら\nあいつと遊び放題\nなんだけどな～", 102, 0, 8, 6592, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123501, 1, "今の街はハロウィン\n一色だからね～\n人混みが大変だよ", 1, 0, 1, 6593, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123501, 1, "この仮装、どうかな？\n気に入ったなら\n来年もしてあげるよ～", 2, 0, 1, 6594, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123501, 1, "実はこの長い袖\nドングリとかを\nため込めるんだよね…", 3, 0, 1, 6595, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123501, 1, "カボチャランタンが\nあるんなら\nドングリランタンも\nあっていいよね～♪", 4, 0, 1, 6596, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123501, 1, "ハロウィンでも\nやる気があれば\nぐうたらできる…\nうん、いいこと言った", 101, 0, 4, 6597, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123501, 1, "一緒にお菓子食べよ？\n寝転びながら堪能する\nハロウィンスイーツは\n最高だからね～", 102, 0, 8, 6598, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123501, 1, "お化けは寝る時間…\n今のあたしはお化け…\nだから、おやすみ～…", 1, 1, 1, 6599, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123501, 1, "お昼か～…\n食後のデザートは\nハロウィンのお菓子で\n決まりだね～♪", 2, 2, 1, 6600, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123501, 1, "ふぁ～、寝よ…\nお化けは夜に\n出るものだけど\nあたしはリスだからね", 3, 3, 1, 6601, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123501, 1, "ハロウィンを楽しむ\nためにも、ぐっすり\n寝とかないとね～", 2, 0, 1, 6603, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123501, 1, "…袖から手が\n出せないから\nこのまま撫でよう\nよしよし", 3, 0, 1, 6604, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123501, 1, "リラックスできる\n素敵な香りだね～\n成仏しそうだよ～", 4, 0, 1, 6605, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123501, 1, "あったかいね～\nここで寝てもいい？\n…着替えないとダメか", 5, 0, 1, 6606, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123501, 1, "もぐもぐ…\nお化けもリスも\nお腹は空くんだよ", 7, 0, 1, 6608, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123501, 1, "キョンシーって\nどんなお化けなのか\n本で調べようかな？", 8, 0, 1, 6609, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123501, 1, "より取り見取りだね～\nとりあえず、これと\nこのお菓子はキープで", 12, 0, 1, 6611, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123601, 1, "よし、衣装もばっちり\nハロウィンパレードへ\n出かけよう♪", 1, 0, 1, 6613, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123601, 1, "仮装してても\n剣は手放せないね\nほとんど体の一部だよ", 2, 0, 1, 6614, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123601, 1, "見回りに誘うふりして\n二人でパレードに\n参加しちゃおうかな♪", 3, 0, 1, 6615, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123601, 1, "…あれ？　手錠の鍵\n落としちゃった…？\nやば、出かける前に\n見つけないと…！", 4, 0, 1, 6616, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123601, 1, "お家でコスプレも\nこれはこれで楽しいな\n決めポーズの練習\nたくさんしておこう！", 5, 0, 1, 6617, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123601, 1, "他にも、お姫様に\nメイドさんに学生服…\nやりたいコスプレが\n多すぎるよ～", 101, 0, 4, 6618, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123601, 1, "漫画もゲームも好き！\n仮装するのも大好き！\n…好きを言葉にすると\nもっと好きになる♪", 102, 0, 8, 6619, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123601, 1, "ずっと目が合ってるね\n見つめてくれるのは\nいいんだけど、今日は\n衣装を見てほしいな♪", 1, 0, 1, 6620, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123601, 1, "トリックオアトリート\nお菓子をくれなきゃ\n逮捕しちゃうぞ♪", 2, 0, 1, 6621, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123601, 1, "あなたにだけはこの姿\nいつでも見せてあげる\nふふ、なんちゃって♪", 3, 0, 1, 6622, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123601, 1, "どうしたの？\nイタズラするなら\n隙を突かなきゃ\nこんなふうに、ね♪", 4, 0, 1, 6623, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123601, 1, "コスプレしてても\n剣の稽古は休まないよ\nちょっと相手になって\nくれるかな？", 101, 0, 4, 6624, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123601, 1, "ひゃっ、びっくりした\n急に触らないでよ～\nハロウィンだからって\nイタズラしないでね？", 102, 0, 8, 6625, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123601, 1, "おはよう！\n今日も朝から\nパトロールに行こう！", 1, 1, 1, 6626, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123601, 1, "カボチャのケーキを\n用意してるよ！\nデザートを食べて\n午後も頑張ろう！", 2, 2, 1, 6627, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123601, 1, "暗くなってきたね\nカボチャのランタンに\n火を灯そうよ♪", 3, 3, 1, 6628, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123601, 1, "ふかふか気持ちいい～\nあっでも衣装がシワに\nなっちゃうかも…？\n…でも気持ちいい～", 2, 0, 1, 6630, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123601, 1, "…かわいい\nこの子もパレードに\n連れて行きたいな", 3, 0, 1, 6631, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123601, 1, "はぁ～癒される♪\nハロウィンの見回りが\n終わった後は、\nまったりくつろごう", 4, 0, 1, 6632, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123601, 1, "ここで二人寄り添って\nカボチャスープ飲もう\n…なんて想像したら\nポカポカしてきた…", 5, 0, 1, 6633, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123601, 1, "パレードに参加すると\n体力を使うから、\nいっぱい食べておこう", 7, 0, 1, 6635, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123601, 1, "この絵本のお姫様\nかわいいね\nこの仮装もいいな♪", 8, 0, 1, 6636, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123601, 1, "わあ、おいしそう！\n見回りに行く前に\n少しつまんじゃおう♪", 12, 0, 1, 6638, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103701, 1, "今日こそは、あの人に\n気持ちを打ち明けて…\nそのあとは…うう\nど、どうしよう？", 103, 0, 12, 6640, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103701, 1, "私が剣なら、あなたは\nさしずめ私の鞘…\nふふ、私の帰る\n場所ってことだよ♪", 103, 0, 12, 6641, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123701, 1, "やっぱり\nコスプレしてると\nテンションあがる～♪", 1, 0, 1, 6642, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123701, 1, "キャラコスにも\n興味あるけど…\nマジカル、ラブリー\nどっちにしよっかな～", 2, 0, 1, 6643, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123701, 1, "キャラコスといえば\nミスティーシャイニー\nもいいんだよねえ…\nうはー！　悩むー！", 3, 0, 1, 6644, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123701, 1, "ギルドハウスで\n宅コスしてる人\n実は結構\nいるんじゃないかな～", 4, 0, 1, 6645, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123701, 1, "コスプレだけど普段着\nって言いはれそう…\nって、ダメダメ！\nオタクは節度が大事！", 5, 0, 1, 6646, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123701, 1, "フェスで買ってきた\n薄い本～♪\nギルドハウスに持って\nきちゃおっかな～", 101, 0, 4, 6647, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123701, 1, "ようこそ\nコスプレ沼へ～\nなんちゃって♪", 102, 0, 8, 6648, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123701, 1, "どうどう？\nナナカちゃんの\nハロウィンコスプレ\n似合ってる？", 1, 0, 1, 6649, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123701, 1, "ハロウィン魔法少女\n参上カボ～！\n…やっぱ今の語尾\nナシで！", 2, 0, 1, 6650, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123701, 1, "ハロウィンといえば\nトリックオア～…\nコスプレ！\nなんちゃって", 3, 0, 1, 6651, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123701, 1, "ランフェスの\n戦利品～♪\nねえねえ\n見たい？　見たい？", 4, 0, 1, 6652, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123701, 1, "私にしてほしい\nコスプレがあったら\n教えて♪　君のために\nや～ってやるぜぇ！", 101, 0, 4, 6653, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123701, 1, "一緒にコスプレしたい\nならナナカちゃんは\nいつでも大歓迎だよ♪", 102, 0, 8, 6654, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123701, 1, "ふぁあ…ねむ\nって、ありゃりゃ　\n昨日コスプレしたまま\n寝ちゃってたかあ…", 1, 1, 1, 6655, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123701, 1, "今日のお昼は\nなんにしよ？\nカボチャ料理に\nしよっかな～", 2, 2, 1, 6656, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123701, 1, "もうこんな時間かあ\nそういえばオタクって\n夜行性の人が多い\n気がするぜぇ…", 3, 3, 1, 6657, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123701, 1, "ふっふっふ\nこのパニエは\n座るときはちゃんと\nふにゃってなるのさ！", 2, 0, 1, 6659, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123701, 1, "いや～\nかわいいものをみると\n癒されますな～", 3, 0, 1, 6660, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123701, 1, "新しい薄い本の匂いの\nアロマとか…ダメ？\n私はあの匂い、なんか\n好きなんだよね～", 4, 0, 1, 6661, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123701, 1, "暖か～い…\nけど近づきすぎて\n衣装が燃えないように\n気を付けなきゃ", 5, 0, 1, 6662, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123701, 1, "コス衣装のために\n体型維持は必須だけど\nついつい\n食べちゃうんだよね～", 7, 0, 1, 6664, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123701, 1, "おっ、次はこの本に\nのってるキャラのコス\nしてみよっかな？", 8, 0, 1, 6665, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123701, 1, "おおっ！\nお菓子がいっぱい！\nベルルちゃんチョコ\n入ってないかな～？", 12, 0, 1, 6667, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123801, 1, "むっ…\nこの耳の手触り…\n驚嘆に値する…！", 1, 0, 1, 6669, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123801, 1, "獲物を狩りまくる…\nそんなウサギがいても\nいいだろう？", 2, 0, 1, 6670, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123801, 1, "ワナなど怖くないぞ？\nむしろ積極的に\n踏んでいく性分だ", 3, 0, 1, 6671, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123801, 1, "一匹狼ならぬ\n一匹ウサギか\nどっちでもかまわん♪", 4, 0, 1, 6672, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123801, 1, "人間とて本を正せば獣\n本能に従えばよいのだ\n遠慮は無用さ☆", 5, 0, 1, 6673, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123801, 1, "脱兎のごとく…\n逃げるものか\n坊やを追い回すのさ♪", 101, 0, 4, 6674, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123801, 1, "獣の道は険しいが…\n坊やめ、音を上げずに\nちゃんとついてきたな\n褒めてやるとしよう♪", 102, 0, 8, 6675, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123801, 1, "ニンジンはいらん\n好物は好敵手だ\nさぁどんどんおくれ♪", 1, 0, 1, 6676, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123801, 1, "尻尾に触りたいだと？\nワタシの乱数聖域を\n崩せたらな…☆", 2, 0, 1, 6677, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123801, 1, "頭で考えるな\n体に率直になれ\n己を野放しにするのさ", 3, 0, 1, 6678, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123801, 1, "そうさモコフワさ♪\nなで心地は最高だが\n意外にどう猛だぞ…？", 4, 0, 1, 6679, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123801, 1, "ワタシにピョンピョン\n跳ねてほしいのか？\nあとでじっくり、な♪", 101, 0, 4, 6680, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123801, 1, "ワタシと番になるか？\nオマエさえよければ\nいつでもかまわんぞ☆", 102, 0, 8, 6681, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123801, 1, "太陽と共に目覚め\n活動する\nそれが獣の生き方さ☆", 1, 1, 1, 6682, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123801, 1, "昼寝をすればいい\nワタシが周囲を\n警戒していてやる", 2, 2, 1, 6683, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123801, 1, "暗くなれば眠るだけさ\nそうすれば余計な\nストレスなどたまらん", 3, 3, 1, 6684, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123801, 1, "むむっ！？\n尻尾がつっかえて\nうまく座れんな…", 2, 0, 1, 6686, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123801, 1, "愛らしさでワタシに\n張り合おうというのか\n５億年後に出直せ☆", 3, 0, 1, 6687, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123801, 1, "野生の芳香か…\n本能が呼び覚まされる\nいいセンスだ♪", 4, 0, 1, 6688, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123801, 1, "寒さも野生のうちだ\n…とはいえ\n温まっていくか♪", 5, 0, 1, 6689, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123801, 1, "悪くはないが物足りん\nこう見えて肉食なのだ\n肉はないのか肉は☆", 7, 0, 1, 6691, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123801, 1, "ウサギ族は地獄耳？\nワタシとの共通点だな", 8, 0, 1, 6692, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106401, 1, "ここが私の家…\nそれはこれからも\nずっと変わらないわ…", 103, 0, 12, 6694, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106401, 1, "ええと、その…今から\nデート…じゃなくって\n散歩でもどうかしら？\n…ふ、二人きりで…", 103, 0, 12, 6695, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123901, 1, "ギルドハウスに\nヒーロー参上！\nみんな仲良くッスよ！", 1, 0, 1, 6696, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123901, 1, "料理もできて\n鎧も作れる！\nお母さんはすごいッス", 2, 0, 1, 6697, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123901, 1, "ううう…\nまたマントをドアに\n挟んじゃったッス…", 3, 0, 1, 6698, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123901, 1, "ずっと鎧を着ていれば\nジュンさんに近づける\nかも？　燃えるッス！", 4, 0, 1, 6699, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123901, 1, "サインの練習…\nもっとしておいた\nほうがいいッスかね～", 5, 0, 1, 6700, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123901, 1, "半人前でも\n胸を張るッス！\nそのうち似合う日が\nくるッスから！", 101, 0, 4, 6701, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 123901, 1, "種族の違いも\n強い魂があれば\n超えられるッス！\n信じてるッスよ！", 102, 0, 8, 6702, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123901, 1, "困ったときは\n自分を呼ぶッス！\nあと楽しいときにも\n呼んでほしいッス！", 1, 0, 1, 6703, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123901, 1, "特訓でも行くッスか？\n望むところッス！\n目指せ、一人前！", 2, 0, 1, 6704, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123901, 1, "新しいショーの台本を\n読みこんでるッス！\nところで…この言葉の\n意味、分かるッスか？", 3, 0, 1, 6705, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123901, 1, "ヒミツがなくなると\nなんか恥ずかしいッス\nあんたはどうッスか？", 4, 0, 1, 6706, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123901, 1, "ヒーローは遅れて\nやってくる…\nあんたもそうッスね♪", 101, 0, 4, 6707, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123901, 1, "誰よりも早く\nあんたに見せたいッス\n一人前になった自分！", 102, 0, 8, 6708, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123901, 1, "うーん、今日も\nよく眠れたッス～！\n絶好調、間違いなし！", 1, 1, 1, 6709, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123901, 1, "おお、もうお昼ッスか\nどうりでお腹が空くと\n思ったッス～えへへ", 2, 2, 1, 6710, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 123901, 1, "パトロールのあとは\n歯みがきに宿題に…\n大忙しッスね、夜！", 3, 3, 1, 6711, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123901, 1, "こんなにリラックス\nできるなんて…\n平和な証拠ッスね～", 2, 0, 1, 6713, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123901, 1, "か、かわいい…♪\nさあ、自分と握手…\nううん、抱っこッス♪", 3, 0, 1, 6714, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123901, 1, "優しい匂いッスね…♪\nこれくらいみんなを\n包み込めるように\nなりたいッス～", 4, 0, 1, 6715, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123901, 1, "ひっくちゅ！　うー…\nちょっと冷えるッスね\nぬくぬくするッス～♪", 5, 0, 1, 6716, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123901, 1, "特訓した分、\nいっぱい食べるッス！\nでっかくなるッス！", 7, 0, 1, 6718, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 123901, 1, "いつか自分の\nヒミツ図鑑が出たり\nして…いやいやいや", 8, 0, 1, 6719, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106901, 1, "牢獄よりは\nマシな環境ね\n我慢してあげるわ", 1, 0, 1, 6721, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106901, 1, "退屈ね…\nキャルでもいじって\n暇をつぶそうかしら", 2, 0, 1, 6722, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106901, 1, "ここにいる連中\n私を天気予報士と\n勘違いしてない？", 3, 0, 1, 6723, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106901, 1, "音波情報が多いわね…\nここ、もう少し静かに\nならないの…？", 4, 0, 1, 6724, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106901, 1, "共同生活なんて\n久しぶりだわ\n…懐かしいわね", 5, 0, 1, 6725, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106901, 1, "あの子たちのギルド\n経営が雑すぎるわ…！\n教育が必要みたいね", 101, 0, 4, 6726, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106901, 1, "とどめを刺される\n準備はできているのに\nまだなのかしら…？", 102, 0, 8, 6727, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106901, 1, "私と戦う気になった？\nあら、まだなのね…\nその時を待ってるわよ", 1, 0, 1, 6728, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106901, 1, "私というリソースは\n有効活用しなさい\n子守なんてごめんだわ", 2, 0, 1, 6729, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106901, 1, "どうしてそんなに\n頭がいいか？\n生まれつきよ", 3, 0, 1, 6730, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106901, 1, "視力は権能に\n影響しないわよ\n当たり前じゃない", 4, 0, 1, 6731, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106901, 1, "あなたまで天気を\n教えろですって…？\nぶっ殺すわよ？", 101, 0, 4, 6732, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106901, 1, "暇なら付き合いなさい\n好きなゲームでいいわ\nどうせ私が勝つもの", 102, 0, 8, 6733, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106901, 1, "人生朝露…\n悔いのない一日を\n過ごしなさい", 1, 1, 1, 6734, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106901, 1, "午睡はいいわよ…\nふわぁ…眠りは思考に\n必要不可欠なの…", 2, 2, 1, 6735, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106901, 1, "夙興夜寝なんて\nやめておきなさい\n休息は大切よ", 3, 3, 1, 6736, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106901, 1, "私を満足させるなんて\n大したものね…晶に\n大量生産させるわ", 2, 0, 1, 6738, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106901, 1, "キャルの代わりに\nズタズタにする物が\n欲しかったの…♪", 3, 0, 1, 6739, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106901, 1, "いい香りじゃない\n見たことのない調合ね\n誰が用意したの？", 4, 0, 1, 6740, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106901, 1, "パワーが足りないわね\nもっとよ、もっと私を\n寛がせなさい", 5, 0, 1, 6741, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106901, 1, "栄養価はともかく\n味のバランスは\n悪くないわね", 7, 0, 1, 6743, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 106901, 1, "つまらない本は\n私が書き直すわ\nこれはどうかしら？", 8, 0, 1, 6744, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124001, 1, "今年はアカリと\nお姉ちゃんでサンタが\n二人…つまり幸せも\n二倍ですね♪", 1, 0, 1, 6746, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124001, 1, "クリスマスに向けての\nサックス練習…\nお兄ちゃんと一緒に\n猛特訓です！", 2, 0, 1, 6747, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124001, 1, "クリスマスツリーの\n飾りつけ、完璧です♪\nお兄ちゃんお姉ちゃん\n喜んでくれるかな？", 3, 0, 1, 6748, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124001, 1, "わあ、お外が真っ白！\n生クリームが\nいーっぱいの\nケーキみたいです♪", 4, 0, 1, 6749, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124001, 1, "煙突から入ると\nお洋服が汚れちゃう…\n玄関からじゃ\nダメなのかなぁ？", 5, 0, 1, 6750, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124001, 1, "お兄ちゃんが来たら\nサンタのお洋服\nいっぱい褒めて\nもらおっと♪", 101, 0, 4, 6751, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124001, 1, "聖夜をお兄ちゃんと\n過ごせたら…\nアカリもちょっぴり\n大人になれるかも♪", 102, 0, 8, 6752, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124001, 1, "今のアカリは\nサンタさんですから♪\nなんでもお願いして\nくださいね？", 1, 0, 1, 6753, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124001, 1, "お外は寒いですから\nアカリとくっついて\nお出かけしましょ♪", 2, 0, 1, 6754, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124001, 1, "お兄ちゃんは\nアカリのトナカイさん\nですから、ずーっと\nそばにいてください♪", 3, 0, 1, 6755, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124001, 1, "お兄ちゃん、助けて！\nアカリのカラダで\nラッピング練習したら\n絡まっちゃいました…", 4, 0, 1, 6756, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124001, 1, "プレゼント、いっぱい\n準備できました♪\nお兄ちゃんの分は…\nまだヒミツでーす♪", 101, 0, 4, 6757, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124001, 1, "アカリ、プレゼントは\n大人っぽいのがいいな\n早くお兄ちゃんに\n追いつきたいから♪", 102, 0, 8, 6758, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124001, 1, "おはようございます♪\nふふ、アカリサンタは\nいい子のお手本なので\n早寝早起きなんです♪", 1, 1, 1, 6759, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124001, 1, "あれ、もうお昼？\nクリスマスナンバーの\n練習してたら\nもうこんな時間…", 2, 2, 1, 6760, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124001, 1, "冬の夜空は星が\nキレイですね～\nお兄ちゃん、一緒に\n見に行きませんか？", 3, 3, 1, 6761, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124001, 1, "はぁ、快適すぎて\nとろけちゃいそう…\nこういうプレゼントも\nいいかもです…", 2, 0, 1, 6763, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124001, 1, "やん、かわいいです♪\nえへへ、お兄ちゃんは\nどんなプレゼントが\n欲しいですか～？", 3, 0, 1, 6764, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124001, 1, "わわ、大人な香り…\nロマンチックな\nクリスマスが\n過ごせそうです♪", 4, 0, 1, 6765, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124001, 1, "これでカラダを温めて\nアカリの温もり…\nお兄ちゃんに\nおすそ分けしよっと♪", 5, 0, 1, 6766, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124001, 1, "お腹がパンパンです…\nでもこれで\nちょっとサンタさんに\n近づけたかも…？", 7, 0, 1, 6768, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124001, 1, "クリスマスイブの\n恋人との過ごし方…？\nはわわ、アカリには\nまだ早いかもです…！", 8, 0, 1, 6769, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124001, 1, "わっ、すごいすごい！\n不思議な絵本ですね♪\nこの本でお兄ちゃんに\nイタズラしちゃお♪", 14, 0, 1, 6771, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124001, 1, "アカリ、ホカホカに\nなっちゃいました～♪\n…ぎゅってしても\nいいですよ？", 15, 0, 1, 6772, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124101, 1, "またエンジェルになる\nかもとは思ってたけど\n…サンタさん要素の\n追加は想定外だったわ", 1, 0, 1, 6773, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124101, 1, "ラフィたちがいない\nときは私がなんとか\nしないとね\n一応…エンジェルだし", 2, 0, 1, 6774, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124101, 1, "部屋の飾りつけとか\nするのも…あり？\nふふ、浮かれすぎかな", 3, 0, 1, 6775, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124101, 1, "パーティーだったら\nみんなで大人数向けの\nボードゲームもできる\nかも…やったっ", 4, 0, 1, 6776, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124101, 1, "この服のモコモコ…\n気づいたら触っちゃう\n…やるわね、ラフィ", 5, 0, 1, 6777, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124101, 1, "アイツと一緒にいると\n人見知りとは違う\n緊張があるのよね…\nって今の聞いてたっ？", 101, 0, 4, 6778, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124101, 1, "ぬいぐるみ…\nいじめてないわよね？\n…大事にしてるって？\nそれはそれで…あうぅ", 102, 0, 8, 6779, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124101, 1, "いい子と悪い子って\nどう見分けるのかしら\n…まさか素直さとか？\nだったら困る！", 1, 0, 1, 6780, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124101, 1, "プレゼントといえば\nやっぱりゲーム！\n年末商戦サイコー♪", 2, 0, 1, 6781, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124101, 1, "サンタさんはお仕事を\n見られちゃ\nいけないのよね？\n…天職？", 3, 0, 1, 6782, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124101, 1, "この服は汚れ対策も\nバッチリなのよね\n煙突に入れるように\n…いや私はNGよ！？", 4, 0, 1, 6783, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124101, 1, "アンタのお願い…\n今なら一つくらい\n聞いてあげるわよ？\nほら、サンタ衣装だし", 101, 0, 4, 6784, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124101, 1, "私たちって\nなぜかぬいぐるみと\n縁があるわよね\n…ふふ", 102, 0, 8, 6785, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124101, 1, "お、おはよっ\n…ふぅ、寝ぐせを\n直すのが間に合って\nよかったぁ", 1, 1, 1, 6786, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124101, 1, "え…一緒にごはん？\nそ、そう…うん。OK\nまあ、せっかくだし？", 2, 2, 1, 6787, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124101, 1, "この時間のゲームって\n妙に楽しいのよね\n…ということで、\nちょっと遊ばない？", 3, 3, 1, 6788, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124101, 1, "ああ～ダメになるぅ…\nもうここでずっと\nゲームしてた～い", 2, 0, 1, 6790, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124101, 1, "プレゼントのために\n研究は必要よね、うん\n…はぁ、かわいい～♪", 3, 0, 1, 6791, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124101, 1, "集中力を高める香りも\nあるのよね…\n勝負をするときには\n持ち歩こうかしら", 4, 0, 1, 6792, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124101, 1, "なんだかまったり\nしちゃうわね…\n時間が溶けるわ…", 5, 0, 1, 6793, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124101, 1, "お腹いっぱい\n食べられるのも\n幸せなことよね", 7, 0, 1, 6795, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124101, 1, "ちっちゃいときは\nアカリと一緒に絵本を\n読んだなぁ…懐かしい", 8, 0, 1, 6796, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124101, 1, "飛び出す絵本…\nこれでゲームブックを\n作ったらおもしろく\nなる気がするわ…", 14, 0, 1, 6798, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124101, 1, "あったまる…\nモフモフ…ふわふわ…\nちょっと休憩しても…\nいいよね…？", 15, 0, 1, 6799, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105101, 1, "あら、どうしたの？\nもしかして、私と\n新しい実験を\n始めたくなった？", 103, 0, 12, 6800, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105101, 1, "調子が悪いときは\n診療所に来て\n君は特別に、じっくり\n診察してあげるわ♪", 103, 0, 12, 6801, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124201, 1, "特別じゃなくても\nまったり過ごす\nクリスマスも\nいいものだね", 1, 0, 1, 6802, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124201, 1, "ニットの帽子に\nマフラーに耳当て…\n素顔用の防寒対策を\n整えなければ", 2, 0, 1, 6803, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124201, 1, "靴下が\n見当たらなかったから\n枕元にはかわりに\n鎧を置いておこう", 3, 0, 1, 6804, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124201, 1, "聖女なんて言われる程\n素晴らしくはないけど\n心は聖くいたいものだ\n日々精進だね", 4, 0, 1, 6805, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124201, 1, "兜がないから\nイルミネーションが\nいつもよりまぶしく\n感じるよ", 5, 0, 1, 6806, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124201, 1, "皆の幸福を\n祈願して\nクリスマスリースを\n飾ろうか", 101, 0, 4, 6807, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124201, 1, "私にはかわいすぎる\n衣装だが…開き直って\nクリスマス気分を\n楽しんでしまおう", 102, 0, 8, 6808, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124201, 1, "ツリーの飾りつけ\n手伝ってくれるかい？\nセンスは少年に\nお任せするよ", 1, 0, 1, 6809, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124201, 1, "パーティーは\n用意してる時も楽しい\nねえ少年、この気持ち\n分かってくれるかい？", 2, 0, 1, 6810, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124201, 1, "なんでトナカイの鼻は\n赤いのかって？\n無灯ソリ運転は違反で\n取り締まられるからだ", 3, 0, 1, 6811, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124201, 1, "トモちゃんや\nマツリちゃんは\nいい子だから、きっと\nサンタさんに会えるね", 4, 0, 1, 6812, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124201, 1, "ヒイラギの髪飾り\n実は気にいってるんだ\n少年にも\n褒めてもらいたいな", 101, 0, 4, 6813, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124201, 1, "ねえ少年…そろそろ\n見慣れてほしい\nそんなにまじまじ\n見つめられると照れる", 102, 0, 8, 6814, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124201, 1, "朝日に照らされて\n街がキラキラ白銀に\n輝いてるよ", 1, 1, 1, 6815, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124201, 1, "お昼ご飯を食べて\nちょっと散歩しようか\n祝祭に沸く街は\nとてもきれいだよ", 2, 2, 1, 6816, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124201, 1, "やっぱりクリスマスは\n夜が本番って感じだね\nキャンドルを灯して\n厳かに過ごそうか", 3, 3, 1, 6817, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124201, 1, "鎧姿では無縁だったが\nふかふかしていて\n気持ちいいな…\nふかふか…ふかふか…", 2, 0, 1, 6819, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124201, 1, "ふふ、かわいいね\n抱きしめると\nあの雪の日に産まれた\n赤ちゃんを思い出す", 3, 0, 1, 6820, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124201, 1, "兜を脱いだおかげで\nこの香りに出会えた\n…クリスマスの魔法も\n悪くないかもね", 4, 0, 1, 6821, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124201, 1, "指先から体の芯まで\n暖まるね\nこれなら雪の日でも\n安心だよ", 5, 0, 1, 6822, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124201, 1, "もぐ、もぐ…\nあまり食べるところ\n見ないでほしい", 7, 0, 1, 6824, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124201, 1, "素敵なレストランの\n特集記事が載ってる\n聖夜に予約が取れたら\n最高に幸せだろうね", 8, 0, 1, 6825, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124201, 1, "わっ！\n…………いや\n驚いてなどいない", 14, 0, 1, 6827, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124201, 1, "ふふ…頬に直接\nふかふかを感じられる\n温かくて気持ちいいよ", 15, 0, 1, 6828, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124301, 1, "ピアノに読書…\nグレアと一緒に\nなにをしようかな～♪", 1, 0, 1, 6829, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124301, 1, "元の世界へ戻った時\n授業に遅れないよう\nグレアと勉強しとこう", 2, 0, 1, 6830, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124301, 1, "街に魔物料理屋さんが\nあるんだ！　グレアと\n食べに行ってみよう♪", 3, 0, 1, 6831, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124301, 1, "せっかく新しい魔法を\n覚えたし、グレアにも\n見てもらいたいな！", 4, 0, 1, 6832, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124301, 1, "グレアも誘って\nランドソルをもっと\n探索したい！", 5, 0, 1, 6833, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124301, 1, "私たちも\nあの人を見習って\nバイトとかしよう！", 101, 0, 4, 6834, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124301, 1, "いつかあの人を\n私たちの学院に\n招待したいな！", 102, 0, 8, 6835, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124301, 1, "あのね！\nこれからグレアと\n魔法愛好会に\n行くんだ！", 1, 0, 1, 6836, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124301, 1, "これからなにするの？\n私たちもついて行って\nいいかな！？", 2, 0, 1, 6837, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124301, 1, "グレアと一緒に\nあなたも魔法の練習\nしてみない？", 3, 0, 1, 6838, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124301, 1, "グレアとよく話すの\nマナリアの制服\nあなたに着せたいって", 4, 0, 1, 6839, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124301, 1, "グレアのそばって\n温かいんだ～！\nぬくぬくだよ～♪", 101, 0, 4, 6840, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124301, 1, "あなたは\nグレアと私の友達！\nこれからもずっと！", 102, 0, 8, 6841, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124301, 1, "おはよう！\n今朝はグレアと一緒に\n外を散歩してきたよ！", 1, 1, 1, 6842, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124301, 1, "お腹空いたな…\nよし、グレアと一緒に\n魔物料理を作ろう！", 2, 2, 1, 6843, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124301, 1, "もうこんな時間だね\n明日のために\nそろそろ寝ようかな！", 3, 3, 1, 6844, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124301, 1, "はぁ～この感触…\nグレアの尻尾みたいで\n安らぐ～", 2, 0, 1, 6846, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124301, 1, "ぬいぐるみって\nいいよね…私もよく\nグレアに抱き付くんだ", 3, 0, 1, 6847, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124301, 1, "いい香り～…\nグレアもこういうの\n好きなんだよ！", 4, 0, 1, 6848, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124301, 1, "あったかいね～…\nグレアにくっついてる\n時を思い出すよ～…", 5, 0, 1, 6849, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124301, 1, "この世界にしかない\nおいしいものが\nたくさんあるよね！", 7, 0, 1, 6851, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124301, 1, "世界を繋ぐ魔法の\nヒント…本に書いて\nないかなぁ…", 8, 0, 1, 6852, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124401, 1, "ピアノに読書…\n今日のアンは\nなにをするのかな…", 1, 0, 1, 6855, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124401, 1, "私はアンほど\n勉強はできないから\nちゃんと自習しないと", 2, 0, 1, 6856, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124401, 1, "一度始めたら\nとことん追求するのが\nアンなんだよね…", 3, 0, 1, 6857, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124401, 1, "ふふ…夢中になってる\nアンを眺めてるだけで\n一日があっという間…", 4, 0, 1, 6858, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124401, 1, "ランドソルは広いね…\nアンと一緒にいろんな\n場所へ行ってみたい", 5, 0, 1, 6859, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124401, 1, "アンやあの人の笑顔を\n見てると、すごく\n安心するんだ…", 101, 0, 4, 6860, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124401, 1, "もしあの人が私たちの\n学院に編入したら…\nふふ、楽しそうだね", 102, 0, 8, 6861, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124401, 1, "アンもあなたも\nすごいな…\nみんなとすぐに\n仲良くなっちゃうから", 1, 0, 1, 6862, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124401, 1, "あなたはこれから\nなにをするの？\nその…アンと一緒に\nついていってもいい？", 2, 0, 1, 6863, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124401, 1, "アンから魔法の練習に\n誘われた？　実は私も\n…一緒にどうかな？", 3, 0, 1, 6864, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124401, 1, "あなたがマナリアに\n編入したら\n楽しいのになって\nアンとよく話すんだ…", 4, 0, 1, 6865, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124401, 1, "こっちの世界にも\nアンのように\n私を受け入れてくれる\n人たちがいるんだね…", 101, 0, 4, 6866, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124401, 1, "元の世界へ帰る日が\n来たとしても私たちは\n「さよなら」より\n「またね」がいいよね", 102, 0, 8, 6867, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124401, 1, "おはよう。さっき\nランドソルの朝日を\nアンと見てきたんだ", 1, 1, 1, 6868, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124401, 1, "私たちは\nこれからなんだけど\nあなたはお昼ごはん\n何を食べたの？", 2, 2, 1, 6869, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124401, 1, "そろそろ\nアンが眠る時間だ…\nあなたもおやすみ", 3, 3, 1, 6870, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124401, 1, "はぁ、疲れが取れる…\nこの尻尾…\nちょっと重いんだよね", 2, 0, 1, 6872, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124401, 1, "これ…\nアンに見せたら\n喜ぶだろうな…", 3, 0, 1, 6873, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124401, 1, "いい香り…\nアンの感想も\n聞いてみたいな", 4, 0, 1, 6874, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124401, 1, "あったかい…\nアンがここにいたら\n本を読みながら\n寝ちゃうんだろうな", 5, 0, 1, 6875, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124401, 1, "おいしいからたくさん\n食べれちゃうけど\nまた尻尾…\n大きくなっちゃうかな", 7, 0, 1, 6877, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124401, 1, "アンがおススメ\nしてくれた本は\nこれかな…？", 8, 0, 1, 6878, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124701, 1, "凶、凶、凶…大凶？\nなによこのおみくじ\n自宅用だからって\n排出率が適当すぎよ…", 1, 0, 1, 6881, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124701, 1, "今年こそ最高の新薬が\nもはや偶然に近い\n最小限の手間で\n作れますように", 2, 0, 1, 6882, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124701, 1, "喉に詰まる前に\n溶けてなくなるお餅…\n最優先の研究課題だわ", 3, 0, 1, 6883, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124701, 1, "よし、完成♪\nミツキ特製…\n「新解釈おせち料理」\n医学的根拠が満載よ", 4, 0, 1, 6884, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124701, 1, "お年玉を彼に…\nあげるべき…？\n色んな意味で微妙ね…", 5, 0, 1, 6885, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124701, 1, "ふふ、今年もまた一年\n彼のバイタルを\n記録できるのね♪", 101, 0, 4, 6886, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124701, 1, "今年こそ\n私の薬で彼を魔改造…\nううん、大人の男に！", 102, 0, 8, 6887, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124701, 1, "コマ回しのコマを\n見てたら目が回った？\nそう…手当は不要よ…", 1, 0, 1, 6888, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124701, 1, "私の顔で\n福笑いを作った？\nあら…隻眼まで\n再現度が高いわね♪", 2, 0, 1, 6889, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124701, 1, "門松よし、しめ縄よし\nこんなギルドでもね\n一応縁起を担ぐのよ", 3, 0, 1, 6890, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124701, 1, "本気の薬草を煮込んだ\n本気の七草粥よ\n色は気にしちゃダメ", 4, 0, 1, 6891, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124701, 1, "君が今年も健康で\nつつがなく…実験台を\n務められますように", 101, 0, 4, 6892, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124701, 1, "いくつ新年を迎えても\n俗世の垢にまみれない\nそんな君こそが…\n私には最高のお薬よ", 102, 0, 8, 6893, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124701, 1, "お正月なんだから\nいつまでも寝てないで\nさぁ、実験開始よ♪", 1, 1, 1, 6894, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124701, 1, "カルタも双六もしたし\nあとは実験くらいしか\nやることがないわね", 2, 2, 1, 6895, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124701, 1, "日頃からたいがい\n意味深な夢を見るけど\n初夢はまた別かしら…", 3, 3, 1, 6896, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124701, 1, "さすがにこの格好で\n寝正月を敢行するのは\nためらわれるわね", 2, 0, 1, 6898, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124701, 1, "や、闇医者たる者\nこんな愛くるしい物に\n心奪われるわけには…", 3, 0, 1, 6899, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124701, 1, "お雑煮のいい香り…\n食欲が増進されるわ\nアロマも立派なお薬ね", 4, 0, 1, 6900, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124701, 1, "胸元は閉じないわよ？\nでも時々ここで\nこっそり暖まるの…", 5, 0, 1, 6901, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124701, 1, "無から有が生まれてる\n新年早々物理の法則を\n無視してくるのね…", 7, 0, 1, 6903, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124701, 1, "どれもこれも読んだわ\n新しい婚活本の\n入荷はまだかしら…", 8, 0, 1, 6904, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124601, 1, "彼が一生懸命\n書いてくれた年賀状\n気持ちがこもっていて\nすごくうれしいわ♪", 1, 0, 1, 6906, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124601, 1, "しっかり丁寧に\n大掃除したから\nとってもいい気持ちで\n新年を迎えられたわ♪", 2, 0, 1, 6907, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124601, 1, "彼の着物姿\nきっと凛々しいわよね\n想像しただけで\n笑顔になるわ～♪", 3, 0, 1, 6908, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124601, 1, "お雑煮は地域によって\n違いがあるけれど\n愛情が隠し味なのは\nどこも一緒よね♪", 4, 0, 1, 6909, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124601, 1, "彼と凧揚げして\n遊びたいけど\n森では引っかかって\nできないかしら…？", 5, 0, 1, 6910, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124601, 1, "今年も彼が素直に\n成長できるように\nたくさんいい子いい子\nしてあげましょう…♪", 101, 0, 4, 6911, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124601, 1, "絵馬に彼と\n書いた通り\n今年もみんなが笑って\n過ごせますように", 102, 0, 8, 6912, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124601, 1, "先生と初詣に\n行きましょう〜♪\n迷子にならないように\n手を繋いで、ね？", 1, 0, 1, 6913, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124601, 1, "初売りセールいいわね\nでも気を付けないと\nあれこれ欲しくなって\n買い過ぎちゃうかも…", 2, 0, 1, 6914, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124601, 1, "風邪をひかないように\n温かくするんですよ～\nえ？　先生、温めて？\nふふ、甘えん坊さんね", 3, 0, 1, 6915, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124601, 1, "あなたのために\nちょっと豪華なおせち\n用意したのよ\nお口に合うかしら？", 4, 0, 1, 6916, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124601, 1, "あなたも着物着たい？\nふふ、貫禄出ちゃって\n先生ドキドキ\nしちゃうかも…♪", 101, 0, 4, 6917, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124601, 1, "あなたの成長は\nとってもうれしいけど\n今年もまだ、あなたの\n先生でいたいな…♪", 102, 0, 8, 6918, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124601, 1, "あら、起きたの？\nお雑煮できてるから\nちゃんとお顔\n洗ってらっしゃいね", 1, 1, 1, 6919, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124601, 1, "お昼ご飯を\nしっかり食べて\nみんなでかるた大会\nしましょう〜♪", 2, 2, 1, 6920, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124601, 1, "は～い、良い子は\nもう寝る時間ですよ\n素敵な初夢が\n見られますように♪", 3, 3, 1, 6921, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124601, 1, "このフカフカちゃんも\n天日干ししたいけど…\nその前にちょっと\n休憩しちゃおうかしら", 2, 0, 1, 6923, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124601, 1, "うふふ、かわいい～\nアオイちゃんや\nハツネちゃんも\nきっと気に入るわね♪", 3, 0, 1, 6924, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124601, 1, "お正月は空気も\n透き通ってるから\nいつも以上に\n素敵な香りに感じるわ", 4, 0, 1, 6925, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124601, 1, "火のぬくもりに\n抱かれていると\n不思議と眠気が…\nふああ…", 5, 0, 1, 6926, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124601, 1, "愛情のこもった料理は\nおいしいだけじゃなく\n心も満たされちゃう♪", 7, 0, 1, 6928, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124601, 1, "お正月のお作法も\nたくさんあるのね～\n先生も日々勉強だわ～", 8, 0, 1, 6929, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104701, 1, "少年への感謝をどんな\n形で伝えるべきか…\n言葉よりも物の方が\n良いのだろうか？", 103, 0, 12, 6931, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104701, 1, "話し相手になって\nもらってもいいかな？\n少年と一緒にいると\nとても楽しいんだ", 103, 0, 12, 6932, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124501, 1, "お正月だからって\nギャングのボスは\n油断できないよ～？", 1, 0, 1, 6933, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124501, 1, "イノリちゃんがお年玉\n欲しいんだって♪\n悪い子だったし\n10ルピでいいかな☆", 2, 0, 1, 6934, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124501, 1, "お正月だからお雑煮を\n作ったんだけど\nカヤちゃんには\n大好評だったよ♪", 3, 0, 1, 6935, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124501, 1, "ラーゴがお餅を\n食べたがってるけど…\nこの子にあげるのは\nちょっと心配かな～？", 4, 0, 1, 6936, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124501, 1, "お正月は晴れ着の子が\nやっぱり多いよね～\n今度、私の漫画でも\n出してみようかな？", 5, 0, 1, 6937, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124501, 1, "君と二人っきりで\n神殿に初詣…\n行ってみたいな～♪", 101, 0, 4, 6938, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124501, 1, "君とお正月を\n過ごしていると\n面倒なことも\n全部忘れられそう…", 102, 0, 8, 6939, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124501, 1, "この着物、どう思う？\n似合ってたら\nうれしいけどな～♪", 1, 0, 1, 6940, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124501, 1, "袖を引っ張ったり\nしないでね？\n着崩れしちゃうから♪", 2, 0, 1, 6941, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124501, 1, "お正月ならではの\n漫画ネタってある？\n参考にしたいんだよね", 3, 0, 1, 6942, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124501, 1, "昔は凧あげとか\n駒回しとかしてたけど\n大人になるとそういう\n機会も減るよね…", 4, 0, 1, 6943, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124501, 1, "君にもお年玉を\nあげようか？\n好きな額を言ってね☆", 101, 0, 4, 6944, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124501, 1, "来年も、再来年も…\nこうしてみんなで\n新年がお祝いできると\n素敵だね～♪", 102, 0, 8, 6945, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124501, 1, "おはよ～♪\n朝ごはんは\n焼いたお餅とか\nどうかな？", 1, 1, 1, 6946, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124501, 1, "お昼だね～\nおせちを用意したから\nみんなで食べよっか", 2, 2, 1, 6947, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124501, 1, "もうこんな時間だね～\n一日中着物だったから\n肩が凝っちゃったよ", 3, 3, 1, 6948, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124501, 1, "このまま寝たら\nしわになるかな？\n着替えた方がいい？", 2, 0, 1, 6950, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124501, 1, "こういうのって\nラーゴのおもちゃに\nちょうどいいかも♪", 3, 0, 1, 6951, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124501, 1, "いい香りだけど\n着物に移らないように\n気をつけないとね", 4, 0, 1, 6952, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124501, 1, "ちょうどいい\n火加減だね♪\nぽかぽかするよ～♪", 5, 0, 1, 6953, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124501, 1, "着物の時は\nほどほどにしないと…\n欲張ったら、お腹が\n苦しくなるからね～", 7, 0, 1, 6955, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124501, 1, "ふーん\nイノリちゃんは最近\nこんな漫画を\n読んでるんだ～♪", 8, 0, 1, 6956, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124801, 1, "楽園の気配は…ナシ！\nみんな前を向いてるね\nそうこなくっちゃ！", 1, 0, 1, 6958, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124801, 1, "レッスンの成果も\nこの格好くらい\n目に見えたら\n分かりやすいのになぁ", 2, 0, 1, 6959, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124801, 1, "次の曲はワイルドで\n悪い感じもいいなぁ…\nカヤとムイミちゃん\nみたいなイメージで！", 3, 0, 1, 6960, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124801, 1, "私…服、着てるよね？\nうんうん、着てる！\nあんまり体が軽いと\n急に焦る瞬間あるな～", 4, 0, 1, 6961, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124801, 1, "最近ファンの年齢層が\n幅広くなってきたかも\nこれって私…国民的\nアイドルに近づいた？", 5, 0, 1, 6962, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124801, 1, "パティスリーとコラボ\nしたらどんな感じかな\n…ノゾミンマカロン♪\nとか？　…いけるかも", 101, 0, 4, 6963, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124801, 1, "彼と一緒にいると…\nうれしくって苦しいな\nでも両方諦められない\n…アイドルらしいや", 102, 0, 8, 6964, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124801, 1, "この格好、チカと\nツムギも似合うと\n思うんだけどな～\nキミもそう思うよね？", 1, 0, 1, 6965, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124801, 1, "街を歩いてたらね？\nライブとか写真集の\n告知イベントだって\n思われちゃった！", 2, 0, 1, 6966, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124801, 1, "私が持ってた剣に\n結んであったリボン…\n何か気づいてくれた？\nヒントは…色だよ♪", 3, 0, 1, 6967, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124801, 1, "キミの気配ってすごく\n分かりやすいんだよ\n近くにいたらすぐに\nピンとくるんだ♪", 4, 0, 1, 6968, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124801, 1, "あ、それ…はじめて\nキミにあげた音響盤！\nね、一緒に聴こうよ", 101, 0, 4, 6969, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124801, 1, "ぼうっとしてたら\nイタズラしちゃうよ♪\nもっともっと私のこと\n見てないとダーメ！", 102, 0, 8, 6970, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124801, 1, "朝は今日のはじまり！\n昨日よりももっと\nいい一日にしようね♪", 1, 1, 1, 6971, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124801, 1, "レッスンがんばったし\nまだお昼だし…\nランチ大盛りにしても\nセーフだよね！", 2, 2, 1, 6972, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124801, 1, "寝る前に明日の予定を\n確認しておかないと！\nえっと、集合場所は…", 3, 3, 1, 6973, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124801, 1, "一休みのつもりが\nなが～い休憩になるの\nあるあるだよね～", 2, 0, 1, 6975, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124801, 1, "ノゾミンの熱烈ハグ♪\n…ここだけ聞いたら\nスクープになっちゃう\nかも？　なーんて！", 3, 0, 1, 6976, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124801, 1, "香りってなんだか\n記憶に残るよねー\n…こういうの私だけ？\nみんなも同じだよね？", 4, 0, 1, 6977, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124801, 1, "なんだかライブの\n熱気を思い出して\nきちゃった…！\nレッスンしよっと！", 5, 0, 1, 6978, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124801, 1, "お腹が減ってたら\nいいパフォーマンスは\nできないもんね！\n…んー、おいし～♪", 7, 0, 1, 6980, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124801, 1, "いい歌詞を書くには\nしっくりくる言葉を\n知っておかないと！", 8, 0, 1, 6981, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124901, 1, "うだうだ悩んでる\nヤツがいたら\nオレのとこ連れてこい\n背中押してやるからさ", 1, 0, 1, 6983, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124901, 1, "最近どことなく\nファミリーの連中が\n浮き足立ってんだよな\n…なんでだ？", 2, 0, 1, 6984, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124901, 1, "イノリのヤツ\nやたらと脇腹\nつついてきやがって…\nくすぐったいっての", 3, 0, 1, 6985, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124901, 1, "身軽になったのは\nいいが…くそっ\nどこもかしこも\nスースーする…", 4, 0, 1, 6986, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124901, 1, "思い出に浸るのも\n悪くはないが\nやっぱ人間\n未来に生きないとな！", 5, 0, 1, 6987, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124901, 1, "またムイミやノゾミと\nツルむのもいいな\nそこにカレもいたら\nなおいいぜ", 101, 0, 4, 6988, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 124901, 1, "未来のことを考えると\nなぜかいつもカレが\n頭に浮かんじまう…\nええい、やめだやめ！", 102, 0, 8, 6989, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124901, 1, "ちょっくらオレと\n腕相撲しねえか？\nこの新しい力\n試させてくれ！", 1, 0, 1, 6990, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124901, 1, "なあ、悪いが上着\n貸してくれないか？\nこのカッコ、割と\n肌寒いんだよ…", 2, 0, 1, 6991, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124901, 1, "おっ、生まれ変わった\n喧嘩屋にご用命か？\nいいぜ、キミの\n依頼なら二つ返事だ！", 3, 0, 1, 6992, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124901, 1, "なんか悩みごとが\nあるなら、なんでも\n話してくれよな\nオレとキミの仲だろ？", 4, 0, 1, 6993, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124901, 1, "このカッコが\nきれいだって？\nや、やめろよ…\n照れくさいだろ…", 101, 0, 4, 6994, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124901, 1, "キミがいると、なにを\nするにも退屈しないぜ\nだから今日も\n付き合ってくれよな！", 102, 0, 8, 6995, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124901, 1, "おはよう！　新しい\n未来がやってきたぜ！\n一秒だって時間は\n無駄にするなよ？", 1, 1, 1, 6996, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124901, 1, "そろそろ昼飯に\nしないか？\n穴場のうまい店に\n案内してやるからさ", 2, 2, 1, 6997, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 124901, 1, "夜更かしは\n未来の前借りだ\n体壊さないように\nほどほどにしとけよ？", 3, 3, 1, 6998, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124901, 1, "お、いいなこれ\nファミリーの\nアジトにも\n置いときたいぜ", 2, 0, 1, 7000, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124901, 1, "べ、別にかわいいもん\nなんて、そんなに\n好きじゃないし…\n嫌いでもないけどさ", 3, 0, 1, 7001, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124901, 1, "嗅いだことない\n香りだな\nなかなか気に入った！", 4, 0, 1, 7002, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124901, 1, "薄着で暖を取るのって\n最高にぜいたくだよな\nへへっ、堪能させて\nもらうとするか", 5, 0, 1, 7003, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124901, 1, "こいつはいいや\nこのカッコだと\n消耗が激しくて\nやたら腹が減るからな", 7, 0, 1, 7005, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 124901, 1, "甘辛コーデ…\nへぇ、そんなのが\nあるのか…\nわ、悪くねえかもな", 8, 0, 1, 7006, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105501, 1, "はぁ…\nもっともっと長い時間\n先輩と過ごせたらなぁ", 103, 0, 12, 7008, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105501, 1, "えへへ…♪\n先輩が、すぐそばに\nいてくれるなんて…\n幸せだなぁ…♪", 103, 0, 12, 7009, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125001, 1, "はーっはっは！\n強くてカッコいい\n大悪党、ここに参上！", 1, 0, 1, 7010, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125001, 1, "みんなおしゃれで\nカッコいいけど\n今のアタシも\n負けてないぞ！", 2, 0, 1, 7011, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125001, 1, "ギャングのアジトも\nよかったけど\nここも賑やかで\n悪くないな！", 3, 0, 1, 7012, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125001, 1, "悪党にも休息は必要だ\nここならゆっくり\n休めそうだ！\n邪魔するぞ！", 4, 0, 1, 7013, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125001, 1, "いっぱい人がいるな…\nアタシの悪党っぷりを\n広めるチャンスだ！", 5, 0, 1, 7014, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125001, 1, "ここにいれば\nいい思い出がたくさん\n作れそうだな！", 101, 0, 4, 7015, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125001, 1, "いつかアタシも\nオクトーと一緒に\nこんなアジトを手に\n入れてやるからな！", 102, 0, 8, 7016, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125001, 1, "ん？　アタシの格好が\n気になるのか？\nならもっと近くで\n見てもいいぞ！", 1, 0, 1, 7017, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125001, 1, "おい、暇だから\nアタシと遊べ！\nカヤとノゾミも呼んで\n大騒ぎするぞ！", 2, 0, 1, 7018, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125001, 1, "オマエ、前より強く\nなってないか！？\nくっそー！　アタシも\n負けてらんないぞー！", 3, 0, 1, 7019, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125001, 1, "今日は特別にアタシの\n思い出話を聞かせて\nやる！　さーて、どの\n悪事から話そうか…", 4, 0, 1, 7020, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125001, 1, "オマエとの付き合いも\n結構長くなったよな？\nこれからも\nよろしく頼むぞ！", 101, 0, 4, 7021, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125001, 1, "オマエまでいなく\nなったらアタシが困る\n勝手にどっか\n行くんじゃないぞ！", 102, 0, 8, 7022, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125001, 1, "起きろ、起きろー！\n大悪党の朝は早いんだ\nさっさと\n顔洗ってこい！", 1, 1, 1, 7023, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125001, 1, "昼飯どうしようか…\nそうだ！　オマエの\nおススメに連れてけ！\nいい店期待してるぞ！", 2, 2, 1, 7024, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125001, 1, "明日を思い切り\n楽しむためにも\n早く寝とけよ！\nまた明日な！", 3, 3, 1, 7025, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125001, 1, "こうやって座ると…\nおお！　すっごく悪党\nっぽいぞ！", 2, 0, 1, 7027, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125001, 1, "オマエ、仲間に\nなりたそうな目を\nしてるな！", 3, 0, 1, 7028, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125001, 1, "うん、いい匂いだ！\n何の匂いかは全然\n分かんないけどな！", 4, 0, 1, 7029, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125001, 1, "炎ってなんだか\nカッコいいよな！\n見てるだけで心が\n燃えてくるぞ！", 5, 0, 1, 7030, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125001, 1, "いっぱい食べれば\nちょっとは\n背が伸びるかな…？", 7, 0, 1, 7032, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125001, 1, "ぐぬぅ…たまには\n本でもと思ったけど\nやっぱりアタシは\nバトル担当だな！", 8, 0, 1, 7033, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118201, 1, "お邪魔しまーす★\nなーんてもう\nすっかりくつろいで\nますけどー♪", 1, 0, 1, 7035, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118201, 1, "騎士さんの\nギルドハウス…★\nなんだかドキドキ\nしちゃいます♪", 2, 0, 1, 7036, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118201, 1, "ぺんぽこりんの\n置き場所はー…\nま、このへんで★", 3, 0, 1, 7037, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118201, 1, "うーん、これは\n壊しがいがありそ…\nウソウソ、冗談です★", 4, 0, 1, 7038, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118201, 1, "オトモダチでも\n作ってみますかー\nくすっ★", 5, 0, 1, 7039, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118201, 1, "退屈ですねー\n騎士さん、構って\nくれないかなー", 101, 0, 4, 7040, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118201, 1, "もっともっと早く\n騎士さんに逢えてたら\n…なんて、わたし\nらしくないですね", 102, 0, 8, 7041, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118201, 1, "わたし一人にするの\n不安だったりします？\nちゃーんとイイコに\nしてますってー★", 1, 0, 1, 7042, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118201, 1, "お出かけします？\nそれともおうちで\nまったりしますー？", 2, 0, 1, 7043, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118201, 1, "女の子をすぐ家に\nあげちゃうなんて…\nふふ、やっぱり隅に\n置けないですねー★", 3, 0, 1, 7044, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118201, 1, "騎士さん騎士さーん★\nウザ絡みしても\nいいですかー？\n騎士さんってばー★", 4, 0, 1, 7045, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118201, 1, "一緒にゴロゴロ\nしませんかー？\nコイビトっぽくて\n楽しそうですし♪", 101, 0, 4, 7046, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118201, 1, "うふふ★\nこーやってあなたに\n優しくされると…\nときめいちゃいます♪", 102, 0, 8, 7047, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118201, 1, "おはようございます★\nあれれー？\nまだおねむですかー？", 1, 1, 1, 7048, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118201, 1, "騎士さん、お昼は\nもう食べましたー？\nわたし、ラーメンが\n食べたいでーす★", 2, 2, 1, 7049, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118201, 1, "あ、夜更かししてちゃ\nダメですよー？\nほらほら、お布団に\nごーごー★", 3, 3, 1, 7050, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118201, 1, "ふかふかのふわふわー\nついついまーったり\nしちゃいます★", 2, 0, 1, 7052, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118201, 1, "かっわいー★\n名前はー、んー…\nぱんぱかぷんで★", 3, 0, 1, 7053, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118201, 1, "くんくん★\nうーん…\nたぶん素敵な香り♪", 4, 0, 1, 7054, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118201, 1, "あったかーい★\n心も体も\nぽっかぽかですねー★", 5, 0, 1, 7055, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118201, 1, "もぐもぐ★\nんー、おいしくって\n止まらないですねー★", 7, 0, 1, 7057, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118201, 1, "へー★\nほほー★\nふんふーん★", 8, 0, 1, 7058, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125101, 1, "さ～て時間もあるし\nラテアートの練習でも\nするかにゃ～♪", 1, 0, 1, 7060, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125101, 1, "カフェの猫たちも\n元気に働いてくれて\nお客さんからの評判も\n上々にゃ～♪", 2, 0, 1, 7061, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125101, 1, "カフェのカウンターに\nファントムキャッツの\n目安箱を置こうかにゃ", 3, 0, 1, 7062, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125101, 1, "あんこもコーヒーも\n原料は豆にゃ～\nそりゃ相性いいに\n決まってるにゃ♪", 4, 0, 1, 7063, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125101, 1, "あいつの為に\nタマキ特製ブレンドを\n開発したいにゃ♪", 5, 0, 1, 7064, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125101, 1, "あいつと働いてると\nなぜかあっという間に\n閉店時間が\n来ちゃうのにゃ～", 101, 0, 4, 7065, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125101, 1, "あいつといると\n新しいアイデアが\n溢れるほど\n出てきちゃうのにゃ♪", 102, 0, 8, 7066, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125101, 1, "キミがコーヒーを\n淹れてくれたのにゃ？\nにゃはは、常温で\n猫舌にも優しいにゃ♪", 1, 0, 1, 7067, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125101, 1, "料理が上手くなったら\nカフェご飯メニューも\n充実させたいにゃ～", 2, 0, 1, 7068, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125101, 1, "お店の経営は大変\nだけど、キミとなら\n乗り越えられるって\n確信してるにゃ！", 3, 0, 1, 7069, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125101, 1, "ん？　この制服\n似合ってるにゃ？\nにゃはは！　褒めても\n１ルピも出ないにゃ♪", 4, 0, 1, 7070, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125101, 1, "あたしが好きなのは\n猫に好かれる人にゃ♪\n…ん、キミ猫に懐かれ\nまくってるにゃ…", 101, 0, 4, 7071, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125101, 1, "キミの制服姿\nけっこーイケてるにゃ\nあたしがお客さんなら\n通いつめちゃうにゃ♪", 102, 0, 8, 7072, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125101, 1, "朝は一杯の\nコーヒーから始まる…\nにゃふふん♪\nシャレた朝だにゃ～♪", 1, 1, 1, 7073, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125101, 1, "お昼はたい焼きと\nコーヒーのセットで\nまったり休憩\nしようにゃ～♪", 2, 2, 1, 7074, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125101, 1, "コーヒー淹れたにゃ\n星空を見ながら\n二人で飲もうにゃ…♪", 3, 3, 1, 7075, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125101, 1, "ふにゃ～ん…ゴロゴロ\nコーヒーと同じくらい\n癒されるにゃ～…♪", 2, 0, 1, 7077, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125101, 1, "こいつかわいいにゃ～\nうちの猫たちとも\nいい勝負なのにゃ！", 3, 0, 1, 7078, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125101, 1, "ん～いい香りだにゃ～\nコーヒーもそうだけど\n香りの癒し効果は\n抜群にゃ～♪", 4, 0, 1, 7079, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125101, 1, "猫に暖かいものは\nまるまれと言ってる\nようなもんにゃ…♪\nまるまる…", 5, 0, 1, 7080, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125101, 1, "にゃは～うまいにゃ♪\nカフェの新メニューに\n採用しちゃおう\nかにゃ～♪", 7, 0, 1, 7082, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125101, 1, "にゃるほど…\n調べれば調べるほど\nコーヒーは\n奥が深いのにゃ…", 8, 0, 1, 7083, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 107101, 1, "ワタシの手料理は\n坊やの口に合うらしい\n胃袋は討ち取ったぞ♪", 103, 0, 12, 7085, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 107101, 1, "胸に再度触れると\n誓約は無効になるのか\n…だと？　鋭い質問だ\n安心しろ。ならん☆", 103, 0, 12, 7086, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125201, 1, "ようやくこの服にも\n慣れてきたかな？\nふふっ、たまには\nこういう服も悪くない", 1, 0, 1, 7087, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125201, 1, "今日も店は好調だが、\n私は軍人だからな\n当初の目的も\n忘れないようにしよう", 2, 0, 1, 7088, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125201, 1, "猫が近くに来ると\nついつい見てしまうな\n仕事が終わったら\n遊んでやるかな♪", 3, 0, 1, 7089, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125201, 1, "コーヒーの淹れ方が\nうまくなってきたな♪\nどうせならお菓子も\n研究してみるか！", 4, 0, 1, 7090, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125201, 1, "利益を考えると\n価格をあげるべきか？\n…いや、利益よりも\n皆の笑顔の方が大事だ", 5, 0, 1, 7091, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125201, 1, "またあいつを\n呼んでしまったな…\n今度、コーヒーでも\n淹れて労ってやるか♪", 101, 0, 4, 7092, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125201, 1, "次に入荷するお菓子は\nあいつの意見を聞くか\nどんな味が好きか\n気になるしな", 102, 0, 8, 7093, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125201, 1, "なに、コーヒーを\n淹れてほしい？\nふふっ♪\n仕方のない奴だな！", 1, 0, 1, 7094, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125201, 1, "…そうだ。\nコーヒーと乾パンは\n合うのではないか？\n今度試してみよう！", 2, 0, 1, 7095, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125201, 1, "コーヒーもいいが、\nココアも悪くないぞ♪\n貴公も一緒にどうだ？", 3, 0, 1, 7096, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125201, 1, "魔法少女に比べれば\n大人しい服だろう？\nだが、カフェには\nこの服でバッチリだ！", 4, 0, 1, 7097, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125201, 1, "貴公はコーヒーを\n飲めるのかっ？それは\nうらやましいな…", 101, 0, 4, 7098, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125201, 1, "貴公の働きぶりには\n目を見張るものがある\nその姿は、その…\n結構かっこいいぞっ！", 102, 0, 8, 7099, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125201, 1, "さぁ、今日も一日、\n頑張るぞっ！\n気合いを入れて\n開店準備だっ！", 1, 1, 1, 7100, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125201, 1, "昼は稼ぎ時だからな！\n荒波を乗り切るため\n貴公も一緒に\n頑張ってくれるか？", 2, 2, 1, 7101, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125201, 1, "ふあぁ…もう夜か\n今日は頑張ったからな\nはやめに休むとしよう", 3, 3, 1, 7102, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125201, 1, "はふぅ～…\nたまにはこうして\nゆったりするのも\n悪くないな…", 2, 0, 1, 7104, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125201, 1, "猫もかわいいが、\nこいつもなかなか…\n見ていると、\n撫でたくなるな♪", 3, 0, 1, 7105, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125201, 1, "これは良い香りだな\nふむ、コーヒーも\nより香り高いものが\n作れるよう試すか！", 4, 0, 1, 7106, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125201, 1, "ふぅ～…\n体を温めなければ、\nすぐに動けないからな", 5, 0, 1, 7107, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125201, 1, "これもうまいが、\n私はお菓子も…\nいや、食べすぎは\n体に良くないな", 7, 0, 1, 7109, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125201, 1, "…書物は参考になるな\n次はこのやり方を\n試してみるかな", 8, 0, 1, 7110, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120001, 1, "愛と希望の魔法少女！\n弟くんの平和は\nお姉ちゃんが守るよ♪", 1, 0, 1, 7112, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120001, 1, "魔法少女の戦い方を\n見せてあげる！\nお姉ちゃんチョップ♪", 2, 0, 1, 7113, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120001, 1, "闇の力が高まってる…\n早く弟くんを\nギュ～ってしないと！", 3, 0, 1, 7114, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120001, 1, "ミニ弟くんを\n部屋中に呼び出して\n弟くんハウスに～♪", 4, 0, 1, 7115, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120001, 1, "決めポーズの練習でも\nしよっかな…\nはい、ポーズ♪", 5, 0, 1, 7116, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120001, 1, "魔法少女の力で\n弟くんのそばに\n瞬間移動したいなぁ", 101, 0, 4, 7117, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 120001, 1, "禁じられた必殺技\nその名も──\n闇のお姉ちゃん頭突き", 102, 0, 8, 7118, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120001, 1, "魔法少女な\nお姉ちゃんに\n興味津々だねっ？\n全部教えてあげるね♪", 1, 0, 1, 7119, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120001, 1, "闇のお姉ちゃんとして\n世界を弟くんのものに\n…な～んて冗談冗談♪", 2, 0, 1, 7120, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120001, 1, "弟くんって魔法少女に\nなりたがってたよね？\n…うん。応援するね！", 3, 0, 1, 7121, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120001, 1, "サインが欲しいの？\nふふっ、任せて！\n愛する弟くんへ、と♪", 4, 0, 1, 7122, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120001, 1, "ふんふふ～ん♪\n魔法少女お姉ちゃんの\nテーマソング完成っ♪", 101, 0, 4, 7123, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120001, 1, "えへへ、弟くんからの\n愛と希望を感じるよ…\n更に変身しちゃうぞ♪", 102, 0, 8, 7124, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120001, 1, "弟くんの朝を魔法で\n守りに来たよ！\n希望の朝だねっ♪", 1, 1, 1, 7125, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120001, 1, "弟くんのお昼を魔法で\n守りに来たよ！\n愛のお弁当をどうぞ♪", 2, 2, 1, 7126, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 120001, 1, "弟くんの夜を魔法で\n守りに来たよ！\nここからは闇の時間♪", 3, 3, 1, 7127, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120001, 1, "魔法少女の一休み♪\n愛と希望と…闇の力が\n回復していくね～♪", 2, 0, 1, 7129, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120001, 1, "かわいいかわいい♪\nお姉ちゃんの魔法で\n増やしちゃうぞ…♪", 3, 0, 1, 7130, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120001, 1, "この香り…\n弟くんと同じ…\nくんくん、くんくん…", 4, 0, 1, 7131, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120001, 1, "体を温めておいて…\nこの熱を、ぜ～んぶ\n弟くんに伝えるよ…！", 5, 0, 1, 7132, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120001, 1, "ミニ弟くんを呼ぶには\nいっぱい食べないと…\n魔法ってお腹減るから", 7, 0, 1, 7134, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 120001, 1, "弟くんの平和を守る…\nそのためには\nチェックチェック…♪", 8, 0, 1, 7135, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125301, 1, "この服も、最初は\n慣れなかったのですが\n今ではすっかり\n体に馴染んでおります", 1, 0, 1, 7137, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125301, 1, "自然を守るためにも\nお勉強は欠かせません\n日々精進でございます", 2, 0, 1, 7138, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125301, 1, "採取した薬草を\n調合しておきましょう\nいつ必要になるか\n分かりませんからね", 3, 0, 1, 7139, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125301, 1, "合宿は過酷でしたが\nおかげでちょっと\n力持ちになったような\n気がいたします…♪", 4, 0, 1, 7140, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125301, 1, "わんわんのほうの\n主さまに、ごはんを\nあげる時間ですね…", 5, 0, 1, 7141, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125301, 1, "もっともっと訓練や\nお勉強を重ねて\n主さまのお役に\n立たなくては…", 101, 0, 4, 7142, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125301, 1, "おや、そろそろ\nわんわんのほうの\n主さまをお散歩に\n連れていく時間です", 102, 0, 8, 7143, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125301, 1, "主さま、よろしければ\nお散歩に行きませんか\n今日の森は穏やかで\n過ごしやすいかと", 1, 0, 1, 7144, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125301, 1, "わんわんのほうの\n主さまも、今では\n【美食殿】の大切な\n仲間でございます", 2, 0, 1, 7145, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125301, 1, "森には危険が\nいっぱいでございます\nケガをなさらないよう\nお気をつけください", 3, 0, 1, 7146, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125301, 1, "もし森や動物のことで\n気になることがあれば\n気軽にお尋ねください", 4, 0, 1, 7147, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125301, 1, "森へ行く前には\n必ず装備の点検を\nお願いいたします\n約束ですよ、主さま？", 101, 0, 4, 7148, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125301, 1, "森の中で主さまたちと\nゆったり過ごす時間は\nわたくしにとって\n大切なひとときです", 102, 0, 8, 7149, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125301, 1, "おはようございます\n朝ごはんをしっかり\n食べて、お勤めに\n備えましょう", 1, 1, 1, 7150, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125301, 1, "わんわんのほうの\n主さまにおやつを\nあげる時間です\nどうぞ、召し上がれ", 2, 2, 1, 7151, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125301, 1, "どれだけ野営に\n慣れたとしても\nベッドで眠れるなら\nそうしたいところです", 3, 3, 1, 7152, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125301, 1, "こちらなのですが\nわんわんのほうの\n主さまもお気に入りの\nようです", 2, 0, 1, 7154, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125301, 1, "わんわんのほうの\n主さまにも、こうした\n玩具を与えるべき\nでしょうか…？", 3, 0, 1, 7155, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125301, 1, "心地よい香りです\n次は、自然由来の\n香料も試しましょう", 4, 0, 1, 7156, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125301, 1, "暖をとるのは\n野営の時などに\nとても大事なことで\nございますね", 5, 0, 1, 7157, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125301, 1, "食べられる時に食べて\nおくのも、大自然では\n重要でございます", 7, 0, 1, 7159, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125301, 1, "より知識を蓄え\n自然の力を活用できる\nようにいたしましょう", 8, 0, 1, 7160, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100901, 1, "今回の「冥風戦記」も\nなかなかよく書けたぞ\nふふっ、シグルドも\n喜んでくれるかな？", 103, 0, 12, 7162, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100901, 1, "貴様のためならば\n我が真なる魔力\nいつでも\n解き放とうぞ！", 103, 0, 12, 7163, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125401, 1, "ニャットちゃんに\nブラッシングを\nしてあげようかな", 1, 0, 1, 7164, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125401, 1, "ふふ…\nこれがレンジャーの\n制服ですか", 2, 0, 1, 7165, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125401, 1, "そろそろ\nパトロールの\n時間だ…", 3, 0, 1, 7166, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125401, 1, "魔物について\nもっと知りたいな\n本を探しに行こう", 4, 0, 1, 7167, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125401, 1, "レンジャーになって\n体調を崩す日が\n減った気がします…", 5, 0, 1, 7168, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125401, 1, "本だけじゃわからない\n世界のことを、\nもっと知りたいです…", 101, 0, 4, 7169, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125401, 1, "こんな私にも\nできることはある…\nそう信じています", 102, 0, 8, 7170, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125401, 1, "あの…良かったら\n一緒に見回りへ\n行きませんか？", 1, 0, 1, 7171, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125401, 1, "またあなたと一緒に\nレンジャーの仕事が\nできたらうれしいです", 2, 0, 1, 7172, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125401, 1, "けほっこほっ\n…だ、大丈夫です\nたまに出るだけなので", 3, 0, 1, 7173, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125401, 1, "ニャットちゃん\nあなたにも\n懐いてきましたね", 4, 0, 1, 7174, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125401, 1, "ニャットちゃんと\n散歩に出かけようかな\n…あの人も誘って", 101, 0, 4, 7175, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125401, 1, "あなたと肩を並べて\nレンジャーの仕事が\nできてうれしいです…", 102, 0, 8, 7176, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125401, 1, "おはようございます\nレンジャー・シオリ\n元気に出勤します", 1, 1, 1, 7177, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125401, 1, "ニャットちゃんにも\nお昼ごはんを\n用意しなくちゃ", 2, 2, 1, 7178, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125401, 1, "明日は仕事だから\n早起きしなくちゃ\nおやすみなさい", 3, 3, 1, 7179, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125401, 1, "ふぅ…仕事で\n走り回ったので\n少し休憩します", 2, 0, 1, 7181, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125401, 1, "仕事が終わった後に\nこうして触れていると\n癒されますね…", 3, 0, 1, 7182, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125401, 1, "森の香りがします…\n今日はよく\n眠れそうです…", 4, 0, 1, 7183, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125401, 1, "ニャットちゃんと\n同じぐらい\n暖かいです…", 5, 0, 1, 7184, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125401, 1, "レンジャーの仕事を\nしていると\nお腹が空きます", 7, 0, 1, 7186, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125401, 1, "どんなに忙しくても\n読書だけは\nやめられませんね", 8, 0, 1, 7187, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125801, 1, "兄様のお留守の間\nわたくしが、兄様の\n御家を守ります！", 1, 0, 1, 7189, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125801, 1, "に、に、兄様の御家…\n招待いただいた以上は\n失礼のないよう\n振る舞わなくては…！", 2, 0, 1, 7190, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125801, 1, "ふふっ…兄様の素敵な\nお人柄が、家中から\nにじみ出ています…♪", 3, 0, 1, 7191, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125801, 1, "靴を脱がずに\n御家に上がるのに\n慣れていなくて…あ！\n飛べばいいのでは…！", 4, 0, 1, 7192, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125801, 1, "ここに花を生けたら\n華やぎそうですね\n兄様はお花…\n好きでしょうか…？", 5, 0, 1, 7193, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125801, 1, "ずっとここにいたい…\nなどと言ったら\n兄様を困らせて\nしまいますよね…", 101, 0, 4, 7194, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125801, 1, "お料理などを作って\n兄様を出迎えられたら\nいいのに…料理の腕も\n磨かなくては…！", 102, 0, 8, 7195, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125801, 1, "このような姿で\n失礼しております…！\nネ、ネズミがいたので\nつい本気を…！", 1, 0, 1, 7196, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125801, 1, "おかえりなさいませ\nお忙しい御身をどうぞ\n癒してくださいね", 2, 0, 1, 7197, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125801, 1, "兄様の話し相手…！？\nど、どうしましょう…\nわたくしには面白い\n話なんて…はうぅ…！", 3, 0, 1, 7198, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125801, 1, "なぜか、動物などに\n好かれる体質で…\n迷い猫捜しのバイトの\nお役に立てますか？", 4, 0, 1, 7199, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125801, 1, "に、兄様の家に来たら\n『添い寝』をするのが\n礼儀とリアさんが…\n本当…でしょうか？", 101, 0, 4, 7200, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125801, 1, "わたくしはここに\nいて…よいのですね？\n『仲間』として…", 102, 0, 8, 7201, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125801, 1, "朝は得意なんです\nもし兄様が苦手なら\nわたくしが起こして\n差し上げますよ", 1, 1, 1, 7202, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125801, 1, "兄様…昼餉を作って\nみたのですが、失敗\nしてしまいました…", 2, 2, 1, 7203, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125801, 1, "ふあぁ…\nはっ！　お…\nお恥ずかしい所を…！", 3, 3, 1, 7204, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125801, 1, "こ、これは…！\n人様の御家なのに\nつい…駄目になって…\nしまいますぅ…！", 2, 0, 1, 7206, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125801, 1, "と、飛びかかっては\nこない…ですよね…？", 3, 0, 1, 7207, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125801, 1, "すぅ…はぁ…\nこのかぐわしさ\n心が洗われます…", 4, 0, 1, 7208, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125801, 1, "なんて暖かい…\n兄様の優しくお広い\n御心のようです…♪", 5, 0, 1, 7209, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125801, 1, "こんなに食べていたら\nはしたない…\nでしょうか？", 7, 0, 1, 7211, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125801, 1, "兄様の本棚はまるで\n知識の宝庫ですね\n己の世間知らずぶりを\n実感してしまいます", 8, 0, 1, 7212, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125501, 1, "次の冒険に向けて\n騎空士弁当を作ろっと\nまずはおにぎりから！", 1, 0, 1, 7214, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125501, 1, "魔法の修行しなくちゃ\nもっともっと頼れる\n団長になりたいから！", 2, 0, 1, 7215, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125501, 1, "ビカラちゃんを\n遊びに誘いたいけど…\nどういう場所が\n好きなんだろう？", 3, 0, 1, 7216, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125501, 1, "仕事を真面目に\nしっかりこなすことが\n騎空艇への\n一番の近道だよね", 4, 0, 1, 7217, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125501, 1, "ここにいる人の中で\n騎空団に興味ある人\nいたりしないかな…？", 5, 0, 1, 7218, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125501, 1, "これまでも想い出は\nいっぱいできたけど…\nこれからもたくさん\n想い出を作りたいな！", 101, 0, 4, 7219, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125501, 1, "仲間がいるから\n騎空士は限界だって\n超えられるんだね", 102, 0, 8, 7220, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125501, 1, "この衣装\n魔法使いらしいでしょ\n服に着られないよう\n頑張らなくちゃね！", 1, 0, 1, 7221, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125501, 1, "あなたとなら\nどんな相手だって\n怖くなんてないよ！", 2, 0, 1, 7222, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125501, 1, "カリンさんから\n新しい依頼が来たよ！\n一緒に頑張ろ！", 3, 0, 1, 7223, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125501, 1, "操舵士になるのは\nずいぶん難しそう…\nいつか頼れる仲間が\n見つかるといいな", 4, 0, 1, 7224, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125501, 1, "手合わせしたいの？\nいいよ、たまには\n団長らしいところ\n見せてあげるね！", 101, 0, 4, 7225, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125501, 1, "まだ二人きりだけど…\nこの騎空団は\n私の宝物だよ！", 102, 0, 8, 7226, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125501, 1, "おはよう～…\n昨日は遅くまで\n魔法の研究してて…\nふわぁ…眠いなぁ…", 1, 1, 1, 7227, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125501, 1, "さてと、お昼休み！\n紅茶淹れてくるから\n一緒に飲もうよ！", 2, 2, 1, 7228, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125501, 1, "遅くまでお疲れさま！\n無理しない程度に\nお互い頑張ろう！", 3, 3, 1, 7229, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125501, 1, "魔法の研究は\n一旦、休憩！\n頭を休めなくちゃ", 2, 0, 1, 7231, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125501, 1, "魔法みたいに\nかわいい！\n騎空団に入る？\nなんてね！", 3, 0, 1, 7232, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125501, 1, "いい匂い…\nそれになんだか\n集中力が増したかも", 4, 0, 1, 7233, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125501, 1, "体だけじゃなくて\n心までポカポカする…\nなんだか魔法みたい", 5, 0, 1, 7234, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125501, 1, "いろいろ考え事したら\nお腹が空いちゃった！\nいただきまーす！", 7, 0, 1, 7236, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125501, 1, "『摂理の彼方へ\n至るには…』\n…この魔導書\n勉強になるなあ", 8, 0, 1, 7237, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125701, 1, "こちらの服を着ると\n冒険に出かけたく\nなっちゃいますね", 1, 0, 1, 7239, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125701, 1, "冒険者の心得その一\n準備はできる限り\nしっかりとこなすべし\nなんてどうでしょう？", 2, 0, 1, 7240, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125701, 1, "調合に必要な素材は\nあれとこれとそれと…\n時間があるとついつい\n考えちゃいますね", 3, 0, 1, 7241, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125701, 1, "せっかくですから\nより良い素材を\n自分で集めたいですね", 4, 0, 1, 7242, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125701, 1, "このポーションが\nみなさんの役に立つと\nうれしいですね", 5, 0, 1, 7243, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125701, 1, "冒険者の心得その二\n危なくなった時は\nまず逃げるべし！\n大事なことですね", 101, 0, 4, 7244, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125701, 1, "あの人の冒険は\nいつも楽しそうで\n元気がもらえます", 102, 0, 8, 7245, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125701, 1, "ぜひおすすめしたい\n依頼があるんです\nこちらをどうぞ♪", 1, 0, 1, 7246, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125701, 1, "ほしいお薬があれば\n錬金術で作って\nさしあげますよ", 2, 0, 1, 7247, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125701, 1, "依頼で気になる\nところがあれば\n何でもご確認ください", 3, 0, 1, 7248, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125701, 1, "このギルドハウスに\n錬金術の実験設備を…\nえ？　だめですか？", 4, 0, 1, 7249, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125701, 1, "ポーションをどうぞ\n大丈夫、効果は\n確認済みですので♪", 101, 0, 4, 7250, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125701, 1, "次はどんな冒険に\n行かれるんですか？\nいつでもお供しますよ", 102, 0, 8, 7251, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125701, 1, "今日もいい一日に\nなりそうですね\n冒険日和といった\nところでしょうか", 1, 1, 1, 7252, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125701, 1, "いい時間ですし\nランチにしませんか？\n次の冒険の\n作戦会議も兼ねて♪", 2, 2, 1, 7253, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125701, 1, "夜も遅いですし\n明日の冒険のため\n今日は早く寝ましょう", 3, 3, 1, 7254, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125701, 1, "ふう…やはり冒険は\n事務仕事と違いますね\n少し休憩を…", 2, 0, 1, 7256, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125701, 1, "冒険は緊張の連続です\nかわいい物で\n心を癒しましょう", 3, 0, 1, 7257, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125701, 1, "ふむ、この香りは…\n素晴らしいバランスで\n調合されていますね", 4, 0, 1, 7258, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125701, 1, "この火力…\n錬金術の調合に\n使えそうですね", 5, 0, 1, 7259, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125701, 1, "冒険の際は食べられる\n時に食べておく！\n…とペコリーヌさんも\nおっしゃってましたね", 7, 0, 1, 7261, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125701, 1, "ふむふむ…なるほど\nこんな調合も\nあるのですね…\n奥が深いです", 8, 0, 1, 7262, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106101, 1, "アイツどこ行った…？\n戦うならアタシも\n連れてけよなー！", 103, 0, 12, 7264, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106101, 1, "今日も悪いヤツ退治…\nの前に腹ごしらえだ！\nなんか食べに行くぞ！", 103, 0, 12, 7265, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125601, 1, "みんなー！\nギルドハウスで\nぼくと握手！", 1, 0, 1, 7266, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125601, 1, "種族も年齢も違うけど\nみんな楽しそうだね！\nやっぱりここはぼくの\nゆ～とぴあさ！", 2, 0, 1, 7267, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125601, 1, "ハハッ！　まるで\nパーティー会場だね！\nこれは楽しませがいが\nあるってものだよ！", 3, 0, 1, 7268, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125601, 1, "やぁみんな！\nご利益満点のネズ耳は\nいかがかな？\nひとつ３ルピだよ！", 4, 0, 1, 7269, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125601, 1, "ここにいれば\n遊び相手に困らないね\nかしこみかしこみ～！", 5, 0, 1, 7270, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125601, 1, "どこか遊びに\n行こうかな♪\nそうだ、あの人にも\n声をかけてみよう！", 101, 0, 4, 7271, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 125601, 1, "あの人といると\nぼくまで楽しくなって\nくるんだよね！\n毎日パレード気分さ！", 102, 0, 8, 7272, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125601, 1, "ネズ耳の在庫が\n切れたんだ！\n作るのを\n手伝ってくれるかい？", 1, 0, 1, 7273, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125601, 1, "ん？　握手かい？\nいいとも！\nいつでも何度でも\nしてあげるよ！", 2, 0, 1, 7274, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125601, 1, "この衣装に興味が\nあるのかい？\nきみ、なかなか\n見る目があるね！", 3, 0, 1, 7275, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125601, 1, "しばらくここに厄介に\nなるつもりなんだ\nあとで街を案内\nしてくれるかい？", 4, 0, 1, 7276, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125601, 1, "とっておきのチーズ\nきみと半分こだ！\nみんなには内緒だよ？", 101, 0, 4, 7277, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125601, 1, "きみはいてほしい時に\nいつもそばに\nいてくれるね\n本当にかしこみだよ！", 102, 0, 8, 7278, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125601, 1, "やぁ、おはよう！\n気持ちのいい朝だね\n今日も楽しくネズ耳\n売りにはげもうか！", 1, 1, 1, 7279, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125601, 1, "お昼だね！\nおいしいチーズを\n持ってきたから\nぼくと半分こだ！", 2, 2, 1, 7280, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 125601, 1, "今日も一日\n楽しかったね！\nいい夢が見られること\n間違いなしさ！", 3, 3, 1, 7281, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125601, 1, "ぼくにも休息は\n必要さ！\nリラックス〜\nリラックス〜♪", 2, 0, 1, 7283, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125601, 1, "ぼくのぬいぐるみも\n作って売ろうかな…\n修繕費も一気に\n集まるかもね！", 3, 0, 1, 7284, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125601, 1, "いい香りだね！\nトーゴクのお香に\nよく似てるよ", 4, 0, 1, 7285, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125601, 1, "はぁ…温まるねぇ…\nそうだ、この火で\nチーズでも炙って\nみようかな", 5, 0, 1, 7286, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125601, 1, "うーん、この味…\nトーゴクを思い出すよ\nどれ、もう一つ\nいただこうか！", 7, 0, 1, 7288, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 125601, 1, "これはランドソルの\nガイド本だね！\nありがたく参考に\nさせてもらうよ！", 8, 0, 1, 7289, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126201, 1, "まさか、わらわが\nこのような装束を\n纏う日がくるとはな…\n感慨深いものじゃ", 1, 0, 1, 7291, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126201, 1, "わらわの美貌で\nこのギルドハウスも\nより華やかに\nなったようじゃな", 2, 0, 1, 7292, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126201, 1, "つまらぬ戦いで\n晴れ着を汚しとうない\n今日は一日\nゆっくりするかの", 3, 0, 1, 7293, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126201, 1, "今日はわらわが食事の\n準備をして、あやつの\n帰りを待ってやろう\n献立は何がよいかの…", 4, 0, 1, 7294, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126201, 1, "ユキといったか…\n掴みどころのない\nやつじゃが、根性は\n認めてやるかの", 5, 0, 1, 7295, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126201, 1, "こうして、仲間と\nのんびり過ごす時間も\n悪くないのう♪", 101, 0, 4, 7296, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126201, 1, "あやつを誘って\n月を見に行こうかの\n夜が楽しみじゃ♪", 102, 0, 8, 7297, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126201, 1, "いかなる衣装で\nあろうとも、わらわは\n完璧に着こなして\nしまうのじゃ♪", 1, 0, 1, 7298, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126201, 1, "なんじゃ？\nわらわの姿に\n見惚れておったとな？\nす、素直でよいのじゃ", 2, 0, 1, 7299, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126201, 1, "吸血鬼であるわらわに\n相応しい装束じゃろう\nあとは真っ赤な血を\n添えたいのう…", 3, 0, 1, 7300, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126201, 1, "お主、疲れておるな？\nわらわが癒してやろう\nほれ、近う寄るのじゃ", 4, 0, 1, 7301, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126201, 1, "お主がおるだけで\nギルドハウスが\n賑やかになるのう\nある種の才能じゃな♪", 101, 0, 4, 7302, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126201, 1, "眷属よ、風呂にするか\nご飯にするか\nそれとも、わらわに…\nいや、忘れるのじゃ", 102, 0, 8, 7303, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126201, 1, "朝一番、わらわに\n会いたかったとな…\n眷属よ\n褒めてつかわすぞ♪", 1, 1, 1, 7304, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126201, 1, "眷属よ、わらわが\n食事を用意したぞ\n忌々しい太陽に負けず\n頑張るのじゃ", 2, 2, 1, 7305, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126201, 1, "眠るには惜しい夜じゃ\n眷属よ、もうしばらく\nわらわの傍に\nおるがよい", 3, 3, 1, 7306, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126201, 1, "ふかふかじゃの♪\n今度あやつにも\n座らせてやろう", 2, 0, 1, 7308, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126201, 1, "愛いやつじゃ\n少しばかり、わらわが\n遊んでやろうかの", 3, 0, 1, 7309, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126201, 1, "気品ある香りじゃのう\nわらわの古城にも\n一つ欲しいところじゃ", 4, 0, 1, 7310, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126201, 1, "よいぬくもりじゃ…\n忌々しい太陽とは\n大違いじゃの", 5, 0, 1, 7311, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126201, 1, "味付けの参考に\nあと、もう一つだけ…\n止まらんのじゃ…", 7, 0, 1, 7313, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126201, 1, "吸血鬼の弱点について\n記されておるのか…\nふむ、眠気が\nさめてしもうたぞ", 8, 0, 1, 7314, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100801, 1, "このままボクが美しく\nなり続けても、彼なら\nきっと隣にいてくれる\nはずだよね！", 103, 0, 12, 7316, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100801, 1, "もっとそばに来なよ\nボクの隣は\nキミのための\n特等席なんだから", 103, 0, 12, 7317, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126301, 1, "ドレスの影響かな…\n前より黒や赤が\n好きになったかも", 1, 0, 1, 7318, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126301, 1, "ボクがいるだけで\nここがぐんっと\n華やかになったね♪", 2, 0, 1, 7319, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126301, 1, "ボクを描いた絵画\n今度持ってきて\n飾ってあげようかな", 3, 0, 1, 7320, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126301, 1, "ボクはボクに\n魅了されっぱなしさ♪\nまさに魔性の存在だよ", 4, 0, 1, 7321, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126301, 1, "美のアドバイスを\n聞きたい人いるかい？\nボクがもっともっと\n輝かせてあげるよ♪", 5, 0, 1, 7322, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126301, 1, "あの人、今度はボクを\nどう褒めるだろう？\nもし語彙が尽きても\n許してあげよっと♪", 101, 0, 4, 7323, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126301, 1, "あの人に似合うコーデ\n考えてあげよっと\n案外、なんでも\n着こなしそうだよね♪", 102, 0, 8, 7324, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126301, 1, "吸血鬼になるなんて\nやっぱりゴメンだね！\n永遠の美よりも\n大事なことがあるのさ", 1, 0, 1, 7325, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126301, 1, "お肌にいいものは\n大体好きだよ\nトマトジュースとかね", 2, 0, 1, 7326, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126301, 1, "銀のアクセサリーが\nボクは大好きでね\nいつでもプレゼント\n待ってるよ♪", 3, 0, 1, 7327, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126301, 1, "イリヤみたいにお城に\n住んでみたいんだ♪\n美を統べる者に\n相応しいでしょ？", 4, 0, 1, 7328, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126301, 1, "今のボクがカップを\n触ると壊しちゃうんだ\nだからほら、キミが\nボクに紅茶を飲ませて", 101, 0, 4, 7329, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126301, 1, "キミも美を磨けば\nもっと輝けるはずさ！\nボクがメイクとか\nしてあげようか？", 102, 0, 8, 7330, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126301, 1, "んん、まだ眠い…\n美のためにも、\nもうひと眠りするよ…", 1, 1, 1, 7331, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126301, 1, "ボクにぴったりな\n日傘を買ったんだ♪\n散歩にでも行こうよ！", 2, 2, 1, 7332, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126301, 1, "夜こそボクの時間！\nさぁ、あまねく人々へ\nボクの美を広めよう！", 3, 3, 1, 7333, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126301, 1, "リラックスは大事だよ\n心の余裕こそが\n美の秘訣だからね♪", 2, 0, 1, 7335, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126301, 1, "キミもボクの\n美の虜にしてあげる♪\nうれしいでしょ？", 3, 0, 1, 7336, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126301, 1, "人々を魅了するほどの\n魔性の香り…\nボクにふさわしいね♪", 4, 0, 1, 7337, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126301, 1, "冷えは美の大敵さ\nボクのように\n美しくなりたいなら\n体を温めるべきだよ", 5, 0, 1, 7338, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126301, 1, "食事をするボクの\nなまめかしさ…♪\n吸血鬼すらも\nドキッとするだろうね", 7, 0, 1, 7340, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126301, 1, "吸血鬼って\n美しい存在だよね…\nボクほどではないけど", 8, 0, 1, 7341, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126401, 1, "ここがナイトさんの\nお家なんですねーっ！\n雰囲気そっくりっ！", 1, 0, 1, 7343, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126401, 1, "ドアも壁もお菓子じゃ\nないんですよねっ？\n不思議ですねーっ♪", 2, 0, 1, 7344, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126401, 1, "失礼のないように\n気をつけないとっ！\nライラエルさまとも\n約束してますからっ！", 3, 0, 1, 7345, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126401, 1, "向こうに帰るころには\nお土産話がいっぱい\nできてるといいな～♪", 4, 0, 1, 7346, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126401, 1, "戦闘用のアームの他に\n観光用のアームも\nあったらいいのにな～", 5, 0, 1, 7347, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126401, 1, "ナイトさんの充填と\nライラエルさまの充填\nどっちも違って\nどっちも大好きです♪", 101, 0, 4, 7348, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126401, 1, "…ナイトさんが\nマスターになって\nくれたら…えへへっ\nなんでもないですっ！", 102, 0, 8, 7349, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126401, 1, "お手伝いだったら\n任せてくださいっ！\nお家の移転とか\nしちゃいますかっ？", 1, 0, 1, 7350, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126401, 1, "こっちに来るときは\nポポおじいさんが\nしっかりメンテして\nくれるんですよっ♪", 2, 0, 1, 7351, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126401, 1, "お家の中にいるのも\nいいですけど、お外も\n気になります～っ！\nお出かけしましょう♪", 3, 0, 1, 7352, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126401, 1, "ヴォルガーノさんに\nそっくりの子猫さんを\n見かけたんですっ！\nご親戚ですかねっ？", 4, 0, 1, 7353, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126401, 1, "ここだけの話ですよ？\n…実はケーブルって\n触られると、ちょっと\nくすぐったいんですっ", 101, 0, 4, 7354, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126401, 1, "このお家の持ち主は\nナイトさんですよね？\nそれじゃここでは\nご主人さまですねっ", 102, 0, 8, 7355, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126401, 1, "おはようございますっ\n今日もフルパワーで\nがんばりましょうっ♪", 1, 1, 1, 7356, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126401, 1, "お昼の時間ですねっ！\nランチの充填、\nお願いしまーすっ♪", 2, 2, 1, 7357, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126401, 1, "そろそろ眠らなくて\n大丈夫ですかっ？\n夜ふかしさんなら\nお付き合いしますよ♪", 3, 3, 1, 7358, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126401, 1, "エネルギーの消費を\n抑えるのも大切\nですよね～っ", 2, 0, 1, 7360, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126401, 1, "わたしが触っても\n平気なんて…！\nかわいくて頑丈っ！\n好きですっ！", 3, 0, 1, 7361, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126401, 1, "センサーによると…\nこの香りはいい香りに\n該当するみたいですっ\nなるほどーっ！", 4, 0, 1, 7362, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126401, 1, "あったかいのって\nいいですよねっ！\nみなさんうれしそう\nですしっ♪", 5, 0, 1, 7363, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126401, 1, "これがこっちの世界の\nごはんなんですねっ\nおいしい気がします！", 7, 0, 1, 7365, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126401, 1, "そーっとそーっと…\n本を破かないように…\n書いてある内容より\n難しいです～っ！", 8, 0, 1, 7366, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126701, 1, "ウィスタリア家の\nプライベートビーチも\nご案内しますわね♪", 1, 0, 1, 7368, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126701, 1, "世界を巡る\nリゾートクルーズも\nご用意できますわよ", 2, 0, 1, 7369, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126701, 1, "避暑地に専用の\n別荘がありますの\n今度ご招待しますわね", 3, 0, 1, 7370, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126701, 1, "日焼けは乙女の\n天敵ですわね\n備えはしっかりと…", 4, 0, 1, 7371, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126701, 1, "線香花火だけでは\n物足りませんわね\nもっとドカーンと…", 5, 0, 1, 7372, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126701, 1, "ギルド活動も全力で！\nバカンスも全力で！\nそれが私ですわ！", 101, 0, 4, 7373, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126701, 1, "私のセンスで選んだ\n特注の水着ですもの\n似合って当然ですわ♪", 102, 0, 8, 7374, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126701, 1, "ご希望ならすぐにでも\n夏の飛空艇遊覧ツアー\nへご招待しますわ♪", 1, 0, 1, 7375, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126701, 1, "夏の賑やかな\n雰囲気は好きですが…\n暑さには参りますわね", 2, 0, 1, 7376, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126701, 1, "日の光を浴びるほどに\n輝きを増す夏の私！\n今日も絶好調ですわ♪", 3, 0, 1, 7377, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126701, 1, "夏祭りに協力するなら\n資金と人材、どちらも\n用意しないとですわね", 4, 0, 1, 7378, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126701, 1, "あなたさま…その…\nそうも見つめられると\n照れてしまいます…", 101, 0, 4, 7379, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126701, 1, "タマキさんの働く\n海の家も訪れて\nみたいですわね♪", 102, 0, 8, 7380, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126701, 1, "港の市場が\n賑わう頃ですわね！\n私たちも参りますわよ", 1, 1, 1, 7381, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126701, 1, "冷たい飲み物でも\nご一緒にいかが？\nうちのシェフに用意\nさせてありますの", 2, 2, 1, 7382, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126701, 1, "まだもう少し…\nこの夏の夜を\nあなたさまと…", 3, 3, 1, 7383, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126701, 1, "あなたさまも\n良ければこちらへ\nいらっしゃいな♪", 2, 0, 1, 7385, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126701, 1, "夏に合わせた装いに\n替えてみるのも\n楽しそうですわね♪", 3, 0, 1, 7386, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126701, 1, "この夏めいた爽やかな\n香り…期間限定で\n売り出したいですわ", 4, 0, 1, 7387, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126701, 1, "く…我慢です…\nこの暑さに耐えてこそ\n美しさにも磨きが…", 5, 0, 1, 7388, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126701, 1, "暑い季節に働く\nみなさまへ差し入れも\n必要ですわね", 7, 0, 1, 7390, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126701, 1, "夏のトレンドを\n押さえておくのも\n大事ですわね", 8, 0, 1, 7391, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126701, 1, "上手くいけば\nヒット商品になる\nポテンシャルを\n感じますわね…！", 10, 0, 1, 7393, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126701, 1, "この極上の肌触り…\n言い値で買いますわ\n一体おいくらですの？", 11, 0, 1, 7394, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126601, 1, "せっかくのバカンス…\n自由に羽を\n伸ばすとしましょう", 1, 0, 1, 7395, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126601, 1, "たとえどのような\n秘密を持とうとも\n私には筒抜けですよ", 2, 0, 1, 7396, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126601, 1, "子供だましの怪談に\n関心はありませんが…\n霊には興味があります\n観察対象として、ね", 3, 0, 1, 7397, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126601, 1, "彼の絵日記…\nあとでゆっくりと\n読ませてもらいます", 4, 0, 1, 7398, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126601, 1, "セミの鳴き声に入道雲\nそしてクリームソーダ\nこれでこそ夏ですね", 5, 0, 1, 7399, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126601, 1, "夏休みの宿題でも\n作るとしましょうか\n彼の限界を少し超えた\n負荷を与えるには…", 101, 0, 4, 7400, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126601, 1, "彼と過ごす夏を\n特別なものにしたい…\nそれが私の望みです", 102, 0, 8, 7401, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126601, 1, "夏だからといって\n浮かれすぎないように\n節度を持って\n楽しみなさい？", 1, 0, 1, 7402, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126601, 1, "ああ見えてもマサキは\n私のプリンセスナイト\nあまり侮られては\n困りますよ？", 2, 0, 1, 7403, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126601, 1, "私の秘密ですか？\nまんじゅうが怖い…\nというのはどうです？", 3, 0, 1, 7404, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126601, 1, "ここは誰かの秘密を\n盗み聞くのに\n最適な場所ですね\n…ジョークですよ", 4, 0, 1, 7405, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126601, 1, "一緒に風鈴の音を\nのんびり楽しもう…？\n…別に構いませんよ\n貴方とならば", 101, 0, 4, 7406, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126601, 1, "貴方に約束しましょう\n決して忘れられない\n夏にしてあげますよ", 102, 0, 8, 7407, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126601, 1, "早起きとは感心ですね\nでは一緒に\n体操でもしましょうか", 1, 1, 1, 7408, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126601, 1, "昼食は流しそうめん？\n非効率的ですね\n…食べないとは\n一言も言っていません", 2, 2, 1, 7409, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126601, 1, "…貴方が望むならば\nいくらでも怖い話を\nしてあげますが…\nいかがですか？", 3, 3, 1, 7410, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126601, 1, "七冠にも休息は\n必要なのです\nしばらくここで\n休ませてもらいますよ", 2, 0, 1, 7412, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126601, 1, "なかなか気に入った…\nというのは\n誰にも秘密です", 3, 0, 1, 7413, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126601, 1, "薫風を思わせる\nこのさわやかな香り…\n悪くないセンスですね", 4, 0, 1, 7414, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126601, 1, "夏だからこそ\n冷え対策は\n重要なのです\n…とはいえ暑いですね", 5, 0, 1, 7415, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126601, 1, "私がどのような味が\n好きなのかは…\n当然秘密です", 7, 0, 1, 7417, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126601, 1, "夏を舞台にした\n小説は数多くあります\n彼が好きそうな本を\n何冊か見繕いましょう", 8, 0, 1, 7418, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126601, 1, "見た目も手触りも\n完璧に覚えました\nいつでも模倣できます", 10, 0, 1, 7420, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126601, 1, "ふむ…夏らしい\nクッションですね\nナイトプールに\n映えそうです", 11, 0, 1, 7421, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105401, 1, "レイ様も格好良いけど\n騎士さんもなかなか…\nって、今の聞こえて\nませんよねっ？", 103, 0, 12, 7422, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105401, 1, "騎士さんはシンプルな\n服が似合いそうです！\n今度、一緒に\n見に行きましょう♪", 103, 0, 12, 7423, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126801, 1, "最初はどうかな～って\n思ったけど…\n水着姿でも案外\n問題ないのよね…", 1, 0, 1, 7424, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126801, 1, "水着で飲みすぎると\nお腹が冷える、って\n言うけど…麦しゅわ\nなら問題ないわね！", 2, 0, 1, 7425, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126801, 1, "暑ければ暑いほど\nその後の麦しゅわは\nおいしくなるのよ…", 3, 0, 1, 7426, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126801, 1, "しっかりと仕事を\nこなしてから、\nしっかりと麦しゅわ！\nやっぱりこれね！", 4, 0, 1, 7427, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126801, 1, "ふふっ、デザインも\n着心地も最高ね\n奮発して用意して\n正解だったわ♪", 5, 0, 1, 7428, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126801, 1, "伝説の麦しゅわ、\n飲めるものなら\nまた飲みたいわね…", 101, 0, 4, 7429, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126801, 1, "二つ名に恥じない\n聖騎士として\n私…飲むわ！", 102, 0, 8, 7430, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126801, 1, "この格好なら水に\n濡れても大丈夫！\nなんだけど、さすがに\n書類は濡らせないわね", 1, 0, 1, 7431, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126801, 1, "そうやってじっと\n見られると…ちょっと\n恥ずかしいわ…", 2, 0, 1, 7432, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126801, 1, "今日もいい天気ね\nふふっ、あとで\nビーチに遊びに\n行きましょ♪", 3, 0, 1, 7433, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126801, 1, "水着姿も慣れると\n案外快適ね\nそうだ、キミも\n水着になっちゃう？", 4, 0, 1, 7434, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126801, 1, "例え水着姿であろうと\n聖騎士としての誇りは\n忘れてないわ！", 101, 0, 4, 7435, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126801, 1, "キミとだったら\n麦しゅわなしでも\n最高の夏のバカンスに\nなると思うわ♪", 102, 0, 8, 7436, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126801, 1, "仕事終わりの\n麦しゅわのために\n今日も一日\n頑張るわよ♪", 1, 1, 1, 7437, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126801, 1, "今日のお昼は\nどうしようかしら…\nさ、さすがに昼からは\n自重してるわよ？", 2, 2, 1, 7438, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126801, 1, "さ、一日の疲れを\n洗い流すために…\n軽～く飲むわよ！", 3, 3, 1, 7439, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126801, 1, "こうやって\n直に触れると\n肌触りの良さが\nよく分かるわ♪", 2, 0, 1, 7441, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126801, 1, "今日もかわいいわね～\nどう？　今度\nお姉さんの一人飲みに\n付き合わない？", 3, 0, 1, 7442, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126801, 1, "いい香りね～…はっ！\n麦しゅわの匂いの\nアロマポットなんて\nあったらよくない？", 4, 0, 1, 7443, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126801, 1, "こんな格好で火に\n当たってると焙られる\nスルメの気分が\nちょっと分かるわ…", 5, 0, 1, 7444, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126801, 1, "もぐもぐ…うん、\n結構おつまみにも\nむいてそうね～♪", 7, 0, 1, 7446, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126801, 1, "試験に使った参考書、\nたまには読み返して\nおかないとね", 8, 0, 1, 7447, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126801, 1, "ふふ♪\nいい手触りね～", 10, 0, 1, 7449, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126801, 1, "…はふう～…\nゴロゴロするのって\n最高ね～", 11, 0, 1, 7450, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126901, 1, "ん…やっぱり\nこの水着…\n大胆、なんじゃ…？", 1, 0, 1, 7451, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126901, 1, "海でもお世話って、\nできるのかしら…？\n浮き輪代わりになる、\nとか…？", 2, 0, 1, 7452, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126901, 1, "～♪\nふふ…海に行く\n準備も、楽しい…かも", 3, 0, 1, 7453, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126901, 1, "あうぅ…水着で\n歩いてると…お尻…\nすぐ食い込んじゃう…", 4, 0, 1, 7454, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126901, 1, "海では、はしゃいだ\nほうがいいのよね…\nわ、わー、い…♪\n…こうかしら？", 5, 0, 1, 7455, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126901, 1, "あなたとなら…\n楽しい夏の思い出、\n作れる…気がするの…", 101, 0, 4, 7456, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126901, 1, "また来年も…\n再来年も…\nあなたと海へ\n行けますように…", 102, 0, 8, 7457, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126901, 1, "水着の着方を\n練習してるの…\n後ろ、変じゃない…？", 1, 0, 1, 7458, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126901, 1, "海に行く支度…\n進んでるかしら…？\nお手伝い、する…？", 2, 0, 1, 7459, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126901, 1, "ひ、日除けの準備は\n万全よ…\nずっと太陽の下に\nいたら…溶けちゃう…", 3, 0, 1, 7460, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126901, 1, "泳ぐ練習…\nこ、ここでするのは\n…さすがに変？", 4, 0, 1, 7461, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126901, 1, "人混みに入るのは\nまだ不安で…\nでも、あなたといれば\nきっと…大丈夫…", 101, 0, 4, 7462, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126901, 1, "あなたのおかげで\n海の楽しいところを\nたくさん知ったわ…\nありがとう…♪", 102, 0, 8, 7463, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126901, 1, "お出かけの日の朝は\n早い、のよね…？\nだから、ずっと\n起きていたわ…", 1, 1, 1, 7464, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126901, 1, "今頃ビーチの日差しは\n強いんでしょうね…\nそ、想像しただけで…\nめまいが…", 2, 2, 1, 7465, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126901, 1, "ビーチの夜といえば\n花火って、聞いたわ…\nバン、って言わない\n花火…ある…？", 3, 3, 1, 7466, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126901, 1, "素肌にふわふわが…\n気持ちいい…♪", 2, 0, 1, 7468, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126901, 1, "うふふ…\n一緒に海…行く？", 3, 0, 1, 7469, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126901, 1, "ふぅ…♪\n気分まで…\n晴れやかになるわ…", 4, 0, 1, 7470, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126901, 1, "暑いような…\n寒いような…\n不思議な気持ちに\nなるわ…ね？", 5, 0, 1, 7471, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126901, 1, "泳いだ後には\nいっぱい食べたほうが\n体によさそう、ね…", 7, 0, 1, 7473, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126901, 1, "ビーチの日陰で\n読書するのも…\n気持ちよさそう…♪", 8, 0, 1, 7474, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126901, 1, "これで合ってる…？\n遊び方わからなくて…", 10, 0, 1, 7476, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126901, 1, "ふわふわ…♪\n海の底にいるみたい…", 11, 0, 1, 7477, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127001, 1, "恥ずかしい格好で\n室内を歩くこの快感\nクセになりそうです…", 1, 0, 1, 7478, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127001, 1, "この水着…\nどうしてかポーズを\n決めたくなりますね…", 2, 0, 1, 7479, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127001, 1, "海にはどんな生き物が\nいるのでしょう～\n胸が高鳴ります～♪", 3, 0, 1, 7480, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127001, 1, "夏は暑いですね…\nクウカの体が熱いのも\n夏のせい…ぐふふ…♪", 4, 0, 1, 7481, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127001, 1, "ドＳさんと無人島に\n取り残されて…という\n妄想はかなりイイです", 5, 0, 1, 7482, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127001, 1, "ドＳさんの\n欲望に満ちた眼差し…\nクウカ、受け止めます", 101, 0, 4, 7483, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127001, 1, "海に行くのが\n楽しみです…\nドＳさんと…ぐふふ♪", 102, 0, 8, 7484, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127001, 1, "「もっと過激な水着を\n見せてみろよ」なんて\nいけませんドＳさん…", 1, 0, 1, 7485, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127001, 1, "水着のままで\n踊ればいいのですね…\nドＳさんの望みなら！", 2, 0, 1, 7486, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127001, 1, "ドＳさんの熱い視線を\n感じます…じゅるり…\nもっと見てください…", 3, 0, 1, 7487, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127001, 1, "こんな格好のクウカを\n放っておくなんて…\nさすがですドＳさん！", 4, 0, 1, 7488, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127001, 1, "海で水着といえば\n何かが起きる予感…！\nドＳさんの新たな攻め\n期待していますね…！", 101, 0, 4, 7489, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127001, 1, "恥ずかしいですけど\n頑張っちゃいました\nドＳさん…こういう\n水着、好きですか？", 102, 0, 8, 7490, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127001, 1, "こんな朝からクウカを\n水着に着替えさせて…\n本当にドＳさんは\n欲望に正直ですね！", 1, 1, 1, 7491, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127001, 1, "こんな昼間から\nドＳさんがヌルヌルの\n液体をクウカに～！\nえっ…日焼け止め？", 2, 2, 1, 7492, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127001, 1, "ドＳさん…これから\nクウカと夜の海を\n見に行きませんか？\nきっと綺麗ですよ…♪", 3, 3, 1, 7493, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127001, 1, "ここなら妄想に\n集中できます～♪\nじゅるり～♪", 2, 0, 1, 7495, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127001, 1, "こうもかわいいと\n妄想に登場させるのも\n気が引けます…", 3, 0, 1, 7496, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127001, 1, "クウカを昂らせる\nこの香り…！\nは！　ドＳさんの罠！", 4, 0, 1, 7497, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127001, 1, "体を熱くさせて\n水着を脱がそうと！？\nテクニカルです…！", 5, 0, 1, 7498, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127001, 1, "クウカの口の中に\nたくさん…♪\n止まりません…！", 7, 0, 1, 7500, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127001, 1, "読書は心が穏やかに\nなりますね…！\nぐふふ…穏やかに…", 8, 0, 1, 7501, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127001, 1, "いい乗り心地です…♪\nあっ…普通に楽しんで\nしまいました…", 10, 0, 1, 7503, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127001, 1, "これは…\n妄想に役立ちそうな\n感触です…！", 11, 0, 1, 7504, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100501, 1, "成長した自分に\nあわせて、もっと\nかっこいいポーズを\n研究するッス！", 103, 0, 12, 7505, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100501, 1, "これからも一緒に\n最高のヒーローを\n目指していくッスよ♪", 103, 0, 12, 7506, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127101, 1, "お父さん、娘の\n水着姿がそんなに\nうれしいの？", 1, 0, 1, 7507, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127101, 1, "怖い話を子どもたちに\n聞かせたら泣かれて\nしまいました…\n加減が難しいです…", 2, 0, 1, 7508, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127101, 1, "海に行くと父が\n水着の女性ばかり\n追いかけて困ります…", 3, 0, 1, 7509, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127101, 1, "拾った貝殻で\n新しい占いでも\nはじめてみようかな", 4, 0, 1, 7510, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127101, 1, "貝殻を耳に当てると\n波の音が…あっ\nこれはダメです\nよくない声がします", 5, 0, 1, 7511, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127101, 1, "この飲み物、スーッと\n体に沁み込んでくる…\nあの人にも\n教えてあげたいです", 101, 0, 4, 7512, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127101, 1, "強い日差しも騒がしい\n海も苦手なのに\nあの人となら不思議と\n楽しく思えるんです", 102, 0, 8, 7513, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127101, 1, "夏限定、水と氷を\n使った特別な占いは\nいかがですか？", 1, 0, 1, 7514, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127101, 1, "暑い日々にはやはり\n怪談ですね\n厳選フルコースで\nお聞かせします", 2, 0, 1, 7515, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127101, 1, "寒気がする？　それは\n霊ではなくかき氷の\n食べ過ぎですよ", 3, 0, 1, 7516, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127101, 1, "今日の占い、あなたの\nラッキーパーソンは…\n白い水着の女性です", 4, 0, 1, 7517, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127101, 1, "あの、よければ\n日焼け止めを塗って\nもらえませんか？\n背中は届かなくて…", 101, 0, 4, 7518, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127101, 1, "海に行きませんか？\n二人きりで静かな\n砂浜を散歩したいです", 102, 0, 8, 7519, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127101, 1, "潮騒だけが響く朝の海\n心が落ち着きます\nこれから一緒に散歩に\n行ってみませんか？", 1, 1, 1, 7520, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127101, 1, "不思議です。どうして\n普通のご飯でも、海で\n食べると特別おいしく\n感じるのでしょう…", 2, 2, 1, 7521, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127101, 1, "夜の海に行きませんか\n今なら夜光虫が\n見られるかも\nしれませんよ", 3, 3, 1, 7522, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127101, 1, "夏は知らぬ間に体力を\n消耗しています\n適度に休息して\nいきましょう", 2, 0, 1, 7524, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127101, 1, "このぬいぐるみ…\n大丈夫です\n何も憑いては\nいませんよ", 3, 0, 1, 7525, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127101, 1, "ほのかにお線香\nみたいな香り…\nなんだか\n落ち着きます", 4, 0, 1, 7526, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127101, 1, "人魂、鬼火\nセントエルモの火…\n炎を見ていると色々と\n連想してしまいます", 5, 0, 1, 7527, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127101, 1, "お腹を壊さぬよう\n食い合わせには\nご注意ください", 7, 0, 1, 7529, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127101, 1, "これはいい\n怪談集ですね…\nつい読みふけって\nしまいます", 8, 0, 1, 7530, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127101, 1, "ふふ、こういうのに\n乗るとなんだか\n子どもに戻った\nみたいですね", 10, 0, 1, 7532, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127101, 1, "こ、これは…\nちょっとメルヘン\n過ぎて恥ずかしい\nですね…", 11, 0, 1, 7533, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126501, 1, "本当に素敵なお家…♪\n騎士たちの人柄を\n感じられるようで…\n不思議と落ち着きます", 1, 0, 1, 7534, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126501, 1, "調度品をつい間食…\nという習慣がこちらの\n世界にはないのですね\n…気をつけないと", 2, 0, 1, 7535, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126501, 1, "手土産のお菓子は\nどちらに置けば\nよいでしょうか？", 3, 0, 1, 7536, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126501, 1, "まるでおとぎ話の中に\n入ってしまったかの\nよう…別世界とは\nよく言ったものです", 4, 0, 1, 7537, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126501, 1, "お忍び…ふふっ♪\nときめく響きです\nなんだかお茶目で…\n密やかな印象が", 5, 0, 1, 7538, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126501, 1, "ふわ…\nああ、ついあくびが…\n居心地がよすぎるのも\n困り物ですね…ふふ♪", 101, 0, 4, 7539, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126501, 1, "騎士には不思議な力が\nあるだけでなく\n何者かの加護が\nあるようですね…", 102, 0, 8, 7540, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126501, 1, "この『王家の装備』は\n少し気難しいのです\nわたし以外が触れると\nその…ビリビリっと", 1, 0, 1, 7541, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126501, 1, "子どもたちに新しい\n遊びを教わりました♪\nにーらめっこしましょ\nあっぷっ…ぷ～っ！", 2, 0, 1, 7542, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126501, 1, "実は憧れていたのです\n友人の家を訪ねると\nいう経験に…ふふっ", 3, 0, 1, 7543, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126501, 1, "ぎるどはうす…\nぎるど活動の拠点だと\n管理協会？　の方に\nうかがいました", 4, 0, 1, 7544, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126501, 1, "あなたは運命を\n信じますか？\nわたしは…信じます\nよい運命だけですが♪", 101, 0, 4, 7545, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126501, 1, "もしわたしが本当に\n天使だったとしたら…\nどうされますか？\n…ふふ、冗談です♪", 102, 0, 8, 7546, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126501, 1, "パンケーキの香りで\n目覚める朝は\nとても良いものですよ\nぜひともお試しを♪", 1, 1, 1, 7547, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126501, 1, "ひと休みの時間ですね\n食事にお昼寝…\nすべきことを行って\n英気を養いましょう", 2, 2, 1, 7548, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126501, 1, "そろそろ休まれては？\n眠れないようであれば\n寝物語か子守歌でも\nふふ、遠慮は無用です", 3, 3, 1, 7549, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126501, 1, "すう………はっ！\n眠ってはいませんよ？\nいくら居心地が良い\nからといって…ふわぁ", 2, 0, 1, 7551, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126501, 1, "まあ、あいらしい…♪\nぜひわたしの寝室に\n招待させてください", 3, 0, 1, 7552, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126501, 1, "なんて、かぐわしい…\nオーブンからただよう\n焼き菓子の香りとは\nまた異なる魅力が…", 4, 0, 1, 7553, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126501, 1, "みなで暖をとりながら\n互いに語り笑い合う\n…心休まる時間ですね", 5, 0, 1, 7554, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126501, 1, "食は心と体に\nゆとりを生みます\n…辛い食べ物の場合？\nそれは、その…", 7, 0, 1, 7556, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126501, 1, "こちらの世界の知識を\n蓄えておかなければ\nテーブルマナーに\nついてのページは…", 8, 0, 1, 7557, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127201, 1, "新しい色の蛍光ペン\n買ったのよね\nこれで勉強が\n楽しくなるわ～♪", 1, 0, 1, 7559, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127201, 1, "学食のランチ、結構\nおいしいのよね～\n今度ペコリーヌに\n教えてやろうかしら？", 2, 0, 1, 7560, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127201, 1, "ふふふ…扉に黒板消し\n仕掛けてるの…あいつ\n引っかかるかしら？", 3, 0, 1, 7561, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127201, 1, "部活するなら運動部も\n悪くないかもね～\n夕日に向かって\n突っ走ってやるわ！", 4, 0, 1, 7562, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127201, 1, "毎日窮屈な制服着て\n学生も大変ね～\n…ま、かわいいけど", 5, 0, 1, 7563, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127201, 1, "あいつに貸したノート\n変な落書きとか\nしてなかったよね…？\n…その、相合傘とか…", 101, 0, 4, 7564, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127201, 1, "昨日の家庭科で\n作ったクッキー…\nあいつにどうやって\n渡そうかな…？", 102, 0, 8, 7565, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127201, 1, "宿題教えてって？\nしょうがないわね～\nじゃあ一緒に\nやりましょ", 1, 0, 1, 7566, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127201, 1, "あ、明日あたしたち\n日直当番よね？\nめんどい仕事は\nあんたに任せるわ～", 2, 0, 1, 7567, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127201, 1, "寮生活は楽しいけど\n就寝時間が早すぎよね\nいきなり消灯されると\nビックリするわ", 3, 0, 1, 7568, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127201, 1, "校則で禁止だけど、\nにけつでほうき通学\nとか青春っぽくない？", 4, 0, 1, 7569, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127201, 1, "制服って好きな人とも\n自然にお揃いになれて\nいいわよね…って別に\nあんたとって訳じゃ…", 101, 0, 4, 7570, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127201, 1, "あんたネクタイ\n曲がってるわよ～？\nほら直したげるから\nこっち向いて？", 102, 0, 8, 7571, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127201, 1, "おはよ～！\n今日も元気に登校よ！\n忘れ物ないようにね！", 1, 1, 1, 7572, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127201, 1, "午前の授業疲れたわ～\nしっかりお昼を食べて\n午後も頑張るわよ！", 2, 2, 1, 7573, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127201, 1, "うう…学校の怪談\n思い出しちゃった…\n…別に怖くないけど！", 3, 3, 1, 7574, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127201, 1, "ふかふか～\n…気持ち良すぎて\n制服のまま\n寝ちゃいそうね…", 2, 0, 1, 7576, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127201, 1, "かわいくて癒されるわ\n寮に持ち込んでも\n大丈夫かしら…？", 3, 0, 1, 7577, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127201, 1, "集中力の高まる香りで\n勉強の効率爆上がりね\n次のテストで\nいい点取るわよ～！", 4, 0, 1, 7578, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127201, 1, "うう、ちょっと冷えて\n来たわね…\nスカートの下に\nジャージ穿こうかしら", 5, 0, 1, 7579, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127201, 1, "もぐっ…もぐっ…\n食べとかないと教室で\nお腹なっちゃうわ～", 7, 0, 1, 7581, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127201, 1, "図書室で借りた本\n明日返却日なのよね…\n今日中に読むわよ！", 8, 0, 1, 7582, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 104301, 1, "あいつに背中を\n預けるのもいい気分だ\nいつかは、全部…", 103, 0, 12, 7584, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 104301, 1, "な、なんだよ？\nじっと見るんじゃねぇ\n尻尾が揺れちまうだろ", 103, 0, 12, 7585, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127301, 1, "魔法の練習！\nむむむ～…\nそれっ…！", 1, 0, 1, 7586, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127301, 1, "そだ！　これ着て\nルー学行こっかな？　\n制服自慢しちゃお♪", 2, 0, 1, 7587, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127301, 1, "赤点回避の魔法とか\nあったら最高じゃね？\n誰かうちに教えて～", 3, 0, 1, 7588, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127301, 1, "制服が一番映える\n場所ってどこだろ？\n教室？　遊園地？", 4, 0, 1, 7589, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127301, 1, "ベンキョーつらたん…\n今度キャルっちに\n助けてもらお", 5, 0, 1, 7590, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127301, 1, "部活か…\nヒデサイと一緒に\nやったら楽し～かも♪", 101, 0, 4, 7591, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127301, 1, "男子の制服\nもにカワだった…\n今度ヒデサイに\n借りよっかな？", 102, 0, 8, 7592, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127301, 1, "ちょすー、うちの\nさいきょー魔法で\nテンアゲしてあげる～", 1, 0, 1, 7593, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127301, 1, "九九って呪文ぽくね？\nほら、例えばヒチシ…\nヒチシ、なんだっけ？", 2, 0, 1, 7594, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127301, 1, "ヒデサイセンセー\nうちにベンキョー\n教えて！", 3, 0, 1, 7595, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127301, 1, "学割マジ助かるー\nヒデサイ、一緒に\nどんどん使ってこー♪", 4, 0, 1, 7596, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127301, 1, "今度ヒデサイに\nお弁当作ったげる\n屋上で一緒に食べよ♪", 101, 0, 4, 7597, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127301, 1, "ヒデサイのバイブス\n上がる言葉教えてよ\nつらたんな時、うちが\nいっぱい言ったげるよ", 102, 0, 8, 7598, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127301, 1, "ちょすちょす～\n今日も朝から\nバイブス十分♪", 1, 1, 1, 7599, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127301, 1, "お昼どうしよっかな\nたまにはガッツリ\nいっちゃう系？", 2, 2, 1, 7600, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127301, 1, "やべ、宿題まだ\nできてないし…\nヒデサイ助けて～", 3, 3, 1, 7601, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127301, 1, "充電ちう～\n…ガッコーのイスも\nこれにして～", 2, 0, 1, 7603, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127301, 1, "かわいいし、\n癒されるし、キュン死\nしちゃうじゃん…！", 3, 0, 1, 7604, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127301, 1, "リフレッシュの\n魔法発見！\nいい香り～♪", 4, 0, 1, 7605, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127301, 1, "ぽかぽかサイコー♪\n今日はもーずーっと\nここにいたいかも～", 5, 0, 1, 7606, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127301, 1, "超おいし～♪\n学食にあったら\n毎日リピする～", 7, 0, 1, 7608, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127301, 1, "ふむふむ…\nねむみパない…\n続きはまた今度～", 8, 0, 1, 7609, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127501, 1, "今度は子どもたちも\nサラサリアに連れて\n行ってあげたいわ", 1, 0, 1, 7611, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127501, 1, "子どもたちから\nお姫さまみたいって\n言われちゃった♪", 2, 0, 1, 7612, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127501, 1, "この剣…火を点けたり\nするのに便利なのよ\nコントロールも効くし", 3, 0, 1, 7613, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127501, 1, "あのサラサリアの\nカラっとした暑さが\nちょっと恋しいわ…", 4, 0, 1, 7614, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127501, 1, "買ってきたお土産、\nお世話になってる\nみんなに配りましょ♪", 5, 0, 1, 7615, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127501, 1, "あんたと一緒なら\nどこの国でだって\n暮らしていけそうね♪", 101, 0, 4, 7616, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127501, 1, "今の事業を大きくして\nあんたと世界中の国を\n飛び回ったり…なんて\nすてきじゃない？", 102, 0, 8, 7617, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127501, 1, "サラサリアで食べた\n料理が恋しい…？\nもう…また今度\n作ってあげるわよ", 1, 0, 1, 7618, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127501, 1, "今度旅行するなら\nどこに行こうかしら？\n希望があれば教えてね", 2, 0, 1, 7619, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127501, 1, "サラサリアの香辛料、\nランドソルでも\n人気が出そうよね\nいいビジネスの予感！", 3, 0, 1, 7620, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127501, 1, "そんなにじっと\n見られるとさすがに\n恥ずかしいってば…", 4, 0, 1, 7621, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127501, 1, "お嫁さんは憧れるけど\nお婿さんになって\nもらうのもありよね…\nあんたはどう思う？", 101, 0, 4, 7622, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127501, 1, "やっぱり、あんたには\nいつもあたしの隣に\n居てほしいな…", 102, 0, 8, 7623, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127501, 1, "陽射しが強くなる前に\n雑多な用事は\n片付けちゃいましょ", 1, 1, 1, 7624, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127501, 1, "サラサリアで食べた\n料理を再現してみたの\n味見してくれる？", 2, 2, 1, 7625, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127501, 1, "暑さに負けないように\n睡眠はちゃんと\n取っておかないと", 3, 3, 1, 7626, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127501, 1, "こっちでもあっちでも\n忙しいのは変わらない\nわね…ちょっと休憩～", 2, 0, 1, 7628, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127501, 1, "サラサリアで見た\nぬいぐるみも\nかわいかったわね", 3, 0, 1, 7629, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127501, 1, "お香って地域ごとの\n特色が出るわよね\nこの香りは…\nちょっと神秘的？", 4, 0, 1, 7630, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127501, 1, "砂漠地方も夜は冷える\nのよね。温かいものが\nあると安心するわ…♪", 5, 0, 1, 7631, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127501, 1, "食べ慣れた味って\nどうしてほっと\nするのかしら…", 7, 0, 1, 7633, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127501, 1, "今後の取引相手に\nなるかもしれないし\n向こうの文化も\n勉強しておかないと…", 8, 0, 1, 7634, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101401, 1, "犯人はこの中にいる！\n探偵として一度は\n言ってみたいセリフさ", 103, 0, 12, 7636, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101401, 1, "助手くんの情報を\n仕入れておかないと…\n理想の私を、ずっと\n見ていてほしいからね", 103, 0, 12, 7637, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127601, 1, "久々の長旅で\n里心がついちまったね\nしばらくランドソルで\nのんびりしようか", 1, 0, 1, 7638, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127601, 1, "たまには違う服装\n違う得物を使うのも\n悪くないね", 2, 0, 1, 7639, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127601, 1, "サラサリアにいた時は\n砂まみれで大丈夫かと\n思ってたけど、今じゃ\n懐かしく感じるよ", 3, 0, 1, 7640, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127601, 1, "魔法の絨毯、あれは\n楽だし速いし便利\nだったねえ…土産に\n一枚欲しかったな", 4, 0, 1, 7641, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127601, 1, "砂上での戦いは\nなかなかいい鍛錬に\nなったね…今度は\n修行しに行くとしよう", 5, 0, 1, 7642, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127601, 1, "サラサリアの人たちも\n気のいい連中だったが\n元気にやってるかねえ", 101, 0, 4, 7643, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127601, 1, "旅の恥はかき捨てって\nいうけど、悪い旅じゃ\nなかったね。次は\nアイツとどこに行こう", 102, 0, 8, 7644, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127601, 1, "この格好を冷やかしに\n来たのかい？\n生憎、もうすっかり\n慣れちまったよ", 1, 0, 1, 7645, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127601, 1, "ギルドの連中に\n土産をわたしに\n行くところなんだ\n付き合っておくれよ", 2, 0, 1, 7646, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127601, 1, "久々に水場で\n釣りがしたいねえ…\nもちろんお前さんも\nくるんだろう？", 3, 0, 1, 7647, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127601, 1, "おっと\nサラサリアの連中から\n便りが来てるよ\nさ、一緒に読もうか", 4, 0, 1, 7648, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127601, 1, "最近サラサリアの踊り\nを練習してるんだ\nあとで振付けを\n見ておくれよ", 101, 0, 4, 7649, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127601, 1, "なんだいお前さん？\n…なんだか若奥さん\nっぽいって？　まあ、\nそりゃあ、ねえ…", 102, 0, 8, 7650, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127601, 1, "いい時間だね\n日が昇りきる前に\nやることをすませ\nとかないと", 1, 1, 1, 7651, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127601, 1, "昼はどうしようかね\nサラサリア風の\n食事でもつくろうか", 2, 2, 1, 7652, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127601, 1, "さて、早めに寝ようか\n砂漠の夜は冷えるから\n…っと、まだ旅行中の\n気分でいたよ", 3, 3, 1, 7653, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127601, 1, "良い座り心地だ\nサラサリア宮殿の\n応接間を\n思い出すねえ…", 2, 0, 1, 7655, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127601, 1, "ランプの魔神に\nあげたら喜ぶかねえ？\n次に会う時までに\n見繕ってみようか", 3, 0, 1, 7656, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127601, 1, "この香り…\nサラサリア土産の\n香木かい？", 4, 0, 1, 7657, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127601, 1, "旅先での焚火を\n思い出すよ\nなんだかほっとするね", 5, 0, 1, 7658, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127601, 1, "ふふ、慣れてる味も\nいいけど、むこうの\n味付けが懐かしく\nなっちまうね", 7, 0, 1, 7660, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127601, 1, "ふむふむ…\n千夜一夜物語か\nおもしろいもんだねえ", 8, 0, 1, 7661, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126001, 1, "立派なギルドハウス♪\n帰る場所が素敵だと\n冒険に出るのも\n楽しくなりそうです♪", 1, 0, 1, 7663, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126001, 1, "お掃除でも…と\n思いましたけど、\nさすがお兄さんのお家\nきれいにしてますね♪", 2, 0, 1, 7664, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126001, 1, "わたしたちもいつか\n立派なギルドハウスが\n持てる冒険者に\nなりたいです…♪", 3, 0, 1, 7665, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126001, 1, "わたし冒険者ですけど\n家を守ってお兄さんの\n帰りを待つのも\n悪くない…かも？", 4, 0, 1, 7666, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126001, 1, "お菓子焼こうかな…♪\nちょっと多めに作って\nお兄さんにも\n食べてもらおうっと♪", 5, 0, 1, 7667, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126001, 1, "このお家、とっても\n大好きになりました♪\nだからその…もう少し\nいてもいいですか…？", 101, 0, 4, 7668, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126001, 1, "お兄さんに\n感謝の手紙を書いて\n枕元に隠して\nおこうかな…♪", 102, 0, 8, 7669, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126001, 1, "…お兄さん\nお暇なんですか？\nじゃあ一緒に冒険へ\n出かけましょう♪", 1, 0, 1, 7670, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126001, 1, "お兄さんは苦手な食材\nとかありますか？\nあの、もしよければ\n今から手料理を…♪", 2, 0, 1, 7671, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126001, 1, "まったり過ごす\nお家時間もいいですね\n冒険者には羽休めも\n大事ですから♪", 3, 0, 1, 7672, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126001, 1, "いつでもお兄さんを\n守れるように\nこの姿でいることに\n慣れておきますね…！", 4, 0, 1, 7673, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126001, 1, "お兄さん眠そうです…\nちょっとお昼寝\nしちゃいましょうか♪\nはい、膝枕…♪", 101, 0, 4, 7674, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126001, 1, "お兄さん怪我を…！？\nわたしが治療を…\nふー…ふー…", 102, 0, 8, 7675, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126001, 1, "おはようございます♪\n朝、元気に挨拶すれば\n幸せな一日が過ごせる\nそうですよ♪", 1, 1, 1, 7676, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126001, 1, "お昼ごはんの時間です\n…手がふさがってる？\nわかりました、じゃあ\nあ～んしますね♪", 2, 2, 1, 7677, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126001, 1, "ふわぁ…眠くなって\nきちゃいました…\nお兄さん…お泊りして\nいいですか…？", 3, 3, 1, 7678, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126001, 1, "ふにゃ～～…♪\nこんな姿、他の人には\n見せられません…♪\nもふもふ～～…♪", 2, 0, 1, 7680, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126001, 1, "お兄さんこういうの\n好きなのかな～…\nふふっ、わたしも♪\nぎゅ～～～っ♪", 3, 0, 1, 7681, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126001, 1, "アロマの癒し効果…\n上手くアレンジして\nみなさんの治療に\n活かせないかな～？", 4, 0, 1, 7682, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126001, 1, "は～気持ちいい～♪\n身体が暖まると\n心もぽかぽかです♪", 5, 0, 1, 7683, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126001, 1, "うふふ、おいしい♪\nプレシアちゃんなら\n無限にぱくぱく～って\nしちゃいそうです…♪", 7, 0, 1, 7685, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126001, 1, "本はわたしを\n知らない世界に\n連れていってくれる…\nまるでお兄さんです♪", 8, 0, 1, 7686, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 103801, 1, "話題の新刊…\nあの人も読んでるかな\n今度会う時、感想\n聞いてみたいな…♪", 103, 0, 12, 7688, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 103801, 1, "うにゃっ…あ、熱を\n測ってくれたんですか\nありがとうございます\nバッチリ元気ですよ♪", 103, 0, 12, 7689, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126101, 1, "お兄の、けはい…\nん、みつけた\nぎゅ", 1, 0, 1, 7690, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126101, 1, "きょろきょろ\nここも\nぱとろる、するよ", 2, 0, 1, 7691, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126101, 1, "おうち\nにぎやか\nさみしく、ない", 3, 0, 1, 7692, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126101, 1, "きょだい、おにぎり\nきょだい、肉まん\nきょだい…お兄？", 4, 0, 1, 7693, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126101, 1, "はっぴーちゃん\nどこ？\nお兄、たべた？", 5, 0, 1, 7694, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126101, 1, "くんくん…この、へや\nお兄の、におい\nすんでる\nすめる？", 101, 0, 4, 7695, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 126101, 1, "おそうじ、にがて\nでも\nれんしゅう\nきゅっきゅ", 102, 0, 8, 7696, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126101, 1, "みみ、さわり、たい？\nんー…だめ…\nむずむず\nなので", 1, 0, 1, 7697, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126101, 1, "くろぷれしあ\nかっこいい？\nん…あざます", 2, 0, 1, 7698, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126101, 1, "お兄の、おうち\nきょうみ、ましまし\nたんけん\nするね", 3, 0, 1, 7699, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126101, 1, "なでなで、される…と\nねむ…く…\nぶぅ…ぶぅ…ぶぅ…", 4, 0, 1, 7700, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126101, 1, "かみ、りぼん\nおてつだい\nよろ\nです", 101, 0, 4, 7701, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126101, 1, "ここ\nすむ\nね？\nまいにち、いっしょ", 102, 0, 8, 7702, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126101, 1, "ぐっもにん\nあさごはん\nあさごはん", 1, 1, 1, 7703, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126101, 1, "お兄\nおつかれ？\nれっつ、まいぺーす", 2, 2, 1, 7704, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 126101, 1, "ひとりで、ねれる\nでも\nひとりじゃ、なくても\nねれる、よ…？", 3, 3, 1, 7705, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126101, 1, "ふかふか\nパパの、おなか\nみたい", 2, 0, 1, 7707, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126101, 1, "こんにちは\nぷれしあ、です\nおともだち、なろ？", 3, 0, 1, 7708, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126101, 1, "いい、におい\nでも、お兄\nもっと、いい、におい", 4, 0, 1, 7709, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126101, 1, "とりま\nお肉、やこ", 5, 0, 1, 7710, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126101, 1, "もぐもぐ…\nむげん肉まん、も\nきぼう", 7, 0, 1, 7712, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 126101, 1, "ぜんぶ、よんだ\nもっと、ぶあついほん\nでも、いーよ？", 8, 0, 1, 7713, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127901, 1, "幸せなクリスマスを\n過ごすため\nギルドハウスを\nきれいにしなくちゃ♪", 1, 0, 1, 7715, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127901, 1, "えへへ♪\n彼へのプレゼント…\n枕元にそっと\nおいておきましょう☆", 2, 0, 1, 7716, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127901, 1, "クリスマスといえば\n七面鳥の丸焼き…☆\nよだれを拭いて\nいざ準備です♪", 3, 0, 1, 7717, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127901, 1, "彼に用意した\nプレゼント…\n大きすぎて\nくつ下に入りません…", 4, 0, 1, 7718, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127901, 1, "プレゼントは\n貰う方も、贈る方も\nどちらも幸せを\n感じますね…☆", 5, 0, 1, 7719, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127901, 1, "夜景が見える\nレストランであの人と\nクリスマスデート…\n憧れちゃいますね☆", 101, 0, 4, 7720, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127901, 1, "プレゼントはわたし…\n恥ずかしいですけど\nこの贈り物、彼に\n喜んでほしいです…☆", 102, 0, 8, 7721, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127901, 1, "オーナメントを\nたくさん用意したので\n二人でツリーに\n飾りつけましょう☆", 1, 0, 1, 7722, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127901, 1, "あはは、お腹\n空いちゃいました？\nパーティーまで\n我慢してください♪", 2, 0, 1, 7723, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127901, 1, "ジングルベ～ル♪\nあなたも一緒に\nクリスマスソング\n歌っちゃいましょう♪", 3, 0, 1, 7724, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127901, 1, "サプライズプレゼント\nの用意をして…って\nあなた！？　い、今の\n聞いてませんよね…？", 4, 0, 1, 7725, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127901, 1, "クリスマスの夜は\nパーティーしましょう\nできれば…その…\n二人っきりで…☆", 101, 0, 4, 7726, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127901, 1, "あなたと過ごした\n最高のクリスマス…\nずっとずっと心の奥に\nしまっておきますね", 102, 0, 8, 7727, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127901, 1, "おはようございます！\n皆の願いが叶うような\n素敵な一日に\nしましょうね☆", 1, 1, 1, 7728, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127901, 1, "お昼ですね！\nディナーの買い出しが\n済んだら、少しお昼寝\nしちゃいましょうか☆", 2, 2, 1, 7729, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127901, 1, "そろそろ就寝の\n時間ですね～\n枕元にくつ下の準備を\nお忘れなく～☆", 3, 3, 1, 7730, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127901, 1, "ふ～気持ちいい…☆\nお家でまったり過ごす\nクリスマスも\nいいですよね…♪", 2, 0, 1, 7732, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127901, 1, "かわいいですね～♪\nサンタさんからの\n贈り物でしょうか～？", 3, 0, 1, 7733, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127901, 1, "すう～…はあ～…\nすごく素敵な香り…\n炊き立てのご飯と\nいい勝負です…♪", 4, 0, 1, 7734, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127901, 1, "暖かいですけど…\nあの日の山小屋を\n思い出して\n恥ずかしいですね…☆", 5, 0, 1, 7735, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127901, 1, "むしゃむしゃもぐもぐ\n止まりませんよ\nクリスマスですから\nむしゃむしゃもぐもぐ", 7, 0, 1, 7737, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127901, 1, "物語に出てくる\nサンタさんみたいに\nわたしも幸せを\n運びたいです…☆", 8, 0, 1, 7738, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127901, 1, "わっ！\nこ…これは素敵な\nサプライズプレゼント\nですね…☆", 14, 0, 1, 7740, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127901, 1, "これを着ればわたしも\n立派なサンタさんに\nなれますかね…？", 15, 0, 1, 7741, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106601, 1, "ここが今のあたしの\n居場所ってやつです\nふふっ、なかなか\n悪くねー気分ですよ", 103, 0, 12, 7742, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106601, 1, "これからはボス代行\nとしてブイブイ\nいわせてやるです\nついてくるですよ！", 103, 0, 12, 7743, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128001, 1, "陛下～♪\n貴女のクレジッタが\nおめかしして\nはせ参じましたわ～♪", 1, 0, 1, 7744, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128001, 1, "パーティーとは景気の\n良さを見せつける場…\n倹約などは\nもっての外でしてよ！", 2, 0, 1, 7745, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128001, 1, "この天井の高さなら\n搬入できそうですわね\n陛下用の特注ケーキ♪", 3, 0, 1, 7746, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128001, 1, "…ぶぇっくしゅ！\nふう…少し背中を\n開けすぎたかしら…？", 4, 0, 1, 7747, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128001, 1, "ここは秘書の目も\n届きませんから\nチクりメモの心配も\nありませんのよね～♪", 5, 0, 1, 7748, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128001, 1, "いくらでも二度見して\nよろしくてよ？\n本当にわたくしだと\n心から納得できるまで", 101, 0, 4, 7749, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128001, 1, "袖の下以外の贈り物も\nなかなかどうして…\n悪くないものですのね", 102, 0, 8, 7750, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128001, 1, "こう見えてダンスの\nたしなみはありますの\nお相手してあげても\nよろしくってよ", 1, 0, 1, 7751, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128001, 1, "ドレスはもちろん\n特注品ですわ♪\nもしや貸衣装だと\n思いまして？", 2, 0, 1, 7752, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128001, 1, "バッグの中身？\n仕事に必要なものと…\n心を潤わせるための\n宝石がいくつか♪", 3, 0, 1, 7753, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128001, 1, "やる気というものは\n仕事に必要な条件です\nいわばドレスコードの\nようなものですわね", 4, 0, 1, 7754, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128001, 1, "全ての仕事は自分の\n血となり肉となり\nお金にもなるもの\nなのです。経験談です", 101, 0, 4, 7755, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128001, 1, "今年の教訓を活かして\n来年は更に素晴らしい\nクリスマスにして\n差し上げますわ！", 102, 0, 8, 7756, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128001, 1, "おはようございます\n今日という稼ぎ時の\nはじまりですわよ！", 1, 1, 1, 7757, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128001, 1, "安くて早くておいしい\nそれがランチに必須の\n栄養素ですわ！", 2, 2, 1, 7758, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128001, 1, "宵越しの仕事は\n安眠の敵！\n熟睡したいのであれば\n今が勝負時ですわよ！", 3, 3, 1, 7759, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128001, 1, "たまにはこうして\n休息を取ることも…\n…はっ！？\nドレスにシワが～っ！", 2, 0, 1, 7761, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128001, 1, "…わたくしの勘が\n言っていますわ\nこの愛らしさ！\n爆売れ確・定！", 3, 0, 1, 7762, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128001, 1, "いい香りですわね…\nお金の匂いには\n敵いませんけれども♪", 4, 0, 1, 7763, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128001, 1, "……ハッ！？\n完全にボーッとして\nしまいましたわ…！\nぬくもり…恐ろしい！", 5, 0, 1, 7764, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128001, 1, "考えませんわ…\n考えませんわよ！\nカロリーのことはっ！", 7, 0, 1, 7766, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128001, 1, "ビジネス本以外の本を\n読むのも、たまには\n悪くありませんわね…", 8, 0, 1, 7767, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128001, 1, "よくできていますわね\nこの絵本！\n子ども向けだと甘く\n見ていましたわ…！", 14, 0, 1, 7769, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128001, 1, "この視界の狭さ…\n思い出しますわね\nティッシュ配りの\nアルバイト時代を…", 15, 0, 1, 7770, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128101, 1, "ふふふ…こっそり\nスイーツを食べる\nリノちゃん見～っけ♪", 1, 0, 1, 7771, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128101, 1, "お姉ちゃんの抜き打ち\nお部屋チェック～♪\n…ケーキの食べこぼし\nこれはリノちゃんだね", 2, 0, 1, 7772, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128101, 1, "この衣装は目立つから\nどこにいても弟くんに\n気付いてもらえるね！", 3, 0, 1, 7773, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128101, 1, "弟くん、協力して！\nリノちゃんにお勉強\nしなさいって圧を\nかけるんだよっ…♪", 4, 0, 1, 7774, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128101, 1, "絆の力を感じるよ\nこれならもっと万全な\n弟くんのお世話が\nできそうだねっ！", 5, 0, 1, 7775, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128101, 1, "お姉ちゃんもたまには\nリノちゃんみたいに\n甘えてみようかな…", 101, 0, 4, 7776, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128101, 1, "弟くんとリノちゃん\nどっちも大切な\n私のきょうだいだよ", 102, 0, 8, 7777, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128101, 1, "分かってるよ♪\nお姉ちゃんの膝枕を\nご所望だねっ？　え？\n違う？　違わないよ？", 1, 0, 1, 7778, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128101, 1, "リノちゃんがね\nお姉ちゃんチョップを\n防ぐ妹白刃取りを\n訓練してるんだよね", 2, 0, 1, 7779, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128101, 1, "お姉ちゃん頭突きは\n封印しないとね…\nだって、今使うと\n頭飾りで威力が…ね", 3, 0, 1, 7780, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128101, 1, "ふふっ♪\n目をつぶってても\n弟くんは見えてるよ\n…そこでしょ？", 4, 0, 1, 7781, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128101, 1, "お姉ちゃんも\nリノちゃんみたいに\nかわいさいっぱいに\n振舞っちゃうぞ♪", 101, 0, 4, 7782, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128101, 1, "弟くんっ♪\nいつもお姉ちゃんの\n声が届く範囲にいてね\n…絶対だよ", 102, 0, 8, 7783, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128101, 1, "おはよう、弟くん！\nリノちゃんが起こして\nくれたんだね♪\n…先を越されちゃった", 1, 1, 1, 7784, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128101, 1, "お姉ちゃんランチの\n時間だぞ♪\nさ、食べさせてあげる\nお口を開けて…♪", 2, 2, 1, 7785, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128101, 1, "おやすみの時間だよ\n…弟くんは真ん中ね\n左右には私たち姉妹が\nいるから安心だよ♪", 3, 3, 1, 7786, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128101, 1, "お姉ちゃんは全力で\nリラックスしてても\n弟くんの声一つで\nすぐにでも動けるよ♪", 2, 0, 1, 7788, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128101, 1, "リノちゃんこういうの\n好きそうだよね\n今度プレゼントして\nみようかな？", 3, 0, 1, 7789, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128101, 1, "いい匂いがするね～\nあれ？\nリノちゃんのケーキの\n甘い匂いも混ざってる", 4, 0, 1, 7790, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128101, 1, "お姉ちゃんの\n弟くんへの愛も\n熱く燃えあがるね…！", 5, 0, 1, 7791, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128101, 1, "たくさん食べて\n栄養を補給して\nお姉ちゃん力に\n全て変換するよっ！", 7, 0, 1, 7793, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128101, 1, "ことわざ辞典…\nリノちゃんにこれを\n読ませようかな", 8, 0, 1, 7794, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128201, 1, "そろ～り、そろり…\nお姉ちゃんが居ぬ間に\nスイーツを～…♪", 1, 0, 1, 7797, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128201, 1, "いつか\nお姉ちゃんに似合う服\n作ってみたいですね", 2, 0, 1, 7798, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128201, 1, "あいたっ！？\nひらひらしたとこ\n踏んづけちゃいました", 3, 0, 1, 7799, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128201, 1, "お姉ちゃんと\nお兄ちゃんの二人から\n勉強しろって圧力が～", 4, 0, 1, 7800, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128201, 1, "私の中にも秘めた力が\n眠っているはず…\nそう、妹パワーが", 5, 0, 1, 7801, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128201, 1, "私もお兄ちゃんを\n甘やかしてみたいです\n…お姉ちゃんみたいに", 101, 0, 4, 7802, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128201, 1, "お兄ちゃんの部屋を\nこっそり拝見～♪\nはっ！？　これは\nお姉ちゃんの視線…！", 102, 0, 8, 7803, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128201, 1, "お姉ちゃん頭突きは\n大地を揺らす威力です\n私とお兄ちゃんは\n挨拶感覚で喰らいます", 1, 0, 1, 7804, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128201, 1, "お姉ちゃんチョップは\n雲を裂く切れ味です\n早く妹白刃取りを\n完成させないと…", 2, 0, 1, 7805, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128201, 1, "お姉ちゃんみたいに\n大人っぽく着こなす\nには一体どうしたら…", 3, 0, 1, 7806, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128201, 1, "弓のデザインも\nかわいいんですよっ♪\n常に持ち歩きたいです\nけど…危ないですよね", 4, 0, 1, 7807, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128201, 1, "きれいな服を着てると\nお姫さま気分に\nなれますね…あとは\n王子さまがいれば…♪", 101, 0, 4, 7808, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128201, 1, "お兄ちゃんっ！\n私は褒められて伸びる\nタイプなのでもっと\nナデナデしてください", 102, 0, 8, 7809, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128201, 1, "妹モーニングコール♪\nお姉ちゃんより先に\n起こしに来れました！", 1, 1, 1, 7810, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128201, 1, "ああっ、お姉ちゃん！\nお兄ちゃんにアーン\nするなんてずるいです\n私もしたいですっ！", 2, 2, 1, 7811, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128201, 1, "おやすみの時間ですね\nえへへ、きょうだいで\n並んで寝るのって\n楽しいですよね！", 3, 3, 1, 7812, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128201, 1, "スイーツを食べた後は\nお腹を休めましょう～\n平気平気…ちゃぁんと\n運動もしますからぁ…", 2, 0, 1, 7814, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128201, 1, "お姉ちゃんも\nこういうかわいいもの\n好きなんですかね", 3, 0, 1, 7815, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128201, 1, "くんくん…\nなんの匂いでしょうか\n新しいスイーツ？", 4, 0, 1, 7816, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128201, 1, "きょうだい３人\n肩を寄せて暖まるとか\nよくないですかっ？", 5, 0, 1, 7817, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128201, 1, "いつも元気な妹で\nいるためには\nよく食べるのが大事！", 7, 0, 1, 7819, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128201, 1, "なんか姉に関する本が\nちらほらと混ざって…\nこれお姉ちゃんの\n仕業ですね！？", 8, 0, 1, 7820, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128301, 1, "ふんふんふ～ん\nお正月で世間は\n浮かれてるですねえ\nま、いいですけど♪", 1, 0, 1, 7823, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128301, 1, "は～、お正月は\nだらだらできて\n最高ですね\n…いつも通りですけど", 2, 0, 1, 7824, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128301, 1, "ビッグな幹部は\nビッグなお年玉が\nもらえるはずです\n楽しみですね", 3, 0, 1, 7825, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128301, 1, "今度ドラちゃんに\nお年玉を持っていって\nやりたいですね", 4, 0, 1, 7826, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128301, 1, "そういえばアユアユと\n約束してたですよ…\nそろそろ出かける\nですかね", 5, 0, 1, 7827, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128301, 1, "お年玉がもらえたら\n何に使うか…\n今のうちに\n考えとくです", 101, 0, 4, 7828, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128301, 1, "年神の報酬の駄菓子が\nたまってきたですね…\nガキどもにでも\n配っちまうですよ", 102, 0, 8, 7829, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128301, 1, "あたしのレアな\n晴れ着姿、今のうちに\nしっかり目に焼き付け\nとくがいいですよ", 1, 0, 1, 7830, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128301, 1, "お兄さんは今日も\nヒマそうですねぇ…\nしょうがないから\nかまってやるですよ", 2, 0, 1, 7831, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128301, 1, "そういえばカヤぴぃ\n見なかったですか？\n喧嘩屋初売りセール\nとか言ってたですけど", 3, 0, 1, 7832, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128301, 1, "お正月ならではの\n大きなシノギ…\nなにかないですかね？", 4, 0, 1, 7833, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128301, 1, "いいとこに来たですよ\nさっき走ったら帯が\n曲がっちゃったです\n直すの手伝うです！", 101, 0, 4, 7834, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128301, 1, "年神の仕事ですか？\nこれからいくとこです\nお兄さんも一緒に\n来やがるですよ！", 102, 0, 8, 7835, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128301, 1, "ふぁあ…\nどうしたですか？\nお正月に早起きなんて\n無粋ってヤツですよ…", 1, 1, 1, 7836, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128301, 1, "小腹が減ったですね\n一緒におせちでも\nつつきにいくですよ！", 2, 2, 1, 7837, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128301, 1, "夜はあたしの時間\nと言いたいとこですが\n初夢のために早く\n寝とくですかね…", 3, 3, 1, 7838, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128301, 1, "正月は食っちゃ寝\nするのが仕事…\nって聞いたこと\nあるですよ", 2, 0, 1, 7840, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128301, 1, "ふふっ、晴れ着の\nあたしの次くらいには\nかわいいですね", 3, 0, 1, 7841, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128301, 1, "なんかいい匂い…？\nよく知らんですけど\n晴れ着からも同じ\n匂いがしてるですね…", 4, 0, 1, 7842, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128301, 1, "ふ～あったまるです…\nお正月の唯一の弱点は\n寒いことですからね…", 5, 0, 1, 7843, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128301, 1, "もぐもぐ…正月太り？\nあたしには関係ない\n言葉ですね", 7, 0, 1, 7845, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128301, 1, "あったあった…\nトーゴクには\nドラゴンの神宮も\nあるんですね", 8, 0, 1, 7846, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128401, 1, "お正月のトラブルも\nハツネちゃんが\nめでたく解決\nしてみせるのだ～！", 1, 0, 1, 7848, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128401, 1, "そうだ、新年会に\nサプライズ大作戦を\nしかけちゃおう！", 2, 0, 1, 7849, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128401, 1, "イノリちゃんも\nカスミンも振袖姿が\nかわいかったな～", 3, 0, 1, 7850, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128401, 1, "シオリンに\nお年玉あげたいし\n彼にバイトを紹介\nしてもらおうかな？", 4, 0, 1, 7851, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128401, 1, "今度はどんな凧を\n飛ばそうかな？\nみんなが驚くような\n凧がいいよね～", 5, 0, 1, 7852, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128401, 1, "ひらめいた！\n彼への年賀状は\nきらーんっ☆って\n感じにしよう！", 101, 0, 4, 7853, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128401, 1, "来年も彼の隣で\n初日の出が\n見られるといいな～", 102, 0, 8, 7854, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128401, 1, "今日はキミが\nギルドハウスに来る\n予知夢を見たんだよ～\nふふ、正夢になったね", 1, 0, 1, 7855, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128401, 1, "この振袖どうかな？\nすっごくかわいいよね\nえへへ、ありがとう～", 2, 0, 1, 7856, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128401, 1, "ハツネちゃんおみくじ\n開始！　きらーんっ☆\nキミは大吉なのだ～\nどう元気出たかな？", 3, 0, 1, 7857, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128401, 1, "今度エルフの森にある\n薬草を使って七草粥を\n作ろうと思うんだ～\nぜひ食べてほしいな♪", 4, 0, 1, 7858, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128401, 1, "お正月っぽい遊びを\n色々と用意したのだ～\nたまにはギルドハウス\nでゆっくり過ごそうよ", 101, 0, 4, 7859, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128401, 1, "もしかして眠たいの？\n…じゃあ、また膝枕\nしてあげてもいいよ？", 102, 0, 8, 7860, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128401, 1, "いっぱい寝たから\n元気いっぱいだよ～\n今日もがんばろう！", 1, 1, 1, 7861, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128401, 1, "お昼だね～\nおせちがあるから\nキミも食べるでしょ？", 2, 2, 1, 7862, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128401, 1, "そろそろ寝る時間だね\n今日はどんな夢を\n見られるかな～", 3, 3, 1, 7863, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128401, 1, "いい座り心地～\n寝正月で過ごすのも\n最高なのだ～", 2, 0, 1, 7865, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128401, 1, "かわいいな～\n癒されてぐっすり\n眠れそうだよ～", 3, 0, 1, 7866, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128401, 1, "いい香り～\nリラックスすると\n眠たくなっちゃうよ…", 4, 0, 1, 7867, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128401, 1, "ポカポカいい気持ち～\n初日の出の温もりを\n思い出しちゃうな～♪", 5, 0, 1, 7868, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128401, 1, "もぐもぐ、おいし～\nでもいっぱい食べたら\n眠くなっちゃった…", 7, 0, 1, 7870, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128401, 1, "ふむふむ\nお正月料理も\n色々あるんだね～\n今度作ってみようかな", 8, 0, 1, 7871, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 106501, 1, "最近カレを見てたら\n何故かおいしそうに\n思えてくるんだよな…", 103, 0, 12, 7873, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 106501, 1, "手握っててくれるか？\nじゃないとオレ…\n力が抑えきれない…", 103, 0, 12, 7874, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128501, 1, "つかの間の平和を享受\nしながらぬくぬくと\n読書に耽る…\nふふ、最高だね", 1, 0, 1, 7875, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128501, 1, "お正月飾りなんて\n置いたらはしゃいでる\nように見えるかな…？", 2, 0, 1, 7876, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128501, 1, "ま、まずい…\n一人遊びしすぎて\nお正月遊びをほぼ\n極めつつある…", 3, 0, 1, 7877, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128501, 1, "ミステリーは\n時と場所を選ばない\nつまり…新年でも\n探偵は休まないよ！", 4, 0, 1, 7878, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128501, 1, "お世話になった人に\n年賀状でも書こうかな\n助手くん、喜んで\nくれるかな…えへへっ", 5, 0, 1, 7879, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128501, 1, "絵馬に願い事を書こう\n助手くんと一緒に\nいられますように…♪\n…は、恥ずかしいな", 101, 0, 4, 7880, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128501, 1, "助手くんはもっと\n派手な着物のほうが\n好みだったかな…？\nき、気になる…", 102, 0, 8, 7881, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128501, 1, "やあ。助手くんも\nお正月休みを\n持て余してるのかい？\nふふ、似た者同士だね", 1, 0, 1, 7882, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128501, 1, "助手くん助手くん！\n凧あげしにいこう！\n…そ、捜査の一環で", 2, 0, 1, 7883, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128501, 1, "着物は転びそうで\n怖いんだよなあ…\nお、押さないでね？\nフリじゃないよ！？", 3, 0, 1, 7884, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128501, 1, "ひ、人目を憚る\n本を読むなら\nみんなが帰省中の今！\nはっ！　助手くん！？", 4, 0, 1, 7885, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128501, 1, "ね、ねえ…助手くん…\n一緒に初詣いかない？\nあ、いや！　深い\n意味はないんだけど！", 101, 0, 4, 7886, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128501, 1, "あはは…助手くんと\n一緒だと家でごろごろ\nしてるだけでも\n楽しいや…♪", 102, 0, 8, 7887, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128501, 1, "ふぁ～…\nお正月休みのおかげで\n昼夜逆転中だよ…", 1, 1, 1, 7888, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128501, 1, "お正月も依頼が\nひっきりなしだね\nさあ、街へ出よう！\nもちろん君も一緒にね", 2, 2, 1, 7889, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128501, 1, "も、もっと\n近くにきてもいいよ？\n冬の夜は…寒いからね", 3, 3, 1, 7890, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128501, 1, "ううぅ～…！\nこのままじゃ\n寝正月だぁ…！", 2, 0, 1, 7892, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128501, 1, "みんなが帰省中でも\nこれがあれば\n寂しくない…かな", 3, 0, 1, 7893, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128501, 1, "うーん…♪\nお正月休みの\nリフレッシュに\nちょうどいいや♪", 4, 0, 1, 7894, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128501, 1, "ふぅ…あったまるな\n冬は寒いから\n助かるよ…", 5, 0, 1, 7895, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128501, 1, "うーん、おいしい♪\n読書のお供に\nちょうどいい", 7, 0, 1, 7897, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128501, 1, "時間に余裕があると\n読書が進むよ\nいざ、本棚読破だ♪", 8, 0, 1, 7898, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128801, 1, "この新しい力…\nさらに使いこなせる\nようになるには…", 1, 0, 1, 7900, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128801, 1, "私に眠る属性の力…\n風の力以外には\nどんな力があるのかな", 2, 0, 1, 7901, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128801, 1, "今の私なら\nどこまでいけるのか…\nやれるだけ\nやってみよう", 3, 0, 1, 7902, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128801, 1, "風の力の制御…\n段々とコツが\n掴めてきたな", 4, 0, 1, 7903, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128801, 1, "風の力か…\n応用の幅は広そうだ\n何ができそうか\n色々試してみよう", 5, 0, 1, 7904, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128801, 1, "今度、私とキミで\n料理対決でもする？\n簡単には負けないよ", 101, 0, 4, 7905, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128801, 1, "この胸に溢れる\n温かな気持ちは…\nきっと彼への…", 102, 0, 8, 7906, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128801, 1, "一段と剣技が\n冴えてきたような…\nいや…油断禁物だ", 1, 0, 1, 7907, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128801, 1, "かっこいいって？\nありがとう…そんなに\n真っすぐ褒められると\n照れてしまうな…", 2, 0, 1, 7908, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128801, 1, "どれほど厚い壁が\n立ち塞がろうと\n打ち破ってみせる", 3, 0, 1, 7909, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128801, 1, "手が空いてるなら\n剣の稽古に少し\n付き合ってくれるかな", 4, 0, 1, 7910, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128801, 1, "試練を乗り越えて…\n私の中にあるものが\nはっきりと分かったよ", 101, 0, 4, 7911, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128801, 1, "キミから流れてくる\n力の温かさ…これが\n私たちの絆なんだ", 102, 0, 8, 7912, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128801, 1, "朝だよ、そろそろ\n起きる時間だ\nそれとも私に起こして\nほしいのかな？", 1, 1, 1, 7913, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128801, 1, "昼食は私が作るよ\nリクエストはある？\nできる範囲で応えるよ", 2, 2, 1, 7914, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128801, 1, "おやすみ。また明日も\n一緒に冒険しよう\n約束だよ？", 3, 3, 1, 7915, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128801, 1, "休める時は休む…\n戦い続けるには\n必要なことだからね", 2, 0, 1, 7917, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128801, 1, "心の安らぎの大切さ…\nそれを知れたのは\nキミたちのおかげだね", 3, 0, 1, 7918, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128801, 1, "気持ちが落ち着くな…\nこの香り、風の力で\n纏えたりしないかな", 4, 0, 1, 7919, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128801, 1, "暖まった空気の流れ…\n以前より風の変化に\n気づくようになったな", 5, 0, 1, 7920, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128801, 1, "この味付け…\n今度試してみようか\n味見は任せたからね", 7, 0, 1, 7922, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128801, 1, "心技体、すべてを\n鍛えるには座学も\n必要だからね", 8, 0, 1, 7923, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128901, 1, "瞑想するのに\nいいところはあるかな\n集中できて…お昼寝は\n我慢できるところ", 1, 0, 1, 7925, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128901, 1, "ここにはあったかい\n空気が流れてるなぁ…\nみんなが受け入れて\nくれるおかげだね", 2, 0, 1, 7926, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128901, 1, "まるで心の中に\n光が満ちてるみたい…\nこの気持ち、みんなに\nおすそ分けしたいな♪", 3, 0, 1, 7927, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128901, 1, "この衣装は属性の力を\n反映しやすいんだよね\nキラキラしてるのも\n納得だよ", 4, 0, 1, 7928, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128901, 1, "エルフの里のみなさん\n元気かなぁ…\nまた会いに行きたいな", 5, 0, 1, 7929, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128901, 1, "まだ変身できてる…\nわたしと騎士クンの\n修行の成果\n出てるなぁ…ふふっ♪", 101, 0, 4, 7930, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128901, 1, "この服を脱いだら\nどうなるのかな…\nた、試す勇気は\nないけど…", 102, 0, 8, 7931, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128901, 1, "ふぅ…あ、騎士クン\nちょうど瞑想してたの\n一緒に…する？", 1, 0, 1, 7932, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128901, 1, "不思議だけど\nいつの間にか\n出たり消えたりするの\n…背中の輪っか！", 2, 0, 1, 7933, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128901, 1, "この服って肌触りが\nとってもいいんだよ\nさ、触ってみる？\nスベスベだよ？", 3, 0, 1, 7934, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128901, 1, "アストラルの力…\nすごく持続できてるね\n修行の成果が出てるよ", 4, 0, 1, 7935, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128901, 1, "この力があれば\n暗いところで\n落とし物をしても\n見つけやすいかも…？", 101, 0, 4, 7936, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128901, 1, "冒険の準備なら\nできてるよ\nいつでも声をかけてね\n…楽しみにしてる！", 102, 0, 8, 7937, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128901, 1, "おはよう騎士クン\n一緒にお散歩とか…\nどうかな？", 1, 1, 1, 7938, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128901, 1, "お昼ご飯のあとの\n瞑想って難しいんだよ\n眠くなっちゃって…", 2, 2, 1, 7939, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128901, 1, "夜ふかしさんは\nほどほどにね？\n一日頑張った自分を\n休ませてあげないと", 3, 3, 1, 7940, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128901, 1, "のんびりしてたら\nケーキが食べたく\nなっちゃうなぁ…\nでも太る…よね…？", 2, 0, 1, 7942, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128901, 1, "わあ、かわいい…！\n見てるだけで\nにやけちゃう…♪", 3, 0, 1, 7943, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128901, 1, "これは…花の香りかな\n知ってる花かも…？\n当ててみようっ", 4, 0, 1, 7944, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128901, 1, "あったかいと\nどうしてこんなに\n落ち着くんだろう…", 5, 0, 1, 7945, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128901, 1, "食べ過ぎたらお腹が\n目立っちゃうかもって\n分かってるのに…！\n止められないよ～っ！", 7, 0, 1, 7947, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128901, 1, "こ、恋の精霊とか\nいたりするのかな…？\nはっ！　ダメダメっ！\n実力で頑張らないと", 8, 0, 1, 7948, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128701, 1, "お部屋の中でも\n修行、修行っ♪\nでも燃やさないように\n注意しなきゃだね", 1, 0, 1, 7950, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128701, 1, "うーん…この衣装\n厚着か薄着か\nどっちなんだろう…？", 2, 0, 1, 7951, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128701, 1, "いつもの肉球がない分\nお片付けしやすいな♪\n騎士くんのお部屋\nピカピカにしとこー！", 3, 0, 1, 7952, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128701, 1, "お休みは騎士くんと\nＢＢＱに行きたいなー\nあたしと一緒なら\n火の心配はいらないよ", 4, 0, 1, 7953, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128701, 1, "この姿ならもっと\n人を助けられる…！\nえへへっ！\n燃えてきたーーっ！", 5, 0, 1, 7954, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128701, 1, "騎士くんが帰ったら\nアイラみたいな笑顔で\nお疲れさまさまって\nお出迎えしよっと…♪", 101, 0, 4, 7955, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 128701, 1, "この姿の時、いつも\n胸が熱く燃えてるのは\nずっと騎士くんのこと\n考えてるからかな…？", 102, 0, 8, 7956, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128701, 1, "騎士くん騎士くん！\nあたしに触れると\nやけどしちゃうよ…？\nなんちゃってー♪", 1, 0, 1, 7957, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128701, 1, "おっ、騎士くん\n修行に行きたいの？\nえへへっ、いいねー♪\n二人で燃えちゃおー！", 2, 0, 1, 7958, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128701, 1, "そうだ騎士くん\nじゃんけんしよっか？\nグローブ外してるから\nチョキも出せるよっ♪", 3, 0, 1, 7959, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128701, 1, "この姿でいられる時間\nすごく増えてきた…！\nえへへ！　あたしたち\n修行の成果出てるね！", 4, 0, 1, 7960, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128701, 1, "じっと見つめないでよ\n顔が真っ赤に…やば！\nほんとに顔から\n火噴いちゃいそう…！", 101, 0, 4, 7961, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128701, 1, "火には強くなれても\n騎士くんに触られた\nほっぺはずーっと\n熱くてたまんないや…", 102, 0, 8, 7962, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128701, 1, "おはよー騎士くん！\n今日も一日\n燃え滾っていくよー！\nファイヤーッ！！", 1, 1, 1, 7963, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128701, 1, "騎士くんお昼だよ！\nちゃんとご飯食べて\n午後からの修行も\n乗り切ろうねー！", 2, 2, 1, 7964, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 128701, 1, "今日もお疲れさま！\nそうだ、寝る前に\nキャンプファイヤー\n囲んじゃおっか♪", 3, 3, 1, 7965, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128701, 1, "ぼふーんっ！\nえへへ、気持ちいい…\n修行の前に\nちょっと休憩ー♪", 2, 0, 1, 7967, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128701, 1, "かわいいな～♪\nなでなでなでなでなで\nなでなでなでなでっ！\nあははっ、摩擦熱！", 3, 0, 1, 7968, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128701, 1, "ん～いい香り♪\n少し熱したほうが\n香りが広がるね…って\n蒸発しちゃった！！！", 4, 0, 1, 7969, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128701, 1, "外でも自分の火で\nあったまれるけど\nお部屋でぬくぬく\nするのが一番だよね♪", 5, 0, 1, 7970, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128701, 1, "もぐもぐっ！\nお腹が減っては\n人助けはできずっ！\nもぐもぐもぐっ！", 7, 0, 1, 7972, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 128701, 1, "ふむふむ…\nなるほどなるほど…\n…これが上手な\nＢＢＱのやり方か…", 8, 0, 1, 7973, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129001, 1, "…壁も扉も柱も\n全て記録で見たまま\n本当に…来ているのね", 1, 0, 1, 7975, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129001, 1, "…騒がしい場所\n騎士クンが受け入れて\nくれるのをいいことに", 2, 0, 1, 7976, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129001, 1, "ここが…騎士クンの\n守りたかった場所\n騎士クンの思い出が\n詰まった場所…ね", 3, 0, 1, 7977, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129001, 1, "視線を感じる…\nこちらの様子を\n探るような…心の動き\n取るに足らないけれど", 4, 0, 1, 7978, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129001, 1, "…そこかしこで…\n言葉にもしたくない\n相手が過ごしていた\n記録を思い出す…", 5, 0, 1, 7979, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129001, 1, "人は多いけれど…\n雪深い山中よりは\n過ごしやすい…か", 101, 0, 4, 7980, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129001, 1, "自覚したことは\nあまりなかったけれど\nひどく…諦めの悪い女\nなのかもしれない", 102, 0, 8, 7981, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129001, 1, "お招きありがとう\nそれで…何をして\nいればいい…かな？", 1, 0, 1, 7982, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129001, 1, "ここは…明るいね\nわたしの城と比べると\nなんだか眩しいくらい", 2, 0, 1, 7983, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129001, 1, "仮面はもう必要ないよ\nだって…わたしの顔を\n騎士クンに見て\nもらえなくなるから", 3, 0, 1, 7984, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129001, 1, "騎士クンさえよければ\nわたしの城に専用の\n部屋を用意するよ\n…いつでも", 4, 0, 1, 7985, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129001, 1, "…騎士クンのマント\nまた貸してほしいな\nきれいに使うから\n…だめ？", 101, 0, 4, 7986, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129001, 1, "いつか二人の願いが\n重なり合えるって…\n信じているね\n…いつまでだって…", 102, 0, 8, 7987, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129001, 1, "おはよう、騎士クン\n…昨日は夢の中で\nお話ししすぎちゃって\nごめんね。ふふっ", 1, 1, 1, 7988, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129001, 1, "お腹は…空いてない？\n食べ物はないけれど\n空腹を忘れる魔法なら\nかけてあげられるよ", 2, 2, 1, 7989, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129001, 1, "騎士クン…\nまだ、眠らないの？\n…そう。それなら…\nわたしも起きているね", 3, 3, 1, 7990, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129001, 1, "肩の力を抜くには…\nまだ時間がかかりそう\nきっと…騎士クンと\n二人きりなら別なのに", 2, 0, 1, 7992, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129001, 1, "ぺんぽこりん…\nそう、確かあれも\nこんな感じだった…", 3, 0, 1, 7993, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129001, 1, "花の香りは…好き\nつかの間\n心を穏やかに\nしてくれるから", 4, 0, 1, 7994, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129001, 1, "あたたかい…けれど\n騎士クンの優しさに\n比べれば、まだまだ", 5, 0, 1, 7995, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129001, 1, "おい、しい…？\nこの感覚が、それ？\nなぜか…納得できる", 7, 0, 1, 7997, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129001, 1, "情報を入手するには\nわざわざ本を開く\n必要があるだなんて…\nプレイヤーは面倒ね", 8, 0, 1, 7998, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129301, 1, "メルクリゾートか…\n良い記憶はないな\nよし忘れよう", 1, 0, 1, 8000, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129301, 1, "知性の戦士ユニ！\nその恐るべき筋力！\nふおお！　うにゃー！", 2, 0, 1, 8001, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129301, 1, "ぽーん！\n石用の水着ください！\nセクシーロゼッタが、\n渚の視線を独占です！", 3, 0, 1, 8002, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129301, 1, "学院から奨学金を\n勝ち取るより某財団を\nだまくらかした方が\n早い気がしてきたな…", 4, 0, 1, 8003, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129301, 1, "わんつぅ、わんつぅ…\nんぎゃあ足攣ったー！\nおのれカルミナとやら\n絶対に許さん！", 5, 0, 1, 8004, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129301, 1, "ところで…なぜぼくは\nプールでもないのに\n水着のままなんだ…\nおのれ世界め！", 101, 0, 4, 8005, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129301, 1, "彼と二人で撮った動画\nうう、凝視に耐えん…\nなんだこのムズ痒さは", 102, 0, 8, 8006, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129301, 1, "なに？　水着姿で\n寒くないのかだと？\n普段からもっと薄着の\n娘が沢山いるだろう", 1, 0, 1, 8007, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129301, 1, "シノブ君にメリゾの\n招待状を送ったのだが\n御尊父の暴走を\n心配していたな…", 2, 0, 1, 8008, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129301, 1, "ミミ君らにメリゾの\n招待状を送ったのだよ\n迎える準備をせねば…\nおしゃぶり持ってる？", 3, 0, 1, 8009, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129301, 1, "本質の賢者…？\nいや元気にしているが\nなぜ君がぼくの身内を\n知っているんだ…？", 4, 0, 1, 8010, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129301, 1, "水着が似合ってる？\nな、なんだね突然…\nやめたまえよふひひ…", 101, 0, 4, 8011, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129301, 1, "今度は夏にメリゾへ\n行きたいものだ\nむろん君も一緒だぞ", 102, 0, 8, 8012, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129301, 1, "ぐああっ、朝陽が…！\n朝陽が昇っている…！\n夜行性ユニ死んじゃう", 1, 1, 1, 8013, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129301, 1, "はぁ…もう昼か…\nそろそろ寝ないとな\n規則正しい生活を\nしないと不健康だ…", 2, 2, 1, 8014, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129301, 1, "ひゃっはー、夜だー！\nオレたちの時間だぜ！\nふおおブチ上がり～！", 3, 3, 1, 8015, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129301, 1, "これは…ぼくにここで\n寝ろということか？\n一向に構わんが…", 2, 0, 1, 8017, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129301, 1, "きゃっきゃっ！\nこれかわい～！\n…よし、今日も\n子供の擬態完璧だな", 3, 0, 1, 8018, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129301, 1, "ふむ、良い香りだ\nだがそれだけか…\nとくに精神高揚効果は\nないのか…ちぇー", 4, 0, 1, 8019, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129301, 1, "火を見ていると…\n魔導罐を思いだすな…\nかに座、元気かな…", 5, 0, 1, 8020, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129301, 1, "ええい、\n固形食物はいらん！　\nポーションを出せ！", 7, 0, 1, 8022, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129301, 1, "やはり書は文系に限る\nなんと心地よい筆致…\nぐああ突然理系文が！\nひぃ目が理系になる！", 8, 0, 1, 8023, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129301, 1, "ほああ～！\n知性の海上騎士ユニ！\nこれは新たな可能性！", 10, 0, 1, 8025, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129301, 1, "これは…なんだ？\n何が面白いんだ？\n難しいな…", 11, 0, 1, 8026, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108901, 1, "大切な人と過ごす\n正月の思い出、か…\n昔の私だったら\n考えられなかったな", 103, 0, 12, 8027, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108901, 1, "叶えたい願いなら\nたくさんあるよ\nそれでも一番を選ぶ\nなら、やはりキミと…", 103, 0, 12, 8028, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108801, 1, "神殿にあった\n恋愛おみくじ…\n引いてみようかな…", 103, 0, 12, 8029, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108801, 1, "騎士クン。来年も\nまた一緒におみくじ\n結ぼうね。約束だよ", 103, 0, 12, 8030, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 108701, 1, "今度は騎士くんと\n一緒にうつった写真、\nおばあちゃんに\n送ろうっと♪", 103, 0, 12, 8031, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 108701, 1, "あ、騎士くん！\n今年も…ううん\nこれからも、よろしく\nおねがいします♪", 103, 0, 12, 8032, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129401, 1, "旧校舎の地下牢…\n真冬のサマー計画…\n等身大のＰＲ…\nうッ、頭が…！", 1, 0, 1, 8033, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129401, 1, "暇だし鏡でも見るか。\nお、いい腹筋育って…\nあ？　何見てんだコラ", 2, 0, 1, 8034, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129401, 1, "オレは金色の疾風！\nんん…なんか違うな。\n我が名は金色の…\nあ？　何見てんだコラ", 3, 0, 1, 8035, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129401, 1, "モニカとユニ先輩って\n口調と身長似てるけど\n子供扱いに対する\n反応は真逆だよね…", 4, 0, 1, 8036, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129401, 1, "シオリン元気かな…\n昔くちゃくちゃに\n撫でくり回して\n嫌がられたっけなァ…", 5, 0, 1, 8037, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129401, 1, "べつにいんだけどさ…\nあんたの知り合いって\n普段から水着みたいな\nカッコの女多くない？", 101, 0, 4, 8038, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129401, 1, "そだ、弟らがあんたに\n会いたがってたンだ。\n暇だったらいつでも\nウチ来てやってよ", 102, 0, 8, 8039, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129401, 1, "特別講座は色々あった\nけど…メリゾ自体は\nまあ悪くなかったよね", 1, 0, 1, 8040, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129401, 1, "水着と合わせる小物に\nセンス出るよね、\nって話をミィコ先輩が\nしててマジわかりみ", 2, 0, 1, 8041, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129401, 1, "水着のミフユねえさん\nやっぱ仕上がってたね\nあれが竜殺しの体か…\nバリかっけーな…", 3, 0, 1, 8042, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129401, 1, "はにょんっ！？\nや、だからさ…\n後ろから声かけんな、\nつってンじゃん…", 4, 0, 1, 8043, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129401, 1, "メリゾの特別講座が\n上手くいったのも\n敏腕カメラマンがいた\nお陰だよ。ありがとね", 101, 0, 4, 8044, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129401, 1, "あ？　この水着が？\n似合っ…え、なんて？\nごめ、聞こえんくて。\nもっかい言ってみて", 102, 0, 8, 8045, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129401, 1, "もう朝とかマジないわ\n昨日二人に付き合って\nオールでカラオケだよ", 1, 1, 1, 8046, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129401, 1, "はぁ授業行かなきゃ…\n真っ昼間から勉強とか\nマジつらたん過ぎ…", 2, 2, 1, 8047, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129401, 1, "今日これから夜勤で…\nいやバイトちゃーくて\nボランティアの話よ？\n校則違反よくない", 3, 3, 1, 8048, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129401, 1, "うわ、ふっかふか…\nオニほど安らぐわ…\nやば、ユニってくる…", 2, 0, 1, 8050, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129401, 1, "うちも妹いりゃ\nこーゆーのもなァ…\nま自分用でもいいか", 3, 0, 1, 8051, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129401, 1, "ふーん…\nいいじゃんこの香り\n抜群に嗅げるわ", 4, 0, 1, 8052, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129401, 1, "うちン家のルーツの森\n大火災でなくなった\nらしンだよね…\n生まれる前の話だけど", 5, 0, 1, 8053, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129401, 1, "何コレありがてぇー\n食料ほくほく過ぎて草\n米ガチで尊いわァ…", 7, 0, 1, 8055, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129401, 1, "いいけど、なんでうち\n水着で本読んでンだよ\nどーゆープレイだよ", 8, 0, 1, 8056, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129401, 1, "金色の海上騎士クロエ\n推して参る！　覇ァ！\nあ？　何見てんだコラ", 10, 0, 1, 8058, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129401, 1, "何コレ、貝の椅子？\nどーやって遊ぶんだ…\nいや座って終わりか…\nまァそりゃそーか…", 11, 0, 1, 8059, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129801, 1, "さてと…\n明日の訓練メニュー\n考えねえとな", 1, 0, 1, 8060, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129801, 1, "また新入りの女子に\n弁当もらっちまった…\nありがたく食うけどよ", 2, 0, 1, 8061, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129801, 1, "種族なんて関係なく\nみんなが力を合わせて\nランドソルを守る…\nいい時代になったよな", 3, 0, 1, 8062, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129801, 1, "誰にだって長所はある\n新入りのいいところを\n見つけてやるのも\n教官の仕事だ", 4, 0, 1, 8063, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129801, 1, "厳し過ぎても\n甘過ぎてもダメ…\n教官ってのは難しいぜ", 5, 0, 1, 8064, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129801, 1, "戦いが苦手な新入りを\nどうフォローするか…\n後であいつと\n相談してみるか", 101, 0, 4, 8065, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129801, 1, "あいつは優し過ぎて\n戦いに向いてねえ\n…そういう甘さ\n嫌いじゃねえけどな", 102, 0, 8, 8066, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129801, 1, "強くなる近道はねえ\n地道な鍛錬あるのみだ\nさ、訓練始めるぞ！", 1, 0, 1, 8067, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129801, 1, "新入りにも\nいつも言ってるが…\n装備の手入れは\n念入りにしろよな？", 2, 0, 1, 8068, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129801, 1, "座学も受けてえのか？\nいい心がけだな！\nなんでも教えてやるよ\n…魔法以外ならな", 3, 0, 1, 8069, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129801, 1, "新入りたちがよく\n恋バナしててな…\nお、おまえは…誰か\n気になるやついるか？", 4, 0, 1, 8070, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129801, 1, "なあ、ストレッチ\n手伝ってくれねえか？\n訓練前にしっかり\n筋肉ほぐさねえと…", 101, 0, 4, 8071, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129801, 1, "ずいぶん頑張って\n鍛えたよな、おまえ\nよし！　ご褒美に\nお菓子作ってやるよ♪", 102, 0, 8, 8072, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129801, 1, "訓練日和だな！\nさぁて…まずは\n走り込みといくか！", 1, 1, 1, 8073, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129801, 1, "昼飯はしっかり食えよ\nでなきゃ、\nこの後もたねえぜ？", 2, 2, 1, 8074, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129801, 1, "とっくに消灯時間だ\nさっさと寝な？\n明日も早いんだからよ", 3, 3, 1, 8075, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129801, 1, "休むのも訓練のうちだ\n今はのんびり\nさせてもらうぜ", 2, 0, 1, 8077, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129801, 1, "いい面構えだ\n気に入ったぜ！\nあたしの班に入るか？\n…なんてな♪", 3, 0, 1, 8078, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129801, 1, "…いやされる匂いだな\n新入りたちにも\nすすめてみるか…", 4, 0, 1, 8079, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129801, 1, "あったけえからって\n寝ないようにしねえと\n教官としての\n威厳に関わるからな…", 5, 0, 1, 8080, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129801, 1, "食事も訓練の一環だ\nとはいえ食い過ぎには\n気をつけねえと…", 7, 0, 1, 8082, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129801, 1, "トーゴクの剣術…か\nなにか訓練に\n活かせるかもしれねえ", 8, 0, 1, 8083, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129901, 1, "あのおかたに近付く\n不埒な輩には、特別な\n訓練で地獄を見て\nいただきましょう", 1, 0, 1, 8085, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129901, 1, "これだけ広ければ\n多少派手に\n動き回っても\n問題なさそうですね", 2, 0, 1, 8086, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129901, 1, "…今誰か私を\n「鬼」教官と\n呼びませんでしたか？", 3, 0, 1, 8087, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129901, 1, "新しく開発したこの薬\n誰か試してくれる人は\nいませんか？", 4, 0, 1, 8088, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129901, 1, "厳しくすれば投げ出し\n優しくすればサボる\n人というのはどこまで\n度し難いのでしょう", 5, 0, 1, 8089, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129901, 1, "はじめましょう\nあのおかたの心を\n射貫くための\nシミュレーションを", 101, 0, 4, 8090, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129901, 1, "あらゆる苦難と\n忌々しい女から守る\nためにも、私自身も\n鍛えておかねば", 102, 0, 8, 8091, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129901, 1, "あなた様、そんなに\n見つめられると…え？\nエリコはカッコいい？\nええ、当然ですとも", 1, 0, 1, 8092, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129901, 1, "不調があればすぐ\n報告してください\n体のケアも教官の\n大事な役目ですので", 2, 0, 1, 8093, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129901, 1, "新しい訓練器具の\n使い心地はどう…え？\n重すぎる？　これでも\n軽くしたのですが…", 3, 0, 1, 8094, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129901, 1, "運動後には特製の\n栄養ドリンクをどうぞ\nクスクス、何も\n入れたりしてませんわ", 4, 0, 1, 8095, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129901, 1, "よくがんばりました\nあなた様。あぁ…\nお疲れになったお顔も\n素敵ですわ", 101, 0, 4, 8096, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129901, 1, "あなた様、二人だけの\n遠征訓練へ\nまいりましょう。\n既に準備万端ですわ", 102, 0, 8, 8097, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129901, 1, "まだ眠いですか？\nならばストレッチで\n体を痛めつ…ではなく\nほぐしていきましょう", 1, 1, 1, 8098, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129901, 1, "お昼はお弁当をご用意\nいたしました。量も\nスタミナも増し増し\n残さず食べてください", 2, 2, 1, 8099, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129901, 1, "よい睡眠こそ健やかな\n体に不可欠。ですので\n私が寝ずの番をさせて\nいただきますわ", 3, 3, 1, 8100, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129901, 1, "休憩用に使おうと思い\nましたが、心地よくて\n立てなくなって\nしまいますわ…", 2, 0, 1, 8102, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129901, 1, "中に重しを詰めれば\n愛でながら運動が\n可能ですわ", 3, 0, 1, 8103, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129901, 1, "香りでリラックスさせ\n同時に暗示を\nかけてみましょうか", 4, 0, 1, 8104, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129901, 1, "…くっ、眠気を誘う\nこの暖かさ…\n油断も隙もありません", 5, 0, 1, 8105, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129901, 1, "主食はこちらで十分\nまかなえますわね\nそれでは私はおかずを\n作りましょう", 7, 0, 1, 8107, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129901, 1, "まあ、実用書の類も\n揃っているのですね\nとても助かりますわ", 8, 0, 1, 8108, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129601, 1, "……（そわそわ）", 1, 0, 1, 8110, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129601, 1, "ここに寄らずの花陣…\n作ったらダメですよね", 2, 0, 1, 8111, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129601, 1, "部屋の隅が\n落ち着きます\nそれか、陽の当たる所", 3, 0, 1, 8112, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129601, 1, "身を隠せる場所が\nあると…\n助かるんですけど", 4, 0, 1, 8113, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129601, 1, "複雑な造りの家ですね\n触って壊れたり─\nしませんか？", 5, 0, 1, 8114, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129601, 1, "出入りするひと\nみんな、笑顔…\n…変わった家ですね", 101, 0, 4, 8115, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129601, 1, "この家なら安全─\nかもしれません\n魔物が出ないので", 102, 0, 8, 8116, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129601, 1, "これも、視察の一環\n遊びに来たわけじゃ\nないです", 1, 0, 1, 8117, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129601, 1, "長話─苦手です\n疲れるので…\nふぅ…", 2, 0, 1, 8118, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129601, 1, "ここで友達を作れと\nネアさんが…\nそう、言われても…", 3, 0, 1, 8119, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129601, 1, "世話をするのは\n得意です\n…花の、ですよ？", 4, 0, 1, 8120, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129601, 1, "媛へのお土産を\n探してるんですけど…\n…貴方のおすすめ、\n聞いておきます", 101, 0, 4, 8121, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129601, 1, "平和でのんきで…\n貴方らしい家です\n一応、褒めてます", 102, 0, 8, 8122, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129601, 1, "花に、お水─？\n…いりません", 1, 1, 1, 8123, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129601, 1, "日が高いうちは\nむやみに外へ出ません\n面倒事、多いので…", 2, 2, 1, 8124, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129601, 1, "夜番。私がします\nあまり眠らない\n性質なので…", 3, 3, 1, 8125, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129601, 1, "ここなら根を張れる\nかもしれません\n…張りませんけど", 2, 0, 1, 8127, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129601, 1, "こんなにやわな体じゃ\n身を守れませんよ─\n…あ。生き物じゃ\nないんですね", 3, 0, 1, 8128, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129601, 1, "花みたいに─\nいい匂い、です", 4, 0, 1, 8129, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129601, 1, "火を見てると、\nジオ・ゲヘナを\n思い出します…ふぅ", 5, 0, 1, 8130, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129601, 1, "食事も立派な戦支度\n媛が言ってました", 7, 0, 1, 8132, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129601, 1, "知識も武器の一つだと\n媛が…でも、\nこれでどうやって…？", 8, 0, 1, 8133, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130701, 1, "天気もいいしお花畑に\nピクニックでも\n行こうかな♪", 1, 0, 1, 8135, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130701, 1, "あ、どこからか\nお花の匂いが…\nいい香りですね", 2, 0, 1, 8136, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130701, 1, "新しいシャベルとか\nじょうろもほしいなぁ\nお小遣いしっかり\nためないとね", 3, 0, 1, 8137, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130701, 1, "お花飾りたいけど\n花瓶はどこかな…\nあとであの人に\n聞いてみよっと", 4, 0, 1, 8138, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130701, 1, "お花の観察日記\n書かなきゃ\n日々勉強は\n欠かせませんよ", 5, 0, 1, 8139, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130701, 1, "衣装にお花を足して\nみたけど、あの人\n気付いてくれるかな？", 101, 0, 4, 8140, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130701, 1, "この花冠、上手に\n作れたかも…\n日頃のお礼にあの人に\nあげようかな…", 102, 0, 8, 8141, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130701, 1, "あなたは何のお花が\n好きですか？　ほら、\n一応知っておいたら\n役に立つかもですし", 1, 0, 1, 8142, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130701, 1, "そのお花、乱暴に\n扱っちゃダメですよ\n花粉が服について\nとれなくなりますから", 2, 0, 1, 8143, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130701, 1, "土いじりしたから\n服が汚れてますよ\nはたいてあげますから\nじっとしててください", 3, 0, 1, 8144, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130701, 1, "そんなにすぐに芽は\n出ませんよ\nまあ、楽しみなのは\n私も同じですけど…", 4, 0, 1, 8145, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130701, 1, "この衣装、そんなに\n気になります？　なら\nこうしてお花を\n着ければおそろいです", 101, 0, 4, 8146, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130701, 1, "お花の種を私に？\nありがとうございます\nせっかくなので一緒に\nお世話しませんか？", 102, 0, 8, 8147, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130701, 1, "お花にお水をあげるの\n手伝ってもらえます？\nお水は朝にあげるのが\nいいんですよ", 1, 1, 1, 8148, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130701, 1, "とっておきのお花見\nスポットに\nご案内しますよ\nお弁当も忘れずに！", 2, 2, 1, 8149, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130701, 1, "もうこんな時間\n早く寝ましょう\n草木も眠るなんとか\nって言いますからね", 3, 3, 1, 8150, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 130701, 1, "お花のお手入れって\nけっこう疲れますね…\nここでちょっと一息\nしちゃおっと", 2, 0, 1, 8152, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 130701, 1, "耳にお花を着ければ…\nえへへ、すっごく\nかわいくなったね！", 3, 0, 1, 8153, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 130701, 1, "これはラベンダー\nこっちはミモザ\nフローラル系の香り\nっていいですねぇ", 4, 0, 1, 8154, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 130701, 1, "ポカポカしますね～\nまるで春の陽気\nみたいです～", 5, 0, 1, 8155, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 130701, 1, "お花を一輪さすだけで\nご飯の時間がとても\n華やかになりますね", 7, 0, 1, 8157, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 130701, 1, "お花の本が\nいっぱいある！\n今日はこれで\nお勉強です！", 8, 0, 1, 8158, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130801, 1, "ふんふ～ん♪\n華やかな服を着ると\n心がウキウキします♪", 1, 0, 1, 8160, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130801, 1, "花祭りで出会った\nみなさん…\nお元気でしょうか？\nまたお会いしたいです", 2, 0, 1, 8161, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130801, 1, "はっ！　そうでした\nこの服の時は\nメイドのお仕事は\nお休みでした…", 3, 0, 1, 8162, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130801, 1, "この服は替えが\nありませんから\n大きなドジには\n気を付けないと…", 4, 0, 1, 8163, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130801, 1, "目立ったほつれもなし\n…うん、いい感じに\n補修できました♪", 5, 0, 1, 8164, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130801, 1, "メイド姿でなくても\n心はいつでも\nお嬢さまの\nメイドです♪", 101, 0, 4, 8165, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130801, 1, "この服を着ると心が\n浮き立つのはあのかた\nと一緒にいる気持ちに\nなるからでしょうか？", 102, 0, 8, 8166, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130801, 1, "はい、今日は一日\nこの服で過ごそうと\n思います\n変じゃないですよね？", 1, 0, 1, 8167, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130801, 1, "花祭りのおかげで\n少しだけお花に\n詳しくなった…\nような気がします！", 2, 0, 1, 8168, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130801, 1, "花祭り、今度は\nお嬢さまと救護院の\nみんなと一緒に\n行ってみたいですね", 3, 0, 1, 8169, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130801, 1, "今度キョウカちゃんと\n花祭りの服でお花を\n見にお出かけ\nするんです", 4, 0, 1, 8170, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130801, 1, "メイド服を全部洗濯\nしちゃうなんて…\nドジっちゃいましたぁ", 101, 0, 4, 8171, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130801, 1, "花祭りの服は私たちの\n思い出の服ですから…\nこれからも\n大切にします♪", 102, 0, 8, 8172, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130801, 1, "はう、そうでした\nお花にお水を\nあげる時間です！", 1, 1, 1, 8173, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130801, 1, "お昼ご飯は\nバスケットを持って\nお花畑に食べ\nに行きませんか？", 2, 2, 1, 8174, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130801, 1, "ふぁあ…繕い物を\nしていたらこんな時間\nになってしまいました\n早く寝ないと…", 3, 3, 1, 8175, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 130801, 1, "ふわふわしてて\nきもちいいです～\nどんな生地を\nつかってるんだろ～？", 2, 0, 1, 8177, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 130801, 1, "かわいいですね～\nぬいぐるみさんにも\n服をつくって\nあげたいです♪", 3, 0, 1, 8178, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 130801, 1, "いい香りです～\nあれ？　もしかして\n私の服についてる花と\n同じ香りが…？", 4, 0, 1, 8179, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 130801, 1, "暖かいですけど\nあまり近づいちゃうと\n頭の花飾りが乾燥\nしちゃいそうですね…", 5, 0, 1, 8180, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 130801, 1, "もぐもぐ…\nいい花を咲かせるには\nしっかりとした栄養が\n必要だそうです！", 7, 0, 1, 8182, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 130801, 1, "なるほどなるほど…\nお花のお手入れは\n奥が深いですね…", 8, 0, 1, 8183, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131001, 1, "マホマホ王国にも\nサーカス団設立や\n夢と希望を届けますえ", 1, 0, 1, 8185, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131001, 1, "せっかくの晴れ舞台や\nかっこいいポーズも\nできるようにしたいわ", 2, 0, 1, 8186, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131001, 1, "むむむむ～っ。うーん\nクルルはんみたいに\n胸から宝石を出すんは\n難しいみたいやわ", 3, 0, 1, 8187, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131001, 1, "あむっ…んん～♪\nやっぱり練習のあとは\n甘いものに限るわぁ", 4, 0, 1, 8188, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131001, 1, "アーサーたちの衣装も\n今のうちに考えんと…\n生地、足りるやろか", 5, 0, 1, 8189, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131001, 1, "王子はんとなら\nどんなアイデアも\n見つけられます♪", 101, 0, 4, 8190, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131001, 1, "うちの晴れ姿が\n王子はんの目に一等\n輝いて見えますように", 102, 0, 8, 8191, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131001, 1, "キリっとしたうちも\nかっこいい？\nもう、上手やなぁ", 1, 0, 1, 8192, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131001, 1, "新しい演目考えたんよ\nなぁ王子はん、意見\n聞かせてもらえます？", 2, 0, 1, 8193, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131001, 1, "聞いてや王子はん\n動物さんたちとさらに\n息が合うてきたんよ\nみんな頑張り屋さんや", 3, 0, 1, 8194, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131001, 1, "演技の感想なら\n大歓迎どすえ♪\nたんと聞かせてや～", 4, 0, 1, 8195, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131001, 1, "王子はん、本番当日は\n最前席で見たってな\nそしたらうち、もっと\n頑張れますさかい♪", 101, 0, 4, 8196, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131001, 1, "この技をあなたに\n捧げます…なんて♪\n驚いたやろ？\nどっきり作戦成功や♪", 102, 0, 8, 8197, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131001, 1, "素敵なショーのためや\n今日も焦らず一歩ずつ\n成長していきまひょ\nな、王子はん♪", 1, 1, 1, 8198, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131001, 1, "お弁当、王子はんも\n一緒にいかがどす？\nみんなで食べて団結や", 2, 2, 1, 8199, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131001, 1, "夜のサーカスも\n幻想的でとっても\n魅力的なんよ\n一緒にいきまひょか♪", 3, 3, 1, 8200, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131001, 1, "練習が終わったあとは\nの～んびりして\n体力を回復せな…", 2, 0, 1, 8202, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131001, 1, "この子たちとなら\n空中ブランコや\n玉乗りもええなぁ", 3, 0, 1, 8203, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131001, 1, "いい香りやなぁ\n本番前の緊張も\n和らげてくれそうやわ", 4, 0, 1, 8204, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131001, 1, "このあったかい感じ…\nスポットライトの熱を\n思い出すわぁ", 5, 0, 1, 8205, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131001, 1, "ちゃんと食べへんと\nいい演技はできまへん\n団長はんも言うてたわ", 7, 0, 1, 8207, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131001, 1, "動物さんたちのことも\nもっと勉強せな\n大事な仲間やさかい", 8, 0, 1, 8208, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 105601, 1, "器用じゃないが、\n情には厚いよ\nこういう女を「一途」\nってそう言うんだろ？", 103, 0, 12, 8210, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 105601, 1, "斬れないものはないと\n思ってきたさ。だけど\n…この気持ちだけは\n斬れないね、絶対に", 103, 0, 12, 8211, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130901, 1, "んしょ、んしょっ…！\n日頃の感謝を込めて\nヒーローさんのお家を\nきれいにしますです♪", 1, 0, 1, 8212, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130901, 1, "ふむふむ…\n新アトラクションは\nこのギルドハウスを\nモチーフに…", 2, 0, 1, 8213, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130901, 1, "ごそごそ、こそこそ…\nヒーローさんに内緒で\nどこかにお礼の宝石を\n隠しておきましょう…", 3, 0, 1, 8214, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130901, 1, "パークの運営が\n落ち着いたら\nわたしもギルド活動？\nしてみたいですます♪", 4, 0, 1, 8215, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130901, 1, "こんなすてきな\nおうちに住めたら…\nいえ！　うさぎ小屋で\n十分です～っ", 5, 0, 1, 8216, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130901, 1, "ヒーローさんのおうち\nだって意識したら\nアトラクションよりも\nドキドキしてきます…", 101, 0, 4, 8217, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130901, 1, "ヒーローさんのことを\n考えると顔が赤く…\nこれってもしかして\nひょっとして…？", 102, 0, 8, 8218, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130901, 1, "あっ、ヒーローさん！\n話しかけてくれた記念\nということで、宝石\n受け取ってください♪", 1, 0, 1, 8219, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130901, 1, "元気がない時は\n語尾にぴょんですっ！\nさあヒーローさんも\nやってみるぴょん♪", 2, 0, 1, 8220, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130901, 1, "ヒーローさんへのご恩\nおばあちゃんうさぎに\nなるまでに、お返し\nできるでしょうか…？", 3, 0, 1, 8221, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130901, 1, "ヒーローさん！\n二人で一緒に\nパレードの練習\nしませんか～♪", 4, 0, 1, 8222, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130901, 1, "あはは♪\nヒーローさんは\n人を笑顔にする力が\nありますです♪", 101, 0, 4, 8223, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130901, 1, "ヒーローさんに\n一日一恩返しですっ！\n返し終わるまで\nやめませんですます♪", 102, 0, 8, 8224, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130901, 1, "おはようございます♪\nんしょ、んしょっ…！\n気持ちよく起きられた\n記念に宝石どうぞ♪", 1, 1, 1, 8225, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130901, 1, "お昼にフードコートで\nかばばん君バーガー\n買ってきましたよ！\n一緒に食べますです♪", 2, 2, 1, 8226, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130901, 1, "今日も一日\nお疲れさまですっ♪\nマッサージしますから\n寝そべってください♪", 3, 3, 1, 8227, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 130901, 1, "はふぅ～…♪\nお仕事の疲れも\nぴょんぴょ～んって\n飛んでっちゃいます♪", 2, 0, 1, 8229, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 130901, 1, "わぁっ、とっても\nかわいいですますっ！\nギルドハウスの\nマスコットですね♪", 3, 0, 1, 8230, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 130901, 1, "ヒーローさんの好きな\n香り…これを宝石に\nつければ、受け取って\nもらえるでしょうか？", 4, 0, 1, 8231, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 130901, 1, "はあ、気持ちいい♪\nヒーローさんの\n好きな温度に\n調整しときますです♪", 5, 0, 1, 8232, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 130901, 1, "支配人は体が資本っ！\nたくさん食べ…すぎて\n動けなくなっちゃい\nましたぁ～…！", 7, 0, 1, 8234, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 130901, 1, "パークの\nガイドブックとか\nあったらいいですね♪", 8, 0, 1, 8235, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131101, 1, "外あつすぎ溶けるわ～\nやっぱ部屋ん中で\n水着がラクだねー", 1, 0, 1, 8237, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131101, 1, "ン～\nカリザきゅんどこー？\nもしかして逃げた？", 2, 0, 1, 8238, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131101, 1, "ミソラちゃんと\nリリちゃん誘って\nそろそろイイコト\nしよっかなー？", 3, 0, 1, 8239, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131101, 1, "おねえちゃんに\n頼まれたシゴトは\nまた今度にすっかー\n夏休みだしー♪", 4, 0, 1, 8240, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131101, 1, "騎士きゅんの\nプリンセスフォームも\n見てみたいんだよなー\nンフ♡", 5, 0, 1, 8241, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131101, 1, "スライムを\nこーしてあーしたら\n騎士きゅんの泳ぐ\n練習に使えるっぽ？", 101, 0, 4, 8242, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131101, 1, "おかえり～♡\nって水着で迎えたら\n騎士きゅん喜んで\nくれるかなー？", 102, 0, 8, 8243, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131101, 1, "騎士きゅん\nこのサングラス\nかけてみる？\nぜってー似合うよー", 1, 0, 1, 8244, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131101, 1, "日焼け痛かったら\nいつでも言ってねー\nスライムでひんやり\nしてあげるよ～♪", 2, 0, 1, 8245, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131101, 1, "今から外で\n焼いてこっかなー\n騎士きゅんもどー？", 3, 0, 1, 8246, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131101, 1, "スライムマッサージ\nしてあげよっかー\nほーれ、もみもみ～♡", 4, 0, 1, 8247, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131101, 1, "騎士きゅんが選んだ\n水着も着てみたーい\nネアおねえちゃんに\nどんなの着てほしい？", 101, 0, 4, 8248, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131101, 1, "騎士きゅ～ん\n尻相撲しよっかー♪\nおねえちゃんに\n勝てたらご褒美だぞ～", 102, 0, 8, 8249, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131101, 1, "暑すぎてムダに\n早起きしちった～\n二度寝すっか～\n…ふぁあ…", 1, 1, 1, 8250, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131101, 1, "夏バテしても\nスイカとかかき氷なら\n全然ウェルカム\nなんだよなー", 2, 2, 1, 8251, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131101, 1, "今日はこのまま\n水着で寝るかー\n夢ん中で泳ごー", 3, 3, 1, 8252, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131101, 1, "いったんキューケイ…\nンー…よし\n充電カンリョ～♪", 2, 0, 1, 8254, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131101, 1, "仲間になりたそうに\nこっち見てんねー\nきゃわいいしおっけ～", 3, 0, 1, 8255, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131101, 1, "ン～いいニオーイ\nお日様あたった\n騎士きゅんの\n次くらいかな～", 4, 0, 1, 8256, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131101, 1, "カラダ冷えてきたから\nたすかるーおかげで\nまだまだ水着イケるわ", 5, 0, 1, 8257, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131101, 1, "うめ～\nとまんねぇ～\n夏バテどこいった～？", 7, 0, 1, 8259, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131101, 1, "ふむふむ…\nこれは意外と\nぬるっと読めるっぽー", 8, 0, 1, 8260, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131101, 1, "ンフフ～\n海の戦士ってカンジ？\nアリよりのアリだね～", 10, 0, 1, 8262, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131101, 1, "あーやべ\n一瞬で寝そー\nヤバだわ～", 11, 0, 1, 8263, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129701, 1, "服や紙が易々と\n燃えぬとは\nランドソルとはまこと\n住み易き国で候", 1, 0, 1, 8264, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129701, 1, "どの世界にあっても\n民は活力に溢れ\n街は賑わうもの…\n善哉、善哉", 2, 0, 1, 8265, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129701, 1, "食べ歩きに世界征服…\nギルドとはかくも\n自由気儘な徒党に\nありんすなぁ", 3, 0, 1, 8266, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129701, 1, "連絡は…今日もなし\n我が妹子は相変わらず\n放埓なことよ喃", 4, 0, 1, 8267, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129701, 1, "モネは相変わらず\n警戒しきりなれど…\nそのようなところも\n愛らしくあり申す", 5, 0, 1, 8268, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129701, 1, "なんと長閑な世にや\nありけむ…この平和は\n騎士殿たちの努力の\n賜物にありんす喃", 101, 0, 4, 8269, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129701, 1, "『王家の装備』は\n使わぬのかと？\n…フフ。すでに\n用いており申す", 102, 0, 8, 8270, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129701, 1, "今ばかりは媛という\n立場は置いて\nこの地の者らと\n歓談したく候", 1, 0, 1, 8271, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129701, 1, "強さの秘密？　…フフ\n女子の秘密を暴くのは\n野暮にござんす\nご寛恕いただきたく候", 2, 0, 1, 8272, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129701, 1, "此方の家屋、\n見目以上に\n堅牢であり申す\n何ぞ仕掛けがありや？", 3, 0, 1, 8273, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129701, 1, "心地よい時間とは\n疾く過ぎ去りゆくもの\n此処で過ごすと改めて\n実感できる喃", 4, 0, 1, 8274, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129701, 1, "視線が気になる？\nこれはすみませぬ\nなにぶん、お前さまは\n見ていて飽きませぬ故", 101, 0, 4, 8275, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129701, 1, "お前さまの前では\n誰もが心を開く…\nなんとまぁ罪作りな\n色男にありんすな？", 102, 0, 8, 8276, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129701, 1, "よく眠れましたかの？\n寝る子は良く育つ、と\n古くより申します由", 1, 1, 1, 8277, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129701, 1, "美食殿の作法に倣い\n皆で共に昼餉を頂くと\n致しんしょう", 2, 2, 1, 8278, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129701, 1, "逢魔ヶ時は望まぬ客も\n参るもの…お前さまも\n気をつけなんし", 3, 3, 1, 8279, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129701, 1, "わっちの蛇に\n勝るとも劣らぬ\n座り心地で候", 2, 0, 1, 8281, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129701, 1, "魂、万物に宿る\nとの云いあらば\n情愛を注いでやるのが\nよかろうの", 3, 0, 1, 8282, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129701, 1, "いと香しき哉…\n騎士殿は香の道にも\n詳しくあられるか", 4, 0, 1, 8283, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129701, 1, "ジオ・ゲヘナの\n灼熱と比ぶれば\nそよ風と変わり\nござんせん", 5, 0, 1, 8284, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129701, 1, "美味なる哉…この味\nぺコリーヌ姫が好むも\n道理にありんす", 7, 0, 1, 8286, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129701, 1, "トーゴクに、オーマ…\n書で見るだけでも\n心惹かれる土地よの", 8, 0, 1, 8287, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131201, 1, "暑い日には\n冷たいものですよねー\nかき氷とか食べて\nすっきりしたいです★", 1, 0, 1, 8289, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131201, 1, "海への行き来は\nテレポーテーションに\nお任せです★", 2, 0, 1, 8290, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131201, 1, "トロピカルジュースが\n飲みたいでーす♪\n海の家で見たんですよ\nカップル用ジュース★", 3, 0, 1, 8291, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131201, 1, "ご飯どうしようかなー\nあ、海鮮ラーメンとか\n美味しそうかも★", 4, 0, 1, 8292, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131201, 1, "水鉄砲、普通に撃つと\n少し物足りないかも…\nどるるるる～ん★って\n感じが欲しいですよね", 5, 0, 1, 8293, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131201, 1, "コイビトっぽいこと\nアオハルっぽいこと\nいっぱいしたいです★\n修羅場とかも♪", 101, 0, 4, 8294, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131201, 1, "騎士さんが溺れたら\n絶対助けに行きますね\n人工呼吸でもなんでも\nしてあげますから★", 102, 0, 8, 8295, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131201, 1, "騎士さーん\n日焼け止めの塗り直し\nお願いします★\n優しくしてくださいね", 1, 0, 1, 8296, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131201, 1, "海で食べたフルーツ？\nはい、まだありますよ\nこっちでもあーんして\n食べさせましょうか★", 2, 0, 1, 8297, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131201, 1, "ぺんぽこりんは\nどうしたのかって？\nどうしたんでしょうね\nどーでもいいです★", 3, 0, 1, 8298, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131201, 1, "今日はどんな\nイイコトしましょうか\n思い出作り？\n人助けもいいですね★", 4, 0, 1, 8299, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131201, 1, "ヒモを引っ張ったら…\n「セ・キ・ニ・ン」\n取ってもらいますよ？\nなーんてウソですけど", 101, 0, 4, 8300, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131201, 1, "みんなでイイコトも\n楽しいですけど～\n…二人でイイコト\nしちゃいますか？", 102, 0, 8, 8301, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131201, 1, "朝ですよー！\n今日も一日、\nイイコト日和ですね♪", 1, 1, 1, 8302, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131201, 1, "日差しにご注意ですね\n日焼け止め…誰かに\n塗ってほしいなー？\n…ちらっ★", 2, 2, 1, 8303, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131201, 1, "夜の海って\n惹かれちゃいますよね\n…ロマンチックです★", 3, 3, 1, 8304, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131201, 1, "う～ん、まったり～\nこれぞ新ジャンル\nおうちバカンス★", 2, 0, 1, 8306, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131201, 1, "よーしよーし★\nわたしとイイコト\n一緒にしましょうねー", 3, 0, 1, 8307, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131201, 1, "いい香りですねー\nリラックスタイムに\nちょうどよさそう★", 4, 0, 1, 8308, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131201, 1, "水遊びのあとは\nしっかり体を\n温めないとですよね★", 5, 0, 1, 8309, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131201, 1, "栄養補給もバッチリ★\n遊びもイイコトも\nお腹が減ってたら\nできませんからねー？", 7, 0, 1, 8311, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131201, 1, "今だと海関連の本とか\n興味あるんですよねー\n魚とか海難救助とか★", 8, 0, 1, 8312, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131201, 1, "かっこいー★\n名前はー…\nぷんぷくぽんとか？", 10, 0, 1, 8314, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131201, 1, "映えますねー\n写真撮ったら\n素敵な思い出に\nなりそうです★", 11, 0, 1, 8315, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131601, 1, "海でもないのに\nこの姿でいるのは…\nやはり、場違いでは\nありませんか…？", 1, 0, 1, 8316, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131601, 1, "スイカとは割ることと\n見つけたり！\n…ですが目隠しすれば\n見当たらないもの…", 2, 0, 1, 8317, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131601, 1, "まぁ、膝に砂が…\n気がつきませんでした\nまるでわんぱくな\nお子様のよう…ふふっ", 3, 0, 1, 8318, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131601, 1, "家の中でスイカを割る\nわけにはまいりません\nここは潔く包丁で\n切りましょう", 4, 0, 1, 8319, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131601, 1, "ミソラ様やネア様の\n大胆さを見習って…\nき、騎士きゅーん…★\n難しいですね…", 5, 0, 1, 8320, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131601, 1, "きゅ、急に暗く…！？\n…失礼しました\n鉢巻きが下がってきた\nだけでした…ううぅ", 101, 0, 4, 8321, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131601, 1, "わたくしに剣の才は\nあるのでしょうか…\nいえ、何事もまずは\nやってみなくては", 102, 0, 8, 8322, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131601, 1, "なにか羽織った方が\nよろしいですよね…？\nそのままでいい？\nそれはそれで…その…", 1, 0, 1, 8323, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131601, 1, "あの、兄様…？\nスイカの種を飛ばす\n遊びもあるというのは\n…本当なのでしょうか", 2, 0, 1, 8324, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131601, 1, "強くなるための修行は\nまだ始まったばかり\nまずは己の目指すべき\n道を見定めなくては", 3, 0, 1, 8325, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131601, 1, "同じ薄着であっても\n黒い天使の姿より…\n気が楽です", 4, 0, 1, 8326, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131601, 1, "はい、ただいまー！\n…あっ！\n海の家でのお手伝いの\nくせが、つい…", 101, 0, 4, 8327, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131601, 1, "兄様さえよろしければ\nまた一緒に海へ…\nあ…申し訳ありません\n浮ついてしまいました", 102, 0, 8, 8328, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131601, 1, "本日もイイコトをして\n清々しい一日を\n過ごしたいものです\nまさに一日一善ですね", 1, 1, 1, 8329, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131601, 1, "お昼時になると\n海の家の料理を\n思い出してしまいます\n焼きそばにかき氷…♪", 2, 2, 1, 8330, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131601, 1, "兄様…よろしければ\n浜辺を歩きませんか？\nきっと、月が\nきれいでしょうから", 3, 3, 1, 8331, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131601, 1, "ふわふわ心地よすぎて\n眠くなってしまいます\n水着姿では\nいけません…ふぁ…", 2, 0, 1, 8333, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131601, 1, "なんてよい手触り…\n癖になってしまいます\nふふ、よしよし…♪", 3, 0, 1, 8334, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131601, 1, "爽やかな香り…♪\n日焼け止めクリームを\n思い出してしまいます", 4, 0, 1, 8335, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131601, 1, "冷たい水で冷えた体が\n温まります…♪\nまるで兄様のようです", 5, 0, 1, 8336, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131601, 1, "お腹が空いては\n修行ができません…！\n動きに障りない程度に\n栄養の補給を…！", 7, 0, 1, 8338, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131601, 1, "剣術とスイカ割りが\n上達する指南書…\n見つかりませんね…", 8, 0, 1, 8339, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131601, 1, "さあ、ともに大海原を\n参りましょう！\nなんて…ふふっ♪", 10, 0, 1, 8341, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131601, 1, "まるでおとぎ話の\n登場人物になった\nような心持ちです…♪", 11, 0, 1, 8342, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131701, 1, "あたしはなんで水着で\n室内をうろついてるの\nかしら…不思議だわ", 1, 0, 1, 8343, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131701, 1, "あ、天井が汚れてる\n仕方ないわね\nひとっ飛びしますか", 2, 0, 1, 8344, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131701, 1, "はい、お疲れさま♪\n出迎えの言葉は\nやっぱこれよね♪", 3, 0, 1, 8345, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131701, 1, "そういえばあたしって\n羽があるけど\n泳げるのかしら…", 4, 0, 1, 8346, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131701, 1, "あいつのために\n浮き輪も用意したし\n抜かりはないわよ", 5, 0, 1, 8347, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131701, 1, "海までのガイド計画も\nまとまったわね\nふふっ…楽しみだわ", 101, 0, 4, 8348, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131701, 1, "この水着、あいつが\n選んでくれたのよね\n…大事にしないと", 102, 0, 8, 8349, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131701, 1, "ネビアは最近どう？\n迷惑かけてない？\n…かけてるのね", 1, 0, 1, 8350, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131701, 1, "あんた…部屋の片付け\nくらい自分でしなさい\n散らかってたわよ？\n…片付けておいたけど", 2, 0, 1, 8351, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131701, 1, "もしかして暇なの？\nだったら記憶でも\n見せましょうか？\n凄く恥ずかしいやつ♪", 3, 0, 1, 8352, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131701, 1, "夏ってこんなに\n暑いのね…\n海にばかり行く\n気持ちが分かったわ", 4, 0, 1, 8353, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131701, 1, "あんたの水着って\n新しいの買わないの？\nなんならあたしが\n選んであげるわよ♪", 101, 0, 4, 8354, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131701, 1, "気軽に触れ合える\n距離にいる…\nあたしにとって\nとても大事なことよ", 102, 0, 8, 8355, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131701, 1, "あらおはよう\n夏の朝は暑いわね\nシャワーでも浴びて\nさっぱりしなさい", 1, 1, 1, 8356, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131701, 1, "お昼は何を食べるの？\nガイド妖精として\n提案しましょうか？", 2, 2, 1, 8357, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131701, 1, "夜更かしは厳禁よ\n無理やり眠らせて\nあげましょうか？", 3, 3, 1, 8358, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131701, 1, "アメスさまだって\nゴロゴロするときは\nあるのよ～", 2, 0, 1, 8360, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131701, 1, "…ふふっ♪\n何よ、あたしが\nかわいいものを\n愛でちゃだめなの？", 3, 0, 1, 8361, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131701, 1, "こういういい香り…\n塔の中じゃなかなか\n楽しめないのよね", 4, 0, 1, 8362, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131701, 1, "水着を着てるのに\n温まるのって\n矛盾してない？\n気にしすぎかしら", 5, 0, 1, 8363, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131701, 1, "あんたと行動するなら\n沢山食べられるように\nならなきゃね…！", 7, 0, 1, 8365, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131701, 1, "文字を読むのと\nデータを読むのは\nまったく違うわ", 8, 0, 1, 8366, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131701, 1, "へぇ、面白いじゃない\nこういう経験、昔じゃ\nできなかったわ", 10, 0, 1, 8368, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131701, 1, "面白いクッションね\nところであんた…\nあたしで遊んでない？\n気のせいならいいけど", 11, 0, 1, 8369, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118601, 1, "また新しい悪戯\n思いついちゃった♪\n誰に仕掛けようかしら", 1, 0, 1, 8370, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118601, 1, "あ～あ、退屈…\nフィオでもからかって\nこようかしら♪", 2, 0, 1, 8371, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118601, 1, "ミソギの悪戯って\nレベル高いのよね…\n負けてらんないわ！", 3, 0, 1, 8372, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118601, 1, "家事ニガテなのよね～\nコッコロたちには\n感謝しないとね", 4, 0, 1, 8373, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118601, 1, "私も魔物料理に\n慣れてきちゃった…\n毒されてるわね…", 5, 0, 1, 8374, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118601, 1, "あいつに仕掛ける\n次の悪戯は～…♪\nこれ…ううん…\nやっぱこっちかしら？", 101, 0, 4, 8375, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 118601, 1, "結局ギルドハウスが\n一番落ち着くわね\n…私たちの家だもの", 102, 0, 8, 8376, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118601, 1, "悪戯のコツでも\n教えてあげよっか？\n…え～興味ないの？", 1, 0, 1, 8377, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118601, 1, "これからもずーっと\n退屈しない毎日に\nしてあげる♪\n私の悪戯でね♪", 2, 0, 1, 8378, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118601, 1, "ちょうど退屈してたの\nたーっぷりと\n構ってもらうわよ♪", 3, 0, 1, 8379, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118601, 1, "案内が必要なら\n頼ってくれていいわよ\n一応は私だって\nガイド妖精だし！", 4, 0, 1, 8380, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118601, 1, "冒険のガイドなら\n私に任せなさい♪\nある意味忘れられない\n旅にしてあげる♪", 101, 0, 4, 8381, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118601, 1, "妖精がいる家は\n栄えるのよ♪　だから\nずっといてあげる\nあんたのそばにね♪", 102, 0, 8, 8382, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118601, 1, "きゃははっ！\n寝ぐせついてるわよ？\nこのままにしましょ！\nだって面白いし！", 1, 1, 1, 8383, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118601, 1, "自由に外へ行けるって\nすっごく幸せなことよ\nさ、散歩行きましょ♪", 2, 2, 1, 8384, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 118601, 1, "安心していいわ\n寝てるあんたに\n悪戯しないから\n…あんたにはね♪", 3, 3, 1, 8385, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118601, 1, "悪くはないけど…\nあいつの頭の方が\n座り心地いいかも", 2, 0, 1, 8387, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118601, 1, "この子を使った\n悪戯は…\n…止めとこっと\nこんなにかわいいし♪", 3, 0, 1, 8388, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118601, 1, "このギルドハウスに\nピッタリな匂いね♪\n一言で言えば…\nカオスって感じ？", 4, 0, 1, 8389, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118601, 1, "なんていうか…\n心があたたかいわ\n…変な感じ！", 5, 0, 1, 8390, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118601, 1, "ガイド妖精にも\n食事の機能があって\nよかったのかも\nしれないわね…もぐ…", 7, 0, 1, 8392, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 118601, 1, "じれったい物語ね…\nこんなの霧を使えば\n一瞬で終わるのに！", 8, 0, 1, 8393, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131801, 1, "わる～い子は\nトゥンヌス漁船に\n乗ってもらおうかな～", 1, 0, 1, 8395, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131801, 1, "たまにはファミリーの\nみんなにもお休みを\nあげないとね～☆", 2, 0, 1, 8396, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131801, 1, "海辺に素敵な\nバーがあるんだ～\n彼が大人になったら\n一緒に行きたいな♪", 3, 0, 1, 8397, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131801, 1, "夏の風を肌で\n感じるの好きかも～\nラーゴはどう？", 4, 0, 1, 8398, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131801, 1, "海岸線を\nのんびり散歩もいいね\n砂や貝殻に紛れた\nお宝探しの旅だよ～☆", 5, 0, 1, 8399, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131801, 1, "みんなで集まって\nビーチパーティーでも\n開いちゃおっかな～♪", 101, 0, 4, 8400, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131801, 1, "さて、この水着…\n彼は気に入って\nくれてるかな～？", 102, 0, 8, 8401, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131801, 1, "心ゆくまで夏を\n楽しんじゃお～☆\nほら君も♪", 1, 0, 1, 8402, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131801, 1, "水着似合ってるって？\nふふ♪　うれしいこと\n言ってくれるね～☆", 2, 0, 1, 8403, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131801, 1, "熱中症には\n気を付けないと\nだめだよ～？", 3, 0, 1, 8404, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131801, 1, "海の家ならうちの\nファミリーでやってる\nとこにしよっか☆\n豪遊しちゃお～♪", 4, 0, 1, 8405, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131801, 1, "君と一緒なら\nどんな夏になるのか\n楽しみだね～♪", 101, 0, 4, 8406, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131801, 1, "君といると子供心を\nくすぐられちゃうな～\n大人だって思いっきり\n遊んでいいんだよね♪", 102, 0, 8, 8407, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131801, 1, "さ、今日は何をして\n過ごそっかな～…\nねえ、君は何したい？", 1, 1, 1, 8408, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131801, 1, "ふふ♪\n波音が心地良い\n時間だね～☆", 2, 2, 1, 8409, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131801, 1, "今夜は星空の下で\n乾杯しよっか～☆\n飲み物取ってくるね♪", 3, 3, 1, 8410, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131801, 1, "ここ涼しい～♪\nちょっと一休み\nしていこっか☆", 2, 0, 1, 8412, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131801, 1, "こういうのも良いけど\nやっぱりラーゴの方が\nかわいいかな～♪", 3, 0, 1, 8413, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131801, 1, "爽やかな香りだね～\nこういう感覚も絵で\n表現してみたいな～", 4, 0, 1, 8414, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131801, 1, "続きを描く前に\n原稿を乾かさないとね\nインクがにじむと\n直すの大変なんだよ～", 5, 0, 1, 8415, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131801, 1, "今日は水着だし…\n食べるのは控えめに\nしておこっかな", 7, 0, 1, 8417, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131801, 1, "話題の作品は\nチェックしないとね～\nあっ、この作家さん\n新刊出すんだ～", 8, 0, 1, 8418, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131801, 1, "うちの出店の客引きに\nこういう乗り物系を\n使うのもアリかな～☆", 10, 0, 1, 8420, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131801, 1, "これ、背景用の\n参考資料に\n一つ欲しいかも♪", 11, 0, 1, 8421, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 181201, 1, "ここに帰るのも\n久しぶり…あれから\n変わったところは\nあるかしら？", 1, 0, 1, 8422, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 181201, 1, "バイトも復帰しなきゃ\nうちのギルドにとって\n食費は重要だもの…！", 2, 0, 1, 8423, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 181201, 1, "コッコロマ…っ\n…うぅ…\nこの癖は\n直らないのね…", 3, 0, 1, 8424, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 181201, 1, "この世界はまだまだ\n知らないことだらけね\n『裏世界』とか…", 4, 0, 1, 8425, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 181201, 1, "必ずみんなを\n助け出してみせるわ\n…もちろん、兄さんも", 5, 0, 1, 8426, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 181201, 1, "またみんなと\n暮らせるなんて…\n夢じゃ、ないのよね", 101, 0, 4, 8427, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 181201, 1, "たくさんの人が\n私に優しくしてくれる\n…だからこそ、私も\n頼れる人になりたいわ", 102, 0, 8, 8428, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 181201, 1, "次はランドソルの\n街の外にも行きたいわ\nあの…よかったらまた\n一緒に回ってくれる？", 1, 0, 1, 8429, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 181201, 1, "独りぼっちで眠るのは\nやっぱり寂しかったわ\n…みんなで雑魚寝？\nそんなの…絶対やるわ", 2, 0, 1, 8430, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 181201, 1, "ネビアってば大泣きで\n私を出迎えてくれたの\n…すごく嬉しかったわ", 3, 0, 1, 8431, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 181201, 1, "この翼、陽に当たると\n本当に綺麗よね…\n自分のだけれど、つい\nずっと見ちゃうもの", 4, 0, 1, 8432, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 181201, 1, "おにーたんっ♪\n…呼びたくなっただけ\n本当にそれだけよっ？", 101, 0, 4, 8433, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 181201, 1, "どうしたの？\n…ふふっ、安心して\nこれからもずっと\n私はここにいるわ", 102, 0, 8, 8434, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 181201, 1, "みんな起きだす時間ね\n街が活気づいてく感じ\n嫌いじゃないわ", 1, 1, 1, 8435, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 181201, 1, "【美食殿】のお昼は\nいつも美味しいから\nまた太っちゃいそうね", 2, 2, 1, 8436, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 181201, 1, "おやすみなさい\n明日も、その先も\n一緒に冒険しましょう", 3, 3, 1, 8437, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 181201, 1, "休憩も大事…よね\nじゃあ遠慮なく…\nふぅ～…♪", 2, 0, 1, 8439, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 181201, 1, "はぁ…ふわふわ…♪\nこのかわいさには\nもう抗えないわ…", 3, 0, 1, 8440, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 181201, 1, "落ち着く香り…\nこの匂いを嗅いでると\n帰ってきたなって\n思うわね", 4, 0, 1, 8441, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 181201, 1, "みんなで野宿した時も\n焚火の明かりを見て\nすごく安心したのよね", 5, 0, 1, 8442, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 181201, 1, "ペコリーヌさんには\n負けるけど…\n美味しくって手が\n止まらないわ…！", 7, 0, 1, 8444, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 181201, 1, "プリンセスフォーム…\n一体どういう仕組みで\n発動するのか、文献に\n書いてないかしら…", 8, 0, 1, 8445, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131901, 1, "すごーく暑いけど、\nこの暑さをどうやって\n乗り越えるかって\n考えるのも楽しいかも", 1, 0, 1, 8447, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131901, 1, "氷で彫刻を作るの？\nそれは楽しそうだけど\nすぐに溶けちゃわ\nないかしら？", 2, 0, 1, 8448, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131901, 1, "お日様の下で遊ぶのも\n部屋の中で涼むのも\nどっちも夏ならではの\n楽しみ方なのね", 3, 0, 1, 8449, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131901, 1, "生き物の観察日記、\n私も書いてみたいな\nパックのお昼寝日記\nなんてどうかしら？", 4, 0, 1, 8450, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131901, 1, "さまぁばけぇしょんは\n計画を立てるもの\nなんだって\n私も挑戦してみるわね", 5, 0, 1, 8451, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131901, 1, "この世界のことを\nもっと知って\nアーラム村のみんなに\nお話ししてあげるの", 101, 0, 4, 8452, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 131901, 1, "いつルグニカに帰るか\nわからないからこそ\n毎日頑張らなきゃね", 102, 0, 8, 8453, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131901, 1, "今日はどこに行くの？\n私は、えーと…\nかき氷が食べたいわ！", 1, 0, 1, 8454, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131901, 1, "ここにも精霊が\n住んでたりするの？\n…精霊じゃなくて\nがいど妖精？", 2, 0, 1, 8455, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131901, 1, "パックったら、\n近所の子にいっぱい\nなでなでされてたわ\nすごーく人気者ね！", 3, 0, 1, 8456, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131901, 1, "実は…線香花火の\n特訓をしてたの！\n次は負けないんだから", 4, 0, 1, 8457, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131901, 1, "昨日、みんなの\n似顔絵も絵日記に\n描いてみたんだけど…\n笑っちゃダメよ？", 101, 0, 4, 8458, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131901, 1, "何気ない時間だって\n後で思い返せば\nずっと忘れない\n素敵な思い出になるの", 102, 0, 8, 8459, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131901, 1, "気持ちよく起きて、\nいい一日を過ごしたい\nそんなときには\nラジーオタイソーね！", 1, 1, 1, 8460, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131901, 1, "お昼ごはんの時間ね\n暑さに負けないように\nしっかり食べましょう", 2, 2, 1, 8461, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 131901, 1, "外は涼しいみたいだし\n夜の散歩に行ってみる\nのはどうかしら？", 3, 3, 1, 8462, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131901, 1, "遊び疲れたら\nここでひとやすみ…\nふわぁ…\n眠くなっちゃった…", 2, 0, 1, 8464, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131901, 1, "細かいところまで\nすごーく丁寧に\n作られてる…", 3, 0, 1, 8465, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131901, 1, "わっ、なんだか\nすーっとする香りね\nちょっぴり涼しく\nなったみたい", 4, 0, 1, 8466, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131901, 1, "海で遊ぶと\n体が冷えるから\nあったかいのも\nすごーく嬉しい…", 5, 0, 1, 8467, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131901, 1, "これも美味しい…！\nペコリーヌが\nどんどん食べちゃう\n気持ちが分かるかも", 7, 0, 1, 8469, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131901, 1, "このお話も面白い…！\nあっという間に時間が\n過ぎちゃったわ…\n…ね、寝てません！", 8, 0, 1, 8470, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131901, 1, "この子と一緒に海に\nいったら楽しそうね！\nどこに行けば\n会えるのかしら？", 10, 0, 1, 8472, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 131901, 1, "ふかふかね！\nこうしてると波の音が\n聞こえてきそう…", 11, 0, 1, 8473, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132101, 1, "こちらでは水着姿で\n過ごすのも\n珍しくないようですね", 1, 0, 1, 8474, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132101, 1, "メイド服ではなく\n姉様と色違いの水着…\nレムは嬉しいです", 2, 0, 1, 8475, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132101, 1, "少し室温が高いかも\nしれませんね…\nいくつか氷を配置して\nおきましょうか", 3, 0, 1, 8476, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132101, 1, "清涼感があるとはいえ\n鉄球をビーチバレーの\nボールに代用するのは\n危ないでしょうか…", 4, 0, 1, 8477, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132101, 1, "ふぅー…ふぅー…\nようやく浮輪が\n膨らみました\n姉様にお渡ししないと", 5, 0, 1, 8478, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132101, 1, "海のことをもっと\n教えていただけますか\n…スバルくんと海の\n話をしたいんです", 101, 0, 4, 8479, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132101, 1, "もし海に行く機会が\nあればレムにも…\nお声がけくださいね\nぜひ、お願いします", 102, 0, 8, 8480, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132101, 1, "何を着ていても\nメイドはメイド\n掃除も料理も\nなんでもござれです", 1, 0, 1, 8481, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132101, 1, "スバルくんとあなたは\n兄弟のように\n仲がいいですね\n姉様とレムと一緒です", 2, 0, 1, 8482, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132101, 1, "この姿で子どもたちと\n遊ぶのは…さすがに\nはばかられますね\nやはり着替えてきます", 3, 0, 1, 8483, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132101, 1, "ナンパ…というものが\n海にはあるそうです\nスバルくんがとても\n警戒していました", 4, 0, 1, 8484, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132101, 1, "魚釣りでしたら\nレムもご一緒します\n氷漬けの魚の備蓄を\n補充したかったので", 101, 0, 4, 8485, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132101, 1, "子どもたちと砂遊びの\n約束をしているんです\nあなたもご一緒に\nいかがですか？", 102, 0, 8, 8486, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132101, 1, "ラジーオタイソーは\nもうされましたか？\n体がほぐれますよ", 1, 1, 1, 8487, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132101, 1, "お昼ですね。では\n姉様に焼きそばを…\nもちろん皆様の分も\n用意してありますよ", 2, 2, 1, 8488, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132101, 1, "熱帯夜であっても\nお休みの際はお腹を\n冷やさないように\n気をつけてくださいね", 3, 3, 1, 8489, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132101, 1, "とても気持ちよさそう\nです…！\nさっそく姉様に\nお教えしないと", 2, 0, 1, 8491, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132101, 1, "季節に合わせた\n着せ替えができれば\n子どもたちも\n楽しめそうです", 3, 0, 1, 8492, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132101, 1, "ふむふむ…この香りは\n疲れを癒やしてくれ\nますね。エミリア様に\nお持ちします", 4, 0, 1, 8493, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132101, 1, "身も心もほぐれます\n思っていたよりも\n体が冷えていたみたい\nですね…ふう…", 5, 0, 1, 8494, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132101, 1, "もぐもぐ…んうっ！？\nこのすっぱさは…\n梅干し、ですか…\nくせになりますね…！", 7, 0, 1, 8496, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132101, 1, "海に関する詩文なども\nあるのでしょうか…\n気になります", 8, 0, 1, 8497, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132101, 1, "地竜とは違った\n乗り心地です…\n本物にも乗ってみたい\nものですね", 10, 0, 1, 8499, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132101, 1, "この大きさで\n本物の貝であれば…\nどんな料理にするか\n想像が膨らみます", 11, 0, 1, 8500, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132001, 1, "これがこの世界の\n居住環境か…\n面白い構造だね", 1, 0, 1, 8501, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132001, 1, "暇を持て余していてね\n誰か話し相手に\nなってくれないかな", 2, 0, 1, 8502, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132001, 1, "目新しいものが\nたくさんあるね\n胸が躍るよ", 3, 0, 1, 8503, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132001, 1, "頑丈そうな建物だ\nここなら彼女たちが\n暴れても大丈夫かな？", 4, 0, 1, 8504, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132001, 1, "力が制限されていて\n良かった面もある\n君も吐かずにすんだし", 5, 0, 1, 8505, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132001, 1, "悪さなんてするわけ\nないじゃないか\n傷つくなぁ", 101, 0, 4, 8506, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132001, 1, "普段と違う格好で\n歩き回るのも\n悪くない経験だ", 102, 0, 8, 8507, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132001, 1, "ボクに聞きたいことが\nあるなら遠慮なく\n質問するといい", 1, 0, 1, 8508, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132001, 1, "気軽に魔女に\n話しかけるなんて\n君は物好きだね", 2, 0, 1, 8509, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132001, 1, "この世界においては\nボクは異物だからね\n大人しくしているさ", 3, 0, 1, 8510, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132001, 1, "ボクの反応を\n見たいのかい？\n君の好奇心には\n恐れ入るよ", 4, 0, 1, 8511, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132001, 1, "ボクの手を借りたい？\nもちろん構わないさ\n対価はいただくけどね", 101, 0, 4, 8512, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132001, 1, "ボクと海を見たい？\nふふ、魅力的な\nお誘いだね", 102, 0, 8, 8513, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132001, 1, "お目覚めかな？\nさて、君の見た夢を\n教えてくれないかな？", 1, 1, 1, 8514, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132001, 1, "食後のお茶を\n一緒にどうだろうか？\nさあ、席に座ってよ", 2, 2, 1, 8515, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132001, 1, "おや、お休みかな？\n魔女の前で無防備だね\n…無論、何もしないさ", 3, 3, 1, 8516, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132001, 1, "こういう椅子は\n茶会には向かないね\nつい気が緩んでしまう", 2, 0, 1, 8518, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132001, 1, "こういうものを好きな\n女の子がいいのかな？\n君の嗜好を知りたいな", 3, 0, 1, 8519, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132001, 1, "いい香りだ\nこれにはどんな\n効能があるんだろう？", 4, 0, 1, 8520, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132001, 1, "火の点け方に\nボクのいた世界との\n違いはないのかな？", 5, 0, 1, 8521, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132001, 1, "これもまた未知の味だ\n存分に味わわせて\nもらうとしよう", 7, 0, 1, 8523, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132001, 1, "素晴らしいね\nここには叡智が\n詰まっている", 8, 0, 1, 8524, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132001, 1, "魔物を模倣した\nぬいぐるみか…\n実物を見たくなるね", 10, 0, 1, 8526, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132001, 1, "いい気持ちだ…ボクと\nしたことが、何も考え\nられなくなりそうだ", 11, 0, 1, 8527, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109701, 1, "願わずにいられません\nどうかこちらの\n世界には…悲劇が\n生まれませんようにと", 103, 0, 12, 8528, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109701, 1, "食事のご希望があれば\nなんなりと。お腹が\nいっぱいになるまで\n沢山ご用意しますね", 103, 0, 12, 8529, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109801, 1, "あの男、これからも\n観察が必要ね\nラムの目の届く範囲に\n置く必要があるわ", 103, 0, 12, 8530, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109801, 1, "素直なところは\nあの男の美点ね\n癪だけど、偶然にも\nラムと同じだわ", 103, 0, 12, 8531, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 109901, 1, "迷惑かけちゃったけど\n雪遊びは楽しかったわ\nまたやりたい…なんて\n言ったら怒られるかも", 103, 0, 12, 8532, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 109901, 1, "数えきれないくらいの\n思い出を、みんなで\n作りましょう！", 103, 0, 12, 8533, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132201, 1, "わわっ、今すごく\nいい香りがしたけど…\n誰の香水かな\n教えてほしいな…♪", 1, 0, 1, 8534, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132201, 1, "錬金術用の釜、\n置くならどこかな？\n自分専用の錬金釜、\n憧れちゃうよね♪", 2, 0, 1, 8535, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132201, 1, "この匂い…誰か\n唐揚げ食べたみたい\nうぅ…お腹空いて\nきちゃうよ", 3, 0, 1, 8536, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132201, 1, "次の新曲のタイトルは\n『恋の錬金術』！\nうーん、ちょっと\nベタ過ぎるかな…", 4, 0, 1, 8537, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132201, 1, "あ、錬金の素材\n切らしてたんだ…\n彼を誘って採集に\n行ってこようかな", 5, 0, 1, 8538, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132201, 1, "瞬時に着替えができる\n薬とかできないかな？\n舞台で衣装チェンジ！\nなーんて、楽しそう♪", 101, 0, 4, 8539, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132201, 1, "彼が遊びに来た後は\n部屋の空気がほんのり\n彼の匂いなんだ…\nなんだか幸せだな♪", 102, 0, 8, 8540, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132201, 1, "今日もバイト？\nそれならこれ飲んで\n私のお手製ポーション\nがんばってきてね！", 1, 0, 1, 8541, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132201, 1, "新しくできたカレー屋\n行ってみない？\n時々スパイスの香りが\n流れてくるんだよ", 2, 0, 1, 8542, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132201, 1, "私のフィギュア\nそっくりでしょ！\nルイちゃんが作って\nくれたんだ", 3, 0, 1, 8543, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132201, 1, "あれ、シャンプー\n変えた？　私と同じの\nだよ！　どうしよう\nドキドキしちゃう…", 4, 0, 1, 8544, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132201, 1, "いらっしゃいませ♪\n何を作りますか？\nなーんてね。錬金堂で\nまた一日店長したいな", 101, 0, 4, 8545, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132201, 1, "クンクン…彼の\nハンカチ…いい匂い♪\n私だけの秘密のお守り\nにしてるんだ", 102, 0, 8, 8546, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132201, 1, "ねえねえ、天気も\nいいし一緒に錬金の\n素材集めに行こうよ！", 1, 1, 1, 8547, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132201, 1, "お昼ご飯、用意したよ\nあはは、さすがに\nランチは錬成しないよ\n私の手作りだよ♪", 2, 2, 1, 8548, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132201, 1, "今日も一日お疲れさま\nこれ飲んでゆっくり\n休んで。薬じゃないよ\nホットミルクだよ？", 3, 3, 1, 8549, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132201, 1, "昨日、遅くまで\n錬金術の勉強してた\nから眠くって…\nちょっと一休みっと", 2, 0, 1, 8551, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132201, 1, "この子にちょっとだけ\n香水かけて、と…\nんー！　だっこした時\nすっごくいい匂い♪", 3, 0, 1, 8552, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132201, 1, "花みたいな、\nお菓子みたいな\nいい香り。気持ちが\n穏やかになるね♪", 4, 0, 1, 8553, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132201, 1, "外でもこの暖かさを\n感じられる道具、\n作れたりしないかな？", 5, 0, 1, 8554, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132201, 1, "素材集めに行くと\nお腹が空くんだよね\nイロハちゃんも\nこの味好きそう", 7, 0, 1, 8556, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132201, 1, "錬金術にはたくさんの\n知識が必要だからね\nさあ、今日も調合書を\n読まなくちゃ", 8, 0, 1, 8557, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127701, 1, "ここが騎士さんたちの\n住んでる場所…！\n粗相がないように\n気をつけなきゃ", 1, 0, 1, 8559, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127701, 1, "ジオ・テオゴニアの\nみんな、私がいない間\nちゃんとお仕事\nしてればいいけど…", 2, 0, 1, 8560, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127701, 1, "コッコロさんも\n従者として長いって\n聞いたような…\nあの年ですごい…！", 3, 0, 1, 8561, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127701, 1, "自分がもてなされる\n側になるのって\nやっぱりちょっと\n落ち着かないかも…", 4, 0, 1, 8562, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127701, 1, "今度のお休みの日は\n何しよう…？\nあっ、最近できたお店\nヴルムと行こうかな？", 5, 0, 1, 8563, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127701, 1, "お買い物したら\nかわいいからって\nたくさんおまけして\nもらっちゃった…♪", 101, 0, 4, 8564, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127701, 1, "ここにいると、つい\nぼんやりしちゃう…\nこれも騎士さんの力…\nなのかな", 102, 0, 8, 8565, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127701, 1, "お手伝いできることは\nありませんか？\nお世話でもなんでも\n任せてくださいっ", 1, 0, 1, 8566, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127701, 1, "ランドソルに初めて\n来たときは驚きました\n砂糖の山と思ったら…\nあんなに寒いなんて！", 2, 0, 1, 8567, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127701, 1, "あの、ヴルムを\n見ませんでしたか？\nもう…あの子ってば\nまた仕事をサボって…", 3, 0, 1, 8568, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127701, 1, "家具や家が壊れても\n食べられないなんて\nなんだかもったいない\nですね", 4, 0, 1, 8569, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127701, 1, "とっておきの紅茶を\n手に入れたんです\n一杯いかがですか？\nその、日頃のお礼です", 101, 0, 4, 8570, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127701, 1, "頑張ってて偉いって…\nほ、褒めてもなんにも\n出ませんからね！？\n…えへへ♪", 102, 0, 8, 8571, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127701, 1, "おはようございます！\nモーニングティーは\nいかがですか？\n私、準備しますねっ", 1, 1, 1, 8572, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127701, 1, "ライラエルさまも\nお昼寝はお好きです\nけど…だからって\n寝すぎはダメですよ！", 2, 2, 1, 8573, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127701, 1, "おやすみなさい\nくれぐれも寝不足には\n気をつけてくださいね", 3, 3, 1, 8574, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127701, 1, "しっかりしないと…\nメイドの私が\n気を抜くなんて…\nそんな…ふぅ…", 2, 0, 1, 8576, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127701, 1, "わぁ、かわいい…♪\nってダメダメ！\nメイドなんだから\n落ち着かないと…！", 3, 0, 1, 8577, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127701, 1, "いい匂い…♪\nこの香り、\nライラエルさまも\n好きそうです！", 4, 0, 1, 8578, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127701, 1, "ランドソルの環境に\n触れると、この光の\nありがたみが\n身に沁みますね…", 5, 0, 1, 8579, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127701, 1, "甘くない食べ物が\n一般的なのも\n慣れてきました\nあむ…おいひぃ…♪", 7, 0, 1, 8581, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127701, 1, "ランドソルの文字、\n私たちのところと\nちょっと違いますね…\n覚えられるかなぁ", 8, 0, 1, 8582, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127801, 1, "甘い匂いがしない…\nジオ・テオゴニアとは\n別の世界なんですね", 1, 0, 1, 8584, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127801, 1, "掃除めんど…\nん…このくらいで\nまー十分でしょ", 2, 0, 1, 8585, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127801, 1, "そろそろ食事の\n用意もしなきゃ…\nあーやる事多い…", 3, 0, 1, 8586, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127801, 1, "秘密の隠れ場所、\nまた姉さんにバレた…\n次のを探さないと", 4, 0, 1, 8587, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127801, 1, "知らない間に仕事が\n終わったり…しないか\nしてくれたらいいのに", 5, 0, 1, 8588, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127801, 1, "きちんともてなすには\n騎士さんの好みも\n覚えてかないと…", 101, 0, 4, 8589, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 127801, 1, "ご主人サマに\nご奉仕します～♪\n…キャラじゃないか\nやめやめ、恥ずかし…", 102, 0, 8, 8590, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127801, 1, "居心地いいっすね…\n今日からここに\n住んでもいいですか", 1, 0, 1, 8591, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127801, 1, "あの、ちょっと姉さん\n怒らせちゃったんで\n匿ってくれません？", 2, 0, 1, 8592, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127801, 1, "羽と尻尾が格好いい？\n意外と邪魔ですよ\n仰向けで寝にくいし", 3, 0, 1, 8593, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127801, 1, "ワークライフバランス\nっていい言葉ですね\nぜひライフ多めで", 4, 0, 1, 8594, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127801, 1, "騎士さんって\n働き者ですね\nそのままあたしの\n仕事も…いえなんでも", 101, 0, 4, 8595, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127801, 1, "熱心に見てても\n尻尾は触らせて\nあげませんってば\n角？　角なら…まぁ", 102, 0, 8, 8596, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127801, 1, "寝ても寝ても\n寝足りない…ふぁ…\nあー…眠たい…", 1, 1, 1, 8597, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127801, 1, "アフタヌーンティーの\n準備は得意ですよ\nつまみ食いとか\n…つまみ食いとか", 2, 2, 1, 8598, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 127801, 1, "よく休んでください\n布団は干しといたんで\n…姉さんが", 3, 3, 1, 8599, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127801, 1, "ふー…\nこのままずーっと\nここに居たい…", 2, 0, 1, 8601, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127801, 1, "あんたも一緒に\nサボっちゃうか～\n…なんて", 3, 0, 1, 8602, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127801, 1, "落ち着く匂い…\n騎士さんの好み\nなんですかね", 4, 0, 1, 8603, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127801, 1, "こういうとこの\n掃除って面倒だし…\n今度でいっか", 5, 0, 1, 8604, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127801, 1, "ご飯の前に\nこんな食べたら\n姉さんきっと怒るな…", 7, 0, 1, 8606, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 127801, 1, "食に関する本が\nやけに多い…\nお国柄ですかね", 8, 0, 1, 8607, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130201, 1, "すんすん…\nお兄さんの匂いが\nぷんぷんする～♡\nザコくっさぁ♡", 1, 0, 1, 8609, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130201, 1, "ぴこーん！\nいひひっ、新作メカの\n面白いアイデア、\n思いついちった～♡", 2, 0, 1, 8610, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130201, 1, "メカもいじりたいし\n服もアクセもほしー…\nあ～、おこづかい\n足んないよ～！", 3, 0, 1, 8611, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130201, 1, "お勉強でもしよっかな\nザコお兄さんには\n絶対わかんない\nよーなやつ♡", 4, 0, 1, 8612, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130201, 1, "お兄さんの部屋って\nどこどこぉ？\nメカの実験室に\n使えそうだよね～♡", 5, 0, 1, 8613, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130201, 1, "次のデートコースは\nどんなのかなー？\n…いや、むしろ次は\nぼくが決めちゃうっ？", 101, 0, 4, 8614, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130201, 1, "強奪計画は念入りに♡\nお兄さんのハートも\n全力でザコザコにする\n計画、立てないとね♡", 102, 0, 8, 8615, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130201, 1, "ねー、ヒマヒマヒマ～\nかまってかまってー♡\nどっか連れてけー♡", 1, 0, 1, 8616, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130201, 1, "今日はこっちで\nご飯食べたいなー♡\n…帰っても５日連続の\nバカナ姉カレーだし…", 2, 0, 1, 8617, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130201, 1, "依頼行くならぼくも\n連れてってよー♡\nお兄さんのザコっぷり\n見ててあげる～♡", 3, 0, 1, 8618, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130201, 1, "お兄さんはぼくより\nオトナだもんね～♡\nじゃあオモテナシ、\n期待してるから～♡", 4, 0, 1, 8619, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130201, 1, "ヤマ姉になんかお土産\n持って帰ろうかなー？\nザコお兄さん、選ぶの\n付きあって～♡", 101, 0, 4, 8620, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130201, 1, "遊びにいくときは\nザコお兄さんも\n誘ってあげるよ♡\nどうせヒマでしょ～？", 102, 0, 8, 8621, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130201, 1, "ふぁ～、ねむぅ～…\n夜更かしぶん寝るから\nあとで起こして～♡\nあ、変なこと禁止～♡", 1, 1, 1, 8622, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130201, 1, "おなかすいたぁ～\nなんか食べ行こ♡\n甘いのおごって～♡", 2, 2, 1, 8623, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130201, 1, "えー、もう寝る時間？\nじゃあ…添い寝して\nあげよっか？\nなーんて、ウッソー♡", 3, 3, 1, 8624, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 130201, 1, "おお～ふわっふわ～♪\nバンディシャーク号の\n座席に使いたーい！", 2, 0, 1, 8626, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 130201, 1, "カワイイ～♡\n改造したらもっと\nカワイくなるかな～？", 3, 0, 1, 8627, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 130201, 1, "くんくん…いい匂い♡\nザコお兄さんって\nこーゆー香りが\n好きなのかなぁ～", 4, 0, 1, 8628, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 130201, 1, "はー、あったかーい♡\nバンディシャーク号\nにも欲しいかもー♪", 5, 0, 1, 8629, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 130201, 1, "食べれるときに\n食べるのが大事って\nヤマ姉言ってたしね～", 7, 0, 1, 8631, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 130201, 1, "あ、この本読んだよ♡\n参考文献リストが\nしっかりしてて\n勉強になったなー♪", 8, 0, 1, 8632, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130001, 1, "部屋は多少ゴチャつい\nてる方が落ち着くのさ\nバンディシャーク号\nみたいにね♪", 1, 0, 1, 8634, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130001, 1, "呆けてるのもいいが\nちいと物足りないねぇ\n麦しゅわもブドウ朱も\n無いなんてさ", 2, 0, 1, 8635, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130001, 1, "この家セキュリティが\nなってないねぇ…\n盗賊目線でちょっくら\n指南でもしてやるか", 3, 0, 1, 8636, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130001, 1, "不思議だねぇ\nここで飲むと、酔いが\n回るのが早い気が\nするよ…ヒック", 4, 0, 1, 8637, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130001, 1, "ふむ…\nクルマが壊れた時は\nアジト代わりに\n使えそうだ♪", 5, 0, 1, 8638, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130001, 1, "カカッ、いい部屋だ！\n妹を迎える準備は\nできてるみたいだねぇ", 101, 0, 4, 8639, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130001, 1, "…っと、油断したら\n落ち着いちまうね\nボーヤの甘っちょろい\n雰囲気のせいだよ", 102, 0, 8, 8640, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130001, 1, "さすがにこの家から\n盗みはしないさ\nいずれは妹たちの\n共有財産になるからね", 1, 0, 1, 8641, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130001, 1, "ボーヤ、暇なら\n博打でもやるかい？\nアタシが負けたら\n掃除でもしてやるよ", 2, 0, 1, 8642, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130001, 1, "立派な家じゃないか！\n一国一城の主とは\nちょっとボーヤのこと\n見くびってたかもねぇ", 3, 0, 1, 8643, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130001, 1, "かわいい義弟のためだ\nアタシが盗ってきた\nお宝をくれてやるよ\n好きな場所に飾りな！", 4, 0, 1, 8644, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130001, 1, "妹と結ばれる前に\n他の女との関係は\n清算しとくように\nわかったね、ボーヤ？", 101, 0, 4, 8645, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130001, 1, "お、ヤシオーリかい？\nフフン、義弟からの\nお酌とあっちゃあ\n断れないねぇ♪", 102, 0, 8, 8646, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130001, 1, "うう…清々しい朝だが\n頭がいてぇ…\nちっと昨日は\n飲みすぎたか…？", 1, 1, 1, 8647, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130001, 1, "ほらこれ、ワカナに\n作らせた弁当だ\nこれで昼からも\nバリバリやりな！", 2, 2, 1, 8648, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130001, 1, "まだまだ夜はこれから\n…と言いたいとこだが\n早寝して立派に育ちな\nそれが妹たちの為だよ", 3, 3, 1, 8649, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 130001, 1, "中々いい座り心地だが\n盗賊が腑抜ける訳には\nいかないね…が…\nもう少しだけ座るか…", 2, 0, 1, 8651, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 130001, 1, "こういうのの中に\n実はレアなブツが\n隠されてるとか\nありがちだねぇ", 3, 0, 1, 8652, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 130001, 1, "ふうん、ボーヤは\nこういう匂いが\n好きなのかい？\n妹たちに教えてやろう", 4, 0, 1, 8653, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 130001, 1, "体を温めんにゃ\n酵露をカッとやるのが\n一番だが、これも存外\n悪くないねぇ…♪", 5, 0, 1, 8654, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 130001, 1, "ガツガツ…\nん、悪くねェ味だ\nこれにヤシオーリでも\nありゃ文句はないね", 7, 0, 1, 8656, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 130001, 1, "本にも載ってない\nレアもんのお宝か…\nフッ、想像しただけで\n血沸き肉躍るねぇ…！", 8, 0, 1, 8657, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130101, 1, "今日の買い物は\nいつものお野菜と…\nあっ、確か特売が\nあったはず…！", 1, 0, 1, 8659, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130101, 1, "お掃除、お掃除…♪\nきゅっきゅっ…\nよいしょ、っと…", 2, 0, 1, 8660, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130101, 1, "洗濯ものも\nたまらないうちに\nやっちゃわないと…", 3, 0, 1, 8661, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130101, 1, "夕飯のメニュー…\n何にしよう？\n期限の早い食材は…", 4, 0, 1, 8662, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130101, 1, "色々お菓子も\n作ってみたいけど…\n予算…足りるかなぁ…", 5, 0, 1, 8663, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130101, 1, "騎士さん…どんな\n料理が好きなんだろ？\n前に作ったコロッケは\n評判よかったけど…", 101, 0, 4, 8664, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 130101, 1, "騎士さんと\nおしゃべりすると、\n胸があたたかくなる…\nなんでだろ…？", 102, 0, 8, 8665, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130101, 1, "あっ、騎士さん\nどうかしましたか？\n御用ならいつでも…♪", 1, 0, 1, 8666, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130101, 1, "削っても削っても\n家計簿は赤字…\n騎士さ～ん…\n助けてください～！", 2, 0, 1, 8667, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130101, 1, "手が空いてたら\n日用品の買い出し、\n手伝ってもらっても\nいいですか…？", 3, 0, 1, 8668, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130101, 1, "やっと一息つけそう…\nあっ、わたし\nお茶を淹れてきますね", 4, 0, 1, 8669, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130101, 1, "困りごとがあったら\n言ってくださいね\nわたし…騎士さんの\n力になりたいです", 101, 0, 4, 8670, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130101, 1, "助けてもらった分は\n必ず返しますから…\nそれまでわたしのこと\n見ててくださいね…♪", 102, 0, 8, 8671, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130101, 1, "忘れ物はないですか？\n今日のお仕事も\n頑張りましょうね…！", 1, 1, 1, 8672, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130101, 1, "お昼ご飯できてますよ\n大したものじゃなくて\n恐縮ですけど…", 2, 2, 1, 8673, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 130101, 1, "夜更かしはだめですよ\nわたしも家事を\n終わらせたら寝ないと", 3, 3, 1, 8674, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 130101, 1, "これ…割れ物防止の\nクッションに\n使えるかも…？", 2, 0, 1, 8676, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 130101, 1, "こういうのいいなぁ…\nけど…フブキちゃんに\n絶対改造されちゃう…", 3, 0, 1, 8677, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 130101, 1, "いい匂い…でも\n切れかけてるかも？\nえっと補充するには…", 4, 0, 1, 8678, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 130101, 1, "あったかい…\nちょっとだけなら\n休んでも…いいよね", 5, 0, 1, 8679, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 130101, 1, "これがあったら\nもうお料理を\n作らなくて済むかも？", 7, 0, 1, 8681, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 130101, 1, "新しい節約レシピは…\nあ、この草って\n食べられるんだ…", 8, 0, 1, 8682, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132801, 1, "愛溢れるランドソルの\nクリスマス…ですか\nいけませんね、つい\n胸が躍ってしまいます", 1, 0, 1, 8684, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132801, 1, "ツリーの飾りと\nわたしの髪…\nくるくるしていて\n似ているかも…くすっ", 2, 0, 1, 8685, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132801, 1, "この姿で傲慢な\nわたしになったら…\nいつもより怖がらせて\nしまうでしょうね…", 3, 0, 1, 8686, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132801, 1, "悪魔として、人を\nドキドキさせなくては\nい、イオから教わった\n…投げキスの練習を…", 4, 0, 1, 8687, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132801, 1, "ジオ・テオゴニアの\nお菓子は、ツリーの\n飾りに向いていそう\nですね…ふふっ", 5, 0, 1, 8688, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132801, 1, "騎士のギルドハウス…\nクリスマスの時期も\n暖かいのですね…", 101, 0, 4, 8689, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132801, 1, "城の皆と過ごす宴も\n楽しいですが…\n騎士と二人の宴も\n幸せです…", 102, 0, 8, 8690, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132801, 1, "騎士、あなたの\nもてなしに感謝します\nクリスマスの何たるか\n教えてくださいね", 1, 0, 1, 8691, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132801, 1, "この黒い羽は\nどう手入れすれば？\nイオなら知っている\nでしょうか…", 2, 0, 1, 8692, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132801, 1, "城の皆も連れてきたい\nほど、クリスマスは\n楽しい行事ですね\nお土産も迷います", 3, 0, 1, 8693, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132801, 1, "クリスマスの晩餐に\n招待されたのですが…\nその…辛いものは\nありませんよね…？", 4, 0, 1, 8694, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132801, 1, "クリスマスと言わず…\nいつでも星に祈って\nいますよ？　騎士達に\n幸せがあるように、と", 101, 0, 4, 8695, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132801, 1, "悪魔として騎士を\nドキドキさせる…？\nそ、そのようなこと\nわたしには…うぅ…", 102, 0, 8, 8696, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132801, 1, "朝は寒いですね…\n雪…積もっている\nでしょうか？", 1, 1, 1, 8697, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132801, 1, "ふあぁ…いけません\n公務の滞在先で\nお昼寝など…", 2, 2, 1, 8698, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132801, 1, "夜は悪魔として\n夢を見せましょう\n騎士はどんな夢が\n見たいですか？", 3, 3, 1, 8699, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132801, 1, "ふぅ…落ち着きます…\n家でゆっくりと過ごす\nクリスマスも\n良いものですね", 2, 0, 1, 8701, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132801, 1, "リンドのプレゼントに\nぬいぐるみは…？\n…子ども扱いすると\n落ち込むでしょうか…", 3, 0, 1, 8702, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132801, 1, "暖かいお部屋に\nこの香りを漂わせたら\nふふっ…素敵な時間を\n過ごせそうです", 4, 0, 1, 8703, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132801, 1, "これがあれば、\n寒い冬も安心ですね\n暖かくて…このまま\n眠ってしまいそうです", 5, 0, 1, 8704, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132801, 1, "悪魔の力を使ったら\nお腹が空いてきて\nしまって…\n恥ずかしいです…", 7, 0, 1, 8706, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132801, 1, "クリスマスには多くの\n伝承があるのですね…\n天使の伝承まで\nありましたし…", 8, 0, 1, 8707, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132803, 1, "まぁ…！\nよくできていますね\nサプライズプレゼント\n…でしょうか。ふふ♪", 14, 0, 1, 8709, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132804, 1, "ど、どうでしょう…？\n親善交流の催しに\nなりそうでしょうか…", 15, 0, 1, 8710, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132901, 1, "ギルドハウスって\nあったかぁ～い♪\n心がぽかぽかするの！", 1, 0, 1, 8711, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132901, 1, "クリスマスの悪魔の\nお仕事はないわね！\nだってここのみんな\nいい子なんですもの♪", 2, 0, 1, 8712, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132901, 1, "みんなちゃんと\n寝られてるかしら？\n夜更かしせずに、\nいい夢見てね♪", 3, 0, 1, 8713, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132901, 1, "毎回、来年が\n楽しみになるような\n素敵なクリスマスを\n過ごしたいわね♪", 4, 0, 1, 8714, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132901, 1, "訳ありケーキを見ると\n刑務作業を思い出すわ\n…あれも貴重な体験\nだったのかしら…？", 5, 0, 1, 8715, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132901, 1, "ロマンチックな\nクリスマスに備えて\n今のうちに少女漫画で\n予習しておかないと！", 101, 0, 4, 8716, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132901, 1, "ほっぺにチューなら\nもう一回しても…\nや、やっぱり無理！\n恥ずかしいわ～！", 102, 0, 8, 8717, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132901, 1, "またみんなで\nセッションしたいわね\nカラーベルの練習した\n成果を見せたいの♪", 1, 0, 1, 8718, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132901, 1, "君はサンタさんに\nどんなプレゼントを\n頼んでいたのかしら？\n気になるわ～！", 2, 0, 1, 8719, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132901, 1, "愛が壊れても、\n何度でも治してあげる\n私たちの間に、\n愛の尽きる日はないわ", 3, 0, 1, 8720, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132901, 1, "冬休みって短いようで\n長く感じちゃうわよね\n学校のみんなと\n早く会いたいな～♪", 4, 0, 1, 8721, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132901, 1, "愛をテーマにした\n冬休みの作文の宿題、\n君の分を読むのが\n今から楽しみね♪", 101, 0, 4, 8722, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132901, 1, "よかったらまた\n私をハニーって呼んで\nほしいの。ダーリン♪", 102, 0, 8, 8723, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132901, 1, "おはよう！\n冬の朝は冷えるから\n風邪を引かないように\n対策しましょうね", 1, 1, 1, 8724, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132901, 1, "お昼ご飯は一緒に\n食べに行きましょう！\nクリスマス限定の\nランチコースよ♪", 2, 2, 1, 8725, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132901, 1, "ねえねえ、キャンドル\nイルミネーションを\n見に行きましょうよ！\nロマンチックよ～♪", 3, 3, 1, 8726, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132901, 1, "暖かいクリスマスを\n過ごすのに必須よね♪\nふふ、柔らかぁい！", 2, 0, 1, 8728, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132901, 1, "どの子もかわいい～♪\nクリスマスツリーの\n下で、一緒にお祝い\nしてもらいたいな♪", 3, 0, 1, 8729, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132901, 1, "なんだか胸の奥が\n温かくなる匂いね～\nリラックスしちゃう♪", 4, 0, 1, 8730, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132901, 1, "あったかぁい♪\nあ、でも夜になったら\nサンタさんが入れる\nように、火を消すのよ", 5, 0, 1, 8731, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132901, 1, "寒い冬だからこそ\nしっかり食べて\n備えておかなきゃ！", 7, 0, 1, 8733, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132901, 1, "『聖夜の６時間』…？\n気になる小説だけど…\n勝手に読んじゃ悪い\nわよね…ガマンよ…！", 8, 0, 1, 8734, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132901, 1, "きゃっ…！\nこんな不思議な本が\nあったのね～\nちょっと面白いかも♪", 14, 0, 1, 8736, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132901, 1, "もふもふ、ふわふわで\nあったかくて\n気持ちいい～…♪", 15, 0, 1, 8737, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129501, 1, "冬といえばサマー！\n…って言うけど夏は\n何をＰＲするんだろ\nメルクリゾート…", 1, 0, 1, 8738, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129501, 1, "奨学金の道は遠いな…\nあまりに遠すぎて\n最近忘れてたまである", 2, 0, 1, 8739, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129501, 1, "暇だし鏡でも見よっと\nはー、今日も顔が良…\nげ、若干むくんでる？\nはー、今日終わった", 3, 0, 1, 8740, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129501, 1, "水着…お姉ちゃんと\n貸し借りするんだけど\nこれって少数派姉妹？", 4, 0, 1, 8741, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129501, 1, "ちゅむ、ツムギちゃん\n今日も可愛いねデュフ\n…なんで推し語りの声\nキモくなるんだろ私…", 5, 0, 1, 8742, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129501, 1, "あれ…なんで私\n水着のまま部屋の中\nうろついてるんだっけ\n…こわ！　てか寒っ！", 101, 0, 4, 8743, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 129501, 1, "先輩よく友達と海に\n行くって言ってたけど\n…え、誰とだろ？\nまあ誰でもいいけどさ", 102, 0, 8, 8744, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129501, 1, "始まる…\nチエルの夏が始まる…\nあ冬でしたっけ？\nまどっちでもいいです", 1, 0, 1, 8745, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129501, 1, "こないだトミヱ先輩\n壁に喧嘩売ってました\n相変わらず元気ですね", 2, 0, 1, 8746, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129501, 1, "ユニ先輩にお芝居の\n出来を聞かれたんで\n可愛いって答えました\n嘘は言ってません", 3, 0, 1, 8747, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129501, 1, "クロエ先輩の腹筋\nかっちょいーですよね\nさりげ触りたいけど\nあいつ隙ないんですよ", 4, 0, 1, 8748, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129501, 1, "先輩もうカメラ係は\nやらないんです？\nチエルはやってますよ\n最高の被写体を常に", 101, 0, 4, 8749, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129501, 1, "メリゾでカップル割の\n企画あるらしいんです\nちなみに先輩は…\nあ、いえ、何でも…", 102, 0, 8, 8750, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129501, 1, "朝ごはんは抜いても\n朝ちぇるだけは\n抜いちゃ駄目ですよ", 1, 1, 1, 8751, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129501, 1, "お昼なに食べるか\n悩んでます？\nおごってくれるなら\nいい店教えますけど", 2, 2, 1, 8752, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 129501, 1, "この時間帯になると\nユニ先輩がごそごそ\n蠢きだすんですよね…", 3, 3, 1, 8753, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129501, 1, "世界の美少女が…\nこんな格好で\nくつろいでたら…\nまずい…事案になる", 2, 0, 1, 8755, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129501, 1, "え、この子って\nウォータープルーフ？\nメリゾにも連れて\nいけちゃうカンジ？", 3, 0, 1, 8756, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129501, 1, "はぁ～、いい香り…\nそういえばメリゾの\nスタッフルームも\n『贅』な匂いしてた…", 4, 0, 1, 8757, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129501, 1, "薄着で暖まるとか…\n冷静になっちゃ\n駄目なやつですよね\nこれ…", 5, 0, 1, 8758, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129501, 1, "おにぎり１個は\nちぇるに換算すると…\n２５ちぇるくらい？", 7, 0, 1, 8760, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129501, 1, "あれー？　この辞書\nまだちぇるの項目\n載ってないんだ？", 8, 0, 1, 8761, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129501, 1, "何これ…地味に楽しい\nあとたぶん\nクロエ先輩が好きそう", 10, 0, 1, 8763, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 129501, 1, "これは良きですねー\n自宅でメリゾ気分とか\nユニ先輩もにっこり", 11, 0, 1, 8764, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133001, 1, "ここが騎士氏の\nお住まいですか\nふむ…地縛霊は\nいなさそうですね", 1, 0, 1, 8765, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133001, 1, "ここは土足厳禁では\nないみたいですね\nま、私には関係ないか\n足、透っけ透けなので", 2, 0, 1, 8766, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133001, 1, "ランドソルの裏路地\nあそこは最高ですね\n暗くてじめじめしてて\n癒されるー", 3, 0, 1, 8767, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133001, 1, "っ！\n今ラップ音が…？\nまさか幽霊？\nやだ、怖い", 4, 0, 1, 8768, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133001, 1, "獣人族にエルフ族…\nなるほど騎士氏が\n幽霊程度では驚かない\n訳です…ちぇ", 5, 0, 1, 8769, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133001, 1, "今日の深夜\n騎士氏が起きるまで\n枕元でずーっと\n立っててみようかな", 101, 0, 4, 8770, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133001, 1, "騎士氏の驚く顔を\n見ると胸が高鳴る…\n変ですね、心臓なんて\n動いてないのに", 102, 0, 8, 8771, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133001, 1, "安心して下さい\n勝手にすり抜けて\n騎士氏の部屋は\n覗きませんよ…多分", 1, 0, 1, 8772, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133001, 1, "なんですか騎士氏\n怖い話でも聞きます？\nじゃあいきますね…\n今貴方の前に幽霊がっ", 2, 0, 1, 8773, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133001, 1, "この家…幽霊が\n寄ってくる間取りに\nなってますね\nまあ、嘘ですけど", 3, 0, 1, 8774, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133001, 1, "料理はしませんよ\n指きったら痛いので\nえ…透ければいい？\n……天才ですか？", 4, 0, 1, 8775, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133001, 1, "お散歩ですか？\n深夜なら付き合います\nあ、やっぱり断ります\n幽霊出たら怖いので", 101, 0, 4, 8776, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133001, 1, "騎士氏の部屋のセンス\n好きですよ…もっと\nじめじめしていれば\nなおよし、ですが", 102, 0, 8, 8777, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133001, 1, "レイスになってから\nあまり言わなくなった\n挨拶第一位はこちら…\nおはようございます", 1, 1, 1, 8778, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133001, 1, "騎士氏。午後からも\n頑張って下さいね\n私は日陰で休みます\nでは、おつです", 2, 2, 1, 8779, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133001, 1, "夜も更けてきましたね\nここからがレイスの\n本領発揮…では早速\nう　ら　め　し　や～", 3, 3, 1, 8780, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133001, 1, "中々座り心地が\nよろしいですね\nブロッケンに跨る方が\n安心はしますが", 2, 0, 1, 8782, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133001, 1, "知ってますよ\nどうせ貴方も\n夜中になったら\n動き回るんでしょ？", 3, 0, 1, 8783, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133001, 1, "すう…はあ…\nすごく落ち着く香り\n…落ち着き過ぎて\n成仏しそうでした", 4, 0, 1, 8784, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133001, 1, "温度は感じませんが\nなぜかほっこり\n温かい気持ちです\nなんででしょうね？", 5, 0, 1, 8785, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133001, 1, "もぐもぐ…\n食べ物は基本好きです\n消化の方法は…\n企業秘密で", 7, 0, 1, 8787, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133001, 1, "苦手なんですよね\nホラー小説\nだって幽霊とか\n怖いじゃないですか", 8, 0, 1, 8788, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133201, 1, "この得も言われぬ\n高揚感…やはり\n正月はいいものだな！", 1, 0, 1, 8790, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133201, 1, "む、いかんな\n気付くと黒豆と\n栗きんとんばかり\n食べてしまう…", 2, 0, 1, 8791, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133201, 1, "ギルドの連中にも\nお年玉を渡してみるか\n少しは士気が上がる\nかもしれないな！", 3, 0, 1, 8792, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133201, 1, "正月くらいはのんびり\n羽を伸ばすとしよう\n適切な休暇も軍人の\n大事な責務だからな", 4, 0, 1, 8793, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133201, 1, "正月で浮かれすぎた\n者も多いはずだ\n念のため街を見回りに\n行くとしよう", 5, 0, 1, 8794, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133201, 1, "む、このおせち料理は\n絶品だな！\nあいつの分も\nとっておいてやろう", 101, 0, 4, 8795, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133201, 1, "今年の抱負は…\nそうだな、あいつと\nさらに高みを目指す\nこれでいこう", 102, 0, 8, 8796, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133201, 1, "えっ、お年玉！\n…い、いや、私は\n子どもではないからな\nえ、遠慮させてもらう", 1, 0, 1, 8797, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133201, 1, "凧あげすごろく\nこま回し、たとえ遊び\nでも手は抜かんぞ\nさあ、勝負だ！", 2, 0, 1, 8798, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133201, 1, "そのきな粉餅も\nうまそうだな…\n私のあんこと交換\nしないか？", 3, 0, 1, 8799, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133201, 1, "貴公、正月料理を\n食べ過ぎてないか？\nじ、実は私も少々、な", 4, 0, 1, 8800, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133201, 1, "貴公、お参りに行くぞ\n何？　外が寒いだと？\n仕方ない、手ぐらいは\nつないでやろう", 101, 0, 4, 8801, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133201, 1, "貴公と新たな年を\n迎えられてうれしいぞ\n今年も一つ\nよろしくたのむ！", 102, 0, 8, 8802, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133201, 1, "起きろ貴公。早朝なら\n参拝客も少ないはず\n今のうちにお参りを\n済ませよう", 1, 1, 1, 8803, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133201, 1, "昼食に餅を焼いたぞ\nのどに詰まらせない\nようにゆっくり噛んで\n食べるんだ", 2, 2, 1, 8804, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133201, 1, "夜ふかしはあまり\n褒められたものでは\nないが…正月くらいは\n目をつむろう", 3, 3, 1, 8805, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133201, 1, "これに座って飲む\n甘酒は格別だな", 2, 0, 1, 8807, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133201, 1, "せっかくの正月だし\nこいつにも着物を\n着せてやりたいな", 3, 0, 1, 8808, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133201, 1, "これは梅の香り…？\nふふ、なかなか\n風流ではないか", 4, 0, 1, 8809, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133201, 1, "冬の寒さが堪えるな…\nここでしっかりと\n暖をとるとしよう", 5, 0, 1, 8810, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133201, 1, "正月はのんびり\nしたいからな、勝手に\n食事が出てくるのは\n本当に助かる", 7, 0, 1, 8812, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133201, 1, "ふむ、これが世界の\n新年のお祝いか\nなかなか興味深いな", 8, 0, 1, 8813, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133501, 1, "一年の計は元旦にあり\n計画を立てて、\n有意義な一年を\n過ごしましょ", 1, 0, 1, 8815, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133501, 1, "年末年始はどうしても\n出費が増えちゃうし\nしばらくはきっちり\n節約しなきゃ…！", 2, 0, 1, 8816, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133501, 1, "タタミにフトン、\nトーゴク文化の内装…\nランドソルでももっと\n流行らないかしら", 3, 0, 1, 8817, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133501, 1, "子どもたちが凧を\n手作りして遊んでたの\n偉いわよね、みんな…", 4, 0, 1, 8818, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133501, 1, "お年玉をあげる側に\nなって、分かる苦労…\n大人になるって\nこういうことかしら", 5, 0, 1, 8819, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133501, 1, "一応実家にも\n顔を出さないと…\nよかったらだけど、\nあんたも来る？", 101, 0, 4, 8820, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133501, 1, "毎日一緒にいて、\nささやかな幸せを\n同じように感じて…\nそれだけで十分ね", 102, 0, 8, 8821, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133501, 1, "着物姿、もっと\nじっくり見たいの？\nさすがに照れるわよ、\nもう…", 1, 0, 1, 8822, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133501, 1, "整理整頓、できてる？\nもし散らかってるなら\nお正月休みのうちに\n片づけちゃいましょ", 2, 0, 1, 8823, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133501, 1, "今年もみんなが無事に\n過ごせますように…\nほら、一緒に\nお祈りしましょ", 3, 0, 1, 8824, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133501, 1, "みんな、あんたと\n遊ぶのが楽しみだって\n子どもに好かれる\nタイプなのよね", 4, 0, 1, 8825, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133501, 1, "あんたの運勢は大吉！\nラッキーパーソンは\n金髪の幼なじみよ\n…なんてねっ", 101, 0, 4, 8826, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133501, 1, "初夢は実現するのよね\nそれじゃ、あたしは\nあんたと…んんっ\nなんでもないわ", 102, 0, 8, 8827, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133501, 1, "外が寒いと、その分\n目も冴えるわよね\nこのまま朝の散歩に\n行こうかしら", 1, 1, 1, 8828, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133501, 1, "お正月休みでも、\n食べてばっかじゃダメ\nしっかり動いて\n体を引き締めましょ", 2, 2, 1, 8829, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133501, 1, "ちゃんとあったかく\nして寝るのよ\nお正月から\n風邪ひかないようにね", 3, 3, 1, 8830, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133501, 1, "着物、しわに\nなっちゃいそう…\nでもそれ以上に、\n心地よくって…はぁ", 2, 0, 1, 8832, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133501, 1, "門松みたいに\nこの子も飾ってみる？\n運勢がよくなるかも♪", 3, 0, 1, 8833, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133501, 1, "この匂い、もしかして\nトーゴクのお香？\nワビサビを感じるわ", 4, 0, 1, 8834, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133501, 1, "ふぅ、あったかい…\n冷えた体に\n染み渡ってくるわ…", 5, 0, 1, 8835, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133501, 1, "んっ、おいしい！\nおせちばっかじゃ\nやっぱり飽きちゃう\nものね…", 7, 0, 1, 8837, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133501, 1, "験担ぎとか縁起物とか\n調べてみると\nいろいろあって\n面白いのよね～", 8, 0, 1, 8838, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133601, 1, "ふーん、いい家ね\nアタシの巣に\nしちゃおうかしら？", 1, 0, 1, 8840, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133601, 1, "お姉ちゃん…\n早く会いたいなぁ…", 2, 0, 1, 8841, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133601, 1, "メドゥシアナも\n気に入るかしら？\n今度連れてこよっと！", 3, 0, 1, 8842, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133601, 1, "お邪魔してるだけ\nなのも悪いわよね…\nお返しはしないとね\n…ネズミ退治とか？", 4, 0, 1, 8843, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133601, 1, "何か平和よね\nランドソルって…\nアタシ的にはもう少し\n荒っぽくてもいいわ", 5, 0, 1, 8844, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133601, 1, "ランドソルも\n楽しい街よね…\n案内してくれる\nアイツのおかげ？", 101, 0, 4, 8845, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133601, 1, "アタシ、アイツに\n頼りすぎてるかも…？\nで、でも！　断らない\nアイツも問題だわ！", 102, 0, 8, 8846, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133601, 1, "ねぇ、つまんなーい！\nもっとアタシを\n楽しませなさいよねー", 1, 0, 1, 8847, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133601, 1, "この家悪くないけど…\nもっとムシムシ\nジメジメにできない？", 2, 0, 1, 8848, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133601, 1, "何よ？\n用もないのに声掛けた\n…とか言ったら\n容赦しないわよ？", 3, 0, 1, 8849, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133601, 1, "少し部屋が寂しいわね\nそうだ、アンタを\n石にして家具に…\n冗談よ、ふふんっ♪", 4, 0, 1, 8850, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133601, 1, "ねぇ、もう少し\n話していかない？\nアンタの話なら…\n特別に聞いてあげるわ", 101, 0, 4, 8851, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133601, 1, "い、いつも\nあ、あ、ありが…\nうぅ、何でもないー！", 102, 0, 8, 8852, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133601, 1, "やっと起きたわね？\n寝ぼすけなんだから！\n尻尾でひっぱたいて\n起こそうかと思ったわ", 1, 1, 1, 8853, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133601, 1, "人捜しは日の高い\nうちに限るわ！\nさぁ、メドゥシアナ！\n全速力で出発進行よ!", 2, 2, 1, 8854, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133601, 1, "アタシ…\n子どもじゃないもん…\n眠くなんか…\nないんだからぁ…", 3, 3, 1, 8855, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133601, 1, "ふわふわであったかい\nつい、とぐろを\n巻きたくなるわね…", 2, 0, 1, 8857, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133601, 1, "アンタ、こんな\nヤワな体じゃ駄目よ\nウロコくらい\n生やしたらどうなの？", 3, 0, 1, 8858, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133601, 1, "くんくん…\n…ふん\n悪くないじゃない\n…くんくんくんくん…", 4, 0, 1, 8859, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133601, 1, "あったかいの大好き！\nジオ・ゲヘナでも\nよく日向ぼっこ\nしたもんだわ…ふわぁ", 5, 0, 1, 8860, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133601, 1, "お姉ちゃんを捜すなら\n…もぐ\n体力つけないとね\n…もぐもぐ", 7, 0, 1, 8862, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133601, 1, "何よ？　たまには\n勉強くらいするわよ\nお姉ちゃんに\n笑われちゃうもの", 8, 0, 1, 8863, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133701, 1, "この装いと力…\n今の世界との\n整合性を上手いこと\n合わせておかないとだ", 1, 0, 1, 8865, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133701, 1, "この格好…開発してた\n当時のまんまだ\nサイズもそのまま♪\n体型維持も完璧っ！", 2, 0, 1, 8866, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133701, 1, "あとで装備の手入れも\nやっとかないと\n…剣なら研ぐだけで\n終わるんだけどなぁ", 3, 0, 1, 8867, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133701, 1, "自分の原点を\n振り返ってみるのも\nたまには必要だね", 4, 0, 1, 8868, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133701, 1, "完璧じゃなくても\n今できる全力を尽くす\nその事に意味がある…\nそうだよね、少年", 5, 0, 1, 8869, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133701, 1, "始まりのあの日、\nそして向かうべき道…\nいつも忘れずにいよう", 101, 0, 4, 8870, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133701, 1, "誰かを救うために\n許容される犠牲なんて\nあっちゃいけない…\n全部、助けてやる", 102, 0, 8, 8871, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133701, 1, "おっと少年、\n何か御用かな？\nキミに関わる事なら\n最優先で対応するよ", 1, 0, 1, 8872, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133701, 1, "困った事があれば\n頼ってもらって\n構わないからね！\nむしろもっと頼って！", 2, 0, 1, 8873, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133701, 1, "そうだ、時間あるなら\n武器のメンテを\n手伝ってくれないかな\n数が多くてさ～", 3, 0, 1, 8874, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133701, 1, "遠距離戦の心得、\n少年にもレクチャー\nしてあげよっか？", 4, 0, 1, 8875, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133701, 1, "無茶しないで、って\n言ってもするだろうし\nせめてアタシの手が\n届く範囲で頼むよ", 101, 0, 4, 8876, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133701, 1, "虚実入り混じる\nこの世界でキミが…\n進むべきルートを\n照らしてくれたんだ", 102, 0, 8, 8877, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133701, 1, "グッモーニン！\n朝だよ少年、今日も\n張り切っていこう！", 1, 1, 1, 8878, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133701, 1, "クレープの代わりに\nガレットを焼いたんだ\nこの味で少年を\n唸らせてみせよう！", 2, 2, 1, 8879, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133701, 1, "もうこんな時間だ\n遅くならないうちに\n作業を切り上げようか\n少年も早く寝なよ？", 3, 3, 1, 8880, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133701, 1, "あえて緊張を解くのも\n戦いには必要だし…\nそうそう…大事大事…", 2, 0, 1, 8882, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133701, 1, "愛着を抱かせる事を\n意図したデザインだね\n心理的効果の観点から\n分析してみようか…", 3, 0, 1, 8883, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133701, 1, "香りは記憶と密接に\n結びつく事がある…\n懐かしい匂いだね…", 4, 0, 1, 8884, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133701, 1, "癒されるな…\n何がこの感情変化を\n引き起こすんだろう？", 5, 0, 1, 8885, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133701, 1, "食の偉大さを感じるな\n戦闘糧食ってやっぱり\n味気ないしなぁ…", 7, 0, 1, 8887, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133701, 1, "実用ばかりじゃなく\n詩や物語ってのも\n人には必要だよね", 8, 0, 1, 8888, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133901, 1, "この場所の開発版も\n探せばあるかもな\nさて、どんなデザイン\nだったのやら…", 1, 0, 1, 8890, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133901, 1, "今まで数々の波乱に\n身を投じたが…やはり\n波乱にはこの姿が一番\n似合いだよ☆", 2, 0, 1, 8891, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133901, 1, "むっ…この角に羽と\n尻尾は家具とぶつかり\nやすいな。兎の時は\n気にならなかったが…", 3, 0, 1, 8892, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133901, 1, "いつもより闘争を\n求める気分になるな\n初心に帰るとは\nこのことか…？", 4, 0, 1, 8893, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133901, 1, "未調整上等☆\nそれを上手く使い\nこなしてこその\nワタシだろう？", 5, 0, 1, 8894, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133901, 1, "坊やとできそうな\n愉しいことは…\nふむ、多すぎて迷うな", 101, 0, 4, 8895, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133901, 1, "時折、坊やの居心地の\nよさに絆されそうに\nなってしまうな\nワタシらしくもない…", 102, 0, 8, 8896, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133901, 1, "どうだ、おろした\n髪型も艶やかだろう？\nそんじょそこらの\n賞賛では満足せんぞ☆", 1, 0, 1, 8897, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133901, 1, "外に出るぞ！\n共に退屈を殺しに\n行こうじゃないか☆", 2, 0, 1, 8898, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133901, 1, "角と翼も触りたい？\nワタシの虚数魔境に\n挑むというなら\n止めはせんよ☆", 3, 0, 1, 8899, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133901, 1, "なんだ、困りごとか？\nクリスティーナさんが\n何でも聞いてやろう☆\n解決するかは別だがな", 4, 0, 1, 8900, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133901, 1, "力が欲しいか？\nならば呼ぶがいい！\nワタシの名を☆", 101, 0, 4, 8901, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133901, 1, "悪魔との契約は絶対\nなのだそうだが…\nどうだ、坊や\n一つ契ってみるか？", 102, 0, 8, 8902, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133901, 1, "まだ眠い？　甘えるな\n寝首を掻かれたく\nなければ飛び起きて\nこい…☆", 1, 1, 1, 8903, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133901, 1, "昼食の準備を\nしてやろう。\n毒見はいらんぞ？\n安心するといい！", 2, 2, 1, 8904, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133901, 1, "坊やは寝る時間だ\n寝ないのならば…\n大人の時間に\n付き合ってもらうか☆", 3, 3, 1, 8905, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133901, 1, "角と翼、尻尾の\nコンディションまで\n完璧だな☆", 1, 0, 1, 8906, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133901, 1, "なかなかのフワモコ\n具合だが…ううむ…\n翼と尻尾が邪魔だな", 2, 0, 1, 8907, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133901, 1, "うーむ、角と尻尾を\n追加して…さらに槍を\n持たせた方が見栄えが\nよさそうだ", 3, 0, 1, 8908, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133901, 1, "いい香りだな\n血と砂塵の臭いで\n上書きするまでの\n間に合わせに最適だ☆", 4, 0, 1, 8909, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133901, 1, "生温いな\nもっと焦がれるほどの\n熱量が欲しいところだ", 5, 0, 1, 8910, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133901, 1, "普段はないものが\n生えているのだから、\n運動でしっかり感覚を\n掴んでおかなければな", 6, 0, 1, 8911, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133901, 1, "腹が減っては戦は…\nと言うからな\nしっかり食べて\nおくとしようか☆", 7, 0, 1, 8912, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133901, 1, "『家庭を築くための\n絶対条件』…？\nふむ…興味がある\nタイトルだ", 8, 0, 1, 8913, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133801, 1, "奇妙な感覚です\nまるであの頃の自分に\n変装しているかのよう", 1, 0, 1, 8915, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133801, 1, "グレゴール？\nおっと、そうでした\n今は昔の姿になって\n離れていましたね", 2, 0, 1, 8916, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133801, 1, "このアバターでいると\n妙に子どもたちからの\n視線を感じますね…", 3, 0, 1, 8917, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133801, 1, "リソースの割り振りが\n変わっただけなのに\n随分と身体が軽く\n感じますね", 4, 0, 1, 8918, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133801, 1, "普段グレゴールに\n任せている分身の管理\n自分でやるには\nいささか煩雑ですね…", 5, 0, 1, 8919, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133801, 1, "長老に頼まれて描いた\n妖精の絵…いったい\n何に使ったのでしょう", 101, 0, 4, 8920, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133801, 1, "私自身をモデルにした\n完全なるNPCたち\nそれが私の分身です", 102, 0, 8, 8921, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133801, 1, "このアバターが\n気になるのですか？\n貴方はつくづく\n妖精に縁がありますね", 1, 0, 1, 8922, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133801, 1, "ちょうどよかった\nグレゴールを見かけ\nませんでしたか？", 2, 0, 1, 8923, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133801, 1, "あの翅は飾りでは\nありません。ただ…\nグレゴールに乗って\n飛ぶ方が楽なだけです", 3, 0, 1, 8924, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133801, 1, "妖精とは本来神話に\nおけるトリックスター\n運命をかき回すことが\n存在意義なのです", 4, 0, 1, 8925, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133801, 1, "変貌大妃の権能との\n相性を考えなければ\nこの姿こそが私たちの\n最強の姿です", 101, 0, 4, 8926, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133801, 1, "変化とは一種の喪失\nだからこそ得られる物\nがある…貴方のそれも\n大切にするのですよ", 102, 0, 8, 8927, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133801, 1, "マサキからの定時連絡\nが来ませんね…心配は\n不要ですが、様子を\n見に行きましょうか", 1, 1, 1, 8928, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133801, 1, "昼食の時間ですね\nグレゴールのために\nカロリーの高い物を\n食べに行きましょう", 2, 2, 1, 8929, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133801, 1, "夜更かしはダメですよ\n悪いスパイが暗躍して\nいる時間帯ですからね", 3, 3, 1, 8930, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133801, 1, "ふむ、座り心地は中々\nですが…私にはやはり\n玉座の方があいますね", 2, 0, 1, 8932, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133801, 1, "ふふ、いいでしょう\n我が配下に加えます\n存分に働きなさい", 3, 0, 1, 8933, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133801, 1, "時折晶からしていた\nあの甘い匂いは何の\nアロマだったのか…", 4, 0, 1, 8934, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133801, 1, "燃え盛る炎もこうして\n暖炉の中なら安全です\nクリスもあの衝動を\n管理さえできれば…", 5, 0, 1, 8935, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133801, 1, "そういえば真那が\n獄中で手持無沙汰に\nひたすらおにぎりを\n食べていましたね…", 7, 0, 1, 8937, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133801, 1, "これは、妖精学の本…\n長老の蔵書から借りて\n読みふけったものです", 8, 0, 1, 8938, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134301, 1, "どんな詩を\n作ろうかしら…？\n…大体、暗く\nなっちゃうけど…", 1, 0, 1, 8940, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134301, 1, "唱喚術の練習…\nここでやったら\n迷惑よね…", 2, 0, 1, 8941, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134301, 1, "チカちゃんとの\nデュエット…\n足をひっぱってない…\nかしら…？", 3, 0, 1, 8942, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134301, 1, "唱喚士の修行…\n滝に打たれるとか…\n崖を登るとか…\nなくて、よかった…", 4, 0, 1, 8943, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134301, 1, "精霊さん、肉じゃが\n食べる、かしら…？\n魔力だけだと…\n飽きちゃいそうだし…", 5, 0, 1, 8944, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134301, 1, "精霊さんのお世話に\n慣れたぶん…\nあのひとのお世話も\n上手にできる…はず？", 101, 0, 4, 8945, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134301, 1, "これからも、歌うわ…\nこの声が、彼の力に\nなる、限り──", 102, 0, 8, 8946, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134301, 1, "この服…踊り子さんに\n間違われちゃって…\nあ…お、お触りしちゃ\nだめよ…？", 1, 0, 1, 8947, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134301, 1, "あなたはどんな\n精霊が…好き…？\n唱喚…してみたいわ…", 2, 0, 1, 8948, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134301, 1, "あんまり白い服って…\n着ないの…その…\nこ、ころんだりして…\n汚すから…", 3, 0, 1, 8949, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134301, 1, "じ、実はね…\nこの体の模様…筆で\n書いてもらうたび…\nくすぐったい…の…", 4, 0, 1, 8950, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134301, 1, "精霊さん…あなたと\n遊びたいみたい…\nや、やっぱり…\nパパ…だから…？", 101, 0, 4, 8951, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134301, 1, "あなたを歌にしたら…\n…ふふっ、だめね\n一曲じゃ…あなたを\n表現できない、わ…", 102, 0, 8, 8952, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134301, 1, "朝の修行は…\nうとうと…し…ちゃ…\n……（かくん）\n……あぅっ？", 1, 1, 1, 8953, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134301, 1, "せ、精霊さんと…\nかくれんぼ中なの…\nうぅ…見つからない…\n日が暮れちゃいそう…", 2, 2, 1, 8954, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134301, 1, "精霊さん、寝たから…\nふふっ…ここからは\nパパとふたりの時間…\n…ね？", 3, 3, 1, 8955, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134301, 1, "ふぅ…修行と育児は…\nちょっとだけ…\n休憩…ふぁ…", 2, 0, 1, 8957, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134301, 1, "あなたとも契約…\n…できる、かしら？\nふふっ…", 3, 0, 1, 8958, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134301, 1, "いい香りに包まれたら\n精神統一も\nはかどりそう…♪", 4, 0, 1, 8959, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134301, 1, "あたたかい場所…\nここで作詞したら…\n穏やかな詩が\n書ける、かも…", 5, 0, 1, 8960, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134301, 1, "精霊さんのお世話を\nしていると…\nお腹、空いちゃって…", 7, 0, 1, 8962, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134301, 1, "ふふっ…♪\n色んな楽譜…\n集めるの、楽しい…♪", 8, 0, 1, 8963, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 122301, 1, "けんぞくまだかな？\nはやく会いたいなー\nヴァンピィちゃん\nずっと待ってるからね", 103, 0, 12, 8965, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 122301, 1, "けんぞくのこと\nずっとずっと守るよ！\nだからいつまでも\n一緒にいてね", 103, 0, 12, 8966, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134401, 1, "私の歌や想いを\n誰かに託せる…\nこれほどうれしい\nことはありません", 1, 0, 1, 8967, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134401, 1, "コッコロさんの\n包容力はすごいです\nあんなふうに私も\n人を包み込めたら…", 2, 0, 1, 8968, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134401, 1, "最近のボイトレは\nランファさんと一緒に\nやるんです。お互いに\n切磋琢磨しますよ！", 3, 0, 1, 8969, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134401, 1, "先輩として相応しい\n立ち振る舞いを\n心がけたいですね\nしっかりしなきゃ…！", 4, 0, 1, 8970, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134401, 1, "ランファさんが\n踊ったら映えるかも…\nアイドルにも興味を\n持ってもらえないかな", 5, 0, 1, 8971, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134401, 1, "支え導くために\n寄り添って考える…\nあの人からたくさん\n教わったことでしたね", 101, 0, 4, 8972, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134401, 1, "いつだってあなたに\n恥じることのない\n姿をお見せしたいです", 102, 0, 8, 8973, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134401, 1, "あなたの手は温かくて\n触れていると\n落ち着きますね…♪", 1, 0, 1, 8974, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134401, 1, "里のみんなから修行の\n差し入れを頂きました\nよろしければ、一緒に\n食べましょう♪", 2, 0, 1, 8975, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134401, 1, "ノゾミにもお土産を\n用意したいですね\n里のお土産選び、\n付き合ってください♪", 3, 0, 1, 8976, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134401, 1, "精霊たちも、あなたが\n大好きみたいです♪\n優しさが伝わって\nいるんでしょうね", 4, 0, 1, 8977, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134401, 1, "特訓が終わったら、\n二人でどこかに\n出かけましょうか♪", 101, 0, 4, 8978, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134401, 1, "ひゃ…す、すみません\n肌の模様を\nなぞられると、\nくすぐったくて…", 102, 0, 8, 8979, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134401, 1, "おはようございます\n精霊たちと一緒に、\n今日も頑張ります\n応援してくださいね♪", 1, 1, 1, 8980, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134401, 1, "お昼の時間です\n今日も精いっぱい\n修行を頑張りましょう", 2, 2, 1, 8981, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134401, 1, "もうすっかり夜ですね\n眠るまで子守唄を\n歌ってあげますよ？\nふふ♪", 3, 3, 1, 8982, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134401, 1, "無理せず休憩するのも\n大切ですよね\nふかふか～…ふふっ♪", 2, 0, 1, 8983, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134401, 1, "今日はこの子に\n歌の特訓を聞いて\nもらいましょう♪", 3, 0, 1, 8984, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134401, 1, "素敵な香り…\nふふ、精霊たちも\n気に入ったみたいです", 4, 0, 1, 8985, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134401, 1, "あったかい…\nこの衣装、やっぱり\n少し冷えやすいものね", 5, 0, 1, 8986, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134401, 1, "特訓の後のご飯は\n美味しいですね♪\nこれでまた頑張れます", 7, 0, 1, 8987, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134401, 1, "唱喚士についての\n書籍はないみたい\nいつか私がここに\n楽譜を入れたいな", 8, 0, 1, 8988, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134601, 1, "ギルドハウスの平和も\n任せてください\n何が来たって\n守ってみせますよ！", 1, 0, 1, 8992, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134601, 1, "このギルドハウスも\nいつの間にか出撃台に\n改造されてたり\nするんでしょうか…？", 2, 0, 1, 8993, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134601, 1, "戦いを経験してこそ\n実感できますね\n平和はいいもの、\n友達は素晴らしいもの", 3, 0, 1, 8994, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134601, 1, "次はランドソルで\n巨大ロボットが\nブームになったり？\n…さすがにないか", 4, 0, 1, 8995, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134601, 1, "改めて見るとこの服…\nか、体の線とか\n結構目立ってるかも…", 5, 0, 1, 8996, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134601, 1, "団長さんって\n知らない人とも\n話せる系ですよね\nコツはいったい…", 101, 0, 4, 8997, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134601, 1, "ＢＢ団、世界を救う！\nまだ信じられないけど\n本当にあった出来事\nなんですよね…！", 102, 0, 8, 8998, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134601, 1, "パイロットも\n視力が大事なんです\n弓使いの経験が\n生きました！", 1, 0, 1, 8999, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134601, 1, "ギガンティック\nマイフレンドくん、\n出撃！\nあ、ただの妄想です", 2, 0, 1, 9000, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134601, 1, "コクピットから\n降りたあとに残る\nふわふわ感…ちょっと\n癖になりませんか？", 3, 0, 1, 9001, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134601, 1, "ひえっ、敵襲！？\nあっすみません、\nちょっと\n寝ぼけてたかも…", 4, 0, 1, 9002, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134601, 1, "共に戦場を駆け抜け\nＢＢ団の絆もより強く\nこれからも結束を\n強めましょうね！", 101, 0, 4, 9003, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134601, 1, "同じコクピットに\n乗ったのに比べれば、\nスキンシップくらい…\nや、やっぱ緊張します", 102, 0, 8, 9004, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134601, 1, "おはようございます！\n今日もがんばりますっ\n…クローチェさんを\n見習ってみました…", 1, 1, 1, 9005, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134601, 1, "お昼ご飯の時間ですね\nがんばって他の人\n誘ってみようかな…", 2, 2, 1, 9006, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134601, 1, "夜の見張りは\n任せてください\n一人きりの夜も\n慣れてますから！", 3, 3, 1, 9007, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134601, 1, "休めるときに\n休んでおかなくちゃ\nいつ何が起こるか\n分かりませんからね", 2, 0, 1, 9009, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134601, 1, "師匠もこういうの\n好きだったのかな…\nなんだかしんみり\nしちゃいます", 3, 0, 1, 9010, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134601, 1, "香りでリラックス…\n余裕のない時でも\nこういう安らぎは\n大切ですよね", 4, 0, 1, 9011, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134601, 1, "あったかい…\nこれはきっと友情と\n同じぬくもり…", 5, 0, 1, 9012, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134601, 1, "新鮮なご飯、感動…！\n師匠たちの世界だと\nご飯は保存食ばかり\nでしたからね…", 7, 0, 1, 9014, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134601, 1, "操縦マニュアル、\nしっかり読んで\n性能を引き出さなきゃ", 8, 0, 1, 9015, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134501, 1, "屋内で飛べなくても\nここなら平気ですっ\n助けてくれる人が\nたくさんいますからっ", 1, 0, 1, 9017, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134501, 1, "新ボディになってから\n人や物が前より身近に\n感じられて、わたし、\n幸せですっ♪", 2, 0, 1, 9018, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134501, 1, "配達以外にも何か\nお仕事したいですっ！\nハグで遊覧飛行とか、\n喜んでもらえますか？", 3, 0, 1, 9019, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134501, 1, "むむむ～…\n文字を書くのって\n難しいですね～っ\n練習しなくちゃっ！", 4, 0, 1, 9020, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134501, 1, "簡易点検、よしっ！\nポポおじいさんに\n点検の仕方を教わって\n今日も絶好調ですっ", 5, 0, 1, 9021, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134501, 1, "明日は北の空、\n明後日は南の空っ♪\nえへへっ、見せたい\n景色がいっぱいですっ", 101, 0, 4, 9022, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134501, 1, "ついナイトさんを\n探しちゃいます…\nわたし、寂しんぼさん\nだったんでしょうかっ", 102, 0, 8, 9023, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134501, 1, "どんなところでも\nひとっ飛びっ♪\nわたしに任せて\nくださいねっ！", 1, 0, 1, 9024, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134501, 1, "まだ知らない機能が\nエアリアルには\n隠されてるかもっ…\nワクワクしますねっ！", 2, 0, 1, 9025, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134501, 1, "ひゃんっ！　…あはは\nエアリアルになっても\nケーブルに触られると\nくすぐったいですねっ", 3, 0, 1, 9026, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134501, 1, "ヴォルガーノさんに\nそっくりの子猫さんに\n最近触れたんですっ！\nかわいかったな～っ♪", 4, 0, 1, 9027, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134501, 1, "ハグしてくれるん\nですかっ？\nえへへっ、ぎゅーっ♪", 101, 0, 4, 9028, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134501, 1, "ナイトさんと一緒に\nいるだけで、胸が\nぽかぽか～って\nあったかいですっ♪", 102, 0, 8, 9029, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134501, 1, "おはようございますっ\n目覚まし飛行は\nいかがでしょうかっ？", 1, 1, 1, 9030, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134501, 1, "お昼ですよっ！\nいっぱい補給して\nまたフルパワーで\nがんばりましょうっ♪", 2, 2, 1, 9031, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134501, 1, "眠れない夜はわたしに\n任せてくださいっ！\n充填さえあればずっと\n一緒にいられますよっ", 3, 3, 1, 9032, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134501, 1, "歩きづらいので\nこういう休憩ポイント\nって助かります～っ", 2, 0, 1, 9034, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134501, 1, "スラスターを付けたら\nこの子たちも空を\n自由に飛べたり\nするんでしょうかっ？", 3, 0, 1, 9035, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134501, 1, "あっ、これに似た\n香りを近くのお店で\n検知したことが\nありますっ♪", 4, 0, 1, 9036, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134501, 1, "高熱源反応を検知っ\nわたしのスラスターも\n熱量の高さなら\nかなりものですよっ♪", 5, 0, 1, 9037, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134501, 1, "もぐもぐもぐ…っ\n食べたぶんはあとで\n真空パックにして\nお返ししますねっ", 7, 0, 1, 9039, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134501, 1, "本を読むのって\n楽しいですねっ♪\n前はびりびりーって\nしちゃってたのでっ", 8, 0, 1, 9040, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134701, 1, "なかなかの住処ですね\nこの私の別邸にして\nあげてもいいですよ♪", 1, 0, 1, 9042, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134701, 1, "はぁ…いつになったら\nすべての神力が\n戻るのでしょう？", 2, 0, 1, 9043, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134701, 1, "ここも悪くないですが\n昔私が作った庭園は、\nもっと良い場所でした\n焼けちゃいましたが…", 3, 0, 1, 9044, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134701, 1, "うう…\nおなかが空きました…\nお米が食べたいです…", 4, 0, 1, 9045, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134701, 1, "ここは平和ですねえ…\nジオ・ゲヘナも\nこのくらい穏やかなら\n住みやすいのですが", 5, 0, 1, 9046, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134701, 1, "騎士さんのおかげで\n神力が戻ってきました\n良い働きですよ♪", 101, 0, 4, 9047, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134701, 1, "騎士さんのいる場所が\n今の私がいるべき場所\nなのかもしれませんね", 102, 0, 8, 9048, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134701, 1, "頭がたかーい！\n…ですよっ！", 1, 0, 1, 9049, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134701, 1, "ほーちゃんと呼ぶ権利\n安売りはしませんよ？\n特別ですから！", 2, 0, 1, 9050, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134701, 1, "私とおしゃべりを？\nまあ、いいでしょう♪\n何を話しましょうか？\nこの羽の秘密とか？", 3, 0, 1, 9051, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134701, 1, "小鳥扱いは\nやめなさーい！\nこれでも神様ですよ？", 4, 0, 1, 9052, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134701, 1, "き、気安く触るのは…\n…い、いえ、\n騎士さんなら…まぁ…", 101, 0, 4, 9053, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134701, 1, "絢爛豪華な絆！\nこの私と結べるなんて\n光栄でしょう？", 102, 0, 8, 9054, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134701, 1, "ふわぁぁ～\nおはようごじゃいます\n…んむむ", 1, 1, 1, 9055, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134701, 1, "お昼ご飯は何を…\nえっ？　私？\nそそんな゛あ゛！", 2, 2, 1, 9056, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134701, 1, "あの…騎士さん？\n添い寝する権利とか…\nほしくないですか？", 3, 3, 1, 9057, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134701, 1, "はぁ～ふかふか…\n…へ？\n鳥の巣にいるみたい？", 2, 0, 1, 9059, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134701, 1, "ふむ…\nそばに置いてあげても\nいいでしょう", 3, 0, 1, 9060, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134701, 1, "ほわぁ～\nいいかおり～", 4, 0, 1, 9061, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134701, 1, "あたたかい…\nなんだか元気が\nでてきますねっ", 5, 0, 1, 9062, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134701, 1, "たくさん食べて力を\n取り戻さなくてはっ\n…んがぐぐっ", 7, 0, 1, 9064, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134701, 1, "ふむふむ…\n人の子にしては\nよく書けていますね♪", 8, 0, 1, 9065, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133101, 1, "素敵なお家ですね\n温かみがあって\nどこか騎士様に\n似ております。ふふ", 1, 0, 1, 9067, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133101, 1, "賑やかな街ですね\nこれほど活気豊かだと\nつい外に出てみたく\nなってしまいます", 2, 0, 1, 9068, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133101, 1, "別世界の存在は知って\nおりましたが、いざ\n目の当たりにすると\n驚きの連続ですわ", 3, 0, 1, 9069, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133101, 1, "この街は様々な種族が\nなにはばかることなく\n混在しているのですね\n…我々も、いつかは…", 4, 0, 1, 9070, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133101, 1, "ランドソルにも香水の\nお店があるのですね\nどんな香りが揃って\nいるのでしょうか…♪", 5, 0, 1, 9071, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133101, 1, "昨日、素敵な花園を\n見つけたのです\n今日のお茶会は\nそちらでしましょうか", 101, 0, 4, 9072, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 133101, 1, "気を抜いてはいけない\nとわかっていても、\nここだとつい安心して\nしまいます…ふう…", 102, 0, 8, 9073, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133101, 1, "ごきげんよう\n街で見つけた茶葉を\n試しているんです\n騎士様も一杯どうぞ♪", 1, 0, 1, 9074, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133101, 1, "親切な方ばかりですね\n素敵なお友だちが\nできそうな予感で、\nワクワクしてます…♪", 2, 0, 1, 9075, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133101, 1, "黄泉の食卓の皆様用に\nお土産を用意しないと\n騎士様のお知恵も\n頼りにしておりますわ", 3, 0, 1, 9076, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133101, 1, "ジオ・ニヴルヘルと\n比べると、この世界は\n本当に明るいですね\n少し眩しいくらいです", 4, 0, 1, 9077, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133101, 1, "あまり、血の巡りが\n良くなくて…\n騎士様の手は\nとても温かいですね", 101, 0, 4, 9078, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133101, 1, "今度はわたくしに\nジオ・ニヴルヘルを\n案内させてくださいね\n観光案内のお返しです", 102, 0, 8, 9079, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133101, 1, "おはようございます\n朝の眩しい太陽には\nなかなか慣れません…", 1, 1, 1, 9080, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133101, 1, "騎士様、お茶は\nいかがでしょうか？\n一度一休みして、\n英気を養いましょう", 2, 2, 1, 9081, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 133101, 1, "眠れないのでしょうか\nまぶたが重くなるまで\nわたくしをお話し\n相手になさって下さい", 3, 3, 1, 9082, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133101, 1, "思わず気が抜けそうに\nなってしまいます…\nふふ、ふわふわ…♪", 2, 0, 1, 9084, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133101, 1, "まあ、かわいらしい\nグレイスもきっと\n気に入りそうな\nデザインですわ", 3, 0, 1, 9085, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133101, 1, "まあ、いい香り…\n心安らぐようですわ\n素敵なセンスを\nお持ちなのですね♪", 4, 0, 1, 9086, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133101, 1, "燃え移りや乾燥がある\nから近づかないほうが\nいいとわかりつつも…\nああ、温かい…♪", 5, 0, 1, 9087, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133101, 1, "食が細いというよりは\n消化があまりうまく\nできなくて…本当は\nまだ頂きたいのですが", 7, 0, 1, 9089, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 133101, 1, "『薔薇の下の契り』？\nまあ、男性同士で…！\nそんな…こ、これ以上\nここで読むわけには！", 8, 0, 1, 9090, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132301, 1, "ダーリンが帰ってくる\n前に部屋の掃除しよ♪\n…ふふ、これってもう\n新婚生活だよね…♪", 1, 0, 1, 9092, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132301, 1, "家具のセンスまで\n私と同じだなんて\n運命って怖いわ…♪", 2, 0, 1, 9093, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132301, 1, "あの人はベッドの下に\n何か隠してるのかな…\nな、なんて全っ然\n気にならないし…っ！", 3, 0, 1, 9094, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132301, 1, "うっかりして\n角引っかけないように\nしないとね…", 4, 0, 1, 9095, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132301, 1, "錬金術を使わなくても\nあなたの心を鷲掴む\n私は恋も錬金術も\n失敗しないんだから！", 5, 0, 1, 9096, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132301, 1, "あの人って破廉恥なの\n興味あるのかしら…？\n基本破廉恥禁止だけど\nあの人だけは…許す…", 101, 0, 4, 9097, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132301, 1, "手狭になれば錬金術で\n新しい部屋作るから\nいつ家族が増えても\n安心だよね…♪", 102, 0, 8, 9098, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132301, 1, "錬金術でカレーライス\n作っちゃおっかな♪\n具材を釜でグツグツ…\nま、料理とも言うわね", 1, 0, 1, 9099, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132301, 1, "錬金術に興味がある？\nじゃあさっそく私が\n手取り足取り教えるわ\nって…やだ破廉恥っ！", 2, 0, 1, 9100, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132301, 1, "イロハちゃんとの\n虫捕りは命懸けよ！\n一万匹捕れるまで\n帰れないんだから…", 3, 0, 1, 9101, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132301, 1, "ソノさんって\n体は小さいけど\nとっても頼れる私の\nお姉さん的存在なの♪", 4, 0, 1, 9102, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132301, 1, "はあ…部屋中に広がる\nダーリンの香り…♪\n…ってノゾミみたいに\nなっちゃってる！？", 101, 0, 4, 9103, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132301, 1, "鹿の獣人族の特徴は\nこのエッチな角…\n違っ！　叡智よ叡智！\n噛んだだけだからっ！", 102, 0, 8, 9104, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132301, 1, "あ、おはよう！\n寝る前に飲んだ\n目覚めすっきり秘薬の\n効果はバッチリかな？", 1, 1, 1, 9105, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132301, 1, "午後からも頑張る\nあなたのために\n愛妻弁当作ったの♪\n一緒に食べよ？", 2, 2, 1, 9106, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132301, 1, "今日も一日お疲れさま\n眠れないなら隣で…\nじゃなかった早寝して\n明日も頑張ろうね！", 3, 3, 1, 9107, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132301, 1, "いい錬成をするには\n適度な休憩も必要よね\n…気持ち良くてたまに\n寝落ちしちゃうけど…", 2, 0, 1, 9109, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132301, 1, "かわいいぬいぐるみ\n錬成じゃなくて\n手編みで作ったら\n驚いてくれるかも♪", 3, 0, 1, 9110, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132301, 1, "くん、くん…\nん～いい匂い♪\nノゾミの香水と\nいい勝負だわ", 4, 0, 1, 9111, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132301, 1, "ああ、暖かい\nでもソノさんの作った\nミートパイの方が\nもっと温かいわ…♪", 5, 0, 1, 9112, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132301, 1, "もぐもぐ…私も\n食べても減らない秘薬\n錬成して他の食べ物も\n作れないかな？", 7, 0, 1, 9114, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132301, 1, "ふむふむ…\nこのレシピ面白いわね\n今度錬成してみよっと", 8, 0, 1, 9115, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132501, 1, "おー、体が軽い\n腰痛も胃もたれも\nないし、最高かよ", 1, 0, 1, 9117, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132501, 1, "いっつも力入れてたら\nいざって時困るし\nやっぱ省エネだな", 2, 0, 1, 9118, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132501, 1, "やば…なんか急に\nブドウ朱飲みたい…\nルイちゃんに頼むか？\nいや、でもなあ…", 3, 0, 1, 9119, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132501, 1, "あれ、あの材料の在庫\nまだあったっけ…\nルイちゃんに確認しよ", 4, 0, 1, 9120, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132501, 1, "イロハのやつ変な\nイタズラしてない\nといいけど…", 5, 0, 1, 9121, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132501, 1, "今日のシフトは～…\nお、彼と一緒じゃん\nふふっ…", 101, 0, 4, 9122, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132501, 1, "イロハのやつ\n不用意に私の体で\n彼に抱きつくなって\nこっちが恥ずかしい…", 102, 0, 8, 9123, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132501, 1, "今日も元気だねえ\n若いってうらやま…\nっていや、今は私の\n方が若いじゃん", 1, 0, 1, 9124, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132501, 1, "ん～？　どしたん\nヒマならうちの店で\nバイトしてく？", 2, 0, 1, 9125, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132501, 1, "イロハ見なかった？\n今日は手伝うって約束\nしてたのにさあ…", 3, 0, 1, 9126, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132501, 1, "ルイちゃん特製\nエナドリ（子ども用）\nこれ飲んで今日も\nお仕事頑張るかー", 4, 0, 1, 9127, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132501, 1, "元の体なら\n君と目線、合うのにね\n…ってドラマかよ\nごめん、今の忘れて", 101, 0, 4, 9128, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132501, 1, "なんかヒマそうじゃん\n用事ないなら\nお姉さんがコーヒー\n淹れたげよっか", 102, 0, 8, 9129, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132501, 1, "ふわあ…おはよ\n今日もお仕事\nがんばりますかー", 1, 1, 1, 9130, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132501, 1, "ちょうどいいや\n一緒にお昼どうよ\nお姉さんが\n奢ってあげよう", 2, 2, 1, 9131, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132501, 1, "ふぁぁ…ふぅ\nここからは大人の時間\nなんだけどさぁ\n眠くてかなわん…", 3, 3, 1, 9132, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132501, 1, "おお～なんか前より\nでっかく感じるわ～\nてそりゃそうか", 2, 0, 1, 9134, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132501, 1, "そいえば、イロハって\nあんまこういうの\n欲しがらなかったな～\nクワガタばっかで", 3, 0, 1, 9135, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132501, 1, "はあ…いい匂いだね\nさすがルイちゃん特製\n錬金アロマ\n次の目玉商品だな", 4, 0, 1, 9136, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132501, 1, "ユカリっち焚火の前で\nキメる麦しゅわに\nハマってるらしいけど\n興味あるわぁ…", 5, 0, 1, 9137, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132501, 1, "うわこれ…\n無限にいけちゃうわ\n子どもの胃袋\n恐るべし…", 7, 0, 1, 9139, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132501, 1, "ふむ、こんな需要が…\n次の新商品として\nルイちゃんに試作\n頼んでみるか～", 8, 0, 1, 9140, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134801, 1, "服はその人の個性です\n仕立て屋は\nその手伝いをする\nプロなんです♪", 1, 0, 1, 9142, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134801, 1, "糸を紡いで紡いで\n巻き巻き～♪", 2, 0, 1, 9143, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134801, 1, "ふむ…あの人の衣装\nなかなか斬新ですね\n後でどうなってるのか\n見せてもらおう♪", 3, 0, 1, 9144, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134801, 1, "動きやすさと\nファッション性\n両立させてみせますよ", 4, 0, 1, 9145, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134801, 1, "今度のコレクション\n新作にワイルドな\nルームウェアは\nどうかな…？", 5, 0, 1, 9146, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134801, 1, "騎士さんもたまには\n衣装の感想とか言って\nくれてもいいのにな～", 101, 0, 4, 9147, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134801, 1, "ノゾミさんとチカさん\nにも、おそろいの服を\nプレゼントしてみよう\n二人とも似合いそう♪", 102, 0, 8, 9148, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134801, 1, "ジオ・ゲヘナでまた\n素材収集したいんです\n手伝ってくれますか？", 1, 0, 1, 9149, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134801, 1, "えっ？　薄着すぎ？\nおしゃれのためなら\nこのくらいへっちゃら\nですよ…っくしゅん！", 2, 0, 1, 9150, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134801, 1, "穴が開いてる？\nこれはそーいう\nデザインですー！", 3, 0, 1, 9151, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134801, 1, "ちょっとどこ\n触ってるんですかー！\n…あ、糸くず\n取ってくれたんですね", 4, 0, 1, 9152, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134801, 1, "騎士さんに\n新しい生地の服を…\nいや、シャレじゃ\nないですよ？", 101, 0, 4, 9153, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134801, 1, "素敵な着こなしには\n健康な体も大切！\n騎士さんに見られても\n恥ずかしくないように", 102, 0, 8, 9154, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134801, 1, "おはようございます！\nさあ、着替えて！\n私が見繕ってあげます", 1, 1, 1, 9155, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134801, 1, "お昼は激辛料理です\nジオ・ゲヘナで\nハマッちゃって…\n一緒にどうですか？", 2, 2, 1, 9156, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134801, 1, "睡眠不足は美容の大敵\nていうかシンプルに…\nね、眠い…", 3, 3, 1, 9157, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134801, 1, "くつろげますね～\nでももうちょっと\n生地の手触りが…\nって、職業病？", 2, 0, 1, 9159, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134801, 1, "かわいいものって\n心を癒してくれますね\n服もそうありたいです", 3, 0, 1, 9160, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134801, 1, "いい香り～♪\nこの匂いにはどんな服\nが合うかなあ…？", 4, 0, 1, 9161, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134801, 1, "この服露出が多いから\n温かいとほっとする～\nずっとこうして\nいたいかも…", 5, 0, 1, 9162, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134801, 1, "いくらでも\n食べられちゃうから、\n自分でストップ\nかけないと…", 7, 0, 1, 9164, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134801, 1, "しっかり勉強して\nジオ・ゲヘナに\nお洒落を伝えたいです", 8, 0, 1, 9165, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134901, 1, "あたくしは高貴なる\nマンティコア！\nサソリでもコウモリ\nでもありませんわよ♪", 1, 0, 1, 9167, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134901, 1, "なかなか居心地がいい\nお部屋ですわね～\n…さてどうやって\nあたくしのものに…", 2, 0, 1, 9168, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134901, 1, "高貴なるお審美眼で\nあの家具の価値を\n測ってやりますわ\n…ギリ合格ですわ！", 3, 0, 1, 9169, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134901, 1, "高貴なる笑い声は\n一日にしてならず\nお子様の頃から練習を\n重ねてきたのですわ！", 4, 0, 1, 9170, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134901, 1, "お美しいあたくし様と\n同じ空間にいられる…\nどうぞその喜びを\n噛みしめなさいな♪", 5, 0, 1, 9171, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134901, 1, "アナタがお土下座して\n頼むなら、あたくしも\nもう少しここに残って\n構いませんわよ♪", 101, 0, 4, 9172, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 134901, 1, "馬ボーンをあたくしに\n振り向かせるには\nどうしたら…って、\nこの言い方は語弊が！", 102, 0, 8, 9173, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134901, 1, "少し寒いですわね…\n…この服装のせい？\nこっちだって好きで\n着てねぇですわよ！", 1, 0, 1, 9174, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134901, 1, "あのラミアが来たら\nあたくし様はお用事で\nお留守って言って\n追い返すんですのよ！", 2, 0, 1, 9175, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134901, 1, "こっちの世界にも\n闘技場がありますの？\nそこもお頂点を極めて\nしまおうかしら～♪", 3, 0, 1, 9176, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134901, 1, "馬ボーンって\n人畜無害そうなお面の\nわりに、時々やること\nえげつねぇですわね…", 4, 0, 1, 9177, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134901, 1, "ねぇ馬ボーン…\nアナタあの魔札、\nまだ持ってまして？\nとっとと捨てなさい！", 101, 0, 4, 9178, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134901, 1, "絶対にあたくし様の\nお尻尾に触るんじゃ\nねぇですわよ…？\nいえ、振りではなく！", 102, 0, 8, 9179, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134901, 1, "ふわぁ…\n打倒ネフィのお作戦を\n夜通し考え過ぎて、\n寝不足ですわぁ…", 1, 1, 1, 9180, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134901, 1, "もうお昼ですの？\nあたくし様に相応しい\n優雅なおランチと\n参りますわよ～♪", 2, 2, 1, 9181, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 134901, 1, "あら馬ボーンったら\nまだ起きてますの？\n…ぐぬぬ、夜の間に\n仕掛ける作戦が…", 3, 3, 1, 9182, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134901, 1, "このおフカフカ感…♪\n剣闘士宿舎の\nゴワゴワなお寝床とは\n大違いですわね！", 2, 0, 1, 9184, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134901, 1, "なんですの、この\n弱っちそうなお顔は…\nおかわいそうですし、\nお僕にしてあげますわ", 3, 0, 1, 9185, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134901, 1, "馬ボーンはこういう\n匂いが好みですの？\nふーん…ほーう…\nなるほどですわ…♪", 4, 0, 1, 9186, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134901, 1, "あぁ～～…\nそうそう、これくらい\n暑い方が快適ですわ…", 5, 0, 1, 9187, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134901, 1, "もぐもぐ、もぐっ…\nまぁそこそこ？\n悪くないお味では\nありますわねっ", 7, 0, 1, 9189, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 134901, 1, "こんな本よりも、\n華麗なるカノン様の\n活躍録でも置いた方が\n世のためになりますわ", 8, 0, 1, 9190, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132401, 1, "さっきねー、外で猫と\n木登り競争したんだよ\nなかなか手ごわい\n相手だった！", 1, 0, 1, 9192, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132401, 1, "いっぱい食べて\n大きくなるんだ～\nあっでもこれ\nソノちゃんの体か", 2, 0, 1, 9193, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132401, 1, "強いおねーさんの\n気配を感じる…\nどこから？\n…そこら中から！", 3, 0, 1, 9194, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132401, 1, "虫捕り網の手入れは\nいつもバッチリ！\nいつ何が出るか\nわからないもんね", 4, 0, 1, 9195, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132401, 1, "うちの中で走り回ると\nソノちゃんに\n怒られちゃうけど…\nここならいける？", 5, 0, 1, 9196, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132401, 1, "おじちゃんも\nお部屋にいろいろ\n隠してるのかな？\nルイちゃんみたいに", 101, 0, 4, 9197, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 132401, 1, "世界一になったら\nおじちゃんにも\nいっぱい褒めて\nほしいな…約束っ♪", 102, 0, 8, 9198, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132401, 1, "高いとこのものが\n取りたいときは\nイロハに言ってね\nイロハ得意だから！", 1, 0, 1, 9199, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132401, 1, "どうも、イロハです\n剣士たるもの、\nあいさつは大事って\n教わったんだ♪", 2, 0, 1, 9200, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132401, 1, "イロハと勝負だね！\n受けて立つよ～！\n…あれ、違った？", 3, 0, 1, 9201, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132401, 1, "あっ！　ルイちゃんに\n素材集め頼まれてたの\n忘れてた…！　やばい\nおじちゃん手伝って！", 4, 0, 1, 9202, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132401, 1, "実はイロハって\n褒めたら伸びる\nタイプなんだ\nだからよろしくね♪", 101, 0, 4, 9203, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132401, 1, "このお部屋、すごく\n居心地がいいんだ～\nやっぱりおじちゃんが\nいるからかな？", 102, 0, 8, 9204, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132401, 1, "昨日仕掛けた罠で\n虫捕れてるかな？\nあとで一緒に\n見に行こうね♪", 1, 1, 1, 9205, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132401, 1, "もうお昼だよね？\nイロハのお腹が\n空いてきたから\n間違いないよ！", 2, 2, 1, 9206, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 132401, 1, "ふわぁ～…\nあ、あくびじゃないよ\nイロハ大人なんだから\n眠くなんか…ふわぁ…", 3, 3, 1, 9207, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132401, 1, "ふわふわしたものって\nつい飛び込みたく\nなっちゃうよね\n…うずうずっ", 2, 0, 1, 9209, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132401, 1, "かわいいのもいいけど\nカッコよさも欲しいな\nそーだ、角とかもっと\nいっぱい付けてみる？", 3, 0, 1, 9210, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132401, 1, "おー、いい匂い♪\nこれもしかして\nルイちゃんの新作？\n爆売れ間違いなしだよ", 4, 0, 1, 9211, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132401, 1, "ぬくぬくぽかぽか、\n気持ちいいー…\nでもちょっと\n暑くなってきたな", 5, 0, 1, 9212, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132401, 1, "もぐもぐもぐ…\nこれはいい食材を\n使っておりますなぁ\nうん、三ツ星！", 7, 0, 1, 9214, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 132401, 1, "ふむふむなるほど…\nこっ、これは…！\n…ごめん、適当に\n言ってみた", 8, 0, 1, 9215, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135001, 1, "また海に行く時に\n備えて…次の計画を\n練る必要がある…", 1, 0, 1, 9217, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135001, 1, "騒がしいのも…\n以前ほど気にならない\n慣れてしまった…？", 2, 0, 1, 9218, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135001, 1, "眩暈がする暑さも\n騎士クンと同じ気温を\n感じられるなら…いい", 3, 0, 1, 9219, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135001, 1, "騎士クンに\n好意を抱く者は\nみんな有罪\n…わたしは例外", 4, 0, 1, 9220, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135001, 1, "体も心も…外の世界に\n馴染んできた気がする\n少しずつ、だけど…", 5, 0, 1, 9221, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135001, 1, "騎士クンの手…\n大きかった…\n…また、つなぎたいな", 101, 0, 4, 9222, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135001, 1, "夏が終わっても…\nその先の季節も\n…その次の夏も\n騎士クンと一緒に…", 102, 0, 8, 9223, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135001, 1, "騎士クンといるとね\n渇いてた心が…\n潤される、気がするの", 1, 0, 1, 9224, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135001, 1, "騎士クンは…\nしたいこと、ある？\n夏にだけ…できること\n一緒に、しようね", 2, 0, 1, 9225, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135001, 1, "仮想領域の夢でも\n現実の海でも…\n騎士クンと一緒なら\nそこが本物の夏、だね", 3, 0, 1, 9226, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135001, 1, "肝試しがしたい…？\n…うまく驚けるかな\n練習しなくちゃ…", 4, 0, 1, 9227, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135001, 1, "騎士クンとなら\nどんな夏でも…\nきっと楽しい…♪", 101, 0, 4, 9228, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135001, 1, "水着…どうかしら…？\n…よかった\nシミュレーションの\n成果ね。ふふ", 102, 0, 8, 9229, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135001, 1, "騎士クン…眠たそう\nもうちょっと…寝る？\n後で起こしてあげるね", 1, 1, 1, 9230, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135001, 1, "お出かけするなら\n水筒を用意するね…\n夏の水分補給は大事…", 2, 2, 1, 9231, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135001, 1, "また…夜の海を\n見に行きたいな\n騎士クンと一緒に\n連れていってくれる？", 3, 3, 1, 9232, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135001, 1, "騎士クンを待つには\nちょうどいい…\n…落ち着く…", 2, 0, 1, 9234, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135001, 1, "こういう物を\n城に置いた方が\nかわいいって思って\nもらえる…かな…？", 3, 0, 1, 9235, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135001, 1, "夏に合わせた香りね…\n涼やかな風が胸を\n吹き抜けるような…", 4, 0, 1, 9236, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135001, 1, "火は落ち着く…\n身体感覚を味わえる\n作り物の…この体でも", 5, 0, 1, 9237, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135001, 1, "『食』…騎士クンと\n話す共通の話題には\n悪くなさそう…", 7, 0, 1, 9239, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135001, 1, "『好きな人と楽しめる\n夏デート特集』…？\n……ふむ…", 8, 0, 1, 9240, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135001, 1, "わたしの趣味では\nないけれど…\n騎士クンが望むなら\n城に置いても…？", 10, 0, 1, 9242, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135001, 1, "む…悪くない\nこれなら\n騎士クンと一緒に…？", 11, 0, 1, 9243, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135101, 1, "一日中水着でいるって\n浮かれているみたいね\n夏は短いから、沢山\n楽しまなくちゃね", 1, 0, 1, 9244, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135101, 1, "ちょっと暑いし\n氷の上を滑って\n涼もうかしら", 2, 0, 1, 9245, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135101, 1, "そろそろ運送ギルドの\nアルバイトの時間だわ\n準備しなきゃ", 3, 0, 1, 9246, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135101, 1, "エリスさん、水着が\nすごく似合ってた…\nどこで買ったのかしら", 4, 0, 1, 9247, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135101, 1, "未練が残らないように\nこの夏休みでやりたい\nことはやらなくちゃ", 5, 0, 1, 9248, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135101, 1, "かき氷を作ったら\n彼も食べるかしら？\nアイスクリームの\nほうがいいかな…", 101, 0, 4, 9249, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135101, 1, "水着を着ていれば\nどこにいても\nリゾート気分だわ♪", 102, 0, 8, 9250, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135101, 1, "夏はおいしいものが\nたくさんあるし\n【美食殿】の活動も\n忙しくなりそうね♪", 1, 0, 1, 9251, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135101, 1, "わ、私の尻尾…\nそんなに気になる？\nつついたらダメよ\nいいわね…？", 2, 0, 1, 9252, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135101, 1, "夏は日が長いし\nまだまだたくさん\n遊べそうね♪", 3, 0, 1, 9253, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135101, 1, "今日は一緒に海に\n行かない？\nあなたも水着\n持ってきてるでしょ", 4, 0, 1, 9254, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135101, 1, "そろそろ日焼け止めを\n塗り直さなきゃ…あの\nお願いしてもいい…？", 101, 0, 4, 9255, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135101, 1, "私たちで最高の\n夏休みにしましょう\nね、おにーたん♪", 102, 0, 8, 9256, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135101, 1, "夏の朝は早いわね\nもうセミも鳴いてるし\nあなたも早起きだわ…\n見習わなくちゃ", 1, 1, 1, 9257, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135101, 1, "日中は暑いだろうし\nゆっくりしましょう\nお昼寝タイムはどう？", 2, 2, 1, 9258, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135101, 1, "この時間のアイス\nクリームは格別よね♪\n何味にしようかしら", 3, 3, 1, 9259, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135101, 1, "暑い夏はしっかり\n休むことが大切\nふぅ…いい気持ち♪", 2, 0, 1, 9261, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135101, 1, "ふわふわの毛並み\n暑くないのかしら\nかわいいだけじゃなく\n忍耐強くもあるのね", 3, 0, 1, 9262, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135101, 1, "爽やかでいい匂い…♪\nジトジトしがちの夏に\nピッタリなアロマね", 4, 0, 1, 9263, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135101, 1, "ずっと水着でいると\n流石に冷えるわね\nうん、いい心地だわ", 5, 0, 1, 9264, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135101, 1, "はむっはむっ…\nしっかり食べて\n夏バテ対策しないと", 7, 0, 1, 9266, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135101, 1, "ふむふむ…面白いわね\n今度プレシアちゃんと\n一緒に読もうかしら", 8, 0, 1, 9267, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135101, 1, "私のフロートと少し\n似ている気がする\nサイズ的にはこっちが\nお兄さんかしら…ふふ", 10, 0, 1, 9269, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135101, 1, "ふふ、きれい♪\nまるで\n海のステージね！", 11, 0, 1, 9270, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135201, 1, "じゅんび、おっけー\nうみでも\nおふろでも\nおんせんでも", 1, 0, 1, 9271, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135201, 1, "ひまわり。すき\nおっきくて\nたね、うまし", 2, 0, 1, 9272, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135201, 1, "ハッピーちゃん\nいい、におい\nひやけ？\nやきぶた？", 3, 0, 1, 9273, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135201, 1, "じーわ、じーわ…\nせみの、なきごえ\nきいてる、だけで\nあつい。かも…", 4, 0, 1, 9274, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135201, 1, "アイス、かきごおり…\nあつい、なつ\nだから\nひえひえ、ひっす", 5, 0, 1, 9275, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135201, 1, "おっきくなるまほー\nきわめたい\nお兄を\nだっこ。するよ", 101, 0, 4, 9276, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135201, 1, "「あなた」\n…ちょっと、ちがう？\nシェフィたち\nみたいに、ならない…", 102, 0, 8, 9277, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135201, 1, "じゃりじゃり\nべたべた\n…シャワー\nいって、きます", 1, 0, 1, 9278, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135201, 1, "（すんすん）\nお兄。ほんのり\nうみの、におい\nおいしそう", 2, 0, 1, 9279, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135201, 1, "お兄。みて\nべろ、まっさお\nぶるーはわい\nカラー", 3, 0, 1, 9280, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135201, 1, "あつくても\nあついもの\nおいしい\n…バーベキュー…", 4, 0, 1, 9281, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135201, 1, "ついんて\nバランス、むずかし\nお兄\nリボン、よろ", 101, 0, 4, 9282, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135201, 1, "みずぎ\nにあってる。って\nお兄。いった\nもっと。みて？", 102, 0, 8, 9283, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135201, 1, "おはよ\nあさごはん\nやきざかな", 1, 1, 1, 9284, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135201, 1, "おひる\nおひるね\nいっしょに、しよ？", 2, 2, 1, 9285, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135201, 1, "おそと\nまっくら\nはなび\nしよ？", 3, 3, 1, 9286, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135201, 1, "ふわぁ…\nあそびすぎ\nおつかれ\nねむねむ", 2, 0, 1, 9288, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135201, 1, "よしよし\nおるすばん\nおねがい。ね", 3, 0, 1, 9289, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135201, 1, "（くんくん）\nれもんの、におい\nじゅる", 4, 0, 1, 9290, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135201, 1, "さかな、くしやき\nすたんばい、おっけー\nいつでも、やける。よ", 5, 0, 1, 9291, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135201, 1, "いっぱい、あそびたい\nいっぱい、つよくなる\nいっぱい、たべる", 7, 0, 1, 9293, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135201, 1, "おさかなの、ほん\nおもしろかった\nでも\nおなかが…", 8, 0, 1, 9294, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135201, 1, "れっつ、ごー\nすいへーせん\nせかい\nいっしゅー", 10, 0, 1, 9296, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135201, 1, "きぶんは、かいばしら\nバターやき\nおすすめ", 11, 0, 1, 9297, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135301, 1, "成長して着れなく\nなっちゃう前に\nこの水着でできること\n全部しちゃいたいな♪", 1, 0, 1, 9298, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135301, 1, "お家で水着だと\n開放的な気分かも♪\nふふ、お兄さんにも\n教えてあげなきゃ♪", 2, 0, 1, 9299, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135301, 1, "泳ぎの練習しちゃお♪\nすいすい、すいすいー\n…ちょっと、\n子供っぽかったかも…", 3, 0, 1, 9300, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135301, 1, "インドア派だけど\n夏の屋外は好き…や…\nお兄さんと一緒なら\nどこでも…", 4, 0, 1, 9301, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135301, 1, "夏だからってちょっと\n羽目外しすぎかな…\nわたし…やっぱり\n真面目じゃない…かも", 5, 0, 1, 9302, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135301, 1, "わたしの水着姿…\n少しは大人っぽいって\n思ってくれたかな…？", 101, 0, 4, 9303, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135301, 1, "家の中は涼しいのに\nお兄さんのこと\n考えると暑くなって…\n夏バテしちゃいそう…", 102, 0, 8, 9304, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135301, 1, "お兄さんと一緒なら\n夏休みの日記\n書くことが多すぎて\nページが足りません♪", 1, 0, 1, 9305, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135301, 1, "かき氷食べましょ♪\n…ふふ、お兄さん\n舌の色すごいですよ♪\nわたしも…べ～っ♪", 2, 0, 1, 9306, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135301, 1, "今日は海に行きます？\nそれともお家で\nのんびりですか？\nわたしはどっちでも♪", 3, 0, 1, 9307, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135301, 1, "夏バテしないように\nスタミナ料理のレシピ\nお勉強しました♪", 4, 0, 1, 9308, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135301, 1, "夏が終わっちゃっても\nこの水着を着てたら\n夏の思い出、忘れずに\nいてくれますか…？", 101, 0, 4, 9309, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135301, 1, "…この日焼け跡\nお兄さんと\nわたしだけの\n秘密…ですよ？", 102, 0, 8, 9310, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135301, 1, "おはようございます！\n朝の体操１、２、３♪\nしゃきっと目覚めて\nいい一日にしましょ♪", 1, 1, 1, 9311, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135301, 1, "お昼休憩ですね♪\n午後からも頑張れる様\nお兄さんのこと\n癒しますね…♪", 2, 2, 1, 9312, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135301, 1, "そろそろ寝る時間…\nお兄さんが眠るまで\n一緒にいますね…\n…ふあぁ…", 3, 3, 1, 9313, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135301, 1, "気持ちいいけど、汗…\n染みちゃわないように\n気をつけないと…", 2, 0, 1, 9315, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135301, 1, "ぎゅう～♪\nなんて、ちょっと\nあざといかな…♪", 3, 0, 1, 9316, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135301, 1, "夏にぴったりの\nフルーティーな香り♪\nお兄さん、好きかな？", 4, 0, 1, 9317, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135301, 1, "水着で暖まるのって\nなんだか贅沢な気分♪\nこたつでアイス的な♪", 5, 0, 1, 9318, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135301, 1, "お腹ぽっこりに\nならないよう\n気をつけないと…\nでも止まらない…", 7, 0, 1, 9320, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135301, 1, "弾ける水着でビーチの\n視線を独り占め…かぁ\nわたしはお兄さんの\n視線だけで…なんて♪", 8, 0, 1, 9321, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135301, 1, "これで夏の大冒険に\nしゅっぱ～つ！\n…なんちゃって♪", 10, 0, 1, 9323, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135301, 1, "なんだか女神さま\nみたい…？\nふふ、わたしには\n似合わないかもです♪", 11, 0, 1, 9324, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135401, 1, "真っ白い日差し─\nジオ・ゲヘナには\nなかったものですね\n─悪くはないです", 1, 0, 1, 9325, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135401, 1, "しばらくここで\n休んでます\n人の多い街中よりは\n落ち着くので─", 2, 0, 1, 9326, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135401, 1, "ここにも─\nあるんでしょうか\n海で使う遊び道具…", 3, 0, 1, 9327, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135401, 1, "見張りは─\nここならあまり\n必要なさそう…", 4, 0, 1, 9328, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135401, 1, "夏のバカンス─\n媛が楽しんでるなら\n私は十分です", 5, 0, 1, 9329, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135401, 1, "知らない相手は、\n苦手だけど─\n…少しだけ、\n話してみても…", 101, 0, 4, 9330, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135401, 1, "ここにいると\n安心する、ような…\n土壌が─良い…？", 102, 0, 8, 9331, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135401, 1, "夏の正装、水着─\n着方、間違えて\nないですよね…？", 1, 0, 1, 9332, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135401, 1, "相変わらず、\nおしゃべりが\n好きなんですね\n私は─普通です", 2, 0, 1, 9333, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135401, 1, "─喉が渇きました\n…いえ、花にお水が\n欲しいわけじゃなくて", 3, 0, 1, 9334, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135401, 1, "─いつもより\n花がつやつやしてる？\n…あ、褒めて\nくれたんですか", 4, 0, 1, 9335, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135401, 1, "この部屋─\n貴方が飾りつけを？\nわかります、何となく", 101, 0, 4, 9336, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135401, 1, "すぐ傍にいると、\n暑くなりませんか？\n─私は別に\nかまいませんが", 102, 0, 8, 9337, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135401, 1, "朝から元気ですね─\nおかげですっかり\n目が覚めました", 1, 1, 1, 9338, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135401, 1, "外─暑そうです\n出かけるなら\n気を付けてください\n倒れたりしないよう…", 2, 2, 1, 9339, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135401, 1, "─貴方も\n夜更かしですか？\n…少しだけなら\n付き合いますよ", 3, 3, 1, 9340, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135401, 1, "貴方もここで\n休みたいですか？\n─ダメです\n今は私が使うので", 2, 0, 1, 9342, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135401, 1, "ゆるい顔つきですね\nジャングルでは\n生き残れそうに\nないです─", 3, 0, 1, 9343, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135401, 1, "─ん…\nこれ、海の香り？\n─いい匂い…", 4, 0, 1, 9344, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135401, 1, "…暑さには\n慣れてますよ\n寒いのよりは、ずっと", 5, 0, 1, 9345, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135401, 1, "栄養は、大事です\n味は─あまり\n気にしたこと、\nありませんでした", 7, 0, 1, 9347, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135401, 1, "適当に本を読んでいる\nだけです─\n…別に、遊びについて\n調べたりしてません", 8, 0, 1, 9348, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135401, 1, "魔物を模した\n人形─ですね\n戦闘訓練用に？", 10, 0, 1, 9350, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135401, 1, "貝のクッションですか\n─噛みついてきたり\nしないですよね", 11, 0, 1, 9351, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 137101, 1, "シントーメッキャク\n火もまた涼し…\nむむむ…やっぱり夏は\n暑いデース！", 1, 0, 1, 9352, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 137101, 1, "海にも新しい忍法の\nヒントがあるはずデス\nショーグンと海底散歩\nも楽しそうデス", 2, 0, 1, 9353, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 137101, 1, "釣りの心得は\nメイキョーシスイ…\n忍者の修行に\nうってつけデス♪", 3, 0, 1, 9354, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 137101, 1, "天下統一の後のことも\n考えねばなりまセン\n今度はテンジョー統一\nデスかね…？", 4, 0, 1, 9355, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 137101, 1, "デケデケデン♪\nテルテル坊主完成デス\n晴れてないとお外で\n遊べまセンからね！", 5, 0, 1, 9356, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 137101, 1, "次のオモテナシも\nショーグンをワクワク\nさせマス♪　今から\n用意しておかねばデス", 101, 0, 4, 9357, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 137101, 1, "新しい忍法の開発に\n休みはないのデス！\nショーグンの天下統一\nを思えばなんのその！", 102, 0, 8, 9358, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 137101, 1, "海の前に準備運動デス\nまずは手を後ろに、\n素早く忍者走り！\nシュバババッ", 1, 0, 1, 9359, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 137101, 1, "夏にお祭りは\n欠かせないデスよね♪\n地域のイベント情報は\n見逃しまセン！", 2, 0, 1, 9360, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 137101, 1, "ショーグン、団扇で\nあおいであげマス！\n忍法・扇風の術ー！\nうりゃりゃりゃー！", 3, 0, 1, 9361, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 137101, 1, "ワタシが必要な時は\n風鈴を鳴らすデス！\n風よりも早く\n駆けつけマスよ！", 4, 0, 1, 9362, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 137101, 1, "じっと見つめられると\n顔が熱くなってきて…\nはっ、ショーグンの\n新しい忍法デスか！？", 101, 0, 4, 9363, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 137101, 1, "ショーグン、日焼けは\nノン、デスよ！\nワタシがクリームぬり\nぬりしてあげマース！", 102, 0, 8, 9364, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 137101, 1, "おはようデス！\n朝ごはんはワタシに\nお任せデス。\n毎日みそ汁作るデス！", 1, 1, 1, 9365, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 137101, 1, "日差しに注意デスよ\nネッチューショーに\n日焼け、どっちも\n大変デスからね！", 2, 2, 1, 9366, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 137101, 1, "ショーグン、\n外で花火しまショウ！\nぱちぱちキレイで\n楽しいデスよ！", 3, 3, 1, 9367, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 137101, 1, "遊びに備えて\n今は休む時…\nガシンショータンデス", 2, 0, 1, 9369, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 137101, 1, "この子たちにも\n手伝ってもらって\n新しい忍法を開発\nしてみまショウ♪", 3, 0, 1, 9370, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 137101, 1, "むむっ、ここまで\n潮の香りが漂って\nきた気がしマス！", 4, 0, 1, 9371, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 137101, 1, "海に入った後は\n体を温めねば\nやはりサウナも\n必要デスね！", 5, 0, 1, 9372, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 137101, 1, "いっぱい食べて\nいっぱい遊ぶデス！\n夏バテなんかに\n負けてられまセン！", 7, 0, 1, 9374, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 137101, 1, "『BUSHIDO』の\n最新刊が待ち遠しい\nデス…マイゾーキン\n特集、読みたいデス", 8, 0, 1, 9375, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 137101, 1, "ショーグンとこの子に\n乗って、天下統一まで\nシュババババッ！\nなんてどうでショウ", 10, 0, 1, 9377, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 137101, 1, "ぽよんぽよ～ん♪\nショーグンと一緒に\n遊んでみたいデス♪", 11, 0, 1, 9378, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135501, 1, "今度はシナツちゃんの\n世界へ行ってみたいな\nエリスちゃんも誘って\n大人女子旅とか最高♪", 1, 0, 1, 9379, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135501, 1, "あっはっはー！\nよし、今日も絶好調！\nま、いつも笑ってたら\n不調なんてないけど♪", 2, 0, 1, 9380, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135501, 1, "ん～あっちの世界と\nほとんど一緒だね！\n違ってるところ探しに\n出かけてみよっかな？", 3, 0, 1, 9381, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135501, 1, "ううーん、のんびり\nするのもいいけど\nどこか行ってみたいな\n人助けとか、ね", 4, 0, 1, 9382, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135501, 1, "あっ、そーだ\n向こうへ帰る前に\nこっちのお土産とか\n買っとかなくちゃ", 5, 0, 1, 9383, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135501, 1, "ふ～ん、騎士くんって\nこういうのが好みか～\n後でヒヨリちゃんに\n教えてあげよ♪", 101, 0, 4, 9384, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135501, 1, "次はあたしが恩を返す\n番だね。騎士くんに\n助けが必要になったら\n絶対駆けつけるよ！", 102, 0, 8, 9385, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135501, 1, "あっ騎士くん！\n困ってることない？\nお姉さんがなんでも\n力になっちゃうよ！", 1, 0, 1, 9386, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135501, 1, "また闘技場に行こうよ\nちょっと運動不足でさ\nバーンと暴れたい気分\nなんだよね♪", 2, 0, 1, 9387, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135501, 1, "いつか大人になったら\n一緒に飲もうよ！\nあははっ、騎士くん\n指切りげんまん♪", 3, 0, 1, 9388, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135501, 1, "一緒に作った麦しゅわ\nカップ、最高だね♪\n騎士くんの分もどうぞ\nちゃんとジュースだよ", 4, 0, 1, 9389, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135501, 1, "ねえ…ぶっちゃけ\nヒヨリちゃんのこと\nどう思ってるのかな？\nお姉さんに聞かせなよ", 101, 0, 4, 9390, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135501, 1, "騎士くん、うっかり\n飲みすぎたあの日の\nコトは誰にも内緒で\nお願いね。約束だよ！", 102, 0, 8, 9391, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135501, 1, "あはは、騎士くん\n新しい朝だよー！\n今日はどんな一日か\nワクワクするね～♪", 1, 1, 1, 9392, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135501, 1, "午後からも笑顔で\n元気に乗り切ろうね！\n外に行くもよし、\n部屋で麦しゅわもよし", 2, 2, 1, 9393, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135501, 1, "ここからは大人の時間\n騎士くんにはまだ少し\n早いかな～？\nだから、おやすみ♪", 3, 3, 1, 9394, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135501, 1, "麦しゅわ飲み過ぎた\n夜はここにダーイブ\n…なんて大人に\nなっちゃダメだよ～？", 2, 0, 1, 9396, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135501, 1, "あたしの世界の子たち\nこういうの好きかな？\nお土産に探してみよう\n気に入るかも♪", 3, 0, 1, 9397, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135501, 1, "ん～いいね♪\nあとでお気に入りの\nフレーバーも\n試してみよっかな？", 4, 0, 1, 9398, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135501, 1, "ほうほう…熱い！\nサラサリアは\nいつも暖かいから\nこういうの新鮮！", 5, 0, 1, 9399, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135501, 1, "ん～おいしい！\nけどやっぱご飯は\nみんなで一緒に\n食べたいな～", 7, 0, 1, 9401, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135501, 1, "文字ばっかの本は\nちょーっと苦手かも…\n必要なら読むけどさ？", 8, 0, 1, 9402, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135601, 1, "皆思い思いに過ごして\nいるのに、不思議と\n落ち着く。いつまでも\nここにいられたら…", 1, 0, 1, 9406, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135601, 1, "皆に挨拶をせねば\n…いや、今は、まだ\nタイミングが悪い…\nまた今度にしよう…", 2, 0, 1, 9407, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135601, 1, "私の服装…この場で\n浮いてないだろうか\nまずい、心配に\nなってきた…！", 3, 0, 1, 9408, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135601, 1, "アイラたちとの旅行も\n楽しかったな…\nまた３人で温泉に\nいきたいものだ", 4, 0, 1, 9409, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135601, 1, "ここで剣の稽古は\nさすがによろしくない\n代わりに今日は瞑想を\nすることにしよう", 5, 0, 1, 9410, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135601, 1, "人とのつながりは\n大切にしなければ\n今日こそは誰かに\n話しかけるぞ…！", 101, 0, 4, 9411, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135601, 1, "できることが増えて、\n迷うばかりの毎日…\nそれが楽しく尊いと\n今実感している…", 102, 0, 8, 9412, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135601, 1, "ええと…奇遇だな\nこんなところで\n会うなんて。いや違う\n…その、こんにちは…", 1, 0, 1, 9413, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135601, 1, "弟子たちへの土産も\n探しておかないとな\nよければキミからも\nアドバイスを頼む…", 3, 0, 1, 9415, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135601, 1, "剣の稽古をしたい？\nふっ…いいとも\nキミとならいくらでも\n準備をしてすぐ戻る", 4, 0, 1, 9416, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135601, 1, "よければまた、\n街を案内してくれ\n名所旧跡もだがキミと\n歩くのは楽しいんだ", 101, 0, 4, 9417, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135601, 1, "感謝の心を\n忘れたことはないよ\nキミたちの優しさに\n救われた一人として…", 102, 0, 8, 9418, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135601, 1, "うむ、いい顔だな\n良い一日は、\n良い目覚めから始まる\n心掛けておくといい", 1, 1, 1, 9419, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135601, 1, "もう昼時か\n誰かを誘って食事に…\n…いや無理だ、\nハードルが高い…！", 2, 2, 1, 9420, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135601, 1, "夜はいい…\n誰とも話さず\n一人きりでいても、\n不自然ではないからな", 3, 3, 1, 9421, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135601, 1, "…いかんな、これは\n気を抜くとこのまま\n離れられなくなる…", 2, 0, 1, 9423, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135601, 1, "シナツだ、よろしく\n……ふぅ\n人形相手の練習なら\n緊張しないのだがな…", 3, 0, 1, 9424, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135601, 1, "ふむ…集中力が\n高まりそうな香りだな\n稽古にも役立つだろう\n皆にも教えてやろうか", 4, 0, 1, 9425, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135601, 1, "凍える人に寄り添い\n温もりを与える…\n私もそんな存在に\nなりたいものだ", 5, 0, 1, 9426, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135601, 1, "食の楽しみ…\nあまり意識して\nいなかったが、\nやはりいいものだな", 7, 0, 1, 9428, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135601, 1, "…っと、いかん\n読みふけっていたら\nいくらでも時間を\n過ごせてしまうぞ…", 8, 0, 1, 9429, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135701, 1, "また空を飛びたいな～\nふわふわ～ってなる\nあの感じが気持ちいい\nんだよね～♪", 1, 0, 1, 9433, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135701, 1, "またサラサリアに\n行きたいなー\n…串焼きの味が\n忘れられなくって♪", 2, 0, 1, 9434, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135701, 1, "悩んでる人がいたら\n背中を押してあげたい\nあたしがそれで、勇気\nづけられたみたいに", 3, 0, 1, 9435, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135701, 1, "気持ちを言葉にするの\n大事って分かってても\nいざとなると緊張して\n上手くできないなぁ…", 4, 0, 1, 9436, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135701, 1, "あっちの世界でも\nギルドハウスは\nあるのかな…？\n今度探してみよっと♪", 5, 0, 1, 9437, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135701, 1, "アイラに会って少し\n成長できたかも…！\n素敵な女の人目指して\nもっと頑張るぞーっ♪", 101, 0, 4, 9438, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135701, 1, "あっちのあたしは\n騎士くんに選ばれた…\nよし、あたしも\n頑張らなくっちゃ…！", 102, 0, 8, 9439, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135701, 1, "部屋の模様替えする？\nサラサリア風の家具も\nあったら素敵だよね！", 1, 0, 1, 9440, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135701, 1, "なんだかお疲れ気味？\nそうだっ、サラサリア\nで教わったマッサージ\nをしてあげるっ♪", 2, 0, 1, 9441, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135701, 1, "騎士くんっ！\n今からあたしとデート\nしませんかっ！", 3, 0, 1, 9442, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135701, 1, "今ちょうど、踊りの\n練習してたとこなの\n騎士くんも\n一緒にどう？", 4, 0, 1, 9443, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135701, 1, "き、騎士くん！\nこの前の続き…\n手、繋いでいい…？", 101, 0, 4, 9444, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135701, 1, "えへへへ、騎士くんが\nあたしの背中を押して\nくれると、勇気も\n100万倍だよっ♪", 102, 0, 8, 9445, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135701, 1, "おはよ、騎士くん！\n今日もいい朝だよ！\nサラサリアみたいに\n太陽が輝いてるもん！", 1, 1, 1, 9446, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135701, 1, "フレフレ、騎士くん！\n午後もファイトー！\nあたしは日陰でお昼寝\nタイム…なんてねっ♪", 2, 2, 1, 9447, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135701, 1, "夜に騎士くんといると\n空飛ぶ絨毯から見た\nサラサリアの夜景を\n思い出しちゃうな♪", 3, 3, 1, 9448, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135701, 1, "休憩きゅうけ～い♪\n強い人と戦った後は\nしっかりと体を\n休ませないとね", 2, 0, 1, 9450, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135701, 1, "にゃ～、\nかわいいにゃ～♪\nアンブラを\n思い出しちゃうよ♪", 3, 0, 1, 9451, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135701, 1, "わっ…すごくいい\n匂い～サラサリアの\n砂漠の匂いにちょっと\n似てるかも", 4, 0, 1, 9452, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135701, 1, "サラサリアの太陽\nみたいに暖かい♪\n日向ぼっこしてる\nみたいでしあわせ…", 5, 0, 1, 9453, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135701, 1, "ん～おいしいっ！\nアイラもきっと\n好きな味だね…！\n今度食べてもらおっ！", 7, 0, 1, 9455, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135701, 1, "これは恋愛指南本…！\nふむ…意中の男性と\nキスするには…って\nこれはまだ早いよ～！", 8, 0, 1, 9456, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135801, 1, "あてがここにいたら、\nみんな寒い思いを\nしちまうかな…\nご、ごめんな", 1, 0, 1, 9460, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135801, 1, "あて、みんなより\n体でっけぇし…\nもうちょっと隅っこ\n行っとこうかな", 2, 0, 1, 9461, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135801, 1, "さ、騒がしいとこは\n苦手だども…\nここはそんなに\n気になんね…", 3, 0, 1, 9462, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135801, 1, "いろいろ勉強してぇな\nイナカもんだから、\nみんなに迷惑とか\nかけねぇように…", 4, 0, 1, 9463, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135801, 1, "家具とか、飾りとか、\n壊しちまったら大変…\nき、気ぃつけねぇとっ", 5, 0, 1, 9464, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135801, 1, "あ、あての三味線、\n聴きたいの…？\nこ、心の準備が\nできてからで、い…？", 101, 0, 4, 9465, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135801, 1, "っ、ぁぶね…\nあてったらまた、\n騎士さのこと見て、\n溶けそうになってた…", 102, 0, 8, 9466, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135801, 1, "お、おめさ、その…\nおやつさん、いらね？\nかき氷とかなら、\nあて、作れるよ…？", 1, 0, 1, 9467, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135801, 1, "あ、あてに…何か用？\n…声、聴きたかった？\nな、なら…えっと…\nおしゃべり…する？", 2, 0, 1, 9468, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135801, 1, "こっちさ来てから…\n向こうの世界の\n何年分も、おしゃべり\nしたかもしんね…", 3, 0, 1, 9469, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135801, 1, "こっちの世界には、\n氷滑りって遊びが\nあるって…？\nあてにもできっかな…", 4, 0, 1, 9470, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135801, 1, "ん…あては元気だよ\n悩みがあっても、\nおめさの声もらうと\n吹っ飛んじまうし…", 101, 0, 4, 9471, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135801, 1, "騎士さが近くに\nいてくれる…\n今は、それだけで\nいいんだ…うん…", 102, 0, 8, 9472, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135801, 1, "お、おはよ…\nおめさのおかげで、\n昨夜もよく眠れたよ…", 1, 1, 1, 9473, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135801, 1, "…あっ、もう昼飯の\n時間かぁ…\n考えごとしてたら\nあっという間だよ", 2, 2, 1, 9474, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135801, 1, "景色は似てるけど…\nこの世界の夜は、\nざわざわ音がして\n賑やかだな…", 3, 3, 1, 9475, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135801, 1, "あんま縮こまってると\n体硬くなっちまうし…\nたまにはのんびり…", 2, 0, 1, 9477, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135801, 1, "えへへ、めんこいな…\nそだ、仁太坊たちも\n並んでたら\nもっとめんこいかも…", 3, 0, 1, 9478, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135801, 1, "この匂い…なんか、\nは、はいから…？\nあて、イナカもん\nだから…", 4, 0, 1, 9479, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135801, 1, "あ、暑くて溶けそう…\nだども、我慢できたら\n修行になる…かも…？", 5, 0, 1, 9480, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135801, 1, "ん…♪　うめぇ…\nこんなうめぇもん\n毎日食べられるの、\n幸せだなぁ…", 7, 0, 1, 9482, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135801, 1, "こっちの世界にも…\n雪女が出る物語、\nあるみてぇだな…\nか、悲しい話だども…", 8, 0, 1, 9483, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135901, 1, "なかなか上等な\n家じゃねェか\nちっとばかし\n軒を借りるぜ", 1, 0, 1, 9487, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135901, 1, "俺の棒っきれの\n置き場所は…\nま、玄関にでも\n立てかけときゃいいか", 2, 0, 1, 9488, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135901, 1, "こっちの世界は\n眩しくって敵わねェぜ\n目がチカチカしやがる", 3, 0, 1, 9489, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135901, 1, "建物の様式が同じでも\n世界と住人が違えば\n印象も変わるモンだな", 4, 0, 1, 9490, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135901, 1, "街の連中は\n戦いなんて知らねェ\nのんきなツラしてんな\n…いいトコじゃねェか", 5, 0, 1, 9491, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135901, 1, "騎士公のヤツ、\nまたぞろ厄介事に\n巻き込まれや\nしてねェだろうな…", 101, 0, 4, 9492, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 135901, 1, "ふわぁ…\nこうも居心地がいいと\n眠くなっちまうぜ", 102, 0, 8, 9493, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135901, 1, "俺ばっかり\n見てんじゃねェよ\n赤鬼になっちまわぁ", 1, 0, 1, 9494, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135901, 1, "注いでくれんのか？\nあんがとよ\nんじゃ俺からも\nお返しだぜ", 2, 0, 1, 9495, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135901, 1, "おう、暇してるなら\n鍛錬に付き合えよ\nじっとしてっと\n体がなまって仕方ねェ", 3, 0, 1, 9496, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135901, 1, "退屈してきたな…\n散歩にでも行くか？\n騎士公なら退屈しねェ\n場所知ってるだろ", 4, 0, 1, 9497, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135901, 1, "女との関係は\nきちっとしとけよ\n俺だって助けらんねぇ\nもんはあるからな", 101, 0, 4, 9498, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135901, 1, "角に触りたいだぁ？\n…まあ、騎士公の\n頼みなら…\nあんま手荒にすんなよ", 102, 0, 8, 9499, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135901, 1, "起きろ\n朝の鍛錬の時間だぜ\nほれ、さっさと\n身支度しちまいな", 1, 1, 1, 9500, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135901, 1, "昼飯ィ？\n俺の分はいらねぇよ\n気ィつかわねぇで\nくんな", 2, 2, 1, 9501, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 135901, 1, "まだ起きてんのか？\nさっさと寝ねェと\nこうやって鬼が来るぜ", 3, 3, 1, 9502, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135901, 1, "悪くない座り心地だな\n気ィ抜くと、ダメに\nされちまいそうだぜ", 2, 0, 1, 9504, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135901, 1, "…今こいつ動いたか？\nグレ公あたりが\n悪さしてんじゃ\nねェだろうな", 3, 0, 1, 9505, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135901, 1, "騎士公はこういう\n香りが好みか？\nお姫とユキノに\n教えてやるとすっか", 4, 0, 1, 9506, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135901, 1, "地獄の釜よりかは\nちとぬるいが…\nなかなか\nいい火力してやがるぜ", 5, 0, 1, 9507, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 135901, 1, "こっちの世界の本にも\n鬼が出てくるのか", 8, 0, 1, 9510, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 136101, 1, "フランシュシュ１号、\nサクラです！　サガの\n心を、歌でみんなに\n届けにきました！", 1, 0, 1, 9514, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 136101, 1, "ランドソルはドキドキ\nいっぱいの街やけん！\nサガとどっちが\n暮らしやすかろうねっ", 2, 0, 1, 9515, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 136101, 1, "ソロ活動がんばって、\n憧れの子にもも～っと\n近づくと！　成長した\n私、早く見せたか～！", 3, 0, 1, 9516, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 136101, 1, "部屋におるとは、伝説\nのメンバーばかり…！\n私もこのランドソルで\n伝説ば刻むとよ！", 4, 0, 1, 9517, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 136101, 1, "あそこの修繕が気に\nなると…。…あのっ！\nあ、ついあの子を\n呼びそうに…えへへっ", 5, 0, 1, 9518, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 136101, 1, "アイドルのレッスン、\n気になると？　まず、\n脚をぐい～っと広げて\n…あわわ、戻らん！？", 101, 0, 4, 9519, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 136101, 1, "騎士さんのコネが\nあれば、いつか堂々と\nゾンビアイドルで\n売り出せるかもっ…！", 102, 0, 8, 9520, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 136101, 1, "おはようござい\nまーす！　挨拶は基本\nやけん、元気よくっ！\nさ、一緒にやろう！", 1, 0, 1, 9521, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 136101, 1, "身だしなみチェック、\nよしっ。今日もいい\n天気っ♪　さあ、\nいってきまーーす！！", 2, 0, 1, 9522, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 136101, 1, "あんまり引っ張ると、\nう、腕が～…！　あ、\n気にせんで。伸びても\nすぐ元に戻るけんっ♪", 3, 0, 1, 9523, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 136101, 1, "私、持っとらんって\n思ってきたけど、それ\nが何ね！　友達ば、\nいっぱい持っとるけん", 4, 0, 1, 9524, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 136101, 1, "嚙めば噛むほど味が\n出る…騎士さんは\nサガのアタリメ\nみたいな人やねっ♪", 101, 0, 4, 9525, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 136101, 1, "騎士さんにだけ教え\nますね。私の秘密…\n私の死因を。え？\nもう分かる気もする？", 102, 0, 8, 9526, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 136101, 1, "ぼ～っとしとかれんね\n「ぶっ殺すぞ」って\n気合入れてくれる子が\n両方の街におるけんっ", 1, 1, 1, 9527, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 136101, 1, "あのすごか食べっぷり\nよう思い出すとよ\n元気かな…変なもん\n食べてないかな…", 2, 2, 1, 9528, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 136101, 1, "夜、出歩くと、怖～い\nアンデッドに襲われる\nかもしれんよ！\n気をつけんと…！", 3, 3, 1, 9529, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 136101, 1, "リラックス！　もっと\nがんばってリラックス\nせんといかんね！\nむむむ～っ！", 2, 0, 1, 9531, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 136101, 1, "が～ばや～らしか～♪\nあの子のお土産に\nしたいけど、同じの\n売っとらんかな…？", 3, 0, 1, 9532, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 136101, 1, "ん～、いい香りっ♪\nいつもこやん素敵な\n香りのする人が、私の\n友達におってね…", 4, 0, 1, 9533, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 136101, 1, "え～と…熱いとその…\n傷んじゃいそうで、\nちょっと心配やけん…\nそ…その…私の体が", 5, 0, 1, 9534, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 136101, 1, "わあ、美味しか～っ！\nランドソルのグルメも\nなかなか手強いっ…！\nサガも負けられんね～", 7, 0, 1, 9536, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 136101, 1, "アイドル雑誌はあると\nですかね？　フラン\nシュシュのランキング\nは…しょぼぼぼーん…", 8, 0, 1, 9537, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 137401, 1, "名探偵から聖職者へ、\n華麗なる変装だ！\nいや、心の中まで\n敬虔に…変貌、かな♪", 1, 0, 1, 9541, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 137401, 1, "真実の鏡で、誰の謎も\n解いてみせるよ。\n恋の謎は…私が解いて\nほしいくらいだ…", 2, 0, 1, 9542, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 137401, 1, "歩き方も…厳かに…\n気品と高潔さが\n感じられるように\n練習しなくてはね", 3, 0, 1, 9543, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 137401, 1, "私の行いはきっと、\nどこかで見られている\nかつてお別れをした\n大切なあの子にも…ね", 4, 0, 1, 9544, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 137401, 1, "異議あり！　…う～ん\nもっと威厳をもって…\n異議あり！　はっ、\n聞いていたのかいっ！", 5, 0, 1, 9545, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 137401, 1, "静かに心を見つめれば\n大切なものが分かる\n助手くんとの時間の\nかけがえなさも、ね", 101, 0, 4, 9546, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 137401, 1, "私も、想い合う二人の\n神聖な誓約の儀と\nいうものに憧れるよ\nつまり結婚式、にね…", 102, 0, 8, 9547, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 137401, 1, "珈琲を一杯お願い\nするよ、助手くん\nこれがないと私の\n推理が冴えなくてね", 1, 0, 1, 9548, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 137401, 1, "懺悔があるなら聞き\n届けよう。…え～っ！\n棚にある私のチョコを\n食べてしまったって！", 2, 0, 1, 9549, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 137401, 1, "助手くん、今日の\n依頼は何件かな\nもし誰も来ないなら\n一緒にお出かけしよう", 3, 0, 1, 9550, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 137401, 1, "さあ、出かけようか\n君と私で巡礼の旅だ\n鞄の中身はただ一つ\n真実を愛する心、さ！", 4, 0, 1, 9551, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 137401, 1, "一緒に祈ってくれる\nかい？　ありがとう\n一人では真実が重荷に\nなるときもあってね", 101, 0, 4, 9552, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 137401, 1, "何でも話してほしい\n分かち合いたいんだ\n喜びも悲しみも、君が\n背負う、なにもかも", 102, 0, 8, 9553, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 137401, 1, "おはよう！　聖職者は\n早起きなのさ♪\nふわぁ…はっ、今の\nあくびは見てないね？", 1, 1, 1, 9554, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 137401, 1, "今日のお昼は…\n一緒に台所で\n作らないかい？\n新婚さんみたいに♪", 2, 2, 1, 9555, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 137401, 1, "夜のお祈りをしよう\n凶悪な事件なんて、\nもう起きないように", 3, 3, 1, 9556, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 137401, 1, "はあ…ふかふか～…\n堕落してしまいそう…\nなんとも罪なアイテム\nだねえ、これは…", 2, 0, 1, 9558, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 137401, 1, "かわいいな…♪\n探偵事務所の机に\n置いたら場を和ませて\nくれそうだね", 3, 0, 1, 9559, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 137401, 1, "はあ…心が落ち着く\nけして、その、小説の\n謎解きにイライラして\nいたわけではないよ？", 4, 0, 1, 9560, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 137401, 1, "…暖かいね。こんな\n暖かさをあまねく\n世に届けるのが、\n今の私の役目だよ", 5, 0, 1, 9561, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 137401, 1, "こんなにおいしい\nなんて、きっと重大な\n秘密が隠されているに\n違いないよね！", 7, 0, 1, 9563, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 137401, 1, "時には推理小説でなく\n教えを説く本もいいね\nちょっとだけ…眠く\nなっちゃうけど…", 8, 0, 1, 9564, 0);
