INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよう\nよく眠れた？　今日も\n一日、頑張ろうね♪", 1, 1, 1, 0, 1, 89, 100202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お腹空いちゃった…\nそうだ。騎士クンも\n一緒にお昼食べない？", 1, 1, 2, 0, 2, 90, 100202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もうこんな時間…\nそろそろ寝なくちゃ\nおやすみなさい", 1, 1, 3, 0, 3, 91, 100202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "よし…\nこれなら\n褒めてもらえるかな？", 1, 1, 0, 0, 1, 92, 100202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "がんばることも\n必要だけど\nお休みも大切だよね", 1, 1, 0, 0, 2, 93, 100202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいい～♪\n騎士クンは\nこういうのって好き？", 1, 1, 0, 0, 3, 94, 100202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "戦う前にもこうやって\n落ち着けたら\nいいのに", 1, 1, 0, 0, 4, 95, 100202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あの…！\nよ、良かったら\n一緒に…！", 1, 1, 0, 0, 5, 96, 100202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "騎士クンと一緒に\n冒険するなら\n体力がないと…！", 1, 1, 0, 0, 6, 97, 100202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "最近服のサイズが\n合わないんだけど…\n食べ過ぎなのかな？", 1, 1, 0, 0, 7, 98, 100202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "…やっぱり\n月刊恋愛指南は\nためになる…", 1, 1, 0, 0, 8, 99, 100202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "装備の手入れでも\nしようかな…", 1, 1, 0, 0, 1, 101, 100301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "次に鍛えるべきは\n肉体面か精神面か\nどちらにしよう…？", 1, 1, 0, 0, 2, 102, 100301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？", 1, 1, 0, 0, 3, 103, 100301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "仲間なんていらない\nそう思ってた頃も\nあったな…", 1, 1, 0, 0, 4, 104, 100301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "考えごとをしていると\nつい部屋中を\n歩いてしまうな", 1, 1, 0, 0, 5, 105, 100301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "彼は\nどこまで強くなるのか\n私も負けられないな", 1, 4, 0, 0, 101, 106, 100301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？", 1, 8, 0, 0, 102, 107, 100301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ああ、キミだったのか\n人の気配がしたから\n誰かと思ったよ", 1, 1, 0, 0, 1, 108, 100301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "斬りかかるつもりなら\nちゃんとかかってきて\n…呼んだだけ？", 1, 1, 0, 0, 2, 109, 100301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なに？　私に相談？\n戦いについてなら\n喜んで", 1, 1, 0, 0, 3, 110, 100301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今のが突き？\n軽い！　もっと鋭く！\nさあ、もう一回！", 1, 1, 0, 0, 4, 111, 100301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ひゃっ！　ど、どこを\n触ってるの！？\n…油断した", 1, 4, 0, 0, 101, 112, 100301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "キミにはこれからも\n私のそばに…\nいや、なんでもない", 1, 8, 0, 0, 102, 113, 100301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよう\n今日も充実した\n一日にしていこう", 1, 1, 1, 0, 1, 114, 100301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そろそろ\nお茶の時間か\nキミも一緒にどう？", 1, 1, 2, 0, 2, 115, 100301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？", 1, 1, 3, 0, 3, 116, 100301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "戦いを経てもなお\n美しくあるように…\nこれでよし♪", 1, 1, 0, 0, 1, 117, 100301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "戦ってばかりで\n休まないと、また\nユイに怒られるな", 1, 1, 0, 0, 2, 118, 100301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう", 1, 1, 0, 0, 3, 119, 100301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これはいい…\n戦いの前の精神統一に\nもってこいじゃないか", 1, 1, 0, 0, 4, 120, 100301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "時々は落ち着いた\n時間を過ごすのも\n悪くない…", 1, 1, 0, 0, 5, 121, 100301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！", 1, 1, 0, 0, 6, 122, 100301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "食事は大切だけど\n食べ過ぎには\n注意しておこう…", 1, 1, 0, 0, 7, 123, 100301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ユイ…！\nこ、こんな本を\n読んでいたのか…！", 1, 1, 0, 0, 8, 124, 100301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "海だけじゃなくて\n川でも釣りがしたいな\n道具も新調しよう", 1, 1, 0, 0, 1, 126, 100302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？", 1, 1, 0, 0, 2, 127, 100302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "彼は\nどこまで強くなるのか\n私も負けられないな", 1, 1, 0, 0, 3, 128, 100302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "そろそろ修行の\n内容を新しくしよう\nより厳しいものにね", 1, 1, 0, 0, 4, 129, 100302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "強くなって…誰にも\n妨げられない自由な\n生き方がしたい", 1, 1, 0, 0, 5, 130, 100302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "まさか風邪を引くとは\n彼の看病のおかげで\nすっかり良く\nなったけど", 1, 8, 0, 0, 101, 131, 100302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？", 1, 8, 0, 0, 102, 132, 100302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どうしたの？\nなんでもない？\nそれなら良かった", 1, 1, 0, 0, 1, 133, 100302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あんまり突っつくと\nお返しするよ？\n覚悟は出来てる？", 1, 1, 0, 0, 2, 134, 100302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ねぇ、今度また\n釣りに行かない？\n大きいのを狙おう", 1, 1, 0, 0, 3, 135, 100302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "修行がしたいの？\nそれを早く言ってよ\nすぐに出発しよう！", 1, 1, 0, 0, 4, 136, 100302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ひょっとして\n今のも一本だなんて\n言わないよね？", 1, 8, 0, 0, 101, 137, 100302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "キミにはこれからも\n私のそばに…\nいや、なんでもない", 1, 8, 0, 0, 102, 138, 100302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日は仕事？\nそれとも修行？\n良ければ付き合うよ", 1, 1, 1, 0, 1, 139, 100302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "休息も時には\n必要だと、仲間から\n教わったよ", 1, 1, 2, 0, 2, 140, 100302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？", 1, 1, 3, 0, 3, 141, 100302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "髪の長さを少し\n変えてみようかな…\nわ、私は何を…！", 1, 1, 0, 0, 1, 142, 100302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "休息があってこそ\n万全な状態で\n戦えるんだ", 1, 1, 0, 0, 2, 143, 100302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう", 1, 1, 0, 0, 3, 144, 100302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "体と同様に\n心もほぐれるね…\nはぁ…", 1, 1, 0, 0, 4, 145, 100302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "休む時は休む\nヒヨリもそろそろ\n覚えて欲しいな…", 1, 1, 0, 0, 5, 146, 100302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！", 1, 1, 0, 0, 6, 147, 100302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "修行のせいか\n最近食べる量が\n増えてきたような…", 1, 1, 0, 0, 7, 148, 100302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ユイ…！\nこ、こんな本を\n読んでいたのか…！", 1, 1, 0, 0, 8, 149, 100302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お宝の予感！\n部屋中を\n探検しちゃうぞ～", 1, 1, 0, 0, 1, 151, 100401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "落とし穴…どこに\n作ったんだっけ？", 1, 1, 0, 0, 2, 152, 100401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな", 1, 1, 0, 0, 3, 153, 100401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？", 1, 1, 0, 0, 4, 154, 100401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "かくれんぼする人\nこの指、と～まれ！", 1, 1, 0, 0, 5, 155, 100401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ミソギ以外の人に\nイタズラされちゃ\nダメだよ！", 1, 4, 0, 0, 101, 156, 100401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ミソギと遊ぶ？\nミソギに遊ばれる？", 1, 8, 0, 0, 102, 157, 100401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "いいこと思いついた！\nい、イタズラとかじゃ\nないよ～", 1, 1, 0, 0, 1, 158, 100401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "うわっ！？\n　にいちゃんが、\nミソギにイタズラ？", 1, 1, 0, 0, 2, 159, 100401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "にいちゃんの\n通りそうなところに\nバナナの皮を…", 1, 1, 0, 0, 3, 160, 100401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…", 1, 1, 0, 0, 4, 161, 100401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ここ、ミソギ探検隊の\n基地にしようよ！", 1, 4, 0, 0, 101, 162, 100401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪", 1, 8, 0, 0, 102, 163, 100401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "にいちゃん、おはよ！\n目覚まし時計を\n止めておいたのに、\nよく起きられたね～♪", 1, 1, 1, 0, 1, 164, 100401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "にいちゃんが\n昼寝したら、\nイタズラし放題♪", 1, 1, 2, 0, 2, 165, 100401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？", 1, 1, 3, 0, 3, 166, 100401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "イタズラには\nお色気も必要…\nうん、完璧！", 1, 1, 0, 0, 1, 167, 100401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "座ったら、ブーッって\nいう音が出たら、\nおもしろいな～♪", 1, 1, 0, 0, 2, 168, 100401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいい！\nミソギ探検隊の\nマスコットにしよう♪", 1, 1, 0, 0, 3, 169, 100401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "吸うとくしゃみが\n止まらなくなるお香を\nここに…くしゅっ！", 1, 1, 0, 0, 4, 170, 100401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "にひひ…イタズラの\n証拠、ここで\n燃やしちゃおう～！", 1, 1, 0, 0, 5, 171, 100401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "探検家は\n体力勝負だよー！", 1, 1, 0, 0, 6, 172, 100401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "みんなに分けて\nあげようかな～\n激辛ソースも\n入れちゃうけどね！", 1, 1, 0, 0, 7, 173, 100401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こういう所に\n宝の地図が\n隠れてるんだよね～", 1, 1, 0, 0, 8, 174, 100401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お宝の予感！\n部屋中を\n探検しちゃうぞ～", 1, 1, 0, 0, 1, 176, 100402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "うぅ、探検に出たい！\nじっとしてなんか\nいられないよ～！", 1, 1, 0, 0, 2, 177, 100402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな", 1, 1, 0, 0, 3, 178, 100402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？", 1, 1, 0, 0, 4, 179, 100402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "かくれんぼする人\nこの指、と～まれ！", 1, 1, 0, 0, 5, 180, 100402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…", 1, 8, 0, 0, 101, 181, 100402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "にいちゃんが喜ぶ\nイタズラ、何かない\nかな～？", 1, 8, 0, 0, 102, 182, 100402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ミソギがしかけた\nイタズラ\n見つけられるかな？", 1, 1, 0, 0, 1, 183, 100402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "探検する？\n冒険する？\nそれとも…\nイタズラされる？", 1, 1, 0, 0, 2, 184, 100402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "将来の夢は探検家！\n探したい物はないけど", 1, 1, 0, 0, 3, 185, 100402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "にいちゃんに宿題！\n落とし穴を10個\n掘ること！", 1, 1, 0, 0, 4, 186, 100402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪", 1, 8, 0, 0, 101, 187, 100402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ここ、ミソギ探検隊の\n基地にしようよ！", 1, 8, 0, 0, 102, 188, 100402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "にいちゃん\n探検に行こうよ！\n今からなら遭難しても\n夜には帰れるよ！", 1, 1, 1, 0, 1, 189, 100402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "にいちゃんが昼寝したら\nイタズラし放題♪", 1, 1, 2, 0, 2, 190, 100402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？", 1, 1, 3, 0, 3, 191, 100402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "イタズラには\nお色気も必要…\nうん、完璧！", 1, 1, 0, 0, 1, 192, 100402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ゆっくりまったりして\n次のイタズラを\n考えよ～！", 1, 1, 0, 0, 2, 193, 100402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいい！\nミソギ探検隊の\nマスコットにしよう♪", 1, 1, 0, 0, 3, 194, 100402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "吸うとくしゃみが\n止まらなくなるお香を\nここに…くしゅっ！", 1, 1, 0, 0, 4, 195, 100402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "にひひ…イタズラの\n証拠、ここで\n燃やしちゃおう～！", 1, 1, 0, 0, 5, 196, 100402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "探検家は\n体力勝負だよー！", 1, 1, 0, 0, 6, 197, 100402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "探検のおやつに\nぴったりかも～！", 1, 1, 0, 0, 7, 198, 100402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こういう所に\n宝の地図が\n隠れてるんだよね～", 1, 1, 0, 0, 8, 199, 100402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "音楽は全部好きだけど\n一番好きなのは\nやっぱりジャズかな", 1, 1, 0, 0, 1, 201, 100601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "はぁ、はぁ…\n一人でもいっぱい\nしないと…\n特訓…", 1, 1, 0, 0, 2, 202, 100601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お姉ちゃんには\nもっと自信を\n持ってほしいなぁ", 1, 1, 0, 0, 3, 203, 100601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お姉ちゃんのマネ\nしちゃおうかな？", 1, 1, 0, 0, 4, 204, 100601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "双子って、\n好みのタイプも\n似ちゃうのかな？", 1, 1, 0, 0, 5, 205, 100601);
