INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そんなところ\n触っちゃダメ♪", 1, 1, 0, 0, 3, 210, 100601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お兄ちゃん、イジワル\nしないでください…", 1, 1, 0, 0, 4, 211, 100601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お兄ちゃん、もっと\n上級編のテクニック、\n教えてくださいね！", 1, 4, 0, 0, 101, 212, 100601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "コーチ、\n今日も特訓\nお願いします！", 1, 8, 0, 0, 102, 213, 100601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようございます\n昨夜は楽しかった\nですね、うふふ♪", 1, 1, 1, 0, 1, 214, 100601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "こんな明るい時間から\nす、するんですか…\n二人っきりの特訓…", 1, 1, 2, 0, 2, 215, 100601);
