INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "汗をかくと\n気持ちいいですよね", 1, 1, 0, 0, 6, 247, 100602);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お兄ちゃんの特訓に\n付き合うために\nたっぷり\n食べておかないと♪", 1, 1, 0, 0, 7, 248, 100602);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この楽譜\nサックスで吹いて\nみたいなぁ♪", 1, 1, 0, 0, 8, 249, 100602);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "プリン、プリン、\nプリン、プリン、\nプリン、プリン～♪", 1, 1, 0, 0, 1, 251, 100701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "う～ら～め～し～や～\nなの～♪", 1, 1, 0, 0, 2, 252, 100701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "プリンがないなら\n死んだほうがマシなの\nあ、もう死んでたの～", 1, 1, 0, 0, 3, 253, 100701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "幽霊の正体見たり、\nミヤコなの～", 1, 1, 0, 0, 4, 254, 100701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ミヤコがいきなり\n消えたら、みんな\n驚くかもなの～♪", 1, 1, 0, 0, 5, 255, 100701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "アイツがいて、\nプリンもある。\nきっとここが天国なの", 1, 4, 0, 0, 101, 256, 100701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "アイツはプリンセス\nナイトじゃなくて\nプリンナイトなの～", 1, 8, 0, 0, 102, 257, 100701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "姿を消して、オマエを\n見張ってやるの～\nむふふ～♪", 1, 1, 0, 0, 1, 258, 100701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もし成仏した先に\nプリンがなかったら\n地獄なの～…", 1, 1, 0, 0, 2, 259, 100701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "右にプリン左にプリン\n後ろにもプリン♪\n前にはオマエなの～！", 1, 1, 0, 0, 3, 260, 100701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "死んだ人には\nお供え物をするの～\nミヤコにも、プリンを\n毎日お供えするの～", 1, 1, 0, 0, 4, 261, 100701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "たくさんオマエと\n遊べて、\n成仏しそうなくらい\nうれしいの～！", 1, 4, 0, 0, 101, 262, 100701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "オマエのこと\n気に入ったからずっと\nとり憑いてやるの～", 1, 8, 0, 0, 102, 263, 100701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "う～　眠いの…\n二度目の二度寝を\nするの…", 1, 1, 1, 0, 1, 264, 100701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼ご飯はプリンなの\n３時のおやつも、\nプリンなの～", 1, 1, 2, 0, 2, 265, 100701);
