INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "そうだ、ナナカに\nこの間借りた本を\n返さないと…", 1, 1, 0, 0, 4, 354, 100901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "まずは包帯をとって\n手から龍の形の\n炎を出して…", 1, 1, 0, 0, 5, 355, 100901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "シグルド…\n今頃何処で何を\nしているのだろう…", 1, 4, 0, 0, 101, 356, 100901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "最近、彼の姿が\nまぶしく見える…\nどうして…？", 1, 8, 0, 0, 102, 357, 100901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "辞書を知らないか？\n…探している\n理由は聞くな", 1, 1, 0, 0, 1, 358, 100901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そうだ！\n貴様の技にも\n名前をつけてやろう！", 1, 1, 0, 0, 2, 359, 100901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私に触れても力を\n奪われないのは\n貴様くらいだぞ？", 1, 1, 0, 0, 3, 360, 100901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "何事だ？\nまさか我が瞳に\n魅入られたのか？", 1, 1, 0, 0, 4, 361, 100901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "貴様がそばにいると\nとても安心できる…\n…不思議だ", 1, 4, 0, 0, 101, 362, 100901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！", 1, 8, 0, 0, 102, 363, 100901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "「覚醒」めたか…\n行くぞ、悪を討つ\n戦いに出発だ！", 1, 1, 1, 0, 1, 364, 100901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "昼間は「ヤツら」も\n襲ってはこない…\n束の間の休息だ", 1, 1, 2, 0, 2, 365, 100901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どうした？　夜はまだ\nこれから…ふぁ…\nね、眠くなどないぞ！", 1, 1, 3, 0, 3, 366, 100901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな", 1, 1, 0, 0, 1, 367, 100901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "くっ…！\n私を眠りの国へ\n誘うつもりか！", 1, 1, 0, 0, 2, 368, 100901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "このようなものに\n懐柔されるものか！\n…されるものか！", 1, 1, 0, 0, 3, 369, 100901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ", 1, 1, 0, 0, 4, 370, 100901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これが我らの求めた\n安寧か…\n悪くない…", 1, 1, 0, 0, 5, 371, 100901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この程度\n前世での激闘に\n比べれば…！", 1, 1, 0, 0, 6, 372, 100901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "最近金欠でな…\nいやぁありがたい…", 1, 1, 0, 0, 7, 373, 100901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ここにも\n前世の記憶が\n眠っていたとは…", 1, 1, 0, 0, 8, 374, 100901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "「第１３部１章\n閃光のシグルド」\n…ついに登場だな", 1, 1, 0, 0, 1, 376, 100902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "何故だ…何故\nこうもあっさり金欠に\n逆戻りするのだ！？", 1, 1, 0, 0, 2, 377, 100902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "やはり転生者という\n設定はカッコイイ…\n天才だな、私！", 1, 1, 0, 0, 3, 378, 100902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "そうだ、ナナカに\nこの間借りた本を\n返さないと…", 1, 1, 0, 0, 4, 379, 100902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "そろそろ今日の分の\n魔力を溜めないと\nまた魔力切れが…", 1, 1, 0, 0, 5, 380, 100902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "最近奴がそばに\nいるだけで、鼓動が\n早くなるのは何故だ？", 1, 1, 0, 0, 101, 381, 100902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "シ、シグルド…！\nだめだ…妄想でも\n上手く言えない…", 1, 8, 0, 0, 102, 382, 100902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なんだ？　新たな\n賞金の情報を\n持ってきたのか？", 1, 8, 0, 0, 1, 383, 100902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "名乗りやポーズは\nその強度で相手を\n圧倒する技術だ", 1, 1, 0, 0, 2, 384, 100902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "いいか、魔法の\n詠唱中は絶対に\n声をかけるなよ？", 1, 1, 0, 0, 3, 385, 100902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おお、ちょうど今\n貴様専用の設定集を\n作成中だぞ！", 1, 1, 0, 0, 4, 386, 100902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "貴様がそばにいると\nとても安心できる…\n…不思議だ", 1, 1, 0, 0, 101, 387, 100902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！", 1, 8, 0, 0, 102, 388, 100902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜更かしのせいか\nまだ眠気が…いや！\nこれは組織の罠だ！", 1, 8, 1, 0, 1, 389, 100902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "平穏な時に\n出来るだけ魔力を\n溜めておかねば…", 1, 1, 2, 0, 2, 390, 100902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜になると、力が\n増した気分になるな\n…なりませんか？", 1, 1, 3, 0, 3, 391, 100902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな", 1, 1, 0, 0, 1, 392, 100902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お、おのれ\nヒュプノス…\n私は負けないぞ…", 1, 1, 0, 0, 2, 393, 100902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "くっくっくっ…\n貴様は特別団員に\nしてやろう", 1, 1, 0, 0, 3, 394, 100902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ", 1, 1, 0, 0, 4, 395, 100902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これが我らの求めた\n安寧か…\n悪くない…", 1, 1, 0, 0, 5, 396, 100902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "新たな戦いに備えて\n魔力だけでなく\n体力も鍛えねば！", 1, 1, 0, 0, 6, 397, 100902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "食事は魔力に\n変換する体力の\n元になるのだ！", 1, 1, 0, 0, 7, 398, 100902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "フフフ…\nこれを覚えれば\nもっと強く…！", 1, 1, 0, 0, 8, 399, 100902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "王子はんほんまに\nはんなりしてて、\n王子はんって感じやわ", 1, 1, 0, 0, 1, 401, 101001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この絵本の王子はん、\n王子はんみたいで\nええなぁ♪", 1, 1, 0, 0, 2, 402, 101001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "うちがガラスの靴を\n落としたら、王子はん\n見つけてくれるやろか", 1, 1, 0, 0, 3, 403, 101001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～", 1, 1, 0, 0, 4, 404, 101001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "王子はんのために\n糸を紡いで、\n編み物でもしまひょか", 1, 1, 0, 0, 5, 405, 101001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "マホ姫と王子はん、\n絵本の世界より\nベストカップルや", 1, 4, 0, 0, 101, 406, 101001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "王子はんと\n一緒にいると\n胸がせわしのー\nうごいてかなん", 1, 8, 0, 0, 102, 407, 101001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "うちら並んでたら\nお雛様みたいに\n見えると思いまへん？", 1, 1, 0, 0, 1, 408, 101001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "王子はんを\n国に連れて帰ったら\nみんな大歓迎して\nくれるやろなぁ", 1, 1, 0, 0, 2, 409, 101001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "王子はんは、\n絵本から出てきた人\nみたいやね～", 1, 1, 0, 0, 3, 410, 101001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "うちの話し方\nのんびりしてます\nやろか？", 1, 1, 0, 0, 4, 411, 101001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "マホ姫の手は\nつべたいから、\n王子はんが温めて？", 1, 4, 0, 0, 101, 412, 101001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？", 1, 8, 0, 0, 102, 413, 101001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "朝は小鳥はんも\nおなかがすいてるから\nよう飛び回りはるねえ", 1, 1, 1, 0, 1, 414, 101001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お茶請けはお大福が\nよろしおすけど\nクッキーもええなあ", 1, 1, 2, 0, 2, 415, 101001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "うち、普段から\n夢見がちやけど…\nほんに眠い時間やわぁ", 1, 1, 3, 0, 3, 416, 101001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "王子はんに\n会いに行くなら、\nおめかしせな\nあかんなぁ", 1, 1, 0, 0, 1, 417, 101001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はんなりした\nええお椅子やわぁ\nほっこりしますなあ", 1, 1, 0, 0, 2, 418, 101001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かいらしもんが\nいっぱいで、\nマホマホ王国みたい\nやわぁ", 1, 1, 0, 0, 3, 419, 101001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "心地ええどす。まるで\nお花畑にいるようやね", 1, 1, 0, 0, 4, 420, 101001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ぽかぽかどす。\n心まであったかく\nなりますわぁ", 1, 1, 0, 0, 5, 421, 101001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はぁはぁ、お姫はんは\n激しい運動は\nしぃひんもんどす～", 1, 1, 0, 0, 6, 422, 101001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もぐもぐ…あんまり\n速う食べられへんけど\n美味しいわぁ", 1, 1, 0, 0, 7, 423, 101001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "絵本、絵本～\n幸せやわぁ♪", 1, 1, 0, 0, 8, 424, 101001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "王子はんにとって\n自慢の姫になるえ～", 1, 1, 0, 0, 1, 426, 101002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "カボチャの馬車に\nガラスの靴…\n欲しいなぁ", 1, 1, 0, 0, 2, 427, 101002);
