INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜に飛んだり鳴いたり\nする鳥はんもおるんよ\n昼は寝てはるんかねぇ", 1, 1, 3, 0, 3, 441, 101002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "王子はんに\n会いに行くなら\nおめかしせな\nあかんなぁ", 1, 1, 0, 0, 1, 442, 101002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "王子はんも一緒に\nゆっくりしていっても\nええんよ？", 1, 1, 0, 0, 2, 443, 101002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かいらしい子やねぇ\nうちとお友だちに\nなってくれるん？", 1, 1, 0, 0, 3, 444, 101002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "癒しアイテムって\nほんま、好っきゃわぁ", 1, 1, 0, 0, 4, 445, 101002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うちのお城にも\nほしいわぁ。帰ったら\nさっそく準備せな\nあかんなぁ", 1, 1, 0, 0, 5, 446, 101002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "部屋の中で運動\nできるのも、なかなか\n面白いもんやわぁ", 1, 1, 0, 0, 6, 447, 101002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "おいひいわ～\nマホマホ王国には\nない味やわぁ", 1, 1, 0, 0, 7, 448, 101002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "しっかり知識を\n身につけんと\n魔法も使えへん\nからなぁ", 1, 1, 0, 0, 8, 449, 101002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "１、２、３、４…\nあれ？　いま、\n何段目まで\n編んだっけ？", 1, 1, 0, 0, 1, 451, 101101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "おやつのケーキは\n苺かチョコ…\nどっちも食べよう！", 1, 1, 0, 0, 2, 452, 101101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "い、いけない！\n縫い針落としちゃった", 1, 1, 0, 0, 3, 453, 101101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ケーキをクレープで\n巻いたら…\n大ヒット商品の予感！", 1, 1, 0, 0, 4, 454, 101101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "「どうだ！妹暮らし」\nってことわざ\nなかったっけ？", 1, 1, 0, 0, 5, 455, 101101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お兄ちゃんに、\nお洋服でも\n作ろうかな？", 1, 4, 0, 0, 101, 456, 101101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここがお兄ちゃんの\n部屋…\nドキドキしちゃう～", 1, 8, 0, 0, 102, 457, 101101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ねぇ、おにいちゃん\n一緒に、スイーツでも\n食べにいかない？", 1, 1, 0, 0, 1, 458, 101101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "クリームついてた！？\nさっき食べた、\nケーキかも…！", 1, 1, 0, 0, 2, 459, 101101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "勉強、見てくれるん\nですか？　やった～♪", 1, 1, 0, 0, 3, 460, 101101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "服がほつれてますよ？\n直してあげます！", 1, 1, 0, 0, 4, 461, 101101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私をお嫁さんに\nしたいなら、予約は\nお早目に、ですよ♪", 1, 4, 0, 0, 101, 462, 101101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ねぇねぇお兄ちゃん！\n…えへへ、\n呼んでみただけ♪", 1, 8, 0, 0, 102, 463, 101101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日も１日\n勉強がんばります！\n…少しは遊ぼうかな", 1, 1, 1, 0, 1, 464, 101101);
