INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいい♪\n私が服を作って\nあげよっか？", 1, 1, 0, 0, 3, 469, 101101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "リラックスできます～\nお兄ちゃんが隣に\nいるみたい！", 1, 1, 0, 0, 4, 470, 101101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "のんびりしたら、\nまた勉強しよっと！", 1, 1, 0, 0, 5, 471, 101101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "運動すると勉強も\nはかどるよね！", 1, 1, 0, 0, 6, 472, 101101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "おいし～！\nお兄ちゃんにも\nおすそ上げしますね！", 1, 1, 0, 0, 7, 473, 101101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お兄ちゃんの記憶を\n戻す方法、どこかに\n書いてないかな？", 1, 1, 0, 0, 8, 474, 101101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ケーキの食べ過ぎで\nお腹が…うぅ、\n因果横暴だよー…", 1, 1, 0, 0, 1, 476, 101102);
