INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いまならどんな試験も\n合格できそうです！", 1, 1, 0, 0, 4, 495, 101102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "のんびりしたら\nまた勉強しよっと！", 1, 1, 0, 0, 5, 496, 101102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お兄ちゃんを\n守るためには\n毎日、鍛えなくちゃ！", 1, 1, 0, 0, 6, 497, 101102);
