INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この本の服\nかわいいなぁ♪\n私に作れるかな？", 1, 1, 0, 0, 8, 499, 101102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "う～ん、今なら\nどこでだって\n眠れちゃいそう…", 1, 1, 0, 0, 1, 501, 101201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "たくさん寝すぎて、\n夢の中か現実か\nわかんなく\nなっちゃうんだよね～", 1, 1, 0, 0, 2, 502, 101201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "…はっ！？\nね、寝てないよ？\nホントだよっ", 1, 1, 0, 0, 3, 503, 101201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ぐっすり眠って\nパワーを\nフルチャージっ☆", 1, 1, 0, 0, 4, 504, 101201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "もし、超能力のことが\nバレたら…ブルブル", 1, 1, 0, 0, 5, 505, 101201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "テレパシーで\nキミの心を覗けたら\n…なんてね！", 1, 4, 0, 0, 101, 506, 101201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "キミを抱き枕に…\nじょ、冗談冗談！", 1, 8, 0, 0, 102, 507, 101201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ん～、なになに？\n一緒にお昼寝する？", 1, 1, 0, 0, 1, 508, 101201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "忘れ物しちゃった。\nま、いっか！\nえ、ダメ？", 1, 1, 0, 0, 2, 509, 101201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おおっ、相談ごと？\nなにかな\nなにかな～？", 1, 1, 0, 0, 3, 510, 101201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "う～ん、もしかして\nキミにも仲良しになる\n超能力があるのかも！", 1, 1, 0, 0, 4, 511, 101201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "むむ～…どう？\nテレパシーで、私の\n気持ちを送ったよ！", 1, 4, 0, 0, 101, 512, 101201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "えっ？　私とキミとの\n相性？　むむ～…\nうん、バッチリだよ☆", 1, 8, 0, 0, 102, 513, 101201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "まだこんな時間？\nベッドに行って、\n二度寝しなくちゃ～", 1, 1, 1, 0, 1, 514, 101201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "適度なお昼寝は\n健康にいいんだって！\n８時間くらいかな？", 1, 1, 2, 0, 2, 515, 101201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どんなにお昼寝しても\n夜は眠いんだ～", 1, 1, 3, 0, 3, 516, 101201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ん～、また寝癖…\nいっつも\n寝てるからかなぁ？", 1, 1, 0, 0, 1, 517, 101201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "一休み、一休み…\nどうせなら百休み\nくらいしようかな…", 1, 1, 0, 0, 2, 518, 101201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これ…可愛い！\nシオリンに\n届けてあげたい！", 1, 1, 0, 0, 3, 519, 101201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ん～…気分爽快！\nそれじゃ、おやすみ～\n…すやぁ…", 1, 1, 0, 0, 4, 520, 101201);
