INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "キミを抱き枕に…\nじょ、冗談冗談！", 1, 8, 0, 0, 102, 532, 101202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "忘れ物しちゃった\nま、いっか！\nえ、ダメ？", 1, 1, 0, 0, 1, 533, 101202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なに？　困ってる？\nちょうど良かった！\n今は体力満タンだよ☆", 1, 1, 0, 0, 2, 534, 101202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "一緒に\nお出かけしたいなー\nデートしよっ！", 1, 1, 0, 0, 3, 535, 101202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "うんうん、そうだねっ\n困ってる人がいたら\n見過ごせないよねっ！", 1, 1, 0, 0, 4, 536, 101202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "むむ～…どう？\nテレパシーで、私の\n気持ちを送ったよ！", 1, 8, 0, 0, 101, 537, 101202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "う～だんだん眠く…\nねぇ一緒に寝よ？", 1, 8, 0, 0, 102, 538, 101202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "キミって早起きだね！\n私は寝ようかな～", 1, 1, 1, 0, 1, 539, 101202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "適度なお昼寝は\n健康にいいんだって！\n８時間くらいかな？", 1, 1, 2, 0, 2, 540, 101202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どんなにお昼寝しても\n夜は眠いんだ～", 1, 1, 3, 0, 3, 541, 101202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ん～、また寝癖…\nいっつも\n寝てるからかなぁ？", 1, 1, 0, 0, 1, 542, 101202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "一休み、一休み…\nどうせなら百休み\nくらいしようかな…", 1, 1, 0, 0, 2, 543, 101202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これ…可愛い！\nシオリンに\n届けてあげたい！", 1, 1, 0, 0, 3, 544, 101202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ん～…気分爽快！\nそれじゃ、おやすみ～\n…すやぁ…", 1, 1, 0, 0, 4, 545, 101202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うう～ん、なんだか\nいつもより眠ーく\nなってきたよ…", 1, 1, 0, 0, 5, 546, 101202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "よーし\n困ってる人のために\nパワーアップ☆", 1, 1, 0, 0, 6, 547, 101202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "食べてすぐ寝ると\n牛になるんだっけ？\nまぁ、いいかぁ～", 1, 1, 0, 0, 7, 548, 101202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "睡眠学習って\n本を読みながら\n寝るんだっけ？", 1, 1, 0, 0, 8, 549, 101202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "モデルは楽しいよ！\nファンの子みんな\n応援してくれるしー！", 1, 1, 0, 0, 1, 551, 101601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今日のおやつは\n駄菓子に決定～！\nいただきまーす！", 1, 1, 0, 0, 2, 552, 101601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "んー、今日のおやつは\n何にしよう？\nやっぱアメ玉かな！", 1, 1, 0, 0, 3, 553, 101601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここで撮影会、\nしようかな～♪", 1, 1, 0, 0, 4, 554, 101601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "７かける８はぁ\n７＋７＋７＋７＋…\nん、何個数えたっけ？", 1, 1, 0, 0, 5, 555, 101601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "やば…\nこのままじゃ、うち\nキュン死にかも…", 1, 4, 0, 0, 101, 556, 101601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "好きなことして\nヒデサイもいて\nうちって超～\nリア充だよね♪", 1, 8, 0, 0, 102, 557, 101601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "うちの写真撮るの？\nどんなポーズ？", 1, 1, 0, 0, 1, 558, 101601);
