INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "やっさー\n踊ってると、どんどん\n楽しくなるさー！", 1, 1, 0, 0, 1, 601, 101701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "このお菓子、でーじ\nおいしいねー！\nキミも食べる？", 1, 1, 0, 0, 2, 602, 101701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ハイターイ！　元気な\nあいさつが一番さ～！", 1, 1, 0, 0, 3, 603, 101701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あまった食材があれば\nチャンプルーにして\n食べたいさー！ ", 1, 1, 0, 0, 4, 604, 101701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "さぁ、みんなで歌って\n踊るさ～♪", 1, 1, 0, 0, 5, 605, 101701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "毎日、最高に幸せさ～\nキミのおかげだねー", 1, 4, 0, 0, 101, 606, 101701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "キミへの気持ちを\n踊りで表現\nしてみるさ～", 1, 8, 0, 0, 102, 607, 101701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "海でひと泳ぎ\nしたいさ～\nキミもどう？", 1, 1, 0, 0, 1, 608, 101701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あぎひゃっ！\n急に声かけたら、\nビックリするさ～", 1, 1, 0, 0, 2, 609, 101701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "踊りなら、\n私が手とり足とり\n教えてあげるさ～！", 1, 1, 0, 0, 3, 610, 101701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "空手の型の\n練習するさー\nキミもどう？", 1, 1, 0, 0, 4, 611, 101701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "よ～し、手をつないで\n踊るさ～！\n恥ずかしがること\nないさ～", 1, 4, 0, 0, 101, 612, 101701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "キミと一緒だから、\n毎日楽しいこと\nばっかりさー", 1, 8, 0, 0, 102, 613, 101701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ハイターイ！\n今日も一日、\nがんばるさー！", 1, 1, 1, 0, 1, 614, 101701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "午後も頑張るために\nお昼ご飯は\nてびちにするさー！", 1, 1, 2, 0, 2, 615, 101701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "故郷では見えない星が\n見られるんだねえ。\nなんか不思議さぁ", 1, 1, 3, 0, 3, 616, 101701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "夢中で踊ってたら、\n髪の毛ぼさぼさに\nなってるさ～！？", 1, 1, 0, 0, 1, 617, 101701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "よく食べてよく踊って\nよく休む。\nそれが元気の秘訣さ～", 1, 1, 0, 0, 2, 618, 101701);
