INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "アカリみたいに\nかわいくなるには、\nどうすればいいのかな", 1, 1, 0, 0, 2, 802, 102201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "レアカードをゲット！\nねえねえ、\nすごいでしょ！？", 1, 1, 0, 0, 3, 803, 102201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ア、アイツと同じ\n部屋にいるのよね…\nって、だから何よ！？", 1, 1, 0, 0, 4, 804, 102201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "楽しいゲームって\nやめどきが\n見つからないわ", 1, 1, 0, 0, 5, 805, 102201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "もっと女の子らしく\nなったらアイツも\n嬉しいかな…って、\n何考えてるの！？", 1, 4, 0, 0, 101, 806, 102201);
