INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "アンタと話すのは割と\n緊張しないのよね\n…なんでかな", 1, 1, 0, 0, 2, 809, 102201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "待ちきれない…\n新作ゲームの発売日が\n待ちきれない…", 1, 1, 0, 0, 3, 810, 102201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "アンタは人見知り\nしないタチよね？\nいいなあ～…", 1, 1, 0, 0, 4, 811, 102201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ア、アンタってさ\n好きな人とかい…\nなな、何でもない！", 1, 4, 0, 0, 101, 812, 102201);
