INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここで、ぷうきち\nフルスイングは、\nまずいかな～？", 1, 1, 0, 0, 4, 854, 102301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この部屋、ぷうきちを\n置く場所はないのかな", 1, 1, 0, 0, 5, 855, 102301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お兄ちゃんって…\nちょっとぷうきちに\n似てるかも♪", 1, 4, 0, 0, 101, 856, 102301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お兄ちゃんにも、\nぷうきちのこと\n好きになって\nもらいたいな～", 1, 8, 0, 0, 102, 857, 102301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おやつは、\n私とぷうきちの\n二人分用意してね♪", 1, 1, 0, 0, 1, 858, 102301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ぷうきちが、お腹\nペコペコだって～\n何かちょうだい！", 1, 1, 0, 0, 2, 859, 102301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "『アヤネに手出したら\n…坊ちゃんでも、\n分かってんな？』", 1, 1, 0, 0, 3, 860, 102301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ぷうきちとの\n連携を磨いてるの！\nもっと仲良くなるよ！", 1, 1, 0, 0, 4, 861, 102301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "特別だからね？\nもっと触っていいよ\n…ぷうきちのこと", 1, 4, 0, 0, 101, 862, 102301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お兄ちゃんのこと、\n「ぷう兄」って\n呼ぼうかな？", 1, 8, 0, 0, 102, 863, 102301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お兄ちゃんって\n朝早いんだね～\n私はまだ眠いよ～", 1, 1, 1, 0, 1, 864, 102301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふぁ～…ぷうきちと\nお昼寝してくるね…", 1, 1, 2, 0, 2, 865, 102301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ぷうきちと\nおしゃべりしてたら\nもうこんな時間…", 1, 1, 3, 0, 3, 866, 102301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "『座り心地がいいな』\nぷうきちは座れない\nでしょ？", 1, 1, 0, 0, 2, 868, 102301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいい～！\nぷうきちのお友達に\nしちゃおうかな？", 1, 1, 0, 0, 3, 869, 102301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いいニオイ～♪\nぷうきちも\n気に入ったみたい！", 1, 1, 0, 0, 4, 870, 102301);
