INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ファンはもちろん\n大事だけど…\n彼のことは、もっと\n大切にしたいな…", 1, 8, 0, 0, 101, 1081, 102902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "トップアイドルの\nさらに先…\n彼とならその景色も\n見られるかな", 1, 8, 0, 0, 102, 1082, 102902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "新曲の衣装、もうすぐ\n出来あがるんだって！\n楽しみだな～", 1, 1, 0, 0, 1, 1083, 102902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "アイドルノゾミだけ\nじゃなくて、私の事も\nちゃんと見てね？", 1, 1, 0, 0, 2, 1084, 102902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "キミに響かないなら\nファンにも響くわけ\nないよ！　練習練習！", 1, 1, 0, 0, 3, 1085, 102902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "衣装のスカートは\n鉄壁なんだよ！\n残念でした～", 1, 1, 0, 0, 4, 1086, 102902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "新曲、聞いてくれた？\nキミとのことを\nテーマにしたんだよ\nどこかは秘密だけど♪", 1, 8, 0, 0, 101, 1087, 102902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ねぇ、私がトップ\nアイドルになっても\nちゃんと隣にいてね\n遠くに行かないでね？", 1, 8, 0, 0, 102, 1088, 102902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "朝の発声練習に\n行くけど、キミも\n一緒に来ない？", 1, 1, 1, 0, 1, 1089, 102902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼ごはん行かない？\n誰にも知られてない\nお店があるんだけど！", 1, 1, 2, 0, 2, 1090, 102902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜の帳が二人を包み\n…こういう方向性の\n歌詞も悪くないなぁ", 1, 1, 3, 0, 3, 1091, 102902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "身だしなみオッケー！\nアイドルはいつでも\n完璧でいなくちゃ！", 1, 1, 0, 0, 1, 1092, 102902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふぅ…\nこんな姿、ファンには\n見せられないな～", 1, 1, 0, 0, 2, 1093, 102902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいい～♪\nこれは強力な\nライバル登場かな！", 1, 1, 0, 0, 3, 1094, 102902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "みんなのイメージする\nアイドルの香りは\nこんな感じかな～", 1, 1, 0, 0, 4, 1095, 102902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "体も暖まったし！\nダンスレッスンに\n行こうかな♪", 1, 1, 0, 0, 5, 1096, 102902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ベストタイム更新！\nライブに向けて\n体力つけないと…", 1, 1, 0, 0, 6, 1097, 102902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "太っちゃったら\nどうしよう…？\nでも手が止まらない～", 1, 1, 0, 0, 7, 1098, 102902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この恋愛小説ステキ！\n私もアイドルじゃ\nなかったら…", 1, 1, 0, 0, 8, 1099, 102902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "手裏剣の数が\n合わないデス！\nどこかに落とした\nデスか…？", 1, 1, 0, 0, 1, 1101, 103001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "クセモノ、隠れても\nむだデス！\n…気のせいデシタ", 1, 1, 0, 0, 2, 1102, 103001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "金は天下の\nマワリモノ！\nところで、\nどういう意味デス？", 1, 1, 0, 0, 3, 1103, 103001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "むむ、アクダイカンの\n気配がするデース", 1, 1, 0, 0, 4, 1104, 103001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "クニトリ合戦、\n楽しみデース！", 1, 1, 0, 0, 5, 1105, 103001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ショーグンと\n出会えて、\nワタシは果報者デス！", 1, 4, 0, 0, 101, 1106, 103001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "最近、ショーグンの\nそばにいると\nドキドキするデス…", 1, 8, 0, 0, 102, 1107, 103001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なんでも\n命令するデス！\nショーグンの命は\n絶対デス！", 1, 1, 0, 0, 1, 1108, 103001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あっ、ショーグン、\n出陣はいつで\nゴザルデス！？", 1, 1, 0, 0, 2, 1109, 103001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "さ、さすが\nショーグンデス…\nいつの間に背後を！？", 1, 1, 0, 0, 3, 1110, 103001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "出陣の合図に\n備えるデス！\nほら貝は\nどこデスか！？", 1, 1, 0, 0, 4, 1111, 103001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "見つめられると\nドキドキするデス…\n新しい忍法デスか？", 1, 4, 0, 0, 101, 1112, 103001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ショーグン！\nえへへ…これからも\nおそばに置いて\nクダサイね？", 1, 8, 0, 0, 102, 1113, 103001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようデス！\n今日も一日\n忍法日和デス！", 1, 1, 1, 0, 1, 1114, 103001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そろそろお腹\n空いてきたデス。\n昼ごはんにするデス！", 1, 1, 2, 0, 2, 1115, 103001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "明日もきっと\nいい日デスよ、\nショーグン！", 1, 1, 3, 0, 3, 1116, 103001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ちょんまげに\nしてみるのも\n面白いかもデス！", 1, 1, 0, 0, 1, 1117, 103001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "心地よくて\n眠くなってきマス…\nもしやテキコクの\n罠デスか！？", 1, 1, 0, 0, 2, 1118, 103001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これを変わり身の\n術に…かわいそう\nだから止めるデス", 1, 1, 0, 0, 3, 1119, 103001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ワビサビというもの\nデスね！\n素晴らしいデス！", 1, 1, 0, 0, 4, 1120, 103001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "身体を暖めて\nイクサに向かうデス！", 1, 1, 0, 0, 5, 1121, 103001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "アクロバットな忍法を\n使うには、体を\n鍛えないと危険デス！", 1, 1, 0, 0, 6, 1122, 103001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うーん、\n美味しいデス…\n幸せデス～", 1, 1, 0, 0, 7, 1123, 103001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これは…\nＢＵＳＨＩＤＯ…\nデスか？", 1, 1, 0, 0, 8, 1124, 103001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ふんふんふ～ん\n武器の手入れは\n念入りにしマスよ～", 1, 1, 0, 0, 1, 1126, 103002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "クセモノ、隠れても\nむだデス！\n…気のせいデシタ", 1, 1, 0, 0, 2, 1127, 103002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "金は天下の\nマワリモノ！\nところで\nどういう意味デス？", 1, 1, 0, 0, 3, 1128, 103002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "むむ、アクダイカンの\n気配がするデース", 1, 1, 0, 0, 4, 1129, 103002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "クニトリ合戦\n楽しみデース！", 1, 1, 0, 0, 5, 1130, 103002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "最近、ショーグンの\nそばにいると\nドキドキするデス…", 1, 8, 0, 0, 101, 1131, 103002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ショーグンの身は\n何があってもワタシが\n守るデス！", 1, 8, 0, 0, 102, 1132, 103002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あっ、ショーグン\n出陣はいつで\nゴザルデス！？", 1, 1, 0, 0, 1, 1133, 103002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "イクサの前に\nホネ休めも大事デスよ\nゆっくりするデース！", 1, 1, 0, 0, 2, 1134, 103002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "さ、さすがショーグン\nデス…いつの間に\n背後を！？", 1, 1, 0, 0, 3, 1135, 103002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "一緒に忍法考える\nデス！\nショーグン用の\n忍法も必要デスね！", 1, 1, 0, 0, 4, 1136, 103002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "見つめられると\nドキドキするデス…\n新しい忍法デスか？", 1, 8, 0, 0, 101, 1137, 103002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ショーグン！\nえへへ…これからも\nおそばに置いて\nクダサイね？", 1, 8, 0, 0, 102, 1138, 103002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "いい朝デス！\nトーゴク風の朝ゴハン\n作るデス！", 1, 1, 1, 0, 1, 1139, 103002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もうお昼デス\nお昼寝するデスか？", 1, 1, 2, 0, 2, 1140, 103002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そろそろおネム\nデース…\n一緒に寝るデス？", 1, 1, 3, 0, 3, 1141, 103002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "服の乱れは心の乱れ\nデス！　きっちり\nしないといけないデス", 1, 1, 0, 0, 1, 1142, 103002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "心地よくて眠く\nなってきマス…\nもしやテキコクの\n罠デスか！？", 1, 1, 0, 0, 2, 1143, 103002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これを変わり身の\n術に…かわいそう\nだから止めるデス", 1, 1, 0, 0, 3, 1144, 103002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ワビサビというもの\nデスね！\n素晴らしいデス！", 1, 1, 0, 0, 4, 1145, 103002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "身体を暖めてイクサに\n向かうデス！", 1, 1, 0, 0, 5, 1146, 103002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ショーグンのお役に\n立つため、修行\nあるのみデス！", 1, 1, 0, 0, 6, 1147, 103002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "美味しいデス…\n酢昆布を和えて\nみたいデス", 1, 1, 0, 0, 7, 1148, 103002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "物事を知って\n来たるべきイクサに\n活かすデス", 1, 1, 0, 0, 8, 1149, 103002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お父さん、そろそろ\n子離れを…しない？\nそうですか…", 1, 1, 0, 0, 1, 1151, 103101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "父は、いつも男性に\nきびしいですね", 1, 1, 0, 0, 2, 1152, 103101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "占ってほしい人は、\nいつでも言って\nください", 1, 1, 0, 0, 3, 1153, 103101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あなたの手相は…\nて、手を握るのって\n緊張しますね", 1, 1, 0, 0, 4, 1154, 103101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この部屋、風水的にも\n運気が上がりそうです", 1, 1, 0, 0, 5, 1155, 103101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あの人とのこと占って\nみようかな…\n好き…嫌い…\n好き♪…き、嫌い", 1, 4, 0, 0, 101, 1156, 103101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "私との相性ですか？\nそれなら占う必要は\nありませんよ…\nふふっ♪", 1, 8, 0, 0, 102, 1157, 103101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "女難の相が\n出ていますね…\n気をつけてください", 1, 1, 0, 0, 1, 1158, 103101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日のあなたの運勢は\n大きょ…\n大吉です", 1, 1, 0, 0, 2, 1159, 103101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "父と散歩中です。\n毎日決まったコースを\n散歩させないと、\nストレスが溜まるので", 1, 1, 0, 0, 3, 1160, 103101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "小僧！\nシノブに触るとは\nいい度胸じゃねぇか！", 1, 1, 0, 0, 4, 1161, 103101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたに触られると、\n動悸が早くなります\nもしかして心霊現象？", 1, 4, 0, 0, 101, 1162, 103101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日もたくさんの幽霊\nに慕われていますね。\n素敵です", 1, 8, 0, 0, 102, 1163, 103101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようございます\n今日の運勢を\n占いましょうか？", 1, 1, 1, 0, 1, 1164, 103101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日のラッキー\nアイテムはドクロです\n良ければ父を\nお貸ししましょうか？", 1, 1, 2, 0, 2, 1165, 103101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日のあなたの運気は\n最高潮でした。\nもうすぐ今日は\n終わりますが", 1, 1, 3, 0, 3, 1166, 103101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お父さん。\nいつも、身だしなみに\n気を使ってるけど、\nあまり変わってないよ", 1, 1, 0, 0, 1, 1167, 103101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "一休みしましょう。\n憑かれて疲れました", 1, 1, 0, 0, 2, 1168, 103101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいい…\n付喪神が宿らないかな", 1, 1, 0, 0, 3, 1169, 103101);
