INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "気持ちいいです…\n極楽浄土みたい…", 1, 1, 0, 0, 5, 1171, 103101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はぁはぁ…\nお、お父さん、\n代わりに運動して…", 1, 1, 0, 0, 6, 1172, 103101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お父さんも\n食べたいの？\n死んでるのに？", 1, 1, 0, 0, 7, 1173, 103101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この本…\nもしかして予言書？", 1, 1, 0, 0, 8, 1174, 103101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここにいる幽霊は\nみんな幸せそうです", 1, 1, 0, 0, 1, 1176, 103102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お父さんが\n女の子ばかりに\n声をかけるので\n困っています", 1, 1, 0, 0, 2, 1177, 103102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "成仏はうれしいこと\nです…でも友達が\n減るのは寂しいですね", 1, 1, 0, 0, 3, 1178, 103102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "幽霊に好かれる人は\n素敵な方です\nもちろん、あなたも", 1, 1, 0, 0, 4, 1179, 103102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お父さん、そろそろ\n子離れを…しない？\nそうですか…", 1, 1, 0, 0, 5, 1180, 103102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "生きている人に\n好かれるには、\nどうすればいい\nのでしょう？", 1, 8, 0, 0, 101, 1181, 103102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お父さん、どうしたら\nあの人のことを\n認めてくれますか？", 1, 8, 0, 0, 102, 1182, 103102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この部屋、風水的にも\n運気が上がりそうです", 1, 1, 0, 0, 1, 1183, 103102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "シノブには指一本\n触れさせん！\nガブッ！！", 1, 1, 0, 0, 2, 1184, 103102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたの手相は…\nて、手を握るのって\n緊張しますね", 1, 1, 0, 0, 3, 1185, 103102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ラップ音？\nいえ、父のいびきです", 1, 1, 0, 0, 4, 1186, 103102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私との相性ですか？\nそれなら占う必要は\nありませんよ…\nふふっ♪", 1, 8, 0, 0, 101, 1187, 103102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "前世って信じますか？\n私とあなたは\nもしかすると…", 1, 8, 0, 0, 102, 1188, 103102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日のラッキー\nアイテムはドクロです\n良ければ父を\nお貸ししましょうか？", 1, 1, 1, 0, 1, 1189, 103102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "明るい時間は\nあまり得意では\nないんです…", 1, 1, 2, 0, 2, 1190, 103102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜のお散歩に\n行きませんか？\n知り合いの幽霊に\n挨拶して回るんです", 1, 1, 3, 0, 3, 1191, 103102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お父さん\nいつも、身だしなみに\n気を使ってるけど\nあまり変わってないよ", 1, 1, 0, 0, 1, 1192, 103102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うとうと…\nあれ…\n走馬灯が見える…", 1, 1, 0, 0, 2, 1193, 103102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいい…\nお父さんみたい…", 1, 1, 0, 0, 3, 1194, 103102);
