INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "快適です…\n幽霊もどんどん\n集まってきますね…", 1, 1, 0, 0, 5, 1196, 103102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はぁはぁ…\nお、お父さん、\n代わりに運動して…", 1, 1, 0, 0, 6, 1197, 103102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お父さんの分も、\n私が食べるね", 1, 1, 0, 0, 7, 1198, 103102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この本の占い\n私の占いと違う…", 1, 1, 0, 0, 8, 1199, 103102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "私だって、一人で\n買い物ぐらい\nできますわ…きっと", 1, 1, 0, 0, 1, 1201, 103201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "そろそろ新しい\n案件を始めないと\nいけませんわね", 1, 1, 0, 0, 2, 1202, 103201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "一日も早く、\n一人前の商人に\nなりたいものです", 1, 1, 0, 0, 3, 1203, 103201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "平和を買うのには\nどれほどのお金が\n必要なのかしら？", 1, 1, 0, 0, 4, 1204, 103201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここももう少し広いと\n落ち着くのですけど。\n改築しませんこと？", 1, 1, 0, 0, 5, 1205, 103201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あの方はお金で雇った\nわけではないのに\n私といてくださる…", 1, 4, 0, 0, 101, 1206, 103201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "たとえ私が間違っても\nきっとあの方が\n正してくれますわ…", 1, 8, 0, 0, 102, 1207, 103201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私と、庶民向けの\nお店へ一緒に\n参りませんか？", 1, 1, 0, 0, 1, 1208, 103201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "資産運用？\nお幾らほど\n増やしたいのです？", 1, 1, 0, 0, 2, 1209, 103201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どんぶり勘定だとよく\n言われるのですが…\n例えが小さすぎます！", 1, 1, 0, 0, 3, 1210, 103201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お金で解決するのは\n悪いことでは\nありませんわよね？", 1, 1, 0, 0, 4, 1211, 103201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "その、よろしければ\n私とお散歩など\nいかがです？", 1, 4, 0, 0, 101, 1212, 103201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もしあなたさまが\n道に迷われるなら、\n私もお供いたします！", 1, 8, 0, 0, 102, 1213, 103201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "早起きは三ルピの徳と\n申しますが…\nもっとお得ですわね", 1, 1, 1, 0, 1, 1214, 103201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "昼食は町の屋台で\n立ったまま食べて\nみたいですわ！", 1, 1, 2, 0, 2, 1215, 103201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "よろしければ\nお休み前のミルクを\n御一緒にいかが？", 1, 1, 3, 0, 3, 1216, 103201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "だらしない身なりでは\n取引相手に信用して\nいただけませんものね", 1, 1, 0, 0, 1, 1217, 103201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この感触と安らぎ…\n素晴らしい\n費用対効果ですわ…", 1, 1, 0, 0, 2, 1218, 103201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "何の見返りも求めず\nただひたすら愛でて\nいたいのです！", 1, 1, 0, 0, 3, 1219, 103201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "目に見えないものに\nお金をかけるのが\n真の贅沢ですわ！", 1, 1, 0, 0, 4, 1220, 103201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "誰ひとり寒い思いを\nしない町にするために\n私にできる事は…", 1, 1, 0, 0, 5, 1221, 103201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "健康はお金では\n買えませんが\n運動器具は別ですわ！", 1, 1, 0, 0, 6, 1222, 103201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "なかなか\n美味ですわ\nシェフはどちらに？", 1, 1, 0, 0, 7, 1223, 103201);
