INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あんちゃんも、\nエリザベスと同じ位\nめんこいべ♪", 1, 4, 0, 0, 101, 1256, 103301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "オラとあんちゃん、\n息がぴったりに\nなってきたべ…！", 1, 8, 0, 0, 102, 1257, 103301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おぉ！　そんな急に\nツッコまれたら、\nびっくらこくべやー！", 1, 1, 0, 0, 1, 1258, 103301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ネタはオラが書くけど\nあんちゃんが\n書きたかったら\n書いてもいいんだぞ？", 1, 1, 0, 0, 2, 1259, 103301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！", 1, 1, 0, 0, 3, 1260, 103301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "押すなよ、\n絶対押すなよ\n…なんて\n言ってないべさ？", 1, 1, 0, 0, 4, 1261, 103301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～", 1, 4, 0, 0, 101, 1262, 103301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "将来的には夫婦漫才\nってのも…あ、ひ、\nひとりごとだべさ～！", 1, 8, 0, 0, 102, 1263, 103301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよう！　一緒に\n牛の乳しぼりいくべ？", 1, 1, 1, 0, 1, 1264, 103301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼だ～腹が減っては\n笑いはできぬ～！", 1, 1, 2, 0, 2, 1265, 103301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日もよく働いたべ！\nさ、今からは\nお笑いの練習だ～！", 1, 1, 3, 0, 3, 1266, 103301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "舞台に上がる前に\n身なりは整えねぇと\nだめだべ～！", 1, 1, 0, 0, 1, 1267, 103301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ナイスなイスに\n座りたい、\nなんちゃって！", 1, 1, 0, 0, 2, 1268, 103301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "めんこいのは、\nスベっても許される\nからずるいべな～？", 1, 1, 0, 0, 3, 1269, 103301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "必要以上に\n癒されちまうべや～", 1, 1, 0, 0, 4, 1270, 103301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ぽかぽかだ…\nウケた会場みてぇだ", 1, 1, 0, 0, 5, 1271, 103301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お笑い芸人は、\n体が資本だべさ～！", 1, 1, 0, 0, 6, 1272, 103301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～", 1, 1, 0, 0, 7, 1273, 103301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ネタ作りのヒントが\n載ってねぇか探すべ", 1, 1, 0, 0, 8, 1274, 103301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "さぁ今日も張り切って\n漫才の練習するべさ！", 1, 1, 0, 0, 1, 1276, 103302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この部屋でも、商品\n売っていいんだべか？", 1, 1, 0, 0, 2, 1277, 103302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "眼鏡を頭の上に乗せて\n「メガネメガネ～」\nって言う練習した方が\nいいべか？", 1, 1, 0, 0, 3, 1278, 103302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "漫才を持ち時間に\n収めるには、何度も\n練習するしかねぇべ", 1, 1, 0, 0, 4, 1279, 103302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "エリザベスも\n連れてきてぇ～", 1, 1, 0, 0, 5, 1280, 103302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～", 1, 8, 0, 0, 101, 1281, 103302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あんちゃんに\n採れたての食材を\n食わしてやりてぇ～", 1, 8, 0, 0, 102, 1282, 103302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "押すなよ\n絶対押すなよ\n…なんて\n言ってないべさ？", 1, 1, 0, 0, 1, 1283, 103302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あんちゃん、百連\nツッコミ特訓だ～！", 1, 1, 0, 0, 2, 1284, 103302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！", 1, 1, 0, 0, 3, 1285, 103302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "「なんでだべ～！」\nって言いながら\nツッコむといいべ！", 1, 1, 0, 0, 4, 1286, 103302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あはは～あんちゃんの\nツッコミはやっぱり\n気持ちいいなぁ～", 1, 8, 0, 0, 101, 1287, 103302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "将来的には夫婦漫才\nってのも…あ、ひ\nひとりごとだべさ～！", 1, 8, 0, 0, 102, 1288, 103302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよう！\n今日もいい\nネタ作り日和だ！", 1, 1, 1, 0, 1, 1289, 103302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼だな\nご飯を食べたら、\nネタの練習するべ！", 1, 1, 2, 0, 2, 1290, 103302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もうこんな時間だべ\nネタ作りは\n明日にするべ…", 1, 1, 3, 0, 3, 1291, 103302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うーん…衣装、\nずれてねぇべか？", 1, 1, 0, 0, 1, 1292, 103302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ナイスなイスに\n座りたい\nなんちゃって！", 1, 1, 0, 0, 2, 1293, 103302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はう～\nなまらめんこいべや～", 1, 1, 0, 0, 3, 1294, 103302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "必要以上に\n癒されちまうべや～", 1, 1, 0, 0, 4, 1295, 103302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "笑いをとることを\n忘れるほど\nあったけぇべ～", 1, 1, 0, 0, 5, 1296, 103302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "たくさん運動して\n動きでも笑いがとれる\nように頑張るべ～！", 1, 1, 0, 0, 6, 1297, 103302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～", 1, 1, 0, 0, 7, 1298, 103302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "幅広く教養を\n身につけて\nいいネタ作るべ！", 1, 1, 0, 0, 8, 1299, 103302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "通りにいる\nカップル…爆発\nすればいいのに…", 1, 1, 0, 0, 1, 1301, 103401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "う～ん、仕事なんて\nしないで、\n飲んでたいな～", 1, 1, 0, 0, 2, 1302, 103401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今日は一日、\n外に出ない！\nもう決めたわ！", 1, 1, 0, 0, 3, 1303, 103401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お腹空いた…\nけど、外に出るのは\n面倒だなぁ", 1, 1, 0, 0, 4, 1304, 103401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "うぅ…頭がガンガン\nする…昨日、\n飲み過ぎたかな…", 1, 1, 0, 0, 5, 1305, 103401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "デートとか…\n誘っても嫌がられ\nないかな…？", 1, 4, 0, 0, 101, 1306, 103401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "もしかして運命の\n相手だったり…\nするのかな", 1, 8, 0, 0, 102, 1307, 103401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "やっぱり一杯目が\n一番おいしいわ～！\n…え、見てた！？", 1, 1, 0, 0, 1, 1308, 103401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "頼れるお姉さんだって\nところ、もっと見せて\nあげるからね", 1, 1, 0, 0, 2, 1309, 103401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ねえ、一緒に買い物に\n行かない？　その…\nの、飲み物とか…", 1, 1, 0, 0, 3, 1310, 103401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ひっく、うふふ…\nもっとお姉さんと\nお話しようよ～", 1, 1, 0, 0, 4, 1311, 103401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…", 1, 4, 0, 0, 101, 1312, 103401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "いつか…\nキミと一緒に飲めたら\n楽しいだろうな", 1, 8, 0, 0, 102, 1313, 103401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよう…朝は\nちょっとつらいな…\n低血圧なのよ…", 1, 1, 1, 0, 1, 1314, 103401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おっひるー♪\nなに食べよっかな～♪", 1, 1, 2, 0, 2, 1315, 103401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふわあ…\n眠る前にちょっと\n一杯…冗談よ", 1, 1, 3, 0, 3, 1316, 103401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うう…\n髪がボサボサ…\nちゃんとしなきゃ…", 1, 1, 0, 0, 1, 1317, 103401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "自然体でゴロゴロ\nするのって\n幸せだなぁ…", 1, 1, 0, 0, 2, 1318, 103401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こういうの男の子に\nプレゼントして\nもらいたいな…", 1, 1, 0, 0, 3, 1319, 103401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うわあ、\nこれ、私の好きな\n香りだー！", 1, 1, 0, 0, 4, 1320, 103401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ぽっかぽか…\nあとは熱い一杯が\nあれば…！", 1, 1, 0, 0, 5, 1321, 103401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "家でゴロゴロして\n付いちゃったお肉を\n落とさないと…！", 1, 1, 0, 0, 6, 1322, 103401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もぐもぐ…\nおーいしー！\n…太らないよね？", 1, 1, 0, 0, 7, 1323, 103401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ", 1, 1, 0, 0, 8, 1324, 103401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "仕事終わりの一杯の\nために、今日も\n頑張らないと", 1, 1, 0, 0, 1, 1326, 103402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今日は\nのんびり\n過ごそうかな～", 1, 1, 0, 0, 2, 1327, 103402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あ…\nお皿、洗って\nなかった…", 1, 1, 0, 0, 3, 1328, 103402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "もう少し、大人の\n魅力が出せるように\nなりたいな", 1, 1, 0, 0, 4, 1329, 103402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "素敵な彼氏…\n欲しいなぁ…", 1, 1, 0, 0, 5, 1330, 103402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "彼と私\n釣り合うかな…\n大丈夫よね？", 1, 8, 0, 0, 101, 1331, 103402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今後のために\nちょっとは花嫁修業\nしてみようかな…？", 1, 8, 0, 0, 102, 1332, 103402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "何事も\nお姉さんと一緒に\n楽しく頑張りましょ？", 1, 1, 0, 0, 1, 1333, 103402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ね、ねえ！\nキミは年上と年下\nどっちが好きかな？", 1, 1, 0, 0, 2, 1334, 103402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もう…\n構ってくれないと\n泣いちゃうぞ～？", 1, 1, 0, 0, 3, 1335, 103402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お姉さんが～\n可愛がって\nあげようか～？", 1, 1, 0, 0, 4, 1336, 103402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…", 1, 8, 0, 0, 101, 1337, 103402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お姉さんとチュー\nする？　ふふふ～\n冗談で～す！", 1, 8, 0, 0, 102, 1338, 103402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "さーて、今日も\nいっちょやるぞー！", 1, 1, 1, 0, 1, 1339, 103402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "後でのんびり\nストレッチでも\nしようっと♪", 1, 1, 2, 0, 2, 1340, 103402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "一日よく働いた～！\n自分にごほうび\nあげたいなー♪", 1, 1, 3, 0, 3, 1341, 103402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "仕事の前に、髪とか\n整えないと…", 1, 1, 0, 0, 1, 1342, 103402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "自然体でゴロゴロ\nするのって\n幸せだなぁ…", 1, 1, 0, 0, 2, 1343, 103402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もう大人だし…\nあんまり可愛いのは\n似合わないかな…？", 1, 1, 0, 0, 3, 1344, 103402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うわあ\nこれ、私の好きな\n香りだー！", 1, 1, 0, 0, 4, 1345, 103402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったかーい…\nだ、だらけ\nちゃいそう…", 1, 1, 0, 0, 5, 1346, 103402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "地道な努力が\nリア充への\n第一歩！", 1, 1, 0, 0, 6, 1347, 103402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "おーいしっ♪\nこのために\n生きてるわねえ", 1, 1, 0, 0, 7, 1348, 103402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ", 1, 1, 0, 0, 8, 1349, 103402);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "大切な人に\n本をすすめるのって\nけっこう難しいなぁ", 1, 1, 0, 0, 1, 1351, 103801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "き、きらーん☆\nお姉ちゃんに\n似てるかな…？", 1, 1, 0, 0, 2, 1352, 103801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "そろそろ好きな\n作家さんの新刊が\n出るから楽しみ…", 1, 1, 0, 0, 3, 1353, 103801);
