INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "むかしむかしある所に\nシオリと\nあの人が…", 1, 4, 0, 0, 101, 1356, 103801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この本の主人公、\nあの人に\n似ているかも…\nかっこいいな", 1, 8, 0, 0, 102, 1357, 103801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ケホケホ…\nコンコン…\nゴホッゴホッ…\nへ、平気ですよ？", 1, 1, 0, 0, 1, 1358, 103801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あ…お姉ちゃんと\n約束があったんだ…", 1, 1, 0, 0, 2, 1359, 103801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "しおりを探している？\nあ、本に挟む方ですね\nドキドキしました…", 1, 1, 0, 0, 3, 1360, 103801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "最近調子がいいのは\n牧場の牛乳やチーズの\nおかげでしょうか？", 1, 1, 0, 0, 4, 1361, 103801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "読みたい本も\nあなたと行きたい所も\n増えるばっかりです", 1, 4, 0, 0, 101, 1362, 103801);
