INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "寝てばかりなので、\n髪が乱れてないかな\n…？　", 1, 1, 0, 0, 1, 1392, 103802);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "少し休もう\n…やっぱり\nたくさん休もう", 1, 1, 0, 0, 2, 1393, 103802);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この子がいれば\n病室に一人でも\n寂しくないですね", 1, 1, 0, 0, 3, 1394, 103802);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いろんな効能が？\n健康になる香りも\nあるのでしょうか？", 1, 1, 0, 0, 4, 1395, 103802);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こうして温まって\nいると、病気も\n治る気がします", 1, 1, 0, 0, 5, 1396, 103802);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "私も、少しは\n体力をつけないと\nいけませんよね　", 1, 1, 0, 0, 6, 1397, 103802);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いっぱい食べて\n健康な体にならないと", 1, 1, 0, 0, 7, 1398, 103802);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "続きが気になって\n読むのを止める\nタイミングが…", 1, 1, 0, 0, 8, 1399, 103802);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "友達ができたら、\n山の上でお弁当…\n夢が広がります…", 1, 1, 0, 0, 1, 1401, 104001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あ、忘れずに\nお花に水を\nあげなきゃ…", 1, 1, 0, 0, 2, 1402, 104001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お菓子を作って、\nみなさんに振る舞って\n友達作戦！　とか…", 1, 1, 0, 0, 3, 1403, 104001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "そろそろ新しい\n草人形を作って\n友達作りの特訓を…", 1, 1, 0, 0, 4, 1404, 104001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "植物のお世話って、\nぼっちにとっては、\n救い…なんです…", 1, 1, 0, 0, 5, 1405, 104001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ＢＢ団の活動日…\nまだかな…\nうふふふっ", 1, 4, 0, 0, 101, 1406, 104001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…", 1, 8, 0, 0, 102, 1407, 104001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あ、こんにちは！\n今日もＢＢ団で\nお世話になります！", 1, 1, 0, 0, 1, 1408, 104001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あの…私の友達作りの\n練習方法、間違って\nませんよね…？", 1, 1, 0, 0, 2, 1409, 104001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そうだ、今度、\n森に一緒に木の苗を\n植えに行きませんか？", 1, 1, 0, 0, 3, 1410, 104001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "人とお話するのって、\n私、大好きです！\n友達ふやすぞ～！", 1, 1, 0, 0, 4, 1411, 104001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたと話してると、\nまるで私がぼっちじゃ\nないみたいです！", 1, 4, 0, 0, 101, 1412, 104001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです", 1, 8, 0, 0, 102, 1413, 104001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようございます！\n今日もＢＢ団、\nがんばるぞー！", 1, 1, 1, 0, 1, 1414, 104001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なんだか、お腹が\n空いてきました。\nお昼ごはんー♪", 1, 1, 2, 0, 2, 1415, 104001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もう眠いかもです…\n明日は友達、\nできるといいなあ…", 1, 1, 3, 0, 3, 1416, 104001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "私がもっと\n可愛ければ、\n友達できるかなあ？", 1, 1, 0, 0, 1, 1417, 104001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふわあ…のんびり\nしすぎないように\n気を付けなきゃです", 1, 1, 0, 0, 2, 1418, 104001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいいなあ。\nこの子とも\n友達になりたい…", 1, 1, 0, 0, 3, 1419, 104001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい香りです…\n精神統一して\n友達のイメトレ…", 1, 1, 0, 0, 4, 1420, 104001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "灯りを見てると、\n心がなごみます～", 1, 1, 0, 0, 5, 1421, 104001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！", 1, 1, 0, 0, 6, 1422, 104001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "おいしいです…\n私に友達がいたら、\n分けてあげたいな", 1, 1, 0, 0, 7, 1423, 104001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "友達作りの\n参考になりそうなこと\n書いてないかな～？", 1, 1, 0, 0, 8, 1424, 104001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "友達作りは、焦らず\n慌てず、じっくりと…", 1, 1, 0, 0, 1, 1426, 104002);
