INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ぼっちでもいい\nたくましく育てば\n…なんて", 1, 1, 0, 0, 5, 1430, 104002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "最近、毎日あの人の\nこと考えちゃいます\nどうしてだろう…", 1, 8, 0, 0, 101, 1431, 104002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…", 1, 8, 0, 0, 102, 1432, 104002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あ！　ＢＢ団の活動\nですか？　嬉しくて\n飛び跳ねちゃいます！", 1, 1, 0, 0, 1, 1433, 104002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたとのお話で友達\n作りの練習になるなら\n一石二鳥です！", 1, 1, 0, 0, 2, 1434, 104002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私少しは人と話しても\nテンパらなくなって\nきましたかね…？", 1, 1, 0, 0, 3, 1435, 104002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あの、友達作りの\n新しい作戦\n考えてみたんですっ！", 1, 1, 0, 0, 4, 1436, 104002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたとお話する時の\nこの気持ち、私の宝物\nなんです…えへへ", 1, 8, 0, 0, 101, 1437, 104002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです", 1, 8, 0, 0, 102, 1438, 104002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "朝ですね！\nこんないい朝は\n友達作りのチャンス！", 1, 1, 1, 0, 1, 1439, 104002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あの…そろそろお腹\nすきませんか？\nお昼ごはんは…", 1, 1, 2, 0, 2, 1440, 104002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もう眠いかもです…\n明日は友達\nできるといいなあ…", 1, 1, 3, 0, 3, 1441, 104002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "私…見た目\nそこまで変じゃ\nないですよね…？", 1, 1, 0, 0, 1, 1442, 104002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ほわあ～…\nふかふかって\nいいものですね～", 1, 1, 0, 0, 2, 1443, 104002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この子みたいな\nあいくるしさを\n目指したいです！", 1, 1, 0, 0, 3, 1444, 104002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "灯りを見てると\n心がなごみます～", 1, 1, 0, 0, 4, 1445, 104002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ぬくぬくで、なんだか\n眠くなっちゃいますね", 1, 1, 0, 0, 5, 1446, 104002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "身体を鍛えて\nみなさんを\n守ります！", 1, 1, 0, 0, 6, 1447, 104002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！", 1, 1, 0, 0, 7, 1448, 104002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "友達作りの\n参考になりそうなこと\n書いてないかな～？", 1, 1, 0, 0, 8, 1449, 104002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "唱喚の歌は、みだりに\n歌っていいものでは\nないんです", 1, 1, 0, 0, 1, 1451, 104201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここでアイドルの\n練習は、さすがに\n迷惑ですよね？", 1, 1, 0, 0, 2, 1452, 104201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "練習してもあんまり\n疲れなくなった…\nアイドルのおかげかな", 1, 1, 0, 0, 3, 1453, 104201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "人々の平和を守る旅に\n同行してくれる人を\n探していて…", 1, 1, 0, 0, 4, 1454, 104201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あとで曲を復習して\nおかないと…", 1, 1, 0, 0, 5, 1455, 104201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "踊りを見られるのは\nまだ恥ずかしいけど\nあの人になら…", 1, 4, 0, 0, 101, 1456, 104201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "浜辺で曲を書こうと\n思うんですけど…\n一緒に行きませんか？", 1, 8, 0, 0, 102, 1457, 104201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "いつも応援ありがとう\nございます♪　これは\nあ、握手の練習です！", 1, 1, 0, 0, 1, 1458, 104201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "え、もう練習の時間？\nごめんなさい\nすぐ準備します！", 1, 1, 0, 0, 2, 1459, 104201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "守るべき人たちに\n笑顔でいてもらうのも\n大切なんですね", 1, 1, 0, 0, 3, 1460, 104201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "歌える曲を増やそうと\n思って。どんな曲が\n聞きたいですか？", 1, 1, 0, 0, 4, 1461, 104201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "～～♪　あれ？\n今私、歌ってました？\n気づきませんでした", 1, 4, 0, 0, 101, 1462, 104201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "…歌って欲しい？\n喜んで♪\nどの歌にしましょう？", 1, 8, 0, 0, 102, 1463, 104201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようございます♪\n少し歌の練習を\nしてきますね", 1, 1, 1, 0, 1, 1464, 104201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もうお昼なんですか？\n練習に夢中で\n気づきませんでした", 1, 1, 2, 0, 2, 1465, 104201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "明日の予定は…\nアイドルはやることが\n多いみたいで…", 1, 1, 3, 0, 3, 1466, 104201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "見た目については\n前よりも気をつけてと\nノゾミに言われてます", 1, 1, 0, 0, 1, 1467, 104201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "歌の調子をよくする\nためには休息も\n必要ですよね…", 1, 1, 0, 0, 2, 1468, 104201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この子たちを\n相手に練習して\nみようかな…♪", 1, 1, 0, 0, 3, 1469, 104201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "…疲れてるのかな\n落ち着けているのが\n自分でもわかります", 1, 1, 0, 0, 4, 1470, 104201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ここにいると\n穏やかな旋律が\n浮かんできます…", 1, 1, 0, 0, 5, 1471, 104201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "歌うためには\n体力づくりは\n欠かせません", 1, 1, 0, 0, 6, 1472, 104201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "歌った後は\nお腹が空いて\nしまうんです", 1, 1, 0, 0, 7, 1473, 104201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "音楽について\n理論も勉強\nしないと…", 1, 1, 0, 0, 8, 1474, 104201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "歌うことの楽しさ\n分かってきました\nもっと歌いたいな…", 1, 1, 0, 0, 1, 1476, 104202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "練習してもあんまり\n疲れなくなった…\nアイドルのおかげかな", 1, 1, 0, 0, 2, 1477, 104202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "精霊も強くなったし\nもっとたくさんの人を\n守りたいです", 1, 1, 0, 0, 3, 1478, 104202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "最近、よく頭の中に\n歌が浮かんできます\n不思議な感覚…", 1, 1, 0, 0, 4, 1479, 104202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "楽譜を買いました\n思いついた歌を\n書き留めておくために", 1, 1, 0, 0, 5, 1480, 104202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "浜辺で曲を書こうと\n思うんですけど…\n一緒に行きませんか？", 1, 8, 0, 0, 101, 1481, 104202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "私の中にある\n私だけの歌…\n作ってみようかな？", 1, 8, 0, 0, 102, 1482, 104202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "守るべき人たちに\n笑顔でいてもらうのも\n大切なんですね", 1, 1, 0, 0, 1, 1483, 104202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "歌える曲を増やそうと\n思って。どんな曲が\n聞きたいですか？", 1, 1, 0, 0, 2, 1484, 104202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "魔物退治ですか！？\n…違います？\n良かった…", 1, 1, 0, 0, 3, 1485, 104202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "いつも応援ありがとう\nございます♪　これは\nあ、握手の練習です！", 1, 1, 0, 0, 4, 1486, 104202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "…歌って欲しい？\n喜んで♪\nどの歌にしましょう？", 1, 8, 0, 0, 101, 1487, 104202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "～～♪　あれ？\n今私、歌ってました？\n気づきませんでした", 1, 8, 0, 0, 102, 1488, 104202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "呼吸を深く、大きく…\nこれでちゃんと\n目が覚めますよ", 1, 1, 1, 0, 1, 1489, 104202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "少し休んだら\n午後の練習に\n行ってきますね", 1, 1, 2, 0, 2, 1490, 104202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "曲を作っていたら\nもうこんな時間…\nおやすみなさい", 1, 1, 3, 0, 3, 1491, 104202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "見た目については\n前よりも気をつけてと\nノゾミに言われてます", 1, 1, 0, 0, 1, 1492, 104202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あ～…♪\nよく体がほぐれます…\nいい歌が歌えそうです", 1, 1, 0, 0, 2, 1493, 104202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいいと思う\n気持ちを、歌にしても\nいいかも知れませんね", 1, 1, 0, 0, 3, 1494, 104202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "…疲れてるのかな\n落ち着けているのが\n自分でもわかります", 1, 1, 0, 0, 4, 1495, 104202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ここにいると\n穏やかな旋律が\n浮かんできます…", 1, 1, 0, 0, 5, 1496, 104202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もっと体力をつけて\n最高のライブを\nファンに届けます！", 1, 1, 0, 0, 6, 1497, 104202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これでも一応\n体形には気をつけて\n食べてるんですよ？", 1, 1, 0, 0, 7, 1498, 104202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "読書は歌に\n新しい風を\n吹き込んでくれます", 1, 1, 0, 0, 8, 1499, 104202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "店の新メニューでも\n考えるかな", 1, 1, 0, 0, 1, 1501, 104301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あたしの店から、\n出前を届けようか？", 1, 1, 0, 0, 2, 1502, 104301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "パトロール、\nパトロール…\nここは異常なしかな？", 1, 1, 0, 0, 3, 1503, 104301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "腹へったら食う！\n悩んだら寝る！\nそれが一番だろ？", 1, 1, 0, 0, 4, 1504, 104301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "のんびりし過ぎるのも\n…性に合わねぇな", 1, 1, 0, 0, 5, 1505, 104301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "そろそろあいつ、\n腹すかしてるん\nじゃないかな？", 1, 4, 0, 0, 101, 1506, 104301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今日の献立は…\nあいつの好きなものに\nしてやるか", 1, 8, 0, 0, 102, 1507, 104301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "強くなりたいなら、\nあたしがビシバシ\n鍛えてやるよ", 1, 1, 0, 0, 1, 1508, 104301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "天気がいい日は\n外で運動したく\nならねぇか？", 1, 1, 0, 0, 2, 1509, 104301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "腹減ったか？　なんか\n作ってやろうか？", 1, 1, 0, 0, 3, 1510, 104301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ん？　なんだよ、\n相談事か？\nなんでも言ってくれ", 1, 1, 0, 0, 4, 1511, 104301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お前が好きな飯って\nなんだ？\n今度、作ってやるよ", 1, 4, 0, 0, 101, 1512, 104301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お前って不思議だよな\n一緒にいるとなんだか\n…なんでもねーよ", 1, 8, 0, 0, 102, 1513, 104301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "気持ちいい朝だな！\n体を動かしたい気分だ\n付き合ってくれるか", 1, 1, 1, 0, 1, 1514, 104301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "昼飯もう食ったか？\nまだなら、あたしが\nなんか作ってやるよ", 1, 1, 2, 0, 2, 1515, 104301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "日中たっぷり動いたら\nそのぶん夜はしっかり\n休んどけよな", 1, 1, 3, 0, 3, 1516, 104301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "た、たまにはその…\nかわいい格好とか\nしても…", 1, 1, 0, 0, 1, 1517, 104301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふわぁ、\n一休みすっかなぁ～", 1, 1, 0, 0, 2, 1518, 104301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うっ！　か、かわいい\nじゃねぇか", 1, 1, 0, 0, 3, 1519, 104301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "へー　いい匂いだな\nあいつもこういうの\n好きなのかな…", 1, 1, 0, 0, 4, 1520, 104301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふわぁ〜！\n温かいと、つい\nウトウトしちまうな", 1, 1, 0, 0, 5, 1521, 104301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "オラオラオラーーー！\n燃えるぜーーー！", 1, 1, 0, 0, 6, 1522, 104301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい味してるぜ！\nうちの店でも\n出してみたいな", 1, 1, 0, 0, 7, 1523, 104301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "文字を読んでると\nどうしても眠く\nなっちまうんだよなぁ", 1, 1, 0, 0, 8, 1524, 104301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "パトロール終了～\n本日も異常なしっと！", 1, 1, 0, 0, 1, 1526, 104302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あたしの店から\n出前を届けようか？", 1, 1, 0, 0, 2, 1527, 104302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あいつ、早く\n帰ってこないかな", 1, 1, 0, 0, 3, 1528, 104302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あいつ\n腹すかせてないと\nいいんだけど…", 1, 1, 0, 0, 4, 1529, 104302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "のんびりし過ぎるのも\n…性に合わねぇな", 1, 1, 0, 0, 5, 1530, 104302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あいつの事を思うと\n胸があったかくなる？\nなんだコレ…？", 1, 8, 0, 0, 101, 1531, 104302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今日の献立は…\nあいつの好きなものに\nしてやるか", 1, 8, 0, 0, 102, 1532, 104302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ん？　なんだよ\n相談事か？\nなんでも言ってくれ", 1, 1, 0, 0, 1, 1533, 104302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "天気がいい日は\n外で運動したく\nならねぇか？", 1, 1, 0, 0, 2, 1534, 104302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "腹減ったか？　なんか\n作ってやろうか？", 1, 1, 0, 0, 3, 1535, 104302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "腹へったら食う！\n悩んだら寝る！\nそれが一番だろ？", 1, 1, 0, 0, 4, 1536, 104302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あのさ…できれば\nもっとそばに…いや\nなんでもねぇ忘れろ！", 1, 8, 0, 0, 101, 1537, 104302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お前って不思議だよな\n一緒にいるとなんだか\n…なんでもねーよ", 1, 8, 0, 0, 102, 1538, 104302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "気持ちいい朝だな！\n体を動かしたい気分だ\n付き合ってくれるか？", 1, 1, 1, 0, 1, 1539, 104302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "昼飯もう食ったか？\nまだなら、あたしが\nなんか作ってやるよ", 1, 1, 2, 0, 2, 1540, 104302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あんま夜更しすんなよ\n明日に響いたら\n大変だろ？", 1, 1, 3, 0, 3, 1541, 104302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "た、たまにはその…\nかわいい格好とか\nしても…", 1, 1, 0, 0, 1, 1542, 104302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふぅ～♪\n疲れがとれるな～", 1, 1, 0, 0, 2, 1543, 104302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うっ！　か、かわいい\nじゃねぇか", 1, 1, 0, 0, 3, 1544, 104302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "へー　いい匂いだな\nあいつもこういうの\n好きなのかな…", 1, 1, 0, 0, 4, 1545, 104302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふわぁ～！\n温かいと、つい\nウトウトしちまうな", 1, 1, 0, 0, 5, 1546, 104302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "オラオラオラーーー！\n燃えるぜーーー！", 1, 1, 0, 0, 6, 1547, 104302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい味してるぜ！\nうちの店でも\n出してみたいな", 1, 1, 0, 0, 7, 1548, 104302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "料理だけじゃなく\n経営術とかも\n学んどかねぇと…", 1, 1, 0, 0, 8, 1549, 104302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "最近なにも\nしてくれません…\n焦らしてるんですね", 1, 1, 0, 0, 1, 1551, 104501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ぐふふ、じゅるり…\nい、いけない、また\nいつの間にかよだれが", 1, 1, 0, 0, 2, 1552, 104501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "好きな武器といえば\n剣より槍より\nやっぱり鞭…ぐふふ", 1, 1, 0, 0, 3, 1553, 104501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "「無数の触手が\nクウカの柔肌に\n食い込み」…ぐふふ", 1, 1, 0, 0, 4, 1554, 104501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！", 1, 1, 0, 0, 5, 1555, 104501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ドＳさんを登場\nさせるだけで、妄想に\n無限の広がりが！", 1, 4, 0, 0, 101, 1556, 104501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あっ…\nジロジロ見てたの\n気づいちゃいました？", 1, 8, 0, 0, 102, 1557, 104501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "視線を感じると\n思ったら…やはり\nドＳさんでしたか", 1, 1, 0, 0, 1, 1558, 104501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日は\nどんなヒドイことを\nしてくれるんですか？\nぐふふふ", 1, 1, 0, 0, 2, 1559, 104501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "いつの間にそこに！？\nまさかクウカを監視\nしていたのですか！？", 1, 1, 0, 0, 3, 1560, 104501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "クウカに触るつもり\nですか？　触るつもり\nなのですねっ！？", 1, 1, 0, 0, 4, 1561, 104501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "クウカにナニをする\nつもりですか…\n期待してしまいます！", 1, 4, 0, 0, 101, 1562, 104501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ドＳさんの視界に入る\nだけでドキドキが…\nこれが恋！？", 1, 8, 0, 0, 102, 1563, 104501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようございます。\n朝からお盛んですね？\nぐふふ…", 1, 1, 1, 0, 1, 1564, 104501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "こんなにも日の高い\nうちからドＳさんは…\nじゅるり", 1, 1, 2, 0, 2, 1565, 104501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜はこれから、\nですよ…\nぐふ、ぐふふふ…", 1, 1, 3, 0, 3, 1566, 104501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "キレイにおめかし\nしたのに力ずくで…\nぐふふふ", 1, 1, 0, 0, 1, 1567, 104501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ソファの背にクウカを\n押し付けて…ぐふふ\nいいですね\nソファドン", 1, 1, 0, 0, 2, 1568, 104501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ", 1, 1, 0, 0, 3, 1569, 104501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "香りを吸ったクウカの\n頬は紅潮し目はうるみ\nもはや抗う力も…！", 1, 1, 0, 0, 4, 1570, 104501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ああ、身体が\n火照って…\nぐふ、ぐふふ…", 1, 1, 0, 0, 5, 1571, 104501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はぁ、はぁ、こうして\nクウカの体力を\n奪ってから…ぐふふ", 1, 1, 0, 0, 6, 1572, 104501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "食欲が満たされたら\nつ、次はやはり…！\nそうなのですね！？", 1, 1, 0, 0, 7, 1573, 104501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この本の下に、\nそういう本を\nカムフラージュ？", 1, 1, 0, 0, 8, 1574, 104501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "「ドＳさんと踊り子\n乱舞の一夜」…\n次の日記はこれで…", 1, 1, 0, 0, 1, 1576, 104502);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "最近なにも\nしてくれません…\n焦らしてるんですね", 1, 1, 0, 0, 2, 1577, 104502);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "も、妄想が\n足りません…\n禁断症状がっ…！", 1, 1, 0, 0, 3, 1578, 104502);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！", 1, 1, 0, 0, 4, 1579, 104502);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "次はどんなドＳを\nされるのか、想像する\nだけで…ぐふふふ", 1, 1, 0, 0, 5, 1580, 104502);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "じっとしてると\nドＳさんのことばかり\n考えてしまいますね", 1, 8, 0, 0, 101, 1581, 104502);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "妄想が止まらなくて\nクウカどうにかなって\nしまいそうです！", 1, 8, 0, 0, 102, 1582, 104502);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ドＳさんといると体が\n火照って…クウカは\nいやらしい子です！", 1, 1, 0, 0, 1, 1583, 104502);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あっ…\nジロジロ見てたの\n気づいちゃいました？", 1, 1, 0, 0, 2, 1584, 104502);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ドＳさんのおかげで\n新しい扉が開けそう\nです…じゅるり", 1, 1, 0, 0, 3, 1585, 104502);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "いやー！　ドＳさんに\n襲われ…襲って\nくれないんですか？", 1, 1, 0, 0, 4, 1586, 104502);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "クウカはドＳさんに\n身も心も堕とされて\nしまったのですね", 1, 8, 0, 0, 101, 1587, 104502);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ドＳさんがお望みなら\nクウカは\nどんなことだって！", 1, 8, 0, 0, 102, 1588, 104502);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "朝だからこそ\nできる妄想が\nあります…ぐふふ", 1, 1, 1, 0, 1, 1589, 104502);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "こんな日の高いうち\nからドＳさんは…\nじゅるり", 1, 1, 2, 0, 2, 1590, 104502);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今夜は一晩中…\nなんですね？\nぐふふ…", 1, 1, 3, 0, 3, 1591, 104502);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "意外と難しいんですよ\n正しい亀甲縛りと\nいうものは…", 1, 1, 0, 0, 1, 1592, 104502);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "くつろいで油断\nしているスキに何を\nされるのでしょう！？", 1, 1, 0, 0, 2, 1593, 104502);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ", 1, 1, 0, 0, 3, 1594, 104502);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これは禁断の香り…\nああっ一段階上の\n妄想が開花するうぅ！", 1, 1, 0, 0, 4, 1595, 104502);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ああっ、暖気がまるで\nロウソクのように\nクウカを攻めますっ！", 1, 1, 0, 0, 5, 1596, 104502);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "激しい攻めを受けきる\nためには、鍛えないと\nいけないのですね", 1, 1, 0, 0, 6, 1597, 104502);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こんなにいっぱい\n入りません…\nお腹にっ！", 1, 1, 0, 0, 7, 1598, 104502);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "どんな本も\nよく見ると\nいやらしいです…！", 1, 1, 0, 0, 8, 1599, 104502);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この前遊んだ猫、\nモッフモフ\nだったにゃあ…♪", 1, 1, 0, 0, 1, 1601, 104601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ほっぺやお腹が、\n肉球ぐらいぷにぷに…\nこれはまずいにゃ…", 1, 1, 0, 0, 2, 1602, 104601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "大きな仕事が\n決まったにゃ！\n腕が鳴るにゃ～♪", 1, 1, 0, 0, 3, 1603, 104601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "おやつのたい焼き、\nつぶあんとこしあん、\nどっちに\nしようかにゃ？", 1, 1, 0, 0, 4, 1604, 104601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "うにゃー…\nもうちょっとお昼寝\nしたかったにゃ…\nむにゃむにゃ…", 1, 1, 0, 0, 5, 1605, 104601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "キミに呼ばれると、\n猫まっしぐらな気分に\nなっちゃうにゃ♪", 1, 4, 0, 0, 101, 1606, 104601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "キミのおかげで\nギルドの仕事も\n大成功だにゃ♪\nありがとにゃ♪", 1, 8, 0, 0, 102, 1607, 104601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "うずうず…その指、\nじゃれついても\nいいかにゃ？", 1, 1, 0, 0, 1, 1608, 104601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "にゃ？　あたしに何か\nご入り用かにゃ？\nお安くしとくにゃ～♪", 1, 1, 0, 0, 2, 1609, 104601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "うーん、なんだか\n退屈にゃ～\n遊んで、遊んで～？", 1, 1, 0, 0, 3, 1610, 104601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "素早い動きのコツ？\nタダじゃ教えて\nあげられないにゃ～♪", 1, 1, 0, 0, 4, 1611, 104601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そんなにあたしを\nなでたいにゃ？\nキミならなでさせて\nあげてもいいかにゃー", 1, 4, 0, 0, 101, 1612, 104601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふにゃあ、ねえねえ、\n一緒にゴロゴロ\nしようにゃあ？", 1, 8, 0, 0, 102, 1613, 104601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "いい朝にゃ！\n顔を洗ってくるにゃ！", 1, 1, 1, 0, 1, 1614, 104601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "むーん、そろそろ\nお昼…猫たちにも\n猫缶を…", 1, 1, 2, 0, 2, 1615, 104601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふわあ…眠いにゃ…\n猫は布団で\n丸くなるにゃ", 1, 1, 3, 0, 3, 1616, 104601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "客商売では、\n外見のチェックも\n欠かせないのにゃ！", 1, 1, 0, 0, 1, 1617, 104601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うにゃ～！\nお昼寝に\nぴったりにゃ", 1, 1, 0, 0, 2, 1618, 104601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これ…量産したら\n子どもにバカ売れ\nしそうだにゃ！", 1, 1, 0, 0, 3, 1619, 104601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この灯り、まるで\n猫みたいな\n癒し効果にゃ！", 1, 1, 0, 0, 4, 1620, 104601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふにゃあ、\nあったかいと猫は丸く\nなっちゃうのにゃあ…", 1, 1, 0, 0, 5, 1621, 104601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "本当は外で\n運動したいん\nだけどにゃ", 1, 1, 0, 0, 6, 1622, 104601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "むーん、\n美味しいにゃー！\nたい焼きに\n匹敵するにゃ！", 1, 1, 0, 0, 7, 1623, 104601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふむふむ…猫の\nしつけ方は、と…", 1, 1, 0, 0, 8, 1624, 104601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "かわいいネコの置物を\nゲットしたにゃ！\nどこに飾ろうか\nにゃ～♪", 1, 1, 0, 0, 1, 1626, 104602);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "仕事の交渉がうまく\n行ったにゃ～♪\nいい気分だにゃ～♪", 1, 1, 0, 0, 2, 1627, 104602);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ねこじゃらしには\n食いついちゃうにゃ\nびしっ！　びしっ！", 1, 1, 0, 0, 3, 1628, 104602);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ほっぺやお腹が、肉球\nぐらいぷにぷに…\nこれはまずいにゃ…", 1, 1, 0, 0, 4, 1629, 104602);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "さっきのたいやき\nおいしかったにゃ♪\nごろごろごろ…", 1, 1, 0, 0, 5, 1630, 104602);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "手作りのたい焼き\nあいつは食べて\nくれるかにゃ…？", 1, 8, 0, 0, 101, 1631, 104602);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あいつには、いつか\nたっぷり恩返しが\nしたいにゃ…", 1, 8, 0, 0, 102, 1632, 104602);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "にゃーに？　あたしの\nことがそんなに気に\nなるのかにゃ？", 1, 1, 0, 0, 1, 1633, 104602);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あたしのこと…\nしっかり捕まえ\nといてにゃ？", 1, 1, 0, 0, 2, 1634, 104602);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あたしの力が\n必要かにゃ？　猫の手\n貸してあげるにゃ♪", 1, 1, 0, 0, 3, 1635, 104602);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "たいやきとクレープを\n合わせれば…\n新しい商売のニオイが\nするにゃ…！", 1, 1, 0, 0, 4, 1636, 104602);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふにゃあ、ねえねえ\n一緒にゴロゴロ\nしようにゃあ？", 1, 8, 0, 0, 101, 1637, 104602);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "キミと一緒にいると\n楽しくなってつい\nじゃれちゃうにゃ！", 1, 8, 0, 0, 102, 1638, 104602);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "いい朝にゃ！\n顔を洗ってくるにゃ！", 1, 1, 1, 0, 1, 1639, 104602);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そろそろおっひる～\nにゃーんか\n食べたい…", 1, 1, 2, 0, 2, 1640, 104602);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "うーん、眠くなって\nきたにゃ。あたしは\n夜行性じゃないのにゃ", 1, 1, 3, 0, 3, 1641, 104602);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "にゃにゃ…\n髪が乱れてるにゃ", 1, 1, 0, 0, 1, 1642, 104602);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふっかふかにゃ～\n猫がねころん\nじゃうにゃ", 1, 1, 0, 0, 2, 1643, 104602);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これ、猫と匹敵する\nぐらいかわいいにゃ！", 1, 1, 0, 0, 3, 1644, 104602);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "猫でもこわくない\n優しい灯りにゃ！", 1, 1, 0, 0, 4, 1645, 104602);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ぽっかぽか\nしあわせな気分にゃ～", 1, 1, 0, 0, 5, 1646, 104602);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "雨の日でも運動\nできるのは、濡れ\nなくて便利だにゃ！", 1, 1, 0, 0, 6, 1647, 104602);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "美味しいにゃ～！\nこれ、朝昼晩\n食べたいにゃ！", 1, 1, 0, 0, 7, 1648, 104602);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "本を読んで\n猫の気持ちを\n勉強するのにゃ！", 1, 1, 0, 0, 8, 1649, 104602);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "内職手伝える人\nいないかしら？", 1, 1, 0, 0, 1, 1651, 104801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "次の依頼は、\nこういう計画で…\nうん、完璧ね！", 1, 1, 0, 0, 2, 1652, 104801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "次の特売セールに\n向けて、しっかり\n準備しないと！", 1, 1, 0, 0, 3, 1653, 104801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "配達の後に売り子\n次に採取のバイト…\nうん、効率的ね", 1, 1, 0, 0, 4, 1654, 104801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "一日に受ける\nバイトの数、もう少し\n増やしたいわね", 1, 1, 0, 0, 5, 1655, 104801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "師匠として\n頼りにされたいし\nもっと頑張らなきゃ！", 1, 4, 0, 0, 101, 1656, 104801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね", 1, 8, 0, 0, 102, 1657, 104801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あら、\nちょうどよかった\n今から一緒に\nバイトでもどう？", 1, 1, 0, 0, 1, 1658, 104801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "相談事かしら？\nそれくらいなら\n無料でいいわよ", 1, 1, 0, 0, 2, 1659, 104801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "背後の気配なら\nすぐ分かるわ\n試してみる？", 1, 1, 0, 0, 3, 1660, 104801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "道具やお金の管理は\n大丈夫？　手伝うから\nいつでも言って", 1, 1, 0, 0, 4, 1661, 104801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね", 1, 4, 0, 0, 101, 1662, 104801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ねえ、あの…\n私はキミにとって…\nいや、何でもないわ", 1, 8, 0, 0, 102, 1663, 104801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよう！\n今日も朝から\nバイトよ！", 1, 1, 1, 0, 1, 1664, 104801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼時ね。\n少し休憩したら、\nまた働くわよ！", 1, 1, 2, 0, 2, 1665, 104801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もうこんな時間！\n明日もバイトで\n朝早いのよ…", 1, 1, 3, 0, 3, 1666, 104801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "接客業の依頼が\nある時は、外見も\nキレイにしないとね", 1, 1, 0, 0, 1, 1667, 104801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はぁ～バイトで\n疲れたから癒される～", 1, 1, 0, 0, 2, 1668, 104801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これを内職で\n作ったら\n儲かるかしら？", 1, 1, 0, 0, 3, 1669, 104801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "リフレッシュできたわ\nこれでまた、バリバリ\n働けるわね！", 1, 1, 0, 0, 4, 1670, 104801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "風邪を引いたら効率が\n落ちちゃうから、\n暖めないと！", 1, 1, 0, 0, 5, 1671, 104801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "働き続けるには\n健康第一！　それには\n運動が一番よ", 1, 1, 0, 0, 6, 1672, 104801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もぐもぐ…\n腹が減っては\n戦ができぬ、よ！", 1, 1, 0, 0, 7, 1673, 104801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "日々絶えず学んで、\n生活に活かすのよ！", 1, 1, 0, 0, 8, 1674, 104801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "家計簿をつけなきゃ！\n覚えている時に\nやっておかないとね", 1, 1, 0, 0, 1, 1676, 104802);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "次の依頼は\nこういう計画で…\nうん、完璧ね！", 1, 1, 0, 0, 2, 1677, 104802);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ギルドの経費\nあとちょっとだけ\n削れそうね…", 1, 1, 0, 0, 3, 1678, 104802);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "少しでも時給のいい\nバイトがしたいけど\nなかなか\n出会えないのよ", 1, 1, 0, 0, 4, 1679, 104802);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今度のバイトも\nなかなか大変そう\nね…腕が鳴るわ", 1, 1, 0, 0, 5, 1680, 104802);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね", 1, 8, 0, 0, 101, 1681, 104802);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "私、どうしたんだろ？\n仕事中もずっと\n彼のこと考えてる…", 1, 8, 0, 0, 102, 1682, 104802);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この間はヘルプに\n来てくれてありがとう\n頼んで正解だったわ", 1, 1, 0, 0, 1, 1683, 104802);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "見つけた！　ちょうど\n頼みたいことが\nあるんだけど、いい？", 1, 1, 0, 0, 2, 1684, 104802);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今から\n休憩するところなの\n一緒にどう？", 1, 1, 0, 0, 3, 1685, 104802);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ん？　どうしたの？\n私のことが\n気になるの？", 1, 1, 0, 0, 4, 1686, 104802);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "…たまには甘えても\nいい？　私だって\n弱る時はあるのよ", 1, 8, 0, 0, 101, 1687, 104802);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね", 1, 8, 0, 0, 102, 1688, 104802);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよう！\nいい朝ね。一緒に\nランニングでもいく？", 1, 1, 1, 0, 1, 1689, 104802);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼時ね\n少し休憩したら\nまた働くわよ！", 1, 1, 2, 0, 2, 1690, 104802);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もうこんな時間！\n明日もバイトで\n朝早いのよ…", 1, 1, 3, 0, 3, 1691, 104802);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "女の子らしく…\nどうも忘れちゃう\nのよね", 1, 1, 0, 0, 1, 1692, 104802);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ここで少し\n休んだら\nまたバイトよ！", 1, 1, 0, 0, 2, 1693, 104802);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これを内職で\n作ったら\n儲かるかしら？", 1, 1, 0, 0, 3, 1694, 104802);
