INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なーに？\nアタシに甘えたいの？", 1, 1, 0, 0, 1, 1783, 105002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "アタシに合う服\n雑誌で探そうっと！\nサイズじゃないわ\nデザインとかの話よ！", 1, 1, 0, 0, 2, 1784, 105002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "頭撫でないで！\nお子様みたいに\nあつかわないでって\n言ってるのに～！", 1, 1, 0, 0, 3, 1785, 105002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "壁ドンの練習…\nあれ？　壁ドンって\n男がするんだっけ？", 1, 1, 0, 0, 4, 1786, 105002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ねーねー！\nアタシ、かわいい？\nかわいいわよね？", 1, 8, 0, 0, 101, 1787, 105002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "これから出かけるの？\nしっかりアタシを\nエスコートしてね！", 1, 8, 0, 0, 102, 1788, 105002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "レディの朝は\n忙しいのよ", 1, 1, 1, 0, 1, 1789, 105002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お腹すいたー\nダイエット\nやめちゃおうかなー", 1, 1, 2, 0, 2, 1790, 105002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "エイジングケアを\nしないとねー", 1, 1, 3, 0, 3, 1791, 105002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "身だしなみチェック\nオッケー！　これで\nいつアイツが来ても\n大丈夫ね！", 1, 1, 0, 0, 1, 1792, 105002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あ！　これ雑誌で\n見たことあるわ～\nだからきっと\nオシャレなのよ！", 1, 1, 0, 0, 2, 1793, 105002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あたしがもっと\nオシャレにかわいく\nしてあげようか？", 1, 1, 0, 0, 3, 1794, 105002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "オシャレ～♪\n大人なレディって\n感じよね♪", 1, 1, 0, 0, 4, 1795, 105002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったかーい♪\nここで宿題\nしようかしら？", 1, 1, 0, 0, 5, 1796, 105002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "運動したら成長\nしないかしら？\n背とか胸とか…", 1, 1, 0, 0, 6, 1797, 105002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "レディは、食べ物にも\n気を使うのよ", 1, 1, 0, 0, 7, 1798, 105002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "おもしろーい！\n…でも絵本って\n子供っぽいかしら？", 1, 1, 0, 0, 8, 1799, 105002);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "魔法と科学\nどっちも極めて\nみせるわ", 1, 1, 0, 0, 1, 1801, 105101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "仕事も片付いたし\n部屋で\n実験実験…♪", 1, 1, 0, 0, 2, 1802, 105101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "たまった治療代の\n請求書を\nまとめないと…", 1, 1, 0, 0, 3, 1803, 105101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "新薬が出来たし\nすぐに試したいけど…\n実験台がいないわ", 1, 1, 0, 0, 4, 1804, 105101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "部屋にこもりすぎると\n曜日の感覚が\nなくなるわね", 1, 1, 0, 0, 5, 1805, 105101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あの子の記憶を\n取り戻せる薬…\n作れないものかしら", 1, 4, 0, 0, 101, 1806, 105101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら", 1, 8, 0, 0, 102, 1807, 105101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あら、ヒマなら\n薬草の整理を\n手伝って", 1, 1, 0, 0, 1, 1808, 105101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もしかして実験に\n付き合ってくれるの？\nえ？　違う？", 1, 1, 0, 0, 2, 1809, 105101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "具合が悪い？\nなら診てあげる\nまずは触診から…", 1, 1, 0, 0, 3, 1810, 105101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "植物に興味あるの？\nなら教えてあげるわ\n色々とね…", 1, 1, 0, 0, 4, 1811, 105101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？", 1, 4, 0, 0, 101, 1812, 105101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも", 1, 8, 0, 0, 102, 1813, 105101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼までに調合用の\n花を集めたいの\n手伝ってくれる？", 1, 1, 1, 0, 1, 1814, 105101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あら、もうお昼？\nそろそろ一息\n入れましょうか", 1, 1, 2, 0, 2, 1815, 105101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜も更けて来たわね\n私は朝まで実験だけど\n見学していく？", 1, 1, 3, 0, 3, 1816, 105101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "医者たるもの\nいつだって\n清潔に…♪", 1, 1, 0, 0, 1, 1817, 105101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いいわねこれ\n仮眠の時に\n使わせてもらうわ", 1, 1, 0, 0, 2, 1818, 105101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ぬいぐるみなら\n動物も…", 1, 1, 0, 0, 3, 1819, 105101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい香り…\n仕事場にも\n置いてみようかしら", 1, 1, 0, 0, 4, 1820, 105101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ここにいると\nついウトウト\nしちゃうわ…", 1, 1, 0, 0, 5, 1821, 105101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "座ってる時間が多いと\n運動不足になるし\nたまにはいいわね", 1, 1, 0, 0, 6, 1822, 105101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "研究していると\nつい食べるのを\n忘れちゃうのよね", 1, 1, 0, 0, 7, 1823, 105101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "実験もいいけど\nいつか成果を\n本にしないと…", 1, 1, 0, 0, 8, 1824, 105101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "徹夜はツラいけど\n集中すると時間の\n感覚がなくなって…", 1, 1, 0, 0, 1, 1826, 105102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "痛みに悶える顔って\nどうしてあんなに\n興奮するのかしら？", 1, 1, 0, 0, 2, 1827, 105102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "彼に罵られたら\nどんな気分かしら\n意外と快感かも？", 1, 1, 0, 0, 3, 1828, 105102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "毒を薄めると\n薬になるのって\n今でも不思議だわ", 1, 1, 0, 0, 4, 1829, 105102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "綺麗なバラには\nトゲがあるけど\n魅力の一つよね", 1, 1, 0, 0, 5, 1830, 105102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "カレをさりげなく\n惚れさせる方法を\n研究するわよ…♪", 1, 8, 0, 0, 101, 1831, 105102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら", 1, 8, 0, 0, 102, 1832, 105102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "構って欲しいの？\nじゃあ、君の体を\n調べさせて？", 1, 1, 0, 0, 1, 1833, 105102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "実験に付き合って\n大丈夫よ、試すのは\nただの栄養剤だから", 1, 1, 0, 0, 2, 1834, 105102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "欲しい薬が\nあったら言ってね\n調合してみるから", 1, 1, 0, 0, 3, 1835, 105102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おしおきされたいの？\nキミにだったら\nいつでもしてあげる", 1, 1, 0, 0, 4, 1836, 105102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？", 1, 8, 0, 0, 101, 1837, 105102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも", 1, 8, 0, 0, 102, 1838, 105102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "朝しか取れない\n植物があるの\n採取しに行くわよ", 1, 1, 1, 0, 1, 1839, 105102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼時ってやけに\n眠たくなるわよね\n…調べたくなるわ", 1, 1, 2, 0, 2, 1840, 105102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日くらいは\nゆっくり眠りたいわ\n…キミと一緒にね", 1, 1, 3, 0, 3, 1841, 105102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "身なりについて\n研究するのも\n楽しそうね", 1, 1, 0, 0, 1, 1842, 105102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "休息に必要な道具は\n妥協せずに選ぶ！\nこれだけは譲れないわ", 1, 1, 0, 0, 2, 1843, 105102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "たまにはこういう\n可愛らしいものに\n触れないとね♪", 1, 1, 0, 0, 3, 1844, 105102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい香り…\n仕事場にも\n置いてみようかしら", 1, 1, 0, 0, 4, 1845, 105102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "仕事がなかったら\nこのまますーっと\n眠りたいのに…", 1, 1, 0, 0, 5, 1846, 105102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "運動しないと\n体型の維持が…", 1, 1, 0, 0, 6, 1847, 105102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "適度な食事も\n大切よね", 1, 1, 0, 0, 7, 1848, 105102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "まさか私が\n恋愛読本を読む\n日が来るなんて…", 1, 1, 0, 0, 8, 1849, 105102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ぺっぺっ！\n…い、いけないっ！", 1, 1, 0, 0, 1, 1851, 105201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "いっけな～い！\nひづめで床に傷が！", 1, 1, 0, 0, 2, 1852, 105201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "太っちゃったかも～！\n伐採でダイエット\nしなくっちゃ！", 1, 1, 0, 0, 3, 1853, 105201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "きちんと生活すると\n毛づやが良くなる\nみたいなのよね～", 1, 1, 0, 0, 4, 1854, 105201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "みんな、体重\nどれくらいなのかな？", 1, 1, 0, 0, 5, 1855, 105201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう", 1, 4, 0, 0, 101, 1856, 105201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "いつか人間の女の子\nみたいなデート、\nしてみたいな～♪", 1, 8, 0, 0, 102, 1857, 105201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私ね「どこにでもいる\n普通の女の子」って\n言葉が憧れなんだ～♪", 1, 1, 0, 0, 1, 1858, 105201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "チャームポイント？\n自分では分からない\nけど…まつげかなぁ？", 1, 1, 0, 0, 2, 1859, 105201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "リマリマ～♪\nはっ！？\nわ、私ったら今何を？", 1, 1, 0, 0, 3, 1860, 105201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "力仕事ならまかせて！", 1, 1, 0, 0, 4, 1861, 105201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪", 1, 4, 0, 0, 101, 1862, 105201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？", 1, 8, 0, 0, 102, 1863, 105201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今朝は寝癖がひどいわ\nしっぽの辺りとか～", 1, 1, 1, 0, 1, 1864, 105201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ランチは\nおしゃれなカフェで\nしてみたいな～♪", 1, 1, 2, 0, 2, 1865, 105201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お肌のケアに\nパックしながら\n寝ようかな～", 1, 1, 3, 0, 3, 1866, 105201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "やだっ、\nまた寝ぐせついてる！\n直さないと！", 1, 1, 0, 0, 1, 1867, 105201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ミシッっていった！\n私、重すぎるかも！？", 1, 1, 0, 0, 2, 1868, 105201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あ！　あなたも\n美人さんね～♪", 1, 1, 0, 0, 3, 1869, 105201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はぁ、まったり…\n四足歩行しちゃい\nそうだわ…", 1, 1, 0, 0, 4, 1870, 105201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったか～い♪\n…私、毛皮だから\n暑すぎるかも", 1, 1, 0, 0, 5, 1871, 105201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "な、なんだか野生に\n帰っちゃいそう！", 1, 1, 0, 0, 6, 1872, 105201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ぱくぱく、もぐもぐ、\nぺっぺっ…\nやだ、はしたない！", 1, 1, 0, 0, 7, 1873, 105201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～", 1, 1, 0, 0, 8, 1874, 105201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "素敵ね～、居るだけで\n女子力があがりそうな\nお部屋だわ～", 1, 1, 0, 0, 1, 1876, 105202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "私、大丈夫かしら？\n他の子に怖がられたり\nしないかな…？", 1, 1, 0, 0, 2, 1877, 105202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "動物苑…って\n嫌なギルド名ね…", 1, 1, 0, 0, 3, 1878, 105202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "好き、嫌い、好き…\nあっ！　うっかりして\n花占いのお花\n食べちゃった！", 1, 1, 0, 0, 4, 1879, 105202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここ、女の子らしい\nものでいっぱいね～！\nときめいちゃうわ～♪", 1, 1, 0, 0, 5, 1880, 105202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう", 1, 8, 0, 0, 101, 1881, 105202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あの人が\n来てくれるまで薪割り\nでもしてよっかな？", 1, 8, 0, 0, 102, 1882, 105202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "一緒にお出かけ？\nうれしいけど…\n心の準備が！\nはわわわ！", 1, 1, 0, 0, 1, 1883, 105202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どうしたの？\n急に抱きついてきて…\n癒されたいの？", 1, 1, 0, 0, 2, 1884, 105202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ぺっぺっ！\n…い、いけないっ！", 1, 1, 0, 0, 3, 1885, 105202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "メタモルアップルを\nここで食べたら、\nみんな驚くわよね", 1, 1, 0, 0, 4, 1886, 105202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？", 1, 8, 0, 0, 101, 1887, 105202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪", 1, 8, 0, 0, 102, 1888, 105202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今朝は寝癖がひどいわ\nしっぽの辺りとか～", 1, 1, 1, 0, 1, 1889, 105202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ダイエット中だから\nお昼は抜いてるの～", 1, 1, 2, 0, 2, 1890, 105202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お肌のケアに\nパックしながら\n寝ようかな～", 1, 1, 3, 0, 3, 1891, 105202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ちゃんとかわいく\nしてないと刈られ…\n狩られちゃうもんね！", 1, 1, 0, 0, 1, 1892, 105202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ミシッっていった！\n私、重すぎるかも！？", 1, 1, 0, 0, 2, 1893, 105202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あ！　あなたも\n美人さんね～♪", 1, 1, 0, 0, 3, 1894, 105202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい香り～\n森の中にいるみたいに\nとっても落ち着く～", 1, 1, 0, 0, 4, 1895, 105202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "素敵～♪　…\nきゃっ！　私の毛が\nたいへんなことに～！", 1, 1, 0, 0, 5, 1896, 105202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "な、なんだか野生に\n帰っちゃいそう！", 1, 1, 0, 0, 6, 1897, 105202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "何を食べるかより\n誰と食べるかが\n大事なのよね", 1, 1, 0, 0, 7, 1898, 105202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～", 1, 1, 0, 0, 8, 1899, 105202);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ヴァイスフリューゲル\nを盛り上げる方法、\n共に語らおう！", 1, 1, 0, 0, 1, 1901, 105301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "私は子供ではない…！\nが、お菓子は\nいただこう！", 1, 1, 0, 0, 2, 1902, 105301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "部屋はきちんと\n片づけておかなくては\n部屋の汚れは\n心の汚れだ", 1, 1, 0, 0, 3, 1903, 105301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "むむむ…明日\n目が覚めたら、身長が\n伸びていないものか…", 1, 1, 0, 0, 4, 1904, 105301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…", 1, 1, 0, 0, 5, 1905, 105301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あいつとの新たな\n鍛錬が\n待ち遠しいな…！", 1, 4, 0, 0, 101, 1906, 105301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "最近、あいつと\n過ごしていて、毎日が\n充実しているな…", 1, 8, 0, 0, 102, 1907, 105301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "軍人の本分は日々の\n研鑽にあるからな。\n今日も鍛錬に\n行かないか？", 1, 1, 0, 0, 1, 1908, 105301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…", 1, 1, 0, 0, 2, 1909, 105301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "貴公にも、飴をくれて\nやろうか？\nあまいぞ～！\n美味しいぞ～！", 1, 1, 0, 0, 3, 1910, 105301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私ともっと高く、\n共に飛ぼう！", 1, 1, 0, 0, 4, 1911, 105301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "貴公は、本当に頼りに\nなる男だな…いや、\nふとそう思っただけだ", 1, 4, 0, 0, 101, 1912, 105301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "貴公と共にいられる\nことは私の喜びだ。\nこれからも\nよろしく頼むぞ", 1, 8, 0, 0, 102, 1913, 105301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "うむ、いい朝だ。\n本国の夜明けを\n思い出してしまうな！", 1, 1, 1, 0, 1, 1914, 105301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そろそろ昼食に\nするか。腹が減っては\n戦はできないからな", 1, 1, 2, 0, 2, 1915, 105301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふわああ…私は子供\nではないが…眠い時は\n眠いのだ…", 1, 1, 3, 0, 3, 1916, 105301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "身だしなみは、心の\n在りようを映すから\n気をつけなくてはな", 1, 1, 0, 0, 1, 1917, 105301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…", 1, 1, 0, 0, 2, 1918, 105301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うむ…このかわいさ…\n本国に持って\n帰りたい…", 1, 1, 0, 0, 3, 1919, 105301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "このともし火、戦いに\n明け暮れる我らの心を\n癒すものだな…", 1, 1, 0, 0, 4, 1920, 105301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！", 1, 1, 0, 0, 5, 1921, 105301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うおおおおおっ！\n鍛錬、鍛錬、\n鍛錬っ！！", 1, 1, 0, 0, 6, 1922, 105301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いつ戦いになっても\n困らぬように、\n腹八分目に\nしておくのだ！", 1, 1, 0, 0, 7, 1923, 105301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これを読んで、新たな\n戦術を編み出すぞっ！", 1, 1, 0, 0, 8, 1924, 105301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "たまには掃除でも\nするか。部屋が\n綺麗になるのは\n喜ばしいことだ", 1, 1, 0, 0, 1, 1926, 105302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "部屋の中では\n剣を振り回せないのが\n玉に瑕だな…", 1, 1, 0, 0, 2, 1927, 105302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…", 1, 1, 0, 0, 3, 1928, 105302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…", 1, 1, 0, 0, 4, 1929, 105302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "家庭を守れない者が\n国を守れるはずが\nない。この部屋も\n私が守るぞ！", 1, 1, 0, 0, 5, 1930, 105302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あいつは今頃、何を\nしているのだろうな…\nふふっ", 1, 8, 0, 0, 101, 1931, 105302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あいつと会える日が\nこんなにも待ち遠しく\nなるとはな…", 1, 8, 0, 0, 102, 1932, 105302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "軍人の本分は日々の\n研鑽にあるからな\n今日も鍛錬に\n行かないか？", 1, 1, 0, 0, 1, 1933, 105302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おお、今日も鍛錬の\n時間だったか！\nすまないな\nすぐ行く！", 1, 1, 0, 0, 2, 1934, 105302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私は子供ではない！\nが…たまには童心に\n帰りたい時もある…", 1, 1, 0, 0, 3, 1935, 105302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "貴公にも、飴を\nくれてやろうか？\nあまいぞ～！\n美味しいぞ～！", 1, 1, 0, 0, 4, 1936, 105302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私と歩きたいのか…？\nそうだな。二人で\n散歩でも行こうか", 1, 8, 0, 0, 101, 1937, 105302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "貴公と共にいられる\nことは私の喜びだ\nこれからも\nよろしく頼むぞ", 1, 8, 0, 0, 102, 1938, 105302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "うむ、いい朝だ\n本国の夜明けを\n思い出してしまうな！", 1, 1, 1, 0, 1, 1939, 105302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そろそろ昼時だな\n鍛錬の手を緩めて\n昼食にするか", 1, 1, 2, 0, 2, 1940, 105302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "寝る前に…あと少し\nだけ、鍛錬を…", 1, 1, 3, 0, 3, 1941, 105302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ヴァイス\nフリューゲルの一員と\nして恥ずかしくないよう\n身なりを整えなくては", 1, 1, 0, 0, 1, 1942, 105302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…", 1, 1, 0, 0, 2, 1943, 105302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うむ…このかわいさ…\n本国に持って\n帰りたい…", 1, 1, 0, 0, 3, 1944, 105302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "このともし火\n戦いに明け暮れる\n我らの心を\n癒すものだな…", 1, 1, 0, 0, 4, 1945, 105302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！", 1, 1, 0, 0, 5, 1946, 105302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うおおおおおっ！\n鍛錬、鍛錬\n鍛錬っ！！", 1, 1, 0, 0, 6, 1947, 105302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いつ戦いになっても\n困らぬように\n腹八分目に\nしておくのだ！", 1, 1, 0, 0, 7, 1948, 105302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うむ…脳細胞が\n活性化してきたぞ\n読書は大事だな", 1, 1, 0, 0, 8, 1949, 105302);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "私たちの騎空艇\nどんなデザインに\nしよう…？", 1, 1, 0, 0, 1, 1951, 105701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "団員募集の\nポスター\n作ろうっと♪", 1, 1, 0, 0, 2, 1952, 105701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "父さん…\n今頃どこで\n何してるんだろ？", 1, 1, 0, 0, 3, 1953, 105701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "騎空艇に乗って、\n誰もいったことがない\n場所を見つけたいな", 1, 1, 0, 0, 4, 1954, 105701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "父さん以上の\n世界一の\n騎空士になりたい！", 1, 1, 0, 0, 5, 1955, 105701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "仲間と行く空の旅…\n何度想像しても\nドキドキするなぁ", 1, 4, 0, 0, 101, 1956, 105701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "父さん…\n私にもね、大切な\n仲間ができたよ", 1, 8, 0, 0, 102, 1957, 105701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "仕事の依頼？\nすぐに支度\nしてくるね！", 1, 1, 0, 0, 1, 1958, 105701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ねえねえ！\n団員募集のポスター\n一緒に作ろうよ！", 1, 1, 0, 0, 2, 1959, 105701);
