INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "のんびりした時間帯\nですね…ふぁ～…\nし、失礼致しました", 1, 1, 2, 0, 2, 2140, 105902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふむ、都会は夜でも\nこんなにも、にぎやか\nなのですね", 1, 1, 3, 0, 3, 2141, 105902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "主さまに会う前に\n身なりを整えなくては\nいけませんね", 1, 1, 0, 0, 1, 2142, 105902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "このふかふかの優しさ\n主さまのようです", 1, 1, 0, 0, 2, 2143, 105902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいい♪\n主さまにそっくり♪", 1, 1, 0, 0, 3, 2144, 105902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この心地よさ…\n主さまのようです♪", 1, 1, 0, 0, 4, 2145, 105902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ここで洗濯物を\n乾かせるでしょうか　", 1, 1, 0, 0, 5, 2146, 105902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はぁっはぁっはぁっ\n…すべては\n主さまのため…", 1, 1, 0, 0, 6, 2147, 105902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "都会の味…\nとてもおいしいです\n…もぐもぐもぐ", 1, 1, 0, 0, 7, 2148, 105902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "都会のことを\n学ばないと…\nふむふむ", 1, 1, 0, 0, 8, 2149, 105902);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふぁ～…あっ\n見られちゃい\nましたか？", 1, 1, 0, 0, 1, 2151, 190801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ギルドハウスの\n使い心地は\nどうですか？", 1, 1, 0, 0, 2, 2152, 190801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ご用があれば\nなんでも言って\nくださいね～♪", 1, 1, 0, 0, 3, 2153, 190801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ときどきみなさんと\nクエストに行きたいな\nって思っちゃいます", 1, 1, 0, 0, 4, 2154, 190801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようございます！\nお掃除の方、\nはじめていきますね", 1, 1, 1, 0, 1, 2155, 190801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "皆さん、お昼ごはんの\nご要望がありましたら\n用意致しますよ♪", 1, 1, 2, 0, 2, 2156, 190801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ここは夜でも\nにぎやかですね", 1, 1, 3, 0, 3, 2157, 190801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "…\n少しうとうと\nしてしまった", 1, 1, 0, 0, 1, 2158, 104701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "室内だし兜を脱ぐか\n…いや\nやはりやめておこう", 1, 1, 0, 0, 2, 2159, 104701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "部屋にいると\n鎧が蒸し暑くなるが\n我慢せねば…", 1, 1, 0, 0, 3, 2160, 104701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "仕事が門番だからか\n部屋にいても\nじっとしてしまう…", 1, 1, 0, 0, 4, 2161, 104701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "少年と会話したことを\n思いだすと\n自然と楽しくなるな", 1, 1, 0, 0, 5, 2162, 104701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "少年のことを考えると\n顔がにやけてしまう", 1, 4, 0, 0, 101, 2163, 104701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "少年は今頃\n平和に過ごせて\nいるかな…", 1, 8, 0, 0, 102, 2164, 104701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ん？　悪いが\n兜は脱がないぞ", 1, 1, 0, 0, 1, 2165, 104701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私と遊びたいのか？\nそう言われても\n何をしていいやら…", 1, 1, 0, 0, 2, 2166, 104701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "鎧が重くないかって？\n大丈夫。けっこう\n鍛えているからな", 1, 1, 0, 0, 3, 2167, 104701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "少年、寒くはないか？\nいや自分がこうなので\n人が薄着に見えてね", 1, 1, 0, 0, 4, 2168, 104701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "君といると、兜の中が\nいつもより暑いんだ", 1, 4, 0, 0, 101, 2169, 104701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私としゃべりたい？\n物好きだな\nでも、嬉しいよ", 1, 8, 0, 0, 102, 2170, 104701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "早いな少年\nえ？　私の仕事に\n合わせてくれたのか", 1, 1, 1, 0, 1, 2171, 104701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "昼食の誘い？\n嬉しいけど\n人前では食べないんだ", 1, 1, 2, 0, 2, 2172, 104701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "王宮騎士団の\n日報を書かなければ…\n『何事もなし』、と", 1, 1, 3, 0, 3, 2173, 104701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "鎧を着ているから\n感触が分かりづらい\nのが残念だ…", 1, 1, 0, 0, 2, 2175, 104701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "か、かわいいな…\n私には似合わないが\nぎゅ…ってしよう", 1, 1, 0, 0, 3, 2176, 104701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "良い香りだ…\n普段はどうしても\n鉄くさくなるからな", 1, 1, 0, 0, 4, 2177, 104701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "わかっていたけど\n鎧を着たまま暖炉に\nあたると暑い…", 1, 1, 0, 0, 5, 2178, 104701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "兜を脱がずに食べる\nうん、段々コツが\n掴めてきた…", 1, 1, 0, 0, 7, 2180, 104701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "兜があっても\n意外と本くらいは\n難なく読めるんだ", 1, 1, 0, 0, 8, 2181, 104701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "居心地よくて\nゴロゴロしちゃうよ～\n…いつも通りだね", 1, 1, 0, 0, 1, 2183, 102601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ドングリの木\n近くに生えてない？", 1, 1, 0, 0, 2, 2184, 102601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "快適な寝床があれば\nそれだけで幸せだねぇ", 1, 1, 0, 0, 3, 2185, 102601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あたしと一緒に\nだらけてくれる人が\nいそうだねぇ", 1, 1, 0, 0, 4, 2186, 102601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここなら牧場よりも\n速く配達が\n届きそうだね～", 1, 1, 0, 0, 5, 2187, 102601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あんパン買ったし\nあいつにも\n分けてあげよう", 1, 4, 0, 0, 101, 2188, 102601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あいつと一緒に\nだらけられたら\n最高に幸せだね", 1, 8, 0, 0, 102, 2189, 102601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ドングリでも\nつまみながら\n一緒にゲームしようよ", 1, 1, 0, 0, 1, 2190, 102601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "横になりたいから\n枕になっておくれよ～\nあんたあったかいしさ", 1, 1, 0, 0, 2, 2191, 102601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ドングリあげるから\nあんパン買ってきて～", 1, 1, 0, 0, 3, 2192, 102601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "う～\n疲れちゃったよ～\nおんぶして運んで～", 1, 1, 0, 0, 4, 2193, 102601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あんたといると\nリア充って\n感じがするよ", 1, 4, 0, 0, 101, 2194, 102601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あんたが一生\n世話してくれたら\n人生安泰なんだけどね", 1, 8, 0, 0, 102, 2195, 102601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふわ～っ…\nおはよう…あたしは\nまだ眠いよ", 1, 1, 1, 0, 1, 2196, 102601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そろそろお昼寝でも\nしたい気分だねぇ", 1, 1, 2, 0, 2, 2197, 102601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "徹夜でゲームするけど\nあんたもどうだい？\nあたしは強いよ", 1, 1, 3, 0, 3, 2198, 102601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あ…だめだこれ。\n気持ち良くて\n落ちそう…", 1, 1, 0, 0, 2, 2200, 102601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいいものに\n囲まれて寝るのも\nよさそうだねぇ…", 1, 1, 0, 0, 3, 2201, 102601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "心身ともに\nリラックスした状態で\nぐうたら出来そうだね", 1, 1, 0, 0, 4, 2202, 102601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふい～…\nごくらくごくらく…\n暖かいねぇ", 1, 1, 0, 0, 5, 2203, 102601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "食べてすぐ寝ると\n牛になるって\n本当なのかな？", 1, 1, 0, 0, 7, 2205, 102601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "おー！　この本なら\nあたしも居眠りせずに\n読めそうだよ", 1, 1, 0, 0, 8, 2206, 102601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ランドソル…\nおっきな街だなぁ…", 1, 1, 0, 0, 1, 2208, 106301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ロザリア…\nきっと、いるよね？", 1, 1, 0, 0, 2, 2209, 106301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "最近、弓の練習\nサボっちゃってるな…", 1, 1, 0, 0, 3, 2210, 106301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今日の私は\n森番じゃなくて\n家番だよ！", 1, 1, 0, 0, 4, 2211, 106301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ちょっとだけ\n慣れてきたかも？", 1, 1, 0, 0, 5, 2212, 106301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "いつか、\nあの人と一緒に…", 1, 4, 0, 0, 101, 2213, 106301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "…なんか最近、\nあの人のこと\nばっかり…", 1, 8, 0, 0, 102, 2214, 106301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もしよかったら\n街に出かけない？", 1, 1, 0, 0, 1, 2215, 106301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この部屋、\n妖精さんも\n気に入るかも！", 1, 1, 0, 0, 2, 2216, 106301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日は\nなににしよっか？", 1, 1, 0, 0, 3, 2217, 106301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日はどこ行く？", 1, 1, 0, 0, 4, 2218, 106301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "たまには二人で…\nな、なんちゃって！", 1, 4, 0, 0, 101, 2219, 106301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "最近、\nずっと楽しいんだ！\nあなたのおかげかな", 1, 8, 0, 0, 102, 2220, 106301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよう！\n今日も一緒に\n頑張ろうね！", 1, 1, 1, 0, 1, 2221, 106301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼はどうする？\nあなたのオススメに\n連れて行って欲しいな", 1, 1, 2, 0, 2, 2222, 106301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ランドソルは\n夜でも賑やかだね！", 1, 1, 3, 0, 3, 2223, 106301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "わぁっ！\nすっごく\nふわふわだよ！", 1, 1, 0, 0, 2, 2225, 106301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわい～！\nぎゅってしたく\nなっちゃうかも！", 1, 1, 0, 0, 3, 2226, 106301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "くんくん\nなんか…いい匂い？", 1, 1, 0, 0, 4, 2227, 106301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "すっごいなー\nあったかいなー！", 1, 1, 0, 0, 5, 2228, 106301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "わぁっ、おいしい！\nもっと食べたい\nかも…！", 1, 1, 0, 0, 7, 2230, 106301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "も、文字は苦手…\nでも、頑張らないと\nだよね…！", 1, 1, 0, 0, 8, 2231, 106301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "にんじんとピーマンを\n食べないでいい方法\nないかな…", 1, 1, 0, 0, 1, 2233, 103601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "カップケーキが\n食べたいな…", 1, 1, 0, 0, 2, 2234, 103601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "予習が終わりました！\nさて、次は復習を\nしないと…", 1, 1, 0, 0, 3, 2235, 103601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あの人の部屋にいる\n私って…もしかして\nふしんしゃなんじゃ？", 1, 1, 0, 0, 4, 2236, 103601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "男の人は、みんな\nへんたいだと\n聞きました…", 1, 1, 0, 0, 5, 2237, 103601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あの人に、一緒に\n遊んでほしいけど…\nな、なんていえば？", 1, 4, 0, 0, 101, 2238, 103601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "早く大人になりたいな\n…あの人と同じくらい", 1, 8, 0, 0, 102, 2239, 103601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ぼ、防犯魔石を\n鳴らしますよ！？", 1, 1, 0, 0, 1, 2240, 103601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "へんたい！？　…\nあ、あなたでしたか～\nやっぱりへんたーい！", 1, 1, 0, 0, 2, 2241, 103601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "これ宿題です\nやっておいて\nくださいね", 1, 1, 0, 0, 3, 2242, 103601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あ、遅刻ですよ\n待っていたんですから", 1, 1, 0, 0, 4, 2243, 103601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "にらめっこですか？\nあ、あなたの前で変顔\nするのはちょっと…", 1, 4, 0, 0, 101, 2244, 103601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あの…何歳まで\n子供だと思いますか？", 1, 8, 0, 0, 102, 2245, 103601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "顔を洗って、着替えて\n朝ごはんを食べて、\n歯を磨いて…", 1, 1, 1, 0, 1, 2246, 103601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "て、鉄棒の練習！？　\nほ、他のことを\nしませんか？", 1, 1, 2, 0, 2, 2247, 103601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ね、眠くないです\n子供ではありません\nから…ふわぁ", 1, 1, 3, 0, 3, 2248, 103601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ひと休みしよっと\n………うん、\nひと休みおしまい♪", 1, 1, 0, 0, 2, 2250, 103601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "つ、つぶらな瞳には\n弱いんです", 1, 1, 0, 0, 3, 2251, 103601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "リラックスできますね\n集中力も\n上がりそうです", 1, 1, 0, 0, 4, 2252, 103601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったかいと\n眠くなっちゃうな…\n勉強しなくちゃなのに", 1, 1, 0, 0, 5, 2253, 103601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ピーマンもこれくらい\nおいしければ\nいいのに…", 1, 1, 0, 0, 7, 2255, 103601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この本、むずかしい\n…もっと勉強\nがんばらなくちゃ", 1, 1, 0, 0, 8, 2256, 103601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今日はみんなに\nなにを読んで\nあげようかしら", 1, 1, 0, 0, 1, 2258, 101501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "子どもたちと同じ\n目線で考えるのが\n大事よね", 1, 1, 0, 0, 2, 2259, 101501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お天気が良い日は\nみんなでお散歩に\n行きたいわね～", 1, 1, 0, 0, 3, 2260, 101501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "鬼ごっこにかくれんぼ\n…みんなは\nどっちが好きかしら", 1, 1, 0, 0, 4, 2261, 101501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お遊戯会の配役、\n考え出すと\n大変だわ～", 1, 1, 0, 0, 5, 2262, 101501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "彼は優しいから、\nきっと良い\nお父さんになるわね", 1, 4, 0, 0, 101, 2263, 101501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "彼には私がいないと\n…っていうのは少し\n過保護すぎるかしら？", 1, 8, 0, 0, 102, 2264, 101501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "保育園の行事を\n考えてるんだけど…\n良い案ないかしら？", 1, 1, 0, 0, 1, 2265, 101501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今度、子どもたちと\nおやつを作るから、\n食べに来てね", 1, 1, 0, 0, 2, 2266, 101501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "よかったら一緒に\n絵本を作らない？", 1, 1, 0, 0, 3, 2267, 101501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたはいつも元気ね\n先生も見習わないと♪", 1, 1, 0, 0, 4, 2268, 101501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "先生は\nずーっと、ずーっと、\nあなたの味方だからね", 1, 4, 0, 0, 101, 2269, 101501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたみたいな\nいい子をお世話できて\n先生は幸せものだわ", 1, 8, 0, 0, 102, 2270, 101501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよう～\nちゃんと朝ごはんは\n食べた？", 1, 1, 1, 0, 1, 2271, 101501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼寝の時間ね♪\nあなたも一緒に\n寝ましょうか", 1, 1, 2, 0, 2, 2272, 101501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "すっかり真っ暗ね。\n今日も一日、\nよくできました♪", 1, 1, 3, 0, 3, 2273, 101501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふかふかねぇ♪\n年甲斐もなく、\n飛び跳ねちゃいそう", 1, 1, 0, 0, 2, 2275, 101501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あらあら、かわいい！\n保育園のみんなに\n見せてあげたいわ", 1, 1, 0, 0, 3, 2276, 101501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい香りね～\n保育園にも\n置こうかしら", 1, 1, 0, 0, 4, 2277, 101501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "寒い日は、ここで\n子どもたちに絵本を\n読んであげましょ♪", 1, 1, 0, 0, 5, 2278, 101501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "美味しい～♪\n遠足に持っていったら\nみんな喜びそう", 1, 1, 0, 0, 7, 2280, 101501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "みんなが\n楽しめる読み聞かせの\n方法特集♪　素敵ねぇ", 1, 1, 0, 0, 8, 2281, 101501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "世界の前に、\nこの部屋を征服\nしてやろう♪", 1, 1, 0, 0, 1, 2283, 104401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "拠点とはやはり\n斯くあるべきじゃの", 1, 1, 0, 0, 2, 2284, 104401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "眷属の物はわらわの物\n故に、この部屋は\nわらわの部屋じゃな！", 1, 1, 0, 0, 3, 2285, 104401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "永く眠っている間に\n随分と世界は変わった\nようじゃな…", 1, 1, 0, 0, 4, 2286, 104401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "うるさいのう～\nまったく、近頃の\n若いもんは…", 1, 1, 0, 0, 5, 2287, 104401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あやつが来てから\n動悸が治まらぬ…\n如何ような病で\nあろうかの…", 1, 4, 0, 0, 101, 2288, 104401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "眷属よ、どこに行った\nのじゃ？\nわらわの世話を\n許可するのじゃ", 1, 8, 0, 0, 102, 2289, 104401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "いずれ世界は\n我が手中に収まる\nふふふ、\n楽しみじゃのう", 1, 1, 0, 0, 1, 2290, 104401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "眷属よ、わらわから\n離れてはならんぞ！\n子供に戻ってしまう\nではないか！", 1, 1, 0, 0, 2, 2291, 104401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "トマトジュースが\n欲しいのじゃ！\n野菜ジュースでは\nダメだぞ！", 1, 1, 0, 0, 3, 2292, 104401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "蚊にくわれたぞ！\nわらわの血を吸う\nとは～！", 1, 1, 0, 0, 4, 2293, 104401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "こら、頭をなでるな\nお主はこの姿でも\n子供扱いするのか？", 1, 4, 0, 0, 101, 2294, 104401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お主が望むなら世界の\n半分をくれてやっても\n良いのじゃぞ？", 1, 8, 0, 0, 102, 2295, 104401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "朝早くから感心じゃの\nさすがわらわの\n眷属じゃ", 1, 1, 1, 0, 1, 2296, 104401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "太陽は苦手じゃ…\nこの部屋の中で\n過ごそうぞ", 1, 1, 2, 0, 2, 2297, 104401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜も更けてきた！\nわらわ達の時間は\nこれからじゃ～♪", 1, 1, 3, 0, 3, 2298, 104401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "よい座り心地じゃ\nわらわの玉座としよう", 1, 1, 0, 0, 2, 2300, 104401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "可愛いではないか\nわらわの眷属に\nしてやろうかのう", 1, 1, 0, 0, 3, 2301, 104401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うむ、良い気持ちじゃ\nこれを作った者、\n褒めてつかわすぞ", 1, 1, 0, 0, 4, 2302, 104401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あたたかい…\n身も心も\n浄化されるようじゃ", 1, 1, 0, 0, 5, 2303, 104401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "吸血鬼のわらわが、\nこのようなもの…\nうまい♪", 1, 1, 0, 0, 7, 2305, 104401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "このまま大人の姿を\n保つ方法…どこかに\n書いてないかのう", 1, 1, 0, 0, 8, 2306, 104401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "いろんな人がいて\nいろんな服を着てて…\n目の保養になります♪", 1, 1, 0, 0, 1, 2308, 105401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "レイ様への新作\nどんな服にしよう…\n方向性が絞れない～！", 1, 1, 0, 0, 2, 2309, 105401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "忘れないうちに\n隠し糸のメンテナンス\nやっておこうっと！", 1, 1, 0, 0, 3, 2310, 105401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "むっ！　あの人の\nスリーサイズは…\nほほう…なるほど♪", 1, 1, 0, 0, 4, 2311, 105401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ノゾミさんの\n衣装はああして\nチカさんのは…", 1, 1, 0, 0, 5, 2312, 105401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "私がアイドル…\n…実感ないけど\n精一杯やってみよう", 1, 4, 0, 0, 101, 2313, 105401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "まさか私が\nアイドルになるなんて\n思ってなかったです", 1, 8, 0, 0, 102, 2314, 105401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なんですかぁ？\n衣装作りで疲れて\n眠いんですぅ…", 1, 1, 0, 0, 1, 2315, 105401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あーっ！　また\n服がほつれてる…\nしょうがないですね", 1, 1, 0, 0, 2, 2316, 105401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "はっ！　ちょうど今\n新しいデザインが\n思い浮かびました！", 1, 1, 0, 0, 3, 2317, 105401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "暇そうですね？\n時間あるなら仕事\n手伝ってください！", 1, 1, 0, 0, 4, 2318, 105401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どうしました？\n新しい服が\n欲しくなりましたか？", 1, 4, 0, 0, 101, 2319, 105401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "騎士さんのおかげで\nカルミナでも\n頑張れてますよ！", 1, 8, 0, 0, 102, 2320, 105401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようございます…\nなんで朝って毎日\nやってくるんでしょう", 1, 1, 1, 0, 1, 2321, 105401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お腹空きましたね\n騎士さんはちゃんと\n御飯食べましたか？", 1, 1, 2, 0, 2, 2322, 105401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜の方が\n作業が捗るのは\n私だけですかね？", 1, 1, 3, 0, 3, 2323, 105401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "このふかふか具合\nいい素材を\n使ってますね", 1, 1, 0, 0, 2, 2325, 105401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "小さくて可愛い♪\nそうだ！　レイ様の\nぬいぐるみを…！", 1, 1, 0, 0, 3, 2326, 105401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こういう時くらい\n何も考えずに\nリラックスしないと♪", 1, 1, 0, 0, 4, 2327, 105401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "炎を使った服って\nカッコ良いかも…\nでも材料がな～…", 1, 1, 0, 0, 5, 2328, 105401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "何をするにも\n食事は大切！\n体力が資本です！", 1, 1, 0, 0, 7, 2330, 105401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "私もいつか\nデザインを\nまとめて本に…", 1, 1, 0, 0, 8, 2331, 105401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "またお魚を\n捕りにいこうかな♪", 1, 1, 0, 0, 1, 2333, 107501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "そろそろみんなも\nお腹が空くころ\nじゃないですか～？", 1, 1, 0, 0, 2, 2334, 107501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここなら、\n日焼けの心配は\nありませんね！", 1, 1, 0, 0, 3, 2335, 107501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お刺身に焼き貝…\nお腹が魚介類を\n求めてますっ！", 1, 1, 0, 0, 4, 2336, 107501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "泳ぐのも食べるのも\n大得意です！", 1, 1, 0, 0, 5, 2337, 107501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あの人と二人で\n浜辺を散歩…\n素敵ですねぇ", 1, 4, 0, 0, 101, 2338, 107501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あの人に水着を\n褒めてもらえたら、\nすごく嬉しいなぁ", 1, 8, 0, 0, 102, 2339, 107501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "たまには釣りじゃなく\n一緒に素潜りでも\n行きませんか？", 1, 1, 0, 0, 1, 2340, 107501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "男の子に水着を\n見られるのって\n少し緊張しちゃいます", 1, 1, 0, 0, 2, 2341, 107501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ギルドハウスの\nみんなとも、\n海に行きたいですね！", 1, 1, 0, 0, 3, 2342, 107501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたの水着、\n私が選んであげます！", 1, 1, 0, 0, 4, 2343, 107501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "泳ぎの練習なら\n付き合いますよ！\n手取り足取り♪", 1, 4, 0, 0, 101, 2344, 107501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "水着姿が好きなら\nいくらでも\n着てあげますよぉ♪", 1, 8, 0, 0, 102, 2345, 107501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "朝の海も\n幻想的で\nいいですよねぇ～！", 1, 1, 1, 0, 1, 2346, 107501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼はみんなで、\n海鮮バーベキューとか\nどうでしょう！", 1, 1, 2, 0, 2, 2347, 107501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜の海は星が\nきれいですよ！\n見に行きましょう！", 1, 1, 3, 0, 3, 2348, 107501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "泳ぎ疲れたので\nちょっと休憩です～", 1, 1, 0, 0, 2, 2350, 107501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あなたにも、\n可愛い水着を\n着せてあげたいです", 1, 1, 0, 0, 3, 2351, 107501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "なんだか、\nリゾート気分です…", 1, 1, 0, 0, 4, 2352, 107501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふい～、泳ぎで冷えた\n身体を暖めましょう～", 1, 1, 0, 0, 5, 2353, 107501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "おいひ～です～\nこれ、海水浴にも\n持っていきたいですね", 1, 1, 0, 0, 7, 2355, 107501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "南国のフルーツ図鑑\n…じゅるり…", 1, 1, 0, 0, 8, 2356, 107501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "わ～♪　おいしそ…\nか、かわいいですね～", 1, 1, 0, 0, 10, 2358, 107501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これくらい大きな貝…\n食べたいな～\n食べたいな～～", 1, 1, 0, 0, 11, 2359, 107501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この格好で\n部屋をうろつくのは\n不思議な感じがします", 1, 1, 0, 0, 1, 2360, 107601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "南国の果実を使って\n主さまにデザートを\nお作りしましょう", 1, 1, 0, 0, 2, 2361, 107601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "せっかくの水着ですし\nこれから定期的に\n海水浴に行きたいです", 1, 1, 0, 0, 3, 2362, 107601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "海の生き物も、\n森の生き物に負けず\n可愛かったです…", 1, 1, 0, 0, 4, 2363, 107601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "クロールの練習なら、\n部屋でもできそうです", 1, 1, 0, 0, 5, 2364, 107601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "主さまに、水着姿を\n褒めていただけた…\nえへへ♪", 1, 4, 0, 0, 101, 2365, 107601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "水着で主さまの\nお世話をしたら、\n喜びますでしょうか…", 1, 8, 0, 0, 102, 2366, 107601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お疲れでしたら\nわたくしの膝を\nお使いくださいね", 1, 1, 0, 0, 1, 2367, 107601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "主さま、海の家で\nつかうおこづかいを\n渡しておきますね", 1, 1, 0, 0, 2, 2368, 107601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "海の美しさは、\n森とはちがう\n魅力がありました", 1, 1, 0, 0, 3, 2369, 107601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この髪飾りですか？\n造花ですから\n水の中も平気です", 1, 1, 0, 0, 4, 2370, 107601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "水着でくっついて\n眠ると、いつもより\n密着して暖かいです", 1, 4, 0, 0, 101, 2371, 107601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "はじめての海が、\n主さまと一緒で\n幸せです♪", 1, 8, 0, 0, 102, 2372, 107601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようございます。\n泳ぎに行く準備は\n万端です", 1, 1, 1, 0, 1, 2373, 107601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "午後は泳ぐのをやめて\nスイカ割りなど\nいかがでしょう？", 1, 1, 2, 0, 2, 2374, 107601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜も更けてきました…\n波の音を聞きながら\n少しお話しませんか？", 1, 1, 3, 0, 3, 2375, 107601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あぅ…この家具には、\n人をダメにする魔法が\nかけられています…", 1, 1, 0, 0, 2, 2377, 107601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふふっ♪\nあなたも、海水浴に\n行きますか？", 1, 1, 0, 0, 3, 2378, 107601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この香り…\nきっと主さまも\n気に入ります", 1, 1, 0, 0, 4, 2379, 107601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これは暖かいです…\n後ほど主さまと一緒に\nあたりましょう", 1, 1, 0, 0, 5, 2380, 107601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "泳いだ後の食事は\n一段と美味しく\n感じます", 1, 1, 0, 0, 7, 2382, 107601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "深海魚の生態…\nこれは主さまにも\nお伝えしなくては…", 1, 1, 0, 0, 8, 2383, 107601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "顔立ちが、主さまに\n似ております…\nかわいらしい♪", 1, 1, 0, 0, 10, 2385, 107601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふかふかですね\n主さまのようです♪", 1, 1, 0, 0, 11, 2386, 107601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お掃除、お掃除\nふんふふ～ん☆\nあ、聞かれてました？", 1, 1, 0, 0, 1, 2387, 107701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "日焼けには回復魔法が\n効きますよ。どうして\n逃げるんですか～！", 1, 1, 0, 0, 2, 2388, 107701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今日はドジを\nしませんように！", 1, 1, 0, 0, 3, 2389, 107701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "接客業を極めれば\nメイドとして\n成長できるかも？", 1, 1, 0, 0, 4, 2390, 107701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "水着って\n動きやすいんですけど\n人の目が気になります", 1, 1, 0, 0, 5, 2391, 107701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あの人の気持ちを知る\n魔法とかあれば…\nな、なんでもないです", 1, 4, 0, 0, 101, 2392, 107701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "水着でご奉仕したら、\nあの人は喜ぶかな？", 1, 8, 0, 0, 102, 2393, 107701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "暑いのでしたら、\n私が魔法で涼しく…\nえ？　いらない？", 1, 1, 0, 0, 1, 2394, 107701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "何か御用ですか？\n特にないのでしたら、\nお話ししましょう♪", 1, 1, 0, 0, 2, 2395, 107701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "水着姿でも\nメイドはメイドです\nご奉仕しますよ！", 1, 1, 0, 0, 3, 2396, 107701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ひゃっ！　あ、危うく\n転ぶところでした…\n…きゃうっ！", 1, 1, 0, 0, 4, 2397, 107701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どうかしましたか？\nあなたの御用なら、\n喜んでお受けしますよ", 1, 4, 0, 0, 101, 2398, 107701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "水着姿をあなたに\n見られると、\nドキドキしちゃいます", 1, 8, 0, 0, 102, 2399, 107701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "朝は暑くてやる気が\n出ない？　でしたら、\nえぇい、元気になぁれ", 1, 1, 1, 0, 1, 2400, 107701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お腹がぺこぺこなら、\nお昼は海の家で\n食べましょう♪", 1, 1, 2, 0, 2, 2401, 107701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜の浜辺でお散歩も\nいいですね。たくさん\n転びそうですけど…", 1, 1, 3, 0, 3, 2402, 107701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "休憩しながら、\nお掃除すれば\n一石二鳥です！", 1, 1, 0, 0, 2, 2404, 107701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "きれいにすれば\nかわいさも倍増\nですよ～♪", 1, 1, 0, 0, 3, 2405, 107701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "素敵な香りです～\n救護院に置けば、\nみんな喜ぶかも～", 1, 1, 0, 0, 4, 2406, 107701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったかいですね…\nはっ！　いけません！\nメイドがサボるなんて", 1, 1, 0, 0, 5, 2407, 107701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "料理のできない\nメイドなんて…\nはぐはぐ、おいしい♪", 1, 1, 0, 0, 7, 2409, 107701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "水着姿で接客をすると\n売り上げが倍になる\n…本当ですか、これ", 1, 1, 0, 0, 8, 2410, 107701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "えへへ♪\n南の島まで\n乗せてください！", 1, 1, 0, 0, 10, 2412, 107701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "なんだか、\n人魚姫に\nなった気分です♪", 1, 1, 0, 0, 11, 2413, 107701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "みんなで過ごす\n休日っていいわよね…\nずっと続かないかな", 1, 1, 0, 0, 1, 2414, 107801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "海に浮輪でぷかぷか\n浮かぶのって\n癖になりそう", 1, 1, 0, 0, 2, 2415, 107801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ビーチにいると\n日焼けが心配だわ", 1, 1, 0, 0, 3, 2416, 107801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "水着姿って楽よね。\n室内だとちょっと\n落ち着かないけど…", 1, 1, 0, 0, 4, 2417, 107801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お、泳ぎの練習なんて\nしないからね！", 1, 1, 0, 0, 5, 2418, 107801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "楽しすぎて立場を\n忘れそう…あんたの\nせいなんだから！", 1, 4, 0, 0, 101, 2419, 107801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "みんなで遊んで\n美味しいもの食べて\nほんっとに楽しいっ♪", 1, 8, 0, 0, 102, 2420, 107801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "マジであっついわね…\nよっし！　海に入って\n涼みましょ！", 1, 1, 0, 0, 1, 2421, 107801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "シャワー浴びたばかり\nなのに…また、遊び\nたくなってきたわ！", 1, 1, 0, 0, 2, 2422, 107801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "遊び足りないって？\nしょうがないわね…\n付き合ってあげるわ♪", 1, 1, 0, 0, 3, 2423, 107801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "一応伝えておくけど\n猫って水が苦手なの！\nわかった！？", 1, 1, 0, 0, 4, 2424, 107801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あたしと話したいの？\nふふ。\nまぁ、いいわよ。\nほら隣に座りなさい", 1, 4, 0, 0, 101, 2425, 107801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "こんなに楽しいの\n初めてかも…\nずっと続けばいいのに", 1, 8, 0, 0, 102, 2426, 107801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "は、早起きしたのは、\n楽しみで眠れなかった\nからじゃないわよ！", 1, 1, 1, 0, 1, 2427, 107801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "昼食後のストレッチ\n終わり！　さあ、\nビーチが待ってるわ！", 1, 1, 2, 0, 2, 2428, 107801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜の海に散歩に\n行こうかな～\nあんたも来なさいよ☆", 1, 1, 3, 0, 3, 2429, 107801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "海ではしゃぎすぎて\n疲れたわ…ちょっと\n休もうかしら？", 1, 1, 0, 0, 2, 2431, 107801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こんな魔物がいたら\n操るのも楽しいかも", 1, 1, 0, 0, 3, 2432, 107801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "にゃう～♪\nまるで南国にいる\n気分ね♪", 1, 1, 0, 0, 4, 2433, 107801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったかぁい♪\n身体が冷えてきたから\n丁度いいわ！", 1, 1, 0, 0, 5, 2434, 107801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "運動後のご飯って\nなんでこんなに\n美味しいんだろ♪", 1, 1, 0, 0, 7, 2436, 107801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "どんなカナヅチでも、\n絶対必ず泳げる方法、\n書いてないかしら？", 1, 1, 0, 0, 8, 2437, 107801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あんたもあたしが\n操ってあげる！\nなんてね～♪", 1, 1, 0, 0, 10, 2439, 107801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "リゾートリゾートっ\nのんびりしましょ♪", 1, 1, 0, 0, 11, 2440, 107801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここなら思う存分\nデスクワークに\n励めるわね！", 1, 1, 0, 0, 1, 2441, 108001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "武器のメンテナンスも\nしっかりしないと\n最高効率は望めないわ", 1, 1, 0, 0, 2, 2442, 108001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "準備をしっかり整えて\nもっともっと\n稼がなくちゃね！", 1, 1, 0, 0, 3, 2443, 108001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "少し休んだら\n次のバイトに\n行ってくるわね！", 1, 1, 0, 0, 4, 2444, 108001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この格好でここに\nいるのって\n気恥ずかしいわね…", 1, 1, 0, 0, 5, 2445, 108001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "キミが望むなら\n今日のバイト\n休んでもいいわよ？", 1, 4, 0, 0, 101, 2446, 108001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "キミだけね…\n私のことをここまで\n理解してくれるのは", 1, 8, 0, 0, 102, 2447, 108001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "普段と違う格好\nだからって\nそんなに見ないでよ…", 1, 1, 0, 0, 1, 2448, 108001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "キミを立派な\nバイト戦士にするのが\n師匠としての役目よね", 1, 1, 0, 0, 2, 2449, 108001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そろそろ次のバイトに\n行かないと…\nキミも一緒に来る？", 1, 1, 0, 0, 3, 2450, 108001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "効率は大事だけど\nゆっくりするのも\n悪くないわね", 1, 1, 0, 0, 4, 2451, 108001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "一緒の時間が楽しくて\nバイトのことを\n忘れちゃいそうね", 1, 4, 0, 0, 101, 2452, 108001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "キミと一緒なら\nどんなミッションも\n楽勝ね！", 1, 8, 0, 0, 102, 2453, 108001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよう、いい朝ね！\n今日もめいっぱい\n稼ぐわよ！", 1, 1, 1, 0, 1, 2454, 108001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もうお昼だったのね…\n内職のバイトに\n集中しすぎちゃったわ", 1, 1, 2, 0, 2, 2455, 108001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "スケジュールは、と…\nこういう管理の徹底が\n効率を生み出すのよ♪", 1, 1, 3, 0, 3, 2456, 108001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "しっかり働いて休む…\n最高効率にまた一歩\n近づいたわね", 1, 1, 0, 0, 2, 2458, 108001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "なごんじゃうわね…\nよぉし、次のバイトも\n頑張らないと！", 1, 1, 0, 0, 3, 2459, 108001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい香りね…\n働く意欲が\n湧いてくるわ", 1, 1, 0, 0, 4, 2460, 108001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "身体が冷えたから\n助かるわ…冷えは\n女の天敵だもの", 1, 1, 0, 0, 5, 2461, 108001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "海のバイトは\n体力勝負よ\nエネルギーはしっかり\n摂取しないとね！", 1, 1, 0, 0, 7, 2463, 108001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "座学も大切ね\nもっと知識を\n身につけないと…", 1, 1, 0, 0, 8, 2464, 108001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "海の楽園みたいね…\n今だけは効率を\n忘れちゃいそう…", 1, 1, 0, 0, 10, 2466, 108001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "落ち着くわ…\n海とひとつになった\n気分ね！", 1, 1, 0, 0, 11, 2467, 108001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ちょっと休憩にゃ～\n潮風は体に\n良くないにゃ～", 1, 1, 0, 0, 1, 2468, 107901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "猫グッズで\n海の家を飾りたいにゃ", 1, 1, 0, 0, 2, 2469, 107901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "海の幸は美味しいけど\n水はイヤだにゃ…", 1, 1, 0, 0, 3, 2470, 107901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "開店にゃ！\nこのたい焼きなら\nきっと大繁盛にゃ！", 1, 1, 0, 0, 4, 2471, 107901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "海と言えば水着にゃ\n似合ってるかにゃ？", 1, 1, 0, 0, 5, 2472, 107901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "キミが頼むなら\n手伝ってあげても\nいいにゃ", 1, 4, 0, 0, 101, 2473, 107901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あたしの宝は\nここにあるにゃ～", 1, 8, 0, 0, 102, 2474, 107901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "にゃ？\nそんなに見つめても\n何も出ないにゃ", 1, 1, 0, 0, 1, 2475, 107901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今からキミを\nあたしの相棒に\n任命するにゃ！", 1, 1, 0, 0, 2, 2476, 107901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "これから\n開店の準備にゃ\nキミも手伝って\nくれるのかにゃ？", 1, 1, 0, 0, 3, 2477, 107901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "海の家で出す\n新しいメニューを\n考え中なのにゃ～", 1, 1, 0, 0, 4, 2478, 107901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "キミと一緒なら\n売上げナンバーワンに\nなれるにゃ！", 1, 4, 0, 0, 101, 2479, 107901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "キミと一緒にいると\n楽しくて、もっと\nいたくなるにゃ", 1, 8, 0, 0, 102, 2480, 107901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようにゃ！\n今日も頑張って\nいくにゃー！", 1, 1, 1, 0, 1, 2481, 107901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お腹すいたにゃ～…\nお魚、ちょっとだけ、\nつまみ食いするにゃ", 1, 1, 2, 0, 2, 2482, 107901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "明日の準備は万端にゃ\nいつでも\n出発できるにゃ", 1, 1, 3, 0, 3, 2483, 107901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "このふかふか具合\nまるで子猫の\n毛並みのようにゃ～", 1, 1, 0, 0, 2, 2485, 107901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "にゃにゃ～\nなんてかわいさにゃ～", 1, 1, 0, 0, 3, 2486, 107901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この匂い…\nもう少し香ばしいと\nいいかもしれないにゃ", 1, 1, 0, 0, 4, 2487, 107901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "にゃ～…\nあったかくて\nぬくぬくだにゃ～", 1, 1, 0, 0, 5, 2488, 107901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "働くにはまず体力にゃ\nしっかり食べて\nスタミナアップ\nだにゃ!", 1, 1, 0, 0, 7, 2490, 107901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふむふむ…\nここにお宝が\n眠ってるんだにゃ", 1, 1, 0, 0, 8, 2491, 107901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "シードレイクにゃ！\n本物は怖かったけど、\nぬいぐるみなら\nかわいいにゃ～", 1, 1, 0, 0, 10, 2493, 107901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "貝なのにふかふかの\nクッションにゃ！\n屋台の前にも\n置いてみたいにゃあ", 1, 1, 0, 0, 11, 2494, 107901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "人の家って、道場とは\n違った香りがするね", 1, 1, 0, 0, 1, 2495, 103701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ちょっと退屈だね…\nあの人をからかいに\n行こうかな♪", 1, 1, 0, 0, 2, 2496, 103701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "普通の女の子みたいに\n遊びたいって思う\nこともあるんだよね", 1, 1, 0, 0, 3, 2497, 103701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "剣の手入れを怠らない\nようにしないと。\nいざという時、困るし", 1, 1, 0, 0, 4, 2498, 103701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "もっと\n上手く立ち回って\n出世したいな", 1, 1, 0, 0, 5, 2499, 103701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "おかえりなさい！\nふふ、なんだか奥さん\nになった気分だよ♪", 1, 4, 0, 0, 101, 2500, 103701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "恥ずかしくて、あなた\nを見られないよ…\n私らしくないなぁ…", 1, 8, 0, 0, 102, 2501, 103701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私、お話好きなんだ。\nどうせなら楽しい話を\nしたいな♪", 1, 1, 0, 0, 1, 2502, 103701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "女の子っぽい子と、\n男勝りな子、あなたは\nどっちが好き？", 1, 1, 0, 0, 2, 2503, 103701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そろそろ見回りの\n時間かな。あなたも\n一緒に行く？", 1, 1, 0, 0, 3, 2504, 103701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "構って欲しいのかな？\nそれじゃ一緒に\nランニングに行こうか", 1, 1, 0, 0, 4, 2505, 103701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "二人で出かけるなら、\n手を繋がないとね。\nあはは、冗談冗談♪", 1, 4, 0, 0, 101, 2506, 103701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ねえ、あなた♪\nおや、顔が真っ赤だ！\n…ふふ、私も、かな？", 1, 8, 0, 0, 102, 2507, 103701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私は朝から鍛錬だよ。\n慣れてるけど大変だ。\nお互いに頑張ろうね", 1, 1, 1, 0, 1, 2508, 103701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "鍛錬でお腹ぺこぺこ。\nお昼ごはんが\n楽しみだね", 1, 1, 2, 0, 2, 2509, 103701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日も一日見回りと\n鍛錬を頑張ったよ。\nあなたもお疲れさま", 1, 1, 3, 0, 3, 2510, 103701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いざという時のため\nにも、休憩は大事だね", 1, 1, 0, 0, 2, 2512, 103701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こういうものには\n今まで\n縁がなかったけど。\nうん、可愛いね♪", 1, 1, 0, 0, 3, 2513, 103701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "精神統一に通じる\nものがあるね。\nふぅ、癒されるなぁ", 1, 1, 0, 0, 4, 2514, 103701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったかいなぁ。\nたまにはのんびり\nしようか", 1, 1, 0, 0, 5, 2515, 103701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "鍛錬後はすごくお腹が\n空くんだよね。\nう～ん、おいしい♪", 1, 1, 0, 0, 7, 2517, 103701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "剣術書だけで強くなる\nには飛びぬけた才能と\n努力が必要だよね", 1, 1, 0, 0, 8, 2518, 103701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "正義の刃で\n悪を挫く！\n…キマったッス♪", 1, 1, 0, 0, 1, 2520, 100501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ヒーローの\n秘密特訓を\n始めるッスよ～！", 1, 1, 0, 0, 2, 2521, 100501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "正義の味方目指して\n今日も明日も\n全力ッスよ♪", 1, 1, 0, 0, 3, 2522, 100501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "一日一善！\nヒーローだったら\n当たり前ッス♪", 1, 1, 0, 0, 4, 2523, 100501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "どうッスか\nこのポーズ！\nイイ感じッスか？", 1, 1, 0, 0, 5, 2524, 100501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "た、たまには\nよく頑張ったねって\n褒めてほしいッス…", 1, 4, 0, 0, 101, 2525, 100501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "自分を信じてくれる\nあんたと一緒なら\n何でもやれるッス！", 1, 8, 0, 0, 102, 2526, 100501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おつかれッス♪\n冒険の準備は\nばっちりッスよ！", 1, 1, 0, 0, 1, 2527, 100501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どうしたんッスか？\n…はっ！　もしや敵が\n迫ってるんスかっ！？", 1, 1, 0, 0, 2, 2528, 100501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "べ、別に\n頭を撫でてほしいとか\n思ってないッスよ？", 1, 1, 0, 0, 3, 2529, 100501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "スーパーヒーロー\nトラタイガーは\n絶対無敵ッス！", 1, 1, 0, 0, 4, 2530, 100501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あんたと自分で\n正義の心を燃やして\n悪を一網打尽ッス！", 1, 4, 0, 0, 101, 2531, 100501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あんたと一緒だと\n幸せな気持ちに\nなるんッス…♪", 1, 8, 0, 0, 102, 2532, 100501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お、早いッスね～♪\n今日も一日一善\nよろしくッス！", 1, 1, 1, 0, 1, 2533, 100501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "一休みしながら\nヒーローについて\n語り合うッスよ～！", 1, 1, 2, 0, 2, 2534, 100501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜のパトロールで\n一緒に平和を\n守るッス！", 1, 1, 3, 0, 3, 2535, 100501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふぅぅ…\n落ち着くッスね\n戦士の休息ッス♪", 1, 1, 0, 0, 2, 2537, 100501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "くぅぅ、可愛いッス…\nヒーローが守るべき\n愛らしさッスね！", 1, 1, 0, 0, 3, 2538, 100501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "なんスかこの香り？\nすぅぅ…ふぅぅ…\n力がみなぎるッス！", 1, 1, 0, 0, 4, 2539, 100501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "優しい光ッスね…\nヒーローの優しさ\nそのものッス！", 1, 1, 0, 0, 5, 2540, 100501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あむあむあむ…\nんぅう、元気が溢れて\n止まらないッス！", 1, 1, 0, 0, 7, 2542, 100501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "トラタイガーのお話は\n笑いあり涙ありで\n超おすすめッス！", 1, 1, 0, 0, 8, 2543, 100501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ハッピーハロウィン\nお父さん、お菓子は…\n食べられないね", 1, 1, 0, 0, 1, 2545, 108101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "トリックオアトリート\nお菓子をくれないと\n不吉な占いをします", 1, 1, 0, 0, 2, 2546, 108101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ふふ、あなたの後ろで\n霊のみなさんが\n踊り始めました", 1, 1, 0, 0, 3, 2547, 108101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "霊感がいつも以上に\n冴えているので\n占いの調子も良好です", 1, 1, 0, 0, 4, 2548, 108101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "父はハロウィンが\n大好きだそうです\n大胆な格好をする子が\n多いので…", 1, 1, 0, 0, 5, 2549, 108101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あなたは霊たちにも\n大人気ですよね\n私も見てほしいな…", 1, 4, 0, 0, 101, 2550, 108101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ハロウィンの賑やかさ\n今は好きです…\nあなたといられるので", 1, 8, 0, 0, 102, 2551, 108101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "霊のみなさんとの\nダンスパーティー…\n楽しみです", 1, 1, 0, 0, 1, 2552, 108101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お父さん、元気だね\nえ？　ここは色んな\n女の子に会えて幸せ？", 1, 1, 0, 0, 2, 2553, 108101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ハロウィンのお菓子\n食べすぎちゃったな…\nでも、美味しかった…", 1, 1, 0, 0, 3, 2554, 108101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたの女難の相…\n悪化していますね\nお祓いが必要かも", 1, 1, 0, 0, 4, 2555, 108101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あの、霊のみなさんが\nあなたと踊りたいと…\nえっと、私も…", 1, 4, 0, 0, 101, 2556, 108101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたと手を繋ぐと\n胸が苦しくなります\nでも、繋ぎたいな…", 1, 8, 0, 0, 102, 2557, 108101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ハッピーハロウィン…\n父が元気なので\n今日の運気は良好です", 1, 1, 1, 0, 1, 2558, 108101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "父はお昼寝中です\n本当に寝ているかは\n私にも分かりませんが", 1, 1, 2, 0, 2, 2559, 108101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お父さん、\nもう眠っちゃったの？\n…眠れるんだったっけ", 1, 1, 3, 0, 3, 2560, 108101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "落ち着きますね…\n霊のみなさんも\n喜んでいます", 1, 1, 0, 0, 2, 2562, 108101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "つん、つん。ふふっ♪\n癒やされます…", 1, 1, 0, 0, 3, 2563, 108101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "精神が研ぎ澄まされる\n香りですね…\n占いが捗ります", 1, 1, 0, 0, 4, 2564, 108101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったかいね\nお父さん…\nえ？　分からない？", 1, 1, 0, 0, 5, 2565, 108101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "霊のみなさんは\n食べられないので…\n私がいただきます", 1, 1, 0, 0, 7, 2567, 108101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お父さんを元に戻す\n手掛かり…\n書かれていないかな？", 1, 1, 0, 0, 8, 2568, 108101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "わぁ、お菓子が沢山…", 1, 1, 0, 0, 12, 2570, 108101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "無料でプリンが\nもらえるなんて、\nハロウィンはすごいの", 1, 1, 0, 0, 1, 2572, 108201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "生きてるときも、\nミヤコはハロウィンが\n好きだった気がするの", 1, 1, 0, 0, 2, 2573, 108201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "みんなに仮装を\n見せつけて、\nプリンをもらうの～", 1, 1, 0, 0, 3, 2574, 108201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この仮装で、\nみんなに恐怖を\n教えてやるの！", 1, 1, 0, 0, 4, 2575, 108201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "アオーン、アオーン！\nミヤコ、狼女に\nなりきってるの～", 1, 1, 0, 0, 5, 2576, 108201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "アイツに秘密で、\nパーティーの\n飾り付けをするの♪", 1, 4, 0, 0, 101, 2577, 108201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "アイツが狼男の\n仮装をすれば、\nおそろいなの～", 1, 8, 0, 0, 102, 2578, 108201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "プリンをくれなきゃ、\nオマエをカボチャに\nしてやるの～", 1, 1, 0, 0, 1, 2579, 108201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ハロウィンは幽霊でも\n参加できるから\n楽しいの～！", 1, 1, 0, 0, 2, 2580, 108201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "プリンがないなら\nカボチャプリンを\n食べればいいの！", 1, 1, 0, 0, 3, 2581, 108201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "プリンの可能性は\n無限大なの～", 1, 1, 0, 0, 4, 2582, 108201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "オマエの隣で\nプリンを食べると、\nいつもより美味しいの", 1, 4, 0, 0, 101, 2583, 108201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "オマエが死んでも、\nミヤコはずーっと\n一緒にいてやるの～", 1, 8, 0, 0, 102, 2584, 108201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "朝ごはんは\nカボチャプリンなの！\nハロウィンっぽいの～", 1, 1, 1, 0, 1, 2585, 108201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ランチは豪華に\nプリンアラモードなの", 1, 1, 2, 0, 2, 2586, 108201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜は幽霊たちと\nハロウィンするの！\nオマエも来るの！", 1, 1, 3, 0, 3, 2587, 108201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "幽霊にも休息は\n必要なの！\nプリンもってこいなの", 1, 1, 0, 0, 2, 2589, 108201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "オマエもミヤコの\nパーティーに\n招待してあげるの～", 1, 1, 0, 0, 3, 2590, 108201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "癒されるの～\nまるで天国なの～", 1, 1, 0, 0, 4, 2591, 108201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ここでなら、\n焼きプリンが\n作れるの！", 1, 1, 0, 0, 5, 2592, 108201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "腹が減っては\nハロウィンは\nできぬなの！", 1, 1, 0, 0, 7, 2594, 108201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "丸ごとカボチャ\nプリン特集！？\n美味しそうなの～♪", 1, 1, 0, 0, 8, 2595, 108201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お菓子がたくさんで、\nミヤコはとっても\nハッピーなの♪", 1, 1, 0, 0, 12, 2597, 108201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "魔女以外にも、\n色々な仮装を\nしてみたいわね", 1, 1, 0, 0, 1, 2599, 108301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "チョコやアメよりも、\nアタシに相応しい\nスイーツがあるはず…", 1, 1, 0, 0, 2, 2600, 108301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お菓子の食べ過ぎは、\nお母さんに\n怒られるかも…", 1, 1, 0, 0, 3, 2601, 108301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この格好で学院に\n行ったら、\nみんな驚くかしら", 1, 1, 0, 0, 4, 2602, 108301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "大人のレディは、\nどういうハロウィンを\n過ごすべきかしら…？", 1, 1, 0, 0, 5, 2603, 108301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お菓子、アイツの分も\nとっといてあげよ！\n大人の余裕ってやつね", 1, 4, 0, 0, 101, 2604, 108301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "アイツを魅了する\n魔法とかないかしら？", 1, 8, 0, 0, 102, 2605, 108301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おしゃれスイーツを\nくれなきゃ\nイタズラするわよ！", 1, 1, 0, 0, 1, 2606, 108301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "アタシの大人センスで\nアンタに似合う仮装を\n選んであげるわ！", 1, 1, 0, 0, 2, 2607, 108301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "不気味よねぇ…\nジャックオーランタン\nこ、怖くないわよ？", 1, 1, 0, 0, 3, 2608, 108301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ハロウィンが楽しくて\n宿題やり忘れてたわ…\nて、手伝って～！", 1, 1, 0, 0, 4, 2609, 108301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お菓子をもらうのも\nアンタと一緒なら\n恥ずかしくないわ！", 1, 4, 0, 0, 101, 2610, 108301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "アタシのエスコートを\n任せられるのは、\nアンタだけなんだから", 1, 8, 0, 0, 102, 2611, 108301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "朝食はオシャレに\nグリーンスムージー！\n…に、にがぁ", 1, 1, 1, 0, 1, 2612, 108301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "昼下がりはカフェで\n優雅なティータイム\n…憧れるわ～", 1, 1, 2, 0, 2, 2613, 108301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ハロウィンナイトの\nはじまりよ！\n街に繰り出しましょ！", 1, 1, 3, 0, 3, 2614, 108301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あう～、お菓子の\n食べすぎで\n動けな～い", 1, 1, 0, 0, 2, 2616, 108301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "アンタも仮装する？\nいまよりもーっと\nかわいくなるわよ！", 1, 1, 0, 0, 3, 2617, 108301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "大人な香りに\n包まれてる～♪", 1, 1, 0, 0, 4, 2618, 108301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ポカポカね～♪\n…ああー！\nチョコが溶けてる！", 1, 1, 0, 0, 5, 2619, 108301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "おいしい～♪\n…じゃなくて！\nび、美味ですわ～", 1, 1, 0, 0, 7, 2621, 108301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この服かわい～♪\nって、なにこの値段！\nお小遣い何年分よ！？", 1, 1, 0, 0, 8, 2622, 108301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お、お菓子なんかで\n喜ぶほど、子供じゃ…", 1, 1, 0, 0, 12, 2624, 108301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "次はどんな策で団長を\nからかってみようか…", 1, 1, 0, 0, 1, 2626, 107101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "む？\n血の臭いがするな\n祭りの時間か☆", 1, 1, 0, 0, 2, 2627, 107101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "平和に飼い殺される\n前に、混沌の愛を\n育みたいね", 1, 1, 0, 0, 3, 2628, 107101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "本気を出した団長と\nいつか手加減なしに\n殺り合いたいな", 1, 1, 0, 0, 4, 2629, 107101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "おばさん呼ばわりする\n新米騎士の小娘を\n懲らしめてやろうか", 1, 1, 0, 0, 5, 2630, 107101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "胸を触られるまで\n誓約のことなど\nすっかり忘れていたな", 1, 4, 0, 0, 101, 2631, 107101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "まさかこのワタシが\nプロポーズするとはな\n…笑えるじゃないか", 1, 8, 0, 0, 102, 2632, 107101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "面白い暇つぶしを\n思いついたんだ\n戯れに付き合え☆", 1, 1, 0, 0, 1, 2633, 107101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "退屈で死にそうだ\n愉しい話題を\nワタシに提供してくれ", 1, 1, 0, 0, 2, 2634, 107101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なんだ、暇なのか？\n心躍ることでも探しに\n出かけようではないか", 1, 1, 0, 0, 3, 2635, 107101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "このワタシを愉快に\nさせてくれる事でも\nあったのか？", 1, 1, 0, 0, 4, 2636, 107101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ワタシに求婚した男の\n数だと？\nいちいち覚えてないな", 1, 4, 0, 0, 101, 2637, 107101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "最強無敵の女と\n結婚できるんだ\n誇りに思えよ☆", 1, 8, 0, 0, 102, 2638, 107101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なにか愉快なことが\n起こりそうな朝だ☆\nワクワクするね", 1, 1, 1, 0, 1, 2639, 107101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "うーむ、腹がすいたな\n愉快なことで\nこの空腹を満たしたい", 1, 1, 2, 0, 2, 2640, 107101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "これからは大人の\n時間だ。愉しみ方を\n知りたいのかい？", 1, 1, 3, 0, 3, 2641, 107101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "このワタシとした事が\n穏やかな気分に\nなってしまうとは…", 1, 1, 0, 0, 2, 2643, 107101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "可愛い姿をしたものが\n惨たらしい\n醜態を晒すのを\n想像してしまう", 1, 1, 0, 0, 3, 2644, 107101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "悪くはない香りだ\nだが、ワタシは血の\n臭いを嗅ぎたいぞ☆", 1, 1, 0, 0, 4, 2645, 107101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ぬるい、ぬるすぎる！\n地獄の業火には\nほど遠い熱さだ！", 1, 1, 0, 0, 5, 2646, 107101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ワタシの腕前に\n勝るとも劣らない\nなかなかの味だ", 1, 1, 0, 0, 7, 2648, 107101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "意中の男性を絶対に\n落とす方法…？\nく、下らんっ！", 1, 1, 0, 0, 8, 2649, 107101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "明日はあの釣り場に\n行くとしようかな\nあの子も誘ってみるか", 1, 1, 0, 0, 1, 2651, 105601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ぷはーっ！\n体を動かしたあとの\n一杯は格別だね", 1, 1, 0, 0, 2, 2652, 105601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "天気が良いと\n昼寝か釣りかで\n迷っちまうな…", 1, 1, 0, 0, 3, 2653, 105601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "大物が釣れたときの\n快感は、他じゃ\n味わえないね", 1, 1, 0, 0, 4, 2654, 105601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ふあぁ～…\n昼寝でもするか", 1, 1, 0, 0, 5, 2655, 105601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ふふっ…\nあの子、見た目より\n根性あるんだね", 1, 4, 0, 0, 101, 2656, 105601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あの子が強くなるには\nどんな魚を食べさせて\nあげればいいのやら…", 1, 8, 0, 0, 102, 2657, 105601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "姉御って呼ぶんじゃ\nないよ。からかうと\nタダじゃおかないよ", 1, 1, 0, 0, 1, 2658, 105601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "釣りのコツ？\n何事も経験が大事だ\n今から行くかい？", 1, 1, 0, 0, 2, 2659, 105601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どうしたのさ？\n助太刀が必要かい？\nアタシにまかせときな", 1, 1, 0, 0, 3, 2660, 105601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "困ってる人がいたら\nまた力を貸しておくれ\n頼りにしてるよ", 1, 1, 0, 0, 4, 2661, 105601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なにか困りごとかい？\nどうやら昼寝は\nまた今度だね", 1, 4, 0, 0, 101, 2662, 105601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "よしよし！\nお人好しのお前さんを\n労ってあげるよ", 1, 8, 0, 0, 102, 2663, 105601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよう、お前さん\n魚屋は早起きでなきゃ\nつとまらないよ？", 1, 1, 1, 0, 1, 2664, 105601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "昼飯かい？　今朝\n仕入れた魚があるんだ\n食べてみるかい？", 1, 1, 2, 0, 2, 2665, 105601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜釣りにぴったりの\n月が出てるよ\nいい釣りが出来そうだ", 1, 1, 3, 0, 3, 2666, 105601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "コイツに座って\nのんびり釣りが\nできりゃいいのにな", 1, 1, 0, 0, 2, 2668, 105601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "よしよし…♪\nおっと、かわいい物も\nつい抱きしめちまうな", 1, 1, 0, 0, 3, 2669, 105601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これで魚の臭いを\n消せれば、猫も\n寄らなくなるのかね", 1, 1, 0, 0, 4, 2670, 105601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "冬場の釣りも\nこれくらい暖かけりゃ\nいいんだけどね", 1, 1, 0, 0, 5, 2671, 105601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "腹が減っては\n釣りもできぬ、ってな\nまずは腹ごしらえだ", 1, 1, 0, 0, 7, 2673, 105601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "へぇ…こんな魚が\nいたとは…いつか\n釣りたいもんだね", 1, 1, 0, 0, 8, 2674, 105601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "流石に室内で\n魔法ぶっぱしたら\n怒られるよねぇ", 1, 1, 0, 0, 1, 2676, 101301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "可愛い女の子が\nたくさんいる！\nここは楽園か！？", 1, 1, 0, 0, 2, 2677, 101301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "みんなに漫画とか\n勧めてみようかな！\n布教用のがあるし！", 1, 1, 0, 0, 3, 2678, 101301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "わ、私一人の\n部屋じゃないから\n色々自重せねば！", 1, 1, 0, 0, 4, 2679, 101301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "「ぬるぽ」\n…くっ、\n誰も反応なしか！", 1, 1, 0, 0, 5, 2680, 101301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "次はどの漫画を\n貸そうかなー♪", 1, 4, 0, 0, 101, 2681, 101301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "次の定期巡回で\nとっておきの店に\n連れてってあげよう！", 1, 8, 0, 0, 102, 2682, 101301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "君に似合いそうな\n衣装を買ったよ！\n君もいざ魔法少女に！", 1, 1, 0, 0, 1, 2683, 101301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "さあ！\n暇なら定期巡回に\nレッツラゴー！", 1, 1, 0, 0, 2, 2684, 101301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "戦闘だけじゃなくて、\n日常にも役立つ\n魔法を習得したいね！", 1, 1, 0, 0, 3, 2685, 101301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "新魔法思いついた！\n私って天才！？", 1, 1, 0, 0, 4, 2686, 101301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "決め台詞は超大事！\n君の決め台詞も\n私が考えてあげよう！", 1, 4, 0, 0, 101, 2687, 101301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私ってもしやリア充？\n…そんなバカな！\n非リア代表のはずが！", 1, 8, 0, 0, 102, 2688, 101301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよー…\n徹夜で本読んでたから\nまだ眠い～", 1, 1, 1, 0, 1, 2689, 101301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "昼飯食べて、\n午後の定期巡回も\n頑張りましょー！", 1, 1, 2, 0, 2, 2690, 101301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜からが、\nオタ活本番でしょ！", 1, 1, 3, 0, 3, 2691, 101301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふいー！\nくつろいで本を読む！\n最高だねー！", 1, 1, 0, 0, 2, 2693, 101301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "君、可愛いね～♪\n魔法少女の\n使い魔みたい！", 1, 1, 0, 0, 3, 2694, 101301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "良い匂いに\n釣られてしまった…\nまさか、孔明の罠！？", 1, 1, 0, 0, 4, 2695, 101301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "暖かいけど、\nここで本を読むのは\nちょっち危ないかな", 1, 1, 0, 0, 5, 2696, 101301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ウマ―！\nこれの詳細きぼんぬ！", 1, 1, 0, 0, 7, 2698, 101301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こ、古代語の本！？\n現代語でおk…", 1, 1, 0, 0, 8, 2699, 101301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ふふふっ♪\n次はどんな悪戯を\nしてやろうかしら♪", 1, 1, 0, 0, 1, 2701, 190901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "幻影湖の霧を吸った\n人間たちの\nリアクションって、\nサイコーよねぇ～♪", 1, 1, 0, 0, 2, 2702, 190901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "室内だけとはいえ…\n自由に動けるって、\nやっぱりいいわ～♪", 1, 1, 0, 0, 3, 2703, 190901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "改めて見ると良い\n部屋ね…悪戯とか\nしやすそうだしぃ♪", 1, 1, 0, 0, 4, 2704, 190901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "私を封じていた部屋\nなのに…急に居心地が\n良くなったわね♪", 1, 1, 0, 0, 5, 2705, 190901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あんたの頭、座り心地\n良いわね…私専用の\n椅子にしてあげる♪", 1, 1, 0, 0, 1, 2706, 190901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "外は大変みたいねぇ…\nまあ、私は好き勝手に\nやらせてもらうわ♪", 1, 1, 0, 0, 2, 2707, 190901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "顔に落書された？\n誰か知らないけど、\n悪いことする奴も\nいるものねぇ～♪", 1, 1, 0, 0, 3, 2708, 190901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "フィオのやつ…\nどこをウロチョロ\nしてるのかしら？", 1, 1, 0, 0, 4, 2709, 190901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "人間の朝は大変ねぇ…\n私はその様を見ながら\nゆっくりするわね♪", 1, 1, 1, 0, 1, 2710, 190901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "う～私も、お日様を\n浴びながら、お外を\nお散歩したーい！", 1, 1, 2, 0, 2, 2711, 190901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この暗さ…閉じ込め\nられていた時を\n思い出すわね…", 1, 1, 3, 0, 3, 2712, 190901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "悪戯しすぎて\n疲れたから、ちょっと\n休憩しよっと♪", 1, 1, 0, 0, 2, 2714, 190901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ガイド妖精たる私が、\nこんな造形物に\n惹かれる…わけが……\nかわいい♪", 1, 1, 0, 0, 3, 2715, 190901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この香りを囮にして、\n周りに悪戯を仕込む…\n完璧ね♪", 1, 1, 0, 0, 4, 2716, 190901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "不思議ねぇ…私は\n温度とか関係ないのに\n離れられないわ", 1, 1, 0, 0, 5, 2717, 190901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "食事の必要がない体…\nつまり、食べたければ\n食べていいのよね～♪", 1, 1, 0, 0, 7, 2719, 190901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "へぇ…最近はこんな\n悪戯もあるのねぇ…\nあいつで試さないと♪", 1, 1, 0, 0, 8, 2720, 190901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "メリークリスマス！\n歌で幸せを\n届けます…！", 1, 1, 0, 0, 1, 2722, 108401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あなたと出会えたのは\n偶然ではなくて…\nクリスマスの奇跡だと\n思うんです", 1, 1, 0, 0, 2, 2723, 108401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "クリスマスは華やかで\nつい子供のように\nはしゃいでしまいます", 1, 1, 0, 0, 3, 2724, 108401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "曲が書けるのは\nあなたが側に\nいてくれるからです…", 1, 1, 0, 0, 4, 2725, 108401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "感動を与えられる歌…\nあなたと一緒になら\n生み出せるはず…", 1, 1, 0, 0, 5, 2726, 108401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "クリスマスの幸せを\nあなたと\n感じ合えたらな…", 1, 4, 0, 0, 101, 2727, 108401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あなたの幸せを願った\nクリスマスソング…\nいつか披露しますね", 1, 8, 0, 0, 102, 2728, 108401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "みなさんの笑顔を\n守りたいから…\n私は歌い続けます", 1, 1, 0, 0, 1, 2729, 108401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "歌を届ける\nサンタさんに\nなりたいな…", 1, 1, 0, 0, 2, 2730, 108401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お暇でしたら\nクリスマスのお祝いを\nしませんか？", 1, 1, 0, 0, 3, 2731, 108401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ステージに立つ前に\n喉を整えないと…\n発声練習は念入りに…", 1, 1, 0, 0, 4, 2732, 108401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "まわりはかわいい子\nばっかりだけど…\nこの服なら\nアピールになるかな？", 1, 4, 0, 0, 101, 2733, 108401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "歌でならいくらでも\nあの人への気持ちを\n込められるのに…", 1, 8, 0, 0, 102, 2734, 108401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日も一日\n幸せな歌で世界が\n包まれますように…", 1, 1, 1, 0, 1, 2735, 108401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お茶を飲みながら\n好きな音楽を\n語り合いませんか？", 1, 1, 2, 0, 2, 2736, 108401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふふ、寝かしつけの\n子守唄を\nお望みですか？", 1, 1, 3, 0, 3, 2737, 108401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふかふかですね\n心地良すぎて\n眠ってしまいそう…", 1, 1, 0, 0, 2, 2739, 108401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "素敵…\n優しい歌みたいに\n気持ちが落ち着く…", 1, 1, 0, 0, 3, 2740, 108401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "インスピレーションが\n溢れてくる…\nいい曲が書けそう…", 1, 1, 0, 0, 4, 2741, 108401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この光みたいに\n灯火になるような\n歌を歌いたい…", 1, 1, 0, 0, 5, 2742, 108401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "喉の健康のためにも\n食事はしっかり\nとるようにしています", 1, 1, 0, 0, 7, 2744, 108401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "物語には\n曲作りのヒントが\n詰まっているんです", 1, 1, 0, 0, 8, 2745, 108401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "絵が飛び出してくる\nなんて…精霊が宿って\nいるのでしょうか？", 1, 1, 0, 0, 14, 2747, 108401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "か、可愛い…♪\nちょっとだけ触っても\nいいですよね…？", 1, 1, 0, 0, 15, 2748, 108401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "クリスマスでも、\n演劇の練習は\nしっかりやらないと！", 1, 1, 0, 0, 1, 2749, 108501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あうぅ…\nクリスマスの準備が\n終わらないよぅ…", 1, 1, 0, 0, 2, 2750, 108501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "アヤネちゃんが\n摘み食いしないように\n見ておかないと…", 1, 1, 0, 0, 3, 2751, 108501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "サンタさんみたいに、\n皆に笑顔で接する…\nうぅ…私には無理ぃ…", 1, 1, 0, 0, 4, 2752, 108501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "新しいお洋服～♪\nスズメお姉ちゃんに\nお礼をいわないと♪", 1, 1, 0, 0, 5, 2753, 108501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お兄ちゃんの為に、\nプレゼントを\n用意しないと♪", 1, 4, 0, 0, 101, 2754, 108501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "クリスマス…\n恋人の日…ひゃわっ！\nわ、私ってば何を…！", 1, 8, 0, 0, 102, 2755, 108501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "メリー\nクリスマスです、\nお兄ちゃん♪", 1, 1, 0, 0, 1, 2756, 108501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そ、そんなに\n見られると…\n恥ずかしいよぉ…", 1, 1, 0, 0, 2, 2757, 108501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ひゃわぁっ！？\n急に触られると、\n驚くからやめてぇ…", 1, 1, 0, 0, 3, 2758, 108501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あ…クリスマスの\n準備を手伝って\nくれるんですか？", 1, 1, 0, 0, 4, 2759, 108501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お兄ちゃんと\n過ごすクリスマス、\n楽しみですぅ…♪", 1, 4, 0, 0, 101, 2760, 108501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "クリスマスはその…\n二人で…あうぅ…\nなんでもないですぅ…", 1, 8, 0, 0, 102, 2761, 108501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "うう…パーティーが\n楽しみで、あんまり\n眠れなかったよぅ…", 1, 1, 1, 0, 1, 2762, 108501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ケーキを食べられなく\nなっちゃうから、\nお昼は軽めにしないと", 1, 1, 2, 0, 2, 2763, 108501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ちょっと眠いけど…\n今日は少しだけ\n夜更かししたいです♪", 1, 1, 3, 0, 3, 2764, 108501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふかふかぁ…\nクリスマスの準備中に\n眠くなっちゃいますぅ", 1, 1, 0, 0, 2, 2766, 108501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この子と一緒に\nおままごとしたいな♪", 1, 1, 0, 0, 3, 2767, 108501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい匂い…♪\nみんなで\nアロマパーティー\nしたいなぁ…♪", 1, 1, 0, 0, 4, 2768, 108501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ぬっくぬく…♪\nもう、ここから\n離れたくないよぉ…♪", 1, 1, 0, 0, 5, 2769, 108501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はむはむ…♪\nクリスマスのお料理\nより美味しいかも～♪", 1, 1, 0, 0, 7, 2771, 108501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "すごい…\nクリスマスの\n演劇って、\nこんなにあるんだ…", 1, 1, 0, 0, 8, 2772, 108501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "すごい…まるで、\nクリスマスの演劇を\n観てるようですぅ…♪", 1, 1, 0, 0, 14, 2774, 108501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "とっても可愛いです♪\nお芝居の相手役に\nしたいなぁ…♪", 1, 1, 0, 0, 15, 2775, 108501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "甘ぁいケーキも、\nデッカイ七面鳥も、\nぜーんぶ私のだー♪", 1, 1, 0, 0, 1, 2776, 108601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "毎日が\nクリスマスだったら\nいいのにな～♪", 1, 1, 0, 0, 2, 2777, 108601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "プレゼントはあるし、\n料理は美味しいし、\nクリスマス最高ー♪", 1, 1, 0, 0, 3, 2778, 108601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここでぷうきちの\nクリスマス一発芸♪\n『無茶ぶりするな！』", 1, 1, 0, 0, 4, 2779, 108601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ぷうきちがいつもより\n重いかも…太ったの？\n『衣装の重さだよ！』", 1, 1, 0, 0, 5, 2780, 108601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お兄ちゃんが\n一緒にいてくれると\nクリスマス楽しいね♪", 1, 4, 0, 0, 101, 2781, 108601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お兄ちゃんに抱きつく\nと、なんだかドキドキ\nする…なんでかな？", 1, 8, 0, 0, 102, 2782, 108601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "メリークリスマス♪\nプレゼントは、\nいつでも受付中だよ♪", 1, 1, 0, 0, 1, 2783, 108601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "このクマさん耳\n可愛いでしょ♪\nぷうきちと\nお揃いなんだー♪", 1, 1, 0, 0, 2, 2784, 108601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お兄ちゃん…私、\n甘ぁいケーキが\n食べたいなぁ…♪", 1, 1, 0, 0, 3, 2785, 108601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "クリスマスぷうきちは\nぬくぬく感が凄いよ！\nお兄ちゃんもさわる？", 1, 1, 0, 0, 4, 2786, 108601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "えへへ…お兄ちゃんの\n手は、大きくて、\n暖かくて大好き～♪", 1, 4, 0, 0, 101, 2787, 108601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "来年のクリスマスも、\nこうやって\nお兄ちゃんに\n甘えていたいな～♪", 1, 8, 0, 0, 102, 2788, 108601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あれぇ…\n私のケーキはぁ…？\n『ちゃんと起きろ』", 1, 1, 1, 0, 1, 2789, 108601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼はデッカイ\n七面鳥が食べたーい♪\n『夜まで我慢しろ』", 1, 1, 2, 0, 2, 2790, 108601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "『もう寝る時間だぞ』\nサンタさんが来る\nまで起きてるぅ…", 1, 1, 3, 0, 3, 2791, 108601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "このふかふか具合…\nぷうきち！\n負けてられないよ！", 1, 1, 0, 0, 2, 2793, 108601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この子、\nすっごくかわいい～♪\n『ライバル登場か！』", 1, 1, 0, 0, 3, 2794, 108601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "良い匂い…あっ！\nぷうきちにこの\n匂いを移せば…！\n『匂い袋にするな』", 1, 1, 0, 0, 4, 2795, 108601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったか～い♪\nえへへ、ぷうきちも\nあっためてあげる♪", 1, 1, 0, 0, 5, 2796, 108601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ぷうきち！　これ、\nとってもおいしいよ♪\n『食べすぎるなよ』", 1, 1, 0, 0, 7, 2798, 108601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お、大人の私なら、\nこれぐらい…読め…\n『無理するな…』", 1, 1, 0, 0, 8, 2799, 108601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "絵が飛び出す…\nぷうきちに絵本を\n入れれば、イタズラに\n使えるかも…♪", 1, 1, 0, 0, 14, 2801, 108601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "か、可愛いけど…\nぷうきちサンタの方が\n可愛いんだからぁ…！", 1, 1, 0, 0, 15, 2802, 108601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "着物も慣れると\n動きやすいものさ", 1, 1, 0, 0, 1, 2803, 108901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "鼻緒が緩んでしまった\n下駄で修業は\n無理があったかな…", 1, 1, 0, 0, 2, 2804, 108901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "小腹が空いた…\nユイが作った餅、\nまだ食べられるかな？", 1, 1, 0, 0, 3, 2805, 108901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "服装も変わったし\nいつもと違う戦い方を\n想定しないと…", 1, 1, 0, 0, 4, 2806, 108901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "…ヒヨリは動きすぎて\n着崩れしてないか\n心配だ", 1, 1, 0, 0, 5, 2807, 108901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "これからもずっと\n仲間と、彼と\n一緒に…", 1, 4, 0, 0, 101, 2808, 108901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "願いを叶えるために\nもっと自分を\n鍛えていかないと！", 1, 8, 0, 0, 102, 2809, 108901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "頭の飾りがずれてた？\nそうか、直してくれて\nありがとう", 1, 1, 0, 0, 1, 2810, 108901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "着物がそんなに\n珍しい？\nキミも着てみる？\n…冗談だよ", 1, 1, 0, 0, 2, 2811, 108901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "肩掛けの触り心地が\n気に入った？\nならもっと触る？", 1, 1, 0, 0, 3, 2812, 108901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ああ、この刀？\n釣り仲間に見繕って\nもらったんだ", 1, 1, 0, 0, 4, 2813, 108901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "キミといると\n気持ちが段々と\n安らいでいくよ", 1, 4, 0, 0, 101, 2814, 108901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "キミは私の行く先を\n照らしてくれる\n光のような存在だよ", 1, 8, 0, 0, 102, 2815, 108901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよう\n朝の修行を始めるけど\nキミもどう？", 1, 1, 1, 0, 1, 2816, 108901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この時間に軽く\n休憩をとるといい\n能率がよくなるよ", 1, 1, 2, 0, 2, 2817, 108901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もう夜か…\n明日もまた頑張ろう\nそれじゃ、おやすみ", 1, 1, 3, 0, 3, 2818, 108901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "…疲れていたのかな\nもう少しで眠る\nところだった…", 1, 1, 0, 0, 2, 2820, 108901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふふっ、正月衣装を\n着せてやるのも\nよさそうだ", 1, 1, 0, 0, 3, 2821, 108901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふぅ…いい香りだ\n慌ただしい日々から\n心が解放される", 1, 1, 0, 0, 4, 2822, 108901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この心地よさ…\n寝てしまわないよう\n気をつけないと", 1, 1, 0, 0, 5, 2823, 108901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "おいしいけど\n帯がきつくなる前に\n手を止めよう…", 1, 1, 0, 0, 7, 2825, 108901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "外を出歩くのも\nいいけど、中で\n読書もありだな", 1, 1, 0, 0, 8, 2826, 108901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "…騎士クンって\nおせち料理で\n何が好きなんだろう？", 1, 1, 0, 0, 1, 2828, 108801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "着物でお掃除や\nお洗濯するの\n慣れてきた♪", 1, 1, 0, 0, 2, 2829, 108801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今年は\nどんな福袋を\n買おうかな…♪", 1, 1, 0, 0, 3, 2830, 108801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お餅に使う調味料\nみんなで違うから\n色々用意しなきゃ", 1, 1, 0, 0, 4, 2831, 108801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ほっぺがお餅みたいに\nぷにぷに…\n食べ過ぎちゃった…", 1, 1, 0, 0, 5, 2832, 108801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "来年も、再来年も\nみんなで一緒に\n新年を迎えたいな", 1, 4, 0, 0, 101, 2833, 108801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お願いです…\nいつかこの気持ちが\n伝わりますように…", 1, 8, 0, 0, 102, 2834, 108801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "髪飾りが落ちそう？\nじゃあ…騎士クン\nつけ直してくれる？", 1, 1, 0, 0, 1, 2835, 108801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "着物が着たいの？\nじゃあ今度一緒に\n買いに…行く？", 1, 1, 0, 0, 2, 2836, 108801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あっ、騎士クン\nお雑煮のお餅\nいくつ入れたい？", 1, 1, 0, 0, 3, 2837, 108801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "外、寒かったよね？\nはいこれ。熱い甘粥\n飲むと温まるよ", 1, 1, 0, 0, 4, 2838, 108801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今年は騎士クンと…\nえっ？　騎士クン！？\nな、なんでもないよ！", 1, 4, 0, 0, 101, 2839, 108801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "騎士クンの願い事\nわたしが\n叶えてあげたいな…", 1, 8, 0, 0, 102, 2840, 108801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよう\n太陽の光に当たると\n元気が出るよ", 1, 1, 1, 0, 1, 2841, 108801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼ごはん\n作らなきゃ…\n騎士クンも食べる？", 1, 1, 2, 0, 2, 2842, 108801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "一日が終わるのは\nあっという間だよね\n一年も同じなのかな？", 1, 1, 3, 0, 3, 2843, 108801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふぅ…\n帯も少し\nゆるめちゃおう…", 1, 1, 0, 0, 2, 2845, 108801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お正月用に\n着物を縫って\nあげたいな", 1, 1, 0, 0, 3, 2846, 108801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これでお肌が\n保湿出来たら\nいいのになぁ", 1, 1, 0, 0, 4, 2847, 108801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ん～…\nぬくぬくしてて\n動きたくないよ～", 1, 1, 0, 0, 5, 2848, 108801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "…冬眠しないのに\n熊さんみたいに\n食べちゃいそう", 1, 1, 0, 0, 7, 2850, 108801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "年末年始は\n告白のチャンス…\nむ、無理無理！", 1, 1, 0, 0, 8, 2851, 108801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "みんな忙しそう…\n待っててね、\nすぐ助けるから！", 1, 1, 0, 0, 1, 2853, 108701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ユイちゃんと\nレイさん…よし！\nどっちも手伝おう！", 1, 1, 0, 0, 2, 2854, 108701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あっ、着崩れてる！\nわわっ、どうしよ！\n帯が結べないよー！", 1, 1, 0, 0, 3, 2855, 108701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "初詣楽しみー！\n出店はどこから\n行こうかな？", 1, 1, 0, 0, 4, 2856, 108701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お正月だし\nみんなで楽しく\n遊んじゃおー！", 1, 1, 0, 0, 5, 2857, 108701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "うーん…\n騎士くんはお正月\n何してるんだろ…", 1, 4, 0, 0, 101, 2858, 108701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ううぅー…\n騎士くんを見てると\nドキってなっちゃう…", 1, 8, 0, 0, 102, 2859, 108701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あたしから\nおいしそうな匂い？\nくんくん…ソースだ！", 1, 1, 0, 0, 1, 2860, 108701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ヘアピンが…\n拾ってくれたんだ！\nありがとう騎士くん！", 1, 1, 0, 0, 2, 2861, 108701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "髪飾りがかわいい？\n耳の近く触りたい？\n…優しくね？　", 1, 1, 0, 0, 3, 2862, 108701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "グローブで食べるの\n大変そう？　特訓して\n今はお箸も使えるよ！", 1, 1, 0, 0, 4, 2863, 108701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "えっ、ほっぺが赤い？\nそうかな…？\n気のせい気のせい！", 1, 4, 0, 0, 101, 2864, 108701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "騎士くんといるとね…\nなんでもない！\nまだ内緒っ！", 1, 8, 0, 0, 102, 2865, 108701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよー！\n今日も明るく元気に\nがんばろー！", 1, 1, 1, 0, 1, 2866, 108701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お腹すいてない？\n一緒にご飯食べよ！\n何にしようかなー♪", 1, 1, 2, 0, 2, 2867, 108701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日もいっぱい\nがんばったー！\nいつも？　…そう？", 1, 1, 3, 0, 3, 2868, 108701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "休憩休憩♪\nちょっと休んで\nガンガン動くぞー！", 1, 1, 0, 0, 2, 2870, 108701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいい♪\nでも、壊しそうで\nちょっと怖い…", 1, 1, 0, 0, 3, 2871, 108701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はぁー♪\nいい香りー…\n気分スッキリ♪", 1, 1, 0, 0, 4, 2872, 108701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ほっこりしてて\nぬくぬく…\n丸まりたい…", 1, 1, 0, 0, 5, 2873, 108701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ど、どうしよう…！\n手が、止まんないよ！", 1, 1, 0, 0, 7, 2875, 108701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "へー…お年玉って\n昔はお餅だったんだ\n…今のほうがいいね", 1, 1, 0, 0, 8, 2876, 108701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "甘いチョコの香りを\n漂わせて…ふふっ\nこれで準備万端ですわ", 1, 1, 0, 0, 1, 2878, 109001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここを私のチョコで\n埋め尽くしますわ", 1, 1, 0, 0, 2, 2879, 109001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "エプロンを着て\nあの方を待つ私…\nまるで…クスクス", 1, 1, 0, 0, 3, 2880, 109001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "キッチンはどこかしら\n今のうちに作って\nおきたいものが…", 1, 1, 0, 0, 4, 2881, 109001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あのお方を私のチョコ\nなしでは生きられない\n体に…ふふっ\n冗談ですわ…", 1, 1, 0, 0, 5, 2882, 109001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "このチョコは運命の味\n一口食べれば私の虜…\nそれも運命ですわ…", 1, 4, 0, 0, 101, 2883, 109001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "私ともあろうものが\nなんだかドキドキして\nきましたわ…", 1, 8, 0, 0, 102, 2884, 109001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなた様。私に\n隠しているものなど\nありませんよね…？", 1, 1, 0, 0, 1, 2885, 109001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私の手作りチョコです\n味の方にも自信\nありますのよ？", 1, 1, 0, 0, 2, 2886, 109001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "少し苦いのは\nチョコがビターなだけ\nお気になさらず…", 1, 1, 0, 0, 3, 2887, 109001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "解毒薬はどこに\nいれたかしら…？", 1, 1, 0, 0, 4, 2888, 109001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "運命で結ばれている\nあなた様と私…\nチョコも特別でないと\nいけませんわ", 1, 4, 0, 0, 101, 2889, 109001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "チョコを食べた後は\nメインディッシュ…\nさぁ、私を召し上がれ", 1, 8, 0, 0, 102, 2890, 109001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "目覚めのチョコですわ\nすっきり目が覚めます\nしばらく眠れなく\nなりますけど…", 1, 1, 1, 0, 1, 2891, 109001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おやつのチョコですわ\nデザートにどうぞ\n空腹を感じなく\nなりますわ…クスクス", 1, 1, 2, 0, 2, 2892, 109001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お夜食のチョコですわ\nとてもよく眠れます\nさ、溶けてしまう前に", 1, 1, 3, 0, 3, 2893, 109001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この心地良さ…\n参考になりますわ\n次の薬は…クスクス", 1, 1, 0, 0, 2, 2895, 109001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この中、何か\n入りそうですわね\n…チョコレート、とか", 1, 1, 0, 0, 3, 2896, 109001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい香り…この香りと\n同じものを調合して\n私も持っていたい", 1, 1, 0, 0, 4, 2897, 109001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この暖かさ…\nチョコが溶けないよう\n気を付けなくては", 1, 1, 0, 0, 5, 2898, 109001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい味をしていますわ\n私も腕を磨きましょう\n料理と、調合の腕を…", 1, 1, 0, 0, 7, 2900, 109001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "勉強になりますわ\n知識はいくらあっても\n困りませんもの\n特に、人体の知識は…", 1, 1, 0, 0, 8, 2901, 109001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "弟くんのことを\n考えると\nあっという間に時間が\n過ぎちゃう…", 1, 1, 0, 0, 1, 2903, 109101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お姉ちゃんの\n元気の源は、弟くんの\n喜ぶ顔なんだから♪", 1, 1, 0, 0, 2, 2904, 109101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "弟くんと会ったら\nまずは抱きしめて\nそれからそれから…", 1, 1, 0, 0, 3, 2905, 109101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今日のお姉ちゃんは\n昨日のお姉ちゃんより\nいいお姉ちゃんだよ♪", 1, 1, 0, 0, 4, 2906, 109101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "弟くん、もっと\nお姉ちゃんに\n甘えてもいいのに♪", 1, 1, 0, 0, 5, 2907, 109101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お姉ちゃんのチョコ\n食べさせてあげたい…", 1, 4, 0, 0, 101, 2908, 109101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ドキドキしてきた…\nお姉ちゃんなのに\nどうしてかなぁ…", 1, 8, 0, 0, 102, 2909, 109101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お姉ちゃんを\n好きなだけ\n触っていいよ♪", 1, 1, 0, 0, 1, 2910, 109101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そんなにお姉ちゃんの\nチョコが欲しいの？\n食いしん坊さん♪", 1, 1, 0, 0, 2, 2911, 109101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ナデナデするのは\nお姉ちゃんの仕事！", 1, 1, 0, 0, 3, 2912, 109101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ありがとうなんて…\nお姉ちゃんとして\n当たり前だよ♪", 1, 1, 0, 0, 4, 2913, 109101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どうしたのかなっ？\nお姉ちゃんはいつでも\n弟くんのそばにいるよ", 1, 4, 0, 0, 101, 2914, 109101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "チョコを作ったよ\n隠し味は、たくさんの\n弟くんへの愛情♪", 1, 8, 0, 0, 102, 2915, 109101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよ。お姉ちゃんが\n顔を洗うの\n手伝ってあげるね♪", 1, 1, 1, 0, 1, 2916, 109101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ご飯を食べたら\n眠たくなるよね…\nお姉ちゃんと寝る？", 1, 1, 2, 0, 2, 2917, 109101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そろそろ眠くなった？\nお姉ちゃんが添い寝\nしてあげるね♪", 1, 1, 3, 0, 3, 2918, 109101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この気持ちよさは\nお姉ちゃんも太鼓判♪", 1, 1, 0, 0, 2, 2920, 109101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "このかわいさは\nお姉ちゃんも大満足♪", 1, 1, 0, 0, 3, 2921, 109101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この香り、チョコに\n使えるかな…？", 1, 1, 0, 0, 4, 2922, 109101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "弟くんと一緒に\nぬくぬくしたいな…♪", 1, 1, 0, 0, 5, 2923, 109101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "すごくおいしいねっ♪\nあとで運動しなきゃ…", 1, 1, 0, 0, 7, 2925, 109101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "本を読んでる\n知的なお姉ちゃん\n素敵でしょ♪", 1, 1, 0, 0, 8, 2926, 109101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "アタシって…\nここにいても\nいいのか…？", 1, 1, 0, 0, 1, 2928, 106101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ちゃんとした建物に\nいるのが久しぶりで\nそわそわする…", 1, 1, 0, 0, 2, 2929, 106101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここで天楼覇断剣\n出す練習したら\nアイツら怒るかな？", 1, 1, 0, 0, 3, 2930, 106101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ギルドハウスか…\nこういう隠れ家\n欲しかったんだよな", 1, 1, 0, 0, 4, 2931, 106101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "めちゃくちゃ暇だ…\n…なんかやること\nないのか？", 1, 1, 0, 0, 5, 2932, 106101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "みんなが早く\nアタシのこと\n思い出さないかな", 1, 4, 0, 0, 101, 2933, 106101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "アイツはいいヤツだし\n戦う時はアタシも\n協力してやるか！", 1, 8, 0, 0, 102, 2934, 106101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ちゃんはいらん！\nノウェムでいい！\n分かったか？", 1, 1, 0, 0, 1, 2935, 106101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どうした？\n戦うのか！？\n…なんだ、違うのか", 1, 1, 0, 0, 2, 2936, 106101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "身長のことは\nあんまり言うな！\n気にしてるからな！", 1, 1, 0, 0, 3, 2937, 106101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "アタシの戦い方は\nなんでもアリだ！\nだから見習うなよ？", 1, 1, 0, 0, 4, 2938, 106101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "オマエとの思い出か…\n敵として戦ってばっか\nだったな！", 1, 4, 0, 0, 101, 2939, 106101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "オマエやオクトーと\n一緒に、あちこち\nいってみたいんだ！", 1, 8, 0, 0, 102, 2940, 106101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "よっ！　おはよう！\n朝だぞー！\n起きてるかー？", 1, 1, 1, 0, 1, 2941, 106101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "腹減ったー！\n飯食うぞ、飯！\nオマエも行くよな？", 1, 1, 2, 0, 2, 2942, 106101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふわぁ…\nまだ起きてんのか？\n元気だなオマエ…", 1, 1, 3, 0, 3, 2943, 106101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふぃ～…\n極楽極楽…", 1, 1, 0, 0, 2, 2945, 106101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "おいオマエ！\n寝技の練習に\n付き合ってもらうぞ", 1, 1, 0, 0, 3, 2946, 106101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "くんくん…\nたまにはこういうのも\n悪くないかもな", 1, 1, 0, 0, 4, 2947, 106101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あー…\nもっと前にコイツと\n出会いたかった…", 1, 1, 0, 0, 5, 2948, 106101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "VRでも飯はうまい！\n腹が減るのはちゃんと\n生きてる気がするな", 1, 1, 0, 0, 7, 2950, 106101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "勉強は大事だな！\nうん！…だめだ！\n全然わからん！", 1, 1, 0, 0, 8, 2951, 106101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "探偵というのは\n謎を追い求める\n生き物なのさっ！", 1, 1, 0, 0, 1, 2953, 101401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "事件のオチは\n往々にして\n取るに足らないものさ", 1, 1, 0, 0, 2, 2954, 101401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "読書で得る知識が\n探偵の\n血肉になるんだよ", 1, 1, 0, 0, 3, 2955, 101401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この前の\n捜査資料…\nどこにあったかな？", 1, 1, 0, 0, 4, 2956, 101401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "どんな小さな謎も\n私にとっては\n宝石みたいなものさ！", 1, 1, 0, 0, 5, 2957, 101401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "助手くんと一緒だと\nよい発想が浮かぶ…\nどうしてだろうね？", 1, 4, 0, 0, 101, 2958, 101401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "助手くんの好きな\nタイプって…\nす、推理してみるっ！", 1, 8, 0, 0, 102, 2959, 101401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "珈琲、淹れたてだよ\n一杯どうだい？", 1, 1, 0, 0, 1, 2960, 101401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "事件の考察を\nまとめてみたんだけど\n聞いてくれるかな？", 1, 1, 0, 0, 2, 2961, 101401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "小さな違和感を\n見逃さない…\n探偵の心得だよ", 1, 1, 0, 0, 3, 2962, 101401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "機能的な手帳とペン…\nこの仕事には\n欠かせないね！", 1, 1, 0, 0, 4, 2963, 101401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "助手くんに\n珈琲…\n淹れてほしいなぁ～", 1, 4, 0, 0, 101, 2964, 101401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "助手くんは\n私にとって\n最高の相棒だよ☆", 1, 8, 0, 0, 102, 2965, 101401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "朝はいいひらめきが\n得られるんだ…\nさあ捜査を始めよう！", 1, 1, 1, 0, 1, 2966, 101401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "一休みしたら\n推理の答え合わせを\nしてみようか？", 1, 1, 2, 0, 2, 2967, 101401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜の張りこみかい？\nなら私も\n同行させてほしいな！", 1, 1, 3, 0, 3, 2968, 101401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "脳を酷使した後は\nゆっくり休む…\nこれも仕事だよ？", 1, 1, 0, 0, 2, 2970, 101401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この愛らしさ…\n要捜査対象だね！", 1, 1, 0, 0, 3, 2971, 101401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "思考が活性化される\n素敵な香りだね…", 1, 1, 0, 0, 4, 2972, 101401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "くぅ、心地よすぎる…\n今だけは捜査も\n一休みだね♪", 1, 1, 0, 0, 5, 2973, 101401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いっぱい食べて\nいっぱい考える\n探偵の本分だね！", 1, 1, 0, 0, 7, 2975, 101401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "探偵だからね\nミステリには\n一家言あるんだ", 1, 1, 0, 0, 8, 2976, 101401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "先輩、素敵だなぁ…\nもっと近づければ\nいいのに…", 1, 1, 0, 0, 1, 2978, 105501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "せ、先輩がいた部屋！\n私には刺激が強い…", 1, 1, 0, 0, 2, 2979, 105501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "先輩を観察しやすい\n場所はあるかな…", 1, 1, 0, 0, 3, 2980, 105501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "バ、バイト代出たし、\nせ、先輩をご飯とかに\n誘いたい…な", 1, 1, 0, 0, 4, 2981, 105501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今頃先輩は\nあの辺かな…？\n見に行ってみよう！", 1, 1, 0, 0, 5, 2982, 105501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "私と先輩の出会いって\n思い返すと少女漫画\nみたい…えへへ", 1, 4, 0, 0, 101, 2983, 105501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "せ、先輩になら\n観察されてもいいなぁ", 1, 8, 0, 0, 102, 2984, 105501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "バイト先に新しい子が\n入って来たんです…\nわ、私、先輩です♪", 1, 1, 0, 0, 1, 2985, 105501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "せ、先輩…？\nえっと、あの、その、\nあわわわわ…！", 1, 1, 0, 0, 2, 2986, 105501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "えへへ…えへへ～\nせ、先輩から\n話しかけてくれました", 1, 1, 0, 0, 3, 2987, 105501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ま、また…\n先輩と一緒に\nバイトしたい…です", 1, 1, 0, 0, 4, 2988, 105501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私と先輩が同じ空間に\n…ゆ、夢みたいです", 1, 4, 0, 0, 101, 2989, 105501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "せ、先輩のそばに\n居られて、私\n今、とっても\n幸せです…", 1, 8, 0, 0, 102, 2990, 105501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "せ、先輩…！\nおひゃっ！\nおはようございます！", 1, 1, 1, 0, 1, 2991, 105501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "先輩ランチに\n行きましょう！\nよし、練習はバッチリ", 1, 1, 2, 0, 2, 2992, 105501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あ、せ、先輩。\nえと、そ、外…\nもう真っ暗ですねぇ", 1, 1, 3, 0, 3, 2993, 105501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こ、ここで先輩も\n休んだり…はっ！\nわ、私ったらなにを！", 1, 1, 0, 0, 2, 2995, 105501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいいですねー\n君も一緒に、\n先輩を観察する？", 1, 1, 0, 0, 3, 2996, 105501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ここから先輩の\n香りがすればなあ", 1, 1, 0, 0, 4, 2997, 105501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "先輩と一緒に\nぬくぬくできたら\n最高なんだけどなぁ", 1, 1, 0, 0, 5, 2998, 105501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "おいし～\nこれは\n先輩も好きな味かな", 1, 1, 0, 0, 7, 3000, 105501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "す、好きな人に\n近づく方法…", 1, 1, 0, 0, 8, 3001, 105501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "私の力は、大切な\n人たちを護るために\n役立てたいんだ", 1, 1, 0, 0, 1, 3003, 109201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "きれいな景色と\n美味しいご飯！\nこの世界も素敵だね", 1, 1, 0, 0, 2, 3004, 109201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この世界のみんなとも\nもっと仲良く\nなりたいな！", 1, 1, 0, 0, 3, 3005, 109201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "わー！\nあれもこれも\n気になっちゃうな！", 1, 1, 0, 0, 4, 3006, 109201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "しっかりしなきゃって\n思ってても、\nグレアといるとつい\n甘えちゃうんだよね～", 1, 1, 0, 0, 5, 3007, 109201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "違う世界でも\n友だちができて、\nすごくうれしいよ！", 1, 4, 0, 0, 101, 3008, 109201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "みんなとも一緒に\n授業とか受けたいな～\n絶対楽しそうだもん！", 1, 8, 0, 0, 102, 3009, 109201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "歩き方がきれい？\nえへへ、そうかな…？", 1, 1, 0, 0, 1, 3010, 109201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "勉強は好きだよ！\n知識が増えていくのは\nやっぱり楽しいから", 1, 1, 0, 0, 2, 3011, 109201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私たちの国マナリアは\n人々の間に魔法が\n伝えられた、その\n始まりの地なんだ！", 1, 1, 0, 0, 3, 3012, 109201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この部屋にも、\nもっとたくさん本棚が\n増えないかな～…？", 1, 1, 0, 0, 4, 3013, 109201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたも魔法、\n覚えてみる？\n私、教えるよ！", 1, 4, 0, 0, 101, 3014, 109201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お茶の時間に\nしたいな～…\n一緒にどう？", 1, 8, 0, 0, 102, 3015, 109201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようっ！\n今日も素敵な\n一日にしようね！", 1, 1, 1, 0, 1, 3016, 109201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼ご飯は\nちゃんと食べた？\n午後も頑張ろうね！", 1, 1, 2, 0, 2, 3017, 109201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "つい本の続きが\n気になって、夜更かし\nしちゃうんだよね～…", 1, 1, 3, 0, 3, 3018, 109201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はぁ、気持ちいい～…\nちょっとだけ\n眠っちゃおうかな…", 1, 1, 0, 0, 2, 3020, 109201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "わあ、これ可愛い！\nグレアにも見せて\nあげたいな", 1, 1, 0, 0, 3, 3021, 109201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい香り～！\nなんだか気持ちが\n落ち着くね", 1, 1, 0, 0, 4, 3022, 109201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あぁ、あったかくて\n幸せ～♪　身体が\nポカポカしてきたよ！", 1, 1, 0, 0, 5, 3023, 109201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こ、これ美味し～！\n止められないよー！", 1, 1, 0, 0, 7, 3025, 109201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふんふん…\n…はっ！　む、夢中に\nなっちゃってた…", 1, 1, 0, 0, 8, 3026, 109201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "新しい建物に入ると\n必ず一度は迷子に\nなっちゃうですよ…", 1, 1, 0, 0, 1, 3028, 109301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "おやつはどこですか？\nお腹が空いて\nきちゃったですぅ", 1, 1, 0, 0, 2, 3029, 109301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "宿題、７つぐらい\n出しそびれたですけど\nまあ大丈夫ですぅ！", 1, 1, 0, 0, 3, 3030, 109301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "不思議なこと\nばかりですけど…\nまぁ大丈夫です！\nこれもせいしゅんです", 1, 1, 0, 0, 4, 3031, 109301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "なにか面白そうなもの\nないですかね～？", 1, 1, 0, 0, 5, 3032, 109301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここのみんなとも\nもうお友だちですぅ！", 1, 4, 0, 0, 101, 3033, 109301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "おなかが減ったです…", 1, 8, 0, 0, 102, 3034, 109301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ごはんも美味しいし\nここはいいところです\nルゥは幸せですぅ！", 1, 1, 0, 0, 1, 3035, 109301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "魔法の話ですか？\nそれはアンせんぱいに\n聞いてほしいですぅ…", 1, 1, 0, 0, 2, 3036, 109301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたもお勉強\n苦手ですか？", 1, 1, 0, 0, 3, 3037, 109301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ルゥの帽子、\nかわいいです？\nお気に入りですぅ♪", 1, 1, 0, 0, 4, 3038, 109301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お友だちいっぱいで、\nルゥはうれしいです♪", 1, 4, 0, 0, 101, 3039, 109301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "一緒にお勉強…！？\nぐえぇ、イヤですぅ！", 1, 8, 0, 0, 102, 3040, 109301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "朝ですねぇ…ふわわ…\nルゥはまだ夢の中に\nいたいですぅ…", 1, 1, 1, 0, 1, 3041, 109301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "午後もいっぱい\n遊ぶですよ～！", 1, 1, 2, 0, 2, 3042, 109301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜ふかしって\nドキドキするです！\nせいしゅんですぅ！", 1, 1, 3, 0, 3, 3043, 109301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふかふかですぅ～♪\nおひるねが\nしたくなるですぅ！", 1, 1, 0, 0, 2, 3045, 109301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これふかふかですぅ！\n机でうとうとする時の\n枕によさそうですぅ", 1, 1, 0, 0, 3, 3046, 109301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふぁぁ、いいにおい…\nなんだか気持ちが\nよくなってきたですぅ", 1, 1, 0, 0, 4, 3047, 109301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ぽっかぽかですぅ～！\nあったまったら\nまた遊びにいくですぅ", 1, 1, 0, 0, 5, 3048, 109301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お、おいしいですぅ！\n全部ルゥに\nくださいですぅ！", 1, 1, 0, 0, 7, 3050, 109301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "本は…読むと、\nすぐ眠気がぁ…\n…ぐぅ…", 1, 1, 0, 0, 8, 3051, 109301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ピアノ…\nどこかにないかな？", 1, 1, 0, 0, 1, 3053, 109401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ふぅ…\n落ち着くな…", 1, 1, 0, 0, 2, 3054, 109401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "マナリアはいつも\n賑やかなんだ…", 1, 1, 0, 0, 3, 3055, 109401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "みんな…\n今頃何してるかな？", 1, 1, 0, 0, 4, 3056, 109401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "まさか違う世界に\n来ちゃうなんて…\nびっくりだよね…", 1, 1, 0, 0, 5, 3057, 109401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "人と話すのは\n得意じゃないな…", 1, 4, 0, 0, 101, 3058, 109401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここのみんなと一緒に\n学院に通えたら…\nちょっと面白いかも", 1, 8, 0, 0, 102, 3059, 109401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "やっぱり、私の翼や\n尻尾…気になる？", 1, 1, 0, 0, 1, 3060, 109401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "一番得意なのは、\nやっぱり炎魔法かな…\n加減は難しいけど…", 1, 1, 0, 0, 2, 3061, 109401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私の故郷は竜の国…\n険しい山脈を\n越えた先にあるんだ", 1, 1, 0, 0, 3, 3062, 109401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "アン…？\nあっ、ご、ごめんね！\n間違えちゃった…", 1, 1, 0, 0, 4, 3063, 109401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふん…ふん…♪\n…わっ！\n今の、聞いてた…？\nは、恥ずかしい…", 1, 4, 0, 0, 101, 3064, 109401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私に手伝えること、\nなにかある…？\nなんでも言ってね", 1, 8, 0, 0, 102, 3065, 109401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "朝の空気って\nすごく\n静かで透き通ってて…\nなんだか落ち着くよね", 1, 1, 1, 0, 1, 3066, 109401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "学院食堂のランチも\n美味しいけど…\nここのご飯も好きかな", 1, 1, 2, 0, 2, 3067, 109401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そろそろベッドに\n行かないとだね\n…寝相、今夜こそ\n気を付けよう…！", 1, 1, 3, 0, 3, 3068, 109401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "羽を伸ばしたく\nなっちゃうね…ふふ", 1, 1, 0, 0, 2, 3070, 109401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "わ、私が持つには\nかわいすぎるかな…\nでも、素敵だね", 1, 1, 0, 0, 3, 3071, 109401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい香り…\nこれ、\nアンも好きそう…", 1, 1, 0, 0, 4, 3072, 109401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "火を見ていると\n少し落ち着くんだ…\nへ、変かな？", 1, 1, 0, 0, 5, 3073, 109401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "わ、美味しい…！\nこれ、どうやって\n作ったのかな…？", 1, 1, 0, 0, 7, 3075, 109401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この本…アンにも\n見せてあげたら\n喜ぶかな、ふふっ", 1, 1, 0, 0, 8, 3076, 109401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "着物はきれいですが\n歩きにくいです…\nいっそ脱いで…ぐふふ", 1, 1, 0, 0, 1, 3078, 109501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あっ、いけません\n着物がはだけて…はっ\nもしやドＳさんが…！", 1, 1, 0, 0, 2, 3079, 109501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この部屋は妄想する\nのにうってつけです…\nぐふふ…じゅるり", 1, 1, 0, 0, 3, 3080, 109501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "扇をゆっくりと開く\nこの感触…なぜだか\n艶めかしさを感じます", 1, 1, 0, 0, 4, 3081, 109501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "「マッサージをすると\n言いながらドＳさんの\n手は次第に」…ぐふふ", 1, 1, 0, 0, 5, 3082, 109501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "…ドＳさんの視線を\n思い出しながら踊りの\n練習です…イイ！", 1, 4, 0, 0, 101, 3083, 109501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "よいではないか\nよいではないか～\nいけませんご堪忍を～", 1, 8, 0, 0, 102, 3084, 109501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あ～れ～～～！\nドＳさんもお好きな人\nですねぇ…ぐふふ", 1, 1, 0, 0, 1, 3085, 109501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どこにいてもドＳさん\nの気配を感じ取れる\nようになりました…", 1, 1, 0, 0, 2, 3086, 109501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "こんな場所で\nなんというプレイを！\n…妄想でした", 1, 1, 0, 0, 3, 3087, 109501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "クウカを押し倒して\nそのまま…じゅるり\nああっ、着物が…！", 1, 1, 0, 0, 4, 3088, 109501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ドＳさんは優しいです\n優しいのにドＳとは\nさすが人格破綻者…！", 1, 4, 0, 0, 101, 3089, 109501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ドＳさんの考えている\nことが伝わってきます\nはい…クウカ脱ぎます", 1, 8, 0, 0, 102, 3090, 109501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "眩しい朝日がクウカの\n体を焦がします\n…それもイイ！", 1, 1, 1, 0, 1, 3091, 109501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼のランチは\nクウカがいいだなんて\n大胆過ぎです…じゅる", 1, 1, 2, 0, 2, 3092, 109501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "…今夜のドＳさんは\n大胆です…ぐふふ…\nはっ、視線が…！？", 1, 1, 3, 0, 3, 3093, 109501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この心地良さ、\n心が休まりすぎて\n体が疼いてきます…！", 1, 1, 0, 0, 2, 3095, 109501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "すごく愛らしいです…\n妄想も出てこない\nくらい…じゅるり", 1, 1, 0, 0, 3, 3096, 109501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この香りに包まれて\nいると、いい気持ちに\nなって…いきますぅ…", 1, 1, 0, 0, 4, 3097, 109501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "「熱を帯びた体は\nどうしようもなく\n誰かを求め…」ぐふふ", 1, 1, 0, 0, 5, 3098, 109501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お腹が膨らんで\nいきます…それは\nまるで…ぐふふ", 1, 1, 0, 0, 7, 3100, 109501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "クウカの探している\nジャンルはどこに\nありますかね？", 1, 1, 0, 0, 8, 3101, 109501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "カチョーフーゲツは\nトーゴクの心を\n表した言葉デスね", 1, 1, 0, 0, 1, 3103, 109601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "オー！　新しい忍法を\n思いついたデス！\nさっそく試すデス！", 1, 1, 0, 0, 2, 3104, 109601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "トーゴクの天気には\n槍が降る日も\nあるそうデス…", 1, 1, 0, 0, 3, 3105, 109601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "我がショーグンは\n永遠にフメツデース\nいざ出陣デース！", 1, 1, 0, 0, 4, 3106, 109601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "壁に耳あり\n障子に…ん？\nメアリーって誰デス？", 1, 1, 0, 0, 5, 3107, 109601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ショーグンがいれば\nワタシはどんな\n忍法も使いこなせマス", 1, 4, 0, 0, 101, 3108, 109601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "不届き者はどこで\nゴザルデス？　忍法で\n退治してくれヨウ！", 1, 8, 0, 0, 102, 3109, 109601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ショーグン！　新しい\n忍法を覚えたデス！\n見てほしいデース！", 1, 1, 0, 0, 1, 3110, 109601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "レッツ手裏剣道デス！\n投げてソウロウで\nゴザルデース！", 1, 1, 0, 0, 2, 3111, 109601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふりかかる火の粉は\nワタシが払うデス！\nバンバン！", 1, 1, 0, 0, 3, 3112, 109601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "とっておきの忍法が\n吹き荒れるデス！\nよそ見はダメデスヨ？", 1, 1, 0, 0, 4, 3113, 109601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ショーグンは\n何階建てのお城を\n建てたいデスか？", 1, 4, 0, 0, 101, 3114, 109601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "テンシュカクから\n平和な城下町を\n眺めてみたいデス…", 1, 8, 0, 0, 102, 3115, 109601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようございマス！\n本日はオヒガラモヨク\n良い日になりそうデス", 1, 1, 1, 0, 1, 3116, 109601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お腹すいたデース…\nショーグンもご飯に\nしまショウ！", 1, 1, 2, 0, 2, 3117, 109601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "闇討ちに遭わぬよう\n夜は気を付けて\n歩くデスよー？", 1, 1, 3, 0, 3, 3118, 109601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "天下分け目の\n戦いに備えて…\nマッタリデース", 1, 1, 0, 0, 2, 3120, 109601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "口寄せの術で\n召喚したいくらい\nかわいいデース", 1, 1, 0, 0, 3, 3121, 109601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい匂いデ～ス\n香り玉の開発の\n参考になりそうデスね", 1, 1, 0, 0, 4, 3122, 109601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ワタシの懐よりも\nゾーリが暖まりマス…\nライバル出現デス…！", 1, 1, 0, 0, 5, 3123, 109601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "腹が減っては\n戦はできぬデース！\n天下統一に向けて\n腹ごしらえデス！", 1, 1, 0, 0, 7, 3125, 109601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ムム、暗号文デス！？\n解読には時間が\nかかりそうデース…", 1, 1, 0, 0, 8, 3126, 109601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "掃除も終わりましたし\nそろそろ、食事の\n準備をしなくては", 1, 1, 0, 0, 1, 3128, 109701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "異世界の演劇…\n許されるならば、\n観てみたいです", 1, 1, 0, 0, 2, 3129, 109701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "こちらの世界には、\nマヨネーズが普通に\n存在するのですね…", 1, 1, 0, 0, 3, 3130, 109701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "こちらの世界は食材も\n面白い物が多いですね\nなにかレシピを\n考えてみましょうか", 1, 1, 0, 0, 4, 3131, 109701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "みょんみょんみょん…\n魔法の練習は\nこれぐらいですね", 1, 1, 0, 0, 5, 3132, 109701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "倒木の撤去で思わぬ\n収入となりました\nサレン様に\nお渡ししておかないと", 1, 4, 0, 0, 101, 3133, 109701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "料理を美味しく\n召し上がって頂けると\nメイド冥利につきます", 1, 8, 0, 0, 102, 3134, 109701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お出かけでしたら、\nレムも同行\nさせてくださいね", 1, 1, 0, 0, 1, 3135, 109701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "レムにできること\nでしたら、なんでも\nお申し付けください", 1, 1, 0, 0, 2, 3136, 109701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "じーー。いえ、\nその髪の長さが少し、\nとても少し、かなり\n少し気になったもので", 1, 1, 0, 0, 3, 3137, 109701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "鉄球ですか？\nあれは護身用ですので\n問題はありませんよ？", 1, 1, 0, 0, 4, 3138, 109701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "計算の勉強は\n進んでおりますか？\nレムでよろしければ、\nいつでもお教えします", 1, 4, 0, 0, 101, 3139, 109701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "次はもっと、大きな\n魔物料理に挑戦します\nワイバーンの姿焼き\nなんてどうでしょうか", 1, 8, 0, 0, 102, 3140, 109701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようございます\n朝食の準備が\nできておりますので、\n食卓におこしください", 1, 1, 1, 0, 1, 3141, 109701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "昼食の時間ですね\n何か食べたいものは\nありますか？", 1, 1, 2, 0, 2, 3142, 109701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "寝ないのか、ですか？\nメイドの就寝は、\n屋敷で最後と\n決まっておりますので", 1, 1, 3, 0, 3, 3143, 109701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふんわり…\nこのふんわりさは、\n鬼をも黙らせます…", 1, 1, 0, 0, 2, 3145, 109701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふふっ…異世界でも\nかわいいものは\n共通なのですね", 1, 1, 0, 0, 3, 3146, 109701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "よい香りですね\n魔女教の悪臭とは\n大違いです", 1, 1, 0, 0, 4, 3147, 109701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ぬくぬくです…はっ！\nこれはいけません！\n人を…いえ、\n鬼をだめにします！", 1, 1, 0, 0, 5, 3148, 109701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はむはむ…美味しい！\nこの味を再現できたら\n褒めてもらえる\nかもしれません…！", 1, 1, 0, 0, 7, 3150, 109701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "絵がいっぱい…\n異世界の本は\nとても面白いですね", 1, 1, 0, 0, 8, 3151, 109701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "異世界であろうと、\nラムのやるべきことは\n変わらないわ\nまずは休憩するわよ", 1, 1, 0, 0, 1, 3153, 109801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "マヨネーズがある\nということは、\nバルスの故郷が近い\nのかもしれないわね", 1, 1, 0, 0, 2, 3154, 109801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この世界では、バルス\nの言う言葉が使われる\nときがあるのね\n忌々しいかぎりだわ", 1, 1, 0, 0, 3, 3155, 109801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ラムたち以外にも\nメイドが居るのね\nラムが…いえ、\n仕事を押しつけるわ", 1, 1, 0, 0, 4, 3156, 109801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "…千里眼で視るかぎり\n周囲に異変はないわね\n危険がないなら、\nあとはレムに任せるわ", 1, 1, 0, 0, 5, 3157, 109801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "…ラムもまた、\n「お姉ちゃん」って\n呼ばれる日が\nくるのかしらね", 1, 4, 0, 0, 101, 3158, 109801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ハッ！　ラムが作る\n蒸かし芋は完璧ね\n…気が向いたら、また\n作ってやろうかしら", 1, 8, 0, 0, 102, 3159, 109801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ラムに構う暇が\nあるなら働きなさい\nそうすれば、\nラムが楽をできるわ", 1, 1, 0, 0, 1, 3160, 109801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ラムに何かを\n期待しても無駄よ\nラムに命令できるのは\nロズワール様だけだわ", 1, 1, 0, 0, 2, 3161, 109801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "暇なら買い物に\n付き合いなさい\n荷物を\n持たせてあげるわ", 1, 1, 0, 0, 3, 3162, 109801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ラムになんの用？\nふざけた内容なら\nねじ切るわよ", 1, 1, 0, 0, 4, 3163, 109801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そういえば、あなたは\n記憶喪失だったわね…\n思い出す努力をなさい\n…家族が悲しむわよ", 1, 4, 0, 0, 101, 3164, 109801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "蒸かし芋が欲しい？\n随分と厚かましいわね\n…まだ残りがあるから\n好きに食べなさい", 1, 8, 0, 0, 102, 3165, 109801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "だらしない顔ね\n顔を洗って\nしゃんとなさい", 1, 1, 1, 0, 1, 3166, 109801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お腹が空く時間ね\nラムの昼食を\n用意させてあげるわ", 1, 1, 2, 0, 2, 3167, 109801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "メイドが寝るのは\n屋敷で最後…\nラムが寝るために、\nさっさと寝なさい", 1, 1, 3, 0, 3, 3168, 109801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "…ここから動かない\nという、誓いを\n立てたくなるわね", 1, 1, 0, 0, 2, 3170, 109801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これは…かわいいわね\nもちろん、\nラムほどではないけど", 1, 1, 0, 0, 3, 3171, 109801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "…香炉かしら？\nこちらの世界にも\n質のよい物があるのね", 1, 1, 0, 0, 4, 3172, 109801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "見つけてしまったの\nかもしれないわ…\nラムの楽園を！", 1, 1, 0, 0, 5, 3173, 109801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "…はむっ\nラムの蒸かし芋ほど\nではないけど、\nなかなかいけるわね", 1, 1, 0, 0, 7, 3175, 109801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "異世界ならではの\n面白い内容もあるわね\nとても興味深いわ", 1, 1, 0, 0, 8, 3176, 109801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ねえパック、\nにゃんにゃか\nにゃかにゃんしよっか", 1, 1, 0, 0, 1, 3178, 109901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "時間もあるし、\nこっちの世界の石で\n石積みしようかな", 1, 1, 0, 0, 2, 3179, 109901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "私もお姉さんだし、\nサレンさんを\n見習わないと…うん！\n私、すごーく頑張る！", 1, 1, 0, 0, 3, 3180, 109901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この世界は、銀髪で\nハーフエルフでも受け\n入れてくれるのね…\nなんだか嬉しいな", 1, 1, 0, 0, 4, 3181, 109901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今日のご飯…\nピーマルは\n入ってないわよね…？", 1, 1, 0, 0, 5, 3182, 109901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "また、みんなで\n一緒に買い物に\n行きたいな", 1, 4, 0, 0, 101, 3183, 109901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "パック以外から\n家族って呼ばれたの\n初めて…ふふっ♪\nちょっと嬉しいかも！", 1, 8, 0, 0, 102, 3184, 109901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今からパックの\n毛繕いをするけど…\n一緒にする？", 1, 1, 0, 0, 1, 3185, 109901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私も何かお手伝いを…\nあ、そうだ！\n料理とかどうかな！", 1, 1, 0, 0, 2, 3186, 109901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ねえ、ここで\n歌の練習って、\nしてもいいかな？", 1, 1, 0, 0, 3, 3187, 109901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "…あ、ごめんなさい！\n話しかけられるのって\nあまり慣れてなくて…", 1, 1, 0, 0, 4, 3188, 109901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "すごーく頑張るから、\nこれからも勉強を\n手伝ってね", 1, 4, 0, 0, 101, 3189, 109901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "せーの、びくとりー！\nふふっ…また、一緒に\nラジーオタイソー\nしようね♪", 1, 8, 0, 0, 102, 3190, 109901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ちゃんと起きてる？\nふふっ…よかった。\nあ、べ、別に心配した\nわけじゃないからね？", 1, 1, 1, 0, 1, 3191, 109901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼ご飯が楽しみね♪\nあ、こっちの世界って\nまよねーずはあるの？", 1, 1, 2, 0, 2, 3192, 109901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そろそろ寝る時間よ。\n夜更かししたら、\nめっ！　だからね！", 1, 1, 3, 0, 3, 3193, 109901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "勉強も終わったし…\nちょっ、ちょっとだけ\n休んでもいいよね…？", 1, 1, 0, 0, 2, 3195, 109901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "か、かわいい…！\nパックのかわいさに\n匹敵するかも…！", 1, 1, 0, 0, 3, 3196, 109901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "良い香り…\nロズワールの屋敷にも\nこういうのって\nあるのかな？", 1, 1, 0, 0, 4, 3197, 109901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったかい…\nずっとここに…はっ！\nダメダメ！　王選の\nために勉強しないと！", 1, 1, 0, 0, 5, 3198, 109901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あ…これ美味しい！\nこっちの世界は色んな\n食べ物があるのね。", 1, 1, 0, 0, 7, 3200, 109901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "知らないことが\nいっぱい書いてある…\n異世界の本って、\n面白いのね", 1, 1, 0, 0, 8, 3201, 109901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "海ってマジ最高！\nビーチの撮影も\n超楽しいしー♪", 1, 1, 0, 0, 1, 3203, 110001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "20パーって何パ？\n今度ヒデサイに\n聞いてみよっ！", 1, 1, 0, 0, 2, 3204, 110001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "夏の思い出を\nヒデサイと一緒に\n作りたいな～", 1, 1, 0, 0, 3, 3205, 110001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "テンションあがる\n水着ゲットして\n超ラッキーだよ～♪", 1, 1, 0, 0, 4, 3206, 110001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "休憩が終わったら\n撮影にもどらないと！\n気合入れてこ♪", 1, 1, 0, 0, 5, 3207, 110001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ヒデサイとビーチで\nパーティーしたいな～\n超楽しそうだよね！", 1, 4, 0, 0, 101, 3208, 110001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ヒデサイといると\n楽しくてうれしくて…\nカレシってこんな感じ\nなのかなぁ？", 1, 8, 0, 0, 102, 3209, 110001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "見て見て～！\n夏らしいネイルに\nしてみたんだ～♪", 1, 1, 0, 0, 1, 3210, 110001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ヒデサイ～\nこの水着どうかな？\nイケてるっしょ♪", 1, 1, 0, 0, 2, 3211, 110001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "水着姿の写真？\nもちいいよ！\nかわいく撮ってね！", 1, 1, 0, 0, 3, 3212, 110001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "うちだって\n夏の海みたいに\nキラキラしちゃうよ！", 1, 1, 0, 0, 4, 3213, 110001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ヒデサイとうちの\nフィーリングって\nめっちゃエモいよね♪", 1, 4, 0, 0, 101, 3214, 110001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ねーねーヒデサイ！\n一緒に泳ぎにいこっ！\nほら、手繋いで♪", 1, 8, 0, 0, 102, 3215, 110001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "水着オッケー！\nネイルもばっちり！\n朝から遊んじゃおー♪", 1, 1, 1, 0, 1, 3216, 110001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼食べたら\n昼寝して\nリフレッシュしよー♪", 1, 1, 2, 0, 2, 3217, 110001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "水着ってマジで\n最強かも…このまま\nお風呂にも入れるし♪", 1, 1, 3, 0, 3, 3218, 110001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "マジ気持ちいいー♪\nおに癒やされるん\nですけど～！", 1, 1, 0, 0, 2, 3220, 110001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "やばばばばー！\nかわいすぎだってば！\nマジらぶだよ～♪", 1, 1, 0, 0, 3, 3221, 110001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あ、この香り超好き！\nモデル仲間に\n教えたげよ～♪", 1, 1, 0, 0, 4, 3222, 110001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったか～い♪\n夏の太陽みたい！", 1, 1, 0, 0, 5, 3223, 110001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "マジ美味しいねこれ♪\nビーチで\n流行っちゃうかも！", 1, 1, 0, 0, 7, 3225, 110001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "夏のために\n最新ファッションを\n勉強ちう！", 1, 1, 0, 0, 8, 3226, 110001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "なにコレもにカワ～♪\nなんて生き物？\nヒデサイ知ってる？", 1, 1, 0, 0, 10, 3228, 110001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うわっ超フカフカ～\nなんかうち眠く\nなってきちゃった…", 1, 1, 0, 0, 11, 3229, 110001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "海の家でのんびり\n過ごしましょう\n熱中症には注意してね", 1, 1, 0, 0, 1, 3230, 110101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "はぁ、どうやったら\n泳げるように\nなれるのかしら～", 1, 1, 0, 0, 2, 3231, 110101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "海で日焼け止めを\n塗らないと、あとで\n大変なことになるわよ", 1, 1, 0, 0, 3, 3232, 110101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "久しぶりに水着を\n着たんだけど\n変じゃなかったかな…", 1, 1, 0, 0, 4, 3233, 110101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "波の音って落ち着くわ\n授業中に聞いたら\nみんな寝ちゃうかも？", 1, 1, 0, 0, 5, 3234, 110101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "海が楽しいって\n思えたのも\nあの子のおかげね", 1, 4, 0, 0, 101, 3235, 110101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あの子と二人で海に\n来られてよかったわ\n一日があっという間ね", 1, 8, 0, 0, 102, 3236, 110101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "男の子と海だなんて\n少女漫画みたいで\n憧れてたの～", 1, 1, 0, 0, 1, 3237, 110101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夏休みの宿題は\nちゃんとやった？\n遊んでばかりではダメ", 1, 1, 0, 0, 2, 3238, 110101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "昼の海と夜の海\nどっちが好き？　私は\n君といる海が一番よ", 1, 1, 0, 0, 3, 3239, 110101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふう、疲れたわ～\n冷たいジュースで\n元気回復ね！", 1, 1, 0, 0, 4, 3240, 110101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "海で見た君の背中\nすごく大きく見えたわ\nやっぱり男の子なのね", 1, 4, 0, 0, 101, 3241, 110101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "君と一緒に見た\n夜の海…\n忘れられそうにないわ", 1, 8, 0, 0, 102, 3242, 110101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "準備ができたら\n海に出かけようよ\n今日も楽しみましょう", 1, 1, 1, 0, 1, 3243, 110101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あら、もうお昼？\n海の家でランチに\nしましょ♪", 1, 1, 2, 0, 2, 3244, 110101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜の海岸についてきて\nくれる？　暗がりは\n一人じゃ怖くて…", 1, 1, 3, 0, 3, 3245, 110101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "泳ぐ練習をしたら\n疲れちゃったわ～\nちょっと一休み…", 1, 1, 0, 0, 2, 3247, 110101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいいわね～\n浮き輪に乗せて\n海に浮かべたいわ～", 1, 1, 0, 0, 3, 3248, 110101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この匂い…\nなんだか夏の海を\n思い出すわ～", 1, 1, 0, 0, 4, 3249, 110101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "夜の海風で冷えない\nように、しっかりと\n身体を暖めましょうね", 1, 1, 0, 0, 5, 3250, 110101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "海で遊ぶ前に\nしっかり食べて\n体力つけましょう", 1, 1, 0, 0, 7, 3252, 110101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "夏休み中でも\nしっかりお勉強\nしないと！", 1, 1, 0, 0, 8, 3253, 110101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この子に乗れば\n海の上でも\nすいすい進めるかしら", 1, 1, 0, 0, 10, 3255, 110101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ビーナスの誕生！\n…なんちゃって\nはしゃぎすぎかしら？", 1, 1, 0, 0, 11, 3256, 110101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "夏の海には商機が\nあるはず…\nのがす手はないわね♪", 1, 1, 0, 0, 1, 3257, 110301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あたしが作った\n日焼け止め…\nこれは売れそうね！", 1, 1, 0, 0, 2, 3258, 110301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "子どもたちのために\n浮き輪を用意して\nおかないと…", 1, 1, 0, 0, 3, 3259, 110301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "海っていいわよね！\nみんなで羽を伸ばしに\n行きたいわ！", 1, 1, 0, 0, 4, 3260, 110301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "波の音を聞いて\nのんびりするのも\n悪くないわね…", 1, 1, 0, 0, 5, 3261, 110301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ただの砂遊びも\nあんたと一緒なら\n楽しいから不思議ね…", 1, 4, 0, 0, 101, 3262, 110301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あんたがあたしの\nなんであれ、大切な\n存在ではあるのよね…", 1, 8, 0, 0, 102, 3263, 110301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夏の海って\nすごい人よね…\n迷子にならないでよ？", 1, 1, 0, 0, 1, 3264, 110301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ぼんやり海に浮かんで\n波に揺られるのも\n悪くないわよ？", 1, 1, 0, 0, 2, 3265, 110301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ビーチで子どもたちと\n一緒に砂のお城を\n作りたいわ♪", 1, 1, 0, 0, 3, 3266, 110301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "海で食べる\n焼きそばパン…\nいいかもしれないわね", 1, 1, 0, 0, 4, 3267, 110301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あたしの水着\n似合ってる？\nそう、ありがとう♪", 1, 4, 0, 0, 101, 3268, 110301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あんたが居ると\nうれしい…って、\n子どもたちが\n言ってたわ！", 1, 8, 0, 0, 102, 3269, 110301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "朝の浜辺って\nいいわよね\n散歩しに行きたいわ", 1, 1, 1, 0, 1, 3270, 110301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "海の家のご飯って\nなんであんなに\nおいしいのかしら♪", 1, 1, 2, 0, 2, 3271, 110301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜の海って、\nロマンチックよね。\nずっと眺めてたいわ…", 1, 1, 3, 0, 3, 3272, 110301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "やわらかくて\n気持ちいい…\nこのまま寝ちゃいそう", 1, 1, 0, 0, 2, 3274, 110301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "海に一緒に入れる\nぬいぐるみとか\nないかしら？", 1, 1, 0, 0, 3, 3275, 110301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "夏らしい\nいい香りね…\nなんの香りかしら？", 1, 1, 0, 0, 4, 3276, 110301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったかい…\n濡れた体を乾かすには\nちょうどいいわね♪", 1, 1, 0, 0, 5, 3277, 110301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "泳いだ後の食事は\n普段の何倍も\nおいしく感じるわね！", 1, 1, 0, 0, 7, 3279, 110301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "海に行く前にいろいろ\n調べなきゃね。備え\nあれば憂いなしよ！", 1, 1, 0, 0, 8, 3280, 110301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "海の守り神？\nずいぶんかわいらしい\n神様ね♪", 1, 1, 0, 0, 10, 3282, 110301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "このぐらい\n大きな貝があったら\n食費も浮くかしら？", 1, 1, 0, 0, 11, 3283, 110301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "浜辺で食うものって\nなんであんなに\nうまいのかな～", 1, 1, 0, 0, 1, 3284, 110401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "いてて…\nちょっと日焼け\nしすぎたかな", 1, 1, 0, 0, 2, 3285, 110401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "砂浜で走ると\nいいトレーニングに\nなるんだぜ", 1, 1, 0, 0, 3, 3286, 110401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "夏ならではの\n料理…か\n何がいいかな…", 1, 1, 0, 0, 4, 3287, 110401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "やっぱりここは\n涼しくて快適だな", 1, 1, 0, 0, 5, 3288, 110401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "泳ぎの練習だったら\nいつでも付き合って\nやるからな！", 1, 4, 0, 0, 101, 3289, 110401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "水着で部屋の中に\nいるのって\n変な感じだな…", 1, 8, 0, 0, 102, 3290, 110401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "うおっ！\nきゅ、急に\n触るなって…\nびっくりした", 1, 1, 0, 0, 1, 3291, 110401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "え、砂がついてた？\n払ってくれたのか\nありがとよ", 1, 1, 0, 0, 2, 3292, 110401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夏バテしてないか？\nここで休んでけよ", 1, 1, 0, 0, 3, 3293, 110401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "…そう見られると\n照れるっていうか…", 1, 1, 0, 0, 4, 3294, 110401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "カキ氷とか\n食いたくないか？\n氷、削ってやるよ", 1, 4, 0, 0, 101, 3295, 110401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おまえと\n夏の思い出ができて\nよかったよ", 1, 8, 0, 0, 102, 3296, 110401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふう…\n朝一番で泳ぐの\nやっぱ気持ちいいな", 1, 1, 1, 0, 1, 3297, 110401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "一番あちぃ時間だ\n水分補給は\nこまめにな！", 1, 1, 2, 0, 2, 3298, 110401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日もお疲れ\n明日のパトロールも\nよろしくな", 1, 1, 3, 0, 3, 3299, 110401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "泳ぎ疲れたからな\n少し休憩だ…", 1, 1, 0, 0, 2, 3301, 110401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "か、かわいい…\nいや、なんでもねぇ！", 1, 1, 0, 0, 3, 3302, 110401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この香り落ち着くな\nこういうのも\n悪くねーかも…", 1, 1, 0, 0, 4, 3303, 110401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったけえ…！\n泳ぎ終わった後って\nけっこう体冷えてよ…", 1, 1, 0, 0, 5, 3304, 110401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "海で食べると\nまた一味違うよな", 1, 1, 0, 0, 7, 3306, 110401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "夏のサバイバル特集…\nちょっと読んでみるか", 1, 1, 0, 0, 8, 3307, 110401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "へー！　こいつ、\nよくできてんな！\nいいなあ、実物も\n乗れたらいいのに", 1, 1, 0, 0, 10, 3309, 110401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "か、かわ…！\nいや、じゃなくて\nあ、あたしがここに\n寝ていいのか…？", 1, 1, 0, 0, 11, 3310, 110401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "故郷の花を飾っても\nいいかな～？\n部屋が楽しくなるよ～", 1, 1, 0, 0, 1, 3311, 110501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "水着で歩いてると\n泳ぎたくなるさ～\n我慢できないさ～！", 1, 1, 0, 0, 2, 3312, 110501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "夏が眩しいのは\nてぃーだがにこにこ\n笑ってるからさ～！", 1, 1, 0, 0, 3, 3313, 110501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "泳ぎ疲れたら\n涼しい木陰で\nお昼寝するさ～", 1, 1, 0, 0, 4, 3314, 110501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "クロールも\n犬かきも\n大得意さ～！", 1, 1, 0, 0, 5, 3315, 110501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "なんだか歌いたい\n気分さ～\nキミもどう～？", 1, 4, 0, 0, 101, 3316, 110501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "海の近くに住んで\nキミと歌って踊って\nそれが理想さ～！", 1, 8, 0, 0, 102, 3317, 110501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私とキミ\nどっちがたくさん\n泳げるか\n競争するさ～！", 1, 1, 0, 0, 1, 3318, 110501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "キミも三線に興味が\nあるのかな～？\n私が教えるさ～！", 1, 1, 0, 0, 2, 3319, 110501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "嫌なことがあっても\n私と歌って踊れば\nなんくるないさー！", 1, 1, 0, 0, 3, 3320, 110501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "たまには時間を忘れて\n私とのーんびり\n海を眺めるさ～", 1, 1, 0, 0, 4, 3321, 110501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ぶるぶるぶるっ！\n毛が濡れちゃっても\nへっちゃらさ～！", 1, 4, 0, 0, 101, 3322, 110501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "泳ぐ前は準備体操～！\n背中押してあげるから\nしっかりやるさ～！", 1, 8, 0, 0, 102, 3323, 110501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "朝の浜辺は\nちゅらさんよ～\n一緒に歩こうよー！", 1, 1, 1, 0, 1, 3324, 110501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼ご飯は\n決まったかな～？\n私はタコライスさ～！", 1, 1, 2, 0, 2, 3325, 110501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "星と波音の中で\n三線鳴らすのも\nオツなものさ～", 1, 1, 3, 0, 3, 3326, 110501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ちょうどよかったさー\nゆっくり休んだら\nまた泳ぎに行くさ～！", 1, 1, 0, 0, 2, 3328, 110501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "砂浜に飾って\nおきたいさ～\nきっとかわいいさ～！", 1, 1, 0, 0, 3, 3329, 110501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "くんくん…はあ\n癒されるさ…\n故郷の夏の匂いさ～", 1, 1, 0, 0, 4, 3330, 110501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "おおー…！\n濡れた髪が\nみるみる乾いてくさ～", 1, 1, 0, 0, 5, 3331, 110501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ん～！　これ、\n海で食べたら\nもっとおいしいさ～", 1, 1, 0, 0, 7, 3333, 110501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "海の生物図鑑ね～\nこの前みかけた魚も\n載ってるかな～？", 1, 1, 0, 0, 8, 3334, 110501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "わぁ～！　みてみて！\n背中に乗れるさー！\nキミと一緒に\n乗りたいさ～！", 1, 1, 0, 0, 10, 3336, 110501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふかふかさ～\n海の底の\n人魚になった気分さ～", 1, 1, 0, 0, 11, 3337, 110501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あの人が\nわたしの騎士だったら\nいいのにな…", 1, 12, 0, 0, 103, 3338, 105801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたのおかげで\nわたしの心はいつも\nお腹いっぱいです♪", 1, 12, 0, 0, 103, 3339, 105801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "許されるのならば、\n今すぐにでも、\n主さまのおそばに\n行きたいです…", 1, 12, 0, 0, 103, 3340, 105901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "主さま…\nお慕いしております…\nいつまでも、おそばに\n居させてください…", 1, 12, 0, 0, 103, 3341, 105901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "なんか最近、\nあいつのことしか\n考えてない…\nあたし…どうしよう…", 1, 12, 0, 0, 103, 3342, 106001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そ、外に行くなら、\n連れていきなさいよね\nその…あたしが\n守ってあげるから…", 1, 12, 0, 0, 103, 3343, 106001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "部屋の中は涼しくて\n気持ちええどすなぁ\n天国やわぁ", 1, 1, 0, 0, 1, 3344, 110601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "クラゲはんって\nフリルいっぱいの\nドレスみたいやねぇ", 1, 1, 0, 0, 2, 3345, 110601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "外でセミはんが\n元気に鳴いてはるねぇ\n今日もええ天気やわぁ", 1, 1, 0, 0, 3, 3346, 110601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "こないに暑いと\n海に入って\n涼みたくなるわぁ", 1, 1, 0, 0, 4, 3347, 110601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "いつか\nマホマホ王国にも\n海の家作りまひょ♪", 1, 1, 0, 0, 5, 3348, 110601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "うちも\n付き合いますえ\n泳ぎの練習しまひょ♪", 1, 4, 0, 0, 101, 3349, 110601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "うちの里帰り\n王子はんも一緒に\n来まへんか？", 1, 8, 0, 0, 102, 3350, 110601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "王子はんは日焼けより\n色白美人の方が\n好きでっしゃろか？", 1, 1, 0, 0, 1, 3351, 110601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "うちと一緒に\n浮き輪で海、\nプカプカしまへん？", 1, 1, 0, 0, 2, 3352, 110601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "日が落ちたら\n線香花火\nやりたいわぁ♪", 1, 1, 0, 0, 3, 3353, 110601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "木陰でのんびり\nお昼寝…\n最高やね～", 1, 1, 0, 0, 4, 3354, 110601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お祭ではぐれても\n王子はんなら見つけて\nくれはります？", 1, 4, 0, 0, 101, 3355, 110601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "楽しい夏やわぁ\nずっとこんな日が\nつづくとええね", 1, 8, 0, 0, 102, 3356, 110601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "うちにしては早起き？\n暑くて目が早う\n覚めてもうたんどす", 1, 1, 1, 0, 1, 3357, 110601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼は冷たい\nおそうめんとか\nどうでっしゃろ", 1, 1, 2, 0, 2, 3358, 110601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜は星がきれいやね\n二人で浜辺でも\n行きまへんか？", 1, 1, 3, 0, 3, 3359, 110601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うーん、遊び疲れた\n身体にちょうどええ\nほっこり具合やわぁ", 1, 1, 0, 0, 2, 3361, 110601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かいらしい子は\nみーんな\nマホマホ王国の\n住人どすえ♪", 1, 1, 0, 0, 3, 3362, 110601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この香り…\n南国にいる気分やねぇ\n癒されるわぁ～", 1, 1, 0, 0, 4, 3363, 110601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これで\n冷えた身体も\nポカポカやねぇ", 1, 1, 0, 0, 5, 3364, 110601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "外はまだ暑いやろうし\nバテんよう\nしっかり食べんとね", 1, 1, 0, 0, 7, 3366, 110601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "海の中にもお城が\nあるんやねぇ\n行ってみたいわぁ", 1, 1, 0, 0, 8, 3367, 110601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この子、ふかふかやね\nこのままマホマホ王国\n乗せてってくれはる？", 1, 1, 0, 0, 10, 3369, 110601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "人魚姫になっても\n最後は王子はんと\nハッピーエンド\nどすえ～", 1, 1, 0, 0, 11, 3370, 110601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "真似したくなる\n人がここには\n大勢いますね", 1, 1, 0, 0, 1, 3371, 107001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "偽物を一人\n紛れこませました\n…冗談です", 1, 1, 0, 0, 2, 3372, 107001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "動物に変身して\nその辺りを\n散歩してきます", 1, 1, 0, 0, 3, 3373, 107001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "失った記憶は\nどの程度回復するの\nでしょうね", 1, 1, 0, 0, 4, 3374, 107001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "人や物だけでなく\nいずれは世界すら\n真似てみせます", 1, 1, 0, 0, 5, 3375, 107001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "次は彼に\nどんなことを\n教えましょうか…", 1, 4, 0, 0, 101, 3376, 107001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "知識はともかく\n私の力はまだ\n必要なようですね", 1, 8, 0, 0, 102, 3377, 107001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どうしました？\n…何でもない？\n…そうですか", 1, 1, 0, 0, 1, 3378, 107001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "退屈しています\n芸でもして\n楽しませなさい", 1, 1, 0, 0, 2, 3379, 107001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "研究のためです\n少し話し相手に\nなりなさい", 1, 1, 0, 0, 3, 3380, 107001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "何か真似して欲しい？\n私の能力を何だと\n思っているのですか", 1, 1, 0, 0, 4, 3381, 107001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "貴方にはまだ色々と\n教えるべきことが\nありますね", 1, 4, 0, 0, 101, 3382, 107001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "随分多くの女性と\n知り合いなのですね\n驚きました", 1, 8, 0, 0, 102, 3383, 107001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "…\nおはようございます\n…まだ朝ですよ", 1, 1, 1, 0, 1, 3384, 107001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "食事にします\n…どうしました？\n早く持ってきなさい", 1, 1, 2, 0, 2, 3385, 107001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "早く寝なさい\n夜更かしすると\n背が伸びませんよ？", 1, 1, 3, 0, 3, 3386, 107001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "マサキより\n座り心地は\nいいですね", 1, 1, 0, 0, 2, 3388, 107001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "意外に複雑な\n構造ですね", 1, 1, 0, 0, 3, 3389, 107001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "真似るなら\n香りまで\n再現しなくては", 1, 1, 0, 0, 4, 3390, 107001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "発火現象の\n模倣…なかなか\n難しいものです", 1, 1, 0, 0, 5, 3391, 107001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "偽物や分身を作るには\nエネルギーが\n必要不可欠なのです", 1, 1, 0, 0, 7, 3393, 107001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "暇つぶしには\nちょうどよさそうです\n…", 1, 1, 0, 0, 8, 3394, 107001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "学院かぁ…\n友達の作り方の\n授業はないかな？", 1, 1, 0, 0, 1, 3396, 110701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "学院でぼっちに\nなったときのために、\n避難場所を\n探しておかないと…", 1, 1, 0, 0, 2, 3397, 110701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ぼっちの私が学院…\nな、なんだか\n目まいがしてきました", 1, 1, 0, 0, 3, 3398, 110701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "クラスメイトに\n話しかける練習…\nむ、難しすぎます…！", 1, 1, 0, 0, 4, 3399, 110701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "目指せ友達100人…\n無理かな…10人…\nや、やっぱり一人！", 1, 1, 0, 0, 5, 3400, 110701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あなたと学院生活が\n送れるなんて、\nとっても\nうれしいです！", 1, 4, 0, 0, 101, 3401, 110701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "えへへ…\n学院でも、\nあなたと一緒に\n居られてうれしいな♪", 1, 8, 0, 0, 102, 3402, 110701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "が、学院ってあんなに\n人が居るんですね…\n友達作りのイメトレを\nやり直さないと！", 1, 1, 0, 0, 1, 3403, 110701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私にとって\n部活動とはＢＢ団！\n今日もたくさん\n活動しましょうね！", 1, 1, 0, 0, 2, 3404, 110701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "休み時間は寝たふり…\nご飯はおトイレ…\nぼっち対策は完璧です", 1, 1, 0, 0, 3, 3405, 110701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "学院で友達を作る\n勉強は完璧ですよ！\n購買部でパンを買って\n渡すんですよね！", 1, 1, 0, 0, 4, 3406, 110701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "学院でも、\nＢＢ団の活動を\nしていきましょうね♪", 1, 4, 0, 0, 101, 3407, 110701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ぼっちに学院生活は\n厳しいですね…でも、\nあなたと一緒なら\n乗りこえられそうです", 1, 8, 0, 0, 102, 3408, 110701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もうこんな時間…\nあの…一緒に学院に\n行ってくれませんか？", 1, 1, 1, 0, 1, 3409, 110701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼ですね！\n言ってくれれば、\nパンでもお握りでも\n買ってきますよ！", 1, 1, 2, 0, 2, 3410, 110701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日もぼっち…\nいつか友達と一緒に、\nパジャマパーティー\nしたいなぁ…", 1, 1, 3, 0, 3, 3411, 110701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "森とは違う\n心地良さが…このまま\n眠っちゃいそうです…", 1, 1, 0, 0, 2, 3413, 110701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいい…この子を、\n話す練習の相手役に\nしてみようかな？", 1, 1, 0, 0, 3, 3414, 110701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい香り…\nいつ友達ができても\nいいように、私の\n部屋にも置こうかな", 1, 1, 0, 0, 4, 3415, 110701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こ、これは…\nぼっちで\n日陰者の私には\n暖かすぎます！", 1, 1, 0, 0, 5, 3416, 110701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "おいしい…！\nお弁当にして、学院に\n持っていけないかな？", 1, 1, 0, 0, 7, 3418, 110701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "学院の授業に\nついていくためにも、\n勉強しておこうかな…", 1, 1, 0, 0, 8, 3419, 110701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あんた、何してんの。\nいいけど、なんでも", 1, 1, 0, 0, 1, 3421, 110801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "邪魔。どいて。\nいや怒ってないし。\nコレがフツーだから", 1, 1, 0, 0, 2, 3422, 110801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "いいけど、\n散らかしめの部屋だと\n片付けたくなるよね…", 1, 1, 0, 0, 3, 3423, 110801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "暇なときは明日の\nコーデ考えてるよ。\n考えるのはタダだし", 1, 1, 0, 0, 4, 3424, 110801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "昔よくシオリン\n構いすぎてハツネに\n文句言われたなァ…", 1, 1, 0, 0, 5, 3425, 110801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "なんかさ。あんたって\n謎に女子トモ多いよね\n…いいけど、べつに", 1, 4, 0, 0, 101, 3426, 110801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ねえ。あのさあ。\nあんた弁当で好きな\n具とかってあんの", 1, 8, 0, 0, 102, 3427, 110801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あ？　何。なんか用。\nや、ないならないで\nいいけど、べつに", 1, 1, 0, 0, 1, 3428, 110801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ごめ、ちょっと\n黙っててくんない。\n明日の試験ヤバだから", 1, 1, 0, 0, 2, 3429, 110801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "歩き方が静か？\n癖になってるんだよね\n足音を殺すの", 1, 1, 0, 0, 3, 3430, 110801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "うちの血液型？\n大雑把のОだけど。\nなんか文句あんの", 1, 1, 0, 0, 4, 3431, 110801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ひゃんっ！\nや、だからさ…急に\n話しかけんなっての", 1, 4, 0, 0, 101, 3432, 110801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "な、何…あんまジッと\n見ないでくんない…\nなんか、その、ハズい", 1, 8, 0, 0, 102, 3433, 110801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "早起きは得意な方だと\n思うけど。昔から\nそれがデフォだったし", 1, 1, 1, 0, 1, 3434, 110801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "こんな時間か…\n昼ごはん\nサクッと作っちゃお", 1, 1, 2, 0, 2, 3435, 110801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あ？　夜遊び？\nはッ…そんなん\n５歳で卒業したっての", 1, 1, 3, 0, 3, 3436, 110801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あーヤバじゃんコレ…\nふかついてる場合\nじゃないっつーのに…", 1, 1, 0, 0, 2, 3438, 110801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "なんだこれ\nかわいいかよ", 1, 1, 0, 0, 3, 3439, 110801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふーん。\nこの匂い…\nなんかいいじゃん", 1, 1, 0, 0, 4, 3440, 110801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "暖炉はアドじゃん。\n煮物、焼き物、蒸し物\nいろいろ作れそう", 1, 1, 0, 0, 5, 3441, 110801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "何コレ\n食べ物困んないじゃん\nうちにも一台くんない", 1, 1, 0, 0, 7, 3443, 110801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "本かァー…んー\n学校の勉強だけで\nいいかなァ…", 1, 1, 0, 0, 8, 3444, 110801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "イタズラってなにを\nしたらいいのかな？", 1, 1, 0, 0, 1, 3446, 111101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "カップケーキ\n上手に作れました\n食べてくれるかな…", 1, 1, 0, 0, 2, 3447, 111101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あの人、\nお菓子くれるかな？\nくれなかったら…ふふ", 1, 1, 0, 0, 3, 3448, 111101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "オバケ…いないよね？\n怖くなんてないけど！", 1, 1, 0, 0, 4, 3449, 111101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ハロウィンは\n楽しいけどオバケの\nお祭りなんだよね…", 1, 1, 0, 0, 5, 3450, 111101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "にゃ～、にゃお～！\nちゃんと怖いかな？\n早く帰ってこないかな", 1, 4, 0, 0, 101, 3451, 111101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あの人のことだから\nお菓子欲しがるよね…\n準備はバッチリです♪", 1, 8, 0, 0, 102, 3452, 111101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "えへへ。猫さんのお耳\nよく出来てるでしょ？", 1, 1, 0, 0, 1, 3453, 111101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "触りましたね？\nイタズラしましたね？\nへんた～い！", 1, 1, 0, 0, 2, 3454, 111101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "フー！　頭を撫でたら\n怒りますよ！　あれ？\n猫みたいでした？", 1, 1, 0, 0, 3, 3455, 111101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "きゃっ！　も、もう、\n脅かさないでください\n怖くありませんけど！", 1, 1, 0, 0, 4, 3456, 111101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お菓子ですか？\nありがとうございます\nハッピーハロウィン♪", 1, 4, 0, 0, 101, 3457, 111101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ゴロゴロ…\nね、猫さんの真似です\n…似てましたか？", 1, 8, 0, 0, 102, 3458, 111101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "さぁ、お菓子作りを\n始めますよ。下準備は\n大事ですからね！", 1, 1, 1, 0, 1, 3459, 111101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今、つまみ食い\nしましたね！？　あっ\nおやつの時間でしたか", 1, 1, 2, 0, 2, 3460, 111101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "さぁ、ハロウィンの\n始まりです…ふぁ…\nね、眠くありません！", 1, 1, 3, 0, 3, 3461, 111101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ちょっと疲れたかも…\nパーティの時間まで\nひと休みしなきゃ", 1, 1, 0, 0, 2, 3463, 111101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "私も今、こんな感じ\nなのかな？　こんなに\nかわいいといいなぁ", 1, 1, 0, 0, 3, 3464, 111101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "甘い匂いがいっぱい…\nお菓子の香りかな？", 1, 1, 0, 0, 4, 3465, 111101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "温かくなると、\n丸まりたくなって…\n…猫になっちゃう！？", 1, 1, 0, 0, 5, 3466, 111101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ニンジンもこれくらい\nおいしければ\nいいのに…", 1, 1, 0, 0, 7, 3468, 111101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ハロウィンの本、\nオバケがたくさん\n載ってて怖いかも…", 1, 1, 0, 0, 8, 3469, 111101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "美味しそうなお菓子が\nいっぱい…これなら\nイタズラされませんね", 1, 1, 0, 0, 12, 3471, 111101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "たくさんいたずら\nできるから\nハロウィン大好き！", 1, 1, 0, 0, 1, 3473, 111201);
