INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたとなら\nどんなことだって\nできる気がするわ♪", 1, 12, 0, 0, 103, 3555, 105201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あーあ、退屈だな\nカレでも探しに\nいこうかな", 1, 1, 0, 0, 1, 3556, 106501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この前のケンカは\n最高に燃えたなぁ\n…拳が疼いてきたぜ", 1, 1, 0, 0, 2, 3557, 106501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "一体、この世界は\nどうなっちまったって\nいうんだ…？", 1, 1, 0, 0, 3, 3558, 106501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "もう仕事の時間か\nボスも人使いが荒いぜ\n…ま、いいけどさ！", 1, 1, 0, 0, 4, 3559, 106501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "オレたち以外に\nドラゴン族はあと何人\nいるんだろうな…", 1, 1, 0, 0, 5, 3560, 106501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "カレの真剣な顔\n面白かったな\nまた遊びたいぜ", 1, 4, 0, 0, 101, 3561, 106501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "どうしてカレがそばに\nいると、腹の底から\n力が湧いてくるんだ？", 1, 8, 0, 0, 102, 3562, 106501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "よっす！\nなんかオイシイ話でも\nあったりしない？", 1, 1, 0, 0, 1, 3563, 106501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なあ、オレと遊ぼうぜ\n心配すんなよ。危ない\nことはよしとくからさ", 1, 1, 0, 0, 2, 3564, 106501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どうした。オレに\nケンカ売ろうってか？\n…ははは、冗談だよ！", 1, 1, 0, 0, 3, 3565, 106501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ちょうど退屈してた\nところだ、特訓に\n付き合ってくれよ", 1, 1, 0, 0, 4, 3566, 106501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ったくよぉ…\nオレがかわいいなんて\n冗談はもうよせよ？", 1, 4, 0, 0, 101, 3567, 106501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "次の仕事も一緒に\n来るだろ？　断っても\n連れて行くけどな！", 1, 8, 0, 0, 102, 3568, 106501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよう！　今日の\n予定はまずケンカして\nで、その後もケンカだ", 1, 1, 1, 0, 1, 3569, 106501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "よっす！\n腹ごしらえが済んだら\nオレと遊ぼうぜ！", 1, 1, 2, 0, 2, 3570, 106501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜は危ないから\nあんま出歩くな？\n…だから面白えんだろ", 1, 1, 3, 0, 3, 3571, 106501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "次のケンカに備えて\n今は休んでおくか…\n全力で暴れたいからな", 1, 1, 0, 0, 2, 3573, 106501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ボスの飼ってる\nラーゴが、中に\n入ってるんじゃ？", 1, 1, 0, 0, 3, 3574, 106501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ああ、たぎる匂いが\nする…ひと暴れ\nしたくなってきた！", 1, 1, 0, 0, 4, 3575, 106501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "おおぉ…\nオレの拳もなんだか\n熱くなってきたぜ…！", 1, 1, 0, 0, 5, 3576, 106501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いつでも全力で\nケンカするために\n力を蓄えないとな", 1, 1, 0, 0, 7, 3578, 106501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "やっぱりな…オレが\n知ってる地図とは\nまるで違ってやがる", 1, 1, 0, 0, 8, 3579, 106501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "さぁプレゼントで\n埋め尽くしてやろう！\n大いに喜ぶがいいよ！", 1, 1, 0, 0, 1, 3581, 111501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "メリークリスマス☆\n聖夜の宴だ\n派手に楽しむとしよう", 1, 1, 0, 0, 2, 3582, 111501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "煙突から入る？\nいいや、ワタシは\nいつでも正面からさ♪", 1, 1, 0, 0, 3, 3583, 111501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "サンタクロースの\nお出ましだ！\n歓迎するがいいよ♪", 1, 1, 0, 0, 4, 3584, 111501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "飾り付けが足りん！\nワタシが演出して\nやろうじゃないか！", 1, 1, 0, 0, 5, 3585, 111501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "人の喜ぶ顔を見るのも\n悪くはないな…おっと\nワタシらしくないな☆", 1, 4, 0, 0, 101, 3586, 111501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "プレゼントはまだか？\nワタシを喜ばせたら\nお返しは弾むぞ♪", 1, 8, 0, 0, 102, 3587, 111501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ワタシを呼んだか？\n退屈な要件だったら\n覚悟しておくことだ", 1, 1, 0, 0, 1, 3588, 111501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なんだ？\nワタシへの挑戦か？\nよし、受けた♪", 1, 1, 0, 0, 2, 3589, 111501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おっと、当たらんよ\nワタシの乱数聖域は\n絶対だからな…♪", 1, 1, 0, 0, 3, 3590, 111501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なんだ、オマエも\n暇なのか？\n奇遇だな、ワタシもさ", 1, 1, 0, 0, 4, 3591, 111501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なにか愉しいことでも\nしようじゃないか\nさぁ、何をする？", 1, 4, 0, 0, 101, 3592, 111501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "こうもワタシに\n気安く触れるのは\nオマエくらいだよ…♪", 1, 8, 0, 0, 102, 3593, 111501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "朝だろうと関係ない！\nさぁ愉しく苛烈に\n騒ごうじゃないか♪", 1, 1, 1, 0, 1, 3594, 111501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "調子の出てくる時間だ\nしっかりワタシを\n愉しませてくれよ♪", 1, 1, 2, 0, 2, 3595, 111501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "眠いなどと言わせんぞ\nまだまだ一日は\n終わらんからな！", 1, 1, 3, 0, 3, 3596, 111501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい具合じゃないか\nこのまま持って帰って\nしまおうか…♪", 1, 1, 0, 0, 2, 3598, 111501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "なかなか愛らしい\n造形をしているな？\nワタシには負けるがな", 1, 1, 0, 0, 3, 3599, 111501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ほう、悪くない香りだ\n戦いの匂いをうまく\n隠してくれそうだ…♪", 1, 1, 0, 0, 4, 3600, 111501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "体が温まってくると\nひと暴れしたくなるな", 1, 1, 0, 0, 5, 3601, 111501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ワタシの舌は厳しいぞ\n…ふーむ、なるほど\n認めざるをえんな", 1, 1, 0, 0, 7, 3603, 111501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "クリスティーナさんは\n知識の収集も\n欠かさないんだよ", 1, 1, 0, 0, 8, 3604, 111501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これは驚いたな\nだがもっと、もっとだ\nワタシを退屈させるな", 1, 1, 0, 0, 14, 3606, 111501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こんな戯れだって\n軽くこなしてしまう\nクリスティーナさんさ", 1, 1, 0, 0, 15, 3607, 111501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "メリークリスマス！\nさあ、パーティーを\n盛り上げるよ！", 1, 1, 0, 0, 1, 3608, 111601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "衣装一つで全然\n気分が違うね！\n最高のクリスマスに\nなりそうだよ！", 1, 1, 0, 0, 2, 3609, 111601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ツリーのてっぺんの\n星って、なんだか\n欲しくなるんだよね", 1, 1, 0, 0, 3, 3610, 111601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "めいっぱい\nクリスマスを楽しむよ\n遊びだって本気\nなんだからね！", 1, 1, 0, 0, 4, 3611, 111601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "赤、白、緑、金\nクリスマスカラー\nってお洒落だよね", 1, 1, 0, 0, 5, 3612, 111601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "雪、降らないかな？\nホワイトクリスマス\nってちょっと\n憧れるなぁ", 1, 4, 0, 0, 101, 3613, 111601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "キミと過ごすこの\n楽しい時間がずーっと\n続けばいいのにな", 1, 8, 0, 0, 102, 3614, 111601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "クリスマスライブ\nチケット用意するから\n絶対来てよね！", 1, 1, 0, 0, 1, 3615, 111601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "カンパーイ！\nジュースだけど\nちょっと大人の\n気分だね", 1, 1, 0, 0, 2, 3616, 111601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "クリスマス衣装どう？\nかわいいでしょ\nあっ、どこ見てるの！", 1, 1, 0, 0, 3, 3617, 111601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ねえ、後で街に\n行こうよ\n大きなツリーを\n一緒に見たいんだ♪", 1, 1, 0, 0, 4, 3618, 111601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ねえ、ダンスの練習に\n付き合ってくれない？\n大丈夫、手取り足取り\n教えてあげるから♪", 1, 4, 0, 0, 101, 3619, 111601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "来年のクリスマスも\n隣にキミが\nいてくれたら\nうれしいのにな", 1, 8, 0, 0, 102, 3620, 111601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "クリスマスって朝から\nドキドキしちゃうね\nなんだか子どもみたい", 1, 1, 1, 0, 1, 3621, 111601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "パーティーの準備も\n大分進んだね\n一息ついて\nランチにしよっか", 1, 1, 2, 0, 2, 3622, 111601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "静かな夜だね\n心臓の音まで\n聞こえちゃいそう…", 1, 1, 3, 0, 3, 3623, 111601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あ～、この絶妙な\n柔らかさ…\nいい仕事してるなぁ…", 1, 1, 0, 0, 2, 3625, 111601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "アイドル顔負けの\nかわいさ、ズルい！\nでもかわいい！", 1, 1, 0, 0, 3, 3626, 111601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ん～、落ち着くなぁ\n今日はゆっくり\n眠れそうだよ", 1, 1, 0, 0, 4, 3627, 111601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ぬくぬく～\nどうしよう、ここから\n動きたくないなぁ", 1, 1, 0, 0, 5, 3628, 111601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ダンスの練習もしたし\nちょっとくらいなら\nいいよね！", 1, 1, 0, 0, 7, 3630, 111601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "アイドルといえど\nかわいいだけじゃダメ\n知識も必要だよね", 1, 1, 0, 0, 8, 3631, 111601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "面白ーい！\nこの演出、ライブでも\nできないかな？", 1, 1, 0, 0, 14, 3633, 111601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もふもふ～♪\n寒い日には最高の\n抱き心地だね！", 1, 1, 0, 0, 15, 3634, 111601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "彼ともっとお話し\nしたいな…もう少し\n補習を増やそうかしら", 1, 12, 0, 0, 103, 3635, 101801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "君のこともっともっと\n教えて欲しいな。私に\n補習授業してくれる？", 1, 12, 0, 0, 103, 3636, 101801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この姿ならあやつも\n子ども扱いできまい！\n…じゃがなぜか、少し\n物足りないような…", 1, 1, 0, 0, 1, 3637, 111701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "時間もあるし、\n聖夜の宴の\n準備をするかのう♪", 1, 1, 0, 0, 2, 3638, 111701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "…わらわの知る聖夜と\n随分違うが、宴は宴\n楽しませて\nもらおうかのう♪", 1, 1, 0, 0, 3, 3639, 111701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "大人の姿で\nできること…ふむ\n少しあやつを\nからかってやるか♪", 1, 1, 0, 0, 4, 3640, 111701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "…クリスマスは\n恋人たちの夜！？\nち、近頃の若いもんは\n破廉恥なのじゃ！", 1, 1, 0, 0, 5, 3641, 111701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "…あやつがおらんと、\n宴も物足りぬな\n…馬鹿者が\n早く帰ってこい…", 1, 4, 0, 0, 101, 3642, 111701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "誰も見ておらぬな…？\n…よし、あやつへの\nプレゼントを\n準備するとしようか！", 1, 8, 0, 0, 102, 3643, 111701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "クリスマスの\nチキンにケーキ…\nべ、別に楽しみになど\nしておらぬぞ！", 1, 1, 0, 0, 1, 3644, 111701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この姿では、わらわの\n頭をなでるのは\n難しかろう…べ、\n別に残念ではないわ！", 1, 1, 0, 0, 2, 3645, 111701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "メリー\nクリスマスじゃ！\nさあ、わらわのために\n宴の準備をするのじゃ", 1, 1, 0, 0, 3, 3646, 111701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そんなにこの衣装が\n気になるのか？\n…だったら、直に\n触ってみるがよい♪", 1, 1, 0, 0, 4, 3647, 111701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "いつもと逆に、お主を\n子ども扱いしてやるか\nほれ、膝枕をしてやる\nからそばに来い♪", 1, 4, 0, 0, 101, 3648, 111701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "…せ、聖夜の予定は\n空けておくのじゃぞ？\nなんでって…\nな、なんでもじゃ！", 1, 8, 0, 0, 102, 3649, 111701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もう朝か…聖夜の宴が\n楽しみで…ではなく！\n準備のせいで、\nあまり眠れんかったぞ", 1, 1, 1, 0, 1, 3650, 111701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "…ずっと雪が\n降っておれば、\n陽光が強いこの時間も\n怯えずにすむのにのう", 1, 1, 2, 0, 2, 3651, 111701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "時は来た…\nさあ眷属よ！\nわらわと共に、\n聖夜の宴を楽しもうぞ", 1, 1, 3, 0, 3, 3652, 111701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "良い椅子ではないか\nここから聖夜の宴を\n眺めるのもよいかもな", 1, 1, 0, 0, 2, 3654, 111701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "か、かわい…こほん\nなかなか良い\nデザインをしておるな", 1, 1, 0, 0, 3, 3655, 111701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふむ、良い香りじゃ\n聖夜の宴でも\nこれを使うとしよう", 1, 1, 0, 0, 4, 3656, 111701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これが癒し…はっ！\nあやうく浄化される\nところであった…", 1, 1, 0, 0, 5, 3657, 111701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ニ、ニンニクは\n入っておらぬよな？\n…うむ、美味じゃ♪", 1, 1, 0, 0, 7, 3659, 111701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "聖夜は恋人たちの夜…\n…き、気になるわけ\nではないが、\n少し調べてみるか", 1, 1, 0, 0, 8, 3660, 111701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ほう…これは面白い\n魔法の使い方じゃな\nまるで本物の\n演劇のようじゃ", 1, 1, 0, 0, 14, 3662, 111701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こ、この程度の\nかわいさに誘惑される\nわらわでは…\nふわもこなのじゃ～♪", 1, 1, 0, 0, 15, 3663, 111701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "おせち料理も\n魔物尽くしだったり\nしないわよね！", 1, 1, 0, 0, 1, 3664, 112001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "初詣で何をお祈り\nしようかしら…\n定番でいいわよね？", 1, 1, 0, 0, 2, 3665, 112001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "初日の出って\n特別な感じがして\nいいわよね…", 1, 1, 0, 0, 3, 3666, 112001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "寒い中並んで飲む\n甘酒って不思議なほど\nおいしいわよね", 1, 1, 0, 0, 4, 3667, 112001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "羽根つきで勝負…\nあいつにだったら\n勝てるかしら？", 1, 1, 0, 0, 5, 3668, 112001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "年賀状になんて書こう\n…いろいろと\n恥ずかしくなるのよね", 1, 4, 0, 0, 101, 3669, 112001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今年もあいつと\n居られること…\n結構うれしいわ", 1, 8, 0, 0, 102, 3670, 112001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "何じっと\n見てんのよ？\nお年玉はあげないわよ", 1, 1, 0, 0, 1, 3671, 112001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おせち料理一緒に\n食べる？\n結構おいしいわよ", 1, 1, 0, 0, 2, 3672, 112001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "羽根つきで負けたら\n顔に墨塗られるのよ\n覚悟しなさい！", 1, 1, 0, 0, 3, 3673, 112001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "門松飾るから\nちょっと手伝って\nくれる？", 1, 1, 0, 0, 4, 3674, 112001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あんたと一緒に\n初詣行くのも悪くない\n…かも", 1, 4, 0, 0, 101, 3675, 112001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "初詣で何をお祈り\nしたか？　うっさい！\n内緒よ！内緒！", 1, 8, 0, 0, 102, 3676, 112001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もう朝なの…\n正月くらいゆっくり\n寝ていたいわよね…", 1, 1, 1, 0, 1, 3677, 112001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おせちに飽きたら\n次は何を食べよう\nかしら…", 1, 1, 2, 0, 2, 3678, 112001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そろそろ寝ましょ\nいい初夢が見られると\nいいわね", 1, 1, 3, 0, 3, 3679, 112001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この格好でいるのも\nなかなか疲れるわね\n休憩～", 1, 1, 0, 0, 2, 3681, 112001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "か、かわいい…う～ん\n鏡餅も顔を描けば\nかわいくなるかしら？", 1, 1, 0, 0, 3, 3682, 112001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい匂いね\n落ち着いて\n眠れそう…", 1, 1, 0, 0, 4, 3683, 112001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ここでお餅焼いたら\n一段とおいしく\nなりそうね", 1, 1, 0, 0, 5, 3684, 112001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "正月に食べるものは\nなんでもおいしく\n感じちゃうわね！", 1, 1, 0, 0, 7, 3686, 112001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "魔物を使わない\nおせちを\n調べておかなきゃ…", 1, 1, 0, 0, 8, 3687, 112001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "新年を気持ちよく\n過ごせるように\nお掃除がんばります！", 1, 1, 0, 0, 1, 3689, 112101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "神殿に通っていたら\nいつの間にか足腰が\n鍛えられたような…", 1, 1, 0, 0, 2, 3690, 112101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "神殿付近は屋台も\nあるので、良い香りが\n漂ってくるんですよ～", 1, 1, 0, 0, 3, 3691, 112101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "おせちを作れるように\nなれば、子どもたちも\n喜んでくれるかな…？", 1, 1, 0, 0, 4, 3692, 112101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "コホォォォォォ…！\nふぅ、深呼吸すると\n落ち着きますね♪", 1, 1, 0, 0, 5, 3693, 112101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お仕事以外の時も\nあなたと…い、いえ！\nなんでもないです！", 1, 4, 0, 0, 101, 3694, 112101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あなたと一緒だと\nなんだかポカポカ\nしてくるんです…", 1, 8, 0, 0, 102, 3695, 112101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この格好をしていると\nなんだか神殿にいる\n気分になりますね", 1, 1, 0, 0, 1, 3696, 112101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そういえば神殿で\n甘酒をもらったんです\nあなたも飲みますか？", 1, 1, 0, 0, 2, 3697, 112101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あっ、髪飾りが\nズレちゃってました！\nいけないいけない…", 1, 1, 0, 0, 3, 3698, 112101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ひゃわっ！？\nく、くすぐったり\nしないでください～", 1, 1, 0, 0, 4, 3699, 112101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そ、そんなに\n見つめられると\nて、照れちゃいます…", 1, 4, 0, 0, 101, 3700, 112101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今度、私が考えた\n運気上昇のおまじない\nかけてあげますね♪", 1, 8, 0, 0, 102, 3701, 112101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようございます\n神殿のお手伝いに行く\n準備もしなきゃですね", 1, 1, 1, 0, 1, 3702, 112101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼になりましたし\nご飯は私が作…\nえっ、ダメですか！？", 1, 1, 2, 0, 2, 3703, 112101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "明日も早いですし\nそろそろ寝る支度を…\nふわぁ…", 1, 1, 3, 0, 3, 3704, 112101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お掃除も終わったので\n少し休憩を…ふわぁ…\n急に眠気が…", 1, 1, 0, 0, 2, 3706, 112101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "新年を気持ちよく\n過ごせるようこの子も\nきれいにしましょう♪", 1, 1, 0, 0, 3, 3707, 112101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "神殿のお香のような\n香りだと落ち着いて\n過ごせそうですよね", 1, 1, 0, 0, 4, 3708, 112101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ぽかぽかですね～…\n甘酒も飲んで体の\n芯から温めましょう", 1, 1, 0, 0, 5, 3709, 112101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "今年こそ\n料理も上手に…\nもぐもぐもぐもぐ…", 1, 1, 0, 0, 7, 3711, 112101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "失敗続きだと\n逆に運が良くなる…\nほ、本当ですかぁ…？", 1, 1, 0, 0, 8, 3712, 112101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "キミにならもう\nどんな私を見られても\n大丈夫かな…♪", 1, 12, 0, 0, 103, 3714, 103401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なになにどしたのぉ？\nお姉さんが撫でたげる\nこっちにきなさぁい♪", 1, 12, 0, 0, 103, 3715, 103401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "わたくしの舞を\n主さまは喜んで\nくださるでしょうか…", 1, 1, 0, 0, 1, 3716, 111901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "年を越したあとに\n食べるおそばも\nおいしいです", 1, 1, 0, 0, 2, 3717, 111901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "甘酒を飲むと\n心まで温まる\n気がいたします", 1, 1, 0, 0, 3, 3718, 111901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "主さまのために\n真心を込めたおせちを\nご用意しなくては", 1, 1, 0, 0, 4, 3719, 111901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "おみくじを引くのは\nドキドキしつつも\n楽しいものです", 1, 1, 0, 0, 5, 3720, 111901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "主さまの温もりは\n心まで温めてくれる\nようです", 1, 4, 0, 0, 101, 3721, 111901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "うまくできなくとも\n心を込めて精いっぱい\n舞いましょう", 1, 8, 0, 0, 102, 3722, 111901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "主さま、この晴れ着は\n似合っている\nでしょうか？", 1, 1, 0, 0, 1, 3723, 111901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お餅はゆっくりと\n召し上がって\nくださいまし", 1, 1, 0, 0, 2, 3724, 111901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "主さまのお顔の\n福笑いなら、完璧に\nできる気がいたします", 1, 1, 0, 0, 3, 3725, 111901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "主さまはどのような\n初夢をご覧になった\nのでしょう？", 1, 1, 0, 0, 4, 3726, 111901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "甘酒の飲み過ぎは\nいけませんよ？\nめっ、でございます", 1, 4, 0, 0, 101, 3727, 111901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "二人羽織とは\nあんなにも暖かい\nものなのですね…", 1, 8, 0, 0, 102, 3728, 111901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようございます\n寒い季節の朝日は\nとても美しいですね", 1, 1, 1, 0, 1, 3729, 111901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おいしくとも\n食べ過ぎは正月太りの\nもとでございます", 1, 1, 2, 0, 2, 3730, 111901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "いい夢が見られるよう\n暖かくして\n眠るといたしましょう", 1, 1, 3, 0, 3, 3731, 111901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふかふか…\nこのまま\n寝てしまいそうです", 1, 1, 0, 0, 2, 3733, 111901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あなたと一緒に\nお正月遊びを\n楽しんでみたいです", 1, 1, 0, 0, 3, 3734, 111901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "神殿の独特な香りを\nおうちに持ち込むのは\nどうでしょうか", 1, 1, 0, 0, 4, 3735, 111901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "風邪をひきませんよう\n暖かくいたしましょう", 1, 1, 0, 0, 5, 3736, 111901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "食べ過ぎは\n正月太りの\nもとだというのに…", 1, 1, 0, 0, 7, 3738, 111901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "主さまのために\n参拝の作法を\n予習しておきましょう", 1, 1, 0, 0, 8, 3739, 111901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "…この杖、変身前には\n無いパーツがあるけど\nこれはどこから…うん\n考えないようにしよう", 1, 1, 0, 0, 1, 3741, 112201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "フ、フリフリの\nかわいい衣装…はっ！\n素に戻ってはダメだ！", 1, 1, 0, 0, 2, 3742, 112201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "魔法探偵\nミスティ★カスミ！\n魔法の力で捜査開始！\n…練習終わりっと", 1, 1, 0, 0, 3, 3743, 112201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "怪しい視線を感じる…\nまさか、近くに\n闇の力を持つ人が！？", 1, 1, 0, 0, 4, 3744, 112201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "時間もあるし、\n魔法少女の修行を…\nじゃ、じゃなくて！\n探偵の仕事をしよう！", 1, 1, 0, 0, 5, 3745, 112201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "助手くんとずっと\n一緒に居られるし、\n魔法少女も\n案外悪くないかも♪", 1, 4, 0, 0, 101, 3746, 112201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "魔法少女の訓練のため\n助手くんの所に行こう\nかな…あ、あくまで\n訓練のためにね！", 1, 8, 0, 0, 102, 3747, 112201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "…髪型が急に\n変わって驚いた？\nき、気になるなら…\n少し触ってみるかい？", 1, 1, 0, 0, 1, 3748, 112201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "助手くんは魔法少女に\nなりたいのかい？\nその場合は魔法騎士…\nいや、魔法助手とか？", 1, 1, 0, 0, 2, 3749, 112201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "頭に浮かぶ呪文…\n強制変身…助手くん\n魔法少女とは\n呪いか何かなのかな？", 1, 1, 0, 0, 3, 3750, 112201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あ、あまり触らないで\nくれ…魔法少女になる\nと感覚が鋭くなって、\n耳も敏感に…ひゃう！", 1, 1, 0, 0, 4, 3751, 112201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "や、やっぱりこの\n格好は変かな…え、\nかわいい？　じゃあ…\nいや、でも、う～ん", 1, 4, 0, 0, 101, 3752, 112201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "…この変身は助手くん\nのせいでもあるんだよ\nだから…せ、責任を\n取ってもらうからね！", 1, 8, 0, 0, 102, 3753, 112201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよう助手くん\nさあ、そろそろ\n街のパトロールに\n行こうじゃないか！", 1, 1, 1, 0, 1, 3754, 112201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もうお昼…助手くん、\nそろそろ本業に戻ろう\n…その顔は何かな？\n私の本業は探偵だぞ！", 1, 1, 2, 0, 2, 3755, 112201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜だけど、私は\nもう少し仕事をするよ\n魔法少女の強い体は\nこんなときに便利だね", 1, 1, 3, 0, 3, 3756, 112201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "気持ちいい…\nこの癒しを、魔法と\n論理の二つの観点から\n解明したいね…", 1, 1, 0, 0, 2, 3758, 112201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ダ、ダメ！！\nここで感情のままに\n飛びついたら、私の\nイメージがぁ～！", 1, 1, 0, 0, 3, 3759, 112201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "心が安らぐ香りだね…\n…今なら、魔法少女も\nすんなりと受け入れ…\nてはダメだっ！", 1, 1, 0, 0, 4, 3760, 112201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったかい…\n魔法少女だろうと\n休息は必要だし、少し\n休んでいこうかな", 1, 1, 0, 0, 5, 3761, 112201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "魔法少女も探偵も、\nやっぱり体が資本！\n食べられるときに\n食べておかないとね♪", 1, 1, 0, 0, 7, 3763, 112201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "…魔法少女\nセリイちゃんの本は\nどこにあるのかな？", 1, 1, 0, 0, 8, 3764, 112201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "魔法少女かぁ…\nせっかくだし思う存分\n体を動かしてみたいな", 1, 1, 0, 0, 1, 3766, 112301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ニャットちゃん…\nこの格好を見て\n私って気づくかな？", 1, 1, 0, 0, 2, 3767, 112301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ふふ…\n今日はなんだか\n調子がいいかも", 1, 1, 0, 0, 3, 3768, 112301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "魔法は便利だけど\nあまり頼りすぎちゃ\nダメだよね", 1, 1, 0, 0, 4, 3769, 112301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "さっき街で小さな子に\n声かけられちゃった\n照れるけどうれしいな", 1, 1, 0, 0, 5, 3770, 112301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あの人がいると\nなんだかホッとする\n不思議…", 1, 4, 0, 0, 101, 3771, 112301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "魔法少女になっても\nあの人への気持ちは\nずっと同じ…", 1, 8, 0, 0, 102, 3772, 112301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日の私…ですか？\nはいとっても元気です\n…あなたのおかげかも", 1, 1, 0, 0, 1, 3773, 112301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "よかったら魔法の\n練習に付き合って\nくれませんか？", 1, 1, 0, 0, 2, 3774, 112301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あの…\nこの格好どうですか？\n似合ってると…いいな", 1, 1, 0, 0, 3, 3775, 112301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "…な、なんですか？\nあまり見られると\n恥ずかしいです…", 1, 1, 0, 0, 4, 3776, 112301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私、あなたのそばに\nいられたら、それで…", 1, 4, 0, 0, 101, 3777, 112301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "想い人と結ばれる\n魔法は…ひゃっ！？\n今の聞いてましたか？", 1, 8, 0, 0, 102, 3778, 112301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようございます\n今日も街の平和の\nために頑張らなきゃ", 1, 1, 1, 0, 1, 3779, 112301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お腹すきましたね。\nあの…一緒にランチは\nいかがですか？", 1, 1, 2, 0, 2, 3780, 112301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あ、もうこんな時間…\nふふ、まだこんなに\n体力が余ってるのに", 1, 1, 3, 0, 3, 3781, 112301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "気持ちいい…\nこの気持ち良さは\nどんな魔法も勝てない", 1, 1, 0, 0, 2, 3783, 112301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "何これ、かわいい…\nお姉ちゃんにも\n見せてあげたいな", 1, 1, 0, 0, 3, 3784, 112301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ん～、いい香り\n…これ、魔法で\n出せたりしないかな？", 1, 1, 0, 0, 4, 3785, 112301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったか～い\nえへへ…なんだか\nお姉ちゃんみたい", 1, 1, 0, 0, 5, 3786, 112301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "おいしいなぁ、これ\n魔法少女になってから\n食欲旺盛かも…", 1, 1, 0, 0, 7, 3788, 112301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふふ…\n魔法少女になっても\n読書はやめられないな", 1, 1, 0, 0, 8, 3789, 112301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "みらくるまほりん\nくるりんぱ～♪\n王子はんのために\n花嫁修業や～♪", 1, 12, 0, 0, 103, 3791, 101001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あ、あのな王子はん\nできたらその…\nお姫さまだっこを\nお願いしてもええ…？", 1, 12, 0, 0, 103, 3792, 101001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "やっぱりわたしたちの\nギルドハウスの\n居心地は最高ですね☆", 1, 1, 0, 0, 1, 3793, 180401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "王宮じゃできないこと\nやっちゃいましょう♪\n例えば、えっと～…", 1, 1, 0, 0, 2, 3794, 180401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "そうだ、隠しておいた\nおやつがありました！\nまだ食べられますね☆", 1, 1, 0, 0, 3, 3795, 180401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ごはんはみんなで\n楽しく食べましょう！\nそれが一番の\n調味料です♪", 1, 1, 0, 0, 4, 3796, 180401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "やっぱりここが、\nわたしの帰る場所です\n…ただいま☆", 1, 1, 0, 0, 5, 3797, 180401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "なんだか物足りません\nきっと、あの人が隣に\nいないからですね…", 1, 4, 0, 0, 101, 3798, 180401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "どこを見ても、大切な\n思い出がたくさん\n詰まっていますね♪", 1, 8, 0, 0, 102, 3799, 180401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "無礼者～、なんて\n冗談です♪　どんどん\n触っちゃってください", 1, 1, 0, 0, 1, 3800, 180401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ぷにぷにでしょう～？\nい～っぱい食べるのが\nぷにぷにの秘訣です！", 1, 1, 0, 0, 2, 3801, 180401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "握手したいんですか？\nはい、にぎにぎ～！\nもう離しませんよ♪", 1, 1, 0, 0, 3, 3802, 180401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どうかしましたか？\n退屈してるんでしたら\nお散歩に行きましょう", 1, 1, 0, 0, 4, 3803, 180401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ごはん王子♪\nわたしは王女ですから\nこれでピッタリですね", 1, 4, 0, 0, 101, 3804, 180401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "大好きです～\n愛してます～\n…ほんと、ですよ♪", 1, 8, 0, 0, 102, 3805, 180401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "朝ごはんは大事です！\n山盛りごはんを食べて\n元気いっぱいですよ☆", 1, 1, 1, 0, 1, 3806, 180401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お腹ぺこぺこになる\n時間ですね～！\nごはん特盛、一つ！", 1, 1, 2, 0, 2, 3807, 180401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お腹いっぱい、今日は\nよく眠れそうですね♪\n明日もおいしい一日を", 1, 1, 3, 0, 3, 3808, 180401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "休憩休憩～♪\nお腹を休めて、次の\nごはんに挑みます…☆", 1, 1, 0, 0, 2, 3810, 180401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいらしい\nぬいぐるみですね～\nキャルちゃんみたい☆", 1, 1, 0, 0, 3, 3811, 180401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "すんすん…この匂い\n食欲増進の効果が\nありそうですね…？", 1, 1, 0, 0, 4, 3812, 180401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "体温が上がってくると\n眠くなっちゃいます…\nほわぁ～", 1, 1, 0, 0, 5, 3813, 180401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もぐもぐもぐもぐもぐ\nむぐむぐむぐむぐむぐ\nはぐはぐはぐはぐはぐ", 1, 1, 0, 0, 7, 3815, 180401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "教養を身に着けるには\n読書が一番です！\nえっと次のレシピは～", 1, 1, 0, 0, 8, 3816, 180401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "アイドルらしくなる\nコツって、何か\nあるんでしょうか？", 1, 1, 0, 0, 1, 3818, 112401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "色んな人と話すのが\n好きなんです。つい\n長くなっちゃって…", 1, 1, 0, 0, 2, 3819, 112401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "アイドルを目指すって\n決めてから、毎日が\nワクワクなんです♪", 1, 1, 0, 0, 3, 3820, 112401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ちゃんとストレッチ\nしておかなくっちゃ\nよいしょ、よいしょ…", 1, 1, 0, 0, 4, 3821, 112401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "リンちゃんミオちゃん\n二人に負けないように\n私、頑張ります！", 1, 1, 0, 0, 5, 3822, 112401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "私の笑顔は素敵だって\n誰かに褒められた…\nそんな気がするんです", 1, 4, 0, 0, 101, 3823, 112401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "私たちを支えてくれる\n誰かが、ずっと一緒に\nいてくれたような…", 1, 8, 0, 0, 102, 3824, 112401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どうかしましたか？\n何か困ってるのなら\nお手伝いしますよ！", 1, 1, 0, 0, 1, 3825, 112401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "みなさん個性的で\n普通じゃない人ばかり\nいるような気が…", 1, 1, 0, 0, 2, 3826, 112401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日はポカポカした\nいい天気ですね～\nお散歩日和です！", 1, 1, 0, 0, 3, 3827, 112401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今、ミルクティーを\n淹れてたんです♪\nあなたも飲みますか？", 1, 1, 0, 0, 4, 3828, 112401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私の得意なこと？\nうーんなんでしょう…\nあっ、肩たたきです！", 1, 4, 0, 0, 101, 3829, 112401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "生・ハム・メローン！\nあ、ライブの掛け声を\n練習してたんですよ♪", 1, 8, 0, 0, 102, 3830, 112401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようございます！\n今日も一日\n頑張りましょうね！", 1, 1, 1, 0, 1, 3831, 112401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "しっかりお昼を食べて\n午後からも張り切って\nいきましょうね♪", 1, 1, 2, 0, 2, 3832, 112401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ぐっすり眠れるよう\n早めに用事を済ませて\nお休みしましょうね", 1, 1, 3, 0, 3, 3833, 112401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ちょっと休んだら\nレッスンの続きも\n頑張ります！", 1, 1, 0, 0, 2, 3835, 112401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "えへへ～\nふわふわもこもこで\nかわいいです～♪", 1, 1, 0, 0, 3, 3836, 112401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "リラックスできる\nいい香りですね～♪", 1, 1, 0, 0, 4, 3837, 112401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "わぁ、ポカポカです♪\n温かい飲み物も\nほしくなりますね", 1, 1, 0, 0, 5, 3838, 112401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "レッスンしたら\nお腹が空いちゃって…\nぱくぱくもぐもぐ…", 1, 1, 0, 0, 7, 3840, 112401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "レッスンに役立つ\n運動の情報とか\n載ってるでしょうか…", 1, 1, 0, 0, 8, 3841, 112401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "きらきらーん☆\nよし、次のステージは\nこれで行けるかも！", 1, 1, 0, 0, 1, 3843, 112601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "私の元気でみんなも\n元気になってくれたら\nうれしいな～", 1, 1, 0, 0, 2, 3844, 112601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ちょっとレッスンで\n疲れちゃったかも…\nでも頑張るぞっ！", 1, 1, 0, 0, 3, 3845, 112601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "晩ごはん何にしよう？\nそーだ、しぶりんにも\n聞いてみよっと！", 1, 1, 0, 0, 4, 3846, 112601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "元気だから笑顔になる\nじゃなくて\n笑顔だから\n元気になるんだよね～", 1, 1, 0, 0, 5, 3847, 112601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "声援を聞いてる時ね\nすごく懐かしいって\n思うことがあるんだ", 1, 4, 0, 0, 101, 3848, 112601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "私を応援してくれる\n一番大事な人が\n隣にいた気がする…", 1, 8, 0, 0, 102, 3849, 112601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なになにー？\nミオちゃんに\n何か用かな～？", 1, 1, 0, 0, 1, 3850, 112601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おっ、やっほー！\n元気そうだね～、キミ\n私？\nもちろん元気～！", 1, 1, 0, 0, 2, 3851, 112601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ねぇねぇ、ちょっと\n遊びに行かない？\nたまには息抜きで！", 1, 1, 0, 0, 3, 3852, 112601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ね、この魔物料理さ\nプリンって言い張れば\n食べてもらえそうかな", 1, 1, 0, 0, 4, 3853, 112601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "えへへ\nいつもありがとねっ！\nパワーもらってるよ～", 1, 4, 0, 0, 101, 3854, 112601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どうしたどうした～\nミオちゃんの魅力に\nメロメロなのかい？", 1, 8, 0, 0, 102, 3855, 112601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おっはよ～！\n一日の始まりだし\nテンション上げてこ！", 1, 1, 1, 0, 1, 3856, 112601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お腹空いた～…\nねぇねぇ、一緒に\nお昼食べようよ～", 1, 1, 2, 0, 2, 3857, 112601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふわぁ～…\n眠くなってきちゃった\nそろそろ休もっかなぁ", 1, 1, 3, 0, 3, 3858, 112601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "わぁ、ふっかふか～！\nレッスンの疲れも\n吹っ飛んじゃうね☆", 1, 1, 0, 0, 2, 3860, 112601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "何これ…かわいーっ！\n早くしまむーに\n教えてあげなきゃ！", 1, 1, 0, 0, 3, 3861, 112601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "すぅ～…\nうんっ、これ私の\n好きな香りだな～♪", 1, 1, 0, 0, 4, 3862, 112601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ポッカポカだね～♪\n…あ～ん、ここから\n動きたくないーっ！", 1, 1, 0, 0, 5, 3863, 112601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "わっ、おーいしー！\nこれはテンション\n上がっちゃうね♪", 1, 1, 0, 0, 7, 3865, 112601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふむふむ…\nふむふむふむ…！\n勉強になった、ブイ☆", 1, 1, 0, 0, 8, 3866, 112601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "なんでドキドキが\n止まらないの…？\n騎士くんのせいだー！", 1, 12, 0, 0, 103, 3868, 100101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ドキドキしてるの\nあたしだけ、かな…\n騎士くんはどう…？", 1, 12, 0, 0, 103, 3869, 100101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "騎士クンの前で\n好きって言えるように\n練習しないと…", 1, 12, 0, 0, 103, 3870, 100201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "騎士クン…大好き\n…き、騎士クン！？\nなんでここに！？", 1, 12, 0, 0, 103, 3871, 100201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "困ったな…\n彼が居ないだけで、\nここまで\n寂しいと思うなんて…", 1, 12, 0, 0, 103, 3872, 100301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "えっと…\n今日は訓練じゃなくて\nその…二人でゆっくり\n過ごしたいな…", 1, 12, 0, 0, 103, 3873, 100301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "アイドルの心構え…\nどういう風に考えると\nしっくりくるのかな", 1, 1, 0, 0, 1, 3874, 112501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "何か始めるのって\nドキドキするよね\nだから最近楽しいんだ", 1, 1, 0, 0, 2, 3875, 112501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "冒険者の時って毎日\nバタバタしてたけど…\n目標ができると違うね", 1, 1, 0, 0, 3, 3876, 112501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "少しお腹が空いた…\nチョコレート一粒だけ\n食べようかな", 1, 1, 0, 0, 4, 3877, 112501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ウヅキもミオも\n頑張ってるし\n私も負けられないね", 1, 1, 0, 0, 5, 3878, 112501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "レッスンしてる時さ\n何か、誰かが足りない\nそう思うことがあるの", 1, 4, 0, 0, 101, 3879, 112501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "大事なのは笑顔…\n誰か、大切な人から\n言われた気がするんだ", 1, 8, 0, 0, 102, 3880, 112501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どうしたの、何か用？\n手伝えることがあれば\nすぐに言ってね", 1, 1, 0, 0, 1, 3881, 112501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "姿勢がいい？\n普段から鍛えてるし\n自然とそうなるのかも", 1, 1, 0, 0, 2, 3882, 112501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "さっき散歩してたら\n犬を見かけたんだ\n人懐っこい子だったよ", 1, 1, 0, 0, 3, 3883, 112501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今から冒険？\nなら私も一緒に行くよ\n前衛は任せておいて", 1, 1, 0, 0, 4, 3884, 112501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今からレッスンに\n行くつもりだったんだ\n少し手伝ってくれる？", 1, 4, 0, 0, 101, 3885, 112501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "蒼の剣を受けよ！\nえ、何してるのって？\nただの発声練習だけど", 1, 8, 0, 0, 102, 3886, 112501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよう、早いね\n今日は依頼もないし\nゆっくりしようかな", 1, 1, 1, 0, 1, 3887, 112501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そろそろお昼だね\nうーん、今日は\n何を食べよう…", 1, 1, 2, 0, 2, 3888, 112501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もうこんな時間…\n明日も早いし\nそろそろ寝なくちゃ", 1, 1, 3, 0, 3, 3889, 112501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "レッスンで疲れたし\nちょっとだけ\n休んでおこうかな", 1, 1, 0, 0, 2, 3891, 112501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふふっ、かわいい\nふわふわしたものって\n撫でたくなるんだよね", 1, 1, 0, 0, 3, 3892, 112501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うん、いい香り…\nこういうものがあると\n落ち着くからいいよね", 1, 1, 0, 0, 4, 3893, 112501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "温かい場所って\n居心地がいいから\nのんびりしちゃうよ", 1, 1, 0, 0, 5, 3894, 112501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "レッスン頑張ったら\nすぐお腹が空くし\n食べておかないと…！", 1, 1, 0, 0, 7, 3896, 112501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "アイドルのことを\nもっと勉強して\n成長しなくちゃ", 1, 1, 0, 0, 8, 3897, 112501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ああ、知りたい…\nなんでもかんでも\nとにかく知りたい…", 1, 1, 0, 0, 1, 3899, 111001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ああ、眠い…\nいつでもどこでも\nとにかく眠い…", 1, 1, 0, 0, 2, 3900, 111001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "君、気を付けたまえ\nぼーっと歩いていると\nほわっ！　転んだ…", 1, 1, 0, 0, 3, 3901, 111001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ミツキ師にいただいた\n新作サプリ…丁度よい\n被験体はいないものか", 1, 1, 0, 0, 4, 3902, 111001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "サレンディア救護院に\nユニちゃん８歳として\n潜入済みだが何か？", 1, 1, 0, 0, 5, 3903, 111001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "君がユニちゃんズの\n同志であること、\nぼくは誇りに思うぞ", 1, 4, 0, 0, 101, 3904, 111001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ぼくは青春がしたい\nおい、君\nぼくと青春しないか", 1, 8, 0, 0, 102, 3905, 111001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ああ、そうだな。\nぼくもそう思う。\n……えっ、何が？", 1, 1, 0, 0, 1, 3906, 111001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "待て、話しかけるな。\n今の閃きをメモに…\n…ああっ、忘れた！", 1, 1, 0, 0, 2, 3907, 111001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "歩き方が綺麗？\n何を今更なこと\n言っとるんだ君は", 1, 1, 0, 0, 3, 3908, 111001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ぼくの血液型？\n大らかのОだ。\n何か異論あるかね", 1, 1, 0, 0, 4, 3909, 111001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "君の声は精神に\n高揚をもたらすな…\n波形を分析したいぞ", 1, 4, 0, 0, 101, 3910, 111001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "妙だな…最近\n君に話しかけられると\n心拍に乱れを覚える", 1, 8, 0, 0, 102, 3911, 111001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おっと、朝だな。\nそろそろ寝るか、\n朝更かしは体に悪い", 1, 1, 1, 0, 1, 3912, 111001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふむ、昼餉時か…\n今日のランチは\nサプリ定食にしよう", 1, 1, 2, 0, 2, 3913, 111001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もう夜だな、\nそろそろ起きるか。\n最高の一日にするぞ！", 1, 1, 3, 0, 3, 3914, 111001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ほわぁ～\n副交感神経が…\n優位に…ふふっひ", 1, 1, 0, 0, 2, 3916, 111001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいすぎるな…\nセクシーユニには\nミスマッチか…", 1, 1, 0, 0, 3, 3917, 111001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ほぁあアあァぁー…\nいい香りー…\nあアァあ…イイ…", 1, 1, 0, 0, 4, 3918, 111001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "炎はいいぞ\nそこには人類発展の\nドラマがある", 1, 1, 0, 0, 5, 3919, 111001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "フオオー！\n次から次へと固形食物\n原理気になるー！", 1, 1, 0, 0, 7, 3921, 111001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "良書は時間を忘れる\n…ふむ、今度\n布団を持ってこよう", 1, 1, 0, 0, 8, 3922, 111001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "きらーん☆\nハツネちゃんに\n不可能はないのだ☆", 1, 12, 0, 0, 103, 3924, 101201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "キミと一緒だと\n寝ちゃうのが\nもったいないよ～", 1, 12, 0, 0, 103, 3925, 101201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ちぇるーん☆", 1, 1, 0, 0, 1, 3933, 192101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ちぇる～ん☆\n今日もチエルは\nかわいいですよね～？", 1, 1, 0, 0, 1, 3951, 110901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "どこかに楽しいもの\n落ちてないかにゃー", 1, 1, 0, 0, 2, 3952, 110901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "チエルのオキニは\n甘々カフェラテ！\nシクヨロでぇ～す☆", 1, 1, 0, 0, 3, 3953, 110901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "先輩、ひまですよね？\nチエル遊んであげても\nいいですけど？", 1, 1, 0, 0, 4, 3954, 110901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "はぁー…しゅき…\nカルミナ尊い…\n一生すこれる…", 1, 1, 0, 0, 5, 3955, 110901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "先輩、ひまー\nちょっとは構えよーう\nデュクシ！デュクシ！", 1, 4, 0, 0, 101, 3956, 110901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "先輩なんか\nおごって～！\nてか…おごらなくても\nいいから遊んでよ…", 1, 8, 0, 0, 102, 3957, 110901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "え、なんですか？\n告白だったら\n間に合ってますけど", 1, 1, 0, 0, 1, 3958, 110901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ちょっと静かに！\n今ヤバかわポーズの\nイメトレ中なんです！", 1, 1, 0, 0, 2, 3959, 110901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "足取りが軽やか？\nあー…ダンスとか\n習ってるからですかね", 1, 1, 0, 0, 3, 3960, 110901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "チエルの血液型？\n大型新人のОです\n何か問題でも？", 1, 1, 0, 0, 4, 3961, 110901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "先輩って何気に\nいい声してますよね\n言うことは変だけど", 1, 4, 0, 0, 101, 3962, 110901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "え、なんですか？\nたまにだったら\n告白してもいいですよ", 1, 8, 0, 0, 102, 3963, 110901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはちぇる～ん☆\nハイ、ご一緒に～？", 1, 1, 1, 0, 1, 3964, 110901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日のランチは\nどこで\n映えよっかにゃー", 1, 1, 2, 0, 2, 3965, 110901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "こんばんちぇるるん☆\nハイ、ご一緒に～？", 1, 1, 3, 0, 3, 3966, 110901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふかふかチエルン\n略して、ふかルン♪", 1, 1, 0, 0, 2, 3968, 110901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいいこの子と\nかわいいチエル！\n映ぁ～え～る～！", 1, 1, 0, 0, 3, 3969, 110901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいい香り～！\n持って帰ろっと！\nすぅーっ…", 1, 1, 0, 0, 4, 3970, 110901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "マシュマロは\n焼かない方のが\nかわいくないです？", 1, 1, 0, 0, 5, 3971, 110901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "チエル食べても\n太らない体質\nなんでぇ～！", 1, 1, 0, 0, 7, 3973, 110901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "読書は好きですよ\nシンプルに楽しいし\nギャップ萌えでしょ？", 1, 1, 0, 0, 8, 3974, 110901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "みんなのあたしを\n見る目が違うね\nま、気にしないけどね", 1, 1, 0, 0, 1, 3976, 112701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今日も疲れた～\nやる気ゼロだし\nもう寝ちゃおうかな", 1, 1, 0, 0, 2, 3977, 112701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あたしの代わりに誰か\n働いてくれないかね", 1, 1, 0, 0, 3, 3978, 112701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "労働の後に食べる\nあんパン最高～", 1, 1, 0, 0, 4, 3979, 112701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "仕事の合間に\nちょっと一息いれよ\nあ～ドングリ美味し～", 1, 1, 0, 0, 5, 3980, 112701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "報酬も入ったことだし\nあいつに何か\n買ってあげようかな", 1, 4, 0, 0, 101, 3981, 112701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "次の休みはあいつと\n一日中だらだら\nしたいな～", 1, 8, 0, 0, 102, 3982, 112701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あたしは褒められて\n伸びるタイプなんだ\nだから褒めて褒めて", 1, 1, 0, 0, 1, 3983, 112701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お、差し入れ？\n気が利くねぇ\nあんがと", 1, 1, 0, 0, 2, 3984, 112701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "新しい依頼？\n今日はもう無理～\n閉店で～す", 1, 1, 0, 0, 3, 3985, 112701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "サ、サボってないよ！\nちょっと休憩してた\nだけだから！", 1, 1, 0, 0, 4, 3986, 112701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ねえ、一緒に\nサボろうよ？\n働き過ぎはよくないよ", 1, 4, 0, 0, 101, 3987, 112701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あんたが近くにいると\n仕事の疲れも\n和らぐんだよね～", 1, 8, 0, 0, 102, 3988, 112701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "はぁ…もう起きなきゃ\nつらい労働の\nはじまりだ～", 1, 1, 1, 0, 1, 3989, 112701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "うぅ、お昼に\nあんパン食べ過ぎた…\n苦しい…眠い…", 1, 1, 2, 0, 2, 3990, 112701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "明日は仕事ないし\nガッツリ夜ふかし\nしちゃうよ～", 1, 1, 3, 0, 3, 3991, 112701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "猫みたいに\n丸くなっちゃうよ～\nあたしリスだけどね", 1, 1, 0, 0, 2, 3993, 112701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふかふか～\nあたしの尻尾と\n互角とはやるね～", 1, 1, 0, 0, 3, 3994, 112701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あんパンの香りの\nアロマとか\nあればいいのにな～", 1, 1, 0, 0, 4, 3995, 112701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これは危険な\nあたたかさだね～\n労働意欲が\nなくなるよ～", 1, 1, 0, 0, 5, 3996, 112701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いっぱい働いたから\nいっぱい食べても\nカロリー０だよ", 1, 1, 0, 0, 7, 3998, 112701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あたしだってたまには\n本くらい読むって\n全然似合わないけどね", 1, 1, 0, 0, 8, 3999, 112701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あたしの作った\nオリジナルたい焼き…\nあいつ、\n喜んでくれるかにゃ？", 1, 12, 0, 0, 103, 4001, 104601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あ、お帰りにゃー♪\nえへへ…キミに\nお帰りって言えるのが\nなんかうれしいにゃ♪", 1, 12, 0, 0, 103, 4002, 104601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "フロンティア\nスピリッツを\nお見せするべ！", 1, 1, 0, 0, 1, 4003, 112801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この鎧を着ると\n気が引き締まる\n感じがするべ", 1, 1, 0, 0, 2, 4004, 112801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "搾りたての牛乳飲んで\n今日も一日\nがんばるべ！", 1, 1, 0, 0, 3, 4005, 112801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "みんなに牧場の\n乳製品を\n味わってもらいてぇべ", 1, 1, 0, 0, 4, 4006, 112801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここはいいとこだべ\nうちの動物たちも\n連れてきてやりてぇな", 1, 1, 0, 0, 5, 4007, 112801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "みんなオラより\n若ぇのに働いてて\n立派だべな", 1, 4, 0, 0, 101, 4008, 112801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "牧場もギルドの\nみんなもオラが\nまとめて面倒見るべ！", 1, 8, 0, 0, 102, 4009, 112801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "さあ、今日も\n忙しくなるべよ\n気合入れてくべ！", 1, 1, 0, 0, 1, 4010, 112801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お、あんちゃん\n丁度いいとこに！\n一緒に街に\n行商しに行くべ！", 1, 1, 0, 0, 2, 4011, 112801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "牧場仕事で培った力が\n戦いでも役立つなら\n一石二鳥だべさ！", 1, 1, 0, 0, 3, 4012, 112801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "疲れた時は牧場産の\nミルク菓子で\n糖分補給だべ", 1, 1, 0, 0, 4, 4013, 112801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "これ、牧場の\n新製品だべ\n一番最初に味見\nしてほしいべさ", 1, 4, 0, 0, 101, 4014, 112801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "働きづめはよくねぇベ\n膝枕してやっから\nこっち来るべさ", 1, 8, 0, 0, 102, 4015, 112801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふぁあ…\n酪農家の朝は\n早ぇんだべ…", 1, 1, 1, 0, 1, 4016, 112801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ごはんの時間だべ！\n腹が減っては酪農も\n戦いもできねえべ！", 1, 1, 2, 0, 2, 4017, 112801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そろそろ寝るべ！\n健全なお笑いは\n健全な肉体に宿るべ！", 1, 1, 3, 0, 3, 4018, 112801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "牧草みたいに\nふっかふかだべ～", 1, 1, 0, 0, 2, 4020, 112801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この子、目の感じが\nエリザベスに似てるべ", 1, 1, 0, 0, 3, 4021, 112801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい匂いだべ～\n疲れた体に\nしみるべな～", 1, 1, 0, 0, 4, 4022, 112801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったけぇな～\n牧場にも\n置いてみっかな～", 1, 1, 0, 0, 5, 4023, 112801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うまいべ！\n生産者のこだわりを\n感じるべさ", 1, 1, 0, 0, 7, 4025, 112801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "酪農も戦いも\nまずは知識だべ！", 1, 1, 0, 0, 8, 4026, 112801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ぴょん、ぴょん♪\nこの服のおかげで、\n体が軽いなぁ…♪", 1, 1, 0, 0, 1, 4028, 113001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今の内に部屋の\n掃除をしておこっと…\nみんな喜んで\nくれるといいなぁ", 1, 1, 0, 0, 2, 4029, 113001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "耳と尻尾…この服、\n本当にウサギさん\nみたいだなぁ", 1, 1, 0, 0, 3, 4030, 113001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "なんだか先輩の\n気配がするような…\n気のせいかな？", 1, 1, 0, 0, 4, 4031, 113001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ウサギさんの真似を\nしたら、存在感が薄い\nとか言われないかな？\nア、アユミだぴょん♪", 1, 1, 0, 0, 5, 4032, 113001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "えへへ…\nいつもとは違う先輩が\nたくさん見られて、\nうれしいな♪", 1, 4, 0, 0, 101, 4033, 113001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "本の中の世界で先輩と\n旅をするなんて…\n幸せすぎて、罰が\n当たりそうだよぉ…", 1, 8, 0, 0, 102, 4034, 113001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "じ～…はっ！？\nす、すみません！\n話しかけてもらえる\nなんて思ってなくて！", 1, 1, 0, 0, 1, 4035, 113001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "先輩がすぐそばに…\nし、幸せすぎて\n倒れそうですぅ！", 1, 1, 0, 0, 2, 4036, 113001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "え、えっと…あの…！\nうう…いざ先輩と\n向き合うと\n緊張しちゃうよぉ…", 1, 1, 0, 0, 3, 4037, 113001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "はうっ！？\nしょ、正面から見る\n先輩は、私にとって\n神々しすぎますぅ…！", 1, 1, 0, 0, 4, 4038, 113001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "先輩…その…\nい、一緒にご飯でも\nどうでひゅか！\nうう…またかんだぁ…", 1, 4, 0, 0, 101, 4039, 113001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "せ、先輩…\nハーブティーを\n入れたんですが、\n一緒にどうですか…？", 1, 8, 0, 0, 102, 4040, 113001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "せ、先輩！\nおはようございます！\nえへへ、練習通りに\nバッチリ言えたぁ♪", 1, 1, 1, 0, 1, 4041, 113001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あ、もうお昼ですね\n時計を持ってて\n良かったです", 1, 1, 2, 0, 2, 4042, 113001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もうこんな時間…\nお休みする\n準備をしちゃいますね", 1, 1, 3, 0, 3, 4043, 113001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あ…ちょっとだけ\n先輩の匂いがする…\nえへへ、幸せだなぁ♪", 1, 1, 0, 0, 2, 4045, 113001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "…私もウサギさんの\nぬいぐるみとして、\n先輩のそばに…はっ！\n私ったら何を…！", 1, 1, 0, 0, 3, 4046, 113001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あ、いい香り…\n先輩もこういうのが\n好きなのかな？", 1, 1, 0, 0, 4, 4047, 113001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "先輩が隣に\n居たら、もっと\n暖かいのになぁ…♪", 1, 1, 0, 0, 5, 4048, 113001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "おいしい…でも、\n先輩と一緒に\n食べたら、もっと\nおいしいだろうな…", 1, 1, 0, 0, 7, 4050, 113001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ウサギさんって、穴を\n掘ったりするんだ…\n私も、隠れたいときに\n掘ってみようかな？", 1, 1, 0, 0, 8, 4051, 113001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ネビアさまのように\n普段からパタパタと\n飛んでみるのも…", 1, 1, 0, 0, 1, 4053, 180501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ご立派になられた\n主さまと並べるように\nもっと精進しなくては", 1, 1, 0, 0, 2, 4054, 180501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "月々の食費が\n増えてしまいました\n申し訳ございません…", 1, 1, 0, 0, 3, 4055, 180501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ワンワンのほうの\n主さまは、この姿に\n驚くでしょうか…？", 1, 1, 0, 0, 4, 4056, 180501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "主さまと離れていても\n主さまとの絆は\n胸に感じております…", 1, 1, 0, 0, 5, 4057, 180501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "このギルドハウスは\nみなさまの帰る場所…\nわたくしが守ります", 1, 4, 0, 0, 101, 4058, 180501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "どんな未来が\n待ち受けようとも\n主さまと添い遂げます", 1, 8, 0, 0, 102, 4059, 180501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おでかけですか？\nではわたくしも\nお供いたします", 1, 1, 0, 0, 1, 4060, 180501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もう二度と主さまと\n離れたくはありません\nいつ何時も…", 1, 1, 0, 0, 2, 4061, 180501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なにかお困りごと\nでございますか？\nわたくしに何なりと…", 1, 1, 0, 0, 3, 4062, 180501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どうぞゆっくりと\n心ゆくまでくつろいで\nくださいまし、主さま", 1, 1, 0, 0, 4, 4063, 180501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この羽で主さまを\nどこまでも遠く…\n高くへと導きます", 1, 4, 0, 0, 101, 4064, 180501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "主さま、主さま…♪\nおそばにいられるのが\nうれしくて、つい…", 1, 8, 0, 0, 102, 4065, 180501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようございます\n今日も一日、主さまの\nサポートをいたします", 1, 1, 1, 0, 1, 4066, 180501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日も頑張っている\n主さまに、スタンプを\nもう一つ…ポン！", 1, 1, 2, 0, 2, 4067, 180501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "寝付けないのでしたら\nわたくしが子守唄を\n歌ってさしあげます", 1, 1, 3, 0, 3, 4068, 180501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふかふか…\nまるで主さまのような\n柔らかさでございます", 1, 1, 0, 0, 2, 4070, 180501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あなたも一緒に\n主さまと遊びますか？\n至福の時間ですよ♪", 1, 1, 0, 0, 3, 4071, 180501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "主さまの優しくて\nくすぐったい匂いが\nたまらなく恋しいです", 1, 1, 0, 0, 4, 4072, 180501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "主さまの心と\n繋がったときのような\n温もりを感じます", 1, 1, 0, 0, 5, 4073, 180501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いけませんっ…！\n元の姿に戻ると、また\n食べてしまうのに…！", 1, 1, 0, 0, 7, 4075, 180501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "主さまに読み聞かせる\n絵本は…妖精が主役の\n物語にいたしましょう", 1, 1, 0, 0, 8, 4076, 180501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "…彼のこと弟みたいに\n思ってきたのに…私、\nどうしたらいいの…？", 1, 12, 0, 0, 103, 4078, 104801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "よ、予定がなかったら\nでいいんだけど…\nその、ふ、二人で\n訓練とか…どうかな？", 1, 12, 0, 0, 103, 4079, 104801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "おめかしもしたし\nあとはお兄ちゃんを\n待つだけですね～♪", 1, 1, 0, 0, 1, 4080, 112901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お兄ちゃんの身の回り\nをお片付け～、これぞ\nデキる妹アピール！", 1, 1, 0, 0, 2, 4081, 112901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あ、衣装がほつれてる\nこれくらいなら自分で\n繕えますね…！", 1, 1, 0, 0, 3, 4082, 112901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この格好で歩いてると\n見慣れた景色も\n違うものに見えますね", 1, 1, 0, 0, 4, 4083, 112901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "勉強もちゃんとします\n…でも、一人だと\n集中力がもちません～", 1, 1, 0, 0, 5, 4084, 112901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お兄ちゃんを大好きな\n妹が待ってますよ～\n…まだですかね～", 1, 4, 0, 0, 101, 4085, 112901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お兄ちゃんのために\n掃除洗濯をする私って\nお嫁さんみたい…\n…えへへ～", 1, 8, 0, 0, 102, 4086, 112901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おに～いちゃん！\n縫い物、一緒に\nやりましょうよ！", 1, 1, 0, 0, 1, 4087, 112901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "かわいい妹を\n愛でたくなりました？\n思う存分、どうぞ♪", 1, 1, 0, 0, 2, 4088, 112901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "わっ、ビックリした～\nもう、お兄ちゃんてば\nイタズラっ子ですね～", 1, 1, 0, 0, 3, 4089, 112901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "一緒にお買い物\n行きませんか？\n妹とデートですよっ♪", 1, 1, 0, 0, 4, 4090, 112901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お兄ちゃん…頭を\n撫でてくれませんか？\nえへへ、これが一番♪", 1, 4, 0, 0, 101, 4091, 112901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お兄ちゃん大好き♪\n何度でも言いますよ！\nお兄ちゃん大好きっ♪", 1, 8, 0, 0, 102, 4092, 112901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよ～お兄ちゃん！\n妹モーニングコールの\n時間ですよっ♪", 1, 1, 1, 0, 1, 4093, 112901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そろそろ休憩ですね！\n甘いお菓子で\n元気回復しましょ～！", 1, 1, 2, 0, 2, 4094, 112901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜更かし厳禁！\nでも、もう少しだけ\nお話ししたいなぁ～", 1, 1, 3, 0, 3, 4095, 112901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふっかふかですよ！\nお兄ちゃんと一緒に\n座りたいなぁ…♪", 1, 1, 0, 0, 2, 4097, 112901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "このぬいぐるみの\nかわいさ…う～ん\n負けられません…！", 1, 1, 0, 0, 3, 4098, 112901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "香りにも気をつかって\nパーフェクトな妹に\nなっちゃいますから！", 1, 1, 0, 0, 4, 4099, 112901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はぁ～ぬくぬくです～\nお兄ちゃんと一緒に\n温まりたいなぁ…♪", 1, 1, 0, 0, 5, 4100, 112901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これは止まりませんね\nもぐもぐ…いくらでも\n食べられちゃいます", 1, 1, 0, 0, 7, 4102, 112901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これ、デザインの\n参考になりそう！\n私にも作れるかなぁ", 1, 1, 0, 0, 8, 4103, 112901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "退屈です…\nなにか楽しいことは\nないですか？", 1, 1, 0, 0, 1, 4105, 106601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ギャングの仕事は\n今日はお休みです\nのんびりするですよ", 1, 1, 0, 0, 2, 4106, 106601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ひうっ！　あたしの\n尻尾なんて食べても\nおいしくないですよ？", 1, 1, 0, 0, 3, 4107, 106601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ふう…どうやら\n例の件はまだボスに\nバレてないですね…", 1, 1, 0, 0, 4, 4108, 106601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "下克上…いい言葉です\nって、ボスに聞かれて\nないですよね…？", 1, 1, 0, 0, 5, 4109, 106601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "いつか絶対ビッグに\nなってやるです！\nあたしを信じるです！", 1, 4, 0, 0, 101, 4110, 106601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あたしが覚醒したら\nきっと誰よりも\n強くなるはずです！", 1, 8, 0, 0, 102, 4111, 106601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なんですか？\n貢ぎ物なら\nいつでも歓迎ですよ！", 1, 1, 0, 0, 1, 4112, 106601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あたしだって\nやる時はやるですよ！\nその時がこないだけ！", 1, 1, 0, 0, 2, 4113, 106601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "しりとり勝負ですか？\nいい度胸ですね、\nかかってくるです！", 1, 1, 0, 0, 3, 4114, 106601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お兄さんは\nヒマそうですね…\nうらやましいです", 1, 1, 0, 0, 4, 4115, 106601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "かまってほしいです？\nまったく…いいですよ\nしょうがないですね", 1, 4, 0, 0, 101, 4116, 106601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お兄さんのことは\nこのあたしが\n守ってやるですよ！", 1, 8, 0, 0, 102, 4117, 106601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "デキるギャングの\n朝は早いのです…\nふぁあ…", 1, 1, 1, 0, 1, 4118, 106601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お腹がすいたですね\nなにか食べにいくです\nお兄さんのおごりで！", 1, 1, 2, 0, 2, 4119, 106601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふぁあ…むにゅう…\nまだ起きてるですよぉ", 1, 1, 3, 0, 3, 4120, 106601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はぁ～…\nもうここから一歩も\n動きたくないです", 1, 1, 0, 0, 2, 4122, 106601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "なかなかいいモフりを\nしてやがるです…\n舎弟にしてやるですよ", 1, 1, 0, 0, 3, 4123, 106601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "なんかおしゃれな\n匂いがするですね…", 1, 1, 0, 0, 4, 4124, 106601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふふーん！\nあたしのブレスの方が\n火力は上ですね！", 1, 1, 0, 0, 5, 4125, 106601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あたしに貢ぎ物とは\nよく分かってるですね\n食べてやるですよ", 1, 1, 0, 0, 7, 4127, 106601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふーむ…\nもっと楽しい本は\nないですかね？", 1, 1, 0, 0, 8, 4128, 106601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "海に眠る秘宝…\n心躍る響きだ！\n早く見つけたい…！", 1, 1, 0, 0, 1, 4130, 113201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "うう～、暑いぃ…\nかき氷を食べて\nすっきりしたいぃ…", 1, 1, 0, 0, 2, 4131, 113201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "アターック！\nいや、もっとこう…\nアタァーック！", 1, 1, 0, 0, 3, 4132, 113201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "スイカ割りで\n目を回さない\nコツはなんだろうか…", 1, 1, 0, 0, 4, 4133, 113201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "せっかく海に来たのだ\n泳ぎも上手に\nなりたいものだな", 1, 1, 0, 0, 5, 4134, 113201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ふっふっふ…\n疼く…疼くぞ！\n日焼けの痕が…", 1, 4, 0, 0, 101, 4135, 113201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "シグルドと私は\n来世でも共にある…\nつまり我らはずっと…\nはわわ…！", 1, 8, 0, 0, 102, 4136, 113201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "シグルド、私と共に\n日光浴に行かないか？\n光の力を集めよう！", 1, 1, 0, 0, 1, 4137, 113201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この格好だからできる\n必殺技を考えている\n何か案はないか？", 1, 1, 0, 0, 2, 4138, 113201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "シグルド～！\nスイカを貰ったから\n一緒に食べよう！", 1, 1, 0, 0, 3, 4139, 113201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "こう日差しが強いと\n日焼けしそうだな\n跡になってないか…？", 1, 1, 0, 0, 4, 4140, 113201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "我らの夏の黙示録\nしっかりと記録\nしておかねばな", 1, 4, 0, 0, 101, 4141, 113201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ちゃんと日焼けどめを\n塗ってもらうべき\nだったかな…？\nシ、シグルドに…！", 1, 8, 0, 0, 102, 4142, 113201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "朝だぞシグルド！\nさぁ、日光を浴びて\n光の加護を得るのだ！", 1, 1, 1, 0, 1, 4143, 113201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もう昼食の時間か…\n私の魔力も減ったし\n早く補給しなければ", 1, 1, 2, 0, 2, 4144, 113201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜でも我らの活動は\n終わることはない！\nさぁ準備を…ふわぁ…", 1, 1, 3, 0, 3, 4145, 113201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "海で遊んでいると\n疲れてしまうからな\n少し休まるとしよう", 1, 1, 0, 0, 2, 4147, 113201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "えへへ～、君の名前は\n何にしまちょうかね♪\n…はっ、見たのか！？", 1, 1, 0, 0, 3, 4148, 113201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "心地よい香りで\n我が精神も安らぐ…", 1, 1, 0, 0, 4, 4149, 113201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "別に寒くなどないぞ！\n寒くないが、ちょっと\n暖まりたくなってな！", 1, 1, 0, 0, 5, 4150, 113201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もぐ…食べ過ぎた分は\n魔力に変換されるのだ\n…たぶん", 1, 1, 0, 0, 7, 4152, 113201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "…なな、なんだ！？\nシグルドはこういう\n趣味なのか…！？", 1, 1, 0, 0, 8, 4153, 113201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "むむ、この乗り心地…\nいざゆかん、\nシードラゴンよ！", 1, 1, 0, 0, 10, 4155, 113201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "疾風の冥姫の名に\nふさわしき\n深海の玉座よ…\nふああ…柔らかい～", 1, 1, 0, 0, 11, 4156, 113201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この姿でいると\nわたしもお姫様に\nなれた気がする…♪", 1, 1, 0, 0, 1, 4157, 180201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お腹空いてきちゃった\nまずは買い出しに…\n…変身解かなくちゃ！", 1, 1, 0, 0, 2, 4158, 180201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "胸のところが空いてて\nスースーするの\nまだ慣れないな…", 1, 1, 0, 0, 3, 4159, 180201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "治癒魔法に使ってた\n魔力を攻撃魔法に…\n頭の中でも練習練習", 1, 1, 0, 0, 4, 4160, 180201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "何もできなかった\nわたしから、少しは\n変われたのかな…？", 1, 1, 0, 0, 5, 4161, 180201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "騎士クン…\n今どこにいるの…？\n…会いたいな", 1, 4, 0, 0, 101, 4162, 180201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "離れてても分かるよ…\n温かい力で騎士クンに\n守られてるの", 1, 8, 0, 0, 102, 4163, 180201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "わたしが戦えてるのは\n騎士クンのおかげだよ\nいつもありがとう！", 1, 1, 0, 0, 1, 4164, 180201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お腹空いてそう…？\n…内緒で作ったおやつ\n騎士クンも食べる？", 1, 1, 0, 0, 2, 4165, 180201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ひゃうっ！　え！？\nせ、背中はダメ～！\n言ってから触って～！", 1, 1, 0, 0, 3, 4166, 180201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この格好、どうかな？\nえ？　似合ってる…？\n本当？　…うれしい！", 1, 1, 0, 0, 4, 4167, 180201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "かわいい？　そうだね\nひらひらが…わたし？\nええっ、そ、そんな！", 1, 4, 0, 0, 101, 4168, 180201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "騎士クン、これからも\nずっと…わたしの\nそばにいてください", 1, 8, 0, 0, 102, 4169, 180201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "朝からこんなに\n食べる日が来るなんて\n思ってなかったよ…", 1, 1, 1, 0, 1, 4170, 180201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼を作らなくちゃ\n騎士クン、一緒に\nお料理してみる？", 1, 1, 2, 0, 2, 4171, 180201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "騎士クン、もう眠い？\n眠るまで、わたしが\nずっとそばにいるね", 1, 1, 3, 0, 3, 4172, 180201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "攻撃参加するからこそ\n適度に体を休めるのは\n大切だよね！", 1, 1, 0, 0, 2, 4174, 180201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいい…騎士クンと\nシェフィちゃんにも\nプレゼントしたいな", 1, 1, 0, 0, 3, 4175, 180201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい香り～…\nあれ？　お腹が空いて\nきちゃった…！？", 1, 1, 0, 0, 4, 4176, 180201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "温かい…\n騎士クンがくれる\n力みたい…", 1, 1, 0, 0, 5, 4177, 180201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ぱくっ、\nあむ、はむっ…\nううぅ～…やっぱり\n手が止まらないよぉ～", 1, 1, 0, 0, 7, 4179, 180201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "新しく覚えたいな…\nみんなで食べられる\n大皿向けの料理♪", 1, 1, 0, 0, 8, 4180, 180201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お姉ちゃんの愛\nこのままじゃ\nあふれちゃうよ…♪", 1, 12, 0, 0, 103, 4182, 104901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "弟くん♪\n弟くんっ♪\nおっとうっとくんっ♪", 1, 12, 0, 0, 103, 4183, 104901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "泳ぐのもいいけど\n今日は砂浜で\nのんびりしようかね", 1, 1, 0, 0, 1, 4184, 113101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "さてと\nちょっくらひと泳ぎ\n行ってくるかね", 1, 1, 0, 0, 2, 4185, 113101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あっついねぇ…\nうちわがないと\nしんどいったらないよ", 1, 1, 0, 0, 3, 4186, 113101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "冷たすぎる飲み物で\n体を冷やさないよう\n気をつけないと…", 1, 1, 0, 0, 4, 4187, 113101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "夏はあっという間だし\n悔いがないように\n楽しまないとね", 1, 1, 0, 0, 5, 4188, 113101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "きっとこの夏は\nアタシにとって大事な\n想い出になるね…", 1, 4, 0, 0, 101, 4189, 113101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "…前に比べたら\nアタシたちの距離も\n近づいたみたいだね", 1, 8, 0, 0, 102, 4190, 113101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もし暇なら\n今日も笹舟釣り\n一緒にどうだい？", 1, 1, 0, 0, 1, 4191, 113101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お前さん、釣りに\n興味が湧いたなら今度\n道具を見に行こうよ", 1, 1, 0, 0, 2, 4192, 113101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今から砂浜に行くんだ\nお前さんも一緒に\n散歩するかい？", 1, 1, 0, 0, 3, 4193, 113101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "え、日焼け跡？\nおや…本当だ\nすぐ焼けちまうねえ", 1, 1, 0, 0, 4, 4194, 113101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ひゃっ！？\nちょっと！\n日焼け跡をいきなり\n触らないでおくれ！", 1, 4, 0, 0, 101, 4195, 113101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お前さんは、その…\nアタシと一緒にいて\n楽しかったかい？", 1, 8, 0, 0, 102, 4196, 113101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようさん\n今日も砂浜に行って\nゆっくりしようか", 1, 1, 1, 0, 1, 4197, 113101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼か…\nそうだ、よかったら\n刺身定食、作ろうか？", 1, 1, 2, 0, 2, 4198, 113101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜の空を見てるとさ\nなつかしい気持ちに\nなるんだよね", 1, 1, 3, 0, 3, 4199, 113101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "適度に休憩して\n夏バテしないよう\n気をつけないとね", 1, 1, 0, 0, 2, 4201, 113101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あんたたちも\n海を楽しみたいって\n思っているのかね", 1, 1, 0, 0, 3, 4202, 113101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ん～…香りでも\nバカンスらしさを\n楽しめるもんだね", 1, 1, 0, 0, 4, 4203, 113101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こういう格好だと\n少し冷えちまうんだよ", 1, 1, 0, 0, 5, 4204, 113101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もぐもぐもぐ…\n遊んだ分、お腹が\n空いちまってねぇ", 1, 1, 0, 0, 7, 4206, 113101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "…ん、なんだいこの本\n催眠…うーん読めない\nナナカに聞いてみるか", 1, 1, 0, 0, 8, 4207, 113101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "へえ…\nなかなかいい\n乗り心地じゃないか", 1, 1, 0, 0, 10, 4209, 113101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "そうそうハマグリって\n竿でも釣れるんだよ\n知ってたかい？", 1, 1, 0, 0, 11, 4210, 113101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "海を満喫しなきゃね\nちょっとひと泳ぎ\n行ってこようかな？", 1, 1, 0, 0, 1, 4211, 113301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "う～ん…♪\nいつもと違った\n開放感～\nなんちゃって！", 1, 1, 0, 0, 2, 4212, 113301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今のナナカちゃんは\nたぶん水属性！", 1, 1, 0, 0, 3, 4213, 113301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "水着もバッチリ！\nサイカワサイツヨ\nナナカちゃ～ん☆", 1, 1, 0, 0, 4, 4214, 113301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "んっふっふ\nドキ！　水着だらけの\nギルドハウス！\nとかどうかな？", 1, 1, 0, 0, 5, 4215, 113301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "そんなに見られると\nちょ～っと\n恥ずかしいかも…\nなんてねなんてね！", 1, 4, 0, 0, 101, 4216, 113301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ちょっとくらい\nドキッとしてくれても\nいいのにな～、もう…", 1, 8, 0, 0, 102, 4217, 113301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "水着魔法少女の\n相方がほしいなぁ～…\nね、君やってみない？", 1, 1, 0, 0, 1, 4218, 113301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "いつもの格好と同じ？\nも～、わかってない！\nセクスィー度が違う！", 1, 1, 0, 0, 2, 4219, 113301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どうかなどうかな？\nこのひらひらのとこが\nかわいーかなー\nとか思うんだけど！", 1, 1, 0, 0, 3, 4220, 113301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "せっかくだから\nギャルゲーイベント\nごっことか\nやってみる？", 1, 1, 0, 0, 4, 4221, 113301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ナナカちゃんの\n好感度を上げたい？\nならおでかけだー！", 1, 4, 0, 0, 101, 4222, 113301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おうちで水着って\nなんだかコスプレ\nみたいで楽しいかも♪", 1, 8, 0, 0, 102, 4223, 113301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おっはよーう！\n今日はバカンス日和！\n海へゴーですぞ～♪", 1, 1, 1, 0, 1, 4224, 113301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼ご飯どうしよ～…\nあ、ナナカちゃんと\nどっか食べに行く？", 1, 1, 2, 0, 2, 4225, 113301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜は本を読むのが\n捗りまくりですな～！\n今日はなに読もっかな", 1, 1, 3, 0, 3, 4226, 113301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "次のイベントもあるし\nちょっと休んで体力を\n回復しなきゃね～", 1, 1, 0, 0, 2, 4228, 113301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こういうマスコット\n私にもいたらなぁ\nいっそ作っちゃう…？", 1, 1, 0, 0, 3, 4229, 113301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "水着魔法少女に\n似合う香りってのも\n考えてみよっかな♪", 1, 1, 0, 0, 4, 4230, 113301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "夜になると急に冷えて\n肌寒いんだよね～…", 1, 1, 0, 0, 5, 4231, 113301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あんまり食べると\nお腹が出て…でも…\nうう、止まらない～！", 1, 1, 0, 0, 7, 4233, 113301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この催眠魔法の本\n誰にもばれないように\n保管しとかなきゃね…", 1, 1, 0, 0, 8, 4234, 113301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ゴーゴーゴーゴーゴー\nナナカちゃーん♪", 1, 1, 0, 0, 10, 4236, 113301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ナナカちゃんの\n誕生～\nなんちゃって", 1, 1, 0, 0, 11, 4237, 113301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ファイトファイト☆\n一生懸命\n応援するね！", 1, 1, 0, 0, 1, 4238, 113401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "みんながんばってる！\n私も応援でみんなを\n盛り上げなきゃ！", 1, 1, 0, 0, 2, 4239, 113401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "もし元気のない人が\nいたら…応援で少し\nでも元気にしたいな！", 1, 1, 0, 0, 3, 4240, 113401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "がんばれ～☆\nよし、発声練習OK！\n応援は、声も大事！", 1, 1, 0, 0, 4, 4241, 113401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "せっかく水着なんだし\n泳いだり潜ったりして\n遊びたいな～！", 1, 1, 0, 0, 5, 4242, 113401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "チアリーダーとして\nキミを精一杯\n応援したいな…！", 1, 4, 0, 0, 101, 4243, 113401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "キミへの応援\nちゃんと\n届いてるかな…", 1, 8, 0, 0, 102, 4244, 113401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "えへへ…今ね、\nキミへの応援歌を\n考えてたのだ～♪", 1, 1, 0, 0, 1, 4245, 113401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どうしたの？\n元気がないなら\n私が応援するよ☆", 1, 1, 0, 0, 2, 4246, 113401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "がんばったあとは休憩\nしなきゃね！　私と\n一緒にお昼寝する？", 1, 1, 0, 0, 3, 4247, 113401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "シオリンとおそろいの\n水着で応援…\nしてみたいなぁ～", 1, 1, 0, 0, 4, 4248, 113401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "次はなにして遊ぶ？\nキミがしたいことを\n教えてよ！", 1, 4, 0, 0, 101, 4249, 113401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "キミを応援\nすることができて…\n私、すごく幸せ…", 1, 8, 0, 0, 102, 4250, 113401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "キミって早起き\nなんだね！　私も\n見習わなきゃ！", 1, 1, 1, 0, 1, 4251, 113401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ちょっとお疲れかな？\nそれじゃあ\n応援してあげるね♪", 1, 1, 2, 0, 2, 4252, 113401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "遅い時間まで\nがんばってえらいね！\nでも無理はダメだよ♪", 1, 1, 3, 0, 3, 4253, 113401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ちょっとだけ休憩…\n…あう、座り心地が\n良すぎて…眠気が…", 1, 1, 0, 0, 2, 4255, 113401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ここにいるみんなを\n私の応援でもっと\n元気にするのだ～！", 1, 1, 0, 0, 3, 4256, 113401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "元気の出る香り～♪\nこれもうまく応援に\n活かせないかな～？", 1, 1, 0, 0, 4, 4257, 113401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ぽかぽかあったまって\n元気をチャージ\nするのだ～☆", 1, 1, 0, 0, 5, 4258, 113401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いっぱい食べて、\nみんなを応援する\nパワーをつけなきゃ！", 1, 1, 0, 0, 7, 4260, 113401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "みんなを元気にする\n方法は…どこかに\n書いてないかなぁ…？", 1, 1, 0, 0, 8, 4261, 113401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "わぁ、この子\n怪獣さんみたいで\nかわいいね～♪", 1, 1, 0, 0, 10, 4263, 113401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "水着でこれに\n乗ってると海にいる\n気分になれるね！", 1, 1, 0, 0, 11, 4264, 113401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "すごく忙しいけど\nとても充実した気分\nこれが青春なのかしら", 1, 1, 0, 0, 1, 4265, 113501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "森の静かな雰囲気も\n海の開放的な空気も\nどちらも素敵ね～", 1, 1, 0, 0, 2, 4266, 113501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "汚れたお洗濯物は\nみんなが頑張った証\nなんだか愛おしいわ", 1, 1, 0, 0, 3, 4267, 113501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "みんなを見てると\n何だか私も\n走りたくなっちゃうわ", 1, 1, 0, 0, 4, 4268, 113501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "せっかくの水着だし\n泳ぐ練習でも\nしてみようかしら", 1, 1, 0, 0, 5, 4269, 113501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "みんな～ファイト～！\n応援するのも大事よね\n練習しておかなきゃ♪", 1, 4, 0, 0, 101, 4270, 113501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "先生に教えられること\nまだまだい～っぱいね\n次は何がいいかしら♪", 1, 8, 0, 0, 102, 4271, 113501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日も暑くなりそうね\nこまめな水分補給を\n忘れないでね", 1, 1, 0, 0, 1, 4272, 113501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "手伝ってくれるの？\nありがとう♪\n先生、頼りにしてるわ", 1, 1, 0, 0, 2, 4273, 113501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "休憩はこまめに\nとりましょうね\n無理は厳禁よ", 1, 1, 0, 0, 3, 4274, 113501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "海の遊びって\nどんなのがあるの？\n先生に教えてね", 1, 1, 0, 0, 4, 4275, 113501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "水着は初めてだけど\nどう？　変じゃない？\nふふ、ありがとう", 1, 4, 0, 0, 101, 4276, 113501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたと過ごした\nこの夏は、きっと…\n忘れられない思い出ね", 1, 8, 0, 0, 102, 4277, 113501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよう～\n朝練前にちゃんと\nごはん食べましょうね", 1, 1, 1, 0, 1, 4278, 113501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お弁当作ってきたの\n栄養いっぱい摂って\n午後も頑張ろうね", 1, 1, 2, 0, 2, 4279, 113501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もうお休みしなきゃね\n先生が子守唄を歌って\nあげましょうか？", 1, 1, 3, 0, 3, 4280, 113501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふふ、いい気持ちねぇ\n先生、少しだけ\n休憩しちゃおうかしら", 1, 1, 0, 0, 2, 4282, 113501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あらかわいい♪\nチームにもこういう\nマスコットが欲しいわ", 1, 1, 0, 0, 3, 4283, 113501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あら、海の香り？\n波の音まで聞こえて\nきちゃいそうだわ", 1, 1, 0, 0, 4, 4284, 113501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うふふ\nこれなら濡れた水着を\n乾かすのも簡単ね♪", 1, 1, 0, 0, 5, 4285, 113501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これ、美味しいわね♪\nでも、食べ過ぎは\n禁物よ", 1, 1, 0, 0, 7, 4287, 113501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "先生だって日々勉強よ\n新しいことを知るのは\nとても楽しいわ♪", 1, 1, 0, 0, 8, 4288, 113501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あらあら、楽しいわね\nみんなもきっと\nおおはしゃぎね", 1, 1, 0, 0, 10, 4290, 113501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふふ、これで\n夏らしい気分を\n満喫するわよ♪", 1, 1, 0, 0, 11, 4291, 113501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "サンドイッチも\n作ったし、\nお兄ちゃんのお出迎え\n準備は完璧だね♪", 1, 12, 0, 0, 103, 4292, 102301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "えへへ、お兄ちゃんと\nぷうきちが居てくれる\nだけで、なんだか\nうれしくなっちゃう♪", 1, 12, 0, 0, 103, 4293, 102301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "少し赤くなっているな\n日焼けなんてしたのは\nいつ以来だろう", 1, 1, 0, 0, 1, 4294, 113601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この格好で\n過ごすのは\n少し勇気がいるな…", 1, 1, 0, 0, 2, 4295, 113601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "暑いのは平気だが\n肌が外気に触れるの\nにはまだ慣れないな…", 1, 1, 0, 0, 3, 4296, 113601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この格好でも\n堂々としていれば\n恥ずかしくはない…\nのかな？", 1, 1, 0, 0, 4, 4297, 113601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "おっと…鎧姿の時と\n重心が違うせいか\nちょっと不安定だな", 1, 1, 0, 0, 5, 4298, 113601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この水着…\n私に似合って\nいるのだろうか…？\n少年には聞きづらいな", 1, 4, 0, 0, 101, 4299, 113601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "少年との\n海のバカンス…\nまた行きたい\nものだな", 1, 8, 0, 0, 102, 4300, 113601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あまり見るのは\nよしてくれ…\n恥ずかしいじゃ\nないか…", 1, 1, 0, 0, 1, 4301, 113601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どうかしたのかい？\n私でよければ\n話し相手くらいには\nなろうじゃないか", 1, 1, 0, 0, 2, 4302, 113601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ひゃっ！\nす、すまない\nちょっと過敏に\nなっているようだ", 1, 1, 0, 0, 3, 4303, 113601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "…っくしゅん！\nすまない\n薄着には慣れて\nないものでね", 1, 1, 0, 0, 4, 4304, 113601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "まあ、少年になら\n見られても…\nい、いや！\nなんでもない！", 1, 4, 0, 0, 101, 4305, 113601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私がこうして\nゆっくりできるのは\n少年がそばにいて\nくれるからだよ", 1, 8, 0, 0, 102, 4306, 113601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この時間なら\nビーチにも人は\n少ないだろうね…\n遊びに行くかい？", 1, 1, 1, 0, 1, 4307, 113601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "昼食のお誘いかい？\nいいよ、付き合おう", 1, 1, 2, 0, 2, 4308, 113601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もう夜か…\n一日が経つのは\nはやいものだね", 1, 1, 3, 0, 3, 4309, 113601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これは楽でいいな…\nいつもは鎧姿で\n家具を傷つけないか\n気を使っているからね", 1, 1, 0, 0, 2, 4311, 113601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うん…ふわふわの\n感触が\nよくわかるな…", 1, 1, 0, 0, 3, 4312, 113601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いつもは\n鎧に匂いが\n移ってしまうのが\n悩みなんだよ…", 1, 1, 0, 0, 4, 4313, 113601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うーむ…\n涼しいのに暖かい\nなんだか奇妙な気分だ", 1, 1, 0, 0, 5, 4314, 113601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "兜を気にせず\n食べられるのは\n楽でいいな…", 1, 1, 0, 0, 7, 4316, 113601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふむ…鎧がないと\nページがめくりやすい", 1, 1, 0, 0, 8, 4317, 113601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "身軽でいいな…\n鎧を着ていると\n騎乗訓練の時\n馬に申し訳がなくてね", 1, 1, 0, 0, 10, 4319, 113601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "貝をうまく使えば\n水中でも\n守りを固められそうだ", 1, 1, 0, 0, 11, 4320, 113601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "どこをアジトと\nつなげようかな～…\nこのへんにしよっと♪", 1, 1, 0, 0, 1, 4321, 106801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここってちょっと\n変わった建物だね\n妖精がいるでしょ？", 1, 1, 0, 0, 2, 4322, 106801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "色んな人を観察できて\n眼福眼福…♪", 1, 1, 0, 0, 3, 4323, 106801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "家具の配置変更なら\nアタシにおまかせあれ\n…家の形は変えない？", 1, 1, 0, 0, 4, 4324, 106801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "新メニューどうしよう\n思いついた人がいたら\nアタシに教えてね！", 1, 1, 0, 0, 5, 4325, 106801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "少年、早く帰って\nこないかな…\nよし、迎えに行こう！", 1, 4, 0, 0, 101, 4326, 106801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "結局またあちこち\n移動する日々だし…\n少年に会いたいな～", 1, 8, 0, 0, 102, 4327, 106801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どしたの少年？\nお姉さんに何でも\n相談してみな～？", 1, 1, 0, 0, 1, 4328, 106801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あれをああして…\nおや少年！　ごめんね\nすぐに気づけなくて！", 1, 1, 0, 0, 2, 4329, 106801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "何？　クリスが\nアタシを探してる？\n…いないって言って！", 1, 1, 0, 0, 3, 4330, 106801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "よければリノちゃんと\nシズルちゃんにも\n顔を見せてあげて", 1, 1, 0, 0, 4, 4331, 106801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "少年～！\nもうヘトヘトだよ～！\nお願い癒して～！", 1, 4, 0, 0, 101, 4332, 106801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "…アタシが素直に\n助けてって言えるのは\n少年だけだよ", 1, 8, 0, 0, 102, 4333, 106801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "朝だよ少年！\n爽やかな朝の\nクレープはいかが？", 1, 1, 1, 0, 1, 4334, 106801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お腹空いたな～\nお昼のクレープ\n少年は食べる？", 1, 1, 2, 0, 2, 4335, 106801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜までクレープは\nやめとけ？　ごめん\nそれだけは無理…！", 1, 1, 3, 0, 3, 4336, 106801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "アタシこんなに\nゆっくりしちゃって\nいいんだっけ～？", 1, 1, 0, 0, 2, 4338, 106801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "…これ\nネネカが変身\nしてないよね？", 1, 1, 0, 0, 3, 4339, 106801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい香りだ…\n解析完了\n今度生成しようっと♪", 1, 1, 0, 0, 4, 4340, 106801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "安心するな～…\nアジトに置けないから\n羨ましいよ", 1, 1, 0, 0, 5, 4341, 106801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "食べ物を消化するって\n仕組み、どんなものか\n気になる…", 1, 1, 0, 0, 7, 4343, 106801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "速読できるけど\n本を読む時くらい\n文字にじっくり\n浸かりたいんだよね…", 1, 1, 0, 0, 8, 4344, 106801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "エンジェルとして\nみんなを元気に…！\nアカリ、頑張ります♪", 1, 1, 0, 0, 1, 4346, 113701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お姉ちゃんと一緒に\nお兄ちゃんを…えへへ\nまだかな、お兄ちゃん", 1, 1, 0, 0, 2, 4347, 113701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "エンジェルの音色を\n目指して、今日も\nサックスの練習です♪", 1, 1, 0, 0, 3, 4348, 113701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お兄ちゃんとの特訓も\n頑張らないと…\n厳しくしてくださいね", 1, 1, 0, 0, 4, 4349, 113701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "かわいいエンジェルの\n格好、お兄ちゃんなら\n喜んでくれますよね♪", 1, 1, 0, 0, 5, 4350, 113701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "エンジェルの力で\nお兄ちゃんのハートも\nイチコロですよ♪", 1, 4, 0, 0, 101, 4351, 113701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "アカリが一番\n元気にしたいのは\nお兄ちゃんですよ♪", 1, 8, 0, 0, 102, 4352, 113701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お兄ちゃん？\n触りたいなら…\nこことかどうですか♪", 1, 1, 0, 0, 1, 4353, 113701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "特訓の時間ですか？\nアカリ、楽しみに\nしてたんですよ♪", 1, 1, 0, 0, 2, 4354, 113701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あ、お兄ちゃん！\nそろそろアカリと\n休憩…しませんか♪", 1, 1, 0, 0, 3, 4355, 113701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この羽、触り心地\nいいですか？　あんっ\nくすぐったいですよぉ", 1, 1, 0, 0, 4, 4356, 113701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お兄ちゃんも元気に\nなりたいんですか？\nアカリにお任せです♪", 1, 4, 0, 0, 101, 4357, 113701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "やんっ♪\nお兄ちゃんったら\nダイタンですね♪", 1, 8, 0, 0, 102, 4358, 113701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お兄ちゃん、朝ですよ\nすっごく気持ちのいい\nお天気ですよ…♪", 1, 1, 1, 0, 1, 4359, 113701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼寝しませんか\nお兄ちゃん？\nアカリの膝枕で…♪", 1, 1, 2, 0, 2, 4360, 113701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜ですね、お兄ちゃん\nアカリ、なんだか胸が\nドキドキしちゃいます", 1, 1, 3, 0, 3, 4361, 113701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "フカフカですね…\n隣にお兄ちゃんが\nいてくれたらなぁ…♪", 1, 1, 0, 0, 2, 4363, 113701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "えへへ、アカリも\nこんな風にかわいく\nなりたいな～", 1, 1, 0, 0, 3, 4364, 113701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お兄ちゃんが好きな\n匂いなのかな？\n参考にしますね！", 1, 1, 0, 0, 4, 4365, 113701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "体が火照ってきて\nなんだかアカリ…\nクラクラしちゃう…", 1, 1, 0, 0, 5, 4366, 113701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はむ、んぐ…あっ\nほっぺについちゃった\nベトベトですよぉ～", 1, 1, 0, 0, 7, 4368, 113701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "エンジェルって色んな\n物語に出てきますよね\nもっと調べなくちゃ！", 1, 1, 0, 0, 8, 4369, 113701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あの方の前では\n常に気高く\nありたいですわね", 1, 12, 0, 0, 103, 4371, 103201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どんなに道に迷っても\nあなたさまの元には\n絶対に辿り着く\n自信がありますわ", 1, 12, 0, 0, 103, 4372, 103201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あんたと一緒に\nいるときが一番楽しい\n一時かもしれないわ", 1, 12, 0, 0, 103, 4373, 102801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "いつかあんたと一緒に\nサレンディア救護院を\n切り盛り…なんてね♪", 1, 12, 0, 0, 103, 4374, 102801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今日も\nエンジェルとして、\nみんなの幸せの\nために頑張らないと", 1, 1, 0, 0, 1, 4375, 113801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "せっかく羽が\nついてるのに\n飛べないのが残念だわ", 1, 1, 0, 0, 2, 4376, 113801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この格好、子供たちに\n妙にウケがいいわね\nま、悪くないけど", 1, 1, 0, 0, 3, 4377, 113801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "や、やっぱりみんな\n注目してくるわね…\nエンジェルって珍しい\nからしかたないか", 1, 1, 0, 0, 4, 4378, 113801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "エンジェルになると\n人見知りがなおる！\n…わけないか", 1, 1, 0, 0, 5, 4379, 113801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "エンジェルの活動は\n大変だけど、自分が\n成長してるって\n実感がうれしいわね", 1, 4, 0, 0, 101, 4380, 113801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "もし誰かの願いを\nかなえられるなら\n私はアイツの…\nって何考えてるのよ！", 1, 8, 0, 0, 102, 4381, 113801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "何ボサっとしてるの！\nさあ、街に出て\n人助けするわよ！", 1, 1, 0, 0, 1, 4382, 113801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "エンジェリック\nアロー！\nなんてね…え、アンタ\nいつから見てたのよ！", 1, 1, 0, 0, 2, 4383, 113801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "コラ！\n羽は触らないで！\n壊れたらどうするのよ", 1, 1, 0, 0, 3, 4384, 113801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "エンジェルじゃなく\nても、アンタはいつも\n人助けしてるわよね\n…ちょっと尊敬するわ", 1, 1, 0, 0, 4, 4385, 113801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この服なら他の子より\n私を見てくれるかも…\nって、な、何\n見てるのよ！", 1, 4, 0, 0, 101, 4386, 113801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ね、ねえ、もし私が\n困ってたら、一番に\n駆けつけて…やっぱり\n何でもないわ！", 1, 8, 0, 0, 102, 4387, 113801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよう…あ、あの、\nエンジェルとして\n町に行くんだけど…\n一緒に行かない？", 1, 1, 1, 0, 1, 4388, 113801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふぅ、いいことした\n後のランチは\nおいしいわね", 1, 1, 2, 0, 2, 4389, 113801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もうこんな時間…\n夜更かししてないで\n早く寝なさいよね", 1, 1, 3, 0, 3, 4390, 113801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい座り心地ね～\nまさに天にも昇る\n心地よさだわ～", 1, 1, 0, 0, 2, 4392, 113801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふふ、かわいい…\nあ、羽付けたら\nもっとかわいく\nなるんじゃないかな？", 1, 1, 0, 0, 3, 4393, 113801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はぁ～落ち着くわ～\n回復アイテムみたいで\n癒やされちゃうわ～", 1, 1, 0, 0, 4, 4394, 113801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "わっ、とと…\n危ない、羽が\nこげちゃうところ\nだったわ", 1, 1, 0, 0, 5, 4395, 113801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "人の役に立つのって\nすごく大変だから、\nこれでライフ回復よ！", 1, 1, 0, 0, 7, 4397, 113801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "エンジェルに関する\n本はあるかしら…あ！\nこれ探してた攻略本！", 1, 1, 0, 0, 8, 4398, 113801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ふっふっふ\n吸血鬼らしく\n驚かせてあげましょう", 1, 1, 0, 0, 1, 4400, 113901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "獲物はどこですか～！\nう～ん、もうちょっと\n迫力が欲しいですね", 1, 1, 0, 0, 2, 4401, 113901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "トリックオアトリート\n…返事がないので\nイタズラしておきます", 1, 1, 0, 0, 3, 4402, 113901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ハロウィンですし\nたまには騎士さんでも\n誘ってあげますかね", 1, 1, 0, 0, 4, 4403, 113901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "マントは動きに\nメリハリがついて\nいいですね", 1, 1, 0, 0, 5, 4404, 113901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "色んな仮装が見られて\nやっぱりハロウィンは\n最高ですね", 1, 4, 0, 0, 101, 4405, 113901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "吸血鬼らしい行動って\n噛みついたり…いや\nそれは大胆過ぎです！", 1, 8, 0, 0, 102, 4406, 113901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "呼びましたか？\n手短にお願いしますよ\n忙しいんですから！", 1, 1, 0, 0, 1, 4407, 113901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "え？　衣装に穴が？\nありがとうございます\n…よく見てるんですね", 1, 1, 0, 0, 2, 4408, 113901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふわあ…吸血鬼の\n姿でも、眠い時は\n眠いんですよね…", 1, 1, 0, 0, 3, 4409, 113901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ハロウィンってライブ\nとは違った賑やかさが\nあってワクワクします", 1, 1, 0, 0, 4, 4410, 113901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私を驚かそうとしても\n無駄ですよ！\nすぐ分かるんですから", 1, 4, 0, 0, 101, 4411, 113901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "騎士さん、一緒に\nハロウィンを…いいえ\nなんでもないですっ！", 1, 8, 0, 0, 102, 4412, 113901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "仕事で寝不足だから\n太陽がまぶしいです…\n私、吸血鬼っぽいかも", 1, 1, 1, 0, 1, 4413, 113901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼ご飯は真っ赤な…\nトマト尽くしのお弁当\nにしてみました", 1, 1, 2, 0, 2, 4414, 113901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜はハロウィンが\n盛り上がる時間…\n行きますよ騎士さん！", 1, 1, 3, 0, 3, 4415, 113901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "このフィット感\nどうにか服作りにも\n活かせれば…！", 1, 1, 0, 0, 2, 4417, 113901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "よく出来てますね…！\nぬいぐるみの服を作る\nのも楽しそうですね！", 1, 1, 0, 0, 3, 4418, 113901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい感じですね…\n服に合わせた香りって\nいうのも、いいかも…", 1, 1, 0, 0, 4, 4419, 113901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はぁ、温まりますね…\nおっと、マントに火が\n点いちゃいそうです！", 1, 1, 0, 0, 5, 4420, 113901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あぁ、美味しい～\n食べた分は、働いて\n燃焼しますからね！", 1, 1, 0, 0, 7, 4422, 113901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "伝説の吸血鬼…\n夜を統べる者…\nほうほう…", 1, 1, 0, 0, 8, 4423, 113901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "やっぱりハロウィンと\nいえばお菓子です！\nさぁ配りましょうか！", 1, 1, 0, 0, 12, 4425, 113901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この格好…\nカッコイイのに\n動きやすくて\n着心地も最高ッス！", 1, 1, 0, 0, 1, 4427, 114101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "キメポーズはこう…\nいやこっちのほうが…\nなやましいッス…！", 1, 1, 0, 0, 2, 4428, 114101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "トリックオア\nジャスティス！\nって言ったら何か\nもらえるッスかね？", 1, 1, 0, 0, 3, 4429, 114101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "新しい必殺技の\n特訓もしなくちゃ\nいけないッスね…\n忙しいッス！", 1, 1, 0, 0, 4, 4430, 114101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "自分のこの姿\nトモね～ちゃんにも\nみせてあげるッス！", 1, 1, 0, 0, 5, 4431, 114101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ホワイトタイガー\n参上ッス！\n今のポーズは\nキマったッス！", 1, 4, 0, 0, 101, 4432, 114101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あの人と一緒だと\nほんとにヒーローに\nなれる気がするッス！", 1, 8, 0, 0, 102, 4433, 114101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どうッスか？\nこの色、自分に\n似合ってるッスかね？", 1, 1, 0, 0, 1, 4434, 114101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ヒマだったら\n必殺技の特訓に\n付き合ってもらって\nいいッスか？", 1, 1, 0, 0, 2, 4435, 114101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ちょうどいいところに\n来たッスね！\nこのキメポーズ\nどうッスか？", 1, 1, 0, 0, 3, 4436, 114101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お！　この格好、\n気になるッスか？\nじっくり見ても\nいいッスよ♪", 1, 1, 0, 0, 4, 4437, 114101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あんたも一緒に\n新しい技の練習\nどうッスか？", 1, 4, 0, 0, 101, 4438, 114101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日もあんたと一緒に\nランドソルの平和を\n守るッスよ！", 1, 8, 0, 0, 102, 4439, 114101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "朝ッス！\n一緒に早朝特訓\nやるッスよ！", 1, 1, 1, 0, 1, 4440, 114101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼休憩ッス！\nヒーローにも\n休息は必要ッス！", 1, 1, 2, 0, 2, 4441, 114101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "闇夜に映える\n白い衣装で\n平和を守るッスよ！", 1, 1, 3, 0, 3, 4442, 114101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "トラも狩りの時以外は\nごろごろしてるって\n本で読んだッスよー", 1, 1, 0, 0, 2, 4444, 114101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もこもこッスね♪\nこのかわいらしさを\n守ってあげるのも\nヒーローの役目ッス！", 1, 1, 0, 0, 3, 4445, 114101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい匂いッスね…\nリラックスして\n瞑想するッス！", 1, 1, 0, 0, 4, 4446, 114101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "燃える炎は\nヒーローの心意気ッス\nいつまでも\n見てられるッスよ", 1, 1, 0, 0, 5, 4447, 114101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もぐもぐ…\nいっぱい食べて\n強いヒーローに\nなるッスよ！", 1, 1, 0, 0, 7, 4449, 114101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ちゃんと勉強の本も\n読んでるッスよ…！\nヒーローには賢さも\n必要らしいッス！", 1, 1, 0, 0, 8, 4450, 114101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "わわっ、お菓子が\nいっぱいッス！\nみんなで\n食べるッスよ！", 1, 1, 0, 0, 12, 4452, 114101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ワタシがチューシンと\nなった今、天下統一は\n目の前デス！", 1, 12, 0, 0, 103, 4454, 103001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もうショーグンってば\nそれはセイシツに\nすることデ～ス…", 1, 12, 0, 0, 103, 4455, 103001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "女幽霊…うらめしや…\nうーん、これは\nちょっと違う気が\nするな…", 1, 1, 0, 0, 1, 4456, 114001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "何だか寒気がする…\nま、まさか\nこの部屋にも幽霊が？", 1, 1, 0, 0, 2, 4457, 114001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ハロウィンは楽しいね\n童心に帰ったような\n気分だよ", 1, 1, 0, 0, 3, 4458, 114001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今度のハロウィンは\nユイとヒヨリを\n誘ってみようかな", 1, 1, 0, 0, 4, 4459, 114001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ハロウィンのお菓子は\nどれもかわいいね\n…一つくらいなら\nいいかな？", 1, 1, 0, 0, 5, 4460, 114001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "次は彼と\nどんなハロウィンを\n過ごそうかな…", 1, 4, 0, 0, 101, 4461, 114001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "こんな賑やかさも\nいいものだね\n彼といられるのなら\nなおさら…", 1, 8, 0, 0, 102, 4462, 114001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "…いたずらしようと\nしてない、キミ？\nふふ、お見通しだよ", 1, 1, 0, 0, 1, 4463, 114001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "え、私にもお菓子を？\nありがとう…ふふ\nうれしいものだね\nハッピーハロウィン！", 1, 1, 0, 0, 2, 4464, 114001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どうしたの？\n出かけるなら\n私も同行しようか", 1, 1, 0, 0, 3, 4465, 114001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この格好、舞踏会にも\n出られそうだね\n一曲、踊ってみる？", 1, 1, 0, 0, 4, 4466, 114001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "やるからには本気で\nやらせてもらおう\nトリックオア\nトリート！", 1, 4, 0, 0, 101, 4467, 114001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "他の子に目移りする\nのも分かるが、その…\nせっかくの特別な衣装\n見てほしいな", 1, 8, 0, 0, 102, 4468, 114001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "寝起きの体を\n慣らしておこうか\nほら、剣を取って", 1, 1, 1, 0, 1, 4469, 114001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お菓子もいいけど\nちゃんと昼食も\nとるんだよ", 1, 1, 2, 0, 2, 4470, 114001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "さあ、行こうか！\nハロウィンは\nこれからが本番だよ", 1, 1, 3, 0, 3, 4471, 114001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ハロウィンはまだ\nこれからだ\n一休みしようか", 1, 1, 0, 0, 2, 4473, 114001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "キミたちも\n仮装に興味は\nあるかな？　…ふふ", 1, 1, 0, 0, 3, 4474, 114001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい香りだけど\n幽霊が集まったり\nしないだろうか…？", 1, 1, 0, 0, 4, 4475, 114001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "暖かい…一息ついたら\nハロウィンの\nパレードを\n見に行こうかな", 1, 1, 0, 0, 5, 4476, 114001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ハロウィンを本気で\n楽しむなら\n食事もしっかり\nとっておかないとね", 1, 1, 0, 0, 7, 4478, 114001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ハロウィンの\nホラー小説か…\nけ、結構\n怖いものだね…？", 1, 1, 0, 0, 8, 4479, 114001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お菓子がいっぱいだ…\nこういうのって\n見ているだけで心が\n躍ってしまうね", 1, 1, 0, 0, 12, 4481, 114001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "こんなにかわいい\n武器で戦えるとは…\nすごいものだな\n魔法少女は", 1, 1, 0, 0, 1, 4483, 114201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "決めポーズの訓練も\nしっかりやっておくか\n…誰も見ていないな？", 1, 1, 0, 0, 2, 4484, 114201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "こういう格好は\nユキあたりがすごく\n似合いそうだな", 1, 1, 0, 0, 3, 4485, 114201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "子どもたちにサインを\n求められてしまった…\nこれもまた私の使命！", 1, 1, 0, 0, 4, 4486, 114201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この格好は目立つな…\nいや、提督とは人々の\n視線を集めるものだ！", 1, 1, 0, 0, 5, 4487, 114201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あいつと作戦会議を\nしておくか。あれで\nなかなか頼れるからな", 1, 4, 0, 0, 101, 4488, 114201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あいつには私の勇姿を\nちゃんと見ていて\nほしいものだな", 1, 8, 0, 0, 102, 4489, 114201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "一仕事終えたことだし\n一緒に甘いものでも\n食べに行くか？", 1, 1, 0, 0, 1, 4490, 114201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "怪しげな気配がするな\n貴公も警戒を\n怠るなよ！", 1, 1, 0, 0, 2, 4491, 114201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "シオリに魔法少女の\n本を借りたんだ\n一緒に読まないか？", 1, 1, 0, 0, 3, 4492, 114201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "巡回の時間まで\nお茶にでもするか\n貴公も付き合うがいい", 1, 1, 0, 0, 4, 4493, 114201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "貴公がサポートして\nくれるのなら心強いな\n頼りにしているぞ", 1, 4, 0, 0, 101, 4494, 114201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "頭を撫でるな～！\n…まったく、貴公だけ\nだぞ、こんな風に私を\n撫でてくるのは…", 1, 8, 0, 0, 102, 4495, 114201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "起きろ貴公！\n魔法少女の朝は\n早いぞ！", 1, 1, 1, 0, 1, 4496, 114201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ほら、食後の\nデザートだ\nて、適度な糖分は\n体に必要なんだ！", 1, 1, 2, 0, 2, 4497, 114201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふわぁ…魔法少女も…\n眠気、には…い、いや\n寝てないぞ！？", 1, 1, 3, 0, 3, 4498, 114201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "任務や訓練で\n疲れた体に\nこれは効くなぁ…", 1, 1, 0, 0, 2, 4500, 114201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うむ、かわいいな\nモーラのやつを隣に\n並べようか", 1, 1, 0, 0, 3, 4501, 114201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ああ、身も心も\n安らぐ…\nまるで魔法だな…", 1, 1, 0, 0, 4, 4502, 114201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この温かさ…\n愛と希望に\n通じるものがある…", 1, 1, 0, 0, 5, 4503, 114201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "食事の大切さは\n魔法少女も軍人も\n変わりない\nしっかり食べるんだ！", 1, 1, 0, 0, 7, 4505, 114201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "魔法少女の本を\n読んでるのだが\nわ、私もこんななのか", 1, 1, 0, 0, 8, 4506, 114201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "オラとあんちゃんは\nボケもツッコミも\nあうんの呼吸だべ♪", 1, 12, 0, 0, 103, 4508, 103301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あんちゃんと二人で\nネタ合わせ…初めての\n共同作業だべ♪", 1, 12, 0, 0, 103, 4509, 103301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ぴっぷる！", 1, 1, 0, 0, 1, 4510, 192501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ぴっぷるぴっぷる♪", 1, 1, 0, 0, 2, 4511, 192501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ぴっ！", 1, 1, 0, 0, 3, 4512, 192501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ぷっぷー", 1, 1, 0, 0, 4, 4513, 192501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ぴぃ？", 1, 1, 0, 0, 5, 4514, 192501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ぴっぷるんるん♪", 1, 1, 0, 0, 1, 4515, 192501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ぷぅ！", 1, 1, 0, 0, 2, 4516, 192501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ぴぴっ♪", 1, 1, 0, 0, 3, 4517, 192501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ぷぅー…", 1, 1, 0, 0, 4, 4518, 192501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ちゅ！", 1, 1, 1, 0, 1, 4519, 192501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ぷはー", 1, 1, 2, 0, 2, 4520, 192501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ぴぇー", 1, 1, 3, 0, 3, 4521, 192501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "魔法少女になったら\n髪型も変わるんだね\n色々試してみようかな", 1, 1, 0, 0, 1, 4531, 114301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "…煌く剣技で、\n悪を断つ！\nうん、今日も\n完璧なポーズだね♪", 1, 1, 0, 0, 2, 4532, 114301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ミクマ流剣術で、\n魔法少女の技に使え\nそうなものは…よし、\n早速試してみよっと♪", 1, 1, 0, 0, 3, 4533, 114301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "魔法少女になったから\nには、サインの練習も\nしないと…\nよし、頑張るぞ！", 1, 1, 0, 0, 4, 4534, 114301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "魔法少女の姿はすごく\nかわいいけど…この\n姿はジュンさんたちに\n見せられないなぁ…", 1, 1, 0, 0, 5, 4535, 114301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あの人は魔法少女\nのこと好きなのかな？\n…好きだったら\nうれしいな♪", 1, 4, 0, 0, 101, 4536, 114301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "…やっぱり、\nあの人がいないと\n特訓に身が入らない…\n早く帰ってこないかな", 1, 8, 0, 0, 102, 4537, 114301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もし時間があるなら、\n私と街の見回りに\n行かない？", 1, 1, 0, 0, 1, 4538, 114301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あ、いいところに！\n新しい魔法剣術を\n編み出したから、\n見てくれないかな？", 1, 1, 0, 0, 2, 4539, 114301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "魔法少女の姿が気に\nなるのは分かるけど…\nず、ずっと見られる\nのは照れるからやめて", 1, 1, 0, 0, 3, 4540, 114301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "変身ポーズ中の動き、\n少し変えた方が\nいいかな…\nあなたはどう思う？", 1, 1, 0, 0, 4, 4541, 114301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "よければ、\n魔法少女の特訓に\n付き合ってくれない？\nその…二人きりでさ…", 1, 4, 0, 0, 101, 4542, 114301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私…あなただけの\n魔法少女になっても…\nふふっ、な～んてね♪", 1, 8, 0, 0, 102, 4543, 114301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日もいい朝だね\nさあ、一緒に\n街の見回りに行こうか", 1, 1, 1, 0, 1, 4544, 114301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もう昼か…なにか\n食べたいものある？\n簡単なのでいいなら、\n私が作ってあげるよ", 1, 1, 2, 0, 2, 4545, 114301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そろそろ休もうか\n…なんなら、添い寝\nしてあげようか？\nふふっ、冗談だよ♪", 1, 1, 3, 0, 3, 4546, 114301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これは…\n心身ともに安らぐね\nなんだか\n眠くなっちゃいそう", 1, 1, 0, 0, 2, 4548, 114301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "か、かわいい…これ、\n魔法で動かせたり\nしないかな？", 1, 1, 0, 0, 3, 4549, 114301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい香りだね\nなんだか頭が冴えて\nきたし、新しい技でも\n考えようかな", 1, 1, 0, 0, 4, 4550, 114301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったかいな…\nこの温もりは、\n魔法少女の魔法でも\n再現不可能だね…", 1, 1, 0, 0, 5, 4551, 114301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こ、これは…\nおいしすぎて手が\n止まらないかも", 1, 1, 0, 0, 7, 4553, 114301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この本の\nキャラの動き、\n変身ポーズの\n参考にできるかも！", 1, 1, 0, 0, 8, 4554, 114301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "メリークリスマス！\nさあ、素敵な\nパーティーを始めると\nいたしましょう！", 1, 1, 0, 0, 1, 4556, 114401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ふふっ、\nいくつになっても、\nやはりクリスマスは、\nワクワクしますわね♪", 1, 1, 0, 0, 2, 4557, 114401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "自分で料理をしてみて\nシェフたちのすごさが\n改めて分かりましたわ", 1, 1, 0, 0, 3, 4558, 114401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "何事も自分で\n体験してみると\n新しい発見が\nあるものですわね♪", 1, 1, 0, 0, 4, 4559, 114401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "やはり、クリスマスは\n街が活発になり\nますわね…これは、\n絶好の商機ですわ♪", 1, 1, 0, 0, 5, 4560, 114401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "他のクリスマス料理も\n作れるようになったら\nあのお方は食べて\nくださるかしら…？", 1, 4, 0, 0, 101, 4561, 114401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あのお方に贈る\nプレゼント…\nこれはかなりの\n難題ですわね…", 1, 8, 0, 0, 102, 4562, 114401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "メリークリスマス！\nよろしければ、一緒に\nクリスマスの準備を\nいたしませんか？", 1, 1, 0, 0, 1, 4563, 114401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "まあ、クリスマスの\n飾りつけを手伝って\nいただけるのですか？\nありがとうございます", 1, 1, 0, 0, 2, 4564, 114401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "クリスマスに合わせて\nあつらえた自慢の衣装\nなのですが…その、\nいかがでしょうか？", 1, 1, 0, 0, 3, 4565, 114401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "幼い頃はサンタさんが\nいらっしゃるのを\n待ちわびたものです\n今は…内緒ですわ♪", 1, 1, 0, 0, 4, 4566, 114401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "も、もしお時間が\nあるようでしたら、\nクリスマスの街を\n一緒に歩きませんか？", 1, 4, 0, 0, 101, 4567, 114401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "来年のクリスマスも\nあのお方と一緒に\n迎えられたら…", 1, 8, 0, 0, 102, 4568, 114401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "時は金なり…\nせっかくの朝ですもの\n時間を有効活用\nいたしましょう！", 1, 1, 1, 0, 1, 4569, 114401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼になりましたわね\nさあ、\nパーティーの準備を\n進めますわよ！", 1, 1, 2, 0, 2, 4570, 114401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "静かな夜ですわね…\nもうちょっとだけ、\nあなたさまとお話を\nしていたい気分です", 1, 1, 3, 0, 3, 4571, 114401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "適度な休息は\n成功の秘訣ですわ！", 1, 1, 0, 0, 2, 4573, 114401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "このかわいらしさ…\nいつまでも見て\nいられそうですわ♪", 1, 1, 0, 0, 3, 4574, 114401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "人をもてなすには\nこうしたところにも\n気を配らないと\nいけませんわね", 1, 1, 0, 0, 4, 4575, 114401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "暖かい…\nこういう憩いの\nひと時こそ、大事に\nしたいものですわね", 1, 1, 0, 0, 5, 4576, 114401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この奥深い味わい…\n何か隠し味など\n使われていまして？", 1, 1, 0, 0, 7, 4578, 114401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "クリスマス料理の\nレシピも、\nこれだけあると\n迷ってしまいますわね", 1, 1, 0, 0, 8, 4579, 114401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この絵本、とっても\n面白いつくりですわね\nうちの商品に\nしたいぐらいですわ", 1, 1, 0, 0, 14, 4581, 114401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これだけ大きな\n着ぐるみ…\n職人の技術が\n光りますわね", 1, 1, 0, 0, 15, 4582, 114401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "クリスマスといえば\nパーティーよね！\nいっぱい飲むわよ～♪", 1, 1, 0, 0, 1, 4583, 114601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "私を麦しゅわだけの\n女と思ったら\n大間違いなんだから♪", 1, 1, 0, 0, 2, 4584, 114601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "クリスマスらしい\n飲み物も結構いいわね\n私も今度\n作ってみようかしら", 1, 1, 0, 0, 3, 4585, 114601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "暖かい部屋で飲む\n麦しゅわも\nまた格別なのよね…", 1, 1, 0, 0, 4, 4586, 114601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今日はオフの日だし\n朝から飲んでも\n怒られないわよね？", 1, 1, 0, 0, 5, 4587, 114601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "クリスマスの\nデートスポットで\n彼と一緒に…ってなに\n考えてるのユカリ！？", 1, 4, 0, 0, 101, 4588, 114601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "背伸びしなくても\nありのままの自分を\n受け入れてくれるって\nすごく…幸せ…", 1, 8, 0, 0, 102, 4589, 114601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "大丈夫よ～\n飲んでない\n飲んでないひゃら～", 1, 1, 0, 0, 1, 4590, 114601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "クリスマスは\n特別だもの\n特別な一杯を\n飲まないとね♪", 1, 1, 0, 0, 2, 4591, 114601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "メリークリスマース！\nほらほらぁ\n乾杯しましょ♪", 1, 1, 0, 0, 3, 4592, 114601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お姉さんの\n頼れるところ\n見せてあげるわね", 1, 1, 0, 0, 4, 4593, 114601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "何かあったら\n頼ってくれても\nいいんだからね？", 1, 4, 0, 0, 101, 4594, 114601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふふ…これからも\nキミのこと…\n頼りにしちゃうからね", 1, 8, 0, 0, 102, 4595, 114601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "さわやかな朝ね\n…二日酔いがないと\n朝ってこんなに\n気持ちいいのね", 1, 1, 1, 0, 1, 4596, 114601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "昼間から飲むのって\nまた格別よね…って\nガマン、ガマンよ…", 1, 1, 2, 0, 2, 4597, 114601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もうこんな時間かあ…\nあとは寝るだけだし\nちょっとくらいは…", 1, 1, 3, 0, 3, 4598, 114601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ゴロゴロするのって\n最高ね～\nあとは麦しゅわが\nあればなぁ", 1, 1, 0, 0, 2, 4600, 114601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "私だって…\nかわいいものは\n好きでいいわよね？", 1, 1, 0, 0, 3, 4601, 114601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "んっ～…いい香りね\n日頃の疲れが\n取れる気がするわ", 1, 1, 0, 0, 4, 4602, 114601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ポカポカする～\n…なんだかのどが\nかわいてきたわね", 1, 1, 0, 0, 5, 4603, 114601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もぐもぐ…労働の\n後のご飯はやっぱり\nおいしいわぁ…", 1, 1, 0, 0, 7, 4605, 114601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "クリスマスデート\n完全攻略マニュアル？\n…読んでおかなくちゃ", 1, 1, 0, 0, 8, 4606, 114601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あら、絵が飛び出して\n見えるけど…\n酔っ払ってるせいじゃ\nないわよね…？", 1, 1, 0, 0, 14, 4608, 114601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい手触りね～\nウチにもほしいけど\n麦しゅわをこぼしたら\n大変そうだわ…", 1, 1, 0, 0, 15, 4609, 114601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ヒデサイの視線\nずっと独り占め\nしてたいな～", 1, 12, 0, 0, 103, 4610, 101601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ヒデサイといるだけで\nうち、おに楽しいし！\nヒデサイもっしょ～？", 1, 12, 0, 0, 103, 4611, 101601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "メリークリスマス！\nふふ、口にするだけで\n何だか楽しくなるわね", 1, 1, 0, 0, 1, 4612, 114501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "いつもは子どもたちを\n見守る方だけど\n今ははしゃいでも\nいいわよね？", 1, 1, 0, 0, 2, 4613, 114501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ツリーや料理\nそしてプレゼント！\nいくつになっても\nやっぱり心躍るわね♪", 1, 1, 0, 0, 3, 4614, 114501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "飾り付けも料理も\nいいセンスしてるわね\n救護院のパーティーの\n参考にさせてもらうわ", 1, 1, 0, 0, 4, 4615, 114501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "焼きそばパンを\nクリスマスの定番に\nするにはどうすれば…", 1, 1, 0, 0, 5, 4616, 114501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "これ、あいつの\n好きそうな料理だわ\n取っといて食べさせて\nあげようかしら♪", 1, 4, 0, 0, 101, 4617, 114501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あいつのプレゼント\nちょっとだけ奮発\nしたのよね…\n喜んでくれるかしら", 1, 8, 0, 0, 102, 4618, 114501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "このドレス\n似合ってるかしら？\n今だけは見とれても\n許してあげるわ♪", 1, 1, 0, 0, 1, 4619, 114501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "見て、このケーキ！\nおいしそうでしょ\nたまにはこんな贅沢も\nいいわよね♪", 1, 1, 0, 0, 2, 4620, 114501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "料理も飲み物も\nおいしいわね！\nいっぱい食べて\n元を取らないと♪", 1, 1, 0, 0, 3, 4621, 114501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "子どもたちと一緒に\n雪遊び、楽しそうね\nあんたもどう？", 1, 1, 0, 0, 4, 4622, 114501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "せっかくの\nクリスマスなんだもの\nエスコート\nよろしくね♪", 1, 4, 0, 0, 101, 4623, 114501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "寒い？　そう…\nなら、もっとこっち\n来ていいわよ…？", 1, 8, 0, 0, 102, 4624, 114501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ほら、早く起きて？\nパーティー用の\n買い出しに\n付き合ってちょうだい", 1, 1, 1, 0, 1, 4625, 114501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "パーティーの料理の\n準備をしながらお昼も\n作っておいたわ\n我ながらいい手際ね", 1, 1, 2, 0, 2, 4626, 114501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "にぎやかなパーティー\nもいいけど、聖夜の\n余韻を感じるのも\n結構素敵ね", 1, 1, 3, 0, 3, 4627, 114501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい座り心地だわ～\n腕のいい職人の仕事ね\nうちでも扱いたいわ", 1, 1, 0, 0, 2, 4629, 114501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいい！\n子どもたちに\nプレゼントしたら\n喜んでくれるかも", 1, 1, 0, 0, 3, 4630, 114501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "華やかな香りね\nクリスマスに\nピッタリだわ", 1, 1, 0, 0, 4, 4631, 114501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "寒い日にはやっぱり\nこれよね…\nはぁ、極楽極楽♪", 1, 1, 0, 0, 5, 4632, 114501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "んー、おいしいわ！\n焼きそばパン味の\nものも、作れない\nかしら？", 1, 1, 0, 0, 7, 4634, 114501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "たまにはのんびり\n読書でも…なんだか\nラブロマンスばかりね\nクリスマスだから？", 1, 1, 0, 0, 8, 4635, 114501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これ、面白いわね！\n子どもたちの\nプレゼントに\nピッタリだわ", 1, 1, 0, 0, 14, 4637, 114501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こんないい毛並み\n初めて見るわ…\n一体どこで手に\n入るのかしら？", 1, 1, 0, 0, 15, 4638, 114501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "アタシもみんなと\n一緒に正月を\n楽しんでいいのか？", 1, 1, 0, 0, 1, 4639, 114701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今年こそ、もう少し\n身長が伸びます\nように…", 1, 1, 0, 0, 2, 4640, 114701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "げっ！　お年玉が\n全然残ってないぞ！？\nそんな、嘘だろ…？", 1, 1, 0, 0, 3, 4641, 114701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "大勢で過ごす\n正月か…初めてだけど\n悪くないもんだな", 1, 1, 0, 0, 4, 4642, 114701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "んー…ここじゃ\n天楼覇断剣の\n出番はなさそうだな", 1, 1, 0, 0, 5, 4643, 114701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "アイツらと作る\n戦い以外の思い出も\nもっと増やしたいな", 1, 4, 0, 0, 101, 4644, 114701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "こんなに賑やかな\n正月は初めてだ\n…温かくて、いいな", 1, 8, 0, 0, 102, 4645, 114701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "記憶がなくったって\n楽しい思い出は\n新しく作れるよな？", 1, 1, 0, 0, 1, 4646, 114701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "早くでかけるぞ！\nアタシはもっと\n正月を楽しみたい！", 1, 1, 0, 0, 2, 4647, 114701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なあ、オマエはもう\nノウェムって名前\n覚えてくれたよな？", 1, 1, 0, 0, 3, 4648, 114701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この服、アタシに\n似合ってるのか？\n…そっか。\nありがとな！", 1, 1, 0, 0, 4, 4649, 114701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "オマエらと見た朝日は\nきっと忘れないぞ！\n思い出ができたな！", 1, 4, 0, 0, 101, 4650, 114701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "いつかオクトーも\n一緒に、みんなで\n正月を過ごせたらな…", 1, 8, 0, 0, 102, 4651, 114701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "朝から元気だな！\nアタシもいっちょ\n見習ってみるか！", 1, 1, 1, 0, 1, 4652, 114701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "昼飯は何にする？\n正月はうまいものが\n多くて迷うよな！", 1, 1, 2, 0, 2, 4653, 114701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "いい夜だな！\nアタシと一緒に\n夜更かしを楽しむか？", 1, 1, 3, 0, 3, 4654, 114701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "正月らしく、たまには\nゆっくりするのも\nいいかもな…", 1, 1, 0, 0, 2, 4656, 114701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "なんだ？\nオマエも初詣に\n行きたいのか？", 1, 1, 0, 0, 3, 4657, 114701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "すーはー…\n神殿のニオイがするな\nいい事ありそうだ！", 1, 1, 0, 0, 4, 4658, 114701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "コイツがあれば\nうまい魚が\n焼けそうだな…", 1, 1, 0, 0, 5, 4659, 114701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ううー\n食べ過ぎて帯が\nきつくなってきたぞ…", 1, 1, 0, 0, 7, 4661, 114701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "おみくじって\n書いてあることが\nむずかしいんだよな…", 1, 1, 0, 0, 8, 4662, 114701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "困ってる人は\nいないみたいだし…\nちょっとここで、\n特訓しようかな♪", 1, 1, 0, 0, 1, 4664, 180101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "空が飛べるんだから\nもっとたくさんの人を\n助けにいけちゃう！\n…ここじゃ飛べないや", 1, 1, 0, 0, 2, 4665, 180101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "炎の翼\nかっこいいなー！\nこれ、もっと大きく\nできないのかな？", 1, 1, 0, 0, 3, 4666, 180101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "やっぱり変身すると\nお腹すくんだね…\n…今ならペコさんの\n食欲の理由が分かるよ", 1, 1, 0, 0, 4, 4667, 180101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "色なんかは違うけど…\nマントがあるから\n騎士くんとお揃いに\nなったみたい♪", 1, 1, 0, 0, 5, 4668, 180101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "…騎士くんがいないと\nなんか調子でない…\n早く会いたいよぉ…", 1, 4, 0, 0, 101, 4669, 180101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この姿になると、\n騎士くんを近くに\n感じる…なんだろ\nドキドキしちゃう…", 1, 8, 0, 0, 102, 4670, 180101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "騎士くん騎士くん！\n今大丈夫？\nトレーニングメニュー\n考えるの手伝って！", 1, 1, 0, 0, 1, 4671, 180101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "…うにゃっ！？\nこ、この姿になっても\n尻尾は触っちゃ\nダメー！", 1, 1, 0, 0, 2, 4672, 180101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もしかして、この服が\n気になってるの？\nだったら、もっと\n近くで見ていいよ♪", 1, 1, 0, 0, 3, 4673, 180101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あ、騎士くん！\n困ってる人がいないか\n見回りに行くんだけど\nついてきて欲しいな！", 1, 1, 0, 0, 4, 4674, 180101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "耳に触りたいの？\nだったら頭もなでて！\nそしたら、好きなだけ\n触っていいよ♪", 1, 4, 0, 0, 101, 4675, 180101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "…あたし、強くなって\n騎士くんを守るよ！\nだから…これからも\n一緒にいていい？", 1, 8, 0, 0, 102, 4676, 180101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おっはよー！\nさあさあ騎士くん！\n今日も一緒に\nはりきっていこー♪", 1, 1, 1, 0, 1, 4677, 180101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼、お昼～♪\n今日のご飯は何かな～\nあ、騎士くん！\n早くお昼食べ行こ！", 1, 1, 2, 0, 2, 4678, 180101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日もお疲れ様ー！\n騎士くん、あたしが\nマッサージしよっか？\n結構評判なんだよ！", 1, 1, 3, 0, 3, 4679, 180101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うにゃぁ…\nこの気持ちよさ…\n溶けちゃいそう…\nにゃぁ…", 1, 1, 0, 0, 2, 4681, 180101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "か、かわいい～\nこれ、なでなで\nしていいんだよね…？\nよしよし…♪", 1, 1, 0, 0, 3, 4682, 180101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "とってもいい香り！\n気分も落ち着くし、\n特訓の後なんかに\nいいかもー！", 1, 1, 0, 0, 4, 4683, 180101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ぬくぬくだぁ…\nなんだか、\nふにゃふにゃに\nなっちゃうよぉ…", 1, 1, 0, 0, 5, 4684, 180101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お、おいひぃ…！\nこの姿になると\nお腹すくし、すっごく\n助かっちゃった！", 1, 1, 0, 0, 7, 4686, 180101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この本で勉強して、\nぶんぶりょーどーの\n超ウルトラヒヨリに\nなっちゃうぞー！", 1, 1, 0, 0, 8, 4687, 180101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今年もみんなで\nおいしいものを\nたくさん食べたいです", 1, 1, 0, 0, 1, 4689, 111801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "挑戦は大切ですよね☆\nというわけで\nおせちに魔物を…", 1, 1, 0, 0, 2, 4690, 111801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "おせちは縁起物です！\n食べれば食べるほど\n幸せになるはずです！", 1, 1, 0, 0, 3, 4691, 111801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "食べ物がたくさん\n入った福袋は\nないんでしょうか…？", 1, 1, 0, 0, 4, 4692, 111801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "初詣で何を\nお願いしましょう…\n五穀豊穣？", 1, 1, 0, 0, 5, 4693, 111801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ずっとこうして\n年の始まりを\n過ごしたいですね", 1, 4, 0, 0, 101, 4694, 111801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "いつか二人きりで\n初日の出を\n見たいです", 1, 8, 0, 0, 102, 4695, 111801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お餅おかわりです！\nいくらでも\n食べられますよ！", 1, 1, 0, 0, 1, 4696, 111801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おせちの食材を\n取りに行きませんか？\nいい運動です☆", 1, 1, 0, 0, 2, 4697, 111801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "凧揚げって\n難しいですね～\n糸が絡まっちゃいます", 1, 1, 0, 0, 3, 4698, 111801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "一年の計は元旦にあり\n今年一年分の\n献立を作りましょう！", 1, 1, 0, 0, 4, 4699, 111801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ほらほら！\nもっと近くで着物を\n見ていいんですよ☆", 1, 4, 0, 0, 101, 4700, 111801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたはどんな\n初夢を見ましたか？\nわたしは…内緒です☆", 1, 8, 0, 0, 102, 4701, 111801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "年明けの朝は\n心なしか\n気持ちいいですね☆", 1, 1, 1, 0, 1, 4702, 111801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お雑煮のあとは\nおしるこで\nおやつタイムです！", 1, 1, 2, 0, 2, 4703, 111801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あたたかくして\n幸せな初夢を\n見ましょうね", 1, 1, 3, 0, 3, 4704, 111801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はぁ～屋台巡りを\n満喫しました☆\n一休み一休み", 1, 1, 0, 0, 2, 4706, 111801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "縁起のいい\nぬいぐるみを並べて\n福を呼びましょう！", 1, 1, 0, 0, 3, 4707, 111801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "おしるこのアロマって\nないんでしょうか？\nそれなら作るしか…", 1, 1, 0, 0, 4, 4708, 111801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ここでお餅を\n焼いたらおいしく\nなりそうですね～♪", 1, 1, 0, 0, 5, 4709, 111801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "おいしすぎて\n食べる手が\n止まりません～", 1, 1, 0, 0, 7, 4711, 111801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "新しいおせち料理の\nヒントは…なるほど…\nふむふむ…", 1, 1, 0, 0, 8, 4712, 111801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お兄ちゃんとあんな\n特訓やこんな特訓が\nしたいなぁ、えへへ♪", 1, 12, 0, 0, 103, 4714, 100601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お兄ちゃんに褒めて\nもらうと、身体が\nじぃーんと\n熱くなっちゃいます♪", 1, 12, 0, 0, 103, 4715, 100601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "見目麗しい衣装に\n身を包むのも\n悪い気分では\nありませんね", 1, 1, 0, 0, 1, 4716, 115001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "縁起物で\n験を担ぐというのも\nたまにはよいでしょう", 1, 1, 0, 0, 2, 4717, 115001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "非効率なので\nあまり行いませんが\n私も手料理くらいは\n作れるのですよ", 1, 1, 0, 0, 3, 4718, 115001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "研究のためなら\n時に遠路を超え\n実地へ赴くことも\n必要なのです", 1, 1, 0, 0, 4, 4719, 115001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "トゥンヌスの\n突然変異…\n大いに興味を\nそそられますね", 1, 1, 0, 0, 5, 4720, 115001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "漁師の技法も\n再現できたことで\nまた一つ、仮説が\n実証できました", 1, 4, 0, 0, 101, 4721, 115001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "正月くらいは\n私ものんびりしたい\n気持ちなのですよ\n意外ですか？", 1, 8, 0, 0, 102, 4722, 115001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "模倣に頼らずとも\n衣装に合わせた\n所作程度は\nできて当然です", 1, 1, 0, 0, 1, 4723, 115001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お年玉の使い道は\n思いつきましたか\nゆっくり考えても\nいいのですよ", 1, 1, 0, 0, 2, 4724, 115001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "トランプでも花札でも\n相手をしましょう\n手心が必要なら\nいつでも言いなさい", 1, 1, 0, 0, 3, 4725, 115001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "初詣でお祈りする\n内容ですか\n…ヒミツ、として\nおきましょう", 1, 1, 0, 0, 4, 4726, 115001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私たちでオーマ中の\nトゥンヌスを\n釣り上げましょうか\n…冗談ですよ", 1, 4, 0, 0, 101, 4727, 115001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "見返りを求めない願い\nそれは祈りに通じるの\nかもしれませんね", 1, 8, 0, 0, 102, 4728, 115001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "…貴方がその眠たげな\n顔を洗ってくる間に、\n温かいお茶でも\n淹れておきましょう", 1, 1, 1, 0, 1, 4729, 115001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "さて、本格的に\n活動を再開します\n貴方も同行しなさい\n昼食は済みましたか？", 1, 1, 2, 0, 2, 4730, 115001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私はもう少し\n作業を片付けてから\n寝ることにします\n早く眠りなさい", 1, 1, 3, 0, 3, 4731, 115001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "少し休みます…\n時間が来たら\n起こすように", 1, 1, 0, 0, 2, 4733, 115001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "贈り物を模倣して\n潜入…試してみる\n価値はありそうですね", 1, 1, 0, 0, 3, 4734, 115001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "嗅覚から得られる\n情報量は侮れません\n動物の模倣において\n注意すべき点ですよ", 1, 1, 0, 0, 4, 4735, 115001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "穏やかな炎による\nリラックス効果は\n本能によるものかも\nしれませんね", 1, 1, 0, 0, 5, 4736, 115001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いかなる強者でも\n兵站、補給は\n必要なものです", 1, 1, 0, 0, 7, 4738, 115001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あらゆる学問は\n完璧な模倣のための\n大きな礎となります", 1, 1, 0, 0, 8, 4739, 115001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "新たに得たこの力で\n仲間と目指す未来を\n切り拓いてみせます", 1, 1, 0, 0, 1, 4741, 115501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここは、みなさまと…\nわたくしの\n帰る場所でございます", 1, 1, 0, 0, 2, 4742, 115501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "主さまも精霊の召喚を\n試してみますか？", 1, 1, 0, 0, 3, 4743, 115501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "主さま、おこづかいは\n足りていますか？\n遠慮は無用ですよ", 1, 1, 0, 0, 4, 4744, 115501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この装束をまとうと\n自然と背筋が伸びる\n思いがいたします", 1, 1, 0, 0, 5, 4745, 115501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "進化した精霊に\n負けぬよう\n修行に励みます", 1, 4, 0, 0, 101, 4746, 115501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "仲間たちといられる\nこの幸せが、末永く\n続きますように…", 1, 8, 0, 0, 102, 4747, 115501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "主さまのお手手は\n大きくて温かいですね\nぎゅっぎゅっ…ふふ♪", 1, 1, 0, 0, 1, 4748, 115501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今度、主さまにも\n里に伝わる昔話を\nお話しいたしましょう", 1, 1, 0, 0, 2, 4749, 115501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "里で採れた果物を\n持って参りました\n召し上がってください", 1, 1, 0, 0, 3, 4750, 115501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "里のお土産を\nここのみなさまに\nお配りいたしましょう", 1, 1, 0, 0, 4, 4751, 115501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "仲間と囲む食卓…この\n温もりは、みなさまの\n心の温かさなのですね", 1, 4, 0, 0, 101, 4752, 115501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "主さまに名前を\n呼んでいただくと\n心が温かくなります♪", 1, 8, 0, 0, 102, 4753, 115501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふふっ…主さま\n寝癖がついて\nおりますよ？", 1, 1, 1, 0, 1, 4754, 115501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日の昼食は\n里の伝統料理です\nお口に合うでしょうか", 1, 1, 2, 0, 2, 4755, 115501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "主さまが眠れるよう\n安らぎの風を…\nおやすみなさいませ", 1, 1, 3, 0, 3, 4756, 115501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この癒し…\n主さまにも味わって\nいただきたい…", 1, 1, 0, 0, 2, 4758, 115501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この愛らしさ…\n精霊たちと似たものを\n感じます…♪", 1, 1, 0, 0, 3, 4759, 115501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "精霊よ…この安らぐ\n香りを、隅々まで\n届けてくださいまし", 1, 1, 0, 0, 4, 4760, 115501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "瞑想するのに\nちょうどよい暖かさで\nございます", 1, 1, 0, 0, 5, 4761, 115501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "進化した精霊を\n使役するためにも\nよく食べて体力を…！", 1, 1, 0, 0, 7, 4763, 115501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "風の精霊について\nもっと詳しく知って\nおかなければ…", 1, 1, 0, 0, 8, 4764, 115501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "儀式の時を思い出して\n精霊に呼びかける…\n出ておいで～♪", 1, 1, 0, 0, 1, 4766, 115601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ひゃうっ！　もう…\n急に背中を触るなんて\nいたずら好きな精霊に\n育てた覚えはないのに", 1, 1, 0, 0, 2, 4767, 115601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "儀式用の衣装…\nあんまり激しい動きは\nできないよね…でも\n戦う時は気にしない！", 1, 1, 0, 0, 3, 4768, 115601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "そうだ…精霊に\n騎士クンのそばで\n見守っていてって\n頼んでみようかな…", 1, 1, 0, 0, 4, 4769, 115601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ふふっ、こうやって、\n楽しく精霊とお話し\nしていられる、平穏な\n日々が続くといいな", 1, 1, 0, 0, 5, 4770, 115601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "き、きき、騎士クン！\nわたしと、あの…！\nうう…練習ですら\n上手く誘えないよぉ…", 1, 4, 0, 0, 101, 4771, 115601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あっ、精霊が…\nふふ、応援ありがとう\nわたしの想い…いつか\n必ず伝えるね", 1, 8, 0, 0, 102, 4772, 115601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "新しく覚えた料理\n作ってみたんだけど\n騎士クンも食べない？", 1, 1, 0, 0, 1, 4773, 115601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "騎士クン、\n疲れてない？\nわたしと精霊で\n治療するよ？", 1, 1, 0, 0, 2, 4774, 115601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もう、いたずらしない\n…騎士クン！？\nご、ごめんなさい！\n精霊だと思って！", 1, 1, 0, 0, 3, 4775, 115601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "騎士クン！\n時間が合ったら、\n精霊と一緒に\nお話しない？", 1, 1, 0, 0, 4, 4776, 115601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "…こういう服着るの\n初めてなんだけど…\nえっ、かわいい…？\nあ、ありがとう…！", 1, 4, 0, 0, 101, 4777, 115601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "困ったことがあったら\n相談してね。わたし、\n騎士クンのためなら…\n色々できるよ、色々！", 1, 8, 0, 0, 102, 4778, 115601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよう騎士クン！\n今からお外で\n瞑想するんだけど…\n騎士クンもどうかな？", 1, 1, 1, 0, 1, 4779, 115601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼の時間だ\nご飯ならわたしが\n用意するから\nゆっくりしてて", 1, 1, 2, 0, 2, 4780, 115601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もうこんな時間…\n騎士クン、はいどうぞ\n寝る前の温かいミルク\nぐっすり眠れるよ♪", 1, 1, 3, 0, 3, 4781, 115601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "…うん、落ち着く\n精霊との魔法で\nこんな風に誰かを\n安心させたいな", 1, 1, 0, 0, 2, 4783, 115601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいい…\n精霊も気に入ってる…\nそうだ、同じ物\n作ってあげようかな？", 1, 1, 0, 0, 3, 4784, 115601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい香り…これ、\nうちに置いたら、\nお母さん喜ぶかも！", 1, 1, 0, 0, 4, 4785, 115601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったかい…\nまるで誰かが\n抱きしめてくれた\nみたい…", 1, 1, 0, 0, 5, 4786, 115601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "自然の恵みに\n感謝して…\nいただきます♪", 1, 1, 0, 0, 7, 4788, 115601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もっと精霊について\n勉強して、みんなを\n守れるように\nなりたいな…", 1, 1, 0, 0, 8, 4789, 115601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "アイツといると胸が\nぎゅーっとなる…\nこれってもしかして…", 1, 12, 0, 0, 103, 4791, 102201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "アンタとならきっと\nどんなゲームでも…\nそ、その、たた、\n楽しいって思えるわ！", 1, 12, 0, 0, 103, 4792, 102201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "色んな人と話したら\n記憶探しの手がかりが\n見つかるかも…？", 1, 1, 0, 0, 1, 4793, 106401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "アイスクリームでも\n作ろうかしら？\nバニラにミントに…", 1, 1, 0, 0, 2, 4794, 106401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ネビアに話しかけると\n逃げられるのよね…\nもう、襲わないのに…", 1, 1, 0, 0, 3, 4795, 106401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "「コッコロさん」、\n「コッコロさん」…\n…間違えないように…", 1, 1, 0, 0, 4, 4796, 106401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "最近少し太ったかも？\nきっとおいしいもの、\n食べ過ぎね…", 1, 1, 0, 0, 5, 4797, 106401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここにいると、\nとても落ち着くわ…\n自分の家、だものね", 1, 4, 0, 0, 101, 4798, 106401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "みんなと一緒の時間、\nずっと記憶に残したい\n…そう思っているのよ", 1, 8, 0, 0, 102, 4799, 106401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ドラゴン族って、\nそんなに珍しいの？\nあまり実感ないけど…", 1, 1, 0, 0, 1, 4800, 106401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "料理、練習してるのよ\n…味見してもらえる？\n期待はしないでね", 1, 1, 0, 0, 2, 4801, 106401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "頭を叩いたら、\n記憶が戻ったり\nしないかしら…？\nやらなくていいわよ？", 1, 1, 0, 0, 3, 4802, 106401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "赤ちゃんみたいだった\n時のことは、忘れて…\nお願いだから…", 1, 1, 0, 0, 4, 4803, 106401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ひゃんっ！？\nし、しっぽを掴むのは\nダメよ？", 1, 4, 0, 0, 101, 4804, 106401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "記憶が戻っても…\n私は、あなたたちと\n一緒にいたいわ", 1, 8, 0, 0, 102, 4805, 106401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよう。\n寝ぐせついてるわよ？\n…ふふっ、これでよし", 1, 1, 1, 0, 1, 4806, 106401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そろそろお昼かしら？\nおいしいごはんを、\nみんなで一緒に…ね", 1, 1, 2, 0, 2, 4807, 106401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もうこんな時間ね。\nそろそろ休みましょ？\n…ふわぁ…", 1, 1, 3, 0, 3, 4808, 106401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふかふか、ぎゅう～…\n…あっ、これはその…\nストレッチしててっ…", 1, 1, 0, 0, 2, 4810, 106401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あまり、興味ないわ。\nけど…けど…\n…か、かわいい…", 1, 1, 0, 0, 3, 4811, 106401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい香りね。\nとっても落ち着くわ…\n…っくしゅっ", 1, 1, 0, 0, 4, 4812, 106401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "冷やす方が得意だけど\nあったかいのも、\nキライじゃないのよ", 1, 1, 0, 0, 5, 4813, 106401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "別に、食いしん坊\nじゃないのよ？\nでも、もう一口だけ…", 1, 1, 0, 0, 7, 4815, 106401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ん…なるほどね。\n勉強になるわ…", 1, 1, 0, 0, 8, 4816, 106401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "水着の探偵か…\nちょっと恥ずかしい\n気がしてきたよ…", 1, 1, 0, 0, 1, 4818, 115701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "依頼があれば\n海でも山でも\n赴くさ！", 1, 1, 0, 0, 2, 4819, 115701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今のうちに\n助手くんの秘密を…\nわ…私はなにをっ！？", 1, 1, 0, 0, 3, 4820, 115701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "休暇も時には必要さ\n海でゆっくりと…\nむっ？　事件の気配！", 1, 1, 0, 0, 4, 4821, 115701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "探偵の秘密道具…\n水着だと隠す\n場所がない…！", 1, 1, 0, 0, 5, 4822, 115701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "そろそろ助手くんが\n現れる時間かな？\nふふっ、楽しみだね", 1, 4, 0, 0, 101, 4823, 115701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "真実は私と助手くん\n二人だからこそ\n解き明かせるのさ", 1, 8, 0, 0, 102, 4824, 115701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ひゃああっ！？\nわ、私の不意を突く\nとは…やるね助手くん", 1, 1, 0, 0, 1, 4825, 115701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "さて、なにか気づいた\nことはあるかな？\n言ってごらん♪", 1, 1, 0, 0, 2, 4826, 115701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この感触…分かった！\n助手くんだね！\nふふっ、やっぱり♪", 1, 1, 0, 0, 3, 4827, 115701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "水着…どうかな？\n探偵らしさは\n残してるつもりだけど", 1, 1, 0, 0, 4, 4828, 115701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ん？　依頼かな？\n助手くんの頼みなら\nなんでもお安い御用さ", 1, 4, 0, 0, 101, 4829, 115701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "助手くん、私と海に\n行かないかな\n…事件じゃないよ？", 1, 8, 0, 0, 102, 4830, 115701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "珈琲でも淹れて\n目を覚ますとしよう\n一緒に飲むかい？", 1, 1, 1, 0, 1, 4831, 115701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "昼食は済ませた？\nなにを食べたか\n当ててあげよう！", 1, 1, 2, 0, 2, 4832, 115701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜は謎が深まる時間…\n闇に覆い隠された真実\n必ず暴いてみせる！", 1, 1, 3, 0, 3, 4833, 115701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "さて、推理の時間だ\n…サボってないよ？\n考えてるんだからね？", 1, 1, 0, 0, 2, 4835, 115701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これはもしや、中に\n秘密のメッセージが？\n…かわいいだけだね", 1, 1, 0, 0, 3, 4836, 115701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふむ、なんだか\n頭がスッキリするね\n…閃いた！", 1, 1, 0, 0, 4, 4837, 115701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ポカポカするね…\n助手くん\n君もおいでよ", 1, 1, 0, 0, 5, 4838, 115701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はむ、はむ…んぐっ\nた、食べてるところを\n見つめられると～…", 1, 1, 0, 0, 7, 4840, 115701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "秘密の依頼とか\nこういう場所に\n隠したりするよね…♪", 1, 1, 0, 0, 8, 4841, 115701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これはいい乗り心地だ\n事件現場にこれで登場\n…は、やめておこう", 1, 1, 0, 0, 10, 4843, 115701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "不思議な感触だね…\nでも、いい発想が\n生まれてきそうだ…♪", 1, 1, 0, 0, 11, 4844, 115701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "これはあたしが\n前へ進むために\nあいつがくれた\n大切な力…！", 1, 1, 0, 0, 1, 4845, 180601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "チェンジ・\nプリンセスフォーム！\n練習練習、っと", 1, 1, 0, 0, 2, 4846, 180601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "もうあいつに助けて\nもらいっぱなしの\nあたしは…\n卒業しなきゃね", 1, 1, 0, 0, 3, 4847, 180601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "気ままに、お気楽に\nあいつと冒険する…\nそれって幸せよね", 1, 1, 0, 0, 4, 4848, 180601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "プリンセスフォーム…\nお姫様みたいね\nあたしがお姫様か…\nそっかぁ…ふふ♪", 1, 1, 0, 0, 5, 4849, 180601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あいつと繋がって\n生まれたこの絆は\nあたしの宝物だわ", 1, 4, 0, 0, 101, 4850, 180601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あたしの命の使い方…\n全て受け止めてくれた\nあいつにだったら…", 1, 8, 0, 0, 102, 4851, 180601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふふ～ん♪\nプリンセスフォームの\n天才魔法使い\nキャル様に用かしら？", 1, 1, 0, 0, 1, 4852, 180601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "これからは\nあたしの力で\nなんでもパパっと\n解決してあげるわね！", 1, 1, 0, 0, 2, 4853, 180601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "魔力がみなぎってる…\nねえ、もしかして\n今のあたしって本気で\nすごいんじゃない？", 1, 1, 0, 0, 3, 4854, 180601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "変身の反動で\nお腹が空いても\n魔物料理だけは\nいやだからね！？", 1, 1, 0, 0, 4, 4855, 180601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ねぇ、あんたになら\nあたしの心…\n全部預けていいかも", 1, 4, 0, 0, 101, 4856, 180601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あいつと一緒に\nいられたら、他には\n何も…って、あああ\nあんたいつの間に！？", 1, 8, 0, 0, 102, 4857, 180601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "早起きも悪くないわね\n読みかけの魔導書\n今のうちに\n読んじゃおうかしら", 1, 1, 1, 0, 1, 4858, 180601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ねぇ～お腹すいたー\nあんたなんか\n作りなさいよ\n魔物料理以外でね！", 1, 1, 2, 0, 2, 4859, 180601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふぁぁ…眠すぎて\n今なら変身しても\nお腹が減らない\n自信があるわ…", 1, 1, 3, 0, 3, 4860, 180601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あー♪　最っ高！\n休んで魔力を高めて…\nあいつの役に\n立ってあげなきゃね", 1, 1, 0, 0, 2, 4862, 180601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "なにこれかわいい…！\nコレクションにしたく\nなっちゃうじゃない", 1, 1, 0, 0, 3, 4863, 180601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この香り…すごく\nリラックスできるわ…\nあ、いい感じの魔法\n思いついちゃったかも", 1, 1, 0, 0, 4, 4864, 180601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったかい…\n落ち着くわ…\nあいつの手の\nぬくもりみたいね", 1, 1, 0, 0, 5, 4865, 180601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "プリンセスフォーム…\nこ、こんなに\nお腹が減るのね\n甘く見てたわ…！", 1, 1, 0, 0, 7, 4867, 180601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ま、魔物料理の本！？\n誰よこんな本\n買ったの！　って…\n一人しかいないか", 1, 1, 0, 0, 8, 4868, 180601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "憧れの舞踏会…\n私、きちんと\n踊れるかしら", 1, 1, 0, 0, 1, 4870, 115801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "アン・ドゥ・トロワ\nアン・ドゥ・トロワ\n練習はばっちりね♪", 1, 1, 0, 0, 2, 4871, 115801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "こんなにすてきな\nドレスを着られて\n本当に夢みたいだわ♪", 1, 1, 0, 0, 3, 4872, 115801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "魔法はいつか解ける…\nでも、その時間は心に\n残り続けるわ♪", 1, 1, 0, 0, 4, 4873, 115801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ガラスの靴は夢へ\n踏み出す魔法の道具…\n私、がんばっちゃう！", 1, 1, 0, 0, 5, 4874, 115801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "舞踏会であなたと\n一緒に踊れたら\nいいのにな", 1, 4, 0, 0, 101, 4875, 115801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "舞踏会に行く前に\n運命の王子様には\n出会ってるのよね", 1, 8, 0, 0, 102, 4876, 115801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どうかしら？\n私、きちんとドレス\n着られてる？", 1, 1, 0, 0, 1, 4877, 115801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたも一緒に\n踊ってみる？\nしゃなりしゃなり♪", 1, 1, 0, 0, 2, 4878, 115801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたにエスコート\nされてきれいなお城を\n歩いてみたいわ♪", 1, 1, 0, 0, 3, 4879, 115801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どう？　レッスンの\n成果はきちんと\n出てるかしら？", 1, 1, 0, 0, 4, 4880, 115801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたが隣にいれば\n私も立派なお姫様に\n見えるかしら♪", 1, 4, 0, 0, 101, 4881, 115801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私にとって\n一番の魔法はあなたが\nくれる勇気よ♪", 1, 8, 0, 0, 102, 4882, 115801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "さぁ！　今日も一日\n立派な淑女を目指して\nいきましょう♪", 1, 1, 1, 0, 1, 4883, 115801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼ごはん代わりに\nメタモルアップルを\n食べておきましょう", 1, 1, 2, 0, 2, 4884, 115801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そろそろ魔法が解ける\n時間…でも淑女の\n心得は忘れないわ♪", 1, 1, 3, 0, 3, 4885, 115801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "淑女にも休息は\n必要よね…少しだけ\n休ませてもらおっと", 1, 1, 0, 0, 2, 4887, 115801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいいものに\n囲まれた部屋も\n女の子の夢よね…", 1, 1, 0, 0, 3, 4888, 115801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "すてきな香り…\nお花かしら？\n森で探してみようかな", 1, 1, 0, 0, 4, 4889, 115801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "暖かい…暖炉のそばで\nおとぎ話を聞くのも\nいいわよね♪", 1, 1, 0, 0, 5, 4890, 115801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "レッスンのために\n栄養補給して\nおかないと！", 1, 1, 0, 0, 7, 4892, 115801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "教養を身に着ける\nことも大切よね！\nなになに…", 1, 1, 0, 0, 8, 4893, 115801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "や、やっぱスカートは\n落ち着かねえな…\nなんか、\nスースーするしよ…", 1, 1, 0, 0, 1, 4895, 115901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "せっかくこんな格好\nしてるし、いつもとは\n違う剣術の訓練を\nしてみるかな", 1, 1, 0, 0, 2, 4896, 115901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "さて、他の連中が\n帰ってくる前に、\nなんか料理でも\n作っといてやるか", 1, 1, 0, 0, 3, 4897, 115901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "こ、こんな服着てる\nわけだし、紅茶とか\n楽しんじゃっても\n変じゃないよな！", 1, 1, 0, 0, 4, 4898, 115901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "暇だし運動でも…\nあ、あぶねえ！\nスカートなの忘れてた\n…誰も見てねえよな？", 1, 1, 0, 0, 5, 4899, 115901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "紅茶が切れてるな…\nよし、あいつと一緒に\n買い物にでも行くか♪", 1, 4, 0, 0, 101, 4900, 115901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "…あいつがいねえと、\nつまんねえな…ったく\n飯用意してやるから、\n早く帰ってこいよな…", 1, 8, 0, 0, 102, 4901, 115901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あ、あんまり\nジロジロ見んなよ\n…結構、\n恥ずかしいんだからな", 1, 1, 0, 0, 1, 4902, 115901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ダンスの相手は\n無理だが、\n剣の訓練なら\nいつでも付き合うぜ！", 1, 1, 0, 0, 2, 4903, 115901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この服に\n触りたいのか？\nすごい作りだし、\n気持ちは分かるぜ", 1, 1, 0, 0, 3, 4904, 115901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お、いいところに！\nクッキーを焼く\nところだったんだが、\nおまえも食うか？", 1, 1, 0, 0, 4, 4905, 115901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おまえにもこういう服\n似合うんじゃねえか？\n今度あたしが\n選んでやるよ♪", 1, 4, 0, 0, 101, 4906, 115901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "…おまえの前でなら、\nこういう格好を\nすんのも…\nな、なんでもねえよ！", 1, 8, 0, 0, 102, 4907, 115901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "朝の訓練を始めるか\n時間あるなら、\nおまえも付き合えよな", 1, 1, 1, 0, 1, 4908, 115901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もうこんな時間か…\n昼飯作るけど、\nなんか\n食いたいものあるか？", 1, 1, 2, 0, 2, 4909, 115901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "眠れないなら、\nホットミルク飲むか？\n体あったまって、\n眠くなると思うぜ", 1, 1, 3, 0, 3, 4910, 115901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ちょっと休憩して…\nおっと、服にしわが\nできないように\nしねえとな", 1, 1, 0, 0, 2, 4912, 115901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "だ、誰も\n見てねえよな？\n…よ～しよしよし、\nかわいいなおまえー♪", 1, 1, 0, 0, 3, 4913, 115901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "すげぇいい香りだな\nうちの居酒屋にも…\nいや、合わねえか", 1, 1, 0, 0, 4, 4914, 115901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったけぇ…\nちょっとだけなら、\n寝ちまっても\nいいよな…？", 1, 1, 0, 0, 5, 4915, 115901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これうめえな！\nたぶん材料は…よし！\nあたしも作ってみるか", 1, 1, 0, 0, 7, 4917, 115901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この本は…\n誰でも分かる作法…か\n最低限このぐらいは\n勉強しとくか…", 1, 1, 0, 0, 8, 4918, 115901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ミミがおとなに\nなっても\nおにいちゃんとは\nずーっといっしょ♪", 1, 12, 0, 0, 103, 4920, 102001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ミミとくべつなだっこ\nしてほしいな…うん！\nおひめさまだっこ～♪", 1, 12, 0, 0, 103, 4921, 102001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "次はにいちゃんに\nどんなイタズラしよう\n早く会いたいな～", 1, 12, 0, 0, 103, 4922, 100401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ミソギの特別な\nイタズラ見てくれる？\nいいよねっ♪", 1, 12, 0, 0, 103, 4923, 100401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "大人になったら、\nあの人と何しようかな\n…楽しみだなぁ", 1, 12, 0, 0, 103, 4924, 103601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "頭を撫でないで\nください！　もう、\n少しだけ、ですからね", 1, 12, 0, 0, 103, 4925, 103601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "くるくる〜♪\nうちのドレス\nかわええなぁ", 1, 1, 0, 0, 1, 4926, 116001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "日差しは苦手やけど\nたまにはひなたの下で\nぽかぽかしたいわぁ", 1, 1, 0, 0, 2, 4927, 116001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "外はお花が咲いてる\n久しぶりにお花を\n活けたいなぁ", 1, 1, 0, 0, 3, 4928, 116001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お花をたくさん集めて\nギルドハウスに飾れば\n喜んでくれはるかな", 1, 1, 0, 0, 4, 4929, 116001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あたたかい日は\n紅茶を飲みながら\nゆっくりしたいなぁ", 1, 1, 0, 0, 5, 4930, 116001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お稽古事を始めるなら\nうちに言ってな\nどんなことも教えたる", 1, 4, 0, 0, 101, 4931, 116001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "うちにいろんな夢を\n見せてくれはって\nほんま、おおきにな♪", 1, 8, 0, 0, 102, 4932, 116001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "王子はん\n一緒におとぎの国へ\nお散歩にいきまひょ♪", 1, 1, 0, 0, 1, 4933, 116001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふんふんふ〜ん♪\n今な、王子はんに\n花冠を作ってるんよ", 1, 1, 0, 0, 2, 4934, 116001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "王子はんは\nお菓子好きどす？\nうち、作りますわ", 1, 1, 0, 0, 3, 4935, 116001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "うーん。ドレスの柄は\nお花とお星さま\nどっちにしまひょか", 1, 1, 0, 0, 4, 4936, 116001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "王子はんとの\n思い出が増えるたび\nうちの宝物も増えます", 1, 4, 0, 0, 101, 4937, 116001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "王子はんと一緒なら\nどんな場所でも\n夢の国になりますわ", 1, 8, 0, 0, 102, 4938, 116001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夢の中におりたいけど\n王子はんに会いたくて\n起きてもうたわ♪", 1, 1, 1, 0, 1, 4939, 116001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "王子はん一緒に\nアフタヌーンティー\nいかがやろか？", 1, 1, 2, 0, 2, 4940, 116001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜の舞踏会…\n夢が広がるわぁ\nもちろんお相手は…♪", 1, 1, 3, 0, 3, 4941, 116001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふわふわや〜\nなんや心地よぉて\n眠ってしまいそうや…", 1, 1, 0, 0, 2, 4943, 116001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かいらしいなぁ\n新しいお洋服でも\n作ってあげまひょか♪", 1, 1, 0, 0, 3, 4944, 116001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お花やろうか…\nリラックスできて\nええ香りやねぇ", 1, 1, 0, 0, 4, 4945, 116001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こういう場所で\n絵本を読むと\n気分が出てええなぁ♪", 1, 1, 0, 0, 5, 4946, 116001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "舞踏会を楽しむなら\nたくさん食べて\nたくさん踊りまひょ", 1, 1, 0, 0, 7, 4948, 116001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うち、この本好きや\n王子さまと結ばれる\nお姫さまのお話なんよ", 1, 1, 0, 0, 8, 4949, 116001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "はぁ、宿題めんどい…\nこれは楽しくない…\nこんな青春いらない…", 1, 1, 0, 0, 1, 4951, 116301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "奨学金の道は遠いなぁ\nメルクリちゃんたちが\nお金くれればいいのに", 1, 1, 0, 0, 2, 4952, 116301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "アオイちゃん元気かな\nチエルに会いたくて\n泣いてるんだろうな…", 1, 1, 0, 0, 3, 4953, 116301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "暇だし鏡でも見るか…\nうわ、顔良っ！\nえ誰この超絶美少女！", 1, 1, 0, 0, 4, 4954, 116301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ワンツーワンツー♪\nうーん…カルミナの\n振りコピ難しいにゃー", 1, 1, 0, 0, 5, 4955, 116301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "先輩も運動してます？\nチエルが一緒に走って\nあげてもいいですよ？", 1, 4, 0, 0, 101, 4956, 116301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "来年のテレフェスも\n今から楽しみだなあ…\nまた一緒にお化け屋敷\n入ってくださいね", 1, 8, 0, 0, 102, 4957, 116301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ちぇる～ん☆\nフェス仕様でも\nかわいいちえるん！", 1, 1, 0, 0, 1, 4958, 116301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "いま話しかけないで！\n来年の聖学祭ライブの\nイメトレ中なんです！", 1, 1, 0, 0, 2, 4959, 116301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "クロエ先輩のウェア姿\nめっちゃカッコよ！\n思わずおへそ触ったら\nぶたれましたけど", 1, 1, 0, 0, 3, 4960, 116301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ユニ先輩の体操着姿…\nあれはあれで実に良き\n一生見てられますよね", 1, 1, 0, 0, 4, 4961, 116301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "先輩って体操着持って\nないんですか？　ハ？\nチエルのを貸せと…？\nえっ…変態かな？", 1, 4, 0, 0, 101, 4962, 116301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "テレフェスでは先輩も\n地味に頑張ってました\nね。まあ格好よかった\nんじゃないですか…", 1, 8, 0, 0, 102, 4963, 116301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはちぇる～ん☆\nさあ今日も楽しいを\n探しに行きますよー！", 1, 1, 1, 0, 1, 4964, 116301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "先輩ビッグニュース！\n今からお昼ごはん\nおごらせてあげます！", 1, 1, 2, 0, 2, 4965, 116301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "こんばんちぇるるん☆\nさあ朝まで楽しいを\n探しに行きますよー！", 1, 1, 3, 0, 3, 4966, 116301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はぁ…ふかふか～…\n運動の後は…眠たく…\nなり…ま…", 1, 1, 0, 0, 2, 4968, 116301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ユニ先輩に似合いそ！\nいやクロエ先輩かな？\nチエルは当然すぎて…", 1, 1, 0, 0, 3, 4969, 116301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "やん、いい香り～！\nまるでチエルのために\n存在するみたい！", 1, 1, 0, 0, 4, 4970, 116301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この火を使って\n聖火リレーしましょう\n先輩、一本抜いて！", 1, 1, 0, 0, 5, 4971, 116301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あむあむ…ちゃんと\n食べないとユニ先輩\nみたいになっちゃう！", 1, 1, 0, 0, 7, 4973, 116301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふむふむ…ちゃんと\n読まないとクロエ先輩\nみたいになっちゃう！", 1, 1, 0, 0, 8, 4974, 116301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今なら書ける気がする\n私の本当の\n想いを込めた\nラブソングを！", 1, 12, 0, 0, 103, 4976, 102901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "輝きを増した私を\nいっぱい見てね！\n…ちょ、ちょっと\n見つめすぎだよ！", 1, 12, 0, 0, 103, 4977, 102901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "テレフェスかぁー…\nあんまいい記憶ないな\nよし、忘れとこ", 1, 1, 0, 0, 1, 4978, 116201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "暇だし鏡でも見るか…\nふふ～ん…いいじゃん\nあ？　何見てんだコラ", 1, 1, 0, 0, 2, 4979, 116201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ワンツー、ワンツー♪\nああクソ、難しいな…\nあ？　何見てんだコラ", 1, 1, 0, 0, 3, 4980, 116201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "アオイ元気してるかな\nまた絶叫したり白目\n剥いたりしてんのかな", 1, 1, 0, 0, 4, 4981, 116201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ミフユねえさんが\nバイトリーダーしてる\n工場…うちも行くか…", 1, 1, 0, 0, 5, 4982, 116201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "うーん…今日の晩御飯\n何にすっかなァ…\nあんたも食べてく？", 1, 4, 0, 0, 101, 4983, 116201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "来年のテレフェスは\nどーなンだかねえ？\nま、一緒にまた弟の\n相手してやってよ", 1, 8, 0, 0, 102, 4984, 116201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "体操着なんてフツーに\n着てもつまんないし？\nその辺センス出るよね", 1, 1, 0, 0, 1, 4985, 116201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "奨学金の道、遠くね？\nアキノ先輩と仲良く\nなった方が早いかも…", 1, 1, 0, 0, 2, 4986, 116201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あんた体操着は持って\nないの。ハ？　うちの\n貸せって？　いいけど\n…いや、よくねえよ", 1, 1, 0, 0, 3, 4987, 116201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "にゃんっ！\nや、だからさ…急に\n話しかけんなっての", 1, 1, 0, 0, 4, 4988, 116201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "え、なに？　この服？\nハ？　似合ってる？\n…え、聞こえなかった\nちょ、もっかい言って", 1, 4, 0, 0, 101, 4989, 116201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あんたもテレフェスで\n一応は頑張ってたよね\nま格好よかったんじゃ\nないの。知んないけど", 1, 8, 0, 0, 102, 4990, 116201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふぁ～あ…おはーす\n昨日も寝たの遅くて\nまだ目がショボだよ…", 1, 1, 1, 0, 1, 4991, 116201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なに。あんた昼御飯\nまだなの。じゃ、うち\n適当になんか作ろうか", 1, 1, 2, 0, 2, 4992, 116201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜っていいよね。色々\n見えすぎない感じとか\n…この話したっけ？", 1, 1, 3, 0, 3, 4993, 116201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あ、ヤバ…運動の後に\nこのフカついたのは…\n秒でスヤれる…すやぁ", 1, 1, 0, 0, 2, 4995, 116201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "へーこんなんチエルに\n似合いそうじゃん。\n…いやユニ先輩かな？", 1, 1, 0, 0, 3, 4996, 116201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お、なんか良さげな\n香りじゃん。これは\n何の…いや分かんね", 1, 1, 0, 0, 4, 4997, 116201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "熱ッ！　この野郎なに\n爆ぜてンだコラァ！\n濡らすぞ木、てめえ！", 1, 1, 0, 0, 5, 4998, 116201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "何コレよく分かんない\nけど、今日モヤシしか\n食ってないから助かる", 1, 1, 0, 0, 7, 5000, 116201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "今日、体操着だしね。\n読書には逆アドかな。\nうん帰ろ。いやあ残念", 1, 1, 0, 0, 8, 5001, 116201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ビッグボス・イノリ様\nふっふっふ…\nしびれる響きです！", 1, 1, 0, 0, 1, 5003, 116501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "いつかお兄さんを\nあたしの側近に\nしてやるですよ！", 1, 1, 0, 0, 2, 5004, 116501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ビッグボスになったら\nみんなからたくさん\nお菓子をもらうです！", 1, 1, 0, 0, 3, 5005, 116501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ふふん！\nこの服はあたしにしか\n着こなせませんよ！", 1, 1, 0, 0, 4, 5006, 116501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ばーんと立って\nどーんと腕を組む！\nこれがボスのポーズ！", 1, 1, 0, 0, 5, 5007, 116501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "イノリ様の側近に\n選ばれるなんて\n名誉なことですよ！", 1, 4, 0, 0, 101, 5008, 116501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "未来の側近として\nあたしをしっかり\n甘やかすですよ！", 1, 8, 0, 0, 102, 5009, 116501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あたしがお兄さんを\n立派なギャングに\nしてあげますからね！", 1, 1, 0, 0, 1, 5010, 116501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "側近たるもの\nボスを甘やかなさいと\nだめですよ！", 1, 1, 0, 0, 2, 5011, 116501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なに見てやがるです…\nくぅ、分かりました！\nお菓子は半分こです！", 1, 1, 0, 0, 3, 5012, 116501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お兄さんも\nあたしがボスなら\nうれしいですよね！", 1, 1, 0, 0, 4, 5013, 116501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お兄さんが側近なら\nあたしも安心して\n夜眠れるですよ！", 1, 4, 0, 0, 101, 5014, 116501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ビッグボスになったら\nお兄さんにいい思い\nさせてあげますから！", 1, 8, 0, 0, 102, 5015, 116501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ボスたるもの\n早起きは鉄則ですよ！\nふぁあ…", 1, 1, 1, 0, 1, 5016, 116501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お腹が空きました…\nご飯食べにいきますよ", 1, 1, 2, 0, 2, 5017, 116501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ボスたるものぉ〜…\n夜更かしぐらい当然…\nぐー…", 1, 1, 3, 0, 3, 5018, 116501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "は〜これは人をだめに\nしやがるやつですね\nあたし専用です", 1, 1, 0, 0, 2, 5020, 116501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これはけしからん\nかわいらしさですね\nあたしにぴったりです", 1, 1, 0, 0, 3, 5021, 116501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "高級な香りがするです\nボスになったら\n部屋に置いてやるです", 1, 1, 0, 0, 4, 5022, 116501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったかいですねぇ\nちょっとこのまま\n一休憩です…", 1, 1, 0, 0, 5, 5023, 116501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はむはむ♪\nボスたるもの食べねば\n戦はできませんよ！", 1, 1, 0, 0, 7, 5025, 116501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ボスなら経済書ぐらい\n…う。難しくて\n読めねーです…", 1, 1, 0, 0, 8, 5026, 116501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あなたはドジな私を\n受け入れてくれる…\nだから、頑張れます！", 1, 12, 0, 0, 103, 5028, 102501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "何でも仰ってください\nちょっぴり大胆な\nことでも…な～んて♪", 1, 12, 0, 0, 103, 5029, 102501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あーあー、よし\nのどの調子もいいし\nはりきってガイド\nしてくるか！", 1, 1, 0, 0, 1, 5030, 116601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "さあ、そろそろ行くか\nガイドが\n遅刻したんじゃ\n話になんないからな", 1, 1, 0, 0, 2, 5031, 116601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "しれっとドラゴン族が\n旅行に来たり\nしてくれないかな", 1, 1, 0, 0, 3, 5032, 116601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "たぎるガイド先…\n火山、魔物の巣…\nダメだ、そんなとこ\nオレしか行けないや", 1, 1, 0, 0, 4, 5033, 116601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この前行った絶景\nスポットよかったな\nキレイな景色を見る\nのもいいもんだぜ", 1, 1, 0, 0, 5, 5034, 116601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お、今日はカレと\n一緒か。なんだか\nやる気出てきたぜ！", 1, 4, 0, 0, 101, 5035, 116601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "カレにガイドして\nもらったら\nどこに行っても\n楽しいだろうな", 1, 8, 0, 0, 102, 5036, 116601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "よっしゃあ！\nどっちが上手く\nガイドできるか\n勝負しようぜ！", 1, 1, 0, 0, 1, 5037, 116601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "飯でも食いがてら\nおススメの店の\n下調べでもしようぜ！", 1, 1, 0, 0, 2, 5038, 116601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なかなかいいだろ？\nこの格好。キミも\n作ってもらったら\nどうだ？", 1, 1, 0, 0, 3, 5039, 116601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "普通は行けない\n場所とか行きたいよな\nキミならどんな\nとこがいい？", 1, 1, 0, 0, 4, 5040, 116601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ガイドの仕事は\nキミの方が得意そうだ\n参考にさせてもらうぜ\nよろしくな！", 1, 4, 0, 0, 101, 5041, 116601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "キミの探してきた店\n結構よさそうじゃん\nそうだ、今度二人で\n下見に行こうぜ！", 1, 8, 0, 0, 102, 5042, 116601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "いい朝だな。絶好の\n仕事日和だぜ！\n今日もよろしくな！", 1, 1, 1, 0, 1, 5043, 116601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "よし、昼飯にしよう\nこの後も激務だし\n飯抜きじゃあ\nぶっ倒れちまうからな", 1, 1, 2, 0, 2, 5044, 116601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お疲れさん！　さーて\n次はオレと遊んで\nくれよ。まだまだ\nいけるだろ？", 1, 1, 3, 0, 3, 5045, 116601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ガイドは一日\n歩きっぱなしだからな\nゆっくり休ませて\nもらうぜ", 1, 1, 0, 0, 2, 5047, 116601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これ、どこで\n売ってるんだろ？\n土産の情報も\n仕入れとかなきゃな", 1, 1, 0, 0, 3, 5048, 116601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "アロマとかよく\n分かんねえけど\n土産にはいいかもな\n覚えておくぜ", 1, 1, 0, 0, 4, 5049, 116601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "体が温まると心も\n熱くなるんだよな\nいい仕事が\nできそうだぜ", 1, 1, 0, 0, 5, 5050, 116601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "食べ放題か\nこれはお客さんも\n喜んでくれそうだな！", 1, 1, 0, 0, 7, 5052, 116601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "へえ、こんなスポット\nあったんだ。やっぱ\nたまには勉強も\nしとくもんだな", 1, 1, 0, 0, 8, 5053, 116601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "バイト先になじめる\nように笑顔の練習を！\nに、にへぇ〜…", 1, 1, 0, 0, 1, 5055, 116701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "働いて働いて働いて\n…いつかその先に\nぼっちの出口が\n見えるのかも…！", 1, 1, 0, 0, 2, 5056, 116701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "バイトのイメトレを\n始めます。ポーズを\n決めて…よし！\n角度を変えて…よし！", 1, 1, 0, 0, 3, 5057, 116701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "バイト仲間に入る\nためには厳しい修業が\n必須と聞きました…！", 1, 1, 0, 0, 4, 5058, 116701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "どんどん技術を磨いて\nバイト仲間として\n認めてもらうんです！\n目指せ職人技…！", 1, 1, 0, 0, 5, 5059, 116701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あの人が応援して\nくれる…だから必ず\nバイト仲間の輪に\n入ってみせます…！", 1, 4, 0, 0, 101, 5060, 116701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あの人がいてくれると\n勇気が湧いてきます…\nぼっち卒業を夢見ても\nいいんだ、って…♪", 1, 8, 0, 0, 102, 5061, 116701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "緊張するけど\n思い切って声をかけて\nみます…バイト仲間に\n入るために…！", 1, 1, 0, 0, 1, 5062, 116701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ぼっちの私が\nおもちゃ作りで\n人と共同作業…！\nか、感動です…！", 1, 1, 0, 0, 2, 5063, 116701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "好きなおもちゃって\nありますか？　私は\nもちろんマイフレンド\nくんです！", 1, 1, 0, 0, 3, 5064, 116701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私が作ったおもちゃも\n誰かの友達に\nなってたら\nいいなぁ…♪", 1, 1, 0, 0, 4, 5065, 116701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日も明日もバイト\nですね！　バイト\n仲間ができる日が\n楽しみです…♪", 1, 4, 0, 0, 101, 5066, 116701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "脱ぼっちを目指して\nこれからも一緒に\n頑張りましょう！\n目指せ、バイト仲間！", 1, 8, 0, 0, 102, 5067, 116701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようございます\n今日も気合いを入れて\nおもちゃ作りします！", 1, 1, 1, 0, 1, 5068, 116701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ランチの時間です…♪\nもちろん工場の隅で\nぼっち飯です…！", 1, 1, 2, 0, 2, 5069, 116701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふわぁ…今日も\nお疲れさまでした\n明日も働きます～！", 1, 1, 3, 0, 3, 5070, 116701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "バイトで頑張った日は\nふわふわの誘惑に\n勝てません…", 1, 1, 0, 0, 2, 5072, 116701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こういうおもちゃの\nアイデアも\n素敵かも…♪", 1, 1, 0, 0, 3, 5073, 116701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい香りがする\nおもちゃはどうかな…\n森の薬草を使って…", 1, 1, 0, 0, 4, 5074, 116701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ぽかぽか…\n幸せなひとときですね", 1, 1, 0, 0, 5, 5075, 116701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もぐもぐ…たくさん\n食べて厳しいバイトを\n乗り切ります…！", 1, 1, 0, 0, 7, 5077, 116701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふむふむ…おもちゃの\n設計図を見るのも\n勉強になりますね！", 1, 1, 0, 0, 8, 5078, 116701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "う～ん\n次はどんなたい焼きを\n作ろうかにゃぁ…", 1, 1, 0, 0, 1, 5080, 116801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あたしが売る\nたい焼きには\n夢が詰まってるにゃ！", 1, 1, 0, 0, 2, 5081, 116801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "いつか絶対に\nたい焼き工場を\n建て直してみせるにゃ", 1, 1, 0, 0, 3, 5082, 116801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "たまにたい焼き以外の\nおやつも食べるにゃ\nたとえば煮干しとか！", 1, 1, 0, 0, 4, 5083, 116801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "悪い商売人がいたら\nファントムキャッツに\n知らせるにゃ！", 1, 1, 0, 0, 5, 5084, 116801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "キミと一緒にいると\nたい焼きのアイデアが\n次々出てくるにゃ♪", 1, 4, 0, 0, 101, 5085, 116801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "出来ないことも\nキミと一緒にいれば\n出来る気がするにゃ♪", 1, 8, 0, 0, 102, 5086, 116801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なんにゃ～？\nお姉さんにかまって\nほしいのかにゃ♪", 1, 1, 0, 0, 1, 5087, 116801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "キミ、いいところに！\nあたしとライバル店へ\n視察にいくにゃ！", 1, 1, 0, 0, 2, 5088, 116801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "くんくん…この匂い！\nたい焼き持ってるなら\nちょうだいにゃ♪", 1, 1, 0, 0, 3, 5089, 116801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "次はどんなたい焼きを\n売ろうかにゃ～\nキミの案も欲しいにゃ", 1, 1, 0, 0, 4, 5090, 116801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ファントムキャッツに\n相棒が必要にゃ\nたとえばキミとか…", 1, 4, 0, 0, 101, 5091, 116801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "相棒！\n…ってキミのこと\n呼んでいいかにゃ？", 1, 8, 0, 0, 102, 5092, 116801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "早起きは三文の徳にゃ\n今日もたい焼き\n売れますよーににゃ！", 1, 1, 1, 0, 1, 5093, 116801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お腹空いたにゃ…\n賄いのたい焼きを\nいただくにゃ！", 1, 1, 2, 0, 2, 5094, 116801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "猫は夜が本番にゃ\nでもあたしは…\nふわぁ～眠いにゃ…", 1, 1, 3, 0, 3, 5095, 116801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふかふかにゃ～\nここで体を丸めて\n一眠りしたいにゃ", 1, 1, 0, 0, 2, 5097, 116801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これは…！\n猫にも負けない\nかわいさだにゃ…！", 1, 1, 0, 0, 3, 5098, 116801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい香りにゃ～\nゴロゴロのどが\n鳴りそうだにゃ～", 1, 1, 0, 0, 4, 5099, 116801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふにゃ～…\nこのまま働かずに\nまるまってたいにゃ…", 1, 1, 0, 0, 5, 5100, 116801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "商売は戦いにゃ\nたくさん食べて\nたくさん働くにゃ！", 1, 1, 0, 0, 7, 5102, 116801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふむふむ。たい焼きの\nアレンジレシピ集…\nためになるにゃ…", 1, 1, 0, 0, 8, 5103, 116801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お芝居の前に\nお兄ちゃんの顔を\n見ると、とっても\n落ち着きますぅ…♪", 1, 12, 0, 0, 103, 5105, 102101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お兄ちゃんにもっと\n見てもらいたい…\nだから私…\n強くなりたいです…！", 1, 12, 0, 0, 103, 5106, 102101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "さらなる効率化で\nみんなといい仕事を\nしたいわね！", 1, 1, 0, 0, 1, 5107, 116901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "他のバイトとの\nスケジュールを\n調整しないと…", 1, 1, 0, 0, 2, 5108, 116901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "時給がアップしたわ！\n働きが認められると\nやる気が出るわね", 1, 1, 0, 0, 3, 5109, 116901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "効率的に動ければ\n業務を追加できる…\n燃えてきたわ！", 1, 1, 0, 0, 4, 5110, 116901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "帳簿付けのコツ…\nユカリさんに教わって\nみようかしら", 1, 1, 0, 0, 5, 5111, 116901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "バイトリーダーとして\nみんなをビシバシ\n鍛えていくわ！", 1, 4, 0, 0, 101, 5112, 116901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "戦いでもバイトでも\nキミなら安心して\n背中を預けられるわね", 1, 8, 0, 0, 102, 5113, 116901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どうしたの？\n仕事のことなら\nなんでも相談してね！", 1, 1, 0, 0, 1, 5114, 116901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "さすがは私の弟子ね\nこれなら安心して\n仕事を任せられるわ", 1, 1, 0, 0, 2, 5115, 116901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "みんなのお手本になる\n働きぶりを見せないと\nいけないわね！", 1, 1, 0, 0, 3, 5116, 116901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "キミのこと…もっと\n頼りにしちゃっても\nいいのかしら…？", 1, 1, 0, 0, 4, 5117, 116901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふふ、キミも一人前の\nバイト戦士として\n立派に成長したわね！", 1, 4, 0, 0, 101, 5118, 116901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "新しいおもちゃの\n企画書を作ってみたの\nちょっと見てくれる？", 1, 8, 0, 0, 102, 5119, 116901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "朝は一番効率がいい\n時間帯よ！\nバリバリ働くわよ！", 1, 1, 1, 0, 1, 5120, 116901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "さ、お昼に行きましょ\n今日の日替わり定食は\n何かしら…楽しみね♪", 1, 1, 2, 0, 2, 5121, 116901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もうこんな時間…\nちゃんと寝て明日に\n備えなくちゃね", 1, 1, 3, 0, 3, 5122, 116901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "適度な労働と休息の\nサイクルが最大効率を\n生み出すのよ…ふぅ…", 1, 1, 0, 0, 2, 5124, 116901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はぁ～なんて素敵な\n抱き心地なのかしら…\n最高効率の癒やしね♪", 1, 1, 0, 0, 3, 5125, 116901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "落ち着く香りね…\nなんだか集中力が\nアップした気がするわ", 1, 1, 0, 0, 4, 5126, 116901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふぅ…癒されるわね…\n仕事のオンオフの\n切り替えは重要よね", 1, 1, 0, 0, 5, 5127, 116901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "働くためには\nエネルギーをちゃんと\n摂っておかないと", 1, 1, 0, 0, 7, 5129, 116901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "効率を上げるため\n生産管理の勉強も\nしておきたいわね", 1, 1, 0, 0, 8, 5130, 116901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "変身している間に\n色々と試しておこう\nまずは…", 1, 1, 0, 0, 1, 5132, 180301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "飛べるというのは\n便利だな。料理が\n暖かいうちに\n持ち帰れる", 1, 1, 0, 0, 2, 5133, 180301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "プリンセスか…\n柄ではないけれど\n嫌な気はしないな", 1, 1, 0, 0, 3, 5134, 180301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この力で\n何を成すべきなのか…\n剣に聞いてみよう", 1, 1, 0, 0, 4, 5135, 180301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "変身したとて私は私\n一時的に強くなろうと\n元が弱くてはだめだ", 1, 1, 0, 0, 5, 5136, 180301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "変身していない時でも\n彼がそばにいると\n力が湧いてくる…", 1, 4, 0, 0, 101, 5137, 180301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "私の思い…\nいつか伝えてみせるよ\n…きっといつか！", 1, 8, 0, 0, 102, 5138, 180301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "何者かと戦うの？\nそれとも修行？\nどちらだとしても\n付き合うよ", 1, 1, 0, 0, 1, 5139, 180301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "変身し続けたら\nどれだけ空腹に…\n私は試さないからね？", 1, 1, 0, 0, 2, 5140, 180301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "本物のプリンセスに\nなるのは御免だが\nナイトがいて\nくれるのは頼もしいよ", 1, 1, 0, 0, 3, 5141, 180301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "いつかこの力を\n誰かを救うために…\n戦い以外に使える時が\nくることを願うよ", 1, 1, 0, 0, 4, 5142, 180301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "キミに感じてほしいな\n私に流れてくる\nキミからの力を…\nその…私の、思いも…", 1, 4, 0, 0, 101, 5143, 180301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "思いを告げる…\nそれがこんなにも\n難しいなんて…\nでも、いつか必ず…！", 1, 8, 0, 0, 102, 5144, 180301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよう！\nいい変身日和だ！\n…ふふっ、冗談だよ", 1, 1, 1, 0, 1, 5145, 180301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "いつもより多く\n昼食を用意しよう\n変身が解けたら大変だ", 1, 1, 2, 0, 2, 5146, 180301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "一日お疲れ様\n私もそろそろ変身を\n解いて休むとするよ", 1, 1, 3, 0, 3, 5147, 180301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "変身してしまうと\n動かずにいても\n空腹になるのかな…？", 1, 1, 0, 0, 2, 5149, 180301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "私もキミたちくらい\n可愛げがある姿に\nなっているかな？", 1, 1, 0, 0, 3, 5150, 180301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "香りに対する感覚も\nいつもより鋭く\nなっているみたいだ…", 1, 1, 0, 0, 4, 5151, 180301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ありがたいな\n少し足元が\n冷えていたところだ", 1, 1, 0, 0, 5, 5152, 180301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これは…！\n持ち運べるように\n改良できないかな？", 1, 1, 0, 0, 7, 5154, 180301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "祖父に貰った\n技法書を読んで\n初心に帰ろう", 1, 1, 0, 0, 8, 5155, 180301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "最強の惚れ薬を作って\nあのおかたの心を\n私の思いのままに…", 1, 1, 0, 0, 1, 5157, 117001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "夏といえば海にスイカ\n花火に水着…\nそして惚れ薬ですわ…", 1, 1, 0, 0, 2, 5158, 117001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あのおかたの心を\n手に入れるための\n準備を進めましょう\nこの夏こそ確実に…", 1, 1, 0, 0, 3, 5159, 117001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あのおかたに近づく\n虫どもを、どうやって\n片付けようかしら…\nまったく忌々しいこと", 1, 1, 0, 0, 4, 5160, 117001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "…クスクス\n薬の調合を考えるのは\n至福の時間ですわ…", 1, 1, 0, 0, 5, 5161, 117001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あのおかたの全てが\n早く私のものに\nなりますように…", 1, 4, 0, 0, 101, 5162, 117001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "かき氷も花火も海も…\nあなた様がいてこそ…\nそう、あなた様は\n私の全てなのですわ", 1, 8, 0, 0, 102, 5163, 117001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "二人だけの夏を\n楽しみましょう…？\n…クスクス", 1, 1, 0, 0, 1, 5164, 117001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あら、あなた様\n私に秘密でどこに\n行こうとして\nいますの…？", 1, 1, 0, 0, 2, 5165, 117001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "海に行きませんか？\nあなた様と一緒に\n過ごすには\n最高の場所ですから…", 1, 1, 0, 0, 3, 5166, 117001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ちょうどいいところに\nいらっしゃいました\nできたての薬を…\nさぁどうぞ…クスクス", 1, 1, 0, 0, 4, 5167, 117001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夏の海のきらめきも\nあなた様の存在が\nなければ色あせて\nしまいますわ…", 1, 4, 0, 0, 101, 5168, 117001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私とあなた様は\n結ばれる運命に\nあるのです…\n逃がしませんわ…", 1, 8, 0, 0, 102, 5169, 117001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "起きてすぐに会いに\n来てくださるなんて\nようやく私の伴侶たる\n自覚が…クスクス", 1, 1, 1, 0, 1, 5170, 117001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お食事はまだですか？\n特製の薬草ランチを\n召し上がれ…クスクス", 1, 1, 2, 0, 2, 5171, 117001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私の夢を見られる薬を\n差し上げますわ…\nおやすみなさいませ\nあなた様…クスクス", 1, 1, 3, 0, 3, 5172, 117001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい肌触りですわ…\n考え事にもぴったり\n眠くなるのが\n玉に瑕ですが…", 1, 1, 0, 0, 2, 5174, 117001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こういったものに\n揮発性の薬を仕込む\nのも一つの手ですわね", 1, 1, 0, 0, 3, 5175, 117001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この香り…開発中の\n薬に使えそうな気が\nしますわ…クスクス", 1, 1, 0, 0, 4, 5176, 117001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あのおかたの\nぬくもりを\n思い出しますわ…", 1, 1, 0, 0, 5, 5177, 117001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "美味しい…この味なら\n薬を混ぜても違和感は\nなさそうですわ…", 1, 1, 0, 0, 7, 5179, 117001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この薬の調合法…\n今度試してみましょう\nもちろんあのおかたに\n飲んでいただいて…", 1, 1, 0, 0, 8, 5180, 117001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "二人乗りができると\nいいですわね…", 1, 1, 0, 0, 10, 5182, 117001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふかふかですわ…\nあのおかたの分も\n欲しくなります…", 1, 1, 0, 0, 11, 5183, 117001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今日はキミと\nいつまでも歌って\n踊りたい気分さ～♪", 1, 12, 0, 0, 103, 5184, 101701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あは～♪\nキミの隣は、故郷より\n落ち着くかも～…♪", 1, 12, 0, 0, 103, 5185, 101701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この水着…\n一番に弟くんに\n見せたいな♪", 1, 1, 0, 0, 1, 5186, 117101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "弟くんをこっそり\n海に運んで…\nサプライズしちゃお♪", 1, 1, 0, 0, 2, 5187, 117101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "いつもと違う髪型に\nしてみたけど…\n気に入ってくれるかな", 1, 1, 0, 0, 3, 5188, 117101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "海は楽しみだけど\n日焼けしないように\n気を付けなきゃね", 1, 1, 0, 0, 4, 5189, 117101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "まだかな、弟くん\n水着のお姉ちゃんが\nお出迎えだよっ♪", 1, 1, 0, 0, 5, 5190, 117101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "海が見えるおうちで\n弟くんと一緒に\n暮らしたいなぁ～", 1, 4, 0, 0, 101, 5191, 117101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "弟くんの水着…\nどんな感じかなぁ\n…あ、想像してたら\nドキドキして…えへへ", 1, 8, 0, 0, 102, 5192, 117101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "こーら、水着の\nお姉ちゃんは\nおさわり禁止♪\n…なんてね？", 1, 1, 0, 0, 1, 5193, 117101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なぁに？\nふふっ、何回でも\n呼んでいいんだよ♪", 1, 1, 0, 0, 2, 5194, 117101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "海で迷子に\nならないように\n手を繋ごうね♪", 1, 1, 0, 0, 3, 5195, 117101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お姉ちゃんが\n海の楽しみ方を\n教えてあげるねっ！", 1, 1, 0, 0, 4, 5196, 117101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "わっ、弟くん\nどこ触ってるの？\nも～驚いちゃったぞ♪", 1, 4, 0, 0, 101, 5197, 117101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お姉ちゃん、海でも\nずっとキミを見てるよ\n…安心してね", 1, 8, 0, 0, 102, 5198, 117101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "まだ眠そうだね？\nほら、海が待ってるよ\n早く起きて起きて♪", 1, 1, 1, 0, 1, 5199, 117101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "大丈夫、疲れてない？\n海で遊び疲れた時は\n素直に休憩しようね", 1, 1, 2, 0, 2, 5200, 117101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ほら、早く寝ないと\n明日もお姉ちゃんと\n遊べないぞ♪", 1, 1, 3, 0, 3, 5201, 117101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "だらけたお姉ちゃんは\n弟くんには\n見せられないな～", 1, 1, 0, 0, 2, 5203, 117101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふふっ、かわいいね♪\n弟くんが持つと\nもっとかわいいよっ♪", 1, 1, 0, 0, 3, 5204, 117101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい香りだね…\n一味違うお姉ちゃんに\nなれそう…♪", 1, 1, 0, 0, 4, 5205, 117101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったか～い♪\nほら、弟くんも\nおいでよ♪", 1, 1, 0, 0, 5, 5206, 117101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "よく食べて、いつでも\n弟くんの呼び声に\n応える準備を…！", 1, 1, 0, 0, 7, 5208, 117101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "弟くんの水着の写真\nこの辺りに隠して\nおこうかな", 1, 1, 0, 0, 8, 5209, 117101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この子の背に乗って\nお姉ちゃんと弟くんの\n楽園へ…な～んて♪", 1, 1, 0, 0, 10, 5211, 117101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "海を感じる感触だね\n弟くんとの楽しい夏を\n思い出しちゃうな…♪", 1, 1, 0, 0, 11, 5212, 117101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この夏、誰よりも\nキラキラしてみせる！\nだって私は\nアイドルだから！", 1, 1, 0, 0, 1, 5213, 117201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "日傘にアームカバー\n帽子にサングラス…\n紫外線対策は完璧！", 1, 1, 0, 0, 2, 5214, 117201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "とっておきの\nサマーソングを\nみんなに届けるよ！", 1, 1, 0, 0, 3, 5215, 117201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "なんにもしないのって\n苦手かも…\n彼、早く来ないかな？", 1, 1, 0, 0, 4, 5216, 117201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ラムネって\n夏って感じするよね\n後で彼と飲もっと♪", 1, 1, 0, 0, 5, 5217, 117201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "夏は恋の季節！\n燃えるような恋…\nいつかしたいな", 1, 4, 0, 0, 101, 5218, 117201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今年の夏の目標は\n…彼との思い出を\nいっぱい作ること！", 1, 8, 0, 0, 102, 5219, 117201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私の水着写真集\n見てくれたんだ！\nどう、かわいかった？", 1, 1, 0, 0, 1, 5220, 117201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "スイカ割りしたいの？\nいいね、楽しそう！\nみんなも誘おうよ♪", 1, 1, 0, 0, 2, 5221, 117201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ねぇ、見て見て！\n水着の映える\nポーズ考えたんだ！", 1, 1, 0, 0, 3, 5222, 117201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ライブ中に\n花火を打ち上げたら\n絶対盛り上がるよね！", 1, 1, 0, 0, 4, 5223, 117201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "最近、忙しいけど…\nキミの顔見たら\n疲れなんて\n消えちゃった！", 1, 4, 0, 0, 101, 5224, 117201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この夏もいーっぱい\nドキドキさせるから！\n覚悟しててよね♪", 1, 8, 0, 0, 102, 5225, 117201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "外は朝から暑いね\nけど、負けないから！\nお仕事、頑張るぞー！", 1, 1, 1, 0, 1, 5226, 117201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "自分へのご褒美に、\nアイス食べちゃお♪\nキミも一緒に食べよ！", 1, 1, 2, 0, 2, 5227, 117201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夏の夜って\nワクワクするね♪\nホタル見に行こうよ！", 1, 1, 3, 0, 3, 5228, 117201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふわぁ…眠い…\n仕事とかの疲れ、\nたまってるのかな…？", 1, 1, 0, 0, 2, 5230, 117201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "私のぬいぐるみが\nグッズになったら…\n彼にかわいがって\nもらえるかな？", 1, 1, 0, 0, 3, 5231, 117201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この香り…\n夏限定のやつでしょ？\nいいセンスしてるね♪", 1, 1, 0, 0, 4, 5232, 117201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "暑～い！\nけど…汗かいて\n気持ちいいかも！", 1, 1, 0, 0, 5, 5233, 117201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "レッスンの後のご飯は\n特別おいしい♪\n…もうひとつだけ\n食べちゃおっかな", 1, 1, 0, 0, 7, 5235, 117201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あ、この前撮影した\n水着グラビアだ\nうんうん、いい感じ！", 1, 1, 0, 0, 8, 5236, 117201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この子、もふもふだね\n触るのクセになりそう\nもふもふもふもふ…", 1, 1, 0, 0, 10, 5238, 117201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "わ、かわいい♪\n今度のグラビア撮影で\nこれ使ってもいい？", 1, 1, 0, 0, 11, 5239, 117201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "水着っていいですよね\n海に浮かぶ一輪の花\nって感じがして！", 1, 1, 0, 0, 1, 5240, 117401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "自分の水着を作るのも\n誰かの水着を作るのも\n楽しいですね♪", 1, 1, 0, 0, 2, 5241, 117401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "水着の最終チェック！\n伸縮性や動きやすさは\n…いい感じですね！", 1, 1, 0, 0, 3, 5242, 117401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "振り付けや衣装作りに\n歌や踊りの練習…\n…やることがいっぱい", 1, 1, 0, 0, 4, 5243, 117401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "はぁ…レイ様の水着…\n私に作らせて\nもらえないかなぁ～…", 1, 1, 0, 0, 5, 5244, 117401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "騎士さんのことは\n気に入らないですけど\n別に嫌いという\nわけじゃなくて…", 1, 4, 0, 0, 101, 5245, 117401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "騎士さん暇そうですね\nに、荷物持ちとして\n誘ってみようかな…", 1, 8, 0, 0, 102, 5246, 117401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "騎士さんいいところに\nこの衣装\nどう思います？", 1, 1, 0, 0, 1, 5247, 117401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "何見てるんですか？\nもしかしてこの水着の\n良さが分かります！？", 1, 1, 0, 0, 2, 5248, 117401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "真夏でも涼しい…\nそれも水着の良さの\n一つですね", 1, 1, 0, 0, 3, 5249, 117401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "仕立て屋も\nアイドルも\n諦めません！", 1, 1, 0, 0, 4, 5250, 117401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夏と言えば\nバカンスですけど…\n予定、あるんですか？", 1, 4, 0, 0, 101, 5251, 117401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "う～ん…騎士さんを\n海へ誘うには…って\nなんでもないですよ！", 1, 8, 0, 0, 102, 5252, 117401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふー…夏はやっぱり\n朝が一番過ごしやすい\nですね～！", 1, 1, 1, 0, 1, 5253, 117401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "暑い時こそ海です！\n体力づくりのためにも\n泳ぎにいきますよ！", 1, 1, 2, 0, 2, 5254, 117401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "外で虫が鳴いてる…\n夏って感じがして\n風流ですねぇ…", 1, 1, 3, 0, 3, 5255, 117401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "少し休憩です…\nこのまま寝ないよう\n気をつけないと…", 1, 1, 0, 0, 2, 5257, 117401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "か、かわいい…！\nこの感動を次の\nステージ衣装にも…！", 1, 1, 0, 0, 3, 5258, 117401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい香りですね～\n波の音が聞こえて\nきそうです…", 1, 1, 0, 0, 4, 5259, 117401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "泳いだ後は\n少し寒いので\nちょうどいいですね", 1, 1, 0, 0, 5, 5260, 117401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "夏バテしないように\n栄養はしっかり\n取らないと！", 1, 1, 0, 0, 7, 5262, 117401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "次のトレンドは…\nふむふむ、やっぱり\nこう来ましたか…", 1, 1, 0, 0, 8, 5263, 117401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "デザインはもちろん\n触り心地も\n最高ですね♪", 1, 1, 0, 0, 10, 5265, 117401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "夏の撮影スポットに\n活用できそうですね\nさっそく衣装考えて…", 1, 1, 0, 0, 11, 5266, 117401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "えへへ…♪\n団長さんが初めての\n友達で本当に良かった", 1, 12, 0, 0, 103, 5267, 104001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あっ、団長さん！\n今日のＢＢ団の活動\nなんですけど…二人で\n大縄跳びしませんか！", 1, 12, 0, 0, 103, 5268, 104001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この衣装で\nライブするのは\n恥ずかしいな…", 1, 1, 0, 0, 1, 5269, 117301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "せっかくの夏だし\n肝試しに\n行ってみたいかも…", 1, 1, 0, 0, 2, 5270, 117301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "歌もダンスも\nより上を目指すために\n夏合宿をしよう…！", 1, 1, 0, 0, 3, 5271, 117301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "夏の暑さにも負けない\n熱いライブに\nしてみせる…！", 1, 1, 0, 0, 4, 5272, 117301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "新曲のアイデア\nまとめたいな…\n少し海辺を散歩しよう", 1, 1, 0, 0, 5, 5273, 117301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "かき氷、食べたいな\n…あの人は何味が\n好きなんだろう？", 1, 4, 0, 0, 101, 5274, 117301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "水着、似合ってるって\n言ってもらえるかな…", 1, 8, 0, 0, 102, 5275, 117301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "サインして、ですか？\nいいですけど…\n少し照れますね…", 1, 1, 0, 0, 1, 5276, 117301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "少し待ってください\n今、新曲の楽譜を\n書いているので…", 1, 1, 0, 0, 2, 5277, 117301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なんでしょう？\nもしかして…\nボイトレに興味が？", 1, 1, 0, 0, 3, 5278, 117301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今度のライブでは\n夏らしい爽やかな歌を\nファンにお届けします", 1, 1, 0, 0, 4, 5279, 117301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "…水鉄砲で遊ぼう？\nふふ。楽しそうですね\nお手柔らかに…♪", 1, 4, 0, 0, 101, 5280, 117301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたの声を\n聴いていると、\n何だか落ち着きます…", 1, 8, 0, 0, 102, 5281, 117301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日も暑くなりそう…\nお互いしっかりと\n水分補給しましょうね", 1, 1, 1, 0, 1, 5282, 117301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お腹空きましたよね\n海の家で、\nなにか買ってきます", 1, 1, 2, 0, 2, 5283, 117301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜の海で月明かりと\n波の音を楽しもうかと\n…一緒に行きますか？", 1, 1, 3, 0, 3, 5284, 117301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "レッスンだけでなく\nリラックスすることも\n大事、ですよね", 1, 1, 0, 0, 2, 5286, 117301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふふ、かわいい\n精霊も負けないくらい\nかわいいですけどね", 1, 1, 0, 0, 3, 5287, 117301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "夏らしい爽やかで\nいい匂い…あなたに\nピッタリな香りですね", 1, 1, 0, 0, 4, 5288, 117301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "海に入った後は、\n体が冷えてしまいます\n一緒に温まりましょう", 1, 1, 0, 0, 5, 5289, 117301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "アイドルとして、\n食べすぎには\n気をつけないと…", 1, 1, 0, 0, 7, 5291, 117301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "夏は恋の季節…\nガンガン攻めるべし…\nなるほど…", 1, 1, 0, 0, 8, 5292, 117301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この子に乗りながら\nライブをする…\nというのはどうですか", 1, 1, 0, 0, 10, 5294, 117301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "まるで人魚になった\n気分…なんて、ふふ", 1, 1, 0, 0, 11, 5295, 117301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お姉ちゃんとして\nシオリンにかっこいい\nとこ見せなくっちゃ☆", 1, 1, 0, 0, 1, 5296, 118301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "いつかシオリンと\n一緒に空を飛んで\n空中散歩してみたいな", 1, 1, 0, 0, 2, 5297, 118301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "すこし眠くなってきた\nかも…シオリンと\nお昼寝しようかな", 1, 1, 0, 0, 3, 5298, 118301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お散歩してこよっと！\nシオリンとお出かけ\nするのにいい場所\n探さなくっちゃ！", 1, 1, 0, 0, 4, 5299, 118301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "シオリンに過保護って\n言われちゃった…♪\nそれでも心配しちゃう\nのが姉心…だよね？", 1, 1, 0, 0, 5, 5300, 118301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "気持ちいい日差し！\nシオリンと彼と、\nお昼寝したら\n気持ちよさそ～♪", 1, 4, 0, 0, 101, 5301, 118301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今度天気がいい日には\nシオリンと彼を誘って\n一緒にお出かけ\nしたいな～♪", 1, 8, 0, 0, 102, 5302, 118301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日はご機嫌なんだ♪\nだって、シオリンの\n調子がいいんだもん☆", 1, 1, 0, 0, 1, 5303, 118301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今からシオリンと\nピクニックに\n行くんだ♪　そうだ！\nキミも一緒にどう？", 1, 1, 0, 0, 2, 5304, 118301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "わっ、びっくりした！\nシオリンから借りた本\n読むのに集中\nしてたから…", 1, 1, 0, 0, 3, 5305, 118301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "うん？　どうしたの？\nあっ、もしかして\nこの服、気になる？", 1, 1, 0, 0, 4, 5306, 118301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "シオリンもキミも元気\nいっぱいだと、私も\n元気になっちゃうよ☆", 1, 4, 0, 0, 101, 5307, 118301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "シオリンの前だけ\nじゃなくて、キミにも\nかっこいい私の姿を\n見てほしいな☆", 1, 8, 0, 0, 102, 5308, 118301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよう！　えへへっ\n今日は早起きなんだ♪\nシオリンにかっこ悪い\nとこ見せたくないから", 1, 1, 1, 0, 1, 5309, 118301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おなかすいたなぁ～…\nシオリンと一緒に\nおいしいもの食べに\n行こうかな！", 1, 1, 2, 0, 2, 5310, 118301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もうこんな時間！？\nシオリンと一緒だと\n時間が早く\n過ぎていっちゃうね！", 1, 1, 3, 0, 3, 5311, 118301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この快適な寝心地…！\nシオリンにもおすすめ\nしなきゃ…！", 1, 1, 0, 0, 2, 5313, 118301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "わあ、かわいい！\nそれにさわり心地も\nいいし、抱き枕に\nちょうどいいかも！", 1, 1, 0, 0, 3, 5314, 118301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いいにおい…♪\nなんだかリラックス\nできて…すやぁ…", 1, 1, 0, 0, 4, 5315, 118301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ぽかぽか～☆\nシオリンと一緒に\nぬくぬくしたら\n気持ちよさそう♪", 1, 1, 0, 0, 5, 5316, 118301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "仲のいい人が一緒だと\nつい食が進んじゃうね\nえへへ…♪", 1, 1, 0, 0, 7, 5318, 118301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふむふむ…この本…\nシオリンが好きかも！\n今度おすすめしよっと", 1, 1, 0, 0, 8, 5319, 118301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お姉ちゃん、\n私のために無理は\nしてないかな…？", 1, 1, 0, 0, 1, 5322, 118401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "新しく買った絵本…\nこういうのなら\nお姉ちゃんと楽しく\n読めるよね", 1, 1, 0, 0, 2, 5323, 118401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ニャットちゃんを\nここに連れてきて\nみんなで遊べないかな", 1, 1, 0, 0, 3, 5324, 118401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "本屋さんに行こうかな\nみんなで読める\n面白い本が\nあるといいなぁ", 1, 1, 0, 0, 4, 5325, 118401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "たまには…読むだけ\nじゃなくて、自分で\nお話を書いて\nみようかな…？", 1, 1, 0, 0, 5, 5326, 118401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "時々…お姉ちゃんと\nあの人が似てるって\n思うことがあります…", 1, 4, 0, 0, 101, 5327, 118401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お姉ちゃんとあの人…\n優しくて頼もしい\n二人に囲まれて…\n私、幸せ者だなぁ…", 1, 8, 0, 0, 102, 5328, 118401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どうかしましたか？\nもしかして…あなたも\n一緒にこの本を\n読みたい…とか？", 1, 1, 0, 0, 1, 5329, 118401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "けほっ…けほっ…\nあっ、心配いりません\n飲み物を飲んで\nむせただけですから", 1, 1, 0, 0, 2, 5330, 118401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この格好…\n似合ってますか…？\nえへへ、照れますね…", 1, 1, 0, 0, 3, 5331, 118401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なぁに、お姉ちゃん？\n…あっ、すみません\n読書に集中していて…\n間違えちゃいました…", 1, 1, 0, 0, 4, 5332, 118401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お姉ちゃん、時々無理\nしちゃうから…\nあなたも一緒だと\n安心できます", 1, 4, 0, 0, 101, 5333, 118401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お姉ちゃんとあなたと\n頼れる人に囲まれて\n私は幸せものですね", 1, 8, 0, 0, 102, 5334, 118401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なんだか朝から\n調子がいい…今日は\nいい日になりそうです", 1, 1, 1, 0, 1, 5335, 118401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "せっかくだから、\nお姉ちゃんを誘って\nお外でランチも\nいいかも", 1, 1, 2, 0, 2, 5336, 118401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お姉ちゃんに心配\nかけないように…もう\nちょっと本を読んだら\n寝なくちゃね", 1, 1, 3, 0, 3, 5337, 118401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふんわり柔らかくて\n気持ちいい手触り…\nお姉ちゃんが寝るのに\nちょうどよさそう", 1, 1, 0, 0, 2, 5339, 118401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふふっ、かわいい♪\n絵本の物語みたいに\nおしゃべりできない\nかな…？", 1, 1, 0, 0, 3, 5340, 118401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "素敵な落ち着く香り♪\nなんだかお姉ちゃん\nみたい、ふふっ", 1, 1, 0, 0, 4, 5341, 118401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったかくてほっと\nするなぁ\nここでゆっくり\n読書しようかな", 1, 1, 0, 0, 5, 5342, 118401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この味…\nお姉ちゃんが好きそう\n少しもらって、\n持って行こうかな…？", 1, 1, 0, 0, 7, 5344, 118401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この本みたいな、\n心躍る大冒険も\nお姉ちゃんと一緒なら\nできちゃいそう", 1, 1, 0, 0, 8, 5345, 118401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "いい部屋ね\nここを私の分院に\nするのもいいかも\nしれないわね…♪", 1, 1, 0, 0, 1, 5348, 117501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "珍しいカンポーが\n手に入ったから\n色々試してみたいわ\n色々と、ね♪", 1, 1, 0, 0, 2, 5349, 117501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この服を着ると\n自分が医者だって\nより実感するわね\nまあ、闇医者だけどね", 1, 1, 0, 0, 3, 5350, 117501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "新薬開発のために\n薬草を採取して\nおこうかしら", 1, 1, 0, 0, 4, 5351, 117501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "いつ急患が来ても\n対応できるように\nしておきましょうか", 1, 1, 0, 0, 5, 5352, 117501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あの子の研究資料も\nだいぶ増えてきたわね\nちゃんとまとめて\nおかないと♪", 1, 4, 0, 0, 101, 5353, 117501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あの子は大事な\n研究対象。だから\nいつまでも健康で\nいてもらわないとね♪", 1, 8, 0, 0, 102, 5354, 117501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "薬に興味があるの？\nなら、今度ゆっくりと\n教えてあげるわね", 1, 1, 0, 0, 1, 5355, 117501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "少し時間があいたから\n一緒に新薬の実験を\nしましょうか♪", 1, 1, 0, 0, 2, 5356, 117501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どうしたの？\nまさか急患…！？\nあら、違うのね\nならよかったわ", 1, 1, 0, 0, 3, 5357, 117501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "具合が悪い時はすぐに\n言うのよ。君には\nいつでも健康でいて\nほしいから", 1, 1, 0, 0, 4, 5358, 117501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "少し仮眠したいわね\n肩…いいえ、膝を\n貸してもらえる？", 1, 4, 0, 0, 101, 5359, 117501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "たまには…君の隣なら\n闇医者も悪い研究者も\nお休みにするのも\n悪くないわね♪", 1, 8, 0, 0, 102, 5360, 117501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "たまには朝の陽ざしを\n浴びるのも…\n悪くないわね", 1, 1, 1, 0, 1, 5361, 117501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "天気がいい日は\nいい薬草が採れるの\n君も集めるの、\n手伝ってくれる？", 1, 1, 2, 0, 2, 5362, 117501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "いけない、もうこんな\n時間なのね\n診察に没頭すると\n時間感覚がなくなるわ", 1, 1, 3, 0, 3, 5363, 117501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もう少し固い方が\n私好みだけど…\n悪くない寝心地ね", 1, 1, 0, 0, 2, 5365, 117501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "このぬいぐるみ…\n闇医者として\n興味深いわね…", 1, 1, 0, 0, 3, 5366, 117501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "落ち着く香りね\n私も診察室で\n使ってみようかしら", 1, 1, 0, 0, 4, 5367, 117501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "冷えは病につながるわ\nしっかり温めて\n体をいたわりましょ", 1, 1, 0, 0, 5, 5368, 117501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "おいしい♪\n研究のお供に\nもってこいの\n味だわ", 1, 1, 0, 0, 7, 5370, 117501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "医学は日進月歩…\n闇医者とはいえ\n最新の論文には\n目を通しておかないと", 1, 1, 0, 0, 8, 5371, 117501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "白衣の天使かあ\nまさにボクのための\n呼び方だね", 1, 1, 0, 0, 1, 5400, 117601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "美しさとは\n日々の努力が\n作るものなんだよ", 1, 1, 0, 0, 2, 5401, 117601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "大変だ…\nボクは美しく\nなりすぎたかも", 1, 1, 0, 0, 3, 5402, 117601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ボクのかわいさで\n癒やされて\nみんな健康に\nなっちゃうね", 1, 1, 0, 0, 4, 5403, 117601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "どんな服装をしても\nボクの美しさは\n変わらないね", 1, 1, 0, 0, 5, 5404, 117601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "これからはボクも\n美の伝道師として\nがんばらないとね♪", 1, 4, 0, 0, 101, 5405, 117601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ボクのかわいい笑顔を\n近くで見る以上の\n癒やしはないよね♪", 1, 8, 0, 0, 102, 5406, 117601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どう？　看護師姿の\nボクもかわいいでしょ\nもっと近くで見る？", 1, 1, 0, 0, 1, 5407, 117601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ほら！　美しいボクの\n看護師姿を見たら\n元気でちゃうでしょ？", 1, 1, 0, 0, 2, 5408, 117601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "白衣の天使のボクも\n最高に美しいね\n存分に癒されてよ♪", 1, 1, 0, 0, 3, 5409, 117601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "具合が悪いの？\nならボクの美しい顔を\n処方してあげる♪", 1, 1, 0, 0, 4, 5410, 117601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "さあ今日もボクと\n一緒に美の追求に\n付き合ってもらうよ♪", 1, 4, 0, 0, 101, 5411, 117601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "かわいい天使のボクが\nいつもそばにいるんだ\nキミって幸せ者だね♪", 1, 8, 0, 0, 102, 5412, 117601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "さすがボク\n目が覚めるような\n美しさでしょ？", 1, 1, 1, 0, 1, 5413, 117601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "美は食で作られる\n美容にいいものを\n食べようよ！", 1, 1, 2, 0, 2, 5414, 117601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふああ…早く寝よ？\n夜更かしは美容の\n大敵だからね", 1, 1, 3, 0, 3, 5415, 117601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふう～…\nリラックスする\nボクも美しい", 1, 1, 0, 0, 2, 5417, 117601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ボクが抱きしめると\n普段よりかわいく\n見えちゃうね♪", 1, 1, 0, 0, 3, 5418, 117601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "美しいボクは\n常にいい香りを\nしていなくちゃね", 1, 1, 0, 0, 4, 5419, 117601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "光がボクを照らしてる\nんじゃなくて、ボクが\n光を輝かせてるのさ", 1, 1, 0, 0, 5, 5420, 117601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "バランスのいい食事が\n美しいボクを\n作るのさ", 1, 1, 0, 0, 7, 5422, 117601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふむふむ…なるほど\nこうすればより美しく\nなれるかもしれない", 1, 1, 0, 0, 8, 5423, 117601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "仲間のためなら\nきっと…どこまでも\n強くなれるよね", 1, 12, 0, 0, 103, 5425, 105701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "プリンセスフォーム？\n私と同じような力を\n持ってる人がいるの？\n会ってみたいな～！", 1, 12, 0, 0, 103, 5426, 105701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここの空気は、\nどうもゆるすぎて…\n落ち着きませんわね", 1, 1, 0, 0, 1, 5427, 118001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "陛下のお越しになる\n別宅としては、\n少し警備が\n甘すぎでは？", 1, 1, 0, 0, 2, 5428, 118001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あらあら？\nサッシにホコリが！\nちょっとネコ助！\n掃除は念入りに！", 1, 1, 0, 0, 3, 5429, 118001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "いつまで入り浸って\nいるのかと、\n秘書に叱られて\nしまいましたわ…", 1, 1, 0, 0, 4, 5430, 118001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "もういっそ、\nここに間借りして\n仕事しようかしら…？", 1, 1, 0, 0, 5, 5431, 118001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "邪魔している身ですし\n家事くらい\nお手伝いさせなさいな", 1, 4, 0, 0, 101, 5432, 118001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "本当は、意外と\n居心地が良いのかも…\n陛下の気持ち、\n少し分かりましたわ", 1, 8, 0, 0, 102, 5433, 118001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ちょっ…わたくしを\n叩いても、ホコリなど\nあまり出ませんわよ！", 1, 1, 0, 0, 1, 5434, 118001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "陛下っ？\nなんだ…貴方でしたの\n用件は手短にお願いね", 1, 1, 0, 0, 2, 5435, 118001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "別に、好き嫌いは\nありませんけれど…\n魔物料理って、意外と\n美味なものですわね", 1, 1, 0, 0, 3, 5436, 118001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "言っておきますけれど\nわたくしは商売人です\n荒事は専門外ですわよ", 1, 1, 0, 0, 4, 5437, 118001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "プリンセスナイトの\n能力…なにか儲け話に\n使えませんかしらね", 1, 4, 0, 0, 101, 5438, 118001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "貴方がたのことも、\n少しくらいは\n信用してないことも\nありませんでしてよ", 1, 8, 0, 0, 102, 5439, 118001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようございます\nさ、今日もバリバリ\n働きますわよ！", 1, 1, 1, 0, 1, 5440, 118001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼ですわね\n栄養補給をしつつ\n働きますわよ！", 1, 1, 2, 0, 2, 5441, 118001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "定時ですって？\n関係ありません！\nわたくし、まだまだ\n働きますわよ～っ！", 1, 1, 3, 0, 3, 5442, 118001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うっ…いけませんわ\n日頃の疲れが…\nふわぁ…", 1, 1, 0, 0, 2, 5444, 118001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もふもふ…ふふっ♪\n…なんですのっ、\nなにかおかしくて！？", 1, 1, 0, 0, 3, 5445, 118001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい香りですわね\nわたくしの執務室にも\n置きたいくらいですわ", 1, 1, 0, 0, 4, 5446, 118001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いけませんわ…\nこんなに暖かいと\n日頃の疲れが…ふわぁ", 1, 1, 0, 0, 5, 5447, 118001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もう一つ食べたら\n仕事に戻りますわよ？\nあと一つだけ…", 1, 1, 0, 0, 7, 5449, 118001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "商人にとって\n知識は武器ですわ\n陛下のため、貴方も\nもっと勉強なさい？", 1, 1, 0, 0, 8, 5450, 118001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "故郷のお菓子を\nいろいろ作ったんだ～\nみんなに\n配ってくるさ～♪", 1, 1, 0, 0, 1, 5452, 117701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "彼ってどんな仮装が\n似合うかな～？\nシーサーとか？", 1, 1, 0, 0, 2, 5453, 117701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "カボチャチャンプルー\n作ってみよっと！\nきっとおいしいさ～♪", 1, 1, 0, 0, 3, 5454, 117701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "視線を感じるさ～\nもしかして幽霊？\nおしゃべりするさ～♪", 1, 1, 0, 0, 4, 5455, 117701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "さっき作った\nサーターアンダギー、\n早く彼に\n食べてほしいさ～♪", 1, 1, 0, 0, 5, 5456, 117701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "彼のために、\n特別なイタズラを\n考えとくさ～♪", 1, 4, 0, 0, 101, 5457, 117701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "いつか彼と、\n故郷のハロウィンを\n楽しみたいさ～♪", 1, 8, 0, 0, 102, 5458, 117701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ハロウィンは\n私たちが守るさ～♪\nさ、見回りに行こ！", 1, 1, 0, 0, 1, 5459, 117701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この制服、\nハロウィンらしくて\n大好きさ～♪", 1, 1, 0, 0, 2, 5460, 117701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "イタズラする気ね～？\nどんなことされるのか\n楽しみさ～♪", 1, 1, 0, 0, 3, 5461, 117701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "カボチャのランタン、\n一緒に作ろ！\n絶対楽しいよ～♪", 1, 1, 0, 0, 4, 5462, 117701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お菓子くれるの？\nあは～、ありがとー♪\nもぐもぐ…\n優しい味がするさ～♪", 1, 4, 0, 0, 101, 5463, 117701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "キミと一緒に過ごす\nハロウィンは、\nドキドキとワクワクが\n止まらないさ～♪", 1, 8, 0, 0, 102, 5464, 117701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ハロウィンは\n朝から大忙しさ～！\nお菓子作り、\n手伝ってほしいさ～", 1, 1, 1, 0, 1, 5465, 117701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お化け屋敷に行こ！\n一緒にいっぱい\nドキドキしようね～♪", 1, 1, 2, 0, 2, 5466, 117701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ここからが\nハロウィン本番さ～！\n幽霊たちと\nいっぱい遊ぶさ～♪", 1, 1, 3, 0, 3, 5467, 117701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ちょっと休んだら、\nお菓子作りを\n再開するさ～♪", 1, 1, 0, 0, 2, 5469, 117701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "でーじかわいいね～♪\nこの子に仮装させたら\nきっと似合うさ～♪", 1, 1, 0, 0, 3, 5470, 117701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お菓子みたいに\n甘い匂い…\nハロウィンらしい\nいい香りさ～♪", 1, 1, 0, 0, 4, 5471, 117701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ランドソルの秋って\n冷えるけど…\nこれがあれば\n体も心もぽかぽかさ～", 1, 1, 0, 0, 5, 5472, 117701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "故郷のご飯も\nおいしいけど、\nこっちのご飯も\nでーじおいしいさ～♪", 1, 1, 0, 0, 7, 5474, 117701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "パンプキンパイの\nレシピは…なるほど～\n勉強になるさ～♪", 1, 1, 0, 0, 8, 5475, 117701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "全部おいしそうで\nどれから食べるか\n迷っちゃうさ～♪", 1, 1, 0, 0, 12, 5477, 117701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ムっ！　幽霊の気配を\n感じるデス！\nさあ姿を現すデス！", 1, 1, 0, 0, 1, 5479, 117801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ムムム…\n妖魔の力を忍法に\n使えないでショウか…", 1, 1, 0, 0, 2, 5480, 117801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "怪しい妖魔を\n成敗するデス！\nハロウィンの平和は\nワタシが守りマスよ！", 1, 1, 0, 0, 3, 5481, 117801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ワタシの祭囃子が\n聞こえマスかー？\nさあ集まるデース！\n陽気に踊りまショウ！", 1, 1, 0, 0, 4, 5482, 117801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お祭りは\n楽しんだ者勝ちデス！\n踊らにゃ\nソンソンデス！", 1, 1, 0, 0, 5, 5483, 117801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "幽霊になる前に\n天下統一を\n果たしたいデスね", 1, 4, 0, 0, 101, 5484, 117801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あの世に行っても\nショーグンは\nワタシの大事な\nショーグンデス！", 1, 8, 0, 0, 102, 5485, 117801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ショーグン！\n悪霊退治に\n出かけるデース！", 1, 1, 0, 0, 1, 5486, 117801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "妖魔を家来に\nしてみるのは\nどうデス？", 1, 1, 0, 0, 2, 5487, 117801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "マントを使って\nウツセミの術の精度を\n高めるデス！", 1, 1, 0, 0, 3, 5488, 117801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ハロウィンにちなんだ\n新しい忍法…\nすなわち妖忍法の\n開発を急ぐデス！", 1, 1, 0, 0, 4, 5489, 117801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あの世でも\nショーグンに\nお仕えしマスよー！", 1, 4, 0, 0, 101, 5490, 117801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ショーグンもワタシと\nハロウィンパレードを\n踊り明かしまセンか？", 1, 8, 0, 0, 102, 5491, 117801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようございマス！\n天下統一に向けて\n急いで準備するデス！", 1, 1, 1, 0, 1, 5492, 117801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お腹減ってまセンか？\nハロウィンスイーツで\n休憩するデ〜ス", 1, 1, 2, 0, 2, 5493, 117801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜更かしすると幽霊が\nでるかもしれまセン…\nヒュ〜ドロロロ〜…", 1, 1, 3, 0, 3, 5494, 117801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もふもふなところが\nわたあめちゃんに\n似ていマス。わんっ！", 1, 1, 0, 0, 2, 5496, 117801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ぬいぐるみや人形には\n魂が宿るという\n言い伝えがありマス…\nマカフシギデ〜ス", 1, 1, 0, 0, 3, 5497, 117801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ウ〜ム…\nなんとも怪しい\n香りがしマスね！", 1, 1, 0, 0, 4, 5498, 117801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "パレードの熱気に\n負けないくらい\n熱いデス！", 1, 1, 0, 0, 5, 5499, 117801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ついつい食べすぎて\nしまうのは\n妖魔のせいデスか！？", 1, 1, 0, 0, 7, 5501, 117801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "トーゴク妖怪の\n図鑑デス！　個性的な\n妖怪ばかりデスね！", 1, 1, 0, 0, 8, 5502, 117801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ハロウィンの甘味を\n集めまシタよ！\nショーグンへの\n貢ぎ物デス！", 1, 1, 0, 0, 12, 5504, 117801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "彼と二人で\n買い物に…って…\nこれってデートに\nなっちゃうのかな！？", 1, 12, 0, 0, 103, 5531, 101301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "とうっ！　どうどう？\n今の決めポーズ！\nかっこいいよね♪", 1, 12, 0, 0, 103, 5532, 101301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ヒデサイとおそろの\nハロウィンコスとか\nしてみたいな～", 1, 1, 0, 0, 1, 5533, 117901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "カボチャ料理かぁ…\n作ったらヒデサイ\n食べてくれるかな～", 1, 1, 0, 0, 2, 5534, 117901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ハロウィンスイーツ…\nどれもおいしすぎて\n激ヤバなんですけど！", 1, 1, 0, 0, 3, 5535, 117901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "霊界ってどんなとこ\nなんだろ～\nわりと近所だったり？", 1, 1, 0, 0, 4, 5536, 117901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "服をハロウィンっぽく\nアレンジするのも\nたのしーよね♪", 1, 1, 0, 0, 5, 5537, 117901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ヒデサイと一緒に\nハロウィンパーティー\n…えへへ、楽しみ～", 1, 4, 0, 0, 101, 5538, 117901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ヒデサイといると\nドキドキする…\nなんでだろ～？", 1, 8, 0, 0, 102, 5539, 117901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ねねね、うちらも\nハロウィンパレード\n見に行こー？", 1, 1, 0, 0, 1, 5540, 117901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ハロウィンセイバーズ\nしゅつどーう！\nヒデサイも行こ～♪", 1, 1, 0, 0, 2, 5541, 117901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "マントのロゴ\nかっこいいっしょ～？\nカボきちってゆーんだ", 1, 1, 0, 0, 3, 5542, 117901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "トリックオア\nトリート！\nねえねえヒデサイ\nお菓子ちょうだい♪", 1, 1, 0, 0, 4, 5543, 117901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ヒデサイも\nメイクしてみる？\nハロウィンぽいやつ♪", 1, 4, 0, 0, 101, 5544, 117901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ヒデサイのこと\nうちがいつでも\nあっためたげるね！\n…あ！　手、手の話！", 1, 8, 0, 0, 102, 5545, 117901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ハロウィンのイベント\n朝からでも\nやってるかな～？", 1, 1, 1, 0, 1, 5546, 117901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "カボチャでおやつ\n作ってみたんだ～\nヒデサイ食べてみて！", 1, 1, 2, 0, 2, 5547, 117901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ハロウィンの夜って\nやっぱテンション\n上がるよね～♪", 1, 1, 3, 0, 3, 5548, 117901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "超ふかふかしてる～\nちょっと休憩させて\nもらっちゃお", 1, 1, 0, 0, 2, 5550, 117901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はぁ…\nもにカワすぎて\nつらいんですけど～！", 1, 1, 0, 0, 3, 5551, 117901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ハロウィン限定の\nフレグランスじゃん！\nいい匂いする～♪", 1, 1, 0, 0, 4, 5552, 117901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "やばばば…\nあったかくて\n眠くなっちゃう～", 1, 1, 0, 0, 5, 5553, 117901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これ食べて\nもうひとがんばり\nしよーっと！", 1, 1, 0, 0, 7, 5555, 117901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ハロウィンっぽい\nファッションとか\n載ってないかなぁ？", 1, 1, 0, 0, 8, 5556, 117901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お菓子いっぱいだ～！\nあとでヒデサイにも\n持ってってあげよ～", 1, 1, 0, 0, 12, 5558, 117901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お掃除が終わったら\n次は書類を片付けて…\nその次は…", 1, 1, 0, 0, 1, 5560, 118501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "皆さんが冒険している\n間の留守は私に\nお任せください♪", 1, 1, 0, 0, 2, 5561, 118501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "美食殿の皆さんが\n持ってきてくれる\nお土産が楽しみです♪", 1, 1, 0, 0, 3, 5562, 118501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ネビアさんとは\nときどき一緒にお茶\nする仲なんです", 1, 1, 0, 0, 4, 5563, 118501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ギルドハウスの乱れは\nギルドの乱れ\n整理整頓が大切です", 1, 1, 0, 0, 5, 5564, 118501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "素敵なギルドハウスの\n管理人ができて\nとっても幸せです♪", 1, 4, 0, 0, 101, 5565, 118501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "皆さんをほんの少し\n特別扱いしちゃう\n私がいます", 1, 8, 0, 0, 102, 5566, 118501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "何かわからないことが\nあるときは私に\n聞いてくださいね", 1, 1, 0, 0, 1, 5567, 118501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "書類を書いていると\n落ち着きます…\n一緒にどうですか？", 1, 1, 0, 0, 2, 5568, 118501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたのまわりは\nいつも賑やかで\n楽しいですね♪", 1, 1, 0, 0, 3, 5569, 118501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あっ！　お頼みしたい\nことがあって\n探してたんです♪", 1, 1, 0, 0, 4, 5570, 118501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ギブアンドテイクは\n大切です。たまには\nお礼させてください♪", 1, 4, 0, 0, 101, 5571, 118501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "陰に日向にあなたを\n支えていきます♪\n期待していてください", 1, 8, 0, 0, 102, 5572, 118501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日も一日\n管理人として\nがんばります♪", 1, 1, 1, 0, 1, 5573, 118501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お腹がすきました…\nそろそろお昼に\nしませんか？", 1, 1, 2, 0, 2, 5574, 118501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もうこんな時間…\n皆さんといると一日が\nあっという間です", 1, 1, 3, 0, 3, 5575, 118501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "良質なお仕事は\n良質な休憩から\nです♪", 1, 1, 0, 0, 2, 5577, 118501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいいものに\n囲まれてお仕事できる\nのは幸せですね♪", 1, 1, 0, 0, 3, 5578, 118501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お仕事に集中できる\nアロマを設置するのも\nよさそうですね♪", 1, 1, 0, 0, 4, 5579, 118501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ポカポカ温まりながら\n書類仕事をするのも\nいいものですね…", 1, 1, 0, 0, 5, 5580, 118501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "栄養補給しないと\n頭が回りませんからね\nきちんと食べましょう", 1, 1, 0, 0, 7, 5582, 118501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "新条例はこのように\n活用されている、と…\nふむふむ…", 1, 1, 0, 0, 8, 5583, 118501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "会合用の資料と\n連載のネーム…\nどっちを片付けよう？", 1, 1, 0, 0, 1, 5585, 106701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "イノリちゃん、また\nお金くすねてる…\n帰ってきたら\nお尻ペンペンだ♪", 1, 1, 0, 0, 2, 5586, 106701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "カヤちゃんの装備\n傷むの早いなぁ〜\nメンテしてあげないと", 1, 1, 0, 0, 3, 5587, 106701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "そうだ、ラーゴのご飯\n買いに行かないと\n今日は何がいいかな？", 1, 1, 0, 0, 4, 5588, 106701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここの人たちは見てて\n飽きない人ばっかり♪\n漫画の参考にしよ〜", 1, 1, 0, 0, 5, 5589, 106701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "また一緒に買い物にも\n行きたいし、冒険も\nしたいな〜…♪", 1, 4, 0, 0, 101, 5590, 106701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "…君は見てるだけで\n満足なの？ ちゃんと\n触って欲しいな…♪", 1, 8, 0, 0, 102, 5591, 106701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私の目が見たいの？\nいいけど…魅了されて\n魂が壊れちゃうよ…？\n…冗談で〜す☆", 1, 1, 0, 0, 1, 5592, 106701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ラーゴがね、君に\n遊んで欲しいって☆\n仲良くしてあげてね", 1, 1, 0, 0, 2, 5593, 106701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "アシスタントの仕事\n気に入ってくれたなら\nまたお願いしていい？", 1, 1, 0, 0, 3, 5594, 106701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私が覚醒できるか？\n見せてあげたいけど…\nすごいことになるから\nまた今度で♪", 1, 1, 0, 0, 4, 5595, 106701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "楽しいトラブルが\nあったら、とりあえず\n声はかけて欲しいな♪", 1, 4, 0, 0, 101, 5596, 106701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "マッサージお願い♪\n変なところは…\n少しだけなら☆", 1, 8, 0, 0, 102, 5597, 106701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよう〜♪\n今日も一日、一緒に\nがんばっていこうね", 1, 1, 1, 0, 1, 5598, 106701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お腹空いちゃった…\n一緒にお昼行かない？\nいいお店があるんだ〜", 1, 1, 2, 0, 2, 5599, 106701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "君、眠れないの？\n誰かに見られてる？\n…きっと気のせいだよ", 1, 1, 3, 0, 3, 5600, 106701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ちょっと休憩〜…\nこのまま原稿すれば\n疲れなくなるのかな？", 1, 1, 0, 0, 2, 5602, 106701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいい〜♪\nラーゴのお友達に\nなってくれる？", 1, 1, 0, 0, 3, 5603, 106701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい香り〜♪\nこの目でどう見えるか\n伝えられたらいいのに", 1, 1, 0, 0, 4, 5604, 106701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ほどほどなのが\nちょうどいい時も\nあるよね…♪", 1, 1, 0, 0, 5, 5605, 106701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この味付け…\nあっちでも再現\nできるかな？", 1, 1, 0, 0, 7, 5607, 106701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふふっ、面白い♪\nイノリちゃんの買う\n漫画にハズレなし☆", 1, 1, 0, 0, 8, 5608, 106701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "私…23歳だもん\n魔法少女だなんて\n名乗れないわ…", 1, 1, 0, 0, 1, 5610, 119001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あん、どぅ、とろわ〜\nみんなイイ子に\nな〜る♪", 1, 1, 0, 0, 2, 5611, 119001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あっ！　イケナイ子の\n気配がするわ！\n風紀の乱れは\n先生が許しませんっ！", 1, 1, 0, 0, 3, 5612, 119001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここで闇の力が\n発動したら大変だわ！\nやだっ…そう思ったら\n緊張して…ダメぇ〜！", 1, 1, 0, 0, 4, 5613, 119001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "オイタした子はみんな\nどうしてうれしそうに\n私の愛のムチを\n欲しがるのかしら…？", 1, 1, 0, 0, 5, 5614, 119001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あの子に次は何を\n教えてあげようかしら\n愛？　希望？\nそれとも…♪", 1, 4, 0, 0, 101, 5615, 119001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あの子のことを\n考えると、愛と希望で\n胸がいっぱいになるの", 1, 8, 0, 0, 102, 5616, 119001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "先生なのに闇の力を\n持ってるのは秘密よ？\n他の子には\n知られたくないから…", 1, 1, 0, 0, 1, 5617, 119001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "秘密の授業の内容？\nそれは…あんなことや\nこんなことよ♪\nそれから、それから…", 1, 1, 0, 0, 2, 5618, 119001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "しーっ！　私の正体が\nルーセント学院の\n先生だって、ここでは\n秘密にしててね？", 1, 1, 0, 0, 3, 5619, 119001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あら？　補習して\nほしいの？　いいわよ\n君のお願いなら\nなんでも聞いちゃう♪", 1, 1, 0, 0, 4, 5620, 119001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "イイ子にはごほうびを\nあげなくちゃ！\n君はなにが欲しい？\n遠慮しちゃダメよ？", 1, 4, 0, 0, 101, 5621, 119001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "君との秘密が\nまた一つ\n増えちゃったね♪", 1, 8, 0, 0, 102, 5622, 119001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日も一日\n元気に過ごしましょう\n先生が指導してあげる", 1, 1, 1, 0, 1, 5623, 119001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜まで頑張れるように\nお昼ご飯はしっかり\n食べましょう〜！", 1, 1, 2, 0, 2, 5624, 119001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜更かしはダメよ？\nイケナイ子が\nすることです！", 1, 1, 3, 0, 3, 5625, 119001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "闇の力に振り回されて\nもうヘトヘトよ〜\n心も体も\n癒されたいわ〜", 1, 1, 0, 0, 2, 5627, 119001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "『秘密の学び舎』に\nこっそり連れて\n行っちゃおうかしら♪", 1, 1, 0, 0, 3, 5628, 119001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "イケナイ甘い香り…\nなんだか胸の奥が\n熱くなってきたわ…♪", 1, 1, 0, 0, 4, 5629, 119001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "やんっ♪\n燃え上がる愛みたい♪\n火照ってきちゃった♪", 1, 1, 0, 0, 5, 5630, 119001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "生徒の見本に\nなれるように\n好き嫌いはしないの", 1, 1, 0, 0, 7, 5632, 119001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "『学び舎に咲く禁断の\n愛』？　気になるわ…\nううん、ダメよっ！\nでも…ちょっとだけ…", 1, 1, 0, 0, 8, 5633, 119001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "次に会う時は、\nまたあそこに一緒に…\nえへへ…！", 1, 12, 0, 0, 103, 5635, 106301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "横顔見てるの、\nバレてないよね…？\n…ちらっ", 1, 12, 0, 0, 103, 5636, 106301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "背が少し伸びたか！？\nあ、あいつのところへ\n報告に行かねばっ！", 1, 12, 0, 0, 103, 5637, 105301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私も貴公の隣に\n立っていられるよう\n日々精進していくぞ！", 1, 12, 0, 0, 103, 5638, 105301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ああ～\nドＭミストが\nあふれてしまいます～", 1, 1, 0, 0, 1, 5639, 119101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "魔法少女として\nクウカ頑張ります～！\n愛と希望の妄想を…！", 1, 1, 0, 0, 2, 5640, 119101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "決めポーズの練習より\nやられポーズの練習を\nしておきます…♪", 1, 1, 0, 0, 3, 5641, 119101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "快感を溜めに溜めて\n放つドＭビームは\n最高ですよ～", 1, 1, 0, 0, 4, 5642, 119101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "はっ！\nこれは闇の気配！\n…クウカ自身でした", 1, 1, 0, 0, 5, 5643, 119101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ドＳさん…ぐふふ…\nクウカを縛りあげ…\nじゅるりぃ～♪", 1, 4, 0, 0, 101, 5644, 119101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ドＳさんに攻められる\nことで、クウカは進化\nしてゆくのです～！", 1, 8, 0, 0, 102, 5645, 119101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "はぅっ！\nもっと激しく…！\n情熱的に～～～！", 1, 1, 0, 0, 1, 5646, 119101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "クウカの変身シーンを\nじっくり眺めたい！？\nは、恥ずかしいです～", 1, 1, 0, 0, 2, 5647, 119101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "「本当に魔法少女か\n確かめてやるよ…！」\nドＳさんはそう言って\nクウカを…ぐふふ…♪", 1, 1, 0, 0, 3, 5648, 119101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "愛と希望の力が\nクウカの中に…\nこの胸の高鳴りが…？", 1, 1, 0, 0, 4, 5649, 119101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ひゃあんっ！\nそこです～っ！\nもっと、もっと～♪", 1, 4, 0, 0, 101, 5650, 119101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "クウカをこんなに\n悦ばせるのは\nドＳさんだけです…♪", 1, 8, 0, 0, 102, 5651, 119101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "明るい時間に出歩くと\n目立ちますね\n魔法少女の衣装…", 1, 1, 1, 0, 1, 5652, 119101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼の見回りです～♪\nああ～うっかりドＭを\n増やしてしまいました", 1, 1, 2, 0, 2, 5653, 119101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜！　それはクウカが\n活性化する時間です！\n闇の活動開始です～♪", 1, 1, 3, 0, 3, 5654, 119101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "気の緩んだクウカを\nドＳさんの鋭い牙が\n狙っています～♪", 1, 1, 0, 0, 2, 5656, 119101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "魔法少女のマスコット\nにいいかもしれません\nもっと鎖とか飾りを…", 1, 1, 0, 0, 3, 5657, 119101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "魅惑的な香りです～\nドＭミストの香りに\n似て…いや違いますね", 1, 1, 0, 0, 4, 5658, 119101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "火照る体…紅潮した\nクウカの肢体を熱く\n見つめるドＳさん…", 1, 1, 0, 0, 5, 5659, 119101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "食欲を満たした次は\n何を満たせば\nいいですかねぇ～♪", 1, 1, 0, 0, 7, 5661, 119101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "魔法少女がひどい目に\n遭う物語は…\nなさそうですね…", 1, 1, 0, 0, 8, 5662, 119101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ケーキも悪くないけど\nプリンが一番なの\nでもせっかくだから\n両方もらってやるの！", 1, 1, 0, 0, 1, 5664, 119901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "クリスマスって聞くと\nなんだかあったかい\n気分になるの～", 1, 1, 0, 0, 2, 5665, 119901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "煙突がなくても\nミヤコはどこからでも\nおうちに入れるの♪", 1, 1, 0, 0, 3, 5666, 119901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "イブもプリン！\nクリスマスもプリン！\nプレゼントもプリン！", 1, 1, 0, 0, 4, 5667, 119901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "クリスマスツリーの\n高いところの\n飾りつけは、ミヤコに\nまかせろなの～！", 1, 1, 0, 0, 5, 5668, 119901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "アイツのプレゼント\n楽しみなの～\nきっと最っ高に特別な\nプリンなの～♪", 1, 4, 0, 0, 101, 5669, 119901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ミヤコがサンタなら\nアイツはトナカイなの\nつまりお似合いって\nことなの～♪", 1, 8, 0, 0, 102, 5670, 119901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ミヤコ、プレゼントを\n配りにいくの～♪\nオマエも手伝えなの！", 1, 1, 0, 0, 1, 5671, 119901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "オマエにも飾りつけ\nしてやるの！\n頭にプリンを乗せると\nかわいいの♪", 1, 1, 0, 0, 2, 5672, 119901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "幽霊のミヤコが\nいるなら、サンタも\nいて当たり前なの～！", 1, 1, 0, 0, 3, 5673, 119901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "靴下にプリンを\n入れたらつぶれるの\nだからプリンは直接\nミヤコによこすの～！", 1, 1, 0, 0, 4, 5674, 119901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日はプリンだけじゃ\n満足してやらないの！\nミヤコをたくさん\n甘やかせなの！", 1, 4, 0, 0, 101, 5675, 119901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ミヤコのプリン\nオマエになら一個…\nうう～、一口だけ\nあげてもいいの！", 1, 8, 0, 0, 102, 5676, 119901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "サンタの朝は早いの～\nミヤコも早起きして\nプレゼント準備したの\nとってもえらいの♪", 1, 1, 1, 0, 1, 5677, 119901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日は一日中\nみんなと遊ぶの～！\nオマエも来いなの！", 1, 1, 2, 0, 2, 5678, 119901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "みんなが寝たら\nチャンスなの～\nこっそりプレゼント\n配りにいくの♪", 1, 1, 3, 0, 3, 5679, 119901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "むふー、気持ちいいの\nこういうプレゼントも\nいいかもしれないの", 1, 1, 0, 0, 2, 5681, 119901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これ気に入ったの～\nプリンとアイツの\n次くらいなの♪", 1, 1, 0, 0, 3, 5682, 119901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いいにおいなの～\nうっかり成仏\nしちゃいそうなの…", 1, 1, 0, 0, 4, 5683, 119901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ここに飾っといた\n雪だるま\nなくなっちゃったの\nきっと成仏したの～", 1, 1, 0, 0, 5, 5684, 119901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これはオマエがくれる\nプリンとは\n別腹なの～", 1, 1, 0, 0, 7, 5686, 119901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "プリンケーキ…\nこれならミヤコにも\n作れそうなの！\nさっそく作るの！", 1, 1, 0, 0, 8, 5687, 119901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "びっくりしたの～！\n幽霊のミヤコを\n驚かすなんて…\nなかなかやるの！", 1, 1, 0, 0, 14, 5689, 119901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "コイツの中…\nあったかいの～♪\n昼寝にぴったりなの～", 1, 1, 0, 0, 15, 5690, 119901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "メリークリスマス！\n聖夜に笑顔を\nお届けするべ♪", 1, 1, 0, 0, 1, 5691, 119201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "クリスマス前は\n乳製品がよく売れて\nありがてぇけんど…\n目が回る忙しさだ～", 1, 1, 0, 0, 2, 5692, 119201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "牧場で売る\nクリスマスリースを\n作っておかねぇと…", 1, 1, 0, 0, 3, 5693, 119201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "うちの動物たちにも\nクリスマスプレゼント\n用意しなきゃなぁ", 1, 1, 0, 0, 4, 5694, 119201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "クリスマスに向けた\n限定商品とか\n作ってみるべか？", 1, 1, 0, 0, 5, 5695, 119201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あんちゃんへの\nプレゼント選び…\nここでボケるか…？\nやめておくべか…？", 1, 4, 0, 0, 101, 5696, 119201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "オラ、あんちゃんから\n本当にたくさんの物を\nもらってんなぁ…", 1, 8, 0, 0, 102, 5697, 119201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "雪遊びがしたいべか？\nへへっ、あんちゃん\nには負けねぇど！", 1, 1, 0, 0, 1, 5698, 119201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ディナーと一緒に\n聖夜のお笑いライブを\nすんのはどうだべか？\nツッコミは任せたべ！", 1, 1, 0, 0, 2, 5699, 119201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "寒くなってくると\n鍋が食べたくなるべや\nあんちゃんはどうだ？", 1, 1, 0, 0, 3, 5700, 119201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あんちゃんは\nサンタさんのこと\n信じてるだか？", 1, 1, 0, 0, 4, 5701, 119201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どでかいクリスマス\nケーキをこさえるべ\nあんちゃんも作るの\n手伝ってくんろ♪", 1, 4, 0, 0, 101, 5702, 119201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あんちゃんが\nオラの旦那だったら…\nって、あんちゃん！\n今の聞いてただか！？", 1, 8, 0, 0, 102, 5703, 119201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "牧場の朝は早いど！\nさぁてと、まずは\n雪かきしに行くべや", 1, 1, 1, 0, 1, 5704, 119201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あんちゃんも一緒に\nお昼食べてくか？\n採れたての冬野菜は\nうんまいど～♪", 1, 1, 2, 0, 2, 5705, 119201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "よい子は早く寝ねぇと\nサンタさんが\n来てくれねぇべよ？", 1, 1, 3, 0, 3, 5706, 119201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こういうのリンリンが\n好きそうだな…\nプレゼント候補に\n入れておくべ！", 1, 1, 0, 0, 2, 5708, 119201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もふもふしてて\nなまらめんこくて…\nあぁ…癒されるべや…", 1, 1, 0, 0, 3, 5709, 119201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この匂い…\n気持ちが和むべや\n夢心地になっちまうな", 1, 1, 0, 0, 4, 5710, 119201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったけぇところは\n落ち着くなぁ…\nつい眠くなっちまう", 1, 1, 0, 0, 5, 5711, 119201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こ、この味は…！\n…うーん、もっと\nリアクション芸も\n練習しねぇとな", 1, 1, 0, 0, 7, 5713, 119201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "シオシオって\nどんな本読んでんだ？\n…難しい字が\nぎっしりだべや…", 1, 1, 0, 0, 8, 5714, 119201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この仕掛け…\nオラのコントに\n活かせるかも\nしんねぇな", 1, 1, 0, 0, 14, 5716, 119201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この着ぐるみで\nリマリマのモノマネを\n練習してみるべか…？", 1, 1, 0, 0, 15, 5717, 119201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "もっとキレイになって\nアイツにぎゃふんと\n言わせてやるんだから", 1, 12, 0, 0, 103, 5718, 105001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "アタシのこと\nどう思う？\nかわいいって…そこは\nキレイって言ってよね", 1, 12, 0, 0, 103, 5719, 105001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ハートマークの刺繍…\nは、やりすぎですかね\nいやちょっとくらい\nアピールしてみても…", 1, 1, 0, 0, 1, 5720, 119301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "雪降らないかな～\nそしたらお兄ちゃんと\n雪だるま作ったり\nできるのに～", 1, 1, 0, 0, 2, 5721, 119301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "プレゼントを入れる\n靴下…いくつ編んでも\n足りません～！", 1, 1, 0, 0, 3, 5722, 119301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お兄ちゃんとの\nクリスマスデートは\n早い者勝ちですからね\n負けませんよ～！", 1, 1, 0, 0, 4, 5723, 119301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "クリスマスだし…\n受験勉強は今日だけ\nお休みです♪", 1, 1, 0, 0, 5, 5724, 119301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お兄ちゃんと\nおそろいのコーデで\nクリスマスデート…\nしたいなぁ～！", 1, 4, 0, 0, 101, 5725, 119301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お兄ちゃんに\nプレゼントする編み物\nこれで３着目～と♪\nあれ、作りすぎ？", 1, 8, 0, 0, 102, 5726, 119301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "えへへ、もっと近くで\n見たくありませんか？\n私のとっておきの\nクリスマスコーデ♪", 1, 1, 0, 0, 1, 5727, 119301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お兄ちゃんが風邪を\n引いたら一大事です！\n私の編み物で暖かく\nしてあげます♪", 1, 1, 0, 0, 2, 5728, 119301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お兄ちゃん、ほっぺに\nケーキのクリームが\nついちゃってますよ♪\n私がとってあげます！", 1, 1, 0, 0, 3, 5729, 119301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お兄ちゃん！\nケーキ、あーんって\nしてあげます！\nほら、あーん♪", 1, 1, 0, 0, 4, 5730, 119301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "クリスマスは大切な\n人と過ごすものっ！\nお兄ちゃん…妹の予定\n空いてますよっ！", 1, 4, 0, 0, 101, 5731, 119301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "クリスマスはずっと、\nお兄ちゃんと一緒に\nいたい…って言ったら\n欲張り…すぎですか？", 1, 8, 0, 0, 102, 5732, 119301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "早起きは３本お得！\n…クリスマスなら\n追加で10本ぐらい\nもらえちゃうかも！？", 1, 1, 1, 0, 1, 5733, 119301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼はケーキ？\nそれとも七面鳥？\nごちそういっぱいで\n迷っちゃいますね～♪", 1, 1, 2, 0, 2, 5734, 119301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お兄ちゃんと\nクリスマスに夜更かし\n…えへへ、ちょっぴり\nドキドキしちゃう♪", 1, 1, 3, 0, 3, 5735, 119301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "編み物をするには\n絶好のくつろぎ空間\nですね～♪", 1, 1, 0, 0, 2, 5737, 119301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "なんというかわいさ…\nクリスマス\nプレゼントに\nもってこいですね！", 1, 1, 0, 0, 3, 5738, 119301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これがお兄ちゃんの\nお部屋の香り…？\nんん～！　ずっと\nかいでいたいです～♪", 1, 1, 0, 0, 4, 5739, 119301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふぁ、あったか～い…\n冬場はこういうのが\nありがたいですね～♪", 1, 1, 0, 0, 5, 5740, 119301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ごちそうだぁ～♪\nほら、お兄ちゃんも\n食べて食べてっ！", 1, 1, 0, 0, 7, 5742, 119301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "クリスマスデートの\n必勝法…こ、これは\n読むしかないです！", 1, 1, 0, 0, 8, 5743, 119301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "わっ、すごいです～！\nこれを飾っておけば\nクリスマスが一層\n賑やかになりますね♪", 1, 1, 0, 0, 14, 5745, 119301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "さぷらーいずっ！\nリマちゃんじゃなくて\nリノちゃんでした～！", 1, 1, 0, 0, 15, 5746, 119301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ミミにもミソギにも\nあの人にも頼りに\nしてもらえるように\nしっかりしなきゃ！", 1, 1, 0, 0, 1, 5747, 120601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ミミはにんじんを\n食べられてすごいな…\n私も見習わなきゃ\nがんばろう…！", 1, 1, 0, 0, 2, 5748, 120601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ミソギから怖い話\n聞いちゃった…\nうぅ…誰か一緒に\nいてくれないかな", 1, 1, 0, 0, 3, 5749, 120601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "立派な大人に\nなれるように\nいっぱい勉強\nしておかなくっちゃ！", 1, 1, 0, 0, 4, 5750, 120601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "なんだか…あの人の\nあやしい視線を\n感じるような…？", 1, 1, 0, 0, 5, 5751, 120601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あの人に勉強を\n教えられるように\nしっかり予習を\nしておこうっと…！", 1, 4, 0, 0, 101, 5752, 120601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今日はあの人と\n一緒に冒険できる…\nなんだかワクワク\nしてきちゃった…！", 1, 8, 0, 0, 102, 5753, 120601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あ、いいとこに来て\nくれましたね！\nあなたに頼みたい\nことがあるんです！", 1, 1, 0, 0, 1, 5754, 120601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "きゃあっ！？\nへ、変なところを\n触らないでください！", 1, 1, 0, 0, 2, 5755, 120601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どうですかこの格好？\nかわいい、ですか…？\nそ、そうですか…\nありがとうございます", 1, 1, 0, 0, 3, 5756, 120601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ひゃあっ…！\nすみません、勉強に\n集中してたので\n驚いちゃいました", 1, 1, 0, 0, 4, 5757, 120601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "一緒に勉強して立派な\n大人になりましょうね\n私がそばで見張ってて\nあげます", 1, 4, 0, 0, 101, 5758, 120601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "まだ立派ではないかも\nですけど、私よりも\n大人なあなたのことは\n頼りにしてますからね", 1, 8, 0, 0, 102, 5759, 120601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "立派な大人は\n朝の時間の使い方が\n上手だと聞きました\nあなたはどうですか？", 1, 1, 1, 0, 1, 5760, 120601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ミミとミソギと\nあなたと…大事な人と\n一緒のお昼ごはんは\nやさしい味がしますね", 1, 1, 2, 0, 2, 5761, 120601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "うぅ…怖い話を\n思い出しちゃいました\n寝るまで一緒に\nいてくれますか…？", 1, 1, 3, 0, 3, 5762, 120601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "すごい座り心地…！\nとっても勉強が\nはかどりそう…！", 1, 1, 0, 0, 2, 5764, 120601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この子…なんだか\nミミに似てるような\n気がする…ふふっ♪", 1, 1, 0, 0, 3, 5765, 120601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい香り…♪\n勉強で疲れた\n頭がリフレッシュ\nできそうです", 1, 1, 0, 0, 4, 5766, 120601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ぽかぽかだ…♪\nミミとミソギと\nあの人と一緒に\nいるときみたい", 1, 1, 0, 0, 5, 5767, 120601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いっぱい食べたら\n早く大人になれたり\nしないかな…？", 1, 1, 0, 0, 7, 5769, 120601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あう…\n知らない単語が…\nちゃんと調べて\n覚えなくっちゃ…！", 1, 1, 0, 0, 8, 5770, 120601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "うさぎさんが\nぴょ～んぴょん♪\nみんなえがおで\nるんたった～♪", 1, 1, 0, 0, 1, 5773, 120401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "キョウカちゃんも\nいつかにんじんさんを\n好きになってくれたら\nうれしいな～♪", 1, 1, 0, 0, 2, 5774, 120401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今日のミソギちゃんの\nイタズラ、びっくり\nしちゃったけど\nおもしろかったな～♪", 1, 1, 0, 0, 3, 5775, 120401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "このおうち、いつも\nいろんな人がいて\nドキドキぴょんぴょん\nって楽しいな～♪", 1, 1, 0, 0, 4, 5776, 120401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "いいお天気～♪\nお外でみんなと\nいっぱい遊びたいな♪", 1, 1, 0, 0, 5, 5777, 120401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ミソギちゃんも\nキョウカちゃんも\nおにいちゃんがいると\nずっと笑顔だね～♪", 1, 4, 0, 0, 101, 5778, 120401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "おにいちゃんにはやく\nあいたいな～♪\nまだかな、まだかな♪", 1, 8, 0, 0, 102, 5779, 120401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "えへへ♪\nおにいちゃんに\nなでなでされると\nぽかぽかするね♪", 1, 1, 0, 0, 1, 5780, 120401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "聞いて、おにいちゃん\nさっきね、かわいい\nちょうちょさんに\n会ったんだよ～♪", 1, 1, 0, 0, 2, 5781, 120401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふわっ…！\nおにいちゃんの顔を\nお絵かきしてたから\nびっくりしちゃった♪", 1, 1, 0, 0, 3, 5782, 120401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おにいちゃんも\n遊びたいの？\nそれじゃあ二人も\n呼んで遊ぼう♪", 1, 1, 0, 0, 4, 5783, 120401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おにいちゃんも\nみんなも、優しい人\nばっかりで\nミミ幸せ～♪", 1, 4, 0, 0, 101, 5784, 120401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おにいちゃん♪\n高い高いしてほしいな\nすっごくぴょんぴょん\nできて、好きなんだ♪", 1, 8, 0, 0, 102, 5785, 120401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよう～♪\nおにいちゃん、今日は\nおひさまみたいに\nきらきら元気だね～♪", 1, 1, 1, 0, 1, 5786, 120401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おひるごはんだね～♪\nにんじんさんを\nいっぱい食べて\n元気いっぱい♪", 1, 1, 2, 0, 2, 5787, 120401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふわあ…\n眠くなってきちゃった\nおにいちゃん、\n一緒に寝よう～？", 1, 1, 3, 0, 3, 5788, 120401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふわぁ、気持ちいい♪\nずっとこうやって\n座ってたくなるね～♪", 1, 1, 0, 0, 2, 5790, 120401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "わあ、かわいい～\nぎゅ～っ、ぎゅ～っ♪\nこれで仲よしだね\nえへへ♪", 1, 1, 0, 0, 3, 5791, 120401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "気持ちいいにおい～♪\nぴょんぴょんしたく\nなってきちゃった♪", 1, 1, 0, 0, 4, 5792, 120401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったかぽかぽか♪\nおにいちゃんも\nきてくれたら、もっと\nあったかくなりそう♪", 1, 1, 0, 0, 5, 5793, 120401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もぐもぐ…ごっくん♪\nおっきくなりたいから\nいっぱい食べるよ～♪", 1, 1, 0, 0, 7, 5795, 120401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "えへへ、おもしろ～い\nこの絵本ね、\nキョウカちゃんから\n教えてもらったんだ♪", 1, 1, 0, 0, 8, 5796, 120401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この部屋…探索の\nしがいがありそう！\nついでにイタズラも\nしかけちゃお♪", 1, 1, 0, 0, 1, 5799, 120501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ミミってああ見えて\n実は大胆で度胸も\nあるよね…ちょっと\nかっこいいな…！", 1, 1, 0, 0, 2, 5800, 120501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "怖い話を聞いた時の\nキョウカって\n面白いんだよね～♪", 1, 1, 0, 0, 3, 5801, 120501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "にいちゃんが\n慣れちゃわないように\nまた新しいイタズラを\n考えなきゃ！", 1, 1, 0, 0, 4, 5802, 120501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "イタズラもいいけど\nミミとキョウカと\nにいちゃんと、普通に\n遊ぶのも楽しいなぁ♪", 1, 1, 0, 0, 5, 5803, 120501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ミミにキョウカ、\nそれににいちゃん！\nみんなそろえば愉快な\n冒険が始まりそう！", 1, 4, 0, 0, 101, 5804, 120501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "次はどんなイタズラを\nしたら、にいちゃんは\n喜んでくれるかな？", 1, 8, 0, 0, 102, 5805, 120501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今ミソギに触ったね？\nヤバいよにいちゃん…\n逃げないとトラップが\n発動しちゃうよ…！", 1, 1, 0, 0, 1, 5806, 120501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "わわっ！？　びっくり\nしたあ…！　新しい\nトラップを作ってて\n気づかなかったよ～", 1, 1, 0, 0, 2, 5807, 120501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お？　にいちゃん、\nイタズラする側にも\n興味があるの？", 1, 1, 0, 0, 3, 5808, 120501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "にいちゃんなにか用？\nあ、もしかして…\nミソギと遊びたく\nなっちゃった？", 1, 1, 0, 0, 4, 5809, 120501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ベストタイミングだよ\nにいちゃん！\n今からみんなで探検に\n行くところなんだ！", 1, 4, 0, 0, 101, 5810, 120501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "にいちゃんってさ\nホントに優しいよね\nなにしても怒らないし\n…ありがとね♪", 1, 8, 0, 0, 102, 5811, 120501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "にいちゃんおはよー！\n景気づけに本日\n一発目のイタズラ、\nやっちゃうよ～♪", 1, 1, 1, 0, 1, 5812, 120501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼ごはんはロシアン\nサンドイッチだよ！\nさあ、にいちゃん！\n好きなの選んでよ！", 1, 1, 2, 0, 2, 5813, 120501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜ってなんだか\nワクワクしてなかなか\n眠れないんだよね\nにいちゃんはどう？", 1, 1, 3, 0, 3, 5814, 120501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この座り心地のよさ…\nなにかのトラップに\nうまく使えないかな？", 1, 1, 0, 0, 2, 5816, 120501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こういうのに細工して\nミミやキョウカを\n驚かせると、いい反応\nするんだよね♪", 1, 1, 0, 0, 3, 5817, 120501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "においをうまく使った\nイタズラって…\nなにかないかな？", 1, 1, 0, 0, 4, 5818, 120501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったまりながら\n新しいイタズラを\n考えると、いい案が\n出て来たりするんだ♪", 1, 1, 0, 0, 5, 5819, 120501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "おいしくてついつい\n食べ過ぎちゃうなぁ…\nはっ…！？　これって\nまさかトラップ…！？", 1, 1, 0, 0, 7, 5821, 120501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "このキョウカから\n借りた冒険物語\nすっごく\nおもしろいんだよね！", 1, 1, 0, 0, 8, 5822, 120501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "晴れ着を着ると\nなんだか心が躍るね\n足取りも軽く\nなるってもんさ", 1, 1, 0, 0, 1, 5825, 120801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "正月の食べ物ってのは\n美味いもんだね\nいくらでも\n食べれちまうよ", 1, 1, 0, 0, 2, 5826, 120801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "普段は好き勝手してる\nアタシらも\n正月くらいは\n大人しくするさ", 1, 1, 0, 0, 3, 5827, 120801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "年越しそばに雑煮\nこの季節は\n温かいものが\n体に沁みるね", 1, 1, 0, 0, 4, 5828, 120801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "正月だからって\nのんびりしすぎたね\nちょっと釣りにでも\n行こうかね", 1, 1, 0, 0, 5, 5829, 120801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "いい正月だね\nいつかあの子と\n一杯やりたいもんだよ", 1, 4, 0, 0, 101, 5830, 120801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "大事な人と過ごす\n正月ってのがこんなに\n楽しいもんだとはね", 1, 8, 0, 0, 102, 5831, 120801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "部屋があったかくて\n顔が火照っちまったよ\nちょいと外に涼みに\n行かないか？", 1, 1, 0, 0, 1, 5832, 120801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "餅ってのは\n何をつけてもうまいね\nお前さんは\nどの味が好きだい？", 1, 1, 0, 0, 2, 5833, 120801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お祝いの鯛が\n足りないって？\nそれじゃあアタシが\n釣ってきてやるよ", 1, 1, 0, 0, 3, 5834, 120801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おせちが美味いのは\n分かるけど\n食べ過ぎには\n気を付けるんだよ", 1, 1, 0, 0, 4, 5835, 120801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お前さんのおかげで\nいい正月を\n迎えられたよ\nありがとさん！", 1, 4, 0, 0, 101, 5836, 120801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "慣れない晴れ着も\nお前さんに見て\nもらえたなら\n着たかいがあったね", 1, 8, 0, 0, 102, 5837, 120801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようさん\n寒いのは分かるけど\nいい加減布団から\n出て顔洗ってきな", 1, 1, 1, 0, 1, 5838, 120801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日の昼飯は雑煮だ\nアタシの郷土の味\nしっかり味わって\nおくれよ", 1, 1, 2, 0, 2, 5839, 120801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おや、お前さんも\n夜ふかしかい？\n退屈してたところさ\n付き合っておくれよ", 1, 1, 3, 0, 3, 5840, 120801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "晴れ着だと座り方にも\n気を遣うね\nしわになったら大変さ", 1, 1, 0, 0, 2, 5842, 120801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "せっかくだし\nこの子にも晴れ着を\n着せてあげたいね", 1, 1, 0, 0, 3, 5843, 120801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これは冬の花の香り…\n風情があって\nいいじゃないか", 1, 1, 0, 0, 4, 5844, 120801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったかいねぇ…\nこんな寒い日には\nまさに天国だよ", 1, 1, 0, 0, 5, 5845, 120801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いつも食べてる\nものでも正月に\n食べると\n新鮮な気がするね", 1, 1, 0, 0, 7, 5847, 120801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "せっかくの正月休みだ\nたまにはゆっくりと\n本でも読もうかね", 1, 1, 0, 0, 8, 5848, 120801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "新年のあいさつ…\n私も街のみんなに\nしてみようかしら…", 1, 1, 0, 0, 1, 5850, 120701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "正月はゆっくり過ごす\nのよね…ゆっくりって\n何したらいいのかしら", 1, 1, 0, 0, 2, 5851, 120701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この格好も\n慣れてくれば\n動きやすいわ", 1, 1, 0, 0, 3, 5852, 120701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "う～ん、今年の抱負…\nやりたいことが\nたくさんあるのよね", 1, 1, 0, 0, 4, 5853, 120701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "おせち料理\nネビアの分も\n用意しなくちゃね", 1, 1, 0, 0, 5, 5854, 120701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ふふっ\n正月っていいわね\nみんな楽しそうだもの", 1, 4, 0, 0, 101, 5855, 120701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "新年の思い出…\nこれからもっと作るわ\nそれが私の抱負よ", 1, 8, 0, 0, 102, 5856, 120701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日は家で過ごすの？\nあなたと一緒なら\nきっと楽しいわね", 1, 1, 0, 0, 1, 5857, 120701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どう？　この格好？\nコッコロさんは\n褒めてくれたけど…", 1, 1, 0, 0, 2, 5858, 120701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ちょうど良かったわ\nあなたも大掃除\n手伝ってくれる？", 1, 1, 0, 0, 3, 5859, 120701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "これから料理するけど\nお雑煮と焼き餅\nどっちが好きかしら？", 1, 1, 0, 0, 4, 5860, 120701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "『今の私』にとって\n初めての正月だもの\nあなたと楽しまないと", 1, 4, 0, 0, 101, 5861, 120701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "去年よりも今年…\nもっと成長するからね\n…お兄ちゃん♪", 1, 8, 0, 0, 102, 5862, 120701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよう\nせっかくの正月だもの\n朝から楽しまないとね", 1, 1, 1, 0, 1, 5863, 120701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ねぇ…\n良かったら正月の遊び\n教えてくれるかしら？", 1, 1, 2, 0, 2, 5864, 120701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふわぁ～…今日が\n終わっちゃうのが…\nもったいないわね…", 1, 1, 3, 0, 3, 5865, 120701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふぅ…お参り後だから\n心地よすぎて眠って\nしまいそうだわ…", 1, 1, 0, 0, 2, 5867, 120701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふふっ\nかわいいものって\n癒されるわね…", 1, 1, 0, 0, 3, 5868, 120701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "…ん\nこれが梅の香り？\n…いい匂いね", 1, 1, 0, 0, 4, 5869, 120701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "キャルじゃないけど…\nこのまま丸く\nなりたいところね…", 1, 1, 0, 0, 5, 5870, 120701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はむはむっ…♪\nせっかくの正月だもの\n堪能しなくちゃね♪", 1, 1, 0, 0, 7, 5872, 120701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "…ふんふん\n帯の結び方って\nこんなにあるのね…", 1, 1, 0, 0, 8, 5873, 120701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今の私が作れる\n最高の一曲…\nあの人に届くといいな", 1, 12, 0, 0, 103, 5875, 104201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あの、歌を…\n聞いてくれませんか？\nあなたに贈る\n特別な歌を", 1, 12, 0, 0, 103, 5876, 104201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ひい、ふう…\nむむ、みなに配る\nお年玉のポチ袋が\n残りわずかなのじゃ…", 1, 1, 0, 0, 1, 5877, 120901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "宴の準備はわらわに\n任せておくのじゃ！\n支配者の手腕、\n見せつけてくれる！", 1, 1, 0, 0, 2, 5878, 120901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "新年の願掛けなら\nわらわにすればよい\n支配者に頼むほうが\n確実じゃろう？", 1, 1, 0, 0, 3, 5879, 120901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "みなからもらった\nこの年賀状とやら…\nわらわも\n書いてみようかの♪", 1, 1, 0, 0, 4, 5880, 120901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "正月飾りにもいろいろ\n種類があるんじゃの～\nどれも華々しくて\nわらわ好みじゃ♪", 1, 1, 0, 0, 5, 5881, 120901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あやつへのお年玉は\nちと多めにしておくか\n…日ごろの感謝も\n込めてのう", 1, 4, 0, 0, 101, 5882, 120901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あやつのことを想うと\nそれだけで行く年\n来る年が楽しみになる\nふふ、愉快なやつじゃ", 1, 8, 0, 0, 102, 5883, 120901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "みなに晴れ着を\n褒められて、とても\nよい気分なのじゃ♪\n次はお主の番じゃぞ？", 1, 1, 0, 0, 1, 5884, 120901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お主へのお年玉は\nいちばん最後じゃ！\n残り物には福が宿る\nそうじゃからのう", 1, 1, 0, 0, 2, 5885, 120901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この季節は手が\nかじかむのう…ほれ、\nお主！　わらわの手を\nあたためるのじゃ！", 1, 1, 0, 0, 3, 5886, 120901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お主のために雑煮を\n手作りしてみたのじゃ\n食べていくがよい♪", 1, 1, 0, 0, 4, 5887, 120901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "眷属のお主に\n正月休みはまだ早い！\nずっとわらわのそばに\n控えておくのじゃ♪", 1, 4, 0, 0, 101, 5888, 120901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふふ、これほど楽しい\n正月は初めてじゃ…\nお主と過ごす時間は\nいつだって特別じゃな", 1, 8, 0, 0, 102, 5889, 120901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "早起きしてわらわを\n待っておったのか？\nさすがはわらわの眷属\n殊勝な心がけじゃな♪", 1, 1, 1, 0, 1, 5890, 120901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "食事どきじゃな\n正月の宴会料理が\nわらわたちを\n待っておるぞ♪", 1, 1, 2, 0, 2, 5891, 120901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜も更けてきたのう\n冬の夜は長い…\n心ゆくまでお主と\n過ごせるわけじゃな♪", 1, 1, 3, 0, 3, 5892, 120901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "実に心地がよい♪\nこのまま寝正月も\n悪くないかも\nしれぬのう", 1, 1, 0, 0, 2, 5894, 120901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "愛いやつじゃのう\nわらわのそばに置いて\nやってもよいぞ♪", 1, 1, 0, 0, 3, 5895, 120901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "品のある香りじゃのう\n寝室に置いておけば\nなにかご利益に\nあずかれそうじゃな", 1, 1, 0, 0, 4, 5896, 120901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "心地よい…\nわらわはしばらく\nここから動かぬぞ\nお主も付き合うがよい", 1, 1, 0, 0, 5, 5897, 120901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "舌鼓が鳴るのう♪\n正月の宴には\n持ってこいの品じゃ！", 1, 1, 0, 0, 7, 5899, 120901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ほう、新春にはそんな\n習わしが…正月を\n制するには、まだまだ\n勉強が必要じゃのう", 1, 1, 0, 0, 8, 5900, 120901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "久しぶりの我が家って\n感じです！　やっぱり\nここが一番ですね〜！", 1, 1, 0, 0, 1, 5902, 121001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "さあ食べましょう！\nみんなで囲める食卓を\n心から味わいますよ☆", 1, 1, 0, 0, 2, 5903, 121001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ランドソルに\n帰ってきた途端に\nお腹が空きました〜！", 1, 1, 0, 0, 3, 5904, 121001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ただいま☆\n幸せ特盛りの\nわたしの日常！", 1, 1, 0, 0, 4, 5905, 121001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ようやくランドソルに\n帰ってきたことですし\nやっぱり魔物料理を\nいただきましょう☆", 1, 1, 0, 0, 5, 5906, 121001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あの人との絆を\n守るために\nもっともっと食べて\n強くならなくちゃ☆", 1, 4, 0, 0, 101, 5907, 121001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "いつもありがとうって\nあの人を思いっきり\n抱きしめたいです☆", 1, 8, 0, 0, 102, 5908, 121001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたの想いは\nこの身をかけて\n全力で受け止めます！", 1, 1, 0, 0, 1, 5909, 121001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おかえりなさい☆\nここにいるみんなも\nあなたの帰りをずっと\n待っていましたよ！", 1, 1, 0, 0, 2, 5910, 121001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "このギルドハウスで\n冒険の疲れを\n癒しましょう〜！", 1, 1, 0, 0, 3, 5911, 121001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたの想いが\n全力全開を超える\n力をくれました！", 1, 1, 0, 0, 4, 5912, 121001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "日常が続く幸せを\nあなたと一緒に\nかみしめたいです☆", 1, 4, 0, 0, 101, 5913, 121001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ぎゅ〜っ！\nわたしたちは\nどんなことがあっても\nずっと一緒ですよ？", 1, 8, 0, 0, 102, 5914, 121001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようございます！\nあなたと過ごせる\n幸せを、今日も\n味わいますよ〜！", 1, 1, 1, 0, 1, 5915, 121001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ご飯の時間ですよー！\nあなたも一緒に\n食べましょうね！", 1, 1, 2, 0, 2, 5916, 121001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "明日もあなたが隣に\nいてくれることを\n心から願ってますよ\nおやすみなさい☆", 1, 1, 3, 0, 3, 5917, 121001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふい〜…\n安心できる場所で\nくつろげるって\n幸せですね〜…", 1, 1, 0, 0, 2, 5919, 121001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "じゅるり…\n食べたくなっちゃう\nくらい、かわいい☆", 1, 1, 0, 0, 3, 5920, 121001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "くんくん…おおー！\nステーキの香り！\nやばいですね☆", 1, 1, 0, 0, 4, 5921, 121001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "体がぽかぽかしてきて\nわたしの食欲も\n燃えてきましたよー！", 1, 1, 0, 0, 5, 5922, 121001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "わたしの食欲に\nついてこられますか？\nまだまだ食べますよ！", 1, 1, 0, 0, 7, 5924, 121001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これがキャルちゃんの\n言っていた、キスで\n目覚める王子様の\nおとぎ話ですか〜☆", 1, 1, 0, 0, 8, 5925, 121001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "自分で自分を\n分析するのも、これは\nこれで面白い…♪", 1, 1, 0, 0, 1, 5927, 121201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "強過ぎる力の扱いには\n注意しないと…\n七冠がいい例だね", 1, 1, 0, 0, 2, 5928, 121201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "少年の交友関係は\n計り知れないな…\nアタシも含むのか！", 1, 1, 0, 0, 3, 5929, 121201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "原理を徹底解明して\n再発を防ぐぞー！\n…誰か手伝って～！", 1, 1, 0, 0, 4, 5930, 121201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "これは祝福にも\n呪いにもなる…\n忘れずにいよう", 1, 1, 0, 0, 5, 5931, 121201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "少年が\n目を覚ましてくれて\n本当によかった…", 1, 4, 0, 0, 101, 5932, 121201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "彼以外を選んでいたら\n今回の事態は\n乗り切れなかったな…\nさすがだよ、少年", 1, 8, 0, 0, 102, 5933, 121201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "少年！　調子はどう？\nクレープ食べられる？\nっていうか食べて？", 1, 1, 0, 0, 1, 5934, 121201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "元気そうで何よりだ\nまた店を手伝いに\nきてよ。二人もキミに\n会いたがってたしさ", 1, 1, 0, 0, 2, 5935, 121201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "キミの診察もあるし\n今はなるべくそばに\nいるよ。迷惑じゃ\nなければだけど", 1, 1, 0, 0, 3, 5936, 121201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "やっぱり少年は\nあちこちで人を\n助けてる時が\n一番輝いてるよ", 1, 1, 0, 0, 4, 5937, 121201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "アタシは大したこと\nしてないけど\nみんなのことは\n褒めてあげてね", 1, 4, 0, 0, 101, 5938, 121201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "みんなを笑顔に\nできるのは\nやっぱり少年だね", 1, 8, 0, 0, 102, 5939, 121201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "やあ少年\n朝寝坊はしてない？\nそれじゃ、よい１日を", 1, 1, 1, 0, 1, 5940, 121201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おや、少年！\nまかないのクレープ\n作ったけど、食べる？", 1, 1, 2, 0, 2, 5941, 121201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふわぁ…もう夜か…\n研究してたら\nあっという間だね…\nおやすみ、少年", 1, 1, 3, 0, 3, 5942, 121201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "体がなまってたのか\n前線に出ると疲れる…\nまさか…年のせい！？", 1, 1, 0, 0, 2, 5944, 121201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "キミたちも今の\nアタシと同じ感じに\nアレンジしてあげよう", 1, 1, 0, 0, 3, 5945, 121201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "誰が置いたんだろう？\nいい香りだねぇ…\nうん、気に入った♪", 1, 1, 0, 0, 4, 5946, 121201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "何も考えてない時間も\n実は思考にとって\n大事なんだよね～…", 1, 1, 0, 0, 5, 5947, 121201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "食の大切さは\nいつの食事からも\n感じられるね♪", 1, 1, 0, 0, 7, 5949, 121201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふむ…面白い！\n今度全巻\n借りにこようっと", 1, 1, 0, 0, 8, 5950, 121201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "聖学祭か…\n良い記憶はないな\nよし忘れよう", 1, 1, 0, 0, 1, 5952, 116401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "健全なる精神は\n健全なる肉体に宿る…\nはっ、知ったことかよ", 1, 1, 0, 0, 2, 5953, 116401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "暇だし鏡でも見るか…\nえへへ、ユニだよ♪\nよし子供料金いけるな", 1, 1, 0, 0, 3, 5954, 116401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "アオイ君は元気に\nしているだろうか…\n彼女は人一倍ぼくに\n懐いていたからな…", 1, 1, 0, 0, 4, 5955, 116401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "そろそろシズル君は\n姉という幻想から\n目覚めただろうか…", 1, 1, 0, 0, 5, 5956, 116401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ぼくは来年も聖学祭に\n行くさ…卒業しても\n君らとは永遠だ", 1, 4, 0, 0, 101, 5957, 116401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "最近同志の登校率が\n減少傾向だな…\nまあ仕方ないのだが…\nないのだが…", 1, 8, 0, 0, 102, 5958, 116401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ほわあああ！\nおい急に話しかけるな\n学びがこぼれる", 1, 1, 0, 0, 1, 5959, 116401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この体操着、周囲から\n大層評判が良いのだよ\nたいそうだけにな\nどやっ！", 1, 1, 0, 0, 2, 5960, 116401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "何を着ていようと\nぼくはぼくさ\n外見など所詮は\n魂の識別符に過ぎん", 1, 1, 0, 0, 3, 5961, 116401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "救護院のアヤネ君が\n最近ぼくの自称年齢に\n疑念を持ち始めてな…\nあの熊…殺るか…", 1, 1, 0, 0, 4, 5962, 116401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "うん？　なんだね？\nなに、用は無い？\n用も無いのに呼ぶとは\nもしや…これが友達！", 1, 4, 0, 0, 101, 5963, 116401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この服が似合ってる？\nな、なんだね突然…\nやめたまえよでゅふふ", 1, 8, 0, 0, 102, 5964, 116401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふぁ～あ…\n朝も更けてきたな…\nそろそろ寝るか…", 1, 1, 1, 0, 1, 5965, 116401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふぁ～あ…もう昼だ…\n人間の起きている\n時間ではないな…\n早く寝なくては…", 1, 1, 2, 0, 2, 5966, 116401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "うーん、よく寝た！\n気持ちのいい夜だな\n今日も一日がんばるぞ", 1, 1, 3, 0, 3, 5967, 116401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ほわぁ～…\nもしやこれは都で噂の\n人を駄目にするやつ…", 1, 1, 0, 0, 2, 5969, 116401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふむ、愛くるしいな\nだが似合い過ぎる…\nぼくが持つと我ながら\n子供にしか見えん", 1, 1, 0, 0, 3, 5970, 116401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "アロマか、興味深いな\nミツキ印ポーションも\n気化させて鼻腔から\n吸入してみるか…", 1, 1, 0, 0, 4, 5971, 116401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "んふ、んふふ…\n火ってイイよね…\nずっと見てられるね…", 1, 1, 0, 0, 5, 5972, 116401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "や、やめろ…\nとめどなく固形食物を\n生産するのはやめろ…", 1, 1, 0, 0, 7, 5974, 116401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この可愛げのない筆致\nやはり作者は理系か！\nぐああ目が理系になる", 1, 1, 0, 0, 8, 5975, 116401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "…あいつのためなら\n無理くらいいくらでも\nしてやるわよ", 1, 1, 0, 0, 1, 5977, 121101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この姿、\n魔力の制御ムズ過ぎ…\nま、あたしは優秀だし\nどうとでもなるけど！", 1, 1, 0, 0, 2, 5978, 121101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あいつのこと、\n全部受け入れるって\n決めたから！", 1, 1, 0, 0, 3, 5979, 121101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今なら誰だろうと\n負ける気がしないわ\nドラゴンでもなんでも\nかかってこいっての！", 1, 1, 0, 0, 4, 5980, 121101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "散々助けられた分、\n今度はあたしが\nあいつのこと助けるわ", 1, 1, 0, 0, 5, 5981, 121101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あいつとの\nこれまでの冒険は\nどれも、あたしの\n大切な思い出…", 1, 4, 0, 0, 101, 5982, 121101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "もしまたあいつが\n寝たままになったら\nなんだってするわ\nキ…キスでも…", 1, 8, 0, 0, 102, 5983, 121101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なに、お腹空いたの？\nなら、おいしいご飯を\n食べに行きましょ♪", 1, 1, 0, 0, 1, 5984, 121101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "やっぱ、\nあんたのアホ面見ると\n安心するわ♪", 1, 1, 0, 0, 2, 5985, 121101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "【美食殿】はあんたと\nぺコリーヌがいなきゃ\n始まんないっての", 1, 1, 0, 0, 3, 5986, 121101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ひゃあっ！？\n急に声かけないで！？\n魔力が暴発しても\n知らないわよ！", 1, 1, 0, 0, 4, 5987, 121101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "…あんたとこうして\nぼーっと過ごす時間、\nまあ…嫌いじゃないわ", 1, 4, 0, 0, 101, 5988, 121101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あんたが信じて\n見守っててくれれば…\nあたし、\nなんだってできるわ", 1, 8, 0, 0, 102, 5989, 121101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよ\n早く顔洗いなさいよ、\nこのねぼすけ♪", 1, 1, 1, 0, 1, 5990, 121101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日のお昼は\nどこで食べる？\nあんたが\n決めていいわよ", 1, 1, 2, 0, 2, 5991, 121101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もしあんたがまた\n夢に囚われても\nあたしが助けるわ\n安心して寝なさいよ", 1, 1, 3, 0, 3, 5992, 121101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "気持ちいい～…♪\nあいつもたまには\nこうして休ませなきゃ", 1, 1, 0, 0, 2, 5994, 121101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "一目惚れしたかも…！\nこの子、\n部屋に飾りたいわー♪", 1, 1, 0, 0, 3, 5995, 121101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "へぇ、あいつって\nこういう匂いが\n好きなのかしら…？", 1, 1, 0, 0, 4, 5996, 121101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "暖かくて最高…♪\n丸くなっちゃいそう…\nうにゃあ…♪", 1, 1, 0, 0, 5, 5997, 121101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "イケるわね、これ！\nやっぱり食べるって\n大事よねー♪", 1, 1, 0, 0, 7, 5999, 121101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ファーストキスは\nどんな味、ね…\nふ、ふ～ん…？\nなるほど…", 1, 1, 0, 0, 8, 6000, 121101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お友だち…\nできるかしら…？\nこんなわたしでも…", 1, 1, 0, 0, 1, 6002, 118101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あまり、きょろきょろ\nしちゃダメよね…\n変な子だと、\n思われてしまうわ…", 1, 1, 0, 0, 2, 6003, 118101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "広い場所、\n落ち着かない…\nはぅ…どこにいれば…", 1, 1, 0, 0, 3, 6004, 118101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "～♪", 1, 1, 0, 0, 4, 6005, 118101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "【レイジ・レギオン】\n…またいつか、\nみんなと…", 1, 1, 0, 0, 5, 6006, 118101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "…一度でいいから、\nわたしもママって…\nあっ、その…\n…な、なんでもないわ", 1, 4, 0, 0, 101, 6007, 118101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここが、今のわたしに\nとっての帰る場所…\n…そう思っても、\nいいのかしら…？", 1, 8, 0, 0, 102, 6008, 118101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ええと…\nお腹、空いてる？\nおねむじゃない？", 1, 1, 0, 0, 1, 6009, 118101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ここにいると\n邪魔かしら…？\nならもっと隅っこに…", 1, 1, 0, 0, 2, 6010, 118101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お喋り…？\nわたしでいいの…？\nうれしい…", 1, 1, 0, 0, 3, 6011, 118101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたの好きな歌…\n教えて…？\n練習、するから…", 1, 1, 0, 0, 4, 6012, 118101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ここは賑やかだけど…\nうるさいとは感じない\nむしろ、心地いい…", 1, 4, 0, 0, 101, 6013, 118101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "安心、して…\nわたしの歌で、\nあなたを守るから…", 1, 8, 0, 0, 102, 6014, 118101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ちょっとだけ…\n朝は、苦手…\n…ふぁ…", 1, 1, 1, 0, 1, 6015, 118101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼は、食べた？\n良かったら、一緒に…\n少し…焦げてるけど…", 1, 1, 2, 0, 2, 6016, 118101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "眠れないのかしら…\nじゃ、じゃあ…\n子守歌…聴く？", 1, 1, 3, 0, 3, 6017, 118101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こんな特等席…\n恐縮、です…", 1, 1, 0, 0, 2, 6019, 118101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "い、今のうちに、\nこの子でお世話の\n練習を…", 1, 1, 0, 0, 3, 6020, 118101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい香り…\n…つらい気持ちも\n癒してくれる", 1, 1, 0, 0, 4, 6021, 118101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったかい…\nいつまでも、\nこうしていられそう…", 1, 1, 0, 0, 5, 6022, 118101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "おいしく出来てる…\nすごいわ…\n見習わなくっちゃ…", 1, 1, 0, 0, 7, 6024, 118101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "『赤ちゃんとママの\nスキンシップ入門』…\n…べ、勉強になるわ…", 1, 1, 0, 0, 8, 6025, 118101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "きれいなお部屋…！\n立派な舞台の\nセットみたい…♪", 1, 1, 0, 0, 1, 6027, 121301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お芝居では私が先輩\nなんだから、しっかり\n私がミサキちゃんを\nエスコートしなきゃ", 1, 1, 0, 0, 2, 6028, 121301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "～～♪\n歌うお芝居も多いから\nもっと上手く\nなりたいな…！", 1, 1, 0, 0, 3, 6029, 121301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここにいる人たちと\n仲良くなりたいな…\nそしたらきっと演技の\n幅も広げられるよね", 1, 1, 0, 0, 4, 6030, 121301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "機会があったら\nアヤネちゃんとも\nお芝居してみたいな♪", 1, 1, 0, 0, 5, 6031, 121301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お兄ちゃんに褒めて\nもらえるように\nお芝居の練習\nいっぱいしなきゃ…♪", 1, 4, 0, 0, 101, 6032, 121301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あう…お兄ちゃんを\n意識したら、台詞が\n飛んじゃったよぅ…！", 1, 8, 0, 0, 102, 6033, 121301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あっ、お兄ちゃん…♪\nお芝居の練習を\n見てくれませんか…？", 1, 1, 0, 0, 1, 6034, 121301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "いまの発声練習\nどうでしたか？\nひゃん…！？\n頭は撫でないでぇ～", 1, 1, 0, 0, 2, 6035, 121301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ミサキちゃんも\nお芝居、とっても\n上達してるんですよ\n私も負けられません♪", 1, 1, 0, 0, 3, 6036, 121301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "また物語を聞かせて\nほしい、ですか…？\n私なんかでよければ\nもちろんよろこんで♪", 1, 1, 0, 0, 4, 6037, 121301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "新しいお話を\n考えてみました\n聞いてくれますか…？\nよかった…♪", 1, 4, 0, 0, 101, 6038, 121301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "いつか…舞台上じゃ\nなくても、照れずに\nお兄ちゃんに気持ちを\n伝えてみせますね…♪", 1, 8, 0, 0, 102, 6039, 121301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようございます♪\nマヒルさんにもらった\nミルク、一緒に\n飲みませんか？", 1, 1, 1, 0, 1, 6040, 121301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "牧場のみなさんに\nドングリパンを\nいただきました～\nおひとつどうぞ♪", 1, 1, 2, 0, 2, 6041, 121301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "寝る前にまたココアを\n飲みながら、お話を\n聞いてもらえますか？\nえへへ、うれしいな♪", 1, 1, 3, 0, 3, 6042, 121301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お芝居の練習の\nあとは、ここで\nお兄ちゃんと\nゆっくりしたいな♪", 1, 1, 0, 0, 2, 6044, 121301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "今日も演技の\n練習に付き合ってね♪", 1, 1, 0, 0, 3, 6045, 121301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "素敵な香り…\nこのイメージも\nお芝居に\n活かせないかな…？", 1, 1, 0, 0, 4, 6046, 121301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "私もお芝居で\nみんなの心を\nぽかぽか温められる\nようになりたいな…♪", 1, 1, 0, 0, 5, 6047, 121301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "舞台に立つんだから\n食べ過ぎないように\n気をつけないと。\nでも…おいしいなぁ♪", 1, 1, 0, 0, 7, 6049, 121301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "このお話、面白い…\n脚本を書く\n参考にもなりそう…♪", 1, 1, 0, 0, 8, 6050, 121301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "えへへ…♪\n二人で見た夕日…\nキレイでしたね…", 1, 12, 0, 0, 103, 6052, 107501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "これから泳ぎの\n練習ですか？\n付き合いますよ♪", 1, 12, 0, 0, 103, 6053, 107501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "主さまを導くことで\nわたくしも未知なる\n領域へと辿り着けます", 1, 12, 0, 0, 103, 6054, 107601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "主さまとなら\n海よりも深く\n空よりも高い場所へも\n目指せそうです", 1, 12, 0, 0, 103, 6055, 107601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あいつと二人で\n夏の遠い海に…って\nあたしってば\nなに考えてるのよ！", 1, 12, 0, 0, 103, 6056, 107801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あたしが楽しく\n過ごせるのはあんたが\nいてくれたからよ\nか、感謝してるわ", 1, 12, 0, 0, 103, 6057, 107801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "はぁ…アタシってば\nこんなにかわいくって\nキレイなんて罪よね～", 1, 1, 0, 0, 1, 6058, 121401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "舞台の衣装を\nクリーニング\nしとかなくちゃ…", 1, 1, 0, 0, 2, 6059, 121401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "舞台映えするメイク…\nスズナなら\n知ってるかしら？", 1, 1, 0, 0, 3, 6060, 121401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "物産展の会計係と\n山盛りの算数の宿題…\nどっちが簡単かしら…", 1, 1, 0, 0, 4, 6061, 121401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "牧場と温泉の仕事に\nお芝居の練習に…\nちょ、ちょっと休憩～", 1, 1, 0, 0, 5, 6062, 121401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お客さんの視線も\nうれしいけど…\nあいつも見てて\nくれてるかしら？", 1, 4, 0, 0, 101, 6063, 121401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "もっともっと\nセクシーになって\nあいつをビックリ\nさせてやるわ♪", 1, 8, 0, 0, 102, 6064, 121401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "アンタも休憩？\nしっかり働くために\n休むのも大事よね", 1, 1, 0, 0, 1, 6065, 121401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふふん♪　アタシも\n料理ができるように\nなったんだから！\n簡単なのだけだけど…", 1, 1, 0, 0, 2, 6066, 121401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "温泉ってお肌に\nいいのよね？\nもっとキレイに\nなったらどうしよう〜", 1, 1, 0, 0, 3, 6067, 121401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "物産展の荷物は\nこれでよし…っと\nそれじゃアンタも\n運ぶの手伝ってよね♪", 1, 1, 0, 0, 4, 6068, 121401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "いつもと違うアタシに\n見とれちゃった？\n仕方ないわね～♪", 1, 4, 0, 0, 101, 6069, 121401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お花見って風流で\n大人って感じ♪\n…でもお団子も\n忘れないでよね！", 1, 8, 0, 0, 102, 6070, 121401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "みんなに朝ごはんを\n作ってみたら\n喜んでくれるかしら？", 1, 1, 1, 0, 1, 6071, 121401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お客さんがいっぱい\n来る時間帯ね！\n元気出していくわ！", 1, 1, 2, 0, 2, 6072, 121401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そろそろ寝ないと…\n睡眠不足はお肌の\n大敵なんだから", 1, 1, 3, 0, 3, 6073, 121401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふかふかしてて\n気持ちいい～♪\n疲れが癒されるぅ…", 1, 1, 0, 0, 2, 6075, 121401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この子もアタシと\nおそろいの衣装に\nしてあげようかしら♪", 1, 1, 0, 0, 3, 6076, 121401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "素敵な衣装で\nいい香りを\n身にまとう…ふふ、\n大人のたしなみよね♪", 1, 1, 0, 0, 4, 6077, 121401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったか～い♪\nここでお昼寝したら\n気持ちよさそう…", 1, 1, 0, 0, 5, 6078, 121401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お仕事がんばると\nいつもよりお腹\n空いちゃう～！", 1, 1, 0, 0, 7, 6080, 121401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "最新のトレンド\n載ってないかしら？\n物産展の売り物に\n取り入れなくちゃ！", 1, 1, 0, 0, 8, 6081, 121401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "怪盗のこと…\nお父さんお母さんに\nもし知られたら\nなんて説明しよう…", 1, 1, 0, 0, 1, 6083, 121501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "たとえ怪盗でも、人の\n家にお邪魔するときは\n身だしなみをしっかり\nしないと…", 1, 1, 0, 0, 2, 6084, 121501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "鍵開けって難しい…\nうーん、もっと\n練習しておかなくちゃ", 1, 1, 0, 0, 3, 6085, 121501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "姿が見えない\n怪盗の噂…？\nわ、私のことじゃ\nありませんように…！", 1, 1, 0, 0, 4, 6086, 121501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "予告状って…\n切手はどこに貼れば\nいいんだろう…？", 1, 1, 0, 0, 5, 6087, 121501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "怪盗活動にも\n慣れてきたけど…\nやっぱり先輩が\nいないと心細いなぁ…", 1, 4, 0, 0, 101, 6088, 121501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "せ、先輩の心も\nいつか盗めるかな…？\n…わわわ、私ってば\nなに言ってるんだろ…", 1, 8, 0, 0, 102, 6089, 121501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "怪盗が大活躍って\n新聞で読んだ…？\nだっ、誰のこと\nでしょうね…あはは", 1, 1, 0, 0, 1, 6090, 121501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "尾行に潜入…\n存在感がないのって\n意外と役立つことも\nあるんですね", 1, 1, 0, 0, 2, 6091, 121501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "せせせ、先輩っ！\n帽子をぎゅーって\nかぶったら、抜けなく\nなっちゃいました…！", 1, 1, 0, 0, 3, 6092, 121501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ぬ、盗み聞きなんて\nしてませんよ？\nみんなが私に気づかず\n話し始めるだけで…", 1, 1, 0, 0, 4, 6093, 121501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "秘密道具が見たい？\nそ、それは内緒です！\n秘密道具ですから…\nで、でも先輩になら…", 1, 4, 0, 0, 101, 6094, 121501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "えへへ\n見つかっちゃいました\n先輩はいつも私を\n見つけてくれますね…", 1, 8, 0, 0, 102, 6095, 121501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようございまふ…\n秘密道具のお手入れで\n夜更かししちゃって…\nちょっと眠たいです～", 1, 1, 1, 0, 1, 6096, 121501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "怪盗が昼に活動する…\n案外そのほうが\n意外性が\nあるのかも…？", 1, 1, 2, 0, 2, 6097, 121501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "外が暗くなりましたね\n…そろそろ怪盗の活動\n始めましょうか？", 1, 1, 3, 0, 3, 6098, 121501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ここでくつろぐと\n疲れが取れます～\n…今日も怪盗\nがんばろっと", 1, 1, 0, 0, 2, 6100, 121501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "わぁ、かわいい…！\n心を盗まれるって\nこういうことかな…？", 1, 1, 0, 0, 3, 6101, 121501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "素敵な香り…でも\n服に香りがついたら\n隠れてるときに\n見つかっちゃうかな…", 1, 1, 0, 0, 4, 6102, 121501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ぽかぽかで\nいい気持ち…\n夜道を歩く前に\n温まっておこうっと…", 1, 1, 0, 0, 5, 6103, 121501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "怪盗活動に備えて\n今のうちにしっかり\n食べておかなきゃ…！", 1, 1, 0, 0, 7, 6105, 121501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "女の子は秘密が\n多いほうが魅力的…？\nそ、そうなんだ…", 1, 1, 0, 0, 8, 6106, 121501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ドＳさんはいつも\nクウカの妄想の中に…\nぐふふ、じゅるり～♪", 1, 12, 0, 0, 103, 6108, 104501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "やさしい触り方も\n嫌いじゃありません…\nは…恥ずかしいですが", 1, 12, 0, 0, 103, 6109, 104501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この大怪盗さまに\n盗めないものなんて\nないです！　たぶん！", 1, 1, 0, 0, 1, 6110, 121601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あたしにふさわしい\nビッグなお宝は\nどこにあるですかね～", 1, 1, 0, 0, 2, 6111, 121601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "そろーりそろーり…\n気配を消せばお菓子も\n食べ放題…にやり", 1, 1, 0, 0, 3, 6112, 121601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "またサボってたのが\nバレたです…\nボスの地獄耳～！", 1, 1, 0, 0, 4, 6113, 121601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ギャングで大怪盗…！\nこの肩書の多さ！\nまさに有能の証です！", 1, 1, 0, 0, 5, 6114, 121601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お兄さんだったら…\nお宝を山分けして\nあげてもいいですよ", 1, 4, 0, 0, 101, 6115, 121601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お兄さんはアレですよ\n大怪盗イノリさまの\n相棒みたいなものです", 1, 8, 0, 0, 102, 6116, 121601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "何事も用心深さが\n大切ですよ。ビ…\nビビりじゃないです！", 1, 1, 0, 0, 1, 6117, 121601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ぬ、盗み聞き？\nしてないですよっ？\nぴゅーぴゅーぴゅー", 1, 1, 0, 0, 2, 6118, 121601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "情報が欲しければ\nお菓子と交換ですよ\nさぁ、交換交換っ♪", 1, 1, 0, 0, 3, 6119, 121601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "しーっ！\n今、尾行中なんです\n話はあとで…", 1, 1, 0, 0, 4, 6120, 121601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お兄さんと話してると\n時間が速いような…？\nこの時間泥棒！", 1, 4, 0, 0, 101, 6121, 121601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ちょうどよかった！\nいい話があるんです\n一口乗りませんか？", 1, 8, 0, 0, 102, 6122, 121601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おてんとうさまが\n出てやがるですね…\n悪事がやりにくいです", 1, 1, 1, 0, 1, 6123, 121601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もうこんな時間ですか\nご飯にお昼寝…\nいやー忙しいです♪", 1, 1, 2, 0, 2, 6124, 121601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふっふっふ！　夜こそ\n大怪盗の時間です！\n……ふぁあ", 1, 1, 3, 0, 3, 6125, 121601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うーん…\n警備の目がないと\n気楽でいいですねぇ", 1, 1, 0, 0, 2, 6127, 121601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "くう…！\nこのかわいさは\n盗めないです…♪", 1, 1, 0, 0, 3, 6128, 121601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "犯行現場に残ったのは\n香水の匂いだけ…\nふふ、おしゃれ大怪盗", 1, 1, 0, 0, 4, 6129, 121601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "意外とこういう場所に\n隠し扉があったり…\nしないです？", 1, 1, 0, 0, 5, 6130, 121601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もぐもぐ…\nこの美味しさ…\nとんでもない罠ですね", 1, 1, 0, 0, 7, 6132, 121601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "むむむ…ちっとも\n分からないです…\nほとんど暗号ですよ", 1, 1, 0, 0, 8, 6133, 121601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あら、サレンさんは\nどこに行かれた\nのかしら？", 1, 1, 0, 0, 1, 6135, 121701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ふんふんふーん♪\nなんだか華やいだ\n気分ですわ♪", 1, 1, 0, 0, 2, 6136, 121701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "さて…次の事業は\nどうしましょう\n何か面白いことは…", 1, 1, 0, 0, 3, 6137, 121701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "おーっほっほっほ！\nあら…楽しくて、つい\n笑ってしまいましたわ", 1, 1, 0, 0, 4, 6138, 121701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "センスがあって\nいいお部屋ですわね\n私も見習いたいですわ", 1, 1, 0, 0, 5, 6139, 121701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "サレンさんとあのお方\n仲がいいですわね\nも、もちろん仲がいい\nのはよいことですわ！", 1, 4, 0, 0, 101, 6140, 121701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "どんなときでも\n正々堂々！\nそれが私の\n誇りですわ！", 1, 8, 0, 0, 102, 6141, 121701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふふ、ごきげんよう\n私、絶好調でしてよ", 1, 1, 0, 0, 1, 6142, 121701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どうかされましたか？\n何かお困りごと\nでしたら\n是非力になりますわよ", 1, 1, 0, 0, 2, 6143, 121701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "たまにはこうして\nゆっくりと\nお話しするのも\nいいものですわね", 1, 1, 0, 0, 3, 6144, 121701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "次の事業に関して\n考え事をしてましたの\nよければ相談に乗って\nくださるかしら？", 1, 1, 0, 0, 4, 6145, 121701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "よろしければ\nお話しませんこと？\nサレンさんのお話を\n聞かせてほしいですわ", 1, 4, 0, 0, 101, 6146, 121701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふふっ…\n先ほどサレンさんと\nあなたさまのお話を\nしていたところですわ", 1, 8, 0, 0, 102, 6147, 121701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "さあ！\n今日も一日\n全力で参りますわ！", 1, 1, 1, 0, 1, 6148, 121701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼はどうしましょう\nサレンさんをお誘い\nしてみようかしら", 1, 1, 2, 0, 2, 6149, 121701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もう少しだけ…\n明日の準備をして\nおかないと…", 1, 1, 3, 0, 3, 6150, 121701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はぁ～…いいですわね\nはっ、私としたことが\nはしたないですわね", 1, 1, 0, 0, 2, 6152, 121701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいらしいですわ\nいくつになっても\nぎゅっと\nしたくなりますわね", 1, 1, 0, 0, 3, 6153, 121701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "落ち着きますわね\nサレンさんはどんな\n香りが好きなのかしら", 1, 1, 0, 0, 4, 6154, 121701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふふっ、燃え盛る\n情熱の炎…\nいいですわね", 1, 1, 0, 0, 5, 6155, 121701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "このおいしさで\n元手がゼロ…\nすばらしい\n商品ですわ！", 1, 1, 0, 0, 7, 6157, 121701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これは…ふむふむ\n新しい事業のヒントに\nなりそうですわ", 1, 1, 0, 0, 8, 6158, 121701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "さて、と…\n今日は何をしなくちゃ\nいけないんだっけ？", 1, 1, 0, 0, 1, 6161, 121801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "やることは山積み\nだけど、手を動かす\nのは嫌いじゃないわ♪", 1, 1, 0, 0, 2, 6162, 121801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あら？　アキノさんの\n声が聞こえたような\n気がしたんだけど…", 1, 1, 0, 0, 3, 6163, 121801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "アキノさんといると\nついこっちまで笑顔に\nなっちゃうのよね", 1, 1, 0, 0, 4, 6164, 121801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "弱音なんか\nはいてられないわ\nアキノさんに\n笑われちゃうもの", 1, 1, 0, 0, 5, 6165, 121801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "気が付けばアキノさん\nのペースだけど…\n不思議と嫌では\nないのよね", 1, 4, 0, 0, 101, 6166, 121801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あたしを好敵手って\n言ってくれる\nアキノさんのためにも\nしっかりしなくちゃ", 1, 8, 0, 0, 102, 6167, 121801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どうしたの？\nアキノさんなら\nさっきそこに\nいなかった？", 1, 1, 0, 0, 1, 6168, 121801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あたしなら大丈夫\n救護院だって\nみんなが助けて\nくれるからね", 1, 1, 0, 0, 2, 6169, 121801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "さっきアキノさんが\nあんたを探してたわよ\nなにかしたの？", 1, 1, 0, 0, 3, 6170, 121801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "こう見えてヒマじゃ\nないんだけど…\n少しくらいなら\n付き合ってあげるわ", 1, 1, 0, 0, 4, 6171, 121801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ねえ、あんたと\nアキノさんって…\nううん、やっぱり\nなんでもないわ…", 1, 4, 0, 0, 101, 6172, 121801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "アキノさんも誘って\nお茶でもしない？\n話したいことが\nたくさんあるの♪", 1, 8, 0, 0, 102, 6173, 121801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "早起きは三文の得よ\nいい一日を迎えるため\n目を覚まさなきゃ", 1, 1, 1, 0, 1, 6174, 121801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日のお昼は…\nアキノさんと同席する\n予定だったわね\nあんたもどう？", 1, 1, 2, 0, 2, 6175, 121801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふぁ…っと、だめね\nたるんだところを\nアキノさんに\n見せるわけには", 1, 1, 3, 0, 3, 6176, 121801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ん～…\n落ち着くわね\nこのまま\n寝ちゃいそう…", 1, 1, 0, 0, 2, 6178, 121801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "救護院の皆も\nぬいぐるみが\n大好きなのよ", 1, 1, 0, 0, 3, 6179, 121801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい香りね…\nこれはアキノさんの\n趣味かしら？", 1, 1, 0, 0, 4, 6180, 121801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こんなに激しく\n燃えているのに\nあったかくて落ち着く\nまるでアキノさんね", 1, 1, 0, 0, 5, 6181, 121801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "どんどん出てくる…\nこれ、救護院にも\nほしいわね…", 1, 1, 0, 0, 7, 6183, 121801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふむふむ…\n前にアキノさんが\n言ってたのは\nこのことかしら？", 1, 1, 0, 0, 8, 6184, 121801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この衣装、私には\nちょっとかわい過ぎて\n恥ずかしいな…", 1, 1, 0, 0, 1, 6187, 122001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "一部の人からは\n海賊より海賊らしいと\n評判の父です", 1, 1, 0, 0, 2, 6188, 122001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "潮風で錆びないよう\n武器の手入れを\nしておきましょうか", 1, 1, 0, 0, 3, 6189, 122001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "宝探しのお手伝いなら\n力になれると思います\n父のやる気次第ですが", 1, 1, 0, 0, 4, 6190, 122001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "除霊は気を遣います\n一歩間違えると父まで\n成仏させてしまうので", 1, 1, 0, 0, 5, 6191, 122001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あの人と見た\n海の景色を…ずっと\n忘れずにいたいな", 1, 4, 0, 0, 101, 6192, 122001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "幽霊になっても\n一目会えるなら…\n私はきっとあの人に…", 1, 8, 0, 0, 102, 6193, 122001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ボートを漕ぐの、少し\n上手になったんですよ\n今度お見せしますね", 1, 1, 0, 0, 1, 6194, 122001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "航海に向けた\n買い出しが必要ですね\n予定は空いていますか", 1, 1, 0, 0, 2, 6195, 122001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "呪われた財宝の\n解呪依頼が多くて…\nふぅ…えっと次は…", 1, 1, 0, 0, 3, 6196, 122001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜の航海では\n父が明かり代わりに\nなってくれるんです", 1, 1, 0, 0, 4, 6197, 122001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "船の上では食材が\n限られるので料理の\nいい練習になります", 1, 4, 0, 0, 101, 6198, 122001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この先もずっと\nあなたと一緒に\nたくさんの思い出を\n作っていきたいです", 1, 8, 0, 0, 102, 6199, 122001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あの人の寝顔…\nもう少しだけ\n眺めていたいな…", 1, 1, 1, 0, 1, 6200, 122001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "船の上では\n規則正しい生活が\n重要ですから", 1, 1, 2, 0, 2, 6201, 122001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜の海は幽霊が\n見分けやすいんです\n青白く光るので", 1, 1, 3, 0, 3, 6202, 122001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "海に揺られるような\n心地よさですね\n気が休まります…", 1, 1, 0, 0, 2, 6204, 122001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あなたも一緒に\n海を見に行きますか？\n…なんて。ふふっ", 1, 1, 0, 0, 3, 6205, 122001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "爽やかな香りですね…\n一面の青い海が\n目に浮かぶようです", 1, 1, 0, 0, 4, 6206, 122001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "温かい…\n潮風で冷えた体に\n沁みますね", 1, 1, 0, 0, 5, 6207, 122001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あ、おいしい…\n日持ちするなら船旅に\n持っていこうかな…", 1, 1, 0, 0, 7, 6209, 122001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "アンナさんに教えて\nもらった小説は…\nあ、これですね", 1, 1, 0, 0, 8, 6210, 122001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "なにもしないを\nしてるんだよ\nあ～いそがしいそがし", 1, 12, 0, 0, 103, 6212, 102601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "はぁ～あんたといると\nどこまでも気持ちよく\nダラダラできるね～", 1, 12, 0, 0, 103, 6213, 102601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "暗黒騎士にして、\n伝説の海賊でもある\n私に不可能はないっ！", 1, 1, 0, 0, 1, 6214, 121901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "海賊団の船員、\n随時募集中です…と\nこれで新たな盟友が\n増えるはずだ…！", 1, 1, 0, 0, 2, 6215, 121901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "肩に乗せるペットが\nほしいな。今のうちに\nペット用品を買いに\n行かなくては…！", 1, 1, 0, 0, 3, 6216, 121901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "財宝の地図はどこで\n売っているのだろう…\n古本屋？　古物商？", 1, 1, 0, 0, 4, 6217, 121901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この海賊衣装に\n身を包んでいると\n前世の新たな記憶が\nよみがえってくるな！", 1, 1, 0, 0, 5, 6218, 121901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "いずれはシグルドと\n海原への冒険に\n繰り出したいな…\nきっと楽しいぞ！", 1, 4, 0, 0, 101, 6219, 121901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "待ちわびたぞ盟友よ！\n私と貴様あっての\n海賊団…だからその…\n今日はそばにいてくれ", 1, 8, 0, 0, 102, 6220, 121901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "シグルド！　港町で\nお魚のおすそ分けを\nもらったんだ！\n一緒に食べよう！", 1, 1, 0, 0, 1, 6221, 121901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "シグルドもこのコート\n羽織ってみないか？\nふふっ、きっとすごく\n似合うと思うぞ！", 1, 1, 0, 0, 2, 6222, 121901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "海賊船の新たな\n秘密兵器を作ったぞ！\nシグルド、これから\n見に来ないか！？", 1, 1, 0, 0, 3, 6223, 121901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私は伝説の海賊だから\n泳ぐのだって得意だ！\nふ、深いところは\n少し怖いが…", 1, 1, 0, 0, 4, 6224, 121901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふふ、いつ無人島に\n漂着してもいいように\n非常食は準備済みだ\nシグルドの分もある！", 1, 4, 0, 0, 101, 6225, 121901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "冥風戦記の新章が\n完成したぞ…！\nシグルド、まずは\n貴様に読んでほしい！", 1, 8, 0, 0, 102, 6226, 121901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふっ、天気明朗\n絶好の航海日和だ！\n早速海に繰り出そう！", 1, 1, 1, 0, 1, 6227, 121901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "昼食用に魚を\n釣っておいたぞ！\nふふ、いかにも\n海賊っぽいだろう？", 1, 1, 2, 0, 2, 6228, 121901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜の海は真っ黒で\n少しだけ怖いな…\nシグルド、私の\nそばを離れるなよ…！", 1, 1, 3, 0, 3, 6229, 121901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "大海賊がくつろぐのに\n相応しい逸品だ…！\nえっと…これ、どこの\nお店で買えますか？", 1, 1, 0, 0, 2, 6231, 121901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "わわ、かわいい…！\n船室のベッドに\n置いておきたいな…\n連れてってはダメか？", 1, 1, 0, 0, 3, 6232, 121901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "おぉ、この香りは…！\n集中力が上がりそうだ\n海賊船の船長室に\nぜひ置きたいな！", 1, 1, 0, 0, 4, 6233, 121901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これが船乗りの\n伝承に聞く、海原を\nさまよう神秘の火か！\n…暖まるな！", 1, 1, 0, 0, 5, 6234, 121901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "腹が減っては\n海賊はできぬ！\n…小説も書けぬ！", 1, 1, 0, 0, 7, 6236, 121901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "女海賊と騎士はその後\n恋に落ち…？　恋愛\nモノではないかっ！？\nき、興味深くはあるが", 1, 1, 0, 0, 8, 6237, 121901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "いつもの山籠もりが\nブームになるなんて…\n人生、なにが起きるか\n分からないなぁ", 1, 1, 0, 0, 1, 6239, 122101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "キャンプ道具…\nいえ、キャンプギア！\nカッコいい響き…♪", 1, 1, 0, 0, 2, 6240, 122101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "レッツ、ＢＢキャン！\nこのフレーズ\n流行るかも…？", 1, 1, 0, 0, 3, 6241, 122101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "キャンプ仲間…\n募集してみようかな？\n友達も兼任で…", 1, 1, 0, 0, 4, 6242, 122101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ユカリさんと\nジュンさん…元気かな\nまた一緒に\nキャンプしたいなぁ…", 1, 1, 0, 0, 5, 6243, 122101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "私の取材が載った本…\n【フォレスティエ】に\n送らなくちゃ…♪", 1, 4, 0, 0, 101, 6244, 122101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "もし、あの人が\n落ち込むことが\nあったら…その時は\n私が力になりたいな", 1, 8, 0, 0, 102, 6245, 122101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ブッシュクラフトは\nマイフレンドくんの\nカスタムにも\n活かせる技術です！", 1, 1, 0, 0, 1, 6246, 122101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "会話に花が咲く…\nって言いますよね\n私の場合、会話相手が\nお花そのものですけど", 1, 1, 0, 0, 2, 6247, 122101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この服、実は…\n昔ミサト先生に\nこしらえてもらった\n物なんです！", 1, 1, 0, 0, 3, 6248, 122101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "昔はやまびこで会話の\n練習をしてたんです\nほとんどオウム返しで\n気まずかったなぁ～", 1, 1, 0, 0, 4, 6249, 122101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どうもマイスターです\n…あ、すみません\nみなさんの期待に\n応えようと練習中で…", 1, 4, 0, 0, 101, 6250, 122101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "たとえぼっちを\n卒業しても…\n私は一生ＢＢ団です！", 1, 8, 0, 0, 102, 6251, 122101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "小鳥さんのさえずりで\n目が覚める…\n心地いいものですね", 1, 1, 1, 0, 1, 6252, 122101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "キャンプといえば\nおいしいご飯です！\n腕を振るいますよ！", 1, 1, 2, 0, 2, 6253, 122101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "すっかり夜ですね…\nおやすみの前に\n星でも見ませんか？", 1, 1, 3, 0, 3, 6254, 122101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "都会でくつろぎ…\n自然の中でくつろぐ…\nくつろぎ無限ループ…", 1, 1, 0, 0, 2, 6256, 122101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "しっかり観察すれば\nブッシュクラフトで\n再現できるかも…？", 1, 1, 0, 0, 3, 6257, 122101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はぁ～…やっぱり\n木の香りを嗅ぐと\n肩の力が抜けますねぇ", 1, 1, 0, 0, 4, 6258, 122101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ああ…あったまります\nなんだか心に\nゆとりがでますね…", 1, 1, 0, 0, 5, 6259, 122101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "このおいしさ…！\n今度はキャンプに\n持っていこうかな…", 1, 1, 0, 0, 7, 6261, 122101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふむふむ…\nいろんな知識があると\nキャンプはもっと\n楽しくなりますよね", 1, 1, 0, 0, 8, 6262, 122101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "部屋がキャンプ用品で\n埋まっていく…\nキャンプ…なんて\n恐ろしい趣味なの…？", 1, 1, 0, 0, 1, 6264, 122201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "仕事でも\nアウトドアでも\n頼れるお姉さんに\nなれたらいいなぁ…", 1, 1, 0, 0, 2, 6265, 122201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この服、かわいい上に\n本当に快適ね～♪\nおしゃれにキャンプを\n楽しめて最高だわ！", 1, 1, 0, 0, 3, 6266, 122201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ん〜次はどんな\nキャンプ飯を\n作ろうかしら？\n彼の好物は、っと…", 1, 1, 0, 0, 4, 6267, 122201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "早く週末が\n来ないかしら…\n仕事を忘れてパーっと\nキャンプ飲みを…！", 1, 1, 0, 0, 5, 6268, 122201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "新しいキャンプ飯の\nレシピを覚えたわ！\n彼も喜んで\nくれるはず…よね♪", 1, 4, 0, 0, 101, 6269, 122201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "彼の分の食器も\nこっそり買い揃えちゃ\nおうかな…そ、それは\nさすがにまだ早いか…", 1, 8, 0, 0, 102, 6270, 122201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今度、財団主催で\n大規模なキャンプを\nやってみるのも\nいいかもしれないわね", 1, 1, 0, 0, 1, 6271, 122201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ソロキャンプも\n悪くはないけど\n私はやっぱりキミと…\nな、なんでもない！", 1, 1, 0, 0, 2, 6272, 122201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "キャンプのことなら\n私に聞いて。なんでも\n教えてあげるから！\n…調べた上でね！", 1, 1, 0, 0, 3, 6273, 122201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "キャンプ場で飲む\n最高の一杯は\n他じゃ味わえないわ！\nさあ、乾杯っ！", 1, 1, 0, 0, 4, 6274, 122201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "うん、決めた！　次は\n禁しゅわキャンプ！\nだってキミとゆっくり\n話したいんだもん♪", 1, 4, 0, 0, 101, 6275, 122201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "キミのおかげで\n本当の最高の一杯が\n味わえたわ…\nありがとう♪", 1, 8, 0, 0, 102, 6276, 122201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "んー、気持ちいい！\nキャンプが趣味に\nなってから、寝覚めが\nよくなったわ♪", 1, 1, 1, 0, 1, 6277, 122201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼は私がごちそう\nするわ。キャンプ飯で\n鍛えた料理の腕前を\n披露しちゃうわよ～！", 1, 1, 2, 0, 2, 6278, 122201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そろそろ寝ましょ？\n明日も朝日を眺める\nためにね。もちろん\nコーヒー付きで♪", 1, 1, 3, 0, 3, 6279, 122201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "くつろぐのに\n持ってこいね～\nテントの中に置いても\nよさそうだわ！", 1, 1, 0, 0, 2, 6281, 122201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふふ、かわいいわね～\n…今度キャンプ場に\n連れていこうかしら？", 1, 1, 0, 0, 3, 6282, 122201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うん、いい匂い…♪\nキャンプ場を思い出す\n自然の香りだわ〜", 1, 1, 0, 0, 4, 6283, 122201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はぁ、温まるぅ～\n冬のキャンプで\n使ってみたいわね", 1, 1, 0, 0, 5, 6284, 122201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "おいしい～♪\nキャンプ飲みの\nおつまみに\nぴったりね！", 1, 1, 0, 0, 7, 6286, 122201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "へえ…こんなキャンプ\n飯もあるんだ？\n彼、好きだったり\nするかな…", 1, 1, 0, 0, 8, 6287, 122201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "一生ついててやるの\n幽霊の一生は\nなっが～～～いの！", 1, 12, 0, 0, 103, 6289, 100701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "む～…あんまり触ると\nすり抜けてやるの！\nウソなの。むふふ♪", 1, 12, 0, 0, 103, 6290, 100701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "んー…\n今日はベスちゃんと\nなにして遊ぼーかなー", 1, 1, 0, 0, 1, 6291, 122301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ヴァンピィちゃんも\nけんぞくの友だちと\n仲良くなりたいな♪", 1, 1, 0, 0, 2, 6292, 122301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "けんぞくのお部屋は\nヴァンピィちゃんが\n守ってあげなくちゃ！", 1, 1, 0, 0, 3, 6293, 122301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お掃除するなら\nヴァンピィちゃんも\n手伝ってあげよー♪", 1, 1, 0, 0, 4, 6294, 122301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ふーん、ふーん♪\nけんぞくってこーゆー\nお部屋が好きなんだー", 1, 1, 0, 0, 5, 6295, 122301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ひまだなー\nけんぞくをさそって\nお散歩いこっかな♪", 1, 4, 0, 0, 101, 6296, 122301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今日のスープに\nけんぞくの大好物を\n入れてあげよっと♪", 1, 8, 0, 0, 102, 6297, 122301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "けんぞくー！\nヴァンピィちゃんと\nお出かけしよ♪", 1, 1, 0, 0, 1, 6298, 122301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "よしよし！\n今日のけんぞくも\nいい子だね♪", 1, 1, 0, 0, 2, 6299, 122301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "んんー…\nやっぱりけんぞくと\nベスちゃんは似てる？", 1, 1, 0, 0, 3, 6300, 122301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "いーい？\nヴァンピィちゃんの\nいうこと聞くのよ？", 1, 1, 0, 0, 4, 6301, 122301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ヴァンピィちゃんの\nけんぞくだもん！\n大切にするからね！", 1, 4, 0, 0, 101, 6302, 122301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "えへへっ！\nやっぱりけんぞくって\nやさしー匂いがする♪", 1, 8, 0, 0, 102, 6303, 122301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよー…\nヴァンピィちゃんは\nもうちょっと眠るねー", 1, 1, 1, 0, 1, 6304, 122301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ヴァンピィちゃん\nひなたぼっこ\nだーいすき！", 1, 1, 2, 0, 2, 6305, 122301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "んー！　夜だ♪\nヴァンピィちゃん\n絶好調だよ！", 1, 1, 3, 0, 3, 6306, 122301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "わー！\nふかふかだ！\nきもちぃー♪", 1, 1, 0, 0, 2, 6308, 122301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいー♪\nベスちゃんに負けない\n抱き心地！", 1, 1, 0, 0, 3, 6309, 122301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この香り…なんだろ？\nヴァンピィちゃん\n気に入っちゃった♪", 1, 1, 0, 0, 4, 6310, 122301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったかいねー…\nヴァンピィちゃん\nずっとここにいるー…", 1, 1, 0, 0, 5, 6311, 122301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はむはむ…\nんー！\nおいしー♪", 1, 1, 0, 0, 7, 6313, 122301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ヴァンピィちゃん\nこの本\n好きなんだよねー♪", 1, 1, 0, 0, 8, 6314, 122301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "夏といえば\nかき氷だよね\n近くにお店は…", 1, 1, 0, 0, 1, 6316, 122501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この格好でいるのも\nなじんできたし\nちょっと鍛錬でも…", 1, 1, 0, 0, 2, 6317, 122501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "動きやすいけれど…\nいつもと違う服装は\n少し落ち着かないな", 1, 1, 0, 0, 3, 6318, 122501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "夏の空気に\n当てられると思わず\n気が緩んでしまうね", 1, 1, 0, 0, 4, 6319, 122501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "砂浜で走ると\nいい具合に体幹を\n鍛えられそうだ", 1, 1, 0, 0, 5, 6320, 122501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "終わらない夏は\nないけれど…\nつい願ってしまうな", 1, 4, 0, 0, 101, 6321, 122501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "胸を張って彼と\n並び立てるよう\n私も、もっと強く…！", 1, 8, 0, 0, 102, 6322, 122501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どうしたの？\n行きたい場所が\nあるなら付き合おうか", 1, 1, 0, 0, 1, 6323, 122501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "快晴で風は穏やか…\n絶好の釣り日和だね\nさあ、出かけようか", 1, 1, 0, 0, 2, 6324, 122501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日の予定を\n立ててるんだけど…\nキミの希望も\n聞きたいな", 1, 1, 0, 0, 3, 6325, 122501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ビーチスポーツか…\nキミとなら\nいい勝負ができそうだ", 1, 1, 0, 0, 4, 6326, 122501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私たちの夏に\n遠慮は無用だよ\n思いきり楽しもう！", 1, 4, 0, 0, 101, 6327, 122501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "いつかまた一緒に\n花火を見に行こう\n…約束だよ？", 1, 8, 0, 0, 102, 6328, 122501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "潮騒の音で\n目覚めるのも\nいいものだね", 1, 1, 1, 0, 1, 6329, 122501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今朝釣ってきた魚が\nあるんだ。これで\nお昼ご飯にしようか", 1, 1, 2, 0, 2, 6330, 122501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜にもイベントが\nあるみたいなんだ\n一緒に見に行かない？", 1, 1, 3, 0, 3, 6331, 122501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "気持ちが安らぐね…\nうっかり眠って…\nしまい…そうだ…", 1, 1, 0, 0, 2, 6333, 122501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "肌触りのよさが\nこの格好だと\nよく分かるよ", 1, 1, 0, 0, 3, 6334, 122501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "海をイメージさせる\n香りだね…\n爽やかで落ち着くよ", 1, 1, 0, 0, 4, 6335, 122501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "濡れたままだと\n風邪を引いてしまうよ\nほら、キミもおいで", 1, 1, 0, 0, 5, 6336, 122501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "夏を楽しみきるには\nエネルギーが\n必要だからね", 1, 1, 0, 0, 7, 6338, 122501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "大切な人と過ごせる\n観光スポット紹介…\n…こ、このページは\nもういいかな！", 1, 1, 0, 0, 8, 6339, 122501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "濡らしてもいいなら\n海に持っていって\n乗るのもよさそうだ", 1, 1, 0, 0, 10, 6341, 122501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こういう名画を\n見た気がするな…\nこんな視点なんだね", 1, 1, 0, 0, 11, 6342, 122501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "水着って\n動きやすくて\nいいよねー♪\nいっちにーさんしー♪", 1, 1, 0, 0, 1, 6343, 122401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ユイちゃんにもらった\n日焼け止め\n塗っておいて\nよかった～", 1, 1, 0, 0, 2, 6344, 122401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "なんだか暑いなあ…\nせっかくだし\nプールで泳いで\nこようかな？", 1, 1, 0, 0, 3, 6345, 122401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この格好で\n出かけようとしたら\nレイさんに\n怒られちゃった…", 1, 1, 0, 0, 4, 6346, 122401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "たとえ水着姿でも\n困ってる人を見たら\n助けに行くからね！", 1, 1, 0, 0, 5, 6347, 122401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "バカンス楽しかった～\nまた騎士くんと\n一緒に行きたいな♪", 1, 4, 0, 0, 101, 6348, 122401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この水着、あたしに\n似合ってるかなあ？\n騎士くんはどう\n思ってるんだろ…", 1, 8, 0, 0, 102, 6349, 122401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "騎士くん！\n海に行くなら\n準備運動\n忘れずにね！", 1, 1, 0, 0, 1, 6350, 122401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "泳ぐ練習がしたいの？\nいいよ！　水着だから\nなにかあったらすぐに\n助けてあげるね！", 1, 1, 0, 0, 2, 6351, 122401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "騎士くんの水着姿も\nかっこよかったよ！\nまた遊びに行こ！\n約束ね！", 1, 1, 0, 0, 3, 6352, 122401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "このグローブの向日葵\nかわいいでしょ？\nユイちゃんが\n選んでくれたんだ♪", 1, 1, 0, 0, 4, 6353, 122401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "実はこの水着\nいつも着てる\n服とおなじなんだ…\nって、冗談だよ？", 1, 4, 0, 0, 101, 6354, 122401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "変な日焼けしてないか\n見てもらってもいい？\n…なし！　今のなし！\nやっぱりなしー！", 1, 8, 0, 0, 102, 6355, 122401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夏のお日様に\n負けないよう\n今日も元気いっぱい\nがんばるぞー！", 1, 1, 1, 0, 1, 6356, 122401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お腹減ってきちゃった\n騎士くんはお腹\n空いてない？\n…聞こえちゃった♪", 1, 1, 2, 0, 2, 6357, 122401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日も一日\n楽しかったー！\n明日もまた\n楽しもうね！", 1, 1, 3, 0, 3, 6358, 122401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふわあ…\nふわふわなのが\nいつもより\nよくわかるよー…", 1, 1, 0, 0, 2, 6360, 122401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "そうだ！　水着なら\n濡れてもいいし\n今度洗ってあげるね♪", 1, 1, 0, 0, 3, 6361, 122401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "んー…いい匂い♪\nなんだか\n身体がぽかぽか\nしてきちゃった！", 1, 1, 0, 0, 4, 6362, 122401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "水着だから、えっと…\n暖か涼しい…！\n不思議な感じー", 1, 1, 0, 0, 5, 6363, 122401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "思いっきり身体を\n動かした後だから\nいくらでも\n食べちゃえるよー！", 1, 1, 0, 0, 7, 6365, 122401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふむふむ…\nなるほどー\n水着の種類って\nそんなにあるんだぁ…", 1, 1, 0, 0, 8, 6366, 122401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "乗り心地バッチリだね\nしゅっぱつしんこー！\nなんちゃって♪", 1, 1, 0, 0, 10, 6368, 122401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "わっ、ぽよんぽよん！\nトランポリンみたいで\nた～のし～！", 1, 1, 0, 0, 11, 6369, 122401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "彼のことを考えてると\n幸せで胸が温かくなる\n…私ったら、もう", 1, 12, 0, 0, 103, 6370, 101501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "うふふふふ\nくすぐったいわ～♪\nもう、イタズラっ子ね\n次は先生の番よ～♪", 1, 12, 0, 0, 103, 6371, 101501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "騎士クンの汗\n拭いてあげたいな…\n余計なお世話かな…？", 1, 1, 0, 0, 1, 6372, 122601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "夏バテ気味かも…\nそうだ、今夜は\nかば焼きにしよう♪", 1, 1, 0, 0, 2, 6373, 122601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "青い空、白い雲\n眩しい日差し…！\n夏って好きだな…", 1, 1, 0, 0, 3, 6374, 122601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ヒリヒリする…？\n日に焼けちゃったかな\nふー…ふー…", 1, 1, 0, 0, 4, 6375, 122601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "花火大会があるんだ…\n夜、浜辺…2人きり…\nむ、無理だよ〜！", 1, 1, 0, 0, 5, 6376, 122601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "水着って不思議…\n引っ込み思案が少し\nおさまる…気がする", 1, 4, 0, 0, 101, 6377, 122601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "騎士クンとまた\n海に行きたいな…\nまた、一緒に…", 1, 8, 0, 0, 102, 6378, 122601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ダ、ダイエット！？\nしてないよ！？\n元々これ位だよ…？", 1, 1, 0, 0, 1, 6379, 122601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "スイカ割りのコツ？\nわたしは、騎士クンに\n教わりたいな…", 1, 1, 0, 0, 2, 6380, 122601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "騎士クン。うちわで\n扇いであげるね\nどこに置いたかな…", 1, 1, 0, 0, 3, 6381, 122601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "うぅ…暑いね…\n騎士クン、温度計は\n見ない方がいいよ～", 1, 1, 0, 0, 4, 6382, 122601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "じ、じっと見ないで…\nええっと…ちょっと！\nちょっとならいいよ…", 1, 4, 0, 0, 101, 6383, 122601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "え、肝試し？\nう〜ん、うう〜ん…\n…騎士クンとなら！", 1, 8, 0, 0, 102, 6384, 122601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよう騎士クン\n見て、あっちの空\nもう入道雲が出てる", 1, 1, 1, 0, 1, 6385, 122601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夕立、来るかなぁ…\nもし雷が鳴ったら\nそばにいてくれる…？", 1, 1, 2, 0, 2, 6386, 122601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "しー。…ほら、虫の声\n…わ、わたしの心臓の\n音は気にしないで…！", 1, 1, 3, 0, 3, 6387, 122601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふふ、水着でゴロゴロ\nするのってなんだか…\nいけないことみたい", 1, 1, 0, 0, 2, 6389, 122601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいい～♪\n日光浴させて\nあげたいな♪", 1, 1, 0, 0, 3, 6390, 122601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ほんのり潮の香りが…\n香ばしくて塩辛くて…\nあ…おなか鳴っちゃう", 1, 1, 0, 0, 4, 6391, 122601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "水着、乾かせるかも…\nそうだ、みんなの分も\n持ってこよう！", 1, 1, 0, 0, 5, 6392, 122601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "夏バテなんてしてたら\nみんなの足を\n引っ張っちゃうよね\nいただきます！", 1, 1, 0, 0, 7, 6394, 122601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "騎士クンの、どうして\n海はしょっぱいの？\nって質問の答えは…\n分かんないよぉ～", 1, 1, 0, 0, 8, 6395, 122601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "顔は怒ってるのに\nなんだかかわいい…\n誰かに似てる…？", 1, 1, 0, 0, 10, 6397, 122601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これを枕に、ビーチで\n騎士クンと…なんて…\nなんて…なんて…！", 1, 1, 0, 0, 11, 6398, 122601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "夏休み、\n毎日たのしいな～♪\nつぎはなにしよう？", 1, 1, 0, 0, 1, 6399, 122901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "自由研究、\nなににしよっかな～？\nプチグリフォンさんの\nかんさつにっきとか？", 1, 1, 0, 0, 2, 6400, 122901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "夏休みって、\n給食ないから…\nそれはちょっぴり\nさみしいかも…？", 1, 1, 0, 0, 3, 6401, 122901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "砂浜をはだしで歩くの\nきもちいいよね♪\nくすぐったいけど\nミミ、好きなんだ～♪", 1, 1, 0, 0, 4, 6402, 122901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "きれいな貝がら\n見つけちゃった♪\nミミのたからもの！", 1, 1, 0, 0, 5, 6403, 122901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ミミ、浴衣を\n買ってもらったんだ♪\n今度、おにいちゃんに\n見せてあげたいな！", 1, 4, 0, 0, 101, 6404, 122901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ミミ、おにいちゃんと\nいっぱい夏休みの\n思い出つくりたいな♪", 1, 8, 0, 0, 102, 6405, 122901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ねえ、おにいちゃん…\n夏休みの宿題、\nわかんないとこ\nおしえてくれる…？", 1, 1, 0, 0, 1, 6406, 122901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おかあさんにね、\n麦茶つくるの上手って\nほめられたんだ～♪\n飲んでみて♪", 1, 1, 0, 0, 2, 6407, 122901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夏休みの宿題で\nポスターかくんだ～♪\nおにいちゃんを\nかいてもいい？", 1, 1, 0, 0, 3, 6408, 122901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "線香花火やりたいな♪\nミミ、長持ちさせるの\n上手なんだよ♪", 1, 1, 0, 0, 4, 6409, 122901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おにいちゃんの\n好きな本ってなあに？\nその本で読書感想文\n書こうかな♪", 1, 4, 0, 0, 101, 6410, 122901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "海であそんだ後ね、\nおにいちゃんに\n髪を拭いてもらうの\nすきなんだ～♪", 1, 8, 0, 0, 102, 6411, 122901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夏休みって、\nワクワクして\n早起きしちゃうよね♪", 1, 1, 1, 0, 1, 6412, 122901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "わぁ♪　お昼は、\n流しそうめんなんだ？\nミミ、流してみたい♪", 1, 1, 2, 0, 2, 6413, 122901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "こわい話聞いて、\nねむれないの…\nねえ、おにいちゃん、\nいっしょに寝ていい？", 1, 1, 3, 0, 3, 6414, 122901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ちょっぴりやすんだら\nまた、いっしょに\n海であそぼうね♪", 1, 1, 0, 0, 2, 6416, 122901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "すっごくかわいいね♪\nこの子のこと、\n絵日記にかこうっと♪", 1, 1, 0, 0, 3, 6417, 122901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "くんくん…\nスイカのにおいだね♪\nこのにおいだいすき！", 1, 1, 0, 0, 4, 6418, 122901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "およいだあとは、\nちゃんと体を\nあっためなくちゃね！", 1, 1, 0, 0, 5, 6419, 122901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "夏休みのご飯って\nなんだかとくべつ\nおいしい気がするね♪", 1, 1, 0, 0, 7, 6421, 122901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "夏の星座って\nすっごくきれいだね♪\nいつかみんなと\nいっしょに見たいな♪", 1, 1, 0, 0, 8, 6422, 122901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "わぁ♪　この子、\nとってもかわいいね！\nおともだちに\nなりたいな♪", 1, 1, 0, 0, 10, 6424, 122901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ミミ、人魚姫に\nなったみたい！\nおにいちゃん、\nとなりにすわって♪", 1, 1, 0, 0, 11, 6425, 122901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "水着姿になると\nバカンス気分を\n味わえちゃうね！", 1, 1, 0, 0, 1, 6426, 122801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "海に行くの待ちきれ\nなくて、もう水着に\n着替えちゃった！", 1, 1, 0, 0, 2, 6427, 122801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "海に行ったら\nずぶぬれ系のイタズラ\nたくさんしかけよう！", 1, 1, 0, 0, 3, 6428, 122801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "部屋の中で泳ぎの練習\nしよう！　にいちゃん\nも水着に着替えて！", 1, 1, 0, 0, 4, 6429, 122801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お日さまとお揃いの\n水着なんだ～♪\n似合ってるかなぁ？", 1, 1, 0, 0, 5, 6430, 122801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "海といえば冒険！\n海底に眠る財宝を\n見つけちゃおう！", 1, 4, 0, 0, 101, 6431, 122801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "にいちゃんと一緒だと\n部屋にいても海気分♪\nスイカわりしよっか！", 1, 8, 0, 0, 102, 6432, 122801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "にいちゃん！\n早く海に行く準備\nしなよ～！", 1, 1, 0, 0, 1, 6433, 122801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ねーにいちゃん\n何秒息止められるか\n勝負しよーよ！", 1, 1, 0, 0, 2, 6434, 122801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "にいちゃんはアイスと\nかき氷どっちが好き？\nミソギは…どっちも！", 1, 1, 0, 0, 3, 6435, 122801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "日焼け止めクリーム\nキョウカに\nもらったんだ～", 1, 1, 0, 0, 4, 6436, 122801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "水中メガネがあれば\n水鉄砲くらっても\n平気だね！", 1, 4, 0, 0, 101, 6437, 122801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "水着なら、毎日夏休み\n気分！　にいちゃんも\n着てみなよ～！", 1, 8, 0, 0, 102, 6438, 122801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "にいちゃんおはよー！\n早く顔洗って、\nいっぱい遊んじゃお！", 1, 1, 1, 0, 1, 6439, 122801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "にいちゃんデザートは\nイチゴのかき氷だよ！\n…激辛ってバレた？", 1, 1, 2, 0, 2, 6440, 122801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日は楽しかった～！\n明日もイタズラ日和に\nなるといいな！", 1, 1, 3, 0, 3, 6441, 122801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "落とし穴の下に\nやわらかいの置けば\nケガしないよね！", 1, 1, 0, 0, 2, 6443, 122801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいいね～！\nこの子も海に\n連れてっちゃおうよ！", 1, 1, 0, 0, 3, 6444, 122801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "このにおいを利用して\nイタズラするには…\nにいちゃんどう思う？", 1, 1, 0, 0, 4, 6445, 122801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "水着を着て暖炉で\nあったまるのって、\n変でおもしろいね～！", 1, 1, 0, 0, 5, 6446, 122801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "海辺で食べる\nお昼ごはんって最高♪\nってことで海行こう！", 1, 1, 0, 0, 7, 6448, 122801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いつか海を越えて、\n地図にも載ってない\n場所を見つけたいな！", 1, 1, 0, 0, 8, 6449, 122801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この口から水が出たら\nみんな驚くよね！\nぷぷ…細工しちゃお！", 1, 1, 0, 0, 10, 6451, 122801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "変な音がするイタズラ\nを仕込んどいて…\n誰か座らないかな～♪", 1, 1, 0, 0, 11, 6452, 122801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あなた様と私の愛を\n周りに見せつけて\n差し上げましょう♪", 1, 12, 0, 0, 103, 6453, 102701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "呼びましたか？\n呼びましたね？\nでは愛を語りましょう\nそうしましょう", 1, 12, 0, 0, 103, 6454, 102701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "水着で歩き回るのは\n恥ずかしいな…\n誰も見てないよね？", 1, 1, 0, 0, 1, 6455, 122701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "夏はやっぱり\nかき氷ですよね\n何色がいいかなぁ…♪", 1, 1, 0, 0, 2, 6456, 122701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あの人、泳げないって\n聞きました！\n私が教えてあげないと", 1, 1, 0, 0, 3, 6457, 122701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "夏休みの宿題は\n計画的に進めましょう\n当たり前のことです！", 1, 1, 0, 0, 4, 6458, 122701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "防犯魔石、どこに\n着けておこうかなぁ\n…ここにしようかな", 1, 1, 0, 0, 5, 6459, 122701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "海の楽しみ方は\n予習済みです！\nあとは実践ですね♪", 1, 4, 0, 0, 101, 6460, 122701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "水着…きっとあの人に\n見られちゃいますよね\n…褒めてくれるかな…", 1, 8, 0, 0, 102, 6461, 122701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "触りたいんですか？\n…いいですよ\n帽子くらいなら", 1, 1, 0, 0, 1, 6462, 122701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夏は体調管理に\n気をつけましょう\n私が管理してあげます", 1, 1, 0, 0, 2, 6463, 122701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "海に行くのが楽しみで\nたまらないんですか？\n私はそこまで…嘘です", 1, 1, 0, 0, 3, 6464, 122701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "いつもより薄着だから\nなんだか恥ずかしい…\nみ、見ないでください", 1, 1, 0, 0, 4, 6465, 122701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "へんたいふしんしゃ～\nえっ？　ああ、これは\n海に叫ぶ練習です", 1, 4, 0, 0, 101, 6466, 122701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたと一緒に\n海に行くなんて…\nなんだか大人…えへへ", 1, 8, 0, 0, 102, 6467, 122701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようございます\nさぁ、海に行って\n私と運動しましょう", 1, 1, 1, 0, 1, 6468, 122701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あっつ～い…\nはっ！？　ダメですよ\nシャキッとしないと！", 1, 1, 2, 0, 2, 6469, 122701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "明日も海で\n遊ぶために、しっかり\nお休みしましょうね", 1, 1, 3, 0, 3, 6470, 122701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "夏を楽しむときは\n休憩も大事ですよね\n計画的に、計画的に！", 1, 1, 0, 0, 2, 6472, 122701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいいなぁ～\n海のお土産屋さんに\n売ってるかなぁ？", 1, 1, 0, 0, 3, 6473, 122701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "大人な香りがします\n私も違いが分かる\n素敵な女性になります", 1, 1, 0, 0, 4, 6474, 122701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "濡れた体を\n乾かしましょう\nタオル、タオル…", 1, 1, 0, 0, 5, 6475, 122701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "泳いだ後は\n栄養補給…だけど\n…おなか膨らんじゃう", 1, 1, 0, 0, 7, 6477, 122701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "遊ぶだけじゃなくて\nお勉強もしてますよ\nあなたもどうですか？", 1, 1, 0, 0, 8, 6478, 122701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ミミが喜びそう…♪\nわ、私ははしゃいだり\nしませんけど…っ！", 1, 1, 0, 0, 10, 6480, 122701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ミソギがいたずらに\n使いそう…しっかりと\n試しておかないと！", 1, 1, 0, 0, 11, 6481, 122701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あたしがまさか\nここにいるなんて…\n自分でも不思議だわ", 1, 1, 0, 0, 1, 6482, 123001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "昔みたいにあちこち\n飛び回れないのって\nちょっと不便かも", 1, 1, 0, 0, 2, 6483, 123001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "こうして誰かと一緒に\n暮らせるのもあいつや\nみんなのおかげね", 1, 1, 0, 0, 3, 6484, 123001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここで厄介になるなら\nあたしも家事とか\n手伝わないと…", 1, 1, 0, 0, 4, 6485, 123001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "…だめ！　誰かの\n世話をせずには\nいられないわ！", 1, 1, 0, 0, 5, 6486, 123001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あいつたちが\n帰ってくるところだし\n綺麗にしないと…", 1, 4, 0, 0, 101, 6487, 123001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "せっかくあたしが\nいるっていうのに\n少しは構いなさいよ…", 1, 8, 0, 0, 102, 6488, 123001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おいしい店とか\n知らないの？\n食べに行きましょ", 1, 1, 0, 0, 1, 6489, 123001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あんた暇でしょ？\nあたしのやること\n手伝いなさい", 1, 1, 0, 0, 2, 6490, 123001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "コッコロたんと\n遊んでくるから\n留守番よろしく♪", 1, 1, 0, 0, 3, 6491, 123001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あらおかえり…って\nボロボロじゃない！\nまた人助けしてたの？", 1, 1, 0, 0, 4, 6492, 123001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "やっぱりあたしは\nあんたのそばが\n一番落ち着くみたい", 1, 4, 0, 0, 101, 6493, 123001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どこまでだって\nあんたと一緒に\nいてやるんだから", 1, 8, 0, 0, 102, 6494, 123001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふわぁ…おはよう\n…大きくなったせいか\n朝は特に体が重いわ", 1, 1, 1, 0, 1, 6495, 123001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼ご飯が食べたい？\nしょうがないわね…\nなんか作って\nあげるから", 1, 1, 2, 0, 2, 6496, 123001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あんたまだ成長期だし\nさっさと寝なさい\n大きくなれないわよ？", 1, 1, 3, 0, 3, 6497, 123001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "たまにはあたしも\nゆっくりしないと\nもたないのよ…", 1, 1, 0, 0, 2, 6499, 123001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいいじゃな～い♪\nコッコロたんに\nプレゼントしたいわ", 1, 1, 0, 0, 3, 6500, 123001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい香りね\n塔にもこういうの\n置いておこうかしら", 1, 1, 0, 0, 4, 6501, 123001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "炎か…前は\n羽が焼けそうで\n怖かったっけ", 1, 1, 0, 0, 5, 6502, 123001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あむ…妖精に\n体重増減の設定って\nあったっけ…？", 1, 1, 0, 0, 7, 6504, 123001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "記憶のデータと違って\n物語はいいわね\n結末が読めなくて", 1, 1, 0, 0, 8, 6505, 123001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "冒険のお土産\nみんなにおすそわけ\nせなあきまへんな", 1, 1, 0, 0, 1, 6507, 123101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "おとぎ話よりも\n不思議なことが\nあるもんやねぇ", 1, 1, 0, 0, 2, 6508, 123101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "冒険しに出かけるなら\n動きやすい服装に\nせななぁ", 1, 1, 0, 0, 3, 6509, 123101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "地図に水筒に…\n他に忘れもんは\nあらへんやろか？", 1, 1, 0, 0, 4, 6510, 123101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "これまでの冒険のこと\nノートに書いて\nまとめてますんよ", 1, 1, 0, 0, 5, 6511, 123101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "王子はんとの冒険は\nどれもマホマホ王国の\n大事な歴史や♪", 1, 4, 0, 0, 101, 6512, 123101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "どこへ冒険するときも\nうちと王子はんは\n一緒どす♪", 1, 8, 0, 0, 102, 6513, 123101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "魔法で火ぃつける\n練習してますんよ～\nよう爆発しますけど…", 1, 1, 0, 0, 1, 6514, 123101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "うちと王子はんで\n冒険道具をお揃いに\nするんもええなぁ♪", 1, 1, 0, 0, 2, 6515, 123101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "テント張りも随分\n上達したんよ\n王子はんのおかげやね", 1, 1, 0, 0, 3, 6516, 123101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ランスロットたちの\n新しい冒険衣装\n思いついたんよ♪", 1, 1, 0, 0, 4, 6517, 123101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "迷子になっても\n王子はんと一緒なら\nきっと大丈夫やね♪", 1, 4, 0, 0, 101, 6518, 123101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "王子はんのこと、\nきっちり守れるように\nなってみせますえ", 1, 8, 0, 0, 102, 6519, 123101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お寝坊さんは\n布団めくって\n起こしにいきますえ～", 1, 1, 1, 0, 1, 6520, 123101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "支度が終わったら\n今日の冒険に\n出かけまひょか", 1, 1, 2, 0, 2, 6521, 123101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もうちょっと\nおしゃべりしたいん？\nあと少しだけやで♪", 1, 1, 3, 0, 3, 6522, 123101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "冒険で疲れた体に\nこの癒しぱわーは\nあきまへん…すや…", 1, 1, 0, 0, 2, 6524, 123101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふふ、あんさんも\nうちと一緒に\n冒険します？", 1, 1, 0, 0, 3, 6525, 123101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "虫除けのアロマは\n冒険の必需品どす\n忘れんようにせんと", 1, 1, 0, 0, 4, 6526, 123101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うちも魔法使わんでも\n火ぃ起こせるよう\n練習せんとあかんなぁ", 1, 1, 0, 0, 5, 6527, 123101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この料理…\n冒険するときに\n作ってみまひょか", 1, 1, 0, 0, 7, 6529, 123101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "王子はんとの\n愛と冒険のおとぎ話…\nはぁ…素敵やわぁ♪", 1, 1, 0, 0, 8, 6530, 123101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あの人との\n最良の未来のために\n占いも霊能力ももっと\n精進しなきゃ", 1, 12, 0, 0, 103, 6532, 103101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたともっと深く\nつながりたいです\n絆も、そして魂までも", 1, 12, 0, 0, 103, 6533, 103101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "絆の契約…\nあやつが望むのなら\n考えぬでもないがのう", 1, 12, 0, 0, 103, 6534, 104401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なんじゃ？\nわらわの姿に\n見惚れたかの？", 1, 12, 0, 0, 103, 6535, 104401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "旅の荷物って多いな…\nねぇぷうきち～\nすこしは持ってよぉ～", 1, 1, 0, 0, 1, 6536, 123201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "傘はなくてもいっか\nぷうきちがいるし♪\n『おいなんだと…？』", 1, 1, 0, 0, 2, 6537, 123201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "旅先で人見知りなんか\nしてちゃだめだよね…\nまずはあいさつだ…！", 1, 1, 0, 0, 3, 6538, 123201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "おみやげはやっぱり\nご当地ぬいぐるみ\nだよね♪　ワクワク…", 1, 1, 0, 0, 4, 6539, 123201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "知らない場所かぁ…\nうぅ…考えただけで\nすでにホームシック…", 1, 1, 0, 0, 5, 6540, 123201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "野宿するってことは…\nお兄ちゃんのマントで\n寝るチャンスかも…！", 1, 4, 0, 0, 101, 6541, 123201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "どんなに危険な冒険も\nお兄ちゃんと一緒なら\n大丈夫！　…だよね？", 1, 8, 0, 0, 102, 6542, 123201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ぷうきちのアイマスク\nも腹巻も持ってくの！\n旅の必需品なの～！", 1, 1, 0, 0, 1, 6543, 123201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "これ全部背負うのって\nけっこうむずかしいの\n順番まちがえると大変", 1, 1, 0, 0, 2, 6544, 123201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "えへへ～\nなでなでして？　帽子\nの上からでもいいよ♪", 1, 1, 0, 0, 3, 6545, 123201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "『悪いが絆創膏の部分\nはアンタッチャブルだ\n綿が飛び出ちまう』", 1, 1, 0, 0, 4, 6546, 123201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お兄ちゃんと一緒に\nもっともっといろんな\n景色が見たいな！", 1, 4, 0, 0, 101, 6547, 123201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "旅って、終わる時は\nきっとさびしいよね…\nお兄ちゃん、帰り道は\n手をつないでもいい？", 1, 8, 0, 0, 102, 6548, 123201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "出発が楽しみすぎて\n寝られなかった…\n『旅行のお約束だな』", 1, 1, 1, 0, 1, 6549, 123201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "わぁ　いいお天気！\n旅の間もずーっと\nぴーかんだといいな♪", 1, 1, 2, 0, 2, 6550, 123201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "知らない場所で寝るの\n怖いな…寝る時だけ\n帰ってこようかな…", 1, 1, 3, 0, 3, 6551, 123201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "旅先にはクッションも\nソファもないよね…\n『俺でいいだろ』", 1, 1, 0, 0, 2, 6553, 123201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ぬいぐるみのみんな\n旅の無事を\n祈ってて…！", 1, 1, 0, 0, 3, 6554, 123201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "旅先の香りも\n思い出に持って\n帰れたらいいのにな～", 1, 1, 0, 0, 4, 6555, 123201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "旅行用に携帯用暖炉\nがあるといいかも…！\n『まだ荷物増やす？』", 1, 1, 0, 0, 5, 6556, 123201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "『もう出発だぜ？』\nえーまってまって！\n腹ごしらえがまだ…", 1, 1, 0, 0, 7, 6558, 123201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "世界ってこんなに広い\nんだ…！　はやく自分\nの目で見てみたいな～", 1, 1, 0, 0, 8, 6559, 123201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "はちゃめちゃヒマ～\nなーんか楽し～コト\nないかなー", 1, 1, 0, 0, 1, 6561, 123301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここ、いートコだねー\nこんどカリザきゅんも\nつれてこよー", 1, 1, 0, 0, 2, 6562, 123301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "かわゆいショタが\n見た～い♪\n触りた～～～い♪", 1, 1, 0, 0, 3, 6563, 123301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "なんか服きっついなー\nちょい太ったかー？\nん～\nま・気のせーっしょ♪", 1, 1, 0, 0, 4, 6564, 123301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あ～やべ\nおねえちゃんに\nメッセすんの忘れたー\nまいっかー", 1, 1, 0, 0, 5, 6565, 123301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "パンケーキ食いてー\n騎士きゅん\n誘ってみよーっと♪", 1, 4, 0, 0, 101, 6566, 123301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "きょーも～\n好きぴに囲まれて～\nシ・ア・ワ・セ♪", 1, 8, 0, 0, 102, 6567, 123301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "アタシと遊ばな～い？\nってなんか\nナンパみスゴー\nウケる～", 1, 1, 0, 0, 1, 6568, 123301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "んー？\n今はだるいからパース\nあとでねー", 1, 1, 0, 0, 2, 6569, 123301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "カリザきゅん\n見なかった～？\nおねえちゃんから\n逃げるとかオシオキー", 1, 1, 0, 0, 3, 6570, 123301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ネイルしたいけど\n無理なんよねー\n爪の間にめっちゃ\nスライム入っからさー", 1, 1, 0, 0, 4, 6571, 123301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おねえちゃんに\nかまってほし～の？\nかわゆ～い♪", 1, 4, 0, 0, 101, 6572, 123301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "な～に騎士きゅん♪\nここでイチャイチャ～\nし・ちゃ・う～？", 1, 8, 0, 0, 102, 6573, 123301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ねんむ～\nけど外から\nショタの声したんで～\nつい起きちった～", 1, 1, 1, 0, 1, 6574, 123301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ランチの後はやっぱ\nデザートっしょー\nベツバラってヤツ～♪", 1, 1, 2, 0, 2, 6575, 123301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "こんな時間まで\n起きてるコは～\n食べちゃうぞ～♪", 1, 1, 3, 0, 3, 6576, 123301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あーラクー\nいっしょーここで\nキューケーできるわー", 1, 1, 0, 0, 2, 6578, 123301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "なにこれ～\nかわいいじゃ～ん\nショタの次くらいに～", 1, 1, 0, 0, 3, 6579, 123301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いーニオイする～\nなんのニオイとかー\nよくわかんないけどー", 1, 1, 0, 0, 4, 6580, 123301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったか～♪\n溶けてスライム\n戻りそ～", 1, 1, 0, 0, 5, 6581, 123301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ン・ン・ン～♪\nお・い・し～♪\nも～っと食べるぅ～♪", 1, 1, 0, 0, 7, 6583, 123301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "わ～なんかムズー\n読めなー\nウケるー", 1, 1, 0, 0, 8, 6584, 123301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あんパンって\nお菓子に入るのかな？\nハロウィンで\nもらえたりしない？", 1, 1, 0, 0, 1, 6586, 123501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お菓子をくれなきゃ\nぐうたらするぞ～！\nハロウィン以外でも！", 1, 1, 0, 0, 2, 6587, 123501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お化けになったら\n仕事しないで\nゴロゴロし放題\nなのかな？", 1, 1, 0, 0, 3, 6588, 123501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "おっと…この仮装\n袖がすごく長いからさ\n引っ掛けないように\n気を付けないとね～", 1, 1, 0, 0, 4, 6589, 123501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "イタズラするのも\n体力がいるよね～\n楽にできそうなの\n何かないかな？", 1, 1, 0, 0, 5, 6590, 123501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あいつって\n仮装とかするのかな？\n似合いそうなのは\nどんなお化けだろ？", 1, 4, 0, 0, 101, 6591, 123501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ずっとハロウィンなら\nあいつと遊び放題\nなんだけどな～", 1, 8, 0, 0, 102, 6592, 123501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今の街はハロウィン\n一色だからね～\n人混みが大変だよ", 1, 1, 0, 0, 1, 6593, 123501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この仮装、どうかな？\n気に入ったなら\n来年もしてあげるよ～", 1, 1, 0, 0, 2, 6594, 123501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "実はこの長い袖\nドングリとかを\nため込めるんだよね…", 1, 1, 0, 0, 3, 6595, 123501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "カボチャランタンが\nあるんなら\nドングリランタンも\nあっていいよね～♪", 1, 1, 0, 0, 4, 6596, 123501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ハロウィンでも\nやる気があれば\nぐうたらできる…\nうん、いいこと言った", 1, 4, 0, 0, 101, 6597, 123501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "一緒にお菓子食べよ？\n寝転びながら堪能する\nハロウィンスイーツは\n最高だからね～", 1, 8, 0, 0, 102, 6598, 123501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お化けは寝る時間…\n今のあたしはお化け…\nだから、おやすみ～…", 1, 1, 1, 0, 1, 6599, 123501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼か～…\n食後のデザートは\nハロウィンのお菓子で\n決まりだね～♪", 1, 1, 2, 0, 2, 6600, 123501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふぁ～、寝よ…\nお化けは夜に\n出るものだけど\nあたしはリスだからね", 1, 1, 3, 0, 3, 6601, 123501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ハロウィンを楽しむ\nためにも、ぐっすり\n寝とかないとね～", 1, 1, 0, 0, 2, 6603, 123501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "…袖から手が\n出せないから\nこのまま撫でよう\nよしよし", 1, 1, 0, 0, 3, 6604, 123501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "リラックスできる\n素敵な香りだね～\n成仏しそうだよ～", 1, 1, 0, 0, 4, 6605, 123501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったかいね～\nここで寝てもいい？\n…着替えないとダメか", 1, 1, 0, 0, 5, 6606, 123501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もぐもぐ…\nお化けもリスも\nお腹は空くんだよ", 1, 1, 0, 0, 7, 6608, 123501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "キョンシーって\nどんなお化けなのか\n本で調べようかな？", 1, 1, 0, 0, 8, 6609, 123501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "より取り見取りだね～\nとりあえず、これと\nこのお菓子はキープで", 1, 1, 0, 0, 12, 6611, 123501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "よし、衣装もばっちり\nハロウィンパレードへ\n出かけよう♪", 1, 1, 0, 0, 1, 6613, 123601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "仮装してても\n剣は手放せないね\nほとんど体の一部だよ", 1, 1, 0, 0, 2, 6614, 123601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "見回りに誘うふりして\n二人でパレードに\n参加しちゃおうかな♪", 1, 1, 0, 0, 3, 6615, 123601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "…あれ？　手錠の鍵\n落としちゃった…？\nやば、出かける前に\n見つけないと…！", 1, 1, 0, 0, 4, 6616, 123601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お家でコスプレも\nこれはこれで楽しいな\n決めポーズの練習\nたくさんしておこう！", 1, 1, 0, 0, 5, 6617, 123601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "他にも、お姫様に\nメイドさんに学生服…\nやりたいコスプレが\n多すぎるよ～", 1, 4, 0, 0, 101, 6618, 123601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "漫画もゲームも好き！\n仮装するのも大好き！\n…好きを言葉にすると\nもっと好きになる♪", 1, 8, 0, 0, 102, 6619, 123601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ずっと目が合ってるね\n見つめてくれるのは\nいいんだけど、今日は\n衣装を見てほしいな♪", 1, 1, 0, 0, 1, 6620, 123601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "トリックオアトリート\nお菓子をくれなきゃ\n逮捕しちゃうぞ♪", 1, 1, 0, 0, 2, 6621, 123601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたにだけはこの姿\nいつでも見せてあげる\nふふ、なんちゃって♪", 1, 1, 0, 0, 3, 6622, 123601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どうしたの？\nイタズラするなら\n隙を突かなきゃ\nこんなふうに、ね♪", 1, 1, 0, 0, 4, 6623, 123601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "コスプレしてても\n剣の稽古は休まないよ\nちょっと相手になって\nくれるかな？", 1, 4, 0, 0, 101, 6624, 123601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ひゃっ、びっくりした\n急に触らないでよ～\nハロウィンだからって\nイタズラしないでね？", 1, 8, 0, 0, 102, 6625, 123601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよう！\n今日も朝から\nパトロールに行こう！", 1, 1, 1, 0, 1, 6626, 123601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "カボチャのケーキを\n用意してるよ！\nデザートを食べて\n午後も頑張ろう！", 1, 1, 2, 0, 2, 6627, 123601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "暗くなってきたね\nカボチャのランタンに\n火を灯そうよ♪", 1, 1, 3, 0, 3, 6628, 123601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふかふか気持ちいい～\nあっでも衣装がシワに\nなっちゃうかも…？\n…でも気持ちいい～", 1, 1, 0, 0, 2, 6630, 123601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "…かわいい\nこの子もパレードに\n連れて行きたいな", 1, 1, 0, 0, 3, 6631, 123601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はぁ～癒される♪\nハロウィンの見回りが\n終わった後は、\nまったりくつろごう", 1, 1, 0, 0, 4, 6632, 123601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ここで二人寄り添って\nカボチャスープ飲もう\n…なんて想像したら\nポカポカしてきた…", 1, 1, 0, 0, 5, 6633, 123601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "パレードに参加すると\n体力を使うから、\nいっぱい食べておこう", 1, 1, 0, 0, 7, 6635, 123601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この絵本のお姫様\nかわいいね\nこの仮装もいいな♪", 1, 1, 0, 0, 8, 6636, 123601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "わあ、おいしそう！\n見回りに行く前に\n少しつまんじゃおう♪", 1, 1, 0, 0, 12, 6638, 123601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今日こそは、あの人に\n気持ちを打ち明けて…\nそのあとは…うう\nど、どうしよう？", 1, 12, 0, 0, 103, 6640, 103701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私が剣なら、あなたは\nさしずめ私の鞘…\nふふ、私の帰る\n場所ってことだよ♪", 1, 12, 0, 0, 103, 6641, 103701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "やっぱり\nコスプレしてると\nテンションあがる～♪", 1, 1, 0, 0, 1, 6642, 123701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "キャラコスにも\n興味あるけど…\nマジカル、ラブリー\nどっちにしよっかな～", 1, 1, 0, 0, 2, 6643, 123701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "キャラコスといえば\nミスティーシャイニー\nもいいんだよねえ…\nうはー！　悩むー！", 1, 1, 0, 0, 3, 6644, 123701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ギルドハウスで\n宅コスしてる人\n実は結構\nいるんじゃないかな～", 1, 1, 0, 0, 4, 6645, 123701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "コスプレだけど普段着\nって言いはれそう…\nって、ダメダメ！\nオタクは節度が大事！", 1, 1, 0, 0, 5, 6646, 123701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "フェスで買ってきた\n薄い本～♪\nギルドハウスに持って\nきちゃおっかな～", 1, 4, 0, 0, 101, 6647, 123701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ようこそ\nコスプレ沼へ～\nなんちゃって♪", 1, 8, 0, 0, 102, 6648, 123701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どうどう？\nナナカちゃんの\nハロウィンコスプレ\n似合ってる？", 1, 1, 0, 0, 1, 6649, 123701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ハロウィン魔法少女\n参上カボ～！\n…やっぱ今の語尾\nナシで！", 1, 1, 0, 0, 2, 6650, 123701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ハロウィンといえば\nトリックオア～…\nコスプレ！\nなんちゃって", 1, 1, 0, 0, 3, 6651, 123701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ランフェスの\n戦利品～♪\nねえねえ\n見たい？　見たい？", 1, 1, 0, 0, 4, 6652, 123701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私にしてほしい\nコスプレがあったら\n教えて♪　君のために\nや～ってやるぜぇ！", 1, 4, 0, 0, 101, 6653, 123701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "一緒にコスプレしたい\nならナナカちゃんは\nいつでも大歓迎だよ♪", 1, 8, 0, 0, 102, 6654, 123701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふぁあ…ねむ\nって、ありゃりゃ　\n昨日コスプレしたまま\n寝ちゃってたかあ…", 1, 1, 1, 0, 1, 6655, 123701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日のお昼は\nなんにしよ？\nカボチャ料理に\nしよっかな～", 1, 1, 2, 0, 2, 6656, 123701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もうこんな時間かあ\nそういえばオタクって\n夜行性の人が多い\n気がするぜぇ…", 1, 1, 3, 0, 3, 6657, 123701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふっふっふ\nこのパニエは\n座るときはちゃんと\nふにゃってなるのさ！", 1, 1, 0, 0, 2, 6659, 123701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いや～\nかわいいものをみると\n癒されますな～", 1, 1, 0, 0, 3, 6660, 123701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "新しい薄い本の匂いの\nアロマとか…ダメ？\n私はあの匂い、なんか\n好きなんだよね～", 1, 1, 0, 0, 4, 6661, 123701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "暖か～い…\nけど近づきすぎて\n衣装が燃えないように\n気を付けなきゃ", 1, 1, 0, 0, 5, 6662, 123701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "コス衣装のために\n体型維持は必須だけど\nついつい\n食べちゃうんだよね～", 1, 1, 0, 0, 7, 6664, 123701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "おっ、次はこの本に\nのってるキャラのコス\nしてみよっかな？", 1, 1, 0, 0, 8, 6665, 123701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "おおっ！\nお菓子がいっぱい！\nベルルちゃんチョコ\n入ってないかな～？", 1, 1, 0, 0, 12, 6667, 123701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "むっ…\nこの耳の手触り…\n驚嘆に値する…！", 1, 1, 0, 0, 1, 6669, 123801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "獲物を狩りまくる…\nそんなウサギがいても\nいいだろう？", 1, 1, 0, 0, 2, 6670, 123801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ワナなど怖くないぞ？\nむしろ積極的に\n踏んでいく性分だ", 1, 1, 0, 0, 3, 6671, 123801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "一匹狼ならぬ\n一匹ウサギか\nどっちでもかまわん♪", 1, 1, 0, 0, 4, 6672, 123801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "人間とて本を正せば獣\n本能に従えばよいのだ\n遠慮は無用さ☆", 1, 1, 0, 0, 5, 6673, 123801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "脱兎のごとく…\n逃げるものか\n坊やを追い回すのさ♪", 1, 4, 0, 0, 101, 6674, 123801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "獣の道は険しいが…\n坊やめ、音を上げずに\nちゃんとついてきたな\n褒めてやるとしよう♪", 1, 8, 0, 0, 102, 6675, 123801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ニンジンはいらん\n好物は好敵手だ\nさぁどんどんおくれ♪", 1, 1, 0, 0, 1, 6676, 123801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "尻尾に触りたいだと？\nワタシの乱数聖域を\n崩せたらな…☆", 1, 1, 0, 0, 2, 6677, 123801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "頭で考えるな\n体に率直になれ\n己を野放しにするのさ", 1, 1, 0, 0, 3, 6678, 123801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そうさモコフワさ♪\nなで心地は最高だが\n意外にどう猛だぞ…？", 1, 1, 0, 0, 4, 6679, 123801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ワタシにピョンピョン\n跳ねてほしいのか？\nあとでじっくり、な♪", 1, 4, 0, 0, 101, 6680, 123801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ワタシと番になるか？\nオマエさえよければ\nいつでもかまわんぞ☆", 1, 8, 0, 0, 102, 6681, 123801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "太陽と共に目覚め\n活動する\nそれが獣の生き方さ☆", 1, 1, 1, 0, 1, 6682, 123801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "昼寝をすればいい\nワタシが周囲を\n警戒していてやる", 1, 1, 2, 0, 2, 6683, 123801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "暗くなれば眠るだけさ\nそうすれば余計な\nストレスなどたまらん", 1, 1, 3, 0, 3, 6684, 123801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "むむっ！？\n尻尾がつっかえて\nうまく座れんな…", 1, 1, 0, 0, 2, 6686, 123801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "愛らしさでワタシに\n張り合おうというのか\n５億年後に出直せ☆", 1, 1, 0, 0, 3, 6687, 123801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "野生の芳香か…\n本能が呼び覚まされる\nいいセンスだ♪", 1, 1, 0, 0, 4, 6688, 123801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "寒さも野生のうちだ\n…とはいえ\n温まっていくか♪", 1, 1, 0, 0, 5, 6689, 123801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "悪くはないが物足りん\nこう見えて肉食なのだ\n肉はないのか肉は☆", 1, 1, 0, 0, 7, 6691, 123801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ウサギ族は地獄耳？\nワタシとの共通点だな", 1, 1, 0, 0, 8, 6692, 123801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここが私の家…\nそれはこれからも\nずっと変わらないわ…", 1, 12, 0, 0, 103, 6694, 106401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ええと、その…今から\nデート…じゃなくって\n散歩でもどうかしら？\n…ふ、二人きりで…", 1, 12, 0, 0, 103, 6695, 106401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ギルドハウスに\nヒーロー参上！\nみんな仲良くッスよ！", 1, 1, 0, 0, 1, 6696, 123901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "料理もできて\n鎧も作れる！\nお母さんはすごいッス", 1, 1, 0, 0, 2, 6697, 123901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ううう…\nまたマントをドアに\n挟んじゃったッス…", 1, 1, 0, 0, 3, 6698, 123901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ずっと鎧を着ていれば\nジュンさんに近づける\nかも？　燃えるッス！", 1, 1, 0, 0, 4, 6699, 123901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "サインの練習…\nもっとしておいた\nほうがいいッスかね～", 1, 1, 0, 0, 5, 6700, 123901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "半人前でも\n胸を張るッス！\nそのうち似合う日が\nくるッスから！", 1, 4, 0, 0, 101, 6701, 123901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "種族の違いも\n強い魂があれば\n超えられるッス！\n信じてるッスよ！", 1, 8, 0, 0, 102, 6702, 123901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "困ったときは\n自分を呼ぶッス！\nあと楽しいときにも\n呼んでほしいッス！", 1, 1, 0, 0, 1, 6703, 123901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "特訓でも行くッスか？\n望むところッス！\n目指せ、一人前！", 1, 1, 0, 0, 2, 6704, 123901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "新しいショーの台本を\n読みこんでるッス！\nところで…この言葉の\n意味、分かるッスか？", 1, 1, 0, 0, 3, 6705, 123901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ヒミツがなくなると\nなんか恥ずかしいッス\nあんたはどうッスか？", 1, 1, 0, 0, 4, 6706, 123901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ヒーローは遅れて\nやってくる…\nあんたもそうッスね♪", 1, 4, 0, 0, 101, 6707, 123901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "誰よりも早く\nあんたに見せたいッス\n一人前になった自分！", 1, 8, 0, 0, 102, 6708, 123901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "うーん、今日も\nよく眠れたッス～！\n絶好調、間違いなし！", 1, 1, 1, 0, 1, 6709, 123901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おお、もうお昼ッスか\nどうりでお腹が空くと\n思ったッス～えへへ", 1, 1, 2, 0, 2, 6710, 123901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "パトロールのあとは\n歯みがきに宿題に…\n大忙しッスね、夜！", 1, 1, 3, 0, 3, 6711, 123901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こんなにリラックス\nできるなんて…\n平和な証拠ッスね～", 1, 1, 0, 0, 2, 6713, 123901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "か、かわいい…♪\nさあ、自分と握手…\nううん、抱っこッス♪", 1, 1, 0, 0, 3, 6714, 123901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "優しい匂いッスね…♪\nこれくらいみんなを\n包み込めるように\nなりたいッス～", 1, 1, 0, 0, 4, 6715, 123901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ひっくちゅ！　うー…\nちょっと冷えるッスね\nぬくぬくするッス～♪", 1, 1, 0, 0, 5, 6716, 123901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "特訓した分、\nいっぱい食べるッス！\nでっかくなるッス！", 1, 1, 0, 0, 7, 6718, 123901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いつか自分の\nヒミツ図鑑が出たり\nして…いやいやいや", 1, 1, 0, 0, 8, 6719, 123901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "牢獄よりは\nマシな環境ね\n我慢してあげるわ", 1, 1, 0, 0, 1, 6721, 106901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "退屈ね…\nキャルでもいじって\n暇をつぶそうかしら", 1, 1, 0, 0, 2, 6722, 106901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここにいる連中\n私を天気予報士と\n勘違いしてない？", 1, 1, 0, 0, 3, 6723, 106901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "音波情報が多いわね…\nここ、もう少し静かに\nならないの…？", 1, 1, 0, 0, 4, 6724, 106901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "共同生活なんて\n久しぶりだわ\n…懐かしいわね", 1, 1, 0, 0, 5, 6725, 106901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あの子たちのギルド\n経営が雑すぎるわ…！\n教育が必要みたいね", 1, 4, 0, 0, 101, 6726, 106901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "とどめを刺される\n準備はできているのに\nまだなのかしら…？", 1, 8, 0, 0, 102, 6727, 106901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私と戦う気になった？\nあら、まだなのね…\nその時を待ってるわよ", 1, 1, 0, 0, 1, 6728, 106901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私というリソースは\n有効活用しなさい\n子守なんてごめんだわ", 1, 1, 0, 0, 2, 6729, 106901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どうしてそんなに\n頭がいいか？\n生まれつきよ", 1, 1, 0, 0, 3, 6730, 106901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "視力は権能に\n影響しないわよ\n当たり前じゃない", 1, 1, 0, 0, 4, 6731, 106901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたまで天気を\n教えろですって…？\nぶっ殺すわよ？", 1, 4, 0, 0, 101, 6732, 106901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "暇なら付き合いなさい\n好きなゲームでいいわ\nどうせ私が勝つもの", 1, 8, 0, 0, 102, 6733, 106901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "人生朝露…\n悔いのない一日を\n過ごしなさい", 1, 1, 1, 0, 1, 6734, 106901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "午睡はいいわよ…\nふわぁ…眠りは思考に\n必要不可欠なの…", 1, 1, 2, 0, 2, 6735, 106901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夙興夜寝なんて\nやめておきなさい\n休息は大切よ", 1, 1, 3, 0, 3, 6736, 106901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "私を満足させるなんて\n大したものね…晶に\n大量生産させるわ", 1, 1, 0, 0, 2, 6738, 106901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "キャルの代わりに\nズタズタにする物が\n欲しかったの…♪", 1, 1, 0, 0, 3, 6739, 106901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい香りじゃない\n見たことのない調合ね\n誰が用意したの？", 1, 1, 0, 0, 4, 6740, 106901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "パワーが足りないわね\nもっとよ、もっと私を\n寛がせなさい", 1, 1, 0, 0, 5, 6741, 106901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "栄養価はともかく\n味のバランスは\n悪くないわね", 1, 1, 0, 0, 7, 6743, 106901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "つまらない本は\n私が書き直すわ\nこれはどうかしら？", 1, 1, 0, 0, 8, 6744, 106901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今年はアカリと\nお姉ちゃんでサンタが\n二人…つまり幸せも\n二倍ですね♪", 1, 1, 0, 0, 1, 6746, 124001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "クリスマスに向けての\nサックス練習…\nお兄ちゃんと一緒に\n猛特訓です！", 1, 1, 0, 0, 2, 6747, 124001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "クリスマスツリーの\n飾りつけ、完璧です♪\nお兄ちゃんお姉ちゃん\n喜んでくれるかな？", 1, 1, 0, 0, 3, 6748, 124001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "わあ、お外が真っ白！\n生クリームが\nいーっぱいの\nケーキみたいです♪", 1, 1, 0, 0, 4, 6749, 124001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "煙突から入ると\nお洋服が汚れちゃう…\n玄関からじゃ\nダメなのかなぁ？", 1, 1, 0, 0, 5, 6750, 124001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お兄ちゃんが来たら\nサンタのお洋服\nいっぱい褒めて\nもらおっと♪", 1, 4, 0, 0, 101, 6751, 124001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "聖夜をお兄ちゃんと\n過ごせたら…\nアカリもちょっぴり\n大人になれるかも♪", 1, 8, 0, 0, 102, 6752, 124001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今のアカリは\nサンタさんですから♪\nなんでもお願いして\nくださいね？", 1, 1, 0, 0, 1, 6753, 124001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お外は寒いですから\nアカリとくっついて\nお出かけしましょ♪", 1, 1, 0, 0, 2, 6754, 124001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お兄ちゃんは\nアカリのトナカイさん\nですから、ずーっと\nそばにいてください♪", 1, 1, 0, 0, 3, 6755, 124001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お兄ちゃん、助けて！\nアカリのカラダで\nラッピング練習したら\n絡まっちゃいました…", 1, 1, 0, 0, 4, 6756, 124001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "プレゼント、いっぱい\n準備できました♪\nお兄ちゃんの分は…\nまだヒミツでーす♪", 1, 4, 0, 0, 101, 6757, 124001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "アカリ、プレゼントは\n大人っぽいのがいいな\n早くお兄ちゃんに\n追いつきたいから♪", 1, 8, 0, 0, 102, 6758, 124001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようございます♪\nふふ、アカリサンタは\nいい子のお手本なので\n早寝早起きなんです♪", 1, 1, 1, 0, 1, 6759, 124001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あれ、もうお昼？\nクリスマスナンバーの\n練習してたら\nもうこんな時間…", 1, 1, 2, 0, 2, 6760, 124001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "冬の夜空は星が\nキレイですね～\nお兄ちゃん、一緒に\n見に行きませんか？", 1, 1, 3, 0, 3, 6761, 124001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はぁ、快適すぎて\nとろけちゃいそう…\nこういうプレゼントも\nいいかもです…", 1, 1, 0, 0, 2, 6763, 124001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "やん、かわいいです♪\nえへへ、お兄ちゃんは\nどんなプレゼントが\n欲しいですか～？", 1, 1, 0, 0, 3, 6764, 124001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "わわ、大人な香り…\nロマンチックな\nクリスマスが\n過ごせそうです♪", 1, 1, 0, 0, 4, 6765, 124001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これでカラダを温めて\nアカリの温もり…\nお兄ちゃんに\nおすそ分けしよっと♪", 1, 1, 0, 0, 5, 6766, 124001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お腹がパンパンです…\nでもこれで\nちょっとサンタさんに\n近づけたかも…？", 1, 1, 0, 0, 7, 6768, 124001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "クリスマスイブの\n恋人との過ごし方…？\nはわわ、アカリには\nまだ早いかもです…！", 1, 1, 0, 0, 8, 6769, 124001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "わっ、すごいすごい！\n不思議な絵本ですね♪\nこの本でお兄ちゃんに\nイタズラしちゃお♪", 1, 1, 0, 0, 14, 6771, 124001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "アカリ、ホカホカに\nなっちゃいました～♪\n…ぎゅってしても\nいいですよ？", 1, 1, 0, 0, 15, 6772, 124001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "またエンジェルになる\nかもとは思ってたけど\n…サンタさん要素の\n追加は想定外だったわ", 1, 1, 0, 0, 1, 6773, 124101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ラフィたちがいない\nときは私がなんとか\nしないとね\n一応…エンジェルだし", 1, 1, 0, 0, 2, 6774, 124101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "部屋の飾りつけとか\nするのも…あり？\nふふ、浮かれすぎかな", 1, 1, 0, 0, 3, 6775, 124101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "パーティーだったら\nみんなで大人数向けの\nボードゲームもできる\nかも…やったっ", 1, 1, 0, 0, 4, 6776, 124101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この服のモコモコ…\n気づいたら触っちゃう\n…やるわね、ラフィ", 1, 1, 0, 0, 5, 6777, 124101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "アイツと一緒にいると\n人見知りとは違う\n緊張があるのよね…\nって今の聞いてたっ？", 1, 4, 0, 0, 101, 6778, 124101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ぬいぐるみ…\nいじめてないわよね？\n…大事にしてるって？\nそれはそれで…あうぅ", 1, 8, 0, 0, 102, 6779, 124101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "いい子と悪い子って\nどう見分けるのかしら\n…まさか素直さとか？\nだったら困る！", 1, 1, 0, 0, 1, 6780, 124101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "プレゼントといえば\nやっぱりゲーム！\n年末商戦サイコー♪", 1, 1, 0, 0, 2, 6781, 124101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "サンタさんはお仕事を\n見られちゃ\nいけないのよね？\n…天職？", 1, 1, 0, 0, 3, 6782, 124101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この服は汚れ対策も\nバッチリなのよね\n煙突に入れるように\n…いや私はNGよ！？", 1, 1, 0, 0, 4, 6783, 124101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "アンタのお願い…\n今なら一つくらい\n聞いてあげるわよ？\nほら、サンタ衣装だし", 1, 4, 0, 0, 101, 6784, 124101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私たちって\nなぜかぬいぐるみと\n縁があるわよね\n…ふふ", 1, 8, 0, 0, 102, 6785, 124101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お、おはよっ\n…ふぅ、寝ぐせを\n直すのが間に合って\nよかったぁ", 1, 1, 1, 0, 1, 6786, 124101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "え…一緒にごはん？\nそ、そう…うん。OK\nまあ、せっかくだし？", 1, 1, 2, 0, 2, 6787, 124101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この時間のゲームって\n妙に楽しいのよね\n…ということで、\nちょっと遊ばない？", 1, 1, 3, 0, 3, 6788, 124101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ああ～ダメになるぅ…\nもうここでずっと\nゲームしてた～い", 1, 1, 0, 0, 2, 6790, 124101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "プレゼントのために\n研究は必要よね、うん\n…はぁ、かわいい～♪", 1, 1, 0, 0, 3, 6791, 124101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "集中力を高める香りも\nあるのよね…\n勝負をするときには\n持ち歩こうかしら", 1, 1, 0, 0, 4, 6792, 124101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "なんだかまったり\nしちゃうわね…\n時間が溶けるわ…", 1, 1, 0, 0, 5, 6793, 124101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お腹いっぱい\n食べられるのも\n幸せなことよね", 1, 1, 0, 0, 7, 6795, 124101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ちっちゃいときは\nアカリと一緒に絵本を\n読んだなぁ…懐かしい", 1, 1, 0, 0, 8, 6796, 124101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "飛び出す絵本…\nこれでゲームブックを\n作ったらおもしろく\nなる気がするわ…", 1, 1, 0, 0, 14, 6798, 124101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったまる…\nモフモフ…ふわふわ…\nちょっと休憩しても…\nいいよね…？", 1, 1, 0, 0, 15, 6799, 124101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あら、どうしたの？\nもしかして、私と\n新しい実験を\n始めたくなった？", 1, 12, 0, 0, 103, 6800, 105101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "調子が悪いときは\n診療所に来て\n君は特別に、じっくり\n診察してあげるわ♪", 1, 12, 0, 0, 103, 6801, 105101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "特別じゃなくても\nまったり過ごす\nクリスマスも\nいいものだね", 1, 1, 0, 0, 1, 6802, 124201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ニットの帽子に\nマフラーに耳当て…\n素顔用の防寒対策を\n整えなければ", 1, 1, 0, 0, 2, 6803, 124201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "靴下が\n見当たらなかったから\n枕元にはかわりに\n鎧を置いておこう", 1, 1, 0, 0, 3, 6804, 124201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "聖女なんて言われる程\n素晴らしくはないけど\n心は聖くいたいものだ\n日々精進だね", 1, 1, 0, 0, 4, 6805, 124201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "兜がないから\nイルミネーションが\nいつもよりまぶしく\n感じるよ", 1, 1, 0, 0, 5, 6806, 124201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "皆の幸福を\n祈願して\nクリスマスリースを\n飾ろうか", 1, 4, 0, 0, 101, 6807, 124201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "私にはかわいすぎる\n衣装だが…開き直って\nクリスマス気分を\n楽しんでしまおう", 1, 8, 0, 0, 102, 6808, 124201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ツリーの飾りつけ\n手伝ってくれるかい？\nセンスは少年に\nお任せするよ", 1, 1, 0, 0, 1, 6809, 124201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "パーティーは\n用意してる時も楽しい\nねえ少年、この気持ち\n分かってくれるかい？", 1, 1, 0, 0, 2, 6810, 124201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なんでトナカイの鼻は\n赤いのかって？\n無灯ソリ運転は違反で\n取り締まられるからだ", 1, 1, 0, 0, 3, 6811, 124201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "トモちゃんや\nマツリちゃんは\nいい子だから、きっと\nサンタさんに会えるね", 1, 1, 0, 0, 4, 6812, 124201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ヒイラギの髪飾り\n実は気にいってるんだ\n少年にも\n褒めてもらいたいな", 1, 4, 0, 0, 101, 6813, 124201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ねえ少年…そろそろ\n見慣れてほしい\nそんなにまじまじ\n見つめられると照れる", 1, 8, 0, 0, 102, 6814, 124201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "朝日に照らされて\n街がキラキラ白銀に\n輝いてるよ", 1, 1, 1, 0, 1, 6815, 124201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼ご飯を食べて\nちょっと散歩しようか\n祝祭に沸く街は\nとてもきれいだよ", 1, 1, 2, 0, 2, 6816, 124201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "やっぱりクリスマスは\n夜が本番って感じだね\nキャンドルを灯して\n厳かに過ごそうか", 1, 1, 3, 0, 3, 6817, 124201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "鎧姿では無縁だったが\nふかふかしていて\n気持ちいいな…\nふかふか…ふかふか…", 1, 1, 0, 0, 2, 6819, 124201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふふ、かわいいね\n抱きしめると\nあの雪の日に産まれた\n赤ちゃんを思い出す", 1, 1, 0, 0, 3, 6820, 124201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "兜を脱いだおかげで\nこの香りに出会えた\n…クリスマスの魔法も\n悪くないかもね", 1, 1, 0, 0, 4, 6821, 124201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "指先から体の芯まで\n暖まるね\nこれなら雪の日でも\n安心だよ", 1, 1, 0, 0, 5, 6822, 124201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もぐ、もぐ…\nあまり食べるところ\n見ないでほしい", 1, 1, 0, 0, 7, 6824, 124201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "素敵なレストランの\n特集記事が載ってる\n聖夜に予約が取れたら\n最高に幸せだろうね", 1, 1, 0, 0, 8, 6825, 124201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "わっ！\n…………いや\n驚いてなどいない", 1, 1, 0, 0, 14, 6827, 124201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふふ…頬に直接\nふかふかを感じられる\n温かくて気持ちいいよ", 1, 1, 0, 0, 15, 6828, 124201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ピアノに読書…\nグレアと一緒に\nなにをしようかな～♪", 1, 1, 0, 0, 1, 6829, 124301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "元の世界へ戻った時\n授業に遅れないよう\nグレアと勉強しとこう", 1, 1, 0, 0, 2, 6830, 124301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "街に魔物料理屋さんが\nあるんだ！　グレアと\n食べに行ってみよう♪", 1, 1, 0, 0, 3, 6831, 124301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "せっかく新しい魔法を\n覚えたし、グレアにも\n見てもらいたいな！", 1, 1, 0, 0, 4, 6832, 124301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "グレアも誘って\nランドソルをもっと\n探索したい！", 1, 1, 0, 0, 5, 6833, 124301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "私たちも\nあの人を見習って\nバイトとかしよう！", 1, 4, 0, 0, 101, 6834, 124301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "いつかあの人を\n私たちの学院に\n招待したいな！", 1, 8, 0, 0, 102, 6835, 124301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あのね！\nこれからグレアと\n魔法愛好会に\n行くんだ！", 1, 1, 0, 0, 1, 6836, 124301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "これからなにするの？\n私たちもついて行って\nいいかな！？", 1, 1, 0, 0, 2, 6837, 124301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "グレアと一緒に\nあなたも魔法の練習\nしてみない？", 1, 1, 0, 0, 3, 6838, 124301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "グレアとよく話すの\nマナリアの制服\nあなたに着せたいって", 1, 1, 0, 0, 4, 6839, 124301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "グレアのそばって\n温かいんだ～！\nぬくぬくだよ～♪", 1, 4, 0, 0, 101, 6840, 124301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたは\nグレアと私の友達！\nこれからもずっと！", 1, 8, 0, 0, 102, 6841, 124301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよう！\n今朝はグレアと一緒に\n外を散歩してきたよ！", 1, 1, 1, 0, 1, 6842, 124301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お腹空いたな…\nよし、グレアと一緒に\n魔物料理を作ろう！", 1, 1, 2, 0, 2, 6843, 124301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もうこんな時間だね\n明日のために\nそろそろ寝ようかな！", 1, 1, 3, 0, 3, 6844, 124301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はぁ～この感触…\nグレアの尻尾みたいで\n安らぐ～", 1, 1, 0, 0, 2, 6846, 124301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ぬいぐるみって\nいいよね…私もよく\nグレアに抱き付くんだ", 1, 1, 0, 0, 3, 6847, 124301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい香り～…\nグレアもこういうの\n好きなんだよ！", 1, 1, 0, 0, 4, 6848, 124301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったかいね～…\nグレアにくっついてる\n時を思い出すよ～…", 1, 1, 0, 0, 5, 6849, 124301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この世界にしかない\nおいしいものが\nたくさんあるよね！", 1, 1, 0, 0, 7, 6851, 124301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "世界を繋ぐ魔法の\nヒント…本に書いて\nないかなぁ…", 1, 1, 0, 0, 8, 6852, 124301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ピアノに読書…\n今日のアンは\nなにをするのかな…", 1, 1, 0, 0, 1, 6855, 124401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "私はアンほど\n勉強はできないから\nちゃんと自習しないと", 1, 1, 0, 0, 2, 6856, 124401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "一度始めたら\nとことん追求するのが\nアンなんだよね…", 1, 1, 0, 0, 3, 6857, 124401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ふふ…夢中になってる\nアンを眺めてるだけで\n一日があっという間…", 1, 1, 0, 0, 4, 6858, 124401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ランドソルは広いね…\nアンと一緒にいろんな\n場所へ行ってみたい", 1, 1, 0, 0, 5, 6859, 124401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "アンやあの人の笑顔を\n見てると、すごく\n安心するんだ…", 1, 4, 0, 0, 101, 6860, 124401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "もしあの人が私たちの\n学院に編入したら…\nふふ、楽しそうだね", 1, 8, 0, 0, 102, 6861, 124401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "アンもあなたも\nすごいな…\nみんなとすぐに\n仲良くなっちゃうから", 1, 1, 0, 0, 1, 6862, 124401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたはこれから\nなにをするの？\nその…アンと一緒に\nついていってもいい？", 1, 1, 0, 0, 2, 6863, 124401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "アンから魔法の練習に\n誘われた？　実は私も\n…一緒にどうかな？", 1, 1, 0, 0, 3, 6864, 124401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたがマナリアに\n編入したら\n楽しいのになって\nアンとよく話すんだ…", 1, 1, 0, 0, 4, 6865, 124401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "こっちの世界にも\nアンのように\n私を受け入れてくれる\n人たちがいるんだね…", 1, 4, 0, 0, 101, 6866, 124401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "元の世界へ帰る日が\n来たとしても私たちは\n「さよなら」より\n「またね」がいいよね", 1, 8, 0, 0, 102, 6867, 124401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよう。さっき\nランドソルの朝日を\nアンと見てきたんだ", 1, 1, 1, 0, 1, 6868, 124401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私たちは\nこれからなんだけど\nあなたはお昼ごはん\n何を食べたの？", 1, 1, 2, 0, 2, 6869, 124401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そろそろ\nアンが眠る時間だ…\nあなたもおやすみ", 1, 1, 3, 0, 3, 6870, 124401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はぁ、疲れが取れる…\nこの尻尾…\nちょっと重いんだよね", 1, 1, 0, 0, 2, 6872, 124401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これ…\nアンに見せたら\n喜ぶだろうな…", 1, 1, 0, 0, 3, 6873, 124401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい香り…\nアンの感想も\n聞いてみたいな", 1, 1, 0, 0, 4, 6874, 124401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったかい…\nアンがここにいたら\n本を読みながら\n寝ちゃうんだろうな", 1, 1, 0, 0, 5, 6875, 124401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "おいしいからたくさん\n食べれちゃうけど\nまた尻尾…\n大きくなっちゃうかな", 1, 1, 0, 0, 7, 6877, 124401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "アンがおススメ\nしてくれた本は\nこれかな…？", 1, 1, 0, 0, 8, 6878, 124401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "凶、凶、凶…大凶？\nなによこのおみくじ\n自宅用だからって\n排出率が適当すぎよ…", 1, 1, 0, 0, 1, 6881, 124701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今年こそ最高の新薬が\nもはや偶然に近い\n最小限の手間で\n作れますように", 1, 1, 0, 0, 2, 6882, 124701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "喉に詰まる前に\n溶けてなくなるお餅…\n最優先の研究課題だわ", 1, 1, 0, 0, 3, 6883, 124701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "よし、完成♪\nミツキ特製…\n「新解釈おせち料理」\n医学的根拠が満載よ", 1, 1, 0, 0, 4, 6884, 124701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お年玉を彼に…\nあげるべき…？\n色んな意味で微妙ね…", 1, 1, 0, 0, 5, 6885, 124701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ふふ、今年もまた一年\n彼のバイタルを\n記録できるのね♪", 1, 4, 0, 0, 101, 6886, 124701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今年こそ\n私の薬で彼を魔改造…\nううん、大人の男に！", 1, 8, 0, 0, 102, 6887, 124701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "コマ回しのコマを\n見てたら目が回った？\nそう…手当は不要よ…", 1, 1, 0, 0, 1, 6888, 124701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私の顔で\n福笑いを作った？\nあら…隻眼まで\n再現度が高いわね♪", 1, 1, 0, 0, 2, 6889, 124701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "門松よし、しめ縄よし\nこんなギルドでもね\n一応縁起を担ぐのよ", 1, 1, 0, 0, 3, 6890, 124701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "本気の薬草を煮込んだ\n本気の七草粥よ\n色は気にしちゃダメ", 1, 1, 0, 0, 4, 6891, 124701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "君が今年も健康で\nつつがなく…実験台を\n務められますように", 1, 4, 0, 0, 101, 6892, 124701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "いくつ新年を迎えても\n俗世の垢にまみれない\nそんな君こそが…\n私には最高のお薬よ", 1, 8, 0, 0, 102, 6893, 124701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お正月なんだから\nいつまでも寝てないで\nさぁ、実験開始よ♪", 1, 1, 1, 0, 1, 6894, 124701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "カルタも双六もしたし\nあとは実験くらいしか\nやることがないわね", 1, 1, 2, 0, 2, 6895, 124701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "日頃からたいがい\n意味深な夢を見るけど\n初夢はまた別かしら…", 1, 1, 3, 0, 3, 6896, 124701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "さすがにこの格好で\n寝正月を敢行するのは\nためらわれるわね", 1, 1, 0, 0, 2, 6898, 124701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "や、闇医者たる者\nこんな愛くるしい物に\n心奪われるわけには…", 1, 1, 0, 0, 3, 6899, 124701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お雑煮のいい香り…\n食欲が増進されるわ\nアロマも立派なお薬ね", 1, 1, 0, 0, 4, 6900, 124701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "胸元は閉じないわよ？\nでも時々ここで\nこっそり暖まるの…", 1, 1, 0, 0, 5, 6901, 124701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "無から有が生まれてる\n新年早々物理の法則を\n無視してくるのね…", 1, 1, 0, 0, 7, 6903, 124701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "どれもこれも読んだわ\n新しい婚活本の\n入荷はまだかしら…", 1, 1, 0, 0, 8, 6904, 124701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "彼が一生懸命\n書いてくれた年賀状\n気持ちがこもっていて\nすごくうれしいわ♪", 1, 1, 0, 0, 1, 6906, 124601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "しっかり丁寧に\n大掃除したから\nとってもいい気持ちで\n新年を迎えられたわ♪", 1, 1, 0, 0, 2, 6907, 124601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "彼の着物姿\nきっと凛々しいわよね\n想像しただけで\n笑顔になるわ～♪", 1, 1, 0, 0, 3, 6908, 124601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お雑煮は地域によって\n違いがあるけれど\n愛情が隠し味なのは\nどこも一緒よね♪", 1, 1, 0, 0, 4, 6909, 124601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "彼と凧揚げして\n遊びたいけど\n森では引っかかって\nできないかしら…？", 1, 1, 0, 0, 5, 6910, 124601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今年も彼が素直に\n成長できるように\nたくさんいい子いい子\nしてあげましょう…♪", 1, 4, 0, 0, 101, 6911, 124601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "絵馬に彼と\n書いた通り\n今年もみんなが笑って\n過ごせますように", 1, 8, 0, 0, 102, 6912, 124601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "先生と初詣に\n行きましょう〜♪\n迷子にならないように\n手を繋いで、ね？", 1, 1, 0, 0, 1, 6913, 124601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "初売りセールいいわね\nでも気を付けないと\nあれこれ欲しくなって\n買い過ぎちゃうかも…", 1, 1, 0, 0, 2, 6914, 124601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "風邪をひかないように\n温かくするんですよ～\nえ？　先生、温めて？\nふふ、甘えん坊さんね", 1, 1, 0, 0, 3, 6915, 124601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたのために\nちょっと豪華なおせち\n用意したのよ\nお口に合うかしら？", 1, 1, 0, 0, 4, 6916, 124601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたも着物着たい？\nふふ、貫禄出ちゃって\n先生ドキドキ\nしちゃうかも…♪", 1, 4, 0, 0, 101, 6917, 124601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたの成長は\nとってもうれしいけど\n今年もまだ、あなたの\n先生でいたいな…♪", 1, 8, 0, 0, 102, 6918, 124601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あら、起きたの？\nお雑煮できてるから\nちゃんとお顔\n洗ってらっしゃいね", 1, 1, 1, 0, 1, 6919, 124601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼ご飯を\nしっかり食べて\nみんなでかるた大会\nしましょう〜♪", 1, 1, 2, 0, 2, 6920, 124601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "は～い、良い子は\nもう寝る時間ですよ\n素敵な初夢が\n見られますように♪", 1, 1, 3, 0, 3, 6921, 124601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "このフカフカちゃんも\n天日干ししたいけど…\nその前にちょっと\n休憩しちゃおうかしら", 1, 1, 0, 0, 2, 6923, 124601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うふふ、かわいい～\nアオイちゃんや\nハツネちゃんも\nきっと気に入るわね♪", 1, 1, 0, 0, 3, 6924, 124601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お正月は空気も\n透き通ってるから\nいつも以上に\n素敵な香りに感じるわ", 1, 1, 0, 0, 4, 6925, 124601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "火のぬくもりに\n抱かれていると\n不思議と眠気が…\nふああ…", 1, 1, 0, 0, 5, 6926, 124601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "愛情のこもった料理は\nおいしいだけじゃなく\n心も満たされちゃう♪", 1, 1, 0, 0, 7, 6928, 124601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お正月のお作法も\nたくさんあるのね～\n先生も日々勉強だわ～", 1, 1, 0, 0, 8, 6929, 124601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "少年への感謝をどんな\n形で伝えるべきか…\n言葉よりも物の方が\n良いのだろうか？", 1, 12, 0, 0, 103, 6931, 104701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "話し相手になって\nもらってもいいかな？\n少年と一緒にいると\nとても楽しいんだ", 1, 12, 0, 0, 103, 6932, 104701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お正月だからって\nギャングのボスは\n油断できないよ～？", 1, 1, 0, 0, 1, 6933, 124501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "イノリちゃんがお年玉\n欲しいんだって♪\n悪い子だったし\n10ルピでいいかな☆", 1, 1, 0, 0, 2, 6934, 124501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お正月だからお雑煮を\n作ったんだけど\nカヤちゃんには\n大好評だったよ♪", 1, 1, 0, 0, 3, 6935, 124501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ラーゴがお餅を\n食べたがってるけど…\nこの子にあげるのは\nちょっと心配かな～？", 1, 1, 0, 0, 4, 6936, 124501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お正月は晴れ着の子が\nやっぱり多いよね～\n今度、私の漫画でも\n出してみようかな？", 1, 1, 0, 0, 5, 6937, 124501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "君と二人っきりで\n神殿に初詣…\n行ってみたいな～♪", 1, 4, 0, 0, 101, 6938, 124501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "君とお正月を\n過ごしていると\n面倒なことも\n全部忘れられそう…", 1, 8, 0, 0, 102, 6939, 124501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この着物、どう思う？\n似合ってたら\nうれしいけどな～♪", 1, 1, 0, 0, 1, 6940, 124501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "袖を引っ張ったり\nしないでね？\n着崩れしちゃうから♪", 1, 1, 0, 0, 2, 6941, 124501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お正月ならではの\n漫画ネタってある？\n参考にしたいんだよね", 1, 1, 0, 0, 3, 6942, 124501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "昔は凧あげとか\n駒回しとかしてたけど\n大人になるとそういう\n機会も減るよね…", 1, 1, 0, 0, 4, 6943, 124501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "君にもお年玉を\nあげようか？\n好きな額を言ってね☆", 1, 4, 0, 0, 101, 6944, 124501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "来年も、再来年も…\nこうしてみんなで\n新年がお祝いできると\n素敵だね～♪", 1, 8, 0, 0, 102, 6945, 124501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよ～♪\n朝ごはんは\n焼いたお餅とか\nどうかな？", 1, 1, 1, 0, 1, 6946, 124501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼だね～\nおせちを用意したから\nみんなで食べよっか", 1, 1, 2, 0, 2, 6947, 124501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もうこんな時間だね～\n一日中着物だったから\n肩が凝っちゃったよ", 1, 1, 3, 0, 3, 6948, 124501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "このまま寝たら\nしわになるかな？\n着替えた方がいい？", 1, 1, 0, 0, 2, 6950, 124501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こういうのって\nラーゴのおもちゃに\nちょうどいいかも♪", 1, 1, 0, 0, 3, 6951, 124501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい香りだけど\n着物に移らないように\n気をつけないとね", 1, 1, 0, 0, 4, 6952, 124501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ちょうどいい\n火加減だね♪\nぽかぽかするよ～♪", 1, 1, 0, 0, 5, 6953, 124501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "着物の時は\nほどほどにしないと…\n欲張ったら、お腹が\n苦しくなるからね～", 1, 1, 0, 0, 7, 6955, 124501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふーん\nイノリちゃんは最近\nこんな漫画を\n読んでるんだ～♪", 1, 1, 0, 0, 8, 6956, 124501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "楽園の気配は…ナシ！\nみんな前を向いてるね\nそうこなくっちゃ！", 1, 1, 0, 0, 1, 6958, 124801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "レッスンの成果も\nこの格好くらい\n目に見えたら\n分かりやすいのになぁ", 1, 1, 0, 0, 2, 6959, 124801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "次の曲はワイルドで\n悪い感じもいいなぁ…\nカヤとムイミちゃん\nみたいなイメージで！", 1, 1, 0, 0, 3, 6960, 124801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "私…服、着てるよね？\nうんうん、着てる！\nあんまり体が軽いと\n急に焦る瞬間あるな～", 1, 1, 0, 0, 4, 6961, 124801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "最近ファンの年齢層が\n幅広くなってきたかも\nこれって私…国民的\nアイドルに近づいた？", 1, 1, 0, 0, 5, 6962, 124801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "パティスリーとコラボ\nしたらどんな感じかな\n…ノゾミンマカロン♪\nとか？　…いけるかも", 1, 4, 0, 0, 101, 6963, 124801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "彼と一緒にいると…\nうれしくって苦しいな\nでも両方諦められない\n…アイドルらしいや", 1, 8, 0, 0, 102, 6964, 124801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この格好、チカと\nツムギも似合うと\n思うんだけどな～\nキミもそう思うよね？", 1, 1, 0, 0, 1, 6965, 124801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "街を歩いてたらね？\nライブとか写真集の\n告知イベントだって\n思われちゃった！", 1, 1, 0, 0, 2, 6966, 124801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私が持ってた剣に\n結んであったリボン…\n何か気づいてくれた？\nヒントは…色だよ♪", 1, 1, 0, 0, 3, 6967, 124801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "キミの気配ってすごく\n分かりやすいんだよ\n近くにいたらすぐに\nピンとくるんだ♪", 1, 1, 0, 0, 4, 6968, 124801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あ、それ…はじめて\nキミにあげた音響盤！\nね、一緒に聴こうよ", 1, 4, 0, 0, 101, 6969, 124801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ぼうっとしてたら\nイタズラしちゃうよ♪\nもっともっと私のこと\n見てないとダーメ！", 1, 8, 0, 0, 102, 6970, 124801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "朝は今日のはじまり！\n昨日よりももっと\nいい一日にしようね♪", 1, 1, 1, 0, 1, 6971, 124801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "レッスンがんばったし\nまだお昼だし…\nランチ大盛りにしても\nセーフだよね！", 1, 1, 2, 0, 2, 6972, 124801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "寝る前に明日の予定を\n確認しておかないと！\nえっと、集合場所は…", 1, 1, 3, 0, 3, 6973, 124801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "一休みのつもりが\nなが～い休憩になるの\nあるあるだよね～", 1, 1, 0, 0, 2, 6975, 124801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ノゾミンの熱烈ハグ♪\n…ここだけ聞いたら\nスクープになっちゃう\nかも？　なーんて！", 1, 1, 0, 0, 3, 6976, 124801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "香りってなんだか\n記憶に残るよねー\n…こういうの私だけ？\nみんなも同じだよね？", 1, 1, 0, 0, 4, 6977, 124801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "なんだかライブの\n熱気を思い出して\nきちゃった…！\nレッスンしよっと！", 1, 1, 0, 0, 5, 6978, 124801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お腹が減ってたら\nいいパフォーマンスは\nできないもんね！\n…んー、おいし～♪", 1, 1, 0, 0, 7, 6980, 124801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい歌詞を書くには\nしっくりくる言葉を\n知っておかないと！", 1, 1, 0, 0, 8, 6981, 124801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "うだうだ悩んでる\nヤツがいたら\nオレのとこ連れてこい\n背中押してやるからさ", 1, 1, 0, 0, 1, 6983, 124901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "最近どことなく\nファミリーの連中が\n浮き足立ってんだよな\n…なんでだ？", 1, 1, 0, 0, 2, 6984, 124901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "イノリのヤツ\nやたらと脇腹\nつついてきやがって…\nくすぐったいっての", 1, 1, 0, 0, 3, 6985, 124901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "身軽になったのは\nいいが…くそっ\nどこもかしこも\nスースーする…", 1, 1, 0, 0, 4, 6986, 124901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "思い出に浸るのも\n悪くはないが\nやっぱ人間\n未来に生きないとな！", 1, 1, 0, 0, 5, 6987, 124901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "またムイミやノゾミと\nツルむのもいいな\nそこにカレもいたら\nなおいいぜ", 1, 4, 0, 0, 101, 6988, 124901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "未来のことを考えると\nなぜかいつもカレが\n頭に浮かんじまう…\nええい、やめだやめ！", 1, 8, 0, 0, 102, 6989, 124901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ちょっくらオレと\n腕相撲しねえか？\nこの新しい力\n試させてくれ！", 1, 1, 0, 0, 1, 6990, 124901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なあ、悪いが上着\n貸してくれないか？\nこのカッコ、割と\n肌寒いんだよ…", 1, 1, 0, 0, 2, 6991, 124901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おっ、生まれ変わった\n喧嘩屋にご用命か？\nいいぜ、キミの\n依頼なら二つ返事だ！", 1, 1, 0, 0, 3, 6992, 124901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なんか悩みごとが\nあるなら、なんでも\n話してくれよな\nオレとキミの仲だろ？", 1, 1, 0, 0, 4, 6993, 124901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "このカッコが\nきれいだって？\nや、やめろよ…\n照れくさいだろ…", 1, 4, 0, 0, 101, 6994, 124901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "キミがいると、なにを\nするにも退屈しないぜ\nだから今日も\n付き合ってくれよな！", 1, 8, 0, 0, 102, 6995, 124901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよう！　新しい\n未来がやってきたぜ！\n一秒だって時間は\n無駄にするなよ？", 1, 1, 1, 0, 1, 6996, 124901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そろそろ昼飯に\nしないか？\n穴場のうまい店に\n案内してやるからさ", 1, 1, 2, 0, 2, 6997, 124901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜更かしは\n未来の前借りだ\n体壊さないように\nほどほどにしとけよ？", 1, 1, 3, 0, 3, 6998, 124901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お、いいなこれ\nファミリーの\nアジトにも\n置いときたいぜ", 1, 1, 0, 0, 2, 7000, 124901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "べ、別にかわいいもん\nなんて、そんなに\n好きじゃないし…\n嫌いでもないけどさ", 1, 1, 0, 0, 3, 7001, 124901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "嗅いだことない\n香りだな\nなかなか気に入った！", 1, 1, 0, 0, 4, 7002, 124901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "薄着で暖を取るのって\n最高にぜいたくだよな\nへへっ、堪能させて\nもらうとするか", 1, 1, 0, 0, 5, 7003, 124901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こいつはいいや\nこのカッコだと\n消耗が激しくて\nやたら腹が減るからな", 1, 1, 0, 0, 7, 7005, 124901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "甘辛コーデ…\nへぇ、そんなのが\nあるのか…\nわ、悪くねえかもな", 1, 1, 0, 0, 8, 7006, 124901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "はぁ…\nもっともっと長い時間\n先輩と過ごせたらなぁ", 1, 12, 0, 0, 103, 7008, 105501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "えへへ…♪\n先輩が、すぐそばに\nいてくれるなんて…\n幸せだなぁ…♪", 1, 12, 0, 0, 103, 7009, 105501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "はーっはっは！\n強くてカッコいい\n大悪党、ここに参上！", 1, 1, 0, 0, 1, 7010, 125001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "みんなおしゃれで\nカッコいいけど\n今のアタシも\n負けてないぞ！", 1, 1, 0, 0, 2, 7011, 125001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ギャングのアジトも\nよかったけど\nここも賑やかで\n悪くないな！", 1, 1, 0, 0, 3, 7012, 125001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "悪党にも休息は必要だ\nここならゆっくり\n休めそうだ！\n邪魔するぞ！", 1, 1, 0, 0, 4, 7013, 125001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "いっぱい人がいるな…\nアタシの悪党っぷりを\n広めるチャンスだ！", 1, 1, 0, 0, 5, 7014, 125001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここにいれば\nいい思い出がたくさん\n作れそうだな！", 1, 4, 0, 0, 101, 7015, 125001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "いつかアタシも\nオクトーと一緒に\nこんなアジトを手に\n入れてやるからな！", 1, 8, 0, 0, 102, 7016, 125001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ん？　アタシの格好が\n気になるのか？\nならもっと近くで\n見てもいいぞ！", 1, 1, 0, 0, 1, 7017, 125001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おい、暇だから\nアタシと遊べ！\nカヤとノゾミも呼んで\n大騒ぎするぞ！", 1, 1, 0, 0, 2, 7018, 125001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "オマエ、前より強く\nなってないか！？\nくっそー！　アタシも\n負けてらんないぞー！", 1, 1, 0, 0, 3, 7019, 125001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日は特別にアタシの\n思い出話を聞かせて\nやる！　さーて、どの\n悪事から話そうか…", 1, 1, 0, 0, 4, 7020, 125001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "オマエとの付き合いも\n結構長くなったよな？\nこれからも\nよろしく頼むぞ！", 1, 4, 0, 0, 101, 7021, 125001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "オマエまでいなく\nなったらアタシが困る\n勝手にどっか\n行くんじゃないぞ！", 1, 8, 0, 0, 102, 7022, 125001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "起きろ、起きろー！\n大悪党の朝は早いんだ\nさっさと\n顔洗ってこい！", 1, 1, 1, 0, 1, 7023, 125001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "昼飯どうしようか…\nそうだ！　オマエの\nおススメに連れてけ！\nいい店期待してるぞ！", 1, 1, 2, 0, 2, 7024, 125001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "明日を思い切り\n楽しむためにも\n早く寝とけよ！\nまた明日な！", 1, 1, 3, 0, 3, 7025, 125001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こうやって座ると…\nおお！　すっごく悪党\nっぽいぞ！", 1, 1, 0, 0, 2, 7027, 125001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "オマエ、仲間に\nなりたそうな目を\nしてるな！", 1, 1, 0, 0, 3, 7028, 125001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うん、いい匂いだ！\n何の匂いかは全然\n分かんないけどな！", 1, 1, 0, 0, 4, 7029, 125001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "炎ってなんだか\nカッコいいよな！\n見てるだけで心が\n燃えてくるぞ！", 1, 1, 0, 0, 5, 7030, 125001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いっぱい食べれば\nちょっとは\n背が伸びるかな…？", 1, 1, 0, 0, 7, 7032, 125001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ぐぬぅ…たまには\n本でもと思ったけど\nやっぱりアタシは\nバトル担当だな！", 1, 1, 0, 0, 8, 7033, 125001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お邪魔しまーす★\nなーんてもう\nすっかりくつろいで\nますけどー♪", 1, 1, 0, 0, 1, 7035, 118201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "騎士さんの\nギルドハウス…★\nなんだかドキドキ\nしちゃいます♪", 1, 1, 0, 0, 2, 7036, 118201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ぺんぽこりんの\n置き場所はー…\nま、このへんで★", 1, 1, 0, 0, 3, 7037, 118201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "うーん、これは\n壊しがいがありそ…\nウソウソ、冗談です★", 1, 1, 0, 0, 4, 7038, 118201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "オトモダチでも\n作ってみますかー\nくすっ★", 1, 1, 0, 0, 5, 7039, 118201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "退屈ですねー\n騎士さん、構って\nくれないかなー", 1, 4, 0, 0, 101, 7040, 118201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "もっともっと早く\n騎士さんに逢えてたら\n…なんて、わたし\nらしくないですね", 1, 8, 0, 0, 102, 7041, 118201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "わたし一人にするの\n不安だったりします？\nちゃーんとイイコに\nしてますってー★", 1, 1, 0, 0, 1, 7042, 118201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お出かけします？\nそれともおうちで\nまったりしますー？", 1, 1, 0, 0, 2, 7043, 118201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "女の子をすぐ家に\nあげちゃうなんて…\nふふ、やっぱり隅に\n置けないですねー★", 1, 1, 0, 0, 3, 7044, 118201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "騎士さん騎士さーん★\nウザ絡みしても\nいいですかー？\n騎士さんってばー★", 1, 1, 0, 0, 4, 7045, 118201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "一緒にゴロゴロ\nしませんかー？\nコイビトっぽくて\n楽しそうですし♪", 1, 4, 0, 0, 101, 7046, 118201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "うふふ★\nこーやってあなたに\n優しくされると…\nときめいちゃいます♪", 1, 8, 0, 0, 102, 7047, 118201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようございます★\nあれれー？\nまだおねむですかー？", 1, 1, 1, 0, 1, 7048, 118201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "騎士さん、お昼は\nもう食べましたー？\nわたし、ラーメンが\n食べたいでーす★", 1, 1, 2, 0, 2, 7049, 118201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あ、夜更かししてちゃ\nダメですよー？\nほらほら、お布団に\nごーごー★", 1, 1, 3, 0, 3, 7050, 118201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふかふかのふわふわー\nついついまーったり\nしちゃいます★", 1, 1, 0, 0, 2, 7052, 118201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かっわいー★\n名前はー、んー…\nぱんぱかぷんで★", 1, 1, 0, 0, 3, 7053, 118201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "くんくん★\nうーん…\nたぶん素敵な香り♪", 1, 1, 0, 0, 4, 7054, 118201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったかーい★\n心も体も\nぽっかぽかですねー★", 1, 1, 0, 0, 5, 7055, 118201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もぐもぐ★\nんー、おいしくって\n止まらないですねー★", 1, 1, 0, 0, 7, 7057, 118201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "へー★\nほほー★\nふんふーん★", 1, 1, 0, 0, 8, 7058, 118201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "さ～て時間もあるし\nラテアートの練習でも\nするかにゃ～♪", 1, 1, 0, 0, 1, 7060, 125101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "カフェの猫たちも\n元気に働いてくれて\nお客さんからの評判も\n上々にゃ～♪", 1, 1, 0, 0, 2, 7061, 125101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "カフェのカウンターに\nファントムキャッツの\n目安箱を置こうかにゃ", 1, 1, 0, 0, 3, 7062, 125101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あんこもコーヒーも\n原料は豆にゃ～\nそりゃ相性いいに\n決まってるにゃ♪", 1, 1, 0, 0, 4, 7063, 125101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あいつの為に\nタマキ特製ブレンドを\n開発したいにゃ♪", 1, 1, 0, 0, 5, 7064, 125101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あいつと働いてると\nなぜかあっという間に\n閉店時間が\n来ちゃうのにゃ～", 1, 4, 0, 0, 101, 7065, 125101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あいつといると\n新しいアイデアが\n溢れるほど\n出てきちゃうのにゃ♪", 1, 8, 0, 0, 102, 7066, 125101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "キミがコーヒーを\n淹れてくれたのにゃ？\nにゃはは、常温で\n猫舌にも優しいにゃ♪", 1, 1, 0, 0, 1, 7067, 125101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "料理が上手くなったら\nカフェご飯メニューも\n充実させたいにゃ～", 1, 1, 0, 0, 2, 7068, 125101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お店の経営は大変\nだけど、キミとなら\n乗り越えられるって\n確信してるにゃ！", 1, 1, 0, 0, 3, 7069, 125101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ん？　この制服\n似合ってるにゃ？\nにゃはは！　褒めても\n１ルピも出ないにゃ♪", 1, 1, 0, 0, 4, 7070, 125101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あたしが好きなのは\n猫に好かれる人にゃ♪\n…ん、キミ猫に懐かれ\nまくってるにゃ…", 1, 4, 0, 0, 101, 7071, 125101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "キミの制服姿\nけっこーイケてるにゃ\nあたしがお客さんなら\n通いつめちゃうにゃ♪", 1, 8, 0, 0, 102, 7072, 125101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "朝は一杯の\nコーヒーから始まる…\nにゃふふん♪\nシャレた朝だにゃ～♪", 1, 1, 1, 0, 1, 7073, 125101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼はたい焼きと\nコーヒーのセットで\nまったり休憩\nしようにゃ～♪", 1, 1, 2, 0, 2, 7074, 125101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "コーヒー淹れたにゃ\n星空を見ながら\n二人で飲もうにゃ…♪", 1, 1, 3, 0, 3, 7075, 125101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふにゃ～ん…ゴロゴロ\nコーヒーと同じくらい\n癒されるにゃ～…♪", 1, 1, 0, 0, 2, 7077, 125101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こいつかわいいにゃ～\nうちの猫たちとも\nいい勝負なのにゃ！", 1, 1, 0, 0, 3, 7078, 125101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ん～いい香りだにゃ～\nコーヒーもそうだけど\n香りの癒し効果は\n抜群にゃ～♪", 1, 1, 0, 0, 4, 7079, 125101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "猫に暖かいものは\nまるまれと言ってる\nようなもんにゃ…♪\nまるまる…", 1, 1, 0, 0, 5, 7080, 125101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "にゃは～うまいにゃ♪\nカフェの新メニューに\n採用しちゃおう\nかにゃ～♪", 1, 1, 0, 0, 7, 7082, 125101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "にゃるほど…\n調べれば調べるほど\nコーヒーは\n奥が深いのにゃ…", 1, 1, 0, 0, 8, 7083, 125101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ワタシの手料理は\n坊やの口に合うらしい\n胃袋は討ち取ったぞ♪", 1, 12, 0, 0, 103, 7085, 107101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "胸に再度触れると\n誓約は無効になるのか\n…だと？　鋭い質問だ\n安心しろ。ならん☆", 1, 12, 0, 0, 103, 7086, 107101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ようやくこの服にも\n慣れてきたかな？\nふふっ、たまには\nこういう服も悪くない", 1, 1, 0, 0, 1, 7087, 125201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今日も店は好調だが、\n私は軍人だからな\n当初の目的も\n忘れないようにしよう", 1, 1, 0, 0, 2, 7088, 125201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "猫が近くに来ると\nついつい見てしまうな\n仕事が終わったら\n遊んでやるかな♪", 1, 1, 0, 0, 3, 7089, 125201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "コーヒーの淹れ方が\nうまくなってきたな♪\nどうせならお菓子も\n研究してみるか！", 1, 1, 0, 0, 4, 7090, 125201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "利益を考えると\n価格をあげるべきか？\n…いや、利益よりも\n皆の笑顔の方が大事だ", 1, 1, 0, 0, 5, 7091, 125201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "またあいつを\n呼んでしまったな…\n今度、コーヒーでも\n淹れて労ってやるか♪", 1, 4, 0, 0, 101, 7092, 125201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "次に入荷するお菓子は\nあいつの意見を聞くか\nどんな味が好きか\n気になるしな", 1, 8, 0, 0, 102, 7093, 125201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なに、コーヒーを\n淹れてほしい？\nふふっ♪\n仕方のない奴だな！", 1, 1, 0, 0, 1, 7094, 125201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "…そうだ。\nコーヒーと乾パンは\n合うのではないか？\n今度試してみよう！", 1, 1, 0, 0, 2, 7095, 125201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "コーヒーもいいが、\nココアも悪くないぞ♪\n貴公も一緒にどうだ？", 1, 1, 0, 0, 3, 7096, 125201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "魔法少女に比べれば\n大人しい服だろう？\nだが、カフェには\nこの服でバッチリだ！", 1, 1, 0, 0, 4, 7097, 125201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "貴公はコーヒーを\n飲めるのかっ？それは\nうらやましいな…", 1, 4, 0, 0, 101, 7098, 125201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "貴公の働きぶりには\n目を見張るものがある\nその姿は、その…\n結構かっこいいぞっ！", 1, 8, 0, 0, 102, 7099, 125201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "さぁ、今日も一日、\n頑張るぞっ！\n気合いを入れて\n開店準備だっ！", 1, 1, 1, 0, 1, 7100, 125201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "昼は稼ぎ時だからな！\n荒波を乗り切るため\n貴公も一緒に\n頑張ってくれるか？", 1, 1, 2, 0, 2, 7101, 125201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふあぁ…もう夜か\n今日は頑張ったからな\nはやめに休むとしよう", 1, 1, 3, 0, 3, 7102, 125201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はふぅ～…\nたまにはこうして\nゆったりするのも\n悪くないな…", 1, 1, 0, 0, 2, 7104, 125201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "猫もかわいいが、\nこいつもなかなか…\n見ていると、\n撫でたくなるな♪", 1, 1, 0, 0, 3, 7105, 125201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これは良い香りだな\nふむ、コーヒーも\nより香り高いものが\n作れるよう試すか！", 1, 1, 0, 0, 4, 7106, 125201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふぅ～…\n体を温めなければ、\nすぐに動けないからな", 1, 1, 0, 0, 5, 7107, 125201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これもうまいが、\n私はお菓子も…\nいや、食べすぎは\n体に良くないな", 1, 1, 0, 0, 7, 7109, 125201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "…書物は参考になるな\n次はこのやり方を\n試してみるかな", 1, 1, 0, 0, 8, 7110, 125201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "愛と希望の魔法少女！\n弟くんの平和は\nお姉ちゃんが守るよ♪", 1, 1, 0, 0, 1, 7112, 120001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "魔法少女の戦い方を\n見せてあげる！\nお姉ちゃんチョップ♪", 1, 1, 0, 0, 2, 7113, 120001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "闇の力が高まってる…\n早く弟くんを\nギュ～ってしないと！", 1, 1, 0, 0, 3, 7114, 120001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ミニ弟くんを\n部屋中に呼び出して\n弟くんハウスに～♪", 1, 1, 0, 0, 4, 7115, 120001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "決めポーズの練習でも\nしよっかな…\nはい、ポーズ♪", 1, 1, 0, 0, 5, 7116, 120001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "魔法少女の力で\n弟くんのそばに\n瞬間移動したいなぁ", 1, 4, 0, 0, 101, 7117, 120001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "禁じられた必殺技\nその名も──\n闇のお姉ちゃん頭突き", 1, 8, 0, 0, 102, 7118, 120001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "魔法少女な\nお姉ちゃんに\n興味津々だねっ？\n全部教えてあげるね♪", 1, 1, 0, 0, 1, 7119, 120001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "闇のお姉ちゃんとして\n世界を弟くんのものに\n…な～んて冗談冗談♪", 1, 1, 0, 0, 2, 7120, 120001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "弟くんって魔法少女に\nなりたがってたよね？\n…うん。応援するね！", 1, 1, 0, 0, 3, 7121, 120001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "サインが欲しいの？\nふふっ、任せて！\n愛する弟くんへ、と♪", 1, 1, 0, 0, 4, 7122, 120001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふんふふ～ん♪\n魔法少女お姉ちゃんの\nテーマソング完成っ♪", 1, 4, 0, 0, 101, 7123, 120001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "えへへ、弟くんからの\n愛と希望を感じるよ…\n更に変身しちゃうぞ♪", 1, 8, 0, 0, 102, 7124, 120001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "弟くんの朝を魔法で\n守りに来たよ！\n希望の朝だねっ♪", 1, 1, 1, 0, 1, 7125, 120001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "弟くんのお昼を魔法で\n守りに来たよ！\n愛のお弁当をどうぞ♪", 1, 1, 2, 0, 2, 7126, 120001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "弟くんの夜を魔法で\n守りに来たよ！\nここからは闇の時間♪", 1, 1, 3, 0, 3, 7127, 120001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "魔法少女の一休み♪\n愛と希望と…闇の力が\n回復していくね～♪", 1, 1, 0, 0, 2, 7129, 120001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいいかわいい♪\nお姉ちゃんの魔法で\n増やしちゃうぞ…♪", 1, 1, 0, 0, 3, 7130, 120001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この香り…\n弟くんと同じ…\nくんくん、くんくん…", 1, 1, 0, 0, 4, 7131, 120001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "体を温めておいて…\nこの熱を、ぜ～んぶ\n弟くんに伝えるよ…！", 1, 1, 0, 0, 5, 7132, 120001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ミニ弟くんを呼ぶには\nいっぱい食べないと…\n魔法ってお腹減るから", 1, 1, 0, 0, 7, 7134, 120001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "弟くんの平和を守る…\nそのためには\nチェックチェック…♪", 1, 1, 0, 0, 8, 7135, 120001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この服も、最初は\n慣れなかったのですが\n今ではすっかり\n体に馴染んでおります", 1, 1, 0, 0, 1, 7137, 125301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "自然を守るためにも\nお勉強は欠かせません\n日々精進でございます", 1, 1, 0, 0, 2, 7138, 125301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "採取した薬草を\n調合しておきましょう\nいつ必要になるか\n分かりませんからね", 1, 1, 0, 0, 3, 7139, 125301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "合宿は過酷でしたが\nおかげでちょっと\n力持ちになったような\n気がいたします…♪", 1, 1, 0, 0, 4, 7140, 125301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "わんわんのほうの\n主さまに、ごはんを\nあげる時間ですね…", 1, 1, 0, 0, 5, 7141, 125301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "もっともっと訓練や\nお勉強を重ねて\n主さまのお役に\n立たなくては…", 1, 4, 0, 0, 101, 7142, 125301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "おや、そろそろ\nわんわんのほうの\n主さまをお散歩に\n連れていく時間です", 1, 8, 0, 0, 102, 7143, 125301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "主さま、よろしければ\nお散歩に行きませんか\n今日の森は穏やかで\n過ごしやすいかと", 1, 1, 0, 0, 1, 7144, 125301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "わんわんのほうの\n主さまも、今では\n【美食殿】の大切な\n仲間でございます", 1, 1, 0, 0, 2, 7145, 125301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "森には危険が\nいっぱいでございます\nケガをなさらないよう\nお気をつけください", 1, 1, 0, 0, 3, 7146, 125301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もし森や動物のことで\n気になることがあれば\n気軽にお尋ねください", 1, 1, 0, 0, 4, 7147, 125301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "森へ行く前には\n必ず装備の点検を\nお願いいたします\n約束ですよ、主さま？", 1, 4, 0, 0, 101, 7148, 125301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "森の中で主さまたちと\nゆったり過ごす時間は\nわたくしにとって\n大切なひとときです", 1, 8, 0, 0, 102, 7149, 125301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようございます\n朝ごはんをしっかり\n食べて、お勤めに\n備えましょう", 1, 1, 1, 0, 1, 7150, 125301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "わんわんのほうの\n主さまにおやつを\nあげる時間です\nどうぞ、召し上がれ", 1, 1, 2, 0, 2, 7151, 125301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どれだけ野営に\n慣れたとしても\nベッドで眠れるなら\nそうしたいところです", 1, 1, 3, 0, 3, 7152, 125301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こちらなのですが\nわんわんのほうの\n主さまもお気に入りの\nようです", 1, 1, 0, 0, 2, 7154, 125301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "わんわんのほうの\n主さまにも、こうした\n玩具を与えるべき\nでしょうか…？", 1, 1, 0, 0, 3, 7155, 125301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "心地よい香りです\n次は、自然由来の\n香料も試しましょう", 1, 1, 0, 0, 4, 7156, 125301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "暖をとるのは\n野営の時などに\nとても大事なことで\nございますね", 1, 1, 0, 0, 5, 7157, 125301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "食べられる時に食べて\nおくのも、大自然では\n重要でございます", 1, 1, 0, 0, 7, 7159, 125301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "より知識を蓄え\n自然の力を活用できる\nようにいたしましょう", 1, 1, 0, 0, 8, 7160, 125301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今回の「冥風戦記」も\nなかなかよく書けたぞ\nふふっ、シグルドも\n喜んでくれるかな？", 1, 12, 0, 0, 103, 7162, 100901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "貴様のためならば\n我が真なる魔力\nいつでも\n解き放とうぞ！", 1, 12, 0, 0, 103, 7163, 100901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ニャットちゃんに\nブラッシングを\nしてあげようかな", 1, 1, 0, 0, 1, 7164, 125401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ふふ…\nこれがレンジャーの\n制服ですか", 1, 1, 0, 0, 2, 7165, 125401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "そろそろ\nパトロールの\n時間だ…", 1, 1, 0, 0, 3, 7166, 125401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "魔物について\nもっと知りたいな\n本を探しに行こう", 1, 1, 0, 0, 4, 7167, 125401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "レンジャーになって\n体調を崩す日が\n減った気がします…", 1, 1, 0, 0, 5, 7168, 125401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "本だけじゃわからない\n世界のことを、\nもっと知りたいです…", 1, 4, 0, 0, 101, 7169, 125401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "こんな私にも\nできることはある…\nそう信じています", 1, 8, 0, 0, 102, 7170, 125401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あの…良かったら\n一緒に見回りへ\n行きませんか？", 1, 1, 0, 0, 1, 7171, 125401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "またあなたと一緒に\nレンジャーの仕事が\nできたらうれしいです", 1, 1, 0, 0, 2, 7172, 125401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "けほっこほっ\n…だ、大丈夫です\nたまに出るだけなので", 1, 1, 0, 0, 3, 7173, 125401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ニャットちゃん\nあなたにも\n懐いてきましたね", 1, 1, 0, 0, 4, 7174, 125401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ニャットちゃんと\n散歩に出かけようかな\n…あの人も誘って", 1, 4, 0, 0, 101, 7175, 125401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたと肩を並べて\nレンジャーの仕事が\nできてうれしいです…", 1, 8, 0, 0, 102, 7176, 125401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようございます\nレンジャー・シオリ\n元気に出勤します", 1, 1, 1, 0, 1, 7177, 125401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ニャットちゃんにも\nお昼ごはんを\n用意しなくちゃ", 1, 1, 2, 0, 2, 7178, 125401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "明日は仕事だから\n早起きしなくちゃ\nおやすみなさい", 1, 1, 3, 0, 3, 7179, 125401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふぅ…仕事で\n走り回ったので\n少し休憩します", 1, 1, 0, 0, 2, 7181, 125401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "仕事が終わった後に\nこうして触れていると\n癒されますね…", 1, 1, 0, 0, 3, 7182, 125401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "森の香りがします…\n今日はよく\n眠れそうです…", 1, 1, 0, 0, 4, 7183, 125401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ニャットちゃんと\n同じぐらい\n暖かいです…", 1, 1, 0, 0, 5, 7184, 125401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "レンジャーの仕事を\nしていると\nお腹が空きます", 1, 1, 0, 0, 7, 7186, 125401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "どんなに忙しくても\n読書だけは\nやめられませんね", 1, 1, 0, 0, 8, 7187, 125401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "兄様のお留守の間\nわたくしが、兄様の\n御家を守ります！", 1, 1, 0, 0, 1, 7189, 125801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "に、に、兄様の御家…\n招待いただいた以上は\n失礼のないよう\n振る舞わなくては…！", 1, 1, 0, 0, 2, 7190, 125801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ふふっ…兄様の素敵な\nお人柄が、家中から\nにじみ出ています…♪", 1, 1, 0, 0, 3, 7191, 125801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "靴を脱がずに\n御家に上がるのに\n慣れていなくて…あ！\n飛べばいいのでは…！", 1, 1, 0, 0, 4, 7192, 125801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここに花を生けたら\n華やぎそうですね\n兄様はお花…\n好きでしょうか…？", 1, 1, 0, 0, 5, 7193, 125801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ずっとここにいたい…\nなどと言ったら\n兄様を困らせて\nしまいますよね…", 1, 4, 0, 0, 101, 7194, 125801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お料理などを作って\n兄様を出迎えられたら\nいいのに…料理の腕も\n磨かなくては…！", 1, 8, 0, 0, 102, 7195, 125801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "このような姿で\n失礼しております…！\nネ、ネズミがいたので\nつい本気を…！", 1, 1, 0, 0, 1, 7196, 125801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おかえりなさいませ\nお忙しい御身をどうぞ\n癒してくださいね", 1, 1, 0, 0, 2, 7197, 125801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "兄様の話し相手…！？\nど、どうしましょう…\nわたくしには面白い\n話なんて…はうぅ…！", 1, 1, 0, 0, 3, 7198, 125801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なぜか、動物などに\n好かれる体質で…\n迷い猫捜しのバイトの\nお役に立てますか？", 1, 1, 0, 0, 4, 7199, 125801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "に、兄様の家に来たら\n『添い寝』をするのが\n礼儀とリアさんが…\n本当…でしょうか？", 1, 4, 0, 0, 101, 7200, 125801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "わたくしはここに\nいて…よいのですね？\n『仲間』として…", 1, 8, 0, 0, 102, 7201, 125801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "朝は得意なんです\nもし兄様が苦手なら\nわたくしが起こして\n差し上げますよ", 1, 1, 1, 0, 1, 7202, 125801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "兄様…昼餉を作って\nみたのですが、失敗\nしてしまいました…", 1, 1, 2, 0, 2, 7203, 125801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふあぁ…\nはっ！　お…\nお恥ずかしい所を…！", 1, 1, 3, 0, 3, 7204, 125801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こ、これは…！\n人様の御家なのに\nつい…駄目になって…\nしまいますぅ…！", 1, 1, 0, 0, 2, 7206, 125801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "と、飛びかかっては\nこない…ですよね…？", 1, 1, 0, 0, 3, 7207, 125801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "すぅ…はぁ…\nこのかぐわしさ\n心が洗われます…", 1, 1, 0, 0, 4, 7208, 125801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "なんて暖かい…\n兄様の優しくお広い\n御心のようです…♪", 1, 1, 0, 0, 5, 7209, 125801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こんなに食べていたら\nはしたない…\nでしょうか？", 1, 1, 0, 0, 7, 7211, 125801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "兄様の本棚はまるで\n知識の宝庫ですね\n己の世間知らずぶりを\n実感してしまいます", 1, 1, 0, 0, 8, 7212, 125801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "次の冒険に向けて\n騎空士弁当を作ろっと\nまずはおにぎりから！", 1, 1, 0, 0, 1, 7214, 125501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "魔法の修行しなくちゃ\nもっともっと頼れる\n団長になりたいから！", 1, 1, 0, 0, 2, 7215, 125501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ビカラちゃんを\n遊びに誘いたいけど…\nどういう場所が\n好きなんだろう？", 1, 1, 0, 0, 3, 7216, 125501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "仕事を真面目に\nしっかりこなすことが\n騎空艇への\n一番の近道だよね", 1, 1, 0, 0, 4, 7217, 125501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここにいる人の中で\n騎空団に興味ある人\nいたりしないかな…？", 1, 1, 0, 0, 5, 7218, 125501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "これまでも想い出は\nいっぱいできたけど…\nこれからもたくさん\n想い出を作りたいな！", 1, 4, 0, 0, 101, 7219, 125501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "仲間がいるから\n騎空士は限界だって\n超えられるんだね", 1, 8, 0, 0, 102, 7220, 125501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この衣装\n魔法使いらしいでしょ\n服に着られないよう\n頑張らなくちゃね！", 1, 1, 0, 0, 1, 7221, 125501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたとなら\nどんな相手だって\n怖くなんてないよ！", 1, 1, 0, 0, 2, 7222, 125501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "カリンさんから\n新しい依頼が来たよ！\n一緒に頑張ろ！", 1, 1, 0, 0, 3, 7223, 125501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "操舵士になるのは\nずいぶん難しそう…\nいつか頼れる仲間が\n見つかるといいな", 1, 1, 0, 0, 4, 7224, 125501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "手合わせしたいの？\nいいよ、たまには\n団長らしいところ\n見せてあげるね！", 1, 4, 0, 0, 101, 7225, 125501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "まだ二人きりだけど…\nこの騎空団は\n私の宝物だよ！", 1, 8, 0, 0, 102, 7226, 125501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよう～…\n昨日は遅くまで\n魔法の研究してて…\nふわぁ…眠いなぁ…", 1, 1, 1, 0, 1, 7227, 125501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "さてと、お昼休み！\n紅茶淹れてくるから\n一緒に飲もうよ！", 1, 1, 2, 0, 2, 7228, 125501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "遅くまでお疲れさま！\n無理しない程度に\nお互い頑張ろう！", 1, 1, 3, 0, 3, 7229, 125501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "魔法の研究は\n一旦、休憩！\n頭を休めなくちゃ", 1, 1, 0, 0, 2, 7231, 125501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "魔法みたいに\nかわいい！\n騎空団に入る？\nなんてね！", 1, 1, 0, 0, 3, 7232, 125501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい匂い…\nそれになんだか\n集中力が増したかも", 1, 1, 0, 0, 4, 7233, 125501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "体だけじゃなくて\n心までポカポカする…\nなんだか魔法みたい", 1, 1, 0, 0, 5, 7234, 125501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いろいろ考え事したら\nお腹が空いちゃった！\nいただきまーす！", 1, 1, 0, 0, 7, 7236, 125501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "『摂理の彼方へ\n至るには…』\n…この魔導書\n勉強になるなあ", 1, 1, 0, 0, 8, 7237, 125501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "こちらの服を着ると\n冒険に出かけたく\nなっちゃいますね", 1, 1, 0, 0, 1, 7239, 125701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "冒険者の心得その一\n準備はできる限り\nしっかりとこなすべし\nなんてどうでしょう？", 1, 1, 0, 0, 2, 7240, 125701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "調合に必要な素材は\nあれとこれとそれと…\n時間があるとついつい\n考えちゃいますね", 1, 1, 0, 0, 3, 7241, 125701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "せっかくですから\nより良い素材を\n自分で集めたいですね", 1, 1, 0, 0, 4, 7242, 125701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "このポーションが\nみなさんの役に立つと\nうれしいですね", 1, 1, 0, 0, 5, 7243, 125701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "冒険者の心得その二\n危なくなった時は\nまず逃げるべし！\n大事なことですね", 1, 4, 0, 0, 101, 7244, 125701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あの人の冒険は\nいつも楽しそうで\n元気がもらえます", 1, 8, 0, 0, 102, 7245, 125701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ぜひおすすめしたい\n依頼があるんです\nこちらをどうぞ♪", 1, 1, 0, 0, 1, 7246, 125701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ほしいお薬があれば\n錬金術で作って\nさしあげますよ", 1, 1, 0, 0, 2, 7247, 125701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "依頼で気になる\nところがあれば\n何でもご確認ください", 1, 1, 0, 0, 3, 7248, 125701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "このギルドハウスに\n錬金術の実験設備を…\nえ？　だめですか？", 1, 1, 0, 0, 4, 7249, 125701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ポーションをどうぞ\n大丈夫、効果は\n確認済みですので♪", 1, 4, 0, 0, 101, 7250, 125701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "次はどんな冒険に\n行かれるんですか？\nいつでもお供しますよ", 1, 8, 0, 0, 102, 7251, 125701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日もいい一日に\nなりそうですね\n冒険日和といった\nところでしょうか", 1, 1, 1, 0, 1, 7252, 125701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "いい時間ですし\nランチにしませんか？\n次の冒険の\n作戦会議も兼ねて♪", 1, 1, 2, 0, 2, 7253, 125701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜も遅いですし\n明日の冒険のため\n今日は早く寝ましょう", 1, 1, 3, 0, 3, 7254, 125701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふう…やはり冒険は\n事務仕事と違いますね\n少し休憩を…", 1, 1, 0, 0, 2, 7256, 125701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "冒険は緊張の連続です\nかわいい物で\n心を癒しましょう", 1, 1, 0, 0, 3, 7257, 125701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふむ、この香りは…\n素晴らしいバランスで\n調合されていますね", 1, 1, 0, 0, 4, 7258, 125701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この火力…\n錬金術の調合に\n使えそうですね", 1, 1, 0, 0, 5, 7259, 125701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "冒険の際は食べられる\n時に食べておく！\n…とペコリーヌさんも\nおっしゃってましたね", 1, 1, 0, 0, 7, 7261, 125701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふむふむ…なるほど\nこんな調合も\nあるのですね…\n奥が深いです", 1, 1, 0, 0, 8, 7262, 125701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "アイツどこ行った…？\n戦うならアタシも\n連れてけよなー！", 1, 12, 0, 0, 103, 7264, 106101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日も悪いヤツ退治…\nの前に腹ごしらえだ！\nなんか食べに行くぞ！", 1, 12, 0, 0, 103, 7265, 106101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "みんなー！\nギルドハウスで\nぼくと握手！", 1, 1, 0, 0, 1, 7266, 125601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "種族も年齢も違うけど\nみんな楽しそうだね！\nやっぱりここはぼくの\nゆ～とぴあさ！", 1, 1, 0, 0, 2, 7267, 125601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ハハッ！　まるで\nパーティー会場だね！\nこれは楽しませがいが\nあるってものだよ！", 1, 1, 0, 0, 3, 7268, 125601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "やぁみんな！\nご利益満点のネズ耳は\nいかがかな？\nひとつ３ルピだよ！", 1, 1, 0, 0, 4, 7269, 125601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここにいれば\n遊び相手に困らないね\nかしこみかしこみ～！", 1, 1, 0, 0, 5, 7270, 125601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "どこか遊びに\n行こうかな♪\nそうだ、あの人にも\n声をかけてみよう！", 1, 4, 0, 0, 101, 7271, 125601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あの人といると\nぼくまで楽しくなって\nくるんだよね！\n毎日パレード気分さ！", 1, 8, 0, 0, 102, 7272, 125601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ネズ耳の在庫が\n切れたんだ！\n作るのを\n手伝ってくれるかい？", 1, 1, 0, 0, 1, 7273, 125601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ん？　握手かい？\nいいとも！\nいつでも何度でも\nしてあげるよ！", 1, 1, 0, 0, 2, 7274, 125601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この衣装に興味が\nあるのかい？\nきみ、なかなか\n見る目があるね！", 1, 1, 0, 0, 3, 7275, 125601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "しばらくここに厄介に\nなるつもりなんだ\nあとで街を案内\nしてくれるかい？", 1, 1, 0, 0, 4, 7276, 125601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "とっておきのチーズ\nきみと半分こだ！\nみんなには内緒だよ？", 1, 4, 0, 0, 101, 7277, 125601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "きみはいてほしい時に\nいつもそばに\nいてくれるね\n本当にかしこみだよ！", 1, 8, 0, 0, 102, 7278, 125601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "やぁ、おはよう！\n気持ちのいい朝だね\n今日も楽しくネズ耳\n売りにはげもうか！", 1, 1, 1, 0, 1, 7279, 125601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼だね！\nおいしいチーズを\n持ってきたから\nぼくと半分こだ！", 1, 1, 2, 0, 2, 7280, 125601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日も一日\n楽しかったね！\nいい夢が見られること\n間違いなしさ！", 1, 1, 3, 0, 3, 7281, 125601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ぼくにも休息は\n必要さ！\nリラックス〜\nリラックス〜♪", 1, 1, 0, 0, 2, 7283, 125601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ぼくのぬいぐるみも\n作って売ろうかな…\n修繕費も一気に\n集まるかもね！", 1, 1, 0, 0, 3, 7284, 125601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい香りだね！\nトーゴクのお香に\nよく似てるよ", 1, 1, 0, 0, 4, 7285, 125601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はぁ…温まるねぇ…\nそうだ、この火で\nチーズでも炙って\nみようかな", 1, 1, 0, 0, 5, 7286, 125601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うーん、この味…\nトーゴクを思い出すよ\nどれ、もう一つ\nいただこうか！", 1, 1, 0, 0, 7, 7288, 125601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これはランドソルの\nガイド本だね！\nありがたく参考に\nさせてもらうよ！", 1, 1, 0, 0, 8, 7289, 125601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "まさか、わらわが\nこのような装束を\n纏う日がくるとはな…\n感慨深いものじゃ", 1, 1, 0, 0, 1, 7291, 126201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "わらわの美貌で\nこのギルドハウスも\nより華やかに\nなったようじゃな", 1, 1, 0, 0, 2, 7292, 126201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "つまらぬ戦いで\n晴れ着を汚しとうない\n今日は一日\nゆっくりするかの", 1, 1, 0, 0, 3, 7293, 126201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今日はわらわが食事の\n準備をして、あやつの\n帰りを待ってやろう\n献立は何がよいかの…", 1, 1, 0, 0, 4, 7294, 126201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ユキといったか…\n掴みどころのない\nやつじゃが、根性は\n認めてやるかの", 1, 1, 0, 0, 5, 7295, 126201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "こうして、仲間と\nのんびり過ごす時間も\n悪くないのう♪", 1, 4, 0, 0, 101, 7296, 126201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あやつを誘って\n月を見に行こうかの\n夜が楽しみじゃ♪", 1, 8, 0, 0, 102, 7297, 126201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "いかなる衣装で\nあろうとも、わらわは\n完璧に着こなして\nしまうのじゃ♪", 1, 1, 0, 0, 1, 7298, 126201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なんじゃ？\nわらわの姿に\n見惚れておったとな？\nす、素直でよいのじゃ", 1, 1, 0, 0, 2, 7299, 126201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "吸血鬼であるわらわに\n相応しい装束じゃろう\nあとは真っ赤な血を\n添えたいのう…", 1, 1, 0, 0, 3, 7300, 126201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お主、疲れておるな？\nわらわが癒してやろう\nほれ、近う寄るのじゃ", 1, 1, 0, 0, 4, 7301, 126201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お主がおるだけで\nギルドハウスが\n賑やかになるのう\nある種の才能じゃな♪", 1, 4, 0, 0, 101, 7302, 126201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "眷属よ、風呂にするか\nご飯にするか\nそれとも、わらわに…\nいや、忘れるのじゃ", 1, 8, 0, 0, 102, 7303, 126201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "朝一番、わらわに\n会いたかったとな…\n眷属よ\n褒めてつかわすぞ♪", 1, 1, 1, 0, 1, 7304, 126201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "眷属よ、わらわが\n食事を用意したぞ\n忌々しい太陽に負けず\n頑張るのじゃ", 1, 1, 2, 0, 2, 7305, 126201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "眠るには惜しい夜じゃ\n眷属よ、もうしばらく\nわらわの傍に\nおるがよい", 1, 1, 3, 0, 3, 7306, 126201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふかふかじゃの♪\n今度あやつにも\n座らせてやろう", 1, 1, 0, 0, 2, 7308, 126201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "愛いやつじゃ\n少しばかり、わらわが\n遊んでやろうかの", 1, 1, 0, 0, 3, 7309, 126201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "気品ある香りじゃのう\nわらわの古城にも\n一つ欲しいところじゃ", 1, 1, 0, 0, 4, 7310, 126201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "よいぬくもりじゃ…\n忌々しい太陽とは\n大違いじゃの", 1, 1, 0, 0, 5, 7311, 126201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "味付けの参考に\nあと、もう一つだけ…\n止まらんのじゃ…", 1, 1, 0, 0, 7, 7313, 126201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "吸血鬼の弱点について\n記されておるのか…\nふむ、眠気が\nさめてしもうたぞ", 1, 1, 0, 0, 8, 7314, 126201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "このままボクが美しく\nなり続けても、彼なら\nきっと隣にいてくれる\nはずだよね！", 1, 12, 0, 0, 103, 7316, 100801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もっとそばに来なよ\nボクの隣は\nキミのための\n特等席なんだから", 1, 12, 0, 0, 103, 7317, 100801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ドレスの影響かな…\n前より黒や赤が\n好きになったかも", 1, 1, 0, 0, 1, 7318, 126301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ボクがいるだけで\nここがぐんっと\n華やかになったね♪", 1, 1, 0, 0, 2, 7319, 126301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ボクを描いた絵画\n今度持ってきて\n飾ってあげようかな", 1, 1, 0, 0, 3, 7320, 126301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ボクはボクに\n魅了されっぱなしさ♪\nまさに魔性の存在だよ", 1, 1, 0, 0, 4, 7321, 126301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "美のアドバイスを\n聞きたい人いるかい？\nボクがもっともっと\n輝かせてあげるよ♪", 1, 1, 0, 0, 5, 7322, 126301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あの人、今度はボクを\nどう褒めるだろう？\nもし語彙が尽きても\n許してあげよっと♪", 1, 4, 0, 0, 101, 7323, 126301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あの人に似合うコーデ\n考えてあげよっと\n案外、なんでも\n着こなしそうだよね♪", 1, 8, 0, 0, 102, 7324, 126301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "吸血鬼になるなんて\nやっぱりゴメンだね！\n永遠の美よりも\n大事なことがあるのさ", 1, 1, 0, 0, 1, 7325, 126301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お肌にいいものは\n大体好きだよ\nトマトジュースとかね", 1, 1, 0, 0, 2, 7326, 126301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "銀のアクセサリーが\nボクは大好きでね\nいつでもプレゼント\n待ってるよ♪", 1, 1, 0, 0, 3, 7327, 126301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "イリヤみたいにお城に\n住んでみたいんだ♪\n美を統べる者に\n相応しいでしょ？", 1, 1, 0, 0, 4, 7328, 126301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今のボクがカップを\n触ると壊しちゃうんだ\nだからほら、キミが\nボクに紅茶を飲ませて", 1, 4, 0, 0, 101, 7329, 126301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "キミも美を磨けば\nもっと輝けるはずさ！\nボクがメイクとか\nしてあげようか？", 1, 8, 0, 0, 102, 7330, 126301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "んん、まだ眠い…\n美のためにも、\nもうひと眠りするよ…", 1, 1, 1, 0, 1, 7331, 126301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ボクにぴったりな\n日傘を買ったんだ♪\n散歩にでも行こうよ！", 1, 1, 2, 0, 2, 7332, 126301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜こそボクの時間！\nさぁ、あまねく人々へ\nボクの美を広めよう！", 1, 1, 3, 0, 3, 7333, 126301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "リラックスは大事だよ\n心の余裕こそが\n美の秘訣だからね♪", 1, 1, 0, 0, 2, 7335, 126301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "キミもボクの\n美の虜にしてあげる♪\nうれしいでしょ？", 1, 1, 0, 0, 3, 7336, 126301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "人々を魅了するほどの\n魔性の香り…\nボクにふさわしいね♪", 1, 1, 0, 0, 4, 7337, 126301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "冷えは美の大敵さ\nボクのように\n美しくなりたいなら\n体を温めるべきだよ", 1, 1, 0, 0, 5, 7338, 126301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "食事をするボクの\nなまめかしさ…♪\n吸血鬼すらも\nドキッとするだろうね", 1, 1, 0, 0, 7, 7340, 126301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "吸血鬼って\n美しい存在だよね…\nボクほどではないけど", 1, 1, 0, 0, 8, 7341, 126301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここがナイトさんの\nお家なんですねーっ！\n雰囲気そっくりっ！", 1, 1, 0, 0, 1, 7343, 126401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ドアも壁もお菓子じゃ\nないんですよねっ？\n不思議ですねーっ♪", 1, 1, 0, 0, 2, 7344, 126401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "失礼のないように\n気をつけないとっ！\nライラエルさまとも\n約束してますからっ！", 1, 1, 0, 0, 3, 7345, 126401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "向こうに帰るころには\nお土産話がいっぱい\nできてるといいな～♪", 1, 1, 0, 0, 4, 7346, 126401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "戦闘用のアームの他に\n観光用のアームも\nあったらいいのにな～", 1, 1, 0, 0, 5, 7347, 126401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ナイトさんの充填と\nライラエルさまの充填\nどっちも違って\nどっちも大好きです♪", 1, 4, 0, 0, 101, 7348, 126401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "…ナイトさんが\nマスターになって\nくれたら…えへへっ\nなんでもないですっ！", 1, 8, 0, 0, 102, 7349, 126401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お手伝いだったら\n任せてくださいっ！\nお家の移転とか\nしちゃいますかっ？", 1, 1, 0, 0, 1, 7350, 126401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "こっちに来るときは\nポポおじいさんが\nしっかりメンテして\nくれるんですよっ♪", 1, 1, 0, 0, 2, 7351, 126401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お家の中にいるのも\nいいですけど、お外も\n気になります～っ！\nお出かけしましょう♪", 1, 1, 0, 0, 3, 7352, 126401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ヴォルガーノさんに\nそっくりの子猫さんを\n見かけたんですっ！\nご親戚ですかねっ？", 1, 1, 0, 0, 4, 7353, 126401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ここだけの話ですよ？\n…実はケーブルって\n触られると、ちょっと\nくすぐったいんですっ", 1, 4, 0, 0, 101, 7354, 126401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "このお家の持ち主は\nナイトさんですよね？\nそれじゃここでは\nご主人さまですねっ", 1, 8, 0, 0, 102, 7355, 126401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようございますっ\n今日もフルパワーで\nがんばりましょうっ♪", 1, 1, 1, 0, 1, 7356, 126401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼の時間ですねっ！\nランチの充填、\nお願いしまーすっ♪", 1, 1, 2, 0, 2, 7357, 126401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そろそろ眠らなくて\n大丈夫ですかっ？\n夜ふかしさんなら\nお付き合いしますよ♪", 1, 1, 3, 0, 3, 7358, 126401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "エネルギーの消費を\n抑えるのも大切\nですよね～っ", 1, 1, 0, 0, 2, 7360, 126401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "わたしが触っても\n平気なんて…！\nかわいくて頑丈っ！\n好きですっ！", 1, 1, 0, 0, 3, 7361, 126401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "センサーによると…\nこの香りはいい香りに\n該当するみたいですっ\nなるほどーっ！", 1, 1, 0, 0, 4, 7362, 126401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったかいのって\nいいですよねっ！\nみなさんうれしそう\nですしっ♪", 1, 1, 0, 0, 5, 7363, 126401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これがこっちの世界の\nごはんなんですねっ\nおいしい気がします！", 1, 1, 0, 0, 7, 7365, 126401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "そーっとそーっと…\n本を破かないように…\n書いてある内容より\n難しいです～っ！", 1, 1, 0, 0, 8, 7366, 126401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ウィスタリア家の\nプライベートビーチも\nご案内しますわね♪", 1, 1, 0, 0, 1, 7368, 126701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "世界を巡る\nリゾートクルーズも\nご用意できますわよ", 1, 1, 0, 0, 2, 7369, 126701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "避暑地に専用の\n別荘がありますの\n今度ご招待しますわね", 1, 1, 0, 0, 3, 7370, 126701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "日焼けは乙女の\n天敵ですわね\n備えはしっかりと…", 1, 1, 0, 0, 4, 7371, 126701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "線香花火だけでは\n物足りませんわね\nもっとドカーンと…", 1, 1, 0, 0, 5, 7372, 126701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ギルド活動も全力で！\nバカンスも全力で！\nそれが私ですわ！", 1, 4, 0, 0, 101, 7373, 126701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "私のセンスで選んだ\n特注の水着ですもの\n似合って当然ですわ♪", 1, 8, 0, 0, 102, 7374, 126701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ご希望ならすぐにでも\n夏の飛空艇遊覧ツアー\nへご招待しますわ♪", 1, 1, 0, 0, 1, 7375, 126701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夏の賑やかな\n雰囲気は好きですが…\n暑さには参りますわね", 1, 1, 0, 0, 2, 7376, 126701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "日の光を浴びるほどに\n輝きを増す夏の私！\n今日も絶好調ですわ♪", 1, 1, 0, 0, 3, 7377, 126701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夏祭りに協力するなら\n資金と人材、どちらも\n用意しないとですわね", 1, 1, 0, 0, 4, 7378, 126701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたさま…その…\nそうも見つめられると\n照れてしまいます…", 1, 4, 0, 0, 101, 7379, 126701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "タマキさんの働く\n海の家も訪れて\nみたいですわね♪", 1, 8, 0, 0, 102, 7380, 126701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "港の市場が\n賑わう頃ですわね！\n私たちも参りますわよ", 1, 1, 1, 0, 1, 7381, 126701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "冷たい飲み物でも\nご一緒にいかが？\nうちのシェフに用意\nさせてありますの", 1, 1, 2, 0, 2, 7382, 126701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "まだもう少し…\nこの夏の夜を\nあなたさまと…", 1, 1, 3, 0, 3, 7383, 126701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あなたさまも\n良ければこちらへ\nいらっしゃいな♪", 1, 1, 0, 0, 2, 7385, 126701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "夏に合わせた装いに\n替えてみるのも\n楽しそうですわね♪", 1, 1, 0, 0, 3, 7386, 126701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この夏めいた爽やかな\n香り…期間限定で\n売り出したいですわ", 1, 1, 0, 0, 4, 7387, 126701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "く…我慢です…\nこの暑さに耐えてこそ\n美しさにも磨きが…", 1, 1, 0, 0, 5, 7388, 126701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "暑い季節に働く\nみなさまへ差し入れも\n必要ですわね", 1, 1, 0, 0, 7, 7390, 126701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "夏のトレンドを\n押さえておくのも\n大事ですわね", 1, 1, 0, 0, 8, 7391, 126701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "上手くいけば\nヒット商品になる\nポテンシャルを\n感じますわね…！", 1, 1, 0, 0, 10, 7393, 126701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この極上の肌触り…\n言い値で買いますわ\n一体おいくらですの？", 1, 1, 0, 0, 11, 7394, 126701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "せっかくのバカンス…\n自由に羽を\n伸ばすとしましょう", 1, 1, 0, 0, 1, 7395, 126601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "たとえどのような\n秘密を持とうとも\n私には筒抜けですよ", 1, 1, 0, 0, 2, 7396, 126601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "子供だましの怪談に\n関心はありませんが…\n霊には興味があります\n観察対象として、ね", 1, 1, 0, 0, 3, 7397, 126601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "彼の絵日記…\nあとでゆっくりと\n読ませてもらいます", 1, 1, 0, 0, 4, 7398, 126601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "セミの鳴き声に入道雲\nそしてクリームソーダ\nこれでこそ夏ですね", 1, 1, 0, 0, 5, 7399, 126601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "夏休みの宿題でも\n作るとしましょうか\n彼の限界を少し超えた\n負荷を与えるには…", 1, 4, 0, 0, 101, 7400, 126601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "彼と過ごす夏を\n特別なものにしたい…\nそれが私の望みです", 1, 8, 0, 0, 102, 7401, 126601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夏だからといって\n浮かれすぎないように\n節度を持って\n楽しみなさい？", 1, 1, 0, 0, 1, 7402, 126601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ああ見えてもマサキは\n私のプリンセスナイト\nあまり侮られては\n困りますよ？", 1, 1, 0, 0, 2, 7403, 126601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私の秘密ですか？\nまんじゅうが怖い…\nというのはどうです？", 1, 1, 0, 0, 3, 7404, 126601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ここは誰かの秘密を\n盗み聞くのに\n最適な場所ですね\n…ジョークですよ", 1, 1, 0, 0, 4, 7405, 126601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "一緒に風鈴の音を\nのんびり楽しもう…？\n…別に構いませんよ\n貴方とならば", 1, 4, 0, 0, 101, 7406, 126601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "貴方に約束しましょう\n決して忘れられない\n夏にしてあげますよ", 1, 8, 0, 0, 102, 7407, 126601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "早起きとは感心ですね\nでは一緒に\n体操でもしましょうか", 1, 1, 1, 0, 1, 7408, 126601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "昼食は流しそうめん？\n非効率的ですね\n…食べないとは\n一言も言っていません", 1, 1, 2, 0, 2, 7409, 126601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "…貴方が望むならば\nいくらでも怖い話を\nしてあげますが…\nいかがですか？", 1, 1, 3, 0, 3, 7410, 126601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "七冠にも休息は\n必要なのです\nしばらくここで\n休ませてもらいますよ", 1, 1, 0, 0, 2, 7412, 126601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "なかなか気に入った…\nというのは\n誰にも秘密です", 1, 1, 0, 0, 3, 7413, 126601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "薫風を思わせる\nこのさわやかな香り…\n悪くないセンスですね", 1, 1, 0, 0, 4, 7414, 126601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "夏だからこそ\n冷え対策は\n重要なのです\n…とはいえ暑いですね", 1, 1, 0, 0, 5, 7415, 126601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "私がどのような味が\n好きなのかは…\n当然秘密です", 1, 1, 0, 0, 7, 7417, 126601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "夏を舞台にした\n小説は数多くあります\n彼が好きそうな本を\n何冊か見繕いましょう", 1, 1, 0, 0, 8, 7418, 126601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "見た目も手触りも\n完璧に覚えました\nいつでも模倣できます", 1, 1, 0, 0, 10, 7420, 126601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふむ…夏らしい\nクッションですね\nナイトプールに\n映えそうです", 1, 1, 0, 0, 11, 7421, 126601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "レイ様も格好良いけど\n騎士さんもなかなか…\nって、今の聞こえて\nませんよねっ？", 1, 12, 0, 0, 103, 7422, 105401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "騎士さんはシンプルな\n服が似合いそうです！\n今度、一緒に\n見に行きましょう♪", 1, 12, 0, 0, 103, 7423, 105401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "最初はどうかな～って\n思ったけど…\n水着姿でも案外\n問題ないのよね…", 1, 1, 0, 0, 1, 7424, 126801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "水着で飲みすぎると\nお腹が冷える、って\n言うけど…麦しゅわ\nなら問題ないわね！", 1, 1, 0, 0, 2, 7425, 126801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "暑ければ暑いほど\nその後の麦しゅわは\nおいしくなるのよ…", 1, 1, 0, 0, 3, 7426, 126801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "しっかりと仕事を\nこなしてから、\nしっかりと麦しゅわ！\nやっぱりこれね！", 1, 1, 0, 0, 4, 7427, 126801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ふふっ、デザインも\n着心地も最高ね\n奮発して用意して\n正解だったわ♪", 1, 1, 0, 0, 5, 7428, 126801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "伝説の麦しゅわ、\n飲めるものなら\nまた飲みたいわね…", 1, 4, 0, 0, 101, 7429, 126801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "二つ名に恥じない\n聖騎士として\n私…飲むわ！", 1, 8, 0, 0, 102, 7430, 126801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この格好なら水に\n濡れても大丈夫！\nなんだけど、さすがに\n書類は濡らせないわね", 1, 1, 0, 0, 1, 7431, 126801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そうやってじっと\n見られると…ちょっと\n恥ずかしいわ…", 1, 1, 0, 0, 2, 7432, 126801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日もいい天気ね\nふふっ、あとで\nビーチに遊びに\n行きましょ♪", 1, 1, 0, 0, 3, 7433, 126801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "水着姿も慣れると\n案外快適ね\nそうだ、キミも\n水着になっちゃう？", 1, 1, 0, 0, 4, 7434, 126801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "例え水着姿であろうと\n聖騎士としての誇りは\n忘れてないわ！", 1, 4, 0, 0, 101, 7435, 126801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "キミとだったら\n麦しゅわなしでも\n最高の夏のバカンスに\nなると思うわ♪", 1, 8, 0, 0, 102, 7436, 126801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "仕事終わりの\n麦しゅわのために\n今日も一日\n頑張るわよ♪", 1, 1, 1, 0, 1, 7437, 126801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日のお昼は\nどうしようかしら…\nさ、さすがに昼からは\n自重してるわよ？", 1, 1, 2, 0, 2, 7438, 126801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "さ、一日の疲れを\n洗い流すために…\n軽～く飲むわよ！", 1, 1, 3, 0, 3, 7439, 126801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こうやって\n直に触れると\n肌触りの良さが\nよく分かるわ♪", 1, 1, 0, 0, 2, 7441, 126801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "今日もかわいいわね～\nどう？　今度\nお姉さんの一人飲みに\n付き合わない？", 1, 1, 0, 0, 3, 7442, 126801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい香りね～…はっ！\n麦しゅわの匂いの\nアロマポットなんて\nあったらよくない？", 1, 1, 0, 0, 4, 7443, 126801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こんな格好で火に\n当たってると焙られる\nスルメの気分が\nちょっと分かるわ…", 1, 1, 0, 0, 5, 7444, 126801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もぐもぐ…うん、\n結構おつまみにも\nむいてそうね～♪", 1, 1, 0, 0, 7, 7446, 126801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "試験に使った参考書、\nたまには読み返して\nおかないとね", 1, 1, 0, 0, 8, 7447, 126801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふふ♪\nいい手触りね～", 1, 1, 0, 0, 10, 7449, 126801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "…はふう～…\nゴロゴロするのって\n最高ね～", 1, 1, 0, 0, 11, 7450, 126801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ん…やっぱり\nこの水着…\n大胆、なんじゃ…？", 1, 1, 0, 0, 1, 7451, 126901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "海でもお世話って、\nできるのかしら…？\n浮き輪代わりになる、\nとか…？", 1, 1, 0, 0, 2, 7452, 126901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "～♪\nふふ…海に行く\n準備も、楽しい…かも", 1, 1, 0, 0, 3, 7453, 126901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あうぅ…水着で\n歩いてると…お尻…\nすぐ食い込んじゃう…", 1, 1, 0, 0, 4, 7454, 126901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "海では、はしゃいだ\nほうがいいのよね…\nわ、わー、い…♪\n…こうかしら？", 1, 1, 0, 0, 5, 7455, 126901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あなたとなら…\n楽しい夏の思い出、\n作れる…気がするの…", 1, 4, 0, 0, 101, 7456, 126901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "また来年も…\n再来年も…\nあなたと海へ\n行けますように…", 1, 8, 0, 0, 102, 7457, 126901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "水着の着方を\n練習してるの…\n後ろ、変じゃない…？", 1, 1, 0, 0, 1, 7458, 126901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "海に行く支度…\n進んでるかしら…？\nお手伝い、する…？", 1, 1, 0, 0, 2, 7459, 126901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ひ、日除けの準備は\n万全よ…\nずっと太陽の下に\nいたら…溶けちゃう…", 1, 1, 0, 0, 3, 7460, 126901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "泳ぐ練習…\nこ、ここでするのは\n…さすがに変？", 1, 1, 0, 0, 4, 7461, 126901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "人混みに入るのは\nまだ不安で…\nでも、あなたといれば\nきっと…大丈夫…", 1, 4, 0, 0, 101, 7462, 126901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたのおかげで\n海の楽しいところを\nたくさん知ったわ…\nありがとう…♪", 1, 8, 0, 0, 102, 7463, 126901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お出かけの日の朝は\n早い、のよね…？\nだから、ずっと\n起きていたわ…", 1, 1, 1, 0, 1, 7464, 126901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今頃ビーチの日差しは\n強いんでしょうね…\nそ、想像しただけで…\nめまいが…", 1, 1, 2, 0, 2, 7465, 126901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ビーチの夜といえば\n花火って、聞いたわ…\nバン、って言わない\n花火…ある…？", 1, 1, 3, 0, 3, 7466, 126901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "素肌にふわふわが…\n気持ちいい…♪", 1, 1, 0, 0, 2, 7468, 126901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うふふ…\n一緒に海…行く？", 1, 1, 0, 0, 3, 7469, 126901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふぅ…♪\n気分まで…\n晴れやかになるわ…", 1, 1, 0, 0, 4, 7470, 126901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "暑いような…\n寒いような…\n不思議な気持ちに\nなるわ…ね？", 1, 1, 0, 0, 5, 7471, 126901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "泳いだ後には\nいっぱい食べたほうが\n体によさそう、ね…", 1, 1, 0, 0, 7, 7473, 126901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ビーチの日陰で\n読書するのも…\n気持ちよさそう…♪", 1, 1, 0, 0, 8, 7474, 126901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これで合ってる…？\n遊び方わからなくて…", 1, 1, 0, 0, 10, 7476, 126901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふわふわ…♪\n海の底にいるみたい…", 1, 1, 0, 0, 11, 7477, 126901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "恥ずかしい格好で\n室内を歩くこの快感\nクセになりそうです…", 1, 1, 0, 0, 1, 7478, 127001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この水着…\nどうしてかポーズを\n決めたくなりますね…", 1, 1, 0, 0, 2, 7479, 127001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "海にはどんな生き物が\nいるのでしょう～\n胸が高鳴ります～♪", 1, 1, 0, 0, 3, 7480, 127001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "夏は暑いですね…\nクウカの体が熱いのも\n夏のせい…ぐふふ…♪", 1, 1, 0, 0, 4, 7481, 127001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ドＳさんと無人島に\n取り残されて…という\n妄想はかなりイイです", 1, 1, 0, 0, 5, 7482, 127001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ドＳさんの\n欲望に満ちた眼差し…\nクウカ、受け止めます", 1, 4, 0, 0, 101, 7483, 127001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "海に行くのが\n楽しみです…\nドＳさんと…ぐふふ♪", 1, 8, 0, 0, 102, 7484, 127001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "「もっと過激な水着を\n見せてみろよ」なんて\nいけませんドＳさん…", 1, 1, 0, 0, 1, 7485, 127001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "水着のままで\n踊ればいいのですね…\nドＳさんの望みなら！", 1, 1, 0, 0, 2, 7486, 127001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ドＳさんの熱い視線を\n感じます…じゅるり…\nもっと見てください…", 1, 1, 0, 0, 3, 7487, 127001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "こんな格好のクウカを\n放っておくなんて…\nさすがですドＳさん！", 1, 1, 0, 0, 4, 7488, 127001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "海で水着といえば\n何かが起きる予感…！\nドＳさんの新たな攻め\n期待していますね…！", 1, 4, 0, 0, 101, 7489, 127001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "恥ずかしいですけど\n頑張っちゃいました\nドＳさん…こういう\n水着、好きですか？", 1, 8, 0, 0, 102, 7490, 127001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "こんな朝からクウカを\n水着に着替えさせて…\n本当にドＳさんは\n欲望に正直ですね！", 1, 1, 1, 0, 1, 7491, 127001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "こんな昼間から\nドＳさんがヌルヌルの\n液体をクウカに～！\nえっ…日焼け止め？", 1, 1, 2, 0, 2, 7492, 127001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ドＳさん…これから\nクウカと夜の海を\n見に行きませんか？\nきっと綺麗ですよ…♪", 1, 1, 3, 0, 3, 7493, 127001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ここなら妄想に\n集中できます～♪\nじゅるり～♪", 1, 1, 0, 0, 2, 7495, 127001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こうもかわいいと\n妄想に登場させるのも\n気が引けます…", 1, 1, 0, 0, 3, 7496, 127001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "クウカを昂らせる\nこの香り…！\nは！　ドＳさんの罠！", 1, 1, 0, 0, 4, 7497, 127001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "体を熱くさせて\n水着を脱がそうと！？\nテクニカルです…！", 1, 1, 0, 0, 5, 7498, 127001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "クウカの口の中に\nたくさん…♪\n止まりません…！", 1, 1, 0, 0, 7, 7500, 127001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "読書は心が穏やかに\nなりますね…！\nぐふふ…穏やかに…", 1, 1, 0, 0, 8, 7501, 127001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい乗り心地です…♪\nあっ…普通に楽しんで\nしまいました…", 1, 1, 0, 0, 10, 7503, 127001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これは…\n妄想に役立ちそうな\n感触です…！", 1, 1, 0, 0, 11, 7504, 127001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "成長した自分に\nあわせて、もっと\nかっこいいポーズを\n研究するッス！", 1, 12, 0, 0, 103, 7505, 100501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "これからも一緒に\n最高のヒーローを\n目指していくッスよ♪", 1, 12, 0, 0, 103, 7506, 100501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お父さん、娘の\n水着姿がそんなに\nうれしいの？", 1, 1, 0, 0, 1, 7507, 127101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "怖い話を子どもたちに\n聞かせたら泣かれて\nしまいました…\n加減が難しいです…", 1, 1, 0, 0, 2, 7508, 127101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "海に行くと父が\n水着の女性ばかり\n追いかけて困ります…", 1, 1, 0, 0, 3, 7509, 127101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "拾った貝殻で\n新しい占いでも\nはじめてみようかな", 1, 1, 0, 0, 4, 7510, 127101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "貝殻を耳に当てると\n波の音が…あっ\nこれはダメです\nよくない声がします", 1, 1, 0, 0, 5, 7511, 127101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この飲み物、スーッと\n体に沁み込んでくる…\nあの人にも\n教えてあげたいです", 1, 4, 0, 0, 101, 7512, 127101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "強い日差しも騒がしい\n海も苦手なのに\nあの人となら不思議と\n楽しく思えるんです", 1, 8, 0, 0, 102, 7513, 127101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夏限定、水と氷を\n使った特別な占いは\nいかがですか？", 1, 1, 0, 0, 1, 7514, 127101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "暑い日々にはやはり\n怪談ですね\n厳選フルコースで\nお聞かせします", 1, 1, 0, 0, 2, 7515, 127101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "寒気がする？　それは\n霊ではなくかき氷の\n食べ過ぎですよ", 1, 1, 0, 0, 3, 7516, 127101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日の占い、あなたの\nラッキーパーソンは…\n白い水着の女性です", 1, 1, 0, 0, 4, 7517, 127101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あの、よければ\n日焼け止めを塗って\nもらえませんか？\n背中は届かなくて…", 1, 4, 0, 0, 101, 7518, 127101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "海に行きませんか？\n二人きりで静かな\n砂浜を散歩したいです", 1, 8, 0, 0, 102, 7519, 127101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "潮騒だけが響く朝の海\n心が落ち着きます\nこれから一緒に散歩に\n行ってみませんか？", 1, 1, 1, 0, 1, 7520, 127101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "不思議です。どうして\n普通のご飯でも、海で\n食べると特別おいしく\n感じるのでしょう…", 1, 1, 2, 0, 2, 7521, 127101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜の海に行きませんか\n今なら夜光虫が\n見られるかも\nしれませんよ", 1, 1, 3, 0, 3, 7522, 127101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "夏は知らぬ間に体力を\n消耗しています\n適度に休息して\nいきましょう", 1, 1, 0, 0, 2, 7524, 127101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "このぬいぐるみ…\n大丈夫です\n何も憑いては\nいませんよ", 1, 1, 0, 0, 3, 7525, 127101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ほのかにお線香\nみたいな香り…\nなんだか\n落ち着きます", 1, 1, 0, 0, 4, 7526, 127101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "人魂、鬼火\nセントエルモの火…\n炎を見ていると色々と\n連想してしまいます", 1, 1, 0, 0, 5, 7527, 127101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お腹を壊さぬよう\n食い合わせには\nご注意ください", 1, 1, 0, 0, 7, 7529, 127101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これはいい\n怪談集ですね…\nつい読みふけって\nしまいます", 1, 1, 0, 0, 8, 7530, 127101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふふ、こういうのに\n乗るとなんだか\n子どもに戻った\nみたいですね", 1, 1, 0, 0, 10, 7532, 127101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こ、これは…\nちょっとメルヘン\n過ぎて恥ずかしい\nですね…", 1, 1, 0, 0, 11, 7533, 127101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "本当に素敵なお家…♪\n騎士たちの人柄を\n感じられるようで…\n不思議と落ち着きます", 1, 1, 0, 0, 1, 7534, 126501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "調度品をつい間食…\nという習慣がこちらの\n世界にはないのですね\n…気をつけないと", 1, 1, 0, 0, 2, 7535, 126501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "手土産のお菓子は\nどちらに置けば\nよいでしょうか？", 1, 1, 0, 0, 3, 7536, 126501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "まるでおとぎ話の中に\n入ってしまったかの\nよう…別世界とは\nよく言ったものです", 1, 1, 0, 0, 4, 7537, 126501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お忍び…ふふっ♪\nときめく響きです\nなんだかお茶目で…\n密やかな印象が", 1, 1, 0, 0, 5, 7538, 126501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ふわ…\nああ、ついあくびが…\n居心地がよすぎるのも\n困り物ですね…ふふ♪", 1, 4, 0, 0, 101, 7539, 126501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "騎士には不思議な力が\nあるだけでなく\n何者かの加護が\nあるようですね…", 1, 8, 0, 0, 102, 7540, 126501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この『王家の装備』は\n少し気難しいのです\nわたし以外が触れると\nその…ビリビリっと", 1, 1, 0, 0, 1, 7541, 126501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "子どもたちに新しい\n遊びを教わりました♪\nにーらめっこしましょ\nあっぷっ…ぷ～っ！", 1, 1, 0, 0, 2, 7542, 126501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "実は憧れていたのです\n友人の家を訪ねると\nいう経験に…ふふっ", 1, 1, 0, 0, 3, 7543, 126501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ぎるどはうす…\nぎるど活動の拠点だと\n管理協会？　の方に\nうかがいました", 1, 1, 0, 0, 4, 7544, 126501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたは運命を\n信じますか？\nわたしは…信じます\nよい運命だけですが♪", 1, 4, 0, 0, 101, 7545, 126501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もしわたしが本当に\n天使だったとしたら…\nどうされますか？\n…ふふ、冗談です♪", 1, 8, 0, 0, 102, 7546, 126501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "パンケーキの香りで\n目覚める朝は\nとても良いものですよ\nぜひともお試しを♪", 1, 1, 1, 0, 1, 7547, 126501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ひと休みの時間ですね\n食事にお昼寝…\nすべきことを行って\n英気を養いましょう", 1, 1, 2, 0, 2, 7548, 126501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そろそろ休まれては？\n眠れないようであれば\n寝物語か子守歌でも\nふふ、遠慮は無用です", 1, 1, 3, 0, 3, 7549, 126501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "すう………はっ！\n眠ってはいませんよ？\nいくら居心地が良い\nからといって…ふわぁ", 1, 1, 0, 0, 2, 7551, 126501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "まあ、あいらしい…♪\nぜひわたしの寝室に\n招待させてください", 1, 1, 0, 0, 3, 7552, 126501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "なんて、かぐわしい…\nオーブンからただよう\n焼き菓子の香りとは\nまた異なる魅力が…", 1, 1, 0, 0, 4, 7553, 126501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "みなで暖をとりながら\n互いに語り笑い合う\n…心休まる時間ですね", 1, 1, 0, 0, 5, 7554, 126501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "食は心と体に\nゆとりを生みます\n…辛い食べ物の場合？\nそれは、その…", 1, 1, 0, 0, 7, 7556, 126501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こちらの世界の知識を\n蓄えておかなければ\nテーブルマナーに\nついてのページは…", 1, 1, 0, 0, 8, 7557, 126501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "新しい色の蛍光ペン\n買ったのよね\nこれで勉強が\n楽しくなるわ～♪", 1, 1, 0, 0, 1, 7559, 127201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "学食のランチ、結構\nおいしいのよね～\n今度ペコリーヌに\n教えてやろうかしら？", 1, 1, 0, 0, 2, 7560, 127201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ふふふ…扉に黒板消し\n仕掛けてるの…あいつ\n引っかかるかしら？", 1, 1, 0, 0, 3, 7561, 127201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "部活するなら運動部も\n悪くないかもね～\n夕日に向かって\n突っ走ってやるわ！", 1, 1, 0, 0, 4, 7562, 127201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "毎日窮屈な制服着て\n学生も大変ね～\n…ま、かわいいけど", 1, 1, 0, 0, 5, 7563, 127201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あいつに貸したノート\n変な落書きとか\nしてなかったよね…？\n…その、相合傘とか…", 1, 4, 0, 0, 101, 7564, 127201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "昨日の家庭科で\n作ったクッキー…\nあいつにどうやって\n渡そうかな…？", 1, 8, 0, 0, 102, 7565, 127201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "宿題教えてって？\nしょうがないわね～\nじゃあ一緒に\nやりましょ", 1, 1, 0, 0, 1, 7566, 127201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あ、明日あたしたち\n日直当番よね？\nめんどい仕事は\nあんたに任せるわ～", 1, 1, 0, 0, 2, 7567, 127201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "寮生活は楽しいけど\n就寝時間が早すぎよね\nいきなり消灯されると\nビックリするわ", 1, 1, 0, 0, 3, 7568, 127201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "校則で禁止だけど、\nにけつでほうき通学\nとか青春っぽくない？", 1, 1, 0, 0, 4, 7569, 127201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "制服って好きな人とも\n自然にお揃いになれて\nいいわよね…って別に\nあんたとって訳じゃ…", 1, 4, 0, 0, 101, 7570, 127201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あんたネクタイ\n曲がってるわよ～？\nほら直したげるから\nこっち向いて？", 1, 8, 0, 0, 102, 7571, 127201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよ～！\n今日も元気に登校よ！\n忘れ物ないようにね！", 1, 1, 1, 0, 1, 7572, 127201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "午前の授業疲れたわ～\nしっかりお昼を食べて\n午後も頑張るわよ！", 1, 1, 2, 0, 2, 7573, 127201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "うう…学校の怪談\n思い出しちゃった…\n…別に怖くないけど！", 1, 1, 3, 0, 3, 7574, 127201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふかふか～\n…気持ち良すぎて\n制服のまま\n寝ちゃいそうね…", 1, 1, 0, 0, 2, 7576, 127201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいくて癒されるわ\n寮に持ち込んでも\n大丈夫かしら…？", 1, 1, 0, 0, 3, 7577, 127201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "集中力の高まる香りで\n勉強の効率爆上がりね\n次のテストで\nいい点取るわよ～！", 1, 1, 0, 0, 4, 7578, 127201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うう、ちょっと冷えて\n来たわね…\nスカートの下に\nジャージ穿こうかしら", 1, 1, 0, 0, 5, 7579, 127201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もぐっ…もぐっ…\n食べとかないと教室で\nお腹なっちゃうわ～", 1, 1, 0, 0, 7, 7581, 127201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "図書室で借りた本\n明日返却日なのよね…\n今日中に読むわよ！", 1, 1, 0, 0, 8, 7582, 127201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あいつに背中を\n預けるのもいい気分だ\nいつかは、全部…", 1, 12, 0, 0, 103, 7584, 104301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "な、なんだよ？\nじっと見るんじゃねぇ\n尻尾が揺れちまうだろ", 1, 12, 0, 0, 103, 7585, 104301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "魔法の練習！\nむむむ～…\nそれっ…！", 1, 1, 0, 0, 1, 7586, 127301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "そだ！　これ着て\nルー学行こっかな？　\n制服自慢しちゃお♪", 1, 1, 0, 0, 2, 7587, 127301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "赤点回避の魔法とか\nあったら最高じゃね？\n誰かうちに教えて～", 1, 1, 0, 0, 3, 7588, 127301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "制服が一番映える\n場所ってどこだろ？\n教室？　遊園地？", 1, 1, 0, 0, 4, 7589, 127301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ベンキョーつらたん…\n今度キャルっちに\n助けてもらお", 1, 1, 0, 0, 5, 7590, 127301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "部活か…\nヒデサイと一緒に\nやったら楽し～かも♪", 1, 4, 0, 0, 101, 7591, 127301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "男子の制服\nもにカワだった…\n今度ヒデサイに\n借りよっかな？", 1, 8, 0, 0, 102, 7592, 127301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ちょすー、うちの\nさいきょー魔法で\nテンアゲしてあげる～", 1, 1, 0, 0, 1, 7593, 127301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "九九って呪文ぽくね？\nほら、例えばヒチシ…\nヒチシ、なんだっけ？", 1, 1, 0, 0, 2, 7594, 127301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ヒデサイセンセー\nうちにベンキョー\n教えて！", 1, 1, 0, 0, 3, 7595, 127301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "学割マジ助かるー\nヒデサイ、一緒に\nどんどん使ってこー♪", 1, 1, 0, 0, 4, 7596, 127301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今度ヒデサイに\nお弁当作ったげる\n屋上で一緒に食べよ♪", 1, 4, 0, 0, 101, 7597, 127301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ヒデサイのバイブス\n上がる言葉教えてよ\nつらたんな時、うちが\nいっぱい言ったげるよ", 1, 8, 0, 0, 102, 7598, 127301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ちょすちょす～\n今日も朝から\nバイブス十分♪", 1, 1, 1, 0, 1, 7599, 127301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼どうしよっかな\nたまにはガッツリ\nいっちゃう系？", 1, 1, 2, 0, 2, 7600, 127301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "やべ、宿題まだ\nできてないし…\nヒデサイ助けて～", 1, 1, 3, 0, 3, 7601, 127301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "充電ちう～\n…ガッコーのイスも\nこれにして～", 1, 1, 0, 0, 2, 7603, 127301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいいし、\n癒されるし、キュン死\nしちゃうじゃん…！", 1, 1, 0, 0, 3, 7604, 127301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "リフレッシュの\n魔法発見！\nいい香り～♪", 1, 1, 0, 0, 4, 7605, 127301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ぽかぽかサイコー♪\n今日はもーずーっと\nここにいたいかも～", 1, 1, 0, 0, 5, 7606, 127301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "超おいし～♪\n学食にあったら\n毎日リピする～", 1, 1, 0, 0, 7, 7608, 127301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふむふむ…\nねむみパない…\n続きはまた今度～", 1, 1, 0, 0, 8, 7609, 127301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今度は子どもたちも\nサラサリアに連れて\n行ってあげたいわ", 1, 1, 0, 0, 1, 7611, 127501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "子どもたちから\nお姫さまみたいって\n言われちゃった♪", 1, 1, 0, 0, 2, 7612, 127501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この剣…火を点けたり\nするのに便利なのよ\nコントロールも効くし", 1, 1, 0, 0, 3, 7613, 127501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あのサラサリアの\nカラっとした暑さが\nちょっと恋しいわ…", 1, 1, 0, 0, 4, 7614, 127501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "買ってきたお土産、\nお世話になってる\nみんなに配りましょ♪", 1, 1, 0, 0, 5, 7615, 127501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あんたと一緒なら\nどこの国でだって\n暮らしていけそうね♪", 1, 4, 0, 0, 101, 7616, 127501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今の事業を大きくして\nあんたと世界中の国を\n飛び回ったり…なんて\nすてきじゃない？", 1, 8, 0, 0, 102, 7617, 127501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "サラサリアで食べた\n料理が恋しい…？\nもう…また今度\n作ってあげるわよ", 1, 1, 0, 0, 1, 7618, 127501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今度旅行するなら\nどこに行こうかしら？\n希望があれば教えてね", 1, 1, 0, 0, 2, 7619, 127501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "サラサリアの香辛料、\nランドソルでも\n人気が出そうよね\nいいビジネスの予感！", 1, 1, 0, 0, 3, 7620, 127501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そんなにじっと\n見られるとさすがに\n恥ずかしいってば…", 1, 1, 0, 0, 4, 7621, 127501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お嫁さんは憧れるけど\nお婿さんになって\nもらうのもありよね…\nあんたはどう思う？", 1, 4, 0, 0, 101, 7622, 127501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "やっぱり、あんたには\nいつもあたしの隣に\n居てほしいな…", 1, 8, 0, 0, 102, 7623, 127501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "陽射しが強くなる前に\n雑多な用事は\n片付けちゃいましょ", 1, 1, 1, 0, 1, 7624, 127501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "サラサリアで食べた\n料理を再現してみたの\n味見してくれる？", 1, 1, 2, 0, 2, 7625, 127501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "暑さに負けないように\n睡眠はちゃんと\n取っておかないと", 1, 1, 3, 0, 3, 7626, 127501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こっちでもあっちでも\n忙しいのは変わらない\nわね…ちょっと休憩～", 1, 1, 0, 0, 2, 7628, 127501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "サラサリアで見た\nぬいぐるみも\nかわいかったわね", 1, 1, 0, 0, 3, 7629, 127501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お香って地域ごとの\n特色が出るわよね\nこの香りは…\nちょっと神秘的？", 1, 1, 0, 0, 4, 7630, 127501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "砂漠地方も夜は冷える\nのよね。温かいものが\nあると安心するわ…♪", 1, 1, 0, 0, 5, 7631, 127501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "食べ慣れた味って\nどうしてほっと\nするのかしら…", 1, 1, 0, 0, 7, 7633, 127501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "今後の取引相手に\nなるかもしれないし\n向こうの文化も\n勉強しておかないと…", 1, 1, 0, 0, 8, 7634, 127501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "犯人はこの中にいる！\n探偵として一度は\n言ってみたいセリフさ", 1, 12, 0, 0, 103, 7636, 101401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "助手くんの情報を\n仕入れておかないと…\n理想の私を、ずっと\n見ていてほしいからね", 1, 12, 0, 0, 103, 7637, 101401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "久々の長旅で\n里心がついちまったね\nしばらくランドソルで\nのんびりしようか", 1, 1, 0, 0, 1, 7638, 127601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "たまには違う服装\n違う得物を使うのも\n悪くないね", 1, 1, 0, 0, 2, 7639, 127601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "サラサリアにいた時は\n砂まみれで大丈夫かと\n思ってたけど、今じゃ\n懐かしく感じるよ", 1, 1, 0, 0, 3, 7640, 127601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "魔法の絨毯、あれは\n楽だし速いし便利\nだったねえ…土産に\n一枚欲しかったな", 1, 1, 0, 0, 4, 7641, 127601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "砂上での戦いは\nなかなかいい鍛錬に\nなったね…今度は\n修行しに行くとしよう", 1, 1, 0, 0, 5, 7642, 127601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "サラサリアの人たちも\n気のいい連中だったが\n元気にやってるかねえ", 1, 4, 0, 0, 101, 7643, 127601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "旅の恥はかき捨てって\nいうけど、悪い旅じゃ\nなかったね。次は\nアイツとどこに行こう", 1, 8, 0, 0, 102, 7644, 127601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この格好を冷やかしに\n来たのかい？\n生憎、もうすっかり\n慣れちまったよ", 1, 1, 0, 0, 1, 7645, 127601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ギルドの連中に\n土産をわたしに\n行くところなんだ\n付き合っておくれよ", 1, 1, 0, 0, 2, 7646, 127601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "久々に水場で\n釣りがしたいねえ…\nもちろんお前さんも\nくるんだろう？", 1, 1, 0, 0, 3, 7647, 127601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おっと\nサラサリアの連中から\n便りが来てるよ\nさ、一緒に読もうか", 1, 1, 0, 0, 4, 7648, 127601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "最近サラサリアの踊り\nを練習してるんだ\nあとで振付けを\n見ておくれよ", 1, 4, 0, 0, 101, 7649, 127601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なんだいお前さん？\n…なんだか若奥さん\nっぽいって？　まあ、\nそりゃあ、ねえ…", 1, 8, 0, 0, 102, 7650, 127601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "いい時間だね\n日が昇りきる前に\nやることをすませ\nとかないと", 1, 1, 1, 0, 1, 7651, 127601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "昼はどうしようかね\nサラサリア風の\n食事でもつくろうか", 1, 1, 2, 0, 2, 7652, 127601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "さて、早めに寝ようか\n砂漠の夜は冷えるから\n…っと、まだ旅行中の\n気分でいたよ", 1, 1, 3, 0, 3, 7653, 127601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "良い座り心地だ\nサラサリア宮殿の\n応接間を\n思い出すねえ…", 1, 1, 0, 0, 2, 7655, 127601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ランプの魔神に\nあげたら喜ぶかねえ？\n次に会う時までに\n見繕ってみようか", 1, 1, 0, 0, 3, 7656, 127601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この香り…\nサラサリア土産の\n香木かい？", 1, 1, 0, 0, 4, 7657, 127601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "旅先での焚火を\n思い出すよ\nなんだかほっとするね", 1, 1, 0, 0, 5, 7658, 127601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふふ、慣れてる味も\nいいけど、むこうの\n味付けが懐かしく\nなっちまうね", 1, 1, 0, 0, 7, 7660, 127601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふむふむ…\n千夜一夜物語か\nおもしろいもんだねえ", 1, 1, 0, 0, 8, 7661, 127601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "立派なギルドハウス♪\n帰る場所が素敵だと\n冒険に出るのも\n楽しくなりそうです♪", 1, 1, 0, 0, 1, 7663, 126001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お掃除でも…と\n思いましたけど、\nさすがお兄さんのお家\nきれいにしてますね♪", 1, 1, 0, 0, 2, 7664, 126001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "わたしたちもいつか\n立派なギルドハウスが\n持てる冒険者に\nなりたいです…♪", 1, 1, 0, 0, 3, 7665, 126001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "わたし冒険者ですけど\n家を守ってお兄さんの\n帰りを待つのも\n悪くない…かも？", 1, 1, 0, 0, 4, 7666, 126001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お菓子焼こうかな…♪\nちょっと多めに作って\nお兄さんにも\n食べてもらおうっと♪", 1, 1, 0, 0, 5, 7667, 126001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "このお家、とっても\n大好きになりました♪\nだからその…もう少し\nいてもいいですか…？", 1, 4, 0, 0, 101, 7668, 126001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お兄さんに\n感謝の手紙を書いて\n枕元に隠して\nおこうかな…♪", 1, 8, 0, 0, 102, 7669, 126001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "…お兄さん\nお暇なんですか？\nじゃあ一緒に冒険へ\n出かけましょう♪", 1, 1, 0, 0, 1, 7670, 126001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お兄さんは苦手な食材\nとかありますか？\nあの、もしよければ\n今から手料理を…♪", 1, 1, 0, 0, 2, 7671, 126001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "まったり過ごす\nお家時間もいいですね\n冒険者には羽休めも\n大事ですから♪", 1, 1, 0, 0, 3, 7672, 126001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "いつでもお兄さんを\n守れるように\nこの姿でいることに\n慣れておきますね…！", 1, 1, 0, 0, 4, 7673, 126001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お兄さん眠そうです…\nちょっとお昼寝\nしちゃいましょうか♪\nはい、膝枕…♪", 1, 4, 0, 0, 101, 7674, 126001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お兄さん怪我を…！？\nわたしが治療を…\nふー…ふー…", 1, 8, 0, 0, 102, 7675, 126001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようございます♪\n朝、元気に挨拶すれば\n幸せな一日が過ごせる\nそうですよ♪", 1, 1, 1, 0, 1, 7676, 126001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼ごはんの時間です\n…手がふさがってる？\nわかりました、じゃあ\nあ～んしますね♪", 1, 1, 2, 0, 2, 7677, 126001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふわぁ…眠くなって\nきちゃいました…\nお兄さん…お泊りして\nいいですか…？", 1, 1, 3, 0, 3, 7678, 126001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふにゃ～～…♪\nこんな姿、他の人には\n見せられません…♪\nもふもふ～～…♪", 1, 1, 0, 0, 2, 7680, 126001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お兄さんこういうの\n好きなのかな～…\nふふっ、わたしも♪\nぎゅ～～～っ♪", 1, 1, 0, 0, 3, 7681, 126001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "アロマの癒し効果…\n上手くアレンジして\nみなさんの治療に\n活かせないかな～？", 1, 1, 0, 0, 4, 7682, 126001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "は～気持ちいい～♪\n身体が暖まると\n心もぽかぽかです♪", 1, 1, 0, 0, 5, 7683, 126001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うふふ、おいしい♪\nプレシアちゃんなら\n無限にぱくぱく～って\nしちゃいそうです…♪", 1, 1, 0, 0, 7, 7685, 126001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "本はわたしを\n知らない世界に\n連れていってくれる…\nまるでお兄さんです♪", 1, 1, 0, 0, 8, 7686, 126001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "話題の新刊…\nあの人も読んでるかな\n今度会う時、感想\n聞いてみたいな…♪", 1, 12, 0, 0, 103, 7688, 103801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "うにゃっ…あ、熱を\n測ってくれたんですか\nありがとうございます\nバッチリ元気ですよ♪", 1, 12, 0, 0, 103, 7689, 103801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お兄の、けはい…\nん、みつけた\nぎゅ", 1, 1, 0, 0, 1, 7690, 126101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "きょろきょろ\nここも\nぱとろる、するよ", 1, 1, 0, 0, 2, 7691, 126101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "おうち\nにぎやか\nさみしく、ない", 1, 1, 0, 0, 3, 7692, 126101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "きょだい、おにぎり\nきょだい、肉まん\nきょだい…お兄？", 1, 1, 0, 0, 4, 7693, 126101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "はっぴーちゃん\nどこ？\nお兄、たべた？", 1, 1, 0, 0, 5, 7694, 126101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "くんくん…この、へや\nお兄の、におい\nすんでる\nすめる？", 1, 4, 0, 0, 101, 7695, 126101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "おそうじ、にがて\nでも\nれんしゅう\nきゅっきゅ", 1, 8, 0, 0, 102, 7696, 126101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "みみ、さわり、たい？\nんー…だめ…\nむずむず\nなので", 1, 1, 0, 0, 1, 7697, 126101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "くろぷれしあ\nかっこいい？\nん…あざます", 1, 1, 0, 0, 2, 7698, 126101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お兄の、おうち\nきょうみ、ましまし\nたんけん\nするね", 1, 1, 0, 0, 3, 7699, 126101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なでなで、される…と\nねむ…く…\nぶぅ…ぶぅ…ぶぅ…", 1, 1, 0, 0, 4, 7700, 126101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "かみ、りぼん\nおてつだい\nよろ\nです", 1, 4, 0, 0, 101, 7701, 126101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ここ\nすむ\nね？\nまいにち、いっしょ", 1, 8, 0, 0, 102, 7702, 126101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ぐっもにん\nあさごはん\nあさごはん", 1, 1, 1, 0, 1, 7703, 126101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お兄\nおつかれ？\nれっつ、まいぺーす", 1, 1, 2, 0, 2, 7704, 126101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ひとりで、ねれる\nでも\nひとりじゃ、なくても\nねれる、よ…？", 1, 1, 3, 0, 3, 7705, 126101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふかふか\nパパの、おなか\nみたい", 1, 1, 0, 0, 2, 7707, 126101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こんにちは\nぷれしあ、です\nおともだち、なろ？", 1, 1, 0, 0, 3, 7708, 126101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい、におい\nでも、お兄\nもっと、いい、におい", 1, 1, 0, 0, 4, 7709, 126101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "とりま\nお肉、やこ", 1, 1, 0, 0, 5, 7710, 126101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もぐもぐ…\nむげん肉まん、も\nきぼう", 1, 1, 0, 0, 7, 7712, 126101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ぜんぶ、よんだ\nもっと、ぶあついほん\nでも、いーよ？", 1, 1, 0, 0, 8, 7713, 126101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "幸せなクリスマスを\n過ごすため\nギルドハウスを\nきれいにしなくちゃ♪", 1, 1, 0, 0, 1, 7715, 127901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "えへへ♪\n彼へのプレゼント…\n枕元にそっと\nおいておきましょう☆", 1, 1, 0, 0, 2, 7716, 127901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "クリスマスといえば\n七面鳥の丸焼き…☆\nよだれを拭いて\nいざ準備です♪", 1, 1, 0, 0, 3, 7717, 127901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "彼に用意した\nプレゼント…\n大きすぎて\nくつ下に入りません…", 1, 1, 0, 0, 4, 7718, 127901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "プレゼントは\n貰う方も、贈る方も\nどちらも幸せを\n感じますね…☆", 1, 1, 0, 0, 5, 7719, 127901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "夜景が見える\nレストランであの人と\nクリスマスデート…\n憧れちゃいますね☆", 1, 4, 0, 0, 101, 7720, 127901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "プレゼントはわたし…\n恥ずかしいですけど\nこの贈り物、彼に\n喜んでほしいです…☆", 1, 8, 0, 0, 102, 7721, 127901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "オーナメントを\nたくさん用意したので\n二人でツリーに\n飾りつけましょう☆", 1, 1, 0, 0, 1, 7722, 127901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あはは、お腹\n空いちゃいました？\nパーティーまで\n我慢してください♪", 1, 1, 0, 0, 2, 7723, 127901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ジングルベ～ル♪\nあなたも一緒に\nクリスマスソング\n歌っちゃいましょう♪", 1, 1, 0, 0, 3, 7724, 127901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "サプライズプレゼント\nの用意をして…って\nあなた！？　い、今の\n聞いてませんよね…？", 1, 1, 0, 0, 4, 7725, 127901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "クリスマスの夜は\nパーティーしましょう\nできれば…その…\n二人っきりで…☆", 1, 4, 0, 0, 101, 7726, 127901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたと過ごした\n最高のクリスマス…\nずっとずっと心の奥に\nしまっておきますね", 1, 8, 0, 0, 102, 7727, 127901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようございます！\n皆の願いが叶うような\n素敵な一日に\nしましょうね☆", 1, 1, 1, 0, 1, 7728, 127901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼ですね！\nディナーの買い出しが\n済んだら、少しお昼寝\nしちゃいましょうか☆", 1, 1, 2, 0, 2, 7729, 127901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そろそろ就寝の\n時間ですね～\n枕元にくつ下の準備を\nお忘れなく～☆", 1, 1, 3, 0, 3, 7730, 127901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふ～気持ちいい…☆\nお家でまったり過ごす\nクリスマスも\nいいですよね…♪", 1, 1, 0, 0, 2, 7732, 127901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいいですね～♪\nサンタさんからの\n贈り物でしょうか～？", 1, 1, 0, 0, 3, 7733, 127901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "すう～…はあ～…\nすごく素敵な香り…\n炊き立てのご飯と\nいい勝負です…♪", 1, 1, 0, 0, 4, 7734, 127901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "暖かいですけど…\nあの日の山小屋を\n思い出して\n恥ずかしいですね…☆", 1, 1, 0, 0, 5, 7735, 127901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "むしゃむしゃもぐもぐ\n止まりませんよ\nクリスマスですから\nむしゃむしゃもぐもぐ", 1, 1, 0, 0, 7, 7737, 127901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "物語に出てくる\nサンタさんみたいに\nわたしも幸せを\n運びたいです…☆", 1, 1, 0, 0, 8, 7738, 127901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "わっ！\nこ…これは素敵な\nサプライズプレゼント\nですね…☆", 1, 1, 0, 0, 14, 7740, 127901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これを着ればわたしも\n立派なサンタさんに\nなれますかね…？", 1, 1, 0, 0, 15, 7741, 127901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここが今のあたしの\n居場所ってやつです\nふふっ、なかなか\n悪くねー気分ですよ", 1, 12, 0, 0, 103, 7742, 106601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "これからはボス代行\nとしてブイブイ\nいわせてやるです\nついてくるですよ！", 1, 12, 0, 0, 103, 7743, 106601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "陛下～♪\n貴女のクレジッタが\nおめかしして\nはせ参じましたわ～♪", 1, 1, 0, 0, 1, 7744, 128001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "パーティーとは景気の\n良さを見せつける場…\n倹約などは\nもっての外でしてよ！", 1, 1, 0, 0, 2, 7745, 128001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この天井の高さなら\n搬入できそうですわね\n陛下用の特注ケーキ♪", 1, 1, 0, 0, 3, 7746, 128001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "…ぶぇっくしゅ！\nふう…少し背中を\n開けすぎたかしら…？", 1, 1, 0, 0, 4, 7747, 128001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここは秘書の目も\n届きませんから\nチクりメモの心配も\nありませんのよね～♪", 1, 1, 0, 0, 5, 7748, 128001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "いくらでも二度見して\nよろしくてよ？\n本当にわたくしだと\n心から納得できるまで", 1, 4, 0, 0, 101, 7749, 128001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "袖の下以外の贈り物も\nなかなかどうして…\n悪くないものですのね", 1, 8, 0, 0, 102, 7750, 128001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "こう見えてダンスの\nたしなみはありますの\nお相手してあげても\nよろしくってよ", 1, 1, 0, 0, 1, 7751, 128001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ドレスはもちろん\n特注品ですわ♪\nもしや貸衣装だと\n思いまして？", 1, 1, 0, 0, 2, 7752, 128001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "バッグの中身？\n仕事に必要なものと…\n心を潤わせるための\n宝石がいくつか♪", 1, 1, 0, 0, 3, 7753, 128001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "やる気というものは\n仕事に必要な条件です\nいわばドレスコードの\nようなものですわね", 1, 1, 0, 0, 4, 7754, 128001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "全ての仕事は自分の\n血となり肉となり\nお金にもなるもの\nなのです。経験談です", 1, 4, 0, 0, 101, 7755, 128001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今年の教訓を活かして\n来年は更に素晴らしい\nクリスマスにして\n差し上げますわ！", 1, 8, 0, 0, 102, 7756, 128001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようございます\n今日という稼ぎ時の\nはじまりですわよ！", 1, 1, 1, 0, 1, 7757, 128001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "安くて早くておいしい\nそれがランチに必須の\n栄養素ですわ！", 1, 1, 2, 0, 2, 7758, 128001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "宵越しの仕事は\n安眠の敵！\n熟睡したいのであれば\n今が勝負時ですわよ！", 1, 1, 3, 0, 3, 7759, 128001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "たまにはこうして\n休息を取ることも…\n…はっ！？\nドレスにシワが～っ！", 1, 1, 0, 0, 2, 7761, 128001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "…わたくしの勘が\n言っていますわ\nこの愛らしさ！\n爆売れ確・定！", 1, 1, 0, 0, 3, 7762, 128001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい香りですわね…\nお金の匂いには\n敵いませんけれども♪", 1, 1, 0, 0, 4, 7763, 128001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "……ハッ！？\n完全にボーッとして\nしまいましたわ…！\nぬくもり…恐ろしい！", 1, 1, 0, 0, 5, 7764, 128001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "考えませんわ…\n考えませんわよ！\nカロリーのことはっ！", 1, 1, 0, 0, 7, 7766, 128001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ビジネス本以外の本を\n読むのも、たまには\n悪くありませんわね…", 1, 1, 0, 0, 8, 7767, 128001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "よくできていますわね\nこの絵本！\n子ども向けだと甘く\n見ていましたわ…！", 1, 1, 0, 0, 14, 7769, 128001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この視界の狭さ…\n思い出しますわね\nティッシュ配りの\nアルバイト時代を…", 1, 1, 0, 0, 15, 7770, 128001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ふふふ…こっそり\nスイーツを食べる\nリノちゃん見～っけ♪", 1, 1, 0, 0, 1, 7771, 128101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お姉ちゃんの抜き打ち\nお部屋チェック～♪\n…ケーキの食べこぼし\nこれはリノちゃんだね", 1, 1, 0, 0, 2, 7772, 128101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この衣装は目立つから\nどこにいても弟くんに\n気付いてもらえるね！", 1, 1, 0, 0, 3, 7773, 128101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "弟くん、協力して！\nリノちゃんにお勉強\nしなさいって圧を\nかけるんだよっ…♪", 1, 1, 0, 0, 4, 7774, 128101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "絆の力を感じるよ\nこれならもっと万全な\n弟くんのお世話が\nできそうだねっ！", 1, 1, 0, 0, 5, 7775, 128101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お姉ちゃんもたまには\nリノちゃんみたいに\n甘えてみようかな…", 1, 4, 0, 0, 101, 7776, 128101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "弟くんとリノちゃん\nどっちも大切な\n私のきょうだいだよ", 1, 8, 0, 0, 102, 7777, 128101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "分かってるよ♪\nお姉ちゃんの膝枕を\nご所望だねっ？　え？\n違う？　違わないよ？", 1, 1, 0, 0, 1, 7778, 128101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "リノちゃんがね\nお姉ちゃんチョップを\n防ぐ妹白刃取りを\n訓練してるんだよね", 1, 1, 0, 0, 2, 7779, 128101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お姉ちゃん頭突きは\n封印しないとね…\nだって、今使うと\n頭飾りで威力が…ね", 1, 1, 0, 0, 3, 7780, 128101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふふっ♪\n目をつぶってても\n弟くんは見えてるよ\n…そこでしょ？", 1, 1, 0, 0, 4, 7781, 128101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お姉ちゃんも\nリノちゃんみたいに\nかわいさいっぱいに\n振舞っちゃうぞ♪", 1, 4, 0, 0, 101, 7782, 128101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "弟くんっ♪\nいつもお姉ちゃんの\n声が届く範囲にいてね\n…絶対だよ", 1, 8, 0, 0, 102, 7783, 128101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよう、弟くん！\nリノちゃんが起こして\nくれたんだね♪\n…先を越されちゃった", 1, 1, 1, 0, 1, 7784, 128101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お姉ちゃんランチの\n時間だぞ♪\nさ、食べさせてあげる\nお口を開けて…♪", 1, 1, 2, 0, 2, 7785, 128101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おやすみの時間だよ\n…弟くんは真ん中ね\n左右には私たち姉妹が\nいるから安心だよ♪", 1, 1, 3, 0, 3, 7786, 128101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お姉ちゃんは全力で\nリラックスしてても\n弟くんの声一つで\nすぐにでも動けるよ♪", 1, 1, 0, 0, 2, 7788, 128101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "リノちゃんこういうの\n好きそうだよね\n今度プレゼントして\nみようかな？", 1, 1, 0, 0, 3, 7789, 128101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい匂いがするね～\nあれ？\nリノちゃんのケーキの\n甘い匂いも混ざってる", 1, 1, 0, 0, 4, 7790, 128101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お姉ちゃんの\n弟くんへの愛も\n熱く燃えあがるね…！", 1, 1, 0, 0, 5, 7791, 128101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "たくさん食べて\n栄養を補給して\nお姉ちゃん力に\n全て変換するよっ！", 1, 1, 0, 0, 7, 7793, 128101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ことわざ辞典…\nリノちゃんにこれを\n読ませようかな", 1, 1, 0, 0, 8, 7794, 128101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "そろ～り、そろり…\nお姉ちゃんが居ぬ間に\nスイーツを～…♪", 1, 1, 0, 0, 1, 7797, 128201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "いつか\nお姉ちゃんに似合う服\n作ってみたいですね", 1, 1, 0, 0, 2, 7798, 128201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あいたっ！？\nひらひらしたとこ\n踏んづけちゃいました", 1, 1, 0, 0, 3, 7799, 128201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お姉ちゃんと\nお兄ちゃんの二人から\n勉強しろって圧力が～", 1, 1, 0, 0, 4, 7800, 128201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "私の中にも秘めた力が\n眠っているはず…\nそう、妹パワーが", 1, 1, 0, 0, 5, 7801, 128201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "私もお兄ちゃんを\n甘やかしてみたいです\n…お姉ちゃんみたいに", 1, 4, 0, 0, 101, 7802, 128201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お兄ちゃんの部屋を\nこっそり拝見～♪\nはっ！？　これは\nお姉ちゃんの視線…！", 1, 8, 0, 0, 102, 7803, 128201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お姉ちゃん頭突きは\n大地を揺らす威力です\n私とお兄ちゃんは\n挨拶感覚で喰らいます", 1, 1, 0, 0, 1, 7804, 128201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お姉ちゃんチョップは\n雲を裂く切れ味です\n早く妹白刃取りを\n完成させないと…", 1, 1, 0, 0, 2, 7805, 128201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お姉ちゃんみたいに\n大人っぽく着こなす\nには一体どうしたら…", 1, 1, 0, 0, 3, 7806, 128201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "弓のデザインも\nかわいいんですよっ♪\n常に持ち歩きたいです\nけど…危ないですよね", 1, 1, 0, 0, 4, 7807, 128201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "きれいな服を着てると\nお姫さま気分に\nなれますね…あとは\n王子さまがいれば…♪", 1, 4, 0, 0, 101, 7808, 128201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お兄ちゃんっ！\n私は褒められて伸びる\nタイプなのでもっと\nナデナデしてください", 1, 8, 0, 0, 102, 7809, 128201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "妹モーニングコール♪\nお姉ちゃんより先に\n起こしに来れました！", 1, 1, 1, 0, 1, 7810, 128201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ああっ、お姉ちゃん！\nお兄ちゃんにアーン\nするなんてずるいです\n私もしたいですっ！", 1, 1, 2, 0, 2, 7811, 128201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おやすみの時間ですね\nえへへ、きょうだいで\n並んで寝るのって\n楽しいですよね！", 1, 1, 3, 0, 3, 7812, 128201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "スイーツを食べた後は\nお腹を休めましょう～\n平気平気…ちゃぁんと\n運動もしますからぁ…", 1, 1, 0, 0, 2, 7814, 128201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お姉ちゃんも\nこういうかわいいもの\n好きなんですかね", 1, 1, 0, 0, 3, 7815, 128201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "くんくん…\nなんの匂いでしょうか\n新しいスイーツ？", 1, 1, 0, 0, 4, 7816, 128201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "きょうだい３人\n肩を寄せて暖まるとか\nよくないですかっ？", 1, 1, 0, 0, 5, 7817, 128201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いつも元気な妹で\nいるためには\nよく食べるのが大事！", 1, 1, 0, 0, 7, 7819, 128201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "なんか姉に関する本が\nちらほらと混ざって…\nこれお姉ちゃんの\n仕業ですね！？", 1, 1, 0, 0, 8, 7820, 128201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ふんふんふ～ん\nお正月で世間は\n浮かれてるですねえ\nま、いいですけど♪", 1, 1, 0, 0, 1, 7823, 128301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "は～、お正月は\nだらだらできて\n最高ですね\n…いつも通りですけど", 1, 1, 0, 0, 2, 7824, 128301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ビッグな幹部は\nビッグなお年玉が\nもらえるはずです\n楽しみですね", 1, 1, 0, 0, 3, 7825, 128301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今度ドラちゃんに\nお年玉を持っていって\nやりたいですね", 1, 1, 0, 0, 4, 7826, 128301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "そういえばアユアユと\n約束してたですよ…\nそろそろ出かける\nですかね", 1, 1, 0, 0, 5, 7827, 128301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お年玉がもらえたら\n何に使うか…\n今のうちに\n考えとくです", 1, 4, 0, 0, 101, 7828, 128301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "年神の報酬の駄菓子が\nたまってきたですね…\nガキどもにでも\n配っちまうですよ", 1, 8, 0, 0, 102, 7829, 128301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あたしのレアな\n晴れ着姿、今のうちに\nしっかり目に焼き付け\nとくがいいですよ", 1, 1, 0, 0, 1, 7830, 128301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お兄さんは今日も\nヒマそうですねぇ…\nしょうがないから\nかまってやるですよ", 1, 1, 0, 0, 2, 7831, 128301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そういえばカヤぴぃ\n見なかったですか？\n喧嘩屋初売りセール\nとか言ってたですけど", 1, 1, 0, 0, 3, 7832, 128301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お正月ならではの\n大きなシノギ…\nなにかないですかね？", 1, 1, 0, 0, 4, 7833, 128301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "いいとこに来たですよ\nさっき走ったら帯が\n曲がっちゃったです\n直すの手伝うです！", 1, 4, 0, 0, 101, 7834, 128301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "年神の仕事ですか？\nこれからいくとこです\nお兄さんも一緒に\n来やがるですよ！", 1, 8, 0, 0, 102, 7835, 128301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふぁあ…\nどうしたですか？\nお正月に早起きなんて\n無粋ってヤツですよ…", 1, 1, 1, 0, 1, 7836, 128301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "小腹が減ったですね\n一緒におせちでも\nつつきにいくですよ！", 1, 1, 2, 0, 2, 7837, 128301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜はあたしの時間\nと言いたいとこですが\n初夢のために早く\n寝とくですかね…", 1, 1, 3, 0, 3, 7838, 128301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "正月は食っちゃ寝\nするのが仕事…\nって聞いたこと\nあるですよ", 1, 1, 0, 0, 2, 7840, 128301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふふっ、晴れ着の\nあたしの次くらいには\nかわいいですね", 1, 1, 0, 0, 3, 7841, 128301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "なんかいい匂い…？\nよく知らんですけど\n晴れ着からも同じ\n匂いがしてるですね…", 1, 1, 0, 0, 4, 7842, 128301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふ～あったまるです…\nお正月の唯一の弱点は\n寒いことですからね…", 1, 1, 0, 0, 5, 7843, 128301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もぐもぐ…正月太り？\nあたしには関係ない\n言葉ですね", 1, 1, 0, 0, 7, 7845, 128301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったあった…\nトーゴクには\nドラゴンの神宮も\nあるんですね", 1, 1, 0, 0, 8, 7846, 128301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お正月のトラブルも\nハツネちゃんが\nめでたく解決\nしてみせるのだ～！", 1, 1, 0, 0, 1, 7848, 128401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "そうだ、新年会に\nサプライズ大作戦を\nしかけちゃおう！", 1, 1, 0, 0, 2, 7849, 128401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "イノリちゃんも\nカスミンも振袖姿が\nかわいかったな～", 1, 1, 0, 0, 3, 7850, 128401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "シオリンに\nお年玉あげたいし\n彼にバイトを紹介\nしてもらおうかな？", 1, 1, 0, 0, 4, 7851, 128401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今度はどんな凧を\n飛ばそうかな？\nみんなが驚くような\n凧がいいよね～", 1, 1, 0, 0, 5, 7852, 128401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ひらめいた！\n彼への年賀状は\nきらーんっ☆って\n感じにしよう！", 1, 4, 0, 0, 101, 7853, 128401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "来年も彼の隣で\n初日の出が\n見られるといいな～", 1, 8, 0, 0, 102, 7854, 128401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日はキミが\nギルドハウスに来る\n予知夢を見たんだよ～\nふふ、正夢になったね", 1, 1, 0, 0, 1, 7855, 128401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この振袖どうかな？\nすっごくかわいいよね\nえへへ、ありがとう～", 1, 1, 0, 0, 2, 7856, 128401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ハツネちゃんおみくじ\n開始！　きらーんっ☆\nキミは大吉なのだ～\nどう元気出たかな？", 1, 1, 0, 0, 3, 7857, 128401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今度エルフの森にある\n薬草を使って七草粥を\n作ろうと思うんだ～\nぜひ食べてほしいな♪", 1, 1, 0, 0, 4, 7858, 128401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お正月っぽい遊びを\n色々と用意したのだ～\nたまにはギルドハウス\nでゆっくり過ごそうよ", 1, 4, 0, 0, 101, 7859, 128401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もしかして眠たいの？\n…じゃあ、また膝枕\nしてあげてもいいよ？", 1, 8, 0, 0, 102, 7860, 128401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "いっぱい寝たから\n元気いっぱいだよ～\n今日もがんばろう！", 1, 1, 1, 0, 1, 7861, 128401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼だね～\nおせちがあるから\nキミも食べるでしょ？", 1, 1, 2, 0, 2, 7862, 128401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そろそろ寝る時間だね\n今日はどんな夢を\n見られるかな～", 1, 1, 3, 0, 3, 7863, 128401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい座り心地～\n寝正月で過ごすのも\n最高なのだ～", 1, 1, 0, 0, 2, 7865, 128401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいいな～\n癒されてぐっすり\n眠れそうだよ～", 1, 1, 0, 0, 3, 7866, 128401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい香り～\nリラックスすると\n眠たくなっちゃうよ…", 1, 1, 0, 0, 4, 7867, 128401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ポカポカいい気持ち～\n初日の出の温もりを\n思い出しちゃうな～♪", 1, 1, 0, 0, 5, 7868, 128401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もぐもぐ、おいし～\nでもいっぱい食べたら\n眠くなっちゃった…", 1, 1, 0, 0, 7, 7870, 128401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふむふむ\nお正月料理も\n色々あるんだね～\n今度作ってみようかな", 1, 1, 0, 0, 8, 7871, 128401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "最近カレを見てたら\n何故かおいしそうに\n思えてくるんだよな…", 1, 12, 0, 0, 103, 7873, 106501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "手握っててくれるか？\nじゃないとオレ…\n力が抑えきれない…", 1, 12, 0, 0, 103, 7874, 106501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "つかの間の平和を享受\nしながらぬくぬくと\n読書に耽る…\nふふ、最高だね", 1, 1, 0, 0, 1, 7875, 128501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お正月飾りなんて\n置いたらはしゃいでる\nように見えるかな…？", 1, 1, 0, 0, 2, 7876, 128501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ま、まずい…\n一人遊びしすぎて\nお正月遊びをほぼ\n極めつつある…", 1, 1, 0, 0, 3, 7877, 128501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ミステリーは\n時と場所を選ばない\nつまり…新年でも\n探偵は休まないよ！", 1, 1, 0, 0, 4, 7878, 128501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お世話になった人に\n年賀状でも書こうかな\n助手くん、喜んで\nくれるかな…えへへっ", 1, 1, 0, 0, 5, 7879, 128501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "絵馬に願い事を書こう\n助手くんと一緒に\nいられますように…♪\n…は、恥ずかしいな", 1, 4, 0, 0, 101, 7880, 128501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "助手くんはもっと\n派手な着物のほうが\n好みだったかな…？\nき、気になる…", 1, 8, 0, 0, 102, 7881, 128501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "やあ。助手くんも\nお正月休みを\n持て余してるのかい？\nふふ、似た者同士だね", 1, 1, 0, 0, 1, 7882, 128501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "助手くん助手くん！\n凧あげしにいこう！\n…そ、捜査の一環で", 1, 1, 0, 0, 2, 7883, 128501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "着物は転びそうで\n怖いんだよなあ…\nお、押さないでね？\nフリじゃないよ！？", 1, 1, 0, 0, 3, 7884, 128501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ひ、人目を憚る\n本を読むなら\nみんなが帰省中の今！\nはっ！　助手くん！？", 1, 1, 0, 0, 4, 7885, 128501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ね、ねえ…助手くん…\n一緒に初詣いかない？\nあ、いや！　深い\n意味はないんだけど！", 1, 4, 0, 0, 101, 7886, 128501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あはは…助手くんと\n一緒だと家でごろごろ\nしてるだけでも\n楽しいや…♪", 1, 8, 0, 0, 102, 7887, 128501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふぁ～…\nお正月休みのおかげで\n昼夜逆転中だよ…", 1, 1, 1, 0, 1, 7888, 128501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お正月も依頼が\nひっきりなしだね\nさあ、街へ出よう！\nもちろん君も一緒にね", 1, 1, 2, 0, 2, 7889, 128501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "も、もっと\n近くにきてもいいよ？\n冬の夜は…寒いからね", 1, 1, 3, 0, 3, 7890, 128501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ううぅ～…！\nこのままじゃ\n寝正月だぁ…！", 1, 1, 0, 0, 2, 7892, 128501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "みんなが帰省中でも\nこれがあれば\n寂しくない…かな", 1, 1, 0, 0, 3, 7893, 128501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うーん…♪\nお正月休みの\nリフレッシュに\nちょうどいいや♪", 1, 1, 0, 0, 4, 7894, 128501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふぅ…あったまるな\n冬は寒いから\n助かるよ…", 1, 1, 0, 0, 5, 7895, 128501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うーん、おいしい♪\n読書のお供に\nちょうどいい", 1, 1, 0, 0, 7, 7897, 128501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "時間に余裕があると\n読書が進むよ\nいざ、本棚読破だ♪", 1, 1, 0, 0, 8, 7898, 128501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この新しい力…\nさらに使いこなせる\nようになるには…", 1, 1, 0, 0, 1, 7900, 128801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "私に眠る属性の力…\n風の力以外には\nどんな力があるのかな", 1, 1, 0, 0, 2, 7901, 128801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今の私なら\nどこまでいけるのか…\nやれるだけ\nやってみよう", 1, 1, 0, 0, 3, 7902, 128801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "風の力の制御…\n段々とコツが\n掴めてきたな", 1, 1, 0, 0, 4, 7903, 128801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "風の力か…\n応用の幅は広そうだ\n何ができそうか\n色々試してみよう", 1, 1, 0, 0, 5, 7904, 128801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今度、私とキミで\n料理対決でもする？\n簡単には負けないよ", 1, 4, 0, 0, 101, 7905, 128801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この胸に溢れる\n温かな気持ちは…\nきっと彼への…", 1, 8, 0, 0, 102, 7906, 128801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "一段と剣技が\n冴えてきたような…\nいや…油断禁物だ", 1, 1, 0, 0, 1, 7907, 128801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "かっこいいって？\nありがとう…そんなに\n真っすぐ褒められると\n照れてしまうな…", 1, 1, 0, 0, 2, 7908, 128801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どれほど厚い壁が\n立ち塞がろうと\n打ち破ってみせる", 1, 1, 0, 0, 3, 7909, 128801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "手が空いてるなら\n剣の稽古に少し\n付き合ってくれるかな", 1, 1, 0, 0, 4, 7910, 128801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "試練を乗り越えて…\n私の中にあるものが\nはっきりと分かったよ", 1, 4, 0, 0, 101, 7911, 128801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "キミから流れてくる\n力の温かさ…これが\n私たちの絆なんだ", 1, 8, 0, 0, 102, 7912, 128801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "朝だよ、そろそろ\n起きる時間だ\nそれとも私に起こして\nほしいのかな？", 1, 1, 1, 0, 1, 7913, 128801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "昼食は私が作るよ\nリクエストはある？\nできる範囲で応えるよ", 1, 1, 2, 0, 2, 7914, 128801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おやすみ。また明日も\n一緒に冒険しよう\n約束だよ？", 1, 1, 3, 0, 3, 7915, 128801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "休める時は休む…\n戦い続けるには\n必要なことだからね", 1, 1, 0, 0, 2, 7917, 128801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "心の安らぎの大切さ…\nそれを知れたのは\nキミたちのおかげだね", 1, 1, 0, 0, 3, 7918, 128801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "気持ちが落ち着くな…\nこの香り、風の力で\n纏えたりしないかな", 1, 1, 0, 0, 4, 7919, 128801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "暖まった空気の流れ…\n以前より風の変化に\n気づくようになったな", 1, 1, 0, 0, 5, 7920, 128801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この味付け…\n今度試してみようか\n味見は任せたからね", 1, 1, 0, 0, 7, 7922, 128801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "心技体、すべてを\n鍛えるには座学も\n必要だからね", 1, 1, 0, 0, 8, 7923, 128801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "瞑想するのに\nいいところはあるかな\n集中できて…お昼寝は\n我慢できるところ", 1, 1, 0, 0, 1, 7925, 128901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここにはあったかい\n空気が流れてるなぁ…\nみんなが受け入れて\nくれるおかげだね", 1, 1, 0, 0, 2, 7926, 128901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "まるで心の中に\n光が満ちてるみたい…\nこの気持ち、みんなに\nおすそ分けしたいな♪", 1, 1, 0, 0, 3, 7927, 128901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この衣装は属性の力を\n反映しやすいんだよね\nキラキラしてるのも\n納得だよ", 1, 1, 0, 0, 4, 7928, 128901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "エルフの里のみなさん\n元気かなぁ…\nまた会いに行きたいな", 1, 1, 0, 0, 5, 7929, 128901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "まだ変身できてる…\nわたしと騎士クンの\n修行の成果\n出てるなぁ…ふふっ♪", 1, 4, 0, 0, 101, 7930, 128901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この服を脱いだら\nどうなるのかな…\nた、試す勇気は\nないけど…", 1, 8, 0, 0, 102, 7931, 128901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふぅ…あ、騎士クン\nちょうど瞑想してたの\n一緒に…する？", 1, 1, 0, 0, 1, 7932, 128901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "不思議だけど\nいつの間にか\n出たり消えたりするの\n…背中の輪っか！", 1, 1, 0, 0, 2, 7933, 128901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この服って肌触りが\nとってもいいんだよ\nさ、触ってみる？\nスベスベだよ？", 1, 1, 0, 0, 3, 7934, 128901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "アストラルの力…\nすごく持続できてるね\n修行の成果が出てるよ", 1, 1, 0, 0, 4, 7935, 128901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この力があれば\n暗いところで\n落とし物をしても\n見つけやすいかも…？", 1, 4, 0, 0, 101, 7936, 128901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "冒険の準備なら\nできてるよ\nいつでも声をかけてね\n…楽しみにしてる！", 1, 8, 0, 0, 102, 7937, 128901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよう騎士クン\n一緒にお散歩とか…\nどうかな？", 1, 1, 1, 0, 1, 7938, 128901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼ご飯のあとの\n瞑想って難しいんだよ\n眠くなっちゃって…", 1, 1, 2, 0, 2, 7939, 128901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜ふかしさんは\nほどほどにね？\n一日頑張った自分を\n休ませてあげないと", 1, 1, 3, 0, 3, 7940, 128901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "のんびりしてたら\nケーキが食べたく\nなっちゃうなぁ…\nでも太る…よね…？", 1, 1, 0, 0, 2, 7942, 128901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "わあ、かわいい…！\n見てるだけで\nにやけちゃう…♪", 1, 1, 0, 0, 3, 7943, 128901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これは…花の香りかな\n知ってる花かも…？\n当ててみようっ", 1, 1, 0, 0, 4, 7944, 128901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったかいと\nどうしてこんなに\n落ち着くんだろう…", 1, 1, 0, 0, 5, 7945, 128901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "食べ過ぎたらお腹が\n目立っちゃうかもって\n分かってるのに…！\n止められないよ～っ！", 1, 1, 0, 0, 7, 7947, 128901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こ、恋の精霊とか\nいたりするのかな…？\nはっ！　ダメダメっ！\n実力で頑張らないと", 1, 1, 0, 0, 8, 7948, 128901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お部屋の中でも\n修行、修行っ♪\nでも燃やさないように\n注意しなきゃだね", 1, 1, 0, 0, 1, 7950, 128701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "うーん…この衣装\n厚着か薄着か\nどっちなんだろう…？", 1, 1, 0, 0, 2, 7951, 128701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "いつもの肉球がない分\nお片付けしやすいな♪\n騎士くんのお部屋\nピカピカにしとこー！", 1, 1, 0, 0, 3, 7952, 128701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お休みは騎士くんと\nＢＢＱに行きたいなー\nあたしと一緒なら\n火の心配はいらないよ", 1, 1, 0, 0, 4, 7953, 128701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この姿ならもっと\n人を助けられる…！\nえへへっ！\n燃えてきたーーっ！", 1, 1, 0, 0, 5, 7954, 128701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "騎士くんが帰ったら\nアイラみたいな笑顔で\nお疲れさまさまって\nお出迎えしよっと…♪", 1, 4, 0, 0, 101, 7955, 128701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この姿の時、いつも\n胸が熱く燃えてるのは\nずっと騎士くんのこと\n考えてるからかな…？", 1, 8, 0, 0, 102, 7956, 128701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "騎士くん騎士くん！\nあたしに触れると\nやけどしちゃうよ…？\nなんちゃってー♪", 1, 1, 0, 0, 1, 7957, 128701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おっ、騎士くん\n修行に行きたいの？\nえへへっ、いいねー♪\n二人で燃えちゃおー！", 1, 1, 0, 0, 2, 7958, 128701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そうだ騎士くん\nじゃんけんしよっか？\nグローブ外してるから\nチョキも出せるよっ♪", 1, 1, 0, 0, 3, 7959, 128701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この姿でいられる時間\nすごく増えてきた…！\nえへへ！　あたしたち\n修行の成果出てるね！", 1, 1, 0, 0, 4, 7960, 128701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "じっと見つめないでよ\n顔が真っ赤に…やば！\nほんとに顔から\n火噴いちゃいそう…！", 1, 4, 0, 0, 101, 7961, 128701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "火には強くなれても\n騎士くんに触られた\nほっぺはずーっと\n熱くてたまんないや…", 1, 8, 0, 0, 102, 7962, 128701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよー騎士くん！\n今日も一日\n燃え滾っていくよー！\nファイヤーッ！！", 1, 1, 1, 0, 1, 7963, 128701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "騎士くんお昼だよ！\nちゃんとご飯食べて\n午後からの修行も\n乗り切ろうねー！", 1, 1, 2, 0, 2, 7964, 128701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日もお疲れさま！\nそうだ、寝る前に\nキャンプファイヤー\n囲んじゃおっか♪", 1, 1, 3, 0, 3, 7965, 128701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ぼふーんっ！\nえへへ、気持ちいい…\n修行の前に\nちょっと休憩ー♪", 1, 1, 0, 0, 2, 7967, 128701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいいな～♪\nなでなでなでなでなで\nなでなでなでなでっ！\nあははっ、摩擦熱！", 1, 1, 0, 0, 3, 7968, 128701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ん～いい香り♪\n少し熱したほうが\n香りが広がるね…って\n蒸発しちゃった！！！", 1, 1, 0, 0, 4, 7969, 128701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "外でも自分の火で\nあったまれるけど\nお部屋でぬくぬく\nするのが一番だよね♪", 1, 1, 0, 0, 5, 7970, 128701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もぐもぐっ！\nお腹が減っては\n人助けはできずっ！\nもぐもぐもぐっ！", 1, 1, 0, 0, 7, 7972, 128701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふむふむ…\nなるほどなるほど…\n…これが上手な\nＢＢＱのやり方か…", 1, 1, 0, 0, 8, 7973, 128701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "…壁も扉も柱も\n全て記録で見たまま\n本当に…来ているのね", 1, 1, 0, 0, 1, 7975, 129001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "…騒がしい場所\n騎士クンが受け入れて\nくれるのをいいことに", 1, 1, 0, 0, 2, 7976, 129001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここが…騎士クンの\n守りたかった場所\n騎士クンの思い出が\n詰まった場所…ね", 1, 1, 0, 0, 3, 7977, 129001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "視線を感じる…\nこちらの様子を\n探るような…心の動き\n取るに足らないけれど", 1, 1, 0, 0, 4, 7978, 129001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "…そこかしこで…\n言葉にもしたくない\n相手が過ごしていた\n記録を思い出す…", 1, 1, 0, 0, 5, 7979, 129001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "人は多いけれど…\n雪深い山中よりは\n過ごしやすい…か", 1, 4, 0, 0, 101, 7980, 129001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "自覚したことは\nあまりなかったけれど\nひどく…諦めの悪い女\nなのかもしれない", 1, 8, 0, 0, 102, 7981, 129001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お招きありがとう\nそれで…何をして\nいればいい…かな？", 1, 1, 0, 0, 1, 7982, 129001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ここは…明るいね\nわたしの城と比べると\nなんだか眩しいくらい", 1, 1, 0, 0, 2, 7983, 129001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "仮面はもう必要ないよ\nだって…わたしの顔を\n騎士クンに見て\nもらえなくなるから", 1, 1, 0, 0, 3, 7984, 129001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "騎士クンさえよければ\nわたしの城に専用の\n部屋を用意するよ\n…いつでも", 1, 1, 0, 0, 4, 7985, 129001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "…騎士クンのマント\nまた貸してほしいな\nきれいに使うから\n…だめ？", 1, 4, 0, 0, 101, 7986, 129001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "いつか二人の願いが\n重なり合えるって…\n信じているね\n…いつまでだって…", 1, 8, 0, 0, 102, 7987, 129001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよう、騎士クン\n…昨日は夢の中で\nお話ししすぎちゃって\nごめんね。ふふっ", 1, 1, 1, 0, 1, 7988, 129001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お腹は…空いてない？\n食べ物はないけれど\n空腹を忘れる魔法なら\nかけてあげられるよ", 1, 1, 2, 0, 2, 7989, 129001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "騎士クン…\nまだ、眠らないの？\n…そう。それなら…\nわたしも起きているね", 1, 1, 3, 0, 3, 7990, 129001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "肩の力を抜くには…\nまだ時間がかかりそう\nきっと…騎士クンと\n二人きりなら別なのに", 1, 1, 0, 0, 2, 7992, 129001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ぺんぽこりん…\nそう、確かあれも\nこんな感じだった…", 1, 1, 0, 0, 3, 7993, 129001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "花の香りは…好き\nつかの間\n心を穏やかに\nしてくれるから", 1, 1, 0, 0, 4, 7994, 129001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あたたかい…けれど\n騎士クンの優しさに\n比べれば、まだまだ", 1, 1, 0, 0, 5, 7995, 129001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "おい、しい…？\nこの感覚が、それ？\nなぜか…納得できる", 1, 1, 0, 0, 7, 7997, 129001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "情報を入手するには\nわざわざ本を開く\n必要があるだなんて…\nプレイヤーは面倒ね", 1, 1, 0, 0, 8, 7998, 129001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "メルクリゾートか…\n良い記憶はないな\nよし忘れよう", 1, 1, 0, 0, 1, 8000, 129301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "知性の戦士ユニ！\nその恐るべき筋力！\nふおお！　うにゃー！", 1, 1, 0, 0, 2, 8001, 129301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ぽーん！\n石用の水着ください！\nセクシーロゼッタが、\n渚の視線を独占です！", 1, 1, 0, 0, 3, 8002, 129301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "学院から奨学金を\n勝ち取るより某財団を\nだまくらかした方が\n早い気がしてきたな…", 1, 1, 0, 0, 4, 8003, 129301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "わんつぅ、わんつぅ…\nんぎゃあ足攣ったー！\nおのれカルミナとやら\n絶対に許さん！", 1, 1, 0, 0, 5, 8004, 129301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ところで…なぜぼくは\nプールでもないのに\n水着のままなんだ…\nおのれ世界め！", 1, 4, 0, 0, 101, 8005, 129301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "彼と二人で撮った動画\nうう、凝視に耐えん…\nなんだこのムズ痒さは", 1, 8, 0, 0, 102, 8006, 129301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なに？　水着姿で\n寒くないのかだと？\n普段からもっと薄着の\n娘が沢山いるだろう", 1, 1, 0, 0, 1, 8007, 129301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "シノブ君にメリゾの\n招待状を送ったのだが\n御尊父の暴走を\n心配していたな…", 1, 1, 0, 0, 2, 8008, 129301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ミミ君らにメリゾの\n招待状を送ったのだよ\n迎える準備をせねば…\nおしゃぶり持ってる？", 1, 1, 0, 0, 3, 8009, 129301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "本質の賢者…？\nいや元気にしているが\nなぜ君がぼくの身内を\n知っているんだ…？", 1, 1, 0, 0, 4, 8010, 129301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "水着が似合ってる？\nな、なんだね突然…\nやめたまえよふひひ…", 1, 4, 0, 0, 101, 8011, 129301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今度は夏にメリゾへ\n行きたいものだ\nむろん君も一緒だぞ", 1, 8, 0, 0, 102, 8012, 129301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ぐああっ、朝陽が…！\n朝陽が昇っている…！\n夜行性ユニ死んじゃう", 1, 1, 1, 0, 1, 8013, 129301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "はぁ…もう昼か…\nそろそろ寝ないとな\n規則正しい生活を\nしないと不健康だ…", 1, 1, 2, 0, 2, 8014, 129301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ひゃっはー、夜だー！\nオレたちの時間だぜ！\nふおおブチ上がり～！", 1, 1, 3, 0, 3, 8015, 129301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これは…ぼくにここで\n寝ろということか？\n一向に構わんが…", 1, 1, 0, 0, 2, 8017, 129301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "きゃっきゃっ！\nこれかわい～！\n…よし、今日も\n子供の擬態完璧だな", 1, 1, 0, 0, 3, 8018, 129301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふむ、良い香りだ\nだがそれだけか…\nとくに精神高揚効果は\nないのか…ちぇー", 1, 1, 0, 0, 4, 8019, 129301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "火を見ていると…\n魔導罐を思いだすな…\nかに座、元気かな…", 1, 1, 0, 0, 5, 8020, 129301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ええい、\n固形食物はいらん！　\nポーションを出せ！", 1, 1, 0, 0, 7, 8022, 129301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "やはり書は文系に限る\nなんと心地よい筆致…\nぐああ突然理系文が！\nひぃ目が理系になる！", 1, 1, 0, 0, 8, 8023, 129301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ほああ～！\n知性の海上騎士ユニ！\nこれは新たな可能性！", 1, 1, 0, 0, 10, 8025, 129301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これは…なんだ？\n何が面白いんだ？\n難しいな…", 1, 1, 0, 0, 11, 8026, 129301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "大切な人と過ごす\n正月の思い出、か…\n昔の私だったら\n考えられなかったな", 1, 12, 0, 0, 103, 8027, 108901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "叶えたい願いなら\nたくさんあるよ\nそれでも一番を選ぶ\nなら、やはりキミと…", 1, 12, 0, 0, 103, 8028, 108901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "神殿にあった\n恋愛おみくじ…\n引いてみようかな…", 1, 12, 0, 0, 103, 8029, 108801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "騎士クン。来年も\nまた一緒におみくじ\n結ぼうね。約束だよ", 1, 12, 0, 0, 103, 8030, 108801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今度は騎士くんと\n一緒にうつった写真、\nおばあちゃんに\n送ろうっと♪", 1, 12, 0, 0, 103, 8031, 108701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あ、騎士くん！\n今年も…ううん\nこれからも、よろしく\nおねがいします♪", 1, 12, 0, 0, 103, 8032, 108701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "旧校舎の地下牢…\n真冬のサマー計画…\n等身大のＰＲ…\nうッ、頭が…！", 1, 1, 0, 0, 1, 8033, 129401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "暇だし鏡でも見るか。\nお、いい腹筋育って…\nあ？　何見てんだコラ", 1, 1, 0, 0, 2, 8034, 129401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "オレは金色の疾風！\nんん…なんか違うな。\n我が名は金色の…\nあ？　何見てんだコラ", 1, 1, 0, 0, 3, 8035, 129401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "モニカとユニ先輩って\n口調と身長似てるけど\n子供扱いに対する\n反応は真逆だよね…", 1, 1, 0, 0, 4, 8036, 129401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "シオリン元気かな…\n昔くちゃくちゃに\n撫でくり回して\n嫌がられたっけなァ…", 1, 1, 0, 0, 5, 8037, 129401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "べつにいんだけどさ…\nあんたの知り合いって\n普段から水着みたいな\nカッコの女多くない？", 1, 4, 0, 0, 101, 8038, 129401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "そだ、弟らがあんたに\n会いたがってたンだ。\n暇だったらいつでも\nウチ来てやってよ", 1, 8, 0, 0, 102, 8039, 129401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "特別講座は色々あった\nけど…メリゾ自体は\nまあ悪くなかったよね", 1, 1, 0, 0, 1, 8040, 129401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "水着と合わせる小物に\nセンス出るよね、\nって話をミィコ先輩が\nしててマジわかりみ", 1, 1, 0, 0, 2, 8041, 129401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "水着のミフユねえさん\nやっぱ仕上がってたね\nあれが竜殺しの体か…\nバリかっけーな…", 1, 1, 0, 0, 3, 8042, 129401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "はにょんっ！？\nや、だからさ…\n後ろから声かけんな、\nつってンじゃん…", 1, 1, 0, 0, 4, 8043, 129401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "メリゾの特別講座が\n上手くいったのも\n敏腕カメラマンがいた\nお陰だよ。ありがとね", 1, 4, 0, 0, 101, 8044, 129401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あ？　この水着が？\n似合っ…え、なんて？\nごめ、聞こえんくて。\nもっかい言ってみて", 1, 8, 0, 0, 102, 8045, 129401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もう朝とかマジないわ\n昨日二人に付き合って\nオールでカラオケだよ", 1, 1, 1, 0, 1, 8046, 129401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "はぁ授業行かなきゃ…\n真っ昼間から勉強とか\nマジつらたん過ぎ…", 1, 1, 2, 0, 2, 8047, 129401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日これから夜勤で…\nいやバイトちゃーくて\nボランティアの話よ？\n校則違反よくない", 1, 1, 3, 0, 3, 8048, 129401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うわ、ふっかふか…\nオニほど安らぐわ…\nやば、ユニってくる…", 1, 1, 0, 0, 2, 8050, 129401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うちも妹いりゃ\nこーゆーのもなァ…\nま自分用でもいいか", 1, 1, 0, 0, 3, 8051, 129401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふーん…\nいいじゃんこの香り\n抜群に嗅げるわ", 1, 1, 0, 0, 4, 8052, 129401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うちン家のルーツの森\n大火災でなくなった\nらしンだよね…\n生まれる前の話だけど", 1, 1, 0, 0, 5, 8053, 129401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "何コレありがてぇー\n食料ほくほく過ぎて草\n米ガチで尊いわァ…", 1, 1, 0, 0, 7, 8055, 129401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いいけど、なんでうち\n水着で本読んでンだよ\nどーゆープレイだよ", 1, 1, 0, 0, 8, 8056, 129401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "金色の海上騎士クロエ\n推して参る！　覇ァ！\nあ？　何見てんだコラ", 1, 1, 0, 0, 10, 8058, 129401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "何コレ、貝の椅子？\nどーやって遊ぶんだ…\nいや座って終わりか…\nまァそりゃそーか…", 1, 1, 0, 0, 11, 8059, 129401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "さてと…\n明日の訓練メニュー\n考えねえとな", 1, 1, 0, 0, 1, 8060, 129801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "また新入りの女子に\n弁当もらっちまった…\nありがたく食うけどよ", 1, 1, 0, 0, 2, 8061, 129801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "種族なんて関係なく\nみんなが力を合わせて\nランドソルを守る…\nいい時代になったよな", 1, 1, 0, 0, 3, 8062, 129801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "誰にだって長所はある\n新入りのいいところを\n見つけてやるのも\n教官の仕事だ", 1, 1, 0, 0, 4, 8063, 129801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "厳し過ぎても\n甘過ぎてもダメ…\n教官ってのは難しいぜ", 1, 1, 0, 0, 5, 8064, 129801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "戦いが苦手な新入りを\nどうフォローするか…\n後であいつと\n相談してみるか", 1, 4, 0, 0, 101, 8065, 129801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あいつは優し過ぎて\n戦いに向いてねえ\n…そういう甘さ\n嫌いじゃねえけどな", 1, 8, 0, 0, 102, 8066, 129801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "強くなる近道はねえ\n地道な鍛錬あるのみだ\nさ、訓練始めるぞ！", 1, 1, 0, 0, 1, 8067, 129801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "新入りにも\nいつも言ってるが…\n装備の手入れは\n念入りにしろよな？", 1, 1, 0, 0, 2, 8068, 129801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "座学も受けてえのか？\nいい心がけだな！\nなんでも教えてやるよ\n…魔法以外ならな", 1, 1, 0, 0, 3, 8069, 129801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "新入りたちがよく\n恋バナしててな…\nお、おまえは…誰か\n気になるやついるか？", 1, 1, 0, 0, 4, 8070, 129801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なあ、ストレッチ\n手伝ってくれねえか？\n訓練前にしっかり\n筋肉ほぐさねえと…", 1, 4, 0, 0, 101, 8071, 129801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ずいぶん頑張って\n鍛えたよな、おまえ\nよし！　ご褒美に\nお菓子作ってやるよ♪", 1, 8, 0, 0, 102, 8072, 129801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "訓練日和だな！\nさぁて…まずは\n走り込みといくか！", 1, 1, 1, 0, 1, 8073, 129801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "昼飯はしっかり食えよ\nでなきゃ、\nこの後もたねえぜ？", 1, 1, 2, 0, 2, 8074, 129801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "とっくに消灯時間だ\nさっさと寝な？\n明日も早いんだからよ", 1, 1, 3, 0, 3, 8075, 129801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "休むのも訓練のうちだ\n今はのんびり\nさせてもらうぜ", 1, 1, 0, 0, 2, 8077, 129801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい面構えだ\n気に入ったぜ！\nあたしの班に入るか？\n…なんてな♪", 1, 1, 0, 0, 3, 8078, 129801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "…いやされる匂いだな\n新入りたちにも\nすすめてみるか…", 1, 1, 0, 0, 4, 8079, 129801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったけえからって\n寝ないようにしねえと\n教官としての\n威厳に関わるからな…", 1, 1, 0, 0, 5, 8080, 129801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "食事も訓練の一環だ\nとはいえ食い過ぎには\n気をつけねえと…", 1, 1, 0, 0, 7, 8082, 129801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "トーゴクの剣術…か\nなにか訓練に\n活かせるかもしれねえ", 1, 1, 0, 0, 8, 8083, 129801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あのおかたに近付く\n不埒な輩には、特別な\n訓練で地獄を見て\nいただきましょう", 1, 1, 0, 0, 1, 8085, 129901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "これだけ広ければ\n多少派手に\n動き回っても\n問題なさそうですね", 1, 1, 0, 0, 2, 8086, 129901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "…今誰か私を\n「鬼」教官と\n呼びませんでしたか？", 1, 1, 0, 0, 3, 8087, 129901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "新しく開発したこの薬\n誰か試してくれる人は\nいませんか？", 1, 1, 0, 0, 4, 8088, 129901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "厳しくすれば投げ出し\n優しくすればサボる\n人というのはどこまで\n度し難いのでしょう", 1, 1, 0, 0, 5, 8089, 129901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "はじめましょう\nあのおかたの心を\n射貫くための\nシミュレーションを", 1, 4, 0, 0, 101, 8090, 129901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あらゆる苦難と\n忌々しい女から守る\nためにも、私自身も\n鍛えておかねば", 1, 8, 0, 0, 102, 8091, 129901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなた様、そんなに\n見つめられると…え？\nエリコはカッコいい？\nええ、当然ですとも", 1, 1, 0, 0, 1, 8092, 129901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "不調があればすぐ\n報告してください\n体のケアも教官の\n大事な役目ですので", 1, 1, 0, 0, 2, 8093, 129901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "新しい訓練器具の\n使い心地はどう…え？\n重すぎる？　これでも\n軽くしたのですが…", 1, 1, 0, 0, 3, 8094, 129901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "運動後には特製の\n栄養ドリンクをどうぞ\nクスクス、何も\n入れたりしてませんわ", 1, 1, 0, 0, 4, 8095, 129901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "よくがんばりました\nあなた様。あぁ…\nお疲れになったお顔も\n素敵ですわ", 1, 4, 0, 0, 101, 8096, 129901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなた様、二人だけの\n遠征訓練へ\nまいりましょう。\n既に準備万端ですわ", 1, 8, 0, 0, 102, 8097, 129901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "まだ眠いですか？\nならばストレッチで\n体を痛めつ…ではなく\nほぐしていきましょう", 1, 1, 1, 0, 1, 8098, 129901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼はお弁当をご用意\nいたしました。量も\nスタミナも増し増し\n残さず食べてください", 1, 1, 2, 0, 2, 8099, 129901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "よい睡眠こそ健やかな\n体に不可欠。ですので\n私が寝ずの番をさせて\nいただきますわ", 1, 1, 3, 0, 3, 8100, 129901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "休憩用に使おうと思い\nましたが、心地よくて\n立てなくなって\nしまいますわ…", 1, 1, 0, 0, 2, 8102, 129901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "中に重しを詰めれば\n愛でながら運動が\n可能ですわ", 1, 1, 0, 0, 3, 8103, 129901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "香りでリラックスさせ\n同時に暗示を\nかけてみましょうか", 1, 1, 0, 0, 4, 8104, 129901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "…くっ、眠気を誘う\nこの暖かさ…\n油断も隙もありません", 1, 1, 0, 0, 5, 8105, 129901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "主食はこちらで十分\nまかなえますわね\nそれでは私はおかずを\n作りましょう", 1, 1, 0, 0, 7, 8107, 129901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "まあ、実用書の類も\n揃っているのですね\nとても助かりますわ", 1, 1, 0, 0, 8, 8108, 129901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "……（そわそわ）", 1, 1, 0, 0, 1, 8110, 129601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここに寄らずの花陣…\n作ったらダメですよね", 1, 1, 0, 0, 2, 8111, 129601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "部屋の隅が\n落ち着きます\nそれか、陽の当たる所", 1, 1, 0, 0, 3, 8112, 129601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "身を隠せる場所が\nあると…\n助かるんですけど", 1, 1, 0, 0, 4, 8113, 129601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "複雑な造りの家ですね\n触って壊れたり─\nしませんか？", 1, 1, 0, 0, 5, 8114, 129601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "出入りするひと\nみんな、笑顔…\n…変わった家ですね", 1, 4, 0, 0, 101, 8115, 129601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この家なら安全─\nかもしれません\n魔物が出ないので", 1, 8, 0, 0, 102, 8116, 129601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "これも、視察の一環\n遊びに来たわけじゃ\nないです", 1, 1, 0, 0, 1, 8117, 129601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "長話─苦手です\n疲れるので…\nふぅ…", 1, 1, 0, 0, 2, 8118, 129601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ここで友達を作れと\nネアさんが…\nそう、言われても…", 1, 1, 0, 0, 3, 8119, 129601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "世話をするのは\n得意です\n…花の、ですよ？", 1, 1, 0, 0, 4, 8120, 129601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "媛へのお土産を\n探してるんですけど…\n…貴方のおすすめ、\n聞いておきます", 1, 4, 0, 0, 101, 8121, 129601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "平和でのんきで…\n貴方らしい家です\n一応、褒めてます", 1, 8, 0, 0, 102, 8122, 129601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "花に、お水─？\n…いりません", 1, 1, 1, 0, 1, 8123, 129601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "日が高いうちは\nむやみに外へ出ません\n面倒事、多いので…", 1, 1, 2, 0, 2, 8124, 129601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜番。私がします\nあまり眠らない\n性質なので…", 1, 1, 3, 0, 3, 8125, 129601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ここなら根を張れる\nかもしれません\n…張りませんけど", 1, 1, 0, 0, 2, 8127, 129601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こんなにやわな体じゃ\n身を守れませんよ─\n…あ。生き物じゃ\nないんですね", 1, 1, 0, 0, 3, 8128, 129601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "花みたいに─\nいい匂い、です", 1, 1, 0, 0, 4, 8129, 129601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "火を見てると、\nジオ・ゲヘナを\n思い出します…ふぅ", 1, 1, 0, 0, 5, 8130, 129601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "食事も立派な戦支度\n媛が言ってました", 1, 1, 0, 0, 7, 8132, 129601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "知識も武器の一つだと\n媛が…でも、\nこれでどうやって…？", 1, 1, 0, 0, 8, 8133, 129601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "天気もいいしお花畑に\nピクニックでも\n行こうかな♪", 1, 1, 0, 0, 1, 8135, 130701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あ、どこからか\nお花の匂いが…\nいい香りですね", 1, 1, 0, 0, 2, 8136, 130701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "新しいシャベルとか\nじょうろもほしいなぁ\nお小遣いしっかり\nためないとね", 1, 1, 0, 0, 3, 8137, 130701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お花飾りたいけど\n花瓶はどこかな…\nあとであの人に\n聞いてみよっと", 1, 1, 0, 0, 4, 8138, 130701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お花の観察日記\n書かなきゃ\n日々勉強は\n欠かせませんよ", 1, 1, 0, 0, 5, 8139, 130701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "衣装にお花を足して\nみたけど、あの人\n気付いてくれるかな？", 1, 4, 0, 0, 101, 8140, 130701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この花冠、上手に\n作れたかも…\n日頃のお礼にあの人に\nあげようかな…", 1, 8, 0, 0, 102, 8141, 130701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたは何のお花が\n好きですか？　ほら、\n一応知っておいたら\n役に立つかもですし", 1, 1, 0, 0, 1, 8142, 130701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そのお花、乱暴に\n扱っちゃダメですよ\n花粉が服について\nとれなくなりますから", 1, 1, 0, 0, 2, 8143, 130701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "土いじりしたから\n服が汚れてますよ\nはたいてあげますから\nじっとしててください", 1, 1, 0, 0, 3, 8144, 130701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そんなにすぐに芽は\n出ませんよ\nまあ、楽しみなのは\n私も同じですけど…", 1, 1, 0, 0, 4, 8145, 130701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この衣装、そんなに\n気になります？　なら\nこうしてお花を\n着ければおそろいです", 1, 4, 0, 0, 101, 8146, 130701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お花の種を私に？\nありがとうございます\nせっかくなので一緒に\nお世話しませんか？", 1, 8, 0, 0, 102, 8147, 130701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お花にお水をあげるの\n手伝ってもらえます？\nお水は朝にあげるのが\nいいんですよ", 1, 1, 1, 0, 1, 8148, 130701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "とっておきのお花見\nスポットに\nご案内しますよ\nお弁当も忘れずに！", 1, 1, 2, 0, 2, 8149, 130701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もうこんな時間\n早く寝ましょう\n草木も眠るなんとか\nって言いますからね", 1, 1, 3, 0, 3, 8150, 130701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お花のお手入れって\nけっこう疲れますね…\nここでちょっと一息\nしちゃおっと", 1, 1, 0, 0, 2, 8152, 130701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "耳にお花を着ければ…\nえへへ、すっごく\nかわいくなったね！", 1, 1, 0, 0, 3, 8153, 130701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これはラベンダー\nこっちはミモザ\nフローラル系の香り\nっていいですねぇ", 1, 1, 0, 0, 4, 8154, 130701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ポカポカしますね～\nまるで春の陽気\nみたいです～", 1, 1, 0, 0, 5, 8155, 130701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お花を一輪さすだけで\nご飯の時間がとても\n華やかになりますね", 1, 1, 0, 0, 7, 8157, 130701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お花の本が\nいっぱいある！\n今日はこれで\nお勉強です！", 1, 1, 0, 0, 8, 8158, 130701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ふんふ～ん♪\n華やかな服を着ると\n心がウキウキします♪", 1, 1, 0, 0, 1, 8160, 130801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "花祭りで出会った\nみなさん…\nお元気でしょうか？\nまたお会いしたいです", 1, 1, 0, 0, 2, 8161, 130801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "はっ！　そうでした\nこの服の時は\nメイドのお仕事は\nお休みでした…", 1, 1, 0, 0, 3, 8162, 130801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この服は替えが\nありませんから\n大きなドジには\n気を付けないと…", 1, 1, 0, 0, 4, 8163, 130801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "目立ったほつれもなし\n…うん、いい感じに\n補修できました♪", 1, 1, 0, 0, 5, 8164, 130801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "メイド姿でなくても\n心はいつでも\nお嬢さまの\nメイドです♪", 1, 4, 0, 0, 101, 8165, 130801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この服を着ると心が\n浮き立つのはあのかた\nと一緒にいる気持ちに\nなるからでしょうか？", 1, 8, 0, 0, 102, 8166, 130801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "はい、今日は一日\nこの服で過ごそうと\n思います\n変じゃないですよね？", 1, 1, 0, 0, 1, 8167, 130801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "花祭りのおかげで\n少しだけお花に\n詳しくなった…\nような気がします！", 1, 1, 0, 0, 2, 8168, 130801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "花祭り、今度は\nお嬢さまと救護院の\nみんなと一緒に\n行ってみたいですね", 1, 1, 0, 0, 3, 8169, 130801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今度キョウカちゃんと\n花祭りの服でお花を\n見にお出かけ\nするんです", 1, 1, 0, 0, 4, 8170, 130801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "メイド服を全部洗濯\nしちゃうなんて…\nドジっちゃいましたぁ", 1, 4, 0, 0, 101, 8171, 130801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "花祭りの服は私たちの\n思い出の服ですから…\nこれからも\n大切にします♪", 1, 8, 0, 0, 102, 8172, 130801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "はう、そうでした\nお花にお水を\nあげる時間です！", 1, 1, 1, 0, 1, 8173, 130801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼ご飯は\nバスケットを持って\nお花畑に食べ\nに行きませんか？", 1, 1, 2, 0, 2, 8174, 130801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふぁあ…繕い物を\nしていたらこんな時間\nになってしまいました\n早く寝ないと…", 1, 1, 3, 0, 3, 8175, 130801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふわふわしてて\nきもちいいです～\nどんな生地を\nつかってるんだろ～？", 1, 1, 0, 0, 2, 8177, 130801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいいですね～\nぬいぐるみさんにも\n服をつくって\nあげたいです♪", 1, 1, 0, 0, 3, 8178, 130801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい香りです～\nあれ？　もしかして\n私の服についてる花と\n同じ香りが…？", 1, 1, 0, 0, 4, 8179, 130801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "暖かいですけど\nあまり近づいちゃうと\n頭の花飾りが乾燥\nしちゃいそうですね…", 1, 1, 0, 0, 5, 8180, 130801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もぐもぐ…\nいい花を咲かせるには\nしっかりとした栄養が\n必要だそうです！", 1, 1, 0, 0, 7, 8182, 130801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "なるほどなるほど…\nお花のお手入れは\n奥が深いですね…", 1, 1, 0, 0, 8, 8183, 130801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "マホマホ王国にも\nサーカス団設立や\n夢と希望を届けますえ", 1, 1, 0, 0, 1, 8185, 131001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "せっかくの晴れ舞台や\nかっこいいポーズも\nできるようにしたいわ", 1, 1, 0, 0, 2, 8186, 131001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "むむむむ～っ。うーん\nクルルはんみたいに\n胸から宝石を出すんは\n難しいみたいやわ", 1, 1, 0, 0, 3, 8187, 131001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あむっ…んん～♪\nやっぱり練習のあとは\n甘いものに限るわぁ", 1, 1, 0, 0, 4, 8188, 131001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "アーサーたちの衣装も\n今のうちに考えんと…\n生地、足りるやろか", 1, 1, 0, 0, 5, 8189, 131001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "王子はんとなら\nどんなアイデアも\n見つけられます♪", 1, 4, 0, 0, 101, 8190, 131001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "うちの晴れ姿が\n王子はんの目に一等\n輝いて見えますように", 1, 8, 0, 0, 102, 8191, 131001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "キリっとしたうちも\nかっこいい？\nもう、上手やなぁ", 1, 1, 0, 0, 1, 8192, 131001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "新しい演目考えたんよ\nなぁ王子はん、意見\n聞かせてもらえます？", 1, 1, 0, 0, 2, 8193, 131001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "聞いてや王子はん\n動物さんたちとさらに\n息が合うてきたんよ\nみんな頑張り屋さんや", 1, 1, 0, 0, 3, 8194, 131001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "演技の感想なら\n大歓迎どすえ♪\nたんと聞かせてや～", 1, 1, 0, 0, 4, 8195, 131001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "王子はん、本番当日は\n最前席で見たってな\nそしたらうち、もっと\n頑張れますさかい♪", 1, 4, 0, 0, 101, 8196, 131001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この技をあなたに\n捧げます…なんて♪\n驚いたやろ？\nどっきり作戦成功や♪", 1, 8, 0, 0, 102, 8197, 131001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "素敵なショーのためや\n今日も焦らず一歩ずつ\n成長していきまひょ\nな、王子はん♪", 1, 1, 1, 0, 1, 8198, 131001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お弁当、王子はんも\n一緒にいかがどす？\nみんなで食べて団結や", 1, 1, 2, 0, 2, 8199, 131001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜のサーカスも\n幻想的でとっても\n魅力的なんよ\n一緒にいきまひょか♪", 1, 1, 3, 0, 3, 8200, 131001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "練習が終わったあとは\nの～んびりして\n体力を回復せな…", 1, 1, 0, 0, 2, 8202, 131001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この子たちとなら\n空中ブランコや\n玉乗りもええなぁ", 1, 1, 0, 0, 3, 8203, 131001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい香りやなぁ\n本番前の緊張も\n和らげてくれそうやわ", 1, 1, 0, 0, 4, 8204, 131001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "このあったかい感じ…\nスポットライトの熱を\n思い出すわぁ", 1, 1, 0, 0, 5, 8205, 131001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ちゃんと食べへんと\nいい演技はできまへん\n団長はんも言うてたわ", 1, 1, 0, 0, 7, 8207, 131001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "動物さんたちのことも\nもっと勉強せな\n大事な仲間やさかい", 1, 1, 0, 0, 8, 8208, 131001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "器用じゃないが、\n情には厚いよ\nこういう女を「一途」\nってそう言うんだろ？", 1, 12, 0, 0, 103, 8210, 105601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "斬れないものはないと\n思ってきたさ。だけど\n…この気持ちだけは\n斬れないね、絶対に", 1, 12, 0, 0, 103, 8211, 105601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "んしょ、んしょっ…！\n日頃の感謝を込めて\nヒーローさんのお家を\nきれいにしますです♪", 1, 1, 0, 0, 1, 8212, 130901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ふむふむ…\n新アトラクションは\nこのギルドハウスを\nモチーフに…", 1, 1, 0, 0, 2, 8213, 130901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ごそごそ、こそこそ…\nヒーローさんに内緒で\nどこかにお礼の宝石を\n隠しておきましょう…", 1, 1, 0, 0, 3, 8214, 130901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "パークの運営が\n落ち着いたら\nわたしもギルド活動？\nしてみたいですます♪", 1, 1, 0, 0, 4, 8215, 130901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "こんなすてきな\nおうちに住めたら…\nいえ！　うさぎ小屋で\n十分です～っ", 1, 1, 0, 0, 5, 8216, 130901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ヒーローさんのおうち\nだって意識したら\nアトラクションよりも\nドキドキしてきます…", 1, 4, 0, 0, 101, 8217, 130901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ヒーローさんのことを\n考えると顔が赤く…\nこれってもしかして\nひょっとして…？", 1, 8, 0, 0, 102, 8218, 130901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あっ、ヒーローさん！\n話しかけてくれた記念\nということで、宝石\n受け取ってください♪", 1, 1, 0, 0, 1, 8219, 130901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "元気がない時は\n語尾にぴょんですっ！\nさあヒーローさんも\nやってみるぴょん♪", 1, 1, 0, 0, 2, 8220, 130901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ヒーローさんへのご恩\nおばあちゃんうさぎに\nなるまでに、お返し\nできるでしょうか…？", 1, 1, 0, 0, 3, 8221, 130901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ヒーローさん！\n二人で一緒に\nパレードの練習\nしませんか～♪", 1, 1, 0, 0, 4, 8222, 130901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あはは♪\nヒーローさんは\n人を笑顔にする力が\nありますです♪", 1, 4, 0, 0, 101, 8223, 130901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ヒーローさんに\n一日一恩返しですっ！\n返し終わるまで\nやめませんですます♪", 1, 8, 0, 0, 102, 8224, 130901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようございます♪\nんしょ、んしょっ…！\n気持ちよく起きられた\n記念に宝石どうぞ♪", 1, 1, 1, 0, 1, 8225, 130901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼にフードコートで\nかばばん君バーガー\n買ってきましたよ！\n一緒に食べますです♪", 1, 1, 2, 0, 2, 8226, 130901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日も一日\nお疲れさまですっ♪\nマッサージしますから\n寝そべってください♪", 1, 1, 3, 0, 3, 8227, 130901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はふぅ～…♪\nお仕事の疲れも\nぴょんぴょ～んって\n飛んでっちゃいます♪", 1, 1, 0, 0, 2, 8229, 130901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "わぁっ、とっても\nかわいいですますっ！\nギルドハウスの\nマスコットですね♪", 1, 1, 0, 0, 3, 8230, 130901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ヒーローさんの好きな\n香り…これを宝石に\nつければ、受け取って\nもらえるでしょうか？", 1, 1, 0, 0, 4, 8231, 130901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はあ、気持ちいい♪\nヒーローさんの\n好きな温度に\n調整しときますです♪", 1, 1, 0, 0, 5, 8232, 130901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "支配人は体が資本っ！\nたくさん食べ…すぎて\n動けなくなっちゃい\nましたぁ～…！", 1, 1, 0, 0, 7, 8234, 130901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "パークの\nガイドブックとか\nあったらいいですね♪", 1, 1, 0, 0, 8, 8235, 130901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "外あつすぎ溶けるわ～\nやっぱ部屋ん中で\n水着がラクだねー", 1, 1, 0, 0, 1, 8237, 131101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ン～\nカリザきゅんどこー？\nもしかして逃げた？", 1, 1, 0, 0, 2, 8238, 131101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ミソラちゃんと\nリリちゃん誘って\nそろそろイイコト\nしよっかなー？", 1, 1, 0, 0, 3, 8239, 131101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "おねえちゃんに\n頼まれたシゴトは\nまた今度にすっかー\n夏休みだしー♪", 1, 1, 0, 0, 4, 8240, 131101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "騎士きゅんの\nプリンセスフォームも\n見てみたいんだよなー\nンフ♡", 1, 1, 0, 0, 5, 8241, 131101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "スライムを\nこーしてあーしたら\n騎士きゅんの泳ぐ\n練習に使えるっぽ？", 1, 4, 0, 0, 101, 8242, 131101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "おかえり～♡\nって水着で迎えたら\n騎士きゅん喜んで\nくれるかなー？", 1, 8, 0, 0, 102, 8243, 131101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "騎士きゅん\nこのサングラス\nかけてみる？\nぜってー似合うよー", 1, 1, 0, 0, 1, 8244, 131101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "日焼け痛かったら\nいつでも言ってねー\nスライムでひんやり\nしてあげるよ～♪", 1, 1, 0, 0, 2, 8245, 131101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今から外で\n焼いてこっかなー\n騎士きゅんもどー？", 1, 1, 0, 0, 3, 8246, 131101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "スライムマッサージ\nしてあげよっかー\nほーれ、もみもみ～♡", 1, 1, 0, 0, 4, 8247, 131101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "騎士きゅんが選んだ\n水着も着てみたーい\nネアおねえちゃんに\nどんなの着てほしい？", 1, 4, 0, 0, 101, 8248, 131101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "騎士きゅ～ん\n尻相撲しよっかー♪\nおねえちゃんに\n勝てたらご褒美だぞ～", 1, 8, 0, 0, 102, 8249, 131101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "暑すぎてムダに\n早起きしちった～\n二度寝すっか～\n…ふぁあ…", 1, 1, 1, 0, 1, 8250, 131101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夏バテしても\nスイカとかかき氷なら\n全然ウェルカム\nなんだよなー", 1, 1, 2, 0, 2, 8251, 131101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日はこのまま\n水着で寝るかー\n夢ん中で泳ごー", 1, 1, 3, 0, 3, 8252, 131101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いったんキューケイ…\nンー…よし\n充電カンリョ～♪", 1, 1, 0, 0, 2, 8254, 131101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "仲間になりたそうに\nこっち見てんねー\nきゃわいいしおっけ～", 1, 1, 0, 0, 3, 8255, 131101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ン～いいニオーイ\nお日様あたった\n騎士きゅんの\n次くらいかな～", 1, 1, 0, 0, 4, 8256, 131101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "カラダ冷えてきたから\nたすかるーおかげで\nまだまだ水着イケるわ", 1, 1, 0, 0, 5, 8257, 131101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うめ～\nとまんねぇ～\n夏バテどこいった～？", 1, 1, 0, 0, 7, 8259, 131101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふむふむ…\nこれは意外と\nぬるっと読めるっぽー", 1, 1, 0, 0, 8, 8260, 131101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ンフフ～\n海の戦士ってカンジ？\nアリよりのアリだね～", 1, 1, 0, 0, 10, 8262, 131101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あーやべ\n一瞬で寝そー\nヤバだわ～", 1, 1, 0, 0, 11, 8263, 131101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "服や紙が易々と\n燃えぬとは\nランドソルとはまこと\n住み易き国で候", 1, 1, 0, 0, 1, 8264, 129701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "どの世界にあっても\n民は活力に溢れ\n街は賑わうもの…\n善哉、善哉", 1, 1, 0, 0, 2, 8265, 129701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "食べ歩きに世界征服…\nギルドとはかくも\n自由気儘な徒党に\nありんすなぁ", 1, 1, 0, 0, 3, 8266, 129701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "連絡は…今日もなし\n我が妹子は相変わらず\n放埓なことよ喃", 1, 1, 0, 0, 4, 8267, 129701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "モネは相変わらず\n警戒しきりなれど…\nそのようなところも\n愛らしくあり申す", 1, 1, 0, 0, 5, 8268, 129701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "なんと長閑な世にや\nありけむ…この平和は\n騎士殿たちの努力の\n賜物にありんす喃", 1, 4, 0, 0, 101, 8269, 129701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "『王家の装備』は\n使わぬのかと？\n…フフ。すでに\n用いており申す", 1, 8, 0, 0, 102, 8270, 129701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今ばかりは媛という\n立場は置いて\nこの地の者らと\n歓談したく候", 1, 1, 0, 0, 1, 8271, 129701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "強さの秘密？　…フフ\n女子の秘密を暴くのは\n野暮にござんす\nご寛恕いただきたく候", 1, 1, 0, 0, 2, 8272, 129701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "此方の家屋、\n見目以上に\n堅牢であり申す\n何ぞ仕掛けがありや？", 1, 1, 0, 0, 3, 8273, 129701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "心地よい時間とは\n疾く過ぎ去りゆくもの\n此処で過ごすと改めて\n実感できる喃", 1, 1, 0, 0, 4, 8274, 129701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "視線が気になる？\nこれはすみませぬ\nなにぶん、お前さまは\n見ていて飽きませぬ故", 1, 4, 0, 0, 101, 8275, 129701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お前さまの前では\n誰もが心を開く…\nなんとまぁ罪作りな\n色男にありんすな？", 1, 8, 0, 0, 102, 8276, 129701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "よく眠れましたかの？\n寝る子は良く育つ、と\n古くより申します由", 1, 1, 1, 0, 1, 8277, 129701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "美食殿の作法に倣い\n皆で共に昼餉を頂くと\n致しんしょう", 1, 1, 2, 0, 2, 8278, 129701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "逢魔ヶ時は望まぬ客も\n参るもの…お前さまも\n気をつけなんし", 1, 1, 3, 0, 3, 8279, 129701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "わっちの蛇に\n勝るとも劣らぬ\n座り心地で候", 1, 1, 0, 0, 2, 8281, 129701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "魂、万物に宿る\nとの云いあらば\n情愛を注いでやるのが\nよかろうの", 1, 1, 0, 0, 3, 8282, 129701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いと香しき哉…\n騎士殿は香の道にも\n詳しくあられるか", 1, 1, 0, 0, 4, 8283, 129701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ジオ・ゲヘナの\n灼熱と比ぶれば\nそよ風と変わり\nござんせん", 1, 1, 0, 0, 5, 8284, 129701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "美味なる哉…この味\nぺコリーヌ姫が好むも\n道理にありんす", 1, 1, 0, 0, 7, 8286, 129701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "トーゴクに、オーマ…\n書で見るだけでも\n心惹かれる土地よの", 1, 1, 0, 0, 8, 8287, 129701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "暑い日には\n冷たいものですよねー\nかき氷とか食べて\nすっきりしたいです★", 1, 1, 0, 0, 1, 8289, 131201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "海への行き来は\nテレポーテーションに\nお任せです★", 1, 1, 0, 0, 2, 8290, 131201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "トロピカルジュースが\n飲みたいでーす♪\n海の家で見たんですよ\nカップル用ジュース★", 1, 1, 0, 0, 3, 8291, 131201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ご飯どうしようかなー\nあ、海鮮ラーメンとか\n美味しそうかも★", 1, 1, 0, 0, 4, 8292, 131201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "水鉄砲、普通に撃つと\n少し物足りないかも…\nどるるるる～ん★って\n感じが欲しいですよね", 1, 1, 0, 0, 5, 8293, 131201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "コイビトっぽいこと\nアオハルっぽいこと\nいっぱいしたいです★\n修羅場とかも♪", 1, 4, 0, 0, 101, 8294, 131201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "騎士さんが溺れたら\n絶対助けに行きますね\n人工呼吸でもなんでも\nしてあげますから★", 1, 8, 0, 0, 102, 8295, 131201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "騎士さーん\n日焼け止めの塗り直し\nお願いします★\n優しくしてくださいね", 1, 1, 0, 0, 1, 8296, 131201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "海で食べたフルーツ？\nはい、まだありますよ\nこっちでもあーんして\n食べさせましょうか★", 1, 1, 0, 0, 2, 8297, 131201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ぺんぽこりんは\nどうしたのかって？\nどうしたんでしょうね\nどーでもいいです★", 1, 1, 0, 0, 3, 8298, 131201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日はどんな\nイイコトしましょうか\n思い出作り？\n人助けもいいですね★", 1, 1, 0, 0, 4, 8299, 131201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ヒモを引っ張ったら…\n「セ・キ・ニ・ン」\n取ってもらいますよ？\nなーんてウソですけど", 1, 4, 0, 0, 101, 8300, 131201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "みんなでイイコトも\n楽しいですけど～\n…二人でイイコト\nしちゃいますか？", 1, 8, 0, 0, 102, 8301, 131201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "朝ですよー！\n今日も一日、\nイイコト日和ですね♪", 1, 1, 1, 0, 1, 8302, 131201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "日差しにご注意ですね\n日焼け止め…誰かに\n塗ってほしいなー？\n…ちらっ★", 1, 1, 2, 0, 2, 8303, 131201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜の海って\n惹かれちゃいますよね\n…ロマンチックです★", 1, 1, 3, 0, 3, 8304, 131201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "う～ん、まったり～\nこれぞ新ジャンル\nおうちバカンス★", 1, 1, 0, 0, 2, 8306, 131201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "よーしよーし★\nわたしとイイコト\n一緒にしましょうねー", 1, 1, 0, 0, 3, 8307, 131201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい香りですねー\nリラックスタイムに\nちょうどよさそう★", 1, 1, 0, 0, 4, 8308, 131201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "水遊びのあとは\nしっかり体を\n温めないとですよね★", 1, 1, 0, 0, 5, 8309, 131201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "栄養補給もバッチリ★\n遊びもイイコトも\nお腹が減ってたら\nできませんからねー？", 1, 1, 0, 0, 7, 8311, 131201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "今だと海関連の本とか\n興味あるんですよねー\n魚とか海難救助とか★", 1, 1, 0, 0, 8, 8312, 131201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かっこいー★\n名前はー…\nぷんぷくぽんとか？", 1, 1, 0, 0, 10, 8314, 131201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "映えますねー\n写真撮ったら\n素敵な思い出に\nなりそうです★", 1, 1, 0, 0, 11, 8315, 131201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "海でもないのに\nこの姿でいるのは…\nやはり、場違いでは\nありませんか…？", 1, 1, 0, 0, 1, 8316, 131601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "スイカとは割ることと\n見つけたり！\n…ですが目隠しすれば\n見当たらないもの…", 1, 1, 0, 0, 2, 8317, 131601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "まぁ、膝に砂が…\n気がつきませんでした\nまるでわんぱくな\nお子様のよう…ふふっ", 1, 1, 0, 0, 3, 8318, 131601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "家の中でスイカを割る\nわけにはまいりません\nここは潔く包丁で\n切りましょう", 1, 1, 0, 0, 4, 8319, 131601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ミソラ様やネア様の\n大胆さを見習って…\nき、騎士きゅーん…★\n難しいですね…", 1, 1, 0, 0, 5, 8320, 131601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "きゅ、急に暗く…！？\n…失礼しました\n鉢巻きが下がってきた\nだけでした…ううぅ", 1, 4, 0, 0, 101, 8321, 131601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "わたくしに剣の才は\nあるのでしょうか…\nいえ、何事もまずは\nやってみなくては", 1, 8, 0, 0, 102, 8322, 131601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なにか羽織った方が\nよろしいですよね…？\nそのままでいい？\nそれはそれで…その…", 1, 1, 0, 0, 1, 8323, 131601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あの、兄様…？\nスイカの種を飛ばす\n遊びもあるというのは\n…本当なのでしょうか", 1, 1, 0, 0, 2, 8324, 131601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "強くなるための修行は\nまだ始まったばかり\nまずは己の目指すべき\n道を見定めなくては", 1, 1, 0, 0, 3, 8325, 131601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "同じ薄着であっても\n黒い天使の姿より…\n気が楽です", 1, 1, 0, 0, 4, 8326, 131601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "はい、ただいまー！\n…あっ！\n海の家でのお手伝いの\nくせが、つい…", 1, 4, 0, 0, 101, 8327, 131601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "兄様さえよろしければ\nまた一緒に海へ…\nあ…申し訳ありません\n浮ついてしまいました", 1, 8, 0, 0, 102, 8328, 131601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "本日もイイコトをして\n清々しい一日を\n過ごしたいものです\nまさに一日一善ですね", 1, 1, 1, 0, 1, 8329, 131601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼時になると\n海の家の料理を\n思い出してしまいます\n焼きそばにかき氷…♪", 1, 1, 2, 0, 2, 8330, 131601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "兄様…よろしければ\n浜辺を歩きませんか？\nきっと、月が\nきれいでしょうから", 1, 1, 3, 0, 3, 8331, 131601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふわふわ心地よすぎて\n眠くなってしまいます\n水着姿では\nいけません…ふぁ…", 1, 1, 0, 0, 2, 8333, 131601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "なんてよい手触り…\n癖になってしまいます\nふふ、よしよし…♪", 1, 1, 0, 0, 3, 8334, 131601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "爽やかな香り…♪\n日焼け止めクリームを\n思い出してしまいます", 1, 1, 0, 0, 4, 8335, 131601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "冷たい水で冷えた体が\n温まります…♪\nまるで兄様のようです", 1, 1, 0, 0, 5, 8336, 131601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お腹が空いては\n修行ができません…！\n動きに障りない程度に\n栄養の補給を…！", 1, 1, 0, 0, 7, 8338, 131601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "剣術とスイカ割りが\n上達する指南書…\n見つかりませんね…", 1, 1, 0, 0, 8, 8339, 131601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "さあ、ともに大海原を\n参りましょう！\nなんて…ふふっ♪", 1, 1, 0, 0, 10, 8341, 131601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "まるでおとぎ話の\n登場人物になった\nような心持ちです…♪", 1, 1, 0, 0, 11, 8342, 131601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あたしはなんで水着で\n室内をうろついてるの\nかしら…不思議だわ", 1, 1, 0, 0, 1, 8343, 131701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あ、天井が汚れてる\n仕方ないわね\nひとっ飛びしますか", 1, 1, 0, 0, 2, 8344, 131701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "はい、お疲れさま♪\n出迎えの言葉は\nやっぱこれよね♪", 1, 1, 0, 0, 3, 8345, 131701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "そういえばあたしって\n羽があるけど\n泳げるのかしら…", 1, 1, 0, 0, 4, 8346, 131701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あいつのために\n浮き輪も用意したし\n抜かりはないわよ", 1, 1, 0, 0, 5, 8347, 131701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "海までのガイド計画も\nまとまったわね\nふふっ…楽しみだわ", 1, 4, 0, 0, 101, 8348, 131701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この水着、あいつが\n選んでくれたのよね\n…大事にしないと", 1, 8, 0, 0, 102, 8349, 131701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ネビアは最近どう？\n迷惑かけてない？\n…かけてるのね", 1, 1, 0, 0, 1, 8350, 131701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あんた…部屋の片付け\nくらい自分でしなさい\n散らかってたわよ？\n…片付けておいたけど", 1, 1, 0, 0, 2, 8351, 131701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もしかして暇なの？\nだったら記憶でも\n見せましょうか？\n凄く恥ずかしいやつ♪", 1, 1, 0, 0, 3, 8352, 131701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夏ってこんなに\n暑いのね…\n海にばかり行く\n気持ちが分かったわ", 1, 1, 0, 0, 4, 8353, 131701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あんたの水着って\n新しいの買わないの？\nなんならあたしが\n選んであげるわよ♪", 1, 4, 0, 0, 101, 8354, 131701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "気軽に触れ合える\n距離にいる…\nあたしにとって\nとても大事なことよ", 1, 8, 0, 0, 102, 8355, 131701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あらおはよう\n夏の朝は暑いわね\nシャワーでも浴びて\nさっぱりしなさい", 1, 1, 1, 0, 1, 8356, 131701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼は何を食べるの？\nガイド妖精として\n提案しましょうか？", 1, 1, 2, 0, 2, 8357, 131701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜更かしは厳禁よ\n無理やり眠らせて\nあげましょうか？", 1, 1, 3, 0, 3, 8358, 131701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "アメスさまだって\nゴロゴロするときは\nあるのよ～", 1, 1, 0, 0, 2, 8360, 131701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "…ふふっ♪\n何よ、あたしが\nかわいいものを\n愛でちゃだめなの？", 1, 1, 0, 0, 3, 8361, 131701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こういういい香り…\n塔の中じゃなかなか\n楽しめないのよね", 1, 1, 0, 0, 4, 8362, 131701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "水着を着てるのに\n温まるのって\n矛盾してない？\n気にしすぎかしら", 1, 1, 0, 0, 5, 8363, 131701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あんたと行動するなら\n沢山食べられるように\nならなきゃね…！", 1, 1, 0, 0, 7, 8365, 131701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "文字を読むのと\nデータを読むのは\nまったく違うわ", 1, 1, 0, 0, 8, 8366, 131701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "へぇ、面白いじゃない\nこういう経験、昔じゃ\nできなかったわ", 1, 1, 0, 0, 10, 8368, 131701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "面白いクッションね\nところであんた…\nあたしで遊んでない？\n気のせいならいいけど", 1, 1, 0, 0, 11, 8369, 131701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "また新しい悪戯\n思いついちゃった♪\n誰に仕掛けようかしら", 1, 1, 0, 0, 1, 8370, 118601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あ～あ、退屈…\nフィオでもからかって\nこようかしら♪", 1, 1, 0, 0, 2, 8371, 118601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ミソギの悪戯って\nレベル高いのよね…\n負けてらんないわ！", 1, 1, 0, 0, 3, 8372, 118601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "家事ニガテなのよね～\nコッコロたちには\n感謝しないとね", 1, 1, 0, 0, 4, 8373, 118601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "私も魔物料理に\n慣れてきちゃった…\n毒されてるわね…", 1, 1, 0, 0, 5, 8374, 118601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あいつに仕掛ける\n次の悪戯は～…♪\nこれ…ううん…\nやっぱこっちかしら？", 1, 4, 0, 0, 101, 8375, 118601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "結局ギルドハウスが\n一番落ち着くわね\n…私たちの家だもの", 1, 8, 0, 0, 102, 8376, 118601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "悪戯のコツでも\n教えてあげよっか？\n…え～興味ないの？", 1, 1, 0, 0, 1, 8377, 118601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "これからもずーっと\n退屈しない毎日に\nしてあげる♪\n私の悪戯でね♪", 1, 1, 0, 0, 2, 8378, 118601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ちょうど退屈してたの\nたーっぷりと\n構ってもらうわよ♪", 1, 1, 0, 0, 3, 8379, 118601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "案内が必要なら\n頼ってくれていいわよ\n一応は私だって\nガイド妖精だし！", 1, 1, 0, 0, 4, 8380, 118601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "冒険のガイドなら\n私に任せなさい♪\nある意味忘れられない\n旅にしてあげる♪", 1, 4, 0, 0, 101, 8381, 118601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "妖精がいる家は\n栄えるのよ♪　だから\nずっといてあげる\nあんたのそばにね♪", 1, 8, 0, 0, 102, 8382, 118601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "きゃははっ！\n寝ぐせついてるわよ？\nこのままにしましょ！\nだって面白いし！", 1, 1, 1, 0, 1, 8383, 118601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "自由に外へ行けるって\nすっごく幸せなことよ\nさ、散歩行きましょ♪", 1, 1, 2, 0, 2, 8384, 118601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "安心していいわ\n寝てるあんたに\n悪戯しないから\n…あんたにはね♪", 1, 1, 3, 0, 3, 8385, 118601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "悪くはないけど…\nあいつの頭の方が\n座り心地いいかも", 1, 1, 0, 0, 2, 8387, 118601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この子を使った\n悪戯は…\n…止めとこっと\nこんなにかわいいし♪", 1, 1, 0, 0, 3, 8388, 118601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "このギルドハウスに\nピッタリな匂いね♪\n一言で言えば…\nカオスって感じ？", 1, 1, 0, 0, 4, 8389, 118601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "なんていうか…\n心があたたかいわ\n…変な感じ！", 1, 1, 0, 0, 5, 8390, 118601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ガイド妖精にも\n食事の機能があって\nよかったのかも\nしれないわね…もぐ…", 1, 1, 0, 0, 7, 8392, 118601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "じれったい物語ね…\nこんなの霧を使えば\n一瞬で終わるのに！", 1, 1, 0, 0, 8, 8393, 118601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "わる～い子は\nトゥンヌス漁船に\n乗ってもらおうかな～", 1, 1, 0, 0, 1, 8395, 131801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "たまにはファミリーの\nみんなにもお休みを\nあげないとね～☆", 1, 1, 0, 0, 2, 8396, 131801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "海辺に素敵な\nバーがあるんだ～\n彼が大人になったら\n一緒に行きたいな♪", 1, 1, 0, 0, 3, 8397, 131801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "夏の風を肌で\n感じるの好きかも～\nラーゴはどう？", 1, 1, 0, 0, 4, 8398, 131801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "海岸線を\nのんびり散歩もいいね\n砂や貝殻に紛れた\nお宝探しの旅だよ～☆", 1, 1, 0, 0, 5, 8399, 131801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "みんなで集まって\nビーチパーティーでも\n開いちゃおっかな～♪", 1, 4, 0, 0, 101, 8400, 131801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "さて、この水着…\n彼は気に入って\nくれてるかな～？", 1, 8, 0, 0, 102, 8401, 131801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "心ゆくまで夏を\n楽しんじゃお～☆\nほら君も♪", 1, 1, 0, 0, 1, 8402, 131801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "水着似合ってるって？\nふふ♪　うれしいこと\n言ってくれるね～☆", 1, 1, 0, 0, 2, 8403, 131801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "熱中症には\n気を付けないと\nだめだよ～？", 1, 1, 0, 0, 3, 8404, 131801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "海の家ならうちの\nファミリーでやってる\nとこにしよっか☆\n豪遊しちゃお～♪", 1, 1, 0, 0, 4, 8405, 131801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "君と一緒なら\nどんな夏になるのか\n楽しみだね～♪", 1, 4, 0, 0, 101, 8406, 131801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "君といると子供心を\nくすぐられちゃうな～\n大人だって思いっきり\n遊んでいいんだよね♪", 1, 8, 0, 0, 102, 8407, 131801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "さ、今日は何をして\n過ごそっかな～…\nねえ、君は何したい？", 1, 1, 1, 0, 1, 8408, 131801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふふ♪\n波音が心地良い\n時間だね～☆", 1, 1, 2, 0, 2, 8409, 131801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今夜は星空の下で\n乾杯しよっか～☆\n飲み物取ってくるね♪", 1, 1, 3, 0, 3, 8410, 131801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ここ涼しい～♪\nちょっと一休み\nしていこっか☆", 1, 1, 0, 0, 2, 8412, 131801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こういうのも良いけど\nやっぱりラーゴの方が\nかわいいかな～♪", 1, 1, 0, 0, 3, 8413, 131801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "爽やかな香りだね～\nこういう感覚も絵で\n表現してみたいな～", 1, 1, 0, 0, 4, 8414, 131801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "続きを描く前に\n原稿を乾かさないとね\nインクがにじむと\n直すの大変なんだよ～", 1, 1, 0, 0, 5, 8415, 131801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "今日は水着だし…\n食べるのは控えめに\nしておこっかな", 1, 1, 0, 0, 7, 8417, 131801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "話題の作品は\nチェックしないとね～\nあっ、この作家さん\n新刊出すんだ～", 1, 1, 0, 0, 8, 8418, 131801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うちの出店の客引きに\nこういう乗り物系を\n使うのもアリかな～☆", 1, 1, 0, 0, 10, 8420, 131801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これ、背景用の\n参考資料に\n一つ欲しいかも♪", 1, 1, 0, 0, 11, 8421, 131801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここに帰るのも\n久しぶり…あれから\n変わったところは\nあるかしら？", 1, 1, 0, 0, 1, 8422, 181201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "バイトも復帰しなきゃ\nうちのギルドにとって\n食費は重要だもの…！", 1, 1, 0, 0, 2, 8423, 181201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "コッコロマ…っ\n…うぅ…\nこの癖は\n直らないのね…", 1, 1, 0, 0, 3, 8424, 181201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この世界はまだまだ\n知らないことだらけね\n『裏世界』とか…", 1, 1, 0, 0, 4, 8425, 181201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "必ずみんなを\n助け出してみせるわ\n…もちろん、兄さんも", 1, 1, 0, 0, 5, 8426, 181201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "またみんなと\n暮らせるなんて…\n夢じゃ、ないのよね", 1, 4, 0, 0, 101, 8427, 181201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "たくさんの人が\n私に優しくしてくれる\n…だからこそ、私も\n頼れる人になりたいわ", 1, 8, 0, 0, 102, 8428, 181201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "次はランドソルの\n街の外にも行きたいわ\nあの…よかったらまた\n一緒に回ってくれる？", 1, 1, 0, 0, 1, 8429, 181201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "独りぼっちで眠るのは\nやっぱり寂しかったわ\n…みんなで雑魚寝？\nそんなの…絶対やるわ", 1, 1, 0, 0, 2, 8430, 181201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ネビアってば大泣きで\n私を出迎えてくれたの\n…すごく嬉しかったわ", 1, 1, 0, 0, 3, 8431, 181201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この翼、陽に当たると\n本当に綺麗よね…\n自分のだけれど、つい\nずっと見ちゃうもの", 1, 1, 0, 0, 4, 8432, 181201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おにーたんっ♪\n…呼びたくなっただけ\n本当にそれだけよっ？", 1, 4, 0, 0, 101, 8433, 181201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どうしたの？\n…ふふっ、安心して\nこれからもずっと\n私はここにいるわ", 1, 8, 0, 0, 102, 8434, 181201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "みんな起きだす時間ね\n街が活気づいてく感じ\n嫌いじゃないわ", 1, 1, 1, 0, 1, 8435, 181201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "【美食殿】のお昼は\nいつも美味しいから\nまた太っちゃいそうね", 1, 1, 2, 0, 2, 8436, 181201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おやすみなさい\n明日も、その先も\n一緒に冒険しましょう", 1, 1, 3, 0, 3, 8437, 181201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "休憩も大事…よね\nじゃあ遠慮なく…\nふぅ～…♪", 1, 1, 0, 0, 2, 8439, 181201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はぁ…ふわふわ…♪\nこのかわいさには\nもう抗えないわ…", 1, 1, 0, 0, 3, 8440, 181201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "落ち着く香り…\nこの匂いを嗅いでると\n帰ってきたなって\n思うわね", 1, 1, 0, 0, 4, 8441, 181201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "みんなで野宿した時も\n焚火の明かりを見て\nすごく安心したのよね", 1, 1, 0, 0, 5, 8442, 181201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ペコリーヌさんには\n負けるけど…\n美味しくって手が\n止まらないわ…！", 1, 1, 0, 0, 7, 8444, 181201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "プリンセスフォーム…\n一体どういう仕組みで\n発動するのか、文献に\n書いてないかしら…", 1, 1, 0, 0, 8, 8445, 181201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "すごーく暑いけど、\nこの暑さをどうやって\n乗り越えるかって\n考えるのも楽しいかも", 1, 1, 0, 0, 1, 8447, 131901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "氷で彫刻を作るの？\nそれは楽しそうだけど\nすぐに溶けちゃわ\nないかしら？", 1, 1, 0, 0, 2, 8448, 131901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お日様の下で遊ぶのも\n部屋の中で涼むのも\nどっちも夏ならではの\n楽しみ方なのね", 1, 1, 0, 0, 3, 8449, 131901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "生き物の観察日記、\n私も書いてみたいな\nパックのお昼寝日記\nなんてどうかしら？", 1, 1, 0, 0, 4, 8450, 131901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "さまぁばけぇしょんは\n計画を立てるもの\nなんだって\n私も挑戦してみるわね", 1, 1, 0, 0, 5, 8451, 131901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この世界のことを\nもっと知って\nアーラム村のみんなに\nお話ししてあげるの", 1, 4, 0, 0, 101, 8452, 131901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "いつルグニカに帰るか\nわからないからこそ\n毎日頑張らなきゃね", 1, 8, 0, 0, 102, 8453, 131901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日はどこに行くの？\n私は、えーと…\nかき氷が食べたいわ！", 1, 1, 0, 0, 1, 8454, 131901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ここにも精霊が\n住んでたりするの？\n…精霊じゃなくて\nがいど妖精？", 1, 1, 0, 0, 2, 8455, 131901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "パックったら、\n近所の子にいっぱい\nなでなでされてたわ\nすごーく人気者ね！", 1, 1, 0, 0, 3, 8456, 131901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "実は…線香花火の\n特訓をしてたの！\n次は負けないんだから", 1, 1, 0, 0, 4, 8457, 131901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "昨日、みんなの\n似顔絵も絵日記に\n描いてみたんだけど…\n笑っちゃダメよ？", 1, 4, 0, 0, 101, 8458, 131901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "何気ない時間だって\n後で思い返せば\nずっと忘れない\n素敵な思い出になるの", 1, 8, 0, 0, 102, 8459, 131901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "気持ちよく起きて、\nいい一日を過ごしたい\nそんなときには\nラジーオタイソーね！", 1, 1, 1, 0, 1, 8460, 131901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼ごはんの時間ね\n暑さに負けないように\nしっかり食べましょう", 1, 1, 2, 0, 2, 8461, 131901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "外は涼しいみたいだし\n夜の散歩に行ってみる\nのはどうかしら？", 1, 1, 3, 0, 3, 8462, 131901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "遊び疲れたら\nここでひとやすみ…\nふわぁ…\n眠くなっちゃった…", 1, 1, 0, 0, 2, 8464, 131901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "細かいところまで\nすごーく丁寧に\n作られてる…", 1, 1, 0, 0, 3, 8465, 131901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "わっ、なんだか\nすーっとする香りね\nちょっぴり涼しく\nなったみたい", 1, 1, 0, 0, 4, 8466, 131901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "海で遊ぶと\n体が冷えるから\nあったかいのも\nすごーく嬉しい…", 1, 1, 0, 0, 5, 8467, 131901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これも美味しい…！\nペコリーヌが\nどんどん食べちゃう\n気持ちが分かるかも", 1, 1, 0, 0, 7, 8469, 131901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "このお話も面白い…！\nあっという間に時間が\n過ぎちゃったわ…\n…ね、寝てません！", 1, 1, 0, 0, 8, 8470, 131901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この子と一緒に海に\nいったら楽しそうね！\nどこに行けば\n会えるのかしら？", 1, 1, 0, 0, 10, 8472, 131901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふかふかね！\nこうしてると波の音が\n聞こえてきそう…", 1, 1, 0, 0, 11, 8473, 131901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "こちらでは水着姿で\n過ごすのも\n珍しくないようですね", 1, 1, 0, 0, 1, 8474, 132101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "メイド服ではなく\n姉様と色違いの水着…\nレムは嬉しいです", 1, 1, 0, 0, 2, 8475, 132101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "少し室温が高いかも\nしれませんね…\nいくつか氷を配置して\nおきましょうか", 1, 1, 0, 0, 3, 8476, 132101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "清涼感があるとはいえ\n鉄球をビーチバレーの\nボールに代用するのは\n危ないでしょうか…", 1, 1, 0, 0, 4, 8477, 132101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ふぅー…ふぅー…\nようやく浮輪が\n膨らみました\n姉様にお渡ししないと", 1, 1, 0, 0, 5, 8478, 132101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "海のことをもっと\n教えていただけますか\n…スバルくんと海の\n話をしたいんです", 1, 4, 0, 0, 101, 8479, 132101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "もし海に行く機会が\nあればレムにも…\nお声がけくださいね\nぜひ、お願いします", 1, 8, 0, 0, 102, 8480, 132101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "何を着ていても\nメイドはメイド\n掃除も料理も\nなんでもござれです", 1, 1, 0, 0, 1, 8481, 132101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "スバルくんとあなたは\n兄弟のように\n仲がいいですね\n姉様とレムと一緒です", 1, 1, 0, 0, 2, 8482, 132101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この姿で子どもたちと\n遊ぶのは…さすがに\nはばかられますね\nやはり着替えてきます", 1, 1, 0, 0, 3, 8483, 132101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ナンパ…というものが\n海にはあるそうです\nスバルくんがとても\n警戒していました", 1, 1, 0, 0, 4, 8484, 132101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "魚釣りでしたら\nレムもご一緒します\n氷漬けの魚の備蓄を\n補充したかったので", 1, 4, 0, 0, 101, 8485, 132101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "子どもたちと砂遊びの\n約束をしているんです\nあなたもご一緒に\nいかがですか？", 1, 8, 0, 0, 102, 8486, 132101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ラジーオタイソーは\nもうされましたか？\n体がほぐれますよ", 1, 1, 1, 0, 1, 8487, 132101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼ですね。では\n姉様に焼きそばを…\nもちろん皆様の分も\n用意してありますよ", 1, 1, 2, 0, 2, 8488, 132101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "熱帯夜であっても\nお休みの際はお腹を\n冷やさないように\n気をつけてくださいね", 1, 1, 3, 0, 3, 8489, 132101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "とても気持ちよさそう\nです…！\nさっそく姉様に\nお教えしないと", 1, 1, 0, 0, 2, 8491, 132101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "季節に合わせた\n着せ替えができれば\n子どもたちも\n楽しめそうです", 1, 1, 0, 0, 3, 8492, 132101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふむふむ…この香りは\n疲れを癒やしてくれ\nますね。エミリア様に\nお持ちします", 1, 1, 0, 0, 4, 8493, 132101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "身も心もほぐれます\n思っていたよりも\n体が冷えていたみたい\nですね…ふう…", 1, 1, 0, 0, 5, 8494, 132101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もぐもぐ…んうっ！？\nこのすっぱさは…\n梅干し、ですか…\nくせになりますね…！", 1, 1, 0, 0, 7, 8496, 132101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "海に関する詩文なども\nあるのでしょうか…\n気になります", 1, 1, 0, 0, 8, 8497, 132101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "地竜とは違った\n乗り心地です…\n本物にも乗ってみたい\nものですね", 1, 1, 0, 0, 10, 8499, 132101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この大きさで\n本物の貝であれば…\nどんな料理にするか\n想像が膨らみます", 1, 1, 0, 0, 11, 8500, 132101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "これがこの世界の\n居住環境か…\n面白い構造だね", 1, 1, 0, 0, 1, 8501, 132001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "暇を持て余していてね\n誰か話し相手に\nなってくれないかな", 1, 1, 0, 0, 2, 8502, 132001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "目新しいものが\nたくさんあるね\n胸が躍るよ", 1, 1, 0, 0, 3, 8503, 132001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "頑丈そうな建物だ\nここなら彼女たちが\n暴れても大丈夫かな？", 1, 1, 0, 0, 4, 8504, 132001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "力が制限されていて\n良かった面もある\n君も吐かずにすんだし", 1, 1, 0, 0, 5, 8505, 132001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "悪さなんてするわけ\nないじゃないか\n傷つくなぁ", 1, 4, 0, 0, 101, 8506, 132001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "普段と違う格好で\n歩き回るのも\n悪くない経験だ", 1, 8, 0, 0, 102, 8507, 132001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ボクに聞きたいことが\nあるなら遠慮なく\n質問するといい", 1, 1, 0, 0, 1, 8508, 132001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "気軽に魔女に\n話しかけるなんて\n君は物好きだね", 1, 1, 0, 0, 2, 8509, 132001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この世界においては\nボクは異物だからね\n大人しくしているさ", 1, 1, 0, 0, 3, 8510, 132001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ボクの反応を\n見たいのかい？\n君の好奇心には\n恐れ入るよ", 1, 1, 0, 0, 4, 8511, 132001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ボクの手を借りたい？\nもちろん構わないさ\n対価はいただくけどね", 1, 4, 0, 0, 101, 8512, 132001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ボクと海を見たい？\nふふ、魅力的な\nお誘いだね", 1, 8, 0, 0, 102, 8513, 132001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お目覚めかな？\nさて、君の見た夢を\n教えてくれないかな？", 1, 1, 1, 0, 1, 8514, 132001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "食後のお茶を\n一緒にどうだろうか？\nさあ、席に座ってよ", 1, 1, 2, 0, 2, 8515, 132001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おや、お休みかな？\n魔女の前で無防備だね\n…無論、何もしないさ", 1, 1, 3, 0, 3, 8516, 132001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こういう椅子は\n茶会には向かないね\nつい気が緩んでしまう", 1, 1, 0, 0, 2, 8518, 132001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こういうものを好きな\n女の子がいいのかな？\n君の嗜好を知りたいな", 1, 1, 0, 0, 3, 8519, 132001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい香りだ\nこれにはどんな\n効能があるんだろう？", 1, 1, 0, 0, 4, 8520, 132001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "火の点け方に\nボクのいた世界との\n違いはないのかな？", 1, 1, 0, 0, 5, 8521, 132001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これもまた未知の味だ\n存分に味わわせて\nもらうとしよう", 1, 1, 0, 0, 7, 8523, 132001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "素晴らしいね\nここには叡智が\n詰まっている", 1, 1, 0, 0, 8, 8524, 132001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "魔物を模倣した\nぬいぐるみか…\n実物を見たくなるね", 1, 1, 0, 0, 10, 8526, 132001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい気持ちだ…ボクと\nしたことが、何も考え\nられなくなりそうだ", 1, 1, 0, 0, 11, 8527, 132001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "願わずにいられません\nどうかこちらの\n世界には…悲劇が\n生まれませんようにと", 1, 12, 0, 0, 103, 8528, 109701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "食事のご希望があれば\nなんなりと。お腹が\nいっぱいになるまで\n沢山ご用意しますね", 1, 12, 0, 0, 103, 8529, 109701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あの男、これからも\n観察が必要ね\nラムの目の届く範囲に\n置く必要があるわ", 1, 12, 0, 0, 103, 8530, 109801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "素直なところは\nあの男の美点ね\n癪だけど、偶然にも\nラムと同じだわ", 1, 12, 0, 0, 103, 8531, 109801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "迷惑かけちゃったけど\n雪遊びは楽しかったわ\nまたやりたい…なんて\n言ったら怒られるかも", 1, 12, 0, 0, 103, 8532, 109901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "数えきれないくらいの\n思い出を、みんなで\n作りましょう！", 1, 12, 0, 0, 103, 8533, 109901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "わわっ、今すごく\nいい香りがしたけど…\n誰の香水かな\n教えてほしいな…♪", 1, 1, 0, 0, 1, 8534, 132201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "錬金術用の釜、\n置くならどこかな？\n自分専用の錬金釜、\n憧れちゃうよね♪", 1, 1, 0, 0, 2, 8535, 132201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この匂い…誰か\n唐揚げ食べたみたい\nうぅ…お腹空いて\nきちゃうよ", 1, 1, 0, 0, 3, 8536, 132201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "次の新曲のタイトルは\n『恋の錬金術』！\nうーん、ちょっと\nベタ過ぎるかな…", 1, 1, 0, 0, 4, 8537, 132201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あ、錬金の素材\n切らしてたんだ…\n彼を誘って採集に\n行ってこようかな", 1, 1, 0, 0, 5, 8538, 132201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "瞬時に着替えができる\n薬とかできないかな？\n舞台で衣装チェンジ！\nなーんて、楽しそう♪", 1, 4, 0, 0, 101, 8539, 132201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "彼が遊びに来た後は\n部屋の空気がほんのり\n彼の匂いなんだ…\nなんだか幸せだな♪", 1, 8, 0, 0, 102, 8540, 132201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日もバイト？\nそれならこれ飲んで\n私のお手製ポーション\nがんばってきてね！", 1, 1, 0, 0, 1, 8541, 132201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "新しくできたカレー屋\n行ってみない？\n時々スパイスの香りが\n流れてくるんだよ", 1, 1, 0, 0, 2, 8542, 132201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私のフィギュア\nそっくりでしょ！\nルイちゃんが作って\nくれたんだ", 1, 1, 0, 0, 3, 8543, 132201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あれ、シャンプー\n変えた？　私と同じの\nだよ！　どうしよう\nドキドキしちゃう…", 1, 1, 0, 0, 4, 8544, 132201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "いらっしゃいませ♪\n何を作りますか？\nなーんてね。錬金堂で\nまた一日店長したいな", 1, 4, 0, 0, 101, 8545, 132201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "クンクン…彼の\nハンカチ…いい匂い♪\n私だけの秘密のお守り\nにしてるんだ", 1, 8, 0, 0, 102, 8546, 132201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ねえねえ、天気も\nいいし一緒に錬金の\n素材集めに行こうよ！", 1, 1, 1, 0, 1, 8547, 132201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼ご飯、用意したよ\nあはは、さすがに\nランチは錬成しないよ\n私の手作りだよ♪", 1, 1, 2, 0, 2, 8548, 132201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日も一日お疲れさま\nこれ飲んでゆっくり\n休んで。薬じゃないよ\nホットミルクだよ？", 1, 1, 3, 0, 3, 8549, 132201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "昨日、遅くまで\n錬金術の勉強してた\nから眠くって…\nちょっと一休みっと", 1, 1, 0, 0, 2, 8551, 132201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この子にちょっとだけ\n香水かけて、と…\nんー！　だっこした時\nすっごくいい匂い♪", 1, 1, 0, 0, 3, 8552, 132201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "花みたいな、\nお菓子みたいな\nいい香り。気持ちが\n穏やかになるね♪", 1, 1, 0, 0, 4, 8553, 132201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "外でもこの暖かさを\n感じられる道具、\n作れたりしないかな？", 1, 1, 0, 0, 5, 8554, 132201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "素材集めに行くと\nお腹が空くんだよね\nイロハちゃんも\nこの味好きそう", 1, 1, 0, 0, 7, 8556, 132201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "錬金術にはたくさんの\n知識が必要だからね\nさあ、今日も調合書を\n読まなくちゃ", 1, 1, 0, 0, 8, 8557, 132201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここが騎士さんたちの\n住んでる場所…！\n粗相がないように\n気をつけなきゃ", 1, 1, 0, 0, 1, 8559, 127701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ジオ・テオゴニアの\nみんな、私がいない間\nちゃんとお仕事\nしてればいいけど…", 1, 1, 0, 0, 2, 8560, 127701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "コッコロさんも\n従者として長いって\n聞いたような…\nあの年ですごい…！", 1, 1, 0, 0, 3, 8561, 127701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "自分がもてなされる\n側になるのって\nやっぱりちょっと\n落ち着かないかも…", 1, 1, 0, 0, 4, 8562, 127701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今度のお休みの日は\n何しよう…？\nあっ、最近できたお店\nヴルムと行こうかな？", 1, 1, 0, 0, 5, 8563, 127701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お買い物したら\nかわいいからって\nたくさんおまけして\nもらっちゃった…♪", 1, 4, 0, 0, 101, 8564, 127701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここにいると、つい\nぼんやりしちゃう…\nこれも騎士さんの力…\nなのかな", 1, 8, 0, 0, 102, 8565, 127701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お手伝いできることは\nありませんか？\nお世話でもなんでも\n任せてくださいっ", 1, 1, 0, 0, 1, 8566, 127701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ランドソルに初めて\n来たときは驚きました\n砂糖の山と思ったら…\nあんなに寒いなんて！", 1, 1, 0, 0, 2, 8567, 127701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あの、ヴルムを\n見ませんでしたか？\nもう…あの子ってば\nまた仕事をサボって…", 1, 1, 0, 0, 3, 8568, 127701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "家具や家が壊れても\n食べられないなんて\nなんだかもったいない\nですね", 1, 1, 0, 0, 4, 8569, 127701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "とっておきの紅茶を\n手に入れたんです\n一杯いかがですか？\nその、日頃のお礼です", 1, 4, 0, 0, 101, 8570, 127701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "頑張ってて偉いって…\nほ、褒めてもなんにも\n出ませんからね！？\n…えへへ♪", 1, 8, 0, 0, 102, 8571, 127701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようございます！\nモーニングティーは\nいかがですか？\n私、準備しますねっ", 1, 1, 1, 0, 1, 8572, 127701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ライラエルさまも\nお昼寝はお好きです\nけど…だからって\n寝すぎはダメですよ！", 1, 1, 2, 0, 2, 8573, 127701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おやすみなさい\nくれぐれも寝不足には\n気をつけてくださいね", 1, 1, 3, 0, 3, 8574, 127701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "しっかりしないと…\nメイドの私が\n気を抜くなんて…\nそんな…ふぅ…", 1, 1, 0, 0, 2, 8576, 127701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "わぁ、かわいい…♪\nってダメダメ！\nメイドなんだから\n落ち着かないと…！", 1, 1, 0, 0, 3, 8577, 127701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい匂い…♪\nこの香り、\nライラエルさまも\n好きそうです！", 1, 1, 0, 0, 4, 8578, 127701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ランドソルの環境に\n触れると、この光の\nありがたみが\n身に沁みますね…", 1, 1, 0, 0, 5, 8579, 127701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "甘くない食べ物が\n一般的なのも\n慣れてきました\nあむ…おいひぃ…♪", 1, 1, 0, 0, 7, 8581, 127701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ランドソルの文字、\n私たちのところと\nちょっと違いますね…\n覚えられるかなぁ", 1, 1, 0, 0, 8, 8582, 127701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "甘い匂いがしない…\nジオ・テオゴニアとは\n別の世界なんですね", 1, 1, 0, 0, 1, 8584, 127801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "掃除めんど…\nん…このくらいで\nまー十分でしょ", 1, 1, 0, 0, 2, 8585, 127801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "そろそろ食事の\n用意もしなきゃ…\nあーやる事多い…", 1, 1, 0, 0, 3, 8586, 127801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "秘密の隠れ場所、\nまた姉さんにバレた…\n次のを探さないと", 1, 1, 0, 0, 4, 8587, 127801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "知らない間に仕事が\n終わったり…しないか\nしてくれたらいいのに", 1, 1, 0, 0, 5, 8588, 127801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "きちんともてなすには\n騎士さんの好みも\n覚えてかないと…", 1, 4, 0, 0, 101, 8589, 127801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ご主人サマに\nご奉仕します～♪\n…キャラじゃないか\nやめやめ、恥ずかし…", 1, 8, 0, 0, 102, 8590, 127801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "居心地いいっすね…\n今日からここに\n住んでもいいですか", 1, 1, 0, 0, 1, 8591, 127801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あの、ちょっと姉さん\n怒らせちゃったんで\n匿ってくれません？", 1, 1, 0, 0, 2, 8592, 127801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "羽と尻尾が格好いい？\n意外と邪魔ですよ\n仰向けで寝にくいし", 1, 1, 0, 0, 3, 8593, 127801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ワークライフバランス\nっていい言葉ですね\nぜひライフ多めで", 1, 1, 0, 0, 4, 8594, 127801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "騎士さんって\n働き者ですね\nそのままあたしの\n仕事も…いえなんでも", 1, 4, 0, 0, 101, 8595, 127801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "熱心に見てても\n尻尾は触らせて\nあげませんってば\n角？　角なら…まぁ", 1, 8, 0, 0, 102, 8596, 127801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "寝ても寝ても\n寝足りない…ふぁ…\nあー…眠たい…", 1, 1, 1, 0, 1, 8597, 127801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "アフタヌーンティーの\n準備は得意ですよ\nつまみ食いとか\n…つまみ食いとか", 1, 1, 2, 0, 2, 8598, 127801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "よく休んでください\n布団は干しといたんで\n…姉さんが", 1, 1, 3, 0, 3, 8599, 127801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふー…\nこのままずーっと\nここに居たい…", 1, 1, 0, 0, 2, 8601, 127801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あんたも一緒に\nサボっちゃうか～\n…なんて", 1, 1, 0, 0, 3, 8602, 127801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "落ち着く匂い…\n騎士さんの好み\nなんですかね", 1, 1, 0, 0, 4, 8603, 127801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こういうとこの\n掃除って面倒だし…\n今度でいっか", 1, 1, 0, 0, 5, 8604, 127801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ご飯の前に\nこんな食べたら\n姉さんきっと怒るな…", 1, 1, 0, 0, 7, 8606, 127801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "食に関する本が\nやけに多い…\nお国柄ですかね", 1, 1, 0, 0, 8, 8607, 127801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "すんすん…\nお兄さんの匂いが\nぷんぷんする～♡\nザコくっさぁ♡", 1, 1, 0, 0, 1, 8609, 130201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ぴこーん！\nいひひっ、新作メカの\n面白いアイデア、\n思いついちった～♡", 1, 1, 0, 0, 2, 8610, 130201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "メカもいじりたいし\n服もアクセもほしー…\nあ～、おこづかい\n足んないよ～！", 1, 1, 0, 0, 3, 8611, 130201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お勉強でもしよっかな\nザコお兄さんには\n絶対わかんない\nよーなやつ♡", 1, 1, 0, 0, 4, 8612, 130201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お兄さんの部屋って\nどこどこぉ？\nメカの実験室に\n使えそうだよね～♡", 1, 1, 0, 0, 5, 8613, 130201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "次のデートコースは\nどんなのかなー？\n…いや、むしろ次は\nぼくが決めちゃうっ？", 1, 4, 0, 0, 101, 8614, 130201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "強奪計画は念入りに♡\nお兄さんのハートも\n全力でザコザコにする\n計画、立てないとね♡", 1, 8, 0, 0, 102, 8615, 130201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ねー、ヒマヒマヒマ～\nかまってかまってー♡\nどっか連れてけー♡", 1, 1, 0, 0, 1, 8616, 130201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日はこっちで\nご飯食べたいなー♡\n…帰っても５日連続の\nバカナ姉カレーだし…", 1, 1, 0, 0, 2, 8617, 130201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "依頼行くならぼくも\n連れてってよー♡\nお兄さんのザコっぷり\n見ててあげる～♡", 1, 1, 0, 0, 3, 8618, 130201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お兄さんはぼくより\nオトナだもんね～♡\nじゃあオモテナシ、\n期待してるから～♡", 1, 1, 0, 0, 4, 8619, 130201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ヤマ姉になんかお土産\n持って帰ろうかなー？\nザコお兄さん、選ぶの\n付きあって～♡", 1, 4, 0, 0, 101, 8620, 130201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "遊びにいくときは\nザコお兄さんも\n誘ってあげるよ♡\nどうせヒマでしょ～？", 1, 8, 0, 0, 102, 8621, 130201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふぁ～、ねむぅ～…\n夜更かしぶん寝るから\nあとで起こして～♡\nあ、変なこと禁止～♡", 1, 1, 1, 0, 1, 8622, 130201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おなかすいたぁ～\nなんか食べ行こ♡\n甘いのおごって～♡", 1, 1, 2, 0, 2, 8623, 130201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "えー、もう寝る時間？\nじゃあ…添い寝して\nあげよっか？\nなーんて、ウッソー♡", 1, 1, 3, 0, 3, 8624, 130201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "おお～ふわっふわ～♪\nバンディシャーク号の\n座席に使いたーい！", 1, 1, 0, 0, 2, 8626, 130201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "カワイイ～♡\n改造したらもっと\nカワイくなるかな～？", 1, 1, 0, 0, 3, 8627, 130201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "くんくん…いい匂い♡\nザコお兄さんって\nこーゆー香りが\n好きなのかなぁ～", 1, 1, 0, 0, 4, 8628, 130201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はー、あったかーい♡\nバンディシャーク号\nにも欲しいかもー♪", 1, 1, 0, 0, 5, 8629, 130201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "食べれるときに\n食べるのが大事って\nヤマ姉言ってたしね～", 1, 1, 0, 0, 7, 8631, 130201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あ、この本読んだよ♡\n参考文献リストが\nしっかりしてて\n勉強になったなー♪", 1, 1, 0, 0, 8, 8632, 130201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "部屋は多少ゴチャつい\nてる方が落ち着くのさ\nバンディシャーク号\nみたいにね♪", 1, 1, 0, 0, 1, 8634, 130001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "呆けてるのもいいが\nちいと物足りないねぇ\n麦しゅわもブドウ朱も\n無いなんてさ", 1, 1, 0, 0, 2, 8635, 130001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この家セキュリティが\nなってないねぇ…\n盗賊目線でちょっくら\n指南でもしてやるか", 1, 1, 0, 0, 3, 8636, 130001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "不思議だねぇ\nここで飲むと、酔いが\n回るのが早い気が\nするよ…ヒック", 1, 1, 0, 0, 4, 8637, 130001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ふむ…\nクルマが壊れた時は\nアジト代わりに\n使えそうだ♪", 1, 1, 0, 0, 5, 8638, 130001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "カカッ、いい部屋だ！\n妹を迎える準備は\nできてるみたいだねぇ", 1, 4, 0, 0, 101, 8639, 130001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "…っと、油断したら\n落ち着いちまうね\nボーヤの甘っちょろい\n雰囲気のせいだよ", 1, 8, 0, 0, 102, 8640, 130001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "さすがにこの家から\n盗みはしないさ\nいずれは妹たちの\n共有財産になるからね", 1, 1, 0, 0, 1, 8641, 130001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ボーヤ、暇なら\n博打でもやるかい？\nアタシが負けたら\n掃除でもしてやるよ", 1, 1, 0, 0, 2, 8642, 130001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "立派な家じゃないか！\n一国一城の主とは\nちょっとボーヤのこと\n見くびってたかもねぇ", 1, 1, 0, 0, 3, 8643, 130001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "かわいい義弟のためだ\nアタシが盗ってきた\nお宝をくれてやるよ\n好きな場所に飾りな！", 1, 1, 0, 0, 4, 8644, 130001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "妹と結ばれる前に\n他の女との関係は\n清算しとくように\nわかったね、ボーヤ？", 1, 4, 0, 0, 101, 8645, 130001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お、ヤシオーリかい？\nフフン、義弟からの\nお酌とあっちゃあ\n断れないねぇ♪", 1, 8, 0, 0, 102, 8646, 130001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "うう…清々しい朝だが\n頭がいてぇ…\nちっと昨日は\n飲みすぎたか…？", 1, 1, 1, 0, 1, 8647, 130001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ほらこれ、ワカナに\n作らせた弁当だ\nこれで昼からも\nバリバリやりな！", 1, 1, 2, 0, 2, 8648, 130001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "まだまだ夜はこれから\n…と言いたいとこだが\n早寝して立派に育ちな\nそれが妹たちの為だよ", 1, 1, 3, 0, 3, 8649, 130001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "中々いい座り心地だが\n盗賊が腑抜ける訳には\nいかないね…が…\nもう少しだけ座るか…", 1, 1, 0, 0, 2, 8651, 130001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こういうのの中に\n実はレアなブツが\n隠されてるとか\nありがちだねぇ", 1, 1, 0, 0, 3, 8652, 130001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふうん、ボーヤは\nこういう匂いが\n好きなのかい？\n妹たちに教えてやろう", 1, 1, 0, 0, 4, 8653, 130001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "体を温めんにゃ\n酵露をカッとやるのが\n一番だが、これも存外\n悪くないねぇ…♪", 1, 1, 0, 0, 5, 8654, 130001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ガツガツ…\nん、悪くねェ味だ\nこれにヤシオーリでも\nありゃ文句はないね", 1, 1, 0, 0, 7, 8656, 130001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "本にも載ってない\nレアもんのお宝か…\nフッ、想像しただけで\n血沸き肉躍るねぇ…！", 1, 1, 0, 0, 8, 8657, 130001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今日の買い物は\nいつものお野菜と…\nあっ、確か特売が\nあったはず…！", 1, 1, 0, 0, 1, 8659, 130101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お掃除、お掃除…♪\nきゅっきゅっ…\nよいしょ、っと…", 1, 1, 0, 0, 2, 8660, 130101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "洗濯ものも\nたまらないうちに\nやっちゃわないと…", 1, 1, 0, 0, 3, 8661, 130101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "夕飯のメニュー…\n何にしよう？\n期限の早い食材は…", 1, 1, 0, 0, 4, 8662, 130101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "色々お菓子も\n作ってみたいけど…\n予算…足りるかなぁ…", 1, 1, 0, 0, 5, 8663, 130101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "騎士さん…どんな\n料理が好きなんだろ？\n前に作ったコロッケは\n評判よかったけど…", 1, 4, 0, 0, 101, 8664, 130101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "騎士さんと\nおしゃべりすると、\n胸があたたかくなる…\nなんでだろ…？", 1, 8, 0, 0, 102, 8665, 130101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あっ、騎士さん\nどうかしましたか？\n御用ならいつでも…♪", 1, 1, 0, 0, 1, 8666, 130101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "削っても削っても\n家計簿は赤字…\n騎士さ～ん…\n助けてください～！", 1, 1, 0, 0, 2, 8667, 130101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "手が空いてたら\n日用品の買い出し、\n手伝ってもらっても\nいいですか…？", 1, 1, 0, 0, 3, 8668, 130101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "やっと一息つけそう…\nあっ、わたし\nお茶を淹れてきますね", 1, 1, 0, 0, 4, 8669, 130101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "困りごとがあったら\n言ってくださいね\nわたし…騎士さんの\n力になりたいです", 1, 4, 0, 0, 101, 8670, 130101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "助けてもらった分は\n必ず返しますから…\nそれまでわたしのこと\n見ててくださいね…♪", 1, 8, 0, 0, 102, 8671, 130101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "忘れ物はないですか？\n今日のお仕事も\n頑張りましょうね…！", 1, 1, 1, 0, 1, 8672, 130101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼ご飯できてますよ\n大したものじゃなくて\n恐縮ですけど…", 1, 1, 2, 0, 2, 8673, 130101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜更かしはだめですよ\nわたしも家事を\n終わらせたら寝ないと", 1, 1, 3, 0, 3, 8674, 130101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これ…割れ物防止の\nクッションに\n使えるかも…？", 1, 1, 0, 0, 2, 8676, 130101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こういうのいいなぁ…\nけど…フブキちゃんに\n絶対改造されちゃう…", 1, 1, 0, 0, 3, 8677, 130101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい匂い…でも\n切れかけてるかも？\nえっと補充するには…", 1, 1, 0, 0, 4, 8678, 130101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったかい…\nちょっとだけなら\n休んでも…いいよね", 1, 1, 0, 0, 5, 8679, 130101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これがあったら\nもうお料理を\n作らなくて済むかも？", 1, 1, 0, 0, 7, 8681, 130101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "新しい節約レシピは…\nあ、この草って\n食べられるんだ…", 1, 1, 0, 0, 8, 8682, 130101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "愛溢れるランドソルの\nクリスマス…ですか\nいけませんね、つい\n胸が躍ってしまいます", 1, 1, 0, 0, 1, 8684, 132801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ツリーの飾りと\nわたしの髪…\nくるくるしていて\n似ているかも…くすっ", 1, 1, 0, 0, 2, 8685, 132801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この姿で傲慢な\nわたしになったら…\nいつもより怖がらせて\nしまうでしょうね…", 1, 1, 0, 0, 3, 8686, 132801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "悪魔として、人を\nドキドキさせなくては\nい、イオから教わった\n…投げキスの練習を…", 1, 1, 0, 0, 4, 8687, 132801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ジオ・テオゴニアの\nお菓子は、ツリーの\n飾りに向いていそう\nですね…ふふっ", 1, 1, 0, 0, 5, 8688, 132801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "騎士のギルドハウス…\nクリスマスの時期も\n暖かいのですね…", 1, 4, 0, 0, 101, 8689, 132801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "城の皆と過ごす宴も\n楽しいですが…\n騎士と二人の宴も\n幸せです…", 1, 8, 0, 0, 102, 8690, 132801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "騎士、あなたの\nもてなしに感謝します\nクリスマスの何たるか\n教えてくださいね", 1, 1, 0, 0, 1, 8691, 132801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この黒い羽は\nどう手入れすれば？\nイオなら知っている\nでしょうか…", 1, 1, 0, 0, 2, 8692, 132801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "城の皆も連れてきたい\nほど、クリスマスは\n楽しい行事ですね\nお土産も迷います", 1, 1, 0, 0, 3, 8693, 132801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "クリスマスの晩餐に\n招待されたのですが…\nその…辛いものは\nありませんよね…？", 1, 1, 0, 0, 4, 8694, 132801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "クリスマスと言わず…\nいつでも星に祈って\nいますよ？　騎士達に\n幸せがあるように、と", 1, 4, 0, 0, 101, 8695, 132801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "悪魔として騎士を\nドキドキさせる…？\nそ、そのようなこと\nわたしには…うぅ…", 1, 8, 0, 0, 102, 8696, 132801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "朝は寒いですね…\n雪…積もっている\nでしょうか？", 1, 1, 1, 0, 1, 8697, 132801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふあぁ…いけません\n公務の滞在先で\nお昼寝など…", 1, 1, 2, 0, 2, 8698, 132801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜は悪魔として\n夢を見せましょう\n騎士はどんな夢が\n見たいですか？", 1, 1, 3, 0, 3, 8699, 132801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふぅ…落ち着きます…\n家でゆっくりと過ごす\nクリスマスも\n良いものですね", 1, 1, 0, 0, 2, 8701, 132801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "リンドのプレゼントに\nぬいぐるみは…？\n…子ども扱いすると\n落ち込むでしょうか…", 1, 1, 0, 0, 3, 8702, 132801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "暖かいお部屋に\nこの香りを漂わせたら\nふふっ…素敵な時間を\n過ごせそうです", 1, 1, 0, 0, 4, 8703, 132801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これがあれば、\n寒い冬も安心ですね\n暖かくて…このまま\n眠ってしまいそうです", 1, 1, 0, 0, 5, 8704, 132801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "悪魔の力を使ったら\nお腹が空いてきて\nしまって…\n恥ずかしいです…", 1, 1, 0, 0, 7, 8706, 132801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "クリスマスには多くの\n伝承があるのですね…\n天使の伝承まで\nありましたし…", 1, 1, 0, 0, 8, 8707, 132801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "まぁ…！\nよくできていますね\nサプライズプレゼント\n…でしょうか。ふふ♪", 1, 1, 0, 0, 14, 8709, 132803);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ど、どうでしょう…？\n親善交流の催しに\nなりそうでしょうか…", 1, 1, 0, 0, 15, 8710, 132804);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ギルドハウスって\nあったかぁ～い♪\n心がぽかぽかするの！", 1, 1, 0, 0, 1, 8711, 132901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "クリスマスの悪魔の\nお仕事はないわね！\nだってここのみんな\nいい子なんですもの♪", 1, 1, 0, 0, 2, 8712, 132901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "みんなちゃんと\n寝られてるかしら？\n夜更かしせずに、\nいい夢見てね♪", 1, 1, 0, 0, 3, 8713, 132901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "毎回、来年が\n楽しみになるような\n素敵なクリスマスを\n過ごしたいわね♪", 1, 1, 0, 0, 4, 8714, 132901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "訳ありケーキを見ると\n刑務作業を思い出すわ\n…あれも貴重な体験\nだったのかしら…？", 1, 1, 0, 0, 5, 8715, 132901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ロマンチックな\nクリスマスに備えて\n今のうちに少女漫画で\n予習しておかないと！", 1, 4, 0, 0, 101, 8716, 132901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ほっぺにチューなら\nもう一回しても…\nや、やっぱり無理！\n恥ずかしいわ～！", 1, 8, 0, 0, 102, 8717, 132901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "またみんなで\nセッションしたいわね\nカラーベルの練習した\n成果を見せたいの♪", 1, 1, 0, 0, 1, 8718, 132901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "君はサンタさんに\nどんなプレゼントを\n頼んでいたのかしら？\n気になるわ～！", 1, 1, 0, 0, 2, 8719, 132901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "愛が壊れても、\n何度でも治してあげる\n私たちの間に、\n愛の尽きる日はないわ", 1, 1, 0, 0, 3, 8720, 132901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "冬休みって短いようで\n長く感じちゃうわよね\n学校のみんなと\n早く会いたいな～♪", 1, 1, 0, 0, 4, 8721, 132901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "愛をテーマにした\n冬休みの作文の宿題、\n君の分を読むのが\n今から楽しみね♪", 1, 4, 0, 0, 101, 8722, 132901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "よかったらまた\n私をハニーって呼んで\nほしいの。ダーリン♪", 1, 8, 0, 0, 102, 8723, 132901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよう！\n冬の朝は冷えるから\n風邪を引かないように\n対策しましょうね", 1, 1, 1, 0, 1, 8724, 132901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼ご飯は一緒に\n食べに行きましょう！\nクリスマス限定の\nランチコースよ♪", 1, 1, 2, 0, 2, 8725, 132901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ねえねえ、キャンドル\nイルミネーションを\n見に行きましょうよ！\nロマンチックよ～♪", 1, 1, 3, 0, 3, 8726, 132901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "暖かいクリスマスを\n過ごすのに必須よね♪\nふふ、柔らかぁい！", 1, 1, 0, 0, 2, 8728, 132901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "どの子もかわいい～♪\nクリスマスツリーの\n下で、一緒にお祝い\nしてもらいたいな♪", 1, 1, 0, 0, 3, 8729, 132901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "なんだか胸の奥が\n温かくなる匂いね～\nリラックスしちゃう♪", 1, 1, 0, 0, 4, 8730, 132901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったかぁい♪\nあ、でも夜になったら\nサンタさんが入れる\nように、火を消すのよ", 1, 1, 0, 0, 5, 8731, 132901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "寒い冬だからこそ\nしっかり食べて\n備えておかなきゃ！", 1, 1, 0, 0, 7, 8733, 132901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "『聖夜の６時間』…？\n気になる小説だけど…\n勝手に読んじゃ悪い\nわよね…ガマンよ…！", 1, 1, 0, 0, 8, 8734, 132901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "きゃっ…！\nこんな不思議な本が\nあったのね～\nちょっと面白いかも♪", 1, 1, 0, 0, 14, 8736, 132901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もふもふ、ふわふわで\nあったかくて\n気持ちいい～…♪", 1, 1, 0, 0, 15, 8737, 132901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "冬といえばサマー！\n…って言うけど夏は\n何をＰＲするんだろ\nメルクリゾート…", 1, 1, 0, 0, 1, 8738, 129501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "奨学金の道は遠いな…\nあまりに遠すぎて\n最近忘れてたまである", 1, 1, 0, 0, 2, 8739, 129501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "暇だし鏡でも見よっと\nはー、今日も顔が良…\nげ、若干むくんでる？\nはー、今日終わった", 1, 1, 0, 0, 3, 8740, 129501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "水着…お姉ちゃんと\n貸し借りするんだけど\nこれって少数派姉妹？", 1, 1, 0, 0, 4, 8741, 129501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ちゅむ、ツムギちゃん\n今日も可愛いねデュフ\n…なんで推し語りの声\nキモくなるんだろ私…", 1, 1, 0, 0, 5, 8742, 129501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あれ…なんで私\n水着のまま部屋の中\nうろついてるんだっけ\n…こわ！　てか寒っ！", 1, 4, 0, 0, 101, 8743, 129501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "先輩よく友達と海に\n行くって言ってたけど\n…え、誰とだろ？\nまあ誰でもいいけどさ", 1, 8, 0, 0, 102, 8744, 129501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "始まる…\nチエルの夏が始まる…\nあ冬でしたっけ？\nまどっちでもいいです", 1, 1, 0, 0, 1, 8745, 129501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "こないだトミヱ先輩\n壁に喧嘩売ってました\n相変わらず元気ですね", 1, 1, 0, 0, 2, 8746, 129501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ユニ先輩にお芝居の\n出来を聞かれたんで\n可愛いって答えました\n嘘は言ってません", 1, 1, 0, 0, 3, 8747, 129501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "クロエ先輩の腹筋\nかっちょいーですよね\nさりげ触りたいけど\nあいつ隙ないんですよ", 1, 1, 0, 0, 4, 8748, 129501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "先輩もうカメラ係は\nやらないんです？\nチエルはやってますよ\n最高の被写体を常に", 1, 4, 0, 0, 101, 8749, 129501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "メリゾでカップル割の\n企画あるらしいんです\nちなみに先輩は…\nあ、いえ、何でも…", 1, 8, 0, 0, 102, 8750, 129501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "朝ごはんは抜いても\n朝ちぇるだけは\n抜いちゃ駄目ですよ", 1, 1, 1, 0, 1, 8751, 129501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼なに食べるか\n悩んでます？\nおごってくれるなら\nいい店教えますけど", 1, 1, 2, 0, 2, 8752, 129501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この時間帯になると\nユニ先輩がごそごそ\n蠢きだすんですよね…", 1, 1, 3, 0, 3, 8753, 129501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "世界の美少女が…\nこんな格好で\nくつろいでたら…\nまずい…事案になる", 1, 1, 0, 0, 2, 8755, 129501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "え、この子って\nウォータープルーフ？\nメリゾにも連れて\nいけちゃうカンジ？", 1, 1, 0, 0, 3, 8756, 129501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はぁ～、いい香り…\nそういえばメリゾの\nスタッフルームも\n『贅』な匂いしてた…", 1, 1, 0, 0, 4, 8757, 129501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "薄着で暖まるとか…\n冷静になっちゃ\n駄目なやつですよね\nこれ…", 1, 1, 0, 0, 5, 8758, 129501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "おにぎり１個は\nちぇるに換算すると…\n２５ちぇるくらい？", 1, 1, 0, 0, 7, 8760, 129501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あれー？　この辞書\nまだちぇるの項目\n載ってないんだ？", 1, 1, 0, 0, 8, 8761, 129501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "何これ…地味に楽しい\nあとたぶん\nクロエ先輩が好きそう", 1, 1, 0, 0, 10, 8763, 129501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これは良きですねー\n自宅でメリゾ気分とか\nユニ先輩もにっこり", 1, 1, 0, 0, 11, 8764, 129501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここが騎士氏の\nお住まいですか\nふむ…地縛霊は\nいなさそうですね", 1, 1, 0, 0, 1, 8765, 133001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここは土足厳禁では\nないみたいですね\nま、私には関係ないか\n足、透っけ透けなので", 1, 1, 0, 0, 2, 8766, 133001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ランドソルの裏路地\nあそこは最高ですね\n暗くてじめじめしてて\n癒されるー", 1, 1, 0, 0, 3, 8767, 133001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "っ！\n今ラップ音が…？\nまさか幽霊？\nやだ、怖い", 1, 1, 0, 0, 4, 8768, 133001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "獣人族にエルフ族…\nなるほど騎士氏が\n幽霊程度では驚かない\n訳です…ちぇ", 1, 1, 0, 0, 5, 8769, 133001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今日の深夜\n騎士氏が起きるまで\n枕元でずーっと\n立っててみようかな", 1, 4, 0, 0, 101, 8770, 133001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "騎士氏の驚く顔を\n見ると胸が高鳴る…\n変ですね、心臓なんて\n動いてないのに", 1, 8, 0, 0, 102, 8771, 133001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "安心して下さい\n勝手にすり抜けて\n騎士氏の部屋は\n覗きませんよ…多分", 1, 1, 0, 0, 1, 8772, 133001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なんですか騎士氏\n怖い話でも聞きます？\nじゃあいきますね…\n今貴方の前に幽霊がっ", 1, 1, 0, 0, 2, 8773, 133001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この家…幽霊が\n寄ってくる間取りに\nなってますね\nまあ、嘘ですけど", 1, 1, 0, 0, 3, 8774, 133001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "料理はしませんよ\n指きったら痛いので\nえ…透ければいい？\n……天才ですか？", 1, 1, 0, 0, 4, 8775, 133001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お散歩ですか？\n深夜なら付き合います\nあ、やっぱり断ります\n幽霊出たら怖いので", 1, 4, 0, 0, 101, 8776, 133001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "騎士氏の部屋のセンス\n好きですよ…もっと\nじめじめしていれば\nなおよし、ですが", 1, 8, 0, 0, 102, 8777, 133001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "レイスになってから\nあまり言わなくなった\n挨拶第一位はこちら…\nおはようございます", 1, 1, 1, 0, 1, 8778, 133001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "騎士氏。午後からも\n頑張って下さいね\n私は日陰で休みます\nでは、おつです", 1, 1, 2, 0, 2, 8779, 133001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜も更けてきましたね\nここからがレイスの\n本領発揮…では早速\nう　ら　め　し　や～", 1, 1, 3, 0, 3, 8780, 133001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "中々座り心地が\nよろしいですね\nブロッケンに跨る方が\n安心はしますが", 1, 1, 0, 0, 2, 8782, 133001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "知ってますよ\nどうせ貴方も\n夜中になったら\n動き回るんでしょ？", 1, 1, 0, 0, 3, 8783, 133001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "すう…はあ…\nすごく落ち着く香り\n…落ち着き過ぎて\n成仏しそうでした", 1, 1, 0, 0, 4, 8784, 133001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "温度は感じませんが\nなぜかほっこり\n温かい気持ちです\nなんででしょうね？", 1, 1, 0, 0, 5, 8785, 133001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もぐもぐ…\n食べ物は基本好きです\n消化の方法は…\n企業秘密で", 1, 1, 0, 0, 7, 8787, 133001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "苦手なんですよね\nホラー小説\nだって幽霊とか\n怖いじゃないですか", 1, 1, 0, 0, 8, 8788, 133001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この得も言われぬ\n高揚感…やはり\n正月はいいものだな！", 1, 1, 0, 0, 1, 8790, 133201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "む、いかんな\n気付くと黒豆と\n栗きんとんばかり\n食べてしまう…", 1, 1, 0, 0, 2, 8791, 133201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ギルドの連中にも\nお年玉を渡してみるか\n少しは士気が上がる\nかもしれないな！", 1, 1, 0, 0, 3, 8792, 133201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "正月くらいはのんびり\n羽を伸ばすとしよう\n適切な休暇も軍人の\n大事な責務だからな", 1, 1, 0, 0, 4, 8793, 133201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "正月で浮かれすぎた\n者も多いはずだ\n念のため街を見回りに\n行くとしよう", 1, 1, 0, 0, 5, 8794, 133201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "む、このおせち料理は\n絶品だな！\nあいつの分も\nとっておいてやろう", 1, 4, 0, 0, 101, 8795, 133201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今年の抱負は…\nそうだな、あいつと\nさらに高みを目指す\nこれでいこう", 1, 8, 0, 0, 102, 8796, 133201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "えっ、お年玉！\n…い、いや、私は\n子どもではないからな\nえ、遠慮させてもらう", 1, 1, 0, 0, 1, 8797, 133201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "凧あげすごろく\nこま回し、たとえ遊び\nでも手は抜かんぞ\nさあ、勝負だ！", 1, 1, 0, 0, 2, 8798, 133201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そのきな粉餅も\nうまそうだな…\n私のあんこと交換\nしないか？", 1, 1, 0, 0, 3, 8799, 133201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "貴公、正月料理を\n食べ過ぎてないか？\nじ、実は私も少々、な", 1, 1, 0, 0, 4, 8800, 133201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "貴公、お参りに行くぞ\n何？　外が寒いだと？\n仕方ない、手ぐらいは\nつないでやろう", 1, 4, 0, 0, 101, 8801, 133201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "貴公と新たな年を\n迎えられてうれしいぞ\n今年も一つ\nよろしくたのむ！", 1, 8, 0, 0, 102, 8802, 133201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "起きろ貴公。早朝なら\n参拝客も少ないはず\n今のうちにお参りを\n済ませよう", 1, 1, 1, 0, 1, 8803, 133201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "昼食に餅を焼いたぞ\nのどに詰まらせない\nようにゆっくり噛んで\n食べるんだ", 1, 1, 2, 0, 2, 8804, 133201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜ふかしはあまり\n褒められたものでは\nないが…正月くらいは\n目をつむろう", 1, 1, 3, 0, 3, 8805, 133201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これに座って飲む\n甘酒は格別だな", 1, 1, 0, 0, 2, 8807, 133201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "せっかくの正月だし\nこいつにも着物を\n着せてやりたいな", 1, 1, 0, 0, 3, 8808, 133201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これは梅の香り…？\nふふ、なかなか\n風流ではないか", 1, 1, 0, 0, 4, 8809, 133201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "冬の寒さが堪えるな…\nここでしっかりと\n暖をとるとしよう", 1, 1, 0, 0, 5, 8810, 133201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "正月はのんびり\nしたいからな、勝手に\n食事が出てくるのは\n本当に助かる", 1, 1, 0, 0, 7, 8812, 133201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふむ、これが世界の\n新年のお祝いか\nなかなか興味深いな", 1, 1, 0, 0, 8, 8813, 133201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "一年の計は元旦にあり\n計画を立てて、\n有意義な一年を\n過ごしましょ", 1, 1, 0, 0, 1, 8815, 133501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "年末年始はどうしても\n出費が増えちゃうし\nしばらくはきっちり\n節約しなきゃ…！", 1, 1, 0, 0, 2, 8816, 133501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "タタミにフトン、\nトーゴク文化の内装…\nランドソルでももっと\n流行らないかしら", 1, 1, 0, 0, 3, 8817, 133501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "子どもたちが凧を\n手作りして遊んでたの\n偉いわよね、みんな…", 1, 1, 0, 0, 4, 8818, 133501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お年玉をあげる側に\nなって、分かる苦労…\n大人になるって\nこういうことかしら", 1, 1, 0, 0, 5, 8819, 133501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "一応実家にも\n顔を出さないと…\nよかったらだけど、\nあんたも来る？", 1, 4, 0, 0, 101, 8820, 133501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "毎日一緒にいて、\nささやかな幸せを\n同じように感じて…\nそれだけで十分ね", 1, 8, 0, 0, 102, 8821, 133501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "着物姿、もっと\nじっくり見たいの？\nさすがに照れるわよ、\nもう…", 1, 1, 0, 0, 1, 8822, 133501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "整理整頓、できてる？\nもし散らかってるなら\nお正月休みのうちに\n片づけちゃいましょ", 1, 1, 0, 0, 2, 8823, 133501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今年もみんなが無事に\n過ごせますように…\nほら、一緒に\nお祈りしましょ", 1, 1, 0, 0, 3, 8824, 133501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "みんな、あんたと\n遊ぶのが楽しみだって\n子どもに好かれる\nタイプなのよね", 1, 1, 0, 0, 4, 8825, 133501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あんたの運勢は大吉！\nラッキーパーソンは\n金髪の幼なじみよ\n…なんてねっ", 1, 4, 0, 0, 101, 8826, 133501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "初夢は実現するのよね\nそれじゃ、あたしは\nあんたと…んんっ\nなんでもないわ", 1, 8, 0, 0, 102, 8827, 133501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "外が寒いと、その分\n目も冴えるわよね\nこのまま朝の散歩に\n行こうかしら", 1, 1, 1, 0, 1, 8828, 133501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お正月休みでも、\n食べてばっかじゃダメ\nしっかり動いて\n体を引き締めましょ", 1, 1, 2, 0, 2, 8829, 133501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ちゃんとあったかく\nして寝るのよ\nお正月から\n風邪ひかないようにね", 1, 1, 3, 0, 3, 8830, 133501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "着物、しわに\nなっちゃいそう…\nでもそれ以上に、\n心地よくって…はぁ", 1, 1, 0, 0, 2, 8832, 133501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "門松みたいに\nこの子も飾ってみる？\n運勢がよくなるかも♪", 1, 1, 0, 0, 3, 8833, 133501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この匂い、もしかして\nトーゴクのお香？\nワビサビを感じるわ", 1, 1, 0, 0, 4, 8834, 133501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふぅ、あったかい…\n冷えた体に\n染み渡ってくるわ…", 1, 1, 0, 0, 5, 8835, 133501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "んっ、おいしい！\nおせちばっかじゃ\nやっぱり飽きちゃう\nものね…", 1, 1, 0, 0, 7, 8837, 133501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "験担ぎとか縁起物とか\n調べてみると\nいろいろあって\n面白いのよね～", 1, 1, 0, 0, 8, 8838, 133501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ふーん、いい家ね\nアタシの巣に\nしちゃおうかしら？", 1, 1, 0, 0, 1, 8840, 133601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お姉ちゃん…\n早く会いたいなぁ…", 1, 1, 0, 0, 2, 8841, 133601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "メドゥシアナも\n気に入るかしら？\n今度連れてこよっと！", 1, 1, 0, 0, 3, 8842, 133601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お邪魔してるだけ\nなのも悪いわよね…\nお返しはしないとね\n…ネズミ退治とか？", 1, 1, 0, 0, 4, 8843, 133601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "何か平和よね\nランドソルって…\nアタシ的にはもう少し\n荒っぽくてもいいわ", 1, 1, 0, 0, 5, 8844, 133601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ランドソルも\n楽しい街よね…\n案内してくれる\nアイツのおかげ？", 1, 4, 0, 0, 101, 8845, 133601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "アタシ、アイツに\n頼りすぎてるかも…？\nで、でも！　断らない\nアイツも問題だわ！", 1, 8, 0, 0, 102, 8846, 133601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ねぇ、つまんなーい！\nもっとアタシを\n楽しませなさいよねー", 1, 1, 0, 0, 1, 8847, 133601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この家悪くないけど…\nもっとムシムシ\nジメジメにできない？", 1, 1, 0, 0, 2, 8848, 133601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "何よ？\n用もないのに声掛けた\n…とか言ったら\n容赦しないわよ？", 1, 1, 0, 0, 3, 8849, 133601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "少し部屋が寂しいわね\nそうだ、アンタを\n石にして家具に…\n冗談よ、ふふんっ♪", 1, 1, 0, 0, 4, 8850, 133601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ねぇ、もう少し\n話していかない？\nアンタの話なら…\n特別に聞いてあげるわ", 1, 4, 0, 0, 101, 8851, 133601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "い、いつも\nあ、あ、ありが…\nうぅ、何でもないー！", 1, 8, 0, 0, 102, 8852, 133601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "やっと起きたわね？\n寝ぼすけなんだから！\n尻尾でひっぱたいて\n起こそうかと思ったわ", 1, 1, 1, 0, 1, 8853, 133601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "人捜しは日の高い\nうちに限るわ！\nさぁ、メドゥシアナ！\n全速力で出発進行よ!", 1, 1, 2, 0, 2, 8854, 133601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "アタシ…\n子どもじゃないもん…\n眠くなんか…\nないんだからぁ…", 1, 1, 3, 0, 3, 8855, 133601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふわふわであったかい\nつい、とぐろを\n巻きたくなるわね…", 1, 1, 0, 0, 2, 8857, 133601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "アンタ、こんな\nヤワな体じゃ駄目よ\nウロコくらい\n生やしたらどうなの？", 1, 1, 0, 0, 3, 8858, 133601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "くんくん…\n…ふん\n悪くないじゃない\n…くんくんくんくん…", 1, 1, 0, 0, 4, 8859, 133601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったかいの大好き！\nジオ・ゲヘナでも\nよく日向ぼっこ\nしたもんだわ…ふわぁ", 1, 1, 0, 0, 5, 8860, 133601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お姉ちゃんを捜すなら\n…もぐ\n体力つけないとね\n…もぐもぐ", 1, 1, 0, 0, 7, 8862, 133601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "何よ？　たまには\n勉強くらいするわよ\nお姉ちゃんに\n笑われちゃうもの", 1, 1, 0, 0, 8, 8863, 133601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この装いと力…\n今の世界との\n整合性を上手いこと\n合わせておかないとだ", 1, 1, 0, 0, 1, 8865, 133701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この格好…開発してた\n当時のまんまだ\nサイズもそのまま♪\n体型維持も完璧っ！", 1, 1, 0, 0, 2, 8866, 133701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あとで装備の手入れも\nやっとかないと\n…剣なら研ぐだけで\n終わるんだけどなぁ", 1, 1, 0, 0, 3, 8867, 133701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "自分の原点を\n振り返ってみるのも\nたまには必要だね", 1, 1, 0, 0, 4, 8868, 133701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "完璧じゃなくても\n今できる全力を尽くす\nその事に意味がある…\nそうだよね、少年", 1, 1, 0, 0, 5, 8869, 133701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "始まりのあの日、\nそして向かうべき道…\nいつも忘れずにいよう", 1, 4, 0, 0, 101, 8870, 133701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "誰かを救うために\n許容される犠牲なんて\nあっちゃいけない…\n全部、助けてやる", 1, 8, 0, 0, 102, 8871, 133701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おっと少年、\n何か御用かな？\nキミに関わる事なら\n最優先で対応するよ", 1, 1, 0, 0, 1, 8872, 133701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "困った事があれば\n頼ってもらって\n構わないからね！\nむしろもっと頼って！", 1, 1, 0, 0, 2, 8873, 133701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そうだ、時間あるなら\n武器のメンテを\n手伝ってくれないかな\n数が多くてさ～", 1, 1, 0, 0, 3, 8874, 133701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "遠距離戦の心得、\n少年にもレクチャー\nしてあげよっか？", 1, 1, 0, 0, 4, 8875, 133701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "無茶しないで、って\n言ってもするだろうし\nせめてアタシの手が\n届く範囲で頼むよ", 1, 4, 0, 0, 101, 8876, 133701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "虚実入り混じる\nこの世界でキミが…\n進むべきルートを\n照らしてくれたんだ", 1, 8, 0, 0, 102, 8877, 133701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "グッモーニン！\n朝だよ少年、今日も\n張り切っていこう！", 1, 1, 1, 0, 1, 8878, 133701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "クレープの代わりに\nガレットを焼いたんだ\nこの味で少年を\n唸らせてみせよう！", 1, 1, 2, 0, 2, 8879, 133701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もうこんな時間だ\n遅くならないうちに\n作業を切り上げようか\n少年も早く寝なよ？", 1, 1, 3, 0, 3, 8880, 133701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あえて緊張を解くのも\n戦いには必要だし…\nそうそう…大事大事…", 1, 1, 0, 0, 2, 8882, 133701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "愛着を抱かせる事を\n意図したデザインだね\n心理的効果の観点から\n分析してみようか…", 1, 1, 0, 0, 3, 8883, 133701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "香りは記憶と密接に\n結びつく事がある…\n懐かしい匂いだね…", 1, 1, 0, 0, 4, 8884, 133701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "癒されるな…\n何がこの感情変化を\n引き起こすんだろう？", 1, 1, 0, 0, 5, 8885, 133701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "食の偉大さを感じるな\n戦闘糧食ってやっぱり\n味気ないしなぁ…", 1, 1, 0, 0, 7, 8887, 133701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "実用ばかりじゃなく\n詩や物語ってのも\n人には必要だよね", 1, 1, 0, 0, 8, 8888, 133701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この場所の開発版も\n探せばあるかもな\nさて、どんなデザイン\nだったのやら…", 1, 1, 0, 0, 1, 8890, 133901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今まで数々の波乱に\n身を投じたが…やはり\n波乱にはこの姿が一番\n似合いだよ☆", 1, 1, 0, 0, 2, 8891, 133901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "むっ…この角に羽と\n尻尾は家具とぶつかり\nやすいな。兎の時は\n気にならなかったが…", 1, 1, 0, 0, 3, 8892, 133901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "いつもより闘争を\n求める気分になるな\n初心に帰るとは\nこのことか…？", 1, 1, 0, 0, 4, 8893, 133901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "未調整上等☆\nそれを上手く使い\nこなしてこその\nワタシだろう？", 1, 1, 0, 0, 5, 8894, 133901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "坊やとできそうな\n愉しいことは…\nふむ、多すぎて迷うな", 1, 4, 0, 0, 101, 8895, 133901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "時折、坊やの居心地の\nよさに絆されそうに\nなってしまうな\nワタシらしくもない…", 1, 8, 0, 0, 102, 8896, 133901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どうだ、おろした\n髪型も艶やかだろう？\nそんじょそこらの\n賞賛では満足せんぞ☆", 1, 1, 0, 0, 1, 8897, 133901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "外に出るぞ！\n共に退屈を殺しに\n行こうじゃないか☆", 1, 1, 0, 0, 2, 8898, 133901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "角と翼も触りたい？\nワタシの虚数魔境に\n挑むというなら\n止めはせんよ☆", 1, 1, 0, 0, 3, 8899, 133901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なんだ、困りごとか？\nクリスティーナさんが\n何でも聞いてやろう☆\n解決するかは別だがな", 1, 1, 0, 0, 4, 8900, 133901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "力が欲しいか？\nならば呼ぶがいい！\nワタシの名を☆", 1, 4, 0, 0, 101, 8901, 133901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "悪魔との契約は絶対\nなのだそうだが…\nどうだ、坊や\n一つ契ってみるか？", 1, 8, 0, 0, 102, 8902, 133901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "まだ眠い？　甘えるな\n寝首を掻かれたく\nなければ飛び起きて\nこい…☆", 1, 1, 1, 0, 1, 8903, 133901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "昼食の準備を\nしてやろう。\n毒見はいらんぞ？\n安心するといい！", 1, 1, 2, 0, 2, 8904, 133901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "坊やは寝る時間だ\n寝ないのならば…\n大人の時間に\n付き合ってもらうか☆", 1, 1, 3, 0, 3, 8905, 133901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "角と翼、尻尾の\nコンディションまで\n完璧だな☆", 1, 1, 0, 0, 1, 8906, 133901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "なかなかのフワモコ\n具合だが…ううむ…\n翼と尻尾が邪魔だな", 1, 1, 0, 0, 2, 8907, 133901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うーむ、角と尻尾を\n追加して…さらに槍を\n持たせた方が見栄えが\nよさそうだ", 1, 1, 0, 0, 3, 8908, 133901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい香りだな\n血と砂塵の臭いで\n上書きするまでの\n間に合わせに最適だ☆", 1, 1, 0, 0, 4, 8909, 133901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "生温いな\nもっと焦がれるほどの\n熱量が欲しいところだ", 1, 1, 0, 0, 5, 8910, 133901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "普段はないものが\n生えているのだから、\n運動でしっかり感覚を\n掴んでおかなければな", 1, 1, 0, 0, 6, 8911, 133901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "腹が減っては戦は…\nと言うからな\nしっかり食べて\nおくとしようか☆", 1, 1, 0, 0, 7, 8912, 133901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "『家庭を築くための\n絶対条件』…？\nふむ…興味がある\nタイトルだ", 1, 1, 0, 0, 8, 8913, 133901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "奇妙な感覚です\nまるであの頃の自分に\n変装しているかのよう", 1, 1, 0, 0, 1, 8915, 133801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "グレゴール？\nおっと、そうでした\n今は昔の姿になって\n離れていましたね", 1, 1, 0, 0, 2, 8916, 133801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "このアバターでいると\n妙に子どもたちからの\n視線を感じますね…", 1, 1, 0, 0, 3, 8917, 133801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "リソースの割り振りが\n変わっただけなのに\n随分と身体が軽く\n感じますね", 1, 1, 0, 0, 4, 8918, 133801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "普段グレゴールに\n任せている分身の管理\n自分でやるには\nいささか煩雑ですね…", 1, 1, 0, 0, 5, 8919, 133801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "長老に頼まれて描いた\n妖精の絵…いったい\n何に使ったのでしょう", 1, 4, 0, 0, 101, 8920, 133801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "私自身をモデルにした\n完全なるNPCたち\nそれが私の分身です", 1, 8, 0, 0, 102, 8921, 133801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "このアバターが\n気になるのですか？\n貴方はつくづく\n妖精に縁がありますね", 1, 1, 0, 0, 1, 8922, 133801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ちょうどよかった\nグレゴールを見かけ\nませんでしたか？", 1, 1, 0, 0, 2, 8923, 133801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あの翅は飾りでは\nありません。ただ…\nグレゴールに乗って\n飛ぶ方が楽なだけです", 1, 1, 0, 0, 3, 8924, 133801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "妖精とは本来神話に\nおけるトリックスター\n運命をかき回すことが\n存在意義なのです", 1, 1, 0, 0, 4, 8925, 133801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "変貌大妃の権能との\n相性を考えなければ\nこの姿こそが私たちの\n最強の姿です", 1, 4, 0, 0, 101, 8926, 133801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "変化とは一種の喪失\nだからこそ得られる物\nがある…貴方のそれも\n大切にするのですよ", 1, 8, 0, 0, 102, 8927, 133801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "マサキからの定時連絡\nが来ませんね…心配は\n不要ですが、様子を\n見に行きましょうか", 1, 1, 1, 0, 1, 8928, 133801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "昼食の時間ですね\nグレゴールのために\nカロリーの高い物を\n食べに行きましょう", 1, 1, 2, 0, 2, 8929, 133801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜更かしはダメですよ\n悪いスパイが暗躍して\nいる時間帯ですからね", 1, 1, 3, 0, 3, 8930, 133801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふむ、座り心地は中々\nですが…私にはやはり\n玉座の方があいますね", 1, 1, 0, 0, 2, 8932, 133801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふふ、いいでしょう\n我が配下に加えます\n存分に働きなさい", 1, 1, 0, 0, 3, 8933, 133801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "時折晶からしていた\nあの甘い匂いは何の\nアロマだったのか…", 1, 1, 0, 0, 4, 8934, 133801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "燃え盛る炎もこうして\n暖炉の中なら安全です\nクリスもあの衝動を\n管理さえできれば…", 1, 1, 0, 0, 5, 8935, 133801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "そういえば真那が\n獄中で手持無沙汰に\nひたすらおにぎりを\n食べていましたね…", 1, 1, 0, 0, 7, 8937, 133801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これは、妖精学の本…\n長老の蔵書から借りて\n読みふけったものです", 1, 1, 0, 0, 8, 8938, 133801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "どんな詩を\n作ろうかしら…？\n…大体、暗く\nなっちゃうけど…", 1, 1, 0, 0, 1, 8940, 134301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "唱喚術の練習…\nここでやったら\n迷惑よね…", 1, 1, 0, 0, 2, 8941, 134301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "チカちゃんとの\nデュエット…\n足をひっぱってない…\nかしら…？", 1, 1, 0, 0, 3, 8942, 134301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "唱喚士の修行…\n滝に打たれるとか…\n崖を登るとか…\nなくて、よかった…", 1, 1, 0, 0, 4, 8943, 134301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "精霊さん、肉じゃが\n食べる、かしら…？\n魔力だけだと…\n飽きちゃいそうだし…", 1, 1, 0, 0, 5, 8944, 134301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "精霊さんのお世話に\n慣れたぶん…\nあのひとのお世話も\n上手にできる…はず？", 1, 4, 0, 0, 101, 8945, 134301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "これからも、歌うわ…\nこの声が、彼の力に\nなる、限り──", 1, 8, 0, 0, 102, 8946, 134301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この服…踊り子さんに\n間違われちゃって…\nあ…お、お触りしちゃ\nだめよ…？", 1, 1, 0, 0, 1, 8947, 134301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたはどんな\n精霊が…好き…？\n唱喚…してみたいわ…", 1, 1, 0, 0, 2, 8948, 134301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あんまり白い服って…\n着ないの…その…\nこ、ころんだりして…\n汚すから…", 1, 1, 0, 0, 3, 8949, 134301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "じ、実はね…\nこの体の模様…筆で\n書いてもらうたび…\nくすぐったい…の…", 1, 1, 0, 0, 4, 8950, 134301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "精霊さん…あなたと\n遊びたいみたい…\nや、やっぱり…\nパパ…だから…？", 1, 4, 0, 0, 101, 8951, 134301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたを歌にしたら…\n…ふふっ、だめね\n一曲じゃ…あなたを\n表現できない、わ…", 1, 8, 0, 0, 102, 8952, 134301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "朝の修行は…\nうとうと…し…ちゃ…\n……（かくん）\n……あぅっ？", 1, 1, 1, 0, 1, 8953, 134301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "せ、精霊さんと…\nかくれんぼ中なの…\nうぅ…見つからない…\n日が暮れちゃいそう…", 1, 1, 2, 0, 2, 8954, 134301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "精霊さん、寝たから…\nふふっ…ここからは\nパパとふたりの時間…\n…ね？", 1, 1, 3, 0, 3, 8955, 134301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふぅ…修行と育児は…\nちょっとだけ…\n休憩…ふぁ…", 1, 1, 0, 0, 2, 8957, 134301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あなたとも契約…\n…できる、かしら？\nふふっ…", 1, 1, 0, 0, 3, 8958, 134301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい香りに包まれたら\n精神統一も\nはかどりそう…♪", 1, 1, 0, 0, 4, 8959, 134301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あたたかい場所…\nここで作詞したら…\n穏やかな詩が\n書ける、かも…", 1, 1, 0, 0, 5, 8960, 134301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "精霊さんのお世話を\nしていると…\nお腹、空いちゃって…", 1, 1, 0, 0, 7, 8962, 134301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふふっ…♪\n色んな楽譜…\n集めるの、楽しい…♪", 1, 1, 0, 0, 8, 8963, 134301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "けんぞくまだかな？\nはやく会いたいなー\nヴァンピィちゃん\nずっと待ってるからね", 1, 12, 0, 0, 103, 8965, 122301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "けんぞくのこと\nずっとずっと守るよ！\nだからいつまでも\n一緒にいてね", 1, 12, 0, 0, 103, 8966, 122301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "私の歌や想いを\n誰かに託せる…\nこれほどうれしい\nことはありません", 1, 1, 0, 0, 1, 8967, 134401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "コッコロさんの\n包容力はすごいです\nあんなふうに私も\n人を包み込めたら…", 1, 1, 0, 0, 2, 8968, 134401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "最近のボイトレは\nランファさんと一緒に\nやるんです。お互いに\n切磋琢磨しますよ！", 1, 1, 0, 0, 3, 8969, 134401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "先輩として相応しい\n立ち振る舞いを\n心がけたいですね\nしっかりしなきゃ…！", 1, 1, 0, 0, 4, 8970, 134401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ランファさんが\n踊ったら映えるかも…\nアイドルにも興味を\n持ってもらえないかな", 1, 1, 0, 0, 5, 8971, 134401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "支え導くために\n寄り添って考える…\nあの人からたくさん\n教わったことでしたね", 1, 4, 0, 0, 101, 8972, 134401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "いつだってあなたに\n恥じることのない\n姿をお見せしたいです", 1, 8, 0, 0, 102, 8973, 134401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あなたの手は温かくて\n触れていると\n落ち着きますね…♪", 1, 1, 0, 0, 1, 8974, 134401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "里のみんなから修行の\n差し入れを頂きました\nよろしければ、一緒に\n食べましょう♪", 1, 1, 0, 0, 2, 8975, 134401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ノゾミにもお土産を\n用意したいですね\n里のお土産選び、\n付き合ってください♪", 1, 1, 0, 0, 3, 8976, 134401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "精霊たちも、あなたが\n大好きみたいです♪\n優しさが伝わって\nいるんでしょうね", 1, 1, 0, 0, 4, 8977, 134401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "特訓が終わったら、\n二人でどこかに\n出かけましょうか♪", 1, 4, 0, 0, 101, 8978, 134401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ひゃ…す、すみません\n肌の模様を\nなぞられると、\nくすぐったくて…", 1, 8, 0, 0, 102, 8979, 134401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようございます\n精霊たちと一緒に、\n今日も頑張ります\n応援してくださいね♪", 1, 1, 1, 0, 1, 8980, 134401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼の時間です\n今日も精いっぱい\n修行を頑張りましょう", 1, 1, 2, 0, 2, 8981, 134401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もうすっかり夜ですね\n眠るまで子守唄を\n歌ってあげますよ？\nふふ♪", 1, 1, 3, 0, 3, 8982, 134401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "無理せず休憩するのも\n大切ですよね\nふかふか～…ふふっ♪", 1, 1, 0, 0, 2, 8983, 134401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "今日はこの子に\n歌の特訓を聞いて\nもらいましょう♪", 1, 1, 0, 0, 3, 8984, 134401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "素敵な香り…\nふふ、精霊たちも\n気に入ったみたいです", 1, 1, 0, 0, 4, 8985, 134401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったかい…\nこの衣装、やっぱり\n少し冷えやすいものね", 1, 1, 0, 0, 5, 8986, 134401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "特訓の後のご飯は\n美味しいですね♪\nこれでまた頑張れます", 1, 1, 0, 0, 7, 8987, 134401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "唱喚士についての\n書籍はないみたい\nいつか私がここに\n楽譜を入れたいな", 1, 1, 0, 0, 8, 8988, 134401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ギルドハウスの平和も\n任せてください\n何が来たって\n守ってみせますよ！", 1, 1, 0, 0, 1, 8992, 134601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "このギルドハウスも\nいつの間にか出撃台に\n改造されてたり\nするんでしょうか…？", 1, 1, 0, 0, 2, 8993, 134601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "戦いを経験してこそ\n実感できますね\n平和はいいもの、\n友達は素晴らしいもの", 1, 1, 0, 0, 3, 8994, 134601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "次はランドソルで\n巨大ロボットが\nブームになったり？\n…さすがにないか", 1, 1, 0, 0, 4, 8995, 134601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "改めて見るとこの服…\nか、体の線とか\n結構目立ってるかも…", 1, 1, 0, 0, 5, 8996, 134601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "団長さんって\n知らない人とも\n話せる系ですよね\nコツはいったい…", 1, 4, 0, 0, 101, 8997, 134601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ＢＢ団、世界を救う！\nまだ信じられないけど\n本当にあった出来事\nなんですよね…！", 1, 8, 0, 0, 102, 8998, 134601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "パイロットも\n視力が大事なんです\n弓使いの経験が\n生きました！", 1, 1, 0, 0, 1, 8999, 134601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ギガンティック\nマイフレンドくん、\n出撃！\nあ、ただの妄想です", 1, 1, 0, 0, 2, 9000, 134601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "コクピットから\n降りたあとに残る\nふわふわ感…ちょっと\n癖になりませんか？", 1, 1, 0, 0, 3, 9001, 134601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ひえっ、敵襲！？\nあっすみません、\nちょっと\n寝ぼけてたかも…", 1, 1, 0, 0, 4, 9002, 134601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "共に戦場を駆け抜け\nＢＢ団の絆もより強く\nこれからも結束を\n強めましょうね！", 1, 4, 0, 0, 101, 9003, 134601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "同じコクピットに\n乗ったのに比べれば、\nスキンシップくらい…\nや、やっぱ緊張します", 1, 8, 0, 0, 102, 9004, 134601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようございます！\n今日もがんばりますっ\n…クローチェさんを\n見習ってみました…", 1, 1, 1, 0, 1, 9005, 134601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼ご飯の時間ですね\nがんばって他の人\n誘ってみようかな…", 1, 1, 2, 0, 2, 9006, 134601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜の見張りは\n任せてください\n一人きりの夜も\n慣れてますから！", 1, 1, 3, 0, 3, 9007, 134601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "休めるときに\n休んでおかなくちゃ\nいつ何が起こるか\n分かりませんからね", 1, 1, 0, 0, 2, 9009, 134601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "師匠もこういうの\n好きだったのかな…\nなんだかしんみり\nしちゃいます", 1, 1, 0, 0, 3, 9010, 134601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "香りでリラックス…\n余裕のない時でも\nこういう安らぎは\n大切ですよね", 1, 1, 0, 0, 4, 9011, 134601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あったかい…\nこれはきっと友情と\n同じぬくもり…", 1, 1, 0, 0, 5, 9012, 134601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "新鮮なご飯、感動…！\n師匠たちの世界だと\nご飯は保存食ばかり\nでしたからね…", 1, 1, 0, 0, 7, 9014, 134601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "操縦マニュアル、\nしっかり読んで\n性能を引き出さなきゃ", 1, 1, 0, 0, 8, 9015, 134601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "屋内で飛べなくても\nここなら平気ですっ\n助けてくれる人が\nたくさんいますからっ", 1, 1, 0, 0, 1, 9017, 134501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "新ボディになってから\n人や物が前より身近に\n感じられて、わたし、\n幸せですっ♪", 1, 1, 0, 0, 2, 9018, 134501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "配達以外にも何か\nお仕事したいですっ！\nハグで遊覧飛行とか、\n喜んでもらえますか？", 1, 1, 0, 0, 3, 9019, 134501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "むむむ～…\n文字を書くのって\n難しいですね～っ\n練習しなくちゃっ！", 1, 1, 0, 0, 4, 9020, 134501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "簡易点検、よしっ！\nポポおじいさんに\n点検の仕方を教わって\n今日も絶好調ですっ", 1, 1, 0, 0, 5, 9021, 134501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "明日は北の空、\n明後日は南の空っ♪\nえへへっ、見せたい\n景色がいっぱいですっ", 1, 4, 0, 0, 101, 9022, 134501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ついナイトさんを\n探しちゃいます…\nわたし、寂しんぼさん\nだったんでしょうかっ", 1, 8, 0, 0, 102, 9023, 134501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どんなところでも\nひとっ飛びっ♪\nわたしに任せて\nくださいねっ！", 1, 1, 0, 0, 1, 9024, 134501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "まだ知らない機能が\nエアリアルには\n隠されてるかもっ…\nワクワクしますねっ！", 1, 1, 0, 0, 2, 9025, 134501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ひゃんっ！　…あはは\nエアリアルになっても\nケーブルに触られると\nくすぐったいですねっ", 1, 1, 0, 0, 3, 9026, 134501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ヴォルガーノさんに\nそっくりの子猫さんに\n最近触れたんですっ！\nかわいかったな～っ♪", 1, 1, 0, 0, 4, 9027, 134501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ハグしてくれるん\nですかっ？\nえへへっ、ぎゅーっ♪", 1, 4, 0, 0, 101, 9028, 134501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ナイトさんと一緒に\nいるだけで、胸が\nぽかぽか～って\nあったかいですっ♪", 1, 8, 0, 0, 102, 9029, 134501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようございますっ\n目覚まし飛行は\nいかがでしょうかっ？", 1, 1, 1, 0, 1, 9030, 134501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼ですよっ！\nいっぱい補給して\nまたフルパワーで\nがんばりましょうっ♪", 1, 1, 2, 0, 2, 9031, 134501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "眠れない夜はわたしに\n任せてくださいっ！\n充填さえあればずっと\n一緒にいられますよっ", 1, 1, 3, 0, 3, 9032, 134501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "歩きづらいので\nこういう休憩ポイント\nって助かります～っ", 1, 1, 0, 0, 2, 9034, 134501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "スラスターを付けたら\nこの子たちも空を\n自由に飛べたり\nするんでしょうかっ？", 1, 1, 0, 0, 3, 9035, 134501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あっ、これに似た\n香りを近くのお店で\n検知したことが\nありますっ♪", 1, 1, 0, 0, 4, 9036, 134501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "高熱源反応を検知っ\nわたしのスラスターも\n熱量の高さなら\nかなりものですよっ♪", 1, 1, 0, 0, 5, 9037, 134501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もぐもぐもぐ…っ\n食べたぶんはあとで\n真空パックにして\nお返ししますねっ", 1, 1, 0, 0, 7, 9039, 134501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "本を読むのって\n楽しいですねっ♪\n前はびりびりーって\nしちゃってたのでっ", 1, 1, 0, 0, 8, 9040, 134501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "なかなかの住処ですね\nこの私の別邸にして\nあげてもいいですよ♪", 1, 1, 0, 0, 1, 9042, 134701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "はぁ…いつになったら\nすべての神力が\n戻るのでしょう？", 1, 1, 0, 0, 2, 9043, 134701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここも悪くないですが\n昔私が作った庭園は、\nもっと良い場所でした\n焼けちゃいましたが…", 1, 1, 0, 0, 3, 9044, 134701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "うう…\nおなかが空きました…\nお米が食べたいです…", 1, 1, 0, 0, 4, 9045, 134701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここは平和ですねえ…\nジオ・ゲヘナも\nこのくらい穏やかなら\n住みやすいのですが", 1, 1, 0, 0, 5, 9046, 134701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "騎士さんのおかげで\n神力が戻ってきました\n良い働きですよ♪", 1, 4, 0, 0, 101, 9047, 134701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "騎士さんのいる場所が\n今の私がいるべき場所\nなのかもしれませんね", 1, 8, 0, 0, 102, 9048, 134701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "頭がたかーい！\n…ですよっ！", 1, 1, 0, 0, 1, 9049, 134701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ほーちゃんと呼ぶ権利\n安売りはしませんよ？\n特別ですから！", 1, 1, 0, 0, 2, 9050, 134701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私とおしゃべりを？\nまあ、いいでしょう♪\n何を話しましょうか？\nこの羽の秘密とか？", 1, 1, 0, 0, 3, 9051, 134701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "小鳥扱いは\nやめなさーい！\nこれでも神様ですよ？", 1, 1, 0, 0, 4, 9052, 134701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "き、気安く触るのは…\n…い、いえ、\n騎士さんなら…まぁ…", 1, 4, 0, 0, 101, 9053, 134701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "絢爛豪華な絆！\nこの私と結べるなんて\n光栄でしょう？", 1, 8, 0, 0, 102, 9054, 134701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふわぁぁ～\nおはようごじゃいます\n…んむむ", 1, 1, 1, 0, 1, 9055, 134701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼ご飯は何を…\nえっ？　私？\nそそんな゛あ゛！", 1, 1, 2, 0, 2, 9056, 134701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あの…騎士さん？\n添い寝する権利とか…\nほしくないですか？", 1, 1, 3, 0, 3, 9057, 134701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はぁ～ふかふか…\n…へ？\n鳥の巣にいるみたい？", 1, 1, 0, 0, 2, 9059, 134701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふむ…\nそばに置いてあげても\nいいでしょう", 1, 1, 0, 0, 3, 9060, 134701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ほわぁ～\nいいかおり～", 1, 1, 0, 0, 4, 9061, 134701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あたたかい…\nなんだか元気が\nでてきますねっ", 1, 1, 0, 0, 5, 9062, 134701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "たくさん食べて力を\n取り戻さなくてはっ\n…んがぐぐっ", 1, 1, 0, 0, 7, 9064, 134701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふむふむ…\n人の子にしては\nよく書けていますね♪", 1, 1, 0, 0, 8, 9065, 134701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "素敵なお家ですね\n温かみがあって\nどこか騎士様に\n似ております。ふふ", 1, 1, 0, 0, 1, 9067, 133101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "賑やかな街ですね\nこれほど活気豊かだと\nつい外に出てみたく\nなってしまいます", 1, 1, 0, 0, 2, 9068, 133101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "別世界の存在は知って\nおりましたが、いざ\n目の当たりにすると\n驚きの連続ですわ", 1, 1, 0, 0, 3, 9069, 133101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "この街は様々な種族が\nなにはばかることなく\n混在しているのですね\n…我々も、いつかは…", 1, 1, 0, 0, 4, 9070, 133101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ランドソルにも香水の\nお店があるのですね\nどんな香りが揃って\nいるのでしょうか…♪", 1, 1, 0, 0, 5, 9071, 133101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "昨日、素敵な花園を\n見つけたのです\n今日のお茶会は\nそちらでしましょうか", 1, 4, 0, 0, 101, 9072, 133101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "気を抜いてはいけない\nとわかっていても、\nここだとつい安心して\nしまいます…ふう…", 1, 8, 0, 0, 102, 9073, 133101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ごきげんよう\n街で見つけた茶葉を\n試しているんです\n騎士様も一杯どうぞ♪", 1, 1, 0, 0, 1, 9074, 133101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "親切な方ばかりですね\n素敵なお友だちが\nできそうな予感で、\nワクワクしてます…♪", 1, 1, 0, 0, 2, 9075, 133101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "黄泉の食卓の皆様用に\nお土産を用意しないと\n騎士様のお知恵も\n頼りにしておりますわ", 1, 1, 0, 0, 3, 9076, 133101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ジオ・ニヴルヘルと\n比べると、この世界は\n本当に明るいですね\n少し眩しいくらいです", 1, 1, 0, 0, 4, 9077, 133101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あまり、血の巡りが\n良くなくて…\n騎士様の手は\nとても温かいですね", 1, 4, 0, 0, 101, 9078, 133101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今度はわたくしに\nジオ・ニヴルヘルを\n案内させてくださいね\n観光案内のお返しです", 1, 8, 0, 0, 102, 9079, 133101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようございます\n朝の眩しい太陽には\nなかなか慣れません…", 1, 1, 1, 0, 1, 9080, 133101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "騎士様、お茶は\nいかがでしょうか？\n一度一休みして、\n英気を養いましょう", 1, 1, 2, 0, 2, 9081, 133101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "眠れないのでしょうか\nまぶたが重くなるまで\nわたくしをお話し\n相手になさって下さい", 1, 1, 3, 0, 3, 9082, 133101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "思わず気が抜けそうに\nなってしまいます…\nふふ、ふわふわ…♪", 1, 1, 0, 0, 2, 9084, 133101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "まあ、かわいらしい\nグレイスもきっと\n気に入りそうな\nデザインですわ", 1, 1, 0, 0, 3, 9085, 133101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "まあ、いい香り…\n心安らぐようですわ\n素敵なセンスを\nお持ちなのですね♪", 1, 1, 0, 0, 4, 9086, 133101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "燃え移りや乾燥がある\nから近づかないほうが\nいいとわかりつつも…\nああ、温かい…♪", 1, 1, 0, 0, 5, 9087, 133101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "食が細いというよりは\n消化があまりうまく\nできなくて…本当は\nまだ頂きたいのですが", 1, 1, 0, 0, 7, 9089, 133101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "『薔薇の下の契り』？\nまあ、男性同士で…！\nそんな…こ、これ以上\nここで読むわけには！", 1, 1, 0, 0, 8, 9090, 133101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ダーリンが帰ってくる\n前に部屋の掃除しよ♪\n…ふふ、これってもう\n新婚生活だよね…♪", 1, 1, 0, 0, 1, 9092, 132301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "家具のセンスまで\n私と同じだなんて\n運命って怖いわ…♪", 1, 1, 0, 0, 2, 9093, 132301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あの人はベッドの下に\n何か隠してるのかな…\nな、なんて全っ然\n気にならないし…っ！", 1, 1, 0, 0, 3, 9094, 132301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "うっかりして\n角引っかけないように\nしないとね…", 1, 1, 0, 0, 4, 9095, 132301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "錬金術を使わなくても\nあなたの心を鷲掴む\n私は恋も錬金術も\n失敗しないんだから！", 1, 1, 0, 0, 5, 9096, 132301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あの人って破廉恥なの\n興味あるのかしら…？\n基本破廉恥禁止だけど\nあの人だけは…許す…", 1, 4, 0, 0, 101, 9097, 132301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "手狭になれば錬金術で\n新しい部屋作るから\nいつ家族が増えても\n安心だよね…♪", 1, 8, 0, 0, 102, 9098, 132301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "錬金術でカレーライス\n作っちゃおっかな♪\n具材を釜でグツグツ…\nま、料理とも言うわね", 1, 1, 0, 0, 1, 9099, 132301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "錬金術に興味がある？\nじゃあさっそく私が\n手取り足取り教えるわ\nって…やだ破廉恥っ！", 1, 1, 0, 0, 2, 9100, 132301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "イロハちゃんとの\n虫捕りは命懸けよ！\n一万匹捕れるまで\n帰れないんだから…", 1, 1, 0, 0, 3, 9101, 132301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ソノさんって\n体は小さいけど\nとっても頼れる私の\nお姉さん的存在なの♪", 1, 1, 0, 0, 4, 9102, 132301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "はあ…部屋中に広がる\nダーリンの香り…♪\n…ってノゾミみたいに\nなっちゃってる！？", 1, 4, 0, 0, 101, 9103, 132301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "鹿の獣人族の特徴は\nこのエッチな角…\n違っ！　叡智よ叡智！\n噛んだだけだからっ！", 1, 8, 0, 0, 102, 9104, 132301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あ、おはよう！\n寝る前に飲んだ\n目覚めすっきり秘薬の\n効果はバッチリかな？", 1, 1, 1, 0, 1, 9105, 132301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "午後からも頑張る\nあなたのために\n愛妻弁当作ったの♪\n一緒に食べよ？", 1, 1, 2, 0, 2, 9106, 132301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日も一日お疲れさま\n眠れないなら隣で…\nじゃなかった早寝して\n明日も頑張ろうね！", 1, 1, 3, 0, 3, 9107, 132301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい錬成をするには\n適度な休憩も必要よね\n…気持ち良くてたまに\n寝落ちしちゃうけど…", 1, 1, 0, 0, 2, 9109, 132301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいいぬいぐるみ\n錬成じゃなくて\n手編みで作ったら\n驚いてくれるかも♪", 1, 1, 0, 0, 3, 9110, 132301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "くん、くん…\nん～いい匂い♪\nノゾミの香水と\nいい勝負だわ", 1, 1, 0, 0, 4, 9111, 132301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ああ、暖かい\nでもソノさんの作った\nミートパイの方が\nもっと温かいわ…♪", 1, 1, 0, 0, 5, 9112, 132301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もぐもぐ…私も\n食べても減らない秘薬\n錬成して他の食べ物も\n作れないかな？", 1, 1, 0, 0, 7, 9114, 132301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふむふむ…\nこのレシピ面白いわね\n今度錬成してみよっと", 1, 1, 0, 0, 8, 9115, 132301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "おー、体が軽い\n腰痛も胃もたれも\nないし、最高かよ", 1, 1, 0, 0, 1, 9117, 132501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "いっつも力入れてたら\nいざって時困るし\nやっぱ省エネだな", 1, 1, 0, 0, 2, 9118, 132501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "やば…なんか急に\nブドウ朱飲みたい…\nルイちゃんに頼むか？\nいや、でもなあ…", 1, 1, 0, 0, 3, 9119, 132501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あれ、あの材料の在庫\nまだあったっけ…\nルイちゃんに確認しよ", 1, 1, 0, 0, 4, 9120, 132501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "イロハのやつ変な\nイタズラしてない\nといいけど…", 1, 1, 0, 0, 5, 9121, 132501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今日のシフトは～…\nお、彼と一緒じゃん\nふふっ…", 1, 4, 0, 0, 101, 9122, 132501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "イロハのやつ\n不用意に私の体で\n彼に抱きつくなって\nこっちが恥ずかしい…", 1, 8, 0, 0, 102, 9123, 132501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日も元気だねえ\n若いってうらやま…\nっていや、今は私の\n方が若いじゃん", 1, 1, 0, 0, 1, 9124, 132501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ん～？　どしたん\nヒマならうちの店で\nバイトしてく？", 1, 1, 0, 0, 2, 9125, 132501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "イロハ見なかった？\n今日は手伝うって約束\nしてたのにさあ…", 1, 1, 0, 0, 3, 9126, 132501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ルイちゃん特製\nエナドリ（子ども用）\nこれ飲んで今日も\nお仕事頑張るかー", 1, 1, 0, 0, 4, 9127, 132501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "元の体なら\n君と目線、合うのにね\n…ってドラマかよ\nごめん、今の忘れて", 1, 4, 0, 0, 101, 9128, 132501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なんかヒマそうじゃん\n用事ないなら\nお姉さんがコーヒー\n淹れたげよっか", 1, 8, 0, 0, 102, 9129, 132501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふわあ…おはよ\n今日もお仕事\nがんばりますかー", 1, 1, 1, 0, 1, 9130, 132501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ちょうどいいや\n一緒にお昼どうよ\nお姉さんが\n奢ってあげよう", 1, 1, 2, 0, 2, 9131, 132501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふぁぁ…ふぅ\nここからは大人の時間\nなんだけどさぁ\n眠くてかなわん…", 1, 1, 3, 0, 3, 9132, 132501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "おお～なんか前より\nでっかく感じるわ～\nてそりゃそうか", 1, 1, 0, 0, 2, 9134, 132501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "そいえば、イロハって\nあんまこういうの\n欲しがらなかったな～\nクワガタばっかで", 1, 1, 0, 0, 3, 9135, 132501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はあ…いい匂いだね\nさすがルイちゃん特製\n錬金アロマ\n次の目玉商品だな", 1, 1, 0, 0, 4, 9136, 132501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ユカリっち焚火の前で\nキメる麦しゅわに\nハマってるらしいけど\n興味あるわぁ…", 1, 1, 0, 0, 5, 9137, 132501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "うわこれ…\n無限にいけちゃうわ\n子どもの胃袋\n恐るべし…", 1, 1, 0, 0, 7, 9139, 132501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふむ、こんな需要が…\n次の新商品として\nルイちゃんに試作\n頼んでみるか～", 1, 1, 0, 0, 8, 9140, 132501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "服はその人の個性です\n仕立て屋は\nその手伝いをする\nプロなんです♪", 1, 1, 0, 0, 1, 9142, 134801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "糸を紡いで紡いで\n巻き巻き～♪", 1, 1, 0, 0, 2, 9143, 134801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ふむ…あの人の衣装\nなかなか斬新ですね\n後でどうなってるのか\n見せてもらおう♪", 1, 1, 0, 0, 3, 9144, 134801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "動きやすさと\nファッション性\n両立させてみせますよ", 1, 1, 0, 0, 4, 9145, 134801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今度のコレクション\n新作にワイルドな\nルームウェアは\nどうかな…？", 1, 1, 0, 0, 5, 9146, 134801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "騎士さんもたまには\n衣装の感想とか言って\nくれてもいいのにな～", 1, 4, 0, 0, 101, 9147, 134801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ノゾミさんとチカさん\nにも、おそろいの服を\nプレゼントしてみよう\n二人とも似合いそう♪", 1, 8, 0, 0, 102, 9148, 134801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ジオ・ゲヘナでまた\n素材収集したいんです\n手伝ってくれますか？", 1, 1, 0, 0, 1, 9149, 134801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "えっ？　薄着すぎ？\nおしゃれのためなら\nこのくらいへっちゃら\nですよ…っくしゅん！", 1, 1, 0, 0, 2, 9150, 134801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "穴が開いてる？\nこれはそーいう\nデザインですー！", 1, 1, 0, 0, 3, 9151, 134801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ちょっとどこ\n触ってるんですかー！\n…あ、糸くず\n取ってくれたんですね", 1, 1, 0, 0, 4, 9152, 134801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "騎士さんに\n新しい生地の服を…\nいや、シャレじゃ\nないですよ？", 1, 4, 0, 0, 101, 9153, 134801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "素敵な着こなしには\n健康な体も大切！\n騎士さんに見られても\n恥ずかしくないように", 1, 8, 0, 0, 102, 9154, 134801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようございます！\nさあ、着替えて！\n私が見繕ってあげます", 1, 1, 1, 0, 1, 9155, 134801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼は激辛料理です\nジオ・ゲヘナで\nハマッちゃって…\n一緒にどうですか？", 1, 1, 2, 0, 2, 9156, 134801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "睡眠不足は美容の大敵\nていうかシンプルに…\nね、眠い…", 1, 1, 3, 0, 3, 9157, 134801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "くつろげますね～\nでももうちょっと\n生地の手触りが…\nって、職業病？", 1, 1, 0, 0, 2, 9159, 134801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいいものって\n心を癒してくれますね\n服もそうありたいです", 1, 1, 0, 0, 3, 9160, 134801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いい香り～♪\nこの匂いにはどんな服\nが合うかなあ…？", 1, 1, 0, 0, 4, 9161, 134801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この服露出が多いから\n温かいとほっとする～\nずっとこうして\nいたいかも…", 1, 1, 0, 0, 5, 9162, 134801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いくらでも\n食べられちゃうから、\n自分でストップ\nかけないと…", 1, 1, 0, 0, 7, 9164, 134801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "しっかり勉強して\nジオ・ゲヘナに\nお洒落を伝えたいです", 1, 1, 0, 0, 8, 9165, 134801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あたくしは高貴なる\nマンティコア！\nサソリでもコウモリ\nでもありませんわよ♪", 1, 1, 0, 0, 1, 9167, 134901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "なかなか居心地がいい\nお部屋ですわね～\n…さてどうやって\nあたくしのものに…", 1, 1, 0, 0, 2, 9168, 134901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "高貴なるお審美眼で\nあの家具の価値を\n測ってやりますわ\n…ギリ合格ですわ！", 1, 1, 0, 0, 3, 9169, 134901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "高貴なる笑い声は\n一日にしてならず\nお子様の頃から練習を\n重ねてきたのですわ！", 1, 1, 0, 0, 4, 9170, 134901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お美しいあたくし様と\n同じ空間にいられる…\nどうぞその喜びを\n噛みしめなさいな♪", 1, 1, 0, 0, 5, 9171, 134901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "アナタがお土下座して\n頼むなら、あたくしも\nもう少しここに残って\n構いませんわよ♪", 1, 4, 0, 0, 101, 9172, 134901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "馬ボーンをあたくしに\n振り向かせるには\nどうしたら…って、\nこの言い方は語弊が！", 1, 8, 0, 0, 102, 9173, 134901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "少し寒いですわね…\n…この服装のせい？\nこっちだって好きで\n着てねぇですわよ！", 1, 1, 0, 0, 1, 9174, 134901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あのラミアが来たら\nあたくし様はお用事で\nお留守って言って\n追い返すんですのよ！", 1, 1, 0, 0, 2, 9175, 134901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "こっちの世界にも\n闘技場がありますの？\nそこもお頂点を極めて\nしまおうかしら～♪", 1, 1, 0, 0, 3, 9176, 134901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "馬ボーンって\n人畜無害そうなお面の\nわりに、時々やること\nえげつねぇですわね…", 1, 1, 0, 0, 4, 9177, 134901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ねぇ馬ボーン…\nアナタあの魔札、\nまだ持ってまして？\nとっとと捨てなさい！", 1, 4, 0, 0, 101, 9178, 134901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "絶対にあたくし様の\nお尻尾に触るんじゃ\nねぇですわよ…？\nいえ、振りではなく！", 1, 8, 0, 0, 102, 9179, 134901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふわぁ…\n打倒ネフィのお作戦を\n夜通し考え過ぎて、\n寝不足ですわぁ…", 1, 1, 1, 0, 1, 9180, 134901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もうお昼ですの？\nあたくし様に相応しい\n優雅なおランチと\n参りますわよ～♪", 1, 1, 2, 0, 2, 9181, 134901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あら馬ボーンったら\nまだ起きてますの？\n…ぐぬぬ、夜の間に\n仕掛ける作戦が…", 1, 1, 3, 0, 3, 9182, 134901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "このおフカフカ感…♪\n剣闘士宿舎の\nゴワゴワなお寝床とは\n大違いですわね！", 1, 1, 0, 0, 2, 9184, 134901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "なんですの、この\n弱っちそうなお顔は…\nおかわいそうですし、\nお僕にしてあげますわ", 1, 1, 0, 0, 3, 9185, 134901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "馬ボーンはこういう\n匂いが好みですの？\nふーん…ほーう…\nなるほどですわ…♪", 1, 1, 0, 0, 4, 9186, 134901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あぁ～～…\nそうそう、これくらい\n暑い方が快適ですわ…", 1, 1, 0, 0, 5, 9187, 134901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もぐもぐ、もぐっ…\nまぁそこそこ？\n悪くないお味では\nありますわねっ", 1, 1, 0, 0, 7, 9189, 134901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こんな本よりも、\n華麗なるカノン様の\n活躍録でも置いた方が\n世のためになりますわ", 1, 1, 0, 0, 8, 9190, 134901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "さっきねー、外で猫と\n木登り競争したんだよ\nなかなか手ごわい\n相手だった！", 1, 1, 0, 0, 1, 9192, 132401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "いっぱい食べて\n大きくなるんだ～\nあっでもこれ\nソノちゃんの体か", 1, 1, 0, 0, 2, 9193, 132401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "強いおねーさんの\n気配を感じる…\nどこから？\n…そこら中から！", 1, 1, 0, 0, 3, 9194, 132401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "虫捕り網の手入れは\nいつもバッチリ！\nいつ何が出るか\nわからないもんね", 1, 1, 0, 0, 4, 9195, 132401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "うちの中で走り回ると\nソノちゃんに\n怒られちゃうけど…\nここならいける？", 1, 1, 0, 0, 5, 9196, 132401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "おじちゃんも\nお部屋にいろいろ\n隠してるのかな？\nルイちゃんみたいに", 1, 4, 0, 0, 101, 9197, 132401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "世界一になったら\nおじちゃんにも\nいっぱい褒めて\nほしいな…約束っ♪", 1, 8, 0, 0, 102, 9198, 132401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "高いとこのものが\n取りたいときは\nイロハに言ってね\nイロハ得意だから！", 1, 1, 0, 0, 1, 9199, 132401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "どうも、イロハです\n剣士たるもの、\nあいさつは大事って\n教わったんだ♪", 1, 1, 0, 0, 2, 9200, 132401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "イロハと勝負だね！\n受けて立つよ～！\n…あれ、違った？", 1, 1, 0, 0, 3, 9201, 132401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あっ！　ルイちゃんに\n素材集め頼まれてたの\n忘れてた…！　やばい\nおじちゃん手伝って！", 1, 1, 0, 0, 4, 9202, 132401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "実はイロハって\n褒めたら伸びる\nタイプなんだ\nだからよろしくね♪", 1, 4, 0, 0, 101, 9203, 132401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "このお部屋、すごく\n居心地がいいんだ～\nやっぱりおじちゃんが\nいるからかな？", 1, 8, 0, 0, 102, 9204, 132401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "昨日仕掛けた罠で\n虫捕れてるかな？\nあとで一緒に\n見に行こうね♪", 1, 1, 1, 0, 1, 9205, 132401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もうお昼だよね？\nイロハのお腹が\n空いてきたから\n間違いないよ！", 1, 1, 2, 0, 2, 9206, 132401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ふわぁ～…\nあ、あくびじゃないよ\nイロハ大人なんだから\n眠くなんか…ふわぁ…", 1, 1, 3, 0, 3, 9207, 132401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふわふわしたものって\nつい飛び込みたく\nなっちゃうよね\n…うずうずっ", 1, 1, 0, 0, 2, 9209, 132401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "かわいいのもいいけど\nカッコよさも欲しいな\nそーだ、角とかもっと\nいっぱい付けてみる？", 1, 1, 0, 0, 3, 9210, 132401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "おー、いい匂い♪\nこれもしかして\nルイちゃんの新作？\n爆売れ間違いなしだよ", 1, 1, 0, 0, 4, 9211, 132401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ぬくぬくぽかぽか、\n気持ちいいー…\nでもちょっと\n暑くなってきたな", 1, 1, 0, 0, 5, 9212, 132401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "もぐもぐもぐ…\nこれはいい食材を\n使っておりますなぁ\nうん、三ツ星！", 1, 1, 0, 0, 7, 9214, 132401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふむふむなるほど…\nこっ、これは…！\n…ごめん、適当に\n言ってみた", 1, 1, 0, 0, 8, 9215, 132401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "また海に行く時に\n備えて…次の計画を\n練る必要がある…", 1, 1, 0, 0, 1, 9217, 135001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "騒がしいのも…\n以前ほど気にならない\n慣れてしまった…？", 1, 1, 0, 0, 2, 9218, 135001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "眩暈がする暑さも\n騎士クンと同じ気温を\n感じられるなら…いい", 1, 1, 0, 0, 3, 9219, 135001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "騎士クンに\n好意を抱く者は\nみんな有罪\n…わたしは例外", 1, 1, 0, 0, 4, 9220, 135001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "体も心も…外の世界に\n馴染んできた気がする\n少しずつ、だけど…", 1, 1, 0, 0, 5, 9221, 135001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "騎士クンの手…\n大きかった…\n…また、つなぎたいな", 1, 4, 0, 0, 101, 9222, 135001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "夏が終わっても…\nその先の季節も\n…その次の夏も\n騎士クンと一緒に…", 1, 8, 0, 0, 102, 9223, 135001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "騎士クンといるとね\n渇いてた心が…\n潤される、気がするの", 1, 1, 0, 0, 1, 9224, 135001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "騎士クンは…\nしたいこと、ある？\n夏にだけ…できること\n一緒に、しようね", 1, 1, 0, 0, 2, 9225, 135001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "仮想領域の夢でも\n現実の海でも…\n騎士クンと一緒なら\nそこが本物の夏、だね", 1, 1, 0, 0, 3, 9226, 135001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "肝試しがしたい…？\n…うまく驚けるかな\n練習しなくちゃ…", 1, 1, 0, 0, 4, 9227, 135001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "騎士クンとなら\nどんな夏でも…\nきっと楽しい…♪", 1, 4, 0, 0, 101, 9228, 135001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "水着…どうかしら…？\n…よかった\nシミュレーションの\n成果ね。ふふ", 1, 8, 0, 0, 102, 9229, 135001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "騎士クン…眠たそう\nもうちょっと…寝る？\n後で起こしてあげるね", 1, 1, 1, 0, 1, 9230, 135001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お出かけするなら\n水筒を用意するね…\n夏の水分補給は大事…", 1, 1, 2, 0, 2, 9231, 135001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "また…夜の海を\n見に行きたいな\n騎士クンと一緒に\n連れていってくれる？", 1, 1, 3, 0, 3, 9232, 135001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "騎士クンを待つには\nちょうどいい…\n…落ち着く…", 1, 1, 0, 0, 2, 9234, 135001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こういう物を\n城に置いた方が\nかわいいって思って\nもらえる…かな…？", 1, 1, 0, 0, 3, 9235, 135001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "夏に合わせた香りね…\n涼やかな風が胸を\n吹き抜けるような…", 1, 1, 0, 0, 4, 9236, 135001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "火は落ち着く…\n身体感覚を味わえる\n作り物の…この体でも", 1, 1, 0, 0, 5, 9237, 135001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "『食』…騎士クンと\n話す共通の話題には\n悪くなさそう…", 1, 1, 0, 0, 7, 9239, 135001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "『好きな人と楽しめる\n夏デート特集』…？\n……ふむ…", 1, 1, 0, 0, 8, 9240, 135001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "わたしの趣味では\nないけれど…\n騎士クンが望むなら\n城に置いても…？", 1, 1, 0, 0, 10, 9242, 135001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "む…悪くない\nこれなら\n騎士クンと一緒に…？", 1, 1, 0, 0, 11, 9243, 135001);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "一日中水着でいるって\n浮かれているみたいね\n夏は短いから、沢山\n楽しまなくちゃね", 1, 1, 0, 0, 1, 9244, 135101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ちょっと暑いし\n氷の上を滑って\n涼もうかしら", 1, 1, 0, 0, 2, 9245, 135101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "そろそろ運送ギルドの\nアルバイトの時間だわ\n準備しなきゃ", 1, 1, 0, 0, 3, 9246, 135101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "エリスさん、水着が\nすごく似合ってた…\nどこで買ったのかしら", 1, 1, 0, 0, 4, 9247, 135101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "未練が残らないように\nこの夏休みでやりたい\nことはやらなくちゃ", 1, 1, 0, 0, 5, 9248, 135101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "かき氷を作ったら\n彼も食べるかしら？\nアイスクリームの\nほうがいいかな…", 1, 4, 0, 0, 101, 9249, 135101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "水着を着ていれば\nどこにいても\nリゾート気分だわ♪", 1, 8, 0, 0, 102, 9250, 135101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夏はおいしいものが\nたくさんあるし\n【美食殿】の活動も\n忙しくなりそうね♪", 1, 1, 0, 0, 1, 9251, 135101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "わ、私の尻尾…\nそんなに気になる？\nつついたらダメよ\nいいわね…？", 1, 1, 0, 0, 2, 9252, 135101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夏は日が長いし\nまだまだたくさん\n遊べそうね♪", 1, 1, 0, 0, 3, 9253, 135101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日は一緒に海に\n行かない？\nあなたも水着\n持ってきてるでしょ", 1, 1, 0, 0, 4, 9254, 135101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そろそろ日焼け止めを\n塗り直さなきゃ…あの\nお願いしてもいい…？", 1, 4, 0, 0, 101, 9255, 135101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "私たちで最高の\n夏休みにしましょう\nね、おにーたん♪", 1, 8, 0, 0, 102, 9256, 135101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夏の朝は早いわね\nもうセミも鳴いてるし\nあなたも早起きだわ…\n見習わなくちゃ", 1, 1, 1, 0, 1, 9257, 135101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "日中は暑いだろうし\nゆっくりしましょう\nお昼寝タイムはどう？", 1, 1, 2, 0, 2, 9258, 135101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この時間のアイス\nクリームは格別よね♪\n何味にしようかしら", 1, 1, 3, 0, 3, 9259, 135101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "暑い夏はしっかり\n休むことが大切\nふぅ…いい気持ち♪", 1, 1, 0, 0, 2, 9261, 135101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふわふわの毛並み\n暑くないのかしら\nかわいいだけじゃなく\n忍耐強くもあるのね", 1, 1, 0, 0, 3, 9262, 135101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "爽やかでいい匂い…♪\nジトジトしがちの夏に\nピッタリなアロマね", 1, 1, 0, 0, 4, 9263, 135101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ずっと水着でいると\n流石に冷えるわね\nうん、いい心地だわ", 1, 1, 0, 0, 5, 9264, 135101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "はむっはむっ…\nしっかり食べて\n夏バテ対策しないと", 1, 1, 0, 0, 7, 9266, 135101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふむふむ…面白いわね\n今度プレシアちゃんと\n一緒に読もうかしら", 1, 1, 0, 0, 8, 9267, 135101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "私のフロートと少し\n似ている気がする\nサイズ的にはこっちが\nお兄さんかしら…ふふ", 1, 1, 0, 0, 10, 9269, 135101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふふ、きれい♪\nまるで\n海のステージね！", 1, 1, 0, 0, 11, 9270, 135101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "じゅんび、おっけー\nうみでも\nおふろでも\nおんせんでも", 1, 1, 0, 0, 1, 9271, 135201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ひまわり。すき\nおっきくて\nたね、うまし", 1, 1, 0, 0, 2, 9272, 135201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ハッピーちゃん\nいい、におい\nひやけ？\nやきぶた？", 1, 1, 0, 0, 3, 9273, 135201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "じーわ、じーわ…\nせみの、なきごえ\nきいてる、だけで\nあつい。かも…", 1, 1, 0, 0, 4, 9274, 135201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "アイス、かきごおり…\nあつい、なつ\nだから\nひえひえ、ひっす", 1, 1, 0, 0, 5, 9275, 135201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "おっきくなるまほー\nきわめたい\nお兄を\nだっこ。するよ", 1, 4, 0, 0, 101, 9276, 135201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "「あなた」\n…ちょっと、ちがう？\nシェフィたち\nみたいに、ならない…", 1, 8, 0, 0, 102, 9277, 135201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "じゃりじゃり\nべたべた\n…シャワー\nいって、きます", 1, 1, 0, 0, 1, 9278, 135201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "（すんすん）\nお兄。ほんのり\nうみの、におい\nおいしそう", 1, 1, 0, 0, 2, 9279, 135201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お兄。みて\nべろ、まっさお\nぶるーはわい\nカラー", 1, 1, 0, 0, 3, 9280, 135201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あつくても\nあついもの\nおいしい\n…バーベキュー…", 1, 1, 0, 0, 4, 9281, 135201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ついんて\nバランス、むずかし\nお兄\nリボン、よろ", 1, 4, 0, 0, 101, 9282, 135201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "みずぎ\nにあってる。って\nお兄。いった\nもっと。みて？", 1, 8, 0, 0, 102, 9283, 135201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよ\nあさごはん\nやきざかな", 1, 1, 1, 0, 1, 9284, 135201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おひる\nおひるね\nいっしょに、しよ？", 1, 1, 2, 0, 2, 9285, 135201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おそと\nまっくら\nはなび\nしよ？", 1, 1, 3, 0, 3, 9286, 135201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふわぁ…\nあそびすぎ\nおつかれ\nねむねむ", 1, 1, 0, 0, 2, 9288, 135201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "よしよし\nおるすばん\nおねがい。ね", 1, 1, 0, 0, 3, 9289, 135201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "（くんくん）\nれもんの、におい\nじゅる", 1, 1, 0, 0, 4, 9290, 135201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "さかな、くしやき\nすたんばい、おっけー\nいつでも、やける。よ", 1, 1, 0, 0, 5, 9291, 135201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いっぱい、あそびたい\nいっぱい、つよくなる\nいっぱい、たべる", 1, 1, 0, 0, 7, 9293, 135201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "おさかなの、ほん\nおもしろかった\nでも\nおなかが…", 1, 1, 0, 0, 8, 9294, 135201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "れっつ、ごー\nすいへーせん\nせかい\nいっしゅー", 1, 1, 0, 0, 10, 9296, 135201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "きぶんは、かいばしら\nバターやき\nおすすめ", 1, 1, 0, 0, 11, 9297, 135201);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "成長して着れなく\nなっちゃう前に\nこの水着でできること\n全部しちゃいたいな♪", 1, 1, 0, 0, 1, 9298, 135301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "お家で水着だと\n開放的な気分かも♪\nふふ、お兄さんにも\n教えてあげなきゃ♪", 1, 1, 0, 0, 2, 9299, 135301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "泳ぎの練習しちゃお♪\nすいすい、すいすいー\n…ちょっと、\n子供っぽかったかも…", 1, 1, 0, 0, 3, 9300, 135301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "インドア派だけど\n夏の屋外は好き…や…\nお兄さんと一緒なら\nどこでも…", 1, 1, 0, 0, 4, 9301, 135301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "夏だからってちょっと\n羽目外しすぎかな…\nわたし…やっぱり\n真面目じゃない…かも", 1, 1, 0, 0, 5, 9302, 135301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "わたしの水着姿…\n少しは大人っぽいって\n思ってくれたかな…？", 1, 4, 0, 0, 101, 9303, 135301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "家の中は涼しいのに\nお兄さんのこと\n考えると暑くなって…\n夏バテしちゃいそう…", 1, 8, 0, 0, 102, 9304, 135301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お兄さんと一緒なら\n夏休みの日記\n書くことが多すぎて\nページが足りません♪", 1, 1, 0, 0, 1, 9305, 135301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "かき氷食べましょ♪\n…ふふ、お兄さん\n舌の色すごいですよ♪\nわたしも…べ～っ♪", 1, 1, 0, 0, 2, 9306, 135301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今日は海に行きます？\nそれともお家で\nのんびりですか？\nわたしはどっちでも♪", 1, 1, 0, 0, 3, 9307, 135301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夏バテしないように\nスタミナ料理のレシピ\nお勉強しました♪", 1, 1, 0, 0, 4, 9308, 135301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夏が終わっちゃっても\nこの水着を着てたら\n夏の思い出、忘れずに\nいてくれますか…？", 1, 4, 0, 0, 101, 9309, 135301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "…この日焼け跡\nお兄さんと\nわたしだけの\n秘密…ですよ？", 1, 8, 0, 0, 102, 9310, 135301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようございます！\n朝の体操１、２、３♪\nしゃきっと目覚めて\nいい一日にしましょ♪", 1, 1, 1, 0, 1, 9311, 135301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お昼休憩ですね♪\n午後からも頑張れる様\nお兄さんのこと\n癒しますね…♪", 1, 1, 2, 0, 2, 9312, 135301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "そろそろ寝る時間…\nお兄さんが眠るまで\n一緒にいますね…\n…ふあぁ…", 1, 1, 3, 0, 3, 9313, 135301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "気持ちいいけど、汗…\n染みちゃわないように\n気をつけないと…", 1, 1, 0, 0, 2, 9315, 135301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ぎゅう～♪\nなんて、ちょっと\nあざといかな…♪", 1, 1, 0, 0, 3, 9316, 135301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "夏にぴったりの\nフルーティーな香り♪\nお兄さん、好きかな？", 1, 1, 0, 0, 4, 9317, 135301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "水着で暖まるのって\nなんだか贅沢な気分♪\nこたつでアイス的な♪", 1, 1, 0, 0, 5, 9318, 135301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "お腹ぽっこりに\nならないよう\n気をつけないと…\nでも止まらない…", 1, 1, 0, 0, 7, 9320, 135301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "弾ける水着でビーチの\n視線を独り占め…かぁ\nわたしはお兄さんの\n視線だけで…なんて♪", 1, 1, 0, 0, 8, 9321, 135301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これで夏の大冒険に\nしゅっぱ～つ！\n…なんちゃって♪", 1, 1, 0, 0, 10, 9323, 135301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "なんだか女神さま\nみたい…？\nふふ、わたしには\n似合わないかもです♪", 1, 1, 0, 0, 11, 9324, 135301);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "真っ白い日差し─\nジオ・ゲヘナには\nなかったものですね\n─悪くはないです", 1, 1, 0, 0, 1, 9325, 135401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "しばらくここで\n休んでます\n人の多い街中よりは\n落ち着くので─", 1, 1, 0, 0, 2, 9326, 135401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここにも─\nあるんでしょうか\n海で使う遊び道具…", 1, 1, 0, 0, 3, 9327, 135401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "見張りは─\nここならあまり\n必要なさそう…", 1, 1, 0, 0, 4, 9328, 135401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "夏のバカンス─\n媛が楽しんでるなら\n私は十分です", 1, 1, 0, 0, 5, 9329, 135401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "知らない相手は、\n苦手だけど─\n…少しだけ、\n話してみても…", 1, 4, 0, 0, 101, 9330, 135401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここにいると\n安心する、ような…\n土壌が─良い…？", 1, 8, 0, 0, 102, 9331, 135401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夏の正装、水着─\n着方、間違えて\nないですよね…？", 1, 1, 0, 0, 1, 9332, 135401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "相変わらず、\nおしゃべりが\n好きなんですね\n私は─普通です", 1, 1, 0, 0, 2, 9333, 135401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "─喉が渇きました\n…いえ、花にお水が\n欲しいわけじゃなくて", 1, 1, 0, 0, 3, 9334, 135401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "─いつもより\n花がつやつやしてる？\n…あ、褒めて\nくれたんですか", 1, 1, 0, 0, 4, 9335, 135401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "この部屋─\n貴方が飾りつけを？\nわかります、何となく", 1, 4, 0, 0, 101, 9336, 135401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "すぐ傍にいると、\n暑くなりませんか？\n─私は別に\nかまいませんが", 1, 8, 0, 0, 102, 9337, 135401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "朝から元気ですね─\nおかげですっかり\n目が覚めました", 1, 1, 1, 0, 1, 9338, 135401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "外─暑そうです\n出かけるなら\n気を付けてください\n倒れたりしないよう…", 1, 1, 2, 0, 2, 9339, 135401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "─貴方も\n夜更かしですか？\n…少しだけなら\n付き合いますよ", 1, 1, 3, 0, 3, 9340, 135401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "貴方もここで\n休みたいですか？\n─ダメです\n今は私が使うので", 1, 1, 0, 0, 2, 9342, 135401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ゆるい顔つきですね\nジャングルでは\n生き残れそうに\nないです─", 1, 1, 0, 0, 3, 9343, 135401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "─ん…\nこれ、海の香り？\n─いい匂い…", 1, 1, 0, 0, 4, 9344, 135401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "…暑さには\n慣れてますよ\n寒いのよりは、ずっと", 1, 1, 0, 0, 5, 9345, 135401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "栄養は、大事です\n味は─あまり\n気にしたこと、\nありませんでした", 1, 1, 0, 0, 7, 9347, 135401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "適当に本を読んでいる\nだけです─\n…別に、遊びについて\n調べたりしてません", 1, 1, 0, 0, 8, 9348, 135401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "魔物を模した\n人形─ですね\n戦闘訓練用に？", 1, 1, 0, 0, 10, 9350, 135401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "貝のクッションですか\n─噛みついてきたり\nしないですよね", 1, 1, 0, 0, 11, 9351, 135401);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "シントーメッキャク\n火もまた涼し…\nむむむ…やっぱり夏は\n暑いデース！", 1, 1, 0, 0, 1, 9352, 137101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "海にも新しい忍法の\nヒントがあるはずデス\nショーグンと海底散歩\nも楽しそうデス", 1, 1, 0, 0, 2, 9353, 137101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "釣りの心得は\nメイキョーシスイ…\n忍者の修行に\nうってつけデス♪", 1, 1, 0, 0, 3, 9354, 137101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "天下統一の後のことも\n考えねばなりまセン\n今度はテンジョー統一\nデスかね…？", 1, 1, 0, 0, 4, 9355, 137101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "デケデケデン♪\nテルテル坊主完成デス\n晴れてないとお外で\n遊べまセンからね！", 1, 1, 0, 0, 5, 9356, 137101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "次のオモテナシも\nショーグンをワクワク\nさせマス♪　今から\n用意しておかねばデス", 1, 4, 0, 0, 101, 9357, 137101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "新しい忍法の開発に\n休みはないのデス！\nショーグンの天下統一\nを思えばなんのその！", 1, 8, 0, 0, 102, 9358, 137101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "海の前に準備運動デス\nまずは手を後ろに、\n素早く忍者走り！\nシュバババッ", 1, 1, 0, 0, 1, 9359, 137101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夏にお祭りは\n欠かせないデスよね♪\n地域のイベント情報は\n見逃しまセン！", 1, 1, 0, 0, 2, 9360, 137101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ショーグン、団扇で\nあおいであげマス！\n忍法・扇風の術ー！\nうりゃりゃりゃー！", 1, 1, 0, 0, 3, 9361, 137101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ワタシが必要な時は\n風鈴を鳴らすデス！\n風よりも早く\n駆けつけマスよ！", 1, 1, 0, 0, 4, 9362, 137101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "じっと見つめられると\n顔が熱くなってきて…\nはっ、ショーグンの\n新しい忍法デスか！？", 1, 4, 0, 0, 101, 9363, 137101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ショーグン、日焼けは\nノン、デスよ！\nワタシがクリームぬり\nぬりしてあげマース！", 1, 8, 0, 0, 102, 9364, 137101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはようデス！\n朝ごはんはワタシに\nお任せデス。\n毎日みそ汁作るデス！", 1, 1, 1, 0, 1, 9365, 137101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "日差しに注意デスよ\nネッチューショーに\n日焼け、どっちも\n大変デスからね！", 1, 1, 2, 0, 2, 9366, 137101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ショーグン、\n外で花火しまショウ！\nぱちぱちキレイで\n楽しいデスよ！", 1, 1, 3, 0, 3, 9367, 137101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "遊びに備えて\n今は休む時…\nガシンショータンデス", 1, 1, 0, 0, 2, 9369, 137101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この子たちにも\n手伝ってもらって\n新しい忍法を開発\nしてみまショウ♪", 1, 1, 0, 0, 3, 9370, 137101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "むむっ、ここまで\n潮の香りが漂って\nきた気がしマス！", 1, 1, 0, 0, 4, 9371, 137101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "海に入った後は\n体を温めねば\nやはりサウナも\n必要デスね！", 1, 1, 0, 0, 5, 9372, 137101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "いっぱい食べて\nいっぱい遊ぶデス！\n夏バテなんかに\n負けてられまセン！", 1, 1, 0, 0, 7, 9374, 137101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "『BUSHIDO』の\n最新刊が待ち遠しい\nデス…マイゾーキン\n特集、読みたいデス", 1, 1, 0, 0, 8, 9375, 137101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ショーグンとこの子に\n乗って、天下統一まで\nシュババババッ！\nなんてどうでショウ", 1, 1, 0, 0, 10, 9377, 137101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ぽよんぽよ～ん♪\nショーグンと一緒に\n遊んでみたいデス♪", 1, 1, 0, 0, 11, 9378, 137101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "今度はシナツちゃんの\n世界へ行ってみたいな\nエリスちゃんも誘って\n大人女子旅とか最高♪", 1, 1, 0, 0, 1, 9379, 135501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あっはっはー！\nよし、今日も絶好調！\nま、いつも笑ってたら\n不調なんてないけど♪", 1, 1, 0, 0, 2, 9380, 135501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ん～あっちの世界と\nほとんど一緒だね！\n違ってるところ探しに\n出かけてみよっかな？", 1, 1, 0, 0, 3, 9381, 135501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ううーん、のんびり\nするのもいいけど\nどこか行ってみたいな\n人助けとか、ね", 1, 1, 0, 0, 4, 9382, 135501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あっ、そーだ\n向こうへ帰る前に\nこっちのお土産とか\n買っとかなくちゃ", 1, 1, 0, 0, 5, 9383, 135501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ふ～ん、騎士くんって\nこういうのが好みか～\n後でヒヨリちゃんに\n教えてあげよ♪", 1, 4, 0, 0, 101, 9384, 135501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "次はあたしが恩を返す\n番だね。騎士くんに\n助けが必要になったら\n絶対駆けつけるよ！", 1, 8, 0, 0, 102, 9385, 135501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あっ騎士くん！\n困ってることない？\nお姉さんがなんでも\n力になっちゃうよ！", 1, 1, 0, 0, 1, 9386, 135501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "また闘技場に行こうよ\nちょっと運動不足でさ\nバーンと暴れたい気分\nなんだよね♪", 1, 1, 0, 0, 2, 9387, 135501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "いつか大人になったら\n一緒に飲もうよ！\nあははっ、騎士くん\n指切りげんまん♪", 1, 1, 0, 0, 3, 9388, 135501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "一緒に作った麦しゅわ\nカップ、最高だね♪\n騎士くんの分もどうぞ\nちゃんとジュースだよ", 1, 1, 0, 0, 4, 9389, 135501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ねえ…ぶっちゃけ\nヒヨリちゃんのこと\nどう思ってるのかな？\nお姉さんに聞かせなよ", 1, 4, 0, 0, 101, 9390, 135501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "騎士くん、うっかり\n飲みすぎたあの日の\nコトは誰にも内緒で\nお願いね。約束だよ！", 1, 8, 0, 0, 102, 9391, 135501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あはは、騎士くん\n新しい朝だよー！\n今日はどんな一日か\nワクワクするね～♪", 1, 1, 1, 0, 1, 9392, 135501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "午後からも笑顔で\n元気に乗り切ろうね！\n外に行くもよし、\n部屋で麦しゅわもよし", 1, 1, 2, 0, 2, 9393, 135501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ここからは大人の時間\n騎士くんにはまだ少し\n早いかな～？\nだから、おやすみ♪", 1, 1, 3, 0, 3, 9394, 135501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "麦しゅわ飲み過ぎた\n夜はここにダーイブ\n…なんて大人に\nなっちゃダメだよ～？", 1, 1, 0, 0, 2, 9396, 135501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あたしの世界の子たち\nこういうの好きかな？\nお土産に探してみよう\n気に入るかも♪", 1, 1, 0, 0, 3, 9397, 135501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ん～いいね♪\nあとでお気に入りの\nフレーバーも\n試してみよっかな？", 1, 1, 0, 0, 4, 9398, 135501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ほうほう…熱い！\nサラサリアは\nいつも暖かいから\nこういうの新鮮！", 1, 1, 0, 0, 5, 9399, 135501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ん～おいしい！\nけどやっぱご飯は\nみんなで一緒に\n食べたいな～", 1, 1, 0, 0, 7, 9401, 135501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "文字ばっかの本は\nちょーっと苦手かも…\n必要なら読むけどさ？", 1, 1, 0, 0, 8, 9402, 135501);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "皆思い思いに過ごして\nいるのに、不思議と\n落ち着く。いつまでも\nここにいられたら…", 1, 1, 0, 0, 1, 9406, 135601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "皆に挨拶をせねば\n…いや、今は、まだ\nタイミングが悪い…\nまた今度にしよう…", 1, 1, 0, 0, 2, 9407, 135601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "私の服装…この場で\n浮いてないだろうか\nまずい、心配に\nなってきた…！", 1, 1, 0, 0, 3, 9408, 135601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "アイラたちとの旅行も\n楽しかったな…\nまた３人で温泉に\nいきたいものだ", 1, 1, 0, 0, 4, 9409, 135601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ここで剣の稽古は\nさすがによろしくない\n代わりに今日は瞑想を\nすることにしよう", 1, 1, 0, 0, 5, 9410, 135601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "人とのつながりは\n大切にしなければ\n今日こそは誰かに\n話しかけるぞ…！", 1, 4, 0, 0, 101, 9411, 135601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "できることが増えて、\n迷うばかりの毎日…\nそれが楽しく尊いと\n今実感している…", 1, 8, 0, 0, 102, 9412, 135601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ええと…奇遇だな\nこんなところで\n会うなんて。いや違う\n…その、こんにちは…", 1, 1, 0, 0, 1, 9413, 135601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "弟子たちへの土産も\n探しておかないとな\nよければキミからも\nアドバイスを頼む…", 1, 1, 0, 0, 3, 9415, 135601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "剣の稽古をしたい？\nふっ…いいとも\nキミとならいくらでも\n準備をしてすぐ戻る", 1, 1, 0, 0, 4, 9416, 135601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "よければまた、\n街を案内してくれ\n名所旧跡もだがキミと\n歩くのは楽しいんだ", 1, 4, 0, 0, 101, 9417, 135601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "感謝の心を\n忘れたことはないよ\nキミたちの優しさに\n救われた一人として…", 1, 8, 0, 0, 102, 9418, 135601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "うむ、いい顔だな\n良い一日は、\n良い目覚めから始まる\n心掛けておくといい", 1, 1, 1, 0, 1, 9419, 135601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "もう昼時か\n誰かを誘って食事に…\n…いや無理だ、\nハードルが高い…！", 1, 1, 2, 0, 2, 9420, 135601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜はいい…\n誰とも話さず\n一人きりでいても、\n不自然ではないからな", 1, 1, 3, 0, 3, 9421, 135601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "…いかんな、これは\n気を抜くとこのまま\n離れられなくなる…", 1, 1, 0, 0, 2, 9423, 135601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "シナツだ、よろしく\n……ふぅ\n人形相手の練習なら\n緊張しないのだがな…", 1, 1, 0, 0, 3, 9424, 135601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ふむ…集中力が\n高まりそうな香りだな\n稽古にも役立つだろう\n皆にも教えてやろうか", 1, 1, 0, 0, 4, 9425, 135601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "凍える人に寄り添い\n温もりを与える…\n私もそんな存在に\nなりたいものだ", 1, 1, 0, 0, 5, 9426, 135601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "食の楽しみ…\nあまり意識して\nいなかったが、\nやはりいいものだな", 1, 1, 0, 0, 7, 9428, 135601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "…っと、いかん\n読みふけっていたら\nいくらでも時間を\n過ごせてしまうぞ…", 1, 1, 0, 0, 8, 9429, 135601);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "また空を飛びたいな～\nふわふわ～ってなる\nあの感じが気持ちいい\nんだよね～♪", 1, 1, 0, 0, 1, 9433, 135701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "またサラサリアに\n行きたいなー\n…串焼きの味が\n忘れられなくって♪", 1, 1, 0, 0, 2, 9434, 135701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "悩んでる人がいたら\n背中を押してあげたい\nあたしがそれで、勇気\nづけられたみたいに", 1, 1, 0, 0, 3, 9435, 135701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "気持ちを言葉にするの\n大事って分かってても\nいざとなると緊張して\n上手くできないなぁ…", 1, 1, 0, 0, 4, 9436, 135701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あっちの世界でも\nギルドハウスは\nあるのかな…？\n今度探してみよっと♪", 1, 1, 0, 0, 5, 9437, 135701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "アイラに会って少し\n成長できたかも…！\n素敵な女の人目指して\nもっと頑張るぞーっ♪", 1, 4, 0, 0, 101, 9438, 135701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あっちのあたしは\n騎士くんに選ばれた…\nよし、あたしも\n頑張らなくっちゃ…！", 1, 8, 0, 0, 102, 9439, 135701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "部屋の模様替えする？\nサラサリア風の家具も\nあったら素敵だよね！", 1, 1, 0, 0, 1, 9440, 135701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "なんだかお疲れ気味？\nそうだっ、サラサリア\nで教わったマッサージ\nをしてあげるっ♪", 1, 1, 0, 0, 2, 9441, 135701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "騎士くんっ！\n今からあたしとデート\nしませんかっ！", 1, 1, 0, 0, 3, 9442, 135701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "今ちょうど、踊りの\n練習してたとこなの\n騎士くんも\n一緒にどう？", 1, 1, 0, 0, 4, 9443, 135701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "き、騎士くん！\nこの前の続き…\n手、繋いでいい…？", 1, 4, 0, 0, 101, 9444, 135701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "えへへへ、騎士くんが\nあたしの背中を押して\nくれると、勇気も\n100万倍だよっ♪", 1, 8, 0, 0, 102, 9445, 135701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おはよ、騎士くん！\n今日もいい朝だよ！\nサラサリアみたいに\n太陽が輝いてるもん！", 1, 1, 1, 0, 1, 9446, 135701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "フレフレ、騎士くん！\n午後もファイトー！\nあたしは日陰でお昼寝\nタイム…なんてねっ♪", 1, 1, 2, 0, 2, 9447, 135701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "夜に騎士くんといると\n空飛ぶ絨毯から見た\nサラサリアの夜景を\n思い出しちゃうな♪", 1, 1, 3, 0, 3, 9448, 135701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "休憩きゅうけ～い♪\n強い人と戦った後は\nしっかりと体を\n休ませないとね", 1, 1, 0, 0, 2, 9450, 135701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "にゃ～、\nかわいいにゃ～♪\nアンブラを\n思い出しちゃうよ♪", 1, 1, 0, 0, 3, 9451, 135701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "わっ…すごくいい\n匂い～サラサリアの\n砂漠の匂いにちょっと\n似てるかも", 1, 1, 0, 0, 4, 9452, 135701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "サラサリアの太陽\nみたいに暖かい♪\n日向ぼっこしてる\nみたいでしあわせ…", 1, 1, 0, 0, 5, 9453, 135701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ん～おいしいっ！\nアイラもきっと\n好きな味だね…！\n今度食べてもらおっ！", 1, 1, 0, 0, 7, 9455, 135701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "これは恋愛指南本…！\nふむ…意中の男性と\nキスするには…って\nこれはまだ早いよ～！", 1, 1, 0, 0, 8, 9456, 135701);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あてがここにいたら、\nみんな寒い思いを\nしちまうかな…\nご、ごめんな", 1, 1, 0, 0, 1, 9460, 135801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あて、みんなより\n体でっけぇし…\nもうちょっと隅っこ\n行っとこうかな", 1, 1, 0, 0, 2, 9461, 135801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "さ、騒がしいとこは\n苦手だども…\nここはそんなに\n気になんね…", 1, 1, 0, 0, 3, 9462, 135801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "いろいろ勉強してぇな\nイナカもんだから、\nみんなに迷惑とか\nかけねぇように…", 1, 1, 0, 0, 4, 9463, 135801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "家具とか、飾りとか、\n壊しちまったら大変…\nき、気ぃつけねぇとっ", 1, 1, 0, 0, 5, 9464, 135801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "あ、あての三味線、\n聴きたいの…？\nこ、心の準備が\nできてからで、い…？", 1, 4, 0, 0, 101, 9465, 135801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "っ、ぁぶね…\nあてったらまた、\n騎士さのこと見て、\n溶けそうになってた…", 1, 8, 0, 0, 102, 9466, 135801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お、おめさ、その…\nおやつさん、いらね？\nかき氷とかなら、\nあて、作れるよ…？", 1, 1, 0, 0, 1, 9467, 135801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "あ、あてに…何か用？\n…声、聴きたかった？\nな、なら…えっと…\nおしゃべり…する？", 1, 1, 0, 0, 2, 9468, 135801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "こっちさ来てから…\n向こうの世界の\n何年分も、おしゃべり\nしたかもしんね…", 1, 1, 0, 0, 3, 9469, 135801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "こっちの世界には、\n氷滑りって遊びが\nあるって…？\nあてにもできっかな…", 1, 1, 0, 0, 4, 9470, 135801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "ん…あては元気だよ\n悩みがあっても、\nおめさの声もらうと\n吹っ飛んじまうし…", 1, 4, 0, 0, 101, 9471, 135801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "騎士さが近くに\nいてくれる…\n今は、それだけで\nいいんだ…うん…", 1, 8, 0, 0, 102, 9472, 135801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "お、おはよ…\nおめさのおかげで、\n昨夜もよく眠れたよ…", 1, 1, 1, 0, 1, 9473, 135801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "…あっ、もう昼飯の\n時間かぁ…\n考えごとしてたら\nあっという間だよ", 1, 1, 2, 0, 2, 9474, 135801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "景色は似てるけど…\nこの世界の夜は、\nざわざわ音がして\n賑やかだな…", 1, 1, 3, 0, 3, 9475, 135801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あんま縮こまってると\n体硬くなっちまうし…\nたまにはのんびり…", 1, 1, 0, 0, 2, 9477, 135801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "えへへ、めんこいな…\nそだ、仁太坊たちも\n並んでたら\nもっとめんこいかも…", 1, 1, 0, 0, 3, 9478, 135801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "この匂い…なんか、\nは、はいから…？\nあて、イナカもん\nだから…", 1, 1, 0, 0, 4, 9479, 135801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "あ、暑くて溶けそう…\nだども、我慢できたら\n修行になる…かも…？", 1, 1, 0, 0, 5, 9480, 135801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "ん…♪　うめぇ…\nこんなうめぇもん\n毎日食べられるの、\n幸せだなぁ…", 1, 1, 0, 0, 7, 9482, 135801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こっちの世界にも…\n雪女が出る物語、\nあるみてぇだな…\nか、悲しい話だども…", 1, 1, 0, 0, 8, 9483, 135801);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "なかなか上等な\n家じゃねェか\nちっとばかし\n軒を借りるぜ", 1, 1, 0, 0, 1, 9487, 135901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "俺の棒っきれの\n置き場所は…\nま、玄関にでも\n立てかけときゃいいか", 1, 1, 0, 0, 2, 9488, 135901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "こっちの世界は\n眩しくって敵わねェぜ\n目がチカチカしやがる", 1, 1, 0, 0, 3, 9489, 135901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "建物の様式が同じでも\n世界と住人が違えば\n印象も変わるモンだな", 1, 1, 0, 0, 4, 9490, 135901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "街の連中は\n戦いなんて知らねェ\nのんきなツラしてんな\n…いいトコじゃねェか", 1, 1, 0, 0, 5, 9491, 135901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "騎士公のヤツ、\nまたぞろ厄介事に\n巻き込まれや\nしてねェだろうな…", 1, 4, 0, 0, 101, 9492, 135901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, "ふわぁ…\nこうも居心地がいいと\n眠くなっちまうぜ", 1, 8, 0, 0, 102, 9493, 135901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "俺ばっかり\n見てんじゃねェよ\n赤鬼になっちまわぁ", 1, 1, 0, 0, 1, 9494, 135901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "注いでくれんのか？\nあんがとよ\nんじゃ俺からも\nお返しだぜ", 1, 1, 0, 0, 2, 9495, 135901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "おう、暇してるなら\n鍛錬に付き合えよ\nじっとしてっと\n体がなまって仕方ねェ", 1, 1, 0, 0, 3, 9496, 135901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "退屈してきたな…\n散歩にでも行くか？\n騎士公なら退屈しねェ\n場所知ってるだろ", 1, 1, 0, 0, 4, 9497, 135901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "女との関係は\nきちっとしとけよ\n俺だって助けらんねぇ\nもんはあるからな", 1, 4, 0, 0, 101, 9498, 135901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "角に触りたいだぁ？\n…まあ、騎士公の\n頼みなら…\nあんま手荒にすんなよ", 1, 8, 0, 0, 102, 9499, 135901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "起きろ\n朝の鍛錬の時間だぜ\nほれ、さっさと\n身支度しちまいな", 1, 1, 1, 0, 1, 9500, 135901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "昼飯ィ？\n俺の分はいらねぇよ\n気ィつかわねぇで\nくんな", 1, 1, 2, 0, 2, 9501, 135901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, "まだ起きてんのか？\nさっさと寝ねェと\nこうやって鬼が来るぜ", 1, 1, 3, 0, 3, 9502, 135901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "悪くない座り心地だな\n気ィ抜くと、ダメに\nされちまいそうだぜ", 1, 1, 0, 0, 2, 9504, 135901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "…今こいつ動いたか？\nグレ公あたりが\n悪さしてんじゃ\nねェだろうな", 1, 1, 0, 0, 3, 9505, 135901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "騎士公はこういう\n香りが好みか？\nお姫とユキノに\n教えてやるとすっか", 1, 1, 0, 0, 4, 9506, 135901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "地獄の釜よりかは\nちとぬるいが…\nなかなか\nいい火力してやがるぜ", 1, 1, 0, 0, 5, 9507, 135901);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, "こっちの世界の本にも\n鬼が出てくるのか", 1, 1, 0, 0, 8, 9510, 135901);
