INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3, "リズムを意識して…\nわんつーわんつー！\nうん、調子はバッチリ", 100101, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4, "ここで特訓していい？\n物は壊さないように\n気をつけるね！", 100101, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5, "なんか落ち着かないや\nもしかして、\n困ってる人が\n近くにいるのかな…？", 100101, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6, "えへへ…いつも\n一緒にいてくれて\nありがとねっ！", 100101, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7, "ちゃんと覚えてるよ\n騎士くんの声…\nあの時はありがとね！", 100101, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8, "あははっ！\nやったなー！\nお返しだー！\nこちょこちょこちょ…", 100101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9, "どうしたの？\nもしかして困ってる？\nあたしにまかせて！", 100101, 2, 2);
