INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 13, "えへへ…\n騎士くんの手だ…\nあったかいな…♪", 100101, 2, 102);
