INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 89, "おはよう\nよく眠れた？　今日も\n一日、頑張ろうね♪", 100202, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 90, "お腹空いちゃった…\nそうだ。騎士クンも\n一緒にお昼食べない？", 100202, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 91, "もうこんな時間…\nそろそろ寝なくちゃ\nおやすみなさい", 100202, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 92, "よし…\nこれなら\n褒めてもらえるかな？", 100202, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 93, "がんばることも\n必要だけど\nお休みも大切だよね", 100202, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 94, "かわいい～♪\n騎士クンは\nこういうのって好き？", 100202, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 95, "戦う前にもこうやって\n落ち着けたら\nいいのに", 100202, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 96, "あの…！\nよ、良かったら\n一緒に…！", 100202, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 97, "騎士クンと一緒に\n冒険するなら\n体力がないと…！", 100202, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 98, "最近服のサイズが\n合わないんだけど…\n食べ過ぎなのかな？", 100202, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 99, "…やっぱり\n月刊恋愛指南は\nためになる…", 100202, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 101, "装備の手入れでも\nしようかな…", 100301, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 102, "次に鍛えるべきは\n肉体面か精神面か\nどちらにしよう…？", 100301, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 103, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？", 100301, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 104, "仲間なんていらない\nそう思ってた頃も\nあったな…", 100301, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 105, "考えごとをしていると\nつい部屋中を\n歩いてしまうな", 100301, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 106, "彼は\nどこまで強くなるのか\n私も負けられないな", 100301, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 107, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？", 100301, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 108, "ああ、キミだったのか\n人の気配がしたから\n誰かと思ったよ", 100301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 109, "斬りかかるつもりなら\nちゃんとかかってきて\n…呼んだだけ？", 100301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 110, "なに？　私に相談？\n戦いについてなら\n喜んで", 100301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 111, "今のが突き？\n軽い！　もっと鋭く！\nさあ、もう一回！", 100301, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 112, "ひゃっ！　ど、どこを\n触ってるの！？\n…油断した", 100301, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 113, "キミにはこれからも\n私のそばに…\nいや、なんでもない", 100301, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 114, "おはよう\n今日も充実した\n一日にしていこう", 100301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 115, "そろそろ\nお茶の時間か\nキミも一緒にどう？", 100301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 116, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？", 100301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 117, "戦いを経てもなお\n美しくあるように…\nこれでよし♪", 100301, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 118, "戦ってばかりで\n休まないと、また\nユイに怒られるな", 100301, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 119, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう", 100301, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 120, "これはいい…\n戦いの前の精神統一に\nもってこいじゃないか", 100301, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 121, "時々は落ち着いた\n時間を過ごすのも\n悪くない…", 100301, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 122, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！", 100301, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 123, "食事は大切だけど\n食べ過ぎには\n注意しておこう…", 100301, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 124, "ユイ…！\nこ、こんな本を\n読んでいたのか…！", 100301, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 126, "海だけじゃなくて\n川でも釣りがしたいな\n道具も新調しよう", 100302, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 127, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？", 100302, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 128, "彼は\nどこまで強くなるのか\n私も負けられないな", 100302, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 129, "そろそろ修行の\n内容を新しくしよう\nより厳しいものにね", 100302, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 130, "強くなって…誰にも\n妨げられない自由な\n生き方がしたい", 100302, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 131, "まさか風邪を引くとは\n彼の看病のおかげで\nすっかり良く\nなったけど", 100302, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 132, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？", 100302, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 133, "どうしたの？\nなんでもない？\nそれなら良かった", 100302, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 134, "あんまり突っつくと\nお返しするよ？\n覚悟は出来てる？", 100302, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 135, "ねぇ、今度また\n釣りに行かない？\n大きいのを狙おう", 100302, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 136, "修行がしたいの？\nそれを早く言ってよ\nすぐに出発しよう！", 100302, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 137, "ひょっとして\n今のも一本だなんて\n言わないよね？", 100302, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 138, "キミにはこれからも\n私のそばに…\nいや、なんでもない", 100302, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 139, "今日は仕事？\nそれとも修行？\n良ければ付き合うよ", 100302, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 140, "休息も時には\n必要だと、仲間から\n教わったよ", 100302, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 141, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？", 100302, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 142, "髪の長さを少し\n変えてみようかな…\nわ、私は何を…！", 100302, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 143, "休息があってこそ\n万全な状態で\n戦えるんだ", 100302, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 144, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう", 100302, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 145, "体と同様に\n心もほぐれるね…\nはぁ…", 100302, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 146, "休む時は休む\nヒヨリもそろそろ\n覚えて欲しいな…", 100302, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 147, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！", 100302, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 148, "修行のせいか\n最近食べる量が\n増えてきたような…", 100302, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 149, "ユイ…！\nこ、こんな本を\n読んでいたのか…！", 100302, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 151, "お宝の予感！\n部屋中を\n探検しちゃうぞ～", 100401, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 152, "落とし穴…どこに\n作ったんだっけ？", 100401, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 153, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな", 100401, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 154, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？", 100401, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 155, "かくれんぼする人\nこの指、と～まれ！", 100401, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 156, "ミソギ以外の人に\nイタズラされちゃ\nダメだよ！", 100401, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 157, "ミソギと遊ぶ？\nミソギに遊ばれる？", 100401, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 158, "いいこと思いついた！\nい、イタズラとかじゃ\nないよ～", 100401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 159, "うわっ！？\n　にいちゃんが、\nミソギにイタズラ？", 100401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 160, "にいちゃんの\n通りそうなところに\nバナナの皮を…", 100401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 161, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…", 100401, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 162, "ここ、ミソギ探検隊の\n基地にしようよ！", 100401, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 163, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪", 100401, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 164, "にいちゃん、おはよ！\n目覚まし時計を\n止めておいたのに、\nよく起きられたね～♪", 100401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 165, "にいちゃんが\n昼寝したら、\nイタズラし放題♪", 100401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 166, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？", 100401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 167, "イタズラには\nお色気も必要…\nうん、完璧！", 100401, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 168, "座ったら、ブーッって\nいう音が出たら、\nおもしろいな～♪", 100401, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 169, "かわいい！\nミソギ探検隊の\nマスコットにしよう♪", 100401, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 170, "吸うとくしゃみが\n止まらなくなるお香を\nここに…くしゅっ！", 100401, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 171, "にひひ…イタズラの\n証拠、ここで\n燃やしちゃおう～！", 100401, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 172, "探検家は\n体力勝負だよー！", 100401, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 173, "みんなに分けて\nあげようかな～\n激辛ソースも\n入れちゃうけどね！", 100401, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 174, "こういう所に\n宝の地図が\n隠れてるんだよね～", 100401, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 176, "お宝の予感！\n部屋中を\n探検しちゃうぞ～", 100402, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 177, "うぅ、探検に出たい！\nじっとしてなんか\nいられないよ～！", 100402, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 178, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな", 100402, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 179, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？", 100402, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 180, "かくれんぼする人\nこの指、と～まれ！", 100402, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 181, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…", 100402, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 182, "にいちゃんが喜ぶ\nイタズラ、何かない\nかな～？", 100402, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 183, "ミソギがしかけた\nイタズラ\n見つけられるかな？", 100402, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 184, "探検する？\n冒険する？\nそれとも…\nイタズラされる？", 100402, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 185, "将来の夢は探検家！\n探したい物はないけど", 100402, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 186, "にいちゃんに宿題！\n落とし穴を10個\n掘ること！", 100402, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 187, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪", 100402, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 188, "ここ、ミソギ探検隊の\n基地にしようよ！", 100402, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 189, "にいちゃん\n探検に行こうよ！\n今からなら遭難しても\n夜には帰れるよ！", 100402, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 190, "にいちゃんが昼寝したら\nイタズラし放題♪", 100402, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 191, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？", 100402, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 192, "イタズラには\nお色気も必要…\nうん、完璧！", 100402, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 193, "ゆっくりまったりして\n次のイタズラを\n考えよ～！", 100402, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 194, "かわいい！\nミソギ探検隊の\nマスコットにしよう♪", 100402, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 195, "吸うとくしゃみが\n止まらなくなるお香を\nここに…くしゅっ！", 100402, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 196, "にひひ…イタズラの\n証拠、ここで\n燃やしちゃおう～！", 100402, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 197, "探検家は\n体力勝負だよー！", 100402, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 198, "探検のおやつに\nぴったりかも～！", 100402, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 199, "こういう所に\n宝の地図が\n隠れてるんだよね～", 100402, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 201, "音楽は全部好きだけど\n一番好きなのは\nやっぱりジャズかな", 100601, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 202, "はぁ、はぁ…\n一人でもいっぱい\nしないと…\n特訓…", 100601, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 203, "お姉ちゃんには\nもっと自信を\n持ってほしいなぁ", 100601, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 204, "お姉ちゃんのマネ\nしちゃおうかな？", 100601, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 205, "双子って、\n好みのタイプも\n似ちゃうのかな？", 100601, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 206, "お兄ちゃんと私は\n相性抜群です♪", 100601, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 207, "もう待ちきれない…\nお兄ちゃん、\n早く帰って来て！", 100601, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 208, "あんっ…着替えよう\nと思ったのに\nお兄ちゃんのエッチ！", 100601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 209, "うふふ、お兄ちゃん！\n今日は何を\nするんですか？", 100601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 210, "そんなところ\n触っちゃダメ♪", 100601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 211, "お兄ちゃん、イジワル\nしないでください…", 100601, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 212, "お兄ちゃん、もっと\n上級編のテクニック、\n教えてくださいね！", 100601, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 213, "コーチ、\n今日も特訓\nお願いします！", 100601, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 214, "おはようございます\n昨夜は楽しかった\nですね、うふふ♪", 100601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 215, "こんな明るい時間から\nす、するんですか…\n二人っきりの特訓…", 100601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 216, "夜はこれからですよ\nお兄ちゃん♪", 100601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 217, "世界一、かわいく\nなるんだから♪", 100601, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 218, "気持ちいい♪\nもう立てないよ～", 100601, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 219, "かわいい♪\nアカリのライバル\nだね", 100601, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 220, "はぁ…気持ちいい、\n私、このままダメに\nなっちゃいそう…", 100601, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 221, "はぁ…アカリ、\nとろけちゃう…", 100601, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 222, "はぁ…はぁはぁ、\n私…もう…ダメ\n限界です…！", 100601, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 223, "わぁ、おいしい～\nけど食べ過ぎは美容に\nよくないですよね", 100601, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 224, "この楽譜、\nサックスで吹いて\nみたいなぁ♪", 100601, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 226, "このごろ一人でいると\nお兄ちゃんのこと\nばかり考えちゃう…", 100602, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 227, "はぁ、はぁ…もっと\nいっぱいしなくちゃ\n…練習", 100602, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 228, "このお部屋、なんだか\n落ち着きます…\nお兄ちゃんが\nいるから？", 100602, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 229, "みんなかわいいな～\n世界一かわいくなる\nのは、難しいな…", 100602, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 230, "双子って、\n好みのタイプも\n似ちゃうのかな？", 100602, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 231, "いつか世界でいちばん\nかわいくなって\nお兄ちゃんと…", 100602, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 232, "もう待ちきれない…\nお兄ちゃん、\n早く帰って来て！", 100602, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 233, "あんっ…着替えよう\nと思ったのに\nお兄ちゃんのエッチ！", 100602, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 234, "二人きりの秘密の特訓\n今度はいつですか？\n待ち遠しいです…", 100602, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 235, "え？　ここでするの？\nみんなの前で恥ずか\nしいな…筋トレ", 100602, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 236, "お兄ちゃん、イジワル\nしないでください…", 100602, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 237, "お兄ちゃんといると\nわ、私…ドキドキ\nしてきちゃう…", 100602, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 238, "コーチ、\n今日も特訓\nお願いします！", 100602, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 239, "おはようございます\n昨夜は楽しかった\nですね、うふふ♪", 100602, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 240, "お兄ちゃんに\n食べてもらいたいなぁ\n手作りのお昼ごはん", 100602, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 241, "お風呂入ってきますね\nそのあとは…\n二人でい～っぱい\n遊びましょうね", 100602, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 242, "お姉ちゃんのマネ\nしちゃおうかな？", 100602, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 243, "気持ちいい♪\nもう立てないよ～", 100602, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 244, "かわいい♪\nアカリのライバル\nだね", 100602, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 245, "はぁ…気持ちいい、\n私、このままダメに\nなっちゃいそう…", 100602, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 246, "あったか～い…\nまるでお兄ちゃんが\n隣にいる時みたい", 100602, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 247, "汗をかくと\n気持ちいいですよね", 100602, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 248, "お兄ちゃんの特訓に\n付き合うために\nたっぷり\n食べておかないと♪", 100602, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 249, "この楽譜\nサックスで吹いて\nみたいなぁ♪", 100602, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 251, "プリン、プリン、\nプリン、プリン、\nプリン、プリン～♪", 100701, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 252, "う～ら～め～し～や～\nなの～♪", 100701, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 253, "プリンがないなら\n死んだほうがマシなの\nあ、もう死んでたの～", 100701, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 254, "幽霊の正体見たり、\nミヤコなの～", 100701, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 255, "ミヤコがいきなり\n消えたら、みんな\n驚くかもなの～♪", 100701, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 256, "アイツがいて、\nプリンもある。\nきっとここが天国なの", 100701, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 257, "アイツはプリンセス\nナイトじゃなくて\nプリンナイトなの～", 100701, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 258, "姿を消して、オマエを\n見張ってやるの～\nむふふ～♪", 100701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 259, "もし成仏した先に\nプリンがなかったら\n地獄なの～…", 100701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 260, "右にプリン左にプリン\n後ろにもプリン♪\n前にはオマエなの～！", 100701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 261, "死んだ人には\nお供え物をするの～\nミヤコにも、プリンを\n毎日お供えするの～", 100701, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 262, "たくさんオマエと\n遊べて、\n成仏しそうなくらい\nうれしいの～！", 100701, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 263, "オマエのこと\n気に入ったからずっと\nとり憑いてやるの～", 100701, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 264, "う～　眠いの…\n二度目の二度寝を\nするの…", 100701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 265, "お昼ご飯はプリンなの\n３時のおやつも、\nプリンなの～", 100701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 266, "ディナーは豪華に、\nプリンのフルコース\nなの～♪", 100701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 267, "体が透けちゃうから\nうまく使えないの～", 100701, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 268, "まったりなの～\nあとはプリンがあれば\n完璧なの～", 100701, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 269, "む～…かわいいけど\nプリンは分けて\nあげないの～", 100701, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 270, "は～　いい気分なの…\n成仏しそうなの…", 100701, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 271, "も、もしかして\nミヤコ、ここで\n火葬されちゃうの？", 100701, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 272, "幽霊には、体力も\n筋力も関係ないの～！", 100701, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 273, "ぱくぱくモグモグ…\nミヤコ、幽霊だけど、\nお腹はすくの～", 100701, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 274, "プリンの写真集が\nあればいいのに\nなの～", 100701, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 276, "うらめしや～…\n裏の飯より\nプリンがいいの～", 100702, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 277, "プリン♪　プリン♪\nプリン♪　なの～♪　", 100702, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 278, "世界中のケーキ屋が\nプリンのおいしさを\n競い合えばいいの～！\n審査員はミヤコなの～", 100702, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 279, "買ってきたプリンには\n全部ミヤコの名前を\n書いておくの～", 100702, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 280, "プリンがないなら\n死んだほうがマシなの\nあ、もう死んでたの～ ", 100702, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 281, "アイツがいて\nプリンもある\nきっとここが天国なの", 100702, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 282, "アイツはプリンセス\nナイトじゃなくて\nプリンナイトなの～", 100702, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 283, "右にプリン左にプリン\n後ろにもプリン♪\n前にはオマエなの～！", 100702, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 284, "もし成仏した先に\nプリンがなかったら\n地獄なの～…", 100702, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 285, "今日はオマエと\nプリンを買いに行くの\n強制なの～　", 100702, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 286, "死んだ人には\nお供え物をするの～\nミヤコにも、プリンを\n毎日お供えするの～", 100702, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 287, "たくさんオマエと\n遊べて\n成仏しそうなくらい\nうれしいの～！ ", 100702, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 288, "ミヤコのプリン\nオマエにだったら\n分けてあげるの～ ", 100702, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 289, "う～　眠いの…\n二度目の二度寝を\nするの… ", 100702, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 290, "う～ら～め～し～や～\n…明るい時間だと\n驚かせないの～ ", 100702, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 291, "ひゅ～どろどろ～ん☆\n真っ暗なうちに\nいたずらしまくるの～♪", 100702, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 292, "体が透けちゃうから\nうまく使えないの～ ", 100702, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 293, "落ち着くの～\nいまプリン食べたら\nきっといつもより\nおいしいの～♪ ", 100702, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 294, "む～…かわいいけど\nプリンは分けて\nあげないの～　", 100702, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 295, "は～　いい気分なの…\n成仏しそうなの…", 100702, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 296, "も、もしかして\nミヤコ、ここで\n火葬されちゃうの？ ", 100702, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 297, "幽霊には、体力も\n筋力も関係ないの～！　", 100702, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 298, "おいしいの～\nプリン味だったら\nもっとおいしいの～ ", 100702, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 299, "プリンの写真集が\nあればいいのに\nなの～ ", 100702, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 301, "もし太陽が滅んでも\nボクの美しさで大地を\n照らしてあげる！", 100801, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 302, "う～ん、この部屋には\nボクの肖像画や\n彫像が必要だね", 100801, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 303, "美の女神と愛の女神が\nボクを作る時、本気\n出しちゃったんだね♪", 100801, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 304, "ボクの誕生とともに\n全ての美しさは過去の\n遺物になったんだよ！", 100801, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 305, "うわっ昨日より可愛く\nなってる。明日は\nどうなっちゃう\nのかな？", 100801, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 306, "このボクがいるだけで\nあの人への\nおもてなしだよね", 100801, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 307, "ボクの可愛さは、\nふふ…あの人の\nためのものさ", 100801, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 308, "ねぇ～、ボクって\n可愛い？\nふふ、知ってる☆", 100801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 309, "呼んだ？\n可愛いボクと話したい\n気持ちはわかるよ", 100801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 310, "ふふ、ボクに触ると\nキミも美しく\nなっちゃうかもよ？", 100801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 311, "どうかな、\n今日のボク。\n美しい？　可愛い？\nねえ、どっち？", 100801, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 312, "あの人も今、\n可愛いボクのこと\n考えてるかな…", 100801, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 313, "美しい…\nキミの瞳に写るボクは\nより一層きれいだ", 100801, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 314, "美しいボクの\n美しい一日が\n始まるよ！", 100801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 315, "さーて、午後からも\n可愛く美しく\n華麗に優雅に♪", 100801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 316, "バラの花びら\nいっぱい浮かべた\nお風呂に入ってから\n寝ようっと♪", 100801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 317, "鏡よ、鏡。この世で\n一番可愛いボクに\nお似合いの人はだれ？", 100801, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 318, "ボクが座ったソファは\n「美しいソファ」に\nランクアップした！", 100801, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 319, "わあ、可愛い！\n…まあ、ボクほど\nじゃないけどさ！", 100801, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 320, "服を羽織るように\n香りをまとうボク…\n美しい、美しすぎる！", 100801, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 321, "気安く触ったら\n火傷する…\n炎はボクに似ているね", 100801, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 322, "運動は苦手なのに\n運動するボクの姿は\n可愛いなんて…罪♪", 100801, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 323, "ボクが食べると、\n食べ物まで\n可愛いなあ…", 100801, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 324, "ボクが触った瞬間に、\nこの本は美を\n携えてしまうんだ", 100801, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 326, "ボクの美しさは\nみんなのために\nあるんだよ！", 100802, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 327, "う～ん、この部屋には\nボクの肖像画や\n彫像が必要だね", 100802, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 328, "これ以上美しくなると\nボクをめぐって\n大陸中で戦争が\n始まっちゃう！", 100802, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 329, "ボクの誕生とともに\n全ての美しさは過去の\n遺物になったんだよ！", 100802, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 330, "ボクの可愛さについて\nみんな話し合わない\nのかな…？", 100802, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 331, "このボクがいるだけで\nあの人への\nおもてなしだよね", 100802, 1, 101);
