INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 333, "美の女神と愛の女神が\nボクを作る時、本気\n出しちゃったんだね♪", 100802, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 334, "ボクの美しさに嫉妬\nしたって意味ないよね\n次元が違うんだから", 100802, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 335, "ボクの化粧品使う？\n一緒に可愛くなろうよ", 100802, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 336, "ボクがいるだけで\nこの部屋は美の神殿♪\nランドソルの新たな\n観光名所になるね！", 100802, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 337, "呼んだ？\n可愛いボクと話したい\n気持ちはわかるよ", 100802, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 338, "美しい…\nキミの瞳に写るボクは\nより一層綺麗だ", 100802, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 339, "朝日と共に\nボクの美しさも\n周りを照らすよ", 100802, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 340, "ボクの美しさを\n維持するために\nお昼ご飯食べなくちゃ", 100802, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 341, "バラの花びらいっぱい\n浮かべたお風呂に\n入ってから\n寝ようっと♪", 100802, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 342, "鏡よ、鏡。この世で\n一番可愛いボクに\nお似合いの人はだれ？", 100802, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 343, "ボクが座ったソファは\n「美しいソファ」に\nランクアップした！", 100802, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 344, "可愛いものを\n可愛いって言える\nボクって可愛いよね！", 100802, 3, 3);
