INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 346, "冷えは美容の大敵\nなんだから！\nここ、ボクの場所ね！", 100802, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 347, "運動は苦手なのに\n運動するボクの姿は\n可愛いなんて…罪♪", 100802, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 348, "ボクが食べると\n食べ物まで\n可愛いなあ…", 100802, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 349, "ボクが触った瞬間に\nこの本は美を\n携えてしまうんだ", 100802, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 351, "「第１０部２０章\n終焉戦争、開幕」\n…これでよし！", 100901, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 352, "今度シグルドに\n私の魔法を一つ\n伝授してやるか", 100901, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 353, "ここでは風が\n吹いてこないな…\n換気したい！", 100901, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 354, "そうだ、ナナカに\nこの間借りた本を\n返さないと…", 100901, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 355, "まずは包帯をとって\n手から龍の形の\n炎を出して…", 100901, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 356, "シグルド…\n今頃何処で何を\nしているのだろう…", 100901, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 357, "最近、彼の姿が\nまぶしく見える…\nどうして…？", 100901, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 358, "辞書を知らないか？\n…探している\n理由は聞くな", 100901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 359, "そうだ！\n貴様の技にも\n名前をつけてやろう！", 100901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 360, "私に触れても力を\n奪われないのは\n貴様くらいだぞ？", 100901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 361, "何事だ？\nまさか我が瞳に\n魅入られたのか？", 100901, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 362, "貴様がそばにいると\nとても安心できる…\n…不思議だ", 100901, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 363, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！", 100901, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 364, "「覚醒」めたか…\n行くぞ、悪を討つ\n戦いに出発だ！", 100901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 365, "昼間は「ヤツら」も\n襲ってはこない…\n束の間の休息だ", 100901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 366, "どうした？　夜はまだ\nこれから…ふぁ…\nね、眠くなどないぞ！", 100901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 367, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな", 100901, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 368, "くっ…！\n私を眠りの国へ\n誘うつもりか！", 100901, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 369, "このようなものに\n懐柔されるものか！\n…されるものか！", 100901, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 370, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ", 100901, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 371, "これが我らの求めた\n安寧か…\n悪くない…", 100901, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 372, "この程度\n前世での激闘に\n比べれば…！", 100901, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 373, "最近金欠でな…\nいやぁありがたい…", 100901, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 374, "ここにも\n前世の記憶が\n眠っていたとは…", 100901, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 376, "「第１３部１章\n閃光のシグルド」\n…ついに登場だな", 100902, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 377, "何故だ…何故\nこうもあっさり金欠に\n逆戻りするのだ！？", 100902, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 378, "やはり転生者という\n設定はカッコイイ…\n天才だな、私！", 100902, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 379, "そうだ、ナナカに\nこの間借りた本を\n返さないと…", 100902, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 380, "そろそろ今日の分の\n魔力を溜めないと\nまた魔力切れが…", 100902, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 381, "最近奴がそばに\nいるだけで、鼓動が\n早くなるのは何故だ？", 100902, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 382, "シ、シグルド…！\nだめだ…妄想でも\n上手く言えない…", 100902, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 383, "なんだ？　新たな\n賞金の情報を\n持ってきたのか？", 100902, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 384, "名乗りやポーズは\nその強度で相手を\n圧倒する技術だ", 100902, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 385, "いいか、魔法の\n詠唱中は絶対に\n声をかけるなよ？", 100902, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 386, "おお、ちょうど今\n貴様専用の設定集を\n作成中だぞ！", 100902, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 387, "貴様がそばにいると\nとても安心できる…\n…不思議だ", 100902, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 388, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！", 100902, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 1, 0, 389, "夜更かしのせいか\nまだ眠気が…いや！\nこれは組織の罠だ！", 100902, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 390, "平穏な時に\n出来るだけ魔力を\n溜めておかねば…", 100902, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 391, "夜になると、力が\n増した気分になるな\n…なりませんか？", 100902, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 392, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな", 100902, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 393, "お、おのれ\nヒュプノス…\n私は負けないぞ…", 100902, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 394, "くっくっくっ…\n貴様は特別団員に\nしてやろう", 100902, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 395, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ", 100902, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 396, "これが我らの求めた\n安寧か…\n悪くない…", 100902, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 397, "新たな戦いに備えて\n魔力だけでなく\n体力も鍛えねば！", 100902, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 398, "食事は魔力に\n変換する体力の\n元になるのだ！", 100902, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 399, "フフフ…\nこれを覚えれば\nもっと強く…！", 100902, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 401, "王子はんほんまに\nはんなりしてて、\n王子はんって感じやわ", 101001, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 402, "この絵本の王子はん、\n王子はんみたいで\nええなぁ♪", 101001, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 403, "うちがガラスの靴を\n落としたら、王子はん\n見つけてくれるやろか", 101001, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 404, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～", 101001, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 405, "王子はんのために\n糸を紡いで、\n編み物でもしまひょか", 101001, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 406, "マホ姫と王子はん、\n絵本の世界より\nベストカップルや", 101001, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 407, "王子はんと\n一緒にいると\n胸がせわしのー\nうごいてかなん", 101001, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 408, "うちら並んでたら\nお雛様みたいに\n見えると思いまへん？", 101001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 409, "王子はんを\n国に連れて帰ったら\nみんな大歓迎して\nくれるやろなぁ", 101001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 410, "王子はんは、\n絵本から出てきた人\nみたいやね～", 101001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 411, "うちの話し方\nのんびりしてます\nやろか？", 101001, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 412, "マホ姫の手は\nつべたいから、\n王子はんが温めて？", 101001, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 413, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？", 101001, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 414, "朝は小鳥はんも\nおなかがすいてるから\nよう飛び回りはるねえ", 101001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 415, "お茶請けはお大福が\nよろしおすけど\nクッキーもええなあ", 101001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 416, "うち、普段から\n夢見がちやけど…\nほんに眠い時間やわぁ", 101001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 417, "王子はんに\n会いに行くなら、\nおめかしせな\nあかんなぁ", 101001, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 418, "はんなりした\nええお椅子やわぁ\nほっこりしますなあ", 101001, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 419, "かいらしもんが\nいっぱいで、\nマホマホ王国みたい\nやわぁ", 101001, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 420, "心地ええどす。まるで\nお花畑にいるようやね", 101001, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 421, "ぽかぽかどす。\n心まであったかく\nなりますわぁ", 101001, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 422, "はぁはぁ、お姫はんは\n激しい運動は\nしぃひんもんどす～", 101001, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 423, "もぐもぐ…あんまり\n速う食べられへんけど\n美味しいわぁ", 101001, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 424, "絵本、絵本～\n幸せやわぁ♪", 101001, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 426, "王子はんにとって\n自慢の姫になるえ～", 101002, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 427, "カボチャの馬車に\nガラスの靴…\n欲しいなぁ", 101002, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 428, "うちがガラスの靴を\n落としたら、王子はん\n見つけてくれるやろか", 101002, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 429, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～", 101002, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 430, "ほんまなぁ\nこの絵本が面白いんよ\n王子はんに\n読んであげよ", 101002, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 431, "マホ姫と王子はん\n絵本の世界より\nベストカップルや", 101002, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 432, "王子はんと\n一緒にいると\n胸がせわしのー\nうごいてかなん", 101002, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 433, "たまには、王子はんの\n好きな絵本も\n知りたいな～", 101002, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 434, "王子はんを\n国に連れて帰ったら\nみんな大歓迎して\nくれるやろなぁ", 101002, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 435, "そのうち、王子はんの\nお国にも連れてって\nもらえますやろか？", 101002, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 436, "絵本読むんも\nええなぁ…", 101002, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 437, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？", 101002, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 438, "うちら並んでたら\nお雛様みたいに\n見えると思いまへん？", 101002, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 439, "朝は小鳥はんも\nおなかがすいてるから\nよう飛び回りはるねえ", 101002, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 440, "マホマホ王国では\nこの時分は\nみんなお昼寝\nしてたんよ", 101002, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 441, "夜に飛んだり鳴いたり\nする鳥はんもおるんよ\n昼は寝てはるんかねぇ", 101002, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 442, "王子はんに\n会いに行くなら\nおめかしせな\nあかんなぁ", 101002, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 443, "王子はんも一緒に\nゆっくりしていっても\nええんよ？", 101002, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 444, "かいらしい子やねぇ\nうちとお友だちに\nなってくれるん？", 101002, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 445, "癒しアイテムって\nほんま、好っきゃわぁ", 101002, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 446, "うちのお城にも\nほしいわぁ。帰ったら\nさっそく準備せな\nあかんなぁ", 101002, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 447, "部屋の中で運動\nできるのも、なかなか\n面白いもんやわぁ", 101002, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 448, "おいひいわ～\nマホマホ王国には\nない味やわぁ", 101002, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 449, "しっかり知識を\n身につけんと\n魔法も使えへん\nからなぁ", 101002, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 451, "１、２、３、４…\nあれ？　いま、\n何段目まで\n編んだっけ？", 101101, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 452, "おやつのケーキは\n苺かチョコ…\nどっちも食べよう！", 101101, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 453, "い、いけない！\n縫い針落としちゃった", 101101, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 454, "ケーキをクレープで\n巻いたら…\n大ヒット商品の予感！", 101101, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 455, "「どうだ！妹暮らし」\nってことわざ\nなかったっけ？", 101101, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 456, "お兄ちゃんに、\nお洋服でも\n作ろうかな？", 101101, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 457, "ここがお兄ちゃんの\n部屋…\nドキドキしちゃう～", 101101, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 458, "ねぇ、おにいちゃん\n一緒に、スイーツでも\n食べにいかない？", 101101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 459, "クリームついてた！？\nさっき食べた、\nケーキかも…！", 101101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 460, "勉強、見てくれるん\nですか？　やった～♪", 101101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 461, "服がほつれてますよ？\n直してあげます！", 101101, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 462, "私をお嫁さんに\nしたいなら、予約は\nお早目に、ですよ♪", 101101, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 463, "ねぇねぇお兄ちゃん！\n…えへへ、\n呼んでみただけ♪", 101101, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 464, "今日も１日\n勉強がんばります！\n…少しは遊ぼうかな", 101101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 465, "お昼休憩に\n甘いものが\n食べたいです～", 101101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 466, "ねむいです～\nでももうちょっと\n勉強しないと～…", 101101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 467, "この服、かわいい♪\n今度作って\nみよっと～！", 101101, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 468, "勉強で疲れたから\n休憩しよ～っと", 101101, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 469, "かわいい♪\n私が服を作って\nあげよっか？", 101101, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 470, "リラックスできます～\nお兄ちゃんが隣に\nいるみたい！", 101101, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 471, "のんびりしたら、\nまた勉強しよっと！", 101101, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 472, "運動すると勉強も\nはかどるよね！", 101101, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 473, "おいし～！\nお兄ちゃんにも\nおすそ上げしますね！", 101101, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 474, "お兄ちゃんの記憶を\n戻す方法、どこかに\n書いてないかな？", 101101, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 476, "ケーキの食べ過ぎで\nお腹が…うぅ、\n因果横暴だよー…", 101102, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 477, "１、２、３、４…\nあれ？　いま、\n何段目まで\n編んだっけ？", 101102, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 478, "い、いけない！\n縫い針落としちゃった", 101102, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 479, "笑う時には服着ます！", 101102, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 480, "「どうだ！妹暮らし」\nってことわざ\nなかったっけ？", 101102, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 481, "お兄ちゃんに、\nお洋服でも\n作ろうかな？", 101102, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 482, "お兄ちゃんが帰って\nきたら、何して遊ぼう\nかな～？", 101102, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 483, "勉強、見てくれるん\nですか？　やった～♪", 101102, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 484, "クリームついてた！？\nさっき食べた、\nケーキかも…！", 101102, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 485, "おにいちゃん！\nそろそろ、おやつに\nしませんか？", 101102, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 486, "勉強は…\n明日にしようっと", 101102, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 487, "妹だけど、大人な\nレディですからね！", 101102, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 488, "な、ナデナデとか\n恥ずかしいな～", 101102, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 489, "今日も１日\n勉強がんばります！\n…少しは遊ぼうかな", 101102, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 490, "お昼休憩に\n甘いものが\n食べたいです～", 101102, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 491, "お兄ちゃんは\n夜強いんですね\n私は眠くて…", 101102, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 492, "服装や髪形が変わると\n気分も変わるよね", 101102, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 493, "ここで寝ちゃったら\n受験勉強が～…\nぐーー", 101102, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 494, "かわいい♪\n私が服を作って\nあげよっか？", 101102, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 495, "いまならどんな試験も\n合格できそうです！", 101102, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 496, "のんびりしたら\nまた勉強しよっと！", 101102, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 497, "お兄ちゃんを\n守るためには\n毎日、鍛えなくちゃ！", 101102, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 498, "おいし～！\nお兄ちゃんにも\nおすそ上げしますね！", 101102, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 499, "この本の服\nかわいいなぁ♪\n私に作れるかな？", 101102, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 501, "う～ん、今なら\nどこでだって\n眠れちゃいそう…", 101201, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 502, "たくさん寝すぎて、\n夢の中か現実か\nわかんなく\nなっちゃうんだよね～", 101201, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 503, "…はっ！？\nね、寝てないよ？\nホントだよっ", 101201, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 504, "ぐっすり眠って\nパワーを\nフルチャージっ☆", 101201, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 505, "もし、超能力のことが\nバレたら…ブルブル", 101201, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 506, "テレパシーで\nキミの心を覗けたら\n…なんてね！", 101201, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 507, "キミを抱き枕に…\nじょ、冗談冗談！", 101201, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 508, "ん～、なになに？\n一緒にお昼寝する？", 101201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 509, "忘れ物しちゃった。\nま、いっか！\nえ、ダメ？", 101201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 510, "おおっ、相談ごと？\nなにかな\nなにかな～？", 101201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 511, "う～ん、もしかして\nキミにも仲良しになる\n超能力があるのかも！", 101201, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 512, "むむ～…どう？\nテレパシーで、私の\n気持ちを送ったよ！", 101201, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 513, "えっ？　私とキミとの\n相性？　むむ～…\nうん、バッチリだよ☆", 101201, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 514, "まだこんな時間？\nベッドに行って、\n二度寝しなくちゃ～", 101201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 515, "適度なお昼寝は\n健康にいいんだって！\n８時間くらいかな？", 101201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 516, "どんなにお昼寝しても\n夜は眠いんだ～", 101201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 517, "ん～、また寝癖…\nいっつも\n寝てるからかなぁ？", 101201, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 518, "一休み、一休み…\nどうせなら百休み\nくらいしようかな…", 101201, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 519, "これ…可愛い！\nシオリンに\n届けてあげたい！", 101201, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 520, "ん～…気分爽快！\nそれじゃ、おやすみ～\n…すやぁ…", 101201, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 521, "気持ちいい…\nはっ！　ダメダメ！\nまた寝るとこだった！", 101201, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 522, "眠くならないように、\n運動しよっと！", 101201, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 523, "お腹いっぱいになると\n眠くなるよね～", 101201, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 524, "睡眠学習って\n本を読みながら\n寝るんだっけ？", 101201, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 526, "う～ん、今なら\nどこでだって\n眠れちゃいそう…", 101202, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 527, "もっといっぱい\n超能力使えたら\nいいのにな～", 101202, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 528, "ねえねえ、\n今から人助け、\n付き合ってほしいなー", 101202, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 529, "ぐっすり眠って\nパワーを\nフルチャージっ☆", 101202, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 530, "人助け、頑張ったら\nなんだか、眠く…\n…はっ、ダメダメ！", 101202, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 531, "キミと一緒なら、\nなんだって出来る！\n間違いない！", 101202, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 532, "キミを抱き枕に…\nじょ、冗談冗談！", 101202, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 533, "忘れ物しちゃった\nま、いっか！\nえ、ダメ？", 101202, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 534, "なに？　困ってる？\nちょうど良かった！\n今は体力満タンだよ☆", 101202, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 535, "一緒に\nお出かけしたいなー\nデートしよっ！", 101202, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 536, "うんうん、そうだねっ\n困ってる人がいたら\n見過ごせないよねっ！", 101202, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 537, "むむ～…どう？\nテレパシーで、私の\n気持ちを送ったよ！", 101202, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 538, "う～だんだん眠く…\nねぇ一緒に寝よ？", 101202, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 539, "キミって早起きだね！\n私は寝ようかな～", 101202, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 540, "適度なお昼寝は\n健康にいいんだって！\n８時間くらいかな？", 101202, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 541, "どんなにお昼寝しても\n夜は眠いんだ～", 101202, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 542, "ん～、また寝癖…\nいっつも\n寝てるからかなぁ？", 101202, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 543, "一休み、一休み…\nどうせなら百休み\nくらいしようかな…", 101202, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 544, "これ…可愛い！\nシオリンに\n届けてあげたい！", 101202, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 545, "ん～…気分爽快！\nそれじゃ、おやすみ～\n…すやぁ…", 101202, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 546, "うう～ん、なんだか\nいつもより眠ーく\nなってきたよ…", 101202, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 547, "よーし\n困ってる人のために\nパワーアップ☆", 101202, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 548, "食べてすぐ寝ると\n牛になるんだっけ？\nまぁ、いいかぁ～", 101202, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 549, "睡眠学習って\n本を読みながら\n寝るんだっけ？", 101202, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 551, "モデルは楽しいよ！\nファンの子みんな\n応援してくれるしー！", 101601, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 552, "今日のおやつは\n駄菓子に決定～！\nいただきまーす！", 101601, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 553, "んー、今日のおやつは\n何にしよう？\nやっぱアメ玉かな！", 101601, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 554, "ここで撮影会、\nしようかな～♪", 101601, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 555, "７かける８はぁ\n７＋７＋７＋７＋…\nん、何個数えたっけ？", 101601, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 556, "やば…\nこのままじゃ、うち\nキュン死にかも…", 101601, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 557, "好きなことして\nヒデサイもいて\nうちって超～\nリア充だよね♪", 101601, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 558, "うちの写真撮るの？\nどんなポーズ？", 101601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 559, "ネイルの勉強中～♪\nうまくなったら、\nヒデサイにしてあげる", 101601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 560, "宿題見てくんない？\nひとりじゃムリ～", 101601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 561, "みてこのネイル♪\n超エモくな～い？\nヒデサイもする～？", 101601, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 562, "新しい美容液で\n肌まじスベスベ～\nヒデサイも\n触ってみる？", 101601, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 563, "ヒデサイと会ってると\n顔熱くなるんだよね\n熱なんかないのに…", 101601, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 564, "早起きとか\nつらたんだよねー\n昼すぎまで寝てたい", 101601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 565, "撮影にむけて\nダイエットちうだから\nお昼ぬいてるんだー", 101601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 566, "お腹すいたー\nでもこの時間に\n食べたら絶対に\n太るやつだよねー", 101601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 567, "どんなシーンにも合う\nきれいめわがまま\nちゃんとコーデだよ♪", 101601, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 568, "ちょっち疲れたかも？\nガン寝しちゃおっかな", 101601, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 569, "きゃわたーん☆\n君もおしゃれしよっか", 101601, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 570, "いいにおいじゃん！\nこれと同じにおいの\n香水ないかな？", 101601, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 571, "あったまるー\nこれ、サイツヨに\n癒しじゃない？", 101601, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 572, "あはは、なんか\nウォーキングの練習\nしてるみたーい！", 101601, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 573, "体形維持しないと\nいけないけど\nついつい食べちゃう！", 101601, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 574, "全然読めない～\nそだ、ヒデサイに\n読んでもらおっと！", 101601, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 576, "そろそろ\n仕事の時間だ～\n出かける準備\nしよっと！", 101602, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 577, "今日のおやつは\n駄菓子に決定～！\nいただきまーす！", 101602, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 578, "ヒデサイを見習って\nうちも少し勉強して\nみよっかな…　", 101602, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 579, "体形維持とか\n気にしてるんだよ～", 101602, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 580, "７かける８はぁ\n７＋７＋７＋７＋…\nん、何個数えたっけ？", 101602, 1, 5);
