INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 584, "ネイルの勉強中～♪\nうまくなったら、\nヒデサイに\nしてあげる♪", 101602, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 585, "この雑誌の服\nガチめにほしぃ～", 101602, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 586, "うちのコーデで\nヒデサイをオシャンに\nしてあげるね♪", 101602, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 587, "新しい美容液で\n肌まじスベスベ～\nヒデサイも\n触ってみる？", 101602, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 588, "好きなことして\nヒデサイもいて\nうちって超～\nリア充だよね♪", 101602, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 589, "今日は早起き\nできたんだー\nメイクもばっち\nきまってるっしょ？", 101602, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 590, "撮影にむけて\nダイエットちうだから\nお昼ぬいてるんだー", 101602, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 591, "お腹すいたー\nでもこの時間に\n食べたら絶対に\n太るやつだよねー", 101602, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 592, "この服ね～\n今年のトレンドなんだ♪\nかわたんでしょ！", 101602, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 593, "ちょっち疲れたかも？\nガン寝しちゃおっかな", 101602, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 594, "うわー\nこのぬいぐるみ\nもきゅすぎでしょ～！", 101602, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 595, "いいにおーい♪\nファンのみんなにも\n教えてあげよー♪", 101602, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 596, "あったまるー\nこれ、サイツヨに\n癒しじゃない？", 101602, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 597, "食べた分は\n消費しないとね～", 101602, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 598, "体形維持しないと\nいけないけど\nついつい食べちゃう！", 101602, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 599, "全然読めない～\nそだ、ヒデサイに\n読んでもらおっと！", 101602, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 601, "やっさー\n踊ってると、どんどん\n楽しくなるさー！", 101701, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 602, "このお菓子、でーじ\nおいしいねー！\nキミも食べる？", 101701, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 603, "ハイターイ！　元気な\nあいさつが一番さ～！", 101701, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 604, "あまった食材があれば\nチャンプルーにして\n食べたいさー！ ", 101701, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 605, "さぁ、みんなで歌って\n踊るさ～♪", 101701, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 606, "毎日、最高に幸せさ～\nキミのおかげだねー", 101701, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 607, "キミへの気持ちを\n踊りで表現\nしてみるさ～", 101701, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 608, "海でひと泳ぎ\nしたいさ～\nキミもどう？", 101701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 609, "あぎひゃっ！\n急に声かけたら、\nビックリするさ～", 101701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 610, "踊りなら、\n私が手とり足とり\n教えてあげるさ～！", 101701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 611, "空手の型の\n練習するさー\nキミもどう？", 101701, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 612, "よ～し、手をつないで\n踊るさ～！\n恥ずかしがること\nないさ～", 101701, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 613, "キミと一緒だから、\n毎日楽しいこと\nばっかりさー", 101701, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 614, "ハイターイ！\n今日も一日、\nがんばるさー！", 101701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 615, "午後も頑張るために\nお昼ご飯は\nてびちにするさー！", 101701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 616, "故郷では見えない星が\n見られるんだねえ。\nなんか不思議さぁ", 101701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 617, "夢中で踊ってたら、\n髪の毛ぼさぼさに\nなってるさ～！？", 101701, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 618, "よく食べてよく踊って\nよく休む。\nそれが元気の秘訣さ～", 101701, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 619, "ちゅらかーぎーねー！\nキミはどこから\n来たのー？", 101701, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 620, "いい香り～、って\n私もカオリだったさ！\nなんだか照れるね☆", 101701, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 621, "故郷はあんまり暖房を\n使わなかったから、\n新鮮さー！", 101701, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 622, "汗をかくのは\n気持ちいいさー！\nどんどん行くよー！", 101701, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 623, "うーん、おいしいさ～\nゴーヤっぽいものを\n混ぜたいさ～", 101701, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 624, "おおー、故郷の民話！\nこんなとこで見るとは\n懐かしいさぁ～！", 101701, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 626, "焦らなくても\nなんくるないさ～\nなるようになるさ～", 101702, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 627, "さぁ、みんなで歌って\n踊るさ～♪", 101702, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 628, "元気がないときは\n私のところに来るさー", 101702, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 629, "あまった食材があれば\nチャンプルーにして\n食べたいさー！ ", 101702, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 630, "ノリノリな音楽と\n踊りで、疲れを\n吹き飛ばすさ～！", 101702, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 631, "毎日、最高に幸せさ～\nキミのおかげだねー", 101702, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 632, "幸せすぎて踊らずには\nいられないさ～", 101702, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 633, "海でひと泳ぎ\nしたいさ～\nキミもどう？", 101702, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 634, "ん？　どうしたの～？\nヒマなら一緒に\n踊ろうさ～", 101702, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 635, "キミと一緒だから\n毎日楽しいこと\nばっかりさー", 101702, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 636, "ハイターイ！　元気な\nあいさつが一番さ～！", 101702, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 637, "キミと踊ってる時が\n一番幸せさ～\nキミもそう思ってたら\nうれしいさ～", 101702, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 638, "海に沈む夕日は\nとっても綺麗さ～！\n今度一緒に見ようね～ ", 101702, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 639, "気持ちのいい朝さー！\nひとっ走り\nしてくるさー！", 101702, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 640, "午後も頑張るために\nお昼ご飯は\nてびちにするさー！ ", 101702, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 641, "故郷では見えない星が\n見られるんだねえ\nなんか不思議さぁ ", 101702, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 642, "夢中で踊ってたら\n髪の毛ぼさぼさに\nなってるさ～！？", 101702, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 643, "よく食べてよく踊って\nよく休む\nそれが元気の秘訣さ～", 101702, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 644, "ちゅらかーぎーねー！\nキミはどこから\n来たのー？ ", 101702, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 645, "いい香り～、って\n私もカオリだったさ！\nなんだか照れるね☆ ", 101702, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 646, "故郷はあんまり暖房を\n使わなかったから\n新鮮さー！", 101702, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 647, "徹夜で踊り続ける\nためにも、まずは\n体を鍛えるさ～", 101702, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 648, "もぐもぐ…もっと\nおかわりがほしいさー", 101702, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 649, "おおー、故郷の民話！\nこんなとこで見るとは\n懐かしいさぁ～！ ", 101702, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 651, "はあ…\nすてきな小説だった～\nキュンキュンしちゃう", 101801, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 652, "このお部屋\n女の子ばかりね。\n魔族の女子校を\n思い出しちゃう♪", 101801, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 653, "次のテスト\n恋愛小説から\n出題したら\nダメかしら…？", 101801, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 654, "キンコンカンコ～ン♪\n授業をはじめま～す", 101801, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 655, "はー…\nちょっと肩が\nこっちゃった…", 101801, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 656, "私、あの子のこと\n無意識に特別扱い\nしちゃってるかな…", 101801, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 657, "物語だと生徒が先生を\n好きになるのにな…\n逆の場合は…", 101801, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 658, "あん！　見ちゃダメよ\n明日のテストの問題を\n作ってるとこなの！", 101801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 659, "勉強勉強で\n疲れたよね？\n膝枕してあげる！", 101801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 660, "テストがんばったね～\nご褒美に先生が\n特別な授業\nしてあげるね？", 101801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 661, "学院の外でも\n私は君の先生よ\nなんでも聞いてね♪", 101801, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 662, "今読んでるのは\nヒロインが年下の子と\n恋に落ちる話…あ、\n深い意味はないのよ～", 101801, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 663, "生徒は全員可愛いけど\n君は少し特別…\nみんなには内緒よ？", 101801, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 664, "学校に\n遅刻しないように\n早く準備しないとね", 101801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 665, "男の子といっしょに\nお弁当を食べるの、\n夢なんだ～", 101801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 666, "明日の予習はやった？\n先生も手伝うから\n寝る前にやりましょ♪", 101801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 667, "もしかして…\n少し露出多いかしら？", 101801, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 668, "リラックスすると\nα波が出て、勉強が\nはかどるんですって", 101801, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 669, "かわいい♪　あなたも\n私の生徒になる？", 101801, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 670, "学院にもこういう\n安らぐ設備が\n必要かしらね？", 101801, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 671, "まだ寒いわぁ\n誰か隣に\n来てくれたら…", 101801, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 672, "運動部の顧問も\nできるかしら？", 101801, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 673, "学校の給食でも\n食べたいわぁ～", 101801, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 674, "いい参考書を\n見つけちゃった♪", 101801, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 676, "私の生徒に\n不良なんていないわ\nみーんないい子♪", 101802, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 677, "このお部屋\n女の子ばかりね\n魔族の女子校を\n思い出しちゃう♪", 101802, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 678, "あの子、まだかな…\n宿題も見てあげたいし\n早く来ないかしら？", 101802, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 679, "はあ…\nすてきな小説だった～\nキュンキュンしちゃう", 101802, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 680, "学校の図書室に\n恋愛小説を\n置いて欲しいなあ", 101802, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 681, "私、あの子のこと\n無意識に特別扱い\nしちゃってるかな…", 101802, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 682, "物語だと生徒が先生を\n好きになるのにな…\n逆の場合は…　", 101802, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 683, "あん！　見ちゃダメよ\n明日のテストの問題を\n作ってるとこなの！", 101802, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 684, "勉強勉強で\n疲れたよね？\n膝枕してあげる！", 101802, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 685, "宿題はもうやった？\n苦手なところは\n先生が見てあげる♪", 101802, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 686, "んん？　なんだか\n元気がないみたい\n先生がぎゅぅ～って\nしてあげる…　", 101802, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 687, "君のお勉強も\nだいぶ進んできたね～\n補習卒業も近いかな？\n少し寂しいけど…", 101802, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 688, "テストがんばったね～\nご褒美に先生が\n特別な授業\nしてあげるね？", 101802, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 689, "朝の特別授業\nしてあげましょうか？", 101802, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 690, "午後の授業って\n眠いでしょう？\n気持ちは分かるけど\nがんばってね♪", 101802, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 691, "明日の予習はやった？\n先生も手伝うから\n寝る前にやりましょ♪", 101802, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 692, "もしかして…\n少し露出多いかしら？", 101802, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 693, "私の膝枕と、どっちが\nフカフカかしら？", 101802, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 694, "可愛いわね～\n教室に置いたら\n生徒が喜ぶかしら？　", 101802, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 695, "は～落ち着くなぁ～\n教師には\nこういうゆとりも\n必要よね♪　", 101802, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 696, "まだ寒いわぁ\n誰か隣に\n来てくれたら…　", 101802, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 697, "運動部の顧問も\nできるかしら？　", 101802, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 698, "あん！\nお胸にこぼしちゃった", 101802, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 699, "あぁーん♪\nこういう物語みたいな\n恋愛あこがれる～　", 101802, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 701, "おにいちゃんは、\nにんじん食べられる？", 102001, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 702, "えへへ、\nお絵かきしよ～♪", 102001, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 703, "あのね、ミミと\nあそんでほしいな～", 102001, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 704, "さっきね、きれいな\nお花見つけたよ。\nおにいちゃんにも\n見せてあげたいな～", 102001, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 705, "うさぎさん、\nぴょんぴょん\nぴょーん♪", 102001, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 706, "おにいちゃんの絵、\nかいてあげるね♪", 102001, 1, 101);
