INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 797, "え、演劇には\nた、体力も重要\nだから～", 102102, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 798, "残さず\n食べますね…！", 102102, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 799, "戯曲を読んで\n自分が演じるところを\n想像するの…", 102102, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 801, "ねえ、人見知りを\n治すための練習に\n付き合ってくれない？", 102201, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 802, "アカリみたいに\nかわいくなるには、\nどうすればいいのかな", 102201, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 803, "レアカードをゲット！\nねえねえ、\nすごいでしょ！？", 102201, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 804, "ア、アイツと同じ\n部屋にいるのよね…\nって、だから何よ！？", 102201, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 805, "楽しいゲームって\nやめどきが\n見つからないわ", 102201, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 806, "もっと女の子らしく\nなったらアイツも\n嬉しいかな…って、\n何考えてるの！？", 102201, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 807, "うぅ…最近、\nアイツを見るとなんか\nドキドキするのよね", 102201, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 808, "ゲームに負けた罰\nだからって、\nくすぐるのは…\nあはは…ダメだって！", 102201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 809, "アンタと話すのは割と\n緊張しないのよね\n…なんでかな", 102201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 810, "待ちきれない…\n新作ゲームの発売日が\n待ちきれない…", 102201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 811, "アンタは人見知り\nしないタチよね？\nいいなあ～…", 102201, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 812, "ア、アンタってさ\n好きな人とかい…\nなな、何でもない！", 102201, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 813, "アンタと…そ、その\nこうして話してるのも\nわわ、悪くないわね！", 102201, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 814, "おおおおはよう！\n…よし！　ちゃんと\nあいさつできたわ！", 102201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 815, "一緒にごはんとか…\nううん、やっぱいい！\nなんでもないからっ！", 102201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 816, "夜はこれからよ！\n朝まで\nゲームするからね！", 102201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 817, "アカリみたいに\n可愛くなるのは\n難しいかな", 102201, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 818, "寝っ転がりながら\nゲームができれば\n気分が良さそう", 102201, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 819, "可愛い…けど、\n私には似合わないかな", 102201, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 820, "はあ、リラックスして\nゲームができるわ", 102201, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 821, "ん…暖かくて、\n寝落ちしちゃいそう", 102201, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 822, "ゲーム感覚でやると、\n体動かすのも\n楽しいわね", 102201, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 823, "ゲームは頭使うから\nそのぶん\n栄養がいるのよ", 102201, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 824, "ふむふむ、なるほど\n友だちを作るには\n笑顔が大事っと…", 102201, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 826, "さあ、今日は\n何のゲームで\n遊ぼうかな？", 102202, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 827, "一緒にゲームができる\n人がいるっていうのも\n新鮮だな…", 102202, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 828, "レアカードをゲット！\nすごいでしょ！？", 102202, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 829, "アカリってどうやって\n友達作ってるんだろ？\nコツがあるのかな…", 102202, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 830, "楽しいゲームって\nやめどきが\n見つからないわ", 102202, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 831, "うぅ…最近\nアイツを見るとなんか\nドキドキするのよね", 102202, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 832, "ね、ねえ\nもう少しその…\n一緒にゲームしない？", 102202, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 833, "ねえ、人見知りを\n治すための練習に\n付き合ってくれない？", 102202, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 834, "欲しいゲームが\n見つからなくて…\n探すの手伝って！", 102202, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 835, "ねえ、見て！\n新しいゲームを\n買って来たの！", 102202, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 836, "ゲームに負けた罰\nだからって、くすぐる\nのは…あ、あはは！\nだ、ダメだって！", 102202, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 837, "ア、アンタってさ\n好きな人とかい…\nなな、何でもない！", 102202, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 838, "あ、あのさ…\nもう少し、そこに\nいてくれてもいいわよ", 102202, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 839, "おおおおはよう！\n…よし！　ちゃんと\nあいさつできたわ！", 102202, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 840, "一緒にごはんとか…\nううん、やっぱいい！\nなんでもないからっ！", 102202, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 841, "夜はこれからよ！\n朝まで\nゲームするからね！", 102202, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 842, "もっと女の子らしく\nなったらアイツも\n嬉しいかな…って、\n何考えてるの！？", 102202, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 843, "ふああ…\n徹夜明けには\n辛い誘惑だな…", 102202, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 844, "可愛い…けど、\n私には似合わないかな", 102202, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 845, "こういうのに詳しいと\n女の子っぽく\n見えたりするのかな", 102202, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 846, "寒い日は\nここでゲームするのが\n最高のぜいたくよね", 102202, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 847, "最近ゲームばかりだし\nたまにはいいかな？", 102202, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 848, "ゲームは頭使うから\nそのぶん\n栄養がいるのよ", 102202, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 849, "ふむふむ、なるほど\n友だちを作るには\n笑顔が大事っと…", 102202, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 851, "じゃんけんぽん！\nあっち向いてほい！\nあ～ん、負けたぁ\n　『勝ったぜ！』", 102301, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 852, "ぷうきちがいれば、\nなんでも出来ちゃうよ", 102301, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 853, "発声練習しよっと\nぷうきち\nフルスイ～ング！", 102301, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 854, "ここで、ぷうきち\nフルスイングは、\nまずいかな～？", 102301, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 855, "この部屋、ぷうきちを\n置く場所はないのかな", 102301, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 856, "お兄ちゃんって…\nちょっとぷうきちに\n似てるかも♪", 102301, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 857, "お兄ちゃんにも、\nぷうきちのこと\n好きになって\nもらいたいな～", 102301, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 858, "おやつは、\n私とぷうきちの\n二人分用意してね♪", 102301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 859, "ぷうきちが、お腹\nペコペコだって～\n何かちょうだい！", 102301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 860, "『アヤネに手出したら\n…坊ちゃんでも、\n分かってんな？』", 102301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 861, "ぷうきちとの\n連携を磨いてるの！\nもっと仲良くなるよ！", 102301, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 862, "特別だからね？\nもっと触っていいよ\n…ぷうきちのこと", 102301, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 863, "お兄ちゃんのこと、\n「ぷう兄」って\n呼ぼうかな？", 102301, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 864, "お兄ちゃんって\n朝早いんだね～\n私はまだ眠いよ～", 102301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 865, "ふぁ～…ぷうきちと\nお昼寝してくるね…", 102301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 866, "ぷうきちと\nおしゃべりしてたら\nもうこんな時間…", 102301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 868, "『座り心地がいいな』\nぷうきちは座れない\nでしょ？", 102301, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 869, "かわいい～！\nぷうきちのお友達に\nしちゃおうかな？", 102301, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 870, "いいニオイ～♪\nぷうきちも\n気に入ったみたい！", 102301, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 871, "ぽわぽわする～\nぷうきちも\n気持ちいい？", 102301, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 873, "おいしい～\nぷうきちも\n食べてみる？", 102301, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 874, "ぷうきち！\nこの本、読んで～！", 102301, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 901, "どうして失敗ばっかり\nこうなったら\n腹を切るしか～！", 102501, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 902, "あれ？　あれ？\nお部屋の中で迷子に\nなっちゃいました～", 102501, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 903, "どうしたら転ばなく\nなるんでしょうか？", 102501, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 904, "わわわわ～～っ\nふう…\nきゃ、きゃあっ！", 102501, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 905, "私のドジで、\nこのお部屋を\n壊しちゃったら\nどうしましょう～～", 102501, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 906, "おいしい料理で\nお出迎えしなくちゃ！", 102501, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 907, "一生懸命\nご奉仕しますよ～♪", 102501, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 908, "耳かきしましょうか？\nえ？　こわい？", 102501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 909, "お疲れでしたら、\nお昼寝しませんか？\n膝枕もしますよ？", 102501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 910, "おかえりなさいませ\nご主人さま♪", 102501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 911, "すみません\nすみません\nすみません～～", 102501, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 912, "メイドの私が、\nこんな気持ちを持って\nいいんでしょうか…", 102501, 2, 101);
