INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 995, "このお部屋は\n良い香りがしますわ\nはっ、あなた様の匂い\nなのでは…！？", 102702, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 996, "素敵なぬくもり…\nまるであの方に\n抱きしめられたような", 102702, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 997, "この運動エネルギー\n何かにうまく\n利用できないかしら？", 102702, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 998, "美味しい。あの方にも\n差し上げましょう…\n薬を入れて…", 102702, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 999, "あの方を虜にする\n方法がどこかに\n載ってるはずですわ", 102702, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1001, "子供たちの遊びって\nあたしが子供の頃と\nあまり変わらないのね", 102801, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1002, "使えるものは\n再利用しないと\n…あ、これとか\nまだ使えるわね", 102801, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1003, "贅沢は敵！　とはいえ\n気持ちに余裕のない\n暮らしはダメよね", 102801, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1004, "お腹空いたわね、\n残り物で\n何か作ろうかしら", 102801, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1005, "商売はノンゼロサムが\n理想だなぁ。誰も損を\nしないってことだもの", 102801, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 1006, "あんたが手伝って\nくれるから、仕事も\n前より楽しいのよね♪", 102801, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1007, "焼きそばパンが\n食べたいな～\nあいつと…", 102801, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1008, "ねぇあんた、どこか\n安いお店知らない？", 102801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1009, "あたし、エレガントに\n振る舞えてる？\n…そう？　嬉しい♪", 102801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1010, "いっぱい働いて遊んで\nよく休んでまた働く。\nそれが健全な生活よね", 102801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1011, "一緒に部屋の\n整頓でもする？\nハマると楽しいわよ♪", 102801, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 1012, "あんたがいると\n子供たちも喜ぶわ♪\nもちろんあたしもね！", 102801, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1013, "ね、あんた今、幸せ？\n何びっくりしてるのよ\n大事なことじゃないの", 102801, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 1014, "面倒な用事は午前中に\n片づけてしまうのが、\n時間の使い方のコツよ", 102801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 1015, "午後のティータイム\nとかどう？\n良いカフェがあるの♪", 102801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 1016, "さ、エレガントな夜を\n楽しみましょ。\nモダンな音楽でも\nかけましょうか？", 102801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1017, "レディたるもの\n身嗜みはしっかりとね\n見本にならないと♪", 102801, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1018, "これで仕事は一段落ね\nちょっと休憩…", 102801, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1019, "うふふ、\nかわいいものを\n見るのは幸せね～", 102801, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1020, "部屋中がいい香り…\nなんだかすごい贅沢\nしてる気分♪", 102801, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1021, "風邪を引かないように\n身体を温めなくちゃ", 102801, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1022, "さっ！　家の中でも\n効率的に\nトレーニングよ！", 102801, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1023, "もぐもぐ…はっ、\n口の周り、\n汚れてないわよね？", 102801, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1024, "知識から過去を学んで\n未来に活かすわよ", 102801, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1026, "安売りのお店の情報\n整理しないと…", 102802, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1027, "どんなに苦労してても\nそう見せないのが真の\nエレガントなレディよ", 102802, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1028, "安く済んであいつが\n喜ぶ料理とか…\nないかしら", 102802, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1029, "子供たちの遊びって\nあたしが子供の頃と\nあまり変わらないのね", 102802, 1, 4);
