INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1031, "焼きそばパンが\n食べたいな～\nあいつと…", 102802, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1032, "あいつと一緒に…\n色々なところに\nいきたいな", 102802, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1033, "今度買い物に\n付き合いなさい\nセールがあるのよ", 102802, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1034, "あんたがいると子供\nたちも喜ぶわ♪\nもちろんあたしもね♪", 102802, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1035, "いっぱい働いて遊んで\nよく休んでまた働く\nそれが健全な生活よね", 102802, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1036, "あんたとこうして\nのんびりするのも\nいいものね", 102802, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1037, "ねえ、今度遊びに\n行きたいな\nもちろん一緒に、よ", 102802, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1038, "あんたが手伝って\nくれるから、仕事も\n前より楽しいのよね♪", 102802, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 1039, "面倒な用事は午前中に\n片づけてしまうのが\n時間の使い方のコツよ", 102802, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 1040, "ランチにして午後の\n仕事に備えましょ！\nやることは一杯よ♪", 102802, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 1041, "ふわあ～\nもうこんな時間\n子供たちは\nもう寝たかしら", 102802, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1042, "忙しい時こそ\n身だしなみは隙なく\nぴしっとね！", 102802, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1043, "これで仕事は一段落ね\nちょっと休憩…", 102802, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1044, "ふふふ\nずっと眺めてたく\nなっちゃうわね", 102802, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1045, "部屋中がいい香り…\nなんだかすごい贅沢\nしてる気分♪", 102802, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1046, "風邪を引かないように\n身体を温めなくちゃ", 102802, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1047, "さっ！　家の中でも\n効率的に\nトレーニングよ！", 102802, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1048, "しっかり食べて\n栄養補給、栄養補給！", 102802, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1049, "知識から過去を学んで\n未来に活かすわよ", 102802, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1051, "可愛い子が\nいっぱいいるなぁ…\n誰か私とユニット\n組んでくれないかな？", 102901, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1052, "今度のライブ会場、\nいつもより大きいから\n楽しみなんだ～！", 102901, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1053, "新曲の衣装、もうすぐ\n出来あがるんだって！\n楽しみだな～", 102901, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1054, "彼とデュエット\nしてみたいな～♪", 102901, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1055, "キラキラ無病息災、\nドキドキ一日一善…\n作詞って難しい～", 102901, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 1056, "ファンはもちろん\n大事だけど…\n彼のことは、もっと\n大切にしたいな…", 102901, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1057, "写真集見なくても、\n本物がここに\nいるのにな～", 102901, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1058, "握手会の練習、\n付き合ってくれない？", 102901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1059, "写真集のポーズ\n練習中～！\nど、どうかな？", 102901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1060, "何て書いてあるか\n読めないって？\nもー！\nこれは私のサイン！", 102901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1061, "ね、次のライブ\n観に来てくれる？\nいい席とっとくから！", 102901, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 1062, "ねぇ、台本読むの\nつきあってくれる？\n恋人役に\nなってほしいんだ", 102901, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1063, "新曲、聞いてくれた？\nキミとのことを\nテーマにしたんだよ。\nどこかは秘密だけど♪", 102901, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 1064, "朝の発声練習に\n行くけど、キミも\n一緒に来ない？", 102901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 1065, "この時間は\nお腹すくけど…\n水着撮影があるから\nお昼抜いてるんだ", 102901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 1066, "夜の帳が二人を包み\n…こういう方向性の\n歌詞も悪くないなぁ。", 102901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1067, "身だしなみオッケー！\nアイドルはいつでも\n完璧でいなくちゃ！", 102901, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1068, "ふぅ…\nこんな姿、ファンには\n見せられないな～", 102901, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1069, "かわいい～♪\nこれは強力な\nライバル登場かな！", 102901, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1070, "ステキな香り～♪\n歌詞のイメージが\n浮かんできそう！", 102901, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1071, "体も暖まったし！\nダンスレッスンに\n行こうかな♪", 102901, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1072, "ベストタイム更新！\nライブに向けて\n体力つけないと…", 102901, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1073, "太っちゃったら\nどうしよう…？\nでも手が止まらない～", 102901, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1074, "ステキなお話…\n新曲の作詞の\n参考になりそう♪", 102901, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1076, "このお部屋すてき！\nファンに追われた時の\n隠れ家にピッタリね！", 102902, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1077, "そろそろイメチェン\nしてみようかな…\nそれともこの路線で\nもう少し…？", 102902, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1078, "みんながくれた\nファンレター…\n私の大事な宝物…", 102902, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1079, "彼とデュエット\nしてみたいな～♪", 102902, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1080, "疾風怒濤、威風堂々\n獅子奮迅、唯我独尊…\nラブソングの作詞って\n難しいな～", 102902, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1081, "ファンはもちろん\n大事だけど…\n彼のことは、もっと\n大切にしたいな…", 102902, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1082, "トップアイドルの\nさらに先…\n彼とならその景色も\n見られるかな", 102902, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1083, "新曲の衣装、もうすぐ\n出来あがるんだって！\n楽しみだな～", 102902, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1084, "アイドルノゾミだけ\nじゃなくて、私の事も\nちゃんと見てね？", 102902, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1085, "キミに響かないなら\nファンにも響くわけ\nないよ！　練習練習！", 102902, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1086, "衣装のスカートは\n鉄壁なんだよ！\n残念でした～", 102902, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1087, "新曲、聞いてくれた？\nキミとのことを\nテーマにしたんだよ\nどこかは秘密だけど♪", 102902, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1088, "ねぇ、私がトップ\nアイドルになっても\nちゃんと隣にいてね\n遠くに行かないでね？", 102902, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 1089, "朝の発声練習に\n行くけど、キミも\n一緒に来ない？", 102902, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 1090, "お昼ごはん行かない？\n誰にも知られてない\nお店があるんだけど！", 102902, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 1091, "夜の帳が二人を包み\n…こういう方向性の\n歌詞も悪くないなぁ", 102902, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1092, "身だしなみオッケー！\nアイドルはいつでも\n完璧でいなくちゃ！", 102902, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1093, "ふぅ…\nこんな姿、ファンには\n見せられないな～", 102902, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1094, "かわいい～♪\nこれは強力な\nライバル登場かな！", 102902, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1095, "みんなのイメージする\nアイドルの香りは\nこんな感じかな～", 102902, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1096, "体も暖まったし！\nダンスレッスンに\n行こうかな♪", 102902, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1097, "ベストタイム更新！\nライブに向けて\n体力つけないと…", 102902, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1098, "太っちゃったら\nどうしよう…？\nでも手が止まらない～", 102902, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1099, "この恋愛小説ステキ！\n私もアイドルじゃ\nなかったら…", 102902, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1101, "手裏剣の数が\n合わないデス！\nどこかに落とした\nデスか…？", 103001, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1102, "クセモノ、隠れても\nむだデス！\n…気のせいデシタ", 103001, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1103, "金は天下の\nマワリモノ！\nところで、\nどういう意味デス？", 103001, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1104, "むむ、アクダイカンの\n気配がするデース", 103001, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1105, "クニトリ合戦、\n楽しみデース！", 103001, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 1106, "ショーグンと\n出会えて、\nワタシは果報者デス！", 103001, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1107, "最近、ショーグンの\nそばにいると\nドキドキするデス…", 103001, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1108, "なんでも\n命令するデス！\nショーグンの命は\n絶対デス！", 103001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1109, "あっ、ショーグン、\n出陣はいつで\nゴザルデス！？", 103001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1110, "さ、さすが\nショーグンデス…\nいつの間に背後を！？", 103001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1111, "出陣の合図に\n備えるデス！\nほら貝は\nどこデスか！？", 103001, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 1112, "見つめられると\nドキドキするデス…\n新しい忍法デスか？", 103001, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1113, "ショーグン！\nえへへ…これからも\nおそばに置いて\nクダサイね？", 103001, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 1114, "おはようデス！\n今日も一日\n忍法日和デス！", 103001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 1115, "そろそろお腹\n空いてきたデス。\n昼ごはんにするデス！", 103001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 1116, "明日もきっと\nいい日デスよ、\nショーグン！", 103001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1117, "ちょんまげに\nしてみるのも\n面白いかもデス！", 103001, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1118, "心地よくて\n眠くなってきマス…\nもしやテキコクの\n罠デスか！？", 103001, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1119, "これを変わり身の\n術に…かわいそう\nだから止めるデス", 103001, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1120, "ワビサビというもの\nデスね！\n素晴らしいデス！", 103001, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1121, "身体を暖めて\nイクサに向かうデス！", 103001, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1122, "アクロバットな忍法を\n使うには、体を\n鍛えないと危険デス！", 103001, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1123, "うーん、\n美味しいデス…\n幸せデス～", 103001, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1124, "これは…\nＢＵＳＨＩＤＯ…\nデスか？", 103001, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1126, "ふんふんふ～ん\n武器の手入れは\n念入りにしマスよ～", 103002, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1127, "クセモノ、隠れても\nむだデス！\n…気のせいデシタ", 103002, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1128, "金は天下の\nマワリモノ！\nところで\nどういう意味デス？", 103002, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1129, "むむ、アクダイカンの\n気配がするデース", 103002, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1130, "クニトリ合戦\n楽しみデース！", 103002, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1131, "最近、ショーグンの\nそばにいると\nドキドキするデス…", 103002, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1132, "ショーグンの身は\n何があってもワタシが\n守るデス！", 103002, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1133, "あっ、ショーグン\n出陣はいつで\nゴザルデス！？", 103002, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1134, "イクサの前に\nホネ休めも大事デスよ\nゆっくりするデース！", 103002, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1135, "さ、さすがショーグン\nデス…いつの間に\n背後を！？", 103002, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1136, "一緒に忍法考える\nデス！\nショーグン用の\n忍法も必要デスね！", 103002, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1137, "見つめられると\nドキドキするデス…\n新しい忍法デスか？", 103002, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1138, "ショーグン！\nえへへ…これからも\nおそばに置いて\nクダサイね？", 103002, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 1139, "いい朝デス！\nトーゴク風の朝ゴハン\n作るデス！", 103002, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 1140, "もうお昼デス\nお昼寝するデスか？", 103002, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 1141, "そろそろおネム\nデース…\n一緒に寝るデス？", 103002, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1142, "服の乱れは心の乱れ\nデス！　きっちり\nしないといけないデス", 103002, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1143, "心地よくて眠く\nなってきマス…\nもしやテキコクの\n罠デスか！？", 103002, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1144, "これを変わり身の\n術に…かわいそう\nだから止めるデス", 103002, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1145, "ワビサビというもの\nデスね！\n素晴らしいデス！", 103002, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1146, "身体を暖めてイクサに\n向かうデス！", 103002, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1147, "ショーグンのお役に\n立つため、修行\nあるのみデス！", 103002, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1148, "美味しいデス…\n酢昆布を和えて\nみたいデス", 103002, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1149, "物事を知って\n来たるべきイクサに\n活かすデス", 103002, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1151, "お父さん、そろそろ\n子離れを…しない？\nそうですか…", 103101, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1152, "父は、いつも男性に\nきびしいですね", 103101, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1153, "占ってほしい人は、\nいつでも言って\nください", 103101, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1154, "あなたの手相は…\nて、手を握るのって\n緊張しますね", 103101, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1155, "この部屋、風水的にも\n運気が上がりそうです", 103101, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 1156, "あの人とのこと占って\nみようかな…\n好き…嫌い…\n好き♪…き、嫌い", 103101, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1157, "私との相性ですか？\nそれなら占う必要は\nありませんよ…\nふふっ♪", 103101, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1158, "女難の相が\n出ていますね…\n気をつけてください", 103101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1159, "今日のあなたの運勢は\n大きょ…\n大吉です", 103101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1160, "父と散歩中です。\n毎日決まったコースを\n散歩させないと、\nストレスが溜まるので", 103101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1161, "小僧！\nシノブに触るとは\nいい度胸じゃねぇか！", 103101, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 1162, "あなたに触られると、\n動悸が早くなります\nもしかして心霊現象？", 103101, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1163, "今日もたくさんの幽霊\nに慕われていますね。\n素敵です", 103101, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 1164, "おはようございます\n今日の運勢を\n占いましょうか？", 103101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 1165, "今日のラッキー\nアイテムはドクロです\n良ければ父を\nお貸ししましょうか？", 103101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 1166, "今日のあなたの運気は\n最高潮でした。\nもうすぐ今日は\n終わりますが", 103101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1167, "お父さん。\nいつも、身だしなみに\n気を使ってるけど、\nあまり変わってないよ", 103101, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1168, "一休みしましょう。\n憑かれて疲れました", 103101, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1169, "かわいい…\n付喪神が宿らないかな", 103101, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1170, "お父さん、\nリラックスし過ぎると\n成仏しちゃうよ？", 103101, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1171, "気持ちいいです…\n極楽浄土みたい…", 103101, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1172, "はぁはぁ…\nお、お父さん、\n代わりに運動して…", 103101, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1173, "お父さんも\n食べたいの？\n死んでるのに？", 103101, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1174, "この本…\nもしかして予言書？", 103101, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1176, "ここにいる幽霊は\nみんな幸せそうです", 103102, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1177, "お父さんが\n女の子ばかりに\n声をかけるので\n困っています", 103102, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1178, "成仏はうれしいこと\nです…でも友達が\n減るのは寂しいですね", 103102, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1179, "幽霊に好かれる人は\n素敵な方です\nもちろん、あなたも", 103102, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1180, "お父さん、そろそろ\n子離れを…しない？\nそうですか…", 103102, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1181, "生きている人に\n好かれるには、\nどうすればいい\nのでしょう？", 103102, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1182, "お父さん、どうしたら\nあの人のことを\n認めてくれますか？", 103102, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1183, "この部屋、風水的にも\n運気が上がりそうです", 103102, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1184, "シノブには指一本\n触れさせん！\nガブッ！！", 103102, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1185, "あなたの手相は…\nて、手を握るのって\n緊張しますね", 103102, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1186, "ラップ音？\nいえ、父のいびきです", 103102, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1187, "私との相性ですか？\nそれなら占う必要は\nありませんよ…\nふふっ♪", 103102, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1188, "前世って信じますか？\n私とあなたは\nもしかすると…", 103102, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 1189, "今日のラッキー\nアイテムはドクロです\n良ければ父を\nお貸ししましょうか？", 103102, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 1190, "明るい時間は\nあまり得意では\nないんです…", 103102, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 1191, "夜のお散歩に\n行きませんか？\n知り合いの幽霊に\n挨拶して回るんです", 103102, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1192, "お父さん\nいつも、身だしなみに\n気を使ってるけど\nあまり変わってないよ", 103102, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1193, "うとうと…\nあれ…\n走馬灯が見える…", 103102, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1194, "かわいい…\nお父さんみたい…", 103102, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1195, "お父さん\nリラックスし過ぎると\n成仏しちゃうよ？", 103102, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1196, "快適です…\n幽霊もどんどん\n集まってきますね…", 103102, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1197, "はぁはぁ…\nお、お父さん、\n代わりに運動して…", 103102, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1198, "お父さんの分も、\n私が食べるね", 103102, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1199, "この本の占い\n私の占いと違う…", 103102, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1201, "私だって、一人で\n買い物ぐらい\nできますわ…きっと", 103201, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1202, "そろそろ新しい\n案件を始めないと\nいけませんわね", 103201, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1203, "一日も早く、\n一人前の商人に\nなりたいものです", 103201, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1204, "平和を買うのには\nどれほどのお金が\n必要なのかしら？", 103201, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1205, "ここももう少し広いと\n落ち着くのですけど。\n改築しませんこと？", 103201, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 1206, "あの方はお金で雇った\nわけではないのに\n私といてくださる…", 103201, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1207, "たとえ私が間違っても\nきっとあの方が\n正してくれますわ…", 103201, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1208, "私と、庶民向けの\nお店へ一緒に\n参りませんか？", 103201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1209, "資産運用？\nお幾らほど\n増やしたいのです？", 103201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1210, "どんぶり勘定だとよく\n言われるのですが…\n例えが小さすぎます！", 103201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1211, "お金で解決するのは\n悪いことでは\nありませんわよね？", 103201, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 1212, "その、よろしければ\n私とお散歩など\nいかがです？", 103201, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1213, "もしあなたさまが\n道に迷われるなら、\n私もお供いたします！", 103201, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 1214, "早起きは三ルピの徳と\n申しますが…\nもっとお得ですわね", 103201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 1215, "昼食は町の屋台で\n立ったまま食べて\nみたいですわ！", 103201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 1216, "よろしければ\nお休み前のミルクを\n御一緒にいかが？", 103201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1217, "だらしない身なりでは\n取引相手に信用して\nいただけませんものね", 103201, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1218, "この感触と安らぎ…\n素晴らしい\n費用対効果ですわ…", 103201, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1219, "何の見返りも求めず\nただひたすら愛でて\nいたいのです！", 103201, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1220, "目に見えないものに\nお金をかけるのが\n真の贅沢ですわ！", 103201, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1221, "誰ひとり寒い思いを\nしない町にするために\n私にできる事は…", 103201, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1222, "健康はお金では\n買えませんが\n運動器具は別ですわ！", 103201, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1223, "なかなか\n美味ですわ\nシェフはどちらに？", 103201, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1224, "経済の仕組みを\nしっかりと\n学ばなくては…", 103201, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1226, "そろそろ新しい\n案件を始めないと\nいけませんわね", 103202, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1227, "ええと、これは\nおいくらで買ったん\nでしたっけ？", 103202, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1228, "お金を見せたら\n態度が変わる人は\n信用できませんわ", 103202, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1229, "地図が読めずとも\n道に迷わない方って\nいらっしゃるのですね", 103202, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1230, "この狭さも\n慣れれば\n快適ですわ♪", 103202, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1231, "あの方はお金で雇った\nわけではないのに\n私といてくださる…", 103202, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1232, "たとえ私が間違っても\nきっとあの方が\n正してくれますわ…", 103202, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1233, "一人前の商人に\n相応しい居振る舞いを\nしたいものですわ", 103202, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1234, "幼い頃\nお店屋さんごっこを\nなさいましたか？", 103202, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1235, "資産運用？\nお幾らほど\n増やしたいのです？", 103202, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1236, "今度一緒に…\nパーティにでも\n行きませんこと？", 103202, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1237, "その、よろしければ\n私とお散歩など\nいかがです？", 103202, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1238, "あなたさまの優しさは\nお金には\n代えられませんわね", 103202, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 1239, "なぜだか\n眠り足りないような\n感じがしますわ…", 103202, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 1240, "昼食は町の屋台で\n立ったまま食べて\nみたいですわ！", 103202, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 1241, "よろしければ\nお休み前のミルクを\n御一緒にいかが？", 103202, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1242, "ドレスは汚れていない\nようですが…やだ\n髪がほつれてますわ！", 103202, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1243, "こういったものに\n腰を沈めると…\nき、気が緩んで…", 103202, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1244, "何の見返りも求めず\nただひたすら愛でて\nいたいのです！", 103202, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1245, "香りは質のいいものと\nそうでないものが\nはっきり分かりますわ", 103202, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1246, "誰ひとり寒い思いを\nしない町にするために\n私にできる事は…", 103202, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1247, "目標は遠くに\n置いた方が励みに\nなりますわ！", 103202, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1248, "なかなか\n美味ですわ\nシェフはどちらに？", 103202, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1249, "商人って、物語では\n悪役にされやすいの\nですね…", 103202, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1251, "家の中のものを使って\n物ボケ、考えるべ！", 103301, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1252, "毎日の日課…それは\n炊事、洗濯、大喜利\nだべ！", 103301, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1253, "うちに塀ができたら、\nへー、って言いたい！", 103301, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1254, "オラが作る料理は\nこの後スタッフで\n美味しくいただくべよ", 103301, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1255, "エリザベスも\n連れてきてぇ～", 103301, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 1256, "あんちゃんも、\nエリザベスと同じ位\nめんこいべ♪", 103301, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1257, "オラとあんちゃん、\n息がぴったりに\nなってきたべ…！", 103301, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1258, "おぉ！　そんな急に\nツッコまれたら、\nびっくらこくべやー！", 103301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1259, "ネタはオラが書くけど\nあんちゃんが\n書きたかったら\n書いてもいいんだぞ？", 103301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1260, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！", 103301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1261, "押すなよ、\n絶対押すなよ\n…なんて\n言ってないべさ？", 103301, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 1262, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～", 103301, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1263, "将来的には夫婦漫才\nってのも…あ、ひ、\nひとりごとだべさ～！", 103301, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 1264, "おはよう！　一緒に\n牛の乳しぼりいくべ？", 103301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 1265, "お昼だ～腹が減っては\n笑いはできぬ～！", 103301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 1266, "今日もよく働いたべ！\nさ、今からは\nお笑いの練習だ～！", 103301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1267, "舞台に上がる前に\n身なりは整えねぇと\nだめだべ～！", 103301, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1268, "ナイスなイスに\n座りたい、\nなんちゃって！", 103301, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1269, "めんこいのは、\nスベっても許される\nからずるいべな～？", 103301, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1270, "必要以上に\n癒されちまうべや～", 103301, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1271, "ぽかぽかだ…\nウケた会場みてぇだ", 103301, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1272, "お笑い芸人は、\n体が資本だべさ～！", 103301, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1273, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～", 103301, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1274, "ネタ作りのヒントが\n載ってねぇか探すべ", 103301, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1276, "さぁ今日も張り切って\n漫才の練習するべさ！", 103302, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1277, "この部屋でも、商品\n売っていいんだべか？", 103302, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1278, "眼鏡を頭の上に乗せて\n「メガネメガネ～」\nって言う練習した方が\nいいべか？", 103302, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1279, "漫才を持ち時間に\n収めるには、何度も\n練習するしかねぇべ", 103302, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1280, "エリザベスも\n連れてきてぇ～", 103302, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1281, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～", 103302, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1282, "あんちゃんに\n採れたての食材を\n食わしてやりてぇ～", 103302, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1283, "押すなよ\n絶対押すなよ\n…なんて\n言ってないべさ？", 103302, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1284, "あんちゃん、百連\nツッコミ特訓だ～！", 103302, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1285, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！", 103302, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1286, "「なんでだべ～！」\nって言いながら\nツッコむといいべ！", 103302, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1287, "あはは～あんちゃんの\nツッコミはやっぱり\n気持ちいいなぁ～", 103302, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1288, "将来的には夫婦漫才\nってのも…あ、ひ\nひとりごとだべさ～！", 103302, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 1289, "おはよう！\n今日もいい\nネタ作り日和だ！", 103302, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 1290, "お昼だな\nご飯を食べたら、\nネタの練習するべ！", 103302, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 1291, "もうこんな時間だべ\nネタ作りは\n明日にするべ…", 103302, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1292, "うーん…衣装、\nずれてねぇべか？", 103302, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1293, "ナイスなイスに\n座りたい\nなんちゃって！", 103302, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1294, "はう～\nなまらめんこいべや～", 103302, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1295, "必要以上に\n癒されちまうべや～", 103302, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1296, "笑いをとることを\n忘れるほど\nあったけぇべ～", 103302, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1297, "たくさん運動して\n動きでも笑いがとれる\nように頑張るべ～！", 103302, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1298, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～", 103302, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1299, "幅広く教養を\n身につけて\nいいネタ作るべ！", 103302, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1301, "通りにいる\nカップル…爆発\nすればいいのに…", 103401, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1302, "う～ん、仕事なんて\nしないで、\n飲んでたいな～", 103401, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1303, "今日は一日、\n外に出ない！\nもう決めたわ！", 103401, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1304, "お腹空いた…\nけど、外に出るのは\n面倒だなぁ", 103401, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1305, "うぅ…頭がガンガン\nする…昨日、\n飲み過ぎたかな…", 103401, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 1306, "デートとか…\n誘っても嫌がられ\nないかな…？", 103401, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1307, "もしかして運命の\n相手だったり…\nするのかな", 103401, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1308, "やっぱり一杯目が\n一番おいしいわ～！\n…え、見てた！？", 103401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1309, "頼れるお姉さんだって\nところ、もっと見せて\nあげるからね", 103401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1310, "ねえ、一緒に買い物に\n行かない？　その…\nの、飲み物とか…", 103401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1311, "ひっく、うふふ…\nもっとお姉さんと\nお話しようよ～", 103401, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 1312, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…", 103401, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1313, "いつか…\nキミと一緒に飲めたら\n楽しいだろうな", 103401, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 1314, "おはよう…朝は\nちょっとつらいな…\n低血圧なのよ…", 103401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 1315, "おっひるー♪\nなに食べよっかな～♪", 103401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 1316, "ふわあ…\n眠る前にちょっと\n一杯…冗談よ", 103401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1317, "うう…\n髪がボサボサ…\nちゃんとしなきゃ…", 103401, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1318, "自然体でゴロゴロ\nするのって\n幸せだなぁ…", 103401, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1319, "こういうの男の子に\nプレゼントして\nもらいたいな…", 103401, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1320, "うわあ、\nこれ、私の好きな\n香りだー！", 103401, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1321, "ぽっかぽか…\nあとは熱い一杯が\nあれば…！", 103401, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1322, "家でゴロゴロして\n付いちゃったお肉を\n落とさないと…！", 103401, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1323, "もぐもぐ…\nおーいしー！\n…太らないよね？", 103401, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1324, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ", 103401, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1326, "仕事終わりの一杯の\nために、今日も\n頑張らないと", 103402, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1327, "今日は\nのんびり\n過ごそうかな～", 103402, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1328, "あ…\nお皿、洗って\nなかった…", 103402, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1329, "もう少し、大人の\n魅力が出せるように\nなりたいな", 103402, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1330, "素敵な彼氏…\n欲しいなぁ…", 103402, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1331, "彼と私\n釣り合うかな…\n大丈夫よね？", 103402, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1332, "今後のために\nちょっとは花嫁修業\nしてみようかな…？", 103402, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1333, "何事も\nお姉さんと一緒に\n楽しく頑張りましょ？", 103402, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1334, "ね、ねえ！\nキミは年上と年下\nどっちが好きかな？", 103402, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1335, "もう…\n構ってくれないと\n泣いちゃうぞ～？", 103402, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1336, "お姉さんが～\n可愛がって\nあげようか～？", 103402, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1337, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…", 103402, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1338, "お姉さんとチュー\nする？　ふふふ～\n冗談で～す！", 103402, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 1339, "さーて、今日も\nいっちょやるぞー！", 103402, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 1340, "後でのんびり\nストレッチでも\nしようっと♪", 103402, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 1341, "一日よく働いた～！\n自分にごほうび\nあげたいなー♪", 103402, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1342, "仕事の前に、髪とか\n整えないと…", 103402, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1343, "自然体でゴロゴロ\nするのって\n幸せだなぁ…", 103402, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1344, "もう大人だし…\nあんまり可愛いのは\n似合わないかな…？", 103402, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1345, "うわあ\nこれ、私の好きな\n香りだー！", 103402, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1346, "あったかーい…\nだ、だらけ\nちゃいそう…", 103402, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1347, "地道な努力が\nリア充への\n第一歩！", 103402, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1348, "おーいしっ♪\nこのために\n生きてるわねえ", 103402, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1349, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ", 103402, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1351, "大切な人に\n本をすすめるのって\nけっこう難しいなぁ", 103801, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1352, "き、きらーん☆\nお姉ちゃんに\n似てるかな…？", 103801, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1353, "そろそろ好きな\n作家さんの新刊が\n出るから楽しみ…", 103801, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1354, "ちょっと、休憩…\n本でも読もうかな", 103801, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1355, "もっとたくさんの本に\n囲まれて生活したいな", 103801, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 1356, "むかしむかしある所に\nシオリと\nあの人が…", 103801, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1357, "この本の主人公、\nあの人に\n似ているかも…\nかっこいいな", 103801, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1358, "ケホケホ…\nコンコン…\nゴホッゴホッ…\nへ、平気ですよ？", 103801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1359, "あ…お姉ちゃんと\n約束があったんだ…", 103801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1360, "しおりを探している？\nあ、本に挟む方ですね\nドキドキしました…", 103801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1361, "最近調子がいいのは\n牧場の牛乳やチーズの\nおかげでしょうか？", 103801, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 1362, "読みたい本も\nあなたと行きたい所も\n増えるばっかりです", 103801, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1363, "私の病気、\n悪化してるのかも！？\nすごく胸がドキドキ\nするんです…", 103801, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 1364, "えっと、朝食の前には\nこの薬を飲んで…\n食後にはこの薬を…", 103801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 1365, "ふぅ…午前中\n動いたので体力が…\nすこし休みますね", 103801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 1366, "早く寝ないと\n体に良くないですよね\nでも気になっている\n本があって…", 103801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1367, "寝てばかりなので、\n髪が乱れてないかな\n…？", 103801, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1368, "少し休もう\n…やっぱり\nたくさん休もう", 103801, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1369, "かわいいものが\n枕元にあると\n安心できるんですよ", 103801, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1370, "いい香り…\n病は気からと言うので\n雰囲気も大事ですね", 103801, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1371, "こうして温まって\nいると、病気も\n治る気がします", 103801, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1372, "運動…イメージ\nトレーニングだけじゃ\nだめでしょうか…", 103801, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1373, "少食ですけど\nこれならたくさん\n食べられそうです", 103801, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1374, "続きが気になって\n読むのを止める\nタイミングが…", 103801, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1376, "しっかり休まないと\n早く回復するように", 103802, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1377, "き、きらーん☆\nお姉ちゃんに\n似てるかな…？　", 103802, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1378, "病室よりも…\nずっとこの部屋に\nいたいですね…　", 103802, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1379, "そろそろ好きな\n作家さんの新刊が\n出るから楽しみ…", 103802, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1380, "大切な人に\n本をすすめるのって\nけっこう難しいなぁ", 103802, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1381, "むかしむかしある所に\nシオリと\nあの人が…", 103802, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1382, "あの人の\nためにも早く\n病気を治さないと…", 103802, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1383, "ケホケホ…\nコンコン…\nゴホッゴホッ…\nへ、平気ですよ？", 103802, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1384, "新記録です！\n一週間連続、\n平熱を保ちました！", 103802, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1385, "あ…お薬の時間…\n苦いけど我慢しなきゃ　", 103802, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1386, "最近調子がいいのは\n牧場の牛乳やチーズの\nおかげでしょうか？", 103802, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1387, "しおりを探している？\nあ、本に挟む方ですね\nドキドキしました…", 103802, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1388, "私の病気\n悪化してるのかも！？\nすごく胸がドキドキ\nするんです…", 103802, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 1389, "えっと、朝食の前には\nこの薬を飲んで…\n食後にはこの薬を…", 103802, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 1390, "…お昼の薬？\nあ、飲み忘れてました\nありがとうございます", 103802, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 1391, "この薬を飲むと\n眠くなっちゃうんです\nでもまだ、あなたと\nお話したいし～", 103802, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1392, "寝てばかりなので、\n髪が乱れてないかな\n…？　", 103802, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1393, "少し休もう\n…やっぱり\nたくさん休もう", 103802, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1394, "この子がいれば\n病室に一人でも\n寂しくないですね", 103802, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1395, "いろんな効能が？\n健康になる香りも\nあるのでしょうか？", 103802, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1396, "こうして温まって\nいると、病気も\n治る気がします", 103802, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1397, "私も、少しは\n体力をつけないと\nいけませんよね　", 103802, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1398, "いっぱい食べて\n健康な体にならないと", 103802, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1399, "続きが気になって\n読むのを止める\nタイミングが…", 103802, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1401, "友達ができたら、\n山の上でお弁当…\n夢が広がります…", 104001, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1402, "あ、忘れずに\nお花に水を\nあげなきゃ…", 104001, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1403, "お菓子を作って、\nみなさんに振る舞って\n友達作戦！　とか…", 104001, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1404, "そろそろ新しい\n草人形を作って\n友達作りの特訓を…", 104001, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1405, "植物のお世話って、\nぼっちにとっては、\n救い…なんです…", 104001, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 1406, "ＢＢ団の活動日…\nまだかな…\nうふふふっ", 104001, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1407, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…", 104001, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1408, "あ、こんにちは！\n今日もＢＢ団で\nお世話になります！", 104001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1409, "あの…私の友達作りの\n練習方法、間違って\nませんよね…？", 104001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1410, "そうだ、今度、\n森に一緒に木の苗を\n植えに行きませんか？", 104001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1411, "人とお話するのって、\n私、大好きです！\n友達ふやすぞ～！", 104001, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 1412, "あなたと話してると、\nまるで私がぼっちじゃ\nないみたいです！", 104001, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1413, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです", 104001, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 1414, "おはようございます！\n今日もＢＢ団、\nがんばるぞー！", 104001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 1415, "なんだか、お腹が\n空いてきました。\nお昼ごはんー♪", 104001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 1416, "もう眠いかもです…\n明日は友達、\nできるといいなあ…", 104001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1417, "私がもっと\n可愛ければ、\n友達できるかなあ？", 104001, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1418, "ふわあ…のんびり\nしすぎないように\n気を付けなきゃです", 104001, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1419, "かわいいなあ。\nこの子とも\n友達になりたい…", 104001, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1420, "いい香りです…\n精神統一して\n友達のイメトレ…", 104001, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1421, "灯りを見てると、\n心がなごみます～", 104001, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1422, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！", 104001, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1423, "おいしいです…\n私に友達がいたら、\n分けてあげたいな", 104001, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1424, "友達作りの\n参考になりそうなこと\n書いてないかな～？", 104001, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1426, "友達作りは、焦らず\n慌てず、じっくりと…", 104002, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1427, "ひとつ屋根の下\nって感じ…\nいいですねえ…", 104002, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1428, "煮汁、煮汁が\n噴きこぼれて\nいませんか～", 104002, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1429, "今度、みなさんと\nピクニックとか…\nいってみたいな", 104002, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1430, "ぼっちでもいい\nたくましく育てば\n…なんて", 104002, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1431, "最近、毎日あの人の\nこと考えちゃいます\nどうしてだろう…", 104002, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1432, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…", 104002, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1433, "あ！　ＢＢ団の活動\nですか？　嬉しくて\n飛び跳ねちゃいます！", 104002, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1434, "あなたとのお話で友達\n作りの練習になるなら\n一石二鳥です！", 104002, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1435, "私少しは人と話しても\nテンパらなくなって\nきましたかね…？", 104002, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1436, "あの、友達作りの\n新しい作戦\n考えてみたんですっ！", 104002, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1437, "あなたとお話する時の\nこの気持ち、私の宝物\nなんです…えへへ", 104002, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1438, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです", 104002, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 1439, "朝ですね！\nこんないい朝は\n友達作りのチャンス！", 104002, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 1440, "あの…そろそろお腹\nすきませんか？\nお昼ごはんは…", 104002, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 1441, "もう眠いかもです…\n明日は友達\nできるといいなあ…", 104002, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1442, "私…見た目\nそこまで変じゃ\nないですよね…？", 104002, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1443, "ほわあ～…\nふかふかって\nいいものですね～", 104002, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1444, "この子みたいな\nあいくるしさを\n目指したいです！", 104002, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1445, "灯りを見てると\n心がなごみます～", 104002, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1446, "ぬくぬくで、なんだか\n眠くなっちゃいますね", 104002, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1447, "身体を鍛えて\nみなさんを\n守ります！", 104002, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1448, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！", 104002, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1449, "友達作りの\n参考になりそうなこと\n書いてないかな～？", 104002, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1451, "唱喚の歌は、みだりに\n歌っていいものでは\nないんです", 104201, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1452, "ここでアイドルの\n練習は、さすがに\n迷惑ですよね？", 104201, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1453, "練習してもあんまり\n疲れなくなった…\nアイドルのおかげかな", 104201, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1454, "人々の平和を守る旅に\n同行してくれる人を\n探していて…", 104201, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1455, "あとで曲を復習して\nおかないと…", 104201, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 1456, "踊りを見られるのは\nまだ恥ずかしいけど\nあの人になら…", 104201, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1457, "浜辺で曲を書こうと\n思うんですけど…\n一緒に行きませんか？", 104201, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1458, "いつも応援ありがとう\nございます♪　これは\nあ、握手の練習です！", 104201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1459, "え、もう練習の時間？\nごめんなさい\nすぐ準備します！", 104201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1460, "守るべき人たちに\n笑顔でいてもらうのも\n大切なんですね", 104201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1461, "歌える曲を増やそうと\n思って。どんな曲が\n聞きたいですか？", 104201, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 1462, "～～♪　あれ？\n今私、歌ってました？\n気づきませんでした", 104201, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1463, "…歌って欲しい？\n喜んで♪\nどの歌にしましょう？", 104201, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 1464, "おはようございます♪\n少し歌の練習を\nしてきますね", 104201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 1465, "もうお昼なんですか？\n練習に夢中で\n気づきませんでした", 104201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 1466, "明日の予定は…\nアイドルはやることが\n多いみたいで…", 104201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1467, "見た目については\n前よりも気をつけてと\nノゾミに言われてます", 104201, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1468, "歌の調子をよくする\nためには休息も\n必要ですよね…", 104201, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1469, "この子たちを\n相手に練習して\nみようかな…♪", 104201, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1470, "…疲れてるのかな\n落ち着けているのが\n自分でもわかります", 104201, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1471, "ここにいると\n穏やかな旋律が\n浮かんできます…", 104201, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1472, "歌うためには\n体力づくりは\n欠かせません", 104201, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1473, "歌った後は\nお腹が空いて\nしまうんです", 104201, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1474, "音楽について\n理論も勉強\nしないと…", 104201, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1476, "歌うことの楽しさ\n分かってきました\nもっと歌いたいな…", 104202, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1477, "練習してもあんまり\n疲れなくなった…\nアイドルのおかげかな", 104202, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1478, "精霊も強くなったし\nもっとたくさんの人を\n守りたいです", 104202, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1479, "最近、よく頭の中に\n歌が浮かんできます\n不思議な感覚…", 104202, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1480, "楽譜を買いました\n思いついた歌を\n書き留めておくために", 104202, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1481, "浜辺で曲を書こうと\n思うんですけど…\n一緒に行きませんか？", 104202, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1482, "私の中にある\n私だけの歌…\n作ってみようかな？", 104202, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1483, "守るべき人たちに\n笑顔でいてもらうのも\n大切なんですね", 104202, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1484, "歌える曲を増やそうと\n思って。どんな曲が\n聞きたいですか？", 104202, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1485, "魔物退治ですか！？\n…違います？\n良かった…", 104202, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1486, "いつも応援ありがとう\nございます♪　これは\nあ、握手の練習です！", 104202, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1487, "…歌って欲しい？\n喜んで♪\nどの歌にしましょう？", 104202, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1488, "～～♪　あれ？\n今私、歌ってました？\n気づきませんでした", 104202, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 1489, "呼吸を深く、大きく…\nこれでちゃんと\n目が覚めますよ", 104202, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 1490, "少し休んだら\n午後の練習に\n行ってきますね", 104202, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 1491, "曲を作っていたら\nもうこんな時間…\nおやすみなさい", 104202, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1492, "見た目については\n前よりも気をつけてと\nノゾミに言われてます", 104202, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1493, "あ～…♪\nよく体がほぐれます…\nいい歌が歌えそうです", 104202, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1494, "かわいいと思う\n気持ちを、歌にしても\nいいかも知れませんね", 104202, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1495, "…疲れてるのかな\n落ち着けているのが\n自分でもわかります", 104202, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1496, "ここにいると\n穏やかな旋律が\n浮かんできます…", 104202, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1497, "もっと体力をつけて\n最高のライブを\nファンに届けます！", 104202, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1498, "これでも一応\n体形には気をつけて\n食べてるんですよ？", 104202, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1499, "読書は歌に\n新しい風を\n吹き込んでくれます", 104202, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1501, "店の新メニューでも\n考えるかな", 104301, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1502, "あたしの店から、\n出前を届けようか？", 104301, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1503, "パトロール、\nパトロール…\nここは異常なしかな？", 104301, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1504, "腹へったら食う！\n悩んだら寝る！\nそれが一番だろ？", 104301, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1505, "のんびりし過ぎるのも\n…性に合わねぇな", 104301, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 1506, "そろそろあいつ、\n腹すかしてるん\nじゃないかな？", 104301, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1507, "今日の献立は…\nあいつの好きなものに\nしてやるか", 104301, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1508, "強くなりたいなら、\nあたしがビシバシ\n鍛えてやるよ", 104301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1509, "天気がいい日は\n外で運動したく\nならねぇか？", 104301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1510, "腹減ったか？　なんか\n作ってやろうか？", 104301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1511, "ん？　なんだよ、\n相談事か？\nなんでも言ってくれ", 104301, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 1512, "お前が好きな飯って\nなんだ？\n今度、作ってやるよ", 104301, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1513, "お前って不思議だよな\n一緒にいるとなんだか\n…なんでもねーよ", 104301, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 1514, "気持ちいい朝だな！\n体を動かしたい気分だ\n付き合ってくれるか", 104301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 1515, "昼飯もう食ったか？\nまだなら、あたしが\nなんか作ってやるよ", 104301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 1516, "日中たっぷり動いたら\nそのぶん夜はしっかり\n休んどけよな", 104301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1517, "た、たまにはその…\nかわいい格好とか\nしても…", 104301, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1518, "ふわぁ、\n一休みすっかなぁ～", 104301, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1519, "うっ！　か、かわいい\nじゃねぇか", 104301, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1520, "へー　いい匂いだな\nあいつもこういうの\n好きなのかな…", 104301, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1521, "ふわぁ〜！\n温かいと、つい\nウトウトしちまうな", 104301, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1522, "オラオラオラーーー！\n燃えるぜーーー！", 104301, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1523, "いい味してるぜ！\nうちの店でも\n出してみたいな", 104301, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1524, "文字を読んでると\nどうしても眠く\nなっちまうんだよなぁ", 104301, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1526, "パトロール終了～\n本日も異常なしっと！", 104302, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1527, "あたしの店から\n出前を届けようか？", 104302, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1528, "あいつ、早く\n帰ってこないかな", 104302, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1529, "あいつ\n腹すかせてないと\nいいんだけど…", 104302, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1530, "のんびりし過ぎるのも\n…性に合わねぇな", 104302, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1531, "あいつの事を思うと\n胸があったかくなる？\nなんだコレ…？", 104302, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1532, "今日の献立は…\nあいつの好きなものに\nしてやるか", 104302, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1533, "ん？　なんだよ\n相談事か？\nなんでも言ってくれ", 104302, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1534, "天気がいい日は\n外で運動したく\nならねぇか？", 104302, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1535, "腹減ったか？　なんか\n作ってやろうか？", 104302, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1536, "腹へったら食う！\n悩んだら寝る！\nそれが一番だろ？", 104302, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1537, "あのさ…できれば\nもっとそばに…いや\nなんでもねぇ忘れろ！", 104302, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1538, "お前って不思議だよな\n一緒にいるとなんだか\n…なんでもねーよ", 104302, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 1539, "気持ちいい朝だな！\n体を動かしたい気分だ\n付き合ってくれるか？", 104302, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 1540, "昼飯もう食ったか？\nまだなら、あたしが\nなんか作ってやるよ", 104302, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 1541, "あんま夜更しすんなよ\n明日に響いたら\n大変だろ？", 104302, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1542, "た、たまにはその…\nかわいい格好とか\nしても…", 104302, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1543, "ふぅ～♪\n疲れがとれるな～", 104302, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1544, "うっ！　か、かわいい\nじゃねぇか", 104302, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1545, "へー　いい匂いだな\nあいつもこういうの\n好きなのかな…", 104302, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1546, "ふわぁ～！\n温かいと、つい\nウトウトしちまうな", 104302, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1547, "オラオラオラーーー！\n燃えるぜーーー！", 104302, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1548, "いい味してるぜ！\nうちの店でも\n出してみたいな", 104302, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1549, "料理だけじゃなく\n経営術とかも\n学んどかねぇと…", 104302, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1551, "最近なにも\nしてくれません…\n焦らしてるんですね", 104501, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1552, "ぐふふ、じゅるり…\nい、いけない、また\nいつの間にかよだれが", 104501, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1553, "好きな武器といえば\n剣より槍より\nやっぱり鞭…ぐふふ", 104501, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1554, "「無数の触手が\nクウカの柔肌に\n食い込み」…ぐふふ", 104501, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1555, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！", 104501, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 1556, "ドＳさんを登場\nさせるだけで、妄想に\n無限の広がりが！", 104501, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1557, "あっ…\nジロジロ見てたの\n気づいちゃいました？", 104501, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1558, "視線を感じると\n思ったら…やはり\nドＳさんでしたか", 104501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1559, "今日は\nどんなヒドイことを\nしてくれるんですか？\nぐふふふ", 104501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1560, "いつの間にそこに！？\nまさかクウカを監視\nしていたのですか！？", 104501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1561, "クウカに触るつもり\nですか？　触るつもり\nなのですねっ！？", 104501, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 1562, "クウカにナニをする\nつもりですか…\n期待してしまいます！", 104501, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1563, "ドＳさんの視界に入る\nだけでドキドキが…\nこれが恋！？", 104501, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 1564, "おはようございます。\n朝からお盛んですね？\nぐふふ…", 104501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 1565, "こんなにも日の高い\nうちからドＳさんは…\nじゅるり", 104501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 1566, "夜はこれから、\nですよ…\nぐふ、ぐふふふ…", 104501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1567, "キレイにおめかし\nしたのに力ずくで…\nぐふふふ", 104501, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1568, "ソファの背にクウカを\n押し付けて…ぐふふ\nいいですね\nソファドン", 104501, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1569, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ", 104501, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1570, "香りを吸ったクウカの\n頬は紅潮し目はうるみ\nもはや抗う力も…！", 104501, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1571, "ああ、身体が\n火照って…\nぐふ、ぐふふ…", 104501, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1572, "はぁ、はぁ、こうして\nクウカの体力を\n奪ってから…ぐふふ", 104501, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1573, "食欲が満たされたら\nつ、次はやはり…！\nそうなのですね！？", 104501, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1574, "この本の下に、\nそういう本を\nカムフラージュ？", 104501, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1576, "「ドＳさんと踊り子\n乱舞の一夜」…\n次の日記はこれで…", 104502, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1577, "最近なにも\nしてくれません…\n焦らしてるんですね", 104502, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1578, "も、妄想が\n足りません…\n禁断症状がっ…！", 104502, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1579, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！", 104502, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1580, "次はどんなドＳを\nされるのか、想像する\nだけで…ぐふふふ", 104502, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1581, "じっとしてると\nドＳさんのことばかり\n考えてしまいますね", 104502, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1582, "妄想が止まらなくて\nクウカどうにかなって\nしまいそうです！", 104502, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1583, "ドＳさんといると体が\n火照って…クウカは\nいやらしい子です！", 104502, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1584, "あっ…\nジロジロ見てたの\n気づいちゃいました？", 104502, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1585, "ドＳさんのおかげで\n新しい扉が開けそう\nです…じゅるり", 104502, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1586, "いやー！　ドＳさんに\n襲われ…襲って\nくれないんですか？", 104502, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1587, "クウカはドＳさんに\n身も心も堕とされて\nしまったのですね", 104502, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1588, "ドＳさんがお望みなら\nクウカは\nどんなことだって！", 104502, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 1589, "朝だからこそ\nできる妄想が\nあります…ぐふふ", 104502, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 1590, "こんな日の高いうち\nからドＳさんは…\nじゅるり", 104502, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 1591, "今夜は一晩中…\nなんですね？\nぐふふ…", 104502, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1592, "意外と難しいんですよ\n正しい亀甲縛りと\nいうものは…", 104502, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1593, "くつろいで油断\nしているスキに何を\nされるのでしょう！？", 104502, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1594, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ", 104502, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1595, "これは禁断の香り…\nああっ一段階上の\n妄想が開花するうぅ！", 104502, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1596, "ああっ、暖気がまるで\nロウソクのように\nクウカを攻めますっ！", 104502, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1597, "激しい攻めを受けきる\nためには、鍛えないと\nいけないのですね", 104502, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1598, "こんなにいっぱい\n入りません…\nお腹にっ！", 104502, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1599, "どんな本も\nよく見ると\nいやらしいです…！", 104502, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1601, "この前遊んだ猫、\nモッフモフ\nだったにゃあ…♪", 104601, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1602, "ほっぺやお腹が、\n肉球ぐらいぷにぷに…\nこれはまずいにゃ…", 104601, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1603, "大きな仕事が\n決まったにゃ！\n腕が鳴るにゃ～♪", 104601, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1604, "おやつのたい焼き、\nつぶあんとこしあん、\nどっちに\nしようかにゃ？", 104601, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1605, "うにゃー…\nもうちょっとお昼寝\nしたかったにゃ…\nむにゃむにゃ…", 104601, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 1606, "キミに呼ばれると、\n猫まっしぐらな気分に\nなっちゃうにゃ♪", 104601, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1607, "キミのおかげで\nギルドの仕事も\n大成功だにゃ♪\nありがとにゃ♪", 104601, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1608, "うずうず…その指、\nじゃれついても\nいいかにゃ？", 104601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1609, "にゃ？　あたしに何か\nご入り用かにゃ？\nお安くしとくにゃ～♪", 104601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1610, "うーん、なんだか\n退屈にゃ～\n遊んで、遊んで～？", 104601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1611, "素早い動きのコツ？\nタダじゃ教えて\nあげられないにゃ～♪", 104601, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 1612, "そんなにあたしを\nなでたいにゃ？\nキミならなでさせて\nあげてもいいかにゃー", 104601, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1613, "ふにゃあ、ねえねえ、\n一緒にゴロゴロ\nしようにゃあ？", 104601, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 1614, "いい朝にゃ！\n顔を洗ってくるにゃ！", 104601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 1615, "むーん、そろそろ\nお昼…猫たちにも\n猫缶を…", 104601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 1616, "ふわあ…眠いにゃ…\n猫は布団で\n丸くなるにゃ", 104601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1617, "客商売では、\n外見のチェックも\n欠かせないのにゃ！", 104601, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1618, "うにゃ～！\nお昼寝に\nぴったりにゃ", 104601, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1619, "これ…量産したら\n子どもにバカ売れ\nしそうだにゃ！", 104601, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1620, "この灯り、まるで\n猫みたいな\n癒し効果にゃ！", 104601, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1621, "ふにゃあ、\nあったかいと猫は丸く\nなっちゃうのにゃあ…", 104601, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1622, "本当は外で\n運動したいん\nだけどにゃ", 104601, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1623, "むーん、\n美味しいにゃー！\nたい焼きに\n匹敵するにゃ！", 104601, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1624, "ふむふむ…猫の\nしつけ方は、と…", 104601, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1626, "かわいいネコの置物を\nゲットしたにゃ！\nどこに飾ろうか\nにゃ～♪", 104602, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1627, "仕事の交渉がうまく\n行ったにゃ～♪\nいい気分だにゃ～♪", 104602, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1628, "ねこじゃらしには\n食いついちゃうにゃ\nびしっ！　びしっ！", 104602, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1629, "ほっぺやお腹が、肉球\nぐらいぷにぷに…\nこれはまずいにゃ…", 104602, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1630, "さっきのたいやき\nおいしかったにゃ♪\nごろごろごろ…", 104602, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1631, "手作りのたい焼き\nあいつは食べて\nくれるかにゃ…？", 104602, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1632, "あいつには、いつか\nたっぷり恩返しが\nしたいにゃ…", 104602, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1633, "にゃーに？　あたしの\nことがそんなに気に\nなるのかにゃ？", 104602, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1634, "あたしのこと…\nしっかり捕まえ\nといてにゃ？", 104602, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1635, "あたしの力が\n必要かにゃ？　猫の手\n貸してあげるにゃ♪", 104602, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1636, "たいやきとクレープを\n合わせれば…\n新しい商売のニオイが\nするにゃ…！", 104602, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1637, "ふにゃあ、ねえねえ\n一緒にゴロゴロ\nしようにゃあ？", 104602, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1638, "キミと一緒にいると\n楽しくなってつい\nじゃれちゃうにゃ！", 104602, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 1639, "いい朝にゃ！\n顔を洗ってくるにゃ！", 104602, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 1640, "そろそろおっひる～\nにゃーんか\n食べたい…", 104602, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 1641, "うーん、眠くなって\nきたにゃ。あたしは\n夜行性じゃないのにゃ", 104602, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1642, "にゃにゃ…\n髪が乱れてるにゃ", 104602, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1643, "ふっかふかにゃ～\n猫がねころん\nじゃうにゃ", 104602, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1644, "これ、猫と匹敵する\nぐらいかわいいにゃ！", 104602, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1645, "猫でもこわくない\n優しい灯りにゃ！", 104602, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1646, "ぽっかぽか\nしあわせな気分にゃ～", 104602, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1647, "雨の日でも運動\nできるのは、濡れ\nなくて便利だにゃ！", 104602, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1648, "美味しいにゃ～！\nこれ、朝昼晩\n食べたいにゃ！", 104602, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1649, "本を読んで\n猫の気持ちを\n勉強するのにゃ！", 104602, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1651, "内職手伝える人\nいないかしら？", 104801, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1652, "次の依頼は、\nこういう計画で…\nうん、完璧ね！", 104801, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1653, "次の特売セールに\n向けて、しっかり\n準備しないと！", 104801, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1654, "配達の後に売り子\n次に採取のバイト…\nうん、効率的ね", 104801, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1655, "一日に受ける\nバイトの数、もう少し\n増やしたいわね", 104801, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 1656, "師匠として\n頼りにされたいし\nもっと頑張らなきゃ！", 104801, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1657, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね", 104801, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1658, "あら、\nちょうどよかった\n今から一緒に\nバイトでもどう？", 104801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1659, "相談事かしら？\nそれくらいなら\n無料でいいわよ", 104801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1660, "背後の気配なら\nすぐ分かるわ\n試してみる？", 104801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1661, "道具やお金の管理は\n大丈夫？　手伝うから\nいつでも言って", 104801, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 1662, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね", 104801, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1663, "ねえ、あの…\n私はキミにとって…\nいや、何でもないわ", 104801, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 1664, "おはよう！\n今日も朝から\nバイトよ！", 104801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 1665, "お昼時ね。\n少し休憩したら、\nまた働くわよ！", 104801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 1666, "もうこんな時間！\n明日もバイトで\n朝早いのよ…", 104801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1667, "接客業の依頼が\nある時は、外見も\nキレイにしないとね", 104801, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1668, "はぁ～バイトで\n疲れたから癒される～", 104801, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1669, "これを内職で\n作ったら\n儲かるかしら？", 104801, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1670, "リフレッシュできたわ\nこれでまた、バリバリ\n働けるわね！", 104801, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1671, "風邪を引いたら効率が\n落ちちゃうから、\n暖めないと！", 104801, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1672, "働き続けるには\n健康第一！　それには\n運動が一番よ", 104801, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1673, "もぐもぐ…\n腹が減っては\n戦ができぬ、よ！", 104801, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1674, "日々絶えず学んで、\n生活に活かすのよ！", 104801, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1676, "家計簿をつけなきゃ！\n覚えている時に\nやっておかないとね", 104802, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1677, "次の依頼は\nこういう計画で…\nうん、完璧ね！", 104802, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1678, "ギルドの経費\nあとちょっとだけ\n削れそうね…", 104802, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1679, "少しでも時給のいい\nバイトがしたいけど\nなかなか\n出会えないのよ", 104802, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1680, "今度のバイトも\nなかなか大変そう\nね…腕が鳴るわ", 104802, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1681, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね", 104802, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1682, "私、どうしたんだろ？\n仕事中もずっと\n彼のこと考えてる…", 104802, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1683, "この間はヘルプに\n来てくれてありがとう\n頼んで正解だったわ", 104802, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1684, "見つけた！　ちょうど\n頼みたいことが\nあるんだけど、いい？", 104802, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1685, "今から\n休憩するところなの\n一緒にどう？", 104802, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1686, "ん？　どうしたの？\n私のことが\n気になるの？", 104802, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1687, "…たまには甘えても\nいい？　私だって\n弱る時はあるのよ", 104802, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1688, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね", 104802, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 1689, "おはよう！\nいい朝ね。一緒に\nランニングでもいく？", 104802, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 1690, "お昼時ね\n少し休憩したら\nまた働くわよ！", 104802, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 1691, "もうこんな時間！\n明日もバイトで\n朝早いのよ…", 104802, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1692, "女の子らしく…\nどうも忘れちゃう\nのよね", 104802, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1693, "ここで少し\n休んだら\nまたバイトよ！", 104802, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1694, "これを内職で\n作ったら\n儲かるかしら？", 104802, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1695, "仕事から帰ってきて\nこういうのがあると\n素敵ね", 104802, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1696, "しっかり身体を\n温めて、血流を\nよくしなくっちゃ", 104802, 3, 5);
