INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1783, "なーに？\nアタシに甘えたいの？", 105002, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1784, "アタシに合う服\n雑誌で探そうっと！\nサイズじゃないわ\nデザインとかの話よ！", 105002, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1785, "頭撫でないで！\nお子様みたいに\nあつかわないでって\n言ってるのに～！", 105002, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1786, "壁ドンの練習…\nあれ？　壁ドンって\n男がするんだっけ？", 105002, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1787, "ねーねー！\nアタシ、かわいい？\nかわいいわよね？", 105002, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1788, "これから出かけるの？\nしっかりアタシを\nエスコートしてね！", 105002, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 1789, "レディの朝は\n忙しいのよ", 105002, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 1790, "お腹すいたー\nダイエット\nやめちゃおうかなー", 105002, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 1791, "エイジングケアを\nしないとねー", 105002, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1792, "身だしなみチェック\nオッケー！　これで\nいつアイツが来ても\n大丈夫ね！", 105002, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1793, "あ！　これ雑誌で\n見たことあるわ～\nだからきっと\nオシャレなのよ！", 105002, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1794, "あたしがもっと\nオシャレにかわいく\nしてあげようか？", 105002, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1795, "オシャレ～♪\n大人なレディって\n感じよね♪", 105002, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1796, "あったかーい♪\nここで宿題\nしようかしら？", 105002, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1797, "運動したら成長\nしないかしら？\n背とか胸とか…", 105002, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1798, "レディは、食べ物にも\n気を使うのよ", 105002, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1799, "おもしろーい！\n…でも絵本って\n子供っぽいかしら？", 105002, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1801, "魔法と科学\nどっちも極めて\nみせるわ", 105101, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1802, "仕事も片付いたし\n部屋で\n実験実験…♪", 105101, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1803, "たまった治療代の\n請求書を\nまとめないと…", 105101, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1804, "新薬が出来たし\nすぐに試したいけど…\n実験台がいないわ", 105101, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1805, "部屋にこもりすぎると\n曜日の感覚が\nなくなるわね", 105101, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 1806, "あの子の記憶を\n取り戻せる薬…\n作れないものかしら", 105101, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1807, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら", 105101, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1808, "あら、ヒマなら\n薬草の整理を\n手伝って", 105101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1809, "もしかして実験に\n付き合ってくれるの？\nえ？　違う？", 105101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1810, "具合が悪い？\nなら診てあげる\nまずは触診から…", 105101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1811, "植物に興味あるの？\nなら教えてあげるわ\n色々とね…", 105101, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 1812, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？", 105101, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1813, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも", 105101, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 1814, "お昼までに調合用の\n花を集めたいの\n手伝ってくれる？", 105101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 1815, "あら、もうお昼？\nそろそろ一息\n入れましょうか", 105101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 1816, "夜も更けて来たわね\n私は朝まで実験だけど\n見学していく？", 105101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1817, "医者たるもの\nいつだって\n清潔に…♪", 105101, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1818, "いいわねこれ\n仮眠の時に\n使わせてもらうわ", 105101, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1819, "ぬいぐるみなら\n動物も…", 105101, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1820, "いい香り…\n仕事場にも\n置いてみようかしら", 105101, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1821, "ここにいると\nついウトウト\nしちゃうわ…", 105101, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1822, "座ってる時間が多いと\n運動不足になるし\nたまにはいいわね", 105101, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1823, "研究していると\nつい食べるのを\n忘れちゃうのよね", 105101, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1824, "実験もいいけど\nいつか成果を\n本にしないと…", 105101, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1826, "徹夜はツラいけど\n集中すると時間の\n感覚がなくなって…", 105102, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1827, "痛みに悶える顔って\nどうしてあんなに\n興奮するのかしら？", 105102, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1828, "彼に罵られたら\nどんな気分かしら\n意外と快感かも？", 105102, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1829, "毒を薄めると\n薬になるのって\n今でも不思議だわ", 105102, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1830, "綺麗なバラには\nトゲがあるけど\n魅力の一つよね", 105102, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1831, "カレをさりげなく\n惚れさせる方法を\n研究するわよ…♪", 105102, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1832, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら", 105102, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1833, "構って欲しいの？\nじゃあ、君の体を\n調べさせて？", 105102, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1834, "実験に付き合って\n大丈夫よ、試すのは\nただの栄養剤だから", 105102, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1835, "欲しい薬が\nあったら言ってね\n調合してみるから", 105102, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1836, "おしおきされたいの？\nキミにだったら\nいつでもしてあげる", 105102, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1837, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？", 105102, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1838, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも", 105102, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 1839, "朝しか取れない\n植物があるの\n採取しに行くわよ", 105102, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 1840, "お昼時ってやけに\n眠たくなるわよね\n…調べたくなるわ", 105102, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 1841, "今日くらいは\nゆっくり眠りたいわ\n…キミと一緒にね", 105102, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1842, "身なりについて\n研究するのも\n楽しそうね", 105102, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1843, "休息に必要な道具は\n妥協せずに選ぶ！\nこれだけは譲れないわ", 105102, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1844, "たまにはこういう\n可愛らしいものに\n触れないとね♪", 105102, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1845, "いい香り…\n仕事場にも\n置いてみようかしら", 105102, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1846, "仕事がなかったら\nこのまますーっと\n眠りたいのに…", 105102, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1847, "運動しないと\n体型の維持が…", 105102, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1848, "適度な食事も\n大切よね", 105102, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1849, "まさか私が\n恋愛読本を読む\n日が来るなんて…", 105102, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1851, "ぺっぺっ！\n…い、いけないっ！", 105201, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1852, "いっけな～い！\nひづめで床に傷が！", 105201, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1853, "太っちゃったかも～！\n伐採でダイエット\nしなくっちゃ！", 105201, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1854, "きちんと生活すると\n毛づやが良くなる\nみたいなのよね～", 105201, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1855, "みんな、体重\nどれくらいなのかな？", 105201, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 1856, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう", 105201, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1857, "いつか人間の女の子\nみたいなデート、\nしてみたいな～♪", 105201, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1858, "私ね「どこにでもいる\n普通の女の子」って\n言葉が憧れなんだ～♪", 105201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1859, "チャームポイント？\n自分では分からない\nけど…まつげかなぁ？", 105201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1860, "リマリマ～♪\nはっ！？\nわ、私ったら今何を？", 105201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1861, "力仕事ならまかせて！", 105201, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 1862, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪", 105201, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1863, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？", 105201, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 1864, "今朝は寝癖がひどいわ\nしっぽの辺りとか～", 105201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 1865, "ランチは\nおしゃれなカフェで\nしてみたいな～♪", 105201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 1866, "お肌のケアに\nパックしながら\n寝ようかな～", 105201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1867, "やだっ、\nまた寝ぐせついてる！\n直さないと！", 105201, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1868, "ミシッっていった！\n私、重すぎるかも！？", 105201, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1869, "あ！　あなたも\n美人さんね～♪", 105201, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1870, "はぁ、まったり…\n四足歩行しちゃい\nそうだわ…", 105201, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1871, "あったか～い♪\n…私、毛皮だから\n暑すぎるかも", 105201, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1872, "な、なんだか野生に\n帰っちゃいそう！", 105201, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1873, "ぱくぱく、もぐもぐ、\nぺっぺっ…\nやだ、はしたない！", 105201, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1874, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～", 105201, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1876, "素敵ね～、居るだけで\n女子力があがりそうな\nお部屋だわ～", 105202, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1877, "私、大丈夫かしら？\n他の子に怖がられたり\nしないかな…？", 105202, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1878, "動物苑…って\n嫌なギルド名ね…", 105202, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1879, "好き、嫌い、好き…\nあっ！　うっかりして\n花占いのお花\n食べちゃった！", 105202, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1880, "ここ、女の子らしい\nものでいっぱいね～！\nときめいちゃうわ～♪", 105202, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1881, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう", 105202, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1882, "あの人が\n来てくれるまで薪割り\nでもしてよっかな？", 105202, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1883, "一緒にお出かけ？\nうれしいけど…\n心の準備が！\nはわわわ！", 105202, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1884, "どうしたの？\n急に抱きついてきて…\n癒されたいの？", 105202, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1885, "ぺっぺっ！\n…い、いけないっ！", 105202, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1886, "メタモルアップルを\nここで食べたら、\nみんな驚くわよね", 105202, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1887, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？", 105202, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1888, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪", 105202, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 1889, "今朝は寝癖がひどいわ\nしっぽの辺りとか～", 105202, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 1890, "ダイエット中だから\nお昼は抜いてるの～", 105202, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 1891, "お肌のケアに\nパックしながら\n寝ようかな～", 105202, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1892, "ちゃんとかわいく\nしてないと刈られ…\n狩られちゃうもんね！", 105202, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1893, "ミシッっていった！\n私、重すぎるかも！？", 105202, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1894, "あ！　あなたも\n美人さんね～♪", 105202, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1895, "いい香り～\n森の中にいるみたいに\nとっても落ち着く～", 105202, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1896, "素敵～♪　…\nきゃっ！　私の毛が\nたいへんなことに～！", 105202, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1897, "な、なんだか野生に\n帰っちゃいそう！", 105202, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1898, "何を食べるかより\n誰と食べるかが\n大事なのよね", 105202, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1899, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～", 105202, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1901, "ヴァイスフリューゲル\nを盛り上げる方法、\n共に語らおう！", 105301, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1902, "私は子供ではない…！\nが、お菓子は\nいただこう！", 105301, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1903, "部屋はきちんと\n片づけておかなくては\n部屋の汚れは\n心の汚れだ", 105301, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1904, "むむむ…明日\n目が覚めたら、身長が\n伸びていないものか…", 105301, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1905, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…", 105301, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 1906, "あいつとの新たな\n鍛錬が\n待ち遠しいな…！", 105301, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1907, "最近、あいつと\n過ごしていて、毎日が\n充実しているな…", 105301, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1908, "軍人の本分は日々の\n研鑽にあるからな。\n今日も鍛錬に\n行かないか？", 105301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1909, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…", 105301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1910, "貴公にも、飴をくれて\nやろうか？\nあまいぞ～！\n美味しいぞ～！", 105301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1911, "私ともっと高く、\n共に飛ぼう！", 105301, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 1912, "貴公は、本当に頼りに\nなる男だな…いや、\nふとそう思っただけだ", 105301, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1913, "貴公と共にいられる\nことは私の喜びだ。\nこれからも\nよろしく頼むぞ", 105301, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 1914, "うむ、いい朝だ。\n本国の夜明けを\n思い出してしまうな！", 105301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 1915, "そろそろ昼食に\nするか。腹が減っては\n戦はできないからな", 105301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 1916, "ふわああ…私は子供\nではないが…眠い時は\n眠いのだ…", 105301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1917, "身だしなみは、心の\n在りようを映すから\n気をつけなくてはな", 105301, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1918, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…", 105301, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1919, "うむ…このかわいさ…\n本国に持って\n帰りたい…", 105301, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1920, "このともし火、戦いに\n明け暮れる我らの心を\n癒すものだな…", 105301, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1921, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！", 105301, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1922, "うおおおおおっ！\n鍛錬、鍛錬、\n鍛錬っ！！", 105301, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1923, "いつ戦いになっても\n困らぬように、\n腹八分目に\nしておくのだ！", 105301, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1924, "これを読んで、新たな\n戦術を編み出すぞっ！", 105301, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1926, "たまには掃除でも\nするか。部屋が\n綺麗になるのは\n喜ばしいことだ", 105302, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1927, "部屋の中では\n剣を振り回せないのが\n玉に瑕だな…", 105302, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1928, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…", 105302, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1929, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…", 105302, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1930, "家庭を守れない者が\n国を守れるはずが\nない。この部屋も\n私が守るぞ！", 105302, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1931, "あいつは今頃、何を\nしているのだろうな…\nふふっ", 105302, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1932, "あいつと会える日が\nこんなにも待ち遠しく\nなるとはな…", 105302, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1933, "軍人の本分は日々の\n研鑽にあるからな\n今日も鍛錬に\n行かないか？", 105302, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1934, "おお、今日も鍛錬の\n時間だったか！\nすまないな\nすぐ行く！", 105302, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1935, "私は子供ではない！\nが…たまには童心に\n帰りたい時もある…", 105302, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1936, "貴公にも、飴を\nくれてやろうか？\nあまいぞ～！\n美味しいぞ～！", 105302, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1937, "私と歩きたいのか…？\nそうだな。二人で\n散歩でも行こうか", 105302, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1938, "貴公と共にいられる\nことは私の喜びだ\nこれからも\nよろしく頼むぞ", 105302, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 1939, "うむ、いい朝だ\n本国の夜明けを\n思い出してしまうな！", 105302, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 1940, "そろそろ昼時だな\n鍛錬の手を緩めて\n昼食にするか", 105302, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 1941, "寝る前に…あと少し\nだけ、鍛錬を…", 105302, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1942, "ヴァイス\nフリューゲルの一員と\nして恥ずかしくないよう\n身なりを整えなくては", 105302, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1943, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…", 105302, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1944, "うむ…このかわいさ…\n本国に持って\n帰りたい…", 105302, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1945, "このともし火\n戦いに明け暮れる\n我らの心を\n癒すものだな…", 105302, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1946, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！", 105302, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1947, "うおおおおおっ！\n鍛錬、鍛錬\n鍛錬っ！！", 105302, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1948, "いつ戦いになっても\n困らぬように\n腹八分目に\nしておくのだ！", 105302, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1949, "うむ…脳細胞が\n活性化してきたぞ\n読書は大事だな", 105302, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1951, "私たちの騎空艇\nどんなデザインに\nしよう…？", 105701, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1952, "団員募集の\nポスター\n作ろうっと♪", 105701, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1953, "父さん…\n今頃どこで\n何してるんだろ？", 105701, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1954, "騎空艇に乗って、\n誰もいったことがない\n場所を見つけたいな", 105701, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1955, "父さん以上の\n世界一の\n騎空士になりたい！", 105701, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 1956, "仲間と行く空の旅…\n何度想像しても\nドキドキするなぁ", 105701, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1957, "父さん…\n私にもね、大切な\n仲間ができたよ", 105701, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1958, "仕事の依頼？\nすぐに支度\nしてくるね！", 105701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1959, "ねえねえ！\n団員募集のポスター\n一緒に作ろうよ！", 105701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1960, "なーにー？\nどうしたの？\n今呼んだよね？", 105701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1961, "今は休憩中だよ？\nだからあなたも\nゆっくりしてて", 105701, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 1962, "私にとってあなたは\nとっても頼りになる、\n自慢の団員だよ！", 105701, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1963, "お父さんに会えたら、\n大切な仲間として\nあなたを紹介するね！", 105701, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 1964, "おはよう！\n体の調子はどう？\n今日は頑張れそう？", 105701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 1965, "ねえねえ、お昼\n食べに行こうよ！\nお腹空いちゃった", 105701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 1966, "…あれ？\nおしゃべりしてたら\nもうこんな時間？", 105701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1967, "どうせならオシャレな\n騎空士って\n呼ばれたいな", 105701, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1968, "ふぅ、疲れた～…\nちょっとだけ\n休んじゃおっと", 105701, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1969, "よし！　キミは\n今から団長代理だ！\n…なんてね♪", 105701, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1970, "いい香り～♪\n気持ちが\n落ち着く～…", 105701, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1971, "これって\n騎空艇に\n置けるかな…？", 105701, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1972, "どんな仕事でも\nこなせるような\n体力がないとね…！", 105701, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1973, "あむっ…うん！\n美味しい～！\n今度作ってみよう♪", 105701, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1974, "空を冒険するには\n色んなことを\n知っておかないと…", 105701, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1976, "…戻らなくなったよ\nどうすれば元に\n戻る…？", 105702, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1977, "暇だよ…\nどこかに強い人は\nいないか？", 105702, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1978, "ジョブチェンジ！\n…叫んでも\n戻らないよ", 105702, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1979, "今のうちに、\n魔物退治の\n仕事に行くね！", 105702, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1980, "ハァ～…\nうん、気の巡りは\n問題ないね！", 105702, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1981, "私の力の源は\n仲間との\n絆だよ！", 105702, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1982, "いつかこの拳を\n空を越えた、星まで\n届かせたいね", 105702, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1983, "功夫が足りない人は\nどうすればいいか？\nもちろん修行だよ！", 105702, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1984, "私はいつも通りに\n喋ってるつもり\nだよ？", 105702, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1985, "どうした？\nもしかして手合せ\nしたいか？", 105702, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 1986, "騎空士としても\n拳法家としても\n目指すは世界一ね！", 105702, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1987, "背中はあなたに\n預けたよ！\nよろしくね！", 105702, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 1988, "あなたと一緒なら\nきっと、今よりもっと\n強くなれるよ！", 105702, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 1989, "ちゃんと起きてるか？\n寝ぼけてたら\nダメよ？", 105702, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 1990, "腹減ったね…\nなにか食べに\n行くよ！", 105702, 2, 2);
