INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2005, "この部屋が、わたしの\n帰る場所です♪", 105801, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2006, "あの人を見ると\n胸が満たされます～\nお腹は空いてますが！", 105801, 1, 101);
