INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2138, "主さまの優しさに\nわたくしはいつも\n癒されています…", 105902, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 2139, "おはようございます♪\n主さまの可愛らしい\n寝顔、堪能致しました", 105902, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 2140, "のんびりした時間帯\nですね…ふぁ～…\nし、失礼致しました", 105902, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 2141, "ふむ、都会は夜でも\nこんなにも、にぎやか\nなのですね", 105902, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2142, "主さまに会う前に\n身なりを整えなくては\nいけませんね", 105902, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2143, "このふかふかの優しさ\n主さまのようです", 105902, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2144, "かわいい♪\n主さまにそっくり♪", 105902, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2145, "この心地よさ…\n主さまのようです♪", 105902, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2146, "ここで洗濯物を\n乾かせるでしょうか　", 105902, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2147, "はぁっはぁっはぁっ\n…すべては\n主さまのため…", 105902, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2148, "都会の味…\nとてもおいしいです\n…もぐもぐもぐ", 105902, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2149, "都会のことを\n学ばないと…\nふむふむ", 105902, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2151, "ふぁ～…あっ\n見られちゃい\nましたか？", 190801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2152, "ギルドハウスの\n使い心地は\nどうですか？", 190801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2153, "ご用があれば\nなんでも言って\nくださいね～♪", 190801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2154, "ときどきみなさんと\nクエストに行きたいな\nって思っちゃいます", 190801, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 2155, "おはようございます！\nお掃除の方、\nはじめていきますね", 190801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 2156, "皆さん、お昼ごはんの\nご要望がありましたら\n用意致しますよ♪", 190801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 2157, "ここは夜でも\nにぎやかですね", 190801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2158, "…\n少しうとうと\nしてしまった", 104701, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2159, "室内だし兜を脱ぐか\n…いや\nやはりやめておこう", 104701, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2160, "部屋にいると\n鎧が蒸し暑くなるが\n我慢せねば…", 104701, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2161, "仕事が門番だからか\n部屋にいても\nじっとしてしまう…", 104701, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2162, "少年と会話したことを\n思いだすと\n自然と楽しくなるな", 104701, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2163, "少年のことを考えると\n顔がにやけてしまう", 104701, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2164, "少年は今頃\n平和に過ごせて\nいるかな…", 104701, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2165, "ん？　悪いが\n兜は脱がないぞ", 104701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2166, "私と遊びたいのか？\nそう言われても\n何をしていいやら…", 104701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2167, "鎧が重くないかって？\n大丈夫。けっこう\n鍛えているからな", 104701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2168, "少年、寒くはないか？\nいや自分がこうなので\n人が薄着に見えてね", 104701, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2169, "君といると、兜の中が\nいつもより暑いんだ", 104701, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2170, "私としゃべりたい？\n物好きだな\nでも、嬉しいよ", 104701, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 2171, "早いな少年\nえ？　私の仕事に\n合わせてくれたのか", 104701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 2172, "昼食の誘い？\n嬉しいけど\n人前では食べないんだ", 104701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 2173, "王宮騎士団の\n日報を書かなければ…\n『何事もなし』、と", 104701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2175, "鎧を着ているから\n感触が分かりづらい\nのが残念だ…", 104701, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2176, "か、かわいいな…\n私には似合わないが\nぎゅ…ってしよう", 104701, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2177, "良い香りだ…\n普段はどうしても\n鉄くさくなるからな", 104701, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2178, "わかっていたけど\n鎧を着たまま暖炉に\nあたると暑い…", 104701, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2180, "兜を脱がずに食べる\nうん、段々コツが\n掴めてきた…", 104701, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2181, "兜があっても\n意外と本くらいは\n難なく読めるんだ", 104701, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2183, "居心地よくて\nゴロゴロしちゃうよ～\n…いつも通りだね", 102601, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2184, "ドングリの木\n近くに生えてない？", 102601, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2185, "快適な寝床があれば\nそれだけで幸せだねぇ", 102601, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2186, "あたしと一緒に\nだらけてくれる人が\nいそうだねぇ", 102601, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2187, "ここなら牧場よりも\n速く配達が\n届きそうだね～", 102601, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2188, "あんパン買ったし\nあいつにも\n分けてあげよう", 102601, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2189, "あいつと一緒に\nだらけられたら\n最高に幸せだね", 102601, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2190, "ドングリでも\nつまみながら\n一緒にゲームしようよ", 102601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2191, "横になりたいから\n枕になっておくれよ～\nあんたあったかいしさ", 102601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2192, "ドングリあげるから\nあんパン買ってきて～", 102601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2193, "う～\n疲れちゃったよ～\nおんぶして運んで～", 102601, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2194, "あんたといると\nリア充って\n感じがするよ", 102601, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2195, "あんたが一生\n世話してくれたら\n人生安泰なんだけどね", 102601, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 2196, "ふわ～っ…\nおはよう…あたしは\nまだ眠いよ", 102601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 2197, "そろそろお昼寝でも\nしたい気分だねぇ", 102601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 2198, "徹夜でゲームするけど\nあんたもどうだい？\nあたしは強いよ", 102601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2200, "あ…だめだこれ。\n気持ち良くて\n落ちそう…", 102601, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2201, "かわいいものに\n囲まれて寝るのも\nよさそうだねぇ…", 102601, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2202, "心身ともに\nリラックスした状態で\nぐうたら出来そうだね", 102601, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2203, "ふい～…\nごくらくごくらく…\n暖かいねぇ", 102601, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2205, "食べてすぐ寝ると\n牛になるって\n本当なのかな？", 102601, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2206, "おー！　この本なら\nあたしも居眠りせずに\n読めそうだよ", 102601, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2208, "ランドソル…\nおっきな街だなぁ…", 106301, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2209, "ロザリア…\nきっと、いるよね？", 106301, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2210, "最近、弓の練習\nサボっちゃってるな…", 106301, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2211, "今日の私は\n森番じゃなくて\n家番だよ！", 106301, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2212, "ちょっとだけ\n慣れてきたかも？", 106301, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2213, "いつか、\nあの人と一緒に…", 106301, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2214, "…なんか最近、\nあの人のこと\nばっかり…", 106301, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2215, "もしよかったら\n街に出かけない？", 106301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2216, "この部屋、\n妖精さんも\n気に入るかも！", 106301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2217, "今日は\nなににしよっか？", 106301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2218, "今日はどこ行く？", 106301, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2219, "たまには二人で…\nな、なんちゃって！", 106301, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2220, "最近、\nずっと楽しいんだ！\nあなたのおかげかな", 106301, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 2221, "おはよう！\n今日も一緒に\n頑張ろうね！", 106301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 2222, "お昼はどうする？\nあなたのオススメに\n連れて行って欲しいな", 106301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 2223, "ランドソルは\n夜でも賑やかだね！", 106301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2225, "わぁっ！\nすっごく\nふわふわだよ！", 106301, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2226, "かわい～！\nぎゅってしたく\nなっちゃうかも！", 106301, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2227, "くんくん\nなんか…いい匂い？", 106301, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2228, "すっごいなー\nあったかいなー！", 106301, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2230, "わぁっ、おいしい！\nもっと食べたい\nかも…！", 106301, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2231, "も、文字は苦手…\nでも、頑張らないと\nだよね…！", 106301, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2233, "にんじんとピーマンを\n食べないでいい方法\nないかな…", 103601, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2234, "カップケーキが\n食べたいな…", 103601, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2235, "予習が終わりました！\nさて、次は復習を\nしないと…", 103601, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2236, "あの人の部屋にいる\n私って…もしかして\nふしんしゃなんじゃ？", 103601, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2237, "男の人は、みんな\nへんたいだと\n聞きました…", 103601, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2238, "あの人に、一緒に\n遊んでほしいけど…\nな、なんていえば？", 103601, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2239, "早く大人になりたいな\n…あの人と同じくらい", 103601, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2240, "ぼ、防犯魔石を\n鳴らしますよ！？", 103601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2241, "へんたい！？　…\nあ、あなたでしたか～\nやっぱりへんたーい！", 103601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2242, "これ宿題です\nやっておいて\nくださいね", 103601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2243, "あ、遅刻ですよ\n待っていたんですから", 103601, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2244, "にらめっこですか？\nあ、あなたの前で変顔\nするのはちょっと…", 103601, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2245, "あの…何歳まで\n子供だと思いますか？", 103601, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 2246, "顔を洗って、着替えて\n朝ごはんを食べて、\n歯を磨いて…", 103601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 2247, "て、鉄棒の練習！？　\nほ、他のことを\nしませんか？", 103601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 2248, "ね、眠くないです\n子供ではありません\nから…ふわぁ", 103601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2250, "ひと休みしよっと\n………うん、\nひと休みおしまい♪", 103601, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2251, "つ、つぶらな瞳には\n弱いんです", 103601, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2252, "リラックスできますね\n集中力も\n上がりそうです", 103601, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2253, "あったかいと\n眠くなっちゃうな…\n勉強しなくちゃなのに", 103601, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2255, "ピーマンもこれくらい\nおいしければ\nいいのに…", 103601, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2256, "この本、むずかしい\n…もっと勉強\nがんばらなくちゃ", 103601, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2258, "今日はみんなに\nなにを読んで\nあげようかしら", 101501, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2259, "子どもたちと同じ\n目線で考えるのが\n大事よね", 101501, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2260, "お天気が良い日は\nみんなでお散歩に\n行きたいわね～", 101501, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2261, "鬼ごっこにかくれんぼ\n…みんなは\nどっちが好きかしら", 101501, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2262, "お遊戯会の配役、\n考え出すと\n大変だわ～", 101501, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2263, "彼は優しいから、\nきっと良い\nお父さんになるわね", 101501, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2264, "彼には私がいないと\n…っていうのは少し\n過保護すぎるかしら？", 101501, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2265, "保育園の行事を\n考えてるんだけど…\n良い案ないかしら？", 101501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2266, "今度、子どもたちと\nおやつを作るから、\n食べに来てね", 101501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2267, "よかったら一緒に\n絵本を作らない？", 101501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2268, "あなたはいつも元気ね\n先生も見習わないと♪", 101501, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2269, "先生は\nずーっと、ずーっと、\nあなたの味方だからね", 101501, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2270, "あなたみたいな\nいい子をお世話できて\n先生は幸せものだわ", 101501, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 2271, "おはよう～\nちゃんと朝ごはんは\n食べた？", 101501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 2272, "お昼寝の時間ね♪\nあなたも一緒に\n寝ましょうか", 101501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 2273, "すっかり真っ暗ね。\n今日も一日、\nよくできました♪", 101501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2275, "ふかふかねぇ♪\n年甲斐もなく、\n飛び跳ねちゃいそう", 101501, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2276, "あらあら、かわいい！\n保育園のみんなに\n見せてあげたいわ", 101501, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2277, "いい香りね～\n保育園にも\n置こうかしら", 101501, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2278, "寒い日は、ここで\n子どもたちに絵本を\n読んであげましょ♪", 101501, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2280, "美味しい～♪\n遠足に持っていったら\nみんな喜びそう", 101501, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2281, "みんなが\n楽しめる読み聞かせの\n方法特集♪　素敵ねぇ", 101501, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2283, "世界の前に、\nこの部屋を征服\nしてやろう♪", 104401, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2284, "拠点とはやはり\n斯くあるべきじゃの", 104401, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2285, "眷属の物はわらわの物\n故に、この部屋は\nわらわの部屋じゃな！", 104401, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2286, "永く眠っている間に\n随分と世界は変わった\nようじゃな…", 104401, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2287, "うるさいのう～\nまったく、近頃の\n若いもんは…", 104401, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2288, "あやつが来てから\n動悸が治まらぬ…\n如何ような病で\nあろうかの…", 104401, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2289, "眷属よ、どこに行った\nのじゃ？\nわらわの世話を\n許可するのじゃ", 104401, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2290, "いずれ世界は\n我が手中に収まる\nふふふ、\n楽しみじゃのう", 104401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2291, "眷属よ、わらわから\n離れてはならんぞ！\n子供に戻ってしまう\nではないか！", 104401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2292, "トマトジュースが\n欲しいのじゃ！\n野菜ジュースでは\nダメだぞ！", 104401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2293, "蚊にくわれたぞ！\nわらわの血を吸う\nとは～！", 104401, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2294, "こら、頭をなでるな\nお主はこの姿でも\n子供扱いするのか？", 104401, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2295, "お主が望むなら世界の\n半分をくれてやっても\n良いのじゃぞ？", 104401, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 2296, "朝早くから感心じゃの\nさすがわらわの\n眷属じゃ", 104401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 2297, "太陽は苦手じゃ…\nこの部屋の中で\n過ごそうぞ", 104401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 2298, "夜も更けてきた！\nわらわ達の時間は\nこれからじゃ～♪", 104401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2300, "よい座り心地じゃ\nわらわの玉座としよう", 104401, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2301, "可愛いではないか\nわらわの眷属に\nしてやろうかのう", 104401, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2302, "うむ、良い気持ちじゃ\nこれを作った者、\n褒めてつかわすぞ", 104401, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2303, "あたたかい…\n身も心も\n浄化されるようじゃ", 104401, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2305, "吸血鬼のわらわが、\nこのようなもの…\nうまい♪", 104401, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2306, "このまま大人の姿を\n保つ方法…どこかに\n書いてないかのう", 104401, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2308, "いろんな人がいて\nいろんな服を着てて…\n目の保養になります♪", 105401, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2309, "レイ様への新作\nどんな服にしよう…\n方向性が絞れない～！", 105401, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2310, "忘れないうちに\n隠し糸のメンテナンス\nやっておこうっと！", 105401, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2311, "むっ！　あの人の\nスリーサイズは…\nほほう…なるほど♪", 105401, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2312, "ノゾミさんの\n衣装はああして\nチカさんのは…", 105401, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2313, "私がアイドル…\n…実感ないけど\n精一杯やってみよう", 105401, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2314, "まさか私が\nアイドルになるなんて\n思ってなかったです", 105401, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2315, "なんですかぁ？\n衣装作りで疲れて\n眠いんですぅ…", 105401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2316, "あーっ！　また\n服がほつれてる…\nしょうがないですね", 105401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2317, "はっ！　ちょうど今\n新しいデザインが\n思い浮かびました！", 105401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2318, "暇そうですね？\n時間あるなら仕事\n手伝ってください！", 105401, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2319, "どうしました？\n新しい服が\n欲しくなりましたか？", 105401, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2320, "騎士さんのおかげで\nカルミナでも\n頑張れてますよ！", 105401, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 2321, "おはようございます…\nなんで朝って毎日\nやってくるんでしょう", 105401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 2322, "お腹空きましたね\n騎士さんはちゃんと\n御飯食べましたか？", 105401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 2323, "夜の方が\n作業が捗るのは\n私だけですかね？", 105401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2325, "このふかふか具合\nいい素材を\n使ってますね", 105401, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2326, "小さくて可愛い♪\nそうだ！　レイ様の\nぬいぐるみを…！", 105401, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2327, "こういう時くらい\n何も考えずに\nリラックスしないと♪", 105401, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2328, "炎を使った服って\nカッコ良いかも…\nでも材料がな～…", 105401, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2330, "何をするにも\n食事は大切！\n体力が資本です！", 105401, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2331, "私もいつか\nデザインを\nまとめて本に…", 105401, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2333, "またお魚を\n捕りにいこうかな♪", 107501, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2334, "そろそろみんなも\nお腹が空くころ\nじゃないですか～？", 107501, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2335, "ここなら、\n日焼けの心配は\nありませんね！", 107501, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2336, "お刺身に焼き貝…\nお腹が魚介類を\n求めてますっ！", 107501, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2337, "泳ぐのも食べるのも\n大得意です！", 107501, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2338, "あの人と二人で\n浜辺を散歩…\n素敵ですねぇ", 107501, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2339, "あの人に水着を\n褒めてもらえたら、\nすごく嬉しいなぁ", 107501, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2340, "たまには釣りじゃなく\n一緒に素潜りでも\n行きませんか？", 107501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2341, "男の子に水着を\n見られるのって\n少し緊張しちゃいます", 107501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2342, "ギルドハウスの\nみんなとも、\n海に行きたいですね！", 107501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2343, "あなたの水着、\n私が選んであげます！", 107501, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2344, "泳ぎの練習なら\n付き合いますよ！\n手取り足取り♪", 107501, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2345, "水着姿が好きなら\nいくらでも\n着てあげますよぉ♪", 107501, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 2346, "朝の海も\n幻想的で\nいいですよねぇ～！", 107501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 2347, "お昼はみんなで、\n海鮮バーベキューとか\nどうでしょう！", 107501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 2348, "夜の海は星が\nきれいですよ！\n見に行きましょう！", 107501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2350, "泳ぎ疲れたので\nちょっと休憩です～", 107501, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2351, "あなたにも、\n可愛い水着を\n着せてあげたいです", 107501, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2352, "なんだか、\nリゾート気分です…", 107501, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2353, "ふい～、泳ぎで冷えた\n身体を暖めましょう～", 107501, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2355, "おいひ～です～\nこれ、海水浴にも\n持っていきたいですね", 107501, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2356, "南国のフルーツ図鑑\n…じゅるり…", 107501, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2358, "わ～♪　おいしそ…\nか、かわいいですね～", 107501, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2359, "これくらい大きな貝…\n食べたいな～\n食べたいな～～", 107501, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2360, "この格好で\n部屋をうろつくのは\n不思議な感じがします", 107601, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2361, "南国の果実を使って\n主さまにデザートを\nお作りしましょう", 107601, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2362, "せっかくの水着ですし\nこれから定期的に\n海水浴に行きたいです", 107601, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2363, "海の生き物も、\n森の生き物に負けず\n可愛かったです…", 107601, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2364, "クロールの練習なら、\n部屋でもできそうです", 107601, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2365, "主さまに、水着姿を\n褒めていただけた…\nえへへ♪", 107601, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2366, "水着で主さまの\nお世話をしたら、\n喜びますでしょうか…", 107601, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2367, "お疲れでしたら\nわたくしの膝を\nお使いくださいね", 107601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2368, "主さま、海の家で\nつかうおこづかいを\n渡しておきますね", 107601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2369, "海の美しさは、\n森とはちがう\n魅力がありました", 107601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2370, "この髪飾りですか？\n造花ですから\n水の中も平気です", 107601, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2371, "水着でくっついて\n眠ると、いつもより\n密着して暖かいです", 107601, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2372, "はじめての海が、\n主さまと一緒で\n幸せです♪", 107601, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 2373, "おはようございます。\n泳ぎに行く準備は\n万端です", 107601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 2374, "午後は泳ぐのをやめて\nスイカ割りなど\nいかがでしょう？", 107601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 2375, "夜も更けてきました…\n波の音を聞きながら\n少しお話しませんか？", 107601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2377, "あぅ…この家具には、\n人をダメにする魔法が\nかけられています…", 107601, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2378, "ふふっ♪\nあなたも、海水浴に\n行きますか？", 107601, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2379, "この香り…\nきっと主さまも\n気に入ります", 107601, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2380, "これは暖かいです…\n後ほど主さまと一緒に\nあたりましょう", 107601, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2382, "泳いだ後の食事は\n一段と美味しく\n感じます", 107601, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2383, "深海魚の生態…\nこれは主さまにも\nお伝えしなくては…", 107601, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2385, "顔立ちが、主さまに\n似ております…\nかわいらしい♪", 107601, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2386, "ふかふかですね\n主さまのようです♪", 107601, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2387, "お掃除、お掃除\nふんふふ～ん☆\nあ、聞かれてました？", 107701, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2388, "日焼けには回復魔法が\n効きますよ。どうして\n逃げるんですか～！", 107701, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2389, "今日はドジを\nしませんように！", 107701, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2390, "接客業を極めれば\nメイドとして\n成長できるかも？", 107701, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2391, "水着って\n動きやすいんですけど\n人の目が気になります", 107701, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2392, "あの人の気持ちを知る\n魔法とかあれば…\nな、なんでもないです", 107701, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2393, "水着でご奉仕したら、\nあの人は喜ぶかな？", 107701, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2394, "暑いのでしたら、\n私が魔法で涼しく…\nえ？　いらない？", 107701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2395, "何か御用ですか？\n特にないのでしたら、\nお話ししましょう♪", 107701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2396, "水着姿でも\nメイドはメイドです\nご奉仕しますよ！", 107701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2397, "ひゃっ！　あ、危うく\n転ぶところでした…\n…きゃうっ！", 107701, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2398, "どうかしましたか？\nあなたの御用なら、\n喜んでお受けしますよ", 107701, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2399, "水着姿をあなたに\n見られると、\nドキドキしちゃいます", 107701, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 2400, "朝は暑くてやる気が\n出ない？　でしたら、\nえぇい、元気になぁれ", 107701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 2401, "お腹がぺこぺこなら、\nお昼は海の家で\n食べましょう♪", 107701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 2402, "夜の浜辺でお散歩も\nいいですね。たくさん\n転びそうですけど…", 107701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2404, "休憩しながら、\nお掃除すれば\n一石二鳥です！", 107701, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2405, "きれいにすれば\nかわいさも倍増\nですよ～♪", 107701, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2406, "素敵な香りです～\n救護院に置けば、\nみんな喜ぶかも～", 107701, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2407, "あったかいですね…\nはっ！　いけません！\nメイドがサボるなんて", 107701, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2409, "料理のできない\nメイドなんて…\nはぐはぐ、おいしい♪", 107701, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2410, "水着姿で接客をすると\n売り上げが倍になる\n…本当ですか、これ", 107701, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2412, "えへへ♪\n南の島まで\n乗せてください！", 107701, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2413, "なんだか、\n人魚姫に\nなった気分です♪", 107701, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2414, "みんなで過ごす\n休日っていいわよね…\nずっと続かないかな", 107801, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2415, "海に浮輪でぷかぷか\n浮かぶのって\n癖になりそう", 107801, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2416, "ビーチにいると\n日焼けが心配だわ", 107801, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2417, "水着姿って楽よね。\n室内だとちょっと\n落ち着かないけど…", 107801, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2418, "お、泳ぎの練習なんて\nしないからね！", 107801, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2419, "楽しすぎて立場を\n忘れそう…あんたの\nせいなんだから！", 107801, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2420, "みんなで遊んで\n美味しいもの食べて\nほんっとに楽しいっ♪", 107801, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2421, "マジであっついわね…\nよっし！　海に入って\n涼みましょ！", 107801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2422, "シャワー浴びたばかり\nなのに…また、遊び\nたくなってきたわ！", 107801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2423, "遊び足りないって？\nしょうがないわね…\n付き合ってあげるわ♪", 107801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2424, "一応伝えておくけど\n猫って水が苦手なの！\nわかった！？", 107801, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2425, "あたしと話したいの？\nふふ。\nまぁ、いいわよ。\nほら隣に座りなさい", 107801, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2426, "こんなに楽しいの\n初めてかも…\nずっと続けばいいのに", 107801, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 2427, "は、早起きしたのは、\n楽しみで眠れなかった\nからじゃないわよ！", 107801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 2428, "昼食後のストレッチ\n終わり！　さあ、\nビーチが待ってるわ！", 107801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 2429, "夜の海に散歩に\n行こうかな～\nあんたも来なさいよ☆", 107801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2431, "海ではしゃぎすぎて\n疲れたわ…ちょっと\n休もうかしら？", 107801, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2432, "こんな魔物がいたら\n操るのも楽しいかも", 107801, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2433, "にゃう～♪\nまるで南国にいる\n気分ね♪", 107801, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2434, "あったかぁい♪\n身体が冷えてきたから\n丁度いいわ！", 107801, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2436, "運動後のご飯って\nなんでこんなに\n美味しいんだろ♪", 107801, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2437, "どんなカナヅチでも、\n絶対必ず泳げる方法、\n書いてないかしら？", 107801, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2439, "あんたもあたしが\n操ってあげる！\nなんてね～♪", 107801, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2440, "リゾートリゾートっ\nのんびりしましょ♪", 107801, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2441, "ここなら思う存分\nデスクワークに\n励めるわね！", 108001, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2442, "武器のメンテナンスも\nしっかりしないと\n最高効率は望めないわ", 108001, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2443, "準備をしっかり整えて\nもっともっと\n稼がなくちゃね！", 108001, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2444, "少し休んだら\n次のバイトに\n行ってくるわね！", 108001, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2445, "この格好でここに\nいるのって\n気恥ずかしいわね…", 108001, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2446, "キミが望むなら\n今日のバイト\n休んでもいいわよ？", 108001, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2447, "キミだけね…\n私のことをここまで\n理解してくれるのは", 108001, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2448, "普段と違う格好\nだからって\nそんなに見ないでよ…", 108001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2449, "キミを立派な\nバイト戦士にするのが\n師匠としての役目よね", 108001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2450, "そろそろ次のバイトに\n行かないと…\nキミも一緒に来る？", 108001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2451, "効率は大事だけど\nゆっくりするのも\n悪くないわね", 108001, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2452, "一緒の時間が楽しくて\nバイトのことを\n忘れちゃいそうね", 108001, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2453, "キミと一緒なら\nどんなミッションも\n楽勝ね！", 108001, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 2454, "おはよう、いい朝ね！\n今日もめいっぱい\n稼ぐわよ！", 108001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 2455, "もうお昼だったのね…\n内職のバイトに\n集中しすぎちゃったわ", 108001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 2456, "スケジュールは、と…\nこういう管理の徹底が\n効率を生み出すのよ♪", 108001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2458, "しっかり働いて休む…\n最高効率にまた一歩\n近づいたわね", 108001, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2459, "なごんじゃうわね…\nよぉし、次のバイトも\n頑張らないと！", 108001, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2460, "いい香りね…\n働く意欲が\n湧いてくるわ", 108001, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2461, "身体が冷えたから\n助かるわ…冷えは\n女の天敵だもの", 108001, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2463, "海のバイトは\n体力勝負よ\nエネルギーはしっかり\n摂取しないとね！", 108001, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2464, "座学も大切ね\nもっと知識を\n身につけないと…", 108001, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2466, "海の楽園みたいね…\n今だけは効率を\n忘れちゃいそう…", 108001, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2467, "落ち着くわ…\n海とひとつになった\n気分ね！", 108001, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2468, "ちょっと休憩にゃ～\n潮風は体に\n良くないにゃ～", 107901, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2469, "猫グッズで\n海の家を飾りたいにゃ", 107901, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2470, "海の幸は美味しいけど\n水はイヤだにゃ…", 107901, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2471, "開店にゃ！\nこのたい焼きなら\nきっと大繁盛にゃ！", 107901, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2472, "海と言えば水着にゃ\n似合ってるかにゃ？", 107901, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2473, "キミが頼むなら\n手伝ってあげても\nいいにゃ", 107901, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2474, "あたしの宝は\nここにあるにゃ～", 107901, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2475, "にゃ？\nそんなに見つめても\n何も出ないにゃ", 107901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2476, "今からキミを\nあたしの相棒に\n任命するにゃ！", 107901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2477, "これから\n開店の準備にゃ\nキミも手伝って\nくれるのかにゃ？", 107901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2478, "海の家で出す\n新しいメニューを\n考え中なのにゃ～", 107901, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2479, "キミと一緒なら\n売上げナンバーワンに\nなれるにゃ！", 107901, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2480, "キミと一緒にいると\n楽しくて、もっと\nいたくなるにゃ", 107901, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 2481, "おはようにゃ！\n今日も頑張って\nいくにゃー！", 107901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 2482, "お腹すいたにゃ～…\nお魚、ちょっとだけ、\nつまみ食いするにゃ", 107901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 2483, "明日の準備は万端にゃ\nいつでも\n出発できるにゃ", 107901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2485, "このふかふか具合\nまるで子猫の\n毛並みのようにゃ～", 107901, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2486, "にゃにゃ～\nなんてかわいさにゃ～", 107901, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2487, "この匂い…\nもう少し香ばしいと\nいいかもしれないにゃ", 107901, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2488, "にゃ～…\nあったかくて\nぬくぬくだにゃ～", 107901, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2490, "働くにはまず体力にゃ\nしっかり食べて\nスタミナアップ\nだにゃ!", 107901, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2491, "ふむふむ…\nここにお宝が\n眠ってるんだにゃ", 107901, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2493, "シードレイクにゃ！\n本物は怖かったけど、\nぬいぐるみなら\nかわいいにゃ～", 107901, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2494, "貝なのにふかふかの\nクッションにゃ！\n屋台の前にも\n置いてみたいにゃあ", 107901, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2495, "人の家って、道場とは\n違った香りがするね", 103701, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2496, "ちょっと退屈だね…\nあの人をからかいに\n行こうかな♪", 103701, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2497, "普通の女の子みたいに\n遊びたいって思う\nこともあるんだよね", 103701, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2498, "剣の手入れを怠らない\nようにしないと。\nいざという時、困るし", 103701, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2499, "もっと\n上手く立ち回って\n出世したいな", 103701, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2500, "おかえりなさい！\nふふ、なんだか奥さん\nになった気分だよ♪", 103701, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2501, "恥ずかしくて、あなた\nを見られないよ…\n私らしくないなぁ…", 103701, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2502, "私、お話好きなんだ。\nどうせなら楽しい話を\nしたいな♪", 103701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2503, "女の子っぽい子と、\n男勝りな子、あなたは\nどっちが好き？", 103701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2504, "そろそろ見回りの\n時間かな。あなたも\n一緒に行く？", 103701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2505, "構って欲しいのかな？\nそれじゃ一緒に\nランニングに行こうか", 103701, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2506, "二人で出かけるなら、\n手を繋がないとね。\nあはは、冗談冗談♪", 103701, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2507, "ねえ、あなた♪\nおや、顔が真っ赤だ！\n…ふふ、私も、かな？", 103701, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 2508, "私は朝から鍛錬だよ。\n慣れてるけど大変だ。\nお互いに頑張ろうね", 103701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 2509, "鍛錬でお腹ぺこぺこ。\nお昼ごはんが\n楽しみだね", 103701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 2510, "今日も一日見回りと\n鍛錬を頑張ったよ。\nあなたもお疲れさま", 103701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2512, "いざという時のため\nにも、休憩は大事だね", 103701, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2513, "こういうものには\n今まで\n縁がなかったけど。\nうん、可愛いね♪", 103701, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2514, "精神統一に通じる\nものがあるね。\nふぅ、癒されるなぁ", 103701, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2515, "あったかいなぁ。\nたまにはのんびり\nしようか", 103701, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2517, "鍛錬後はすごくお腹が\n空くんだよね。\nう～ん、おいしい♪", 103701, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2518, "剣術書だけで強くなる\nには飛びぬけた才能と\n努力が必要だよね", 103701, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2520, "正義の刃で\n悪を挫く！\n…キマったッス♪", 100501, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2521, "ヒーローの\n秘密特訓を\n始めるッスよ～！", 100501, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2522, "正義の味方目指して\n今日も明日も\n全力ッスよ♪", 100501, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2523, "一日一善！\nヒーローだったら\n当たり前ッス♪", 100501, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2524, "どうッスか\nこのポーズ！\nイイ感じッスか？", 100501, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2525, "た、たまには\nよく頑張ったねって\n褒めてほしいッス…", 100501, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2526, "自分を信じてくれる\nあんたと一緒なら\n何でもやれるッス！", 100501, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2527, "おつかれッス♪\n冒険の準備は\nばっちりッスよ！", 100501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2528, "どうしたんッスか？\n…はっ！　もしや敵が\n迫ってるんスかっ！？", 100501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2529, "べ、別に\n頭を撫でてほしいとか\n思ってないッスよ？", 100501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2530, "スーパーヒーロー\nトラタイガーは\n絶対無敵ッス！", 100501, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2531, "あんたと自分で\n正義の心を燃やして\n悪を一網打尽ッス！", 100501, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2532, "あんたと一緒だと\n幸せな気持ちに\nなるんッス…♪", 100501, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 2533, "お、早いッスね～♪\n今日も一日一善\nよろしくッス！", 100501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 2534, "一休みしながら\nヒーローについて\n語り合うッスよ～！", 100501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 2535, "夜のパトロールで\n一緒に平和を\n守るッス！", 100501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2537, "ふぅぅ…\n落ち着くッスね\n戦士の休息ッス♪", 100501, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2538, "くぅぅ、可愛いッス…\nヒーローが守るべき\n愛らしさッスね！", 100501, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2539, "なんスかこの香り？\nすぅぅ…ふぅぅ…\n力がみなぎるッス！", 100501, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2540, "優しい光ッスね…\nヒーローの優しさ\nそのものッス！", 100501, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2542, "あむあむあむ…\nんぅう、元気が溢れて\n止まらないッス！", 100501, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2543, "トラタイガーのお話は\n笑いあり涙ありで\n超おすすめッス！", 100501, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2545, "ハッピーハロウィン\nお父さん、お菓子は…\n食べられないね", 108101, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2546, "トリックオアトリート\nお菓子をくれないと\n不吉な占いをします", 108101, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2547, "ふふ、あなたの後ろで\n霊のみなさんが\n踊り始めました", 108101, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2548, "霊感がいつも以上に\n冴えているので\n占いの調子も良好です", 108101, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2549, "父はハロウィンが\n大好きだそうです\n大胆な格好をする子が\n多いので…", 108101, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2550, "あなたは霊たちにも\n大人気ですよね\n私も見てほしいな…", 108101, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2551, "ハロウィンの賑やかさ\n今は好きです…\nあなたといられるので", 108101, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2552, "霊のみなさんとの\nダンスパーティー…\n楽しみです", 108101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2553, "お父さん、元気だね\nえ？　ここは色んな\n女の子に会えて幸せ？", 108101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2554, "ハロウィンのお菓子\n食べすぎちゃったな…\nでも、美味しかった…", 108101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2555, "あなたの女難の相…\n悪化していますね\nお祓いが必要かも", 108101, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2556, "あの、霊のみなさんが\nあなたと踊りたいと…\nえっと、私も…", 108101, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2557, "あなたと手を繋ぐと\n胸が苦しくなります\nでも、繋ぎたいな…", 108101, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 2558, "ハッピーハロウィン…\n父が元気なので\n今日の運気は良好です", 108101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 2559, "父はお昼寝中です\n本当に寝ているかは\n私にも分かりませんが", 108101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 2560, "お父さん、\nもう眠っちゃったの？\n…眠れるんだったっけ", 108101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2562, "落ち着きますね…\n霊のみなさんも\n喜んでいます", 108101, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2563, "つん、つん。ふふっ♪\n癒やされます…", 108101, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2564, "精神が研ぎ澄まされる\n香りですね…\n占いが捗ります", 108101, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2565, "あったかいね\nお父さん…\nえ？　分からない？", 108101, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2567, "霊のみなさんは\n食べられないので…\n私がいただきます", 108101, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2568, "お父さんを元に戻す\n手掛かり…\n書かれていないかな？", 108101, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2570, "わぁ、お菓子が沢山…", 108101, 3, 12);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2572, "無料でプリンが\nもらえるなんて、\nハロウィンはすごいの", 108201, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2573, "生きてるときも、\nミヤコはハロウィンが\n好きだった気がするの", 108201, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2574, "みんなに仮装を\n見せつけて、\nプリンをもらうの～", 108201, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2575, "この仮装で、\nみんなに恐怖を\n教えてやるの！", 108201, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2576, "アオーン、アオーン！\nミヤコ、狼女に\nなりきってるの～", 108201, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2577, "アイツに秘密で、\nパーティーの\n飾り付けをするの♪", 108201, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2578, "アイツが狼男の\n仮装をすれば、\nおそろいなの～", 108201, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2579, "プリンをくれなきゃ、\nオマエをカボチャに\nしてやるの～", 108201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2580, "ハロウィンは幽霊でも\n参加できるから\n楽しいの～！", 108201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2581, "プリンがないなら\nカボチャプリンを\n食べればいいの！", 108201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2582, "プリンの可能性は\n無限大なの～", 108201, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2583, "オマエの隣で\nプリンを食べると、\nいつもより美味しいの", 108201, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2584, "オマエが死んでも、\nミヤコはずーっと\n一緒にいてやるの～", 108201, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 2585, "朝ごはんは\nカボチャプリンなの！\nハロウィンっぽいの～", 108201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 2586, "ランチは豪華に\nプリンアラモードなの", 108201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 2587, "夜は幽霊たちと\nハロウィンするの！\nオマエも来るの！", 108201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2589, "幽霊にも休息は\n必要なの！\nプリンもってこいなの", 108201, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2590, "オマエもミヤコの\nパーティーに\n招待してあげるの～", 108201, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2591, "癒されるの～\nまるで天国なの～", 108201, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2592, "ここでなら、\n焼きプリンが\n作れるの！", 108201, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2594, "腹が減っては\nハロウィンは\nできぬなの！", 108201, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2595, "丸ごとカボチャ\nプリン特集！？\n美味しそうなの～♪", 108201, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2597, "お菓子がたくさんで、\nミヤコはとっても\nハッピーなの♪", 108201, 3, 12);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2599, "魔女以外にも、\n色々な仮装を\nしてみたいわね", 108301, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2600, "チョコやアメよりも、\nアタシに相応しい\nスイーツがあるはず…", 108301, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2601, "お菓子の食べ過ぎは、\nお母さんに\n怒られるかも…", 108301, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2602, "この格好で学院に\n行ったら、\nみんな驚くかしら", 108301, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2603, "大人のレディは、\nどういうハロウィンを\n過ごすべきかしら…？", 108301, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2604, "お菓子、アイツの分も\nとっといてあげよ！\n大人の余裕ってやつね", 108301, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2605, "アイツを魅了する\n魔法とかないかしら？", 108301, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2606, "おしゃれスイーツを\nくれなきゃ\nイタズラするわよ！", 108301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2607, "アタシの大人センスで\nアンタに似合う仮装を\n選んであげるわ！", 108301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2608, "不気味よねぇ…\nジャックオーランタン\nこ、怖くないわよ？", 108301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2609, "ハロウィンが楽しくて\n宿題やり忘れてたわ…\nて、手伝って～！", 108301, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2610, "お菓子をもらうのも\nアンタと一緒なら\n恥ずかしくないわ！", 108301, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2611, "アタシのエスコートを\n任せられるのは、\nアンタだけなんだから", 108301, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 2612, "朝食はオシャレに\nグリーンスムージー！\n…に、にがぁ", 108301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 2613, "昼下がりはカフェで\n優雅なティータイム\n…憧れるわ～", 108301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 2614, "ハロウィンナイトの\nはじまりよ！\n街に繰り出しましょ！", 108301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2616, "あう～、お菓子の\n食べすぎで\n動けな～い", 108301, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2617, "アンタも仮装する？\nいまよりもーっと\nかわいくなるわよ！", 108301, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2618, "大人な香りに\n包まれてる～♪", 108301, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2619, "ポカポカね～♪\n…ああー！\nチョコが溶けてる！", 108301, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2621, "おいしい～♪\n…じゃなくて！\nび、美味ですわ～", 108301, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2622, "この服かわい～♪\nって、なにこの値段！\nお小遣い何年分よ！？", 108301, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2624, "お、お菓子なんかで\n喜ぶほど、子供じゃ…", 108301, 3, 12);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2626, "次はどんな策で団長を\nからかってみようか…", 107101, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2627, "む？\n血の臭いがするな\n祭りの時間か☆", 107101, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2628, "平和に飼い殺される\n前に、混沌の愛を\n育みたいね", 107101, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2629, "本気を出した団長と\nいつか手加減なしに\n殺り合いたいな", 107101, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2630, "おばさん呼ばわりする\n新米騎士の小娘を\n懲らしめてやろうか", 107101, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2631, "胸を触られるまで\n誓約のことなど\nすっかり忘れていたな", 107101, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2632, "まさかこのワタシが\nプロポーズするとはな\n…笑えるじゃないか", 107101, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2633, "面白い暇つぶしを\n思いついたんだ\n戯れに付き合え☆", 107101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2634, "退屈で死にそうだ\n愉しい話題を\nワタシに提供してくれ", 107101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2635, "なんだ、暇なのか？\n心躍ることでも探しに\n出かけようではないか", 107101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2636, "このワタシを愉快に\nさせてくれる事でも\nあったのか？", 107101, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2637, "ワタシに求婚した男の\n数だと？\nいちいち覚えてないな", 107101, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2638, "最強無敵の女と\n結婚できるんだ\n誇りに思えよ☆", 107101, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 2639, "なにか愉快なことが\n起こりそうな朝だ☆\nワクワクするね", 107101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 2640, "うーむ、腹がすいたな\n愉快なことで\nこの空腹を満たしたい", 107101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 2641, "これからは大人の\n時間だ。愉しみ方を\n知りたいのかい？", 107101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2643, "このワタシとした事が\n穏やかな気分に\nなってしまうとは…", 107101, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2644, "可愛い姿をしたものが\n惨たらしい\n醜態を晒すのを\n想像してしまう", 107101, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2645, "悪くはない香りだ\nだが、ワタシは血の\n臭いを嗅ぎたいぞ☆", 107101, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2646, "ぬるい、ぬるすぎる！\n地獄の業火には\nほど遠い熱さだ！", 107101, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2648, "ワタシの腕前に\n勝るとも劣らない\nなかなかの味だ", 107101, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2649, "意中の男性を絶対に\n落とす方法…？\nく、下らんっ！", 107101, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2651, "明日はあの釣り場に\n行くとしようかな\nあの子も誘ってみるか", 105601, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2652, "ぷはーっ！\n体を動かしたあとの\n一杯は格別だね", 105601, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2653, "天気が良いと\n昼寝か釣りかで\n迷っちまうな…", 105601, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2654, "大物が釣れたときの\n快感は、他じゃ\n味わえないね", 105601, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2655, "ふあぁ～…\n昼寝でもするか", 105601, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2656, "ふふっ…\nあの子、見た目より\n根性あるんだね", 105601, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2657, "あの子が強くなるには\nどんな魚を食べさせて\nあげればいいのやら…", 105601, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2658, "姉御って呼ぶんじゃ\nないよ。からかうと\nタダじゃおかないよ", 105601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2659, "釣りのコツ？\n何事も経験が大事だ\n今から行くかい？", 105601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2660, "どうしたのさ？\n助太刀が必要かい？\nアタシにまかせときな", 105601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2661, "困ってる人がいたら\nまた力を貸しておくれ\n頼りにしてるよ", 105601, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2662, "なにか困りごとかい？\nどうやら昼寝は\nまた今度だね", 105601, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2663, "よしよし！\nお人好しのお前さんを\n労ってあげるよ", 105601, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 2664, "おはよう、お前さん\n魚屋は早起きでなきゃ\nつとまらないよ？", 105601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 2665, "昼飯かい？　今朝\n仕入れた魚があるんだ\n食べてみるかい？", 105601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 2666, "夜釣りにぴったりの\n月が出てるよ\nいい釣りが出来そうだ", 105601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2668, "コイツに座って\nのんびり釣りが\nできりゃいいのにな", 105601, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2669, "よしよし…♪\nおっと、かわいい物も\nつい抱きしめちまうな", 105601, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2670, "これで魚の臭いを\n消せれば、猫も\n寄らなくなるのかね", 105601, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2671, "冬場の釣りも\nこれくらい暖かけりゃ\nいいんだけどね", 105601, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2673, "腹が減っては\n釣りもできぬ、ってな\nまずは腹ごしらえだ", 105601, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2674, "へぇ…こんな魚が\nいたとは…いつか\n釣りたいもんだね", 105601, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2676, "流石に室内で\n魔法ぶっぱしたら\n怒られるよねぇ", 101301, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2677, "可愛い女の子が\nたくさんいる！\nここは楽園か！？", 101301, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2678, "みんなに漫画とか\n勧めてみようかな！\n布教用のがあるし！", 101301, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2679, "わ、私一人の\n部屋じゃないから\n色々自重せねば！", 101301, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2680, "「ぬるぽ」\n…くっ、\n誰も反応なしか！", 101301, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2681, "次はどの漫画を\n貸そうかなー♪", 101301, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2682, "次の定期巡回で\nとっておきの店に\n連れてってあげよう！", 101301, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2683, "君に似合いそうな\n衣装を買ったよ！\n君もいざ魔法少女に！", 101301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2684, "さあ！\n暇なら定期巡回に\nレッツラゴー！", 101301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2685, "戦闘だけじゃなくて、\n日常にも役立つ\n魔法を習得したいね！", 101301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2686, "新魔法思いついた！\n私って天才！？", 101301, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2687, "決め台詞は超大事！\n君の決め台詞も\n私が考えてあげよう！", 101301, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2688, "私ってもしやリア充？\n…そんなバカな！\n非リア代表のはずが！", 101301, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 2689, "おはよー…\n徹夜で本読んでたから\nまだ眠い～", 101301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 2690, "昼飯食べて、\n午後の定期巡回も\n頑張りましょー！", 101301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 2691, "夜からが、\nオタ活本番でしょ！", 101301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2693, "ふいー！\nくつろいで本を読む！\n最高だねー！", 101301, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2694, "君、可愛いね～♪\n魔法少女の\n使い魔みたい！", 101301, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2695, "良い匂いに\n釣られてしまった…\nまさか、孔明の罠！？", 101301, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2696, "暖かいけど、\nここで本を読むのは\nちょっち危ないかな", 101301, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2698, "ウマ―！\nこれの詳細きぼんぬ！", 101301, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2699, "こ、古代語の本！？\n現代語でおk…", 101301, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2701, "ふふふっ♪\n次はどんな悪戯を\nしてやろうかしら♪", 190901, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2702, "幻影湖の霧を吸った\n人間たちの\nリアクションって、\nサイコーよねぇ～♪", 190901, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2703, "室内だけとはいえ…\n自由に動けるって、\nやっぱりいいわ～♪", 190901, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2704, "改めて見ると良い\n部屋ね…悪戯とか\nしやすそうだしぃ♪", 190901, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2705, "私を封じていた部屋\nなのに…急に居心地が\n良くなったわね♪", 190901, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2706, "あんたの頭、座り心地\n良いわね…私専用の\n椅子にしてあげる♪", 190901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2707, "外は大変みたいねぇ…\nまあ、私は好き勝手に\nやらせてもらうわ♪", 190901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2708, "顔に落書された？\n誰か知らないけど、\n悪いことする奴も\nいるものねぇ～♪", 190901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2709, "フィオのやつ…\nどこをウロチョロ\nしてるのかしら？", 190901, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 2710, "人間の朝は大変ねぇ…\n私はその様を見ながら\nゆっくりするわね♪", 190901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 2711, "う～私も、お日様を\n浴びながら、お外を\nお散歩したーい！", 190901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 2712, "この暗さ…閉じ込め\nられていた時を\n思い出すわね…", 190901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2714, "悪戯しすぎて\n疲れたから、ちょっと\n休憩しよっと♪", 190901, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2715, "ガイド妖精たる私が、\nこんな造形物に\n惹かれる…わけが……\nかわいい♪", 190901, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2716, "この香りを囮にして、\n周りに悪戯を仕込む…\n完璧ね♪", 190901, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2717, "不思議ねぇ…私は\n温度とか関係ないのに\n離れられないわ", 190901, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2719, "食事の必要がない体…\nつまり、食べたければ\n食べていいのよね～♪", 190901, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2720, "へぇ…最近はこんな\n悪戯もあるのねぇ…\nあいつで試さないと♪", 190901, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2722, "メリークリスマス！\n歌で幸せを\n届けます…！", 108401, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2723, "あなたと出会えたのは\n偶然ではなくて…\nクリスマスの奇跡だと\n思うんです", 108401, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2724, "クリスマスは華やかで\nつい子供のように\nはしゃいでしまいます", 108401, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2725, "曲が書けるのは\nあなたが側に\nいてくれるからです…", 108401, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2726, "感動を与えられる歌…\nあなたと一緒になら\n生み出せるはず…", 108401, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2727, "クリスマスの幸せを\nあなたと\n感じ合えたらな…", 108401, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2728, "あなたの幸せを願った\nクリスマスソング…\nいつか披露しますね", 108401, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2729, "みなさんの笑顔を\n守りたいから…\n私は歌い続けます", 108401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2730, "歌を届ける\nサンタさんに\nなりたいな…", 108401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2731, "お暇でしたら\nクリスマスのお祝いを\nしませんか？", 108401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2732, "ステージに立つ前に\n喉を整えないと…\n発声練習は念入りに…", 108401, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2733, "まわりはかわいい子\nばっかりだけど…\nこの服なら\nアピールになるかな？", 108401, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2734, "歌でならいくらでも\nあの人への気持ちを\n込められるのに…", 108401, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 2735, "今日も一日\n幸せな歌で世界が\n包まれますように…", 108401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 2736, "お茶を飲みながら\n好きな音楽を\n語り合いませんか？", 108401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 2737, "ふふ、寝かしつけの\n子守唄を\nお望みですか？", 108401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2739, "ふかふかですね\n心地良すぎて\n眠ってしまいそう…", 108401, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2740, "素敵…\n優しい歌みたいに\n気持ちが落ち着く…", 108401, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2741, "インスピレーションが\n溢れてくる…\nいい曲が書けそう…", 108401, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2742, "この光みたいに\n灯火になるような\n歌を歌いたい…", 108401, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2744, "喉の健康のためにも\n食事はしっかり\nとるようにしています", 108401, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2745, "物語には\n曲作りのヒントが\n詰まっているんです", 108401, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2747, "絵が飛び出してくる\nなんて…精霊が宿って\nいるのでしょうか？", 108401, 3, 14);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2748, "か、可愛い…♪\nちょっとだけ触っても\nいいですよね…？", 108401, 3, 15);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2749, "クリスマスでも、\n演劇の練習は\nしっかりやらないと！", 108501, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2750, "あうぅ…\nクリスマスの準備が\n終わらないよぅ…", 108501, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2751, "アヤネちゃんが\n摘み食いしないように\n見ておかないと…", 108501, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2752, "サンタさんみたいに、\n皆に笑顔で接する…\nうぅ…私には無理ぃ…", 108501, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2753, "新しいお洋服～♪\nスズメお姉ちゃんに\nお礼をいわないと♪", 108501, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2754, "お兄ちゃんの為に、\nプレゼントを\n用意しないと♪", 108501, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2755, "クリスマス…\n恋人の日…ひゃわっ！\nわ、私ってば何を…！", 108501, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2756, "メリー\nクリスマスです、\nお兄ちゃん♪", 108501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2757, "そ、そんなに\n見られると…\n恥ずかしいよぉ…", 108501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2758, "ひゃわぁっ！？\n急に触られると、\n驚くからやめてぇ…", 108501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2759, "あ…クリスマスの\n準備を手伝って\nくれるんですか？", 108501, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2760, "お兄ちゃんと\n過ごすクリスマス、\n楽しみですぅ…♪", 108501, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2761, "クリスマスはその…\n二人で…あうぅ…\nなんでもないですぅ…", 108501, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 2762, "うう…パーティーが\n楽しみで、あんまり\n眠れなかったよぅ…", 108501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 2763, "ケーキを食べられなく\nなっちゃうから、\nお昼は軽めにしないと", 108501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 2764, "ちょっと眠いけど…\n今日は少しだけ\n夜更かししたいです♪", 108501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2766, "ふかふかぁ…\nクリスマスの準備中に\n眠くなっちゃいますぅ", 108501, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2767, "この子と一緒に\nおままごとしたいな♪", 108501, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2768, "いい匂い…♪\nみんなで\nアロマパーティー\nしたいなぁ…♪", 108501, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2769, "ぬっくぬく…♪\nもう、ここから\n離れたくないよぉ…♪", 108501, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2771, "はむはむ…♪\nクリスマスのお料理\nより美味しいかも～♪", 108501, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2772, "すごい…\nクリスマスの\n演劇って、\nこんなにあるんだ…", 108501, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2774, "すごい…まるで、\nクリスマスの演劇を\n観てるようですぅ…♪", 108501, 3, 14);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2775, "とっても可愛いです♪\nお芝居の相手役に\nしたいなぁ…♪", 108501, 3, 15);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2776, "甘ぁいケーキも、\nデッカイ七面鳥も、\nぜーんぶ私のだー♪", 108601, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2777, "毎日が\nクリスマスだったら\nいいのにな～♪", 108601, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2778, "プレゼントはあるし、\n料理は美味しいし、\nクリスマス最高ー♪", 108601, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2779, "ここでぷうきちの\nクリスマス一発芸♪\n『無茶ぶりするな！』", 108601, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2780, "ぷうきちがいつもより\n重いかも…太ったの？\n『衣装の重さだよ！』", 108601, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2781, "お兄ちゃんが\n一緒にいてくれると\nクリスマス楽しいね♪", 108601, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2782, "お兄ちゃんに抱きつく\nと、なんだかドキドキ\nする…なんでかな？", 108601, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2783, "メリークリスマス♪\nプレゼントは、\nいつでも受付中だよ♪", 108601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2784, "このクマさん耳\n可愛いでしょ♪\nぷうきちと\nお揃いなんだー♪", 108601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2785, "お兄ちゃん…私、\n甘ぁいケーキが\n食べたいなぁ…♪", 108601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2786, "クリスマスぷうきちは\nぬくぬく感が凄いよ！\nお兄ちゃんもさわる？", 108601, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2787, "えへへ…お兄ちゃんの\n手は、大きくて、\n暖かくて大好き～♪", 108601, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2788, "来年のクリスマスも、\nこうやって\nお兄ちゃんに\n甘えていたいな～♪", 108601, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 2789, "あれぇ…\n私のケーキはぁ…？\n『ちゃんと起きろ』", 108601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 2790, "お昼はデッカイ\n七面鳥が食べたーい♪\n『夜まで我慢しろ』", 108601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 2791, "『もう寝る時間だぞ』\nサンタさんが来る\nまで起きてるぅ…", 108601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2793, "このふかふか具合…\nぷうきち！\n負けてられないよ！", 108601, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2794, "この子、\nすっごくかわいい～♪\n『ライバル登場か！』", 108601, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2795, "良い匂い…あっ！\nぷうきちにこの\n匂いを移せば…！\n『匂い袋にするな』", 108601, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2796, "あったか～い♪\nえへへ、ぷうきちも\nあっためてあげる♪", 108601, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2798, "ぷうきち！　これ、\nとってもおいしいよ♪\n『食べすぎるなよ』", 108601, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2799, "お、大人の私なら、\nこれぐらい…読め…\n『無理するな…』", 108601, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2801, "絵が飛び出す…\nぷうきちに絵本を\n入れれば、イタズラに\n使えるかも…♪", 108601, 3, 14);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2802, "か、可愛いけど…\nぷうきちサンタの方が\n可愛いんだからぁ…！", 108601, 3, 15);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2803, "着物も慣れると\n動きやすいものさ", 108901, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2804, "鼻緒が緩んでしまった\n下駄で修業は\n無理があったかな…", 108901, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2805, "小腹が空いた…\nユイが作った餅、\nまだ食べられるかな？", 108901, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2806, "服装も変わったし\nいつもと違う戦い方を\n想定しないと…", 108901, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2807, "…ヒヨリは動きすぎて\n着崩れしてないか\n心配だ", 108901, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2808, "これからもずっと\n仲間と、彼と\n一緒に…", 108901, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2809, "願いを叶えるために\nもっと自分を\n鍛えていかないと！", 108901, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2810, "頭の飾りがずれてた？\nそうか、直してくれて\nありがとう", 108901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2811, "着物がそんなに\n珍しい？\nキミも着てみる？\n…冗談だよ", 108901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2812, "肩掛けの触り心地が\n気に入った？\nならもっと触る？", 108901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2813, "ああ、この刀？\n釣り仲間に見繕って\nもらったんだ", 108901, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2814, "キミといると\n気持ちが段々と\n安らいでいくよ", 108901, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2815, "キミは私の行く先を\n照らしてくれる\n光のような存在だよ", 108901, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 2816, "おはよう\n朝の修行を始めるけど\nキミもどう？", 108901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 2817, "この時間に軽く\n休憩をとるといい\n能率がよくなるよ", 108901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 2818, "もう夜か…\n明日もまた頑張ろう\nそれじゃ、おやすみ", 108901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2820, "…疲れていたのかな\nもう少しで眠る\nところだった…", 108901, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2821, "ふふっ、正月衣装を\n着せてやるのも\nよさそうだ", 108901, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2822, "ふぅ…いい香りだ\n慌ただしい日々から\n心が解放される", 108901, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2823, "この心地よさ…\n寝てしまわないよう\n気をつけないと", 108901, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2825, "おいしいけど\n帯がきつくなる前に\n手を止めよう…", 108901, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2826, "外を出歩くのも\nいいけど、中で\n読書もありだな", 108901, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2828, "…騎士クンって\nおせち料理で\n何が好きなんだろう？", 108801, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2829, "着物でお掃除や\nお洗濯するの\n慣れてきた♪", 108801, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2830, "今年は\nどんな福袋を\n買おうかな…♪", 108801, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2831, "お餅に使う調味料\nみんなで違うから\n色々用意しなきゃ", 108801, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2832, "ほっぺがお餅みたいに\nぷにぷに…\n食べ過ぎちゃった…", 108801, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2833, "来年も、再来年も\nみんなで一緒に\n新年を迎えたいな", 108801, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2834, "お願いです…\nいつかこの気持ちが\n伝わりますように…", 108801, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2835, "髪飾りが落ちそう？\nじゃあ…騎士クン\nつけ直してくれる？", 108801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2836, "着物が着たいの？\nじゃあ今度一緒に\n買いに…行く？", 108801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2837, "あっ、騎士クン\nお雑煮のお餅\nいくつ入れたい？", 108801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2838, "外、寒かったよね？\nはいこれ。熱い甘粥\n飲むと温まるよ", 108801, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2839, "今年は騎士クンと…\nえっ？　騎士クン！？\nな、なんでもないよ！", 108801, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2840, "騎士クンの願い事\nわたしが\n叶えてあげたいな…", 108801, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 2841, "おはよう\n太陽の光に当たると\n元気が出るよ", 108801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 2842, "お昼ごはん\n作らなきゃ…\n騎士クンも食べる？", 108801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 2843, "一日が終わるのは\nあっという間だよね\n一年も同じなのかな？", 108801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2845, "ふぅ…\n帯も少し\nゆるめちゃおう…", 108801, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2846, "お正月用に\n着物を縫って\nあげたいな", 108801, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2847, "これでお肌が\n保湿出来たら\nいいのになぁ", 108801, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2848, "ん～…\nぬくぬくしてて\n動きたくないよ～", 108801, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2850, "…冬眠しないのに\n熊さんみたいに\n食べちゃいそう", 108801, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2851, "年末年始は\n告白のチャンス…\nむ、無理無理！", 108801, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2853, "みんな忙しそう…\n待っててね、\nすぐ助けるから！", 108701, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2854, "ユイちゃんと\nレイさん…よし！\nどっちも手伝おう！", 108701, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2855, "あっ、着崩れてる！\nわわっ、どうしよ！\n帯が結べないよー！", 108701, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2856, "初詣楽しみー！\n出店はどこから\n行こうかな？", 108701, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2857, "お正月だし\nみんなで楽しく\n遊んじゃおー！", 108701, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2858, "うーん…\n騎士くんはお正月\n何してるんだろ…", 108701, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2859, "ううぅー…\n騎士くんを見てると\nドキってなっちゃう…", 108701, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2860, "あたしから\nおいしそうな匂い？\nくんくん…ソースだ！", 108701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2861, "ヘアピンが…\n拾ってくれたんだ！\nありがとう騎士くん！", 108701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2862, "髪飾りがかわいい？\n耳の近く触りたい？\n…優しくね？　", 108701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2863, "グローブで食べるの\n大変そう？　特訓して\n今はお箸も使えるよ！", 108701, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2864, "えっ、ほっぺが赤い？\nそうかな…？\n気のせい気のせい！", 108701, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2865, "騎士くんといるとね…\nなんでもない！\nまだ内緒っ！", 108701, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 2866, "おはよー！\n今日も明るく元気に\nがんばろー！", 108701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 2867, "お腹すいてない？\n一緒にご飯食べよ！\n何にしようかなー♪", 108701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 2868, "今日もいっぱい\nがんばったー！\nいつも？　…そう？", 108701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2870, "休憩休憩♪\nちょっと休んで\nガンガン動くぞー！", 108701, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2871, "かわいい♪\nでも、壊しそうで\nちょっと怖い…", 108701, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2872, "はぁー♪\nいい香りー…\n気分スッキリ♪", 108701, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2873, "ほっこりしてて\nぬくぬく…\n丸まりたい…", 108701, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2875, "ど、どうしよう…！\n手が、止まんないよ！", 108701, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2876, "へー…お年玉って\n昔はお餅だったんだ\n…今のほうがいいね", 108701, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2878, "甘いチョコの香りを\n漂わせて…ふふっ\nこれで準備万端ですわ", 109001, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2879, "ここを私のチョコで\n埋め尽くしますわ", 109001, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2880, "エプロンを着て\nあの方を待つ私…\nまるで…クスクス", 109001, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2881, "キッチンはどこかしら\n今のうちに作って\nおきたいものが…", 109001, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2882, "あのお方を私のチョコ\nなしでは生きられない\n体に…ふふっ\n冗談ですわ…", 109001, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2883, "このチョコは運命の味\n一口食べれば私の虜…\nそれも運命ですわ…", 109001, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2884, "私ともあろうものが\nなんだかドキドキして\nきましたわ…", 109001, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2885, "あなた様。私に\n隠しているものなど\nありませんよね…？", 109001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2886, "私の手作りチョコです\n味の方にも自信\nありますのよ？", 109001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2887, "少し苦いのは\nチョコがビターなだけ\nお気になさらず…", 109001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2888, "解毒薬はどこに\nいれたかしら…？", 109001, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2889, "運命で結ばれている\nあなた様と私…\nチョコも特別でないと\nいけませんわ", 109001, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2890, "チョコを食べた後は\nメインディッシュ…\nさぁ、私を召し上がれ", 109001, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 2891, "目覚めのチョコですわ\nすっきり目が覚めます\nしばらく眠れなく\nなりますけど…", 109001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 2892, "おやつのチョコですわ\nデザートにどうぞ\n空腹を感じなく\nなりますわ…クスクス", 109001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 2893, "お夜食のチョコですわ\nとてもよく眠れます\nさ、溶けてしまう前に", 109001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2895, "この心地良さ…\n参考になりますわ\n次の薬は…クスクス", 109001, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2896, "この中、何か\n入りそうですわね\n…チョコレート、とか", 109001, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2897, "いい香り…この香りと\n同じものを調合して\n私も持っていたい", 109001, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2898, "この暖かさ…\nチョコが溶けないよう\n気を付けなくては", 109001, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2900, "いい味をしていますわ\n私も腕を磨きましょう\n料理と、調合の腕を…", 109001, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2901, "勉強になりますわ\n知識はいくらあっても\n困りませんもの\n特に、人体の知識は…", 109001, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2903, "弟くんのことを\n考えると\nあっという間に時間が\n過ぎちゃう…", 109101, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2904, "お姉ちゃんの\n元気の源は、弟くんの\n喜ぶ顔なんだから♪", 109101, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2905, "弟くんと会ったら\nまずは抱きしめて\nそれからそれから…", 109101, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2906, "今日のお姉ちゃんは\n昨日のお姉ちゃんより\nいいお姉ちゃんだよ♪", 109101, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2907, "弟くん、もっと\nお姉ちゃんに\n甘えてもいいのに♪", 109101, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2908, "お姉ちゃんのチョコ\n食べさせてあげたい…", 109101, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2909, "ドキドキしてきた…\nお姉ちゃんなのに\nどうしてかなぁ…", 109101, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2910, "お姉ちゃんを\n好きなだけ\n触っていいよ♪", 109101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2911, "そんなにお姉ちゃんの\nチョコが欲しいの？\n食いしん坊さん♪", 109101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2912, "ナデナデするのは\nお姉ちゃんの仕事！", 109101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2913, "ありがとうなんて…\nお姉ちゃんとして\n当たり前だよ♪", 109101, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2914, "どうしたのかなっ？\nお姉ちゃんはいつでも\n弟くんのそばにいるよ", 109101, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2915, "チョコを作ったよ\n隠し味は、たくさんの\n弟くんへの愛情♪", 109101, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 2916, "おはよ。お姉ちゃんが\n顔を洗うの\n手伝ってあげるね♪", 109101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 2917, "ご飯を食べたら\n眠たくなるよね…\nお姉ちゃんと寝る？", 109101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 2918, "そろそろ眠くなった？\nお姉ちゃんが添い寝\nしてあげるね♪", 109101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2920, "この気持ちよさは\nお姉ちゃんも太鼓判♪", 109101, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2921, "このかわいさは\nお姉ちゃんも大満足♪", 109101, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2922, "この香り、チョコに\n使えるかな…？", 109101, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2923, "弟くんと一緒に\nぬくぬくしたいな…♪", 109101, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2925, "すごくおいしいねっ♪\nあとで運動しなきゃ…", 109101, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2926, "本を読んでる\n知的なお姉ちゃん\n素敵でしょ♪", 109101, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2928, "アタシって…\nここにいても\nいいのか…？", 106101, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2929, "ちゃんとした建物に\nいるのが久しぶりで\nそわそわする…", 106101, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2930, "ここで天楼覇断剣\n出す練習したら\nアイツら怒るかな？", 106101, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2931, "ギルドハウスか…\nこういう隠れ家\n欲しかったんだよな", 106101, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2932, "めちゃくちゃ暇だ…\n…なんかやること\nないのか？", 106101, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2933, "みんなが早く\nアタシのこと\n思い出さないかな", 106101, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2934, "アイツはいいヤツだし\n戦う時はアタシも\n協力してやるか！", 106101, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2935, "ちゃんはいらん！\nノウェムでいい！\n分かったか？", 106101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2936, "どうした？\n戦うのか！？\n…なんだ、違うのか", 106101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2937, "身長のことは\nあんまり言うな！\n気にしてるからな！", 106101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2938, "アタシの戦い方は\nなんでもアリだ！\nだから見習うなよ？", 106101, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2939, "オマエとの思い出か…\n敵として戦ってばっか\nだったな！", 106101, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2940, "オマエやオクトーと\n一緒に、あちこち\nいってみたいんだ！", 106101, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 2941, "よっ！　おはよう！\n朝だぞー！\n起きてるかー？", 106101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 2942, "腹減ったー！\n飯食うぞ、飯！\nオマエも行くよな？", 106101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 2943, "ふわぁ…\nまだ起きてんのか？\n元気だなオマエ…", 106101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2945, "ふぃ～…\n極楽極楽…", 106101, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2946, "おいオマエ！\n寝技の練習に\n付き合ってもらうぞ", 106101, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2947, "くんくん…\nたまにはこういうのも\n悪くないかもな", 106101, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2948, "あー…\nもっと前にコイツと\n出会いたかった…", 106101, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2950, "VRでも飯はうまい！\n腹が減るのはちゃんと\n生きてる気がするな", 106101, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2951, "勉強は大事だな！\nうん！…だめだ！\n全然わからん！", 106101, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2953, "探偵というのは\n謎を追い求める\n生き物なのさっ！", 101401, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2954, "事件のオチは\n往々にして\n取るに足らないものさ", 101401, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2955, "読書で得る知識が\n探偵の\n血肉になるんだよ", 101401, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2956, "この前の\n捜査資料…\nどこにあったかな？", 101401, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2957, "どんな小さな謎も\n私にとっては\n宝石みたいなものさ！", 101401, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2958, "助手くんと一緒だと\nよい発想が浮かぶ…\nどうしてだろうね？", 101401, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2959, "助手くんの好きな\nタイプって…\nす、推理してみるっ！", 101401, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2960, "珈琲、淹れたてだよ\n一杯どうだい？", 101401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2961, "事件の考察を\nまとめてみたんだけど\n聞いてくれるかな？", 101401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2962, "小さな違和感を\n見逃さない…\n探偵の心得だよ", 101401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2963, "機能的な手帳とペン…\nこの仕事には\n欠かせないね！", 101401, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2964, "助手くんに\n珈琲…\n淹れてほしいなぁ～", 101401, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2965, "助手くんは\n私にとって\n最高の相棒だよ☆", 101401, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 2966, "朝はいいひらめきが\n得られるんだ…\nさあ捜査を始めよう！", 101401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 2967, "一休みしたら\n推理の答え合わせを\nしてみようか？", 101401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 2968, "夜の張りこみかい？\nなら私も\n同行させてほしいな！", 101401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2970, "脳を酷使した後は\nゆっくり休む…\nこれも仕事だよ？", 101401, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2971, "この愛らしさ…\n要捜査対象だね！", 101401, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2972, "思考が活性化される\n素敵な香りだね…", 101401, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2973, "くぅ、心地よすぎる…\n今だけは捜査も\n一休みだね♪", 101401, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2975, "いっぱい食べて\nいっぱい考える\n探偵の本分だね！", 101401, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2976, "探偵だからね\nミステリには\n一家言あるんだ", 101401, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2978, "先輩、素敵だなぁ…\nもっと近づければ\nいいのに…", 105501, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2979, "せ、先輩がいた部屋！\n私には刺激が強い…", 105501, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2980, "先輩を観察しやすい\n場所はあるかな…", 105501, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2981, "バ、バイト代出たし、\nせ、先輩をご飯とかに\n誘いたい…な", 105501, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2982, "今頃先輩は\nあの辺かな…？\n見に行ってみよう！", 105501, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2983, "私と先輩の出会いって\n思い返すと少女漫画\nみたい…えへへ", 105501, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2984, "せ、先輩になら\n観察されてもいいなぁ", 105501, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2985, "バイト先に新しい子が\n入って来たんです…\nわ、私、先輩です♪", 105501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2986, "せ、先輩…？\nえっと、あの、その、\nあわわわわ…！", 105501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2987, "えへへ…えへへ～\nせ、先輩から\n話しかけてくれました", 105501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2988, "ま、また…\n先輩と一緒に\nバイトしたい…です", 105501, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 2989, "私と先輩が同じ空間に\n…ゆ、夢みたいです", 105501, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 2990, "せ、先輩のそばに\n居られて、私\n今、とっても\n幸せです…", 105501, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 2991, "せ、先輩…！\nおひゃっ！\nおはようございます！", 105501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 2992, "先輩ランチに\n行きましょう！\nよし、練習はバッチリ", 105501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 2993, "あ、せ、先輩。\nえと、そ、外…\nもう真っ暗ですねぇ", 105501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2995, "こ、ここで先輩も\n休んだり…はっ！\nわ、私ったらなにを！", 105501, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2996, "かわいいですねー\n君も一緒に、\n先輩を観察する？", 105501, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2997, "ここから先輩の\n香りがすればなあ", 105501, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 2998, "先輩と一緒に\nぬくぬくできたら\n最高なんだけどなぁ", 105501, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3000, "おいし～\nこれは\n先輩も好きな味かな", 105501, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3001, "す、好きな人に\n近づく方法…", 105501, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3003, "私の力は、大切な\n人たちを護るために\n役立てたいんだ", 109201, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3004, "きれいな景色と\n美味しいご飯！\nこの世界も素敵だね", 109201, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3005, "この世界のみんなとも\nもっと仲良く\nなりたいな！", 109201, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3006, "わー！\nあれもこれも\n気になっちゃうな！", 109201, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3007, "しっかりしなきゃって\n思ってても、\nグレアといるとつい\n甘えちゃうんだよね～", 109201, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3008, "違う世界でも\n友だちができて、\nすごくうれしいよ！", 109201, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3009, "みんなとも一緒に\n授業とか受けたいな～\n絶対楽しそうだもん！", 109201, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3010, "歩き方がきれい？\nえへへ、そうかな…？", 109201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3011, "勉強は好きだよ！\n知識が増えていくのは\nやっぱり楽しいから", 109201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3012, "私たちの国マナリアは\n人々の間に魔法が\n伝えられた、その\n始まりの地なんだ！", 109201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3013, "この部屋にも、\nもっとたくさん本棚が\n増えないかな～…？", 109201, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3014, "あなたも魔法、\n覚えてみる？\n私、教えるよ！", 109201, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3015, "お茶の時間に\nしたいな～…\n一緒にどう？", 109201, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 3016, "おはようっ！\n今日も素敵な\n一日にしようね！", 109201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 3017, "お昼ご飯は\nちゃんと食べた？\n午後も頑張ろうね！", 109201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 3018, "つい本の続きが\n気になって、夜更かし\nしちゃうんだよね～…", 109201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3020, "はぁ、気持ちいい～…\nちょっとだけ\n眠っちゃおうかな…", 109201, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3021, "わあ、これ可愛い！\nグレアにも見せて\nあげたいな", 109201, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3022, "いい香り～！\nなんだか気持ちが\n落ち着くね", 109201, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3023, "あぁ、あったかくて\n幸せ～♪　身体が\nポカポカしてきたよ！", 109201, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3025, "こ、これ美味し～！\n止められないよー！", 109201, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3026, "ふんふん…\n…はっ！　む、夢中に\nなっちゃってた…", 109201, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3028, "新しい建物に入ると\n必ず一度は迷子に\nなっちゃうですよ…", 109301, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3029, "おやつはどこですか？\nお腹が空いて\nきちゃったですぅ", 109301, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3030, "宿題、７つぐらい\n出しそびれたですけど\nまあ大丈夫ですぅ！", 109301, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3031, "不思議なこと\nばかりですけど…\nまぁ大丈夫です！\nこれもせいしゅんです", 109301, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3032, "なにか面白そうなもの\nないですかね～？", 109301, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3033, "ここのみんなとも\nもうお友だちですぅ！", 109301, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3034, "おなかが減ったです…", 109301, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3035, "ごはんも美味しいし\nここはいいところです\nルゥは幸せですぅ！", 109301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3036, "魔法の話ですか？\nそれはアンせんぱいに\n聞いてほしいですぅ…", 109301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3037, "あなたもお勉強\n苦手ですか？", 109301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3038, "ルゥの帽子、\nかわいいです？\nお気に入りですぅ♪", 109301, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3039, "お友だちいっぱいで、\nルゥはうれしいです♪", 109301, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3040, "一緒にお勉強…！？\nぐえぇ、イヤですぅ！", 109301, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 3041, "朝ですねぇ…ふわわ…\nルゥはまだ夢の中に\nいたいですぅ…", 109301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 3042, "午後もいっぱい\n遊ぶですよ～！", 109301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 3043, "夜ふかしって\nドキドキするです！\nせいしゅんですぅ！", 109301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3045, "ふかふかですぅ～♪\nおひるねが\nしたくなるですぅ！", 109301, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3046, "これふかふかですぅ！\n机でうとうとする時の\n枕によさそうですぅ", 109301, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3047, "ふぁぁ、いいにおい…\nなんだか気持ちが\nよくなってきたですぅ", 109301, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3048, "ぽっかぽかですぅ～！\nあったまったら\nまた遊びにいくですぅ", 109301, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3050, "お、おいしいですぅ！\n全部ルゥに\nくださいですぅ！", 109301, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3051, "本は…読むと、\nすぐ眠気がぁ…\n…ぐぅ…", 109301, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3053, "ピアノ…\nどこかにないかな？", 109401, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3054, "ふぅ…\n落ち着くな…", 109401, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3055, "マナリアはいつも\n賑やかなんだ…", 109401, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3056, "みんな…\n今頃何してるかな？", 109401, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3057, "まさか違う世界に\n来ちゃうなんて…\nびっくりだよね…", 109401, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3058, "人と話すのは\n得意じゃないな…", 109401, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3059, "ここのみんなと一緒に\n学院に通えたら…\nちょっと面白いかも", 109401, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3060, "やっぱり、私の翼や\n尻尾…気になる？", 109401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3061, "一番得意なのは、\nやっぱり炎魔法かな…\n加減は難しいけど…", 109401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3062, "私の故郷は竜の国…\n険しい山脈を\n越えた先にあるんだ", 109401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3063, "アン…？\nあっ、ご、ごめんね！\n間違えちゃった…", 109401, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3064, "ふん…ふん…♪\n…わっ！\n今の、聞いてた…？\nは、恥ずかしい…", 109401, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3065, "私に手伝えること、\nなにかある…？\nなんでも言ってね", 109401, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 3066, "朝の空気って\nすごく\n静かで透き通ってて…\nなんだか落ち着くよね", 109401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 3067, "学院食堂のランチも\n美味しいけど…\nここのご飯も好きかな", 109401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 3068, "そろそろベッドに\n行かないとだね\n…寝相、今夜こそ\n気を付けよう…！", 109401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3070, "羽を伸ばしたく\nなっちゃうね…ふふ", 109401, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3071, "わ、私が持つには\nかわいすぎるかな…\nでも、素敵だね", 109401, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3072, "いい香り…\nこれ、\nアンも好きそう…", 109401, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3073, "火を見ていると\n少し落ち着くんだ…\nへ、変かな？", 109401, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3075, "わ、美味しい…！\nこれ、どうやって\n作ったのかな…？", 109401, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3076, "この本…アンにも\n見せてあげたら\n喜ぶかな、ふふっ", 109401, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3078, "着物はきれいですが\n歩きにくいです…\nいっそ脱いで…ぐふふ", 109501, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3079, "あっ、いけません\n着物がはだけて…はっ\nもしやドＳさんが…！", 109501, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3080, "この部屋は妄想する\nのにうってつけです…\nぐふふ…じゅるり", 109501, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3081, "扇をゆっくりと開く\nこの感触…なぜだか\n艶めかしさを感じます", 109501, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3082, "「マッサージをすると\n言いながらドＳさんの\n手は次第に」…ぐふふ", 109501, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3083, "…ドＳさんの視線を\n思い出しながら踊りの\n練習です…イイ！", 109501, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3084, "よいではないか\nよいではないか～\nいけませんご堪忍を～", 109501, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3085, "あ～れ～～～！\nドＳさんもお好きな人\nですねぇ…ぐふふ", 109501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3086, "どこにいてもドＳさん\nの気配を感じ取れる\nようになりました…", 109501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3087, "こんな場所で\nなんというプレイを！\n…妄想でした", 109501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3088, "クウカを押し倒して\nそのまま…じゅるり\nああっ、着物が…！", 109501, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3089, "ドＳさんは優しいです\n優しいのにドＳとは\nさすが人格破綻者…！", 109501, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3090, "ドＳさんの考えている\nことが伝わってきます\nはい…クウカ脱ぎます", 109501, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 3091, "眩しい朝日がクウカの\n体を焦がします\n…それもイイ！", 109501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 3092, "お昼のランチは\nクウカがいいだなんて\n大胆過ぎです…じゅる", 109501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 3093, "…今夜のドＳさんは\n大胆です…ぐふふ…\nはっ、視線が…！？", 109501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3095, "この心地良さ、\n心が休まりすぎて\n体が疼いてきます…！", 109501, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3096, "すごく愛らしいです…\n妄想も出てこない\nくらい…じゅるり", 109501, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3097, "この香りに包まれて\nいると、いい気持ちに\nなって…いきますぅ…", 109501, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3098, "「熱を帯びた体は\nどうしようもなく\n誰かを求め…」ぐふふ", 109501, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3100, "お腹が膨らんで\nいきます…それは\nまるで…ぐふふ", 109501, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3101, "クウカの探している\nジャンルはどこに\nありますかね？", 109501, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3103, "カチョーフーゲツは\nトーゴクの心を\n表した言葉デスね", 109601, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3104, "オー！　新しい忍法を\n思いついたデス！\nさっそく試すデス！", 109601, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3105, "トーゴクの天気には\n槍が降る日も\nあるそうデス…", 109601, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3106, "我がショーグンは\n永遠にフメツデース\nいざ出陣デース！", 109601, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3107, "壁に耳あり\n障子に…ん？\nメアリーって誰デス？", 109601, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3108, "ショーグンがいれば\nワタシはどんな\n忍法も使いこなせマス", 109601, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3109, "不届き者はどこで\nゴザルデス？　忍法で\n退治してくれヨウ！", 109601, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3110, "ショーグン！　新しい\n忍法を覚えたデス！\n見てほしいデース！", 109601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3111, "レッツ手裏剣道デス！\n投げてソウロウで\nゴザルデース！", 109601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3112, "ふりかかる火の粉は\nワタシが払うデス！\nバンバン！", 109601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3113, "とっておきの忍法が\n吹き荒れるデス！\nよそ見はダメデスヨ？", 109601, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3114, "ショーグンは\n何階建てのお城を\n建てたいデスか？", 109601, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3115, "テンシュカクから\n平和な城下町を\n眺めてみたいデス…", 109601, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 3116, "おはようございマス！\n本日はオヒガラモヨク\n良い日になりそうデス", 109601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 3117, "お腹すいたデース…\nショーグンもご飯に\nしまショウ！", 109601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 3118, "闇討ちに遭わぬよう\n夜は気を付けて\n歩くデスよー？", 109601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3120, "天下分け目の\n戦いに備えて…\nマッタリデース", 109601, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3121, "口寄せの術で\n召喚したいくらい\nかわいいデース", 109601, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3122, "いい匂いデ～ス\n香り玉の開発の\n参考になりそうデスね", 109601, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3123, "ワタシの懐よりも\nゾーリが暖まりマス…\nライバル出現デス…！", 109601, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3125, "腹が減っては\n戦はできぬデース！\n天下統一に向けて\n腹ごしらえデス！", 109601, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3126, "ムム、暗号文デス！？\n解読には時間が\nかかりそうデース…", 109601, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3128, "掃除も終わりましたし\nそろそろ、食事の\n準備をしなくては", 109701, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3129, "異世界の演劇…\n許されるならば、\n観てみたいです", 109701, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3130, "こちらの世界には、\nマヨネーズが普通に\n存在するのですね…", 109701, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3131, "こちらの世界は食材も\n面白い物が多いですね\nなにかレシピを\n考えてみましょうか", 109701, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3132, "みょんみょんみょん…\n魔法の練習は\nこれぐらいですね", 109701, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3133, "倒木の撤去で思わぬ\n収入となりました\nサレン様に\nお渡ししておかないと", 109701, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3134, "料理を美味しく\n召し上がって頂けると\nメイド冥利につきます", 109701, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3135, "お出かけでしたら、\nレムも同行\nさせてくださいね", 109701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3136, "レムにできること\nでしたら、なんでも\nお申し付けください", 109701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3137, "じーー。いえ、\nその髪の長さが少し、\nとても少し、かなり\n少し気になったもので", 109701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3138, "鉄球ですか？\nあれは護身用ですので\n問題はありませんよ？", 109701, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3139, "計算の勉強は\n進んでおりますか？\nレムでよろしければ、\nいつでもお教えします", 109701, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3140, "次はもっと、大きな\n魔物料理に挑戦します\nワイバーンの姿焼き\nなんてどうでしょうか", 109701, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 3141, "おはようございます\n朝食の準備が\nできておりますので、\n食卓におこしください", 109701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 3142, "昼食の時間ですね\n何か食べたいものは\nありますか？", 109701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 3143, "寝ないのか、ですか？\nメイドの就寝は、\n屋敷で最後と\n決まっておりますので", 109701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3145, "ふんわり…\nこのふんわりさは、\n鬼をも黙らせます…", 109701, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3146, "ふふっ…異世界でも\nかわいいものは\n共通なのですね", 109701, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3147, "よい香りですね\n魔女教の悪臭とは\n大違いです", 109701, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3148, "ぬくぬくです…はっ！\nこれはいけません！\n人を…いえ、\n鬼をだめにします！", 109701, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3150, "はむはむ…美味しい！\nこの味を再現できたら\n褒めてもらえる\nかもしれません…！", 109701, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3151, "絵がいっぱい…\n異世界の本は\nとても面白いですね", 109701, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3153, "異世界であろうと、\nラムのやるべきことは\n変わらないわ\nまずは休憩するわよ", 109801, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3154, "マヨネーズがある\nということは、\nバルスの故郷が近い\nのかもしれないわね", 109801, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3155, "この世界では、バルス\nの言う言葉が使われる\nときがあるのね\n忌々しいかぎりだわ", 109801, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3156, "ラムたち以外にも\nメイドが居るのね\nラムが…いえ、\n仕事を押しつけるわ", 109801, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3157, "…千里眼で視るかぎり\n周囲に異変はないわね\n危険がないなら、\nあとはレムに任せるわ", 109801, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3158, "…ラムもまた、\n「お姉ちゃん」って\n呼ばれる日が\nくるのかしらね", 109801, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3159, "ハッ！　ラムが作る\n蒸かし芋は完璧ね\n…気が向いたら、また\n作ってやろうかしら", 109801, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3160, "ラムに構う暇が\nあるなら働きなさい\nそうすれば、\nラムが楽をできるわ", 109801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3161, "ラムに何かを\n期待しても無駄よ\nラムに命令できるのは\nロズワール様だけだわ", 109801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3162, "暇なら買い物に\n付き合いなさい\n荷物を\n持たせてあげるわ", 109801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3163, "ラムになんの用？\nふざけた内容なら\nねじ切るわよ", 109801, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3164, "そういえば、あなたは\n記憶喪失だったわね…\n思い出す努力をなさい\n…家族が悲しむわよ", 109801, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3165, "蒸かし芋が欲しい？\n随分と厚かましいわね\n…まだ残りがあるから\n好きに食べなさい", 109801, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 3166, "だらしない顔ね\n顔を洗って\nしゃんとなさい", 109801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 3167, "お腹が空く時間ね\nラムの昼食を\n用意させてあげるわ", 109801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 3168, "メイドが寝るのは\n屋敷で最後…\nラムが寝るために、\nさっさと寝なさい", 109801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3170, "…ここから動かない\nという、誓いを\n立てたくなるわね", 109801, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3171, "これは…かわいいわね\nもちろん、\nラムほどではないけど", 109801, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3172, "…香炉かしら？\nこちらの世界にも\n質のよい物があるのね", 109801, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3173, "見つけてしまったの\nかもしれないわ…\nラムの楽園を！", 109801, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3175, "…はむっ\nラムの蒸かし芋ほど\nではないけど、\nなかなかいけるわね", 109801, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3176, "異世界ならではの\n面白い内容もあるわね\nとても興味深いわ", 109801, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3178, "ねえパック、\nにゃんにゃか\nにゃかにゃんしよっか", 109901, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3179, "時間もあるし、\nこっちの世界の石で\n石積みしようかな", 109901, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3180, "私もお姉さんだし、\nサレンさんを\n見習わないと…うん！\n私、すごーく頑張る！", 109901, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3181, "この世界は、銀髪で\nハーフエルフでも受け\n入れてくれるのね…\nなんだか嬉しいな", 109901, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3182, "今日のご飯…\nピーマルは\n入ってないわよね…？", 109901, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3183, "また、みんなで\n一緒に買い物に\n行きたいな", 109901, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3184, "パック以外から\n家族って呼ばれたの\n初めて…ふふっ♪\nちょっと嬉しいかも！", 109901, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3185, "今からパックの\n毛繕いをするけど…\n一緒にする？", 109901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3186, "私も何かお手伝いを…\nあ、そうだ！\n料理とかどうかな！", 109901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3187, "ねえ、ここで\n歌の練習って、\nしてもいいかな？", 109901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3188, "…あ、ごめんなさい！\n話しかけられるのって\nあまり慣れてなくて…", 109901, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3189, "すごーく頑張るから、\nこれからも勉強を\n手伝ってね", 109901, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3190, "せーの、びくとりー！\nふふっ…また、一緒に\nラジーオタイソー\nしようね♪", 109901, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 3191, "ちゃんと起きてる？\nふふっ…よかった。\nあ、べ、別に心配した\nわけじゃないからね？", 109901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 3192, "お昼ご飯が楽しみね♪\nあ、こっちの世界って\nまよねーずはあるの？", 109901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 3193, "そろそろ寝る時間よ。\n夜更かししたら、\nめっ！　だからね！", 109901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3195, "勉強も終わったし…\nちょっ、ちょっとだけ\n休んでもいいよね…？", 109901, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3196, "か、かわいい…！\nパックのかわいさに\n匹敵するかも…！", 109901, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3197, "良い香り…\nロズワールの屋敷にも\nこういうのって\nあるのかな？", 109901, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3198, "あったかい…\nずっとここに…はっ！\nダメダメ！　王選の\nために勉強しないと！", 109901, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3200, "あ…これ美味しい！\nこっちの世界は色んな\n食べ物があるのね。", 109901, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3201, "知らないことが\nいっぱい書いてある…\n異世界の本って、\n面白いのね", 109901, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3203, "海ってマジ最高！\nビーチの撮影も\n超楽しいしー♪", 110001, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3204, "20パーって何パ？\n今度ヒデサイに\n聞いてみよっ！", 110001, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3205, "夏の思い出を\nヒデサイと一緒に\n作りたいな～", 110001, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3206, "テンションあがる\n水着ゲットして\n超ラッキーだよ～♪", 110001, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3207, "休憩が終わったら\n撮影にもどらないと！\n気合入れてこ♪", 110001, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3208, "ヒデサイとビーチで\nパーティーしたいな～\n超楽しそうだよね！", 110001, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3209, "ヒデサイといると\n楽しくてうれしくて…\nカレシってこんな感じ\nなのかなぁ？", 110001, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3210, "見て見て～！\n夏らしいネイルに\nしてみたんだ～♪", 110001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3211, "ヒデサイ～\nこの水着どうかな？\nイケてるっしょ♪", 110001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3212, "水着姿の写真？\nもちいいよ！\nかわいく撮ってね！", 110001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3213, "うちだって\n夏の海みたいに\nキラキラしちゃうよ！", 110001, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3214, "ヒデサイとうちの\nフィーリングって\nめっちゃエモいよね♪", 110001, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3215, "ねーねーヒデサイ！\n一緒に泳ぎにいこっ！\nほら、手繋いで♪", 110001, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 3216, "水着オッケー！\nネイルもばっちり！\n朝から遊んじゃおー♪", 110001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 3217, "お昼食べたら\n昼寝して\nリフレッシュしよー♪", 110001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 3218, "水着ってマジで\n最強かも…このまま\nお風呂にも入れるし♪", 110001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3220, "マジ気持ちいいー♪\nおに癒やされるん\nですけど～！", 110001, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3221, "やばばばばー！\nかわいすぎだってば！\nマジらぶだよ～♪", 110001, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3222, "あ、この香り超好き！\nモデル仲間に\n教えたげよ～♪", 110001, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3223, "あったか～い♪\n夏の太陽みたい！", 110001, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3225, "マジ美味しいねこれ♪\nビーチで\n流行っちゃうかも！", 110001, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3226, "夏のために\n最新ファッションを\n勉強ちう！", 110001, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3228, "なにコレもにカワ～♪\nなんて生き物？\nヒデサイ知ってる？", 110001, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3229, "うわっ超フカフカ～\nなんかうち眠く\nなってきちゃった…", 110001, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3230, "海の家でのんびり\n過ごしましょう\n熱中症には注意してね", 110101, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3231, "はぁ、どうやったら\n泳げるように\nなれるのかしら～", 110101, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3232, "海で日焼け止めを\n塗らないと、あとで\n大変なことになるわよ", 110101, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3233, "久しぶりに水着を\n着たんだけど\n変じゃなかったかな…", 110101, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3234, "波の音って落ち着くわ\n授業中に聞いたら\nみんな寝ちゃうかも？", 110101, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3235, "海が楽しいって\n思えたのも\nあの子のおかげね", 110101, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3236, "あの子と二人で海に\n来られてよかったわ\n一日があっという間ね", 110101, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3237, "男の子と海だなんて\n少女漫画みたいで\n憧れてたの～", 110101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3238, "夏休みの宿題は\nちゃんとやった？\n遊んでばかりではダメ", 110101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3239, "昼の海と夜の海\nどっちが好き？　私は\n君といる海が一番よ", 110101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3240, "ふう、疲れたわ～\n冷たいジュースで\n元気回復ね！", 110101, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3241, "海で見た君の背中\nすごく大きく見えたわ\nやっぱり男の子なのね", 110101, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3242, "君と一緒に見た\n夜の海…\n忘れられそうにないわ", 110101, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 3243, "準備ができたら\n海に出かけようよ\n今日も楽しみましょう", 110101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 3244, "あら、もうお昼？\n海の家でランチに\nしましょ♪", 110101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 3245, "夜の海岸についてきて\nくれる？　暗がりは\n一人じゃ怖くて…", 110101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3247, "泳ぐ練習をしたら\n疲れちゃったわ～\nちょっと一休み…", 110101, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3248, "かわいいわね～\n浮き輪に乗せて\n海に浮かべたいわ～", 110101, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3249, "この匂い…\nなんだか夏の海を\n思い出すわ～", 110101, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3250, "夜の海風で冷えない\nように、しっかりと\n身体を暖めましょうね", 110101, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3252, "海で遊ぶ前に\nしっかり食べて\n体力つけましょう", 110101, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3253, "夏休み中でも\nしっかりお勉強\nしないと！", 110101, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3255, "この子に乗れば\n海の上でも\nすいすい進めるかしら", 110101, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3256, "ビーナスの誕生！\n…なんちゃって\nはしゃぎすぎかしら？", 110101, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3257, "夏の海には商機が\nあるはず…\nのがす手はないわね♪", 110301, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3258, "あたしが作った\n日焼け止め…\nこれは売れそうね！", 110301, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3259, "子どもたちのために\n浮き輪を用意して\nおかないと…", 110301, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3260, "海っていいわよね！\nみんなで羽を伸ばしに\n行きたいわ！", 110301, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3261, "波の音を聞いて\nのんびりするのも\n悪くないわね…", 110301, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3262, "ただの砂遊びも\nあんたと一緒なら\n楽しいから不思議ね…", 110301, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3263, "あんたがあたしの\nなんであれ、大切な\n存在ではあるのよね…", 110301, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3264, "夏の海って\nすごい人よね…\n迷子にならないでよ？", 110301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3265, "ぼんやり海に浮かんで\n波に揺られるのも\n悪くないわよ？", 110301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3266, "ビーチで子どもたちと\n一緒に砂のお城を\n作りたいわ♪", 110301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3267, "海で食べる\n焼きそばパン…\nいいかもしれないわね", 110301, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3268, "あたしの水着\n似合ってる？\nそう、ありがとう♪", 110301, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3269, "あんたが居ると\nうれしい…って、\n子どもたちが\n言ってたわ！", 110301, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 3270, "朝の浜辺って\nいいわよね\n散歩しに行きたいわ", 110301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 3271, "海の家のご飯って\nなんであんなに\nおいしいのかしら♪", 110301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 3272, "夜の海って、\nロマンチックよね。\nずっと眺めてたいわ…", 110301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3274, "やわらかくて\n気持ちいい…\nこのまま寝ちゃいそう", 110301, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3275, "海に一緒に入れる\nぬいぐるみとか\nないかしら？", 110301, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3276, "夏らしい\nいい香りね…\nなんの香りかしら？", 110301, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3277, "あったかい…\n濡れた体を乾かすには\nちょうどいいわね♪", 110301, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3279, "泳いだ後の食事は\n普段の何倍も\nおいしく感じるわね！", 110301, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3280, "海に行く前にいろいろ\n調べなきゃね。備え\nあれば憂いなしよ！", 110301, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3282, "海の守り神？\nずいぶんかわいらしい\n神様ね♪", 110301, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3283, "このぐらい\n大きな貝があったら\n食費も浮くかしら？", 110301, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3284, "浜辺で食うものって\nなんであんなに\nうまいのかな～", 110401, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3285, "いてて…\nちょっと日焼け\nしすぎたかな", 110401, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3286, "砂浜で走ると\nいいトレーニングに\nなるんだぜ", 110401, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3287, "夏ならではの\n料理…か\n何がいいかな…", 110401, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3288, "やっぱりここは\n涼しくて快適だな", 110401, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3289, "泳ぎの練習だったら\nいつでも付き合って\nやるからな！", 110401, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3290, "水着で部屋の中に\nいるのって\n変な感じだな…", 110401, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3291, "うおっ！\nきゅ、急に\n触るなって…\nびっくりした", 110401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3292, "え、砂がついてた？\n払ってくれたのか\nありがとよ", 110401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3293, "夏バテしてないか？\nここで休んでけよ", 110401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3294, "…そう見られると\n照れるっていうか…", 110401, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3295, "カキ氷とか\n食いたくないか？\n氷、削ってやるよ", 110401, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3296, "おまえと\n夏の思い出ができて\nよかったよ", 110401, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 3297, "ふう…\n朝一番で泳ぐの\nやっぱ気持ちいいな", 110401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 3298, "一番あちぃ時間だ\n水分補給は\nこまめにな！", 110401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 3299, "今日もお疲れ\n明日のパトロールも\nよろしくな", 110401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3301, "泳ぎ疲れたからな\n少し休憩だ…", 110401, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3302, "か、かわいい…\nいや、なんでもねぇ！", 110401, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3303, "この香り落ち着くな\nこういうのも\n悪くねーかも…", 110401, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3304, "あったけえ…！\n泳ぎ終わった後って\nけっこう体冷えてよ…", 110401, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3306, "海で食べると\nまた一味違うよな", 110401, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3307, "夏のサバイバル特集…\nちょっと読んでみるか", 110401, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3309, "へー！　こいつ、\nよくできてんな！\nいいなあ、実物も\n乗れたらいいのに", 110401, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3310, "か、かわ…！\nいや、じゃなくて\nあ、あたしがここに\n寝ていいのか…？", 110401, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3311, "故郷の花を飾っても\nいいかな～？\n部屋が楽しくなるよ～", 110501, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3312, "水着で歩いてると\n泳ぎたくなるさ～\n我慢できないさ～！", 110501, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3313, "夏が眩しいのは\nてぃーだがにこにこ\n笑ってるからさ～！", 110501, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3314, "泳ぎ疲れたら\n涼しい木陰で\nお昼寝するさ～", 110501, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3315, "クロールも\n犬かきも\n大得意さ～！", 110501, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3316, "なんだか歌いたい\n気分さ～\nキミもどう～？", 110501, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3317, "海の近くに住んで\nキミと歌って踊って\nそれが理想さ～！", 110501, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3318, "私とキミ\nどっちがたくさん\n泳げるか\n競争するさ～！", 110501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3319, "キミも三線に興味が\nあるのかな～？\n私が教えるさ～！", 110501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3320, "嫌なことがあっても\n私と歌って踊れば\nなんくるないさー！", 110501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3321, "たまには時間を忘れて\n私とのーんびり\n海を眺めるさ～", 110501, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3322, "ぶるぶるぶるっ！\n毛が濡れちゃっても\nへっちゃらさ～！", 110501, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3323, "泳ぐ前は準備体操～！\n背中押してあげるから\nしっかりやるさ～！", 110501, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 3324, "朝の浜辺は\nちゅらさんよ～\n一緒に歩こうよー！", 110501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 3325, "お昼ご飯は\n決まったかな～？\n私はタコライスさ～！", 110501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 3326, "星と波音の中で\n三線鳴らすのも\nオツなものさ～", 110501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3328, "ちょうどよかったさー\nゆっくり休んだら\nまた泳ぎに行くさ～！", 110501, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3329, "砂浜に飾って\nおきたいさ～\nきっとかわいいさ～！", 110501, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3330, "くんくん…はあ\n癒されるさ…\n故郷の夏の匂いさ～", 110501, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3331, "おおー…！\n濡れた髪が\nみるみる乾いてくさ～", 110501, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3333, "ん～！　これ、\n海で食べたら\nもっとおいしいさ～", 110501, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3334, "海の生物図鑑ね～\nこの前みかけた魚も\n載ってるかな～？", 110501, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3336, "わぁ～！　みてみて！\n背中に乗れるさー！\nキミと一緒に\n乗りたいさ～！", 110501, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3337, "ふかふかさ～\n海の底の\n人魚になった気分さ～", 110501, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 3338, "あの人が\nわたしの騎士だったら\nいいのにな…", 105801, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 3339, "あなたのおかげで\nわたしの心はいつも\nお腹いっぱいです♪", 105801, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 3340, "許されるのならば、\n今すぐにでも、\n主さまのおそばに\n行きたいです…", 105901, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 3341, "主さま…\nお慕いしております…\nいつまでも、おそばに\n居させてください…", 105901, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 3342, "なんか最近、\nあいつのことしか\n考えてない…\nあたし…どうしよう…", 106001, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 3343, "そ、外に行くなら、\n連れていきなさいよね\nその…あたしが\n守ってあげるから…", 106001, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3344, "部屋の中は涼しくて\n気持ちええどすなぁ\n天国やわぁ", 110601, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3345, "クラゲはんって\nフリルいっぱいの\nドレスみたいやねぇ", 110601, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3346, "外でセミはんが\n元気に鳴いてはるねぇ\n今日もええ天気やわぁ", 110601, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3347, "こないに暑いと\n海に入って\n涼みたくなるわぁ", 110601, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3348, "いつか\nマホマホ王国にも\n海の家作りまひょ♪", 110601, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3349, "うちも\n付き合いますえ\n泳ぎの練習しまひょ♪", 110601, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3350, "うちの里帰り\n王子はんも一緒に\n来まへんか？", 110601, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3351, "王子はんは日焼けより\n色白美人の方が\n好きでっしゃろか？", 110601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3352, "うちと一緒に\n浮き輪で海、\nプカプカしまへん？", 110601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3353, "日が落ちたら\n線香花火\nやりたいわぁ♪", 110601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3354, "木陰でのんびり\nお昼寝…\n最高やね～", 110601, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3355, "お祭ではぐれても\n王子はんなら見つけて\nくれはります？", 110601, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3356, "楽しい夏やわぁ\nずっとこんな日が\nつづくとええね", 110601, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 3357, "うちにしては早起き？\n暑くて目が早う\n覚めてもうたんどす", 110601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 3358, "お昼は冷たい\nおそうめんとか\nどうでっしゃろ", 110601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 3359, "夜は星がきれいやね\n二人で浜辺でも\n行きまへんか？", 110601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3361, "うーん、遊び疲れた\n身体にちょうどええ\nほっこり具合やわぁ", 110601, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3362, "かいらしい子は\nみーんな\nマホマホ王国の\n住人どすえ♪", 110601, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3363, "この香り…\n南国にいる気分やねぇ\n癒されるわぁ～", 110601, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3364, "これで\n冷えた身体も\nポカポカやねぇ", 110601, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3366, "外はまだ暑いやろうし\nバテんよう\nしっかり食べんとね", 110601, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3367, "海の中にもお城が\nあるんやねぇ\n行ってみたいわぁ", 110601, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3369, "この子、ふかふかやね\nこのままマホマホ王国\n乗せてってくれはる？", 110601, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3370, "人魚姫になっても\n最後は王子はんと\nハッピーエンド\nどすえ～", 110601, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3371, "真似したくなる\n人がここには\n大勢いますね", 107001, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3372, "偽物を一人\n紛れこませました\n…冗談です", 107001, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3373, "動物に変身して\nその辺りを\n散歩してきます", 107001, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3374, "失った記憶は\nどの程度回復するの\nでしょうね", 107001, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3375, "人や物だけでなく\nいずれは世界すら\n真似てみせます", 107001, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3376, "次は彼に\nどんなことを\n教えましょうか…", 107001, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3377, "知識はともかく\n私の力はまだ\n必要なようですね", 107001, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3378, "どうしました？\n…何でもない？\n…そうですか", 107001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3379, "退屈しています\n芸でもして\n楽しませなさい", 107001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3380, "研究のためです\n少し話し相手に\nなりなさい", 107001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3381, "何か真似して欲しい？\n私の能力を何だと\n思っているのですか", 107001, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3382, "貴方にはまだ色々と\n教えるべきことが\nありますね", 107001, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3383, "随分多くの女性と\n知り合いなのですね\n驚きました", 107001, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 3384, "…\nおはようございます\n…まだ朝ですよ", 107001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 3385, "食事にします\n…どうしました？\n早く持ってきなさい", 107001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 3386, "早く寝なさい\n夜更かしすると\n背が伸びませんよ？", 107001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3388, "マサキより\n座り心地は\nいいですね", 107001, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3389, "意外に複雑な\n構造ですね", 107001, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3390, "真似るなら\n香りまで\n再現しなくては", 107001, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3391, "発火現象の\n模倣…なかなか\n難しいものです", 107001, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3393, "偽物や分身を作るには\nエネルギーが\n必要不可欠なのです", 107001, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3394, "暇つぶしには\nちょうどよさそうです\n…", 107001, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3396, "学院かぁ…\n友達の作り方の\n授業はないかな？", 110701, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3397, "学院でぼっちに\nなったときのために、\n避難場所を\n探しておかないと…", 110701, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3398, "ぼっちの私が学院…\nな、なんだか\n目まいがしてきました", 110701, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3399, "クラスメイトに\n話しかける練習…\nむ、難しすぎます…！", 110701, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3400, "目指せ友達100人…\n無理かな…10人…\nや、やっぱり一人！", 110701, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3401, "あなたと学院生活が\n送れるなんて、\nとっても\nうれしいです！", 110701, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3402, "えへへ…\n学院でも、\nあなたと一緒に\n居られてうれしいな♪", 110701, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3403, "が、学院ってあんなに\n人が居るんですね…\n友達作りのイメトレを\nやり直さないと！", 110701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3404, "私にとって\n部活動とはＢＢ団！\n今日もたくさん\n活動しましょうね！", 110701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3405, "休み時間は寝たふり…\nご飯はおトイレ…\nぼっち対策は完璧です", 110701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3406, "学院で友達を作る\n勉強は完璧ですよ！\n購買部でパンを買って\n渡すんですよね！", 110701, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3407, "学院でも、\nＢＢ団の活動を\nしていきましょうね♪", 110701, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3408, "ぼっちに学院生活は\n厳しいですね…でも、\nあなたと一緒なら\n乗りこえられそうです", 110701, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 3409, "もうこんな時間…\nあの…一緒に学院に\n行ってくれませんか？", 110701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 3410, "お昼ですね！\n言ってくれれば、\nパンでもお握りでも\n買ってきますよ！", 110701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 3411, "今日もぼっち…\nいつか友達と一緒に、\nパジャマパーティー\nしたいなぁ…", 110701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3413, "森とは違う\n心地良さが…このまま\n眠っちゃいそうです…", 110701, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3414, "かわいい…この子を、\n話す練習の相手役に\nしてみようかな？", 110701, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3415, "いい香り…\nいつ友達ができても\nいいように、私の\n部屋にも置こうかな", 110701, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3416, "こ、これは…\nぼっちで\n日陰者の私には\n暖かすぎます！", 110701, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3418, "おいしい…！\nお弁当にして、学院に\n持っていけないかな？", 110701, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3419, "学院の授業に\nついていくためにも、\n勉強しておこうかな…", 110701, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3421, "あんた、何してんの。\nいいけど、なんでも", 110801, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3422, "邪魔。どいて。\nいや怒ってないし。\nコレがフツーだから", 110801, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3423, "いいけど、\n散らかしめの部屋だと\n片付けたくなるよね…", 110801, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3424, "暇なときは明日の\nコーデ考えてるよ。\n考えるのはタダだし", 110801, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3425, "昔よくシオリン\n構いすぎてハツネに\n文句言われたなァ…", 110801, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3426, "なんかさ。あんたって\n謎に女子トモ多いよね\n…いいけど、べつに", 110801, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3427, "ねえ。あのさあ。\nあんた弁当で好きな\n具とかってあんの", 110801, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3428, "あ？　何。なんか用。\nや、ないならないで\nいいけど、べつに", 110801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3429, "ごめ、ちょっと\n黙っててくんない。\n明日の試験ヤバだから", 110801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3430, "歩き方が静か？\n癖になってるんだよね\n足音を殺すの", 110801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3431, "うちの血液型？\n大雑把のОだけど。\nなんか文句あんの", 110801, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3432, "ひゃんっ！\nや、だからさ…急に\n話しかけんなっての", 110801, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3433, "な、何…あんまジッと\n見ないでくんない…\nなんか、その、ハズい", 110801, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 3434, "早起きは得意な方だと\n思うけど。昔から\nそれがデフォだったし", 110801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 3435, "こんな時間か…\n昼ごはん\nサクッと作っちゃお", 110801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 3436, "あ？　夜遊び？\nはッ…そんなん\n５歳で卒業したっての", 110801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3438, "あーヤバじゃんコレ…\nふかついてる場合\nじゃないっつーのに…", 110801, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3439, "なんだこれ\nかわいいかよ", 110801, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3440, "ふーん。\nこの匂い…\nなんかいいじゃん", 110801, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3441, "暖炉はアドじゃん。\n煮物、焼き物、蒸し物\nいろいろ作れそう", 110801, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3443, "何コレ\n食べ物困んないじゃん\nうちにも一台くんない", 110801, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3444, "本かァー…んー\n学校の勉強だけで\nいいかなァ…", 110801, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3446, "イタズラってなにを\nしたらいいのかな？", 111101, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3447, "カップケーキ\n上手に作れました\n食べてくれるかな…", 111101, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3448, "あの人、\nお菓子くれるかな？\nくれなかったら…ふふ", 111101, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3449, "オバケ…いないよね？\n怖くなんてないけど！", 111101, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3450, "ハロウィンは\n楽しいけどオバケの\nお祭りなんだよね…", 111101, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3451, "にゃ～、にゃお～！\nちゃんと怖いかな？\n早く帰ってこないかな", 111101, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3452, "あの人のことだから\nお菓子欲しがるよね…\n準備はバッチリです♪", 111101, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3453, "えへへ。猫さんのお耳\nよく出来てるでしょ？", 111101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3454, "触りましたね？\nイタズラしましたね？\nへんた～い！", 111101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3455, "フー！　頭を撫でたら\n怒りますよ！　あれ？\n猫みたいでした？", 111101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3456, "きゃっ！　も、もう、\n脅かさないでください\n怖くありませんけど！", 111101, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3457, "お菓子ですか？\nありがとうございます\nハッピーハロウィン♪", 111101, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3458, "ゴロゴロ…\nね、猫さんの真似です\n…似てましたか？", 111101, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 3459, "さぁ、お菓子作りを\n始めますよ。下準備は\n大事ですからね！", 111101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 3460, "今、つまみ食い\nしましたね！？　あっ\nおやつの時間でしたか", 111101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 3461, "さぁ、ハロウィンの\n始まりです…ふぁ…\nね、眠くありません！", 111101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3463, "ちょっと疲れたかも…\nパーティの時間まで\nひと休みしなきゃ", 111101, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3464, "私も今、こんな感じ\nなのかな？　こんなに\nかわいいといいなぁ", 111101, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3465, "甘い匂いがいっぱい…\nお菓子の香りかな？", 111101, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3466, "温かくなると、\n丸まりたくなって…\n…猫になっちゃう！？", 111101, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3468, "ニンジンもこれくらい\nおいしければ\nいいのに…", 111101, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3469, "ハロウィンの本、\nオバケがたくさん\n載ってて怖いかも…", 111101, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3471, "美味しそうなお菓子が\nいっぱい…これなら\nイタズラされませんね", 111101, 3, 12);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3473, "たくさんいたずら\nできるから\nハロウィン大好き！", 111201, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3474, "ハロウィンだから\nいつもよりすごい\nいたずらしたいな～！", 111201, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3475, "カボチャを綺麗に\nくり抜いてすごく怖い\nランタン作っちゃお！", 111201, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3476, "お菓子がたくさんで\nどれから食べるか\n迷っちゃうな～！", 111201, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3477, "どうすればみんなを\nびっくりさせられる\nかな～？", 111201, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3478, "カボチャ見てたら\nお腹空いて来たな～\nお菓子食べちゃお♪", 111201, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3479, "にいちゃんには\n特別ないたずらを\n用意してあげるね！", 111201, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3480, "カボチャにクラッカー\nを仕掛けて…わっ！？\nみ、見ちゃダメだよ！", 111201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3481, "ん？　なぁに？\nお菓子ほしいの？\nもう、しかたないな～", 111201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3482, "にいちゃんの\n面白い仮装が\n見てみたいな～！", 111201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3483, "お菓子くれたお礼に\nとびっきりの\nいたずらしちゃうね！", 111201, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3484, "にいちゃんは\nハロウィンでも最高の\nいたずら相手だよ！", 111201, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3485, "にいちゃんと一緒に\nハロウィンパレード\n歩きたいな～！", 111201, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 3486, "おはようにいちゃん！\n仮装して出かける\n準備しなきゃ！", 111201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 3487, "お腹すいたな～\nもらったお菓子\n食べちゃだめかな？", 111201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 3488, "たくさんいたずらして\nもうクタクタ～\nミソギもう寝るね…", 111201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3490, "やわらか～い…\nここでひと休みして\nまたいたずらしよう！", 111201, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3491, "かわいいもので\n油断させてわっ！\nってするのどうかな？", 111201, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3492, "いいにお～い！\nいたずらのアイデアが\nわいてくるよ！", 111201, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3493, "もらったお菓子\nあったかくしたら\nおいしいかな～？", 111201, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3495, "お菓子ばっかりじゃ\n飽きちゃうもんね！\nご飯も食べよう！", 111201, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3496, "本のなかに\nいたずらのヒント\nないかな～？", 111201, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3498, "お菓子がたくさん！\n一人で食べていいの？\nやっぱりだめか～！", 111201, 3, 12);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 3500, "お勉強は順調ですよ\nやっぱりお兄ちゃんの\nおかげなんでしょうか", 101101, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 3501, "お兄ちゃんとの\n思い出の場所\nまた行きましょうね", 101101, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3502, "えへへ～♪\nハロウィンの\nミイラさんうさぎさん\nぴょんぴょ～ん！", 111301, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3503, "るんたった～\nハロウィンハロウィン\n楽しいな～♪", 111301, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3504, "ハロウィンって\nどんなお菓子を\nもらえるのかな～？", 111301, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3505, "お菓子をもらう\n練習をしなくちゃ！\nトリックオア\nトリート～♪", 111301, 1, 4);
