INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3507, "おにいちゃんと\nいっしょにお菓子を\n食べたいな～", 111301, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3508, "おにいちゃんと\n毎日ハロウィン\nできたらいいな～♪", 111301, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3509, "えへへ～\nおにいちゃん！\nハッピーハロウィン♪", 111301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3510, "おにいちゃんも\nいっしょに\nハロウィンパレード\nしようね～！", 111301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3511, "ミイラさんうさぎさん\nだぞ～…えへへ\nミミこわかったかな？", 111301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3512, "おにいちゃん！\nハロウィンのお菓子\nおいしいね～♪", 111301, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3513, "おにいちゃんと\nいっしょなら\nおばけさんも\nこわくないよ！", 111301, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3514, "ミミね\nハロウィンも\nおにいちゃんも\nだ～いすき♪", 111301, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 3515, "おひさまさんも\nハロウィンの\nおようふく\n着たいかな～？", 111301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 3516, "ねえねえおにいちゃん\nお昼ごはんはもらった\nお菓子でもい～い？", 111301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 3517, "まだハロウィン…？\nうぅん…眠くないよぅ\nでもちょっと…ふぁ～", 111301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3519, "ふかふか～♪\nおばけさんが\n中にいるのかな～？", 111301, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3520, "わぁ、かわいい～♪\nミミといっしょに\nあそぼ～！", 111301, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3521, "ふわぁ～\nミミ、このにおい\nだ～いすき♪", 111301, 3, 4);
