INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 3555, "あなたとなら\nどんなことだって\nできる気がするわ♪", 105201, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3556, "あーあ、退屈だな\nカレでも探しに\nいこうかな", 106501, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3557, "この前のケンカは\n最高に燃えたなぁ\n…拳が疼いてきたぜ", 106501, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3558, "一体、この世界は\nどうなっちまったって\nいうんだ…？", 106501, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3559, "もう仕事の時間か\nボスも人使いが荒いぜ\n…ま、いいけどさ！", 106501, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3560, "オレたち以外に\nドラゴン族はあと何人\nいるんだろうな…", 106501, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3561, "カレの真剣な顔\n面白かったな\nまた遊びたいぜ", 106501, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3562, "どうしてカレがそばに\nいると、腹の底から\n力が湧いてくるんだ？", 106501, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3563, "よっす！\nなんかオイシイ話でも\nあったりしない？", 106501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3564, "なあ、オレと遊ぼうぜ\n心配すんなよ。危ない\nことはよしとくからさ", 106501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3565, "どうした。オレに\nケンカ売ろうってか？\n…ははは、冗談だよ！", 106501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3566, "ちょうど退屈してた\nところだ、特訓に\n付き合ってくれよ", 106501, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3567, "ったくよぉ…\nオレがかわいいなんて\n冗談はもうよせよ？", 106501, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3568, "次の仕事も一緒に\n来るだろ？　断っても\n連れて行くけどな！", 106501, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 3569, "おはよう！　今日の\n予定はまずケンカして\nで、その後もケンカだ", 106501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 3570, "よっす！\n腹ごしらえが済んだら\nオレと遊ぼうぜ！", 106501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 3571, "夜は危ないから\nあんま出歩くな？\n…だから面白えんだろ", 106501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3573, "次のケンカに備えて\n今は休んでおくか…\n全力で暴れたいからな", 106501, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3574, "ボスの飼ってる\nラーゴが、中に\n入ってるんじゃ？", 106501, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3575, "ああ、たぎる匂いが\nする…ひと暴れ\nしたくなってきた！", 106501, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3576, "おおぉ…\nオレの拳もなんだか\n熱くなってきたぜ…！", 106501, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3578, "いつでも全力で\nケンカするために\n力を蓄えないとな", 106501, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3579, "やっぱりな…オレが\n知ってる地図とは\nまるで違ってやがる", 106501, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3581, "さぁプレゼントで\n埋め尽くしてやろう！\n大いに喜ぶがいいよ！", 111501, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3582, "メリークリスマス☆\n聖夜の宴だ\n派手に楽しむとしよう", 111501, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3583, "煙突から入る？\nいいや、ワタシは\nいつでも正面からさ♪", 111501, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3584, "サンタクロースの\nお出ましだ！\n歓迎するがいいよ♪", 111501, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3585, "飾り付けが足りん！\nワタシが演出して\nやろうじゃないか！", 111501, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3586, "人の喜ぶ顔を見るのも\n悪くはないな…おっと\nワタシらしくないな☆", 111501, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3587, "プレゼントはまだか？\nワタシを喜ばせたら\nお返しは弾むぞ♪", 111501, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3588, "ワタシを呼んだか？\n退屈な要件だったら\n覚悟しておくことだ", 111501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3589, "なんだ？\nワタシへの挑戦か？\nよし、受けた♪", 111501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3590, "おっと、当たらんよ\nワタシの乱数聖域は\n絶対だからな…♪", 111501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3591, "なんだ、オマエも\n暇なのか？\n奇遇だな、ワタシもさ", 111501, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3592, "なにか愉しいことでも\nしようじゃないか\nさぁ、何をする？", 111501, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3593, "こうもワタシに\n気安く触れるのは\nオマエくらいだよ…♪", 111501, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 3594, "朝だろうと関係ない！\nさぁ愉しく苛烈に\n騒ごうじゃないか♪", 111501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 3595, "調子の出てくる時間だ\nしっかりワタシを\n愉しませてくれよ♪", 111501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 3596, "眠いなどと言わせんぞ\nまだまだ一日は\n終わらんからな！", 111501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3598, "いい具合じゃないか\nこのまま持って帰って\nしまおうか…♪", 111501, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3599, "なかなか愛らしい\n造形をしているな？\nワタシには負けるがな", 111501, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3600, "ほう、悪くない香りだ\n戦いの匂いをうまく\n隠してくれそうだ…♪", 111501, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3601, "体が温まってくると\nひと暴れしたくなるな", 111501, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3603, "ワタシの舌は厳しいぞ\n…ふーむ、なるほど\n認めざるをえんな", 111501, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3604, "クリスティーナさんは\n知識の収集も\n欠かさないんだよ", 111501, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3606, "これは驚いたな\nだがもっと、もっとだ\nワタシを退屈させるな", 111501, 3, 14);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3607, "こんな戯れだって\n軽くこなしてしまう\nクリスティーナさんさ", 111501, 3, 15);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3608, "メリークリスマス！\nさあ、パーティーを\n盛り上げるよ！", 111601, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3609, "衣装一つで全然\n気分が違うね！\n最高のクリスマスに\nなりそうだよ！", 111601, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3610, "ツリーのてっぺんの\n星って、なんだか\n欲しくなるんだよね", 111601, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3611, "めいっぱい\nクリスマスを楽しむよ\n遊びだって本気\nなんだからね！", 111601, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3612, "赤、白、緑、金\nクリスマスカラー\nってお洒落だよね", 111601, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3613, "雪、降らないかな？\nホワイトクリスマス\nってちょっと\n憧れるなぁ", 111601, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3614, "キミと過ごすこの\n楽しい時間がずーっと\n続けばいいのにな", 111601, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3615, "クリスマスライブ\nチケット用意するから\n絶対来てよね！", 111601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3616, "カンパーイ！\nジュースだけど\nちょっと大人の\n気分だね", 111601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3617, "クリスマス衣装どう？\nかわいいでしょ\nあっ、どこ見てるの！", 111601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3618, "ねえ、後で街に\n行こうよ\n大きなツリーを\n一緒に見たいんだ♪", 111601, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3619, "ねえ、ダンスの練習に\n付き合ってくれない？\n大丈夫、手取り足取り\n教えてあげるから♪", 111601, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3620, "来年のクリスマスも\n隣にキミが\nいてくれたら\nうれしいのにな", 111601, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 3621, "クリスマスって朝から\nドキドキしちゃうね\nなんだか子どもみたい", 111601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 3622, "パーティーの準備も\n大分進んだね\n一息ついて\nランチにしよっか", 111601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 3623, "静かな夜だね\n心臓の音まで\n聞こえちゃいそう…", 111601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3625, "あ～、この絶妙な\n柔らかさ…\nいい仕事してるなぁ…", 111601, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3626, "アイドル顔負けの\nかわいさ、ズルい！\nでもかわいい！", 111601, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3627, "ん～、落ち着くなぁ\n今日はゆっくり\n眠れそうだよ", 111601, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3628, "ぬくぬく～\nどうしよう、ここから\n動きたくないなぁ", 111601, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3630, "ダンスの練習もしたし\nちょっとくらいなら\nいいよね！", 111601, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3631, "アイドルといえど\nかわいいだけじゃダメ\n知識も必要だよね", 111601, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3633, "面白ーい！\nこの演出、ライブでも\nできないかな？", 111601, 3, 14);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3634, "もふもふ～♪\n寒い日には最高の\n抱き心地だね！", 111601, 3, 15);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 3635, "彼ともっとお話し\nしたいな…もう少し\n補習を増やそうかしら", 101801, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 3636, "君のこともっともっと\n教えて欲しいな。私に\n補習授業してくれる？", 101801, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3637, "この姿ならあやつも\n子ども扱いできまい！\n…じゃがなぜか、少し\n物足りないような…", 111701, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3638, "時間もあるし、\n聖夜の宴の\n準備をするかのう♪", 111701, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3639, "…わらわの知る聖夜と\n随分違うが、宴は宴\n楽しませて\nもらおうかのう♪", 111701, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3640, "大人の姿で\nできること…ふむ\n少しあやつを\nからかってやるか♪", 111701, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3641, "…クリスマスは\n恋人たちの夜！？\nち、近頃の若いもんは\n破廉恥なのじゃ！", 111701, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3642, "…あやつがおらんと、\n宴も物足りぬな\n…馬鹿者が\n早く帰ってこい…", 111701, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3643, "誰も見ておらぬな…？\n…よし、あやつへの\nプレゼントを\n準備するとしようか！", 111701, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3644, "クリスマスの\nチキンにケーキ…\nべ、別に楽しみになど\nしておらぬぞ！", 111701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3645, "この姿では、わらわの\n頭をなでるのは\n難しかろう…べ、\n別に残念ではないわ！", 111701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3646, "メリー\nクリスマスじゃ！\nさあ、わらわのために\n宴の準備をするのじゃ", 111701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3647, "そんなにこの衣装が\n気になるのか？\n…だったら、直に\n触ってみるがよい♪", 111701, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3648, "いつもと逆に、お主を\n子ども扱いしてやるか\nほれ、膝枕をしてやる\nからそばに来い♪", 111701, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3649, "…せ、聖夜の予定は\n空けておくのじゃぞ？\nなんでって…\nな、なんでもじゃ！", 111701, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 3650, "もう朝か…聖夜の宴が\n楽しみで…ではなく！\n準備のせいで、\nあまり眠れんかったぞ", 111701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 3651, "…ずっと雪が\n降っておれば、\n陽光が強いこの時間も\n怯えずにすむのにのう", 111701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 3652, "時は来た…\nさあ眷属よ！\nわらわと共に、\n聖夜の宴を楽しもうぞ", 111701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3654, "良い椅子ではないか\nここから聖夜の宴を\n眺めるのもよいかもな", 111701, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3655, "か、かわい…こほん\nなかなか良い\nデザインをしておるな", 111701, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3656, "ふむ、良い香りじゃ\n聖夜の宴でも\nこれを使うとしよう", 111701, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3657, "これが癒し…はっ！\nあやうく浄化される\nところであった…", 111701, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3659, "ニ、ニンニクは\n入っておらぬよな？\n…うむ、美味じゃ♪", 111701, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3660, "聖夜は恋人たちの夜…\n…き、気になるわけ\nではないが、\n少し調べてみるか", 111701, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3662, "ほう…これは面白い\n魔法の使い方じゃな\nまるで本物の\n演劇のようじゃ", 111701, 3, 14);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3663, "こ、この程度の\nかわいさに誘惑される\nわらわでは…\nふわもこなのじゃ～♪", 111701, 3, 15);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3664, "おせち料理も\n魔物尽くしだったり\nしないわよね！", 112001, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3665, "初詣で何をお祈り\nしようかしら…\n定番でいいわよね？", 112001, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3666, "初日の出って\n特別な感じがして\nいいわよね…", 112001, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3667, "寒い中並んで飲む\n甘酒って不思議なほど\nおいしいわよね", 112001, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3668, "羽根つきで勝負…\nあいつにだったら\n勝てるかしら？", 112001, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3669, "年賀状になんて書こう\n…いろいろと\n恥ずかしくなるのよね", 112001, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3670, "今年もあいつと\n居られること…\n結構うれしいわ", 112001, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3671, "何じっと\n見てんのよ？\nお年玉はあげないわよ", 112001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3672, "おせち料理一緒に\n食べる？\n結構おいしいわよ", 112001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3673, "羽根つきで負けたら\n顔に墨塗られるのよ\n覚悟しなさい！", 112001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3674, "門松飾るから\nちょっと手伝って\nくれる？", 112001, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3675, "あんたと一緒に\n初詣行くのも悪くない\n…かも", 112001, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3676, "初詣で何をお祈り\nしたか？　うっさい！\n内緒よ！内緒！", 112001, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 3677, "もう朝なの…\n正月くらいゆっくり\n寝ていたいわよね…", 112001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 3678, "おせちに飽きたら\n次は何を食べよう\nかしら…", 112001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 3679, "そろそろ寝ましょ\nいい初夢が見られると\nいいわね", 112001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3681, "この格好でいるのも\nなかなか疲れるわね\n休憩～", 112001, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3682, "か、かわいい…う～ん\n鏡餅も顔を描けば\nかわいくなるかしら？", 112001, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3683, "いい匂いね\n落ち着いて\n眠れそう…", 112001, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3684, "ここでお餅焼いたら\n一段とおいしく\nなりそうね", 112001, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3686, "正月に食べるものは\nなんでもおいしく\n感じちゃうわね！", 112001, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3687, "魔物を使わない\nおせちを\n調べておかなきゃ…", 112001, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3689, "新年を気持ちよく\n過ごせるように\nお掃除がんばります！", 112101, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3690, "神殿に通っていたら\nいつの間にか足腰が\n鍛えられたような…", 112101, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3691, "神殿付近は屋台も\nあるので、良い香りが\n漂ってくるんですよ～", 112101, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3692, "おせちを作れるように\nなれば、子どもたちも\n喜んでくれるかな…？", 112101, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3693, "コホォォォォォ…！\nふぅ、深呼吸すると\n落ち着きますね♪", 112101, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3694, "お仕事以外の時も\nあなたと…い、いえ！\nなんでもないです！", 112101, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3695, "あなたと一緒だと\nなんだかポカポカ\nしてくるんです…", 112101, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3696, "この格好をしていると\nなんだか神殿にいる\n気分になりますね", 112101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3697, "そういえば神殿で\n甘酒をもらったんです\nあなたも飲みますか？", 112101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3698, "あっ、髪飾りが\nズレちゃってました！\nいけないいけない…", 112101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3699, "ひゃわっ！？\nく、くすぐったり\nしないでください～", 112101, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3700, "そ、そんなに\n見つめられると\nて、照れちゃいます…", 112101, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3701, "今度、私が考えた\n運気上昇のおまじない\nかけてあげますね♪", 112101, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 3702, "おはようございます\n神殿のお手伝いに行く\n準備もしなきゃですね", 112101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 3703, "お昼になりましたし\nご飯は私が作…\nえっ、ダメですか！？", 112101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 3704, "明日も早いですし\nそろそろ寝る支度を…\nふわぁ…", 112101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3706, "お掃除も終わったので\n少し休憩を…ふわぁ…\n急に眠気が…", 112101, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3707, "新年を気持ちよく\n過ごせるようこの子も\nきれいにしましょう♪", 112101, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3708, "神殿のお香のような\n香りだと落ち着いて\n過ごせそうですよね", 112101, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3709, "ぽかぽかですね～…\n甘酒も飲んで体の\n芯から温めましょう", 112101, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3711, "今年こそ\n料理も上手に…\nもぐもぐもぐもぐ…", 112101, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3712, "失敗続きだと\n逆に運が良くなる…\nほ、本当ですかぁ…？", 112101, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 3714, "キミにならもう\nどんな私を見られても\n大丈夫かな…♪", 103401, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 3715, "なになにどしたのぉ？\nお姉さんが撫でたげる\nこっちにきなさぁい♪", 103401, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3716, "わたくしの舞を\n主さまは喜んで\nくださるでしょうか…", 111901, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3717, "年を越したあとに\n食べるおそばも\nおいしいです", 111901, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3718, "甘酒を飲むと\n心まで温まる\n気がいたします", 111901, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3719, "主さまのために\n真心を込めたおせちを\nご用意しなくては", 111901, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3720, "おみくじを引くのは\nドキドキしつつも\n楽しいものです", 111901, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3721, "主さまの温もりは\n心まで温めてくれる\nようです", 111901, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3722, "うまくできなくとも\n心を込めて精いっぱい\n舞いましょう", 111901, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3723, "主さま、この晴れ着は\n似合っている\nでしょうか？", 111901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3724, "お餅はゆっくりと\n召し上がって\nくださいまし", 111901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3725, "主さまのお顔の\n福笑いなら、完璧に\nできる気がいたします", 111901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3726, "主さまはどのような\n初夢をご覧になった\nのでしょう？", 111901, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3727, "甘酒の飲み過ぎは\nいけませんよ？\nめっ、でございます", 111901, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3728, "二人羽織とは\nあんなにも暖かい\nものなのですね…", 111901, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 3729, "おはようございます\n寒い季節の朝日は\nとても美しいですね", 111901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 3730, "おいしくとも\n食べ過ぎは正月太りの\nもとでございます", 111901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 3731, "いい夢が見られるよう\n暖かくして\n眠るといたしましょう", 111901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3733, "ふかふか…\nこのまま\n寝てしまいそうです", 111901, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3734, "あなたと一緒に\nお正月遊びを\n楽しんでみたいです", 111901, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3735, "神殿の独特な香りを\nおうちに持ち込むのは\nどうでしょうか", 111901, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3736, "風邪をひきませんよう\n暖かくいたしましょう", 111901, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3738, "食べ過ぎは\n正月太りの\nもとだというのに…", 111901, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3739, "主さまのために\n参拝の作法を\n予習しておきましょう", 111901, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3741, "…この杖、変身前には\n無いパーツがあるけど\nこれはどこから…うん\n考えないようにしよう", 112201, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3742, "フ、フリフリの\nかわいい衣装…はっ！\n素に戻ってはダメだ！", 112201, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3743, "魔法探偵\nミスティ★カスミ！\n魔法の力で捜査開始！\n…練習終わりっと", 112201, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3744, "怪しい視線を感じる…\nまさか、近くに\n闇の力を持つ人が！？", 112201, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3745, "時間もあるし、\n魔法少女の修行を…\nじゃ、じゃなくて！\n探偵の仕事をしよう！", 112201, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3746, "助手くんとずっと\n一緒に居られるし、\n魔法少女も\n案外悪くないかも♪", 112201, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3747, "魔法少女の訓練のため\n助手くんの所に行こう\nかな…あ、あくまで\n訓練のためにね！", 112201, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3748, "…髪型が急に\n変わって驚いた？\nき、気になるなら…\n少し触ってみるかい？", 112201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3749, "助手くんは魔法少女に\nなりたいのかい？\nその場合は魔法騎士…\nいや、魔法助手とか？", 112201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3750, "頭に浮かぶ呪文…\n強制変身…助手くん\n魔法少女とは\n呪いか何かなのかな？", 112201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3751, "あ、あまり触らないで\nくれ…魔法少女になる\nと感覚が鋭くなって、\n耳も敏感に…ひゃう！", 112201, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3752, "や、やっぱりこの\n格好は変かな…え、\nかわいい？　じゃあ…\nいや、でも、う～ん", 112201, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3753, "…この変身は助手くん\nのせいでもあるんだよ\nだから…せ、責任を\n取ってもらうからね！", 112201, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 3754, "おはよう助手くん\nさあ、そろそろ\n街のパトロールに\n行こうじゃないか！", 112201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 3755, "もうお昼…助手くん、\nそろそろ本業に戻ろう\n…その顔は何かな？\n私の本業は探偵だぞ！", 112201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 3756, "夜だけど、私は\nもう少し仕事をするよ\n魔法少女の強い体は\nこんなときに便利だね", 112201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3758, "気持ちいい…\nこの癒しを、魔法と\n論理の二つの観点から\n解明したいね…", 112201, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3759, "ダ、ダメ！！\nここで感情のままに\n飛びついたら、私の\nイメージがぁ～！", 112201, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3760, "心が安らぐ香りだね…\n…今なら、魔法少女も\nすんなりと受け入れ…\nてはダメだっ！", 112201, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3761, "あったかい…\n魔法少女だろうと\n休息は必要だし、少し\n休んでいこうかな", 112201, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3763, "魔法少女も探偵も、\nやっぱり体が資本！\n食べられるときに\n食べておかないとね♪", 112201, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3764, "…魔法少女\nセリイちゃんの本は\nどこにあるのかな？", 112201, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3766, "魔法少女かぁ…\nせっかくだし思う存分\n体を動かしてみたいな", 112301, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3767, "ニャットちゃん…\nこの格好を見て\n私って気づくかな？", 112301, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3768, "ふふ…\n今日はなんだか\n調子がいいかも", 112301, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3769, "魔法は便利だけど\nあまり頼りすぎちゃ\nダメだよね", 112301, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3770, "さっき街で小さな子に\n声かけられちゃった\n照れるけどうれしいな", 112301, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3771, "あの人がいると\nなんだかホッとする\n不思議…", 112301, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3772, "魔法少女になっても\nあの人への気持ちは\nずっと同じ…", 112301, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3773, "今日の私…ですか？\nはいとっても元気です\n…あなたのおかげかも", 112301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3774, "よかったら魔法の\n練習に付き合って\nくれませんか？", 112301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3775, "あの…\nこの格好どうですか？\n似合ってると…いいな", 112301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3776, "…な、なんですか？\nあまり見られると\n恥ずかしいです…", 112301, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3777, "私、あなたのそばに\nいられたら、それで…", 112301, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3778, "想い人と結ばれる\n魔法は…ひゃっ！？\n今の聞いてましたか？", 112301, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 3779, "おはようございます\n今日も街の平和の\nために頑張らなきゃ", 112301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 3780, "お腹すきましたね。\nあの…一緒にランチは\nいかがですか？", 112301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 3781, "あ、もうこんな時間…\nふふ、まだこんなに\n体力が余ってるのに", 112301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3783, "気持ちいい…\nこの気持ち良さは\nどんな魔法も勝てない", 112301, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3784, "何これ、かわいい…\nお姉ちゃんにも\n見せてあげたいな", 112301, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3785, "ん～、いい香り\n…これ、魔法で\n出せたりしないかな？", 112301, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3786, "あったか～い\nえへへ…なんだか\nお姉ちゃんみたい", 112301, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3788, "おいしいなぁ、これ\n魔法少女になってから\n食欲旺盛かも…", 112301, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3789, "ふふ…\n魔法少女になっても\n読書はやめられないな", 112301, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 3791, "みらくるまほりん\nくるりんぱ～♪\n王子はんのために\n花嫁修業や～♪", 101001, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 3792, "あ、あのな王子はん\nできたらその…\nお姫さまだっこを\nお願いしてもええ…？", 101001, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3793, "やっぱりわたしたちの\nギルドハウスの\n居心地は最高ですね☆", 180401, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3794, "王宮じゃできないこと\nやっちゃいましょう♪\n例えば、えっと～…", 180401, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3795, "そうだ、隠しておいた\nおやつがありました！\nまだ食べられますね☆", 180401, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3796, "ごはんはみんなで\n楽しく食べましょう！\nそれが一番の\n調味料です♪", 180401, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3797, "やっぱりここが、\nわたしの帰る場所です\n…ただいま☆", 180401, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3798, "なんだか物足りません\nきっと、あの人が隣に\nいないからですね…", 180401, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3799, "どこを見ても、大切な\n思い出がたくさん\n詰まっていますね♪", 180401, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3800, "無礼者～、なんて\n冗談です♪　どんどん\n触っちゃってください", 180401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3801, "ぷにぷにでしょう～？\nい～っぱい食べるのが\nぷにぷにの秘訣です！", 180401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3802, "握手したいんですか？\nはい、にぎにぎ～！\nもう離しませんよ♪", 180401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3803, "どうかしましたか？\n退屈してるんでしたら\nお散歩に行きましょう", 180401, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3804, "ごはん王子♪\nわたしは王女ですから\nこれでピッタリですね", 180401, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3805, "大好きです～\n愛してます～\n…ほんと、ですよ♪", 180401, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 3806, "朝ごはんは大事です！\n山盛りごはんを食べて\n元気いっぱいですよ☆", 180401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 3807, "お腹ぺこぺこになる\n時間ですね～！\nごはん特盛、一つ！", 180401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 3808, "お腹いっぱい、今日は\nよく眠れそうですね♪\n明日もおいしい一日を", 180401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3810, "休憩休憩～♪\nお腹を休めて、次の\nごはんに挑みます…☆", 180401, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3811, "かわいらしい\nぬいぐるみですね～\nキャルちゃんみたい☆", 180401, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3812, "すんすん…この匂い\n食欲増進の効果が\nありそうですね…？", 180401, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3813, "体温が上がってくると\n眠くなっちゃいます…\nほわぁ～", 180401, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3815, "もぐもぐもぐもぐもぐ\nむぐむぐむぐむぐむぐ\nはぐはぐはぐはぐはぐ", 180401, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3816, "教養を身に着けるには\n読書が一番です！\nえっと次のレシピは～", 180401, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3818, "アイドルらしくなる\nコツって、何か\nあるんでしょうか？", 112401, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3819, "色んな人と話すのが\n好きなんです。つい\n長くなっちゃって…", 112401, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3820, "アイドルを目指すって\n決めてから、毎日が\nワクワクなんです♪", 112401, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3821, "ちゃんとストレッチ\nしておかなくっちゃ\nよいしょ、よいしょ…", 112401, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3822, "リンちゃんミオちゃん\n二人に負けないように\n私、頑張ります！", 112401, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3823, "私の笑顔は素敵だって\n誰かに褒められた…\nそんな気がするんです", 112401, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3824, "私たちを支えてくれる\n誰かが、ずっと一緒に\nいてくれたような…", 112401, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3825, "どうかしましたか？\n何か困ってるのなら\nお手伝いしますよ！", 112401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3826, "みなさん個性的で\n普通じゃない人ばかり\nいるような気が…", 112401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3827, "今日はポカポカした\nいい天気ですね～\nお散歩日和です！", 112401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3828, "今、ミルクティーを\n淹れてたんです♪\nあなたも飲みますか？", 112401, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3829, "私の得意なこと？\nうーんなんでしょう…\nあっ、肩たたきです！", 112401, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3830, "生・ハム・メローン！\nあ、ライブの掛け声を\n練習してたんですよ♪", 112401, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 3831, "おはようございます！\n今日も一日\n頑張りましょうね！", 112401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 3832, "しっかりお昼を食べて\n午後からも張り切って\nいきましょうね♪", 112401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 3833, "ぐっすり眠れるよう\n早めに用事を済ませて\nお休みしましょうね", 112401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3835, "ちょっと休んだら\nレッスンの続きも\n頑張ります！", 112401, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3836, "えへへ～\nふわふわもこもこで\nかわいいです～♪", 112401, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3837, "リラックスできる\nいい香りですね～♪", 112401, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3838, "わぁ、ポカポカです♪\n温かい飲み物も\nほしくなりますね", 112401, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3840, "レッスンしたら\nお腹が空いちゃって…\nぱくぱくもぐもぐ…", 112401, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3841, "レッスンに役立つ\n運動の情報とか\n載ってるでしょうか…", 112401, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3843, "きらきらーん☆\nよし、次のステージは\nこれで行けるかも！", 112601, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3844, "私の元気でみんなも\n元気になってくれたら\nうれしいな～", 112601, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3845, "ちょっとレッスンで\n疲れちゃったかも…\nでも頑張るぞっ！", 112601, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3846, "晩ごはん何にしよう？\nそーだ、しぶりんにも\n聞いてみよっと！", 112601, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3847, "元気だから笑顔になる\nじゃなくて\n笑顔だから\n元気になるんだよね～", 112601, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3848, "声援を聞いてる時ね\nすごく懐かしいって\n思うことがあるんだ", 112601, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3849, "私を応援してくれる\n一番大事な人が\n隣にいた気がする…", 112601, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3850, "なになにー？\nミオちゃんに\n何か用かな～？", 112601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3851, "おっ、やっほー！\n元気そうだね～、キミ\n私？\nもちろん元気～！", 112601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3852, "ねぇねぇ、ちょっと\n遊びに行かない？\nたまには息抜きで！", 112601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3853, "ね、この魔物料理さ\nプリンって言い張れば\n食べてもらえそうかな", 112601, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3854, "えへへ\nいつもありがとねっ！\nパワーもらってるよ～", 112601, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3855, "どうしたどうした～\nミオちゃんの魅力に\nメロメロなのかい？", 112601, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 3856, "おっはよ～！\n一日の始まりだし\nテンション上げてこ！", 112601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 3857, "お腹空いた～…\nねぇねぇ、一緒に\nお昼食べようよ～", 112601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 3858, "ふわぁ～…\n眠くなってきちゃった\nそろそろ休もっかなぁ", 112601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3860, "わぁ、ふっかふか～！\nレッスンの疲れも\n吹っ飛んじゃうね☆", 112601, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3861, "何これ…かわいーっ！\n早くしまむーに\n教えてあげなきゃ！", 112601, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3862, "すぅ～…\nうんっ、これ私の\n好きな香りだな～♪", 112601, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3863, "ポッカポカだね～♪\n…あ～ん、ここから\n動きたくないーっ！", 112601, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3865, "わっ、おーいしー！\nこれはテンション\n上がっちゃうね♪", 112601, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3866, "ふむふむ…\nふむふむふむ…！\n勉強になった、ブイ☆", 112601, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 3868, "なんでドキドキが\n止まらないの…？\n騎士くんのせいだー！", 100101, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 3869, "ドキドキしてるの\nあたしだけ、かな…\n騎士くんはどう…？", 100101, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 3870, "騎士クンの前で\n好きって言えるように\n練習しないと…", 100201, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 3871, "騎士クン…大好き\n…き、騎士クン！？\nなんでここに！？", 100201, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 3872, "困ったな…\n彼が居ないだけで、\nここまで\n寂しいと思うなんて…", 100301, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 3873, "えっと…\n今日は訓練じゃなくて\nその…二人でゆっくり\n過ごしたいな…", 100301, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3874, "アイドルの心構え…\nどういう風に考えると\nしっくりくるのかな", 112501, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3875, "何か始めるのって\nドキドキするよね\nだから最近楽しいんだ", 112501, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3876, "冒険者の時って毎日\nバタバタしてたけど…\n目標ができると違うね", 112501, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3877, "少しお腹が空いた…\nチョコレート一粒だけ\n食べようかな", 112501, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3878, "ウヅキもミオも\n頑張ってるし\n私も負けられないね", 112501, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3879, "レッスンしてる時さ\n何か、誰かが足りない\nそう思うことがあるの", 112501, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3880, "大事なのは笑顔…\n誰か、大切な人から\n言われた気がするんだ", 112501, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3881, "どうしたの、何か用？\n手伝えることがあれば\nすぐに言ってね", 112501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3882, "姿勢がいい？\n普段から鍛えてるし\n自然とそうなるのかも", 112501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3883, "さっき散歩してたら\n犬を見かけたんだ\n人懐っこい子だったよ", 112501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3884, "今から冒険？\nなら私も一緒に行くよ\n前衛は任せておいて", 112501, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3885, "今からレッスンに\n行くつもりだったんだ\n少し手伝ってくれる？", 112501, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3886, "蒼の剣を受けよ！\nえ、何してるのって？\nただの発声練習だけど", 112501, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 3887, "おはよう、早いね\n今日は依頼もないし\nゆっくりしようかな", 112501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 3888, "そろそろお昼だね\nうーん、今日は\n何を食べよう…", 112501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 3889, "もうこんな時間…\n明日も早いし\nそろそろ寝なくちゃ", 112501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3891, "レッスンで疲れたし\nちょっとだけ\n休んでおこうかな", 112501, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3892, "ふふっ、かわいい\nふわふわしたものって\n撫でたくなるんだよね", 112501, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3893, "うん、いい香り…\nこういうものがあると\n落ち着くからいいよね", 112501, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3894, "温かい場所って\n居心地がいいから\nのんびりしちゃうよ", 112501, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3896, "レッスン頑張ったら\nすぐお腹が空くし\n食べておかないと…！", 112501, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3897, "アイドルのことを\nもっと勉強して\n成長しなくちゃ", 112501, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3899, "ああ、知りたい…\nなんでもかんでも\nとにかく知りたい…", 111001, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3900, "ああ、眠い…\nいつでもどこでも\nとにかく眠い…", 111001, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3901, "君、気を付けたまえ\nぼーっと歩いていると\nほわっ！　転んだ…", 111001, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3902, "ミツキ師にいただいた\n新作サプリ…丁度よい\n被験体はいないものか", 111001, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3903, "サレンディア救護院に\nユニちゃん８歳として\n潜入済みだが何か？", 111001, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3904, "君がユニちゃんズの\n同志であること、\nぼくは誇りに思うぞ", 111001, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3905, "ぼくは青春がしたい\nおい、君\nぼくと青春しないか", 111001, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3906, "ああ、そうだな。\nぼくもそう思う。\n……えっ、何が？", 111001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3907, "待て、話しかけるな。\n今の閃きをメモに…\n…ああっ、忘れた！", 111001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3908, "歩き方が綺麗？\n何を今更なこと\n言っとるんだ君は", 111001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3909, "ぼくの血液型？\n大らかのОだ。\n何か異論あるかね", 111001, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3910, "君の声は精神に\n高揚をもたらすな…\n波形を分析したいぞ", 111001, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3911, "妙だな…最近\n君に話しかけられると\n心拍に乱れを覚える", 111001, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 3912, "おっと、朝だな。\nそろそろ寝るか、\n朝更かしは体に悪い", 111001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 3913, "ふむ、昼餉時か…\n今日のランチは\nサプリ定食にしよう", 111001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 3914, "もう夜だな、\nそろそろ起きるか。\n最高の一日にするぞ！", 111001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3916, "ほわぁ～\n副交感神経が…\n優位に…ふふっひ", 111001, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3917, "かわいすぎるな…\nセクシーユニには\nミスマッチか…", 111001, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3918, "ほぁあアあァぁー…\nいい香りー…\nあアァあ…イイ…", 111001, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3919, "炎はいいぞ\nそこには人類発展の\nドラマがある", 111001, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3921, "フオオー！\n次から次へと固形食物\n原理気になるー！", 111001, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3922, "良書は時間を忘れる\n…ふむ、今度\n布団を持ってこよう", 111001, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 3924, "きらーん☆\nハツネちゃんに\n不可能はないのだ☆", 101201, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 3925, "キミと一緒だと\n寝ちゃうのが\nもったいないよ～", 101201, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3933, "ちぇるーん☆", 192101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3951, "ちぇる～ん☆\n今日もチエルは\nかわいいですよね～？", 110901, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3952, "どこかに楽しいもの\n落ちてないかにゃー", 110901, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3953, "チエルのオキニは\n甘々カフェラテ！\nシクヨロでぇ～す☆", 110901, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3954, "先輩、ひまですよね？\nチエル遊んであげても\nいいですけど？", 110901, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3955, "はぁー…しゅき…\nカルミナ尊い…\n一生すこれる…", 110901, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3956, "先輩、ひまー\nちょっとは構えよーう\nデュクシ！デュクシ！", 110901, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3957, "先輩なんか\nおごって～！\nてか…おごらなくても\nいいから遊んでよ…", 110901, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3958, "え、なんですか？\n告白だったら\n間に合ってますけど", 110901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3959, "ちょっと静かに！\n今ヤバかわポーズの\nイメトレ中なんです！", 110901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3960, "足取りが軽やか？\nあー…ダンスとか\n習ってるからですかね", 110901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3961, "チエルの血液型？\n大型新人のОです\n何か問題でも？", 110901, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3962, "先輩って何気に\nいい声してますよね\n言うことは変だけど", 110901, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3963, "え、なんですか？\nたまにだったら\n告白してもいいですよ", 110901, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 3964, "おはちぇる～ん☆\nハイ、ご一緒に～？", 110901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 3965, "今日のランチは\nどこで\n映えよっかにゃー", 110901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 3966, "こんばんちぇるるん☆\nハイ、ご一緒に～？", 110901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3968, "ふかふかチエルン\n略して、ふかルン♪", 110901, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3969, "かわいいこの子と\nかわいいチエル！\n映ぁ～え～る～！", 110901, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3970, "かわいい香り～！\n持って帰ろっと！\nすぅーっ…", 110901, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3971, "マシュマロは\n焼かない方のが\nかわいくないです？", 110901, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3973, "チエル食べても\n太らない体質\nなんでぇ～！", 110901, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3974, "読書は好きですよ\nシンプルに楽しいし\nギャップ萌えでしょ？", 110901, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3976, "みんなのあたしを\n見る目が違うね\nま、気にしないけどね", 112701, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3977, "今日も疲れた～\nやる気ゼロだし\nもう寝ちゃおうかな", 112701, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3978, "あたしの代わりに誰か\n働いてくれないかね", 112701, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3979, "労働の後に食べる\nあんパン最高～", 112701, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3980, "仕事の合間に\nちょっと一息いれよ\nあ～ドングリ美味し～", 112701, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3981, "報酬も入ったことだし\nあいつに何か\n買ってあげようかな", 112701, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3982, "次の休みはあいつと\n一日中だらだら\nしたいな～", 112701, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3983, "あたしは褒められて\n伸びるタイプなんだ\nだから褒めて褒めて", 112701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3984, "お、差し入れ？\n気が利くねぇ\nあんがと", 112701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3985, "新しい依頼？\n今日はもう無理～\n閉店で～す", 112701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3986, "サ、サボってないよ！\nちょっと休憩してた\nだけだから！", 112701, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 3987, "ねえ、一緒に\nサボろうよ？\n働き過ぎはよくないよ", 112701, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 3988, "あんたが近くにいると\n仕事の疲れも\n和らぐんだよね～", 112701, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 3989, "はぁ…もう起きなきゃ\nつらい労働の\nはじまりだ～", 112701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 3990, "うぅ、お昼に\nあんパン食べ過ぎた…\n苦しい…眠い…", 112701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 3991, "明日は仕事ないし\nガッツリ夜ふかし\nしちゃうよ～", 112701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3993, "猫みたいに\n丸くなっちゃうよ～\nあたしリスだけどね", 112701, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3994, "ふかふか～\nあたしの尻尾と\n互角とはやるね～", 112701, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3995, "あんパンの香りの\nアロマとか\nあればいいのにな～", 112701, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3996, "これは危険な\nあたたかさだね～\n労働意欲が\nなくなるよ～", 112701, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3998, "いっぱい働いたから\nいっぱい食べても\nカロリー０だよ", 112701, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 3999, "あたしだってたまには\n本くらい読むって\n全然似合わないけどね", 112701, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 4001, "あたしの作った\nオリジナルたい焼き…\nあいつ、\n喜んでくれるかにゃ？", 104601, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 4002, "あ、お帰りにゃー♪\nえへへ…キミに\nお帰りって言えるのが\nなんかうれしいにゃ♪", 104601, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4003, "フロンティア\nスピリッツを\nお見せするべ！", 112801, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4004, "この鎧を着ると\n気が引き締まる\n感じがするべ", 112801, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4005, "搾りたての牛乳飲んで\n今日も一日\nがんばるべ！", 112801, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4006, "みんなに牧場の\n乳製品を\n味わってもらいてぇべ", 112801, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4007, "ここはいいとこだべ\nうちの動物たちも\n連れてきてやりてぇな", 112801, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4008, "みんなオラより\n若ぇのに働いてて\n立派だべな", 112801, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4009, "牧場もギルドの\nみんなもオラが\nまとめて面倒見るべ！", 112801, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4010, "さあ、今日も\n忙しくなるべよ\n気合入れてくべ！", 112801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4011, "お、あんちゃん\n丁度いいとこに！\n一緒に街に\n行商しに行くべ！", 112801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4012, "牧場仕事で培った力が\n戦いでも役立つなら\n一石二鳥だべさ！", 112801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4013, "疲れた時は牧場産の\nミルク菓子で\n糖分補給だべ", 112801, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4014, "これ、牧場の\n新製品だべ\n一番最初に味見\nしてほしいべさ", 112801, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4015, "働きづめはよくねぇベ\n膝枕してやっから\nこっち来るべさ", 112801, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 4016, "ふぁあ…\n酪農家の朝は\n早ぇんだべ…", 112801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 4017, "ごはんの時間だべ！\n腹が減っては酪農も\n戦いもできねえべ！", 112801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 4018, "そろそろ寝るべ！\n健全なお笑いは\n健全な肉体に宿るべ！", 112801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4020, "牧草みたいに\nふっかふかだべ～", 112801, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4021, "この子、目の感じが\nエリザベスに似てるべ", 112801, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4022, "いい匂いだべ～\n疲れた体に\nしみるべな～", 112801, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4023, "あったけぇな～\n牧場にも\n置いてみっかな～", 112801, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4025, "うまいべ！\n生産者のこだわりを\n感じるべさ", 112801, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4026, "酪農も戦いも\nまずは知識だべ！", 112801, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4028, "ぴょん、ぴょん♪\nこの服のおかげで、\n体が軽いなぁ…♪", 113001, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4029, "今の内に部屋の\n掃除をしておこっと…\nみんな喜んで\nくれるといいなぁ", 113001, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4030, "耳と尻尾…この服、\n本当にウサギさん\nみたいだなぁ", 113001, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4031, "なんだか先輩の\n気配がするような…\n気のせいかな？", 113001, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4032, "ウサギさんの真似を\nしたら、存在感が薄い\nとか言われないかな？\nア、アユミだぴょん♪", 113001, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4033, "えへへ…\nいつもとは違う先輩が\nたくさん見られて、\nうれしいな♪", 113001, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4034, "本の中の世界で先輩と\n旅をするなんて…\n幸せすぎて、罰が\n当たりそうだよぉ…", 113001, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4035, "じ～…はっ！？\nす、すみません！\n話しかけてもらえる\nなんて思ってなくて！", 113001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4036, "先輩がすぐそばに…\nし、幸せすぎて\n倒れそうですぅ！", 113001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4037, "え、えっと…あの…！\nうう…いざ先輩と\n向き合うと\n緊張しちゃうよぉ…", 113001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4038, "はうっ！？\nしょ、正面から見る\n先輩は、私にとって\n神々しすぎますぅ…！", 113001, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4039, "先輩…その…\nい、一緒にご飯でも\nどうでひゅか！\nうう…またかんだぁ…", 113001, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4040, "せ、先輩…\nハーブティーを\n入れたんですが、\n一緒にどうですか…？", 113001, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 4041, "せ、先輩！\nおはようございます！\nえへへ、練習通りに\nバッチリ言えたぁ♪", 113001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 4042, "あ、もうお昼ですね\n時計を持ってて\n良かったです", 113001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 4043, "もうこんな時間…\nお休みする\n準備をしちゃいますね", 113001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4045, "あ…ちょっとだけ\n先輩の匂いがする…\nえへへ、幸せだなぁ♪", 113001, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4046, "…私もウサギさんの\nぬいぐるみとして、\n先輩のそばに…はっ！\n私ったら何を…！", 113001, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4047, "あ、いい香り…\n先輩もこういうのが\n好きなのかな？", 113001, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4048, "先輩が隣に\n居たら、もっと\n暖かいのになぁ…♪", 113001, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4050, "おいしい…でも、\n先輩と一緒に\n食べたら、もっと\nおいしいだろうな…", 113001, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4051, "ウサギさんって、穴を\n掘ったりするんだ…\n私も、隠れたいときに\n掘ってみようかな？", 113001, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4053, "ネビアさまのように\n普段からパタパタと\n飛んでみるのも…", 180501, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4054, "ご立派になられた\n主さまと並べるように\nもっと精進しなくては", 180501, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4055, "月々の食費が\n増えてしまいました\n申し訳ございません…", 180501, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4056, "ワンワンのほうの\n主さまは、この姿に\n驚くでしょうか…？", 180501, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4057, "主さまと離れていても\n主さまとの絆は\n胸に感じております…", 180501, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4058, "このギルドハウスは\nみなさまの帰る場所…\nわたくしが守ります", 180501, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4059, "どんな未来が\n待ち受けようとも\n主さまと添い遂げます", 180501, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4060, "おでかけですか？\nではわたくしも\nお供いたします", 180501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4061, "もう二度と主さまと\n離れたくはありません\nいつ何時も…", 180501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4062, "なにかお困りごと\nでございますか？\nわたくしに何なりと…", 180501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4063, "どうぞゆっくりと\n心ゆくまでくつろいで\nくださいまし、主さま", 180501, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4064, "この羽で主さまを\nどこまでも遠く…\n高くへと導きます", 180501, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4065, "主さま、主さま…♪\nおそばにいられるのが\nうれしくて、つい…", 180501, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 4066, "おはようございます\n今日も一日、主さまの\nサポートをいたします", 180501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 4067, "今日も頑張っている\n主さまに、スタンプを\nもう一つ…ポン！", 180501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 4068, "寝付けないのでしたら\nわたくしが子守唄を\n歌ってさしあげます", 180501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4070, "ふかふか…\nまるで主さまのような\n柔らかさでございます", 180501, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4071, "あなたも一緒に\n主さまと遊びますか？\n至福の時間ですよ♪", 180501, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4072, "主さまの優しくて\nくすぐったい匂いが\nたまらなく恋しいです", 180501, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4073, "主さまの心と\n繋がったときのような\n温もりを感じます", 180501, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4075, "いけませんっ…！\n元の姿に戻ると、また\n食べてしまうのに…！", 180501, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4076, "主さまに読み聞かせる\n絵本は…妖精が主役の\n物語にいたしましょう", 180501, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 4078, "…彼のこと弟みたいに\n思ってきたのに…私、\nどうしたらいいの…？", 104801, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 4079, "よ、予定がなかったら\nでいいんだけど…\nその、ふ、二人で\n訓練とか…どうかな？", 104801, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4080, "おめかしもしたし\nあとはお兄ちゃんを\n待つだけですね～♪", 112901, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4081, "お兄ちゃんの身の回り\nをお片付け～、これぞ\nデキる妹アピール！", 112901, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4082, "あ、衣装がほつれてる\nこれくらいなら自分で\n繕えますね…！", 112901, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4083, "この格好で歩いてると\n見慣れた景色も\n違うものに見えますね", 112901, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4084, "勉強もちゃんとします\n…でも、一人だと\n集中力がもちません～", 112901, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4085, "お兄ちゃんを大好きな\n妹が待ってますよ～\n…まだですかね～", 112901, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4086, "お兄ちゃんのために\n掃除洗濯をする私って\nお嫁さんみたい…\n…えへへ～", 112901, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4087, "おに～いちゃん！\n縫い物、一緒に\nやりましょうよ！", 112901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4088, "かわいい妹を\n愛でたくなりました？\n思う存分、どうぞ♪", 112901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4089, "わっ、ビックリした～\nもう、お兄ちゃんてば\nイタズラっ子ですね～", 112901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4090, "一緒にお買い物\n行きませんか？\n妹とデートですよっ♪", 112901, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4091, "お兄ちゃん…頭を\n撫でてくれませんか？\nえへへ、これが一番♪", 112901, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4092, "お兄ちゃん大好き♪\n何度でも言いますよ！\nお兄ちゃん大好きっ♪", 112901, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 4093, "おはよ～お兄ちゃん！\n妹モーニングコールの\n時間ですよっ♪", 112901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 4094, "そろそろ休憩ですね！\n甘いお菓子で\n元気回復しましょ～！", 112901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 4095, "夜更かし厳禁！\nでも、もう少しだけ\nお話ししたいなぁ～", 112901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4097, "ふっかふかですよ！\nお兄ちゃんと一緒に\n座りたいなぁ…♪", 112901, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4098, "このぬいぐるみの\nかわいさ…う～ん\n負けられません…！", 112901, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4099, "香りにも気をつかって\nパーフェクトな妹に\nなっちゃいますから！", 112901, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4100, "はぁ～ぬくぬくです～\nお兄ちゃんと一緒に\n温まりたいなぁ…♪", 112901, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4102, "これは止まりませんね\nもぐもぐ…いくらでも\n食べられちゃいます", 112901, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4103, "これ、デザインの\n参考になりそう！\n私にも作れるかなぁ", 112901, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4105, "退屈です…\nなにか楽しいことは\nないですか？", 106601, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4106, "ギャングの仕事は\n今日はお休みです\nのんびりするですよ", 106601, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4107, "ひうっ！　あたしの\n尻尾なんて食べても\nおいしくないですよ？", 106601, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4108, "ふう…どうやら\n例の件はまだボスに\nバレてないですね…", 106601, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4109, "下克上…いい言葉です\nって、ボスに聞かれて\nないですよね…？", 106601, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4110, "いつか絶対ビッグに\nなってやるです！\nあたしを信じるです！", 106601, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4111, "あたしが覚醒したら\nきっと誰よりも\n強くなるはずです！", 106601, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4112, "なんですか？\n貢ぎ物なら\nいつでも歓迎ですよ！", 106601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4113, "あたしだって\nやる時はやるですよ！\nその時がこないだけ！", 106601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4114, "しりとり勝負ですか？\nいい度胸ですね、\nかかってくるです！", 106601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4115, "お兄さんは\nヒマそうですね…\nうらやましいです", 106601, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4116, "かまってほしいです？\nまったく…いいですよ\nしょうがないですね", 106601, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4117, "お兄さんのことは\nこのあたしが\n守ってやるですよ！", 106601, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 4118, "デキるギャングの\n朝は早いのです…\nふぁあ…", 106601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 4119, "お腹がすいたですね\nなにか食べにいくです\nお兄さんのおごりで！", 106601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 4120, "ふぁあ…むにゅう…\nまだ起きてるですよぉ", 106601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4122, "はぁ～…\nもうここから一歩も\n動きたくないです", 106601, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4123, "なかなかいいモフりを\nしてやがるです…\n舎弟にしてやるですよ", 106601, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4124, "なんかおしゃれな\n匂いがするですね…", 106601, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4125, "ふふーん！\nあたしのブレスの方が\n火力は上ですね！", 106601, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4127, "あたしに貢ぎ物とは\nよく分かってるですね\n食べてやるですよ", 106601, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4128, "ふーむ…\nもっと楽しい本は\nないですかね？", 106601, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4130, "海に眠る秘宝…\n心躍る響きだ！\n早く見つけたい…！", 113201, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4131, "うう～、暑いぃ…\nかき氷を食べて\nすっきりしたいぃ…", 113201, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4132, "アターック！\nいや、もっとこう…\nアタァーック！", 113201, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4133, "スイカ割りで\n目を回さない\nコツはなんだろうか…", 113201, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4134, "せっかく海に来たのだ\n泳ぎも上手に\nなりたいものだな", 113201, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4135, "ふっふっふ…\n疼く…疼くぞ！\n日焼けの痕が…", 113201, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4136, "シグルドと私は\n来世でも共にある…\nつまり我らはずっと…\nはわわ…！", 113201, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4137, "シグルド、私と共に\n日光浴に行かないか？\n光の力を集めよう！", 113201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4138, "この格好だからできる\n必殺技を考えている\n何か案はないか？", 113201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4139, "シグルド～！\nスイカを貰ったから\n一緒に食べよう！", 113201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4140, "こう日差しが強いと\n日焼けしそうだな\n跡になってないか…？", 113201, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4141, "我らの夏の黙示録\nしっかりと記録\nしておかねばな", 113201, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4142, "ちゃんと日焼けどめを\n塗ってもらうべき\nだったかな…？\nシ、シグルドに…！", 113201, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 4143, "朝だぞシグルド！\nさぁ、日光を浴びて\n光の加護を得るのだ！", 113201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 4144, "もう昼食の時間か…\n私の魔力も減ったし\n早く補給しなければ", 113201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 4145, "夜でも我らの活動は\n終わることはない！\nさぁ準備を…ふわぁ…", 113201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4147, "海で遊んでいると\n疲れてしまうからな\n少し休まるとしよう", 113201, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4148, "えへへ～、君の名前は\n何にしまちょうかね♪\n…はっ、見たのか！？", 113201, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4149, "心地よい香りで\n我が精神も安らぐ…", 113201, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4150, "別に寒くなどないぞ！\n寒くないが、ちょっと\n暖まりたくなってな！", 113201, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4152, "もぐ…食べ過ぎた分は\n魔力に変換されるのだ\n…たぶん", 113201, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4153, "…なな、なんだ！？\nシグルドはこういう\n趣味なのか…！？", 113201, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4155, "むむ、この乗り心地…\nいざゆかん、\nシードラゴンよ！", 113201, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4156, "疾風の冥姫の名に\nふさわしき\n深海の玉座よ…\nふああ…柔らかい～", 113201, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4157, "この姿でいると\nわたしもお姫様に\nなれた気がする…♪", 180201, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4158, "お腹空いてきちゃった\nまずは買い出しに…\n…変身解かなくちゃ！", 180201, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4159, "胸のところが空いてて\nスースーするの\nまだ慣れないな…", 180201, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4160, "治癒魔法に使ってた\n魔力を攻撃魔法に…\n頭の中でも練習練習", 180201, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4161, "何もできなかった\nわたしから、少しは\n変われたのかな…？", 180201, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4162, "騎士クン…\n今どこにいるの…？\n…会いたいな", 180201, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4163, "離れてても分かるよ…\n温かい力で騎士クンに\n守られてるの", 180201, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4164, "わたしが戦えてるのは\n騎士クンのおかげだよ\nいつもありがとう！", 180201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4165, "お腹空いてそう…？\n…内緒で作ったおやつ\n騎士クンも食べる？", 180201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4166, "ひゃうっ！　え！？\nせ、背中はダメ～！\n言ってから触って～！", 180201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4167, "この格好、どうかな？\nえ？　似合ってる…？\n本当？　…うれしい！", 180201, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4168, "かわいい？　そうだね\nひらひらが…わたし？\nええっ、そ、そんな！", 180201, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4169, "騎士クン、これからも\nずっと…わたしの\nそばにいてください", 180201, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 4170, "朝からこんなに\n食べる日が来るなんて\n思ってなかったよ…", 180201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 4171, "お昼を作らなくちゃ\n騎士クン、一緒に\nお料理してみる？", 180201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 4172, "騎士クン、もう眠い？\n眠るまで、わたしが\nずっとそばにいるね", 180201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4174, "攻撃参加するからこそ\n適度に体を休めるのは\n大切だよね！", 180201, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4175, "かわいい…騎士クンと\nシェフィちゃんにも\nプレゼントしたいな", 180201, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4176, "いい香り～…\nあれ？　お腹が空いて\nきちゃった…！？", 180201, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4177, "温かい…\n騎士クンがくれる\n力みたい…", 180201, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4179, "ぱくっ、\nあむ、はむっ…\nううぅ～…やっぱり\n手が止まらないよぉ～", 180201, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4180, "新しく覚えたいな…\nみんなで食べられる\n大皿向けの料理♪", 180201, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 4182, "お姉ちゃんの愛\nこのままじゃ\nあふれちゃうよ…♪", 104901, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 4183, "弟くん♪\n弟くんっ♪\nおっとうっとくんっ♪", 104901, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4184, "泳ぐのもいいけど\n今日は砂浜で\nのんびりしようかね", 113101, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4185, "さてと\nちょっくらひと泳ぎ\n行ってくるかね", 113101, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4186, "あっついねぇ…\nうちわがないと\nしんどいったらないよ", 113101, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4187, "冷たすぎる飲み物で\n体を冷やさないよう\n気をつけないと…", 113101, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4188, "夏はあっという間だし\n悔いがないように\n楽しまないとね", 113101, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4189, "きっとこの夏は\nアタシにとって大事な\n想い出になるね…", 113101, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4190, "…前に比べたら\nアタシたちの距離も\n近づいたみたいだね", 113101, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4191, "もし暇なら\n今日も笹舟釣り\n一緒にどうだい？", 113101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4192, "お前さん、釣りに\n興味が湧いたなら今度\n道具を見に行こうよ", 113101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4193, "今から砂浜に行くんだ\nお前さんも一緒に\n散歩するかい？", 113101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4194, "え、日焼け跡？\nおや…本当だ\nすぐ焼けちまうねえ", 113101, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4195, "ひゃっ！？\nちょっと！\n日焼け跡をいきなり\n触らないでおくれ！", 113101, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4196, "お前さんは、その…\nアタシと一緒にいて\n楽しかったかい？", 113101, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 4197, "おはようさん\n今日も砂浜に行って\nゆっくりしようか", 113101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 4198, "お昼か…\nそうだ、よかったら\n刺身定食、作ろうか？", 113101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 4199, "夜の空を見てるとさ\nなつかしい気持ちに\nなるんだよね", 113101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4201, "適度に休憩して\n夏バテしないよう\n気をつけないとね", 113101, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4202, "あんたたちも\n海を楽しみたいって\n思っているのかね", 113101, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4203, "ん～…香りでも\nバカンスらしさを\n楽しめるもんだね", 113101, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4204, "こういう格好だと\n少し冷えちまうんだよ", 113101, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4206, "もぐもぐもぐ…\n遊んだ分、お腹が\n空いちまってねぇ", 113101, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4207, "…ん、なんだいこの本\n催眠…うーん読めない\nナナカに聞いてみるか", 113101, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4209, "へえ…\nなかなかいい\n乗り心地じゃないか", 113101, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4210, "そうそうハマグリって\n竿でも釣れるんだよ\n知ってたかい？", 113101, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4211, "海を満喫しなきゃね\nちょっとひと泳ぎ\n行ってこようかな？", 113301, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4212, "う～ん…♪\nいつもと違った\n開放感～\nなんちゃって！", 113301, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4213, "今のナナカちゃんは\nたぶん水属性！", 113301, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4214, "水着もバッチリ！\nサイカワサイツヨ\nナナカちゃ～ん☆", 113301, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4215, "んっふっふ\nドキ！　水着だらけの\nギルドハウス！\nとかどうかな？", 113301, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4216, "そんなに見られると\nちょ～っと\n恥ずかしいかも…\nなんてねなんてね！", 113301, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4217, "ちょっとくらい\nドキッとしてくれても\nいいのにな～、もう…", 113301, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4218, "水着魔法少女の\n相方がほしいなぁ～…\nね、君やってみない？", 113301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4219, "いつもの格好と同じ？\nも～、わかってない！\nセクスィー度が違う！", 113301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4220, "どうかなどうかな？\nこのひらひらのとこが\nかわいーかなー\nとか思うんだけど！", 113301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4221, "せっかくだから\nギャルゲーイベント\nごっことか\nやってみる？", 113301, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4222, "ナナカちゃんの\n好感度を上げたい？\nならおでかけだー！", 113301, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4223, "おうちで水着って\nなんだかコスプレ\nみたいで楽しいかも♪", 113301, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 4224, "おっはよーう！\n今日はバカンス日和！\n海へゴーですぞ～♪", 113301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 4225, "お昼ご飯どうしよ～…\nあ、ナナカちゃんと\nどっか食べに行く？", 113301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 4226, "夜は本を読むのが\n捗りまくりですな～！\n今日はなに読もっかな", 113301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4228, "次のイベントもあるし\nちょっと休んで体力を\n回復しなきゃね～", 113301, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4229, "こういうマスコット\n私にもいたらなぁ\nいっそ作っちゃう…？", 113301, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4230, "水着魔法少女に\n似合う香りってのも\n考えてみよっかな♪", 113301, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4231, "夜になると急に冷えて\n肌寒いんだよね～…", 113301, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4233, "あんまり食べると\nお腹が出て…でも…\nうう、止まらない～！", 113301, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4234, "この催眠魔法の本\n誰にもばれないように\n保管しとかなきゃね…", 113301, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4236, "ゴーゴーゴーゴーゴー\nナナカちゃーん♪", 113301, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4237, "ナナカちゃんの\n誕生～\nなんちゃって", 113301, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4238, "ファイトファイト☆\n一生懸命\n応援するね！", 113401, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4239, "みんながんばってる！\n私も応援でみんなを\n盛り上げなきゃ！", 113401, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4240, "もし元気のない人が\nいたら…応援で少し\nでも元気にしたいな！", 113401, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4241, "がんばれ～☆\nよし、発声練習OK！\n応援は、声も大事！", 113401, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4242, "せっかく水着なんだし\n泳いだり潜ったりして\n遊びたいな～！", 113401, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4243, "チアリーダーとして\nキミを精一杯\n応援したいな…！", 113401, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4244, "キミへの応援\nちゃんと\n届いてるかな…", 113401, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4245, "えへへ…今ね、\nキミへの応援歌を\n考えてたのだ～♪", 113401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4246, "どうしたの？\n元気がないなら\n私が応援するよ☆", 113401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4247, "がんばったあとは休憩\nしなきゃね！　私と\n一緒にお昼寝する？", 113401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4248, "シオリンとおそろいの\n水着で応援…\nしてみたいなぁ～", 113401, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4249, "次はなにして遊ぶ？\nキミがしたいことを\n教えてよ！", 113401, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4250, "キミを応援\nすることができて…\n私、すごく幸せ…", 113401, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 4251, "キミって早起き\nなんだね！　私も\n見習わなきゃ！", 113401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 4252, "ちょっとお疲れかな？\nそれじゃあ\n応援してあげるね♪", 113401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 4253, "遅い時間まで\nがんばってえらいね！\nでも無理はダメだよ♪", 113401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4255, "ちょっとだけ休憩…\n…あう、座り心地が\n良すぎて…眠気が…", 113401, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4256, "ここにいるみんなを\n私の応援でもっと\n元気にするのだ～！", 113401, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4257, "元気の出る香り～♪\nこれもうまく応援に\n活かせないかな～？", 113401, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4258, "ぽかぽかあったまって\n元気をチャージ\nするのだ～☆", 113401, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4260, "いっぱい食べて、\nみんなを応援する\nパワーをつけなきゃ！", 113401, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4261, "みんなを元気にする\n方法は…どこかに\n書いてないかなぁ…？", 113401, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4263, "わぁ、この子\n怪獣さんみたいで\nかわいいね～♪", 113401, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4264, "水着でこれに\n乗ってると海にいる\n気分になれるね！", 113401, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4265, "すごく忙しいけど\nとても充実した気分\nこれが青春なのかしら", 113501, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4266, "森の静かな雰囲気も\n海の開放的な空気も\nどちらも素敵ね～", 113501, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4267, "汚れたお洗濯物は\nみんなが頑張った証\nなんだか愛おしいわ", 113501, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4268, "みんなを見てると\n何だか私も\n走りたくなっちゃうわ", 113501, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4269, "せっかくの水着だし\n泳ぐ練習でも\nしてみようかしら", 113501, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4270, "みんな～ファイト～！\n応援するのも大事よね\n練習しておかなきゃ♪", 113501, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4271, "先生に教えられること\nまだまだい～っぱいね\n次は何がいいかしら♪", 113501, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4272, "今日も暑くなりそうね\nこまめな水分補給を\n忘れないでね", 113501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4273, "手伝ってくれるの？\nありがとう♪\n先生、頼りにしてるわ", 113501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4274, "休憩はこまめに\nとりましょうね\n無理は厳禁よ", 113501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4275, "海の遊びって\nどんなのがあるの？\n先生に教えてね", 113501, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4276, "水着は初めてだけど\nどう？　変じゃない？\nふふ、ありがとう", 113501, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4277, "あなたと過ごした\nこの夏は、きっと…\n忘れられない思い出ね", 113501, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 4278, "おはよう～\n朝練前にちゃんと\nごはん食べましょうね", 113501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 4279, "お弁当作ってきたの\n栄養いっぱい摂って\n午後も頑張ろうね", 113501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 4280, "もうお休みしなきゃね\n先生が子守唄を歌って\nあげましょうか？", 113501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4282, "ふふ、いい気持ちねぇ\n先生、少しだけ\n休憩しちゃおうかしら", 113501, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4283, "あらかわいい♪\nチームにもこういう\nマスコットが欲しいわ", 113501, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4284, "あら、海の香り？\n波の音まで聞こえて\nきちゃいそうだわ", 113501, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4285, "うふふ\nこれなら濡れた水着を\n乾かすのも簡単ね♪", 113501, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4287, "これ、美味しいわね♪\nでも、食べ過ぎは\n禁物よ", 113501, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4288, "先生だって日々勉強よ\n新しいことを知るのは\nとても楽しいわ♪", 113501, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4290, "あらあら、楽しいわね\nみんなもきっと\nおおはしゃぎね", 113501, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4291, "ふふ、これで\n夏らしい気分を\n満喫するわよ♪", 113501, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 4292, "サンドイッチも\n作ったし、\nお兄ちゃんのお出迎え\n準備は完璧だね♪", 102301, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 4293, "えへへ、お兄ちゃんと\nぷうきちが居てくれる\nだけで、なんだか\nうれしくなっちゃう♪", 102301, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4294, "少し赤くなっているな\n日焼けなんてしたのは\nいつ以来だろう", 113601, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4295, "この格好で\n過ごすのは\n少し勇気がいるな…", 113601, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4296, "暑いのは平気だが\n肌が外気に触れるの\nにはまだ慣れないな…", 113601, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4297, "この格好でも\n堂々としていれば\n恥ずかしくはない…\nのかな？", 113601, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4298, "おっと…鎧姿の時と\n重心が違うせいか\nちょっと不安定だな", 113601, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4299, "この水着…\n私に似合って\nいるのだろうか…？\n少年には聞きづらいな", 113601, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4300, "少年との\n海のバカンス…\nまた行きたい\nものだな", 113601, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4301, "あまり見るのは\nよしてくれ…\n恥ずかしいじゃ\nないか…", 113601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4302, "どうかしたのかい？\n私でよければ\n話し相手くらいには\nなろうじゃないか", 113601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4303, "ひゃっ！\nす、すまない\nちょっと過敏に\nなっているようだ", 113601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4304, "…っくしゅん！\nすまない\n薄着には慣れて\nないものでね", 113601, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4305, "まあ、少年になら\n見られても…\nい、いや！\nなんでもない！", 113601, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4306, "私がこうして\nゆっくりできるのは\n少年がそばにいて\nくれるからだよ", 113601, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 4307, "この時間なら\nビーチにも人は\n少ないだろうね…\n遊びに行くかい？", 113601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 4308, "昼食のお誘いかい？\nいいよ、付き合おう", 113601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 4309, "もう夜か…\n一日が経つのは\nはやいものだね", 113601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4311, "これは楽でいいな…\nいつもは鎧姿で\n家具を傷つけないか\n気を使っているからね", 113601, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4312, "うん…ふわふわの\n感触が\nよくわかるな…", 113601, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4313, "いつもは\n鎧に匂いが\n移ってしまうのが\n悩みなんだよ…", 113601, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4314, "うーむ…\n涼しいのに暖かい\nなんだか奇妙な気分だ", 113601, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4316, "兜を気にせず\n食べられるのは\n楽でいいな…", 113601, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4317, "ふむ…鎧がないと\nページがめくりやすい", 113601, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4319, "身軽でいいな…\n鎧を着ていると\n騎乗訓練の時\n馬に申し訳がなくてね", 113601, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4320, "貝をうまく使えば\n水中でも\n守りを固められそうだ", 113601, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4321, "どこをアジトと\nつなげようかな～…\nこのへんにしよっと♪", 106801, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4322, "ここってちょっと\n変わった建物だね\n妖精がいるでしょ？", 106801, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4323, "色んな人を観察できて\n眼福眼福…♪", 106801, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4324, "家具の配置変更なら\nアタシにおまかせあれ\n…家の形は変えない？", 106801, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4325, "新メニューどうしよう\n思いついた人がいたら\nアタシに教えてね！", 106801, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4326, "少年、早く帰って\nこないかな…\nよし、迎えに行こう！", 106801, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4327, "結局またあちこち\n移動する日々だし…\n少年に会いたいな～", 106801, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4328, "どしたの少年？\nお姉さんに何でも\n相談してみな～？", 106801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4329, "あれをああして…\nおや少年！　ごめんね\nすぐに気づけなくて！", 106801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4330, "何？　クリスが\nアタシを探してる？\n…いないって言って！", 106801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4331, "よければリノちゃんと\nシズルちゃんにも\n顔を見せてあげて", 106801, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4332, "少年～！\nもうヘトヘトだよ～！\nお願い癒して～！", 106801, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4333, "…アタシが素直に\n助けてって言えるのは\n少年だけだよ", 106801, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 4334, "朝だよ少年！\n爽やかな朝の\nクレープはいかが？", 106801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 4335, "お腹空いたな～\nお昼のクレープ\n少年は食べる？", 106801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 4336, "夜までクレープは\nやめとけ？　ごめん\nそれだけは無理…！", 106801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4338, "アタシこんなに\nゆっくりしちゃって\nいいんだっけ～？", 106801, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4339, "…これ\nネネカが変身\nしてないよね？", 106801, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4340, "いい香りだ…\n解析完了\n今度生成しようっと♪", 106801, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4341, "安心するな～…\nアジトに置けないから\n羨ましいよ", 106801, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4343, "食べ物を消化するって\n仕組み、どんなものか\n気になる…", 106801, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4344, "速読できるけど\n本を読む時くらい\n文字にじっくり\n浸かりたいんだよね…", 106801, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4346, "エンジェルとして\nみんなを元気に…！\nアカリ、頑張ります♪", 113701, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4347, "お姉ちゃんと一緒に\nお兄ちゃんを…えへへ\nまだかな、お兄ちゃん", 113701, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4348, "エンジェルの音色を\n目指して、今日も\nサックスの練習です♪", 113701, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4349, "お兄ちゃんとの特訓も\n頑張らないと…\n厳しくしてくださいね", 113701, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4350, "かわいいエンジェルの\n格好、お兄ちゃんなら\n喜んでくれますよね♪", 113701, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4351, "エンジェルの力で\nお兄ちゃんのハートも\nイチコロですよ♪", 113701, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4352, "アカリが一番\n元気にしたいのは\nお兄ちゃんですよ♪", 113701, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4353, "お兄ちゃん？\n触りたいなら…\nこことかどうですか♪", 113701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4354, "特訓の時間ですか？\nアカリ、楽しみに\nしてたんですよ♪", 113701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4355, "あ、お兄ちゃん！\nそろそろアカリと\n休憩…しませんか♪", 113701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4356, "この羽、触り心地\nいいですか？　あんっ\nくすぐったいですよぉ", 113701, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4357, "お兄ちゃんも元気に\nなりたいんですか？\nアカリにお任せです♪", 113701, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4358, "やんっ♪\nお兄ちゃんったら\nダイタンですね♪", 113701, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 4359, "お兄ちゃん、朝ですよ\nすっごく気持ちのいい\nお天気ですよ…♪", 113701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 4360, "お昼寝しませんか\nお兄ちゃん？\nアカリの膝枕で…♪", 113701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 4361, "夜ですね、お兄ちゃん\nアカリ、なんだか胸が\nドキドキしちゃいます", 113701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4363, "フカフカですね…\n隣にお兄ちゃんが\nいてくれたらなぁ…♪", 113701, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4364, "えへへ、アカリも\nこんな風にかわいく\nなりたいな～", 113701, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4365, "お兄ちゃんが好きな\n匂いなのかな？\n参考にしますね！", 113701, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4366, "体が火照ってきて\nなんだかアカリ…\nクラクラしちゃう…", 113701, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4368, "はむ、んぐ…あっ\nほっぺについちゃった\nベトベトですよぉ～", 113701, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4369, "エンジェルって色んな\n物語に出てきますよね\nもっと調べなくちゃ！", 113701, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 4371, "あの方の前では\n常に気高く\nありたいですわね", 103201, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 4372, "どんなに道に迷っても\nあなたさまの元には\n絶対に辿り着く\n自信がありますわ", 103201, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 4373, "あんたと一緒に\nいるときが一番楽しい\n一時かもしれないわ", 102801, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 4374, "いつかあんたと一緒に\nサレンディア救護院を\n切り盛り…なんてね♪", 102801, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4375, "今日も\nエンジェルとして、\nみんなの幸せの\nために頑張らないと", 113801, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4376, "せっかく羽が\nついてるのに\n飛べないのが残念だわ", 113801, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4377, "この格好、子供たちに\n妙にウケがいいわね\nま、悪くないけど", 113801, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4378, "や、やっぱりみんな\n注目してくるわね…\nエンジェルって珍しい\nからしかたないか", 113801, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4379, "エンジェルになると\n人見知りがなおる！\n…わけないか", 113801, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4380, "エンジェルの活動は\n大変だけど、自分が\n成長してるって\n実感がうれしいわね", 113801, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4381, "もし誰かの願いを\nかなえられるなら\n私はアイツの…\nって何考えてるのよ！", 113801, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4382, "何ボサっとしてるの！\nさあ、街に出て\n人助けするわよ！", 113801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4383, "エンジェリック\nアロー！\nなんてね…え、アンタ\nいつから見てたのよ！", 113801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4384, "コラ！\n羽は触らないで！\n壊れたらどうするのよ", 113801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4385, "エンジェルじゃなく\nても、アンタはいつも\n人助けしてるわよね\n…ちょっと尊敬するわ", 113801, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4386, "この服なら他の子より\n私を見てくれるかも…\nって、な、何\n見てるのよ！", 113801, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4387, "ね、ねえ、もし私が\n困ってたら、一番に\n駆けつけて…やっぱり\n何でもないわ！", 113801, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 4388, "おはよう…あ、あの、\nエンジェルとして\n町に行くんだけど…\n一緒に行かない？", 113801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 4389, "ふぅ、いいことした\n後のランチは\nおいしいわね", 113801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 4390, "もうこんな時間…\n夜更かししてないで\n早く寝なさいよね", 113801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4392, "いい座り心地ね～\nまさに天にも昇る\n心地よさだわ～", 113801, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4393, "ふふ、かわいい…\nあ、羽付けたら\nもっとかわいく\nなるんじゃないかな？", 113801, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4394, "はぁ～落ち着くわ～\n回復アイテムみたいで\n癒やされちゃうわ～", 113801, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4395, "わっ、とと…\n危ない、羽が\nこげちゃうところ\nだったわ", 113801, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4397, "人の役に立つのって\nすごく大変だから、\nこれでライフ回復よ！", 113801, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4398, "エンジェルに関する\n本はあるかしら…あ！\nこれ探してた攻略本！", 113801, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4400, "ふっふっふ\n吸血鬼らしく\n驚かせてあげましょう", 113901, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4401, "獲物はどこですか～！\nう～ん、もうちょっと\n迫力が欲しいですね", 113901, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4402, "トリックオアトリート\n…返事がないので\nイタズラしておきます", 113901, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4403, "ハロウィンですし\nたまには騎士さんでも\n誘ってあげますかね", 113901, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4404, "マントは動きに\nメリハリがついて\nいいですね", 113901, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4405, "色んな仮装が見られて\nやっぱりハロウィンは\n最高ですね", 113901, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4406, "吸血鬼らしい行動って\n噛みついたり…いや\nそれは大胆過ぎです！", 113901, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4407, "呼びましたか？\n手短にお願いしますよ\n忙しいんですから！", 113901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4408, "え？　衣装に穴が？\nありがとうございます\n…よく見てるんですね", 113901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4409, "ふわあ…吸血鬼の\n姿でも、眠い時は\n眠いんですよね…", 113901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4410, "ハロウィンってライブ\nとは違った賑やかさが\nあってワクワクします", 113901, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4411, "私を驚かそうとしても\n無駄ですよ！\nすぐ分かるんですから", 113901, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4412, "騎士さん、一緒に\nハロウィンを…いいえ\nなんでもないですっ！", 113901, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 4413, "仕事で寝不足だから\n太陽がまぶしいです…\n私、吸血鬼っぽいかも", 113901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 4414, "お昼ご飯は真っ赤な…\nトマト尽くしのお弁当\nにしてみました", 113901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 4415, "夜はハロウィンが\n盛り上がる時間…\n行きますよ騎士さん！", 113901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4417, "このフィット感\nどうにか服作りにも\n活かせれば…！", 113901, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4418, "よく出来てますね…！\nぬいぐるみの服を作る\nのも楽しそうですね！", 113901, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4419, "いい感じですね…\n服に合わせた香りって\nいうのも、いいかも…", 113901, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4420, "はぁ、温まりますね…\nおっと、マントに火が\n点いちゃいそうです！", 113901, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4422, "あぁ、美味しい～\n食べた分は、働いて\n燃焼しますからね！", 113901, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4423, "伝説の吸血鬼…\n夜を統べる者…\nほうほう…", 113901, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4425, "やっぱりハロウィンと\nいえばお菓子です！\nさぁ配りましょうか！", 113901, 3, 12);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4427, "この格好…\nカッコイイのに\n動きやすくて\n着心地も最高ッス！", 114101, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4428, "キメポーズはこう…\nいやこっちのほうが…\nなやましいッス…！", 114101, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4429, "トリックオア\nジャスティス！\nって言ったら何か\nもらえるッスかね？", 114101, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4430, "新しい必殺技の\n特訓もしなくちゃ\nいけないッスね…\n忙しいッス！", 114101, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4431, "自分のこの姿\nトモね～ちゃんにも\nみせてあげるッス！", 114101, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4432, "ホワイトタイガー\n参上ッス！\n今のポーズは\nキマったッス！", 114101, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4433, "あの人と一緒だと\nほんとにヒーローに\nなれる気がするッス！", 114101, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4434, "どうッスか？\nこの色、自分に\n似合ってるッスかね？", 114101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4435, "ヒマだったら\n必殺技の特訓に\n付き合ってもらって\nいいッスか？", 114101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4436, "ちょうどいいところに\n来たッスね！\nこのキメポーズ\nどうッスか？", 114101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4437, "お！　この格好、\n気になるッスか？\nじっくり見ても\nいいッスよ♪", 114101, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4438, "あんたも一緒に\n新しい技の練習\nどうッスか？", 114101, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4439, "今日もあんたと一緒に\nランドソルの平和を\n守るッスよ！", 114101, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 4440, "朝ッス！\n一緒に早朝特訓\nやるッスよ！", 114101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 4441, "お昼休憩ッス！\nヒーローにも\n休息は必要ッス！", 114101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 4442, "闇夜に映える\n白い衣装で\n平和を守るッスよ！", 114101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4444, "トラも狩りの時以外は\nごろごろしてるって\n本で読んだッスよー", 114101, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4445, "もこもこッスね♪\nこのかわいらしさを\n守ってあげるのも\nヒーローの役目ッス！", 114101, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4446, "いい匂いッスね…\nリラックスして\n瞑想するッス！", 114101, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4447, "燃える炎は\nヒーローの心意気ッス\nいつまでも\n見てられるッスよ", 114101, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4449, "もぐもぐ…\nいっぱい食べて\n強いヒーローに\nなるッスよ！", 114101, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4450, "ちゃんと勉強の本も\n読んでるッスよ…！\nヒーローには賢さも\n必要らしいッス！", 114101, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4452, "わわっ、お菓子が\nいっぱいッス！\nみんなで\n食べるッスよ！", 114101, 3, 12);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 4454, "ワタシがチューシンと\nなった今、天下統一は\n目の前デス！", 103001, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 4455, "もうショーグンってば\nそれはセイシツに\nすることデ～ス…", 103001, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4456, "女幽霊…うらめしや…\nうーん、これは\nちょっと違う気が\nするな…", 114001, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4457, "何だか寒気がする…\nま、まさか\nこの部屋にも幽霊が？", 114001, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4458, "ハロウィンは楽しいね\n童心に帰ったような\n気分だよ", 114001, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4459, "今度のハロウィンは\nユイとヒヨリを\n誘ってみようかな", 114001, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4460, "ハロウィンのお菓子は\nどれもかわいいね\n…一つくらいなら\nいいかな？", 114001, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4461, "次は彼と\nどんなハロウィンを\n過ごそうかな…", 114001, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4462, "こんな賑やかさも\nいいものだね\n彼といられるのなら\nなおさら…", 114001, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4463, "…いたずらしようと\nしてない、キミ？\nふふ、お見通しだよ", 114001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4464, "え、私にもお菓子を？\nありがとう…ふふ\nうれしいものだね\nハッピーハロウィン！", 114001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4465, "どうしたの？\n出かけるなら\n私も同行しようか", 114001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4466, "この格好、舞踏会にも\n出られそうだね\n一曲、踊ってみる？", 114001, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4467, "やるからには本気で\nやらせてもらおう\nトリックオア\nトリート！", 114001, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4468, "他の子に目移りする\nのも分かるが、その…\nせっかくの特別な衣装\n見てほしいな", 114001, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 4469, "寝起きの体を\n慣らしておこうか\nほら、剣を取って", 114001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 4470, "お菓子もいいけど\nちゃんと昼食も\nとるんだよ", 114001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 4471, "さあ、行こうか！\nハロウィンは\nこれからが本番だよ", 114001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4473, "ハロウィンはまだ\nこれからだ\n一休みしようか", 114001, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4474, "キミたちも\n仮装に興味は\nあるかな？　…ふふ", 114001, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4475, "いい香りだけど\n幽霊が集まったり\nしないだろうか…？", 114001, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4476, "暖かい…一息ついたら\nハロウィンの\nパレードを\n見に行こうかな", 114001, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4478, "ハロウィンを本気で\n楽しむなら\n食事もしっかり\nとっておかないとね", 114001, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4479, "ハロウィンの\nホラー小説か…\nけ、結構\n怖いものだね…？", 114001, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4481, "お菓子がいっぱいだ…\nこういうのって\n見ているだけで心が\n躍ってしまうね", 114001, 3, 12);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4483, "こんなにかわいい\n武器で戦えるとは…\nすごいものだな\n魔法少女は", 114201, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4484, "決めポーズの訓練も\nしっかりやっておくか\n…誰も見ていないな？", 114201, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4485, "こういう格好は\nユキあたりがすごく\n似合いそうだな", 114201, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4486, "子どもたちにサインを\n求められてしまった…\nこれもまた私の使命！", 114201, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4487, "この格好は目立つな…\nいや、提督とは人々の\n視線を集めるものだ！", 114201, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4488, "あいつと作戦会議を\nしておくか。あれで\nなかなか頼れるからな", 114201, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4489, "あいつには私の勇姿を\nちゃんと見ていて\nほしいものだな", 114201, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4490, "一仕事終えたことだし\n一緒に甘いものでも\n食べに行くか？", 114201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4491, "怪しげな気配がするな\n貴公も警戒を\n怠るなよ！", 114201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4492, "シオリに魔法少女の\n本を借りたんだ\n一緒に読まないか？", 114201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4493, "巡回の時間まで\nお茶にでもするか\n貴公も付き合うがいい", 114201, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4494, "貴公がサポートして\nくれるのなら心強いな\n頼りにしているぞ", 114201, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4495, "頭を撫でるな～！\n…まったく、貴公だけ\nだぞ、こんな風に私を\n撫でてくるのは…", 114201, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 4496, "起きろ貴公！\n魔法少女の朝は\n早いぞ！", 114201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 4497, "ほら、食後の\nデザートだ\nて、適度な糖分は\n体に必要なんだ！", 114201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 4498, "ふわぁ…魔法少女も…\n眠気、には…い、いや\n寝てないぞ！？", 114201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4500, "任務や訓練で\n疲れた体に\nこれは効くなぁ…", 114201, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4501, "うむ、かわいいな\nモーラのやつを隣に\n並べようか", 114201, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4502, "ああ、身も心も\n安らぐ…\nまるで魔法だな…", 114201, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4503, "この温かさ…\n愛と希望に\n通じるものがある…", 114201, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4505, "食事の大切さは\n魔法少女も軍人も\n変わりない\nしっかり食べるんだ！", 114201, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4506, "魔法少女の本を\n読んでるのだが\nわ、私もこんななのか", 114201, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 4508, "オラとあんちゃんは\nボケもツッコミも\nあうんの呼吸だべ♪", 103301, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 4509, "あんちゃんと二人で\nネタ合わせ…初めての\n共同作業だべ♪", 103301, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4510, "ぴっぷる！", 192501, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4511, "ぴっぷるぴっぷる♪", 192501, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4512, "ぴっ！", 192501, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4513, "ぷっぷー", 192501, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4514, "ぴぃ？", 192501, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4515, "ぴっぷるんるん♪", 192501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4516, "ぷぅ！", 192501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4517, "ぴぴっ♪", 192501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4518, "ぷぅー…", 192501, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 4519, "ちゅ！", 192501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 4520, "ぷはー", 192501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 4521, "ぴぇー", 192501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4531, "魔法少女になったら\n髪型も変わるんだね\n色々試してみようかな", 114301, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4532, "…煌く剣技で、\n悪を断つ！\nうん、今日も\n完璧なポーズだね♪", 114301, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4533, "ミクマ流剣術で、\n魔法少女の技に使え\nそうなものは…よし、\n早速試してみよっと♪", 114301, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4534, "魔法少女になったから\nには、サインの練習も\nしないと…\nよし、頑張るぞ！", 114301, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4535, "魔法少女の姿はすごく\nかわいいけど…この\n姿はジュンさんたちに\n見せられないなぁ…", 114301, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4536, "あの人は魔法少女\nのこと好きなのかな？\n…好きだったら\nうれしいな♪", 114301, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4537, "…やっぱり、\nあの人がいないと\n特訓に身が入らない…\n早く帰ってこないかな", 114301, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4538, "もし時間があるなら、\n私と街の見回りに\n行かない？", 114301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4539, "あ、いいところに！\n新しい魔法剣術を\n編み出したから、\n見てくれないかな？", 114301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4540, "魔法少女の姿が気に\nなるのは分かるけど…\nず、ずっと見られる\nのは照れるからやめて", 114301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4541, "変身ポーズ中の動き、\n少し変えた方が\nいいかな…\nあなたはどう思う？", 114301, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4542, "よければ、\n魔法少女の特訓に\n付き合ってくれない？\nその…二人きりでさ…", 114301, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4543, "私…あなただけの\n魔法少女になっても…\nふふっ、な～んてね♪", 114301, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 4544, "今日もいい朝だね\nさあ、一緒に\n街の見回りに行こうか", 114301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 4545, "もう昼か…なにか\n食べたいものある？\n簡単なのでいいなら、\n私が作ってあげるよ", 114301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 4546, "そろそろ休もうか\n…なんなら、添い寝\nしてあげようか？\nふふっ、冗談だよ♪", 114301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4548, "これは…\n心身ともに安らぐね\nなんだか\n眠くなっちゃいそう", 114301, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4549, "か、かわいい…これ、\n魔法で動かせたり\nしないかな？", 114301, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4550, "いい香りだね\nなんだか頭が冴えて\nきたし、新しい技でも\n考えようかな", 114301, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4551, "あったかいな…\nこの温もりは、\n魔法少女の魔法でも\n再現不可能だね…", 114301, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4553, "こ、これは…\nおいしすぎて手が\n止まらないかも", 114301, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4554, "この本の\nキャラの動き、\n変身ポーズの\n参考にできるかも！", 114301, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4556, "メリークリスマス！\nさあ、素敵な\nパーティーを始めると\nいたしましょう！", 114401, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4557, "ふふっ、\nいくつになっても、\nやはりクリスマスは、\nワクワクしますわね♪", 114401, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4558, "自分で料理をしてみて\nシェフたちのすごさが\n改めて分かりましたわ", 114401, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4559, "何事も自分で\n体験してみると\n新しい発見が\nあるものですわね♪", 114401, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4560, "やはり、クリスマスは\n街が活発になり\nますわね…これは、\n絶好の商機ですわ♪", 114401, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4561, "他のクリスマス料理も\n作れるようになったら\nあのお方は食べて\nくださるかしら…？", 114401, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4562, "あのお方に贈る\nプレゼント…\nこれはかなりの\n難題ですわね…", 114401, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4563, "メリークリスマス！\nよろしければ、一緒に\nクリスマスの準備を\nいたしませんか？", 114401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4564, "まあ、クリスマスの\n飾りつけを手伝って\nいただけるのですか？\nありがとうございます", 114401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4565, "クリスマスに合わせて\nあつらえた自慢の衣装\nなのですが…その、\nいかがでしょうか？", 114401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4566, "幼い頃はサンタさんが\nいらっしゃるのを\n待ちわびたものです\n今は…内緒ですわ♪", 114401, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4567, "も、もしお時間が\nあるようでしたら、\nクリスマスの街を\n一緒に歩きませんか？", 114401, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4568, "来年のクリスマスも\nあのお方と一緒に\n迎えられたら…", 114401, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 4569, "時は金なり…\nせっかくの朝ですもの\n時間を有効活用\nいたしましょう！", 114401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 4570, "お昼になりましたわね\nさあ、\nパーティーの準備を\n進めますわよ！", 114401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 4571, "静かな夜ですわね…\nもうちょっとだけ、\nあなたさまとお話を\nしていたい気分です", 114401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4573, "適度な休息は\n成功の秘訣ですわ！", 114401, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4574, "このかわいらしさ…\nいつまでも見て\nいられそうですわ♪", 114401, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4575, "人をもてなすには\nこうしたところにも\n気を配らないと\nいけませんわね", 114401, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4576, "暖かい…\nこういう憩いの\nひと時こそ、大事に\nしたいものですわね", 114401, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4578, "この奥深い味わい…\n何か隠し味など\n使われていまして？", 114401, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4579, "クリスマス料理の\nレシピも、\nこれだけあると\n迷ってしまいますわね", 114401, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4581, "この絵本、とっても\n面白いつくりですわね\nうちの商品に\nしたいぐらいですわ", 114401, 3, 14);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4582, "これだけ大きな\n着ぐるみ…\n職人の技術が\n光りますわね", 114401, 3, 15);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4583, "クリスマスといえば\nパーティーよね！\nいっぱい飲むわよ～♪", 114601, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4584, "私を麦しゅわだけの\n女と思ったら\n大間違いなんだから♪", 114601, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4585, "クリスマスらしい\n飲み物も結構いいわね\n私も今度\n作ってみようかしら", 114601, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4586, "暖かい部屋で飲む\n麦しゅわも\nまた格別なのよね…", 114601, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4587, "今日はオフの日だし\n朝から飲んでも\n怒られないわよね？", 114601, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4588, "クリスマスの\nデートスポットで\n彼と一緒に…ってなに\n考えてるのユカリ！？", 114601, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4589, "背伸びしなくても\nありのままの自分を\n受け入れてくれるって\nすごく…幸せ…", 114601, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4590, "大丈夫よ～\n飲んでない\n飲んでないひゃら～", 114601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4591, "クリスマスは\n特別だもの\n特別な一杯を\n飲まないとね♪", 114601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4592, "メリークリスマース！\nほらほらぁ\n乾杯しましょ♪", 114601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4593, "お姉さんの\n頼れるところ\n見せてあげるわね", 114601, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4594, "何かあったら\n頼ってくれても\nいいんだからね？", 114601, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4595, "ふふ…これからも\nキミのこと…\n頼りにしちゃうからね", 114601, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 4596, "さわやかな朝ね\n…二日酔いがないと\n朝ってこんなに\n気持ちいいのね", 114601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 4597, "昼間から飲むのって\nまた格別よね…って\nガマン、ガマンよ…", 114601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 4598, "もうこんな時間かあ…\nあとは寝るだけだし\nちょっとくらいは…", 114601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4600, "ゴロゴロするのって\n最高ね～\nあとは麦しゅわが\nあればなぁ", 114601, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4601, "私だって…\nかわいいものは\n好きでいいわよね？", 114601, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4602, "んっ～…いい香りね\n日頃の疲れが\n取れる気がするわ", 114601, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4603, "ポカポカする～\n…なんだかのどが\nかわいてきたわね", 114601, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4605, "もぐもぐ…労働の\n後のご飯はやっぱり\nおいしいわぁ…", 114601, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4606, "クリスマスデート\n完全攻略マニュアル？\n…読んでおかなくちゃ", 114601, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4608, "あら、絵が飛び出して\n見えるけど…\n酔っ払ってるせいじゃ\nないわよね…？", 114601, 3, 14);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4609, "いい手触りね～\nウチにもほしいけど\n麦しゅわをこぼしたら\n大変そうだわ…", 114601, 3, 15);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 4610, "ヒデサイの視線\nずっと独り占め\nしてたいな～", 101601, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 4611, "ヒデサイといるだけで\nうち、おに楽しいし！\nヒデサイもっしょ～？", 101601, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4612, "メリークリスマス！\nふふ、口にするだけで\n何だか楽しくなるわね", 114501, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4613, "いつもは子どもたちを\n見守る方だけど\n今ははしゃいでも\nいいわよね？", 114501, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4614, "ツリーや料理\nそしてプレゼント！\nいくつになっても\nやっぱり心躍るわね♪", 114501, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4615, "飾り付けも料理も\nいいセンスしてるわね\n救護院のパーティーの\n参考にさせてもらうわ", 114501, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4616, "焼きそばパンを\nクリスマスの定番に\nするにはどうすれば…", 114501, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4617, "これ、あいつの\n好きそうな料理だわ\n取っといて食べさせて\nあげようかしら♪", 114501, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4618, "あいつのプレゼント\nちょっとだけ奮発\nしたのよね…\n喜んでくれるかしら", 114501, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4619, "このドレス\n似合ってるかしら？\n今だけは見とれても\n許してあげるわ♪", 114501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4620, "見て、このケーキ！\nおいしそうでしょ\nたまにはこんな贅沢も\nいいわよね♪", 114501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4621, "料理も飲み物も\nおいしいわね！\nいっぱい食べて\n元を取らないと♪", 114501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4622, "子どもたちと一緒に\n雪遊び、楽しそうね\nあんたもどう？", 114501, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4623, "せっかくの\nクリスマスなんだもの\nエスコート\nよろしくね♪", 114501, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4624, "寒い？　そう…\nなら、もっとこっち\n来ていいわよ…？", 114501, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 4625, "ほら、早く起きて？\nパーティー用の\n買い出しに\n付き合ってちょうだい", 114501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 4626, "パーティーの料理の\n準備をしながらお昼も\n作っておいたわ\n我ながらいい手際ね", 114501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 4627, "にぎやかなパーティー\nもいいけど、聖夜の\n余韻を感じるのも\n結構素敵ね", 114501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4629, "いい座り心地だわ～\n腕のいい職人の仕事ね\nうちでも扱いたいわ", 114501, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4630, "かわいい！\n子どもたちに\nプレゼントしたら\n喜んでくれるかも", 114501, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4631, "華やかな香りね\nクリスマスに\nピッタリだわ", 114501, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4632, "寒い日にはやっぱり\nこれよね…\nはぁ、極楽極楽♪", 114501, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4634, "んー、おいしいわ！\n焼きそばパン味の\nものも、作れない\nかしら？", 114501, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4635, "たまにはのんびり\n読書でも…なんだか\nラブロマンスばかりね\nクリスマスだから？", 114501, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4637, "これ、面白いわね！\n子どもたちの\nプレゼントに\nピッタリだわ", 114501, 3, 14);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4638, "こんないい毛並み\n初めて見るわ…\n一体どこで手に\n入るのかしら？", 114501, 3, 15);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4639, "アタシもみんなと\n一緒に正月を\n楽しんでいいのか？", 114701, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4640, "今年こそ、もう少し\n身長が伸びます\nように…", 114701, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4641, "げっ！　お年玉が\n全然残ってないぞ！？\nそんな、嘘だろ…？", 114701, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4642, "大勢で過ごす\n正月か…初めてだけど\n悪くないもんだな", 114701, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4643, "んー…ここじゃ\n天楼覇断剣の\n出番はなさそうだな", 114701, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4644, "アイツらと作る\n戦い以外の思い出も\nもっと増やしたいな", 114701, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4645, "こんなに賑やかな\n正月は初めてだ\n…温かくて、いいな", 114701, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4646, "記憶がなくったって\n楽しい思い出は\n新しく作れるよな？", 114701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4647, "早くでかけるぞ！\nアタシはもっと\n正月を楽しみたい！", 114701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4648, "なあ、オマエはもう\nノウェムって名前\n覚えてくれたよな？", 114701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4649, "この服、アタシに\n似合ってるのか？\n…そっか。\nありがとな！", 114701, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4650, "オマエらと見た朝日は\nきっと忘れないぞ！\n思い出ができたな！", 114701, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4651, "いつかオクトーも\n一緒に、みんなで\n正月を過ごせたらな…", 114701, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 4652, "朝から元気だな！\nアタシもいっちょ\n見習ってみるか！", 114701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 4653, "昼飯は何にする？\n正月はうまいものが\n多くて迷うよな！", 114701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 4654, "いい夜だな！\nアタシと一緒に\n夜更かしを楽しむか？", 114701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4656, "正月らしく、たまには\nゆっくりするのも\nいいかもな…", 114701, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4657, "なんだ？\nオマエも初詣に\n行きたいのか？", 114701, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4658, "すーはー…\n神殿のニオイがするな\nいい事ありそうだ！", 114701, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4659, "コイツがあれば\nうまい魚が\n焼けそうだな…", 114701, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4661, "ううー\n食べ過ぎて帯が\nきつくなってきたぞ…", 114701, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4662, "おみくじって\n書いてあることが\nむずかしいんだよな…", 114701, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4664, "困ってる人は\nいないみたいだし…\nちょっとここで、\n特訓しようかな♪", 180101, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4665, "空が飛べるんだから\nもっとたくさんの人を\n助けにいけちゃう！\n…ここじゃ飛べないや", 180101, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4666, "炎の翼\nかっこいいなー！\nこれ、もっと大きく\nできないのかな？", 180101, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4667, "やっぱり変身すると\nお腹すくんだね…\n…今ならペコさんの\n食欲の理由が分かるよ", 180101, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4668, "色なんかは違うけど…\nマントがあるから\n騎士くんとお揃いに\nなったみたい♪", 180101, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4669, "…騎士くんがいないと\nなんか調子でない…\n早く会いたいよぉ…", 180101, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4670, "この姿になると、\n騎士くんを近くに\n感じる…なんだろ\nドキドキしちゃう…", 180101, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4671, "騎士くん騎士くん！\n今大丈夫？\nトレーニングメニュー\n考えるの手伝って！", 180101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4672, "…うにゃっ！？\nこ、この姿になっても\n尻尾は触っちゃ\nダメー！", 180101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4673, "もしかして、この服が\n気になってるの？\nだったら、もっと\n近くで見ていいよ♪", 180101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4674, "あ、騎士くん！\n困ってる人がいないか\n見回りに行くんだけど\nついてきて欲しいな！", 180101, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4675, "耳に触りたいの？\nだったら頭もなでて！\nそしたら、好きなだけ\n触っていいよ♪", 180101, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4676, "…あたし、強くなって\n騎士くんを守るよ！\nだから…これからも\n一緒にいていい？", 180101, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 4677, "おっはよー！\nさあさあ騎士くん！\n今日も一緒に\nはりきっていこー♪", 180101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 4678, "お昼、お昼～♪\n今日のご飯は何かな～\nあ、騎士くん！\n早くお昼食べ行こ！", 180101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 4679, "今日もお疲れ様ー！\n騎士くん、あたしが\nマッサージしよっか？\n結構評判なんだよ！", 180101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4681, "うにゃぁ…\nこの気持ちよさ…\n溶けちゃいそう…\nにゃぁ…", 180101, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4682, "か、かわいい～\nこれ、なでなで\nしていいんだよね…？\nよしよし…♪", 180101, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4683, "とってもいい香り！\n気分も落ち着くし、\n特訓の後なんかに\nいいかもー！", 180101, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4684, "ぬくぬくだぁ…\nなんだか、\nふにゃふにゃに\nなっちゃうよぉ…", 180101, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4686, "お、おいひぃ…！\nこの姿になると\nお腹すくし、すっごく\n助かっちゃった！", 180101, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4687, "この本で勉強して、\nぶんぶりょーどーの\n超ウルトラヒヨリに\nなっちゃうぞー！", 180101, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4689, "今年もみんなで\nおいしいものを\nたくさん食べたいです", 111801, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4690, "挑戦は大切ですよね☆\nというわけで\nおせちに魔物を…", 111801, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4691, "おせちは縁起物です！\n食べれば食べるほど\n幸せになるはずです！", 111801, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4692, "食べ物がたくさん\n入った福袋は\nないんでしょうか…？", 111801, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4693, "初詣で何を\nお願いしましょう…\n五穀豊穣？", 111801, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4694, "ずっとこうして\n年の始まりを\n過ごしたいですね", 111801, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4695, "いつか二人きりで\n初日の出を\n見たいです", 111801, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4696, "お餅おかわりです！\nいくらでも\n食べられますよ！", 111801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4697, "おせちの食材を\n取りに行きませんか？\nいい運動です☆", 111801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4698, "凧揚げって\n難しいですね～\n糸が絡まっちゃいます", 111801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4699, "一年の計は元旦にあり\n今年一年分の\n献立を作りましょう！", 111801, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4700, "ほらほら！\nもっと近くで着物を\n見ていいんですよ☆", 111801, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4701, "あなたはどんな\n初夢を見ましたか？\nわたしは…内緒です☆", 111801, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 4702, "年明けの朝は\n心なしか\n気持ちいいですね☆", 111801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 4703, "お雑煮のあとは\nおしるこで\nおやつタイムです！", 111801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 4704, "あたたかくして\n幸せな初夢を\n見ましょうね", 111801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4706, "はぁ～屋台巡りを\n満喫しました☆\n一休み一休み", 111801, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4707, "縁起のいい\nぬいぐるみを並べて\n福を呼びましょう！", 111801, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4708, "おしるこのアロマって\nないんでしょうか？\nそれなら作るしか…", 111801, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4709, "ここでお餅を\n焼いたらおいしく\nなりそうですね～♪", 111801, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4711, "おいしすぎて\n食べる手が\n止まりません～", 111801, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4712, "新しいおせち料理の\nヒントは…なるほど…\nふむふむ…", 111801, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 4714, "お兄ちゃんとあんな\n特訓やこんな特訓が\nしたいなぁ、えへへ♪", 100601, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 4715, "お兄ちゃんに褒めて\nもらうと、身体が\nじぃーんと\n熱くなっちゃいます♪", 100601, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4716, "見目麗しい衣装に\n身を包むのも\n悪い気分では\nありませんね", 115001, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4717, "縁起物で\n験を担ぐというのも\nたまにはよいでしょう", 115001, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4718, "非効率なので\nあまり行いませんが\n私も手料理くらいは\n作れるのですよ", 115001, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4719, "研究のためなら\n時に遠路を超え\n実地へ赴くことも\n必要なのです", 115001, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4720, "トゥンヌスの\n突然変異…\n大いに興味を\nそそられますね", 115001, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4721, "漁師の技法も\n再現できたことで\nまた一つ、仮説が\n実証できました", 115001, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4722, "正月くらいは\n私ものんびりしたい\n気持ちなのですよ\n意外ですか？", 115001, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4723, "模倣に頼らずとも\n衣装に合わせた\n所作程度は\nできて当然です", 115001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4724, "お年玉の使い道は\n思いつきましたか\nゆっくり考えても\nいいのですよ", 115001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4725, "トランプでも花札でも\n相手をしましょう\n手心が必要なら\nいつでも言いなさい", 115001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4726, "初詣でお祈りする\n内容ですか\n…ヒミツ、として\nおきましょう", 115001, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4727, "私たちでオーマ中の\nトゥンヌスを\n釣り上げましょうか\n…冗談ですよ", 115001, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4728, "見返りを求めない願い\nそれは祈りに通じるの\nかもしれませんね", 115001, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 4729, "…貴方がその眠たげな\n顔を洗ってくる間に、\n温かいお茶でも\n淹れておきましょう", 115001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 4730, "さて、本格的に\n活動を再開します\n貴方も同行しなさい\n昼食は済みましたか？", 115001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 4731, "私はもう少し\n作業を片付けてから\n寝ることにします\n早く眠りなさい", 115001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4733, "少し休みます…\n時間が来たら\n起こすように", 115001, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4734, "贈り物を模倣して\n潜入…試してみる\n価値はありそうですね", 115001, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4735, "嗅覚から得られる\n情報量は侮れません\n動物の模倣において\n注意すべき点ですよ", 115001, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4736, "穏やかな炎による\nリラックス効果は\n本能によるものかも\nしれませんね", 115001, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4738, "いかなる強者でも\n兵站、補給は\n必要なものです", 115001, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4739, "あらゆる学問は\n完璧な模倣のための\n大きな礎となります", 115001, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4741, "新たに得たこの力で\n仲間と目指す未来を\n切り拓いてみせます", 115501, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4742, "ここは、みなさまと…\nわたくしの\n帰る場所でございます", 115501, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4743, "主さまも精霊の召喚を\n試してみますか？", 115501, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4744, "主さま、おこづかいは\n足りていますか？\n遠慮は無用ですよ", 115501, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4745, "この装束をまとうと\n自然と背筋が伸びる\n思いがいたします", 115501, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4746, "進化した精霊に\n負けぬよう\n修行に励みます", 115501, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4747, "仲間たちといられる\nこの幸せが、末永く\n続きますように…", 115501, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4748, "主さまのお手手は\n大きくて温かいですね\nぎゅっぎゅっ…ふふ♪", 115501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4749, "今度、主さまにも\n里に伝わる昔話を\nお話しいたしましょう", 115501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4750, "里で採れた果物を\n持って参りました\n召し上がってください", 115501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4751, "里のお土産を\nここのみなさまに\nお配りいたしましょう", 115501, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4752, "仲間と囲む食卓…この\n温もりは、みなさまの\n心の温かさなのですね", 115501, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4753, "主さまに名前を\n呼んでいただくと\n心が温かくなります♪", 115501, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 4754, "ふふっ…主さま\n寝癖がついて\nおりますよ？", 115501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 4755, "今日の昼食は\n里の伝統料理です\nお口に合うでしょうか", 115501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 4756, "主さまが眠れるよう\n安らぎの風を…\nおやすみなさいませ", 115501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4758, "この癒し…\n主さまにも味わって\nいただきたい…", 115501, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4759, "この愛らしさ…\n精霊たちと似たものを\n感じます…♪", 115501, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4760, "精霊よ…この安らぐ\n香りを、隅々まで\n届けてくださいまし", 115501, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4761, "瞑想するのに\nちょうどよい暖かさで\nございます", 115501, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4763, "進化した精霊を\n使役するためにも\nよく食べて体力を…！", 115501, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4764, "風の精霊について\nもっと詳しく知って\nおかなければ…", 115501, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4766, "儀式の時を思い出して\n精霊に呼びかける…\n出ておいで～♪", 115601, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4767, "ひゃうっ！　もう…\n急に背中を触るなんて\nいたずら好きな精霊に\n育てた覚えはないのに", 115601, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4768, "儀式用の衣装…\nあんまり激しい動きは\nできないよね…でも\n戦う時は気にしない！", 115601, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4769, "そうだ…精霊に\n騎士クンのそばで\n見守っていてって\n頼んでみようかな…", 115601, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4770, "ふふっ、こうやって、\n楽しく精霊とお話し\nしていられる、平穏な\n日々が続くといいな", 115601, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4771, "き、きき、騎士クン！\nわたしと、あの…！\nうう…練習ですら\n上手く誘えないよぉ…", 115601, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4772, "あっ、精霊が…\nふふ、応援ありがとう\nわたしの想い…いつか\n必ず伝えるね", 115601, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4773, "新しく覚えた料理\n作ってみたんだけど\n騎士クンも食べない？", 115601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4774, "騎士クン、\n疲れてない？\nわたしと精霊で\n治療するよ？", 115601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4775, "もう、いたずらしない\n…騎士クン！？\nご、ごめんなさい！\n精霊だと思って！", 115601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4776, "騎士クン！\n時間が合ったら、\n精霊と一緒に\nお話しない？", 115601, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4777, "…こういう服着るの\n初めてなんだけど…\nえっ、かわいい…？\nあ、ありがとう…！", 115601, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4778, "困ったことがあったら\n相談してね。わたし、\n騎士クンのためなら…\n色々できるよ、色々！", 115601, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 4779, "おはよう騎士クン！\n今からお外で\n瞑想するんだけど…\n騎士クンもどうかな？", 115601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 4780, "お昼の時間だ\nご飯ならわたしが\n用意するから\nゆっくりしてて", 115601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 4781, "もうこんな時間…\n騎士クン、はいどうぞ\n寝る前の温かいミルク\nぐっすり眠れるよ♪", 115601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4783, "…うん、落ち着く\n精霊との魔法で\nこんな風に誰かを\n安心させたいな", 115601, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4784, "かわいい…\n精霊も気に入ってる…\nそうだ、同じ物\n作ってあげようかな？", 115601, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4785, "いい香り…これ、\nうちに置いたら、\nお母さん喜ぶかも！", 115601, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4786, "あったかい…\nまるで誰かが\n抱きしめてくれた\nみたい…", 115601, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4788, "自然の恵みに\n感謝して…\nいただきます♪", 115601, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4789, "もっと精霊について\n勉強して、みんなを\n守れるように\nなりたいな…", 115601, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 4791, "アイツといると胸が\nぎゅーっとなる…\nこれってもしかして…", 102201, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 4792, "アンタとならきっと\nどんなゲームでも…\nそ、その、たた、\n楽しいって思えるわ！", 102201, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4793, "色んな人と話したら\n記憶探しの手がかりが\n見つかるかも…？", 106401, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4794, "アイスクリームでも\n作ろうかしら？\nバニラにミントに…", 106401, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4795, "ネビアに話しかけると\n逃げられるのよね…\nもう、襲わないのに…", 106401, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4796, "「コッコロさん」、\n「コッコロさん」…\n…間違えないように…", 106401, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4797, "最近少し太ったかも？\nきっとおいしいもの、\n食べ過ぎね…", 106401, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4798, "ここにいると、\nとても落ち着くわ…\n自分の家、だものね", 106401, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4799, "みんなと一緒の時間、\nずっと記憶に残したい\n…そう思っているのよ", 106401, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4800, "ドラゴン族って、\nそんなに珍しいの？\nあまり実感ないけど…", 106401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4801, "料理、練習してるのよ\n…味見してもらえる？\n期待はしないでね", 106401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4802, "頭を叩いたら、\n記憶が戻ったり\nしないかしら…？\nやらなくていいわよ？", 106401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4803, "赤ちゃんみたいだった\n時のことは、忘れて…\nお願いだから…", 106401, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4804, "ひゃんっ！？\nし、しっぽを掴むのは\nダメよ？", 106401, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4805, "記憶が戻っても…\n私は、あなたたちと\n一緒にいたいわ", 106401, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 4806, "おはよう。\n寝ぐせついてるわよ？\n…ふふっ、これでよし", 106401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 4807, "そろそろお昼かしら？\nおいしいごはんを、\nみんなで一緒に…ね", 106401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 4808, "もうこんな時間ね。\nそろそろ休みましょ？\n…ふわぁ…", 106401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4810, "ふかふか、ぎゅう～…\n…あっ、これはその…\nストレッチしててっ…", 106401, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4811, "あまり、興味ないわ。\nけど…けど…\n…か、かわいい…", 106401, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4812, "いい香りね。\nとっても落ち着くわ…\n…っくしゅっ", 106401, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4813, "冷やす方が得意だけど\nあったかいのも、\nキライじゃないのよ", 106401, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4815, "別に、食いしん坊\nじゃないのよ？\nでも、もう一口だけ…", 106401, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4816, "ん…なるほどね。\n勉強になるわ…", 106401, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4818, "水着の探偵か…\nちょっと恥ずかしい\n気がしてきたよ…", 115701, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4819, "依頼があれば\n海でも山でも\n赴くさ！", 115701, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4820, "今のうちに\n助手くんの秘密を…\nわ…私はなにをっ！？", 115701, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4821, "休暇も時には必要さ\n海でゆっくりと…\nむっ？　事件の気配！", 115701, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4822, "探偵の秘密道具…\n水着だと隠す\n場所がない…！", 115701, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4823, "そろそろ助手くんが\n現れる時間かな？\nふふっ、楽しみだね", 115701, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4824, "真実は私と助手くん\n二人だからこそ\n解き明かせるのさ", 115701, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4825, "ひゃああっ！？\nわ、私の不意を突く\nとは…やるね助手くん", 115701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4826, "さて、なにか気づいた\nことはあるかな？\n言ってごらん♪", 115701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4827, "この感触…分かった！\n助手くんだね！\nふふっ、やっぱり♪", 115701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4828, "水着…どうかな？\n探偵らしさは\n残してるつもりだけど", 115701, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4829, "ん？　依頼かな？\n助手くんの頼みなら\nなんでもお安い御用さ", 115701, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4830, "助手くん、私と海に\n行かないかな\n…事件じゃないよ？", 115701, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 4831, "珈琲でも淹れて\n目を覚ますとしよう\n一緒に飲むかい？", 115701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 4832, "昼食は済ませた？\nなにを食べたか\n当ててあげよう！", 115701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 4833, "夜は謎が深まる時間…\n闇に覆い隠された真実\n必ず暴いてみせる！", 115701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4835, "さて、推理の時間だ\n…サボってないよ？\n考えてるんだからね？", 115701, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4836, "これはもしや、中に\n秘密のメッセージが？\n…かわいいだけだね", 115701, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4837, "ふむ、なんだか\n頭がスッキリするね\n…閃いた！", 115701, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4838, "ポカポカするね…\n助手くん\n君もおいでよ", 115701, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4840, "はむ、はむ…んぐっ\nた、食べてるところを\n見つめられると～…", 115701, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4841, "秘密の依頼とか\nこういう場所に\n隠したりするよね…♪", 115701, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4843, "これはいい乗り心地だ\n事件現場にこれで登場\n…は、やめておこう", 115701, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4844, "不思議な感触だね…\nでも、いい発想が\n生まれてきそうだ…♪", 115701, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4845, "これはあたしが\n前へ進むために\nあいつがくれた\n大切な力…！", 180601, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4846, "チェンジ・\nプリンセスフォーム！\n練習練習、っと", 180601, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4847, "もうあいつに助けて\nもらいっぱなしの\nあたしは…\n卒業しなきゃね", 180601, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4848, "気ままに、お気楽に\nあいつと冒険する…\nそれって幸せよね", 180601, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4849, "プリンセスフォーム…\nお姫様みたいね\nあたしがお姫様か…\nそっかぁ…ふふ♪", 180601, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4850, "あいつと繋がって\n生まれたこの絆は\nあたしの宝物だわ", 180601, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4851, "あたしの命の使い方…\n全て受け止めてくれた\nあいつにだったら…", 180601, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4852, "ふふ～ん♪\nプリンセスフォームの\n天才魔法使い\nキャル様に用かしら？", 180601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4853, "これからは\nあたしの力で\nなんでもパパっと\n解決してあげるわね！", 180601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4854, "魔力がみなぎってる…\nねえ、もしかして\n今のあたしって本気で\nすごいんじゃない？", 180601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4855, "変身の反動で\nお腹が空いても\n魔物料理だけは\nいやだからね！？", 180601, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4856, "ねぇ、あんたになら\nあたしの心…\n全部預けていいかも", 180601, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4857, "あいつと一緒に\nいられたら、他には\n何も…って、あああ\nあんたいつの間に！？", 180601, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 4858, "早起きも悪くないわね\n読みかけの魔導書\n今のうちに\n読んじゃおうかしら", 180601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 4859, "ねぇ～お腹すいたー\nあんたなんか\n作りなさいよ\n魔物料理以外でね！", 180601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 4860, "ふぁぁ…眠すぎて\n今なら変身しても\nお腹が減らない\n自信があるわ…", 180601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4862, "あー♪　最っ高！\n休んで魔力を高めて…\nあいつの役に\n立ってあげなきゃね", 180601, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4863, "なにこれかわいい…！\nコレクションにしたく\nなっちゃうじゃない", 180601, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4864, "この香り…すごく\nリラックスできるわ…\nあ、いい感じの魔法\n思いついちゃったかも", 180601, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4865, "あったかい…\n落ち着くわ…\nあいつの手の\nぬくもりみたいね", 180601, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4867, "プリンセスフォーム…\nこ、こんなに\nお腹が減るのね\n甘く見てたわ…！", 180601, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4868, "ま、魔物料理の本！？\n誰よこんな本\n買ったの！　って…\n一人しかいないか", 180601, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4870, "憧れの舞踏会…\n私、きちんと\n踊れるかしら", 115801, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4871, "アン・ドゥ・トロワ\nアン・ドゥ・トロワ\n練習はばっちりね♪", 115801, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4872, "こんなにすてきな\nドレスを着られて\n本当に夢みたいだわ♪", 115801, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4873, "魔法はいつか解ける…\nでも、その時間は心に\n残り続けるわ♪", 115801, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4874, "ガラスの靴は夢へ\n踏み出す魔法の道具…\n私、がんばっちゃう！", 115801, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4875, "舞踏会であなたと\n一緒に踊れたら\nいいのにな", 115801, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4876, "舞踏会に行く前に\n運命の王子様には\n出会ってるのよね", 115801, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4877, "どうかしら？\n私、きちんとドレス\n着られてる？", 115801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4878, "あなたも一緒に\n踊ってみる？\nしゃなりしゃなり♪", 115801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4879, "あなたにエスコート\nされてきれいなお城を\n歩いてみたいわ♪", 115801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4880, "どう？　レッスンの\n成果はきちんと\n出てるかしら？", 115801, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4881, "あなたが隣にいれば\n私も立派なお姫様に\n見えるかしら♪", 115801, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4882, "私にとって\n一番の魔法はあなたが\nくれる勇気よ♪", 115801, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 4883, "さぁ！　今日も一日\n立派な淑女を目指して\nいきましょう♪", 115801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 4884, "お昼ごはん代わりに\nメタモルアップルを\n食べておきましょう", 115801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 4885, "そろそろ魔法が解ける\n時間…でも淑女の\n心得は忘れないわ♪", 115801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4887, "淑女にも休息は\n必要よね…少しだけ\n休ませてもらおっと", 115801, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4888, "かわいいものに\n囲まれた部屋も\n女の子の夢よね…", 115801, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4889, "すてきな香り…\nお花かしら？\n森で探してみようかな", 115801, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4890, "暖かい…暖炉のそばで\nおとぎ話を聞くのも\nいいわよね♪", 115801, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4892, "レッスンのために\n栄養補給して\nおかないと！", 115801, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4893, "教養を身に着ける\nことも大切よね！\nなになに…", 115801, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4895, "や、やっぱスカートは\n落ち着かねえな…\nなんか、\nスースーするしよ…", 115901, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4896, "せっかくこんな格好\nしてるし、いつもとは\n違う剣術の訓練を\nしてみるかな", 115901, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4897, "さて、他の連中が\n帰ってくる前に、\nなんか料理でも\n作っといてやるか", 115901, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4898, "こ、こんな服着てる\nわけだし、紅茶とか\n楽しんじゃっても\n変じゃないよな！", 115901, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4899, "暇だし運動でも…\nあ、あぶねえ！\nスカートなの忘れてた\n…誰も見てねえよな？", 115901, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4900, "紅茶が切れてるな…\nよし、あいつと一緒に\n買い物にでも行くか♪", 115901, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4901, "…あいつがいねえと、\nつまんねえな…ったく\n飯用意してやるから、\n早く帰ってこいよな…", 115901, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4902, "あ、あんまり\nジロジロ見んなよ\n…結構、\n恥ずかしいんだからな", 115901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4903, "ダンスの相手は\n無理だが、\n剣の訓練なら\nいつでも付き合うぜ！", 115901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4904, "この服に\n触りたいのか？\nすごい作りだし、\n気持ちは分かるぜ", 115901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4905, "お、いいところに！\nクッキーを焼く\nところだったんだが、\nおまえも食うか？", 115901, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4906, "おまえにもこういう服\n似合うんじゃねえか？\n今度あたしが\n選んでやるよ♪", 115901, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4907, "…おまえの前でなら、\nこういう格好を\nすんのも…\nな、なんでもねえよ！", 115901, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 4908, "朝の訓練を始めるか\n時間あるなら、\nおまえも付き合えよな", 115901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 4909, "もうこんな時間か…\n昼飯作るけど、\nなんか\n食いたいものあるか？", 115901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 4910, "眠れないなら、\nホットミルク飲むか？\n体あったまって、\n眠くなると思うぜ", 115901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4912, "ちょっと休憩して…\nおっと、服にしわが\nできないように\nしねえとな", 115901, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4913, "だ、誰も\n見てねえよな？\n…よ～しよしよし、\nかわいいなおまえー♪", 115901, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4914, "すげぇいい香りだな\nうちの居酒屋にも…\nいや、合わねえか", 115901, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4915, "あったけぇ…\nちょっとだけなら、\n寝ちまっても\nいいよな…？", 115901, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4917, "これうめえな！\nたぶん材料は…よし！\nあたしも作ってみるか", 115901, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4918, "この本は…\n誰でも分かる作法…か\n最低限このぐらいは\n勉強しとくか…", 115901, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 4920, "ミミがおとなに\nなっても\nおにいちゃんとは\nずーっといっしょ♪", 102001, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 4921, "ミミとくべつなだっこ\nしてほしいな…うん！\nおひめさまだっこ～♪", 102001, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 4922, "次はにいちゃんに\nどんなイタズラしよう\n早く会いたいな～", 100401, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 4923, "ミソギの特別な\nイタズラ見てくれる？\nいいよねっ♪", 100401, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 4924, "大人になったら、\nあの人と何しようかな\n…楽しみだなぁ", 103601, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 4925, "頭を撫でないで\nください！　もう、\n少しだけ、ですからね", 103601, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4926, "くるくる〜♪\nうちのドレス\nかわええなぁ", 116001, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4927, "日差しは苦手やけど\nたまにはひなたの下で\nぽかぽかしたいわぁ", 116001, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4928, "外はお花が咲いてる\n久しぶりにお花を\n活けたいなぁ", 116001, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4929, "お花をたくさん集めて\nギルドハウスに飾れば\n喜んでくれはるかな", 116001, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4930, "あたたかい日は\n紅茶を飲みながら\nゆっくりしたいなぁ", 116001, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4931, "お稽古事を始めるなら\nうちに言ってな\nどんなことも教えたる", 116001, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4932, "うちにいろんな夢を\n見せてくれはって\nほんま、おおきにな♪", 116001, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4933, "王子はん\n一緒におとぎの国へ\nお散歩にいきまひょ♪", 116001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4934, "ふんふんふ〜ん♪\n今な、王子はんに\n花冠を作ってるんよ", 116001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4935, "王子はんは\nお菓子好きどす？\nうち、作りますわ", 116001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4936, "うーん。ドレスの柄は\nお花とお星さま\nどっちにしまひょか", 116001, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4937, "王子はんとの\n思い出が増えるたび\nうちの宝物も増えます", 116001, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4938, "王子はんと一緒なら\nどんな場所でも\n夢の国になりますわ", 116001, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 4939, "夢の中におりたいけど\n王子はんに会いたくて\n起きてもうたわ♪", 116001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 4940, "王子はん一緒に\nアフタヌーンティー\nいかがやろか？", 116001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 4941, "夜の舞踏会…\n夢が広がるわぁ\nもちろんお相手は…♪", 116001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4943, "ふわふわや〜\nなんや心地よぉて\n眠ってしまいそうや…", 116001, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4944, "かいらしいなぁ\n新しいお洋服でも\n作ってあげまひょか♪", 116001, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4945, "お花やろうか…\nリラックスできて\nええ香りやねぇ", 116001, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4946, "こういう場所で\n絵本を読むと\n気分が出てええなぁ♪", 116001, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4948, "舞踏会を楽しむなら\nたくさん食べて\nたくさん踊りまひょ", 116001, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4949, "うち、この本好きや\n王子さまと結ばれる\nお姫さまのお話なんよ", 116001, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4951, "はぁ、宿題めんどい…\nこれは楽しくない…\nこんな青春いらない…", 116301, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4952, "奨学金の道は遠いなぁ\nメルクリちゃんたちが\nお金くれればいいのに", 116301, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4953, "アオイちゃん元気かな\nチエルに会いたくて\n泣いてるんだろうな…", 116301, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4954, "暇だし鏡でも見るか…\nうわ、顔良っ！\nえ誰この超絶美少女！", 116301, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4955, "ワンツーワンツー♪\nうーん…カルミナの\n振りコピ難しいにゃー", 116301, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4956, "先輩も運動してます？\nチエルが一緒に走って\nあげてもいいですよ？", 116301, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4957, "来年のテレフェスも\n今から楽しみだなあ…\nまた一緒にお化け屋敷\n入ってくださいね", 116301, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4958, "ちぇる～ん☆\nフェス仕様でも\nかわいいちえるん！", 116301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4959, "いま話しかけないで！\n来年の聖学祭ライブの\nイメトレ中なんです！", 116301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4960, "クロエ先輩のウェア姿\nめっちゃカッコよ！\n思わずおへそ触ったら\nぶたれましたけど", 116301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4961, "ユニ先輩の体操着姿…\nあれはあれで実に良き\n一生見てられますよね", 116301, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4962, "先輩って体操着持って\nないんですか？　ハ？\nチエルのを貸せと…？\nえっ…変態かな？", 116301, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4963, "テレフェスでは先輩も\n地味に頑張ってました\nね。まあ格好よかった\nんじゃないですか…", 116301, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 4964, "おはちぇる～ん☆\nさあ今日も楽しいを\n探しに行きますよー！", 116301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 4965, "先輩ビッグニュース！\n今からお昼ごはん\nおごらせてあげます！", 116301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 4966, "こんばんちぇるるん☆\nさあ朝まで楽しいを\n探しに行きますよー！", 116301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4968, "はぁ…ふかふか～…\n運動の後は…眠たく…\nなり…ま…", 116301, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4969, "ユニ先輩に似合いそ！\nいやクロエ先輩かな？\nチエルは当然すぎて…", 116301, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4970, "やん、いい香り～！\nまるでチエルのために\n存在するみたい！", 116301, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4971, "この火を使って\n聖火リレーしましょう\n先輩、一本抜いて！", 116301, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4973, "あむあむ…ちゃんと\n食べないとユニ先輩\nみたいになっちゃう！", 116301, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4974, "ふむふむ…ちゃんと\n読まないとクロエ先輩\nみたいになっちゃう！", 116301, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 4976, "今なら書ける気がする\n私の本当の\n想いを込めた\nラブソングを！", 102901, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 4977, "輝きを増した私を\nいっぱい見てね！\n…ちょ、ちょっと\n見つめすぎだよ！", 102901, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4978, "テレフェスかぁー…\nあんまいい記憶ないな\nよし、忘れとこ", 116201, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4979, "暇だし鏡でも見るか…\nふふ～ん…いいじゃん\nあ？　何見てんだコラ", 116201, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4980, "ワンツー、ワンツー♪\nああクソ、難しいな…\nあ？　何見てんだコラ", 116201, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4981, "アオイ元気してるかな\nまた絶叫したり白目\n剥いたりしてんのかな", 116201, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4982, "ミフユねえさんが\nバイトリーダーしてる\n工場…うちも行くか…", 116201, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4983, "うーん…今日の晩御飯\n何にすっかなァ…\nあんたも食べてく？", 116201, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4984, "来年のテレフェスは\nどーなンだかねえ？\nま、一緒にまた弟の\n相手してやってよ", 116201, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4985, "体操着なんてフツーに\n着てもつまんないし？\nその辺センス出るよね", 116201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4986, "奨学金の道、遠くね？\nアキノ先輩と仲良く\nなった方が早いかも…", 116201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4987, "あんた体操着は持って\nないの。ハ？　うちの\n貸せって？　いいけど\n…いや、よくねえよ", 116201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4988, "にゃんっ！\nや、だからさ…急に\n話しかけんなっての", 116201, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 4989, "え、なに？　この服？\nハ？　似合ってる？\n…え、聞こえなかった\nちょ、もっかい言って", 116201, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 4990, "あんたもテレフェスで\n一応は頑張ってたよね\nま格好よかったんじゃ\nないの。知んないけど", 116201, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 4991, "ふぁ～あ…おはーす\n昨日も寝たの遅くて\nまだ目がショボだよ…", 116201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 4992, "なに。あんた昼御飯\nまだなの。じゃ、うち\n適当になんか作ろうか", 116201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 4993, "夜っていいよね。色々\n見えすぎない感じとか\n…この話したっけ？", 116201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4995, "あ、ヤバ…運動の後に\nこのフカついたのは…\n秒でスヤれる…すやぁ", 116201, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4996, "へーこんなんチエルに\n似合いそうじゃん。\n…いやユニ先輩かな？", 116201, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4997, "お、なんか良さげな\n香りじゃん。これは\n何の…いや分かんね", 116201, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 4998, "熱ッ！　この野郎なに\n爆ぜてンだコラァ！\n濡らすぞ木、てめえ！", 116201, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5000, "何コレよく分かんない\nけど、今日モヤシしか\n食ってないから助かる", 116201, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5001, "今日、体操着だしね。\n読書には逆アドかな。\nうん帰ろ。いやあ残念", 116201, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5003, "ビッグボス・イノリ様\nふっふっふ…\nしびれる響きです！", 116501, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5004, "いつかお兄さんを\nあたしの側近に\nしてやるですよ！", 116501, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5005, "ビッグボスになったら\nみんなからたくさん\nお菓子をもらうです！", 116501, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5006, "ふふん！\nこの服はあたしにしか\n着こなせませんよ！", 116501, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5007, "ばーんと立って\nどーんと腕を組む！\nこれがボスのポーズ！", 116501, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5008, "イノリ様の側近に\n選ばれるなんて\n名誉なことですよ！", 116501, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5009, "未来の側近として\nあたしをしっかり\n甘やかすですよ！", 116501, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5010, "あたしがお兄さんを\n立派なギャングに\nしてあげますからね！", 116501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5011, "側近たるもの\nボスを甘やかなさいと\nだめですよ！", 116501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5012, "なに見てやがるです…\nくぅ、分かりました！\nお菓子は半分こです！", 116501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5013, "お兄さんも\nあたしがボスなら\nうれしいですよね！", 116501, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5014, "お兄さんが側近なら\nあたしも安心して\n夜眠れるですよ！", 116501, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5015, "ビッグボスになったら\nお兄さんにいい思い\nさせてあげますから！", 116501, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 5016, "ボスたるもの\n早起きは鉄則ですよ！\nふぁあ…", 116501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 5017, "お腹が空きました…\nご飯食べにいきますよ", 116501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 5018, "ボスたるものぉ〜…\n夜更かしぐらい当然…\nぐー…", 116501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5020, "は〜これは人をだめに\nしやがるやつですね\nあたし専用です", 116501, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5021, "これはけしからん\nかわいらしさですね\nあたしにぴったりです", 116501, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5022, "高級な香りがするです\nボスになったら\n部屋に置いてやるです", 116501, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5023, "あったかいですねぇ\nちょっとこのまま\n一休憩です…", 116501, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5025, "はむはむ♪\nボスたるもの食べねば\n戦はできませんよ！", 116501, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5026, "ボスなら経済書ぐらい\n…う。難しくて\n読めねーです…", 116501, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 5028, "あなたはドジな私を\n受け入れてくれる…\nだから、頑張れます！", 102501, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 5029, "何でも仰ってください\nちょっぴり大胆な\nことでも…な～んて♪", 102501, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5030, "あーあー、よし\nのどの調子もいいし\nはりきってガイド\nしてくるか！", 116601, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5031, "さあ、そろそろ行くか\nガイドが\n遅刻したんじゃ\n話になんないからな", 116601, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5032, "しれっとドラゴン族が\n旅行に来たり\nしてくれないかな", 116601, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5033, "たぎるガイド先…\n火山、魔物の巣…\nダメだ、そんなとこ\nオレしか行けないや", 116601, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5034, "この前行った絶景\nスポットよかったな\nキレイな景色を見る\nのもいいもんだぜ", 116601, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5035, "お、今日はカレと\n一緒か。なんだか\nやる気出てきたぜ！", 116601, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5036, "カレにガイドして\nもらったら\nどこに行っても\n楽しいだろうな", 116601, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5037, "よっしゃあ！\nどっちが上手く\nガイドできるか\n勝負しようぜ！", 116601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5038, "飯でも食いがてら\nおススメの店の\n下調べでもしようぜ！", 116601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5039, "なかなかいいだろ？\nこの格好。キミも\n作ってもらったら\nどうだ？", 116601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5040, "普通は行けない\n場所とか行きたいよな\nキミならどんな\nとこがいい？", 116601, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5041, "ガイドの仕事は\nキミの方が得意そうだ\n参考にさせてもらうぜ\nよろしくな！", 116601, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5042, "キミの探してきた店\n結構よさそうじゃん\nそうだ、今度二人で\n下見に行こうぜ！", 116601, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 5043, "いい朝だな。絶好の\n仕事日和だぜ！\n今日もよろしくな！", 116601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 5044, "よし、昼飯にしよう\nこの後も激務だし\n飯抜きじゃあ\nぶっ倒れちまうからな", 116601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 5045, "お疲れさん！　さーて\n次はオレと遊んで\nくれよ。まだまだ\nいけるだろ？", 116601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5047, "ガイドは一日\n歩きっぱなしだからな\nゆっくり休ませて\nもらうぜ", 116601, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5048, "これ、どこで\n売ってるんだろ？\n土産の情報も\n仕入れとかなきゃな", 116601, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5049, "アロマとかよく\n分かんねえけど\n土産にはいいかもな\n覚えておくぜ", 116601, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5050, "体が温まると心も\n熱くなるんだよな\nいい仕事が\nできそうだぜ", 116601, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5052, "食べ放題か\nこれはお客さんも\n喜んでくれそうだな！", 116601, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5053, "へえ、こんなスポット\nあったんだ。やっぱ\nたまには勉強も\nしとくもんだな", 116601, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5055, "バイト先になじめる\nように笑顔の練習を！\nに、にへぇ〜…", 116701, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5056, "働いて働いて働いて\n…いつかその先に\nぼっちの出口が\n見えるのかも…！", 116701, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5057, "バイトのイメトレを\n始めます。ポーズを\n決めて…よし！\n角度を変えて…よし！", 116701, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5058, "バイト仲間に入る\nためには厳しい修業が\n必須と聞きました…！", 116701, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5059, "どんどん技術を磨いて\nバイト仲間として\n認めてもらうんです！\n目指せ職人技…！", 116701, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5060, "あの人が応援して\nくれる…だから必ず\nバイト仲間の輪に\n入ってみせます…！", 116701, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5061, "あの人がいてくれると\n勇気が湧いてきます…\nぼっち卒業を夢見ても\nいいんだ、って…♪", 116701, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5062, "緊張するけど\n思い切って声をかけて\nみます…バイト仲間に\n入るために…！", 116701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5063, "ぼっちの私が\nおもちゃ作りで\n人と共同作業…！\nか、感動です…！", 116701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5064, "好きなおもちゃって\nありますか？　私は\nもちろんマイフレンド\nくんです！", 116701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5065, "私が作ったおもちゃも\n誰かの友達に\nなってたら\nいいなぁ…♪", 116701, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5066, "今日も明日もバイト\nですね！　バイト\n仲間ができる日が\n楽しみです…♪", 116701, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5067, "脱ぼっちを目指して\nこれからも一緒に\n頑張りましょう！\n目指せ、バイト仲間！", 116701, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 5068, "おはようございます\n今日も気合いを入れて\nおもちゃ作りします！", 116701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 5069, "ランチの時間です…♪\nもちろん工場の隅で\nぼっち飯です…！", 116701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 5070, "ふわぁ…今日も\nお疲れさまでした\n明日も働きます～！", 116701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5072, "バイトで頑張った日は\nふわふわの誘惑に\n勝てません…", 116701, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5073, "こういうおもちゃの\nアイデアも\n素敵かも…♪", 116701, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5074, "いい香りがする\nおもちゃはどうかな…\n森の薬草を使って…", 116701, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5075, "ぽかぽか…\n幸せなひとときですね", 116701, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5077, "もぐもぐ…たくさん\n食べて厳しいバイトを\n乗り切ります…！", 116701, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5078, "ふむふむ…おもちゃの\n設計図を見るのも\n勉強になりますね！", 116701, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5080, "う～ん\n次はどんなたい焼きを\n作ろうかにゃぁ…", 116801, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5081, "あたしが売る\nたい焼きには\n夢が詰まってるにゃ！", 116801, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5082, "いつか絶対に\nたい焼き工場を\n建て直してみせるにゃ", 116801, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5083, "たまにたい焼き以外の\nおやつも食べるにゃ\nたとえば煮干しとか！", 116801, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5084, "悪い商売人がいたら\nファントムキャッツに\n知らせるにゃ！", 116801, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5085, "キミと一緒にいると\nたい焼きのアイデアが\n次々出てくるにゃ♪", 116801, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5086, "出来ないことも\nキミと一緒にいれば\n出来る気がするにゃ♪", 116801, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5087, "なんにゃ～？\nお姉さんにかまって\nほしいのかにゃ♪", 116801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5088, "キミ、いいところに！\nあたしとライバル店へ\n視察にいくにゃ！", 116801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5089, "くんくん…この匂い！\nたい焼き持ってるなら\nちょうだいにゃ♪", 116801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5090, "次はどんなたい焼きを\n売ろうかにゃ～\nキミの案も欲しいにゃ", 116801, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5091, "ファントムキャッツに\n相棒が必要にゃ\nたとえばキミとか…", 116801, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5092, "相棒！\n…ってキミのこと\n呼んでいいかにゃ？", 116801, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 5093, "早起きは三文の徳にゃ\n今日もたい焼き\n売れますよーににゃ！", 116801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 5094, "お腹空いたにゃ…\n賄いのたい焼きを\nいただくにゃ！", 116801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 5095, "猫は夜が本番にゃ\nでもあたしは…\nふわぁ～眠いにゃ…", 116801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5097, "ふかふかにゃ～\nここで体を丸めて\n一眠りしたいにゃ", 116801, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5098, "これは…！\n猫にも負けない\nかわいさだにゃ…！", 116801, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5099, "いい香りにゃ～\nゴロゴロのどが\n鳴りそうだにゃ～", 116801, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5100, "ふにゃ～…\nこのまま働かずに\nまるまってたいにゃ…", 116801, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5102, "商売は戦いにゃ\nたくさん食べて\nたくさん働くにゃ！", 116801, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5103, "ふむふむ。たい焼きの\nアレンジレシピ集…\nためになるにゃ…", 116801, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 5105, "お芝居の前に\nお兄ちゃんの顔を\n見ると、とっても\n落ち着きますぅ…♪", 102101, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 5106, "お兄ちゃんにもっと\n見てもらいたい…\nだから私…\n強くなりたいです…！", 102101, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5107, "さらなる効率化で\nみんなといい仕事を\nしたいわね！", 116901, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5108, "他のバイトとの\nスケジュールを\n調整しないと…", 116901, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5109, "時給がアップしたわ！\n働きが認められると\nやる気が出るわね", 116901, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5110, "効率的に動ければ\n業務を追加できる…\n燃えてきたわ！", 116901, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5111, "帳簿付けのコツ…\nユカリさんに教わって\nみようかしら", 116901, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5112, "バイトリーダーとして\nみんなをビシバシ\n鍛えていくわ！", 116901, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5113, "戦いでもバイトでも\nキミなら安心して\n背中を預けられるわね", 116901, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5114, "どうしたの？\n仕事のことなら\nなんでも相談してね！", 116901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5115, "さすがは私の弟子ね\nこれなら安心して\n仕事を任せられるわ", 116901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5116, "みんなのお手本になる\n働きぶりを見せないと\nいけないわね！", 116901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5117, "キミのこと…もっと\n頼りにしちゃっても\nいいのかしら…？", 116901, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5118, "ふふ、キミも一人前の\nバイト戦士として\n立派に成長したわね！", 116901, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5119, "新しいおもちゃの\n企画書を作ってみたの\nちょっと見てくれる？", 116901, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 5120, "朝は一番効率がいい\n時間帯よ！\nバリバリ働くわよ！", 116901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 5121, "さ、お昼に行きましょ\n今日の日替わり定食は\n何かしら…楽しみね♪", 116901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 5122, "もうこんな時間…\nちゃんと寝て明日に\n備えなくちゃね", 116901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5124, "適度な労働と休息の\nサイクルが最大効率を\n生み出すのよ…ふぅ…", 116901, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5125, "はぁ～なんて素敵な\n抱き心地なのかしら…\n最高効率の癒やしね♪", 116901, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5126, "落ち着く香りね…\nなんだか集中力が\nアップした気がするわ", 116901, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5127, "ふぅ…癒されるわね…\n仕事のオンオフの\n切り替えは重要よね", 116901, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5129, "働くためには\nエネルギーをちゃんと\n摂っておかないと", 116901, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5130, "効率を上げるため\n生産管理の勉強も\nしておきたいわね", 116901, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5132, "変身している間に\n色々と試しておこう\nまずは…", 180301, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5133, "飛べるというのは\n便利だな。料理が\n暖かいうちに\n持ち帰れる", 180301, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5134, "プリンセスか…\n柄ではないけれど\n嫌な気はしないな", 180301, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5135, "この力で\n何を成すべきなのか…\n剣に聞いてみよう", 180301, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5136, "変身したとて私は私\n一時的に強くなろうと\n元が弱くてはだめだ", 180301, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5137, "変身していない時でも\n彼がそばにいると\n力が湧いてくる…", 180301, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5138, "私の思い…\nいつか伝えてみせるよ\n…きっといつか！", 180301, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5139, "何者かと戦うの？\nそれとも修行？\nどちらだとしても\n付き合うよ", 180301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5140, "変身し続けたら\nどれだけ空腹に…\n私は試さないからね？", 180301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5141, "本物のプリンセスに\nなるのは御免だが\nナイトがいて\nくれるのは頼もしいよ", 180301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5142, "いつかこの力を\n誰かを救うために…\n戦い以外に使える時が\nくることを願うよ", 180301, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5143, "キミに感じてほしいな\n私に流れてくる\nキミからの力を…\nその…私の、思いも…", 180301, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5144, "思いを告げる…\nそれがこんなにも\n難しいなんて…\nでも、いつか必ず…！", 180301, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 5145, "おはよう！\nいい変身日和だ！\n…ふふっ、冗談だよ", 180301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 5146, "いつもより多く\n昼食を用意しよう\n変身が解けたら大変だ", 180301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 5147, "一日お疲れ様\n私もそろそろ変身を\n解いて休むとするよ", 180301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5149, "変身してしまうと\n動かずにいても\n空腹になるのかな…？", 180301, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5150, "私もキミたちくらい\n可愛げがある姿に\nなっているかな？", 180301, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5151, "香りに対する感覚も\nいつもより鋭く\nなっているみたいだ…", 180301, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5152, "ありがたいな\n少し足元が\n冷えていたところだ", 180301, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5154, "これは…！\n持ち運べるように\n改良できないかな？", 180301, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5155, "祖父に貰った\n技法書を読んで\n初心に帰ろう", 180301, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5157, "最強の惚れ薬を作って\nあのおかたの心を\n私の思いのままに…", 117001, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5158, "夏といえば海にスイカ\n花火に水着…\nそして惚れ薬ですわ…", 117001, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5159, "あのおかたの心を\n手に入れるための\n準備を進めましょう\nこの夏こそ確実に…", 117001, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5160, "あのおかたに近づく\n虫どもを、どうやって\n片付けようかしら…\nまったく忌々しいこと", 117001, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5161, "…クスクス\n薬の調合を考えるのは\n至福の時間ですわ…", 117001, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5162, "あのおかたの全てが\n早く私のものに\nなりますように…", 117001, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5163, "かき氷も花火も海も…\nあなた様がいてこそ…\nそう、あなた様は\n私の全てなのですわ", 117001, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5164, "二人だけの夏を\n楽しみましょう…？\n…クスクス", 117001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5165, "あら、あなた様\n私に秘密でどこに\n行こうとして\nいますの…？", 117001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5166, "海に行きませんか？\nあなた様と一緒に\n過ごすには\n最高の場所ですから…", 117001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5167, "ちょうどいいところに\nいらっしゃいました\nできたての薬を…\nさぁどうぞ…クスクス", 117001, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5168, "夏の海のきらめきも\nあなた様の存在が\nなければ色あせて\nしまいますわ…", 117001, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5169, "私とあなた様は\n結ばれる運命に\nあるのです…\n逃がしませんわ…", 117001, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 5170, "起きてすぐに会いに\n来てくださるなんて\nようやく私の伴侶たる\n自覚が…クスクス", 117001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 5171, "お食事はまだですか？\n特製の薬草ランチを\n召し上がれ…クスクス", 117001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 5172, "私の夢を見られる薬を\n差し上げますわ…\nおやすみなさいませ\nあなた様…クスクス", 117001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5174, "いい肌触りですわ…\n考え事にもぴったり\n眠くなるのが\n玉に瑕ですが…", 117001, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5175, "こういったものに\n揮発性の薬を仕込む\nのも一つの手ですわね", 117001, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5176, "この香り…開発中の\n薬に使えそうな気が\nしますわ…クスクス", 117001, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5177, "あのおかたの\nぬくもりを\n思い出しますわ…", 117001, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5179, "美味しい…この味なら\n薬を混ぜても違和感は\nなさそうですわ…", 117001, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5180, "この薬の調合法…\n今度試してみましょう\nもちろんあのおかたに\n飲んでいただいて…", 117001, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5182, "二人乗りができると\nいいですわね…", 117001, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5183, "ふかふかですわ…\nあのおかたの分も\n欲しくなります…", 117001, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 5184, "今日はキミと\nいつまでも歌って\n踊りたい気分さ～♪", 101701, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 5185, "あは～♪\nキミの隣は、故郷より\n落ち着くかも～…♪", 101701, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5186, "この水着…\n一番に弟くんに\n見せたいな♪", 117101, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5187, "弟くんをこっそり\n海に運んで…\nサプライズしちゃお♪", 117101, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5188, "いつもと違う髪型に\nしてみたけど…\n気に入ってくれるかな", 117101, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5189, "海は楽しみだけど\n日焼けしないように\n気を付けなきゃね", 117101, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5190, "まだかな、弟くん\n水着のお姉ちゃんが\nお出迎えだよっ♪", 117101, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5191, "海が見えるおうちで\n弟くんと一緒に\n暮らしたいなぁ～", 117101, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5192, "弟くんの水着…\nどんな感じかなぁ\n…あ、想像してたら\nドキドキして…えへへ", 117101, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5193, "こーら、水着の\nお姉ちゃんは\nおさわり禁止♪\n…なんてね？", 117101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5194, "なぁに？\nふふっ、何回でも\n呼んでいいんだよ♪", 117101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5195, "海で迷子に\nならないように\n手を繋ごうね♪", 117101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5196, "お姉ちゃんが\n海の楽しみ方を\n教えてあげるねっ！", 117101, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5197, "わっ、弟くん\nどこ触ってるの？\nも～驚いちゃったぞ♪", 117101, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5198, "お姉ちゃん、海でも\nずっとキミを見てるよ\n…安心してね", 117101, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 5199, "まだ眠そうだね？\nほら、海が待ってるよ\n早く起きて起きて♪", 117101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 5200, "大丈夫、疲れてない？\n海で遊び疲れた時は\n素直に休憩しようね", 117101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 5201, "ほら、早く寝ないと\n明日もお姉ちゃんと\n遊べないぞ♪", 117101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5203, "だらけたお姉ちゃんは\n弟くんには\n見せられないな～", 117101, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5204, "ふふっ、かわいいね♪\n弟くんが持つと\nもっとかわいいよっ♪", 117101, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5205, "いい香りだね…\n一味違うお姉ちゃんに\nなれそう…♪", 117101, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5206, "あったか～い♪\nほら、弟くんも\nおいでよ♪", 117101, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5208, "よく食べて、いつでも\n弟くんの呼び声に\n応える準備を…！", 117101, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5209, "弟くんの水着の写真\nこの辺りに隠して\nおこうかな", 117101, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5211, "この子の背に乗って\nお姉ちゃんと弟くんの\n楽園へ…な～んて♪", 117101, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5212, "海を感じる感触だね\n弟くんとの楽しい夏を\n思い出しちゃうな…♪", 117101, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5213, "この夏、誰よりも\nキラキラしてみせる！\nだって私は\nアイドルだから！", 117201, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5214, "日傘にアームカバー\n帽子にサングラス…\n紫外線対策は完璧！", 117201, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5215, "とっておきの\nサマーソングを\nみんなに届けるよ！", 117201, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5216, "なんにもしないのって\n苦手かも…\n彼、早く来ないかな？", 117201, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5217, "ラムネって\n夏って感じするよね\n後で彼と飲もっと♪", 117201, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5218, "夏は恋の季節！\n燃えるような恋…\nいつかしたいな", 117201, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5219, "今年の夏の目標は\n…彼との思い出を\nいっぱい作ること！", 117201, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5220, "私の水着写真集\n見てくれたんだ！\nどう、かわいかった？", 117201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5221, "スイカ割りしたいの？\nいいね、楽しそう！\nみんなも誘おうよ♪", 117201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5222, "ねぇ、見て見て！\n水着の映える\nポーズ考えたんだ！", 117201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5223, "ライブ中に\n花火を打ち上げたら\n絶対盛り上がるよね！", 117201, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5224, "最近、忙しいけど…\nキミの顔見たら\n疲れなんて\n消えちゃった！", 117201, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5225, "この夏もいーっぱい\nドキドキさせるから！\n覚悟しててよね♪", 117201, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 5226, "外は朝から暑いね\nけど、負けないから！\nお仕事、頑張るぞー！", 117201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 5227, "自分へのご褒美に、\nアイス食べちゃお♪\nキミも一緒に食べよ！", 117201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 5228, "夏の夜って\nワクワクするね♪\nホタル見に行こうよ！", 117201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5230, "ふわぁ…眠い…\n仕事とかの疲れ、\nたまってるのかな…？", 117201, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5231, "私のぬいぐるみが\nグッズになったら…\n彼にかわいがって\nもらえるかな？", 117201, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5232, "この香り…\n夏限定のやつでしょ？\nいいセンスしてるね♪", 117201, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5233, "暑～い！\nけど…汗かいて\n気持ちいいかも！", 117201, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5235, "レッスンの後のご飯は\n特別おいしい♪\n…もうひとつだけ\n食べちゃおっかな", 117201, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5236, "あ、この前撮影した\n水着グラビアだ\nうんうん、いい感じ！", 117201, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5238, "この子、もふもふだね\n触るのクセになりそう\nもふもふもふもふ…", 117201, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5239, "わ、かわいい♪\n今度のグラビア撮影で\nこれ使ってもいい？", 117201, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5240, "水着っていいですよね\n海に浮かぶ一輪の花\nって感じがして！", 117401, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5241, "自分の水着を作るのも\n誰かの水着を作るのも\n楽しいですね♪", 117401, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5242, "水着の最終チェック！\n伸縮性や動きやすさは\n…いい感じですね！", 117401, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5243, "振り付けや衣装作りに\n歌や踊りの練習…\n…やることがいっぱい", 117401, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5244, "はぁ…レイ様の水着…\n私に作らせて\nもらえないかなぁ～…", 117401, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5245, "騎士さんのことは\n気に入らないですけど\n別に嫌いという\nわけじゃなくて…", 117401, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5246, "騎士さん暇そうですね\nに、荷物持ちとして\n誘ってみようかな…", 117401, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5247, "騎士さんいいところに\nこの衣装\nどう思います？", 117401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5248, "何見てるんですか？\nもしかしてこの水着の\n良さが分かります！？", 117401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5249, "真夏でも涼しい…\nそれも水着の良さの\n一つですね", 117401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5250, "仕立て屋も\nアイドルも\n諦めません！", 117401, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5251, "夏と言えば\nバカンスですけど…\n予定、あるんですか？", 117401, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5252, "う～ん…騎士さんを\n海へ誘うには…って\nなんでもないですよ！", 117401, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 5253, "ふー…夏はやっぱり\n朝が一番過ごしやすい\nですね～！", 117401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 5254, "暑い時こそ海です！\n体力づくりのためにも\n泳ぎにいきますよ！", 117401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 5255, "外で虫が鳴いてる…\n夏って感じがして\n風流ですねぇ…", 117401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5257, "少し休憩です…\nこのまま寝ないよう\n気をつけないと…", 117401, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5258, "か、かわいい…！\nこの感動を次の\nステージ衣装にも…！", 117401, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5259, "いい香りですね～\n波の音が聞こえて\nきそうです…", 117401, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5260, "泳いだ後は\n少し寒いので\nちょうどいいですね", 117401, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5262, "夏バテしないように\n栄養はしっかり\n取らないと！", 117401, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5263, "次のトレンドは…\nふむふむ、やっぱり\nこう来ましたか…", 117401, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5265, "デザインはもちろん\n触り心地も\n最高ですね♪", 117401, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5266, "夏の撮影スポットに\n活用できそうですね\nさっそく衣装考えて…", 117401, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 5267, "えへへ…♪\n団長さんが初めての\n友達で本当に良かった", 104001, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 5268, "あっ、団長さん！\n今日のＢＢ団の活動\nなんですけど…二人で\n大縄跳びしませんか！", 104001, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5269, "この衣装で\nライブするのは\n恥ずかしいな…", 117301, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5270, "せっかくの夏だし\n肝試しに\n行ってみたいかも…", 117301, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5271, "歌もダンスも\nより上を目指すために\n夏合宿をしよう…！", 117301, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5272, "夏の暑さにも負けない\n熱いライブに\nしてみせる…！", 117301, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5273, "新曲のアイデア\nまとめたいな…\n少し海辺を散歩しよう", 117301, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5274, "かき氷、食べたいな\n…あの人は何味が\n好きなんだろう？", 117301, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5275, "水着、似合ってるって\n言ってもらえるかな…", 117301, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5276, "サインして、ですか？\nいいですけど…\n少し照れますね…", 117301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5277, "少し待ってください\n今、新曲の楽譜を\n書いているので…", 117301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5278, "なんでしょう？\nもしかして…\nボイトレに興味が？", 117301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5279, "今度のライブでは\n夏らしい爽やかな歌を\nファンにお届けします", 117301, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5280, "…水鉄砲で遊ぼう？\nふふ。楽しそうですね\nお手柔らかに…♪", 117301, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5281, "あなたの声を\n聴いていると、\n何だか落ち着きます…", 117301, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 5282, "今日も暑くなりそう…\nお互いしっかりと\n水分補給しましょうね", 117301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 5283, "お腹空きましたよね\n海の家で、\nなにか買ってきます", 117301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 5284, "夜の海で月明かりと\n波の音を楽しもうかと\n…一緒に行きますか？", 117301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5286, "レッスンだけでなく\nリラックスすることも\n大事、ですよね", 117301, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5287, "ふふ、かわいい\n精霊も負けないくらい\nかわいいですけどね", 117301, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5288, "夏らしい爽やかで\nいい匂い…あなたに\nピッタリな香りですね", 117301, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5289, "海に入った後は、\n体が冷えてしまいます\n一緒に温まりましょう", 117301, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5291, "アイドルとして、\n食べすぎには\n気をつけないと…", 117301, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5292, "夏は恋の季節…\nガンガン攻めるべし…\nなるほど…", 117301, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5294, "この子に乗りながら\nライブをする…\nというのはどうですか", 117301, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5295, "まるで人魚になった\n気分…なんて、ふふ", 117301, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5296, "お姉ちゃんとして\nシオリンにかっこいい\nとこ見せなくっちゃ☆", 118301, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5297, "いつかシオリンと\n一緒に空を飛んで\n空中散歩してみたいな", 118301, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5298, "すこし眠くなってきた\nかも…シオリンと\nお昼寝しようかな", 118301, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5299, "お散歩してこよっと！\nシオリンとお出かけ\nするのにいい場所\n探さなくっちゃ！", 118301, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5300, "シオリンに過保護って\n言われちゃった…♪\nそれでも心配しちゃう\nのが姉心…だよね？", 118301, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5301, "気持ちいい日差し！\nシオリンと彼と、\nお昼寝したら\n気持ちよさそ～♪", 118301, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5302, "今度天気がいい日には\nシオリンと彼を誘って\n一緒にお出かけ\nしたいな～♪", 118301, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5303, "今日はご機嫌なんだ♪\nだって、シオリンの\n調子がいいんだもん☆", 118301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5304, "今からシオリンと\nピクニックに\n行くんだ♪　そうだ！\nキミも一緒にどう？", 118301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5305, "わっ、びっくりした！\nシオリンから借りた本\n読むのに集中\nしてたから…", 118301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5306, "うん？　どうしたの？\nあっ、もしかして\nこの服、気になる？", 118301, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5307, "シオリンもキミも元気\nいっぱいだと、私も\n元気になっちゃうよ☆", 118301, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5308, "シオリンの前だけ\nじゃなくて、キミにも\nかっこいい私の姿を\n見てほしいな☆", 118301, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 5309, "おはよう！　えへへっ\n今日は早起きなんだ♪\nシオリンにかっこ悪い\nとこ見せたくないから", 118301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 5310, "おなかすいたなぁ～…\nシオリンと一緒に\nおいしいもの食べに\n行こうかな！", 118301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 5311, "もうこんな時間！？\nシオリンと一緒だと\n時間が早く\n過ぎていっちゃうね！", 118301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5313, "この快適な寝心地…！\nシオリンにもおすすめ\nしなきゃ…！", 118301, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5314, "わあ、かわいい！\nそれにさわり心地も\nいいし、抱き枕に\nちょうどいいかも！", 118301, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5315, "いいにおい…♪\nなんだかリラックス\nできて…すやぁ…", 118301, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5316, "ぽかぽか～☆\nシオリンと一緒に\nぬくぬくしたら\n気持ちよさそう♪", 118301, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5318, "仲のいい人が一緒だと\nつい食が進んじゃうね\nえへへ…♪", 118301, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5319, "ふむふむ…この本…\nシオリンが好きかも！\n今度おすすめしよっと", 118301, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5322, "お姉ちゃん、\n私のために無理は\nしてないかな…？", 118401, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5323, "新しく買った絵本…\nこういうのなら\nお姉ちゃんと楽しく\n読めるよね", 118401, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5324, "ニャットちゃんを\nここに連れてきて\nみんなで遊べないかな", 118401, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5325, "本屋さんに行こうかな\nみんなで読める\n面白い本が\nあるといいなぁ", 118401, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5326, "たまには…読むだけ\nじゃなくて、自分で\nお話を書いて\nみようかな…？", 118401, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5327, "時々…お姉ちゃんと\nあの人が似てるって\n思うことがあります…", 118401, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5328, "お姉ちゃんとあの人…\n優しくて頼もしい\n二人に囲まれて…\n私、幸せ者だなぁ…", 118401, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5329, "どうかしましたか？\nもしかして…あなたも\n一緒にこの本を\n読みたい…とか？", 118401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5330, "けほっ…けほっ…\nあっ、心配いりません\n飲み物を飲んで\nむせただけですから", 118401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5331, "この格好…\n似合ってますか…？\nえへへ、照れますね…", 118401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5332, "なぁに、お姉ちゃん？\n…あっ、すみません\n読書に集中していて…\n間違えちゃいました…", 118401, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5333, "お姉ちゃん、時々無理\nしちゃうから…\nあなたも一緒だと\n安心できます", 118401, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5334, "お姉ちゃんとあなたと\n頼れる人に囲まれて\n私は幸せものですね", 118401, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 5335, "なんだか朝から\n調子がいい…今日は\nいい日になりそうです", 118401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 5336, "せっかくだから、\nお姉ちゃんを誘って\nお外でランチも\nいいかも", 118401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 5337, "お姉ちゃんに心配\nかけないように…もう\nちょっと本を読んだら\n寝なくちゃね", 118401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5339, "ふんわり柔らかくて\n気持ちいい手触り…\nお姉ちゃんが寝るのに\nちょうどよさそう", 118401, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5340, "ふふっ、かわいい♪\n絵本の物語みたいに\nおしゃべりできない\nかな…？", 118401, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5341, "素敵な落ち着く香り♪\nなんだかお姉ちゃん\nみたい、ふふっ", 118401, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5342, "あったかくてほっと\nするなぁ\nここでゆっくり\n読書しようかな", 118401, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5344, "この味…\nお姉ちゃんが好きそう\n少しもらって、\n持って行こうかな…？", 118401, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5345, "この本みたいな、\n心躍る大冒険も\nお姉ちゃんと一緒なら\nできちゃいそう", 118401, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5348, "いい部屋ね\nここを私の分院に\nするのもいいかも\nしれないわね…♪", 117501, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5349, "珍しいカンポーが\n手に入ったから\n色々試してみたいわ\n色々と、ね♪", 117501, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5350, "この服を着ると\n自分が医者だって\nより実感するわね\nまあ、闇医者だけどね", 117501, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5351, "新薬開発のために\n薬草を採取して\nおこうかしら", 117501, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5352, "いつ急患が来ても\n対応できるように\nしておきましょうか", 117501, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5353, "あの子の研究資料も\nだいぶ増えてきたわね\nちゃんとまとめて\nおかないと♪", 117501, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5354, "あの子は大事な\n研究対象。だから\nいつまでも健康で\nいてもらわないとね♪", 117501, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5355, "薬に興味があるの？\nなら、今度ゆっくりと\n教えてあげるわね", 117501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5356, "少し時間があいたから\n一緒に新薬の実験を\nしましょうか♪", 117501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5357, "どうしたの？\nまさか急患…！？\nあら、違うのね\nならよかったわ", 117501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5358, "具合が悪い時はすぐに\n言うのよ。君には\nいつでも健康でいて\nほしいから", 117501, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5359, "少し仮眠したいわね\n肩…いいえ、膝を\n貸してもらえる？", 117501, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5360, "たまには…君の隣なら\n闇医者も悪い研究者も\nお休みにするのも\n悪くないわね♪", 117501, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 5361, "たまには朝の陽ざしを\n浴びるのも…\n悪くないわね", 117501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 5362, "天気がいい日は\nいい薬草が採れるの\n君も集めるの、\n手伝ってくれる？", 117501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 5363, "いけない、もうこんな\n時間なのね\n診察に没頭すると\n時間感覚がなくなるわ", 117501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5365, "もう少し固い方が\n私好みだけど…\n悪くない寝心地ね", 117501, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5366, "このぬいぐるみ…\n闇医者として\n興味深いわね…", 117501, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5367, "落ち着く香りね\n私も診察室で\n使ってみようかしら", 117501, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5368, "冷えは病につながるわ\nしっかり温めて\n体をいたわりましょ", 117501, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5370, "おいしい♪\n研究のお供に\nもってこいの\n味だわ", 117501, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5371, "医学は日進月歩…\n闇医者とはいえ\n最新の論文には\n目を通しておかないと", 117501, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5400, "白衣の天使かあ\nまさにボクのための\n呼び方だね", 117601, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5401, "美しさとは\n日々の努力が\n作るものなんだよ", 117601, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5402, "大変だ…\nボクは美しく\nなりすぎたかも", 117601, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5403, "ボクのかわいさで\n癒やされて\nみんな健康に\nなっちゃうね", 117601, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5404, "どんな服装をしても\nボクの美しさは\n変わらないね", 117601, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5405, "これからはボクも\n美の伝道師として\nがんばらないとね♪", 117601, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5406, "ボクのかわいい笑顔を\n近くで見る以上の\n癒やしはないよね♪", 117601, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5407, "どう？　看護師姿の\nボクもかわいいでしょ\nもっと近くで見る？", 117601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5408, "ほら！　美しいボクの\n看護師姿を見たら\n元気でちゃうでしょ？", 117601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5409, "白衣の天使のボクも\n最高に美しいね\n存分に癒されてよ♪", 117601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5410, "具合が悪いの？\nならボクの美しい顔を\n処方してあげる♪", 117601, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5411, "さあ今日もボクと\n一緒に美の追求に\n付き合ってもらうよ♪", 117601, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5412, "かわいい天使のボクが\nいつもそばにいるんだ\nキミって幸せ者だね♪", 117601, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 5413, "さすがボク\n目が覚めるような\n美しさでしょ？", 117601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 5414, "美は食で作られる\n美容にいいものを\n食べようよ！", 117601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 5415, "ふああ…早く寝よ？\n夜更かしは美容の\n大敵だからね", 117601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5417, "ふう～…\nリラックスする\nボクも美しい", 117601, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5418, "ボクが抱きしめると\n普段よりかわいく\n見えちゃうね♪", 117601, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5419, "美しいボクは\n常にいい香りを\nしていなくちゃね", 117601, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5420, "光がボクを照らしてる\nんじゃなくて、ボクが\n光を輝かせてるのさ", 117601, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5422, "バランスのいい食事が\n美しいボクを\n作るのさ", 117601, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5423, "ふむふむ…なるほど\nこうすればより美しく\nなれるかもしれない", 117601, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 5425, "仲間のためなら\nきっと…どこまでも\n強くなれるよね", 105701, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 5426, "プリンセスフォーム？\n私と同じような力を\n持ってる人がいるの？\n会ってみたいな～！", 105701, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5427, "ここの空気は、\nどうもゆるすぎて…\n落ち着きませんわね", 118001, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5428, "陛下のお越しになる\n別宅としては、\n少し警備が\n甘すぎでは？", 118001, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5429, "あらあら？\nサッシにホコリが！\nちょっとネコ助！\n掃除は念入りに！", 118001, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5430, "いつまで入り浸って\nいるのかと、\n秘書に叱られて\nしまいましたわ…", 118001, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5431, "もういっそ、\nここに間借りして\n仕事しようかしら…？", 118001, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5432, "邪魔している身ですし\n家事くらい\nお手伝いさせなさいな", 118001, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5433, "本当は、意外と\n居心地が良いのかも…\n陛下の気持ち、\n少し分かりましたわ", 118001, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5434, "ちょっ…わたくしを\n叩いても、ホコリなど\nあまり出ませんわよ！", 118001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5435, "陛下っ？\nなんだ…貴方でしたの\n用件は手短にお願いね", 118001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5436, "別に、好き嫌いは\nありませんけれど…\n魔物料理って、意外と\n美味なものですわね", 118001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5437, "言っておきますけれど\nわたくしは商売人です\n荒事は専門外ですわよ", 118001, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5438, "プリンセスナイトの\n能力…なにか儲け話に\n使えませんかしらね", 118001, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5439, "貴方がたのことも、\n少しくらいは\n信用してないことも\nありませんでしてよ", 118001, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 5440, "おはようございます\nさ、今日もバリバリ\n働きますわよ！", 118001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 5441, "お昼ですわね\n栄養補給をしつつ\n働きますわよ！", 118001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 5442, "定時ですって？\n関係ありません！\nわたくし、まだまだ\n働きますわよ～っ！", 118001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5444, "うっ…いけませんわ\n日頃の疲れが…\nふわぁ…", 118001, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5445, "もふもふ…ふふっ♪\n…なんですのっ、\nなにかおかしくて！？", 118001, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5446, "いい香りですわね\nわたくしの執務室にも\n置きたいくらいですわ", 118001, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5447, "いけませんわ…\nこんなに暖かいと\n日頃の疲れが…ふわぁ", 118001, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5449, "もう一つ食べたら\n仕事に戻りますわよ？\nあと一つだけ…", 118001, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5450, "商人にとって\n知識は武器ですわ\n陛下のため、貴方も\nもっと勉強なさい？", 118001, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5452, "故郷のお菓子を\nいろいろ作ったんだ～\nみんなに\n配ってくるさ～♪", 117701, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5453, "彼ってどんな仮装が\n似合うかな～？\nシーサーとか？", 117701, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5454, "カボチャチャンプルー\n作ってみよっと！\nきっとおいしいさ～♪", 117701, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5455, "視線を感じるさ～\nもしかして幽霊？\nおしゃべりするさ～♪", 117701, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5456, "さっき作った\nサーターアンダギー、\n早く彼に\n食べてほしいさ～♪", 117701, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5457, "彼のために、\n特別なイタズラを\n考えとくさ～♪", 117701, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5458, "いつか彼と、\n故郷のハロウィンを\n楽しみたいさ～♪", 117701, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5459, "ハロウィンは\n私たちが守るさ～♪\nさ、見回りに行こ！", 117701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5460, "この制服、\nハロウィンらしくて\n大好きさ～♪", 117701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5461, "イタズラする気ね～？\nどんなことされるのか\n楽しみさ～♪", 117701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5462, "カボチャのランタン、\n一緒に作ろ！\n絶対楽しいよ～♪", 117701, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5463, "お菓子くれるの？\nあは～、ありがとー♪\nもぐもぐ…\n優しい味がするさ～♪", 117701, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5464, "キミと一緒に過ごす\nハロウィンは、\nドキドキとワクワクが\n止まらないさ～♪", 117701, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 5465, "ハロウィンは\n朝から大忙しさ～！\nお菓子作り、\n手伝ってほしいさ～", 117701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 5466, "お化け屋敷に行こ！\n一緒にいっぱい\nドキドキしようね～♪", 117701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 5467, "ここからが\nハロウィン本番さ～！\n幽霊たちと\nいっぱい遊ぶさ～♪", 117701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5469, "ちょっと休んだら、\nお菓子作りを\n再開するさ～♪", 117701, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5470, "でーじかわいいね～♪\nこの子に仮装させたら\nきっと似合うさ～♪", 117701, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5471, "お菓子みたいに\n甘い匂い…\nハロウィンらしい\nいい香りさ～♪", 117701, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5472, "ランドソルの秋って\n冷えるけど…\nこれがあれば\n体も心もぽかぽかさ～", 117701, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5474, "故郷のご飯も\nおいしいけど、\nこっちのご飯も\nでーじおいしいさ～♪", 117701, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5475, "パンプキンパイの\nレシピは…なるほど～\n勉強になるさ～♪", 117701, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5477, "全部おいしそうで\nどれから食べるか\n迷っちゃうさ～♪", 117701, 3, 12);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5479, "ムっ！　幽霊の気配を\n感じるデス！\nさあ姿を現すデス！", 117801, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5480, "ムムム…\n妖魔の力を忍法に\n使えないでショウか…", 117801, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5481, "怪しい妖魔を\n成敗するデス！\nハロウィンの平和は\nワタシが守りマスよ！", 117801, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5482, "ワタシの祭囃子が\n聞こえマスかー？\nさあ集まるデース！\n陽気に踊りまショウ！", 117801, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5483, "お祭りは\n楽しんだ者勝ちデス！\n踊らにゃ\nソンソンデス！", 117801, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5484, "幽霊になる前に\n天下統一を\n果たしたいデスね", 117801, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5485, "あの世に行っても\nショーグンは\nワタシの大事な\nショーグンデス！", 117801, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5486, "ショーグン！\n悪霊退治に\n出かけるデース！", 117801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5487, "妖魔を家来に\nしてみるのは\nどうデス？", 117801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5488, "マントを使って\nウツセミの術の精度を\n高めるデス！", 117801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5489, "ハロウィンにちなんだ\n新しい忍法…\nすなわち妖忍法の\n開発を急ぐデス！", 117801, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5490, "あの世でも\nショーグンに\nお仕えしマスよー！", 117801, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5491, "ショーグンもワタシと\nハロウィンパレードを\n踊り明かしまセンか？", 117801, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 5492, "おはようございマス！\n天下統一に向けて\n急いで準備するデス！", 117801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 5493, "お腹減ってまセンか？\nハロウィンスイーツで\n休憩するデ〜ス", 117801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 5494, "夜更かしすると幽霊が\nでるかもしれまセン…\nヒュ〜ドロロロ〜…", 117801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5496, "もふもふなところが\nわたあめちゃんに\n似ていマス。わんっ！", 117801, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5497, "ぬいぐるみや人形には\n魂が宿るという\n言い伝えがありマス…\nマカフシギデ〜ス", 117801, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5498, "ウ〜ム…\nなんとも怪しい\n香りがしマスね！", 117801, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5499, "パレードの熱気に\n負けないくらい\n熱いデス！", 117801, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5501, "ついつい食べすぎて\nしまうのは\n妖魔のせいデスか！？", 117801, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5502, "トーゴク妖怪の\n図鑑デス！　個性的な\n妖怪ばかりデスね！", 117801, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5504, "ハロウィンの甘味を\n集めまシタよ！\nショーグンへの\n貢ぎ物デス！", 117801, 3, 12);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 5531, "彼と二人で\n買い物に…って…\nこれってデートに\nなっちゃうのかな！？", 101301, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 5532, "とうっ！　どうどう？\n今の決めポーズ！\nかっこいいよね♪", 101301, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5533, "ヒデサイとおそろの\nハロウィンコスとか\nしてみたいな～", 117901, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5534, "カボチャ料理かぁ…\n作ったらヒデサイ\n食べてくれるかな～", 117901, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5535, "ハロウィンスイーツ…\nどれもおいしすぎて\n激ヤバなんですけど！", 117901, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5536, "霊界ってどんなとこ\nなんだろ～\nわりと近所だったり？", 117901, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5537, "服をハロウィンっぽく\nアレンジするのも\nたのしーよね♪", 117901, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5538, "ヒデサイと一緒に\nハロウィンパーティー\n…えへへ、楽しみ～", 117901, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5539, "ヒデサイといると\nドキドキする…\nなんでだろ～？", 117901, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5540, "ねねね、うちらも\nハロウィンパレード\n見に行こー？", 117901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5541, "ハロウィンセイバーズ\nしゅつどーう！\nヒデサイも行こ～♪", 117901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5542, "マントのロゴ\nかっこいいっしょ～？\nカボきちってゆーんだ", 117901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5543, "トリックオア\nトリート！\nねえねえヒデサイ\nお菓子ちょうだい♪", 117901, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5544, "ヒデサイも\nメイクしてみる？\nハロウィンぽいやつ♪", 117901, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5545, "ヒデサイのこと\nうちがいつでも\nあっためたげるね！\n…あ！　手、手の話！", 117901, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 5546, "ハロウィンのイベント\n朝からでも\nやってるかな～？", 117901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 5547, "カボチャでおやつ\n作ってみたんだ～\nヒデサイ食べてみて！", 117901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 5548, "ハロウィンの夜って\nやっぱテンション\n上がるよね～♪", 117901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5550, "超ふかふかしてる～\nちょっと休憩させて\nもらっちゃお", 117901, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5551, "はぁ…\nもにカワすぎて\nつらいんですけど～！", 117901, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5552, "ハロウィン限定の\nフレグランスじゃん！\nいい匂いする～♪", 117901, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5553, "やばばば…\nあったかくて\n眠くなっちゃう～", 117901, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5555, "これ食べて\nもうひとがんばり\nしよーっと！", 117901, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5556, "ハロウィンっぽい\nファッションとか\n載ってないかなぁ？", 117901, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5558, "お菓子いっぱいだ～！\nあとでヒデサイにも\n持ってってあげよ～", 117901, 3, 12);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5560, "お掃除が終わったら\n次は書類を片付けて…\nその次は…", 118501, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5561, "皆さんが冒険している\n間の留守は私に\nお任せください♪", 118501, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5562, "美食殿の皆さんが\n持ってきてくれる\nお土産が楽しみです♪", 118501, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5563, "ネビアさんとは\nときどき一緒にお茶\nする仲なんです", 118501, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5564, "ギルドハウスの乱れは\nギルドの乱れ\n整理整頓が大切です", 118501, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5565, "素敵なギルドハウスの\n管理人ができて\nとっても幸せです♪", 118501, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5566, "皆さんをほんの少し\n特別扱いしちゃう\n私がいます", 118501, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5567, "何かわからないことが\nあるときは私に\n聞いてくださいね", 118501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5568, "書類を書いていると\n落ち着きます…\n一緒にどうですか？", 118501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5569, "あなたのまわりは\nいつも賑やかで\n楽しいですね♪", 118501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5570, "あっ！　お頼みしたい\nことがあって\n探してたんです♪", 118501, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5571, "ギブアンドテイクは\n大切です。たまには\nお礼させてください♪", 118501, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5572, "陰に日向にあなたを\n支えていきます♪\n期待していてください", 118501, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 5573, "今日も一日\n管理人として\nがんばります♪", 118501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 5574, "お腹がすきました…\nそろそろお昼に\nしませんか？", 118501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 5575, "もうこんな時間…\n皆さんといると一日が\nあっという間です", 118501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5577, "良質なお仕事は\n良質な休憩から\nです♪", 118501, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5578, "かわいいものに\n囲まれてお仕事できる\nのは幸せですね♪", 118501, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5579, "お仕事に集中できる\nアロマを設置するのも\nよさそうですね♪", 118501, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5580, "ポカポカ温まりながら\n書類仕事をするのも\nいいものですね…", 118501, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5582, "栄養補給しないと\n頭が回りませんからね\nきちんと食べましょう", 118501, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5583, "新条例はこのように\n活用されている、と…\nふむふむ…", 118501, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5585, "会合用の資料と\n連載のネーム…\nどっちを片付けよう？", 106701, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5586, "イノリちゃん、また\nお金くすねてる…\n帰ってきたら\nお尻ペンペンだ♪", 106701, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5587, "カヤちゃんの装備\n傷むの早いなぁ〜\nメンテしてあげないと", 106701, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5588, "そうだ、ラーゴのご飯\n買いに行かないと\n今日は何がいいかな？", 106701, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5589, "ここの人たちは見てて\n飽きない人ばっかり♪\n漫画の参考にしよ〜", 106701, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5590, "また一緒に買い物にも\n行きたいし、冒険も\nしたいな〜…♪", 106701, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5591, "…君は見てるだけで\n満足なの？ ちゃんと\n触って欲しいな…♪", 106701, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5592, "私の目が見たいの？\nいいけど…魅了されて\n魂が壊れちゃうよ…？\n…冗談で〜す☆", 106701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5593, "ラーゴがね、君に\n遊んで欲しいって☆\n仲良くしてあげてね", 106701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5594, "アシスタントの仕事\n気に入ってくれたなら\nまたお願いしていい？", 106701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5595, "私が覚醒できるか？\n見せてあげたいけど…\nすごいことになるから\nまた今度で♪", 106701, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5596, "楽しいトラブルが\nあったら、とりあえず\n声はかけて欲しいな♪", 106701, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5597, "マッサージお願い♪\n変なところは…\n少しだけなら☆", 106701, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 5598, "おはよう〜♪\n今日も一日、一緒に\nがんばっていこうね", 106701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 5599, "お腹空いちゃった…\n一緒にお昼行かない？\nいいお店があるんだ〜", 106701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 5600, "君、眠れないの？\n誰かに見られてる？\n…きっと気のせいだよ", 106701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5602, "ちょっと休憩〜…\nこのまま原稿すれば\n疲れなくなるのかな？", 106701, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5603, "かわいい〜♪\nラーゴのお友達に\nなってくれる？", 106701, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5604, "いい香り〜♪\nこの目でどう見えるか\n伝えられたらいいのに", 106701, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5605, "ほどほどなのが\nちょうどいい時も\nあるよね…♪", 106701, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5607, "この味付け…\nあっちでも再現\nできるかな？", 106701, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5608, "ふふっ、面白い♪\nイノリちゃんの買う\n漫画にハズレなし☆", 106701, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5610, "私…23歳だもん\n魔法少女だなんて\n名乗れないわ…", 119001, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5611, "あん、どぅ、とろわ〜\nみんなイイ子に\nな〜る♪", 119001, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5612, "あっ！　イケナイ子の\n気配がするわ！\n風紀の乱れは\n先生が許しませんっ！", 119001, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5613, "ここで闇の力が\n発動したら大変だわ！\nやだっ…そう思ったら\n緊張して…ダメぇ〜！", 119001, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5614, "オイタした子はみんな\nどうしてうれしそうに\n私の愛のムチを\n欲しがるのかしら…？", 119001, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5615, "あの子に次は何を\n教えてあげようかしら\n愛？　希望？\nそれとも…♪", 119001, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5616, "あの子のことを\n考えると、愛と希望で\n胸がいっぱいになるの", 119001, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5617, "先生なのに闇の力を\n持ってるのは秘密よ？\n他の子には\n知られたくないから…", 119001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5618, "秘密の授業の内容？\nそれは…あんなことや\nこんなことよ♪\nそれから、それから…", 119001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5619, "しーっ！　私の正体が\nルーセント学院の\n先生だって、ここでは\n秘密にしててね？", 119001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5620, "あら？　補習して\nほしいの？　いいわよ\n君のお願いなら\nなんでも聞いちゃう♪", 119001, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5621, "イイ子にはごほうびを\nあげなくちゃ！\n君はなにが欲しい？\n遠慮しちゃダメよ？", 119001, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5622, "君との秘密が\nまた一つ\n増えちゃったね♪", 119001, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 5623, "今日も一日\n元気に過ごしましょう\n先生が指導してあげる", 119001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 5624, "夜まで頑張れるように\nお昼ご飯はしっかり\n食べましょう〜！", 119001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 5625, "夜更かしはダメよ？\nイケナイ子が\nすることです！", 119001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5627, "闇の力に振り回されて\nもうヘトヘトよ〜\n心も体も\n癒されたいわ〜", 119001, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5628, "『秘密の学び舎』に\nこっそり連れて\n行っちゃおうかしら♪", 119001, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5629, "イケナイ甘い香り…\nなんだか胸の奥が\n熱くなってきたわ…♪", 119001, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5630, "やんっ♪\n燃え上がる愛みたい♪\n火照ってきちゃった♪", 119001, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5632, "生徒の見本に\nなれるように\n好き嫌いはしないの", 119001, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5633, "『学び舎に咲く禁断の\n愛』？　気になるわ…\nううん、ダメよっ！\nでも…ちょっとだけ…", 119001, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 5635, "次に会う時は、\nまたあそこに一緒に…\nえへへ…！", 106301, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 5636, "横顔見てるの、\nバレてないよね…？\n…ちらっ", 106301, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 5637, "背が少し伸びたか！？\nあ、あいつのところへ\n報告に行かねばっ！", 105301, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 5638, "私も貴公の隣に\n立っていられるよう\n日々精進していくぞ！", 105301, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5639, "ああ～\nドＭミストが\nあふれてしまいます～", 119101, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5640, "魔法少女として\nクウカ頑張ります～！\n愛と希望の妄想を…！", 119101, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5641, "決めポーズの練習より\nやられポーズの練習を\nしておきます…♪", 119101, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5642, "快感を溜めに溜めて\n放つドＭビームは\n最高ですよ～", 119101, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5643, "はっ！\nこれは闇の気配！\n…クウカ自身でした", 119101, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5644, "ドＳさん…ぐふふ…\nクウカを縛りあげ…\nじゅるりぃ～♪", 119101, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5645, "ドＳさんに攻められる\nことで、クウカは進化\nしてゆくのです～！", 119101, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5646, "はぅっ！\nもっと激しく…！\n情熱的に～～～！", 119101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5647, "クウカの変身シーンを\nじっくり眺めたい！？\nは、恥ずかしいです～", 119101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5648, "「本当に魔法少女か\n確かめてやるよ…！」\nドＳさんはそう言って\nクウカを…ぐふふ…♪", 119101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5649, "愛と希望の力が\nクウカの中に…\nこの胸の高鳴りが…？", 119101, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5650, "ひゃあんっ！\nそこです～っ！\nもっと、もっと～♪", 119101, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5651, "クウカをこんなに\n悦ばせるのは\nドＳさんだけです…♪", 119101, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 5652, "明るい時間に出歩くと\n目立ちますね\n魔法少女の衣装…", 119101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 5653, "お昼の見回りです～♪\nああ～うっかりドＭを\n増やしてしまいました", 119101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 5654, "夜！　それはクウカが\n活性化する時間です！\n闇の活動開始です～♪", 119101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5656, "気の緩んだクウカを\nドＳさんの鋭い牙が\n狙っています～♪", 119101, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5657, "魔法少女のマスコット\nにいいかもしれません\nもっと鎖とか飾りを…", 119101, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5658, "魅惑的な香りです～\nドＭミストの香りに\n似て…いや違いますね", 119101, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5659, "火照る体…紅潮した\nクウカの肢体を熱く\n見つめるドＳさん…", 119101, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5661, "食欲を満たした次は\n何を満たせば\nいいですかねぇ～♪", 119101, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5662, "魔法少女がひどい目に\n遭う物語は…\nなさそうですね…", 119101, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5664, "ケーキも悪くないけど\nプリンが一番なの\nでもせっかくだから\n両方もらってやるの！", 119901, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5665, "クリスマスって聞くと\nなんだかあったかい\n気分になるの～", 119901, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5666, "煙突がなくても\nミヤコはどこからでも\nおうちに入れるの♪", 119901, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5667, "イブもプリン！\nクリスマスもプリン！\nプレゼントもプリン！", 119901, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5668, "クリスマスツリーの\n高いところの\n飾りつけは、ミヤコに\nまかせろなの～！", 119901, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5669, "アイツのプレゼント\n楽しみなの～\nきっと最っ高に特別な\nプリンなの～♪", 119901, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5670, "ミヤコがサンタなら\nアイツはトナカイなの\nつまりお似合いって\nことなの～♪", 119901, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5671, "ミヤコ、プレゼントを\n配りにいくの～♪\nオマエも手伝えなの！", 119901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5672, "オマエにも飾りつけ\nしてやるの！\n頭にプリンを乗せると\nかわいいの♪", 119901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5673, "幽霊のミヤコが\nいるなら、サンタも\nいて当たり前なの～！", 119901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5674, "靴下にプリンを\n入れたらつぶれるの\nだからプリンは直接\nミヤコによこすの～！", 119901, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5675, "今日はプリンだけじゃ\n満足してやらないの！\nミヤコをたくさん\n甘やかせなの！", 119901, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5676, "ミヤコのプリン\nオマエになら一個…\nうう～、一口だけ\nあげてもいいの！", 119901, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 5677, "サンタの朝は早いの～\nミヤコも早起きして\nプレゼント準備したの\nとってもえらいの♪", 119901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 5678, "今日は一日中\nみんなと遊ぶの～！\nオマエも来いなの！", 119901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 5679, "みんなが寝たら\nチャンスなの～\nこっそりプレゼント\n配りにいくの♪", 119901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5681, "むふー、気持ちいいの\nこういうプレゼントも\nいいかもしれないの", 119901, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5682, "これ気に入ったの～\nプリンとアイツの\n次くらいなの♪", 119901, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5683, "いいにおいなの～\nうっかり成仏\nしちゃいそうなの…", 119901, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5684, "ここに飾っといた\n雪だるま\nなくなっちゃったの\nきっと成仏したの～", 119901, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5686, "これはオマエがくれる\nプリンとは\n別腹なの～", 119901, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5687, "プリンケーキ…\nこれならミヤコにも\n作れそうなの！\nさっそく作るの！", 119901, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5689, "びっくりしたの～！\n幽霊のミヤコを\n驚かすなんて…\nなかなかやるの！", 119901, 3, 14);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5690, "コイツの中…\nあったかいの～♪\n昼寝にぴったりなの～", 119901, 3, 15);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5691, "メリークリスマス！\n聖夜に笑顔を\nお届けするべ♪", 119201, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5692, "クリスマス前は\n乳製品がよく売れて\nありがてぇけんど…\n目が回る忙しさだ～", 119201, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5693, "牧場で売る\nクリスマスリースを\n作っておかねぇと…", 119201, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5694, "うちの動物たちにも\nクリスマスプレゼント\n用意しなきゃなぁ", 119201, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5695, "クリスマスに向けた\n限定商品とか\n作ってみるべか？", 119201, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5696, "あんちゃんへの\nプレゼント選び…\nここでボケるか…？\nやめておくべか…？", 119201, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5697, "オラ、あんちゃんから\n本当にたくさんの物を\nもらってんなぁ…", 119201, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5698, "雪遊びがしたいべか？\nへへっ、あんちゃん\nには負けねぇど！", 119201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5699, "ディナーと一緒に\n聖夜のお笑いライブを\nすんのはどうだべか？\nツッコミは任せたべ！", 119201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5700, "寒くなってくると\n鍋が食べたくなるべや\nあんちゃんはどうだ？", 119201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5701, "あんちゃんは\nサンタさんのこと\n信じてるだか？", 119201, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5702, "どでかいクリスマス\nケーキをこさえるべ\nあんちゃんも作るの\n手伝ってくんろ♪", 119201, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5703, "あんちゃんが\nオラの旦那だったら…\nって、あんちゃん！\n今の聞いてただか！？", 119201, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 5704, "牧場の朝は早いど！\nさぁてと、まずは\n雪かきしに行くべや", 119201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 5705, "あんちゃんも一緒に\nお昼食べてくか？\n採れたての冬野菜は\nうんまいど～♪", 119201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 5706, "よい子は早く寝ねぇと\nサンタさんが\n来てくれねぇべよ？", 119201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5708, "こういうのリンリンが\n好きそうだな…\nプレゼント候補に\n入れておくべ！", 119201, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5709, "もふもふしてて\nなまらめんこくて…\nあぁ…癒されるべや…", 119201, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5710, "この匂い…\n気持ちが和むべや\n夢心地になっちまうな", 119201, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5711, "あったけぇところは\n落ち着くなぁ…\nつい眠くなっちまう", 119201, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5713, "こ、この味は…！\n…うーん、もっと\nリアクション芸も\n練習しねぇとな", 119201, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5714, "シオシオって\nどんな本読んでんだ？\n…難しい字が\nぎっしりだべや…", 119201, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5716, "この仕掛け…\nオラのコントに\n活かせるかも\nしんねぇな", 119201, 3, 14);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5717, "この着ぐるみで\nリマリマのモノマネを\n練習してみるべか…？", 119201, 3, 15);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 5718, "もっとキレイになって\nアイツにぎゃふんと\n言わせてやるんだから", 105001, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 5719, "アタシのこと\nどう思う？\nかわいいって…そこは\nキレイって言ってよね", 105001, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5720, "ハートマークの刺繍…\nは、やりすぎですかね\nいやちょっとくらい\nアピールしてみても…", 119301, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5721, "雪降らないかな～\nそしたらお兄ちゃんと\n雪だるま作ったり\nできるのに～", 119301, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5722, "プレゼントを入れる\n靴下…いくつ編んでも\n足りません～！", 119301, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5723, "お兄ちゃんとの\nクリスマスデートは\n早い者勝ちですからね\n負けませんよ～！", 119301, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5724, "クリスマスだし…\n受験勉強は今日だけ\nお休みです♪", 119301, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5725, "お兄ちゃんと\nおそろいのコーデで\nクリスマスデート…\nしたいなぁ～！", 119301, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5726, "お兄ちゃんに\nプレゼントする編み物\nこれで３着目～と♪\nあれ、作りすぎ？", 119301, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5727, "えへへ、もっと近くで\n見たくありませんか？\n私のとっておきの\nクリスマスコーデ♪", 119301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5728, "お兄ちゃんが風邪を\n引いたら一大事です！\n私の編み物で暖かく\nしてあげます♪", 119301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5729, "お兄ちゃん、ほっぺに\nケーキのクリームが\nついちゃってますよ♪\n私がとってあげます！", 119301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5730, "お兄ちゃん！\nケーキ、あーんって\nしてあげます！\nほら、あーん♪", 119301, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5731, "クリスマスは大切な\n人と過ごすものっ！\nお兄ちゃん…妹の予定\n空いてますよっ！", 119301, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5732, "クリスマスはずっと、\nお兄ちゃんと一緒に\nいたい…って言ったら\n欲張り…すぎですか？", 119301, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 5733, "早起きは３本お得！\n…クリスマスなら\n追加で10本ぐらい\nもらえちゃうかも！？", 119301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 5734, "お昼はケーキ？\nそれとも七面鳥？\nごちそういっぱいで\n迷っちゃいますね～♪", 119301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 5735, "お兄ちゃんと\nクリスマスに夜更かし\n…えへへ、ちょっぴり\nドキドキしちゃう♪", 119301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5737, "編み物をするには\n絶好のくつろぎ空間\nですね～♪", 119301, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5738, "なんというかわいさ…\nクリスマス\nプレゼントに\nもってこいですね！", 119301, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5739, "これがお兄ちゃんの\nお部屋の香り…？\nんん～！　ずっと\nかいでいたいです～♪", 119301, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5740, "ふぁ、あったか～い…\n冬場はこういうのが\nありがたいですね～♪", 119301, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5742, "ごちそうだぁ～♪\nほら、お兄ちゃんも\n食べて食べてっ！", 119301, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5743, "クリスマスデートの\n必勝法…こ、これは\n読むしかないです！", 119301, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5745, "わっ、すごいです～！\nこれを飾っておけば\nクリスマスが一層\n賑やかになりますね♪", 119301, 3, 14);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5746, "さぷらーいずっ！\nリマちゃんじゃなくて\nリノちゃんでした～！", 119301, 3, 15);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5747, "ミミにもミソギにも\nあの人にも頼りに\nしてもらえるように\nしっかりしなきゃ！", 120601, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5748, "ミミはにんじんを\n食べられてすごいな…\n私も見習わなきゃ\nがんばろう…！", 120601, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5749, "ミソギから怖い話\n聞いちゃった…\nうぅ…誰か一緒に\nいてくれないかな", 120601, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5750, "立派な大人に\nなれるように\nいっぱい勉強\nしておかなくっちゃ！", 120601, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5751, "なんだか…あの人の\nあやしい視線を\n感じるような…？", 120601, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5752, "あの人に勉強を\n教えられるように\nしっかり予習を\nしておこうっと…！", 120601, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5753, "今日はあの人と\n一緒に冒険できる…\nなんだかワクワク\nしてきちゃった…！", 120601, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5754, "あ、いいとこに来て\nくれましたね！\nあなたに頼みたい\nことがあるんです！", 120601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5755, "きゃあっ！？\nへ、変なところを\n触らないでください！", 120601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5756, "どうですかこの格好？\nかわいい、ですか…？\nそ、そうですか…\nありがとうございます", 120601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5757, "ひゃあっ…！\nすみません、勉強に\n集中してたので\n驚いちゃいました", 120601, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5758, "一緒に勉強して立派な\n大人になりましょうね\n私がそばで見張ってて\nあげます", 120601, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5759, "まだ立派ではないかも\nですけど、私よりも\n大人なあなたのことは\n頼りにしてますからね", 120601, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 5760, "立派な大人は\n朝の時間の使い方が\n上手だと聞きました\nあなたはどうですか？", 120601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 5761, "ミミとミソギと\nあなたと…大事な人と\n一緒のお昼ごはんは\nやさしい味がしますね", 120601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 5762, "うぅ…怖い話を\n思い出しちゃいました\n寝るまで一緒に\nいてくれますか…？", 120601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5764, "すごい座り心地…！\nとっても勉強が\nはかどりそう…！", 120601, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5765, "この子…なんだか\nミミに似てるような\n気がする…ふふっ♪", 120601, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5766, "いい香り…♪\n勉強で疲れた\n頭がリフレッシュ\nできそうです", 120601, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5767, "ぽかぽかだ…♪\nミミとミソギと\nあの人と一緒に\nいるときみたい", 120601, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5769, "いっぱい食べたら\n早く大人になれたり\nしないかな…？", 120601, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5770, "あう…\n知らない単語が…\nちゃんと調べて\n覚えなくっちゃ…！", 120601, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5773, "うさぎさんが\nぴょ～んぴょん♪\nみんなえがおで\nるんたった～♪", 120401, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5774, "キョウカちゃんも\nいつかにんじんさんを\n好きになってくれたら\nうれしいな～♪", 120401, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5775, "今日のミソギちゃんの\nイタズラ、びっくり\nしちゃったけど\nおもしろかったな～♪", 120401, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5776, "このおうち、いつも\nいろんな人がいて\nドキドキぴょんぴょん\nって楽しいな～♪", 120401, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5777, "いいお天気～♪\nお外でみんなと\nいっぱい遊びたいな♪", 120401, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5778, "ミソギちゃんも\nキョウカちゃんも\nおにいちゃんがいると\nずっと笑顔だね～♪", 120401, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5779, "おにいちゃんにはやく\nあいたいな～♪\nまだかな、まだかな♪", 120401, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5780, "えへへ♪\nおにいちゃんに\nなでなでされると\nぽかぽかするね♪", 120401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5781, "聞いて、おにいちゃん\nさっきね、かわいい\nちょうちょさんに\n会ったんだよ～♪", 120401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5782, "ふわっ…！\nおにいちゃんの顔を\nお絵かきしてたから\nびっくりしちゃった♪", 120401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5783, "おにいちゃんも\n遊びたいの？\nそれじゃあ二人も\n呼んで遊ぼう♪", 120401, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5784, "おにいちゃんも\nみんなも、優しい人\nばっかりで\nミミ幸せ～♪", 120401, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5785, "おにいちゃん♪\n高い高いしてほしいな\nすっごくぴょんぴょん\nできて、好きなんだ♪", 120401, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 5786, "おはよう～♪\nおにいちゃん、今日は\nおひさまみたいに\nきらきら元気だね～♪", 120401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 5787, "おひるごはんだね～♪\nにんじんさんを\nいっぱい食べて\n元気いっぱい♪", 120401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 5788, "ふわあ…\n眠くなってきちゃった\nおにいちゃん、\n一緒に寝よう～？", 120401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5790, "ふわぁ、気持ちいい♪\nずっとこうやって\n座ってたくなるね～♪", 120401, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5791, "わあ、かわいい～\nぎゅ～っ、ぎゅ～っ♪\nこれで仲よしだね\nえへへ♪", 120401, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5792, "気持ちいいにおい～♪\nぴょんぴょんしたく\nなってきちゃった♪", 120401, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5793, "あったかぽかぽか♪\nおにいちゃんも\nきてくれたら、もっと\nあったかくなりそう♪", 120401, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5795, "もぐもぐ…ごっくん♪\nおっきくなりたいから\nいっぱい食べるよ～♪", 120401, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5796, "えへへ、おもしろ～い\nこの絵本ね、\nキョウカちゃんから\n教えてもらったんだ♪", 120401, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5799, "この部屋…探索の\nしがいがありそう！\nついでにイタズラも\nしかけちゃお♪", 120501, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5800, "ミミってああ見えて\n実は大胆で度胸も\nあるよね…ちょっと\nかっこいいな…！", 120501, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5801, "怖い話を聞いた時の\nキョウカって\n面白いんだよね～♪", 120501, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5802, "にいちゃんが\n慣れちゃわないように\nまた新しいイタズラを\n考えなきゃ！", 120501, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5803, "イタズラもいいけど\nミミとキョウカと\nにいちゃんと、普通に\n遊ぶのも楽しいなぁ♪", 120501, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5804, "ミミにキョウカ、\nそれににいちゃん！\nみんなそろえば愉快な\n冒険が始まりそう！", 120501, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5805, "次はどんなイタズラを\nしたら、にいちゃんは\n喜んでくれるかな？", 120501, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5806, "今ミソギに触ったね？\nヤバいよにいちゃん…\n逃げないとトラップが\n発動しちゃうよ…！", 120501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5807, "わわっ！？　びっくり\nしたあ…！　新しい\nトラップを作ってて\n気づかなかったよ～", 120501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5808, "お？　にいちゃん、\nイタズラする側にも\n興味があるの？", 120501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5809, "にいちゃんなにか用？\nあ、もしかして…\nミソギと遊びたく\nなっちゃった？", 120501, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5810, "ベストタイミングだよ\nにいちゃん！\n今からみんなで探検に\n行くところなんだ！", 120501, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5811, "にいちゃんってさ\nホントに優しいよね\nなにしても怒らないし\n…ありがとね♪", 120501, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 5812, "にいちゃんおはよー！\n景気づけに本日\n一発目のイタズラ、\nやっちゃうよ～♪", 120501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 5813, "お昼ごはんはロシアン\nサンドイッチだよ！\nさあ、にいちゃん！\n好きなの選んでよ！", 120501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 5814, "夜ってなんだか\nワクワクしてなかなか\n眠れないんだよね\nにいちゃんはどう？", 120501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5816, "この座り心地のよさ…\nなにかのトラップに\nうまく使えないかな？", 120501, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5817, "こういうのに細工して\nミミやキョウカを\n驚かせると、いい反応\nするんだよね♪", 120501, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5818, "においをうまく使った\nイタズラって…\nなにかないかな？", 120501, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5819, "あったまりながら\n新しいイタズラを\n考えると、いい案が\n出て来たりするんだ♪", 120501, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5821, "おいしくてついつい\n食べ過ぎちゃうなぁ…\nはっ…！？　これって\nまさかトラップ…！？", 120501, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5822, "このキョウカから\n借りた冒険物語\nすっごく\nおもしろいんだよね！", 120501, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5825, "晴れ着を着ると\nなんだか心が躍るね\n足取りも軽く\nなるってもんさ", 120801, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5826, "正月の食べ物ってのは\n美味いもんだね\nいくらでも\n食べれちまうよ", 120801, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5827, "普段は好き勝手してる\nアタシらも\n正月くらいは\n大人しくするさ", 120801, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5828, "年越しそばに雑煮\nこの季節は\n温かいものが\n体に沁みるね", 120801, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5829, "正月だからって\nのんびりしすぎたね\nちょっと釣りにでも\n行こうかね", 120801, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5830, "いい正月だね\nいつかあの子と\n一杯やりたいもんだよ", 120801, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5831, "大事な人と過ごす\n正月ってのがこんなに\n楽しいもんだとはね", 120801, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5832, "部屋があったかくて\n顔が火照っちまったよ\nちょいと外に涼みに\n行かないか？", 120801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5833, "餅ってのは\n何をつけてもうまいね\nお前さんは\nどの味が好きだい？", 120801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5834, "お祝いの鯛が\n足りないって？\nそれじゃあアタシが\n釣ってきてやるよ", 120801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5835, "おせちが美味いのは\n分かるけど\n食べ過ぎには\n気を付けるんだよ", 120801, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5836, "お前さんのおかげで\nいい正月を\n迎えられたよ\nありがとさん！", 120801, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5837, "慣れない晴れ着も\nお前さんに見て\nもらえたなら\n着たかいがあったね", 120801, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 5838, "おはようさん\n寒いのは分かるけど\nいい加減布団から\n出て顔洗ってきな", 120801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 5839, "今日の昼飯は雑煮だ\nアタシの郷土の味\nしっかり味わって\nおくれよ", 120801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 5840, "おや、お前さんも\n夜ふかしかい？\n退屈してたところさ\n付き合っておくれよ", 120801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5842, "晴れ着だと座り方にも\n気を遣うね\nしわになったら大変さ", 120801, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5843, "せっかくだし\nこの子にも晴れ着を\n着せてあげたいね", 120801, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5844, "これは冬の花の香り…\n風情があって\nいいじゃないか", 120801, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5845, "あったかいねぇ…\nこんな寒い日には\nまさに天国だよ", 120801, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5847, "いつも食べてる\nものでも正月に\n食べると\n新鮮な気がするね", 120801, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5848, "せっかくの正月休みだ\nたまにはゆっくりと\n本でも読もうかね", 120801, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5850, "新年のあいさつ…\n私も街のみんなに\nしてみようかしら…", 120701, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5851, "正月はゆっくり過ごす\nのよね…ゆっくりって\n何したらいいのかしら", 120701, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5852, "この格好も\n慣れてくれば\n動きやすいわ", 120701, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5853, "う～ん、今年の抱負…\nやりたいことが\nたくさんあるのよね", 120701, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5854, "おせち料理\nネビアの分も\n用意しなくちゃね", 120701, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5855, "ふふっ\n正月っていいわね\nみんな楽しそうだもの", 120701, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5856, "新年の思い出…\nこれからもっと作るわ\nそれが私の抱負よ", 120701, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5857, "今日は家で過ごすの？\nあなたと一緒なら\nきっと楽しいわね", 120701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5858, "どう？　この格好？\nコッコロさんは\n褒めてくれたけど…", 120701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5859, "ちょうど良かったわ\nあなたも大掃除\n手伝ってくれる？", 120701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5860, "これから料理するけど\nお雑煮と焼き餅\nどっちが好きかしら？", 120701, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5861, "『今の私』にとって\n初めての正月だもの\nあなたと楽しまないと", 120701, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5862, "去年よりも今年…\nもっと成長するからね\n…お兄ちゃん♪", 120701, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 5863, "おはよう\nせっかくの正月だもの\n朝から楽しまないとね", 120701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 5864, "ねぇ…\n良かったら正月の遊び\n教えてくれるかしら？", 120701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 5865, "ふわぁ～…今日が\n終わっちゃうのが…\nもったいないわね…", 120701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5867, "ふぅ…お参り後だから\n心地よすぎて眠って\nしまいそうだわ…", 120701, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5868, "ふふっ\nかわいいものって\n癒されるわね…", 120701, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5869, "…ん\nこれが梅の香り？\n…いい匂いね", 120701, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5870, "キャルじゃないけど…\nこのまま丸く\nなりたいところね…", 120701, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5872, "はむはむっ…♪\nせっかくの正月だもの\n堪能しなくちゃね♪", 120701, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5873, "…ふんふん\n帯の結び方って\nこんなにあるのね…", 120701, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 5875, "今の私が作れる\n最高の一曲…\nあの人に届くといいな", 104201, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 5876, "あの、歌を…\n聞いてくれませんか？\nあなたに贈る\n特別な歌を", 104201, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5877, "ひい、ふう…\nむむ、みなに配る\nお年玉のポチ袋が\n残りわずかなのじゃ…", 120901, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5878, "宴の準備はわらわに\n任せておくのじゃ！\n支配者の手腕、\n見せつけてくれる！", 120901, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5879, "新年の願掛けなら\nわらわにすればよい\n支配者に頼むほうが\n確実じゃろう？", 120901, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5880, "みなからもらった\nこの年賀状とやら…\nわらわも\n書いてみようかの♪", 120901, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5881, "正月飾りにもいろいろ\n種類があるんじゃの～\nどれも華々しくて\nわらわ好みじゃ♪", 120901, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5882, "あやつへのお年玉は\nちと多めにしておくか\n…日ごろの感謝も\n込めてのう", 120901, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5883, "あやつのことを想うと\nそれだけで行く年\n来る年が楽しみになる\nふふ、愉快なやつじゃ", 120901, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5884, "みなに晴れ着を\n褒められて、とても\nよい気分なのじゃ♪\n次はお主の番じゃぞ？", 120901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5885, "お主へのお年玉は\nいちばん最後じゃ！\n残り物には福が宿る\nそうじゃからのう", 120901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5886, "この季節は手が\nかじかむのう…ほれ、\nお主！　わらわの手を\nあたためるのじゃ！", 120901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5887, "お主のために雑煮を\n手作りしてみたのじゃ\n食べていくがよい♪", 120901, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5888, "眷属のお主に\n正月休みはまだ早い！\nずっとわらわのそばに\n控えておくのじゃ♪", 120901, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5889, "ふふ、これほど楽しい\n正月は初めてじゃ…\nお主と過ごす時間は\nいつだって特別じゃな", 120901, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 5890, "早起きしてわらわを\n待っておったのか？\nさすがはわらわの眷属\n殊勝な心がけじゃな♪", 120901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 5891, "食事どきじゃな\n正月の宴会料理が\nわらわたちを\n待っておるぞ♪", 120901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 5892, "夜も更けてきたのう\n冬の夜は長い…\n心ゆくまでお主と\n過ごせるわけじゃな♪", 120901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5894, "実に心地がよい♪\nこのまま寝正月も\n悪くないかも\nしれぬのう", 120901, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5895, "愛いやつじゃのう\nわらわのそばに置いて\nやってもよいぞ♪", 120901, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5896, "品のある香りじゃのう\n寝室に置いておけば\nなにかご利益に\nあずかれそうじゃな", 120901, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5897, "心地よい…\nわらわはしばらく\nここから動かぬぞ\nお主も付き合うがよい", 120901, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5899, "舌鼓が鳴るのう♪\n正月の宴には\n持ってこいの品じゃ！", 120901, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5900, "ほう、新春にはそんな\n習わしが…正月を\n制するには、まだまだ\n勉強が必要じゃのう", 120901, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5902, "久しぶりの我が家って\n感じです！　やっぱり\nここが一番ですね〜！", 121001, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5903, "さあ食べましょう！\nみんなで囲める食卓を\n心から味わいますよ☆", 121001, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5904, "ランドソルに\n帰ってきた途端に\nお腹が空きました〜！", 121001, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5905, "ただいま☆\n幸せ特盛りの\nわたしの日常！", 121001, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5906, "ようやくランドソルに\n帰ってきたことですし\nやっぱり魔物料理を\nいただきましょう☆", 121001, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5907, "あの人との絆を\n守るために\nもっともっと食べて\n強くならなくちゃ☆", 121001, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5908, "いつもありがとうって\nあの人を思いっきり\n抱きしめたいです☆", 121001, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5909, "あなたの想いは\nこの身をかけて\n全力で受け止めます！", 121001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5910, "おかえりなさい☆\nここにいるみんなも\nあなたの帰りをずっと\n待っていましたよ！", 121001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5911, "このギルドハウスで\n冒険の疲れを\n癒しましょう〜！", 121001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5912, "あなたの想いが\n全力全開を超える\n力をくれました！", 121001, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5913, "日常が続く幸せを\nあなたと一緒に\nかみしめたいです☆", 121001, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5914, "ぎゅ〜っ！\nわたしたちは\nどんなことがあっても\nずっと一緒ですよ？", 121001, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 5915, "おはようございます！\nあなたと過ごせる\n幸せを、今日も\n味わいますよ〜！", 121001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 5916, "ご飯の時間ですよー！\nあなたも一緒に\n食べましょうね！", 121001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 5917, "明日もあなたが隣に\nいてくれることを\n心から願ってますよ\nおやすみなさい☆", 121001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5919, "ふい〜…\n安心できる場所で\nくつろげるって\n幸せですね〜…", 121001, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5920, "じゅるり…\n食べたくなっちゃう\nくらい、かわいい☆", 121001, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5921, "くんくん…おおー！\nステーキの香り！\nやばいですね☆", 121001, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5922, "体がぽかぽかしてきて\nわたしの食欲も\n燃えてきましたよー！", 121001, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5924, "わたしの食欲に\nついてこられますか？\nまだまだ食べますよ！", 121001, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5925, "これがキャルちゃんの\n言っていた、キスで\n目覚める王子様の\nおとぎ話ですか〜☆", 121001, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5927, "自分で自分を\n分析するのも、これは\nこれで面白い…♪", 121201, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5928, "強過ぎる力の扱いには\n注意しないと…\n七冠がいい例だね", 121201, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5929, "少年の交友関係は\n計り知れないな…\nアタシも含むのか！", 121201, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5930, "原理を徹底解明して\n再発を防ぐぞー！\n…誰か手伝って～！", 121201, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5931, "これは祝福にも\n呪いにもなる…\n忘れずにいよう", 121201, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5932, "少年が\n目を覚ましてくれて\n本当によかった…", 121201, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5933, "彼以外を選んでいたら\n今回の事態は\n乗り切れなかったな…\nさすがだよ、少年", 121201, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5934, "少年！　調子はどう？\nクレープ食べられる？\nっていうか食べて？", 121201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5935, "元気そうで何よりだ\nまた店を手伝いに\nきてよ。二人もキミに\n会いたがってたしさ", 121201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5936, "キミの診察もあるし\n今はなるべくそばに\nいるよ。迷惑じゃ\nなければだけど", 121201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5937, "やっぱり少年は\nあちこちで人を\n助けてる時が\n一番輝いてるよ", 121201, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5938, "アタシは大したこと\nしてないけど\nみんなのことは\n褒めてあげてね", 121201, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5939, "みんなを笑顔に\nできるのは\nやっぱり少年だね", 121201, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 5940, "やあ少年\n朝寝坊はしてない？\nそれじゃ、よい１日を", 121201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 5941, "おや、少年！\nまかないのクレープ\n作ったけど、食べる？", 121201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 5942, "ふわぁ…もう夜か…\n研究してたら\nあっという間だね…\nおやすみ、少年", 121201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5944, "体がなまってたのか\n前線に出ると疲れる…\nまさか…年のせい！？", 121201, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5945, "キミたちも今の\nアタシと同じ感じに\nアレンジしてあげよう", 121201, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5946, "誰が置いたんだろう？\nいい香りだねぇ…\nうん、気に入った♪", 121201, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5947, "何も考えてない時間も\n実は思考にとって\n大事なんだよね～…", 121201, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5949, "食の大切さは\nいつの食事からも\n感じられるね♪", 121201, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5950, "ふむ…面白い！\n今度全巻\n借りにこようっと", 121201, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5952, "聖学祭か…\n良い記憶はないな\nよし忘れよう", 116401, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5953, "健全なる精神は\n健全なる肉体に宿る…\nはっ、知ったことかよ", 116401, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5954, "暇だし鏡でも見るか…\nえへへ、ユニだよ♪\nよし子供料金いけるな", 116401, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5955, "アオイ君は元気に\nしているだろうか…\n彼女は人一倍ぼくに\n懐いていたからな…", 116401, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5956, "そろそろシズル君は\n姉という幻想から\n目覚めただろうか…", 116401, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5957, "ぼくは来年も聖学祭に\n行くさ…卒業しても\n君らとは永遠だ", 116401, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5958, "最近同志の登校率が\n減少傾向だな…\nまあ仕方ないのだが…\nないのだが…", 116401, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5959, "ほわあああ！\nおい急に話しかけるな\n学びがこぼれる", 116401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5960, "この体操着、周囲から\n大層評判が良いのだよ\nたいそうだけにな\nどやっ！", 116401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5961, "何を着ていようと\nぼくはぼくさ\n外見など所詮は\n魂の識別符に過ぎん", 116401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5962, "救護院のアヤネ君が\n最近ぼくの自称年齢に\n疑念を持ち始めてな…\nあの熊…殺るか…", 116401, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5963, "うん？　なんだね？\nなに、用は無い？\n用も無いのに呼ぶとは\nもしや…これが友達！", 116401, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5964, "この服が似合ってる？\nな、なんだね突然…\nやめたまえよでゅふふ", 116401, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 5965, "ふぁ～あ…\n朝も更けてきたな…\nそろそろ寝るか…", 116401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 5966, "ふぁ～あ…もう昼だ…\n人間の起きている\n時間ではないな…\n早く寝なくては…", 116401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 5967, "うーん、よく寝た！\n気持ちのいい夜だな\n今日も一日がんばるぞ", 116401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5969, "ほわぁ～…\nもしやこれは都で噂の\n人を駄目にするやつ…", 116401, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5970, "ふむ、愛くるしいな\nだが似合い過ぎる…\nぼくが持つと我ながら\n子供にしか見えん", 116401, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5971, "アロマか、興味深いな\nミツキ印ポーションも\n気化させて鼻腔から\n吸入してみるか…", 116401, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5972, "んふ、んふふ…\n火ってイイよね…\nずっと見てられるね…", 116401, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5974, "や、やめろ…\nとめどなく固形食物を\n生産するのはやめろ…", 116401, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5975, "この可愛げのない筆致\nやはり作者は理系か！\nぐああ目が理系になる", 116401, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5977, "…あいつのためなら\n無理くらいいくらでも\nしてやるわよ", 121101, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5978, "この姿、\n魔力の制御ムズ過ぎ…\nま、あたしは優秀だし\nどうとでもなるけど！", 121101, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5979, "あいつのこと、\n全部受け入れるって\n決めたから！", 121101, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5980, "今なら誰だろうと\n負ける気がしないわ\nドラゴンでもなんでも\nかかってこいっての！", 121101, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5981, "散々助けられた分、\n今度はあたしが\nあいつのこと助けるわ", 121101, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5982, "あいつとの\nこれまでの冒険は\nどれも、あたしの\n大切な思い出…", 121101, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5983, "もしまたあいつが\n寝たままになったら\nなんだってするわ\nキ…キスでも…", 121101, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5984, "なに、お腹空いたの？\nなら、おいしいご飯を\n食べに行きましょ♪", 121101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5985, "やっぱ、\nあんたのアホ面見ると\n安心するわ♪", 121101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5986, "【美食殿】はあんたと\nぺコリーヌがいなきゃ\n始まんないっての", 121101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5987, "ひゃあっ！？\n急に声かけないで！？\n魔力が暴発しても\n知らないわよ！", 121101, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 5988, "…あんたとこうして\nぼーっと過ごす時間、\nまあ…嫌いじゃないわ", 121101, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 5989, "あんたが信じて\n見守っててくれれば…\nあたし、\nなんだってできるわ", 121101, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 5990, "おはよ\n早く顔洗いなさいよ、\nこのねぼすけ♪", 121101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 5991, "今日のお昼は\nどこで食べる？\nあんたが\n決めていいわよ", 121101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 5992, "もしあんたがまた\n夢に囚われても\nあたしが助けるわ\n安心して寝なさいよ", 121101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5994, "気持ちいい～…♪\nあいつもたまには\nこうして休ませなきゃ", 121101, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5995, "一目惚れしたかも…！\nこの子、\n部屋に飾りたいわー♪", 121101, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5996, "へぇ、あいつって\nこういう匂いが\n好きなのかしら…？", 121101, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5997, "暖かくて最高…♪\n丸くなっちゃいそう…\nうにゃあ…♪", 121101, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 5999, "イケるわね、これ！\nやっぱり食べるって\n大事よねー♪", 121101, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6000, "ファーストキスは\nどんな味、ね…\nふ、ふ～ん…？\nなるほど…", 121101, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6002, "お友だち…\nできるかしら…？\nこんなわたしでも…", 118101, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6003, "あまり、きょろきょろ\nしちゃダメよね…\n変な子だと、\n思われてしまうわ…", 118101, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6004, "広い場所、\n落ち着かない…\nはぅ…どこにいれば…", 118101, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6005, "～♪", 118101, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6006, "【レイジ・レギオン】\n…またいつか、\nみんなと…", 118101, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6007, "…一度でいいから、\nわたしもママって…\nあっ、その…\n…な、なんでもないわ", 118101, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6008, "ここが、今のわたしに\nとっての帰る場所…\n…そう思っても、\nいいのかしら…？", 118101, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6009, "ええと…\nお腹、空いてる？\nおねむじゃない？", 118101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6010, "ここにいると\n邪魔かしら…？\nならもっと隅っこに…", 118101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6011, "お喋り…？\nわたしでいいの…？\nうれしい…", 118101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6012, "あなたの好きな歌…\n教えて…？\n練習、するから…", 118101, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6013, "ここは賑やかだけど…\nうるさいとは感じない\nむしろ、心地いい…", 118101, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6014, "安心、して…\nわたしの歌で、\nあなたを守るから…", 118101, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 6015, "ちょっとだけ…\n朝は、苦手…\n…ふぁ…", 118101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 6016, "お昼は、食べた？\n良かったら、一緒に…\n少し…焦げてるけど…", 118101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 6017, "眠れないのかしら…\nじゃ、じゃあ…\n子守歌…聴く？", 118101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6019, "こんな特等席…\n恐縮、です…", 118101, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6020, "い、今のうちに、\nこの子でお世話の\n練習を…", 118101, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6021, "いい香り…\n…つらい気持ちも\n癒してくれる", 118101, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6022, "あったかい…\nいつまでも、\nこうしていられそう…", 118101, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6024, "おいしく出来てる…\nすごいわ…\n見習わなくっちゃ…", 118101, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6025, "『赤ちゃんとママの\nスキンシップ入門』…\n…べ、勉強になるわ…", 118101, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6027, "きれいなお部屋…！\n立派な舞台の\nセットみたい…♪", 121301, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6028, "お芝居では私が先輩\nなんだから、しっかり\n私がミサキちゃんを\nエスコートしなきゃ", 121301, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6029, "～～♪\n歌うお芝居も多いから\nもっと上手く\nなりたいな…！", 121301, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6030, "ここにいる人たちと\n仲良くなりたいな…\nそしたらきっと演技の\n幅も広げられるよね", 121301, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6031, "機会があったら\nアヤネちゃんとも\nお芝居してみたいな♪", 121301, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6032, "お兄ちゃんに褒めて\nもらえるように\nお芝居の練習\nいっぱいしなきゃ…♪", 121301, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6033, "あう…お兄ちゃんを\n意識したら、台詞が\n飛んじゃったよぅ…！", 121301, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6034, "あっ、お兄ちゃん…♪\nお芝居の練習を\n見てくれませんか…？", 121301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6035, "いまの発声練習\nどうでしたか？\nひゃん…！？\n頭は撫でないでぇ～", 121301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6036, "ミサキちゃんも\nお芝居、とっても\n上達してるんですよ\n私も負けられません♪", 121301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6037, "また物語を聞かせて\nほしい、ですか…？\n私なんかでよければ\nもちろんよろこんで♪", 121301, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6038, "新しいお話を\n考えてみました\n聞いてくれますか…？\nよかった…♪", 121301, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6039, "いつか…舞台上じゃ\nなくても、照れずに\nお兄ちゃんに気持ちを\n伝えてみせますね…♪", 121301, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 6040, "おはようございます♪\nマヒルさんにもらった\nミルク、一緒に\n飲みませんか？", 121301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 6041, "牧場のみなさんに\nドングリパンを\nいただきました～\nおひとつどうぞ♪", 121301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 6042, "寝る前にまたココアを\n飲みながら、お話を\n聞いてもらえますか？\nえへへ、うれしいな♪", 121301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6044, "お芝居の練習の\nあとは、ここで\nお兄ちゃんと\nゆっくりしたいな♪", 121301, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6045, "今日も演技の\n練習に付き合ってね♪", 121301, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6046, "素敵な香り…\nこのイメージも\nお芝居に\n活かせないかな…？", 121301, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6047, "私もお芝居で\nみんなの心を\nぽかぽか温められる\nようになりたいな…♪", 121301, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6049, "舞台に立つんだから\n食べ過ぎないように\n気をつけないと。\nでも…おいしいなぁ♪", 121301, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6050, "このお話、面白い…\n脚本を書く\n参考にもなりそう…♪", 121301, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 6052, "えへへ…♪\n二人で見た夕日…\nキレイでしたね…", 107501, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 6053, "これから泳ぎの\n練習ですか？\n付き合いますよ♪", 107501, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 6054, "主さまを導くことで\nわたくしも未知なる\n領域へと辿り着けます", 107601, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 6055, "主さまとなら\n海よりも深く\n空よりも高い場所へも\n目指せそうです", 107601, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 6056, "あいつと二人で\n夏の遠い海に…って\nあたしってば\nなに考えてるのよ！", 107801, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 6057, "あたしが楽しく\n過ごせるのはあんたが\nいてくれたからよ\nか、感謝してるわ", 107801, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6058, "はぁ…アタシってば\nこんなにかわいくって\nキレイなんて罪よね～", 121401, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6059, "舞台の衣装を\nクリーニング\nしとかなくちゃ…", 121401, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6060, "舞台映えするメイク…\nスズナなら\n知ってるかしら？", 121401, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6061, "物産展の会計係と\n山盛りの算数の宿題…\nどっちが簡単かしら…", 121401, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6062, "牧場と温泉の仕事に\nお芝居の練習に…\nちょ、ちょっと休憩～", 121401, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6063, "お客さんの視線も\nうれしいけど…\nあいつも見てて\nくれてるかしら？", 121401, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6064, "もっともっと\nセクシーになって\nあいつをビックリ\nさせてやるわ♪", 121401, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6065, "アンタも休憩？\nしっかり働くために\n休むのも大事よね", 121401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6066, "ふふん♪　アタシも\n料理ができるように\nなったんだから！\n簡単なのだけだけど…", 121401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6067, "温泉ってお肌に\nいいのよね？\nもっとキレイに\nなったらどうしよう〜", 121401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6068, "物産展の荷物は\nこれでよし…っと\nそれじゃアンタも\n運ぶの手伝ってよね♪", 121401, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6069, "いつもと違うアタシに\n見とれちゃった？\n仕方ないわね～♪", 121401, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6070, "お花見って風流で\n大人って感じ♪\n…でもお団子も\n忘れないでよね！", 121401, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 6071, "みんなに朝ごはんを\n作ってみたら\n喜んでくれるかしら？", 121401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 6072, "お客さんがいっぱい\n来る時間帯ね！\n元気出していくわ！", 121401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 6073, "そろそろ寝ないと…\n睡眠不足はお肌の\n大敵なんだから", 121401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6075, "ふかふかしてて\n気持ちいい～♪\n疲れが癒されるぅ…", 121401, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6076, "この子もアタシと\nおそろいの衣装に\nしてあげようかしら♪", 121401, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6077, "素敵な衣装で\nいい香りを\n身にまとう…ふふ、\n大人のたしなみよね♪", 121401, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6078, "あったか～い♪\nここでお昼寝したら\n気持ちよさそう…", 121401, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6080, "お仕事がんばると\nいつもよりお腹\n空いちゃう～！", 121401, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6081, "最新のトレンド\n載ってないかしら？\n物産展の売り物に\n取り入れなくちゃ！", 121401, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6083, "怪盗のこと…\nお父さんお母さんに\nもし知られたら\nなんて説明しよう…", 121501, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6084, "たとえ怪盗でも、人の\n家にお邪魔するときは\n身だしなみをしっかり\nしないと…", 121501, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6085, "鍵開けって難しい…\nうーん、もっと\n練習しておかなくちゃ", 121501, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6086, "姿が見えない\n怪盗の噂…？\nわ、私のことじゃ\nありませんように…！", 121501, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6087, "予告状って…\n切手はどこに貼れば\nいいんだろう…？", 121501, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6088, "怪盗活動にも\n慣れてきたけど…\nやっぱり先輩が\nいないと心細いなぁ…", 121501, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6089, "せ、先輩の心も\nいつか盗めるかな…？\n…わわわ、私ってば\nなに言ってるんだろ…", 121501, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6090, "怪盗が大活躍って\n新聞で読んだ…？\nだっ、誰のこと\nでしょうね…あはは", 121501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6091, "尾行に潜入…\n存在感がないのって\n意外と役立つことも\nあるんですね", 121501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6092, "せせせ、先輩っ！\n帽子をぎゅーって\nかぶったら、抜けなく\nなっちゃいました…！", 121501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6093, "ぬ、盗み聞きなんて\nしてませんよ？\nみんなが私に気づかず\n話し始めるだけで…", 121501, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6094, "秘密道具が見たい？\nそ、それは内緒です！\n秘密道具ですから…\nで、でも先輩になら…", 121501, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6095, "えへへ\n見つかっちゃいました\n先輩はいつも私を\n見つけてくれますね…", 121501, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 6096, "おはようございまふ…\n秘密道具のお手入れで\n夜更かししちゃって…\nちょっと眠たいです～", 121501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 6097, "怪盗が昼に活動する…\n案外そのほうが\n意外性が\nあるのかも…？", 121501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 6098, "外が暗くなりましたね\n…そろそろ怪盗の活動\n始めましょうか？", 121501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6100, "ここでくつろぐと\n疲れが取れます～\n…今日も怪盗\nがんばろっと", 121501, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6101, "わぁ、かわいい…！\n心を盗まれるって\nこういうことかな…？", 121501, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6102, "素敵な香り…でも\n服に香りがついたら\n隠れてるときに\n見つかっちゃうかな…", 121501, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6103, "ぽかぽかで\nいい気持ち…\n夜道を歩く前に\n温まっておこうっと…", 121501, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6105, "怪盗活動に備えて\n今のうちにしっかり\n食べておかなきゃ…！", 121501, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6106, "女の子は秘密が\n多いほうが魅力的…？\nそ、そうなんだ…", 121501, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 6108, "ドＳさんはいつも\nクウカの妄想の中に…\nぐふふ、じゅるり～♪", 104501, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 6109, "やさしい触り方も\n嫌いじゃありません…\nは…恥ずかしいですが", 104501, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6110, "この大怪盗さまに\n盗めないものなんて\nないです！　たぶん！", 121601, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6111, "あたしにふさわしい\nビッグなお宝は\nどこにあるですかね～", 121601, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6112, "そろーりそろーり…\n気配を消せばお菓子も\n食べ放題…にやり", 121601, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6113, "またサボってたのが\nバレたです…\nボスの地獄耳～！", 121601, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6114, "ギャングで大怪盗…！\nこの肩書の多さ！\nまさに有能の証です！", 121601, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6115, "お兄さんだったら…\nお宝を山分けして\nあげてもいいですよ", 121601, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6116, "お兄さんはアレですよ\n大怪盗イノリさまの\n相棒みたいなものです", 121601, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6117, "何事も用心深さが\n大切ですよ。ビ…\nビビりじゃないです！", 121601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6118, "ぬ、盗み聞き？\nしてないですよっ？\nぴゅーぴゅーぴゅー", 121601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6119, "情報が欲しければ\nお菓子と交換ですよ\nさぁ、交換交換っ♪", 121601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6120, "しーっ！\n今、尾行中なんです\n話はあとで…", 121601, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6121, "お兄さんと話してると\n時間が速いような…？\nこの時間泥棒！", 121601, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6122, "ちょうどよかった！\nいい話があるんです\n一口乗りませんか？", 121601, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 6123, "おてんとうさまが\n出てやがるですね…\n悪事がやりにくいです", 121601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 6124, "もうこんな時間ですか\nご飯にお昼寝…\nいやー忙しいです♪", 121601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 6125, "ふっふっふ！　夜こそ\n大怪盗の時間です！\n……ふぁあ", 121601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6127, "うーん…\n警備の目がないと\n気楽でいいですねぇ", 121601, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6128, "くう…！\nこのかわいさは\n盗めないです…♪", 121601, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6129, "犯行現場に残ったのは\n香水の匂いだけ…\nふふ、おしゃれ大怪盗", 121601, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6130, "意外とこういう場所に\n隠し扉があったり…\nしないです？", 121601, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6132, "もぐもぐ…\nこの美味しさ…\nとんでもない罠ですね", 121601, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6133, "むむむ…ちっとも\n分からないです…\nほとんど暗号ですよ", 121601, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6135, "あら、サレンさんは\nどこに行かれた\nのかしら？", 121701, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6136, "ふんふんふーん♪\nなんだか華やいだ\n気分ですわ♪", 121701, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6137, "さて…次の事業は\nどうしましょう\n何か面白いことは…", 121701, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6138, "おーっほっほっほ！\nあら…楽しくて、つい\n笑ってしまいましたわ", 121701, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6139, "センスがあって\nいいお部屋ですわね\n私も見習いたいですわ", 121701, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6140, "サレンさんとあのお方\n仲がいいですわね\nも、もちろん仲がいい\nのはよいことですわ！", 121701, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6141, "どんなときでも\n正々堂々！\nそれが私の\n誇りですわ！", 121701, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6142, "ふふ、ごきげんよう\n私、絶好調でしてよ", 121701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6143, "どうかされましたか？\n何かお困りごと\nでしたら\n是非力になりますわよ", 121701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6144, "たまにはこうして\nゆっくりと\nお話しするのも\nいいものですわね", 121701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6145, "次の事業に関して\n考え事をしてましたの\nよければ相談に乗って\nくださるかしら？", 121701, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6146, "よろしければ\nお話しませんこと？\nサレンさんのお話を\n聞かせてほしいですわ", 121701, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6147, "ふふっ…\n先ほどサレンさんと\nあなたさまのお話を\nしていたところですわ", 121701, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 6148, "さあ！\n今日も一日\n全力で参りますわ！", 121701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 6149, "お昼はどうしましょう\nサレンさんをお誘い\nしてみようかしら", 121701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 6150, "もう少しだけ…\n明日の準備をして\nおかないと…", 121701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6152, "はぁ～…いいですわね\nはっ、私としたことが\nはしたないですわね", 121701, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6153, "かわいらしいですわ\nいくつになっても\nぎゅっと\nしたくなりますわね", 121701, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6154, "落ち着きますわね\nサレンさんはどんな\n香りが好きなのかしら", 121701, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6155, "ふふっ、燃え盛る\n情熱の炎…\nいいですわね", 121701, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6157, "このおいしさで\n元手がゼロ…\nすばらしい\n商品ですわ！", 121701, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6158, "これは…ふむふむ\n新しい事業のヒントに\nなりそうですわ", 121701, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6161, "さて、と…\n今日は何をしなくちゃ\nいけないんだっけ？", 121801, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6162, "やることは山積み\nだけど、手を動かす\nのは嫌いじゃないわ♪", 121801, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6163, "あら？　アキノさんの\n声が聞こえたような\n気がしたんだけど…", 121801, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6164, "アキノさんといると\nついこっちまで笑顔に\nなっちゃうのよね", 121801, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6165, "弱音なんか\nはいてられないわ\nアキノさんに\n笑われちゃうもの", 121801, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6166, "気が付けばアキノさん\nのペースだけど…\n不思議と嫌では\nないのよね", 121801, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6167, "あたしを好敵手って\n言ってくれる\nアキノさんのためにも\nしっかりしなくちゃ", 121801, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6168, "どうしたの？\nアキノさんなら\nさっきそこに\nいなかった？", 121801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6169, "あたしなら大丈夫\n救護院だって\nみんなが助けて\nくれるからね", 121801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6170, "さっきアキノさんが\nあんたを探してたわよ\nなにかしたの？", 121801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6171, "こう見えてヒマじゃ\nないんだけど…\n少しくらいなら\n付き合ってあげるわ", 121801, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6172, "ねえ、あんたと\nアキノさんって…\nううん、やっぱり\nなんでもないわ…", 121801, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6173, "アキノさんも誘って\nお茶でもしない？\n話したいことが\nたくさんあるの♪", 121801, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 6174, "早起きは三文の得よ\nいい一日を迎えるため\n目を覚まさなきゃ", 121801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 6175, "今日のお昼は…\nアキノさんと同席する\n予定だったわね\nあんたもどう？", 121801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 6176, "ふぁ…っと、だめね\nたるんだところを\nアキノさんに\n見せるわけには", 121801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6178, "ん～…\n落ち着くわね\nこのまま\n寝ちゃいそう…", 121801, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6179, "救護院の皆も\nぬいぐるみが\n大好きなのよ", 121801, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6180, "いい香りね…\nこれはアキノさんの\n趣味かしら？", 121801, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6181, "こんなに激しく\n燃えているのに\nあったかくて落ち着く\nまるでアキノさんね", 121801, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6183, "どんどん出てくる…\nこれ、救護院にも\nほしいわね…", 121801, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6184, "ふむふむ…\n前にアキノさんが\n言ってたのは\nこのことかしら？", 121801, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6187, "この衣装、私には\nちょっとかわい過ぎて\n恥ずかしいな…", 122001, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6188, "一部の人からは\n海賊より海賊らしいと\n評判の父です", 122001, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6189, "潮風で錆びないよう\n武器の手入れを\nしておきましょうか", 122001, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6190, "宝探しのお手伝いなら\n力になれると思います\n父のやる気次第ですが", 122001, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6191, "除霊は気を遣います\n一歩間違えると父まで\n成仏させてしまうので", 122001, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6192, "あの人と見た\n海の景色を…ずっと\n忘れずにいたいな", 122001, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6193, "幽霊になっても\n一目会えるなら…\n私はきっとあの人に…", 122001, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6194, "ボートを漕ぐの、少し\n上手になったんですよ\n今度お見せしますね", 122001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6195, "航海に向けた\n買い出しが必要ですね\n予定は空いていますか", 122001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6196, "呪われた財宝の\n解呪依頼が多くて…\nふぅ…えっと次は…", 122001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6197, "夜の航海では\n父が明かり代わりに\nなってくれるんです", 122001, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6198, "船の上では食材が\n限られるので料理の\nいい練習になります", 122001, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6199, "この先もずっと\nあなたと一緒に\nたくさんの思い出を\n作っていきたいです", 122001, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 6200, "あの人の寝顔…\nもう少しだけ\n眺めていたいな…", 122001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 6201, "船の上では\n規則正しい生活が\n重要ですから", 122001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 6202, "夜の海は幽霊が\n見分けやすいんです\n青白く光るので", 122001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6204, "海に揺られるような\n心地よさですね\n気が休まります…", 122001, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6205, "あなたも一緒に\n海を見に行きますか？\n…なんて。ふふっ", 122001, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6206, "爽やかな香りですね…\n一面の青い海が\n目に浮かぶようです", 122001, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6207, "温かい…\n潮風で冷えた体に\n沁みますね", 122001, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6209, "あ、おいしい…\n日持ちするなら船旅に\n持っていこうかな…", 122001, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6210, "アンナさんに教えて\nもらった小説は…\nあ、これですね", 122001, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 6212, "なにもしないを\nしてるんだよ\nあ～いそがしいそがし", 102601, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 6213, "はぁ～あんたといると\nどこまでも気持ちよく\nダラダラできるね～", 102601, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6214, "暗黒騎士にして、\n伝説の海賊でもある\n私に不可能はないっ！", 121901, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6215, "海賊団の船員、\n随時募集中です…と\nこれで新たな盟友が\n増えるはずだ…！", 121901, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6216, "肩に乗せるペットが\nほしいな。今のうちに\nペット用品を買いに\n行かなくては…！", 121901, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6217, "財宝の地図はどこで\n売っているのだろう…\n古本屋？　古物商？", 121901, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6218, "この海賊衣装に\n身を包んでいると\n前世の新たな記憶が\nよみがえってくるな！", 121901, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6219, "いずれはシグルドと\n海原への冒険に\n繰り出したいな…\nきっと楽しいぞ！", 121901, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6220, "待ちわびたぞ盟友よ！\n私と貴様あっての\n海賊団…だからその…\n今日はそばにいてくれ", 121901, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6221, "シグルド！　港町で\nお魚のおすそ分けを\nもらったんだ！\n一緒に食べよう！", 121901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6222, "シグルドもこのコート\n羽織ってみないか？\nふふっ、きっとすごく\n似合うと思うぞ！", 121901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6223, "海賊船の新たな\n秘密兵器を作ったぞ！\nシグルド、これから\n見に来ないか！？", 121901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6224, "私は伝説の海賊だから\n泳ぐのだって得意だ！\nふ、深いところは\n少し怖いが…", 121901, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6225, "ふふ、いつ無人島に\n漂着してもいいように\n非常食は準備済みだ\nシグルドの分もある！", 121901, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6226, "冥風戦記の新章が\n完成したぞ…！\nシグルド、まずは\n貴様に読んでほしい！", 121901, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 6227, "ふっ、天気明朗\n絶好の航海日和だ！\n早速海に繰り出そう！", 121901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 6228, "昼食用に魚を\n釣っておいたぞ！\nふふ、いかにも\n海賊っぽいだろう？", 121901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 6229, "夜の海は真っ黒で\n少しだけ怖いな…\nシグルド、私の\nそばを離れるなよ…！", 121901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6231, "大海賊がくつろぐのに\n相応しい逸品だ…！\nえっと…これ、どこの\nお店で買えますか？", 121901, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6232, "わわ、かわいい…！\n船室のベッドに\n置いておきたいな…\n連れてってはダメか？", 121901, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6233, "おぉ、この香りは…！\n集中力が上がりそうだ\n海賊船の船長室に\nぜひ置きたいな！", 121901, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6234, "これが船乗りの\n伝承に聞く、海原を\nさまよう神秘の火か！\n…暖まるな！", 121901, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6236, "腹が減っては\n海賊はできぬ！\n…小説も書けぬ！", 121901, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6237, "女海賊と騎士はその後\n恋に落ち…？　恋愛\nモノではないかっ！？\nき、興味深くはあるが", 121901, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6239, "いつもの山籠もりが\nブームになるなんて…\n人生、なにが起きるか\n分からないなぁ", 122101, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6240, "キャンプ道具…\nいえ、キャンプギア！\nカッコいい響き…♪", 122101, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6241, "レッツ、ＢＢキャン！\nこのフレーズ\n流行るかも…？", 122101, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6242, "キャンプ仲間…\n募集してみようかな？\n友達も兼任で…", 122101, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6243, "ユカリさんと\nジュンさん…元気かな\nまた一緒に\nキャンプしたいなぁ…", 122101, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6244, "私の取材が載った本…\n【フォレスティエ】に\n送らなくちゃ…♪", 122101, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6245, "もし、あの人が\n落ち込むことが\nあったら…その時は\n私が力になりたいな", 122101, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6246, "ブッシュクラフトは\nマイフレンドくんの\nカスタムにも\n活かせる技術です！", 122101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6247, "会話に花が咲く…\nって言いますよね\n私の場合、会話相手が\nお花そのものですけど", 122101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6248, "この服、実は…\n昔ミサト先生に\nこしらえてもらった\n物なんです！", 122101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6249, "昔はやまびこで会話の\n練習をしてたんです\nほとんどオウム返しで\n気まずかったなぁ～", 122101, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6250, "どうもマイスターです\n…あ、すみません\nみなさんの期待に\n応えようと練習中で…", 122101, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6251, "たとえぼっちを\n卒業しても…\n私は一生ＢＢ団です！", 122101, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 6252, "小鳥さんのさえずりで\n目が覚める…\n心地いいものですね", 122101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 6253, "キャンプといえば\nおいしいご飯です！\n腕を振るいますよ！", 122101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 6254, "すっかり夜ですね…\nおやすみの前に\n星でも見ませんか？", 122101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6256, "都会でくつろぎ…\n自然の中でくつろぐ…\nくつろぎ無限ループ…", 122101, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6257, "しっかり観察すれば\nブッシュクラフトで\n再現できるかも…？", 122101, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6258, "はぁ～…やっぱり\n木の香りを嗅ぐと\n肩の力が抜けますねぇ", 122101, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6259, "ああ…あったまります\nなんだか心に\nゆとりがでますね…", 122101, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6261, "このおいしさ…！\n今度はキャンプに\n持っていこうかな…", 122101, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6262, "ふむふむ…\nいろんな知識があると\nキャンプはもっと\n楽しくなりますよね", 122101, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6264, "部屋がキャンプ用品で\n埋まっていく…\nキャンプ…なんて\n恐ろしい趣味なの…？", 122201, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6265, "仕事でも\nアウトドアでも\n頼れるお姉さんに\nなれたらいいなぁ…", 122201, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6266, "この服、かわいい上に\n本当に快適ね～♪\nおしゃれにキャンプを\n楽しめて最高だわ！", 122201, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6267, "ん〜次はどんな\nキャンプ飯を\n作ろうかしら？\n彼の好物は、っと…", 122201, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6268, "早く週末が\n来ないかしら…\n仕事を忘れてパーっと\nキャンプ飲みを…！", 122201, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6269, "新しいキャンプ飯の\nレシピを覚えたわ！\n彼も喜んで\nくれるはず…よね♪", 122201, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6270, "彼の分の食器も\nこっそり買い揃えちゃ\nおうかな…そ、それは\nさすがにまだ早いか…", 122201, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6271, "今度、財団主催で\n大規模なキャンプを\nやってみるのも\nいいかもしれないわね", 122201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6272, "ソロキャンプも\n悪くはないけど\n私はやっぱりキミと…\nな、なんでもない！", 122201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6273, "キャンプのことなら\n私に聞いて。なんでも\n教えてあげるから！\n…調べた上でね！", 122201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6274, "キャンプ場で飲む\n最高の一杯は\n他じゃ味わえないわ！\nさあ、乾杯っ！", 122201, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6275, "うん、決めた！　次は\n禁しゅわキャンプ！\nだってキミとゆっくり\n話したいんだもん♪", 122201, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6276, "キミのおかげで\n本当の最高の一杯が\n味わえたわ…\nありがとう♪", 122201, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 6277, "んー、気持ちいい！\nキャンプが趣味に\nなってから、寝覚めが\nよくなったわ♪", 122201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 6278, "お昼は私がごちそう\nするわ。キャンプ飯で\n鍛えた料理の腕前を\n披露しちゃうわよ～！", 122201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 6279, "そろそろ寝ましょ？\n明日も朝日を眺める\nためにね。もちろん\nコーヒー付きで♪", 122201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6281, "くつろぐのに\n持ってこいね～\nテントの中に置いても\nよさそうだわ！", 122201, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6282, "ふふ、かわいいわね～\n…今度キャンプ場に\n連れていこうかしら？", 122201, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6283, "うん、いい匂い…♪\nキャンプ場を思い出す\n自然の香りだわ〜", 122201, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6284, "はぁ、温まるぅ～\n冬のキャンプで\n使ってみたいわね", 122201, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6286, "おいしい～♪\nキャンプ飲みの\nおつまみに\nぴったりね！", 122201, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6287, "へえ…こんなキャンプ\n飯もあるんだ？\n彼、好きだったり\nするかな…", 122201, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 6289, "一生ついててやるの\n幽霊の一生は\nなっが～～～いの！", 100701, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 6290, "む～…あんまり触ると\nすり抜けてやるの！\nウソなの。むふふ♪", 100701, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6291, "んー…\n今日はベスちゃんと\nなにして遊ぼーかなー", 122301, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6292, "ヴァンピィちゃんも\nけんぞくの友だちと\n仲良くなりたいな♪", 122301, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6293, "けんぞくのお部屋は\nヴァンピィちゃんが\n守ってあげなくちゃ！", 122301, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6294, "お掃除するなら\nヴァンピィちゃんも\n手伝ってあげよー♪", 122301, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6295, "ふーん、ふーん♪\nけんぞくってこーゆー\nお部屋が好きなんだー", 122301, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6296, "ひまだなー\nけんぞくをさそって\nお散歩いこっかな♪", 122301, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6297, "今日のスープに\nけんぞくの大好物を\n入れてあげよっと♪", 122301, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6298, "けんぞくー！\nヴァンピィちゃんと\nお出かけしよ♪", 122301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6299, "よしよし！\n今日のけんぞくも\nいい子だね♪", 122301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6300, "んんー…\nやっぱりけんぞくと\nベスちゃんは似てる？", 122301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6301, "いーい？\nヴァンピィちゃんの\nいうこと聞くのよ？", 122301, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6302, "ヴァンピィちゃんの\nけんぞくだもん！\n大切にするからね！", 122301, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6303, "えへへっ！\nやっぱりけんぞくって\nやさしー匂いがする♪", 122301, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 6304, "おはよー…\nヴァンピィちゃんは\nもうちょっと眠るねー", 122301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 6305, "ヴァンピィちゃん\nひなたぼっこ\nだーいすき！", 122301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 6306, "んー！　夜だ♪\nヴァンピィちゃん\n絶好調だよ！", 122301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6308, "わー！\nふかふかだ！\nきもちぃー♪", 122301, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6309, "かわいー♪\nベスちゃんに負けない\n抱き心地！", 122301, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6310, "この香り…なんだろ？\nヴァンピィちゃん\n気に入っちゃった♪", 122301, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6311, "あったかいねー…\nヴァンピィちゃん\nずっとここにいるー…", 122301, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6313, "はむはむ…\nんー！\nおいしー♪", 122301, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6314, "ヴァンピィちゃん\nこの本\n好きなんだよねー♪", 122301, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6316, "夏といえば\nかき氷だよね\n近くにお店は…", 122501, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6317, "この格好でいるのも\nなじんできたし\nちょっと鍛錬でも…", 122501, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6318, "動きやすいけれど…\nいつもと違う服装は\n少し落ち着かないな", 122501, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6319, "夏の空気に\n当てられると思わず\n気が緩んでしまうね", 122501, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6320, "砂浜で走ると\nいい具合に体幹を\n鍛えられそうだ", 122501, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6321, "終わらない夏は\nないけれど…\nつい願ってしまうな", 122501, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6322, "胸を張って彼と\n並び立てるよう\n私も、もっと強く…！", 122501, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6323, "どうしたの？\n行きたい場所が\nあるなら付き合おうか", 122501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6324, "快晴で風は穏やか…\n絶好の釣り日和だね\nさあ、出かけようか", 122501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6325, "今日の予定を\n立ててるんだけど…\nキミの希望も\n聞きたいな", 122501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6326, "ビーチスポーツか…\nキミとなら\nいい勝負ができそうだ", 122501, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6327, "私たちの夏に\n遠慮は無用だよ\n思いきり楽しもう！", 122501, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6328, "いつかまた一緒に\n花火を見に行こう\n…約束だよ？", 122501, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 6329, "潮騒の音で\n目覚めるのも\nいいものだね", 122501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 6330, "今朝釣ってきた魚が\nあるんだ。これで\nお昼ご飯にしようか", 122501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 6331, "夜にもイベントが\nあるみたいなんだ\n一緒に見に行かない？", 122501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6333, "気持ちが安らぐね…\nうっかり眠って…\nしまい…そうだ…", 122501, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6334, "肌触りのよさが\nこの格好だと\nよく分かるよ", 122501, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6335, "海をイメージさせる\n香りだね…\n爽やかで落ち着くよ", 122501, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6336, "濡れたままだと\n風邪を引いてしまうよ\nほら、キミもおいで", 122501, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6338, "夏を楽しみきるには\nエネルギーが\n必要だからね", 122501, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6339, "大切な人と過ごせる\n観光スポット紹介…\n…こ、このページは\nもういいかな！", 122501, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6341, "濡らしてもいいなら\n海に持っていって\n乗るのもよさそうだ", 122501, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6342, "こういう名画を\n見た気がするな…\nこんな視点なんだね", 122501, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6343, "水着って\n動きやすくて\nいいよねー♪\nいっちにーさんしー♪", 122401, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6344, "ユイちゃんにもらった\n日焼け止め\n塗っておいて\nよかった～", 122401, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6345, "なんだか暑いなあ…\nせっかくだし\nプールで泳いで\nこようかな？", 122401, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6346, "この格好で\n出かけようとしたら\nレイさんに\n怒られちゃった…", 122401, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6347, "たとえ水着姿でも\n困ってる人を見たら\n助けに行くからね！", 122401, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6348, "バカンス楽しかった～\nまた騎士くんと\n一緒に行きたいな♪", 122401, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6349, "この水着、あたしに\n似合ってるかなあ？\n騎士くんはどう\n思ってるんだろ…", 122401, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6350, "騎士くん！\n海に行くなら\n準備運動\n忘れずにね！", 122401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6351, "泳ぐ練習がしたいの？\nいいよ！　水着だから\nなにかあったらすぐに\n助けてあげるね！", 122401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6352, "騎士くんの水着姿も\nかっこよかったよ！\nまた遊びに行こ！\n約束ね！", 122401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6353, "このグローブの向日葵\nかわいいでしょ？\nユイちゃんが\n選んでくれたんだ♪", 122401, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6354, "実はこの水着\nいつも着てる\n服とおなじなんだ…\nって、冗談だよ？", 122401, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6355, "変な日焼けしてないか\n見てもらってもいい？\n…なし！　今のなし！\nやっぱりなしー！", 122401, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 6356, "夏のお日様に\n負けないよう\n今日も元気いっぱい\nがんばるぞー！", 122401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 6357, "お腹減ってきちゃった\n騎士くんはお腹\n空いてない？\n…聞こえちゃった♪", 122401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 6358, "今日も一日\n楽しかったー！\n明日もまた\n楽しもうね！", 122401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6360, "ふわあ…\nふわふわなのが\nいつもより\nよくわかるよー…", 122401, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6361, "そうだ！　水着なら\n濡れてもいいし\n今度洗ってあげるね♪", 122401, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6362, "んー…いい匂い♪\nなんだか\n身体がぽかぽか\nしてきちゃった！", 122401, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6363, "水着だから、えっと…\n暖か涼しい…！\n不思議な感じー", 122401, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6365, "思いっきり身体を\n動かした後だから\nいくらでも\n食べちゃえるよー！", 122401, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6366, "ふむふむ…\nなるほどー\n水着の種類って\nそんなにあるんだぁ…", 122401, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6368, "乗り心地バッチリだね\nしゅっぱつしんこー！\nなんちゃって♪", 122401, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6369, "わっ、ぽよんぽよん！\nトランポリンみたいで\nた～のし～！", 122401, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 6370, "彼のことを考えてると\n幸せで胸が温かくなる\n…私ったら、もう", 101501, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 6371, "うふふふふ\nくすぐったいわ～♪\nもう、イタズラっ子ね\n次は先生の番よ～♪", 101501, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6372, "騎士クンの汗\n拭いてあげたいな…\n余計なお世話かな…？", 122601, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6373, "夏バテ気味かも…\nそうだ、今夜は\nかば焼きにしよう♪", 122601, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6374, "青い空、白い雲\n眩しい日差し…！\n夏って好きだな…", 122601, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6375, "ヒリヒリする…？\n日に焼けちゃったかな\nふー…ふー…", 122601, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6376, "花火大会があるんだ…\n夜、浜辺…2人きり…\nむ、無理だよ〜！", 122601, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6377, "水着って不思議…\n引っ込み思案が少し\nおさまる…気がする", 122601, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6378, "騎士クンとまた\n海に行きたいな…\nまた、一緒に…", 122601, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6379, "ダ、ダイエット！？\nしてないよ！？\n元々これ位だよ…？", 122601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6380, "スイカ割りのコツ？\nわたしは、騎士クンに\n教わりたいな…", 122601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6381, "騎士クン。うちわで\n扇いであげるね\nどこに置いたかな…", 122601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6382, "うぅ…暑いね…\n騎士クン、温度計は\n見ない方がいいよ～", 122601, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6383, "じ、じっと見ないで…\nええっと…ちょっと！\nちょっとならいいよ…", 122601, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6384, "え、肝試し？\nう〜ん、うう〜ん…\n…騎士クンとなら！", 122601, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 6385, "おはよう騎士クン\n見て、あっちの空\nもう入道雲が出てる", 122601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 6386, "夕立、来るかなぁ…\nもし雷が鳴ったら\nそばにいてくれる…？", 122601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 6387, "しー。…ほら、虫の声\n…わ、わたしの心臓の\n音は気にしないで…！", 122601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6389, "ふふ、水着でゴロゴロ\nするのってなんだか…\nいけないことみたい", 122601, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6390, "かわいい～♪\n日光浴させて\nあげたいな♪", 122601, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6391, "ほんのり潮の香りが…\n香ばしくて塩辛くて…\nあ…おなか鳴っちゃう", 122601, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6392, "水着、乾かせるかも…\nそうだ、みんなの分も\n持ってこよう！", 122601, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6394, "夏バテなんてしてたら\nみんなの足を\n引っ張っちゃうよね\nいただきます！", 122601, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6395, "騎士クンの、どうして\n海はしょっぱいの？\nって質問の答えは…\n分かんないよぉ～", 122601, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6397, "顔は怒ってるのに\nなんだかかわいい…\n誰かに似てる…？", 122601, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6398, "これを枕に、ビーチで\n騎士クンと…なんて…\nなんて…なんて…！", 122601, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6399, "夏休み、\n毎日たのしいな～♪\nつぎはなにしよう？", 122901, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6400, "自由研究、\nなににしよっかな～？\nプチグリフォンさんの\nかんさつにっきとか？", 122901, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6401, "夏休みって、\n給食ないから…\nそれはちょっぴり\nさみしいかも…？", 122901, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6402, "砂浜をはだしで歩くの\nきもちいいよね♪\nくすぐったいけど\nミミ、好きなんだ～♪", 122901, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6403, "きれいな貝がら\n見つけちゃった♪\nミミのたからもの！", 122901, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6404, "ミミ、浴衣を\n買ってもらったんだ♪\n今度、おにいちゃんに\n見せてあげたいな！", 122901, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6405, "ミミ、おにいちゃんと\nいっぱい夏休みの\n思い出つくりたいな♪", 122901, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6406, "ねえ、おにいちゃん…\n夏休みの宿題、\nわかんないとこ\nおしえてくれる…？", 122901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6407, "おかあさんにね、\n麦茶つくるの上手って\nほめられたんだ～♪\n飲んでみて♪", 122901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6408, "夏休みの宿題で\nポスターかくんだ～♪\nおにいちゃんを\nかいてもいい？", 122901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6409, "線香花火やりたいな♪\nミミ、長持ちさせるの\n上手なんだよ♪", 122901, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6410, "おにいちゃんの\n好きな本ってなあに？\nその本で読書感想文\n書こうかな♪", 122901, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6411, "海であそんだ後ね、\nおにいちゃんに\n髪を拭いてもらうの\nすきなんだ～♪", 122901, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 6412, "夏休みって、\nワクワクして\n早起きしちゃうよね♪", 122901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 6413, "わぁ♪　お昼は、\n流しそうめんなんだ？\nミミ、流してみたい♪", 122901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 6414, "こわい話聞いて、\nねむれないの…\nねえ、おにいちゃん、\nいっしょに寝ていい？", 122901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6416, "ちょっぴりやすんだら\nまた、いっしょに\n海であそぼうね♪", 122901, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6417, "すっごくかわいいね♪\nこの子のこと、\n絵日記にかこうっと♪", 122901, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6418, "くんくん…\nスイカのにおいだね♪\nこのにおいだいすき！", 122901, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6419, "およいだあとは、\nちゃんと体を\nあっためなくちゃね！", 122901, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6421, "夏休みのご飯って\nなんだかとくべつ\nおいしい気がするね♪", 122901, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6422, "夏の星座って\nすっごくきれいだね♪\nいつかみんなと\nいっしょに見たいな♪", 122901, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6424, "わぁ♪　この子、\nとってもかわいいね！\nおともだちに\nなりたいな♪", 122901, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6425, "ミミ、人魚姫に\nなったみたい！\nおにいちゃん、\nとなりにすわって♪", 122901, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6426, "水着姿になると\nバカンス気分を\n味わえちゃうね！", 122801, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6427, "海に行くの待ちきれ\nなくて、もう水着に\n着替えちゃった！", 122801, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6428, "海に行ったら\nずぶぬれ系のイタズラ\nたくさんしかけよう！", 122801, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6429, "部屋の中で泳ぎの練習\nしよう！　にいちゃん\nも水着に着替えて！", 122801, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6430, "お日さまとお揃いの\n水着なんだ～♪\n似合ってるかなぁ？", 122801, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6431, "海といえば冒険！\n海底に眠る財宝を\n見つけちゃおう！", 122801, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6432, "にいちゃんと一緒だと\n部屋にいても海気分♪\nスイカわりしよっか！", 122801, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6433, "にいちゃん！\n早く海に行く準備\nしなよ～！", 122801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6434, "ねーにいちゃん\n何秒息止められるか\n勝負しよーよ！", 122801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6435, "にいちゃんはアイスと\nかき氷どっちが好き？\nミソギは…どっちも！", 122801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6436, "日焼け止めクリーム\nキョウカに\nもらったんだ～", 122801, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6437, "水中メガネがあれば\n水鉄砲くらっても\n平気だね！", 122801, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6438, "水着なら、毎日夏休み\n気分！　にいちゃんも\n着てみなよ～！", 122801, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 6439, "にいちゃんおはよー！\n早く顔洗って、\nいっぱい遊んじゃお！", 122801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 6440, "にいちゃんデザートは\nイチゴのかき氷だよ！\n…激辛ってバレた？", 122801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 6441, "今日は楽しかった～！\n明日もイタズラ日和に\nなるといいな！", 122801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6443, "落とし穴の下に\nやわらかいの置けば\nケガしないよね！", 122801, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6444, "かわいいね～！\nこの子も海に\n連れてっちゃおうよ！", 122801, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6445, "このにおいを利用して\nイタズラするには…\nにいちゃんどう思う？", 122801, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6446, "水着を着て暖炉で\nあったまるのって、\n変でおもしろいね～！", 122801, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6448, "海辺で食べる\nお昼ごはんって最高♪\nってことで海行こう！", 122801, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6449, "いつか海を越えて、\n地図にも載ってない\n場所を見つけたいな！", 122801, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6451, "この口から水が出たら\nみんな驚くよね！\nぷぷ…細工しちゃお！", 122801, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6452, "変な音がするイタズラ\nを仕込んどいて…\n誰か座らないかな～♪", 122801, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 6453, "あなた様と私の愛を\n周りに見せつけて\n差し上げましょう♪", 102701, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 6454, "呼びましたか？\n呼びましたね？\nでは愛を語りましょう\nそうしましょう", 102701, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6455, "水着で歩き回るのは\n恥ずかしいな…\n誰も見てないよね？", 122701, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6456, "夏はやっぱり\nかき氷ですよね\n何色がいいかなぁ…♪", 122701, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6457, "あの人、泳げないって\n聞きました！\n私が教えてあげないと", 122701, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6458, "夏休みの宿題は\n計画的に進めましょう\n当たり前のことです！", 122701, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6459, "防犯魔石、どこに\n着けておこうかなぁ\n…ここにしようかな", 122701, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6460, "海の楽しみ方は\n予習済みです！\nあとは実践ですね♪", 122701, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6461, "水着…きっとあの人に\n見られちゃいますよね\n…褒めてくれるかな…", 122701, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6462, "触りたいんですか？\n…いいですよ\n帽子くらいなら", 122701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6463, "夏は体調管理に\n気をつけましょう\n私が管理してあげます", 122701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6464, "海に行くのが楽しみで\nたまらないんですか？\n私はそこまで…嘘です", 122701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6465, "いつもより薄着だから\nなんだか恥ずかしい…\nみ、見ないでください", 122701, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6466, "へんたいふしんしゃ～\nえっ？　ああ、これは\n海に叫ぶ練習です", 122701, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6467, "あなたと一緒に\n海に行くなんて…\nなんだか大人…えへへ", 122701, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 6468, "おはようございます\nさぁ、海に行って\n私と運動しましょう", 122701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 6469, "あっつ～い…\nはっ！？　ダメですよ\nシャキッとしないと！", 122701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 6470, "明日も海で\n遊ぶために、しっかり\nお休みしましょうね", 122701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6472, "夏を楽しむときは\n休憩も大事ですよね\n計画的に、計画的に！", 122701, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6473, "かわいいなぁ～\n海のお土産屋さんに\n売ってるかなぁ？", 122701, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6474, "大人な香りがします\n私も違いが分かる\n素敵な女性になります", 122701, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6475, "濡れた体を\n乾かしましょう\nタオル、タオル…", 122701, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6477, "泳いだ後は\n栄養補給…だけど\n…おなか膨らんじゃう", 122701, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6478, "遊ぶだけじゃなくて\nお勉強もしてますよ\nあなたもどうですか？", 122701, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6480, "ミミが喜びそう…♪\nわ、私ははしゃいだり\nしませんけど…っ！", 122701, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6481, "ミソギがいたずらに\n使いそう…しっかりと\n試しておかないと！", 122701, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6482, "あたしがまさか\nここにいるなんて…\n自分でも不思議だわ", 123001, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6483, "昔みたいにあちこち\n飛び回れないのって\nちょっと不便かも", 123001, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6484, "こうして誰かと一緒に\n暮らせるのもあいつや\nみんなのおかげね", 123001, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6485, "ここで厄介になるなら\nあたしも家事とか\n手伝わないと…", 123001, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6486, "…だめ！　誰かの\n世話をせずには\nいられないわ！", 123001, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6487, "あいつたちが\n帰ってくるところだし\n綺麗にしないと…", 123001, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6488, "せっかくあたしが\nいるっていうのに\n少しは構いなさいよ…", 123001, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6489, "おいしい店とか\n知らないの？\n食べに行きましょ", 123001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6490, "あんた暇でしょ？\nあたしのやること\n手伝いなさい", 123001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6491, "コッコロたんと\n遊んでくるから\n留守番よろしく♪", 123001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6492, "あらおかえり…って\nボロボロじゃない！\nまた人助けしてたの？", 123001, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6493, "やっぱりあたしは\nあんたのそばが\n一番落ち着くみたい", 123001, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6494, "どこまでだって\nあんたと一緒に\nいてやるんだから", 123001, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 6495, "ふわぁ…おはよう\n…大きくなったせいか\n朝は特に体が重いわ", 123001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 6496, "お昼ご飯が食べたい？\nしょうがないわね…\nなんか作って\nあげるから", 123001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 6497, "あんたまだ成長期だし\nさっさと寝なさい\n大きくなれないわよ？", 123001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6499, "たまにはあたしも\nゆっくりしないと\nもたないのよ…", 123001, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6500, "かわいいじゃな～い♪\nコッコロたんに\nプレゼントしたいわ", 123001, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6501, "いい香りね\n塔にもこういうの\n置いておこうかしら", 123001, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6502, "炎か…前は\n羽が焼けそうで\n怖かったっけ", 123001, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6504, "あむ…妖精に\n体重増減の設定って\nあったっけ…？", 123001, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6505, "記憶のデータと違って\n物語はいいわね\n結末が読めなくて", 123001, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6507, "冒険のお土産\nみんなにおすそわけ\nせなあきまへんな", 123101, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6508, "おとぎ話よりも\n不思議なことが\nあるもんやねぇ", 123101, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6509, "冒険しに出かけるなら\n動きやすい服装に\nせななぁ", 123101, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6510, "地図に水筒に…\n他に忘れもんは\nあらへんやろか？", 123101, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6511, "これまでの冒険のこと\nノートに書いて\nまとめてますんよ", 123101, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6512, "王子はんとの冒険は\nどれもマホマホ王国の\n大事な歴史や♪", 123101, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6513, "どこへ冒険するときも\nうちと王子はんは\n一緒どす♪", 123101, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6514, "魔法で火ぃつける\n練習してますんよ～\nよう爆発しますけど…", 123101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6515, "うちと王子はんで\n冒険道具をお揃いに\nするんもええなぁ♪", 123101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6516, "テント張りも随分\n上達したんよ\n王子はんのおかげやね", 123101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6517, "ランスロットたちの\n新しい冒険衣装\n思いついたんよ♪", 123101, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6518, "迷子になっても\n王子はんと一緒なら\nきっと大丈夫やね♪", 123101, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6519, "王子はんのこと、\nきっちり守れるように\nなってみせますえ", 123101, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 6520, "お寝坊さんは\n布団めくって\n起こしにいきますえ～", 123101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 6521, "支度が終わったら\n今日の冒険に\n出かけまひょか", 123101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 6522, "もうちょっと\nおしゃべりしたいん？\nあと少しだけやで♪", 123101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6524, "冒険で疲れた体に\nこの癒しぱわーは\nあきまへん…すや…", 123101, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6525, "ふふ、あんさんも\nうちと一緒に\n冒険します？", 123101, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6526, "虫除けのアロマは\n冒険の必需品どす\n忘れんようにせんと", 123101, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6527, "うちも魔法使わんでも\n火ぃ起こせるよう\n練習せんとあかんなぁ", 123101, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6529, "この料理…\n冒険するときに\n作ってみまひょか", 123101, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6530, "王子はんとの\n愛と冒険のおとぎ話…\nはぁ…素敵やわぁ♪", 123101, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 6532, "あの人との\n最良の未来のために\n占いも霊能力ももっと\n精進しなきゃ", 103101, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 6533, "あなたともっと深く\nつながりたいです\n絆も、そして魂までも", 103101, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 6534, "絆の契約…\nあやつが望むのなら\n考えぬでもないがのう", 104401, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 6535, "なんじゃ？\nわらわの姿に\n見惚れたかの？", 104401, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6536, "旅の荷物って多いな…\nねぇぷうきち～\nすこしは持ってよぉ～", 123201, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6537, "傘はなくてもいっか\nぷうきちがいるし♪\n『おいなんだと…？』", 123201, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6538, "旅先で人見知りなんか\nしてちゃだめだよね…\nまずはあいさつだ…！", 123201, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6539, "おみやげはやっぱり\nご当地ぬいぐるみ\nだよね♪　ワクワク…", 123201, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6540, "知らない場所かぁ…\nうぅ…考えただけで\nすでにホームシック…", 123201, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6541, "野宿するってことは…\nお兄ちゃんのマントで\n寝るチャンスかも…！", 123201, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6542, "どんなに危険な冒険も\nお兄ちゃんと一緒なら\n大丈夫！　…だよね？", 123201, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6543, "ぷうきちのアイマスク\nも腹巻も持ってくの！\n旅の必需品なの～！", 123201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6544, "これ全部背負うのって\nけっこうむずかしいの\n順番まちがえると大変", 123201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6545, "えへへ～\nなでなでして？　帽子\nの上からでもいいよ♪", 123201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6546, "『悪いが絆創膏の部分\nはアンタッチャブルだ\n綿が飛び出ちまう』", 123201, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6547, "お兄ちゃんと一緒に\nもっともっといろんな\n景色が見たいな！", 123201, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6548, "旅って、終わる時は\nきっとさびしいよね…\nお兄ちゃん、帰り道は\n手をつないでもいい？", 123201, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 6549, "出発が楽しみすぎて\n寝られなかった…\n『旅行のお約束だな』", 123201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 6550, "わぁ　いいお天気！\n旅の間もずーっと\nぴーかんだといいな♪", 123201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 6551, "知らない場所で寝るの\n怖いな…寝る時だけ\n帰ってこようかな…", 123201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6553, "旅先にはクッションも\nソファもないよね…\n『俺でいいだろ』", 123201, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6554, "ぬいぐるみのみんな\n旅の無事を\n祈ってて…！", 123201, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6555, "旅先の香りも\n思い出に持って\n帰れたらいいのにな～", 123201, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6556, "旅行用に携帯用暖炉\nがあるといいかも…！\n『まだ荷物増やす？』", 123201, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6558, "『もう出発だぜ？』\nえーまってまって！\n腹ごしらえがまだ…", 123201, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6559, "世界ってこんなに広い\nんだ…！　はやく自分\nの目で見てみたいな～", 123201, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6561, "はちゃめちゃヒマ～\nなーんか楽し～コト\nないかなー", 123301, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6562, "ここ、いートコだねー\nこんどカリザきゅんも\nつれてこよー", 123301, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6563, "かわゆいショタが\n見た～い♪\n触りた～～～い♪", 123301, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6564, "なんか服きっついなー\nちょい太ったかー？\nん～\nま・気のせーっしょ♪", 123301, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6565, "あ～やべ\nおねえちゃんに\nメッセすんの忘れたー\nまいっかー", 123301, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6566, "パンケーキ食いてー\n騎士きゅん\n誘ってみよーっと♪", 123301, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6567, "きょーも～\n好きぴに囲まれて～\nシ・ア・ワ・セ♪", 123301, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6568, "アタシと遊ばな～い？\nってなんか\nナンパみスゴー\nウケる～", 123301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6569, "んー？\n今はだるいからパース\nあとでねー", 123301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6570, "カリザきゅん\n見なかった～？\nおねえちゃんから\n逃げるとかオシオキー", 123301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6571, "ネイルしたいけど\n無理なんよねー\n爪の間にめっちゃ\nスライム入っからさー", 123301, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6572, "おねえちゃんに\nかまってほし～の？\nかわゆ～い♪", 123301, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6573, "な～に騎士きゅん♪\nここでイチャイチャ～\nし・ちゃ・う～？", 123301, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 6574, "ねんむ～\nけど外から\nショタの声したんで～\nつい起きちった～", 123301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 6575, "ランチの後はやっぱ\nデザートっしょー\nベツバラってヤツ～♪", 123301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 6576, "こんな時間まで\n起きてるコは～\n食べちゃうぞ～♪", 123301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6578, "あーラクー\nいっしょーここで\nキューケーできるわー", 123301, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6579, "なにこれ～\nかわいいじゃ～ん\nショタの次くらいに～", 123301, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6580, "いーニオイする～\nなんのニオイとかー\nよくわかんないけどー", 123301, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6581, "あったか～♪\n溶けてスライム\n戻りそ～", 123301, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6583, "ン・ン・ン～♪\nお・い・し～♪\nも～っと食べるぅ～♪", 123301, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6584, "わ～なんかムズー\n読めなー\nウケるー", 123301, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6586, "あんパンって\nお菓子に入るのかな？\nハロウィンで\nもらえたりしない？", 123501, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6587, "お菓子をくれなきゃ\nぐうたらするぞ～！\nハロウィン以外でも！", 123501, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6588, "お化けになったら\n仕事しないで\nゴロゴロし放題\nなのかな？", 123501, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6589, "おっと…この仮装\n袖がすごく長いからさ\n引っ掛けないように\n気を付けないとね～", 123501, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6590, "イタズラするのも\n体力がいるよね～\n楽にできそうなの\n何かないかな？", 123501, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6591, "あいつって\n仮装とかするのかな？\n似合いそうなのは\nどんなお化けだろ？", 123501, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6592, "ずっとハロウィンなら\nあいつと遊び放題\nなんだけどな～", 123501, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6593, "今の街はハロウィン\n一色だからね～\n人混みが大変だよ", 123501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6594, "この仮装、どうかな？\n気に入ったなら\n来年もしてあげるよ～", 123501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6595, "実はこの長い袖\nドングリとかを\nため込めるんだよね…", 123501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6596, "カボチャランタンが\nあるんなら\nドングリランタンも\nあっていいよね～♪", 123501, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6597, "ハロウィンでも\nやる気があれば\nぐうたらできる…\nうん、いいこと言った", 123501, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6598, "一緒にお菓子食べよ？\n寝転びながら堪能する\nハロウィンスイーツは\n最高だからね～", 123501, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 6599, "お化けは寝る時間…\n今のあたしはお化け…\nだから、おやすみ～…", 123501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 6600, "お昼か～…\n食後のデザートは\nハロウィンのお菓子で\n決まりだね～♪", 123501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 6601, "ふぁ～、寝よ…\nお化けは夜に\n出るものだけど\nあたしはリスだからね", 123501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6603, "ハロウィンを楽しむ\nためにも、ぐっすり\n寝とかないとね～", 123501, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6604, "…袖から手が\n出せないから\nこのまま撫でよう\nよしよし", 123501, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6605, "リラックスできる\n素敵な香りだね～\n成仏しそうだよ～", 123501, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6606, "あったかいね～\nここで寝てもいい？\n…着替えないとダメか", 123501, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6608, "もぐもぐ…\nお化けもリスも\nお腹は空くんだよ", 123501, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6609, "キョンシーって\nどんなお化けなのか\n本で調べようかな？", 123501, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6611, "より取り見取りだね～\nとりあえず、これと\nこのお菓子はキープで", 123501, 3, 12);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6613, "よし、衣装もばっちり\nハロウィンパレードへ\n出かけよう♪", 123601, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6614, "仮装してても\n剣は手放せないね\nほとんど体の一部だよ", 123601, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6615, "見回りに誘うふりして\n二人でパレードに\n参加しちゃおうかな♪", 123601, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6616, "…あれ？　手錠の鍵\n落としちゃった…？\nやば、出かける前に\n見つけないと…！", 123601, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6617, "お家でコスプレも\nこれはこれで楽しいな\n決めポーズの練習\nたくさんしておこう！", 123601, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6618, "他にも、お姫様に\nメイドさんに学生服…\nやりたいコスプレが\n多すぎるよ～", 123601, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6619, "漫画もゲームも好き！\n仮装するのも大好き！\n…好きを言葉にすると\nもっと好きになる♪", 123601, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6620, "ずっと目が合ってるね\n見つめてくれるのは\nいいんだけど、今日は\n衣装を見てほしいな♪", 123601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6621, "トリックオアトリート\nお菓子をくれなきゃ\n逮捕しちゃうぞ♪", 123601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6622, "あなたにだけはこの姿\nいつでも見せてあげる\nふふ、なんちゃって♪", 123601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6623, "どうしたの？\nイタズラするなら\n隙を突かなきゃ\nこんなふうに、ね♪", 123601, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6624, "コスプレしてても\n剣の稽古は休まないよ\nちょっと相手になって\nくれるかな？", 123601, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6625, "ひゃっ、びっくりした\n急に触らないでよ～\nハロウィンだからって\nイタズラしないでね？", 123601, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 6626, "おはよう！\n今日も朝から\nパトロールに行こう！", 123601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 6627, "カボチャのケーキを\n用意してるよ！\nデザートを食べて\n午後も頑張ろう！", 123601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 6628, "暗くなってきたね\nカボチャのランタンに\n火を灯そうよ♪", 123601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6630, "ふかふか気持ちいい～\nあっでも衣装がシワに\nなっちゃうかも…？\n…でも気持ちいい～", 123601, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6631, "…かわいい\nこの子もパレードに\n連れて行きたいな", 123601, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6632, "はぁ～癒される♪\nハロウィンの見回りが\n終わった後は、\nまったりくつろごう", 123601, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6633, "ここで二人寄り添って\nカボチャスープ飲もう\n…なんて想像したら\nポカポカしてきた…", 123601, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6635, "パレードに参加すると\n体力を使うから、\nいっぱい食べておこう", 123601, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6636, "この絵本のお姫様\nかわいいね\nこの仮装もいいな♪", 123601, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6638, "わあ、おいしそう！\n見回りに行く前に\n少しつまんじゃおう♪", 123601, 3, 12);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 6640, "今日こそは、あの人に\n気持ちを打ち明けて…\nそのあとは…うう\nど、どうしよう？", 103701, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 6641, "私が剣なら、あなたは\nさしずめ私の鞘…\nふふ、私の帰る\n場所ってことだよ♪", 103701, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6642, "やっぱり\nコスプレしてると\nテンションあがる～♪", 123701, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6643, "キャラコスにも\n興味あるけど…\nマジカル、ラブリー\nどっちにしよっかな～", 123701, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6644, "キャラコスといえば\nミスティーシャイニー\nもいいんだよねえ…\nうはー！　悩むー！", 123701, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6645, "ギルドハウスで\n宅コスしてる人\n実は結構\nいるんじゃないかな～", 123701, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6646, "コスプレだけど普段着\nって言いはれそう…\nって、ダメダメ！\nオタクは節度が大事！", 123701, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6647, "フェスで買ってきた\n薄い本～♪\nギルドハウスに持って\nきちゃおっかな～", 123701, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6648, "ようこそ\nコスプレ沼へ～\nなんちゃって♪", 123701, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6649, "どうどう？\nナナカちゃんの\nハロウィンコスプレ\n似合ってる？", 123701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6650, "ハロウィン魔法少女\n参上カボ～！\n…やっぱ今の語尾\nナシで！", 123701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6651, "ハロウィンといえば\nトリックオア～…\nコスプレ！\nなんちゃって", 123701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6652, "ランフェスの\n戦利品～♪\nねえねえ\n見たい？　見たい？", 123701, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6653, "私にしてほしい\nコスプレがあったら\n教えて♪　君のために\nや～ってやるぜぇ！", 123701, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6654, "一緒にコスプレしたい\nならナナカちゃんは\nいつでも大歓迎だよ♪", 123701, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 6655, "ふぁあ…ねむ\nって、ありゃりゃ　\n昨日コスプレしたまま\n寝ちゃってたかあ…", 123701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 6656, "今日のお昼は\nなんにしよ？\nカボチャ料理に\nしよっかな～", 123701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 6657, "もうこんな時間かあ\nそういえばオタクって\n夜行性の人が多い\n気がするぜぇ…", 123701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6659, "ふっふっふ\nこのパニエは\n座るときはちゃんと\nふにゃってなるのさ！", 123701, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6660, "いや～\nかわいいものをみると\n癒されますな～", 123701, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6661, "新しい薄い本の匂いの\nアロマとか…ダメ？\n私はあの匂い、なんか\n好きなんだよね～", 123701, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6662, "暖か～い…\nけど近づきすぎて\n衣装が燃えないように\n気を付けなきゃ", 123701, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6664, "コス衣装のために\n体型維持は必須だけど\nついつい\n食べちゃうんだよね～", 123701, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6665, "おっ、次はこの本に\nのってるキャラのコス\nしてみよっかな？", 123701, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6667, "おおっ！\nお菓子がいっぱい！\nベルルちゃんチョコ\n入ってないかな～？", 123701, 3, 12);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6669, "むっ…\nこの耳の手触り…\n驚嘆に値する…！", 123801, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6670, "獲物を狩りまくる…\nそんなウサギがいても\nいいだろう？", 123801, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6671, "ワナなど怖くないぞ？\nむしろ積極的に\n踏んでいく性分だ", 123801, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6672, "一匹狼ならぬ\n一匹ウサギか\nどっちでもかまわん♪", 123801, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6673, "人間とて本を正せば獣\n本能に従えばよいのだ\n遠慮は無用さ☆", 123801, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6674, "脱兎のごとく…\n逃げるものか\n坊やを追い回すのさ♪", 123801, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6675, "獣の道は険しいが…\n坊やめ、音を上げずに\nちゃんとついてきたな\n褒めてやるとしよう♪", 123801, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6676, "ニンジンはいらん\n好物は好敵手だ\nさぁどんどんおくれ♪", 123801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6677, "尻尾に触りたいだと？\nワタシの乱数聖域を\n崩せたらな…☆", 123801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6678, "頭で考えるな\n体に率直になれ\n己を野放しにするのさ", 123801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6679, "そうさモコフワさ♪\nなで心地は最高だが\n意外にどう猛だぞ…？", 123801, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6680, "ワタシにピョンピョン\n跳ねてほしいのか？\nあとでじっくり、な♪", 123801, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6681, "ワタシと番になるか？\nオマエさえよければ\nいつでもかまわんぞ☆", 123801, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 6682, "太陽と共に目覚め\n活動する\nそれが獣の生き方さ☆", 123801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 6683, "昼寝をすればいい\nワタシが周囲を\n警戒していてやる", 123801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 6684, "暗くなれば眠るだけさ\nそうすれば余計な\nストレスなどたまらん", 123801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6686, "むむっ！？\n尻尾がつっかえて\nうまく座れんな…", 123801, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6687, "愛らしさでワタシに\n張り合おうというのか\n５億年後に出直せ☆", 123801, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6688, "野生の芳香か…\n本能が呼び覚まされる\nいいセンスだ♪", 123801, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6689, "寒さも野生のうちだ\n…とはいえ\n温まっていくか♪", 123801, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6691, "悪くはないが物足りん\nこう見えて肉食なのだ\n肉はないのか肉は☆", 123801, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6692, "ウサギ族は地獄耳？\nワタシとの共通点だな", 123801, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 6694, "ここが私の家…\nそれはこれからも\nずっと変わらないわ…", 106401, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 6695, "ええと、その…今から\nデート…じゃなくって\n散歩でもどうかしら？\n…ふ、二人きりで…", 106401, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6696, "ギルドハウスに\nヒーロー参上！\nみんな仲良くッスよ！", 123901, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6697, "料理もできて\n鎧も作れる！\nお母さんはすごいッス", 123901, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6698, "ううう…\nまたマントをドアに\n挟んじゃったッス…", 123901, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6699, "ずっと鎧を着ていれば\nジュンさんに近づける\nかも？　燃えるッス！", 123901, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6700, "サインの練習…\nもっとしておいた\nほうがいいッスかね～", 123901, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6701, "半人前でも\n胸を張るッス！\nそのうち似合う日が\nくるッスから！", 123901, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6702, "種族の違いも\n強い魂があれば\n超えられるッス！\n信じてるッスよ！", 123901, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6703, "困ったときは\n自分を呼ぶッス！\nあと楽しいときにも\n呼んでほしいッス！", 123901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6704, "特訓でも行くッスか？\n望むところッス！\n目指せ、一人前！", 123901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6705, "新しいショーの台本を\n読みこんでるッス！\nところで…この言葉の\n意味、分かるッスか？", 123901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6706, "ヒミツがなくなると\nなんか恥ずかしいッス\nあんたはどうッスか？", 123901, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6707, "ヒーローは遅れて\nやってくる…\nあんたもそうッスね♪", 123901, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6708, "誰よりも早く\nあんたに見せたいッス\n一人前になった自分！", 123901, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 6709, "うーん、今日も\nよく眠れたッス～！\n絶好調、間違いなし！", 123901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 6710, "おお、もうお昼ッスか\nどうりでお腹が空くと\n思ったッス～えへへ", 123901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 6711, "パトロールのあとは\n歯みがきに宿題に…\n大忙しッスね、夜！", 123901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6713, "こんなにリラックス\nできるなんて…\n平和な証拠ッスね～", 123901, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6714, "か、かわいい…♪\nさあ、自分と握手…\nううん、抱っこッス♪", 123901, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6715, "優しい匂いッスね…♪\nこれくらいみんなを\n包み込めるように\nなりたいッス～", 123901, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6716, "ひっくちゅ！　うー…\nちょっと冷えるッスね\nぬくぬくするッス～♪", 123901, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6718, "特訓した分、\nいっぱい食べるッス！\nでっかくなるッス！", 123901, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6719, "いつか自分の\nヒミツ図鑑が出たり\nして…いやいやいや", 123901, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6721, "牢獄よりは\nマシな環境ね\n我慢してあげるわ", 106901, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6722, "退屈ね…\nキャルでもいじって\n暇をつぶそうかしら", 106901, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6723, "ここにいる連中\n私を天気予報士と\n勘違いしてない？", 106901, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6724, "音波情報が多いわね…\nここ、もう少し静かに\nならないの…？", 106901, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6725, "共同生活なんて\n久しぶりだわ\n…懐かしいわね", 106901, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6726, "あの子たちのギルド\n経営が雑すぎるわ…！\n教育が必要みたいね", 106901, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6727, "とどめを刺される\n準備はできているのに\nまだなのかしら…？", 106901, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6728, "私と戦う気になった？\nあら、まだなのね…\nその時を待ってるわよ", 106901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6729, "私というリソースは\n有効活用しなさい\n子守なんてごめんだわ", 106901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6730, "どうしてそんなに\n頭がいいか？\n生まれつきよ", 106901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6731, "視力は権能に\n影響しないわよ\n当たり前じゃない", 106901, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6732, "あなたまで天気を\n教えろですって…？\nぶっ殺すわよ？", 106901, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6733, "暇なら付き合いなさい\n好きなゲームでいいわ\nどうせ私が勝つもの", 106901, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 6734, "人生朝露…\n悔いのない一日を\n過ごしなさい", 106901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 6735, "午睡はいいわよ…\nふわぁ…眠りは思考に\n必要不可欠なの…", 106901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 6736, "夙興夜寝なんて\nやめておきなさい\n休息は大切よ", 106901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6738, "私を満足させるなんて\n大したものね…晶に\n大量生産させるわ", 106901, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6739, "キャルの代わりに\nズタズタにする物が\n欲しかったの…♪", 106901, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6740, "いい香りじゃない\n見たことのない調合ね\n誰が用意したの？", 106901, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6741, "パワーが足りないわね\nもっとよ、もっと私を\n寛がせなさい", 106901, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6743, "栄養価はともかく\n味のバランスは\n悪くないわね", 106901, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6744, "つまらない本は\n私が書き直すわ\nこれはどうかしら？", 106901, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6746, "今年はアカリと\nお姉ちゃんでサンタが\n二人…つまり幸せも\n二倍ですね♪", 124001, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6747, "クリスマスに向けての\nサックス練習…\nお兄ちゃんと一緒に\n猛特訓です！", 124001, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6748, "クリスマスツリーの\n飾りつけ、完璧です♪\nお兄ちゃんお姉ちゃん\n喜んでくれるかな？", 124001, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6749, "わあ、お外が真っ白！\n生クリームが\nいーっぱいの\nケーキみたいです♪", 124001, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6750, "煙突から入ると\nお洋服が汚れちゃう…\n玄関からじゃ\nダメなのかなぁ？", 124001, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6751, "お兄ちゃんが来たら\nサンタのお洋服\nいっぱい褒めて\nもらおっと♪", 124001, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6752, "聖夜をお兄ちゃんと\n過ごせたら…\nアカリもちょっぴり\n大人になれるかも♪", 124001, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6753, "今のアカリは\nサンタさんですから♪\nなんでもお願いして\nくださいね？", 124001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6754, "お外は寒いですから\nアカリとくっついて\nお出かけしましょ♪", 124001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6755, "お兄ちゃんは\nアカリのトナカイさん\nですから、ずーっと\nそばにいてください♪", 124001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6756, "お兄ちゃん、助けて！\nアカリのカラダで\nラッピング練習したら\n絡まっちゃいました…", 124001, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6757, "プレゼント、いっぱい\n準備できました♪\nお兄ちゃんの分は…\nまだヒミツでーす♪", 124001, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6758, "アカリ、プレゼントは\n大人っぽいのがいいな\n早くお兄ちゃんに\n追いつきたいから♪", 124001, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 6759, "おはようございます♪\nふふ、アカリサンタは\nいい子のお手本なので\n早寝早起きなんです♪", 124001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 6760, "あれ、もうお昼？\nクリスマスナンバーの\n練習してたら\nもうこんな時間…", 124001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 6761, "冬の夜空は星が\nキレイですね～\nお兄ちゃん、一緒に\n見に行きませんか？", 124001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6763, "はぁ、快適すぎて\nとろけちゃいそう…\nこういうプレゼントも\nいいかもです…", 124001, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6764, "やん、かわいいです♪\nえへへ、お兄ちゃんは\nどんなプレゼントが\n欲しいですか～？", 124001, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6765, "わわ、大人な香り…\nロマンチックな\nクリスマスが\n過ごせそうです♪", 124001, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6766, "これでカラダを温めて\nアカリの温もり…\nお兄ちゃんに\nおすそ分けしよっと♪", 124001, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6768, "お腹がパンパンです…\nでもこれで\nちょっとサンタさんに\n近づけたかも…？", 124001, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6769, "クリスマスイブの\n恋人との過ごし方…？\nはわわ、アカリには\nまだ早いかもです…！", 124001, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6771, "わっ、すごいすごい！\n不思議な絵本ですね♪\nこの本でお兄ちゃんに\nイタズラしちゃお♪", 124001, 3, 14);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6772, "アカリ、ホカホカに\nなっちゃいました～♪\n…ぎゅってしても\nいいですよ？", 124001, 3, 15);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6773, "またエンジェルになる\nかもとは思ってたけど\n…サンタさん要素の\n追加は想定外だったわ", 124101, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6774, "ラフィたちがいない\nときは私がなんとか\nしないとね\n一応…エンジェルだし", 124101, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6775, "部屋の飾りつけとか\nするのも…あり？\nふふ、浮かれすぎかな", 124101, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6776, "パーティーだったら\nみんなで大人数向けの\nボードゲームもできる\nかも…やったっ", 124101, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6777, "この服のモコモコ…\n気づいたら触っちゃう\n…やるわね、ラフィ", 124101, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6778, "アイツと一緒にいると\n人見知りとは違う\n緊張があるのよね…\nって今の聞いてたっ？", 124101, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6779, "ぬいぐるみ…\nいじめてないわよね？\n…大事にしてるって？\nそれはそれで…あうぅ", 124101, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6780, "いい子と悪い子って\nどう見分けるのかしら\n…まさか素直さとか？\nだったら困る！", 124101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6781, "プレゼントといえば\nやっぱりゲーム！\n年末商戦サイコー♪", 124101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6782, "サンタさんはお仕事を\n見られちゃ\nいけないのよね？\n…天職？", 124101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6783, "この服は汚れ対策も\nバッチリなのよね\n煙突に入れるように\n…いや私はNGよ！？", 124101, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6784, "アンタのお願い…\n今なら一つくらい\n聞いてあげるわよ？\nほら、サンタ衣装だし", 124101, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6785, "私たちって\nなぜかぬいぐるみと\n縁があるわよね\n…ふふ", 124101, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 6786, "お、おはよっ\n…ふぅ、寝ぐせを\n直すのが間に合って\nよかったぁ", 124101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 6787, "え…一緒にごはん？\nそ、そう…うん。OK\nまあ、せっかくだし？", 124101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 6788, "この時間のゲームって\n妙に楽しいのよね\n…ということで、\nちょっと遊ばない？", 124101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6790, "ああ～ダメになるぅ…\nもうここでずっと\nゲームしてた～い", 124101, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6791, "プレゼントのために\n研究は必要よね、うん\n…はぁ、かわいい～♪", 124101, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6792, "集中力を高める香りも\nあるのよね…\n勝負をするときには\n持ち歩こうかしら", 124101, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6793, "なんだかまったり\nしちゃうわね…\n時間が溶けるわ…", 124101, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6795, "お腹いっぱい\n食べられるのも\n幸せなことよね", 124101, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6796, "ちっちゃいときは\nアカリと一緒に絵本を\n読んだなぁ…懐かしい", 124101, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6798, "飛び出す絵本…\nこれでゲームブックを\n作ったらおもしろく\nなる気がするわ…", 124101, 3, 14);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6799, "あったまる…\nモフモフ…ふわふわ…\nちょっと休憩しても…\nいいよね…？", 124101, 3, 15);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 6800, "あら、どうしたの？\nもしかして、私と\n新しい実験を\n始めたくなった？", 105101, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 6801, "調子が悪いときは\n診療所に来て\n君は特別に、じっくり\n診察してあげるわ♪", 105101, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6802, "特別じゃなくても\nまったり過ごす\nクリスマスも\nいいものだね", 124201, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6803, "ニットの帽子に\nマフラーに耳当て…\n素顔用の防寒対策を\n整えなければ", 124201, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6804, "靴下が\n見当たらなかったから\n枕元にはかわりに\n鎧を置いておこう", 124201, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6805, "聖女なんて言われる程\n素晴らしくはないけど\n心は聖くいたいものだ\n日々精進だね", 124201, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6806, "兜がないから\nイルミネーションが\nいつもよりまぶしく\n感じるよ", 124201, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6807, "皆の幸福を\n祈願して\nクリスマスリースを\n飾ろうか", 124201, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6808, "私にはかわいすぎる\n衣装だが…開き直って\nクリスマス気分を\n楽しんでしまおう", 124201, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6809, "ツリーの飾りつけ\n手伝ってくれるかい？\nセンスは少年に\nお任せするよ", 124201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6810, "パーティーは\n用意してる時も楽しい\nねえ少年、この気持ち\n分かってくれるかい？", 124201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6811, "なんでトナカイの鼻は\n赤いのかって？\n無灯ソリ運転は違反で\n取り締まられるからだ", 124201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6812, "トモちゃんや\nマツリちゃんは\nいい子だから、きっと\nサンタさんに会えるね", 124201, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6813, "ヒイラギの髪飾り\n実は気にいってるんだ\n少年にも\n褒めてもらいたいな", 124201, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6814, "ねえ少年…そろそろ\n見慣れてほしい\nそんなにまじまじ\n見つめられると照れる", 124201, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 6815, "朝日に照らされて\n街がキラキラ白銀に\n輝いてるよ", 124201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 6816, "お昼ご飯を食べて\nちょっと散歩しようか\n祝祭に沸く街は\nとてもきれいだよ", 124201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 6817, "やっぱりクリスマスは\n夜が本番って感じだね\nキャンドルを灯して\n厳かに過ごそうか", 124201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6819, "鎧姿では無縁だったが\nふかふかしていて\n気持ちいいな…\nふかふか…ふかふか…", 124201, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6820, "ふふ、かわいいね\n抱きしめると\nあの雪の日に産まれた\n赤ちゃんを思い出す", 124201, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6821, "兜を脱いだおかげで\nこの香りに出会えた\n…クリスマスの魔法も\n悪くないかもね", 124201, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6822, "指先から体の芯まで\n暖まるね\nこれなら雪の日でも\n安心だよ", 124201, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6824, "もぐ、もぐ…\nあまり食べるところ\n見ないでほしい", 124201, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6825, "素敵なレストランの\n特集記事が載ってる\n聖夜に予約が取れたら\n最高に幸せだろうね", 124201, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6827, "わっ！\n…………いや\n驚いてなどいない", 124201, 3, 14);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6828, "ふふ…頬に直接\nふかふかを感じられる\n温かくて気持ちいいよ", 124201, 3, 15);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6829, "ピアノに読書…\nグレアと一緒に\nなにをしようかな～♪", 124301, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6830, "元の世界へ戻った時\n授業に遅れないよう\nグレアと勉強しとこう", 124301, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6831, "街に魔物料理屋さんが\nあるんだ！　グレアと\n食べに行ってみよう♪", 124301, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6832, "せっかく新しい魔法を\n覚えたし、グレアにも\n見てもらいたいな！", 124301, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6833, "グレアも誘って\nランドソルをもっと\n探索したい！", 124301, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6834, "私たちも\nあの人を見習って\nバイトとかしよう！", 124301, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6835, "いつかあの人を\n私たちの学院に\n招待したいな！", 124301, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6836, "あのね！\nこれからグレアと\n魔法愛好会に\n行くんだ！", 124301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6837, "これからなにするの？\n私たちもついて行って\nいいかな！？", 124301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6838, "グレアと一緒に\nあなたも魔法の練習\nしてみない？", 124301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6839, "グレアとよく話すの\nマナリアの制服\nあなたに着せたいって", 124301, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6840, "グレアのそばって\n温かいんだ～！\nぬくぬくだよ～♪", 124301, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6841, "あなたは\nグレアと私の友達！\nこれからもずっと！", 124301, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 6842, "おはよう！\n今朝はグレアと一緒に\n外を散歩してきたよ！", 124301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 6843, "お腹空いたな…\nよし、グレアと一緒に\n魔物料理を作ろう！", 124301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 6844, "もうこんな時間だね\n明日のために\nそろそろ寝ようかな！", 124301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6846, "はぁ～この感触…\nグレアの尻尾みたいで\n安らぐ～", 124301, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6847, "ぬいぐるみって\nいいよね…私もよく\nグレアに抱き付くんだ", 124301, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6848, "いい香り～…\nグレアもこういうの\n好きなんだよ！", 124301, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6849, "あったかいね～…\nグレアにくっついてる\n時を思い出すよ～…", 124301, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6851, "この世界にしかない\nおいしいものが\nたくさんあるよね！", 124301, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6852, "世界を繋ぐ魔法の\nヒント…本に書いて\nないかなぁ…", 124301, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6855, "ピアノに読書…\n今日のアンは\nなにをするのかな…", 124401, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6856, "私はアンほど\n勉強はできないから\nちゃんと自習しないと", 124401, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6857, "一度始めたら\nとことん追求するのが\nアンなんだよね…", 124401, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6858, "ふふ…夢中になってる\nアンを眺めてるだけで\n一日があっという間…", 124401, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6859, "ランドソルは広いね…\nアンと一緒にいろんな\n場所へ行ってみたい", 124401, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6860, "アンやあの人の笑顔を\n見てると、すごく\n安心するんだ…", 124401, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6861, "もしあの人が私たちの\n学院に編入したら…\nふふ、楽しそうだね", 124401, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6862, "アンもあなたも\nすごいな…\nみんなとすぐに\n仲良くなっちゃうから", 124401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6863, "あなたはこれから\nなにをするの？\nその…アンと一緒に\nついていってもいい？", 124401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6864, "アンから魔法の練習に\n誘われた？　実は私も\n…一緒にどうかな？", 124401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6865, "あなたがマナリアに\n編入したら\n楽しいのになって\nアンとよく話すんだ…", 124401, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6866, "こっちの世界にも\nアンのように\n私を受け入れてくれる\n人たちがいるんだね…", 124401, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6867, "元の世界へ帰る日が\n来たとしても私たちは\n「さよなら」より\n「またね」がいいよね", 124401, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 6868, "おはよう。さっき\nランドソルの朝日を\nアンと見てきたんだ", 124401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 6869, "私たちは\nこれからなんだけど\nあなたはお昼ごはん\n何を食べたの？", 124401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 6870, "そろそろ\nアンが眠る時間だ…\nあなたもおやすみ", 124401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6872, "はぁ、疲れが取れる…\nこの尻尾…\nちょっと重いんだよね", 124401, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6873, "これ…\nアンに見せたら\n喜ぶだろうな…", 124401, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6874, "いい香り…\nアンの感想も\n聞いてみたいな", 124401, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6875, "あったかい…\nアンがここにいたら\n本を読みながら\n寝ちゃうんだろうな", 124401, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6877, "おいしいからたくさん\n食べれちゃうけど\nまた尻尾…\n大きくなっちゃうかな", 124401, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6878, "アンがおススメ\nしてくれた本は\nこれかな…？", 124401, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6881, "凶、凶、凶…大凶？\nなによこのおみくじ\n自宅用だからって\n排出率が適当すぎよ…", 124701, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6882, "今年こそ最高の新薬が\nもはや偶然に近い\n最小限の手間で\n作れますように", 124701, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6883, "喉に詰まる前に\n溶けてなくなるお餅…\n最優先の研究課題だわ", 124701, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6884, "よし、完成♪\nミツキ特製…\n「新解釈おせち料理」\n医学的根拠が満載よ", 124701, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6885, "お年玉を彼に…\nあげるべき…？\n色んな意味で微妙ね…", 124701, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6886, "ふふ、今年もまた一年\n彼のバイタルを\n記録できるのね♪", 124701, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6887, "今年こそ\n私の薬で彼を魔改造…\nううん、大人の男に！", 124701, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6888, "コマ回しのコマを\n見てたら目が回った？\nそう…手当は不要よ…", 124701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6889, "私の顔で\n福笑いを作った？\nあら…隻眼まで\n再現度が高いわね♪", 124701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6890, "門松よし、しめ縄よし\nこんなギルドでもね\n一応縁起を担ぐのよ", 124701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6891, "本気の薬草を煮込んだ\n本気の七草粥よ\n色は気にしちゃダメ", 124701, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6892, "君が今年も健康で\nつつがなく…実験台を\n務められますように", 124701, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6893, "いくつ新年を迎えても\n俗世の垢にまみれない\nそんな君こそが…\n私には最高のお薬よ", 124701, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 6894, "お正月なんだから\nいつまでも寝てないで\nさぁ、実験開始よ♪", 124701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 6895, "カルタも双六もしたし\nあとは実験くらいしか\nやることがないわね", 124701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 6896, "日頃からたいがい\n意味深な夢を見るけど\n初夢はまた別かしら…", 124701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6898, "さすがにこの格好で\n寝正月を敢行するのは\nためらわれるわね", 124701, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6899, "や、闇医者たる者\nこんな愛くるしい物に\n心奪われるわけには…", 124701, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6900, "お雑煮のいい香り…\n食欲が増進されるわ\nアロマも立派なお薬ね", 124701, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6901, "胸元は閉じないわよ？\nでも時々ここで\nこっそり暖まるの…", 124701, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6903, "無から有が生まれてる\n新年早々物理の法則を\n無視してくるのね…", 124701, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6904, "どれもこれも読んだわ\n新しい婚活本の\n入荷はまだかしら…", 124701, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6906, "彼が一生懸命\n書いてくれた年賀状\n気持ちがこもっていて\nすごくうれしいわ♪", 124601, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6907, "しっかり丁寧に\n大掃除したから\nとってもいい気持ちで\n新年を迎えられたわ♪", 124601, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6908, "彼の着物姿\nきっと凛々しいわよね\n想像しただけで\n笑顔になるわ～♪", 124601, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6909, "お雑煮は地域によって\n違いがあるけれど\n愛情が隠し味なのは\nどこも一緒よね♪", 124601, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6910, "彼と凧揚げして\n遊びたいけど\n森では引っかかって\nできないかしら…？", 124601, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6911, "今年も彼が素直に\n成長できるように\nたくさんいい子いい子\nしてあげましょう…♪", 124601, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6912, "絵馬に彼と\n書いた通り\n今年もみんなが笑って\n過ごせますように", 124601, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6913, "先生と初詣に\n行きましょう〜♪\n迷子にならないように\n手を繋いで、ね？", 124601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6914, "初売りセールいいわね\nでも気を付けないと\nあれこれ欲しくなって\n買い過ぎちゃうかも…", 124601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6915, "風邪をひかないように\n温かくするんですよ～\nえ？　先生、温めて？\nふふ、甘えん坊さんね", 124601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6916, "あなたのために\nちょっと豪華なおせち\n用意したのよ\nお口に合うかしら？", 124601, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6917, "あなたも着物着たい？\nふふ、貫禄出ちゃって\n先生ドキドキ\nしちゃうかも…♪", 124601, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6918, "あなたの成長は\nとってもうれしいけど\n今年もまだ、あなたの\n先生でいたいな…♪", 124601, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 6919, "あら、起きたの？\nお雑煮できてるから\nちゃんとお顔\n洗ってらっしゃいね", 124601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 6920, "お昼ご飯を\nしっかり食べて\nみんなでかるた大会\nしましょう〜♪", 124601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 6921, "は～い、良い子は\nもう寝る時間ですよ\n素敵な初夢が\n見られますように♪", 124601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6923, "このフカフカちゃんも\n天日干ししたいけど…\nその前にちょっと\n休憩しちゃおうかしら", 124601, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6924, "うふふ、かわいい～\nアオイちゃんや\nハツネちゃんも\nきっと気に入るわね♪", 124601, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6925, "お正月は空気も\n透き通ってるから\nいつも以上に\n素敵な香りに感じるわ", 124601, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6926, "火のぬくもりに\n抱かれていると\n不思議と眠気が…\nふああ…", 124601, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6928, "愛情のこもった料理は\nおいしいだけじゃなく\n心も満たされちゃう♪", 124601, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6929, "お正月のお作法も\nたくさんあるのね～\n先生も日々勉強だわ～", 124601, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 6931, "少年への感謝をどんな\n形で伝えるべきか…\n言葉よりも物の方が\n良いのだろうか？", 104701, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 6932, "話し相手になって\nもらってもいいかな？\n少年と一緒にいると\nとても楽しいんだ", 104701, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6933, "お正月だからって\nギャングのボスは\n油断できないよ～？", 124501, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6934, "イノリちゃんがお年玉\n欲しいんだって♪\n悪い子だったし\n10ルピでいいかな☆", 124501, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6935, "お正月だからお雑煮を\n作ったんだけど\nカヤちゃんには\n大好評だったよ♪", 124501, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6936, "ラーゴがお餅を\n食べたがってるけど…\nこの子にあげるのは\nちょっと心配かな～？", 124501, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6937, "お正月は晴れ着の子が\nやっぱり多いよね～\n今度、私の漫画でも\n出してみようかな？", 124501, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6938, "君と二人っきりで\n神殿に初詣…\n行ってみたいな～♪", 124501, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6939, "君とお正月を\n過ごしていると\n面倒なことも\n全部忘れられそう…", 124501, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6940, "この着物、どう思う？\n似合ってたら\nうれしいけどな～♪", 124501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6941, "袖を引っ張ったり\nしないでね？\n着崩れしちゃうから♪", 124501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6942, "お正月ならではの\n漫画ネタってある？\n参考にしたいんだよね", 124501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6943, "昔は凧あげとか\n駒回しとかしてたけど\n大人になるとそういう\n機会も減るよね…", 124501, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6944, "君にもお年玉を\nあげようか？\n好きな額を言ってね☆", 124501, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6945, "来年も、再来年も…\nこうしてみんなで\n新年がお祝いできると\n素敵だね～♪", 124501, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 6946, "おはよ～♪\n朝ごはんは\n焼いたお餅とか\nどうかな？", 124501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 6947, "お昼だね～\nおせちを用意したから\nみんなで食べよっか", 124501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 6948, "もうこんな時間だね～\n一日中着物だったから\n肩が凝っちゃったよ", 124501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6950, "このまま寝たら\nしわになるかな？\n着替えた方がいい？", 124501, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6951, "こういうのって\nラーゴのおもちゃに\nちょうどいいかも♪", 124501, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6952, "いい香りだけど\n着物に移らないように\n気をつけないとね", 124501, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6953, "ちょうどいい\n火加減だね♪\nぽかぽかするよ～♪", 124501, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6955, "着物の時は\nほどほどにしないと…\n欲張ったら、お腹が\n苦しくなるからね～", 124501, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6956, "ふーん\nイノリちゃんは最近\nこんな漫画を\n読んでるんだ～♪", 124501, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6958, "楽園の気配は…ナシ！\nみんな前を向いてるね\nそうこなくっちゃ！", 124801, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6959, "レッスンの成果も\nこの格好くらい\n目に見えたら\n分かりやすいのになぁ", 124801, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6960, "次の曲はワイルドで\n悪い感じもいいなぁ…\nカヤとムイミちゃん\nみたいなイメージで！", 124801, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6961, "私…服、着てるよね？\nうんうん、着てる！\nあんまり体が軽いと\n急に焦る瞬間あるな～", 124801, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6962, "最近ファンの年齢層が\n幅広くなってきたかも\nこれって私…国民的\nアイドルに近づいた？", 124801, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6963, "パティスリーとコラボ\nしたらどんな感じかな\n…ノゾミンマカロン♪\nとか？　…いけるかも", 124801, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6964, "彼と一緒にいると…\nうれしくって苦しいな\nでも両方諦められない\n…アイドルらしいや", 124801, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6965, "この格好、チカと\nツムギも似合うと\n思うんだけどな～\nキミもそう思うよね？", 124801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6966, "街を歩いてたらね？\nライブとか写真集の\n告知イベントだって\n思われちゃった！", 124801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6967, "私が持ってた剣に\n結んであったリボン…\n何か気づいてくれた？\nヒントは…色だよ♪", 124801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6968, "キミの気配ってすごく\n分かりやすいんだよ\n近くにいたらすぐに\nピンとくるんだ♪", 124801, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6969, "あ、それ…はじめて\nキミにあげた音響盤！\nね、一緒に聴こうよ", 124801, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6970, "ぼうっとしてたら\nイタズラしちゃうよ♪\nもっともっと私のこと\n見てないとダーメ！", 124801, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 6971, "朝は今日のはじまり！\n昨日よりももっと\nいい一日にしようね♪", 124801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 6972, "レッスンがんばったし\nまだお昼だし…\nランチ大盛りにしても\nセーフだよね！", 124801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 6973, "寝る前に明日の予定を\n確認しておかないと！\nえっと、集合場所は…", 124801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6975, "一休みのつもりが\nなが～い休憩になるの\nあるあるだよね～", 124801, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6976, "ノゾミンの熱烈ハグ♪\n…ここだけ聞いたら\nスクープになっちゃう\nかも？　なーんて！", 124801, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6977, "香りってなんだか\n記憶に残るよねー\n…こういうの私だけ？\nみんなも同じだよね？", 124801, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6978, "なんだかライブの\n熱気を思い出して\nきちゃった…！\nレッスンしよっと！", 124801, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6980, "お腹が減ってたら\nいいパフォーマンスは\nできないもんね！\n…んー、おいし～♪", 124801, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6981, "いい歌詞を書くには\nしっくりくる言葉を\n知っておかないと！", 124801, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6983, "うだうだ悩んでる\nヤツがいたら\nオレのとこ連れてこい\n背中押してやるからさ", 124901, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6984, "最近どことなく\nファミリーの連中が\n浮き足立ってんだよな\n…なんでだ？", 124901, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6985, "イノリのヤツ\nやたらと脇腹\nつついてきやがって…\nくすぐったいっての", 124901, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6986, "身軽になったのは\nいいが…くそっ\nどこもかしこも\nスースーする…", 124901, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6987, "思い出に浸るのも\n悪くはないが\nやっぱ人間\n未来に生きないとな！", 124901, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6988, "またムイミやノゾミと\nツルむのもいいな\nそこにカレもいたら\nなおいいぜ", 124901, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6989, "未来のことを考えると\nなぜかいつもカレが\n頭に浮かんじまう…\nええい、やめだやめ！", 124901, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6990, "ちょっくらオレと\n腕相撲しねえか？\nこの新しい力\n試させてくれ！", 124901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6991, "なあ、悪いが上着\n貸してくれないか？\nこのカッコ、割と\n肌寒いんだよ…", 124901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6992, "おっ、生まれ変わった\n喧嘩屋にご用命か？\nいいぜ、キミの\n依頼なら二つ返事だ！", 124901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 6993, "なんか悩みごとが\nあるなら、なんでも\n話してくれよな\nオレとキミの仲だろ？", 124901, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 6994, "このカッコが\nきれいだって？\nや、やめろよ…\n照れくさいだろ…", 124901, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 6995, "キミがいると、なにを\nするにも退屈しないぜ\nだから今日も\n付き合ってくれよな！", 124901, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 6996, "おはよう！　新しい\n未来がやってきたぜ！\n一秒だって時間は\n無駄にするなよ？", 124901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 6997, "そろそろ昼飯に\nしないか？\n穴場のうまい店に\n案内してやるからさ", 124901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 6998, "夜更かしは\n未来の前借りだ\n体壊さないように\nほどほどにしとけよ？", 124901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7000, "お、いいなこれ\nファミリーの\nアジトにも\n置いときたいぜ", 124901, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7001, "べ、別にかわいいもん\nなんて、そんなに\n好きじゃないし…\n嫌いでもないけどさ", 124901, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7002, "嗅いだことない\n香りだな\nなかなか気に入った！", 124901, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7003, "薄着で暖を取るのって\n最高にぜいたくだよな\nへへっ、堪能させて\nもらうとするか", 124901, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7005, "こいつはいいや\nこのカッコだと\n消耗が激しくて\nやたら腹が減るからな", 124901, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7006, "甘辛コーデ…\nへぇ、そんなのが\nあるのか…\nわ、悪くねえかもな", 124901, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 7008, "はぁ…\nもっともっと長い時間\n先輩と過ごせたらなぁ", 105501, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 7009, "えへへ…♪\n先輩が、すぐそばに\nいてくれるなんて…\n幸せだなぁ…♪", 105501, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7010, "はーっはっは！\n強くてカッコいい\n大悪党、ここに参上！", 125001, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7011, "みんなおしゃれで\nカッコいいけど\n今のアタシも\n負けてないぞ！", 125001, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7012, "ギャングのアジトも\nよかったけど\nここも賑やかで\n悪くないな！", 125001, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7013, "悪党にも休息は必要だ\nここならゆっくり\n休めそうだ！\n邪魔するぞ！", 125001, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7014, "いっぱい人がいるな…\nアタシの悪党っぷりを\n広めるチャンスだ！", 125001, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7015, "ここにいれば\nいい思い出がたくさん\n作れそうだな！", 125001, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7016, "いつかアタシも\nオクトーと一緒に\nこんなアジトを手に\n入れてやるからな！", 125001, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7017, "ん？　アタシの格好が\n気になるのか？\nならもっと近くで\n見てもいいぞ！", 125001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7018, "おい、暇だから\nアタシと遊べ！\nカヤとノゾミも呼んで\n大騒ぎするぞ！", 125001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7019, "オマエ、前より強く\nなってないか！？\nくっそー！　アタシも\n負けてらんないぞー！", 125001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7020, "今日は特別にアタシの\n思い出話を聞かせて\nやる！　さーて、どの\n悪事から話そうか…", 125001, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7021, "オマエとの付き合いも\n結構長くなったよな？\nこれからも\nよろしく頼むぞ！", 125001, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7022, "オマエまでいなく\nなったらアタシが困る\n勝手にどっか\n行くんじゃないぞ！", 125001, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 7023, "起きろ、起きろー！\n大悪党の朝は早いんだ\nさっさと\n顔洗ってこい！", 125001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 7024, "昼飯どうしようか…\nそうだ！　オマエの\nおススメに連れてけ！\nいい店期待してるぞ！", 125001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 7025, "明日を思い切り\n楽しむためにも\n早く寝とけよ！\nまた明日な！", 125001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7027, "こうやって座ると…\nおお！　すっごく悪党\nっぽいぞ！", 125001, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7028, "オマエ、仲間に\nなりたそうな目を\nしてるな！", 125001, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7029, "うん、いい匂いだ！\n何の匂いかは全然\n分かんないけどな！", 125001, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7030, "炎ってなんだか\nカッコいいよな！\n見てるだけで心が\n燃えてくるぞ！", 125001, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7032, "いっぱい食べれば\nちょっとは\n背が伸びるかな…？", 125001, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7033, "ぐぬぅ…たまには\n本でもと思ったけど\nやっぱりアタシは\nバトル担当だな！", 125001, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7035, "お邪魔しまーす★\nなーんてもう\nすっかりくつろいで\nますけどー♪", 118201, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7036, "騎士さんの\nギルドハウス…★\nなんだかドキドキ\nしちゃいます♪", 118201, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7037, "ぺんぽこりんの\n置き場所はー…\nま、このへんで★", 118201, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7038, "うーん、これは\n壊しがいがありそ…\nウソウソ、冗談です★", 118201, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7039, "オトモダチでも\n作ってみますかー\nくすっ★", 118201, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7040, "退屈ですねー\n騎士さん、構って\nくれないかなー", 118201, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7041, "もっともっと早く\n騎士さんに逢えてたら\n…なんて、わたし\nらしくないですね", 118201, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7042, "わたし一人にするの\n不安だったりします？\nちゃーんとイイコに\nしてますってー★", 118201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7043, "お出かけします？\nそれともおうちで\nまったりしますー？", 118201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7044, "女の子をすぐ家に\nあげちゃうなんて…\nふふ、やっぱり隅に\n置けないですねー★", 118201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7045, "騎士さん騎士さーん★\nウザ絡みしても\nいいですかー？\n騎士さんってばー★", 118201, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7046, "一緒にゴロゴロ\nしませんかー？\nコイビトっぽくて\n楽しそうですし♪", 118201, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7047, "うふふ★\nこーやってあなたに\n優しくされると…\nときめいちゃいます♪", 118201, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 7048, "おはようございます★\nあれれー？\nまだおねむですかー？", 118201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 7049, "騎士さん、お昼は\nもう食べましたー？\nわたし、ラーメンが\n食べたいでーす★", 118201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 7050, "あ、夜更かししてちゃ\nダメですよー？\nほらほら、お布団に\nごーごー★", 118201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7052, "ふかふかのふわふわー\nついついまーったり\nしちゃいます★", 118201, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7053, "かっわいー★\n名前はー、んー…\nぱんぱかぷんで★", 118201, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7054, "くんくん★\nうーん…\nたぶん素敵な香り♪", 118201, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7055, "あったかーい★\n心も体も\nぽっかぽかですねー★", 118201, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7057, "もぐもぐ★\nんー、おいしくって\n止まらないですねー★", 118201, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7058, "へー★\nほほー★\nふんふーん★", 118201, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7060, "さ～て時間もあるし\nラテアートの練習でも\nするかにゃ～♪", 125101, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7061, "カフェの猫たちも\n元気に働いてくれて\nお客さんからの評判も\n上々にゃ～♪", 125101, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7062, "カフェのカウンターに\nファントムキャッツの\n目安箱を置こうかにゃ", 125101, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7063, "あんこもコーヒーも\n原料は豆にゃ～\nそりゃ相性いいに\n決まってるにゃ♪", 125101, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7064, "あいつの為に\nタマキ特製ブレンドを\n開発したいにゃ♪", 125101, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7065, "あいつと働いてると\nなぜかあっという間に\n閉店時間が\n来ちゃうのにゃ～", 125101, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7066, "あいつといると\n新しいアイデアが\n溢れるほど\n出てきちゃうのにゃ♪", 125101, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7067, "キミがコーヒーを\n淹れてくれたのにゃ？\nにゃはは、常温で\n猫舌にも優しいにゃ♪", 125101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7068, "料理が上手くなったら\nカフェご飯メニューも\n充実させたいにゃ～", 125101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7069, "お店の経営は大変\nだけど、キミとなら\n乗り越えられるって\n確信してるにゃ！", 125101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7070, "ん？　この制服\n似合ってるにゃ？\nにゃはは！　褒めても\n１ルピも出ないにゃ♪", 125101, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7071, "あたしが好きなのは\n猫に好かれる人にゃ♪\n…ん、キミ猫に懐かれ\nまくってるにゃ…", 125101, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7072, "キミの制服姿\nけっこーイケてるにゃ\nあたしがお客さんなら\n通いつめちゃうにゃ♪", 125101, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 7073, "朝は一杯の\nコーヒーから始まる…\nにゃふふん♪\nシャレた朝だにゃ～♪", 125101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 7074, "お昼はたい焼きと\nコーヒーのセットで\nまったり休憩\nしようにゃ～♪", 125101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 7075, "コーヒー淹れたにゃ\n星空を見ながら\n二人で飲もうにゃ…♪", 125101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7077, "ふにゃ～ん…ゴロゴロ\nコーヒーと同じくらい\n癒されるにゃ～…♪", 125101, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7078, "こいつかわいいにゃ～\nうちの猫たちとも\nいい勝負なのにゃ！", 125101, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7079, "ん～いい香りだにゃ～\nコーヒーもそうだけど\n香りの癒し効果は\n抜群にゃ～♪", 125101, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7080, "猫に暖かいものは\nまるまれと言ってる\nようなもんにゃ…♪\nまるまる…", 125101, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7082, "にゃは～うまいにゃ♪\nカフェの新メニューに\n採用しちゃおう\nかにゃ～♪", 125101, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7083, "にゃるほど…\n調べれば調べるほど\nコーヒーは\n奥が深いのにゃ…", 125101, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 7085, "ワタシの手料理は\n坊やの口に合うらしい\n胃袋は討ち取ったぞ♪", 107101, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 7086, "胸に再度触れると\n誓約は無効になるのか\n…だと？　鋭い質問だ\n安心しろ。ならん☆", 107101, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7087, "ようやくこの服にも\n慣れてきたかな？\nふふっ、たまには\nこういう服も悪くない", 125201, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7088, "今日も店は好調だが、\n私は軍人だからな\n当初の目的も\n忘れないようにしよう", 125201, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7089, "猫が近くに来ると\nついつい見てしまうな\n仕事が終わったら\n遊んでやるかな♪", 125201, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7090, "コーヒーの淹れ方が\nうまくなってきたな♪\nどうせならお菓子も\n研究してみるか！", 125201, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7091, "利益を考えると\n価格をあげるべきか？\n…いや、利益よりも\n皆の笑顔の方が大事だ", 125201, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7092, "またあいつを\n呼んでしまったな…\n今度、コーヒーでも\n淹れて労ってやるか♪", 125201, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7093, "次に入荷するお菓子は\nあいつの意見を聞くか\nどんな味が好きか\n気になるしな", 125201, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7094, "なに、コーヒーを\n淹れてほしい？\nふふっ♪\n仕方のない奴だな！", 125201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7095, "…そうだ。\nコーヒーと乾パンは\n合うのではないか？\n今度試してみよう！", 125201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7096, "コーヒーもいいが、\nココアも悪くないぞ♪\n貴公も一緒にどうだ？", 125201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7097, "魔法少女に比べれば\n大人しい服だろう？\nだが、カフェには\nこの服でバッチリだ！", 125201, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7098, "貴公はコーヒーを\n飲めるのかっ？それは\nうらやましいな…", 125201, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7099, "貴公の働きぶりには\n目を見張るものがある\nその姿は、その…\n結構かっこいいぞっ！", 125201, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 7100, "さぁ、今日も一日、\n頑張るぞっ！\n気合いを入れて\n開店準備だっ！", 125201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 7101, "昼は稼ぎ時だからな！\n荒波を乗り切るため\n貴公も一緒に\n頑張ってくれるか？", 125201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 7102, "ふあぁ…もう夜か\n今日は頑張ったからな\nはやめに休むとしよう", 125201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7104, "はふぅ～…\nたまにはこうして\nゆったりするのも\n悪くないな…", 125201, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7105, "猫もかわいいが、\nこいつもなかなか…\n見ていると、\n撫でたくなるな♪", 125201, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7106, "これは良い香りだな\nふむ、コーヒーも\nより香り高いものが\n作れるよう試すか！", 125201, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7107, "ふぅ～…\n体を温めなければ、\nすぐに動けないからな", 125201, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7109, "これもうまいが、\n私はお菓子も…\nいや、食べすぎは\n体に良くないな", 125201, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7110, "…書物は参考になるな\n次はこのやり方を\n試してみるかな", 125201, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7112, "愛と希望の魔法少女！\n弟くんの平和は\nお姉ちゃんが守るよ♪", 120001, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7113, "魔法少女の戦い方を\n見せてあげる！\nお姉ちゃんチョップ♪", 120001, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7114, "闇の力が高まってる…\n早く弟くんを\nギュ～ってしないと！", 120001, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7115, "ミニ弟くんを\n部屋中に呼び出して\n弟くんハウスに～♪", 120001, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7116, "決めポーズの練習でも\nしよっかな…\nはい、ポーズ♪", 120001, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7117, "魔法少女の力で\n弟くんのそばに\n瞬間移動したいなぁ", 120001, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7118, "禁じられた必殺技\nその名も──\n闇のお姉ちゃん頭突き", 120001, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7119, "魔法少女な\nお姉ちゃんに\n興味津々だねっ？\n全部教えてあげるね♪", 120001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7120, "闇のお姉ちゃんとして\n世界を弟くんのものに\n…な～んて冗談冗談♪", 120001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7121, "弟くんって魔法少女に\nなりたがってたよね？\n…うん。応援するね！", 120001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7122, "サインが欲しいの？\nふふっ、任せて！\n愛する弟くんへ、と♪", 120001, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7123, "ふんふふ～ん♪\n魔法少女お姉ちゃんの\nテーマソング完成っ♪", 120001, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7124, "えへへ、弟くんからの\n愛と希望を感じるよ…\n更に変身しちゃうぞ♪", 120001, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 7125, "弟くんの朝を魔法で\n守りに来たよ！\n希望の朝だねっ♪", 120001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 7126, "弟くんのお昼を魔法で\n守りに来たよ！\n愛のお弁当をどうぞ♪", 120001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 7127, "弟くんの夜を魔法で\n守りに来たよ！\nここからは闇の時間♪", 120001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7129, "魔法少女の一休み♪\n愛と希望と…闇の力が\n回復していくね～♪", 120001, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7130, "かわいいかわいい♪\nお姉ちゃんの魔法で\n増やしちゃうぞ…♪", 120001, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7131, "この香り…\n弟くんと同じ…\nくんくん、くんくん…", 120001, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7132, "体を温めておいて…\nこの熱を、ぜ～んぶ\n弟くんに伝えるよ…！", 120001, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7134, "ミニ弟くんを呼ぶには\nいっぱい食べないと…\n魔法ってお腹減るから", 120001, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7135, "弟くんの平和を守る…\nそのためには\nチェックチェック…♪", 120001, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7137, "この服も、最初は\n慣れなかったのですが\n今ではすっかり\n体に馴染んでおります", 125301, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7138, "自然を守るためにも\nお勉強は欠かせません\n日々精進でございます", 125301, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7139, "採取した薬草を\n調合しておきましょう\nいつ必要になるか\n分かりませんからね", 125301, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7140, "合宿は過酷でしたが\nおかげでちょっと\n力持ちになったような\n気がいたします…♪", 125301, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7141, "わんわんのほうの\n主さまに、ごはんを\nあげる時間ですね…", 125301, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7142, "もっともっと訓練や\nお勉強を重ねて\n主さまのお役に\n立たなくては…", 125301, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7143, "おや、そろそろ\nわんわんのほうの\n主さまをお散歩に\n連れていく時間です", 125301, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7144, "主さま、よろしければ\nお散歩に行きませんか\n今日の森は穏やかで\n過ごしやすいかと", 125301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7145, "わんわんのほうの\n主さまも、今では\n【美食殿】の大切な\n仲間でございます", 125301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7146, "森には危険が\nいっぱいでございます\nケガをなさらないよう\nお気をつけください", 125301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7147, "もし森や動物のことで\n気になることがあれば\n気軽にお尋ねください", 125301, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7148, "森へ行く前には\n必ず装備の点検を\nお願いいたします\n約束ですよ、主さま？", 125301, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7149, "森の中で主さまたちと\nゆったり過ごす時間は\nわたくしにとって\n大切なひとときです", 125301, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 7150, "おはようございます\n朝ごはんをしっかり\n食べて、お勤めに\n備えましょう", 125301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 7151, "わんわんのほうの\n主さまにおやつを\nあげる時間です\nどうぞ、召し上がれ", 125301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 7152, "どれだけ野営に\n慣れたとしても\nベッドで眠れるなら\nそうしたいところです", 125301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7154, "こちらなのですが\nわんわんのほうの\n主さまもお気に入りの\nようです", 125301, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7155, "わんわんのほうの\n主さまにも、こうした\n玩具を与えるべき\nでしょうか…？", 125301, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7156, "心地よい香りです\n次は、自然由来の\n香料も試しましょう", 125301, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7157, "暖をとるのは\n野営の時などに\nとても大事なことで\nございますね", 125301, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7159, "食べられる時に食べて\nおくのも、大自然では\n重要でございます", 125301, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7160, "より知識を蓄え\n自然の力を活用できる\nようにいたしましょう", 125301, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 7162, "今回の「冥風戦記」も\nなかなかよく書けたぞ\nふふっ、シグルドも\n喜んでくれるかな？", 100901, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 7163, "貴様のためならば\n我が真なる魔力\nいつでも\n解き放とうぞ！", 100901, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7164, "ニャットちゃんに\nブラッシングを\nしてあげようかな", 125401, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7165, "ふふ…\nこれがレンジャーの\n制服ですか", 125401, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7166, "そろそろ\nパトロールの\n時間だ…", 125401, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7167, "魔物について\nもっと知りたいな\n本を探しに行こう", 125401, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7168, "レンジャーになって\n体調を崩す日が\n減った気がします…", 125401, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7169, "本だけじゃわからない\n世界のことを、\nもっと知りたいです…", 125401, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7170, "こんな私にも\nできることはある…\nそう信じています", 125401, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7171, "あの…良かったら\n一緒に見回りへ\n行きませんか？", 125401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7172, "またあなたと一緒に\nレンジャーの仕事が\nできたらうれしいです", 125401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7173, "けほっこほっ\n…だ、大丈夫です\nたまに出るだけなので", 125401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7174, "ニャットちゃん\nあなたにも\n懐いてきましたね", 125401, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7175, "ニャットちゃんと\n散歩に出かけようかな\n…あの人も誘って", 125401, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7176, "あなたと肩を並べて\nレンジャーの仕事が\nできてうれしいです…", 125401, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 7177, "おはようございます\nレンジャー・シオリ\n元気に出勤します", 125401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 7178, "ニャットちゃんにも\nお昼ごはんを\n用意しなくちゃ", 125401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 7179, "明日は仕事だから\n早起きしなくちゃ\nおやすみなさい", 125401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7181, "ふぅ…仕事で\n走り回ったので\n少し休憩します", 125401, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7182, "仕事が終わった後に\nこうして触れていると\n癒されますね…", 125401, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7183, "森の香りがします…\n今日はよく\n眠れそうです…", 125401, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7184, "ニャットちゃんと\n同じぐらい\n暖かいです…", 125401, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7186, "レンジャーの仕事を\nしていると\nお腹が空きます", 125401, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7187, "どんなに忙しくても\n読書だけは\nやめられませんね", 125401, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7189, "兄様のお留守の間\nわたくしが、兄様の\n御家を守ります！", 125801, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7190, "に、に、兄様の御家…\n招待いただいた以上は\n失礼のないよう\n振る舞わなくては…！", 125801, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7191, "ふふっ…兄様の素敵な\nお人柄が、家中から\nにじみ出ています…♪", 125801, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7192, "靴を脱がずに\n御家に上がるのに\n慣れていなくて…あ！\n飛べばいいのでは…！", 125801, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7193, "ここに花を生けたら\n華やぎそうですね\n兄様はお花…\n好きでしょうか…？", 125801, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7194, "ずっとここにいたい…\nなどと言ったら\n兄様を困らせて\nしまいますよね…", 125801, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7195, "お料理などを作って\n兄様を出迎えられたら\nいいのに…料理の腕も\n磨かなくては…！", 125801, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7196, "このような姿で\n失礼しております…！\nネ、ネズミがいたので\nつい本気を…！", 125801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7197, "おかえりなさいませ\nお忙しい御身をどうぞ\n癒してくださいね", 125801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7198, "兄様の話し相手…！？\nど、どうしましょう…\nわたくしには面白い\n話なんて…はうぅ…！", 125801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7199, "なぜか、動物などに\n好かれる体質で…\n迷い猫捜しのバイトの\nお役に立てますか？", 125801, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7200, "に、兄様の家に来たら\n『添い寝』をするのが\n礼儀とリアさんが…\n本当…でしょうか？", 125801, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7201, "わたくしはここに\nいて…よいのですね？\n『仲間』として…", 125801, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 7202, "朝は得意なんです\nもし兄様が苦手なら\nわたくしが起こして\n差し上げますよ", 125801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 7203, "兄様…昼餉を作って\nみたのですが、失敗\nしてしまいました…", 125801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 7204, "ふあぁ…\nはっ！　お…\nお恥ずかしい所を…！", 125801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7206, "こ、これは…！\n人様の御家なのに\nつい…駄目になって…\nしまいますぅ…！", 125801, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7207, "と、飛びかかっては\nこない…ですよね…？", 125801, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7208, "すぅ…はぁ…\nこのかぐわしさ\n心が洗われます…", 125801, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7209, "なんて暖かい…\n兄様の優しくお広い\n御心のようです…♪", 125801, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7211, "こんなに食べていたら\nはしたない…\nでしょうか？", 125801, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7212, "兄様の本棚はまるで\n知識の宝庫ですね\n己の世間知らずぶりを\n実感してしまいます", 125801, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7214, "次の冒険に向けて\n騎空士弁当を作ろっと\nまずはおにぎりから！", 125501, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7215, "魔法の修行しなくちゃ\nもっともっと頼れる\n団長になりたいから！", 125501, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7216, "ビカラちゃんを\n遊びに誘いたいけど…\nどういう場所が\n好きなんだろう？", 125501, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7217, "仕事を真面目に\nしっかりこなすことが\n騎空艇への\n一番の近道だよね", 125501, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7218, "ここにいる人の中で\n騎空団に興味ある人\nいたりしないかな…？", 125501, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7219, "これまでも想い出は\nいっぱいできたけど…\nこれからもたくさん\n想い出を作りたいな！", 125501, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7220, "仲間がいるから\n騎空士は限界だって\n超えられるんだね", 125501, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7221, "この衣装\n魔法使いらしいでしょ\n服に着られないよう\n頑張らなくちゃね！", 125501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7222, "あなたとなら\nどんな相手だって\n怖くなんてないよ！", 125501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7223, "カリンさんから\n新しい依頼が来たよ！\n一緒に頑張ろ！", 125501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7224, "操舵士になるのは\nずいぶん難しそう…\nいつか頼れる仲間が\n見つかるといいな", 125501, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7225, "手合わせしたいの？\nいいよ、たまには\n団長らしいところ\n見せてあげるね！", 125501, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7226, "まだ二人きりだけど…\nこの騎空団は\n私の宝物だよ！", 125501, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 7227, "おはよう～…\n昨日は遅くまで\n魔法の研究してて…\nふわぁ…眠いなぁ…", 125501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 7228, "さてと、お昼休み！\n紅茶淹れてくるから\n一緒に飲もうよ！", 125501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 7229, "遅くまでお疲れさま！\n無理しない程度に\nお互い頑張ろう！", 125501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7231, "魔法の研究は\n一旦、休憩！\n頭を休めなくちゃ", 125501, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7232, "魔法みたいに\nかわいい！\n騎空団に入る？\nなんてね！", 125501, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7233, "いい匂い…\nそれになんだか\n集中力が増したかも", 125501, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7234, "体だけじゃなくて\n心までポカポカする…\nなんだか魔法みたい", 125501, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7236, "いろいろ考え事したら\nお腹が空いちゃった！\nいただきまーす！", 125501, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7237, "『摂理の彼方へ\n至るには…』\n…この魔導書\n勉強になるなあ", 125501, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7239, "こちらの服を着ると\n冒険に出かけたく\nなっちゃいますね", 125701, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7240, "冒険者の心得その一\n準備はできる限り\nしっかりとこなすべし\nなんてどうでしょう？", 125701, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7241, "調合に必要な素材は\nあれとこれとそれと…\n時間があるとついつい\n考えちゃいますね", 125701, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7242, "せっかくですから\nより良い素材を\n自分で集めたいですね", 125701, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7243, "このポーションが\nみなさんの役に立つと\nうれしいですね", 125701, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7244, "冒険者の心得その二\n危なくなった時は\nまず逃げるべし！\n大事なことですね", 125701, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7245, "あの人の冒険は\nいつも楽しそうで\n元気がもらえます", 125701, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7246, "ぜひおすすめしたい\n依頼があるんです\nこちらをどうぞ♪", 125701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7247, "ほしいお薬があれば\n錬金術で作って\nさしあげますよ", 125701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7248, "依頼で気になる\nところがあれば\n何でもご確認ください", 125701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7249, "このギルドハウスに\n錬金術の実験設備を…\nえ？　だめですか？", 125701, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7250, "ポーションをどうぞ\n大丈夫、効果は\n確認済みですので♪", 125701, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7251, "次はどんな冒険に\n行かれるんですか？\nいつでもお供しますよ", 125701, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 7252, "今日もいい一日に\nなりそうですね\n冒険日和といった\nところでしょうか", 125701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 7253, "いい時間ですし\nランチにしませんか？\n次の冒険の\n作戦会議も兼ねて♪", 125701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 7254, "夜も遅いですし\n明日の冒険のため\n今日は早く寝ましょう", 125701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7256, "ふう…やはり冒険は\n事務仕事と違いますね\n少し休憩を…", 125701, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7257, "冒険は緊張の連続です\nかわいい物で\n心を癒しましょう", 125701, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7258, "ふむ、この香りは…\n素晴らしいバランスで\n調合されていますね", 125701, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7259, "この火力…\n錬金術の調合に\n使えそうですね", 125701, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7261, "冒険の際は食べられる\n時に食べておく！\n…とペコリーヌさんも\nおっしゃってましたね", 125701, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7262, "ふむふむ…なるほど\nこんな調合も\nあるのですね…\n奥が深いです", 125701, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 7264, "アイツどこ行った…？\n戦うならアタシも\n連れてけよなー！", 106101, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 7265, "今日も悪いヤツ退治…\nの前に腹ごしらえだ！\nなんか食べに行くぞ！", 106101, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7266, "みんなー！\nギルドハウスで\nぼくと握手！", 125601, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7267, "種族も年齢も違うけど\nみんな楽しそうだね！\nやっぱりここはぼくの\nゆ～とぴあさ！", 125601, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7268, "ハハッ！　まるで\nパーティー会場だね！\nこれは楽しませがいが\nあるってものだよ！", 125601, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7269, "やぁみんな！\nご利益満点のネズ耳は\nいかがかな？\nひとつ３ルピだよ！", 125601, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7270, "ここにいれば\n遊び相手に困らないね\nかしこみかしこみ～！", 125601, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7271, "どこか遊びに\n行こうかな♪\nそうだ、あの人にも\n声をかけてみよう！", 125601, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7272, "あの人といると\nぼくまで楽しくなって\nくるんだよね！\n毎日パレード気分さ！", 125601, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7273, "ネズ耳の在庫が\n切れたんだ！\n作るのを\n手伝ってくれるかい？", 125601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7274, "ん？　握手かい？\nいいとも！\nいつでも何度でも\nしてあげるよ！", 125601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7275, "この衣装に興味が\nあるのかい？\nきみ、なかなか\n見る目があるね！", 125601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7276, "しばらくここに厄介に\nなるつもりなんだ\nあとで街を案内\nしてくれるかい？", 125601, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7277, "とっておきのチーズ\nきみと半分こだ！\nみんなには内緒だよ？", 125601, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7278, "きみはいてほしい時に\nいつもそばに\nいてくれるね\n本当にかしこみだよ！", 125601, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 7279, "やぁ、おはよう！\n気持ちのいい朝だね\n今日も楽しくネズ耳\n売りにはげもうか！", 125601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 7280, "お昼だね！\nおいしいチーズを\n持ってきたから\nぼくと半分こだ！", 125601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 7281, "今日も一日\n楽しかったね！\nいい夢が見られること\n間違いなしさ！", 125601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7283, "ぼくにも休息は\n必要さ！\nリラックス〜\nリラックス〜♪", 125601, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7284, "ぼくのぬいぐるみも\n作って売ろうかな…\n修繕費も一気に\n集まるかもね！", 125601, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7285, "いい香りだね！\nトーゴクのお香に\nよく似てるよ", 125601, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7286, "はぁ…温まるねぇ…\nそうだ、この火で\nチーズでも炙って\nみようかな", 125601, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7288, "うーん、この味…\nトーゴクを思い出すよ\nどれ、もう一つ\nいただこうか！", 125601, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7289, "これはランドソルの\nガイド本だね！\nありがたく参考に\nさせてもらうよ！", 125601, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7291, "まさか、わらわが\nこのような装束を\n纏う日がくるとはな…\n感慨深いものじゃ", 126201, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7292, "わらわの美貌で\nこのギルドハウスも\nより華やかに\nなったようじゃな", 126201, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7293, "つまらぬ戦いで\n晴れ着を汚しとうない\n今日は一日\nゆっくりするかの", 126201, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7294, "今日はわらわが食事の\n準備をして、あやつの\n帰りを待ってやろう\n献立は何がよいかの…", 126201, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7295, "ユキといったか…\n掴みどころのない\nやつじゃが、根性は\n認めてやるかの", 126201, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7296, "こうして、仲間と\nのんびり過ごす時間も\n悪くないのう♪", 126201, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7297, "あやつを誘って\n月を見に行こうかの\n夜が楽しみじゃ♪", 126201, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7298, "いかなる衣装で\nあろうとも、わらわは\n完璧に着こなして\nしまうのじゃ♪", 126201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7299, "なんじゃ？\nわらわの姿に\n見惚れておったとな？\nす、素直でよいのじゃ", 126201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7300, "吸血鬼であるわらわに\n相応しい装束じゃろう\nあとは真っ赤な血を\n添えたいのう…", 126201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7301, "お主、疲れておるな？\nわらわが癒してやろう\nほれ、近う寄るのじゃ", 126201, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7302, "お主がおるだけで\nギルドハウスが\n賑やかになるのう\nある種の才能じゃな♪", 126201, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7303, "眷属よ、風呂にするか\nご飯にするか\nそれとも、わらわに…\nいや、忘れるのじゃ", 126201, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 7304, "朝一番、わらわに\n会いたかったとな…\n眷属よ\n褒めてつかわすぞ♪", 126201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 7305, "眷属よ、わらわが\n食事を用意したぞ\n忌々しい太陽に負けず\n頑張るのじゃ", 126201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 7306, "眠るには惜しい夜じゃ\n眷属よ、もうしばらく\nわらわの傍に\nおるがよい", 126201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7308, "ふかふかじゃの♪\n今度あやつにも\n座らせてやろう", 126201, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7309, "愛いやつじゃ\n少しばかり、わらわが\n遊んでやろうかの", 126201, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7310, "気品ある香りじゃのう\nわらわの古城にも\n一つ欲しいところじゃ", 126201, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7311, "よいぬくもりじゃ…\n忌々しい太陽とは\n大違いじゃの", 126201, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7313, "味付けの参考に\nあと、もう一つだけ…\n止まらんのじゃ…", 126201, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7314, "吸血鬼の弱点について\n記されておるのか…\nふむ、眠気が\nさめてしもうたぞ", 126201, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 7316, "このままボクが美しく\nなり続けても、彼なら\nきっと隣にいてくれる\nはずだよね！", 100801, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 7317, "もっとそばに来なよ\nボクの隣は\nキミのための\n特等席なんだから", 100801, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7318, "ドレスの影響かな…\n前より黒や赤が\n好きになったかも", 126301, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7319, "ボクがいるだけで\nここがぐんっと\n華やかになったね♪", 126301, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7320, "ボクを描いた絵画\n今度持ってきて\n飾ってあげようかな", 126301, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7321, "ボクはボクに\n魅了されっぱなしさ♪\nまさに魔性の存在だよ", 126301, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7322, "美のアドバイスを\n聞きたい人いるかい？\nボクがもっともっと\n輝かせてあげるよ♪", 126301, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7323, "あの人、今度はボクを\nどう褒めるだろう？\nもし語彙が尽きても\n許してあげよっと♪", 126301, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7324, "あの人に似合うコーデ\n考えてあげよっと\n案外、なんでも\n着こなしそうだよね♪", 126301, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7325, "吸血鬼になるなんて\nやっぱりゴメンだね！\n永遠の美よりも\n大事なことがあるのさ", 126301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7326, "お肌にいいものは\n大体好きだよ\nトマトジュースとかね", 126301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7327, "銀のアクセサリーが\nボクは大好きでね\nいつでもプレゼント\n待ってるよ♪", 126301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7328, "イリヤみたいにお城に\n住んでみたいんだ♪\n美を統べる者に\n相応しいでしょ？", 126301, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7329, "今のボクがカップを\n触ると壊しちゃうんだ\nだからほら、キミが\nボクに紅茶を飲ませて", 126301, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7330, "キミも美を磨けば\nもっと輝けるはずさ！\nボクがメイクとか\nしてあげようか？", 126301, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 7331, "んん、まだ眠い…\n美のためにも、\nもうひと眠りするよ…", 126301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 7332, "ボクにぴったりな\n日傘を買ったんだ♪\n散歩にでも行こうよ！", 126301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 7333, "夜こそボクの時間！\nさぁ、あまねく人々へ\nボクの美を広めよう！", 126301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7335, "リラックスは大事だよ\n心の余裕こそが\n美の秘訣だからね♪", 126301, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7336, "キミもボクの\n美の虜にしてあげる♪\nうれしいでしょ？", 126301, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7337, "人々を魅了するほどの\n魔性の香り…\nボクにふさわしいね♪", 126301, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7338, "冷えは美の大敵さ\nボクのように\n美しくなりたいなら\n体を温めるべきだよ", 126301, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7340, "食事をするボクの\nなまめかしさ…♪\n吸血鬼すらも\nドキッとするだろうね", 126301, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7341, "吸血鬼って\n美しい存在だよね…\nボクほどではないけど", 126301, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7343, "ここがナイトさんの\nお家なんですねーっ！\n雰囲気そっくりっ！", 126401, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7344, "ドアも壁もお菓子じゃ\nないんですよねっ？\n不思議ですねーっ♪", 126401, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7345, "失礼のないように\n気をつけないとっ！\nライラエルさまとも\n約束してますからっ！", 126401, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7346, "向こうに帰るころには\nお土産話がいっぱい\nできてるといいな～♪", 126401, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7347, "戦闘用のアームの他に\n観光用のアームも\nあったらいいのにな～", 126401, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7348, "ナイトさんの充填と\nライラエルさまの充填\nどっちも違って\nどっちも大好きです♪", 126401, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7349, "…ナイトさんが\nマスターになって\nくれたら…えへへっ\nなんでもないですっ！", 126401, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7350, "お手伝いだったら\n任せてくださいっ！\nお家の移転とか\nしちゃいますかっ？", 126401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7351, "こっちに来るときは\nポポおじいさんが\nしっかりメンテして\nくれるんですよっ♪", 126401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7352, "お家の中にいるのも\nいいですけど、お外も\n気になります～っ！\nお出かけしましょう♪", 126401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7353, "ヴォルガーノさんに\nそっくりの子猫さんを\n見かけたんですっ！\nご親戚ですかねっ？", 126401, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7354, "ここだけの話ですよ？\n…実はケーブルって\n触られると、ちょっと\nくすぐったいんですっ", 126401, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7355, "このお家の持ち主は\nナイトさんですよね？\nそれじゃここでは\nご主人さまですねっ", 126401, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 7356, "おはようございますっ\n今日もフルパワーで\nがんばりましょうっ♪", 126401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 7357, "お昼の時間ですねっ！\nランチの充填、\nお願いしまーすっ♪", 126401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 7358, "そろそろ眠らなくて\n大丈夫ですかっ？\n夜ふかしさんなら\nお付き合いしますよ♪", 126401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7360, "エネルギーの消費を\n抑えるのも大切\nですよね～っ", 126401, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7361, "わたしが触っても\n平気なんて…！\nかわいくて頑丈っ！\n好きですっ！", 126401, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7362, "センサーによると…\nこの香りはいい香りに\n該当するみたいですっ\nなるほどーっ！", 126401, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7363, "あったかいのって\nいいですよねっ！\nみなさんうれしそう\nですしっ♪", 126401, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7365, "これがこっちの世界の\nごはんなんですねっ\nおいしい気がします！", 126401, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7366, "そーっとそーっと…\n本を破かないように…\n書いてある内容より\n難しいです～っ！", 126401, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7368, "ウィスタリア家の\nプライベートビーチも\nご案内しますわね♪", 126701, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7369, "世界を巡る\nリゾートクルーズも\nご用意できますわよ", 126701, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7370, "避暑地に専用の\n別荘がありますの\n今度ご招待しますわね", 126701, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7371, "日焼けは乙女の\n天敵ですわね\n備えはしっかりと…", 126701, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7372, "線香花火だけでは\n物足りませんわね\nもっとドカーンと…", 126701, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7373, "ギルド活動も全力で！\nバカンスも全力で！\nそれが私ですわ！", 126701, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7374, "私のセンスで選んだ\n特注の水着ですもの\n似合って当然ですわ♪", 126701, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7375, "ご希望ならすぐにでも\n夏の飛空艇遊覧ツアー\nへご招待しますわ♪", 126701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7376, "夏の賑やかな\n雰囲気は好きですが…\n暑さには参りますわね", 126701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7377, "日の光を浴びるほどに\n輝きを増す夏の私！\n今日も絶好調ですわ♪", 126701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7378, "夏祭りに協力するなら\n資金と人材、どちらも\n用意しないとですわね", 126701, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7379, "あなたさま…その…\nそうも見つめられると\n照れてしまいます…", 126701, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7380, "タマキさんの働く\n海の家も訪れて\nみたいですわね♪", 126701, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 7381, "港の市場が\n賑わう頃ですわね！\n私たちも参りますわよ", 126701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 7382, "冷たい飲み物でも\nご一緒にいかが？\nうちのシェフに用意\nさせてありますの", 126701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 7383, "まだもう少し…\nこの夏の夜を\nあなたさまと…", 126701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7385, "あなたさまも\n良ければこちらへ\nいらっしゃいな♪", 126701, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7386, "夏に合わせた装いに\n替えてみるのも\n楽しそうですわね♪", 126701, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7387, "この夏めいた爽やかな\n香り…期間限定で\n売り出したいですわ", 126701, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7388, "く…我慢です…\nこの暑さに耐えてこそ\n美しさにも磨きが…", 126701, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7390, "暑い季節に働く\nみなさまへ差し入れも\n必要ですわね", 126701, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7391, "夏のトレンドを\n押さえておくのも\n大事ですわね", 126701, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7393, "上手くいけば\nヒット商品になる\nポテンシャルを\n感じますわね…！", 126701, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7394, "この極上の肌触り…\n言い値で買いますわ\n一体おいくらですの？", 126701, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7395, "せっかくのバカンス…\n自由に羽を\n伸ばすとしましょう", 126601, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7396, "たとえどのような\n秘密を持とうとも\n私には筒抜けですよ", 126601, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7397, "子供だましの怪談に\n関心はありませんが…\n霊には興味があります\n観察対象として、ね", 126601, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7398, "彼の絵日記…\nあとでゆっくりと\n読ませてもらいます", 126601, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7399, "セミの鳴き声に入道雲\nそしてクリームソーダ\nこれでこそ夏ですね", 126601, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7400, "夏休みの宿題でも\n作るとしましょうか\n彼の限界を少し超えた\n負荷を与えるには…", 126601, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7401, "彼と過ごす夏を\n特別なものにしたい…\nそれが私の望みです", 126601, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7402, "夏だからといって\n浮かれすぎないように\n節度を持って\n楽しみなさい？", 126601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7403, "ああ見えてもマサキは\n私のプリンセスナイト\nあまり侮られては\n困りますよ？", 126601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7404, "私の秘密ですか？\nまんじゅうが怖い…\nというのはどうです？", 126601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7405, "ここは誰かの秘密を\n盗み聞くのに\n最適な場所ですね\n…ジョークですよ", 126601, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7406, "一緒に風鈴の音を\nのんびり楽しもう…？\n…別に構いませんよ\n貴方とならば", 126601, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7407, "貴方に約束しましょう\n決して忘れられない\n夏にしてあげますよ", 126601, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 7408, "早起きとは感心ですね\nでは一緒に\n体操でもしましょうか", 126601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 7409, "昼食は流しそうめん？\n非効率的ですね\n…食べないとは\n一言も言っていません", 126601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 7410, "…貴方が望むならば\nいくらでも怖い話を\nしてあげますが…\nいかがですか？", 126601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7412, "七冠にも休息は\n必要なのです\nしばらくここで\n休ませてもらいますよ", 126601, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7413, "なかなか気に入った…\nというのは\n誰にも秘密です", 126601, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7414, "薫風を思わせる\nこのさわやかな香り…\n悪くないセンスですね", 126601, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7415, "夏だからこそ\n冷え対策は\n重要なのです\n…とはいえ暑いですね", 126601, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7417, "私がどのような味が\n好きなのかは…\n当然秘密です", 126601, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7418, "夏を舞台にした\n小説は数多くあります\n彼が好きそうな本を\n何冊か見繕いましょう", 126601, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7420, "見た目も手触りも\n完璧に覚えました\nいつでも模倣できます", 126601, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7421, "ふむ…夏らしい\nクッションですね\nナイトプールに\n映えそうです", 126601, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 7422, "レイ様も格好良いけど\n騎士さんもなかなか…\nって、今の聞こえて\nませんよねっ？", 105401, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 7423, "騎士さんはシンプルな\n服が似合いそうです！\n今度、一緒に\n見に行きましょう♪", 105401, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7424, "最初はどうかな～って\n思ったけど…\n水着姿でも案外\n問題ないのよね…", 126801, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7425, "水着で飲みすぎると\nお腹が冷える、って\n言うけど…麦しゅわ\nなら問題ないわね！", 126801, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7426, "暑ければ暑いほど\nその後の麦しゅわは\nおいしくなるのよ…", 126801, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7427, "しっかりと仕事を\nこなしてから、\nしっかりと麦しゅわ！\nやっぱりこれね！", 126801, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7428, "ふふっ、デザインも\n着心地も最高ね\n奮発して用意して\n正解だったわ♪", 126801, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7429, "伝説の麦しゅわ、\n飲めるものなら\nまた飲みたいわね…", 126801, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7430, "二つ名に恥じない\n聖騎士として\n私…飲むわ！", 126801, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7431, "この格好なら水に\n濡れても大丈夫！\nなんだけど、さすがに\n書類は濡らせないわね", 126801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7432, "そうやってじっと\n見られると…ちょっと\n恥ずかしいわ…", 126801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7433, "今日もいい天気ね\nふふっ、あとで\nビーチに遊びに\n行きましょ♪", 126801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7434, "水着姿も慣れると\n案外快適ね\nそうだ、キミも\n水着になっちゃう？", 126801, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7435, "例え水着姿であろうと\n聖騎士としての誇りは\n忘れてないわ！", 126801, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7436, "キミとだったら\n麦しゅわなしでも\n最高の夏のバカンスに\nなると思うわ♪", 126801, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 7437, "仕事終わりの\n麦しゅわのために\n今日も一日\n頑張るわよ♪", 126801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 7438, "今日のお昼は\nどうしようかしら…\nさ、さすがに昼からは\n自重してるわよ？", 126801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 7439, "さ、一日の疲れを\n洗い流すために…\n軽～く飲むわよ！", 126801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7441, "こうやって\n直に触れると\n肌触りの良さが\nよく分かるわ♪", 126801, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7442, "今日もかわいいわね～\nどう？　今度\nお姉さんの一人飲みに\n付き合わない？", 126801, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7443, "いい香りね～…はっ！\n麦しゅわの匂いの\nアロマポットなんて\nあったらよくない？", 126801, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7444, "こんな格好で火に\n当たってると焙られる\nスルメの気分が\nちょっと分かるわ…", 126801, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7446, "もぐもぐ…うん、\n結構おつまみにも\nむいてそうね～♪", 126801, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7447, "試験に使った参考書、\nたまには読み返して\nおかないとね", 126801, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7449, "ふふ♪\nいい手触りね～", 126801, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7450, "…はふう～…\nゴロゴロするのって\n最高ね～", 126801, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7451, "ん…やっぱり\nこの水着…\n大胆、なんじゃ…？", 126901, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7452, "海でもお世話って、\nできるのかしら…？\n浮き輪代わりになる、\nとか…？", 126901, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7453, "～♪\nふふ…海に行く\n準備も、楽しい…かも", 126901, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7454, "あうぅ…水着で\n歩いてると…お尻…\nすぐ食い込んじゃう…", 126901, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7455, "海では、はしゃいだ\nほうがいいのよね…\nわ、わー、い…♪\n…こうかしら？", 126901, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7456, "あなたとなら…\n楽しい夏の思い出、\n作れる…気がするの…", 126901, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7457, "また来年も…\n再来年も…\nあなたと海へ\n行けますように…", 126901, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7458, "水着の着方を\n練習してるの…\n後ろ、変じゃない…？", 126901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7459, "海に行く支度…\n進んでるかしら…？\nお手伝い、する…？", 126901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7460, "ひ、日除けの準備は\n万全よ…\nずっと太陽の下に\nいたら…溶けちゃう…", 126901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7461, "泳ぐ練習…\nこ、ここでするのは\n…さすがに変？", 126901, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7462, "人混みに入るのは\nまだ不安で…\nでも、あなたといれば\nきっと…大丈夫…", 126901, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7463, "あなたのおかげで\n海の楽しいところを\nたくさん知ったわ…\nありがとう…♪", 126901, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 7464, "お出かけの日の朝は\n早い、のよね…？\nだから、ずっと\n起きていたわ…", 126901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 7465, "今頃ビーチの日差しは\n強いんでしょうね…\nそ、想像しただけで…\nめまいが…", 126901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 7466, "ビーチの夜といえば\n花火って、聞いたわ…\nバン、って言わない\n花火…ある…？", 126901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7468, "素肌にふわふわが…\n気持ちいい…♪", 126901, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7469, "うふふ…\n一緒に海…行く？", 126901, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7470, "ふぅ…♪\n気分まで…\n晴れやかになるわ…", 126901, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7471, "暑いような…\n寒いような…\n不思議な気持ちに\nなるわ…ね？", 126901, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7473, "泳いだ後には\nいっぱい食べたほうが\n体によさそう、ね…", 126901, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7474, "ビーチの日陰で\n読書するのも…\n気持ちよさそう…♪", 126901, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7476, "これで合ってる…？\n遊び方わからなくて…", 126901, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7477, "ふわふわ…♪\n海の底にいるみたい…", 126901, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7478, "恥ずかしい格好で\n室内を歩くこの快感\nクセになりそうです…", 127001, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7479, "この水着…\nどうしてかポーズを\n決めたくなりますね…", 127001, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7480, "海にはどんな生き物が\nいるのでしょう～\n胸が高鳴ります～♪", 127001, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7481, "夏は暑いですね…\nクウカの体が熱いのも\n夏のせい…ぐふふ…♪", 127001, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7482, "ドＳさんと無人島に\n取り残されて…という\n妄想はかなりイイです", 127001, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7483, "ドＳさんの\n欲望に満ちた眼差し…\nクウカ、受け止めます", 127001, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7484, "海に行くのが\n楽しみです…\nドＳさんと…ぐふふ♪", 127001, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7485, "「もっと過激な水着を\n見せてみろよ」なんて\nいけませんドＳさん…", 127001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7486, "水着のままで\n踊ればいいのですね…\nドＳさんの望みなら！", 127001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7487, "ドＳさんの熱い視線を\n感じます…じゅるり…\nもっと見てください…", 127001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7488, "こんな格好のクウカを\n放っておくなんて…\nさすがですドＳさん！", 127001, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7489, "海で水着といえば\n何かが起きる予感…！\nドＳさんの新たな攻め\n期待していますね…！", 127001, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7490, "恥ずかしいですけど\n頑張っちゃいました\nドＳさん…こういう\n水着、好きですか？", 127001, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 7491, "こんな朝からクウカを\n水着に着替えさせて…\n本当にドＳさんは\n欲望に正直ですね！", 127001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 7492, "こんな昼間から\nドＳさんがヌルヌルの\n液体をクウカに～！\nえっ…日焼け止め？", 127001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 7493, "ドＳさん…これから\nクウカと夜の海を\n見に行きませんか？\nきっと綺麗ですよ…♪", 127001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7495, "ここなら妄想に\n集中できます～♪\nじゅるり～♪", 127001, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7496, "こうもかわいいと\n妄想に登場させるのも\n気が引けます…", 127001, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7497, "クウカを昂らせる\nこの香り…！\nは！　ドＳさんの罠！", 127001, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7498, "体を熱くさせて\n水着を脱がそうと！？\nテクニカルです…！", 127001, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7500, "クウカの口の中に\nたくさん…♪\n止まりません…！", 127001, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7501, "読書は心が穏やかに\nなりますね…！\nぐふふ…穏やかに…", 127001, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7503, "いい乗り心地です…♪\nあっ…普通に楽しんで\nしまいました…", 127001, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7504, "これは…\n妄想に役立ちそうな\n感触です…！", 127001, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 7505, "成長した自分に\nあわせて、もっと\nかっこいいポーズを\n研究するッス！", 100501, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 7506, "これからも一緒に\n最高のヒーローを\n目指していくッスよ♪", 100501, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7507, "お父さん、娘の\n水着姿がそんなに\nうれしいの？", 127101, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7508, "怖い話を子どもたちに\n聞かせたら泣かれて\nしまいました…\n加減が難しいです…", 127101, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7509, "海に行くと父が\n水着の女性ばかり\n追いかけて困ります…", 127101, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7510, "拾った貝殻で\n新しい占いでも\nはじめてみようかな", 127101, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7511, "貝殻を耳に当てると\n波の音が…あっ\nこれはダメです\nよくない声がします", 127101, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7512, "この飲み物、スーッと\n体に沁み込んでくる…\nあの人にも\n教えてあげたいです", 127101, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7513, "強い日差しも騒がしい\n海も苦手なのに\nあの人となら不思議と\n楽しく思えるんです", 127101, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7514, "夏限定、水と氷を\n使った特別な占いは\nいかがですか？", 127101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7515, "暑い日々にはやはり\n怪談ですね\n厳選フルコースで\nお聞かせします", 127101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7516, "寒気がする？　それは\n霊ではなくかき氷の\n食べ過ぎですよ", 127101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7517, "今日の占い、あなたの\nラッキーパーソンは…\n白い水着の女性です", 127101, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7518, "あの、よければ\n日焼け止めを塗って\nもらえませんか？\n背中は届かなくて…", 127101, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7519, "海に行きませんか？\n二人きりで静かな\n砂浜を散歩したいです", 127101, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 7520, "潮騒だけが響く朝の海\n心が落ち着きます\nこれから一緒に散歩に\n行ってみませんか？", 127101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 7521, "不思議です。どうして\n普通のご飯でも、海で\n食べると特別おいしく\n感じるのでしょう…", 127101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 7522, "夜の海に行きませんか\n今なら夜光虫が\n見られるかも\nしれませんよ", 127101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7524, "夏は知らぬ間に体力を\n消耗しています\n適度に休息して\nいきましょう", 127101, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7525, "このぬいぐるみ…\n大丈夫です\n何も憑いては\nいませんよ", 127101, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7526, "ほのかにお線香\nみたいな香り…\nなんだか\n落ち着きます", 127101, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7527, "人魂、鬼火\nセントエルモの火…\n炎を見ていると色々と\n連想してしまいます", 127101, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7529, "お腹を壊さぬよう\n食い合わせには\nご注意ください", 127101, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7530, "これはいい\n怪談集ですね…\nつい読みふけって\nしまいます", 127101, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7532, "ふふ、こういうのに\n乗るとなんだか\n子どもに戻った\nみたいですね", 127101, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7533, "こ、これは…\nちょっとメルヘン\n過ぎて恥ずかしい\nですね…", 127101, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7534, "本当に素敵なお家…♪\n騎士たちの人柄を\n感じられるようで…\n不思議と落ち着きます", 126501, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7535, "調度品をつい間食…\nという習慣がこちらの\n世界にはないのですね\n…気をつけないと", 126501, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7536, "手土産のお菓子は\nどちらに置けば\nよいでしょうか？", 126501, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7537, "まるでおとぎ話の中に\n入ってしまったかの\nよう…別世界とは\nよく言ったものです", 126501, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7538, "お忍び…ふふっ♪\nときめく響きです\nなんだかお茶目で…\n密やかな印象が", 126501, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7539, "ふわ…\nああ、ついあくびが…\n居心地がよすぎるのも\n困り物ですね…ふふ♪", 126501, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7540, "騎士には不思議な力が\nあるだけでなく\n何者かの加護が\nあるようですね…", 126501, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7541, "この『王家の装備』は\n少し気難しいのです\nわたし以外が触れると\nその…ビリビリっと", 126501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7542, "子どもたちに新しい\n遊びを教わりました♪\nにーらめっこしましょ\nあっぷっ…ぷ～っ！", 126501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7543, "実は憧れていたのです\n友人の家を訪ねると\nいう経験に…ふふっ", 126501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7544, "ぎるどはうす…\nぎるど活動の拠点だと\n管理協会？　の方に\nうかがいました", 126501, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7545, "あなたは運命を\n信じますか？\nわたしは…信じます\nよい運命だけですが♪", 126501, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7546, "もしわたしが本当に\n天使だったとしたら…\nどうされますか？\n…ふふ、冗談です♪", 126501, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 7547, "パンケーキの香りで\n目覚める朝は\nとても良いものですよ\nぜひともお試しを♪", 126501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 7548, "ひと休みの時間ですね\n食事にお昼寝…\nすべきことを行って\n英気を養いましょう", 126501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 7549, "そろそろ休まれては？\n眠れないようであれば\n寝物語か子守歌でも\nふふ、遠慮は無用です", 126501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7551, "すう………はっ！\n眠ってはいませんよ？\nいくら居心地が良い\nからといって…ふわぁ", 126501, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7552, "まあ、あいらしい…♪\nぜひわたしの寝室に\n招待させてください", 126501, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7553, "なんて、かぐわしい…\nオーブンからただよう\n焼き菓子の香りとは\nまた異なる魅力が…", 126501, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7554, "みなで暖をとりながら\n互いに語り笑い合う\n…心休まる時間ですね", 126501, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7556, "食は心と体に\nゆとりを生みます\n…辛い食べ物の場合？\nそれは、その…", 126501, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7557, "こちらの世界の知識を\n蓄えておかなければ\nテーブルマナーに\nついてのページは…", 126501, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7559, "新しい色の蛍光ペン\n買ったのよね\nこれで勉強が\n楽しくなるわ～♪", 127201, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7560, "学食のランチ、結構\nおいしいのよね～\n今度ペコリーヌに\n教えてやろうかしら？", 127201, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7561, "ふふふ…扉に黒板消し\n仕掛けてるの…あいつ\n引っかかるかしら？", 127201, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7562, "部活するなら運動部も\n悪くないかもね～\n夕日に向かって\n突っ走ってやるわ！", 127201, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7563, "毎日窮屈な制服着て\n学生も大変ね～\n…ま、かわいいけど", 127201, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7564, "あいつに貸したノート\n変な落書きとか\nしてなかったよね…？\n…その、相合傘とか…", 127201, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7565, "昨日の家庭科で\n作ったクッキー…\nあいつにどうやって\n渡そうかな…？", 127201, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7566, "宿題教えてって？\nしょうがないわね～\nじゃあ一緒に\nやりましょ", 127201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7567, "あ、明日あたしたち\n日直当番よね？\nめんどい仕事は\nあんたに任せるわ～", 127201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7568, "寮生活は楽しいけど\n就寝時間が早すぎよね\nいきなり消灯されると\nビックリするわ", 127201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7569, "校則で禁止だけど、\nにけつでほうき通学\nとか青春っぽくない？", 127201, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7570, "制服って好きな人とも\n自然にお揃いになれて\nいいわよね…って別に\nあんたとって訳じゃ…", 127201, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7571, "あんたネクタイ\n曲がってるわよ～？\nほら直したげるから\nこっち向いて？", 127201, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 7572, "おはよ～！\n今日も元気に登校よ！\n忘れ物ないようにね！", 127201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 7573, "午前の授業疲れたわ～\nしっかりお昼を食べて\n午後も頑張るわよ！", 127201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 7574, "うう…学校の怪談\n思い出しちゃった…\n…別に怖くないけど！", 127201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7576, "ふかふか～\n…気持ち良すぎて\n制服のまま\n寝ちゃいそうね…", 127201, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7577, "かわいくて癒されるわ\n寮に持ち込んでも\n大丈夫かしら…？", 127201, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7578, "集中力の高まる香りで\n勉強の効率爆上がりね\n次のテストで\nいい点取るわよ～！", 127201, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7579, "うう、ちょっと冷えて\n来たわね…\nスカートの下に\nジャージ穿こうかしら", 127201, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7581, "もぐっ…もぐっ…\n食べとかないと教室で\nお腹なっちゃうわ～", 127201, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7582, "図書室で借りた本\n明日返却日なのよね…\n今日中に読むわよ！", 127201, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 7584, "あいつに背中を\n預けるのもいい気分だ\nいつかは、全部…", 104301, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 7585, "な、なんだよ？\nじっと見るんじゃねぇ\n尻尾が揺れちまうだろ", 104301, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7586, "魔法の練習！\nむむむ～…\nそれっ…！", 127301, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7587, "そだ！　これ着て\nルー学行こっかな？　\n制服自慢しちゃお♪", 127301, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7588, "赤点回避の魔法とか\nあったら最高じゃね？\n誰かうちに教えて～", 127301, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7589, "制服が一番映える\n場所ってどこだろ？\n教室？　遊園地？", 127301, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7590, "ベンキョーつらたん…\n今度キャルっちに\n助けてもらお", 127301, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7591, "部活か…\nヒデサイと一緒に\nやったら楽し～かも♪", 127301, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7592, "男子の制服\nもにカワだった…\n今度ヒデサイに\n借りよっかな？", 127301, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7593, "ちょすー、うちの\nさいきょー魔法で\nテンアゲしてあげる～", 127301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7594, "九九って呪文ぽくね？\nほら、例えばヒチシ…\nヒチシ、なんだっけ？", 127301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7595, "ヒデサイセンセー\nうちにベンキョー\n教えて！", 127301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7596, "学割マジ助かるー\nヒデサイ、一緒に\nどんどん使ってこー♪", 127301, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7597, "今度ヒデサイに\nお弁当作ったげる\n屋上で一緒に食べよ♪", 127301, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7598, "ヒデサイのバイブス\n上がる言葉教えてよ\nつらたんな時、うちが\nいっぱい言ったげるよ", 127301, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 7599, "ちょすちょす～\n今日も朝から\nバイブス十分♪", 127301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 7600, "お昼どうしよっかな\nたまにはガッツリ\nいっちゃう系？", 127301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 7601, "やべ、宿題まだ\nできてないし…\nヒデサイ助けて～", 127301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7603, "充電ちう～\n…ガッコーのイスも\nこれにして～", 127301, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7604, "かわいいし、\n癒されるし、キュン死\nしちゃうじゃん…！", 127301, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7605, "リフレッシュの\n魔法発見！\nいい香り～♪", 127301, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7606, "ぽかぽかサイコー♪\n今日はもーずーっと\nここにいたいかも～", 127301, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7608, "超おいし～♪\n学食にあったら\n毎日リピする～", 127301, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7609, "ふむふむ…\nねむみパない…\n続きはまた今度～", 127301, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7611, "今度は子どもたちも\nサラサリアに連れて\n行ってあげたいわ", 127501, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7612, "子どもたちから\nお姫さまみたいって\n言われちゃった♪", 127501, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7613, "この剣…火を点けたり\nするのに便利なのよ\nコントロールも効くし", 127501, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7614, "あのサラサリアの\nカラっとした暑さが\nちょっと恋しいわ…", 127501, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7615, "買ってきたお土産、\nお世話になってる\nみんなに配りましょ♪", 127501, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7616, "あんたと一緒なら\nどこの国でだって\n暮らしていけそうね♪", 127501, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7617, "今の事業を大きくして\nあんたと世界中の国を\n飛び回ったり…なんて\nすてきじゃない？", 127501, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7618, "サラサリアで食べた\n料理が恋しい…？\nもう…また今度\n作ってあげるわよ", 127501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7619, "今度旅行するなら\nどこに行こうかしら？\n希望があれば教えてね", 127501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7620, "サラサリアの香辛料、\nランドソルでも\n人気が出そうよね\nいいビジネスの予感！", 127501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7621, "そんなにじっと\n見られるとさすがに\n恥ずかしいってば…", 127501, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7622, "お嫁さんは憧れるけど\nお婿さんになって\nもらうのもありよね…\nあんたはどう思う？", 127501, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7623, "やっぱり、あんたには\nいつもあたしの隣に\n居てほしいな…", 127501, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 7624, "陽射しが強くなる前に\n雑多な用事は\n片付けちゃいましょ", 127501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 7625, "サラサリアで食べた\n料理を再現してみたの\n味見してくれる？", 127501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 7626, "暑さに負けないように\n睡眠はちゃんと\n取っておかないと", 127501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7628, "こっちでもあっちでも\n忙しいのは変わらない\nわね…ちょっと休憩～", 127501, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7629, "サラサリアで見た\nぬいぐるみも\nかわいかったわね", 127501, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7630, "お香って地域ごとの\n特色が出るわよね\nこの香りは…\nちょっと神秘的？", 127501, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7631, "砂漠地方も夜は冷える\nのよね。温かいものが\nあると安心するわ…♪", 127501, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7633, "食べ慣れた味って\nどうしてほっと\nするのかしら…", 127501, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7634, "今後の取引相手に\nなるかもしれないし\n向こうの文化も\n勉強しておかないと…", 127501, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 7636, "犯人はこの中にいる！\n探偵として一度は\n言ってみたいセリフさ", 101401, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 7637, "助手くんの情報を\n仕入れておかないと…\n理想の私を、ずっと\n見ていてほしいからね", 101401, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7638, "久々の長旅で\n里心がついちまったね\nしばらくランドソルで\nのんびりしようか", 127601, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7639, "たまには違う服装\n違う得物を使うのも\n悪くないね", 127601, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7640, "サラサリアにいた時は\n砂まみれで大丈夫かと\n思ってたけど、今じゃ\n懐かしく感じるよ", 127601, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7641, "魔法の絨毯、あれは\n楽だし速いし便利\nだったねえ…土産に\n一枚欲しかったな", 127601, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7642, "砂上での戦いは\nなかなかいい鍛錬に\nなったね…今度は\n修行しに行くとしよう", 127601, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7643, "サラサリアの人たちも\n気のいい連中だったが\n元気にやってるかねえ", 127601, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7644, "旅の恥はかき捨てって\nいうけど、悪い旅じゃ\nなかったね。次は\nアイツとどこに行こう", 127601, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7645, "この格好を冷やかしに\n来たのかい？\n生憎、もうすっかり\n慣れちまったよ", 127601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7646, "ギルドの連中に\n土産をわたしに\n行くところなんだ\n付き合っておくれよ", 127601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7647, "久々に水場で\n釣りがしたいねえ…\nもちろんお前さんも\nくるんだろう？", 127601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7648, "おっと\nサラサリアの連中から\n便りが来てるよ\nさ、一緒に読もうか", 127601, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7649, "最近サラサリアの踊り\nを練習してるんだ\nあとで振付けを\n見ておくれよ", 127601, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7650, "なんだいお前さん？\n…なんだか若奥さん\nっぽいって？　まあ、\nそりゃあ、ねえ…", 127601, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 7651, "いい時間だね\n日が昇りきる前に\nやることをすませ\nとかないと", 127601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 7652, "昼はどうしようかね\nサラサリア風の\n食事でもつくろうか", 127601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 7653, "さて、早めに寝ようか\n砂漠の夜は冷えるから\n…っと、まだ旅行中の\n気分でいたよ", 127601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7655, "良い座り心地だ\nサラサリア宮殿の\n応接間を\n思い出すねえ…", 127601, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7656, "ランプの魔神に\nあげたら喜ぶかねえ？\n次に会う時までに\n見繕ってみようか", 127601, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7657, "この香り…\nサラサリア土産の\n香木かい？", 127601, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7658, "旅先での焚火を\n思い出すよ\nなんだかほっとするね", 127601, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7660, "ふふ、慣れてる味も\nいいけど、むこうの\n味付けが懐かしく\nなっちまうね", 127601, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7661, "ふむふむ…\n千夜一夜物語か\nおもしろいもんだねえ", 127601, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7663, "立派なギルドハウス♪\n帰る場所が素敵だと\n冒険に出るのも\n楽しくなりそうです♪", 126001, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7664, "お掃除でも…と\n思いましたけど、\nさすがお兄さんのお家\nきれいにしてますね♪", 126001, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7665, "わたしたちもいつか\n立派なギルドハウスが\n持てる冒険者に\nなりたいです…♪", 126001, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7666, "わたし冒険者ですけど\n家を守ってお兄さんの\n帰りを待つのも\n悪くない…かも？", 126001, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7667, "お菓子焼こうかな…♪\nちょっと多めに作って\nお兄さんにも\n食べてもらおうっと♪", 126001, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7668, "このお家、とっても\n大好きになりました♪\nだからその…もう少し\nいてもいいですか…？", 126001, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7669, "お兄さんに\n感謝の手紙を書いて\n枕元に隠して\nおこうかな…♪", 126001, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7670, "…お兄さん\nお暇なんですか？\nじゃあ一緒に冒険へ\n出かけましょう♪", 126001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7671, "お兄さんは苦手な食材\nとかありますか？\nあの、もしよければ\n今から手料理を…♪", 126001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7672, "まったり過ごす\nお家時間もいいですね\n冒険者には羽休めも\n大事ですから♪", 126001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7673, "いつでもお兄さんを\n守れるように\nこの姿でいることに\n慣れておきますね…！", 126001, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7674, "お兄さん眠そうです…\nちょっとお昼寝\nしちゃいましょうか♪\nはい、膝枕…♪", 126001, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7675, "お兄さん怪我を…！？\nわたしが治療を…\nふー…ふー…", 126001, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 7676, "おはようございます♪\n朝、元気に挨拶すれば\n幸せな一日が過ごせる\nそうですよ♪", 126001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 7677, "お昼ごはんの時間です\n…手がふさがってる？\nわかりました、じゃあ\nあ～んしますね♪", 126001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 7678, "ふわぁ…眠くなって\nきちゃいました…\nお兄さん…お泊りして\nいいですか…？", 126001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7680, "ふにゃ～～…♪\nこんな姿、他の人には\n見せられません…♪\nもふもふ～～…♪", 126001, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7681, "お兄さんこういうの\n好きなのかな～…\nふふっ、わたしも♪\nぎゅ～～～っ♪", 126001, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7682, "アロマの癒し効果…\n上手くアレンジして\nみなさんの治療に\n活かせないかな～？", 126001, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7683, "は～気持ちいい～♪\n身体が暖まると\n心もぽかぽかです♪", 126001, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7685, "うふふ、おいしい♪\nプレシアちゃんなら\n無限にぱくぱく～って\nしちゃいそうです…♪", 126001, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7686, "本はわたしを\n知らない世界に\n連れていってくれる…\nまるでお兄さんです♪", 126001, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 7688, "話題の新刊…\nあの人も読んでるかな\n今度会う時、感想\n聞いてみたいな…♪", 103801, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 7689, "うにゃっ…あ、熱を\n測ってくれたんですか\nありがとうございます\nバッチリ元気ですよ♪", 103801, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7690, "お兄の、けはい…\nん、みつけた\nぎゅ", 126101, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7691, "きょろきょろ\nここも\nぱとろる、するよ", 126101, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7692, "おうち\nにぎやか\nさみしく、ない", 126101, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7693, "きょだい、おにぎり\nきょだい、肉まん\nきょだい…お兄？", 126101, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7694, "はっぴーちゃん\nどこ？\nお兄、たべた？", 126101, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7695, "くんくん…この、へや\nお兄の、におい\nすんでる\nすめる？", 126101, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7696, "おそうじ、にがて\nでも\nれんしゅう\nきゅっきゅ", 126101, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7697, "みみ、さわり、たい？\nんー…だめ…\nむずむず\nなので", 126101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7698, "くろぷれしあ\nかっこいい？\nん…あざます", 126101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7699, "お兄の、おうち\nきょうみ、ましまし\nたんけん\nするね", 126101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7700, "なでなで、される…と\nねむ…く…\nぶぅ…ぶぅ…ぶぅ…", 126101, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7701, "かみ、りぼん\nおてつだい\nよろ\nです", 126101, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7702, "ここ\nすむ\nね？\nまいにち、いっしょ", 126101, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 7703, "ぐっもにん\nあさごはん\nあさごはん", 126101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 7704, "お兄\nおつかれ？\nれっつ、まいぺーす", 126101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 7705, "ひとりで、ねれる\nでも\nひとりじゃ、なくても\nねれる、よ…？", 126101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7707, "ふかふか\nパパの、おなか\nみたい", 126101, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7708, "こんにちは\nぷれしあ、です\nおともだち、なろ？", 126101, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7709, "いい、におい\nでも、お兄\nもっと、いい、におい", 126101, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7710, "とりま\nお肉、やこ", 126101, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7712, "もぐもぐ…\nむげん肉まん、も\nきぼう", 126101, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7713, "ぜんぶ、よんだ\nもっと、ぶあついほん\nでも、いーよ？", 126101, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7715, "幸せなクリスマスを\n過ごすため\nギルドハウスを\nきれいにしなくちゃ♪", 127901, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7716, "えへへ♪\n彼へのプレゼント…\n枕元にそっと\nおいておきましょう☆", 127901, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7717, "クリスマスといえば\n七面鳥の丸焼き…☆\nよだれを拭いて\nいざ準備です♪", 127901, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7718, "彼に用意した\nプレゼント…\n大きすぎて\nくつ下に入りません…", 127901, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7719, "プレゼントは\n貰う方も、贈る方も\nどちらも幸せを\n感じますね…☆", 127901, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7720, "夜景が見える\nレストランであの人と\nクリスマスデート…\n憧れちゃいますね☆", 127901, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7721, "プレゼントはわたし…\n恥ずかしいですけど\nこの贈り物、彼に\n喜んでほしいです…☆", 127901, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7722, "オーナメントを\nたくさん用意したので\n二人でツリーに\n飾りつけましょう☆", 127901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7723, "あはは、お腹\n空いちゃいました？\nパーティーまで\n我慢してください♪", 127901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7724, "ジングルベ～ル♪\nあなたも一緒に\nクリスマスソング\n歌っちゃいましょう♪", 127901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7725, "サプライズプレゼント\nの用意をして…って\nあなた！？　い、今の\n聞いてませんよね…？", 127901, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7726, "クリスマスの夜は\nパーティーしましょう\nできれば…その…\n二人っきりで…☆", 127901, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7727, "あなたと過ごした\n最高のクリスマス…\nずっとずっと心の奥に\nしまっておきますね", 127901, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 7728, "おはようございます！\n皆の願いが叶うような\n素敵な一日に\nしましょうね☆", 127901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 7729, "お昼ですね！\nディナーの買い出しが\n済んだら、少しお昼寝\nしちゃいましょうか☆", 127901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 7730, "そろそろ就寝の\n時間ですね～\n枕元にくつ下の準備を\nお忘れなく～☆", 127901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7732, "ふ～気持ちいい…☆\nお家でまったり過ごす\nクリスマスも\nいいですよね…♪", 127901, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7733, "かわいいですね～♪\nサンタさんからの\n贈り物でしょうか～？", 127901, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7734, "すう～…はあ～…\nすごく素敵な香り…\n炊き立てのご飯と\nいい勝負です…♪", 127901, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7735, "暖かいですけど…\nあの日の山小屋を\n思い出して\n恥ずかしいですね…☆", 127901, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7737, "むしゃむしゃもぐもぐ\n止まりませんよ\nクリスマスですから\nむしゃむしゃもぐもぐ", 127901, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7738, "物語に出てくる\nサンタさんみたいに\nわたしも幸せを\n運びたいです…☆", 127901, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7740, "わっ！\nこ…これは素敵な\nサプライズプレゼント\nですね…☆", 127901, 3, 14);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7741, "これを着ればわたしも\n立派なサンタさんに\nなれますかね…？", 127901, 3, 15);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 7742, "ここが今のあたしの\n居場所ってやつです\nふふっ、なかなか\n悪くねー気分ですよ", 106601, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 7743, "これからはボス代行\nとしてブイブイ\nいわせてやるです\nついてくるですよ！", 106601, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7744, "陛下～♪\n貴女のクレジッタが\nおめかしして\nはせ参じましたわ～♪", 128001, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7745, "パーティーとは景気の\n良さを見せつける場…\n倹約などは\nもっての外でしてよ！", 128001, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7746, "この天井の高さなら\n搬入できそうですわね\n陛下用の特注ケーキ♪", 128001, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7747, "…ぶぇっくしゅ！\nふう…少し背中を\n開けすぎたかしら…？", 128001, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7748, "ここは秘書の目も\n届きませんから\nチクりメモの心配も\nありませんのよね～♪", 128001, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7749, "いくらでも二度見して\nよろしくてよ？\n本当にわたくしだと\n心から納得できるまで", 128001, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7750, "袖の下以外の贈り物も\nなかなかどうして…\n悪くないものですのね", 128001, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7751, "こう見えてダンスの\nたしなみはありますの\nお相手してあげても\nよろしくってよ", 128001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7752, "ドレスはもちろん\n特注品ですわ♪\nもしや貸衣装だと\n思いまして？", 128001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7753, "バッグの中身？\n仕事に必要なものと…\n心を潤わせるための\n宝石がいくつか♪", 128001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7754, "やる気というものは\n仕事に必要な条件です\nいわばドレスコードの\nようなものですわね", 128001, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7755, "全ての仕事は自分の\n血となり肉となり\nお金にもなるもの\nなのです。経験談です", 128001, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7756, "今年の教訓を活かして\n来年は更に素晴らしい\nクリスマスにして\n差し上げますわ！", 128001, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 7757, "おはようございます\n今日という稼ぎ時の\nはじまりですわよ！", 128001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 7758, "安くて早くておいしい\nそれがランチに必須の\n栄養素ですわ！", 128001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 7759, "宵越しの仕事は\n安眠の敵！\n熟睡したいのであれば\n今が勝負時ですわよ！", 128001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7761, "たまにはこうして\n休息を取ることも…\n…はっ！？\nドレスにシワが～っ！", 128001, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7762, "…わたくしの勘が\n言っていますわ\nこの愛らしさ！\n爆売れ確・定！", 128001, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7763, "いい香りですわね…\nお金の匂いには\n敵いませんけれども♪", 128001, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7764, "……ハッ！？\n完全にボーッとして\nしまいましたわ…！\nぬくもり…恐ろしい！", 128001, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7766, "考えませんわ…\n考えませんわよ！\nカロリーのことはっ！", 128001, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7767, "ビジネス本以外の本を\n読むのも、たまには\n悪くありませんわね…", 128001, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7769, "よくできていますわね\nこの絵本！\n子ども向けだと甘く\n見ていましたわ…！", 128001, 3, 14);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7770, "この視界の狭さ…\n思い出しますわね\nティッシュ配りの\nアルバイト時代を…", 128001, 3, 15);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7771, "ふふふ…こっそり\nスイーツを食べる\nリノちゃん見～っけ♪", 128101, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7772, "お姉ちゃんの抜き打ち\nお部屋チェック～♪\n…ケーキの食べこぼし\nこれはリノちゃんだね", 128101, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7773, "この衣装は目立つから\nどこにいても弟くんに\n気付いてもらえるね！", 128101, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7774, "弟くん、協力して！\nリノちゃんにお勉強\nしなさいって圧を\nかけるんだよっ…♪", 128101, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7775, "絆の力を感じるよ\nこれならもっと万全な\n弟くんのお世話が\nできそうだねっ！", 128101, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7776, "お姉ちゃんもたまには\nリノちゃんみたいに\n甘えてみようかな…", 128101, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7777, "弟くんとリノちゃん\nどっちも大切な\n私のきょうだいだよ", 128101, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7778, "分かってるよ♪\nお姉ちゃんの膝枕を\nご所望だねっ？　え？\n違う？　違わないよ？", 128101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7779, "リノちゃんがね\nお姉ちゃんチョップを\n防ぐ妹白刃取りを\n訓練してるんだよね", 128101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7780, "お姉ちゃん頭突きは\n封印しないとね…\nだって、今使うと\n頭飾りで威力が…ね", 128101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7781, "ふふっ♪\n目をつぶってても\n弟くんは見えてるよ\n…そこでしょ？", 128101, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7782, "お姉ちゃんも\nリノちゃんみたいに\nかわいさいっぱいに\n振舞っちゃうぞ♪", 128101, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7783, "弟くんっ♪\nいつもお姉ちゃんの\n声が届く範囲にいてね\n…絶対だよ", 128101, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 7784, "おはよう、弟くん！\nリノちゃんが起こして\nくれたんだね♪\n…先を越されちゃった", 128101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 7785, "お姉ちゃんランチの\n時間だぞ♪\nさ、食べさせてあげる\nお口を開けて…♪", 128101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 7786, "おやすみの時間だよ\n…弟くんは真ん中ね\n左右には私たち姉妹が\nいるから安心だよ♪", 128101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7788, "お姉ちゃんは全力で\nリラックスしてても\n弟くんの声一つで\nすぐにでも動けるよ♪", 128101, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7789, "リノちゃんこういうの\n好きそうだよね\n今度プレゼントして\nみようかな？", 128101, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7790, "いい匂いがするね～\nあれ？\nリノちゃんのケーキの\n甘い匂いも混ざってる", 128101, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7791, "お姉ちゃんの\n弟くんへの愛も\n熱く燃えあがるね…！", 128101, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7793, "たくさん食べて\n栄養を補給して\nお姉ちゃん力に\n全て変換するよっ！", 128101, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7794, "ことわざ辞典…\nリノちゃんにこれを\n読ませようかな", 128101, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7797, "そろ～り、そろり…\nお姉ちゃんが居ぬ間に\nスイーツを～…♪", 128201, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7798, "いつか\nお姉ちゃんに似合う服\n作ってみたいですね", 128201, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7799, "あいたっ！？\nひらひらしたとこ\n踏んづけちゃいました", 128201, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7800, "お姉ちゃんと\nお兄ちゃんの二人から\n勉強しろって圧力が～", 128201, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7801, "私の中にも秘めた力が\n眠っているはず…\nそう、妹パワーが", 128201, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7802, "私もお兄ちゃんを\n甘やかしてみたいです\n…お姉ちゃんみたいに", 128201, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7803, "お兄ちゃんの部屋を\nこっそり拝見～♪\nはっ！？　これは\nお姉ちゃんの視線…！", 128201, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7804, "お姉ちゃん頭突きは\n大地を揺らす威力です\n私とお兄ちゃんは\n挨拶感覚で喰らいます", 128201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7805, "お姉ちゃんチョップは\n雲を裂く切れ味です\n早く妹白刃取りを\n完成させないと…", 128201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7806, "お姉ちゃんみたいに\n大人っぽく着こなす\nには一体どうしたら…", 128201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7807, "弓のデザインも\nかわいいんですよっ♪\n常に持ち歩きたいです\nけど…危ないですよね", 128201, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7808, "きれいな服を着てると\nお姫さま気分に\nなれますね…あとは\n王子さまがいれば…♪", 128201, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7809, "お兄ちゃんっ！\n私は褒められて伸びる\nタイプなのでもっと\nナデナデしてください", 128201, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 7810, "妹モーニングコール♪\nお姉ちゃんより先に\n起こしに来れました！", 128201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 7811, "ああっ、お姉ちゃん！\nお兄ちゃんにアーン\nするなんてずるいです\n私もしたいですっ！", 128201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 7812, "おやすみの時間ですね\nえへへ、きょうだいで\n並んで寝るのって\n楽しいですよね！", 128201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7814, "スイーツを食べた後は\nお腹を休めましょう～\n平気平気…ちゃぁんと\n運動もしますからぁ…", 128201, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7815, "お姉ちゃんも\nこういうかわいいもの\n好きなんですかね", 128201, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7816, "くんくん…\nなんの匂いでしょうか\n新しいスイーツ？", 128201, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7817, "きょうだい３人\n肩を寄せて暖まるとか\nよくないですかっ？", 128201, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7819, "いつも元気な妹で\nいるためには\nよく食べるのが大事！", 128201, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7820, "なんか姉に関する本が\nちらほらと混ざって…\nこれお姉ちゃんの\n仕業ですね！？", 128201, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7823, "ふんふんふ～ん\nお正月で世間は\n浮かれてるですねえ\nま、いいですけど♪", 128301, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7824, "は～、お正月は\nだらだらできて\n最高ですね\n…いつも通りですけど", 128301, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7825, "ビッグな幹部は\nビッグなお年玉が\nもらえるはずです\n楽しみですね", 128301, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7826, "今度ドラちゃんに\nお年玉を持っていって\nやりたいですね", 128301, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7827, "そういえばアユアユと\n約束してたですよ…\nそろそろ出かける\nですかね", 128301, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7828, "お年玉がもらえたら\n何に使うか…\n今のうちに\n考えとくです", 128301, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7829, "年神の報酬の駄菓子が\nたまってきたですね…\nガキどもにでも\n配っちまうですよ", 128301, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7830, "あたしのレアな\n晴れ着姿、今のうちに\nしっかり目に焼き付け\nとくがいいですよ", 128301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7831, "お兄さんは今日も\nヒマそうですねぇ…\nしょうがないから\nかまってやるですよ", 128301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7832, "そういえばカヤぴぃ\n見なかったですか？\n喧嘩屋初売りセール\nとか言ってたですけど", 128301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7833, "お正月ならではの\n大きなシノギ…\nなにかないですかね？", 128301, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7834, "いいとこに来たですよ\nさっき走ったら帯が\n曲がっちゃったです\n直すの手伝うです！", 128301, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7835, "年神の仕事ですか？\nこれからいくとこです\nお兄さんも一緒に\n来やがるですよ！", 128301, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 7836, "ふぁあ…\nどうしたですか？\nお正月に早起きなんて\n無粋ってヤツですよ…", 128301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 7837, "小腹が減ったですね\n一緒におせちでも\nつつきにいくですよ！", 128301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 7838, "夜はあたしの時間\nと言いたいとこですが\n初夢のために早く\n寝とくですかね…", 128301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7840, "正月は食っちゃ寝\nするのが仕事…\nって聞いたこと\nあるですよ", 128301, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7841, "ふふっ、晴れ着の\nあたしの次くらいには\nかわいいですね", 128301, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7842, "なんかいい匂い…？\nよく知らんですけど\n晴れ着からも同じ\n匂いがしてるですね…", 128301, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7843, "ふ～あったまるです…\nお正月の唯一の弱点は\n寒いことですからね…", 128301, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7845, "もぐもぐ…正月太り？\nあたしには関係ない\n言葉ですね", 128301, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7846, "あったあった…\nトーゴクには\nドラゴンの神宮も\nあるんですね", 128301, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7848, "お正月のトラブルも\nハツネちゃんが\nめでたく解決\nしてみせるのだ～！", 128401, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7849, "そうだ、新年会に\nサプライズ大作戦を\nしかけちゃおう！", 128401, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7850, "イノリちゃんも\nカスミンも振袖姿が\nかわいかったな～", 128401, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7851, "シオリンに\nお年玉あげたいし\n彼にバイトを紹介\nしてもらおうかな？", 128401, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7852, "今度はどんな凧を\n飛ばそうかな？\nみんなが驚くような\n凧がいいよね～", 128401, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7853, "ひらめいた！\n彼への年賀状は\nきらーんっ☆って\n感じにしよう！", 128401, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7854, "来年も彼の隣で\n初日の出が\n見られるといいな～", 128401, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7855, "今日はキミが\nギルドハウスに来る\n予知夢を見たんだよ～\nふふ、正夢になったね", 128401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7856, "この振袖どうかな？\nすっごくかわいいよね\nえへへ、ありがとう～", 128401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7857, "ハツネちゃんおみくじ\n開始！　きらーんっ☆\nキミは大吉なのだ～\nどう元気出たかな？", 128401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7858, "今度エルフの森にある\n薬草を使って七草粥を\n作ろうと思うんだ～\nぜひ食べてほしいな♪", 128401, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7859, "お正月っぽい遊びを\n色々と用意したのだ～\nたまにはギルドハウス\nでゆっくり過ごそうよ", 128401, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7860, "もしかして眠たいの？\n…じゃあ、また膝枕\nしてあげてもいいよ？", 128401, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 7861, "いっぱい寝たから\n元気いっぱいだよ～\n今日もがんばろう！", 128401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 7862, "お昼だね～\nおせちがあるから\nキミも食べるでしょ？", 128401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 7863, "そろそろ寝る時間だね\n今日はどんな夢を\n見られるかな～", 128401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7865, "いい座り心地～\n寝正月で過ごすのも\n最高なのだ～", 128401, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7866, "かわいいな～\n癒されてぐっすり\n眠れそうだよ～", 128401, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7867, "いい香り～\nリラックスすると\n眠たくなっちゃうよ…", 128401, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7868, "ポカポカいい気持ち～\n初日の出の温もりを\n思い出しちゃうな～♪", 128401, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7870, "もぐもぐ、おいし～\nでもいっぱい食べたら\n眠くなっちゃった…", 128401, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7871, "ふむふむ\nお正月料理も\n色々あるんだね～\n今度作ってみようかな", 128401, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 7873, "最近カレを見てたら\n何故かおいしそうに\n思えてくるんだよな…", 106501, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 7874, "手握っててくれるか？\nじゃないとオレ…\n力が抑えきれない…", 106501, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7875, "つかの間の平和を享受\nしながらぬくぬくと\n読書に耽る…\nふふ、最高だね", 128501, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7876, "お正月飾りなんて\n置いたらはしゃいでる\nように見えるかな…？", 128501, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7877, "ま、まずい…\n一人遊びしすぎて\nお正月遊びをほぼ\n極めつつある…", 128501, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7878, "ミステリーは\n時と場所を選ばない\nつまり…新年でも\n探偵は休まないよ！", 128501, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7879, "お世話になった人に\n年賀状でも書こうかな\n助手くん、喜んで\nくれるかな…えへへっ", 128501, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7880, "絵馬に願い事を書こう\n助手くんと一緒に\nいられますように…♪\n…は、恥ずかしいな", 128501, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7881, "助手くんはもっと\n派手な着物のほうが\n好みだったかな…？\nき、気になる…", 128501, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7882, "やあ。助手くんも\nお正月休みを\n持て余してるのかい？\nふふ、似た者同士だね", 128501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7883, "助手くん助手くん！\n凧あげしにいこう！\n…そ、捜査の一環で", 128501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7884, "着物は転びそうで\n怖いんだよなあ…\nお、押さないでね？\nフリじゃないよ！？", 128501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7885, "ひ、人目を憚る\n本を読むなら\nみんなが帰省中の今！\nはっ！　助手くん！？", 128501, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7886, "ね、ねえ…助手くん…\n一緒に初詣いかない？\nあ、いや！　深い\n意味はないんだけど！", 128501, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7887, "あはは…助手くんと\n一緒だと家でごろごろ\nしてるだけでも\n楽しいや…♪", 128501, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 7888, "ふぁ～…\nお正月休みのおかげで\n昼夜逆転中だよ…", 128501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 7889, "お正月も依頼が\nひっきりなしだね\nさあ、街へ出よう！\nもちろん君も一緒にね", 128501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 7890, "も、もっと\n近くにきてもいいよ？\n冬の夜は…寒いからね", 128501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7892, "ううぅ～…！\nこのままじゃ\n寝正月だぁ…！", 128501, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7893, "みんなが帰省中でも\nこれがあれば\n寂しくない…かな", 128501, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7894, "うーん…♪\nお正月休みの\nリフレッシュに\nちょうどいいや♪", 128501, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7895, "ふぅ…あったまるな\n冬は寒いから\n助かるよ…", 128501, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7897, "うーん、おいしい♪\n読書のお供に\nちょうどいい", 128501, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7898, "時間に余裕があると\n読書が進むよ\nいざ、本棚読破だ♪", 128501, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7900, "この新しい力…\nさらに使いこなせる\nようになるには…", 128801, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7901, "私に眠る属性の力…\n風の力以外には\nどんな力があるのかな", 128801, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7902, "今の私なら\nどこまでいけるのか…\nやれるだけ\nやってみよう", 128801, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7903, "風の力の制御…\n段々とコツが\n掴めてきたな", 128801, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7904, "風の力か…\n応用の幅は広そうだ\n何ができそうか\n色々試してみよう", 128801, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7905, "今度、私とキミで\n料理対決でもする？\n簡単には負けないよ", 128801, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7906, "この胸に溢れる\n温かな気持ちは…\nきっと彼への…", 128801, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7907, "一段と剣技が\n冴えてきたような…\nいや…油断禁物だ", 128801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7908, "かっこいいって？\nありがとう…そんなに\n真っすぐ褒められると\n照れてしまうな…", 128801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7909, "どれほど厚い壁が\n立ち塞がろうと\n打ち破ってみせる", 128801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7910, "手が空いてるなら\n剣の稽古に少し\n付き合ってくれるかな", 128801, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7911, "試練を乗り越えて…\n私の中にあるものが\nはっきりと分かったよ", 128801, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7912, "キミから流れてくる\n力の温かさ…これが\n私たちの絆なんだ", 128801, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 7913, "朝だよ、そろそろ\n起きる時間だ\nそれとも私に起こして\nほしいのかな？", 128801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 7914, "昼食は私が作るよ\nリクエストはある？\nできる範囲で応えるよ", 128801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 7915, "おやすみ。また明日も\n一緒に冒険しよう\n約束だよ？", 128801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7917, "休める時は休む…\n戦い続けるには\n必要なことだからね", 128801, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7918, "心の安らぎの大切さ…\nそれを知れたのは\nキミたちのおかげだね", 128801, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7919, "気持ちが落ち着くな…\nこの香り、風の力で\n纏えたりしないかな", 128801, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7920, "暖まった空気の流れ…\n以前より風の変化に\n気づくようになったな", 128801, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7922, "この味付け…\n今度試してみようか\n味見は任せたからね", 128801, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7923, "心技体、すべてを\n鍛えるには座学も\n必要だからね", 128801, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7925, "瞑想するのに\nいいところはあるかな\n集中できて…お昼寝は\n我慢できるところ", 128901, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7926, "ここにはあったかい\n空気が流れてるなぁ…\nみんなが受け入れて\nくれるおかげだね", 128901, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7927, "まるで心の中に\n光が満ちてるみたい…\nこの気持ち、みんなに\nおすそ分けしたいな♪", 128901, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7928, "この衣装は属性の力を\n反映しやすいんだよね\nキラキラしてるのも\n納得だよ", 128901, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7929, "エルフの里のみなさん\n元気かなぁ…\nまた会いに行きたいな", 128901, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7930, "まだ変身できてる…\nわたしと騎士クンの\n修行の成果\n出てるなぁ…ふふっ♪", 128901, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7931, "この服を脱いだら\nどうなるのかな…\nた、試す勇気は\nないけど…", 128901, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7932, "ふぅ…あ、騎士クン\nちょうど瞑想してたの\n一緒に…する？", 128901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7933, "不思議だけど\nいつの間にか\n出たり消えたりするの\n…背中の輪っか！", 128901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7934, "この服って肌触りが\nとってもいいんだよ\nさ、触ってみる？\nスベスベだよ？", 128901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7935, "アストラルの力…\nすごく持続できてるね\n修行の成果が出てるよ", 128901, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7936, "この力があれば\n暗いところで\n落とし物をしても\n見つけやすいかも…？", 128901, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7937, "冒険の準備なら\nできてるよ\nいつでも声をかけてね\n…楽しみにしてる！", 128901, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 7938, "おはよう騎士クン\n一緒にお散歩とか…\nどうかな？", 128901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 7939, "お昼ご飯のあとの\n瞑想って難しいんだよ\n眠くなっちゃって…", 128901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 7940, "夜ふかしさんは\nほどほどにね？\n一日頑張った自分を\n休ませてあげないと", 128901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7942, "のんびりしてたら\nケーキが食べたく\nなっちゃうなぁ…\nでも太る…よね…？", 128901, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7943, "わあ、かわいい…！\n見てるだけで\nにやけちゃう…♪", 128901, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7944, "これは…花の香りかな\n知ってる花かも…？\n当ててみようっ", 128901, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7945, "あったかいと\nどうしてこんなに\n落ち着くんだろう…", 128901, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7947, "食べ過ぎたらお腹が\n目立っちゃうかもって\n分かってるのに…！\n止められないよ～っ！", 128901, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7948, "こ、恋の精霊とか\nいたりするのかな…？\nはっ！　ダメダメっ！\n実力で頑張らないと", 128901, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7950, "お部屋の中でも\n修行、修行っ♪\nでも燃やさないように\n注意しなきゃだね", 128701, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7951, "うーん…この衣装\n厚着か薄着か\nどっちなんだろう…？", 128701, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7952, "いつもの肉球がない分\nお片付けしやすいな♪\n騎士くんのお部屋\nピカピカにしとこー！", 128701, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7953, "お休みは騎士くんと\nＢＢＱに行きたいなー\nあたしと一緒なら\n火の心配はいらないよ", 128701, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7954, "この姿ならもっと\n人を助けられる…！\nえへへっ！\n燃えてきたーーっ！", 128701, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7955, "騎士くんが帰ったら\nアイラみたいな笑顔で\nお疲れさまさまって\nお出迎えしよっと…♪", 128701, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7956, "この姿の時、いつも\n胸が熱く燃えてるのは\nずっと騎士くんのこと\n考えてるからかな…？", 128701, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7957, "騎士くん騎士くん！\nあたしに触れると\nやけどしちゃうよ…？\nなんちゃってー♪", 128701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7958, "おっ、騎士くん\n修行に行きたいの？\nえへへっ、いいねー♪\n二人で燃えちゃおー！", 128701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7959, "そうだ騎士くん\nじゃんけんしよっか？\nグローブ外してるから\nチョキも出せるよっ♪", 128701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7960, "この姿でいられる時間\nすごく増えてきた…！\nえへへ！　あたしたち\n修行の成果出てるね！", 128701, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7961, "じっと見つめないでよ\n顔が真っ赤に…やば！\nほんとに顔から\n火噴いちゃいそう…！", 128701, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7962, "火には強くなれても\n騎士くんに触られた\nほっぺはずーっと\n熱くてたまんないや…", 128701, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 7963, "おはよー騎士くん！\n今日も一日\n燃え滾っていくよー！\nファイヤーッ！！", 128701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 7964, "騎士くんお昼だよ！\nちゃんとご飯食べて\n午後からの修行も\n乗り切ろうねー！", 128701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 7965, "今日もお疲れさま！\nそうだ、寝る前に\nキャンプファイヤー\n囲んじゃおっか♪", 128701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7967, "ぼふーんっ！\nえへへ、気持ちいい…\n修行の前に\nちょっと休憩ー♪", 128701, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7968, "かわいいな～♪\nなでなでなでなでなで\nなでなでなでなでっ！\nあははっ、摩擦熱！", 128701, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7969, "ん～いい香り♪\n少し熱したほうが\n香りが広がるね…って\n蒸発しちゃった！！！", 128701, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7970, "外でも自分の火で\nあったまれるけど\nお部屋でぬくぬく\nするのが一番だよね♪", 128701, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7972, "もぐもぐっ！\nお腹が減っては\n人助けはできずっ！\nもぐもぐもぐっ！", 128701, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7973, "ふむふむ…\nなるほどなるほど…\n…これが上手な\nＢＢＱのやり方か…", 128701, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7975, "…壁も扉も柱も\n全て記録で見たまま\n本当に…来ているのね", 129001, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7976, "…騒がしい場所\n騎士クンが受け入れて\nくれるのをいいことに", 129001, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7977, "ここが…騎士クンの\n守りたかった場所\n騎士クンの思い出が\n詰まった場所…ね", 129001, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7978, "視線を感じる…\nこちらの様子を\n探るような…心の動き\n取るに足らないけれど", 129001, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7979, "…そこかしこで…\n言葉にもしたくない\n相手が過ごしていた\n記録を思い出す…", 129001, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7980, "人は多いけれど…\n雪深い山中よりは\n過ごしやすい…か", 129001, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7981, "自覚したことは\nあまりなかったけれど\nひどく…諦めの悪い女\nなのかもしれない", 129001, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7982, "お招きありがとう\nそれで…何をして\nいればいい…かな？", 129001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7983, "ここは…明るいね\nわたしの城と比べると\nなんだか眩しいくらい", 129001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7984, "仮面はもう必要ないよ\nだって…わたしの顔を\n騎士クンに見て\nもらえなくなるから", 129001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7985, "騎士クンさえよければ\nわたしの城に専用の\n部屋を用意するよ\n…いつでも", 129001, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 7986, "…騎士クンのマント\nまた貸してほしいな\nきれいに使うから\n…だめ？", 129001, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 7987, "いつか二人の願いが\n重なり合えるって…\n信じているね\n…いつまでだって…", 129001, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 7988, "おはよう、騎士クン\n…昨日は夢の中で\nお話ししすぎちゃって\nごめんね。ふふっ", 129001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 7989, "お腹は…空いてない？\n食べ物はないけれど\n空腹を忘れる魔法なら\nかけてあげられるよ", 129001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 7990, "騎士クン…\nまだ、眠らないの？\n…そう。それなら…\nわたしも起きているね", 129001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7992, "肩の力を抜くには…\nまだ時間がかかりそう\nきっと…騎士クンと\n二人きりなら別なのに", 129001, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7993, "ぺんぽこりん…\nそう、確かあれも\nこんな感じだった…", 129001, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7994, "花の香りは…好き\nつかの間\n心を穏やかに\nしてくれるから", 129001, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7995, "あたたかい…けれど\n騎士クンの優しさに\n比べれば、まだまだ", 129001, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7997, "おい、しい…？\nこの感覚が、それ？\nなぜか…納得できる", 129001, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 7998, "情報を入手するには\nわざわざ本を開く\n必要があるだなんて…\nプレイヤーは面倒ね", 129001, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8000, "メルクリゾートか…\n良い記憶はないな\nよし忘れよう", 129301, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8001, "知性の戦士ユニ！\nその恐るべき筋力！\nふおお！　うにゃー！", 129301, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8002, "ぽーん！\n石用の水着ください！\nセクシーロゼッタが、\n渚の視線を独占です！", 129301, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8003, "学院から奨学金を\n勝ち取るより某財団を\nだまくらかした方が\n早い気がしてきたな…", 129301, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8004, "わんつぅ、わんつぅ…\nんぎゃあ足攣ったー！\nおのれカルミナとやら\n絶対に許さん！", 129301, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8005, "ところで…なぜぼくは\nプールでもないのに\n水着のままなんだ…\nおのれ世界め！", 129301, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8006, "彼と二人で撮った動画\nうう、凝視に耐えん…\nなんだこのムズ痒さは", 129301, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8007, "なに？　水着姿で\n寒くないのかだと？\n普段からもっと薄着の\n娘が沢山いるだろう", 129301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8008, "シノブ君にメリゾの\n招待状を送ったのだが\n御尊父の暴走を\n心配していたな…", 129301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8009, "ミミ君らにメリゾの\n招待状を送ったのだよ\n迎える準備をせねば…\nおしゃぶり持ってる？", 129301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8010, "本質の賢者…？\nいや元気にしているが\nなぜ君がぼくの身内を\n知っているんだ…？", 129301, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8011, "水着が似合ってる？\nな、なんだね突然…\nやめたまえよふひひ…", 129301, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8012, "今度は夏にメリゾへ\n行きたいものだ\nむろん君も一緒だぞ", 129301, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 8013, "ぐああっ、朝陽が…！\n朝陽が昇っている…！\n夜行性ユニ死んじゃう", 129301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 8014, "はぁ…もう昼か…\nそろそろ寝ないとな\n規則正しい生活を\nしないと不健康だ…", 129301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 8015, "ひゃっはー、夜だー！\nオレたちの時間だぜ！\nふおおブチ上がり～！", 129301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8017, "これは…ぼくにここで\n寝ろということか？\n一向に構わんが…", 129301, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8018, "きゃっきゃっ！\nこれかわい～！\n…よし、今日も\n子供の擬態完璧だな", 129301, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8019, "ふむ、良い香りだ\nだがそれだけか…\nとくに精神高揚効果は\nないのか…ちぇー", 129301, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8020, "火を見ていると…\n魔導罐を思いだすな…\nかに座、元気かな…", 129301, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8022, "ええい、\n固形食物はいらん！　\nポーションを出せ！", 129301, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8023, "やはり書は文系に限る\nなんと心地よい筆致…\nぐああ突然理系文が！\nひぃ目が理系になる！", 129301, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8025, "ほああ～！\n知性の海上騎士ユニ！\nこれは新たな可能性！", 129301, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8026, "これは…なんだ？\n何が面白いんだ？\n難しいな…", 129301, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 8027, "大切な人と過ごす\n正月の思い出、か…\n昔の私だったら\n考えられなかったな", 108901, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 8028, "叶えたい願いなら\nたくさんあるよ\nそれでも一番を選ぶ\nなら、やはりキミと…", 108901, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 8029, "神殿にあった\n恋愛おみくじ…\n引いてみようかな…", 108801, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 8030, "騎士クン。来年も\nまた一緒におみくじ\n結ぼうね。約束だよ", 108801, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 8031, "今度は騎士くんと\n一緒にうつった写真、\nおばあちゃんに\n送ろうっと♪", 108701, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 8032, "あ、騎士くん！\n今年も…ううん\nこれからも、よろしく\nおねがいします♪", 108701, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8033, "旧校舎の地下牢…\n真冬のサマー計画…\n等身大のＰＲ…\nうッ、頭が…！", 129401, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8034, "暇だし鏡でも見るか。\nお、いい腹筋育って…\nあ？　何見てんだコラ", 129401, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8035, "オレは金色の疾風！\nんん…なんか違うな。\n我が名は金色の…\nあ？　何見てんだコラ", 129401, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8036, "モニカとユニ先輩って\n口調と身長似てるけど\n子供扱いに対する\n反応は真逆だよね…", 129401, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8037, "シオリン元気かな…\n昔くちゃくちゃに\n撫でくり回して\n嫌がられたっけなァ…", 129401, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8038, "べつにいんだけどさ…\nあんたの知り合いって\n普段から水着みたいな\nカッコの女多くない？", 129401, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8039, "そだ、弟らがあんたに\n会いたがってたンだ。\n暇だったらいつでも\nウチ来てやってよ", 129401, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8040, "特別講座は色々あった\nけど…メリゾ自体は\nまあ悪くなかったよね", 129401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8041, "水着と合わせる小物に\nセンス出るよね、\nって話をミィコ先輩が\nしててマジわかりみ", 129401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8042, "水着のミフユねえさん\nやっぱ仕上がってたね\nあれが竜殺しの体か…\nバリかっけーな…", 129401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8043, "はにょんっ！？\nや、だからさ…\n後ろから声かけんな、\nつってンじゃん…", 129401, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8044, "メリゾの特別講座が\n上手くいったのも\n敏腕カメラマンがいた\nお陰だよ。ありがとね", 129401, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8045, "あ？　この水着が？\n似合っ…え、なんて？\nごめ、聞こえんくて。\nもっかい言ってみて", 129401, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 8046, "もう朝とかマジないわ\n昨日二人に付き合って\nオールでカラオケだよ", 129401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 8047, "はぁ授業行かなきゃ…\n真っ昼間から勉強とか\nマジつらたん過ぎ…", 129401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 8048, "今日これから夜勤で…\nいやバイトちゃーくて\nボランティアの話よ？\n校則違反よくない", 129401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8050, "うわ、ふっかふか…\nオニほど安らぐわ…\nやば、ユニってくる…", 129401, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8051, "うちも妹いりゃ\nこーゆーのもなァ…\nま自分用でもいいか", 129401, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8052, "ふーん…\nいいじゃんこの香り\n抜群に嗅げるわ", 129401, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8053, "うちン家のルーツの森\n大火災でなくなった\nらしンだよね…\n生まれる前の話だけど", 129401, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8055, "何コレありがてぇー\n食料ほくほく過ぎて草\n米ガチで尊いわァ…", 129401, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8056, "いいけど、なんでうち\n水着で本読んでンだよ\nどーゆープレイだよ", 129401, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8058, "金色の海上騎士クロエ\n推して参る！　覇ァ！\nあ？　何見てんだコラ", 129401, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8059, "何コレ、貝の椅子？\nどーやって遊ぶんだ…\nいや座って終わりか…\nまァそりゃそーか…", 129401, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8060, "さてと…\n明日の訓練メニュー\n考えねえとな", 129801, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8061, "また新入りの女子に\n弁当もらっちまった…\nありがたく食うけどよ", 129801, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8062, "種族なんて関係なく\nみんなが力を合わせて\nランドソルを守る…\nいい時代になったよな", 129801, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8063, "誰にだって長所はある\n新入りのいいところを\n見つけてやるのも\n教官の仕事だ", 129801, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8064, "厳し過ぎても\n甘過ぎてもダメ…\n教官ってのは難しいぜ", 129801, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8065, "戦いが苦手な新入りを\nどうフォローするか…\n後であいつと\n相談してみるか", 129801, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8066, "あいつは優し過ぎて\n戦いに向いてねえ\n…そういう甘さ\n嫌いじゃねえけどな", 129801, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8067, "強くなる近道はねえ\n地道な鍛錬あるのみだ\nさ、訓練始めるぞ！", 129801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8068, "新入りにも\nいつも言ってるが…\n装備の手入れは\n念入りにしろよな？", 129801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8069, "座学も受けてえのか？\nいい心がけだな！\nなんでも教えてやるよ\n…魔法以外ならな", 129801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8070, "新入りたちがよく\n恋バナしててな…\nお、おまえは…誰か\n気になるやついるか？", 129801, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8071, "なあ、ストレッチ\n手伝ってくれねえか？\n訓練前にしっかり\n筋肉ほぐさねえと…", 129801, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8072, "ずいぶん頑張って\n鍛えたよな、おまえ\nよし！　ご褒美に\nお菓子作ってやるよ♪", 129801, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 8073, "訓練日和だな！\nさぁて…まずは\n走り込みといくか！", 129801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 8074, "昼飯はしっかり食えよ\nでなきゃ、\nこの後もたねえぜ？", 129801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 8075, "とっくに消灯時間だ\nさっさと寝な？\n明日も早いんだからよ", 129801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8077, "休むのも訓練のうちだ\n今はのんびり\nさせてもらうぜ", 129801, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8078, "いい面構えだ\n気に入ったぜ！\nあたしの班に入るか？\n…なんてな♪", 129801, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8079, "…いやされる匂いだな\n新入りたちにも\nすすめてみるか…", 129801, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8080, "あったけえからって\n寝ないようにしねえと\n教官としての\n威厳に関わるからな…", 129801, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8082, "食事も訓練の一環だ\nとはいえ食い過ぎには\n気をつけねえと…", 129801, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8083, "トーゴクの剣術…か\nなにか訓練に\n活かせるかもしれねえ", 129801, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8085, "あのおかたに近付く\n不埒な輩には、特別な\n訓練で地獄を見て\nいただきましょう", 129901, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8086, "これだけ広ければ\n多少派手に\n動き回っても\n問題なさそうですね", 129901, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8087, "…今誰か私を\n「鬼」教官と\n呼びませんでしたか？", 129901, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8088, "新しく開発したこの薬\n誰か試してくれる人は\nいませんか？", 129901, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8089, "厳しくすれば投げ出し\n優しくすればサボる\n人というのはどこまで\n度し難いのでしょう", 129901, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8090, "はじめましょう\nあのおかたの心を\n射貫くための\nシミュレーションを", 129901, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8091, "あらゆる苦難と\n忌々しい女から守る\nためにも、私自身も\n鍛えておかねば", 129901, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8092, "あなた様、そんなに\n見つめられると…え？\nエリコはカッコいい？\nええ、当然ですとも", 129901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8093, "不調があればすぐ\n報告してください\n体のケアも教官の\n大事な役目ですので", 129901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8094, "新しい訓練器具の\n使い心地はどう…え？\n重すぎる？　これでも\n軽くしたのですが…", 129901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8095, "運動後には特製の\n栄養ドリンクをどうぞ\nクスクス、何も\n入れたりしてませんわ", 129901, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8096, "よくがんばりました\nあなた様。あぁ…\nお疲れになったお顔も\n素敵ですわ", 129901, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8097, "あなた様、二人だけの\n遠征訓練へ\nまいりましょう。\n既に準備万端ですわ", 129901, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 8098, "まだ眠いですか？\nならばストレッチで\n体を痛めつ…ではなく\nほぐしていきましょう", 129901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 8099, "お昼はお弁当をご用意\nいたしました。量も\nスタミナも増し増し\n残さず食べてください", 129901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 8100, "よい睡眠こそ健やかな\n体に不可欠。ですので\n私が寝ずの番をさせて\nいただきますわ", 129901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8102, "休憩用に使おうと思い\nましたが、心地よくて\n立てなくなって\nしまいますわ…", 129901, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8103, "中に重しを詰めれば\n愛でながら運動が\n可能ですわ", 129901, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8104, "香りでリラックスさせ\n同時に暗示を\nかけてみましょうか", 129901, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8105, "…くっ、眠気を誘う\nこの暖かさ…\n油断も隙もありません", 129901, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8107, "主食はこちらで十分\nまかなえますわね\nそれでは私はおかずを\n作りましょう", 129901, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8108, "まあ、実用書の類も\n揃っているのですね\nとても助かりますわ", 129901, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8110, "……（そわそわ）", 129601, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8111, "ここに寄らずの花陣…\n作ったらダメですよね", 129601, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8112, "部屋の隅が\n落ち着きます\nそれか、陽の当たる所", 129601, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8113, "身を隠せる場所が\nあると…\n助かるんですけど", 129601, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8114, "複雑な造りの家ですね\n触って壊れたり─\nしませんか？", 129601, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8115, "出入りするひと\nみんな、笑顔…\n…変わった家ですね", 129601, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8116, "この家なら安全─\nかもしれません\n魔物が出ないので", 129601, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8117, "これも、視察の一環\n遊びに来たわけじゃ\nないです", 129601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8118, "長話─苦手です\n疲れるので…\nふぅ…", 129601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8119, "ここで友達を作れと\nネアさんが…\nそう、言われても…", 129601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8120, "世話をするのは\n得意です\n…花の、ですよ？", 129601, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8121, "媛へのお土産を\n探してるんですけど…\n…貴方のおすすめ、\n聞いておきます", 129601, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8122, "平和でのんきで…\n貴方らしい家です\n一応、褒めてます", 129601, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 8123, "花に、お水─？\n…いりません", 129601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 8124, "日が高いうちは\nむやみに外へ出ません\n面倒事、多いので…", 129601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 8125, "夜番。私がします\nあまり眠らない\n性質なので…", 129601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8127, "ここなら根を張れる\nかもしれません\n…張りませんけど", 129601, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8128, "こんなにやわな体じゃ\n身を守れませんよ─\n…あ。生き物じゃ\nないんですね", 129601, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8129, "花みたいに─\nいい匂い、です", 129601, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8130, "火を見てると、\nジオ・ゲヘナを\n思い出します…ふぅ", 129601, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8132, "食事も立派な戦支度\n媛が言ってました", 129601, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8133, "知識も武器の一つだと\n媛が…でも、\nこれでどうやって…？", 129601, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8135, "天気もいいしお花畑に\nピクニックでも\n行こうかな♪", 130701, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8136, "あ、どこからか\nお花の匂いが…\nいい香りですね", 130701, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8137, "新しいシャベルとか\nじょうろもほしいなぁ\nお小遣いしっかり\nためないとね", 130701, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8138, "お花飾りたいけど\n花瓶はどこかな…\nあとであの人に\n聞いてみよっと", 130701, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8139, "お花の観察日記\n書かなきゃ\n日々勉強は\n欠かせませんよ", 130701, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8140, "衣装にお花を足して\nみたけど、あの人\n気付いてくれるかな？", 130701, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8141, "この花冠、上手に\n作れたかも…\n日頃のお礼にあの人に\nあげようかな…", 130701, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8142, "あなたは何のお花が\n好きですか？　ほら、\n一応知っておいたら\n役に立つかもですし", 130701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8143, "そのお花、乱暴に\n扱っちゃダメですよ\n花粉が服について\nとれなくなりますから", 130701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8144, "土いじりしたから\n服が汚れてますよ\nはたいてあげますから\nじっとしててください", 130701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8145, "そんなにすぐに芽は\n出ませんよ\nまあ、楽しみなのは\n私も同じですけど…", 130701, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8146, "この衣装、そんなに\n気になります？　なら\nこうしてお花を\n着ければおそろいです", 130701, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8147, "お花の種を私に？\nありがとうございます\nせっかくなので一緒に\nお世話しませんか？", 130701, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 8148, "お花にお水をあげるの\n手伝ってもらえます？\nお水は朝にあげるのが\nいいんですよ", 130701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 8149, "とっておきのお花見\nスポットに\nご案内しますよ\nお弁当も忘れずに！", 130701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 8150, "もうこんな時間\n早く寝ましょう\n草木も眠るなんとか\nって言いますからね", 130701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8152, "お花のお手入れって\nけっこう疲れますね…\nここでちょっと一息\nしちゃおっと", 130701, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8153, "耳にお花を着ければ…\nえへへ、すっごく\nかわいくなったね！", 130701, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8154, "これはラベンダー\nこっちはミモザ\nフローラル系の香り\nっていいですねぇ", 130701, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8155, "ポカポカしますね～\nまるで春の陽気\nみたいです～", 130701, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8157, "お花を一輪さすだけで\nご飯の時間がとても\n華やかになりますね", 130701, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8158, "お花の本が\nいっぱいある！\n今日はこれで\nお勉強です！", 130701, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8160, "ふんふ～ん♪\n華やかな服を着ると\n心がウキウキします♪", 130801, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8161, "花祭りで出会った\nみなさん…\nお元気でしょうか？\nまたお会いしたいです", 130801, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8162, "はっ！　そうでした\nこの服の時は\nメイドのお仕事は\nお休みでした…", 130801, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8163, "この服は替えが\nありませんから\n大きなドジには\n気を付けないと…", 130801, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8164, "目立ったほつれもなし\n…うん、いい感じに\n補修できました♪", 130801, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8165, "メイド姿でなくても\n心はいつでも\nお嬢さまの\nメイドです♪", 130801, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8166, "この服を着ると心が\n浮き立つのはあのかた\nと一緒にいる気持ちに\nなるからでしょうか？", 130801, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8167, "はい、今日は一日\nこの服で過ごそうと\n思います\n変じゃないですよね？", 130801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8168, "花祭りのおかげで\n少しだけお花に\n詳しくなった…\nような気がします！", 130801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8169, "花祭り、今度は\nお嬢さまと救護院の\nみんなと一緒に\n行ってみたいですね", 130801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8170, "今度キョウカちゃんと\n花祭りの服でお花を\n見にお出かけ\nするんです", 130801, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8171, "メイド服を全部洗濯\nしちゃうなんて…\nドジっちゃいましたぁ", 130801, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8172, "花祭りの服は私たちの\n思い出の服ですから…\nこれからも\n大切にします♪", 130801, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 8173, "はう、そうでした\nお花にお水を\nあげる時間です！", 130801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 8174, "お昼ご飯は\nバスケットを持って\nお花畑に食べ\nに行きませんか？", 130801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 8175, "ふぁあ…繕い物を\nしていたらこんな時間\nになってしまいました\n早く寝ないと…", 130801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8177, "ふわふわしてて\nきもちいいです～\nどんな生地を\nつかってるんだろ～？", 130801, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8178, "かわいいですね～\nぬいぐるみさんにも\n服をつくって\nあげたいです♪", 130801, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8179, "いい香りです～\nあれ？　もしかして\n私の服についてる花と\n同じ香りが…？", 130801, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8180, "暖かいですけど\nあまり近づいちゃうと\n頭の花飾りが乾燥\nしちゃいそうですね…", 130801, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8182, "もぐもぐ…\nいい花を咲かせるには\nしっかりとした栄養が\n必要だそうです！", 130801, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8183, "なるほどなるほど…\nお花のお手入れは\n奥が深いですね…", 130801, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8185, "マホマホ王国にも\nサーカス団設立や\n夢と希望を届けますえ", 131001, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8186, "せっかくの晴れ舞台や\nかっこいいポーズも\nできるようにしたいわ", 131001, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8187, "むむむむ～っ。うーん\nクルルはんみたいに\n胸から宝石を出すんは\n難しいみたいやわ", 131001, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8188, "あむっ…んん～♪\nやっぱり練習のあとは\n甘いものに限るわぁ", 131001, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8189, "アーサーたちの衣装も\n今のうちに考えんと…\n生地、足りるやろか", 131001, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8190, "王子はんとなら\nどんなアイデアも\n見つけられます♪", 131001, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8191, "うちの晴れ姿が\n王子はんの目に一等\n輝いて見えますように", 131001, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8192, "キリっとしたうちも\nかっこいい？\nもう、上手やなぁ", 131001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8193, "新しい演目考えたんよ\nなぁ王子はん、意見\n聞かせてもらえます？", 131001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8194, "聞いてや王子はん\n動物さんたちとさらに\n息が合うてきたんよ\nみんな頑張り屋さんや", 131001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8195, "演技の感想なら\n大歓迎どすえ♪\nたんと聞かせてや～", 131001, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8196, "王子はん、本番当日は\n最前席で見たってな\nそしたらうち、もっと\n頑張れますさかい♪", 131001, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8197, "この技をあなたに\n捧げます…なんて♪\n驚いたやろ？\nどっきり作戦成功や♪", 131001, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 8198, "素敵なショーのためや\n今日も焦らず一歩ずつ\n成長していきまひょ\nな、王子はん♪", 131001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 8199, "お弁当、王子はんも\n一緒にいかがどす？\nみんなで食べて団結や", 131001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 8200, "夜のサーカスも\n幻想的でとっても\n魅力的なんよ\n一緒にいきまひょか♪", 131001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8202, "練習が終わったあとは\nの～んびりして\n体力を回復せな…", 131001, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8203, "この子たちとなら\n空中ブランコや\n玉乗りもええなぁ", 131001, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8204, "いい香りやなぁ\n本番前の緊張も\n和らげてくれそうやわ", 131001, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8205, "このあったかい感じ…\nスポットライトの熱を\n思い出すわぁ", 131001, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8207, "ちゃんと食べへんと\nいい演技はできまへん\n団長はんも言うてたわ", 131001, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8208, "動物さんたちのことも\nもっと勉強せな\n大事な仲間やさかい", 131001, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 8210, "器用じゃないが、\n情には厚いよ\nこういう女を「一途」\nってそう言うんだろ？", 105601, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 8211, "斬れないものはないと\n思ってきたさ。だけど\n…この気持ちだけは\n斬れないね、絶対に", 105601, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8212, "んしょ、んしょっ…！\n日頃の感謝を込めて\nヒーローさんのお家を\nきれいにしますです♪", 130901, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8213, "ふむふむ…\n新アトラクションは\nこのギルドハウスを\nモチーフに…", 130901, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8214, "ごそごそ、こそこそ…\nヒーローさんに内緒で\nどこかにお礼の宝石を\n隠しておきましょう…", 130901, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8215, "パークの運営が\n落ち着いたら\nわたしもギルド活動？\nしてみたいですます♪", 130901, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8216, "こんなすてきな\nおうちに住めたら…\nいえ！　うさぎ小屋で\n十分です～っ", 130901, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8217, "ヒーローさんのおうち\nだって意識したら\nアトラクションよりも\nドキドキしてきます…", 130901, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8218, "ヒーローさんのことを\n考えると顔が赤く…\nこれってもしかして\nひょっとして…？", 130901, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8219, "あっ、ヒーローさん！\n話しかけてくれた記念\nということで、宝石\n受け取ってください♪", 130901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8220, "元気がない時は\n語尾にぴょんですっ！\nさあヒーローさんも\nやってみるぴょん♪", 130901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8221, "ヒーローさんへのご恩\nおばあちゃんうさぎに\nなるまでに、お返し\nできるでしょうか…？", 130901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8222, "ヒーローさん！\n二人で一緒に\nパレードの練習\nしませんか～♪", 130901, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8223, "あはは♪\nヒーローさんは\n人を笑顔にする力が\nありますです♪", 130901, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8224, "ヒーローさんに\n一日一恩返しですっ！\n返し終わるまで\nやめませんですます♪", 130901, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 8225, "おはようございます♪\nんしょ、んしょっ…！\n気持ちよく起きられた\n記念に宝石どうぞ♪", 130901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 8226, "お昼にフードコートで\nかばばん君バーガー\n買ってきましたよ！\n一緒に食べますです♪", 130901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 8227, "今日も一日\nお疲れさまですっ♪\nマッサージしますから\n寝そべってください♪", 130901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8229, "はふぅ～…♪\nお仕事の疲れも\nぴょんぴょ～んって\n飛んでっちゃいます♪", 130901, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8230, "わぁっ、とっても\nかわいいですますっ！\nギルドハウスの\nマスコットですね♪", 130901, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8231, "ヒーローさんの好きな\n香り…これを宝石に\nつければ、受け取って\nもらえるでしょうか？", 130901, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8232, "はあ、気持ちいい♪\nヒーローさんの\n好きな温度に\n調整しときますです♪", 130901, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8234, "支配人は体が資本っ！\nたくさん食べ…すぎて\n動けなくなっちゃい\nましたぁ～…！", 130901, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8235, "パークの\nガイドブックとか\nあったらいいですね♪", 130901, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8237, "外あつすぎ溶けるわ～\nやっぱ部屋ん中で\n水着がラクだねー", 131101, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8238, "ン～\nカリザきゅんどこー？\nもしかして逃げた？", 131101, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8239, "ミソラちゃんと\nリリちゃん誘って\nそろそろイイコト\nしよっかなー？", 131101, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8240, "おねえちゃんに\n頼まれたシゴトは\nまた今度にすっかー\n夏休みだしー♪", 131101, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8241, "騎士きゅんの\nプリンセスフォームも\n見てみたいんだよなー\nンフ♡", 131101, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8242, "スライムを\nこーしてあーしたら\n騎士きゅんの泳ぐ\n練習に使えるっぽ？", 131101, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8243, "おかえり～♡\nって水着で迎えたら\n騎士きゅん喜んで\nくれるかなー？", 131101, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8244, "騎士きゅん\nこのサングラス\nかけてみる？\nぜってー似合うよー", 131101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8245, "日焼け痛かったら\nいつでも言ってねー\nスライムでひんやり\nしてあげるよ～♪", 131101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8246, "今から外で\n焼いてこっかなー\n騎士きゅんもどー？", 131101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8247, "スライムマッサージ\nしてあげよっかー\nほーれ、もみもみ～♡", 131101, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8248, "騎士きゅんが選んだ\n水着も着てみたーい\nネアおねえちゃんに\nどんなの着てほしい？", 131101, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8249, "騎士きゅ～ん\n尻相撲しよっかー♪\nおねえちゃんに\n勝てたらご褒美だぞ～", 131101, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 8250, "暑すぎてムダに\n早起きしちった～\n二度寝すっか～\n…ふぁあ…", 131101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 8251, "夏バテしても\nスイカとかかき氷なら\n全然ウェルカム\nなんだよなー", 131101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 8252, "今日はこのまま\n水着で寝るかー\n夢ん中で泳ごー", 131101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8254, "いったんキューケイ…\nンー…よし\n充電カンリョ～♪", 131101, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8255, "仲間になりたそうに\nこっち見てんねー\nきゃわいいしおっけ～", 131101, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8256, "ン～いいニオーイ\nお日様あたった\n騎士きゅんの\n次くらいかな～", 131101, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8257, "カラダ冷えてきたから\nたすかるーおかげで\nまだまだ水着イケるわ", 131101, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8259, "うめ～\nとまんねぇ～\n夏バテどこいった～？", 131101, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8260, "ふむふむ…\nこれは意外と\nぬるっと読めるっぽー", 131101, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8262, "ンフフ～\n海の戦士ってカンジ？\nアリよりのアリだね～", 131101, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8263, "あーやべ\n一瞬で寝そー\nヤバだわ～", 131101, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8264, "服や紙が易々と\n燃えぬとは\nランドソルとはまこと\n住み易き国で候", 129701, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8265, "どの世界にあっても\n民は活力に溢れ\n街は賑わうもの…\n善哉、善哉", 129701, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8266, "食べ歩きに世界征服…\nギルドとはかくも\n自由気儘な徒党に\nありんすなぁ", 129701, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8267, "連絡は…今日もなし\n我が妹子は相変わらず\n放埓なことよ喃", 129701, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8268, "モネは相変わらず\n警戒しきりなれど…\nそのようなところも\n愛らしくあり申す", 129701, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8269, "なんと長閑な世にや\nありけむ…この平和は\n騎士殿たちの努力の\n賜物にありんす喃", 129701, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8270, "『王家の装備』は\n使わぬのかと？\n…フフ。すでに\n用いており申す", 129701, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8271, "今ばかりは媛という\n立場は置いて\nこの地の者らと\n歓談したく候", 129701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8272, "強さの秘密？　…フフ\n女子の秘密を暴くのは\n野暮にござんす\nご寛恕いただきたく候", 129701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8273, "此方の家屋、\n見目以上に\n堅牢であり申す\n何ぞ仕掛けがありや？", 129701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8274, "心地よい時間とは\n疾く過ぎ去りゆくもの\n此処で過ごすと改めて\n実感できる喃", 129701, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8275, "視線が気になる？\nこれはすみませぬ\nなにぶん、お前さまは\n見ていて飽きませぬ故", 129701, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8276, "お前さまの前では\n誰もが心を開く…\nなんとまぁ罪作りな\n色男にありんすな？", 129701, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 8277, "よく眠れましたかの？\n寝る子は良く育つ、と\n古くより申します由", 129701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 8278, "美食殿の作法に倣い\n皆で共に昼餉を頂くと\n致しんしょう", 129701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 8279, "逢魔ヶ時は望まぬ客も\n参るもの…お前さまも\n気をつけなんし", 129701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8281, "わっちの蛇に\n勝るとも劣らぬ\n座り心地で候", 129701, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8282, "魂、万物に宿る\nとの云いあらば\n情愛を注いでやるのが\nよかろうの", 129701, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8283, "いと香しき哉…\n騎士殿は香の道にも\n詳しくあられるか", 129701, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8284, "ジオ・ゲヘナの\n灼熱と比ぶれば\nそよ風と変わり\nござんせん", 129701, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8286, "美味なる哉…この味\nぺコリーヌ姫が好むも\n道理にありんす", 129701, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8287, "トーゴクに、オーマ…\n書で見るだけでも\n心惹かれる土地よの", 129701, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8289, "暑い日には\n冷たいものですよねー\nかき氷とか食べて\nすっきりしたいです★", 131201, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8290, "海への行き来は\nテレポーテーションに\nお任せです★", 131201, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8291, "トロピカルジュースが\n飲みたいでーす♪\n海の家で見たんですよ\nカップル用ジュース★", 131201, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8292, "ご飯どうしようかなー\nあ、海鮮ラーメンとか\n美味しそうかも★", 131201, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8293, "水鉄砲、普通に撃つと\n少し物足りないかも…\nどるるるる～ん★って\n感じが欲しいですよね", 131201, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8294, "コイビトっぽいこと\nアオハルっぽいこと\nいっぱいしたいです★\n修羅場とかも♪", 131201, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8295, "騎士さんが溺れたら\n絶対助けに行きますね\n人工呼吸でもなんでも\nしてあげますから★", 131201, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8296, "騎士さーん\n日焼け止めの塗り直し\nお願いします★\n優しくしてくださいね", 131201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8297, "海で食べたフルーツ？\nはい、まだありますよ\nこっちでもあーんして\n食べさせましょうか★", 131201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8298, "ぺんぽこりんは\nどうしたのかって？\nどうしたんでしょうね\nどーでもいいです★", 131201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8299, "今日はどんな\nイイコトしましょうか\n思い出作り？\n人助けもいいですね★", 131201, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8300, "ヒモを引っ張ったら…\n「セ・キ・ニ・ン」\n取ってもらいますよ？\nなーんてウソですけど", 131201, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8301, "みんなでイイコトも\n楽しいですけど～\n…二人でイイコト\nしちゃいますか？", 131201, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 8302, "朝ですよー！\n今日も一日、\nイイコト日和ですね♪", 131201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 8303, "日差しにご注意ですね\n日焼け止め…誰かに\n塗ってほしいなー？\n…ちらっ★", 131201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 8304, "夜の海って\n惹かれちゃいますよね\n…ロマンチックです★", 131201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8306, "う～ん、まったり～\nこれぞ新ジャンル\nおうちバカンス★", 131201, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8307, "よーしよーし★\nわたしとイイコト\n一緒にしましょうねー", 131201, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8308, "いい香りですねー\nリラックスタイムに\nちょうどよさそう★", 131201, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8309, "水遊びのあとは\nしっかり体を\n温めないとですよね★", 131201, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8311, "栄養補給もバッチリ★\n遊びもイイコトも\nお腹が減ってたら\nできませんからねー？", 131201, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8312, "今だと海関連の本とか\n興味あるんですよねー\n魚とか海難救助とか★", 131201, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8314, "かっこいー★\n名前はー…\nぷんぷくぽんとか？", 131201, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8315, "映えますねー\n写真撮ったら\n素敵な思い出に\nなりそうです★", 131201, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8316, "海でもないのに\nこの姿でいるのは…\nやはり、場違いでは\nありませんか…？", 131601, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8317, "スイカとは割ることと\n見つけたり！\n…ですが目隠しすれば\n見当たらないもの…", 131601, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8318, "まぁ、膝に砂が…\n気がつきませんでした\nまるでわんぱくな\nお子様のよう…ふふっ", 131601, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8319, "家の中でスイカを割る\nわけにはまいりません\nここは潔く包丁で\n切りましょう", 131601, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8320, "ミソラ様やネア様の\n大胆さを見習って…\nき、騎士きゅーん…★\n難しいですね…", 131601, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8321, "きゅ、急に暗く…！？\n…失礼しました\n鉢巻きが下がってきた\nだけでした…ううぅ", 131601, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8322, "わたくしに剣の才は\nあるのでしょうか…\nいえ、何事もまずは\nやってみなくては", 131601, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8323, "なにか羽織った方が\nよろしいですよね…？\nそのままでいい？\nそれはそれで…その…", 131601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8324, "あの、兄様…？\nスイカの種を飛ばす\n遊びもあるというのは\n…本当なのでしょうか", 131601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8325, "強くなるための修行は\nまだ始まったばかり\nまずは己の目指すべき\n道を見定めなくては", 131601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8326, "同じ薄着であっても\n黒い天使の姿より…\n気が楽です", 131601, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8327, "はい、ただいまー！\n…あっ！\n海の家でのお手伝いの\nくせが、つい…", 131601, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8328, "兄様さえよろしければ\nまた一緒に海へ…\nあ…申し訳ありません\n浮ついてしまいました", 131601, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 8329, "本日もイイコトをして\n清々しい一日を\n過ごしたいものです\nまさに一日一善ですね", 131601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 8330, "お昼時になると\n海の家の料理を\n思い出してしまいます\n焼きそばにかき氷…♪", 131601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 8331, "兄様…よろしければ\n浜辺を歩きませんか？\nきっと、月が\nきれいでしょうから", 131601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8333, "ふわふわ心地よすぎて\n眠くなってしまいます\n水着姿では\nいけません…ふぁ…", 131601, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8334, "なんてよい手触り…\n癖になってしまいます\nふふ、よしよし…♪", 131601, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8335, "爽やかな香り…♪\n日焼け止めクリームを\n思い出してしまいます", 131601, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8336, "冷たい水で冷えた体が\n温まります…♪\nまるで兄様のようです", 131601, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8338, "お腹が空いては\n修行ができません…！\n動きに障りない程度に\n栄養の補給を…！", 131601, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8339, "剣術とスイカ割りが\n上達する指南書…\n見つかりませんね…", 131601, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8341, "さあ、ともに大海原を\n参りましょう！\nなんて…ふふっ♪", 131601, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8342, "まるでおとぎ話の\n登場人物になった\nような心持ちです…♪", 131601, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8343, "あたしはなんで水着で\n室内をうろついてるの\nかしら…不思議だわ", 131701, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8344, "あ、天井が汚れてる\n仕方ないわね\nひとっ飛びしますか", 131701, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8345, "はい、お疲れさま♪\n出迎えの言葉は\nやっぱこれよね♪", 131701, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8346, "そういえばあたしって\n羽があるけど\n泳げるのかしら…", 131701, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8347, "あいつのために\n浮き輪も用意したし\n抜かりはないわよ", 131701, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8348, "海までのガイド計画も\nまとまったわね\nふふっ…楽しみだわ", 131701, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8349, "この水着、あいつが\n選んでくれたのよね\n…大事にしないと", 131701, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8350, "ネビアは最近どう？\n迷惑かけてない？\n…かけてるのね", 131701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8351, "あんた…部屋の片付け\nくらい自分でしなさい\n散らかってたわよ？\n…片付けておいたけど", 131701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8352, "もしかして暇なの？\nだったら記憶でも\n見せましょうか？\n凄く恥ずかしいやつ♪", 131701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8353, "夏ってこんなに\n暑いのね…\n海にばかり行く\n気持ちが分かったわ", 131701, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8354, "あんたの水着って\n新しいの買わないの？\nなんならあたしが\n選んであげるわよ♪", 131701, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8355, "気軽に触れ合える\n距離にいる…\nあたしにとって\nとても大事なことよ", 131701, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 8356, "あらおはよう\n夏の朝は暑いわね\nシャワーでも浴びて\nさっぱりしなさい", 131701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 8357, "お昼は何を食べるの？\nガイド妖精として\n提案しましょうか？", 131701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 8358, "夜更かしは厳禁よ\n無理やり眠らせて\nあげましょうか？", 131701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8360, "アメスさまだって\nゴロゴロするときは\nあるのよ～", 131701, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8361, "…ふふっ♪\n何よ、あたしが\nかわいいものを\n愛でちゃだめなの？", 131701, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8362, "こういういい香り…\n塔の中じゃなかなか\n楽しめないのよね", 131701, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8363, "水着を着てるのに\n温まるのって\n矛盾してない？\n気にしすぎかしら", 131701, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8365, "あんたと行動するなら\n沢山食べられるように\nならなきゃね…！", 131701, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8366, "文字を読むのと\nデータを読むのは\nまったく違うわ", 131701, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8368, "へぇ、面白いじゃない\nこういう経験、昔じゃ\nできなかったわ", 131701, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8369, "面白いクッションね\nところであんた…\nあたしで遊んでない？\n気のせいならいいけど", 131701, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8370, "また新しい悪戯\n思いついちゃった♪\n誰に仕掛けようかしら", 118601, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8371, "あ～あ、退屈…\nフィオでもからかって\nこようかしら♪", 118601, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8372, "ミソギの悪戯って\nレベル高いのよね…\n負けてらんないわ！", 118601, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8373, "家事ニガテなのよね～\nコッコロたちには\n感謝しないとね", 118601, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8374, "私も魔物料理に\n慣れてきちゃった…\n毒されてるわね…", 118601, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8375, "あいつに仕掛ける\n次の悪戯は～…♪\nこれ…ううん…\nやっぱこっちかしら？", 118601, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8376, "結局ギルドハウスが\n一番落ち着くわね\n…私たちの家だもの", 118601, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8377, "悪戯のコツでも\n教えてあげよっか？\n…え～興味ないの？", 118601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8378, "これからもずーっと\n退屈しない毎日に\nしてあげる♪\n私の悪戯でね♪", 118601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8379, "ちょうど退屈してたの\nたーっぷりと\n構ってもらうわよ♪", 118601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8380, "案内が必要なら\n頼ってくれていいわよ\n一応は私だって\nガイド妖精だし！", 118601, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8381, "冒険のガイドなら\n私に任せなさい♪\nある意味忘れられない\n旅にしてあげる♪", 118601, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8382, "妖精がいる家は\n栄えるのよ♪　だから\nずっといてあげる\nあんたのそばにね♪", 118601, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 8383, "きゃははっ！\n寝ぐせついてるわよ？\nこのままにしましょ！\nだって面白いし！", 118601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 8384, "自由に外へ行けるって\nすっごく幸せなことよ\nさ、散歩行きましょ♪", 118601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 8385, "安心していいわ\n寝てるあんたに\n悪戯しないから\n…あんたにはね♪", 118601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8387, "悪くはないけど…\nあいつの頭の方が\n座り心地いいかも", 118601, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8388, "この子を使った\n悪戯は…\n…止めとこっと\nこんなにかわいいし♪", 118601, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8389, "このギルドハウスに\nピッタリな匂いね♪\n一言で言えば…\nカオスって感じ？", 118601, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8390, "なんていうか…\n心があたたかいわ\n…変な感じ！", 118601, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8392, "ガイド妖精にも\n食事の機能があって\nよかったのかも\nしれないわね…もぐ…", 118601, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8393, "じれったい物語ね…\nこんなの霧を使えば\n一瞬で終わるのに！", 118601, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8395, "わる～い子は\nトゥンヌス漁船に\n乗ってもらおうかな～", 131801, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8396, "たまにはファミリーの\nみんなにもお休みを\nあげないとね～☆", 131801, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8397, "海辺に素敵な\nバーがあるんだ～\n彼が大人になったら\n一緒に行きたいな♪", 131801, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8398, "夏の風を肌で\n感じるの好きかも～\nラーゴはどう？", 131801, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8399, "海岸線を\nのんびり散歩もいいね\n砂や貝殻に紛れた\nお宝探しの旅だよ～☆", 131801, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8400, "みんなで集まって\nビーチパーティーでも\n開いちゃおっかな～♪", 131801, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8401, "さて、この水着…\n彼は気に入って\nくれてるかな～？", 131801, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8402, "心ゆくまで夏を\n楽しんじゃお～☆\nほら君も♪", 131801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8403, "水着似合ってるって？\nふふ♪　うれしいこと\n言ってくれるね～☆", 131801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8404, "熱中症には\n気を付けないと\nだめだよ～？", 131801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8405, "海の家ならうちの\nファミリーでやってる\nとこにしよっか☆\n豪遊しちゃお～♪", 131801, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8406, "君と一緒なら\nどんな夏になるのか\n楽しみだね～♪", 131801, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8407, "君といると子供心を\nくすぐられちゃうな～\n大人だって思いっきり\n遊んでいいんだよね♪", 131801, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 8408, "さ、今日は何をして\n過ごそっかな～…\nねえ、君は何したい？", 131801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 8409, "ふふ♪\n波音が心地良い\n時間だね～☆", 131801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 8410, "今夜は星空の下で\n乾杯しよっか～☆\n飲み物取ってくるね♪", 131801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8412, "ここ涼しい～♪\nちょっと一休み\nしていこっか☆", 131801, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8413, "こういうのも良いけど\nやっぱりラーゴの方が\nかわいいかな～♪", 131801, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8414, "爽やかな香りだね～\nこういう感覚も絵で\n表現してみたいな～", 131801, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8415, "続きを描く前に\n原稿を乾かさないとね\nインクがにじむと\n直すの大変なんだよ～", 131801, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8417, "今日は水着だし…\n食べるのは控えめに\nしておこっかな", 131801, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8418, "話題の作品は\nチェックしないとね～\nあっ、この作家さん\n新刊出すんだ～", 131801, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8420, "うちの出店の客引きに\nこういう乗り物系を\n使うのもアリかな～☆", 131801, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8421, "これ、背景用の\n参考資料に\n一つ欲しいかも♪", 131801, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8422, "ここに帰るのも\n久しぶり…あれから\n変わったところは\nあるかしら？", 181201, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8423, "バイトも復帰しなきゃ\nうちのギルドにとって\n食費は重要だもの…！", 181201, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8424, "コッコロマ…っ\n…うぅ…\nこの癖は\n直らないのね…", 181201, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8425, "この世界はまだまだ\n知らないことだらけね\n『裏世界』とか…", 181201, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8426, "必ずみんなを\n助け出してみせるわ\n…もちろん、兄さんも", 181201, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8427, "またみんなと\n暮らせるなんて…\n夢じゃ、ないのよね", 181201, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8428, "たくさんの人が\n私に優しくしてくれる\n…だからこそ、私も\n頼れる人になりたいわ", 181201, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8429, "次はランドソルの\n街の外にも行きたいわ\nあの…よかったらまた\n一緒に回ってくれる？", 181201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8430, "独りぼっちで眠るのは\nやっぱり寂しかったわ\n…みんなで雑魚寝？\nそんなの…絶対やるわ", 181201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8431, "ネビアってば大泣きで\n私を出迎えてくれたの\n…すごく嬉しかったわ", 181201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8432, "この翼、陽に当たると\n本当に綺麗よね…\n自分のだけれど、つい\nずっと見ちゃうもの", 181201, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8433, "おにーたんっ♪\n…呼びたくなっただけ\n本当にそれだけよっ？", 181201, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8434, "どうしたの？\n…ふふっ、安心して\nこれからもずっと\n私はここにいるわ", 181201, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 8435, "みんな起きだす時間ね\n街が活気づいてく感じ\n嫌いじゃないわ", 181201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 8436, "【美食殿】のお昼は\nいつも美味しいから\nまた太っちゃいそうね", 181201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 8437, "おやすみなさい\n明日も、その先も\n一緒に冒険しましょう", 181201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8439, "休憩も大事…よね\nじゃあ遠慮なく…\nふぅ～…♪", 181201, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8440, "はぁ…ふわふわ…♪\nこのかわいさには\nもう抗えないわ…", 181201, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8441, "落ち着く香り…\nこの匂いを嗅いでると\n帰ってきたなって\n思うわね", 181201, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8442, "みんなで野宿した時も\n焚火の明かりを見て\nすごく安心したのよね", 181201, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8444, "ペコリーヌさんには\n負けるけど…\n美味しくって手が\n止まらないわ…！", 181201, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8445, "プリンセスフォーム…\n一体どういう仕組みで\n発動するのか、文献に\n書いてないかしら…", 181201, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8447, "すごーく暑いけど、\nこの暑さをどうやって\n乗り越えるかって\n考えるのも楽しいかも", 131901, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8448, "氷で彫刻を作るの？\nそれは楽しそうだけど\nすぐに溶けちゃわ\nないかしら？", 131901, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8449, "お日様の下で遊ぶのも\n部屋の中で涼むのも\nどっちも夏ならではの\n楽しみ方なのね", 131901, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8450, "生き物の観察日記、\n私も書いてみたいな\nパックのお昼寝日記\nなんてどうかしら？", 131901, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8451, "さまぁばけぇしょんは\n計画を立てるもの\nなんだって\n私も挑戦してみるわね", 131901, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8452, "この世界のことを\nもっと知って\nアーラム村のみんなに\nお話ししてあげるの", 131901, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8453, "いつルグニカに帰るか\nわからないからこそ\n毎日頑張らなきゃね", 131901, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8454, "今日はどこに行くの？\n私は、えーと…\nかき氷が食べたいわ！", 131901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8455, "ここにも精霊が\n住んでたりするの？\n…精霊じゃなくて\nがいど妖精？", 131901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8456, "パックったら、\n近所の子にいっぱい\nなでなでされてたわ\nすごーく人気者ね！", 131901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8457, "実は…線香花火の\n特訓をしてたの！\n次は負けないんだから", 131901, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8458, "昨日、みんなの\n似顔絵も絵日記に\n描いてみたんだけど…\n笑っちゃダメよ？", 131901, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8459, "何気ない時間だって\n後で思い返せば\nずっと忘れない\n素敵な思い出になるの", 131901, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 8460, "気持ちよく起きて、\nいい一日を過ごしたい\nそんなときには\nラジーオタイソーね！", 131901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 8461, "お昼ごはんの時間ね\n暑さに負けないように\nしっかり食べましょう", 131901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 8462, "外は涼しいみたいだし\n夜の散歩に行ってみる\nのはどうかしら？", 131901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8464, "遊び疲れたら\nここでひとやすみ…\nふわぁ…\n眠くなっちゃった…", 131901, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8465, "細かいところまで\nすごーく丁寧に\n作られてる…", 131901, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8466, "わっ、なんだか\nすーっとする香りね\nちょっぴり涼しく\nなったみたい", 131901, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8467, "海で遊ぶと\n体が冷えるから\nあったかいのも\nすごーく嬉しい…", 131901, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8469, "これも美味しい…！\nペコリーヌが\nどんどん食べちゃう\n気持ちが分かるかも", 131901, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8470, "このお話も面白い…！\nあっという間に時間が\n過ぎちゃったわ…\n…ね、寝てません！", 131901, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8472, "この子と一緒に海に\nいったら楽しそうね！\nどこに行けば\n会えるのかしら？", 131901, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8473, "ふかふかね！\nこうしてると波の音が\n聞こえてきそう…", 131901, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8474, "こちらでは水着姿で\n過ごすのも\n珍しくないようですね", 132101, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8475, "メイド服ではなく\n姉様と色違いの水着…\nレムは嬉しいです", 132101, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8476, "少し室温が高いかも\nしれませんね…\nいくつか氷を配置して\nおきましょうか", 132101, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8477, "清涼感があるとはいえ\n鉄球をビーチバレーの\nボールに代用するのは\n危ないでしょうか…", 132101, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8478, "ふぅー…ふぅー…\nようやく浮輪が\n膨らみました\n姉様にお渡ししないと", 132101, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8479, "海のことをもっと\n教えていただけますか\n…スバルくんと海の\n話をしたいんです", 132101, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8480, "もし海に行く機会が\nあればレムにも…\nお声がけくださいね\nぜひ、お願いします", 132101, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8481, "何を着ていても\nメイドはメイド\n掃除も料理も\nなんでもござれです", 132101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8482, "スバルくんとあなたは\n兄弟のように\n仲がいいですね\n姉様とレムと一緒です", 132101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8483, "この姿で子どもたちと\n遊ぶのは…さすがに\nはばかられますね\nやはり着替えてきます", 132101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8484, "ナンパ…というものが\n海にはあるそうです\nスバルくんがとても\n警戒していました", 132101, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8485, "魚釣りでしたら\nレムもご一緒します\n氷漬けの魚の備蓄を\n補充したかったので", 132101, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8486, "子どもたちと砂遊びの\n約束をしているんです\nあなたもご一緒に\nいかがですか？", 132101, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 8487, "ラジーオタイソーは\nもうされましたか？\n体がほぐれますよ", 132101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 8488, "お昼ですね。では\n姉様に焼きそばを…\nもちろん皆様の分も\n用意してありますよ", 132101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 8489, "熱帯夜であっても\nお休みの際はお腹を\n冷やさないように\n気をつけてくださいね", 132101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8491, "とても気持ちよさそう\nです…！\nさっそく姉様に\nお教えしないと", 132101, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8492, "季節に合わせた\n着せ替えができれば\n子どもたちも\n楽しめそうです", 132101, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8493, "ふむふむ…この香りは\n疲れを癒やしてくれ\nますね。エミリア様に\nお持ちします", 132101, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8494, "身も心もほぐれます\n思っていたよりも\n体が冷えていたみたい\nですね…ふう…", 132101, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8496, "もぐもぐ…んうっ！？\nこのすっぱさは…\n梅干し、ですか…\nくせになりますね…！", 132101, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8497, "海に関する詩文なども\nあるのでしょうか…\n気になります", 132101, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8499, "地竜とは違った\n乗り心地です…\n本物にも乗ってみたい\nものですね", 132101, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8500, "この大きさで\n本物の貝であれば…\nどんな料理にするか\n想像が膨らみます", 132101, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8501, "これがこの世界の\n居住環境か…\n面白い構造だね", 132001, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8502, "暇を持て余していてね\n誰か話し相手に\nなってくれないかな", 132001, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8503, "目新しいものが\nたくさんあるね\n胸が躍るよ", 132001, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8504, "頑丈そうな建物だ\nここなら彼女たちが\n暴れても大丈夫かな？", 132001, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8505, "力が制限されていて\n良かった面もある\n君も吐かずにすんだし", 132001, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8506, "悪さなんてするわけ\nないじゃないか\n傷つくなぁ", 132001, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8507, "普段と違う格好で\n歩き回るのも\n悪くない経験だ", 132001, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8508, "ボクに聞きたいことが\nあるなら遠慮なく\n質問するといい", 132001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8509, "気軽に魔女に\n話しかけるなんて\n君は物好きだね", 132001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8510, "この世界においては\nボクは異物だからね\n大人しくしているさ", 132001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8511, "ボクの反応を\n見たいのかい？\n君の好奇心には\n恐れ入るよ", 132001, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8512, "ボクの手を借りたい？\nもちろん構わないさ\n対価はいただくけどね", 132001, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8513, "ボクと海を見たい？\nふふ、魅力的な\nお誘いだね", 132001, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 8514, "お目覚めかな？\nさて、君の見た夢を\n教えてくれないかな？", 132001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 8515, "食後のお茶を\n一緒にどうだろうか？\nさあ、席に座ってよ", 132001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 8516, "おや、お休みかな？\n魔女の前で無防備だね\n…無論、何もしないさ", 132001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8518, "こういう椅子は\n茶会には向かないね\nつい気が緩んでしまう", 132001, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8519, "こういうものを好きな\n女の子がいいのかな？\n君の嗜好を知りたいな", 132001, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8520, "いい香りだ\nこれにはどんな\n効能があるんだろう？", 132001, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8521, "火の点け方に\nボクのいた世界との\n違いはないのかな？", 132001, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8523, "これもまた未知の味だ\n存分に味わわせて\nもらうとしよう", 132001, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8524, "素晴らしいね\nここには叡智が\n詰まっている", 132001, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8526, "魔物を模倣した\nぬいぐるみか…\n実物を見たくなるね", 132001, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8527, "いい気持ちだ…ボクと\nしたことが、何も考え\nられなくなりそうだ", 132001, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 8528, "願わずにいられません\nどうかこちらの\n世界には…悲劇が\n生まれませんようにと", 109701, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 8529, "食事のご希望があれば\nなんなりと。お腹が\nいっぱいになるまで\n沢山ご用意しますね", 109701, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 8530, "あの男、これからも\n観察が必要ね\nラムの目の届く範囲に\n置く必要があるわ", 109801, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 8531, "素直なところは\nあの男の美点ね\n癪だけど、偶然にも\nラムと同じだわ", 109801, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 8532, "迷惑かけちゃったけど\n雪遊びは楽しかったわ\nまたやりたい…なんて\n言ったら怒られるかも", 109901, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 8533, "数えきれないくらいの\n思い出を、みんなで\n作りましょう！", 109901, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8534, "わわっ、今すごく\nいい香りがしたけど…\n誰の香水かな\n教えてほしいな…♪", 132201, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8535, "錬金術用の釜、\n置くならどこかな？\n自分専用の錬金釜、\n憧れちゃうよね♪", 132201, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8536, "この匂い…誰か\n唐揚げ食べたみたい\nうぅ…お腹空いて\nきちゃうよ", 132201, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8537, "次の新曲のタイトルは\n『恋の錬金術』！\nうーん、ちょっと\nベタ過ぎるかな…", 132201, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8538, "あ、錬金の素材\n切らしてたんだ…\n彼を誘って採集に\n行ってこようかな", 132201, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8539, "瞬時に着替えができる\n薬とかできないかな？\n舞台で衣装チェンジ！\nなーんて、楽しそう♪", 132201, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8540, "彼が遊びに来た後は\n部屋の空気がほんのり\n彼の匂いなんだ…\nなんだか幸せだな♪", 132201, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8541, "今日もバイト？\nそれならこれ飲んで\n私のお手製ポーション\nがんばってきてね！", 132201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8542, "新しくできたカレー屋\n行ってみない？\n時々スパイスの香りが\n流れてくるんだよ", 132201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8543, "私のフィギュア\nそっくりでしょ！\nルイちゃんが作って\nくれたんだ", 132201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8544, "あれ、シャンプー\n変えた？　私と同じの\nだよ！　どうしよう\nドキドキしちゃう…", 132201, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8545, "いらっしゃいませ♪\n何を作りますか？\nなーんてね。錬金堂で\nまた一日店長したいな", 132201, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8546, "クンクン…彼の\nハンカチ…いい匂い♪\n私だけの秘密のお守り\nにしてるんだ", 132201, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 8547, "ねえねえ、天気も\nいいし一緒に錬金の\n素材集めに行こうよ！", 132201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 8548, "お昼ご飯、用意したよ\nあはは、さすがに\nランチは錬成しないよ\n私の手作りだよ♪", 132201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 8549, "今日も一日お疲れさま\nこれ飲んでゆっくり\n休んで。薬じゃないよ\nホットミルクだよ？", 132201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8551, "昨日、遅くまで\n錬金術の勉強してた\nから眠くって…\nちょっと一休みっと", 132201, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8552, "この子にちょっとだけ\n香水かけて、と…\nんー！　だっこした時\nすっごくいい匂い♪", 132201, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8553, "花みたいな、\nお菓子みたいな\nいい香り。気持ちが\n穏やかになるね♪", 132201, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8554, "外でもこの暖かさを\n感じられる道具、\n作れたりしないかな？", 132201, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8556, "素材集めに行くと\nお腹が空くんだよね\nイロハちゃんも\nこの味好きそう", 132201, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8557, "錬金術にはたくさんの\n知識が必要だからね\nさあ、今日も調合書を\n読まなくちゃ", 132201, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8559, "ここが騎士さんたちの\n住んでる場所…！\n粗相がないように\n気をつけなきゃ", 127701, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8560, "ジオ・テオゴニアの\nみんな、私がいない間\nちゃんとお仕事\nしてればいいけど…", 127701, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8561, "コッコロさんも\n従者として長いって\n聞いたような…\nあの年ですごい…！", 127701, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8562, "自分がもてなされる\n側になるのって\nやっぱりちょっと\n落ち着かないかも…", 127701, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8563, "今度のお休みの日は\n何しよう…？\nあっ、最近できたお店\nヴルムと行こうかな？", 127701, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8564, "お買い物したら\nかわいいからって\nたくさんおまけして\nもらっちゃった…♪", 127701, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8565, "ここにいると、つい\nぼんやりしちゃう…\nこれも騎士さんの力…\nなのかな", 127701, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8566, "お手伝いできることは\nありませんか？\nお世話でもなんでも\n任せてくださいっ", 127701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8567, "ランドソルに初めて\n来たときは驚きました\n砂糖の山と思ったら…\nあんなに寒いなんて！", 127701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8568, "あの、ヴルムを\n見ませんでしたか？\nもう…あの子ってば\nまた仕事をサボって…", 127701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8569, "家具や家が壊れても\n食べられないなんて\nなんだかもったいない\nですね", 127701, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8570, "とっておきの紅茶を\n手に入れたんです\n一杯いかがですか？\nその、日頃のお礼です", 127701, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8571, "頑張ってて偉いって…\nほ、褒めてもなんにも\n出ませんからね！？\n…えへへ♪", 127701, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 8572, "おはようございます！\nモーニングティーは\nいかがですか？\n私、準備しますねっ", 127701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 8573, "ライラエルさまも\nお昼寝はお好きです\nけど…だからって\n寝すぎはダメですよ！", 127701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 8574, "おやすみなさい\nくれぐれも寝不足には\n気をつけてくださいね", 127701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8576, "しっかりしないと…\nメイドの私が\n気を抜くなんて…\nそんな…ふぅ…", 127701, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8577, "わぁ、かわいい…♪\nってダメダメ！\nメイドなんだから\n落ち着かないと…！", 127701, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8578, "いい匂い…♪\nこの香り、\nライラエルさまも\n好きそうです！", 127701, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8579, "ランドソルの環境に\n触れると、この光の\nありがたみが\n身に沁みますね…", 127701, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8581, "甘くない食べ物が\n一般的なのも\n慣れてきました\nあむ…おいひぃ…♪", 127701, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8582, "ランドソルの文字、\n私たちのところと\nちょっと違いますね…\n覚えられるかなぁ", 127701, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8584, "甘い匂いがしない…\nジオ・テオゴニアとは\n別の世界なんですね", 127801, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8585, "掃除めんど…\nん…このくらいで\nまー十分でしょ", 127801, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8586, "そろそろ食事の\n用意もしなきゃ…\nあーやる事多い…", 127801, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8587, "秘密の隠れ場所、\nまた姉さんにバレた…\n次のを探さないと", 127801, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8588, "知らない間に仕事が\n終わったり…しないか\nしてくれたらいいのに", 127801, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8589, "きちんともてなすには\n騎士さんの好みも\n覚えてかないと…", 127801, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8590, "ご主人サマに\nご奉仕します～♪\n…キャラじゃないか\nやめやめ、恥ずかし…", 127801, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8591, "居心地いいっすね…\n今日からここに\n住んでもいいですか", 127801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8592, "あの、ちょっと姉さん\n怒らせちゃったんで\n匿ってくれません？", 127801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8593, "羽と尻尾が格好いい？\n意外と邪魔ですよ\n仰向けで寝にくいし", 127801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8594, "ワークライフバランス\nっていい言葉ですね\nぜひライフ多めで", 127801, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8595, "騎士さんって\n働き者ですね\nそのままあたしの\n仕事も…いえなんでも", 127801, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8596, "熱心に見てても\n尻尾は触らせて\nあげませんってば\n角？　角なら…まぁ", 127801, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 8597, "寝ても寝ても\n寝足りない…ふぁ…\nあー…眠たい…", 127801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 8598, "アフタヌーンティーの\n準備は得意ですよ\nつまみ食いとか\n…つまみ食いとか", 127801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 8599, "よく休んでください\n布団は干しといたんで\n…姉さんが", 127801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8601, "ふー…\nこのままずーっと\nここに居たい…", 127801, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8602, "あんたも一緒に\nサボっちゃうか～\n…なんて", 127801, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8603, "落ち着く匂い…\n騎士さんの好み\nなんですかね", 127801, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8604, "こういうとこの\n掃除って面倒だし…\n今度でいっか", 127801, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8606, "ご飯の前に\nこんな食べたら\n姉さんきっと怒るな…", 127801, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8607, "食に関する本が\nやけに多い…\nお国柄ですかね", 127801, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8609, "すんすん…\nお兄さんの匂いが\nぷんぷんする～♡\nザコくっさぁ♡", 130201, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8610, "ぴこーん！\nいひひっ、新作メカの\n面白いアイデア、\n思いついちった～♡", 130201, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8611, "メカもいじりたいし\n服もアクセもほしー…\nあ～、おこづかい\n足んないよ～！", 130201, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8612, "お勉強でもしよっかな\nザコお兄さんには\n絶対わかんない\nよーなやつ♡", 130201, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8613, "お兄さんの部屋って\nどこどこぉ？\nメカの実験室に\n使えそうだよね～♡", 130201, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8614, "次のデートコースは\nどんなのかなー？\n…いや、むしろ次は\nぼくが決めちゃうっ？", 130201, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8615, "強奪計画は念入りに♡\nお兄さんのハートも\n全力でザコザコにする\n計画、立てないとね♡", 130201, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8616, "ねー、ヒマヒマヒマ～\nかまってかまってー♡\nどっか連れてけー♡", 130201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8617, "今日はこっちで\nご飯食べたいなー♡\n…帰っても５日連続の\nバカナ姉カレーだし…", 130201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8618, "依頼行くならぼくも\n連れてってよー♡\nお兄さんのザコっぷり\n見ててあげる～♡", 130201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8619, "お兄さんはぼくより\nオトナだもんね～♡\nじゃあオモテナシ、\n期待してるから～♡", 130201, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8620, "ヤマ姉になんかお土産\n持って帰ろうかなー？\nザコお兄さん、選ぶの\n付きあって～♡", 130201, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8621, "遊びにいくときは\nザコお兄さんも\n誘ってあげるよ♡\nどうせヒマでしょ～？", 130201, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 8622, "ふぁ～、ねむぅ～…\n夜更かしぶん寝るから\nあとで起こして～♡\nあ、変なこと禁止～♡", 130201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 8623, "おなかすいたぁ～\nなんか食べ行こ♡\n甘いのおごって～♡", 130201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 8624, "えー、もう寝る時間？\nじゃあ…添い寝して\nあげよっか？\nなーんて、ウッソー♡", 130201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8626, "おお～ふわっふわ～♪\nバンディシャーク号の\n座席に使いたーい！", 130201, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8627, "カワイイ～♡\n改造したらもっと\nカワイくなるかな～？", 130201, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8628, "くんくん…いい匂い♡\nザコお兄さんって\nこーゆー香りが\n好きなのかなぁ～", 130201, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8629, "はー、あったかーい♡\nバンディシャーク号\nにも欲しいかもー♪", 130201, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8631, "食べれるときに\n食べるのが大事って\nヤマ姉言ってたしね～", 130201, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8632, "あ、この本読んだよ♡\n参考文献リストが\nしっかりしてて\n勉強になったなー♪", 130201, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8634, "部屋は多少ゴチャつい\nてる方が落ち着くのさ\nバンディシャーク号\nみたいにね♪", 130001, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8635, "呆けてるのもいいが\nちいと物足りないねぇ\n麦しゅわもブドウ朱も\n無いなんてさ", 130001, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8636, "この家セキュリティが\nなってないねぇ…\n盗賊目線でちょっくら\n指南でもしてやるか", 130001, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8637, "不思議だねぇ\nここで飲むと、酔いが\n回るのが早い気が\nするよ…ヒック", 130001, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8638, "ふむ…\nクルマが壊れた時は\nアジト代わりに\n使えそうだ♪", 130001, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8639, "カカッ、いい部屋だ！\n妹を迎える準備は\nできてるみたいだねぇ", 130001, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8640, "…っと、油断したら\n落ち着いちまうね\nボーヤの甘っちょろい\n雰囲気のせいだよ", 130001, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8641, "さすがにこの家から\n盗みはしないさ\nいずれは妹たちの\n共有財産になるからね", 130001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8642, "ボーヤ、暇なら\n博打でもやるかい？\nアタシが負けたら\n掃除でもしてやるよ", 130001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8643, "立派な家じゃないか！\n一国一城の主とは\nちょっとボーヤのこと\n見くびってたかもねぇ", 130001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8644, "かわいい義弟のためだ\nアタシが盗ってきた\nお宝をくれてやるよ\n好きな場所に飾りな！", 130001, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8645, "妹と結ばれる前に\n他の女との関係は\n清算しとくように\nわかったね、ボーヤ？", 130001, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8646, "お、ヤシオーリかい？\nフフン、義弟からの\nお酌とあっちゃあ\n断れないねぇ♪", 130001, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 8647, "うう…清々しい朝だが\n頭がいてぇ…\nちっと昨日は\n飲みすぎたか…？", 130001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 8648, "ほらこれ、ワカナに\n作らせた弁当だ\nこれで昼からも\nバリバリやりな！", 130001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 8649, "まだまだ夜はこれから\n…と言いたいとこだが\n早寝して立派に育ちな\nそれが妹たちの為だよ", 130001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8651, "中々いい座り心地だが\n盗賊が腑抜ける訳には\nいかないね…が…\nもう少しだけ座るか…", 130001, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8652, "こういうのの中に\n実はレアなブツが\n隠されてるとか\nありがちだねぇ", 130001, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8653, "ふうん、ボーヤは\nこういう匂いが\n好きなのかい？\n妹たちに教えてやろう", 130001, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8654, "体を温めんにゃ\n酵露をカッとやるのが\n一番だが、これも存外\n悪くないねぇ…♪", 130001, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8656, "ガツガツ…\nん、悪くねェ味だ\nこれにヤシオーリでも\nありゃ文句はないね", 130001, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8657, "本にも載ってない\nレアもんのお宝か…\nフッ、想像しただけで\n血沸き肉躍るねぇ…！", 130001, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8659, "今日の買い物は\nいつものお野菜と…\nあっ、確か特売が\nあったはず…！", 130101, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8660, "お掃除、お掃除…♪\nきゅっきゅっ…\nよいしょ、っと…", 130101, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8661, "洗濯ものも\nたまらないうちに\nやっちゃわないと…", 130101, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8662, "夕飯のメニュー…\n何にしよう？\n期限の早い食材は…", 130101, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8663, "色々お菓子も\n作ってみたいけど…\n予算…足りるかなぁ…", 130101, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8664, "騎士さん…どんな\n料理が好きなんだろ？\n前に作ったコロッケは\n評判よかったけど…", 130101, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8665, "騎士さんと\nおしゃべりすると、\n胸があたたかくなる…\nなんでだろ…？", 130101, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8666, "あっ、騎士さん\nどうかしましたか？\n御用ならいつでも…♪", 130101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8667, "削っても削っても\n家計簿は赤字…\n騎士さ～ん…\n助けてください～！", 130101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8668, "手が空いてたら\n日用品の買い出し、\n手伝ってもらっても\nいいですか…？", 130101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8669, "やっと一息つけそう…\nあっ、わたし\nお茶を淹れてきますね", 130101, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8670, "困りごとがあったら\n言ってくださいね\nわたし…騎士さんの\n力になりたいです", 130101, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8671, "助けてもらった分は\n必ず返しますから…\nそれまでわたしのこと\n見ててくださいね…♪", 130101, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 8672, "忘れ物はないですか？\n今日のお仕事も\n頑張りましょうね…！", 130101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 8673, "お昼ご飯できてますよ\n大したものじゃなくて\n恐縮ですけど…", 130101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 8674, "夜更かしはだめですよ\nわたしも家事を\n終わらせたら寝ないと", 130101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8676, "これ…割れ物防止の\nクッションに\n使えるかも…？", 130101, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8677, "こういうのいいなぁ…\nけど…フブキちゃんに\n絶対改造されちゃう…", 130101, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8678, "いい匂い…でも\n切れかけてるかも？\nえっと補充するには…", 130101, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8679, "あったかい…\nちょっとだけなら\n休んでも…いいよね", 130101, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8681, "これがあったら\nもうお料理を\n作らなくて済むかも？", 130101, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8682, "新しい節約レシピは…\nあ、この草って\n食べられるんだ…", 130101, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8684, "愛溢れるランドソルの\nクリスマス…ですか\nいけませんね、つい\n胸が躍ってしまいます", 132801, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8685, "ツリーの飾りと\nわたしの髪…\nくるくるしていて\n似ているかも…くすっ", 132801, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8686, "この姿で傲慢な\nわたしになったら…\nいつもより怖がらせて\nしまうでしょうね…", 132801, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8687, "悪魔として、人を\nドキドキさせなくては\nい、イオから教わった\n…投げキスの練習を…", 132801, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8688, "ジオ・テオゴニアの\nお菓子は、ツリーの\n飾りに向いていそう\nですね…ふふっ", 132801, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8689, "騎士のギルドハウス…\nクリスマスの時期も\n暖かいのですね…", 132801, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8690, "城の皆と過ごす宴も\n楽しいですが…\n騎士と二人の宴も\n幸せです…", 132801, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8691, "騎士、あなたの\nもてなしに感謝します\nクリスマスの何たるか\n教えてくださいね", 132801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8692, "この黒い羽は\nどう手入れすれば？\nイオなら知っている\nでしょうか…", 132801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8693, "城の皆も連れてきたい\nほど、クリスマスは\n楽しい行事ですね\nお土産も迷います", 132801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8694, "クリスマスの晩餐に\n招待されたのですが…\nその…辛いものは\nありませんよね…？", 132801, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8695, "クリスマスと言わず…\nいつでも星に祈って\nいますよ？　騎士達に\n幸せがあるように、と", 132801, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8696, "悪魔として騎士を\nドキドキさせる…？\nそ、そのようなこと\nわたしには…うぅ…", 132801, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 8697, "朝は寒いですね…\n雪…積もっている\nでしょうか？", 132801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 8698, "ふあぁ…いけません\n公務の滞在先で\nお昼寝など…", 132801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 8699, "夜は悪魔として\n夢を見せましょう\n騎士はどんな夢が\n見たいですか？", 132801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8701, "ふぅ…落ち着きます…\n家でゆっくりと過ごす\nクリスマスも\n良いものですね", 132801, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8702, "リンドのプレゼントに\nぬいぐるみは…？\n…子ども扱いすると\n落ち込むでしょうか…", 132801, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8703, "暖かいお部屋に\nこの香りを漂わせたら\nふふっ…素敵な時間を\n過ごせそうです", 132801, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8704, "これがあれば、\n寒い冬も安心ですね\n暖かくて…このまま\n眠ってしまいそうです", 132801, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8706, "悪魔の力を使ったら\nお腹が空いてきて\nしまって…\n恥ずかしいです…", 132801, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8707, "クリスマスには多くの\n伝承があるのですね…\n天使の伝承まで\nありましたし…", 132801, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8709, "まぁ…！\nよくできていますね\nサプライズプレゼント\n…でしょうか。ふふ♪", 132803, 3, 14);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8710, "ど、どうでしょう…？\n親善交流の催しに\nなりそうでしょうか…", 132804, 3, 15);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8711, "ギルドハウスって\nあったかぁ～い♪\n心がぽかぽかするの！", 132901, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8712, "クリスマスの悪魔の\nお仕事はないわね！\nだってここのみんな\nいい子なんですもの♪", 132901, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8713, "みんなちゃんと\n寝られてるかしら？\n夜更かしせずに、\nいい夢見てね♪", 132901, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8714, "毎回、来年が\n楽しみになるような\n素敵なクリスマスを\n過ごしたいわね♪", 132901, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8715, "訳ありケーキを見ると\n刑務作業を思い出すわ\n…あれも貴重な体験\nだったのかしら…？", 132901, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8716, "ロマンチックな\nクリスマスに備えて\n今のうちに少女漫画で\n予習しておかないと！", 132901, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8717, "ほっぺにチューなら\nもう一回しても…\nや、やっぱり無理！\n恥ずかしいわ～！", 132901, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8718, "またみんなで\nセッションしたいわね\nカラーベルの練習した\n成果を見せたいの♪", 132901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8719, "君はサンタさんに\nどんなプレゼントを\n頼んでいたのかしら？\n気になるわ～！", 132901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8720, "愛が壊れても、\n何度でも治してあげる\n私たちの間に、\n愛の尽きる日はないわ", 132901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8721, "冬休みって短いようで\n長く感じちゃうわよね\n学校のみんなと\n早く会いたいな～♪", 132901, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8722, "愛をテーマにした\n冬休みの作文の宿題、\n君の分を読むのが\n今から楽しみね♪", 132901, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8723, "よかったらまた\n私をハニーって呼んで\nほしいの。ダーリン♪", 132901, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 8724, "おはよう！\n冬の朝は冷えるから\n風邪を引かないように\n対策しましょうね", 132901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 8725, "お昼ご飯は一緒に\n食べに行きましょう！\nクリスマス限定の\nランチコースよ♪", 132901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 8726, "ねえねえ、キャンドル\nイルミネーションを\n見に行きましょうよ！\nロマンチックよ～♪", 132901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8728, "暖かいクリスマスを\n過ごすのに必須よね♪\nふふ、柔らかぁい！", 132901, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8729, "どの子もかわいい～♪\nクリスマスツリーの\n下で、一緒にお祝い\nしてもらいたいな♪", 132901, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8730, "なんだか胸の奥が\n温かくなる匂いね～\nリラックスしちゃう♪", 132901, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8731, "あったかぁい♪\nあ、でも夜になったら\nサンタさんが入れる\nように、火を消すのよ", 132901, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8733, "寒い冬だからこそ\nしっかり食べて\n備えておかなきゃ！", 132901, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8734, "『聖夜の６時間』…？\n気になる小説だけど…\n勝手に読んじゃ悪い\nわよね…ガマンよ…！", 132901, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8736, "きゃっ…！\nこんな不思議な本が\nあったのね～\nちょっと面白いかも♪", 132901, 3, 14);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8737, "もふもふ、ふわふわで\nあったかくて\n気持ちいい～…♪", 132901, 3, 15);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8738, "冬といえばサマー！\n…って言うけど夏は\n何をＰＲするんだろ\nメルクリゾート…", 129501, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8739, "奨学金の道は遠いな…\nあまりに遠すぎて\n最近忘れてたまである", 129501, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8740, "暇だし鏡でも見よっと\nはー、今日も顔が良…\nげ、若干むくんでる？\nはー、今日終わった", 129501, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8741, "水着…お姉ちゃんと\n貸し借りするんだけど\nこれって少数派姉妹？", 129501, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8742, "ちゅむ、ツムギちゃん\n今日も可愛いねデュフ\n…なんで推し語りの声\nキモくなるんだろ私…", 129501, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8743, "あれ…なんで私\n水着のまま部屋の中\nうろついてるんだっけ\n…こわ！　てか寒っ！", 129501, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8744, "先輩よく友達と海に\n行くって言ってたけど\n…え、誰とだろ？\nまあ誰でもいいけどさ", 129501, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8745, "始まる…\nチエルの夏が始まる…\nあ冬でしたっけ？\nまどっちでもいいです", 129501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8746, "こないだトミヱ先輩\n壁に喧嘩売ってました\n相変わらず元気ですね", 129501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8747, "ユニ先輩にお芝居の\n出来を聞かれたんで\n可愛いって答えました\n嘘は言ってません", 129501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8748, "クロエ先輩の腹筋\nかっちょいーですよね\nさりげ触りたいけど\nあいつ隙ないんですよ", 129501, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8749, "先輩もうカメラ係は\nやらないんです？\nチエルはやってますよ\n最高の被写体を常に", 129501, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8750, "メリゾでカップル割の\n企画あるらしいんです\nちなみに先輩は…\nあ、いえ、何でも…", 129501, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 8751, "朝ごはんは抜いても\n朝ちぇるだけは\n抜いちゃ駄目ですよ", 129501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 8752, "お昼なに食べるか\n悩んでます？\nおごってくれるなら\nいい店教えますけど", 129501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 8753, "この時間帯になると\nユニ先輩がごそごそ\n蠢きだすんですよね…", 129501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8755, "世界の美少女が…\nこんな格好で\nくつろいでたら…\nまずい…事案になる", 129501, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8756, "え、この子って\nウォータープルーフ？\nメリゾにも連れて\nいけちゃうカンジ？", 129501, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8757, "はぁ～、いい香り…\nそういえばメリゾの\nスタッフルームも\n『贅』な匂いしてた…", 129501, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8758, "薄着で暖まるとか…\n冷静になっちゃ\n駄目なやつですよね\nこれ…", 129501, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8760, "おにぎり１個は\nちぇるに換算すると…\n２５ちぇるくらい？", 129501, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8761, "あれー？　この辞書\nまだちぇるの項目\n載ってないんだ？", 129501, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8763, "何これ…地味に楽しい\nあとたぶん\nクロエ先輩が好きそう", 129501, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8764, "これは良きですねー\n自宅でメリゾ気分とか\nユニ先輩もにっこり", 129501, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8765, "ここが騎士氏の\nお住まいですか\nふむ…地縛霊は\nいなさそうですね", 133001, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8766, "ここは土足厳禁では\nないみたいですね\nま、私には関係ないか\n足、透っけ透けなので", 133001, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8767, "ランドソルの裏路地\nあそこは最高ですね\n暗くてじめじめしてて\n癒されるー", 133001, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8768, "っ！\n今ラップ音が…？\nまさか幽霊？\nやだ、怖い", 133001, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8769, "獣人族にエルフ族…\nなるほど騎士氏が\n幽霊程度では驚かない\n訳です…ちぇ", 133001, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8770, "今日の深夜\n騎士氏が起きるまで\n枕元でずーっと\n立っててみようかな", 133001, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8771, "騎士氏の驚く顔を\n見ると胸が高鳴る…\n変ですね、心臓なんて\n動いてないのに", 133001, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8772, "安心して下さい\n勝手にすり抜けて\n騎士氏の部屋は\n覗きませんよ…多分", 133001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8773, "なんですか騎士氏\n怖い話でも聞きます？\nじゃあいきますね…\n今貴方の前に幽霊がっ", 133001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8774, "この家…幽霊が\n寄ってくる間取りに\nなってますね\nまあ、嘘ですけど", 133001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8775, "料理はしませんよ\n指きったら痛いので\nえ…透ければいい？\n……天才ですか？", 133001, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8776, "お散歩ですか？\n深夜なら付き合います\nあ、やっぱり断ります\n幽霊出たら怖いので", 133001, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8777, "騎士氏の部屋のセンス\n好きですよ…もっと\nじめじめしていれば\nなおよし、ですが", 133001, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 8778, "レイスになってから\nあまり言わなくなった\n挨拶第一位はこちら…\nおはようございます", 133001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 8779, "騎士氏。午後からも\n頑張って下さいね\n私は日陰で休みます\nでは、おつです", 133001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 8780, "夜も更けてきましたね\nここからがレイスの\n本領発揮…では早速\nう　ら　め　し　や～", 133001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8782, "中々座り心地が\nよろしいですね\nブロッケンに跨る方が\n安心はしますが", 133001, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8783, "知ってますよ\nどうせ貴方も\n夜中になったら\n動き回るんでしょ？", 133001, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8784, "すう…はあ…\nすごく落ち着く香り\n…落ち着き過ぎて\n成仏しそうでした", 133001, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8785, "温度は感じませんが\nなぜかほっこり\n温かい気持ちです\nなんででしょうね？", 133001, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8787, "もぐもぐ…\n食べ物は基本好きです\n消化の方法は…\n企業秘密で", 133001, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8788, "苦手なんですよね\nホラー小説\nだって幽霊とか\n怖いじゃないですか", 133001, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8790, "この得も言われぬ\n高揚感…やはり\n正月はいいものだな！", 133201, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8791, "む、いかんな\n気付くと黒豆と\n栗きんとんばかり\n食べてしまう…", 133201, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8792, "ギルドの連中にも\nお年玉を渡してみるか\n少しは士気が上がる\nかもしれないな！", 133201, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8793, "正月くらいはのんびり\n羽を伸ばすとしよう\n適切な休暇も軍人の\n大事な責務だからな", 133201, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8794, "正月で浮かれすぎた\n者も多いはずだ\n念のため街を見回りに\n行くとしよう", 133201, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8795, "む、このおせち料理は\n絶品だな！\nあいつの分も\nとっておいてやろう", 133201, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8796, "今年の抱負は…\nそうだな、あいつと\nさらに高みを目指す\nこれでいこう", 133201, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8797, "えっ、お年玉！\n…い、いや、私は\n子どもではないからな\nえ、遠慮させてもらう", 133201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8798, "凧あげすごろく\nこま回し、たとえ遊び\nでも手は抜かんぞ\nさあ、勝負だ！", 133201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8799, "そのきな粉餅も\nうまそうだな…\n私のあんこと交換\nしないか？", 133201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8800, "貴公、正月料理を\n食べ過ぎてないか？\nじ、実は私も少々、な", 133201, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8801, "貴公、お参りに行くぞ\n何？　外が寒いだと？\n仕方ない、手ぐらいは\nつないでやろう", 133201, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8802, "貴公と新たな年を\n迎えられてうれしいぞ\n今年も一つ\nよろしくたのむ！", 133201, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 8803, "起きろ貴公。早朝なら\n参拝客も少ないはず\n今のうちにお参りを\n済ませよう", 133201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 8804, "昼食に餅を焼いたぞ\nのどに詰まらせない\nようにゆっくり噛んで\n食べるんだ", 133201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 8805, "夜ふかしはあまり\n褒められたものでは\nないが…正月くらいは\n目をつむろう", 133201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8807, "これに座って飲む\n甘酒は格別だな", 133201, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8808, "せっかくの正月だし\nこいつにも着物を\n着せてやりたいな", 133201, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8809, "これは梅の香り…？\nふふ、なかなか\n風流ではないか", 133201, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8810, "冬の寒さが堪えるな…\nここでしっかりと\n暖をとるとしよう", 133201, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8812, "正月はのんびり\nしたいからな、勝手に\n食事が出てくるのは\n本当に助かる", 133201, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8813, "ふむ、これが世界の\n新年のお祝いか\nなかなか興味深いな", 133201, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8815, "一年の計は元旦にあり\n計画を立てて、\n有意義な一年を\n過ごしましょ", 133501, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8816, "年末年始はどうしても\n出費が増えちゃうし\nしばらくはきっちり\n節約しなきゃ…！", 133501, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8817, "タタミにフトン、\nトーゴク文化の内装…\nランドソルでももっと\n流行らないかしら", 133501, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8818, "子どもたちが凧を\n手作りして遊んでたの\n偉いわよね、みんな…", 133501, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8819, "お年玉をあげる側に\nなって、分かる苦労…\n大人になるって\nこういうことかしら", 133501, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8820, "一応実家にも\n顔を出さないと…\nよかったらだけど、\nあんたも来る？", 133501, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8821, "毎日一緒にいて、\nささやかな幸せを\n同じように感じて…\nそれだけで十分ね", 133501, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8822, "着物姿、もっと\nじっくり見たいの？\nさすがに照れるわよ、\nもう…", 133501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8823, "整理整頓、できてる？\nもし散らかってるなら\nお正月休みのうちに\n片づけちゃいましょ", 133501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8824, "今年もみんなが無事に\n過ごせますように…\nほら、一緒に\nお祈りしましょ", 133501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8825, "みんな、あんたと\n遊ぶのが楽しみだって\n子どもに好かれる\nタイプなのよね", 133501, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8826, "あんたの運勢は大吉！\nラッキーパーソンは\n金髪の幼なじみよ\n…なんてねっ", 133501, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8827, "初夢は実現するのよね\nそれじゃ、あたしは\nあんたと…んんっ\nなんでもないわ", 133501, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 8828, "外が寒いと、その分\n目も冴えるわよね\nこのまま朝の散歩に\n行こうかしら", 133501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 8829, "お正月休みでも、\n食べてばっかじゃダメ\nしっかり動いて\n体を引き締めましょ", 133501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 8830, "ちゃんとあったかく\nして寝るのよ\nお正月から\n風邪ひかないようにね", 133501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8832, "着物、しわに\nなっちゃいそう…\nでもそれ以上に、\n心地よくって…はぁ", 133501, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8833, "門松みたいに\nこの子も飾ってみる？\n運勢がよくなるかも♪", 133501, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8834, "この匂い、もしかして\nトーゴクのお香？\nワビサビを感じるわ", 133501, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8835, "ふぅ、あったかい…\n冷えた体に\n染み渡ってくるわ…", 133501, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8837, "んっ、おいしい！\nおせちばっかじゃ\nやっぱり飽きちゃう\nものね…", 133501, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8838, "験担ぎとか縁起物とか\n調べてみると\nいろいろあって\n面白いのよね～", 133501, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8840, "ふーん、いい家ね\nアタシの巣に\nしちゃおうかしら？", 133601, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8841, "お姉ちゃん…\n早く会いたいなぁ…", 133601, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8842, "メドゥシアナも\n気に入るかしら？\n今度連れてこよっと！", 133601, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8843, "お邪魔してるだけ\nなのも悪いわよね…\nお返しはしないとね\n…ネズミ退治とか？", 133601, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8844, "何か平和よね\nランドソルって…\nアタシ的にはもう少し\n荒っぽくてもいいわ", 133601, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8845, "ランドソルも\n楽しい街よね…\n案内してくれる\nアイツのおかげ？", 133601, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8846, "アタシ、アイツに\n頼りすぎてるかも…？\nで、でも！　断らない\nアイツも問題だわ！", 133601, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8847, "ねぇ、つまんなーい！\nもっとアタシを\n楽しませなさいよねー", 133601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8848, "この家悪くないけど…\nもっとムシムシ\nジメジメにできない？", 133601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8849, "何よ？\n用もないのに声掛けた\n…とか言ったら\n容赦しないわよ？", 133601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8850, "少し部屋が寂しいわね\nそうだ、アンタを\n石にして家具に…\n冗談よ、ふふんっ♪", 133601, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8851, "ねぇ、もう少し\n話していかない？\nアンタの話なら…\n特別に聞いてあげるわ", 133601, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8852, "い、いつも\nあ、あ、ありが…\nうぅ、何でもないー！", 133601, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 8853, "やっと起きたわね？\n寝ぼすけなんだから！\n尻尾でひっぱたいて\n起こそうかと思ったわ", 133601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 8854, "人捜しは日の高い\nうちに限るわ！\nさぁ、メドゥシアナ！\n全速力で出発進行よ!", 133601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 8855, "アタシ…\n子どもじゃないもん…\n眠くなんか…\nないんだからぁ…", 133601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8857, "ふわふわであったかい\nつい、とぐろを\n巻きたくなるわね…", 133601, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8858, "アンタ、こんな\nヤワな体じゃ駄目よ\nウロコくらい\n生やしたらどうなの？", 133601, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8859, "くんくん…\n…ふん\n悪くないじゃない\n…くんくんくんくん…", 133601, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8860, "あったかいの大好き！\nジオ・ゲヘナでも\nよく日向ぼっこ\nしたもんだわ…ふわぁ", 133601, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8862, "お姉ちゃんを捜すなら\n…もぐ\n体力つけないとね\n…もぐもぐ", 133601, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8863, "何よ？　たまには\n勉強くらいするわよ\nお姉ちゃんに\n笑われちゃうもの", 133601, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8865, "この装いと力…\n今の世界との\n整合性を上手いこと\n合わせておかないとだ", 133701, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8866, "この格好…開発してた\n当時のまんまだ\nサイズもそのまま♪\n体型維持も完璧っ！", 133701, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8867, "あとで装備の手入れも\nやっとかないと\n…剣なら研ぐだけで\n終わるんだけどなぁ", 133701, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8868, "自分の原点を\n振り返ってみるのも\nたまには必要だね", 133701, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8869, "完璧じゃなくても\n今できる全力を尽くす\nその事に意味がある…\nそうだよね、少年", 133701, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8870, "始まりのあの日、\nそして向かうべき道…\nいつも忘れずにいよう", 133701, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8871, "誰かを救うために\n許容される犠牲なんて\nあっちゃいけない…\n全部、助けてやる", 133701, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8872, "おっと少年、\n何か御用かな？\nキミに関わる事なら\n最優先で対応するよ", 133701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8873, "困った事があれば\n頼ってもらって\n構わないからね！\nむしろもっと頼って！", 133701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8874, "そうだ、時間あるなら\n武器のメンテを\n手伝ってくれないかな\n数が多くてさ～", 133701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8875, "遠距離戦の心得、\n少年にもレクチャー\nしてあげよっか？", 133701, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8876, "無茶しないで、って\n言ってもするだろうし\nせめてアタシの手が\n届く範囲で頼むよ", 133701, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8877, "虚実入り混じる\nこの世界でキミが…\n進むべきルートを\n照らしてくれたんだ", 133701, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 8878, "グッモーニン！\n朝だよ少年、今日も\n張り切っていこう！", 133701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 8879, "クレープの代わりに\nガレットを焼いたんだ\nこの味で少年を\n唸らせてみせよう！", 133701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 8880, "もうこんな時間だ\n遅くならないうちに\n作業を切り上げようか\n少年も早く寝なよ？", 133701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8882, "あえて緊張を解くのも\n戦いには必要だし…\nそうそう…大事大事…", 133701, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8883, "愛着を抱かせる事を\n意図したデザインだね\n心理的効果の観点から\n分析してみようか…", 133701, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8884, "香りは記憶と密接に\n結びつく事がある…\n懐かしい匂いだね…", 133701, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8885, "癒されるな…\n何がこの感情変化を\n引き起こすんだろう？", 133701, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8887, "食の偉大さを感じるな\n戦闘糧食ってやっぱり\n味気ないしなぁ…", 133701, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8888, "実用ばかりじゃなく\n詩や物語ってのも\n人には必要だよね", 133701, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8890, "この場所の開発版も\n探せばあるかもな\nさて、どんなデザイン\nだったのやら…", 133901, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8891, "今まで数々の波乱に\n身を投じたが…やはり\n波乱にはこの姿が一番\n似合いだよ☆", 133901, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8892, "むっ…この角に羽と\n尻尾は家具とぶつかり\nやすいな。兎の時は\n気にならなかったが…", 133901, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8893, "いつもより闘争を\n求める気分になるな\n初心に帰るとは\nこのことか…？", 133901, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8894, "未調整上等☆\nそれを上手く使い\nこなしてこその\nワタシだろう？", 133901, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8895, "坊やとできそうな\n愉しいことは…\nふむ、多すぎて迷うな", 133901, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8896, "時折、坊やの居心地の\nよさに絆されそうに\nなってしまうな\nワタシらしくもない…", 133901, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8897, "どうだ、おろした\n髪型も艶やかだろう？\nそんじょそこらの\n賞賛では満足せんぞ☆", 133901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8898, "外に出るぞ！\n共に退屈を殺しに\n行こうじゃないか☆", 133901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8899, "角と翼も触りたい？\nワタシの虚数魔境に\n挑むというなら\n止めはせんよ☆", 133901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8900, "なんだ、困りごとか？\nクリスティーナさんが\n何でも聞いてやろう☆\n解決するかは別だがな", 133901, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8901, "力が欲しいか？\nならば呼ぶがいい！\nワタシの名を☆", 133901, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8902, "悪魔との契約は絶対\nなのだそうだが…\nどうだ、坊や\n一つ契ってみるか？", 133901, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 8903, "まだ眠い？　甘えるな\n寝首を掻かれたく\nなければ飛び起きて\nこい…☆", 133901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 8904, "昼食の準備を\nしてやろう。\n毒見はいらんぞ？\n安心するといい！", 133901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 8905, "坊やは寝る時間だ\n寝ないのならば…\n大人の時間に\n付き合ってもらうか☆", 133901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8906, "角と翼、尻尾の\nコンディションまで\n完璧だな☆", 133901, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8907, "なかなかのフワモコ\n具合だが…ううむ…\n翼と尻尾が邪魔だな", 133901, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8908, "うーむ、角と尻尾を\n追加して…さらに槍を\n持たせた方が見栄えが\nよさそうだ", 133901, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8909, "いい香りだな\n血と砂塵の臭いで\n上書きするまでの\n間に合わせに最適だ☆", 133901, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8910, "生温いな\nもっと焦がれるほどの\n熱量が欲しいところだ", 133901, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8911, "普段はないものが\n生えているのだから、\n運動でしっかり感覚を\n掴んでおかなければな", 133901, 3, 6);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8912, "腹が減っては戦は…\nと言うからな\nしっかり食べて\nおくとしようか☆", 133901, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8913, "『家庭を築くための\n絶対条件』…？\nふむ…興味がある\nタイトルだ", 133901, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8915, "奇妙な感覚です\nまるであの頃の自分に\n変装しているかのよう", 133801, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8916, "グレゴール？\nおっと、そうでした\n今は昔の姿になって\n離れていましたね", 133801, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8917, "このアバターでいると\n妙に子どもたちからの\n視線を感じますね…", 133801, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8918, "リソースの割り振りが\n変わっただけなのに\n随分と身体が軽く\n感じますね", 133801, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8919, "普段グレゴールに\n任せている分身の管理\n自分でやるには\nいささか煩雑ですね…", 133801, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8920, "長老に頼まれて描いた\n妖精の絵…いったい\n何に使ったのでしょう", 133801, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8921, "私自身をモデルにした\n完全なるNPCたち\nそれが私の分身です", 133801, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8922, "このアバターが\n気になるのですか？\n貴方はつくづく\n妖精に縁がありますね", 133801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8923, "ちょうどよかった\nグレゴールを見かけ\nませんでしたか？", 133801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8924, "あの翅は飾りでは\nありません。ただ…\nグレゴールに乗って\n飛ぶ方が楽なだけです", 133801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8925, "妖精とは本来神話に\nおけるトリックスター\n運命をかき回すことが\n存在意義なのです", 133801, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8926, "変貌大妃の権能との\n相性を考えなければ\nこの姿こそが私たちの\n最強の姿です", 133801, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8927, "変化とは一種の喪失\nだからこそ得られる物\nがある…貴方のそれも\n大切にするのですよ", 133801, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 8928, "マサキからの定時連絡\nが来ませんね…心配は\n不要ですが、様子を\n見に行きましょうか", 133801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 8929, "昼食の時間ですね\nグレゴールのために\nカロリーの高い物を\n食べに行きましょう", 133801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 8930, "夜更かしはダメですよ\n悪いスパイが暗躍して\nいる時間帯ですからね", 133801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8932, "ふむ、座り心地は中々\nですが…私にはやはり\n玉座の方があいますね", 133801, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8933, "ふふ、いいでしょう\n我が配下に加えます\n存分に働きなさい", 133801, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8934, "時折晶からしていた\nあの甘い匂いは何の\nアロマだったのか…", 133801, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8935, "燃え盛る炎もこうして\n暖炉の中なら安全です\nクリスもあの衝動を\n管理さえできれば…", 133801, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8937, "そういえば真那が\n獄中で手持無沙汰に\nひたすらおにぎりを\n食べていましたね…", 133801, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8938, "これは、妖精学の本…\n長老の蔵書から借りて\n読みふけったものです", 133801, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8940, "どんな詩を\n作ろうかしら…？\n…大体、暗く\nなっちゃうけど…", 134301, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8941, "唱喚術の練習…\nここでやったら\n迷惑よね…", 134301, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8942, "チカちゃんとの\nデュエット…\n足をひっぱってない…\nかしら…？", 134301, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8943, "唱喚士の修行…\n滝に打たれるとか…\n崖を登るとか…\nなくて、よかった…", 134301, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8944, "精霊さん、肉じゃが\n食べる、かしら…？\n魔力だけだと…\n飽きちゃいそうだし…", 134301, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8945, "精霊さんのお世話に\n慣れたぶん…\nあのひとのお世話も\n上手にできる…はず？", 134301, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8946, "これからも、歌うわ…\nこの声が、彼の力に\nなる、限り──", 134301, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8947, "この服…踊り子さんに\n間違われちゃって…\nあ…お、お触りしちゃ\nだめよ…？", 134301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8948, "あなたはどんな\n精霊が…好き…？\n唱喚…してみたいわ…", 134301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8949, "あんまり白い服って…\n着ないの…その…\nこ、ころんだりして…\n汚すから…", 134301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8950, "じ、実はね…\nこの体の模様…筆で\n書いてもらうたび…\nくすぐったい…の…", 134301, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8951, "精霊さん…あなたと\n遊びたいみたい…\nや、やっぱり…\nパパ…だから…？", 134301, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8952, "あなたを歌にしたら…\n…ふふっ、だめね\n一曲じゃ…あなたを\n表現できない、わ…", 134301, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 8953, "朝の修行は…\nうとうと…し…ちゃ…\n……（かくん）\n……あぅっ？", 134301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 8954, "せ、精霊さんと…\nかくれんぼ中なの…\nうぅ…見つからない…\n日が暮れちゃいそう…", 134301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 8955, "精霊さん、寝たから…\nふふっ…ここからは\nパパとふたりの時間…\n…ね？", 134301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8957, "ふぅ…修行と育児は…\nちょっとだけ…\n休憩…ふぁ…", 134301, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8958, "あなたとも契約…\n…できる、かしら？\nふふっ…", 134301, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8959, "いい香りに包まれたら\n精神統一も\nはかどりそう…♪", 134301, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8960, "あたたかい場所…\nここで作詞したら…\n穏やかな詩が\n書ける、かも…", 134301, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8962, "精霊さんのお世話を\nしていると…\nお腹、空いちゃって…", 134301, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8963, "ふふっ…♪\n色んな楽譜…\n集めるの、楽しい…♪", 134301, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 8965, "けんぞくまだかな？\nはやく会いたいなー\nヴァンピィちゃん\nずっと待ってるからね", 122301, 1, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (12, 1, 0, 0, 8966, "けんぞくのこと\nずっとずっと守るよ！\nだからいつまでも\n一緒にいてね", 122301, 2, 103);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8967, "私の歌や想いを\n誰かに託せる…\nこれほどうれしい\nことはありません", 134401, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8968, "コッコロさんの\n包容力はすごいです\nあんなふうに私も\n人を包み込めたら…", 134401, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8969, "最近のボイトレは\nランファさんと一緒に\nやるんです。お互いに\n切磋琢磨しますよ！", 134401, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8970, "先輩として相応しい\n立ち振る舞いを\n心がけたいですね\nしっかりしなきゃ…！", 134401, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8971, "ランファさんが\n踊ったら映えるかも…\nアイドルにも興味を\n持ってもらえないかな", 134401, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8972, "支え導くために\n寄り添って考える…\nあの人からたくさん\n教わったことでしたね", 134401, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8973, "いつだってあなたに\n恥じることのない\n姿をお見せしたいです", 134401, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8974, "あなたの手は温かくて\n触れていると\n落ち着きますね…♪", 134401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8975, "里のみんなから修行の\n差し入れを頂きました\nよろしければ、一緒に\n食べましょう♪", 134401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8976, "ノゾミにもお土産を\n用意したいですね\n里のお土産選び、\n付き合ってください♪", 134401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8977, "精霊たちも、あなたが\n大好きみたいです♪\n優しさが伝わって\nいるんでしょうね", 134401, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8978, "特訓が終わったら、\n二人でどこかに\n出かけましょうか♪", 134401, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8979, "ひゃ…す、すみません\n肌の模様を\nなぞられると、\nくすぐったくて…", 134401, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 8980, "おはようございます\n精霊たちと一緒に、\n今日も頑張ります\n応援してくださいね♪", 134401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 8981, "お昼の時間です\n今日も精いっぱい\n修行を頑張りましょう", 134401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 8982, "もうすっかり夜ですね\n眠るまで子守唄を\n歌ってあげますよ？\nふふ♪", 134401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8983, "無理せず休憩するのも\n大切ですよね\nふかふか～…ふふっ♪", 134401, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8984, "今日はこの子に\n歌の特訓を聞いて\nもらいましょう♪", 134401, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8985, "素敵な香り…\nふふ、精霊たちも\n気に入ったみたいです", 134401, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8986, "あったかい…\nこの衣装、やっぱり\n少し冷えやすいものね", 134401, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8987, "特訓の後のご飯は\n美味しいですね♪\nこれでまた頑張れます", 134401, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8988, "唱喚士についての\n書籍はないみたい\nいつか私がここに\n楽譜を入れたいな", 134401, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8992, "ギルドハウスの平和も\n任せてください\n何が来たって\n守ってみせますよ！", 134601, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8993, "このギルドハウスも\nいつの間にか出撃台に\n改造されてたり\nするんでしょうか…？", 134601, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8994, "戦いを経験してこそ\n実感できますね\n平和はいいもの、\n友達は素晴らしいもの", 134601, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8995, "次はランドソルで\n巨大ロボットが\nブームになったり？\n…さすがにないか", 134601, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8996, "改めて見るとこの服…\nか、体の線とか\n結構目立ってるかも…", 134601, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 8997, "団長さんって\n知らない人とも\n話せる系ですよね\nコツはいったい…", 134601, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 8998, "ＢＢ団、世界を救う！\nまだ信じられないけど\n本当にあった出来事\nなんですよね…！", 134601, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 8999, "パイロットも\n視力が大事なんです\n弓使いの経験が\n生きました！", 134601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9000, "ギガンティック\nマイフレンドくん、\n出撃！\nあ、ただの妄想です", 134601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9001, "コクピットから\n降りたあとに残る\nふわふわ感…ちょっと\n癖になりませんか？", 134601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9002, "ひえっ、敵襲！？\nあっすみません、\nちょっと\n寝ぼけてたかも…", 134601, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9003, "共に戦場を駆け抜け\nＢＢ団の絆もより強く\nこれからも結束を\n強めましょうね！", 134601, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9004, "同じコクピットに\n乗ったのに比べれば、\nスキンシップくらい…\nや、やっぱ緊張します", 134601, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 9005, "おはようございます！\n今日もがんばりますっ\n…クローチェさんを\n見習ってみました…", 134601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 9006, "お昼ご飯の時間ですね\nがんばって他の人\n誘ってみようかな…", 134601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 9007, "夜の見張りは\n任せてください\n一人きりの夜も\n慣れてますから！", 134601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9009, "休めるときに\n休んでおかなくちゃ\nいつ何が起こるか\n分かりませんからね", 134601, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9010, "師匠もこういうの\n好きだったのかな…\nなんだかしんみり\nしちゃいます", 134601, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9011, "香りでリラックス…\n余裕のない時でも\nこういう安らぎは\n大切ですよね", 134601, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9012, "あったかい…\nこれはきっと友情と\n同じぬくもり…", 134601, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9014, "新鮮なご飯、感動…！\n師匠たちの世界だと\nご飯は保存食ばかり\nでしたからね…", 134601, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9015, "操縦マニュアル、\nしっかり読んで\n性能を引き出さなきゃ", 134601, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9017, "屋内で飛べなくても\nここなら平気ですっ\n助けてくれる人が\nたくさんいますからっ", 134501, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9018, "新ボディになってから\n人や物が前より身近に\n感じられて、わたし、\n幸せですっ♪", 134501, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9019, "配達以外にも何か\nお仕事したいですっ！\nハグで遊覧飛行とか、\n喜んでもらえますか？", 134501, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9020, "むむむ～…\n文字を書くのって\n難しいですね～っ\n練習しなくちゃっ！", 134501, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9021, "簡易点検、よしっ！\nポポおじいさんに\n点検の仕方を教わって\n今日も絶好調ですっ", 134501, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9022, "明日は北の空、\n明後日は南の空っ♪\nえへへっ、見せたい\n景色がいっぱいですっ", 134501, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9023, "ついナイトさんを\n探しちゃいます…\nわたし、寂しんぼさん\nだったんでしょうかっ", 134501, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9024, "どんなところでも\nひとっ飛びっ♪\nわたしに任せて\nくださいねっ！", 134501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9025, "まだ知らない機能が\nエアリアルには\n隠されてるかもっ…\nワクワクしますねっ！", 134501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9026, "ひゃんっ！　…あはは\nエアリアルになっても\nケーブルに触られると\nくすぐったいですねっ", 134501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9027, "ヴォルガーノさんに\nそっくりの子猫さんに\n最近触れたんですっ！\nかわいかったな～っ♪", 134501, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9028, "ハグしてくれるん\nですかっ？\nえへへっ、ぎゅーっ♪", 134501, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9029, "ナイトさんと一緒に\nいるだけで、胸が\nぽかぽか～って\nあったかいですっ♪", 134501, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 9030, "おはようございますっ\n目覚まし飛行は\nいかがでしょうかっ？", 134501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 9031, "お昼ですよっ！\nいっぱい補給して\nまたフルパワーで\nがんばりましょうっ♪", 134501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 9032, "眠れない夜はわたしに\n任せてくださいっ！\n充填さえあればずっと\n一緒にいられますよっ", 134501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9034, "歩きづらいので\nこういう休憩ポイント\nって助かります～っ", 134501, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9035, "スラスターを付けたら\nこの子たちも空を\n自由に飛べたり\nするんでしょうかっ？", 134501, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9036, "あっ、これに似た\n香りを近くのお店で\n検知したことが\nありますっ♪", 134501, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9037, "高熱源反応を検知っ\nわたしのスラスターも\n熱量の高さなら\nかなりものですよっ♪", 134501, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9039, "もぐもぐもぐ…っ\n食べたぶんはあとで\n真空パックにして\nお返ししますねっ", 134501, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9040, "本を読むのって\n楽しいですねっ♪\n前はびりびりーって\nしちゃってたのでっ", 134501, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9042, "なかなかの住処ですね\nこの私の別邸にして\nあげてもいいですよ♪", 134701, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9043, "はぁ…いつになったら\nすべての神力が\n戻るのでしょう？", 134701, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9044, "ここも悪くないですが\n昔私が作った庭園は、\nもっと良い場所でした\n焼けちゃいましたが…", 134701, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9045, "うう…\nおなかが空きました…\nお米が食べたいです…", 134701, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9046, "ここは平和ですねえ…\nジオ・ゲヘナも\nこのくらい穏やかなら\n住みやすいのですが", 134701, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9047, "騎士さんのおかげで\n神力が戻ってきました\n良い働きですよ♪", 134701, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9048, "騎士さんのいる場所が\n今の私がいるべき場所\nなのかもしれませんね", 134701, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9049, "頭がたかーい！\n…ですよっ！", 134701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9050, "ほーちゃんと呼ぶ権利\n安売りはしませんよ？\n特別ですから！", 134701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9051, "私とおしゃべりを？\nまあ、いいでしょう♪\n何を話しましょうか？\nこの羽の秘密とか？", 134701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9052, "小鳥扱いは\nやめなさーい！\nこれでも神様ですよ？", 134701, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9053, "き、気安く触るのは…\n…い、いえ、\n騎士さんなら…まぁ…", 134701, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9054, "絢爛豪華な絆！\nこの私と結べるなんて\n光栄でしょう？", 134701, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 9055, "ふわぁぁ～\nおはようごじゃいます\n…んむむ", 134701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 9056, "お昼ご飯は何を…\nえっ？　私？\nそそんな゛あ゛！", 134701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 9057, "あの…騎士さん？\n添い寝する権利とか…\nほしくないですか？", 134701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9059, "はぁ～ふかふか…\n…へ？\n鳥の巣にいるみたい？", 134701, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9060, "ふむ…\nそばに置いてあげても\nいいでしょう", 134701, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9061, "ほわぁ～\nいいかおり～", 134701, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9062, "あたたかい…\nなんだか元気が\nでてきますねっ", 134701, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9064, "たくさん食べて力を\n取り戻さなくてはっ\n…んがぐぐっ", 134701, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9065, "ふむふむ…\n人の子にしては\nよく書けていますね♪", 134701, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9067, "素敵なお家ですね\n温かみがあって\nどこか騎士様に\n似ております。ふふ", 133101, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9068, "賑やかな街ですね\nこれほど活気豊かだと\nつい外に出てみたく\nなってしまいます", 133101, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9069, "別世界の存在は知って\nおりましたが、いざ\n目の当たりにすると\n驚きの連続ですわ", 133101, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9070, "この街は様々な種族が\nなにはばかることなく\n混在しているのですね\n…我々も、いつかは…", 133101, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9071, "ランドソルにも香水の\nお店があるのですね\nどんな香りが揃って\nいるのでしょうか…♪", 133101, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9072, "昨日、素敵な花園を\n見つけたのです\n今日のお茶会は\nそちらでしましょうか", 133101, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9073, "気を抜いてはいけない\nとわかっていても、\nここだとつい安心して\nしまいます…ふう…", 133101, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9074, "ごきげんよう\n街で見つけた茶葉を\n試しているんです\n騎士様も一杯どうぞ♪", 133101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9075, "親切な方ばかりですね\n素敵なお友だちが\nできそうな予感で、\nワクワクしてます…♪", 133101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9076, "黄泉の食卓の皆様用に\nお土産を用意しないと\n騎士様のお知恵も\n頼りにしておりますわ", 133101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9077, "ジオ・ニヴルヘルと\n比べると、この世界は\n本当に明るいですね\n少し眩しいくらいです", 133101, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9078, "あまり、血の巡りが\n良くなくて…\n騎士様の手は\nとても温かいですね", 133101, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9079, "今度はわたくしに\nジオ・ニヴルヘルを\n案内させてくださいね\n観光案内のお返しです", 133101, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 9080, "おはようございます\n朝の眩しい太陽には\nなかなか慣れません…", 133101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 9081, "騎士様、お茶は\nいかがでしょうか？\n一度一休みして、\n英気を養いましょう", 133101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 9082, "眠れないのでしょうか\nまぶたが重くなるまで\nわたくしをお話し\n相手になさって下さい", 133101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9084, "思わず気が抜けそうに\nなってしまいます…\nふふ、ふわふわ…♪", 133101, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9085, "まあ、かわいらしい\nグレイスもきっと\n気に入りそうな\nデザインですわ", 133101, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9086, "まあ、いい香り…\n心安らぐようですわ\n素敵なセンスを\nお持ちなのですね♪", 133101, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9087, "燃え移りや乾燥がある\nから近づかないほうが\nいいとわかりつつも…\nああ、温かい…♪", 133101, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9089, "食が細いというよりは\n消化があまりうまく\nできなくて…本当は\nまだ頂きたいのですが", 133101, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9090, "『薔薇の下の契り』？\nまあ、男性同士で…！\nそんな…こ、これ以上\nここで読むわけには！", 133101, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9092, "ダーリンが帰ってくる\n前に部屋の掃除しよ♪\n…ふふ、これってもう\n新婚生活だよね…♪", 132301, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9093, "家具のセンスまで\n私と同じだなんて\n運命って怖いわ…♪", 132301, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9094, "あの人はベッドの下に\n何か隠してるのかな…\nな、なんて全っ然\n気にならないし…っ！", 132301, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9095, "うっかりして\n角引っかけないように\nしないとね…", 132301, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9096, "錬金術を使わなくても\nあなたの心を鷲掴む\n私は恋も錬金術も\n失敗しないんだから！", 132301, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9097, "あの人って破廉恥なの\n興味あるのかしら…？\n基本破廉恥禁止だけど\nあの人だけは…許す…", 132301, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9098, "手狭になれば錬金術で\n新しい部屋作るから\nいつ家族が増えても\n安心だよね…♪", 132301, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9099, "錬金術でカレーライス\n作っちゃおっかな♪\n具材を釜でグツグツ…\nま、料理とも言うわね", 132301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9100, "錬金術に興味がある？\nじゃあさっそく私が\n手取り足取り教えるわ\nって…やだ破廉恥っ！", 132301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9101, "イロハちゃんとの\n虫捕りは命懸けよ！\n一万匹捕れるまで\n帰れないんだから…", 132301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9102, "ソノさんって\n体は小さいけど\nとっても頼れる私の\nお姉さん的存在なの♪", 132301, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9103, "はあ…部屋中に広がる\nダーリンの香り…♪\n…ってノゾミみたいに\nなっちゃってる！？", 132301, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9104, "鹿の獣人族の特徴は\nこのエッチな角…\n違っ！　叡智よ叡智！\n噛んだだけだからっ！", 132301, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 9105, "あ、おはよう！\n寝る前に飲んだ\n目覚めすっきり秘薬の\n効果はバッチリかな？", 132301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 9106, "午後からも頑張る\nあなたのために\n愛妻弁当作ったの♪\n一緒に食べよ？", 132301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 9107, "今日も一日お疲れさま\n眠れないなら隣で…\nじゃなかった早寝して\n明日も頑張ろうね！", 132301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9109, "いい錬成をするには\n適度な休憩も必要よね\n…気持ち良くてたまに\n寝落ちしちゃうけど…", 132301, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9110, "かわいいぬいぐるみ\n錬成じゃなくて\n手編みで作ったら\n驚いてくれるかも♪", 132301, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9111, "くん、くん…\nん～いい匂い♪\nノゾミの香水と\nいい勝負だわ", 132301, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9112, "ああ、暖かい\nでもソノさんの作った\nミートパイの方が\nもっと温かいわ…♪", 132301, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9114, "もぐもぐ…私も\n食べても減らない秘薬\n錬成して他の食べ物も\n作れないかな？", 132301, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9115, "ふむふむ…\nこのレシピ面白いわね\n今度錬成してみよっと", 132301, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9117, "おー、体が軽い\n腰痛も胃もたれも\nないし、最高かよ", 132501, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9118, "いっつも力入れてたら\nいざって時困るし\nやっぱ省エネだな", 132501, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9119, "やば…なんか急に\nブドウ朱飲みたい…\nルイちゃんに頼むか？\nいや、でもなあ…", 132501, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9120, "あれ、あの材料の在庫\nまだあったっけ…\nルイちゃんに確認しよ", 132501, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9121, "イロハのやつ変な\nイタズラしてない\nといいけど…", 132501, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9122, "今日のシフトは～…\nお、彼と一緒じゃん\nふふっ…", 132501, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9123, "イロハのやつ\n不用意に私の体で\n彼に抱きつくなって\nこっちが恥ずかしい…", 132501, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9124, "今日も元気だねえ\n若いってうらやま…\nっていや、今は私の\n方が若いじゃん", 132501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9125, "ん～？　どしたん\nヒマならうちの店で\nバイトしてく？", 132501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9126, "イロハ見なかった？\n今日は手伝うって約束\nしてたのにさあ…", 132501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9127, "ルイちゃん特製\nエナドリ（子ども用）\nこれ飲んで今日も\nお仕事頑張るかー", 132501, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9128, "元の体なら\n君と目線、合うのにね\n…ってドラマかよ\nごめん、今の忘れて", 132501, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9129, "なんかヒマそうじゃん\n用事ないなら\nお姉さんがコーヒー\n淹れたげよっか", 132501, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 9130, "ふわあ…おはよ\n今日もお仕事\nがんばりますかー", 132501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 9131, "ちょうどいいや\n一緒にお昼どうよ\nお姉さんが\n奢ってあげよう", 132501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 9132, "ふぁぁ…ふぅ\nここからは大人の時間\nなんだけどさぁ\n眠くてかなわん…", 132501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9134, "おお～なんか前より\nでっかく感じるわ～\nてそりゃそうか", 132501, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9135, "そいえば、イロハって\nあんまこういうの\n欲しがらなかったな～\nクワガタばっかで", 132501, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9136, "はあ…いい匂いだね\nさすがルイちゃん特製\n錬金アロマ\n次の目玉商品だな", 132501, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9137, "ユカリっち焚火の前で\nキメる麦しゅわに\nハマってるらしいけど\n興味あるわぁ…", 132501, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9139, "うわこれ…\n無限にいけちゃうわ\n子どもの胃袋\n恐るべし…", 132501, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9140, "ふむ、こんな需要が…\n次の新商品として\nルイちゃんに試作\n頼んでみるか～", 132501, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9142, "服はその人の個性です\n仕立て屋は\nその手伝いをする\nプロなんです♪", 134801, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9143, "糸を紡いで紡いで\n巻き巻き～♪", 134801, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9144, "ふむ…あの人の衣装\nなかなか斬新ですね\n後でどうなってるのか\n見せてもらおう♪", 134801, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9145, "動きやすさと\nファッション性\n両立させてみせますよ", 134801, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9146, "今度のコレクション\n新作にワイルドな\nルームウェアは\nどうかな…？", 134801, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9147, "騎士さんもたまには\n衣装の感想とか言って\nくれてもいいのにな～", 134801, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9148, "ノゾミさんとチカさん\nにも、おそろいの服を\nプレゼントしてみよう\n二人とも似合いそう♪", 134801, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9149, "ジオ・ゲヘナでまた\n素材収集したいんです\n手伝ってくれますか？", 134801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9150, "えっ？　薄着すぎ？\nおしゃれのためなら\nこのくらいへっちゃら\nですよ…っくしゅん！", 134801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9151, "穴が開いてる？\nこれはそーいう\nデザインですー！", 134801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9152, "ちょっとどこ\n触ってるんですかー！\n…あ、糸くず\n取ってくれたんですね", 134801, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9153, "騎士さんに\n新しい生地の服を…\nいや、シャレじゃ\nないですよ？", 134801, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9154, "素敵な着こなしには\n健康な体も大切！\n騎士さんに見られても\n恥ずかしくないように", 134801, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 9155, "おはようございます！\nさあ、着替えて！\n私が見繕ってあげます", 134801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 9156, "お昼は激辛料理です\nジオ・ゲヘナで\nハマッちゃって…\n一緒にどうですか？", 134801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 9157, "睡眠不足は美容の大敵\nていうかシンプルに…\nね、眠い…", 134801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9159, "くつろげますね～\nでももうちょっと\n生地の手触りが…\nって、職業病？", 134801, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9160, "かわいいものって\n心を癒してくれますね\n服もそうありたいです", 134801, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9161, "いい香り～♪\nこの匂いにはどんな服\nが合うかなあ…？", 134801, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9162, "この服露出が多いから\n温かいとほっとする～\nずっとこうして\nいたいかも…", 134801, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9164, "いくらでも\n食べられちゃうから、\n自分でストップ\nかけないと…", 134801, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9165, "しっかり勉強して\nジオ・ゲヘナに\nお洒落を伝えたいです", 134801, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9167, "あたくしは高貴なる\nマンティコア！\nサソリでもコウモリ\nでもありませんわよ♪", 134901, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9168, "なかなか居心地がいい\nお部屋ですわね～\n…さてどうやって\nあたくしのものに…", 134901, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9169, "高貴なるお審美眼で\nあの家具の価値を\n測ってやりますわ\n…ギリ合格ですわ！", 134901, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9170, "高貴なる笑い声は\n一日にしてならず\nお子様の頃から練習を\n重ねてきたのですわ！", 134901, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9171, "お美しいあたくし様と\n同じ空間にいられる…\nどうぞその喜びを\n噛みしめなさいな♪", 134901, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9172, "アナタがお土下座して\n頼むなら、あたくしも\nもう少しここに残って\n構いませんわよ♪", 134901, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9173, "馬ボーンをあたくしに\n振り向かせるには\nどうしたら…って、\nこの言い方は語弊が！", 134901, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9174, "少し寒いですわね…\n…この服装のせい？\nこっちだって好きで\n着てねぇですわよ！", 134901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9175, "あのラミアが来たら\nあたくし様はお用事で\nお留守って言って\n追い返すんですのよ！", 134901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9176, "こっちの世界にも\n闘技場がありますの？\nそこもお頂点を極めて\nしまおうかしら～♪", 134901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9177, "馬ボーンって\n人畜無害そうなお面の\nわりに、時々やること\nえげつねぇですわね…", 134901, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9178, "ねぇ馬ボーン…\nアナタあの魔札、\nまだ持ってまして？\nとっとと捨てなさい！", 134901, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9179, "絶対にあたくし様の\nお尻尾に触るんじゃ\nねぇですわよ…？\nいえ、振りではなく！", 134901, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 9180, "ふわぁ…\n打倒ネフィのお作戦を\n夜通し考え過ぎて、\n寝不足ですわぁ…", 134901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 9181, "もうお昼ですの？\nあたくし様に相応しい\n優雅なおランチと\n参りますわよ～♪", 134901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 9182, "あら馬ボーンったら\nまだ起きてますの？\n…ぐぬぬ、夜の間に\n仕掛ける作戦が…", 134901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9184, "このおフカフカ感…♪\n剣闘士宿舎の\nゴワゴワなお寝床とは\n大違いですわね！", 134901, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9185, "なんですの、この\n弱っちそうなお顔は…\nおかわいそうですし、\nお僕にしてあげますわ", 134901, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9186, "馬ボーンはこういう\n匂いが好みですの？\nふーん…ほーう…\nなるほどですわ…♪", 134901, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9187, "あぁ～～…\nそうそう、これくらい\n暑い方が快適ですわ…", 134901, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9189, "もぐもぐ、もぐっ…\nまぁそこそこ？\n悪くないお味では\nありますわねっ", 134901, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9190, "こんな本よりも、\n華麗なるカノン様の\n活躍録でも置いた方が\n世のためになりますわ", 134901, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9192, "さっきねー、外で猫と\n木登り競争したんだよ\nなかなか手ごわい\n相手だった！", 132401, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9193, "いっぱい食べて\n大きくなるんだ～\nあっでもこれ\nソノちゃんの体か", 132401, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9194, "強いおねーさんの\n気配を感じる…\nどこから？\n…そこら中から！", 132401, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9195, "虫捕り網の手入れは\nいつもバッチリ！\nいつ何が出るか\nわからないもんね", 132401, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9196, "うちの中で走り回ると\nソノちゃんに\n怒られちゃうけど…\nここならいける？", 132401, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9197, "おじちゃんも\nお部屋にいろいろ\n隠してるのかな？\nルイちゃんみたいに", 132401, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9198, "世界一になったら\nおじちゃんにも\nいっぱい褒めて\nほしいな…約束っ♪", 132401, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9199, "高いとこのものが\n取りたいときは\nイロハに言ってね\nイロハ得意だから！", 132401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9200, "どうも、イロハです\n剣士たるもの、\nあいさつは大事って\n教わったんだ♪", 132401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9201, "イロハと勝負だね！\n受けて立つよ～！\n…あれ、違った？", 132401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9202, "あっ！　ルイちゃんに\n素材集め頼まれてたの\n忘れてた…！　やばい\nおじちゃん手伝って！", 132401, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9203, "実はイロハって\n褒めたら伸びる\nタイプなんだ\nだからよろしくね♪", 132401, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9204, "このお部屋、すごく\n居心地がいいんだ～\nやっぱりおじちゃんが\nいるからかな？", 132401, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 9205, "昨日仕掛けた罠で\n虫捕れてるかな？\nあとで一緒に\n見に行こうね♪", 132401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 9206, "もうお昼だよね？\nイロハのお腹が\n空いてきたから\n間違いないよ！", 132401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 9207, "ふわぁ～…\nあ、あくびじゃないよ\nイロハ大人なんだから\n眠くなんか…ふわぁ…", 132401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9209, "ふわふわしたものって\nつい飛び込みたく\nなっちゃうよね\n…うずうずっ", 132401, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9210, "かわいいのもいいけど\nカッコよさも欲しいな\nそーだ、角とかもっと\nいっぱい付けてみる？", 132401, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9211, "おー、いい匂い♪\nこれもしかして\nルイちゃんの新作？\n爆売れ間違いなしだよ", 132401, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9212, "ぬくぬくぽかぽか、\n気持ちいいー…\nでもちょっと\n暑くなってきたな", 132401, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9214, "もぐもぐもぐ…\nこれはいい食材を\n使っておりますなぁ\nうん、三ツ星！", 132401, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9215, "ふむふむなるほど…\nこっ、これは…！\n…ごめん、適当に\n言ってみた", 132401, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9217, "また海に行く時に\n備えて…次の計画を\n練る必要がある…", 135001, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9218, "騒がしいのも…\n以前ほど気にならない\n慣れてしまった…？", 135001, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9219, "眩暈がする暑さも\n騎士クンと同じ気温を\n感じられるなら…いい", 135001, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9220, "騎士クンに\n好意を抱く者は\nみんな有罪\n…わたしは例外", 135001, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9221, "体も心も…外の世界に\n馴染んできた気がする\n少しずつ、だけど…", 135001, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9222, "騎士クンの手…\n大きかった…\n…また、つなぎたいな", 135001, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9223, "夏が終わっても…\nその先の季節も\n…その次の夏も\n騎士クンと一緒に…", 135001, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9224, "騎士クンといるとね\n渇いてた心が…\n潤される、気がするの", 135001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9225, "騎士クンは…\nしたいこと、ある？\n夏にだけ…できること\n一緒に、しようね", 135001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9226, "仮想領域の夢でも\n現実の海でも…\n騎士クンと一緒なら\nそこが本物の夏、だね", 135001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9227, "肝試しがしたい…？\n…うまく驚けるかな\n練習しなくちゃ…", 135001, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9228, "騎士クンとなら\nどんな夏でも…\nきっと楽しい…♪", 135001, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9229, "水着…どうかしら…？\n…よかった\nシミュレーションの\n成果ね。ふふ", 135001, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 9230, "騎士クン…眠たそう\nもうちょっと…寝る？\n後で起こしてあげるね", 135001, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 9231, "お出かけするなら\n水筒を用意するね…\n夏の水分補給は大事…", 135001, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 9232, "また…夜の海を\n見に行きたいな\n騎士クンと一緒に\n連れていってくれる？", 135001, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9234, "騎士クンを待つには\nちょうどいい…\n…落ち着く…", 135001, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9235, "こういう物を\n城に置いた方が\nかわいいって思って\nもらえる…かな…？", 135001, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9236, "夏に合わせた香りね…\n涼やかな風が胸を\n吹き抜けるような…", 135001, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9237, "火は落ち着く…\n身体感覚を味わえる\n作り物の…この体でも", 135001, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9239, "『食』…騎士クンと\n話す共通の話題には\n悪くなさそう…", 135001, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9240, "『好きな人と楽しめる\n夏デート特集』…？\n……ふむ…", 135001, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9242, "わたしの趣味では\nないけれど…\n騎士クンが望むなら\n城に置いても…？", 135001, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9243, "む…悪くない\nこれなら\n騎士クンと一緒に…？", 135001, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9244, "一日中水着でいるって\n浮かれているみたいね\n夏は短いから、沢山\n楽しまなくちゃね", 135101, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9245, "ちょっと暑いし\n氷の上を滑って\n涼もうかしら", 135101, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9246, "そろそろ運送ギルドの\nアルバイトの時間だわ\n準備しなきゃ", 135101, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9247, "エリスさん、水着が\nすごく似合ってた…\nどこで買ったのかしら", 135101, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9248, "未練が残らないように\nこの夏休みでやりたい\nことはやらなくちゃ", 135101, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9249, "かき氷を作ったら\n彼も食べるかしら？\nアイスクリームの\nほうがいいかな…", 135101, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9250, "水着を着ていれば\nどこにいても\nリゾート気分だわ♪", 135101, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9251, "夏はおいしいものが\nたくさんあるし\n【美食殿】の活動も\n忙しくなりそうね♪", 135101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9252, "わ、私の尻尾…\nそんなに気になる？\nつついたらダメよ\nいいわね…？", 135101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9253, "夏は日が長いし\nまだまだたくさん\n遊べそうね♪", 135101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9254, "今日は一緒に海に\n行かない？\nあなたも水着\n持ってきてるでしょ", 135101, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9255, "そろそろ日焼け止めを\n塗り直さなきゃ…あの\nお願いしてもいい…？", 135101, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9256, "私たちで最高の\n夏休みにしましょう\nね、おにーたん♪", 135101, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 9257, "夏の朝は早いわね\nもうセミも鳴いてるし\nあなたも早起きだわ…\n見習わなくちゃ", 135101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 9258, "日中は暑いだろうし\nゆっくりしましょう\nお昼寝タイムはどう？", 135101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 9259, "この時間のアイス\nクリームは格別よね♪\n何味にしようかしら", 135101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9261, "暑い夏はしっかり\n休むことが大切\nふぅ…いい気持ち♪", 135101, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9262, "ふわふわの毛並み\n暑くないのかしら\nかわいいだけじゃなく\n忍耐強くもあるのね", 135101, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9263, "爽やかでいい匂い…♪\nジトジトしがちの夏に\nピッタリなアロマね", 135101, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9264, "ずっと水着でいると\n流石に冷えるわね\nうん、いい心地だわ", 135101, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9266, "はむっはむっ…\nしっかり食べて\n夏バテ対策しないと", 135101, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9267, "ふむふむ…面白いわね\n今度プレシアちゃんと\n一緒に読もうかしら", 135101, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9269, "私のフロートと少し\n似ている気がする\nサイズ的にはこっちが\nお兄さんかしら…ふふ", 135101, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9270, "ふふ、きれい♪\nまるで\n海のステージね！", 135101, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9271, "じゅんび、おっけー\nうみでも\nおふろでも\nおんせんでも", 135201, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9272, "ひまわり。すき\nおっきくて\nたね、うまし", 135201, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9273, "ハッピーちゃん\nいい、におい\nひやけ？\nやきぶた？", 135201, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9274, "じーわ、じーわ…\nせみの、なきごえ\nきいてる、だけで\nあつい。かも…", 135201, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9275, "アイス、かきごおり…\nあつい、なつ\nだから\nひえひえ、ひっす", 135201, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9276, "おっきくなるまほー\nきわめたい\nお兄を\nだっこ。するよ", 135201, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9277, "「あなた」\n…ちょっと、ちがう？\nシェフィたち\nみたいに、ならない…", 135201, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9278, "じゃりじゃり\nべたべた\n…シャワー\nいって、きます", 135201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9279, "（すんすん）\nお兄。ほんのり\nうみの、におい\nおいしそう", 135201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9280, "お兄。みて\nべろ、まっさお\nぶるーはわい\nカラー", 135201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9281, "あつくても\nあついもの\nおいしい\n…バーベキュー…", 135201, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9282, "ついんて\nバランス、むずかし\nお兄\nリボン、よろ", 135201, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9283, "みずぎ\nにあってる。って\nお兄。いった\nもっと。みて？", 135201, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 9284, "おはよ\nあさごはん\nやきざかな", 135201, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 9285, "おひる\nおひるね\nいっしょに、しよ？", 135201, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 9286, "おそと\nまっくら\nはなび\nしよ？", 135201, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9288, "ふわぁ…\nあそびすぎ\nおつかれ\nねむねむ", 135201, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9289, "よしよし\nおるすばん\nおねがい。ね", 135201, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9290, "（くんくん）\nれもんの、におい\nじゅる", 135201, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9291, "さかな、くしやき\nすたんばい、おっけー\nいつでも、やける。よ", 135201, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9293, "いっぱい、あそびたい\nいっぱい、つよくなる\nいっぱい、たべる", 135201, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9294, "おさかなの、ほん\nおもしろかった\nでも\nおなかが…", 135201, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9296, "れっつ、ごー\nすいへーせん\nせかい\nいっしゅー", 135201, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9297, "きぶんは、かいばしら\nバターやき\nおすすめ", 135201, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9298, "成長して着れなく\nなっちゃう前に\nこの水着でできること\n全部しちゃいたいな♪", 135301, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9299, "お家で水着だと\n開放的な気分かも♪\nふふ、お兄さんにも\n教えてあげなきゃ♪", 135301, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9300, "泳ぎの練習しちゃお♪\nすいすい、すいすいー\n…ちょっと、\n子供っぽかったかも…", 135301, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9301, "インドア派だけど\n夏の屋外は好き…や…\nお兄さんと一緒なら\nどこでも…", 135301, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9302, "夏だからってちょっと\n羽目外しすぎかな…\nわたし…やっぱり\n真面目じゃない…かも", 135301, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9303, "わたしの水着姿…\n少しは大人っぽいって\n思ってくれたかな…？", 135301, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9304, "家の中は涼しいのに\nお兄さんのこと\n考えると暑くなって…\n夏バテしちゃいそう…", 135301, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9305, "お兄さんと一緒なら\n夏休みの日記\n書くことが多すぎて\nページが足りません♪", 135301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9306, "かき氷食べましょ♪\n…ふふ、お兄さん\n舌の色すごいですよ♪\nわたしも…べ～っ♪", 135301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9307, "今日は海に行きます？\nそれともお家で\nのんびりですか？\nわたしはどっちでも♪", 135301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9308, "夏バテしないように\nスタミナ料理のレシピ\nお勉強しました♪", 135301, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9309, "夏が終わっちゃっても\nこの水着を着てたら\n夏の思い出、忘れずに\nいてくれますか…？", 135301, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9310, "…この日焼け跡\nお兄さんと\nわたしだけの\n秘密…ですよ？", 135301, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 9311, "おはようございます！\n朝の体操１、２、３♪\nしゃきっと目覚めて\nいい一日にしましょ♪", 135301, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 9312, "お昼休憩ですね♪\n午後からも頑張れる様\nお兄さんのこと\n癒しますね…♪", 135301, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 9313, "そろそろ寝る時間…\nお兄さんが眠るまで\n一緒にいますね…\n…ふあぁ…", 135301, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9315, "気持ちいいけど、汗…\n染みちゃわないように\n気をつけないと…", 135301, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9316, "ぎゅう～♪\nなんて、ちょっと\nあざといかな…♪", 135301, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9317, "夏にぴったりの\nフルーティーな香り♪\nお兄さん、好きかな？", 135301, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9318, "水着で暖まるのって\nなんだか贅沢な気分♪\nこたつでアイス的な♪", 135301, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9320, "お腹ぽっこりに\nならないよう\n気をつけないと…\nでも止まらない…", 135301, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9321, "弾ける水着でビーチの\n視線を独り占め…かぁ\nわたしはお兄さんの\n視線だけで…なんて♪", 135301, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9323, "これで夏の大冒険に\nしゅっぱ～つ！\n…なんちゃって♪", 135301, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9324, "なんだか女神さま\nみたい…？\nふふ、わたしには\n似合わないかもです♪", 135301, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9325, "真っ白い日差し─\nジオ・ゲヘナには\nなかったものですね\n─悪くはないです", 135401, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9326, "しばらくここで\n休んでます\n人の多い街中よりは\n落ち着くので─", 135401, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9327, "ここにも─\nあるんでしょうか\n海で使う遊び道具…", 135401, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9328, "見張りは─\nここならあまり\n必要なさそう…", 135401, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9329, "夏のバカンス─\n媛が楽しんでるなら\n私は十分です", 135401, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9330, "知らない相手は、\n苦手だけど─\n…少しだけ、\n話してみても…", 135401, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9331, "ここにいると\n安心する、ような…\n土壌が─良い…？", 135401, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9332, "夏の正装、水着─\n着方、間違えて\nないですよね…？", 135401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9333, "相変わらず、\nおしゃべりが\n好きなんですね\n私は─普通です", 135401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9334, "─喉が渇きました\n…いえ、花にお水が\n欲しいわけじゃなくて", 135401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9335, "─いつもより\n花がつやつやしてる？\n…あ、褒めて\nくれたんですか", 135401, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9336, "この部屋─\n貴方が飾りつけを？\nわかります、何となく", 135401, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9337, "すぐ傍にいると、\n暑くなりませんか？\n─私は別に\nかまいませんが", 135401, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 9338, "朝から元気ですね─\nおかげですっかり\n目が覚めました", 135401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 9339, "外─暑そうです\n出かけるなら\n気を付けてください\n倒れたりしないよう…", 135401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 9340, "─貴方も\n夜更かしですか？\n…少しだけなら\n付き合いますよ", 135401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9342, "貴方もここで\n休みたいですか？\n─ダメです\n今は私が使うので", 135401, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9343, "ゆるい顔つきですね\nジャングルでは\n生き残れそうに\nないです─", 135401, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9344, "─ん…\nこれ、海の香り？\n─いい匂い…", 135401, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9345, "…暑さには\n慣れてますよ\n寒いのよりは、ずっと", 135401, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9347, "栄養は、大事です\n味は─あまり\n気にしたこと、\nありませんでした", 135401, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9348, "適当に本を読んでいる\nだけです─\n…別に、遊びについて\n調べたりしてません", 135401, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9350, "魔物を模した\n人形─ですね\n戦闘訓練用に？", 135401, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9351, "貝のクッションですか\n─噛みついてきたり\nしないですよね", 135401, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9352, "シントーメッキャク\n火もまた涼し…\nむむむ…やっぱり夏は\n暑いデース！", 137101, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9353, "海にも新しい忍法の\nヒントがあるはずデス\nショーグンと海底散歩\nも楽しそうデス", 137101, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9354, "釣りの心得は\nメイキョーシスイ…\n忍者の修行に\nうってつけデス♪", 137101, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9355, "天下統一の後のことも\n考えねばなりまセン\n今度はテンジョー統一\nデスかね…？", 137101, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9356, "デケデケデン♪\nテルテル坊主完成デス\n晴れてないとお外で\n遊べまセンからね！", 137101, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9357, "次のオモテナシも\nショーグンをワクワク\nさせマス♪　今から\n用意しておかねばデス", 137101, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9358, "新しい忍法の開発に\n休みはないのデス！\nショーグンの天下統一\nを思えばなんのその！", 137101, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9359, "海の前に準備運動デス\nまずは手を後ろに、\n素早く忍者走り！\nシュバババッ", 137101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9360, "夏にお祭りは\n欠かせないデスよね♪\n地域のイベント情報は\n見逃しまセン！", 137101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9361, "ショーグン、団扇で\nあおいであげマス！\n忍法・扇風の術ー！\nうりゃりゃりゃー！", 137101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9362, "ワタシが必要な時は\n風鈴を鳴らすデス！\n風よりも早く\n駆けつけマスよ！", 137101, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9363, "じっと見つめられると\n顔が熱くなってきて…\nはっ、ショーグンの\n新しい忍法デスか！？", 137101, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9364, "ショーグン、日焼けは\nノン、デスよ！\nワタシがクリームぬり\nぬりしてあげマース！", 137101, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 9365, "おはようデス！\n朝ごはんはワタシに\nお任せデス。\n毎日みそ汁作るデス！", 137101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 9366, "日差しに注意デスよ\nネッチューショーに\n日焼け、どっちも\n大変デスからね！", 137101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 9367, "ショーグン、\n外で花火しまショウ！\nぱちぱちキレイで\n楽しいデスよ！", 137101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9369, "遊びに備えて\n今は休む時…\nガシンショータンデス", 137101, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9370, "この子たちにも\n手伝ってもらって\n新しい忍法を開発\nしてみまショウ♪", 137101, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9371, "むむっ、ここまで\n潮の香りが漂って\nきた気がしマス！", 137101, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9372, "海に入った後は\n体を温めねば\nやはりサウナも\n必要デスね！", 137101, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9374, "いっぱい食べて\nいっぱい遊ぶデス！\n夏バテなんかに\n負けてられまセン！", 137101, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9375, "『BUSHIDO』の\n最新刊が待ち遠しい\nデス…マイゾーキン\n特集、読みたいデス", 137101, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9377, "ショーグンとこの子に\n乗って、天下統一まで\nシュババババッ！\nなんてどうでショウ", 137101, 3, 10);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9378, "ぽよんぽよ～ん♪\nショーグンと一緒に\n遊んでみたいデス♪", 137101, 3, 11);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9379, "今度はシナツちゃんの\n世界へ行ってみたいな\nエリスちゃんも誘って\n大人女子旅とか最高♪", 135501, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9380, "あっはっはー！\nよし、今日も絶好調！\nま、いつも笑ってたら\n不調なんてないけど♪", 135501, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9381, "ん～あっちの世界と\nほとんど一緒だね！\n違ってるところ探しに\n出かけてみよっかな？", 135501, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9382, "ううーん、のんびり\nするのもいいけど\nどこか行ってみたいな\n人助けとか、ね", 135501, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9383, "あっ、そーだ\n向こうへ帰る前に\nこっちのお土産とか\n買っとかなくちゃ", 135501, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9384, "ふ～ん、騎士くんって\nこういうのが好みか～\n後でヒヨリちゃんに\n教えてあげよ♪", 135501, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9385, "次はあたしが恩を返す\n番だね。騎士くんに\n助けが必要になったら\n絶対駆けつけるよ！", 135501, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9386, "あっ騎士くん！\n困ってることない？\nお姉さんがなんでも\n力になっちゃうよ！", 135501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9387, "また闘技場に行こうよ\nちょっと運動不足でさ\nバーンと暴れたい気分\nなんだよね♪", 135501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9388, "いつか大人になったら\n一緒に飲もうよ！\nあははっ、騎士くん\n指切りげんまん♪", 135501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9389, "一緒に作った麦しゅわ\nカップ、最高だね♪\n騎士くんの分もどうぞ\nちゃんとジュースだよ", 135501, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9390, "ねえ…ぶっちゃけ\nヒヨリちゃんのこと\nどう思ってるのかな？\nお姉さんに聞かせなよ", 135501, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9391, "騎士くん、うっかり\n飲みすぎたあの日の\nコトは誰にも内緒で\nお願いね。約束だよ！", 135501, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 9392, "あはは、騎士くん\n新しい朝だよー！\n今日はどんな一日か\nワクワクするね～♪", 135501, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 9393, "午後からも笑顔で\n元気に乗り切ろうね！\n外に行くもよし、\n部屋で麦しゅわもよし", 135501, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 9394, "ここからは大人の時間\n騎士くんにはまだ少し\n早いかな～？\nだから、おやすみ♪", 135501, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9396, "麦しゅわ飲み過ぎた\n夜はここにダーイブ\n…なんて大人に\nなっちゃダメだよ～？", 135501, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9397, "あたしの世界の子たち\nこういうの好きかな？\nお土産に探してみよう\n気に入るかも♪", 135501, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9398, "ん～いいね♪\nあとでお気に入りの\nフレーバーも\n試してみよっかな？", 135501, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9399, "ほうほう…熱い！\nサラサリアは\nいつも暖かいから\nこういうの新鮮！", 135501, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9401, "ん～おいしい！\nけどやっぱご飯は\nみんなで一緒に\n食べたいな～", 135501, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9402, "文字ばっかの本は\nちょーっと苦手かも…\n必要なら読むけどさ？", 135501, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9406, "皆思い思いに過ごして\nいるのに、不思議と\n落ち着く。いつまでも\nここにいられたら…", 135601, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9407, "皆に挨拶をせねば\n…いや、今は、まだ\nタイミングが悪い…\nまた今度にしよう…", 135601, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9408, "私の服装…この場で\n浮いてないだろうか\nまずい、心配に\nなってきた…！", 135601, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9409, "アイラたちとの旅行も\n楽しかったな…\nまた３人で温泉に\nいきたいものだ", 135601, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9410, "ここで剣の稽古は\nさすがによろしくない\n代わりに今日は瞑想を\nすることにしよう", 135601, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9411, "人とのつながりは\n大切にしなければ\n今日こそは誰かに\n話しかけるぞ…！", 135601, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9412, "できることが増えて、\n迷うばかりの毎日…\nそれが楽しく尊いと\n今実感している…", 135601, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9413, "ええと…奇遇だな\nこんなところで\n会うなんて。いや違う\n…その、こんにちは…", 135601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9415, "弟子たちへの土産も\n探しておかないとな\nよければキミからも\nアドバイスを頼む…", 135601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9416, "剣の稽古をしたい？\nふっ…いいとも\nキミとならいくらでも\n準備をしてすぐ戻る", 135601, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9417, "よければまた、\n街を案内してくれ\n名所旧跡もだがキミと\n歩くのは楽しいんだ", 135601, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9418, "感謝の心を\n忘れたことはないよ\nキミたちの優しさに\n救われた一人として…", 135601, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 9419, "うむ、いい顔だな\n良い一日は、\n良い目覚めから始まる\n心掛けておくといい", 135601, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 9420, "もう昼時か\n誰かを誘って食事に…\n…いや無理だ、\nハードルが高い…！", 135601, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 9421, "夜はいい…\n誰とも話さず\n一人きりでいても、\n不自然ではないからな", 135601, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9423, "…いかんな、これは\n気を抜くとこのまま\n離れられなくなる…", 135601, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9424, "シナツだ、よろしく\n……ふぅ\n人形相手の練習なら\n緊張しないのだがな…", 135601, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9425, "ふむ…集中力が\n高まりそうな香りだな\n稽古にも役立つだろう\n皆にも教えてやろうか", 135601, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9426, "凍える人に寄り添い\n温もりを与える…\n私もそんな存在に\nなりたいものだ", 135601, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9428, "食の楽しみ…\nあまり意識して\nいなかったが、\nやはりいいものだな", 135601, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9429, "…っと、いかん\n読みふけっていたら\nいくらでも時間を\n過ごせてしまうぞ…", 135601, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9433, "また空を飛びたいな～\nふわふわ～ってなる\nあの感じが気持ちいい\nんだよね～♪", 135701, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9434, "またサラサリアに\n行きたいなー\n…串焼きの味が\n忘れられなくって♪", 135701, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9435, "悩んでる人がいたら\n背中を押してあげたい\nあたしがそれで、勇気\nづけられたみたいに", 135701, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9436, "気持ちを言葉にするの\n大事って分かってても\nいざとなると緊張して\n上手くできないなぁ…", 135701, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9437, "あっちの世界でも\nギルドハウスは\nあるのかな…？\n今度探してみよっと♪", 135701, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9438, "アイラに会って少し\n成長できたかも…！\n素敵な女の人目指して\nもっと頑張るぞーっ♪", 135701, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9439, "あっちのあたしは\n騎士くんに選ばれた…\nよし、あたしも\n頑張らなくっちゃ…！", 135701, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9440, "部屋の模様替えする？\nサラサリア風の家具も\nあったら素敵だよね！", 135701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9441, "なんだかお疲れ気味？\nそうだっ、サラサリア\nで教わったマッサージ\nをしてあげるっ♪", 135701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9442, "騎士くんっ！\n今からあたしとデート\nしませんかっ！", 135701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9443, "今ちょうど、踊りの\n練習してたとこなの\n騎士くんも\n一緒にどう？", 135701, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9444, "き、騎士くん！\nこの前の続き…\n手、繋いでいい…？", 135701, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9445, "えへへへ、騎士くんが\nあたしの背中を押して\nくれると、勇気も\n100万倍だよっ♪", 135701, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 9446, "おはよ、騎士くん！\n今日もいい朝だよ！\nサラサリアみたいに\n太陽が輝いてるもん！", 135701, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 9447, "フレフレ、騎士くん！\n午後もファイトー！\nあたしは日陰でお昼寝\nタイム…なんてねっ♪", 135701, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 9448, "夜に騎士くんといると\n空飛ぶ絨毯から見た\nサラサリアの夜景を\n思い出しちゃうな♪", 135701, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9450, "休憩きゅうけ～い♪\n強い人と戦った後は\nしっかりと体を\n休ませないとね", 135701, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9451, "にゃ～、\nかわいいにゃ～♪\nアンブラを\n思い出しちゃうよ♪", 135701, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9452, "わっ…すごくいい\n匂い～サラサリアの\n砂漠の匂いにちょっと\n似てるかも", 135701, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9453, "サラサリアの太陽\nみたいに暖かい♪\n日向ぼっこしてる\nみたいでしあわせ…", 135701, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9455, "ん～おいしいっ！\nアイラもきっと\n好きな味だね…！\n今度食べてもらおっ！", 135701, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9456, "これは恋愛指南本…！\nふむ…意中の男性と\nキスするには…って\nこれはまだ早いよ～！", 135701, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9460, "あてがここにいたら、\nみんな寒い思いを\nしちまうかな…\nご、ごめんな", 135801, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9461, "あて、みんなより\n体でっけぇし…\nもうちょっと隅っこ\n行っとこうかな", 135801, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9462, "さ、騒がしいとこは\n苦手だども…\nここはそんなに\n気になんね…", 135801, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9463, "いろいろ勉強してぇな\nイナカもんだから、\nみんなに迷惑とか\nかけねぇように…", 135801, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9464, "家具とか、飾りとか、\n壊しちまったら大変…\nき、気ぃつけねぇとっ", 135801, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9465, "あ、あての三味線、\n聴きたいの…？\nこ、心の準備が\nできてからで、い…？", 135801, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9466, "っ、ぁぶね…\nあてったらまた、\n騎士さのこと見て、\n溶けそうになってた…", 135801, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9467, "お、おめさ、その…\nおやつさん、いらね？\nかき氷とかなら、\nあて、作れるよ…？", 135801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9468, "あ、あてに…何か用？\n…声、聴きたかった？\nな、なら…えっと…\nおしゃべり…する？", 135801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9469, "こっちさ来てから…\n向こうの世界の\n何年分も、おしゃべり\nしたかもしんね…", 135801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9470, "こっちの世界には、\n氷滑りって遊びが\nあるって…？\nあてにもできっかな…", 135801, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9471, "ん…あては元気だよ\n悩みがあっても、\nおめさの声もらうと\n吹っ飛んじまうし…", 135801, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9472, "騎士さが近くに\nいてくれる…\n今は、それだけで\nいいんだ…うん…", 135801, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 9473, "お、おはよ…\nおめさのおかげで、\n昨夜もよく眠れたよ…", 135801, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 9474, "…あっ、もう昼飯の\n時間かぁ…\n考えごとしてたら\nあっという間だよ", 135801, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 9475, "景色は似てるけど…\nこの世界の夜は、\nざわざわ音がして\n賑やかだな…", 135801, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9477, "あんま縮こまってると\n体硬くなっちまうし…\nたまにはのんびり…", 135801, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9478, "えへへ、めんこいな…\nそだ、仁太坊たちも\n並んでたら\nもっとめんこいかも…", 135801, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9479, "この匂い…なんか、\nは、はいから…？\nあて、イナカもん\nだから…", 135801, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9480, "あ、暑くて溶けそう…\nだども、我慢できたら\n修行になる…かも…？", 135801, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9482, "ん…♪　うめぇ…\nこんなうめぇもん\n毎日食べられるの、\n幸せだなぁ…", 135801, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9483, "こっちの世界にも…\n雪女が出る物語、\nあるみてぇだな…\nか、悲しい話だども…", 135801, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9487, "なかなか上等な\n家じゃねェか\nちっとばかし\n軒を借りるぜ", 135901, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9488, "俺の棒っきれの\n置き場所は…\nま、玄関にでも\n立てかけときゃいいか", 135901, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9489, "こっちの世界は\n眩しくって敵わねェぜ\n目がチカチカしやがる", 135901, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9490, "建物の様式が同じでも\n世界と住人が違えば\n印象も変わるモンだな", 135901, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9491, "街の連中は\n戦いなんて知らねェ\nのんきなツラしてんな\n…いいトコじゃねェか", 135901, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9492, "騎士公のヤツ、\nまたぞろ厄介事に\n巻き込まれや\nしてねェだろうな…", 135901, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9493, "ふわぁ…\nこうも居心地がいいと\n眠くなっちまうぜ", 135901, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9494, "俺ばっかり\n見てんじゃねェよ\n赤鬼になっちまわぁ", 135901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9495, "注いでくれんのか？\nあんがとよ\nんじゃ俺からも\nお返しだぜ", 135901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9496, "おう、暇してるなら\n鍛錬に付き合えよ\nじっとしてっと\n体がなまって仕方ねェ", 135901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9497, "退屈してきたな…\n散歩にでも行くか？\n騎士公なら退屈しねェ\n場所知ってるだろ", 135901, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9498, "女との関係は\nきちっとしとけよ\n俺だって助けらんねぇ\nもんはあるからな", 135901, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9499, "角に触りたいだぁ？\n…まあ、騎士公の\n頼みなら…\nあんま手荒にすんなよ", 135901, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 9500, "起きろ\n朝の鍛錬の時間だぜ\nほれ、さっさと\n身支度しちまいな", 135901, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 9501, "昼飯ィ？\n俺の分はいらねぇよ\n気ィつかわねぇで\nくんな", 135901, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 9502, "まだ起きてんのか？\nさっさと寝ねェと\nこうやって鬼が来るぜ", 135901, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9504, "悪くない座り心地だな\n気ィ抜くと、ダメに\nされちまいそうだぜ", 135901, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9505, "…今こいつ動いたか？\nグレ公あたりが\n悪さしてんじゃ\nねェだろうな", 135901, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9506, "騎士公はこういう\n香りが好みか？\nお姫とユキノに\n教えてやるとすっか", 135901, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9507, "地獄の釜よりかは\nちとぬるいが…\nなかなか\nいい火力してやがるぜ", 135901, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9510, "こっちの世界の本にも\n鬼が出てくるのか", 135901, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9514, "フランシュシュ１号、\nサクラです！　サガの\n心を、歌でみんなに\n届けにきました！", 136101, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9515, "ランドソルはドキドキ\nいっぱいの街やけん！\nサガとどっちが\n暮らしやすかろうねっ", 136101, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9516, "ソロ活動がんばって、\n憧れの子にもも～っと\n近づくと！　成長した\n私、早く見せたか～！", 136101, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9517, "部屋におるとは、伝説\nのメンバーばかり…！\n私もこのランドソルで\n伝説ば刻むとよ！", 136101, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9518, "あそこの修繕が気に\nなると…。…あのっ！\nあ、ついあの子を\n呼びそうに…えへへっ", 136101, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9519, "アイドルのレッスン、\n気になると？　まず、\n脚をぐい～っと広げて\n…あわわ、戻らん！？", 136101, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9520, "騎士さんのコネが\nあれば、いつか堂々と\nゾンビアイドルで\n売り出せるかもっ…！", 136101, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9521, "おはようござい\nまーす！　挨拶は基本\nやけん、元気よくっ！\nさ、一緒にやろう！", 136101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9522, "身だしなみチェック、\nよしっ。今日もいい\n天気っ♪　さあ、\nいってきまーーす！！", 136101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9523, "あんまり引っ張ると、\nう、腕が～…！　あ、\n気にせんで。伸びても\nすぐ元に戻るけんっ♪", 136101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9524, "私、持っとらんって\n思ってきたけど、それ\nが何ね！　友達ば、\nいっぱい持っとるけん", 136101, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9525, "嚙めば噛むほど味が\n出る…騎士さんは\nサガのアタリメ\nみたいな人やねっ♪", 136101, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9526, "騎士さんにだけ教え\nますね。私の秘密…\n私の死因を。え？\nもう分かる気もする？", 136101, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 9527, "ぼ～っとしとかれんね\n「ぶっ殺すぞ」って\n気合入れてくれる子が\n両方の街におるけんっ", 136101, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 9528, "あのすごか食べっぷり\nよう思い出すとよ\n元気かな…変なもん\n食べてないかな…", 136101, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 9529, "夜、出歩くと、怖～い\nアンデッドに襲われる\nかもしれんよ！\n気をつけんと…！", 136101, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9531, "リラックス！　もっと\nがんばってリラックス\nせんといかんね！\nむむむ～っ！", 136101, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9532, "が～ばや～らしか～♪\nあの子のお土産に\nしたいけど、同じの\n売っとらんかな…？", 136101, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9533, "ん～、いい香りっ♪\nいつもこやん素敵な\n香りのする人が、私の\n友達におってね…", 136101, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9534, "え～と…熱いとその…\n傷んじゃいそうで、\nちょっと心配やけん…\nそ…その…私の体が", 136101, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9536, "わあ、美味しか～っ！\nランドソルのグルメも\nなかなか手強いっ…！\nサガも負けられんね～", 136101, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9537, "アイドル雑誌はあると\nですかね？　フラン\nシュシュのランキング\nは…しょぼぼぼーん…", 136101, 3, 8);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9541, "名探偵から聖職者へ、\n華麗なる変装だ！\nいや、心の中まで\n敬虔に…変貌、かな♪", 137401, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9542, "真実の鏡で、誰の謎も\n解いてみせるよ。\n恋の謎は…私が解いて\nほしいくらいだ…", 137401, 1, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9543, "歩き方も…厳かに…\n気品と高潔さが\n感じられるように\n練習しなくてはね", 137401, 1, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9544, "私の行いはきっと、\nどこかで見られている\nかつてお別れをした\n大切なあの子にも…ね", 137401, 1, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9545, "異議あり！　…う～ん\nもっと威厳をもって…\n異議あり！　はっ、\n聞いていたのかいっ！", 137401, 1, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9546, "静かに心を見つめれば\n大切なものが分かる\n助手くんとの時間の\nかけがえなさも、ね", 137401, 1, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9547, "私も、想い合う二人の\n神聖な誓約の儀と\nいうものに憧れるよ\nつまり結婚式、にね…", 137401, 1, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9548, "珈琲を一杯お願い\nするよ、助手くん\nこれがないと私の\n推理が冴えなくてね", 137401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9549, "懺悔があるなら聞き\n届けよう。…え～っ！\n棚にある私のチョコを\n食べてしまったって！", 137401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9550, "助手くん、今日の\n依頼は何件かな\nもし誰も来ないなら\n一緒にお出かけしよう", 137401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9551, "さあ、出かけようか\n君と私で巡礼の旅だ\n鞄の中身はただ一つ\n真実を愛する心、さ！", 137401, 2, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (4, 1, 0, 0, 9552, "一緒に祈ってくれる\nかい？　ありがとう\n一人では真実が重荷に\nなるときもあってね", 137401, 2, 101);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (8, 1, 0, 0, 9553, "何でも話してほしい\n分かち合いたいんだ\n喜びも悲しみも、君が\n背負う、なにもかも", 137401, 2, 102);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 1, 0, 9554, "おはよう！　聖職者は\n早起きなのさ♪\nふわぁ…はっ、今の\nあくびは見てないね？", 137401, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 2, 0, 9555, "今日のお昼は…\n一緒に台所で\n作らないかい？\n新婚さんみたいに♪", 137401, 2, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 3, 0, 9556, "夜のお祈りをしよう\n凶悪な事件なんて、\nもう起きないように", 137401, 2, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9558, "はあ…ふかふか～…\n堕落してしまいそう…\nなんとも罪なアイテム\nだねえ、これは…", 137401, 3, 2);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9559, "かわいいな…♪\n探偵事務所の机に\n置いたら場を和ませて\nくれそうだね", 137401, 3, 3);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9560, "はあ…心が落ち着く\nけして、その、小説の\n謎解きにイライラして\nいたわけではないよ？", 137401, 3, 4);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9561, "…暖かいね。こんな\n暖かさをあまねく\n世に届けるのが、\n今の私の役目だよ", 137401, 3, 5);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9563, "こんなにおいしい\nなんて、きっと重大な\n秘密が隠されているに\n違いないよね！", 137401, 3, 7);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 1, 0, 0, 9564, "時には推理小説でなく\n教えを説く本もいいね\nちょっとだけ…眠く\nなっちゃうけど…", 137401, 3, 8);
