INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (100, 5, "ペコリーヌのプリン", "やばいですね☆なの！", 1, "プリンセスなプリンなの\nお店にあったいろんな食べもの\nを入れられるだけ入れてみたの\nどんな味なのか楽しみなの", 3, 1, "おいしすぎて食べ始めたら\n止まらない最高のプリンなの\n今のミヤコを止められるヤツは\n誰もいないの");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (101, 5, "コッコロのプリン", "主さま、なの！", 1, "いつもオマエのそばに寄りそう\nヤツのぷりんなの。ミヤコの\nワガママもやさしく受け入れて\nくれそうなの", 3, 2, "なんだかすごくやさしい気持ち\nになる味なの…\nおはようからおやすみまで\nずっと一緒な味なの…");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (102, 5, "キャルのプリン", "ぶっ殺すぞ！なの！\n…デレデレはどこなの？", 0, "ツンツンデレデレなヤツの\nプリンなの。砂糖とスパイスを\n混ぜてみたの。どんな味になる\nか楽しみなの", 3, 3, "いろんな味が一度で楽しめる\nプリンなの。ツンデレはプリン\nにするとおいしいの。ミヤコも\nツンデレを目指してみるの");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (103, 5, "シェフィのプリン", "おにーたん♪なの！", 1, "オマエと同じで、一度赤ちゃん\nみたいになったヤツのプリン\nなの。ヒンヤリ、プリンアイス\nにしてみたの", 3, 4, "あったかいところで食べる\nプリンアイスは最高なの～♪\n安心しすぎて昔の記憶のない\nミヤコも心が子どもになるの～");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (104, 5, "イリヤのプリン", "ハーハッハッハ！\n世界征服なのじゃなのー！", 1, "イリヤを見てたら思いついた\nトマトジュースが隠し味の\nプリンなの。栄養満点なの", 3, 5, "トマトで栄養補給も完璧なの\nどんどん力がみなぎってきて\n復活も夢じゃないの\nミヤコが復活したら…");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (105, 5, "アカリのプリン", "お兄ちゃんのエッチ！　なの！", 1, "アカリのプリンなの。ヨリと\n同じくらいかわいく作って\nやったの。正直ミヤコは\nおいしければなんでもいいの", 3, 6, "見た目がかわいくてもミヤコは\n味にも厳しいの。あむっ…\nこ、これは…！なんだかすごく\n変な気分になってきたの…");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (106, 5, "ヨリのプリン", "えっと…その…あんたのこと…\nだ、大っ嫌いなのーー！", 1, "かわいくつくらないとアカリが\n怒りそうだからヨリのプリンは\nかわいく作ったの。自信作なの", 3, 7, "んむぅ…これはなかなか感想が\n難しいプリンなの…率直に言い\nたいような…心のうちに秘めて\nおきたいような…");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (107, 5, "シノブのプリン", "お父さん…\nいいかげんにしてなの！\nん？プリンの未来は…\n見えなかったの…", 0, "シノブはいつもミヤコに\nやさしくしてくれるから\n丁寧に作ったの。ドクロ親父は\nいらないから作ってないの", 3, 8, "このプリンを食べるとシノブが\nミヤコの中にいるみたいなの！\nシノブの力があれば未来の\nプリンを見放題なの！");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (108, 5, "ジュンのプリン", "インフェルノシールド！なの！", 1, "黒い鎧を脱がないヤツのプリン\nなの。固めのプリンを目指したら\nカチカチになりすぎたの", 3, 9, "固すぎてかじれないから丸吞み\nにしたの。なんだか体が硬く\nなった気がするの。みんなを\n守れる気がするの！");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (109, 5, "トモのプリン", "あなたといると調子が狂うな\n…なの\n翻弄してたのはオマエなの？", 1, "オマエをいっつもからかってる\nヤツのプリンなの\nちょっぴり変わったスパイスで\nからかいを表現したの", 3, 10, "口の中でスパイスがプチプチ\nはじけて新食感なの！\nプチプチがミヤコを\n翻弄するの！");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (110, 5, "マツリのプリン", "タイガーヒーローボンバー！\nなの！", 1, "王宮騎士団にいるちっこいヤツ\nなの。ちっこいけどやるヤツ\nらしいから、濃いめの味付けに\nしてみたの", 3, 11, "ん～見かけによらず濃厚な\nパンチのある味わいが\n口いっぱいに広がるの！");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (111, 5, "クロエのプリン", "ま、いいんじゃん？\nしらんけどなの", 1, "なんかすごくクールなプリンなの\nなんかテンションも少し\n低そうなの。これ\n食べちゃっても大丈夫なの？", 3, 13, "クールな見た目なのにとっても\n優しい味なの！なんだか\n懐かしさを感じるの…");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (112, 5, "チエルのプリン", "ぷりーん★なの！", 1, "きれいにデコれたの！\nカラフルでおいしそうなの\nこれは期待できそうなの", 3, 14, "なんか変わった食感なの…\nちゃる…ちょる…ちぇる？\nちぇるってしてるの\nでもちぇるってなんなの？");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (113, 5, "ユニのプリン", "端的に換言すれば\n青春の味なの？", 1, "見た目は完ぺきなの！\nこれをつくったやつは\nきっと天才なの！\n早く食べたいの！", 3, 15, "味はなんか妙に\n甘酸っぱいの…\n食べたことない味なの\nこの味ってもしかして…");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (114, 5, "サレンのプリン", "幼なじみはあんた…なの？", 1, "オマエから聞いた懐かしい\n味ってヤツを全部入れてみたの\nオマエ記憶がないのに懐かしい\nってどういうことなの？", 3, 16, "ミヤコも幽霊になる前の記憶\nがないのに、なんだか懐かしい\n気持ちになってくるの…");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (115, 5, "スズメのプリン", "ひゃああんなの～！", 1, "たまたま会ったメイドに教えて\nもらった作り方なの。メイドは\nプリンも作れるはずなの。\nなのにこの不安は何なの…？", 3, 17, "おかしいの。このプリンを\n食べてから、３回も転んだの\nミヤコは幽霊だから転んだり\nしないの。なんでなの…？");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (116, 5, "クルミのプリン", "サルキデバレヤ！なの！", 0, "怖がりな子どもが教えてくれた\n小さくてかわいいプリンなの　\n触ってないのにぷるぷる震える\n驚きのやわらかさなの", 3, 18, "甘さのなかに少しのしょっぱさ\nクセになる味なの\nこのプリンはがんばればきっと\nいろんな美味しいになれるの");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (117, 5, "アヤネのプリン", "プリンフルスイングなの～！", 1, "くまのぬいぐるみを見ていたら\nひらめいたの。小さいけど\nしっかりした甘さの元気に\nなれるプリンなの！", 3, 19, "このプリン、食べるとなんだか\nお姉ちゃんになった気分に\nなるの。ピンチのプリンは\nミヤコが守ってやるの！");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (118, 5, "マホのプリン", "みらくるみやりん\nくるりんぱ～☆なの！", 1, "マホマホ王国って国にいる\nお姫様のプリンなの\n魔物も虜にしちゃう\nすてきなプリンなの", 3, 20, "メルヘンパワーがあふれてきて\nいまならミヤコにも魔法が\n使える気がするの！\nほな、いきまひょ～なの！");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (119, 5, "マコトのプリン", "ウルフェンバイトなの！", 1, "カッコいいプリンを目指して\nワイルドなものをガンガン\n入れてみたの。きっと熱い\nプリンができるはずなの", 3, 21, "うおーん！ミヤコなんだか\n強くなった気がするの！\n困ったことがあればミヤコが\nまとめて面倒見てやるの！");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (120, 5, "カオリのプリン", "なんくるないさ～！なの！", 1, "どっかの国でよくとれる\nゴーヤーに似てるやつを\nいれてみたの！ちょっと\n苦いらしいけど…", 3, 22, "一口食べたら元気が\nあふれて止まらないの！\n気分がいいから、一曲\n踊りでも披露してやるの！");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (121, 5, "カスミのプリン", "ふふん。簡単な推理なの", 1, "勘の鋭いやつが好きな食べ物を\nたくさん入れてみたの\n難しいこと考えるのにぴったり\nらしいけど、なんでなの？", 3, 23, "むむむ…なるほどなの\n上質な糖分をふんだんに\n使うことで、脳をより活性化\nできるからなの！");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (122, 5, "マヒルのプリン", "エリザベス～！なの～！", 1, "搾りたての牛乳を\nたっぷり使ったプリンなの～\nマヒルに手伝ってもらって\nミヤコ直々に搾ってきたの！", 3, 24, "ミルクがとっても濃厚で\nほっぺたが落ちちゃうの～♪\nこのプリンからは牧場の…\n自然の息吹を感じるの！");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (123, 5, "シオリのプリン", "私の好きな本…読んで\nもらえたらうれしいです…\nなの", 1, "マヒルからやさしいヤツだって\n聞いたの。やさしさを表現\nするために奥ゆかしい甘さに\n仕上げてみたの", 3, 25, "ほのかな甘さでありながら\nしっかりとした主張も\n感じさせる\n味わい深いプリンなの");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (124, 5, "リマのプリン", "もふもふにしてあげるなの！", 1, "牧場にいるヤツらしいの\n牛とは違うらしいの\nもふもふ気持ちいいらしいから\n食感にこだわって作ったの", 3, 26, "こ、これは…！口の中に入れた\n瞬間にふんわり溶けて\nなくなっちゃうの！\nその食感がやみつきなの！");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (125, 5, "リンのプリン", "めんどくさいな～なの…", 0, "いっつも寝てるやつらしいの\nぐっすり眠れる薬草をたくさん\n入れてみたの。プリンとして\nおいしくなるかはわからないの", 3, 27, "んむぅ…評価が難しい味なの…\nおいしいのはおいしいの…\nん～考えてたら…\n眠くなってきたの…");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (126, 5, "ラビリスタのプリン", "プリンの権能を解放するの～！", 1, "クレープ屋の真っ赤な人に\n教わったプリンなの\n作り方を丁寧に教えてくれて\nとっても優しい人だったの！", 3, 28, "プ…プリンなのにクレープの味\nがするの。味もすごく普通なの\nでも…なんだかプリンの新しい\n扉を開けそうな気がするの…！");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (127, 5, "シズルのプリン", "お姉ちゃんパワー全開！なの！", 0, "お店に落ちてた謎のレシピの\nプリンなの。レシピの表紙には\n『お姉ちゃんの愛情たっぷり』\nって書いてあったの", 3, 29, "とろける食感が絶品なの～♪\n食べるとなんだか幸せな\n気持ちになれるの…これが\n愛情ってやつなの？");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (128, 5, "リノのプリン", "お兄ちゃん、プリンをよこすの！", 1, "リノから作り方を教わったの！\nさすがクレープ屋で働いてる\nだけあって美味しそうなの！", 3, 30, "これで妹力？があがるらしいの。\n妹になればお兄ちゃんに\nプリンをたかり放題なの！\n最高なの～！");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (129, 5, "ヒヨリのプリン", "困った時はお互いさまさま！\nなの！", 1, "プリンのレシピで悩んでたら\n通りすがりのおねーさんが\n一緒に考えてくれたプリンなの\nいいヤツもたまにはいるの～♪", 3, 31, "食べたら元気が湧いてくるの\nだからプリンのレシピのお礼に\nミヤコも人助けを手伝って\nやったの。ミヤコはえらいの！");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (130, 5, "ユイのプリン", "フラワリングヒールなの～♪", 1, "ミヤコががんばって作った\nこだわりのプリンなの～\nプリンは癒しなの。だから優しい\n気持ちで丁寧に作ったの", 3, 32, "はっ…思わずお花畑が見えたの\n身も心も包まれるような\nとっても優しい味がするの\nこれは…まさに癒しなの～♪");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (131, 5, "レイのプリン", "スラッシュテンペスト！なの。", 1, "究極のプリンを作るため\nミヤコは厳しい修行をしたの…\nその結果あって完成した\nクールで完璧なプリンなの！", 3, 33, "確かに完璧なプリンなの\nでも究極のプリンと呼ぶには\nまだまだ修行が必要なの…\nプリン作りは奥が深いの…！");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (132, 5, "アキノのプリン", "おーほっほほほ！なの！", 1, "豪華で高級な\nお嬢様プリンなの\n中には宝石みたいな\n大きいイチゴを入れるの！", 3, 34, "味にも素材にもこだわりが\nあるの。一流は妥協しないの\nもっと豪華に、もっとおいしく\nできるはず…");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (133, 5, "ユカリのプリン", "プリしゅわ最高～！なの！", 1, "外はしっかり、中はふわとろの\nプリンにするの\n泡みたいなしゅわしゅわの\n生クリームも入れるの♪", 3, 35, "プリンは飲み物とも言うの\nぐいっていってみるの\n…ふわしゅわでとろけるの～！\n体に染みわたるの～");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (134, 5, "タマキのプリン", "にゃにゃにゃ！なの！", 1, "餡子を入れて\nオーブンでこんがり焼くの\nたい…焼きプリンなの\n斬新なの！", 3, 36, "このプリンは焼きたてのうちに\n食べるのが一番なの…！\n熱いけどおいしくて\nうずうずしてきたの…！");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (135, 5, "ミフユのプリン", "効率全開！なの！", 0, "栄養バランスを考えて\n木の実をたくさん入れるの\nあれこれ忙しいときでも\n頼りになるプリンなの！", 3, 37, "プリンはいつでも手軽に\n食べられるのも魅力なの\nこれは食べ応えもあるから一つ\n食べればいっぱい動けるの");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (136, 5, "ミサトのプリン", "あらあら～なの～♪", 1, "エルフの森で子どもにもらった\n薬草を使って作ったの\nこんな優しい味のプリンは\n初めてなの…", 3, 38, "このプリンを食べてから\nいい子になった気がするの\n大好きなプリンを泣いてる子に\nあげたの。いい気分なの");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (137, 5, "ハツネのプリン", "スヤスヤ～…なの…", 1, "お昼寝でみた夢の中に\nでてきた不思議なプリンを\n作ってみたの。見てたらまた\n眠くなってきたの…", 3, 39, "あまいプリンが口の中で\nぽんぽん弾ける未知の味なの！\n今なら不思議な力で世界も\n救えそうなの！でも眠くなるの…");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (138, 5, "アオイのプリン", "ぼっちは卒業なの～！", 1, "お店のはしっこに一つだけ\n残ったおもちゃを見て思い\nついたの。他のプリンと一緒に\n置いておくと震えだすの", 3, 40, "知らないうちにはしっこに\n隠れちゃってたの。食べたら\nミヤコ、シノブたちに会いに\nいきたくなってきたの");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (139, 5, "ノゾミのプリン", "リズムあげるのー！", 1, "トップオブアイドルの\nプリンなの。キラキラした\nものを入れて、あつ～い\nオーブンで焼いてみたの", 3, 41, "口の中に華やかな甘さが\n広がる…これがアイドルなの？\n急に歌って踊りたく\nなってきたの！");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (140, 5, "チカのプリン", "風の加護を！なの！", 1, "ミヤコにプリンをくれるすごく\nいいヤツのプリンなの\n精霊に呼びかけながら\n一生懸命かき混ぜたの", 3, 42, "プリンの生地がとっても\nなめらかで最高の舌触りなの！\n精霊、ミヤコのプリンのために…");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (141, 5, "ツムギのプリン", "騎士さんのために\nしたんじゃありません！なの！", 1, "歌って踊って衣装も作れるヤツ\nのプリンなの。このプリンは\nきれいに包装するの。プリンを\n最高の形で着飾らせるの", 3, 43, "包装は食べるまでの手間が\n増えてめんどくさいの。でも\nそのめんどくささもプリンの\n味を引き立てるの");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (142, 5, "クリスティーナのプリン", "宴の始まりなの！", 0, "プリンをくれないならどうでも\nいいヤツなの。でも横で\nいろいろ言ってきたから\nがんばって作ったの", 3, 12, "んむっ！？\nく、口のなかでプリンの\nおいしさが大暴れなの！\n止まらないの！");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (143, 5, "ルカのプリン", "一刀両断、覚悟しな！なの！", 1, "大人な甘さ控えめのプリンを\n作るの。試しにお魚を入れて\nみるの。プリンの可能性は\n無限大なの！", 3, 44, "大人の味なの。今やミヤコは\nみんなの頼れる姐御なの！\n悪さするやつはミヤコが\n許さないの！");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (144, 5, "ナナカのプリン", "きゅぴーん！キタコレ！なの！", 1, "サイカワサイツヨな\n魔法のプリンなの\n中身をカラフルにするの～\n甘いプリンは頭が冴えるの！", 3, 45, "一口食べる度に味が変わって\n魔法みたいなの！\nもしかして全部食べたら\nミヤコも魔法少女になれるの？");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (145, 5, "アンナのプリン", "人呼んで、疾風のミヤコなの！", 1, "小難しい言葉をささやきながら\n作ったら魔力あふれるプリンが\n爆誕したの！でも、不思議と\nイタイタしいのはなぜなの～？", 3, 46, "一口食べたら謎の組織に\n狙われたり、前世の記憶が蘇る\n気がするの～設定盛りすぎなの\nミヤコは、ミヤコは…");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (146, 5, "エリコのプリン", "クスクス…クスクスクス…なの", 1, "甘酸っぱい赤い木の実を\n切り刻んで入れるの\n甘くてちょっぴり刺激的な\nヤンデレのプリンなの", 3, 47, "なんか普通のプリンよりも\nいい匂いがするの～…\nプリンのことがもっともっと\n好きになっちゃうの…");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (147, 5, "ミツキのプリン", "どんなプリンも\nミヤコの隻眼からは\n逃れられないの～！", 1, "健康に気を遣うミヤコは\nプリンにお薬を入れてみたの～\nでもなんだか毒々しいから\n彩りにバラを添えてみたの～", 3, 48, "鼻に抜ける薬のにおいがアレ\nだけど大人っぽくて頭が\nよくなった気がするの。無性に\n実験したくてしかたないの");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (148, 5, "モニカのプリン", "私は子どもではない！なの！", 1, "小さいけどしっかりした\n軍人のプリンなの\nちょっと固めにして\n砂糖はいっぱい入れるの", 3, 49, "ん～王道なプリンは\n甘くて安定したおいしさなの～\n思わず笑顔になっちゃうの\nにこにこなの～♪…はっ！");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (149, 5, "ニノンのプリン", "ショーグン！　ミヤコと一緒に\n天下統一するデースなの！", 1, "オーエド町で手に入れた素材と\nトーゴクに伝わる術をかけて\nプリンを作るの。シュババ！\n忍法隠し味の術なの！", 3, 50, "このプリンを食べてから\n誰かにお仕えしなきゃ\nいけない気がするデースなの\nそれはショ、ショー…");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (150, 5, "クウカのプリン", "…ぐふふ、じゅるり～なの", 0, "オマエが持ってきた手錠を\nヒントに作ったプリンなの\nプリンが動けないように\nカチカチに固めたの", 3, 51, "カチカチに固めたせいで食感が\nプリンのようで\nプリンじゃないの…でもこの\nもどかしさが癖になるの…");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (151, 5, "ユキのプリン", "あぁ、ミヤコはなんて美しいの～", 1, "とっても美しいプリンなの\n美しいものを手当たり次第\n入れたらすごいことになったの", 3, 52, "味も美しい以外の感想が\n出てこないの。ていうかこれを\n食べたミヤコも美しいの！\n鏡、鏡はどこなの！");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (152, 5, "アユミのプリン", "ジー…ってミヤコなにしてるの\nミヤコはストーカーじゃないの！", 0, "知らないうちにお菓子が売れた\n時に思いついたプリンなの。\n作り方もよく覚えてない…\nけど、きっとおいしいの！", 3, 53, "うん、やっぱりおいしく…\nん？あそこにいるのは\nアイツなの。あれ？\n体が勝手にアイツの方に…");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (153, 5, "ミソギのプリン", "にひひ…イタズラの時間なの！", 0, "イタズラ好きの手下と一緒に\n作ったプリンなの。見た目は\nかわいいけど、作り方は\n意外と複雑なの～", 3, 54, "見た目通り甘々で優しい味なの\nでも、レシピを見たら裏面に\n別の作り方が書いてあるの。\nこれだと激辛プリンに…");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (154, 5, "ミミのプリン", "ぴょんぴょこぴょーん、なの♪", 0, "素直でやさしい手下と一緒に\n動物のぬいぐるみをいっぱい\n並べて作ったの", 3, 55, "ん～、ふわふわの甘々なの！\nなんだか楽しくなって\n心も体もるんたったしてくるの");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (155, 5, "キョウカのプリン", "へんたいふしんしゃさんなの！", 0, "幽霊を怖がっている手下と\n一緒に作ったプリンなの。\n小さいクセにきっちりしてて\nとってもかわいいプリンなの", 3, 56, "ひんやりしてて暑い日でも\nおいしく食べられそうなの！\nん？これを買いたい大人が\nいる？それって…");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (156, 5, "スズナのプリン", "ハート狙い撃ち！なの！", 1, "カリスマモデルなプリンなの\nモリモリにデコレーションして\nカリスマ感を演出したの", 3, 57, "見て食べて楽しめる新機軸の\nプリンなの。ミヤコは幽霊\nだけど自分の足でランウェイを\n歩きたくなってきたの！");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (157, 5, "イオのプリン", "授業を始めます♪なの！", 0, "学校の先生をしてるヤツの\nプリンなの。お堅くしようと\nしたら、いつの間にか色っぽく\nなっちゃったの。なんでなの？", 3, 58, "プ、プリンは全部ミヤコのもの\nなのに、なんだかこのプリンは\nいろんな人に教えたく\nなっちゃう不思議な味なの！");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (158, 5, "ミサキのプリン", "立派なレディ、なの！", 1, "少し背伸びしたおませな子を\n表現したプリンなの。あえて\n甘さ控えめなの。カラメルは\n後乗せできるから安心するの", 3, 59, "ミヤコはプリンならどんなもの\nでも食べるの。でもカラメルを\nちょっとだけ足すの。\nちょっとだけなら…");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (159, 5, "ホマレのプリン", "プリンを持ってこないと\nお仕置きだよ～☆なの！", 1, "謎に包まれたプリンができたの\n何か隠してるようなの…\nこれは危険なプリンなの！", 3, 60, "味はすごいゴージャスなの！\nボスの風格があるの！\nビッグなミヤコに\nプリンを捧げるの！");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (160, 5, "カヤのプリン", "たぎってきたの！", 1, "ワイルドな見た目なの！\nこれは期待できそうなの。\nきっと躍動感あふれる味なの！", 3, 61, "最初の一口目が\nすごいインパクトなの！\n無性にケンカしたくなるの！\nプリンをよこせなの！");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (161, 5, "イノリのプリン", "下剋上、やってやるですなの！", 0, "なんか見た目がパッとしないの\nこれほんとに大丈夫なの？\nなんか頼りないの…", 3, 62, "見た目はアレだけど味はおいし\nいの…あと主張もはっきりして\nるの！やる気と自信だけは\n出てきたのうおおおーなの！");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (162, 5, "ムイミのプリン", "どけどけ～！なの～！", 1, "天下の大悪党のプリンなの\nこれまでのジョーシキに\n捉われないプリンを\n作ってみたの～", 3, 63, "むむむ…食べたら\nやる気が湧いてきたの！\nミヤコを邪魔するやつはみんな\nまとめてぶっ飛ばしてやるの～");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (163, 5, "ネネカのプリン", "ミラーミラー！なの！", 1, "いろんなものに変身したり\n分身したりできるヤツの\nプリンなの。型で同じプリンを\nたくさんたくさん作ったの", 3, 64, "いつのまにか作ったの全部\n食べちゃったの。なんで\nプリンは分身しないの？\n無限に増えろなの！");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (164, 5, "クレジッタのプリン", "プリンに忠誠を誓いますわ！\nなの！", 1, "金の亡者のプリンなの\n金にものを言わせて作って\nみたの。おばあちゃんの\nプリンと対極なの", 3, 65, "むぅ…認めたくないけど\nおいしいの…これを何個も\n作れるほどのお金を稼ぐのは\n大変なの…でもおいしいから…");
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (165, 5, "カリンのプリン", "ギルド管理協会へようこそ！\nなの！", 1, "みんなお世話になるギルド\n管理協会職員のプリンなの\nお役所感を出すために手堅く\n王道に作ったの", 3, 66, "手堅い…！手堅いの…！\n隙のないプリンなの！\n今のミヤコなら完璧な\n書類作成ができそうなの！");
