INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (100, 1, "ペコリーヌのプリン", "プリンセスなプリンなの\nお店にあったいろんな食べもの\nを入れられるだけ入れてみたの\nどんな味なのか楽しみなの", "やばいですね☆なの！", 3, "おいしすぎて食べ始めたら\n止まらない最高のプリンなの\n今のミヤコを止められるヤツは\n誰もいないの", 1, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (101, 1, "コッコロのプリン", "いつもオマエのそばに寄りそう\nヤツのぷりんなの。ミヤコの\nワガママもやさしく受け入れて\nくれそうなの", "主さま、なの！", 3, "なんだかすごくやさしい気持ち\nになる味なの…\nおはようからおやすみまで\nずっと一緒な味なの…", 2, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (102, 0, "キャルのプリン", "ツンツンデレデレなヤツの\nプリンなの。砂糖とスパイスを\n混ぜてみたの。どんな味になる\nか楽しみなの", "ぶっ殺すぞ！なの！\n…デレデレはどこなの？", 3, "いろんな味が一度で楽しめる\nプリンなの。ツンデレはプリン\nにするとおいしいの。ミヤコも\nツンデレを目指してみるの", 3, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (103, 1, "シェフィのプリン", "オマエと同じで、一度赤ちゃん\nみたいになったヤツのプリン\nなの。ヒンヤリ、プリンアイス\nにしてみたの", "おにーたん♪なの！", 3, "あったかいところで食べる\nプリンアイスは最高なの～♪\n安心しすぎて昔の記憶のない\nミヤコも心が子どもになるの～", 4, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (104, 1, "イリヤのプリン", "イリヤを見てたら思いついた\nトマトジュースが隠し味の\nプリンなの。栄養満点なの", "ハーハッハッハ！\n世界征服なのじゃなのー！", 3, "トマトで栄養補給も完璧なの\nどんどん力がみなぎってきて\n復活も夢じゃないの\nミヤコが復活したら…", 5, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (105, 1, "アカリのプリン", "アカリのプリンなの。ヨリと\n同じくらいかわいく作って\nやったの。正直ミヤコは\nおいしければなんでもいいの", "お兄ちゃんのエッチ！　なの！", 3, "見た目がかわいくてもミヤコは\n味にも厳しいの。あむっ…\nこ、これは…！なんだかすごく\n変な気分になってきたの…", 6, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (106, 1, "ヨリのプリン", "かわいくつくらないとアカリが\n怒りそうだからヨリのプリンは\nかわいく作ったの。自信作なの", "えっと…その…あんたのこと…\nだ、大っ嫌いなのーー！", 3, "んむぅ…これはなかなか感想が\n難しいプリンなの…率直に言い\nたいような…心のうちに秘めて\nおきたいような…", 7, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (107, 0, "シノブのプリン", "シノブはいつもミヤコに\nやさしくしてくれるから\n丁寧に作ったの。ドクロ親父は\nいらないから作ってないの", "お父さん…\nいいかげんにしてなの！\nん？プリンの未来は…\n見えなかったの…", 3, "このプリンを食べるとシノブが\nミヤコの中にいるみたいなの！\nシノブの力があれば未来の\nプリンを見放題なの！", 8, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (108, 1, "ジュンのプリン", "黒い鎧を脱がないヤツのプリン\nなの。固めのプリンを目指したら\nカチカチになりすぎたの", "インフェルノシールド！なの！", 3, "固すぎてかじれないから丸吞み\nにしたの。なんだか体が硬く\nなった気がするの。みんなを\n守れる気がするの！", 9, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (109, 1, "トモのプリン", "オマエをいっつもからかってる\nヤツのプリンなの\nちょっぴり変わったスパイスで\nからかいを表現したの", "あなたといると調子が狂うな\n…なの\n翻弄してたのはオマエなの？", 3, "口の中でスパイスがプチプチ\nはじけて新食感なの！\nプチプチがミヤコを\n翻弄するの！", 10, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (110, 1, "マツリのプリン", "王宮騎士団にいるちっこいヤツ\nなの。ちっこいけどやるヤツ\nらしいから、濃いめの味付けに\nしてみたの", "タイガーヒーローボンバー！\nなの！", 3, "ん～見かけによらず濃厚な\nパンチのある味わいが\n口いっぱいに広がるの！", 11, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (111, 1, "クロエのプリン", "なんかすごくクールなプリンなの\nなんかテンションも少し\n低そうなの。これ\n食べちゃっても大丈夫なの？", "ま、いいんじゃん？\nしらんけどなの", 3, "クールな見た目なのにとっても\n優しい味なの！なんだか\n懐かしさを感じるの…", 13, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (112, 1, "チエルのプリン", "きれいにデコれたの！\nカラフルでおいしそうなの\nこれは期待できそうなの", "ぷりーん★なの！", 3, "なんか変わった食感なの…\nちゃる…ちょる…ちぇる？\nちぇるってしてるの\nでもちぇるってなんなの？", 14, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (113, 1, "ユニのプリン", "見た目は完ぺきなの！\nこれをつくったやつは\nきっと天才なの！\n早く食べたいの！", "端的に換言すれば\n青春の味なの？", 3, "味はなんか妙に\n甘酸っぱいの…\n食べたことない味なの\nこの味ってもしかして…", 15, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (114, 1, "サレンのプリン", "オマエから聞いた懐かしい\n味ってヤツを全部入れてみたの\nオマエ記憶がないのに懐かしい\nってどういうことなの？", "幼なじみはあんた…なの？", 3, "ミヤコも幽霊になる前の記憶\nがないのに、なんだか懐かしい\n気持ちになってくるの…", 16, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (115, 1, "スズメのプリン", "たまたま会ったメイドに教えて\nもらった作り方なの。メイドは\nプリンも作れるはずなの。\nなのにこの不安は何なの…？", "ひゃああんなの～！", 3, "おかしいの。このプリンを\n食べてから、３回も転んだの\nミヤコは幽霊だから転んだり\nしないの。なんでなの…？", 17, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (116, 0, "クルミのプリン", "怖がりな子どもが教えてくれた\n小さくてかわいいプリンなの　\n触ってないのにぷるぷる震える\n驚きのやわらかさなの", "サルキデバレヤ！なの！", 3, "甘さのなかに少しのしょっぱさ\nクセになる味なの\nこのプリンはがんばればきっと\nいろんな美味しいになれるの", 18, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (117, 1, "アヤネのプリン", "くまのぬいぐるみを見ていたら\nひらめいたの。小さいけど\nしっかりした甘さの元気に\nなれるプリンなの！", "プリンフルスイングなの～！", 3, "このプリン、食べるとなんだか\nお姉ちゃんになった気分に\nなるの。ピンチのプリンは\nミヤコが守ってやるの！", 19, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (118, 1, "マホのプリン", "マホマホ王国って国にいる\nお姫様のプリンなの\n魔物も虜にしちゃう\nすてきなプリンなの", "みらくるみやりん\nくるりんぱ～☆なの！", 3, "メルヘンパワーがあふれてきて\nいまならミヤコにも魔法が\n使える気がするの！\nほな、いきまひょ～なの！", 20, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (119, 1, "マコトのプリン", "カッコいいプリンを目指して\nワイルドなものをガンガン\n入れてみたの。きっと熱い\nプリンができるはずなの", "ウルフェンバイトなの！", 3, "うおーん！ミヤコなんだか\n強くなった気がするの！\n困ったことがあればミヤコが\nまとめて面倒見てやるの！", 21, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (120, 1, "カオリのプリン", "どっかの国でよくとれる\nゴーヤーに似てるやつを\nいれてみたの！ちょっと\n苦いらしいけど…", "なんくるないさ～！なの！", 3, "一口食べたら元気が\nあふれて止まらないの！\n気分がいいから、一曲\n踊りでも披露してやるの！", 22, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (121, 1, "カスミのプリン", "勘の鋭いやつが好きな食べ物を\nたくさん入れてみたの\n難しいこと考えるのにぴったり\nらしいけど、なんでなの？", "ふふん。簡単な推理なの", 3, "むむむ…なるほどなの\n上質な糖分をふんだんに\n使うことで、脳をより活性化\nできるからなの！", 23, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (122, 1, "マヒルのプリン", "搾りたての牛乳を\nたっぷり使ったプリンなの～\nマヒルに手伝ってもらって\nミヤコ直々に搾ってきたの！", "エリザベス～！なの～！", 3, "ミルクがとっても濃厚で\nほっぺたが落ちちゃうの～♪\nこのプリンからは牧場の…\n自然の息吹を感じるの！", 24, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (123, 1, "シオリのプリン", "マヒルからやさしいヤツだって\n聞いたの。やさしさを表現\nするために奥ゆかしい甘さに\n仕上げてみたの", "私の好きな本…読んで\nもらえたらうれしいです…\nなの", 3, "ほのかな甘さでありながら\nしっかりとした主張も\n感じさせる\n味わい深いプリンなの", 25, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (124, 1, "リマのプリン", "牧場にいるヤツらしいの\n牛とは違うらしいの\nもふもふ気持ちいいらしいから\n食感にこだわって作ったの", "もふもふにしてあげるなの！", 3, "こ、これは…！口の中に入れた\n瞬間にふんわり溶けて\nなくなっちゃうの！\nその食感がやみつきなの！", 26, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (125, 0, "リンのプリン", "いっつも寝てるやつらしいの\nぐっすり眠れる薬草をたくさん\n入れてみたの。プリンとして\nおいしくなるかはわからないの", "めんどくさいな～なの…", 3, "んむぅ…評価が難しい味なの…\nおいしいのはおいしいの…\nん～考えてたら…\n眠くなってきたの…", 27, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (126, 1, "ラビリスタのプリン", "クレープ屋の真っ赤な人に\n教わったプリンなの\n作り方を丁寧に教えてくれて\nとっても優しい人だったの！", "プリンの権能を解放するの～！", 3, "プ…プリンなのにクレープの味\nがするの。味もすごく普通なの\nでも…なんだかプリンの新しい\n扉を開けそうな気がするの…！", 28, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (127, 0, "シズルのプリン", "お店に落ちてた謎のレシピの\nプリンなの。レシピの表紙には\n『お姉ちゃんの愛情たっぷり』\nって書いてあったの", "お姉ちゃんパワー全開！なの！", 3, "とろける食感が絶品なの～♪\n食べるとなんだか幸せな\n気持ちになれるの…これが\n愛情ってやつなの？", 29, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (128, 1, "リノのプリン", "リノから作り方を教わったの！\nさすがクレープ屋で働いてる\nだけあって美味しそうなの！", "お兄ちゃん、プリンをよこすの！", 3, "これで妹力？があがるらしいの。\n妹になればお兄ちゃんに\nプリンをたかり放題なの！\n最高なの～！", 30, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (129, 1, "ヒヨリのプリン", "プリンのレシピで悩んでたら\n通りすがりのおねーさんが\n一緒に考えてくれたプリンなの\nいいヤツもたまにはいるの～♪", "困った時はお互いさまさま！\nなの！", 3, "食べたら元気が湧いてくるの\nだからプリンのレシピのお礼に\nミヤコも人助けを手伝って\nやったの。ミヤコはえらいの！", 31, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (130, 1, "ユイのプリン", "ミヤコががんばって作った\nこだわりのプリンなの～\nプリンは癒しなの。だから優しい\n気持ちで丁寧に作ったの", "フラワリングヒールなの～♪", 3, "はっ…思わずお花畑が見えたの\n身も心も包まれるような\nとっても優しい味がするの\nこれは…まさに癒しなの～♪", 32, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (131, 1, "レイのプリン", "究極のプリンを作るため\nミヤコは厳しい修行をしたの…\nその結果あって完成した\nクールで完璧なプリンなの！", "スラッシュテンペスト！なの。", 3, "確かに完璧なプリンなの\nでも究極のプリンと呼ぶには\nまだまだ修行が必要なの…\nプリン作りは奥が深いの…！", 33, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (132, 1, "アキノのプリン", "豪華で高級な\nお嬢様プリンなの\n中には宝石みたいな\n大きいイチゴを入れるの！", "おーほっほほほ！なの！", 3, "味にも素材にもこだわりが\nあるの。一流は妥協しないの\nもっと豪華に、もっとおいしく\nできるはず…", 34, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (133, 1, "ユカリのプリン", "外はしっかり、中はふわとろの\nプリンにするの\n泡みたいなしゅわしゅわの\n生クリームも入れるの♪", "プリしゅわ最高～！なの！", 3, "プリンは飲み物とも言うの\nぐいっていってみるの\n…ふわしゅわでとろけるの～！\n体に染みわたるの～", 35, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (134, 1, "タマキのプリン", "餡子を入れて\nオーブンでこんがり焼くの\nたい…焼きプリンなの\n斬新なの！", "にゃにゃにゃ！なの！", 3, "このプリンは焼きたてのうちに\n食べるのが一番なの…！\n熱いけどおいしくて\nうずうずしてきたの…！", 36, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (135, 0, "ミフユのプリン", "栄養バランスを考えて\n木の実をたくさん入れるの\nあれこれ忙しいときでも\n頼りになるプリンなの！", "効率全開！なの！", 3, "プリンはいつでも手軽に\n食べられるのも魅力なの\nこれは食べ応えもあるから一つ\n食べればいっぱい動けるの", 37, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (136, 1, "ミサトのプリン", "エルフの森で子どもにもらった\n薬草を使って作ったの\nこんな優しい味のプリンは\n初めてなの…", "あらあら～なの～♪", 3, "このプリンを食べてから\nいい子になった気がするの\n大好きなプリンを泣いてる子に\nあげたの。いい気分なの", 38, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (137, 1, "ハツネのプリン", "お昼寝でみた夢の中に\nでてきた不思議なプリンを\n作ってみたの。見てたらまた\n眠くなってきたの…", "スヤスヤ～…なの…", 3, "あまいプリンが口の中で\nぽんぽん弾ける未知の味なの！\n今なら不思議な力で世界も\n救えそうなの！でも眠くなるの…", 39, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (138, 1, "アオイのプリン", "お店のはしっこに一つだけ\n残ったおもちゃを見て思い\nついたの。他のプリンと一緒に\n置いておくと震えだすの", "ぼっちは卒業なの～！", 3, "知らないうちにはしっこに\n隠れちゃってたの。食べたら\nミヤコ、シノブたちに会いに\nいきたくなってきたの", 40, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (139, 1, "ノゾミのプリン", "トップオブアイドルの\nプリンなの。キラキラした\nものを入れて、あつ～い\nオーブンで焼いてみたの", "リズムあげるのー！", 3, "口の中に華やかな甘さが\n広がる…これがアイドルなの？\n急に歌って踊りたく\nなってきたの！", 41, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (140, 1, "チカのプリン", "ミヤコにプリンをくれるすごく\nいいヤツのプリンなの\n精霊に呼びかけながら\n一生懸命かき混ぜたの", "風の加護を！なの！", 3, "プリンの生地がとっても\nなめらかで最高の舌触りなの！\n精霊、ミヤコのプリンのために…", 42, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (141, 1, "ツムギのプリン", "歌って踊って衣装も作れるヤツ\nのプリンなの。このプリンは\nきれいに包装するの。プリンを\n最高の形で着飾らせるの", "騎士さんのために\nしたんじゃありません！なの！", 3, "包装は食べるまでの手間が\n増えてめんどくさいの。でも\nそのめんどくささもプリンの\n味を引き立てるの", 43, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (142, 0, "クリスティーナのプリン", "プリンをくれないならどうでも\nいいヤツなの。でも横で\nいろいろ言ってきたから\nがんばって作ったの", "宴の始まりなの！", 3, "んむっ！？\nく、口のなかでプリンの\nおいしさが大暴れなの！\n止まらないの！", 12, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (143, 1, "ルカのプリン", "大人な甘さ控えめのプリンを\n作るの。試しにお魚を入れて\nみるの。プリンの可能性は\n無限大なの！", "一刀両断、覚悟しな！なの！", 3, "大人の味なの。今やミヤコは\nみんなの頼れる姐御なの！\n悪さするやつはミヤコが\n許さないの！", 44, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (144, 1, "ナナカのプリン", "サイカワサイツヨな\n魔法のプリンなの\n中身をカラフルにするの～\n甘いプリンは頭が冴えるの！", "きゅぴーん！キタコレ！なの！", 3, "一口食べる度に味が変わって\n魔法みたいなの！\nもしかして全部食べたら\nミヤコも魔法少女になれるの？", 45, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (145, 1, "アンナのプリン", "小難しい言葉をささやきながら\n作ったら魔力あふれるプリンが\n爆誕したの！でも、不思議と\nイタイタしいのはなぜなの～？", "人呼んで、疾風のミヤコなの！", 3, "一口食べたら謎の組織に\n狙われたり、前世の記憶が蘇る\n気がするの～設定盛りすぎなの\nミヤコは、ミヤコは…", 46, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (146, 1, "エリコのプリン", "甘酸っぱい赤い木の実を\n切り刻んで入れるの\n甘くてちょっぴり刺激的な\nヤンデレのプリンなの", "クスクス…クスクスクス…なの", 3, "なんか普通のプリンよりも\nいい匂いがするの～…\nプリンのことがもっともっと\n好きになっちゃうの…", 47, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (147, 1, "ミツキのプリン", "健康に気を遣うミヤコは\nプリンにお薬を入れてみたの～\nでもなんだか毒々しいから\n彩りにバラを添えてみたの～", "どんなプリンも\nミヤコの隻眼からは\n逃れられないの～！", 3, "鼻に抜ける薬のにおいがアレ\nだけど大人っぽくて頭が\nよくなった気がするの。無性に\n実験したくてしかたないの", 48, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (148, 1, "モニカのプリン", "小さいけどしっかりした\n軍人のプリンなの\nちょっと固めにして\n砂糖はいっぱい入れるの", "私は子どもではない！なの！", 3, "ん～王道なプリンは\n甘くて安定したおいしさなの～\n思わず笑顔になっちゃうの\nにこにこなの～♪…はっ！", 49, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (149, 1, "ニノンのプリン", "オーエド町で手に入れた素材と\nトーゴクに伝わる術をかけて\nプリンを作るの。シュババ！\n忍法隠し味の術なの！", "ショーグン！　ミヤコと一緒に\n天下統一するデースなの！", 3, "このプリンを食べてから\n誰かにお仕えしなきゃ\nいけない気がするデースなの\nそれはショ、ショー…", 50, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (150, 0, "クウカのプリン", "オマエが持ってきた手錠を\nヒントに作ったプリンなの\nプリンが動けないように\nカチカチに固めたの", "…ぐふふ、じゅるり～なの", 3, "カチカチに固めたせいで食感が\nプリンのようで\nプリンじゃないの…でもこの\nもどかしさが癖になるの…", 51, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (151, 1, "ユキのプリン", "とっても美しいプリンなの\n美しいものを手当たり次第\n入れたらすごいことになったの", "あぁ、ミヤコはなんて美しいの～", 3, "味も美しい以外の感想が\n出てこないの。ていうかこれを\n食べたミヤコも美しいの！\n鏡、鏡はどこなの！", 52, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (152, 0, "アユミのプリン", "知らないうちにお菓子が売れた\n時に思いついたプリンなの。\n作り方もよく覚えてない…\nけど、きっとおいしいの！", "ジー…ってミヤコなにしてるの\nミヤコはストーカーじゃないの！", 3, "うん、やっぱりおいしく…\nん？あそこにいるのは\nアイツなの。あれ？\n体が勝手にアイツの方に…", 53, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (153, 0, "ミソギのプリン", "イタズラ好きの手下と一緒に\n作ったプリンなの。見た目は\nかわいいけど、作り方は\n意外と複雑なの～", "にひひ…イタズラの時間なの！", 3, "見た目通り甘々で優しい味なの\nでも、レシピを見たら裏面に\n別の作り方が書いてあるの。\nこれだと激辛プリンに…", 54, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (154, 0, "ミミのプリン", "素直でやさしい手下と一緒に\n動物のぬいぐるみをいっぱい\n並べて作ったの", "ぴょんぴょこぴょーん、なの♪", 3, "ん～、ふわふわの甘々なの！\nなんだか楽しくなって\n心も体もるんたったしてくるの", 55, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (155, 0, "キョウカのプリン", "幽霊を怖がっている手下と\n一緒に作ったプリンなの。\n小さいクセにきっちりしてて\nとってもかわいいプリンなの", "へんたいふしんしゃさんなの！", 3, "ひんやりしてて暑い日でも\nおいしく食べられそうなの！\nん？これを買いたい大人が\nいる？それって…", 56, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (156, 1, "スズナのプリン", "カリスマモデルなプリンなの\nモリモリにデコレーションして\nカリスマ感を演出したの", "ハート狙い撃ち！なの！", 3, "見て食べて楽しめる新機軸の\nプリンなの。ミヤコは幽霊\nだけど自分の足でランウェイを\n歩きたくなってきたの！", 57, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (157, 0, "イオのプリン", "学校の先生をしてるヤツの\nプリンなの。お堅くしようと\nしたら、いつの間にか色っぽく\nなっちゃったの。なんでなの？", "授業を始めます♪なの！", 3, "プ、プリンは全部ミヤコのもの\nなのに、なんだかこのプリンは\nいろんな人に教えたく\nなっちゃう不思議な味なの！", 58, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (158, 1, "ミサキのプリン", "少し背伸びしたおませな子を\n表現したプリンなの。あえて\n甘さ控えめなの。カラメルは\n後乗せできるから安心するの", "立派なレディ、なの！", 3, "ミヤコはプリンならどんなもの\nでも食べるの。でもカラメルを\nちょっとだけ足すの。\nちょっとだけなら…", 59, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (159, 1, "ホマレのプリン", "謎に包まれたプリンができたの\n何か隠してるようなの…\nこれは危険なプリンなの！", "プリンを持ってこないと\nお仕置きだよ～☆なの！", 3, "味はすごいゴージャスなの！\nボスの風格があるの！\nビッグなミヤコに\nプリンを捧げるの！", 60, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (160, 1, "カヤのプリン", "ワイルドな見た目なの！\nこれは期待できそうなの。\nきっと躍動感あふれる味なの！", "たぎってきたの！", 3, "最初の一口目が\nすごいインパクトなの！\n無性にケンカしたくなるの！\nプリンをよこせなの！", 61, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (161, 0, "イノリのプリン", "なんか見た目がパッとしないの\nこれほんとに大丈夫なの？\nなんか頼りないの…", "下剋上、やってやるですなの！", 3, "見た目はアレだけど味はおいし\nいの…あと主張もはっきりして\nるの！やる気と自信だけは\n出てきたのうおおおーなの！", 62, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (162, 1, "ムイミのプリン", "天下の大悪党のプリンなの\nこれまでのジョーシキに\n捉われないプリンを\n作ってみたの～", "どけどけ～！なの～！", 3, "むむむ…食べたら\nやる気が湧いてきたの！\nミヤコを邪魔するやつはみんな\nまとめてぶっ飛ばしてやるの～", 63, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (163, 1, "ネネカのプリン", "いろんなものに変身したり\n分身したりできるヤツの\nプリンなの。型で同じプリンを\nたくさんたくさん作ったの", "ミラーミラー！なの！", 3, "いつのまにか作ったの全部\n食べちゃったの。なんで\nプリンは分身しないの？\n無限に増えろなの！", 64, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (164, 1, "クレジッタのプリン", "金の亡者のプリンなの\n金にものを言わせて作って\nみたの。おばあちゃんの\nプリンと対極なの", "プリンに忠誠を誓いますわ！\nなの！", 3, "むぅ…認めたくないけど\nおいしいの…これを何個も\n作れるほどのお金を稼ぐのは\n大変なの…でもおいしいから…", 65, 5);
INSERT INTO `v1_22755d7ca1546e966ef35ad700f2a7b77697c92a97cc9835b8cbd80b7204d0e3` VALUES (165, 1, "カリンのプリン", "みんなお世話になるギルド\n管理協会職員のプリンなの\nお役所感を出すために手堅く\n王道に作ったの", "ギルド管理協会へようこそ！\nなの！", 3, "手堅い…！手堅いの…！\n隙のないプリンなの！\n今のミヤコなら完璧な\n書類作成ができそうなの！", 66, 5);
